INSERT INTO `songs` (`id`, `author_id`, `title`, `content`) VALUES
	(5001, 494, '水调歌头·明月几时有', '丙辰中秋，欢饮达旦，大醉，作此篇，兼怀子由。 明月几时有？把酒问青天。不知天上宫阙，今夕是何年。我欲乘风归去，又恐琼楼玉宇，高处不胜寒。起舞弄清影，何似在人间？(何似 一作：何时；又恐 一作：惟 / 唯恐) 转朱阁，低绮户，照无眠。不应有恨，何事长向别时圆？人有悲欢离合，月有阴晴圆缺，此事古难全。但愿人长久，千里共婵娟。(长向 一作：偏向)'),
	(5002, 173, '满江红·写怀', '怒发冲冠，凭栏处、潇潇雨歇。抬望眼，仰天长啸，壮怀激烈。三十功名尘与土，八千里路云和月。莫等闲，白了少年头，空悲切！(栏 通：阑) 靖康耻，犹未雪。臣子恨，何时灭！驾长车，踏破贺兰山缺。壮志饥餐胡虏肉，笑谈渴饮匈奴血。待从头、收拾旧山河，朝天阙。(壮志 一作：壮士；兰山缺 一作：兰山阙)'),
	(5003, 494, '念奴娇·赤壁怀古', '大江东去，浪淘尽，千古风流人物。 故垒西边，人道是，三国周郎赤壁。 乱石穿空，惊涛拍岸，卷起千堆雪。 江山如画，一时多少豪杰。 遥想公瑾当年，小乔初嫁了，雄姿英发。 羽扇纶巾，谈笑间，樯橹灰飞烟灭。(樯橹 一作：强虏) 故国神游，多情应笑我，早生华发。 人生如梦，一尊还酹江月。(人生 一作：人间；尊 通：樽)'),
	(5004, 633, '卜算子·咏梅', '驿外断桥边，寂寞开无主。已是黄昏独自愁，更著风和雨。 无意苦争春，一任群芳妒。零落成泥碾作尘，只有香如故。'),
	(5005, 305, '声声慢·寻寻觅觅', '寻寻觅觅，冷冷清清，凄凄惨惨戚戚。乍暖还寒时候，最难将息。三杯两盏淡酒，怎敌他、晚来风急？雁过也，正伤心，却是旧时相识。 满地黄花堆积。憔悴损，如今有谁堪摘？守着窗儿，独自怎生得黑？梧桐更兼细雨，到黄昏、点点滴滴。这次第，怎一个愁字了得！（守着窗儿 一作：守著窗儿）'),
	(5006, 591, '青玉案·元夕', '东风夜放花千树。更吹落、星如雨。宝马雕车香满路。凤箫声动，玉壶光转，一夜鱼龙舞。 蛾儿雪柳黄金缕。笑语盈盈暗香去。众里寻他千百度。蓦然回首，那人却在，灯火阑珊处。'),
	(5007, 591, '破阵子·为陈同甫赋壮词以寄之', '醉里挑灯看剑，梦回吹角连营。八百里分麾下炙，五十弦翻塞外声。沙场秋点兵。 马作的卢飞快，弓如霹雳弦惊。了却君王天下事，赢得生前身后名。可怜白发生！'),
	(5008, 494, '江城子·乙卯正月二十日夜记梦', '十年生死两茫茫，不思量，自难忘。千里孤坟，无处话凄凉。纵使相逢应不识，尘满面，鬓如霜。 夜来幽梦忽还乡，小轩窗，正梳妆。相顾无言，惟有泪千行。料得年年肠断处，明月夜，短松冈。(肠断 一作：断肠)'),
	(5009, 591, '西江月·夜行黄沙道中', '明月别枝惊鹊，清风半夜鸣蝉。稻花香里说丰年，听取蛙声一片。 七八个星天外，两三点雨山前。旧时茅店社林边，路转溪桥忽见。(溪桥 一作：溪头)'),
	(5010, 305, '武陵春·春晚', '风住尘香花已尽，日晚倦梳头。物是人非事事休，欲语泪先流。 闻说双溪春尚好，也拟泛轻舟。只恐双溪舴艋舟，载不动许多愁。'),
	(5011, 496, '渔家傲·秋思', '塞下秋来风景异，衡阳雁去无留意。四面边声连角起，千嶂里，长烟落日孤城闭。 浊酒一杯家万里，燕然未勒归无计。羌管悠悠霜满地，人不寐，将军白发征夫泪。'),
	(5012, 494, '浣溪沙·游蕲水清泉寺', '游蕲水清泉寺，寺临兰溪，溪水西流。 山下兰芽短浸溪，松间沙路净无泥，潇潇暮雨子规啼。(潇潇 一作：萧萧) 谁道人生无再少？门前流水尚能西！休将白发唱黄鸡。'),
	(5013, 344, '雨霖铃·寒蝉凄切', '寒蝉凄切，对长亭晚，骤雨初歇。都门帐饮无绪，留恋处，兰舟催发。执手相看泪眼，竟无语凝噎。念去去，千里烟波，暮霭沉沉楚天阔。 多情自古伤离别，更那堪，冷落清秋节！今宵酒醒何处？杨柳岸，晓风残月。此去经年，应是良辰好景虚设。便纵有千种风情，更与何人说？(好景 一作：美景)'),
	(5014, 494, '江城子·密州出猎', '老夫聊发少年狂，左牵黄，右擎苍，锦帽貂裘，千骑卷平冈。为报倾城随太守，亲射虎，看孙郎。 酒酣胸胆尚开张，鬓微霜，又何妨！持节云中，何日遣冯唐？会挽雕弓如满月，西北望，射天狼。'),
	(5015, 251, '浣溪沙·一曲新词酒一杯', '一曲新词酒一杯，去年天气旧亭台。夕阳西下几时回？ 无可奈何花落去，似曾相识燕归来。小园香径独徘徊。'),
	(5016, 305, '如梦令·常记溪亭日暮', '常记溪亭日暮，沉醉不知归路。 兴尽晚回舟，误入藕花深处。 争渡，争渡，惊起一滩鸥鹭。'),
	(5017, 591, '清平乐·村居', '茅檐低小，溪上青青草。 醉里吴音相媚好，白发谁家翁媪？ 大儿锄豆溪东，中儿正织鸡笼。 最喜小儿亡赖，溪头卧剥莲蓬。(亡 通：无)'),
	(5018, 305, '一剪梅·红藕香残玉簟秋', '红藕香残玉簟秋。轻解罗裳，独上兰舟。云中谁寄锦书来，雁字回时，月满西楼。 花自飘零水自流。一种相思，两处闲愁。此情无计可消除，才下眉头，却上心头。'),
	(5019, 305, '如梦令·昨夜雨疏风骤', '昨夜雨疏风骤，浓睡不消残酒。试问卷帘人，却道海棠依旧。知否，知否？应是绿肥红瘦。'),
	(5020, 591, '南乡子·登京口北固亭有怀', '何处望神州？满眼风光北固楼。千古兴亡多少事？悠悠。不尽长江滚滚流。 年少万兜鍪，坐断东南战未休。天下英雄谁敌手？曹刘。生子当如孙仲谋。'),
	(5021, 591, '永遇乐·京口北固亭怀古', '千古江山，英雄无觅，孙仲谋处。舞榭歌台，风流总被雨打风吹去。斜阳草树，寻常巷陌，人道寄奴曾住。想当年，金戈铁马，气吞万里如虎。 元嘉草草，封狼居胥，赢得仓皇北顾。四十三年，望中犹记，烽火扬州路。可堪回首，佛狸祠下，一片神鸦社鼓。凭谁问，廉颇老矣，尚能饭否？'),
	(5022, 451, '鹊桥仙·纤云弄巧', '纤云弄巧，飞星传恨，银汉迢迢暗度。金风玉露一相逢，便胜却人间无数。(度 通：渡) 柔情似水，佳期如梦，忍顾鹊桥归路。两情若是久长时，又岂在朝朝暮暮。'),
	(5023, 494, '定风波·莫听穿林打叶声', '三月七日，沙湖道中遇雨。雨具先去，同行皆狼狈，余独不觉，已而遂晴，故作此词。 莫听穿林打叶声，何妨吟啸且徐行。竹杖芒鞋轻胜马，谁怕？一蓑烟雨任平生。 料峭春风吹酒醒，微冷，山头斜照却相迎。回首向来萧瑟处，归去，也无风雨也无晴。'),
	(5024, 344, '蝶恋花·伫倚危楼风细细', '伫倚危楼风细细，望极春愁，黯黯生天际。草色烟光残照里，无言谁会凭阑意。(阑 通 栏) 拟把疏狂图一醉，对酒当歌，强乐还无味。衣带渐宽终不悔，为伊消得人憔悴。'),
	(5025, 305, '醉花阴·薄雾浓云愁永昼', '薄雾浓云愁永昼，瑞脑消金兽。佳节又重阳，玉枕纱橱，半夜凉初透。(橱 通：厨) 东篱把酒黄昏后，有暗香盈袖。莫道不销魂，帘卷西风，人比黄花瘦。(人比 一作：人似；销 一作：消)'),
	(5026, 494, '蝶恋花·春景', '花褪残红青杏小。燕子飞时，绿水人家绕。枝上柳绵吹又少。天涯何处无芳草！ 墙里秋千墙外道。墙外行人，墙里佳人笑。笑渐不闻声渐悄。多情却被无情恼。'),
	(5027, 496, '苏幕遮·怀旧', '碧云天，黄叶地。秋色连波，波上寒烟翠。山映斜阳天接水。芳草无情，更在斜阳外。 黯乡魂，追旅思。夜夜除非，好梦留人睡。明月楼高休独倚。酒入愁肠，化作相思泪。(留人睡 一作：留人醉)'),
	(5028, 433, '卜算子·送鲍浩然之浙东', '水是眼波横，山是眉峰聚。欲问行人去那边？眉眼盈盈处。 才始送春归，又送君归去。若到江南赶上春，千万和春住。'),
	(5029, 591, '丑奴儿·书博山道中壁', '少年不识愁滋味，爱上层楼。爱上层楼。为赋新词强说愁。 而今识尽愁滋味，欲说还休。欲说还休。却道天凉好个秋。'),
	(5030, 633, '钗头凤·红酥手', '红酥手，黄縢酒，满城春色宫墙柳。东风恶，欢情薄。一怀愁绪，几年离索。错、错、错。 春如旧，人空瘦，泪痕红浥鲛绡透。桃花落，闲池阁。山盟虽在，锦书难托。莫、莫、莫！'),
	(5031, 591, '菩萨蛮·书江西造口壁', '郁孤台下清江水，中间多少行人泪。西北望长安，可怜无数山。 青山遮不住，毕竟东流去。江晚正愁余，山深闻鹧鸪。 (愁余 一作：愁予)'),
	(5032, 278, '卜算子·我住长江头', '我住长江头，君住长江尾。日日思君不见君，共饮长江水。 此水几时休，此恨何时已。只愿君心似我心，定不负相思意。'),
	(5033, 305, '渔家傲·天接云涛连晓雾', '天接云涛连晓雾，星河欲转千帆舞。仿佛梦魂归帝所。闻天语，殷勤问我归何处。 我报路长嗟日暮，学诗谩有惊人句。九万里风鹏正举。风休住，蓬舟吹取三山去！'),
	(5034, 352, '蝶恋花·庭院深深深几许', '庭院深深深几许，杨柳堆烟，帘幕无重数。玉勒雕鞍游冶处，楼高不见章台路。 雨横风狂三月暮，门掩黄昏，无计留春住。泪眼问花花不语，乱红飞过秋千去。'),
	(5035, 353, '生查子·元夕', '去年元夜时，花市灯如昼。 月上柳梢头，人约黄昏后。 今年元夜时，月与灯依旧。 不见去年人，泪湿春衫袖。(泪湿 ：一作：满)'),
	(5036, 451, '浣溪沙·漠漠轻寒上小楼', '漠漠轻寒上小楼，晓阴无赖似穷秋。淡烟流水画屏幽。 自在飞花轻似梦，无边丝雨细如愁。宝帘闲挂小银钩。'),
	(5037, 633, '诉衷情·当年万里觅封侯', '当年万里觅封侯，匹马戍梁州。关河梦断何处？尘暗旧貂裘。 胡未灭，鬓先秋，泪空流。此生谁料，心在天山，身老沧洲。'),
	(5038, 494, '浣溪沙·簌簌衣巾落枣花', '徐门石潭谢雨，道上作五首。潭在城东二十里，常与泗水增减清浊相应。 簌簌衣巾落枣花，村南村北响缲车，牛衣古柳卖黄瓜。(缲 通：缫) 酒困路长惟欲睡，日高人渴漫思茶。敲门试问野人家。'),
	(5039, 133, '钗头凤·世情薄', '世情薄，人情恶，雨送黄昏花易落。晓风干，泪痕残。欲笺心事，独语斜阑。难，难，难！ 人成各，今非昨，病魂常似秋千索。角声寒，夜阑珊。怕人寻问，咽泪装欢。瞒，瞒，瞒！(装欢 通：妆)'),
	(5040, 591, '水龙吟·登建康赏心亭', '楚天千里清秋，水随天去秋无际。遥岑远目，献愁供恨，玉簪螺髻。落日楼头，断鸿声里，江南游子。把吴钩看了，栏杆拍遍，无人会，登临意。(栏杆 一作：阑干) 休说鲈鱼堪脍，尽西风，季鹰归未？求田问舍，怕应羞见，刘郎才气。可惜流年，忧愁风雨，树犹如此！倩何人唤取，红巾翠袖，揾英雄泪！'),
	(5041, 251, '破阵子·春景', '燕子来时新社，梨花落后清明。池上碧苔三四点，叶底黄鹂一两声。日长飞絮轻。 巧笑东邻女伴，采桑径里逢迎。疑怪昨宵春梦好，元是今朝斗草赢。笑从双脸生。'),
	(5042, 251, '蝶恋花·槛菊愁烟兰泣露', '槛菊愁烟兰泣露，罗幕轻寒，燕子双飞去。明月不谙离恨苦，斜光到晓穿朱户。(双飞去 一作：双来去 离恨苦 一作：离别苦) 昨夜西风凋碧树，独上高楼，望尽天涯路。欲寄彩笺兼尺素，山长水阔知何处？'),
	(5043, 515, '虞美人·听雨', '少年听雨歌楼上。红烛昏罗帐。壮年听雨客舟中。江阔云低、断雁叫西风。 而今听雨僧庐下。鬓已星星也。悲欢离合总无情。一任阶前、点滴到天明。'),
	(5044, 494, '阳关曲·中秋月', '中秋作本名小秦王，入腔即阳关曲 暮云收尽溢清寒，银汉无声转玉盘。 此生此夜不长好，明月明年何处看。'),
	(5045, 305, '清平乐·年年雪里', '年年雪里，常插梅花醉。挼尽梅花无好意，赢得满衣清泪。 今年海角天涯，萧萧两鬓生华。看取晚来风势，故应难看梅花。'),
	(5046, 344, '望海潮·东南形胜', '东南形胜，三吴都会，钱塘自古繁华，烟柳画桥，风帘翠幕，参差十万人家。云树绕堤沙，怒涛卷霜雪，天堑无涯。市列珠玑，户盈罗绮，竞豪奢。 重湖叠巘清嘉。有三秋桂子，十里荷花。羌管弄晴，菱歌泛夜，嬉嬉钓叟莲娃。千骑拥高牙。乘醉听箫鼓，吟赏烟霞。异日图将好景，归去凤池夸。'),
	(5047, 344, '八声甘州·对潇潇暮雨洒江天', '对潇潇暮雨洒江天，一番洗清秋。渐霜风凄紧，关河冷落，残照当楼。是处红衰翠减，苒苒物华休。唯有长江水，无语东流。 不忍登高临远，望故乡渺邈，归思难收。叹年来踪迹，何事苦淹留？想佳人妆楼颙望，误几回、天际识归舟。争知我，倚阑杆处，正恁凝愁！(阑 一作：栏)'),
	(5048, 149, '扬州慢·淮左名都', '淳熙丙申至日，予过维扬。夜雪初霁，荠麦弥望。入其城，则四顾萧条，寒水自碧，暮色渐起，戍角悲吟。予怀怆然，感慨今昔，因自度此曲。千岩老人以为有“黍离”之悲也。 淮左名都，竹西佳处，解鞍少驻初程。过春风十里。尽荠麦青青。自胡马窥江去后，废池乔木，犹厌言兵。渐黄昏，清角吹寒。都在空城。 杜郎俊赏，算而今、重到须惊。纵豆蔻词工，青楼梦好，难赋深情。二十四桥仍在，波心荡、冷月无声。念桥边红药，年年知为谁生。'),
	(5049, 494, '水龙吟·次韵章质夫杨花词', '似花还似非花，也无人惜从教坠。抛家傍路，思量却是，无情有思。萦损柔肠，困酣娇眼，欲开还闭。梦随风万里，寻郎去处，又还被、莺呼起。 不恨此花飞尽，恨西园、落红难缀。晓来雨过，遗踪何在？一池萍碎。春色三分，二分尘土，一分流水。细看来，不是杨花，点点是离人泪。'),
	(5050, 494, '望江南·超然台作', '春未老，风细柳斜斜。试上超然台上看，半壕春水一城花。烟雨暗千家。 寒食后，酒醒却咨嗟。休对故人思故国，且将新火试新茶。诗酒趁年华。'),
	(5051, 494, '行香子·秋与', '昨夜霜风。先入梧桐。浑无处、回避衰容。问公何事，不语书空。但一回醉，一回病，一回慵； 朝来庭下，光阴如箭，似无言、有意伤侬。都将万事，付与千钟。任酒花白，眼花乱，烛花红。'),
	(5052, 494, '西江月·世事一场大梦', '世事一场大梦，人生几度秋凉？夜来风叶已鸣廊。看取眉头鬓上。(秋凉 一作：新凉) 酒贱常愁客少，月明多被云妨。中秋谁与共孤光。把盏凄然北望。'),
	(5053, 250, '临江仙·梦后楼台高锁', '梦后楼台高锁，酒醒帘幕低垂。去年春恨却来时。落花人独立，微雨燕双飞。 记得小蘋初见，两重心字罗衣。琵琶弦上说相思。当时明月在，曾照彩云归。(蘋 通：苹)'),
	(5054, 494, '临江仙·夜饮东坡醒复醉', '夜饮东坡醒复醉，归来仿佛三更。家童鼻息已雷鸣。敲门都不应，倚杖听江声。 长恨此身非我有，何时忘却营营。夜阑风静縠纹平。小舟从此逝，江海寄余生。'),
	(5055, 305, '点绛唇·蹴罢秋千', '蹴罢秋千，起来慵整纤纤手。露浓花瘦，薄汗轻衣透。 见客入来，袜刬金钗溜。和羞走，倚门回首，却把青梅嗅。'),
	(5056, 305, '蝶恋花·泪湿罗衣脂粉满', '泪湿罗衣脂粉满，四叠阳关，唱到千千遍。人道山长水又断，萧萧微雨闻孤馆。 惜别伤离方寸乱，忘了临行，酒盏深和浅。好把音书凭过雁，东莱不似蓬莱远。'),
	(5057, 173, '小重山·昨夜寒蛩不住鸣', '昨夜寒蛩不住鸣。惊回千里梦，已三更。起来独自绕阶行。人悄悄，帘外月胧明。 白首为功名。旧山松竹老，阻归程。欲将心事付瑶琴。知音少，弦断有谁听。'),
	(5058, 352, '玉楼春·尊前拟把归期说', '尊前拟把归期说，欲语春容先惨咽。人生自是有情痴，此恨不关风与月。 离歌且莫翻新阕，一曲能教肠寸结。直须看尽洛城花，始共春风容易别。'),
	(5059, 591, '鹧鸪天·送人', '唱彻《阳关》泪未干，功名馀事且加餐。浮天水送无穷树，带雨云埋一半山。 今古恨，几千般，只应离合是悲欢？江头未是风波恶，别有人间行路难！'),
	(5060, 352, '浪淘沙·把酒祝东风', '把酒祝东风，且共从容。垂杨紫陌洛城东。总是当时携手处，游遍芳丛。 聚散苦匆匆，此恨无穷。今年花胜去年红。可惜明年花更好，知与谁同？'),
	(5061, 344, '鹤冲天·黄金榜上', '黄金榜上，偶失龙头望。明代暂遗贤，如何向。未遂风云便，争不恣狂荡。何须论得丧？才子词人，自是白衣卿相。 烟花巷陌，依约丹青屏障。幸有意中人，堪寻访。且恁偎红倚翠，风流事，平生畅。青春都一饷。忍把浮名，换了浅斟低唱！'),
	(5062, 305, '鹧鸪天·桂花', '暗淡轻黄体性柔，情疏迹远只香留。何须浅碧深红色，自是花中第一流。(深红 一作：轻) 梅定妒，菊应羞，画阑开处冠中秋。骚人可煞无情思，何事当年不见收。(阑 通：栏)'),
	(5063, 162, '玉楼春·春景', '东城渐觉风光好。縠皱波纹迎客棹。绿杨烟外晓寒轻，红杏枝头春意闹。 浮生长恨欢娱少。肯爱千金轻一笑。为君持酒劝斜阳，且向花间留晚照。'),
	(5064, 284, '蝶恋花·春暮', '遥夜亭皋闲信步。才过清明，渐觉伤春暮。数点雨声风约住。朦胧淡月云来去。 桃杏依稀香暗渡。谁在秋千，笑里轻轻语。一寸相思千万绪。人间没个安排处。'),
	(5065, 515, '一剪梅·舟过吴江', '一片春愁待酒浇。江上舟摇，楼上帘招。秋娘渡与泰娘桥，风又飘飘，雨又萧萧。（渡 一作：度。桥 一作：娇） 何日归家洗客袍？银字笙调，心字香烧。流光容易把人抛，红了樱桃，绿了芭蕉。'),
	(5066, 250, '鹧鸪天·彩袖殷勤捧玉钟', '彩袖殷勤捧玉钟，当年拚却醉颜红。舞低杨柳楼心月，歌尽桃花扇底风。 从别后，忆相逢，几回魂梦与君同。今宵剩把银釭照，犹恐相逢是梦中。'),
	(5067, 130, '苏幕遮·燎沉香', '燎沉香，消溽暑。鸟雀呼晴，侵晓窥檐语。叶上初阳干宿雨、水面清圆，一一风荷举。 故乡遥，何日去。家住吴门，久作长安旅。五月渔郎相忆否。小楫轻舟，梦入芙蓉浦。'),
	(5068, 494, '临江仙·送钱穆父', '一别都门三改火，天涯踏尽红尘。依然一笑作春温。无波真古井，有节是秋筠。 惆怅孤帆连夜发，送行淡月微云。尊前不用翠眉颦。人生如逆旅，我亦是行人。(尊 通：樽)'),
	(5069, 397, '酒泉子·长忆观潮', '长忆观潮，满郭人争江上望。来疑沧海尽成空。万面鼓声中。 弄潮儿向涛头立。手把红旗旗不湿。别来几向梦中看。梦觉尚心寒。'),
	(5070, 305, '浣溪沙·闺情', '绣面芙蓉一笑开。斜飞宝鸭衬香腮。眼波才动被人猜。 一面风情深有韵，半笺娇恨寄幽怀。月移花影约重来。'),
	(5071, 173, '满江红·登黄鹤楼有感', '遥望中原，荒烟外、许多城郭。想当年、花遮柳护，凤楼龙阁。万岁山前珠翠绕，蓬壶殿里笙歌作。到而今、铁骑满郊畿，风尘恶。 兵安在？膏锋锷。民安在？填沟壑。叹江山如故，千村寥落。何日请缨提锐旅，一鞭直渡清河洛。却归来、再续汉阳游，骑黄鹤。'),
	(5072, 494, '水调歌头·安石在东海', '余去岁在东武，作《水调歌头》以寄子由。今年子由相从彭门居百余日，过中秋而去，作此曲以别。余以其语过悲，乃为和之，其意以不早退为戒，以退而相从之乐为慰云耳 安石在东海，从事鬓惊秋。中年亲友难别，丝竹缓离愁。一旦功成名遂，准拟东还海道，扶病入西州。雅志困轩冕，遗恨寄沧洲。 岁云暮，须早计，要褐裘。故乡归去千里，佳处辄迟留。我醉歌时君和，醉倒须君扶我，惟酒可忘忧。一任刘玄德，相对卧高楼。'),
	(5073, 451, '踏莎行·郴州旅舍', '雾失楼台，月迷津渡。桃源望断无寻处。可堪孤馆闭春寒，杜鹃声里斜阳暮。 驿寄梅花，鱼传尺素。砌成此恨无重数。郴江幸自绕郴山，为谁流下潇湘去。'),
	(5074, 305, '浣溪沙·小院闲窗春色深', '小院闲窗春已深，重帘未卷影沉沉。倚楼无语理瑶琴。(春已深 一作：春色深) 远岫出云催薄暮，细风吹雨弄轻阴。梨花欲谢恐难禁。'),
	(5075, 549, '青玉案·凌波不过横塘路', '凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月桥花院，琐窗朱户，只有春知处。 飞云冉冉蘅皋暮，彩笔新题断肠句。试问闲情都几许？一川烟草，满城风絮，梅子黄时雨。(版本一) 凌波不过横塘路，但目送、芳尘去。锦瑟华年谁与度？月台花榭，琐窗朱户，只有春知处。 碧云冉冉蘅皋暮，彩笔新题断肠句。试问闲愁都几许？一川烟草，满城风絮，梅子黄时雨。(版本二)'),
	(5076, 249, '洞仙歌·泗州中秋作', '青烟幂处，碧海飞金镜。永夜闲阶卧桂影。露凉时、零乱多少寒螀，神京远，惟有蓝桥路近。 水晶帘不下，云母屏开，冷浸佳人淡脂粉。待都将许多明，付与金尊，投晓共、流霞倾尽。更携取、胡床上南楼，看玉做人间，素秋千顷。'),
	(5077, 591, '摸鱼儿·更能消几番风雨', '淳熙己亥，自湖北漕移湖南，同官王正之置酒小山亭，为赋。 更能消、几番风雨，匆匆春又归去。惜春长怕花开早，何况落红无数。春且住，见说道、天涯芳草无归路。怨春不语。算只有殷勤，画檐蛛网，尽日惹飞絮。(怕 一作：恨) (无归路 一作：迷归路） 长门事，准拟佳期又误。蛾眉曾有人妒。千金纵买相如赋，脉脉此情谁诉？君莫舞，君不见、玉环飞燕皆尘土！闲愁最苦！休去倚危栏，斜阳正在，烟柳断肠处。'),
	(5078, 591, '一剪梅·中秋元月', '忆对中秋丹桂丛，花也杯中，月也杯中。今宵楼上一尊同，云湿纱窗，雨湿纱窗。(月也杯中 一作：月在杯中) 浑欲乘风问化工，路也难通，信也难通。满堂唯有烛花红，歌且从容，杯且从容。'),
	(5079, 591, '鹧鸪天·代人赋', '晚日寒鸦一片愁。柳塘新绿却温柔。若教眼底无离恨，不信人间有白头。 肠已断，泪难收。相思重上小红楼。情知已被山遮断，频倚阑干不自由。'),
	(5080, 352, '采桑子·群芳过后西湖好', '群芳过后西湖好，狼籍残红，飞絮濛濛。垂柳阑干尽日风。 笙歌散尽游人去，始觉春空。垂下帘栊，双燕归来细雨中。'),
	(5081, 494, '定风波·南海归赠王定国侍人寓娘', '常羡人间琢玉郎，天应乞与点酥娘。尽道清歌传皓齿，风起，雪飞炎海变清凉。 万里归来颜愈少，微笑，笑时犹带岭梅香。试问岭南应不好，却道：此心安处是吾乡。'),
	(5082, 305, '多丽·咏白菊', '小楼寒，夜长帘幕低垂。恨萧萧、无情风雨，夜来揉损琼肌。也不似、贵妃醉脸，也不似、孙寿愁眉。韩令偷香，徐娘傅粉，莫将比拟未新奇。细看取、屈平陶令，风韵正相宜。微风起，清芬酝藉，不减酴醿。 渐秋阑、雪清玉瘦，向人无限依依。似愁凝、汉皋解佩，似泪洒、纨扇题诗。朗月清风，浓烟暗雨，天教憔悴度芳姿。纵爱惜、不知从此，留得几多时。人情好，何须更忆，泽畔东篱。'),
	(5083, 693, '清平乐·春归何处', '春归何处。寂寞无行路。若有人知春去处。唤取归来同住。 春无踪迹谁知。除非问取黄鹂。百啭无人能解，因风飞过蔷薇。'),
	(5084, 495, '水调歌头·徐州中秋', '离别一何久，七度过中秋。去年东武今夕，明月不胜愁。岂意彭城山下，同泛清河古汴，船上载凉州。鼓吹助清赏，鸿雁起汀洲。 坐中客，翠羽帔，紫绮裘。素娥无赖，西去曾不为人留。今夜清尊对客，明夜孤帆水驿，依旧照离忧。但恐同王粲，相对永登楼。'),
	(5085, 4, '三台·清明应制', '见梨花初带夜月，海棠半含朝雨。内苑春、不禁过青门，御沟涨、潜通南浦。东风静、细柳垂金缕。望凤阙、非烟非雾。好时代、朝野多欢，遍九陌、太平箫鼓。 乍莺儿百啭断续，燕子飞来飞去。近绿水、台榭映秋千，斗草聚、双双游女。饧香更、酒冷踏青路。会暗识、夭桃朱户。向晚骤、宝马雕鞍，醉襟惹、乱花飞絮。 正轻寒轻暖漏永，半阴半晴云暮。禁火天、已是试新妆，岁华到、三分佳处。清明看、汉宫传蜡炬。散翠烟、飞入槐府。敛兵卫、阊阖门开，住传宣、又还休务。'),
	(5086, 494, '念奴娇·中秋', '凭高眺远，见长空万里，云无留迹。桂魄飞来，光射处，冷浸一天秋碧。玉宇琼楼，乘鸾来去，人在清凉国。江山如画，望中烟树历历。 我醉拍手狂歌，举杯邀月，对影成三客。起舞徘徊风露下，今夕不知何夕？便欲乘风，翻然归去，何用骑鹏翼。水晶宫里，一声吹断横笛。'),
	(5087, 305, '蝶恋花·暖雨晴风初破冻', '暖雨晴风初破冻，柳眼梅腮，已觉春心动。酒意诗情谁与共？泪融残粉花钿重。 乍试夹衫金缕缝，山枕斜欹，枕损钗头凤。独抱浓愁无好梦，夜阑犹剪灯花弄。'),
	(5088, 251, '浣溪沙·一向年光有限身', '一向年光有限身，等闲离别易销魂，酒筵歌席莫辞频。 满目山河空念远，落花风雨更伤春，不如怜取眼前人。'),
	(5089, 69, '鹧鸪天·雪照山城玉指寒', '雪照山城玉指寒，一声羌管怨楼间。江南几度梅花发，人在天涯鬓已斑。 星点点，月团团。倒流河汉入杯盘。翰林风月三千首，寄与吴姬忍泪看。'),
	(5090, 494, '水调歌头·黄州快哉亭赠张偓佺', '落日绣帘卷，亭下水连空。知君为我新作，窗户湿青红。长记平山堂上，欹枕江南烟雨，杳杳没孤鸿。认得醉翁语，山色有无中。 一千顷，都镜净，倒碧峰。忽然浪起，掀舞一叶白头翁。堪笑兰台公子，未解庄生天籁，刚道有雌雄。一点浩然气，千里快哉风。'),
	(5091, 188, '念奴娇·过洞庭', '洞庭青草，近中秋，更无一点风色。玉界琼田三万顷，着我扁舟一叶。素月分辉，明河共影，表里俱澄澈。悠然心会，妙处难与君说。(著 同：着；玉界 一作：玉鉴) 应念岭表经年，孤光自照，肝胆皆冰雪。短发萧骚襟袖冷，稳泛沧浪空阔。尽挹西江，细斟北斗，万象为宾客。扣舷独啸，不知今夕何夕。(沧浪 一作：沧冥；岭表 一作：岭海)'),
	(5092, 305, '诉衷情·夜来沉醉卸妆迟', '夜来沉醉卸妆迟，梅萼插残枝。酒醒熏破春睡，梦远不成归。 人悄悄，月依依，翠帘垂。更挼残蕊，更捻余香，更得些时。'),
	(5093, 494, '南乡子·和杨元素时移守密州', '东武望余杭，云海天涯两渺茫。何日功成名遂了，还乡，醉笑陪公三万场。 不用诉离觞，痛饮从来别有肠。今夜送归灯火冷，河塘，堕泪羊公却姓杨。'),
	(5094, 691, '湘春夜月·近清明', '近清明。翠禽枝上消魂。可惜一片清歌，都付与黄昏。欲共柳花低诉，怕柳花轻薄，不解伤春。念楚乡旅宿，柔情别绪，谁与温存。 空樽夜泣，青山不语，残月当门。翠玉楼前，惟是有、一波湘水，摇荡湘云。天长梦短，问甚时、重见桃根。这次第，算人间没个并刀，剪断心上愁痕。'),
	(5095, 591, '西江月·遣兴', '醉里且贪欢笑，要愁那得工夫。 近来始觉古人书。信著全无是处。(著 通：着) 昨夜松边醉倒，问松我醉何如。 只疑松动要来扶。以手推松曰去。'),
	(5096, 251, '玉楼春·春恨', '绿杨芳草长亭路，年少抛人容易去。楼头残梦五更钟，花底离愁三月雨。 无情不似多情苦，一寸还成千万缕。天涯地角有穷时，只有相思无尽处。'),
	(5097, 250, '少年游·离多最是', '离多最是，东西流水，终解两相逢。 浅情终似，行云无定，犹到梦魂中。 可怜人意，薄于云水，佳会更难重。 细想从来，断肠多处，不与今番同。'),
	(5098, 591, '太常引·建康中秋夜为吕叔潜赋 / 太常引·一轮秋影转金波', '一轮秋影转金波，飞镜又重磨。把酒问姮娥：被白发、欺人奈何？ 乘风好去，长空万里，直下看山河。斫去桂婆娑，人道是、清光更多。'),
	(5099, 352, '踏莎行·候馆梅残', '候馆梅残，溪桥柳细。草薰风暖摇征辔。离愁渐远渐无穷，迢迢不断如春水。 寸寸柔肠，盈盈粉泪。楼高莫近危阑倚。平芜尽处是春山，行人更在春山外。'),
	(5100, 591, '破阵子·掷地刘郎玉斗', '为范南伯寿。时南伯为张南轩辟宰泸溪，南伯迟迟未行。因作此词以勉之。 掷地刘郎玉斗，挂帆西子扁舟。千古风流今在此，万里功名莫放休。君王三百州。 燕雀岂知鸿鹄，貂蝉元出兜鍪。却笑泸溪如斗大，肯把牛刀试手不？寿君双玉瓯。'),
	(5101, 186, '千秋岁·数声鶗鴂', '数声鶗鴂，又报芳菲歇。惜春更把残红折。雨轻风色暴，梅子青时节。永丰柳，无人尽日花飞雪。 莫把幺弦拨，怨极弦能说。天不老，情难绝。心似双丝网，中有千千结。夜过也，东窗未白凝残月。(凝残月 一作：孤灯灭)'),
	(5102, 409, '桂枝香·金陵怀古', '登临送目，正故国晚秋，天气初肃。千里澄江似练，翠峰如簇。归帆去棹残阳里，背西风，酒旗斜矗。彩舟云淡，星河鹭起，画图难足。(归帆 一作：征帆) 念往昔，繁华竞逐，叹门外楼头，悲恨相续。千古凭高对此，谩嗟荣辱。六朝旧事随流水，但寒烟衰草凝绿。至今商女，时时犹唱，后庭遗曲。'),
	(5103, 117, '海棠春·已未清明对海棠有赋', '海棠亭午沾疏雨。便一饷、胭脂尽吐。老去惜花心，相对花无语。 羽书万里飞来处。报扫荡、狐嗥兔舞。濯锦古江头，飞景还如许。'),
	(5104, 494, '浣溪沙·细雨斜风作晓寒', '元丰七年十二月二十四日，从泗州刘倩叔游南山 细雨斜风作晓寒，淡烟疏柳媚晴滩。入淮清洛渐漫漫。 雪沫乳花浮午盏，蓼茸蒿笋试春盘。人间有味是清欢。'),
	(5105, 12, '卜算子·不是爱风尘', '不是爱风尘，似被前缘误。花落花开自有时，总赖东君主。 去也终须去，住也如何住！若得山花插满头，莫问奴归处。'),
	(5106, 251, '清平乐·红笺小字', '红笺小字。说尽平生意。鸿雁在云鱼在水。惆怅此情难寄。 斜阳独倚西楼。遥山恰对帘钩。人面不知何处，绿波依旧东流。'),
	(5107, 305, '点绛唇·闺思', '寂寞深闺，柔肠一寸愁千缕。惜春春去。几点催花雨。 倚遍阑干，只是无情绪。人何处。连天衰草，望断归来路。(衰草 一作：芳)'),
	(5108, 13, '卜算子·答施', '相思似海深，旧事如天远。泪滴千千万万行，更使人、愁肠断。 要见无因见，拚了终难拚。若是前生未有缘，待重结、来生愿。'),
	(5109, 639, '水调歌头·送章德茂大卿使虏', '不见南师久，漫说北群空。当场只手，毕竟还我万夫雄。自笑堂堂汉使，得似洋洋河水，依旧只流东？且复穹庐拜，会向藁街逢！ 尧之都，舜之壤，禹之封。于中应有，一个半个耻臣戎！万里腥膻如许，千古英灵安在，磅礴几时通？胡运何须问，赫日自当中！'),
	(5110, 305, '临江仙·梅', '庭院深深深几许，云窗雾阁春迟。为谁憔悴损芳姿。夜来清梦好，应是发南枝。 玉瘦檀轻无限恨，南楼羌管休吹。浓香吹尽有谁知。暖风迟日也，别到杏花肥。'),
	(5111, 305, '浣溪沙·淡荡春光寒食天', '淡荡春光寒食天，玉炉沉水袅残烟。梦回山枕隐花钿。 海燕未来人斗草，江梅已过柳生绵。黄昏疏雨湿秋千。'),
	(5112, 202, '风入松', '裳古曲。仇仁近一诗精妙详尽，余词不能工也。 危楼古镜影犹寒。倒景忽相看。桃花不识东西晋，想如今、也梦邯郸。缥缈神仙海上，飘零图画人间。 宝光丹气共回环。水弱小舟闲。秋风难老三珠树，尚依依、脆管清弹。说与霓裳莫舞，银桥不到深山。'),
	(5113, 494, '西江月·顷在黄州', '顷在黄州，春夜行蕲水中，过酒家饮，酒醉，乘月至一溪桥上，解鞍，由肱醉卧少休。及觉已晓，乱山攒拥，流水锵然，疑非尘世也。书此语桥柱上。(由肱 一作：曲肱) 照野弥弥浅浪，横空隐隐层霄。障泥未解玉骢骄，我欲醉眠芳草。 可惜一溪风月，莫教踏碎琼瑶。解鞍欹枕绿杨桥，杜宇一声春晓。'),
	(5114, 468, '水调歌头·中秋', '砧声送风急，蟠蟀思高秋。我来对景，不学宋玉解悲愁。收拾凄凉兴况，分付尊中醽醁，倍觉不胜幽。自有多情处，明月挂南楼。 怅襟怀，横玉笛，韵悠悠。清时良夜，借我此地倒金瓯。可爱一天风物，遍倚阑干十二，宇宙若萍浮。醉困不知醒，欹枕卧江流。'),
	(5115, 186, '天仙子·水调数声持酒听', '水调数声持酒听，午醉醒来愁未醒。送春春去几时回？临晚镜，伤流景，往事后期空记省。 沙上并禽池上瞑，云破月来花弄影。重重帘幕密遮灯，风不定，人初静，明日落红应满径。'),
	(5116, 305, '永遇乐·落日熔金', '落日熔金，暮云合璧，人在何处。染柳烟浓，吹梅笛怨，春意知几许。元宵佳节，融和天气，次第岂无风雨。来相召、香车宝马，谢他酒朋诗侣。(熔金 一作：镕金) 中州盛日，闺门多暇，记得偏重三五。铺翠冠儿，捻金雪柳，簇带争济楚。如今憔悴，风鬟霜鬓，怕见夜间出去。不如向、帘儿底下，听人笑语。'),
	(5117, 635, '临江仙·夜登小阁忆洛中旧游', '忆昔午桥桥上饮，坐中多是豪英。长沟流月去无声。杏花疏影里，吹笛到天明。 二十余年如一梦，此身虽在堪惊。闲登小阁看新晴。古今多少事，渔唱起三更。'),
	(5118, 451, '满庭芳·山抹微云', '山抹微云，天连衰草，画角声断谯门。暂停征棹，聊共引离尊。多少蓬莱旧事，空回首、烟霭纷纷。斜阳外，寒鸦万点，流水绕孤村。(万点 一作：数点) 销魂。当此际，香囊暗解，罗带轻分。谩赢得、青楼薄幸名存。此去何时见也，襟袖上、空惹啼痕。伤情处，高城望断，灯火已黄昏。'),
	(5119, 494, '定风波·两两轻红半晕腮', '十月九日，孟亨之置酒秋香亭。有双拒霜，独向君猷而开，坐客喜笑，以为非使君莫可当此花，故作是篇。 两两轻红半晕腮，依依独为使君回。若道使君无此意，何为，双花不向别人开。 但看低昂烟雨里，不已。劝君休诉十分杯。更问尊前狂副使。来岁。花开时节与谁来。'),
	(5120, 305, '行香子·天与秋光', '天与秋光，转转情伤，探金英知近重阳。薄衣初试，绿蚁新尝，渐一番风，一番雨，一番凉。 黄昏院落，凄凄惶惶，酒醒时往事愁肠。那堪永夜，明月空床。闻砧声捣，蛩声细，漏声长。'),
	(5121, 494, '浣溪沙', '伽花彩甚奇，谓有初春之兴。因作二首，寄袁公济 雪颔霜髯不自惊。更将翦彩发春荣。羞颜未醉已先赪。 莫唱黄鸡并白发，且呼张丈唤殷兄。有人归去欲卿卿。'),
	(5122, 494, '水调歌头·昵昵儿女语', '欧阳文忠公尝问余：“琴诗何者最善？答以退之听颖师琴诗最善。公曰：此诗最奇丽，然非听琴，乃听琵琶也。余深然之。建安章质夫家善琵琶者，乞为歌词。余久不作，特取退之词，稍加隐括，使就声律，以遗之云。 昵昵儿女语，灯火夜微明。恩怨尔汝来去，弹指泪和声。忽变轩昂勇士，一鼓填然作气，千里不留行。回首暮云远，飞絮搅青冥。 众禽里，真彩凤，独不鸣。跻攀寸步千险，一落百寻轻。烦子指间风雨，置我肠中冰炭，起坐不能平。推手从归去，无泪与君倾。'),
	(5123, 591, '鹧鸪天·有客慨然谈功名因追念少年时事戏作', '壮岁旌旗拥万夫，锦襜突骑渡江初。燕兵夜娖银胡觮，汉箭朝飞金仆姑。 追往事，叹今吾，春风不染白髭须。却将万字平戎策，换得东家种树书。'),
	(5124, 639, '水龙吟·春恨', '闹花深处层楼，画帘半卷东风软。春归翠陌，平莎茸嫩，垂杨金浅。迟日催花，淡云阁雨，轻寒轻暖。恨芳菲世界，游人未赏，都付与、莺和燕。 寂寞凭高念远。向南楼、一声归雁。金钗斗草，青丝勒马，风流云散。罗绶分香，翠绡对泪，几多幽怨。正销魂，又是疏烟淡月，子规声断。'),
	(5125, 591, '贺新郎·甚矣吾衰矣', '邑中园亭，仆皆为赋此词。一日，独坐停云，水声山色，竞来相娱。意溪山欲援例者，遂作数语，庶几仿佛渊明思亲友之意云。 甚矣吾衰矣。怅平生、交游零落，只今馀几！白发空垂三千丈，一笑人间万事。问何物、能令公喜？我见青山多妩媚，料青山见我应如是。情与貌，略相似。 一尊搔首东窗里。想渊明《停云》诗就，此时风味。江左沉酣求名者，岂识浊醪妙理。回首叫、云飞风起。不恨古人吾不见，恨古人不见吾狂耳。知我者，二三子。'),
	(5126, 344, '玉蝴蝶·望处雨收云断', '望处雨收云断，凭阑悄悄，目送秋光。晚景萧疏，堪动宋玉悲凉。水风轻，蘋花渐老，月露冷、梧叶飘黄。遣情伤。故人何在，烟水茫茫。 难忘，文期酒会，几孤风月，屡变星霜。海阔山遥，未知何处是潇湘。念双燕、难凭远信，指暮天、空识归航。黯相望。断鸿声里，立尽斜阳。'),
	(5127, 130, '兰陵王·柳', '柳阴直，烟里丝丝弄碧。隋堤上、曾见几番，拂水飘绵送行色。登临望故国，谁识京华倦客？长亭路，年去岁来，应折柔条过千尺。 闲寻旧踪迹，又酒趁哀弦，灯照离席。梨花榆火催寒食。愁一箭风快，半篙波暖，回头迢递便数驿，望人在天北。 凄恻，恨堆积！渐别浦萦回，津堠岑寂，斜阳冉冉春无极。念月榭携手，露桥闻笛。沉思前事，似梦里，泪暗滴。'),
	(5128, 642, '浣溪沙', '浅画香膏拂紫绵。牡丹花重翠云偏。手挼梅子并郎肩。 病起心情终是怯，困来模样不禁怜。旋移针线小窗前。'),
	(5129, 451, '江城子·西城杨柳弄春柔', '西城杨柳弄春柔，动离忧，泪难收。犹记多情、曾为系归舟。碧野朱桥当日事，人不见，水空流。 韶华不为少年留，恨悠悠，几时休？飞絮落花时候、一登楼。便作春江都是泪，流不尽，许多愁。'),
	(5130, 591, '清平乐·独宿博山王氏庵', '绕床饥鼠，蝙蝠翻灯舞。屋上松风吹急雨，破纸窗间自语。 平生塞北江南，归来华发苍颜。布被秋宵梦觉，眼前万里江山。'),
	(5131, 494, '卜算子·黄州定慧院寓居作', '缺月挂疏桐，漏断人初静。时见幽人独往来，缥缈孤鸿影。 惊起却回头，有恨无人省。拣尽寒枝不肯栖，寂寞沙洲冷。'),
	(5132, 305, '行香子·七夕', '草际鸣蛩。惊落梧桐。正人间、天上愁浓。云阶月地，关锁千重。纵浮槎来，浮槎去，不相逢。 星桥鹊驾，经年才见，想离情、别恨难穷。牵牛织女，莫是离中。甚霎儿晴，霎儿雨，霎儿风。'),
	(5133, 352, '玉楼春·别后不知君远近', '别后不知君远近。触目凄凉多少闷。渐行渐远渐无书，水阔鱼沉何处问。 夜深风竹敲秋韵。万叶千声皆是恨。故欹单枕梦中寻，梦又不成灯又烬。'),
	(5134, 112, '满江红·翠幕深庭', '翠幕深庭，露红晚、闲花自发。春不断、亭台成趣，翠阴蒙密。紫燕雏飞帘额静，金鳞影转池心阔。有花香、竹色赋闲情，供吟笔。 闲问字，评风月。时载酒，调冰雪。似初秋入夜，浅凉欺葛。人境不教车马近，醉乡莫放笙歌歇。倩双成、一曲紫云回，红莲折。'),
	(5135, 352, '长相思·花似伊', '花似伊，柳似伊。花柳青春人别离。低头双泪垂。 长江东，长江西。两岸鸳鸯两处飞。相逢知几时。'),
	(5136, 305, '渔家傲·雪里已知春信至', '雪里已知春信至，寒梅点缀琼枝腻。香脸半开娇旖旎，当庭际，玉人浴出新妆洗。 造化可能偏有意，故教明月玲珑地。共赏金尊沈绿蚁，莫辞醉，此花不与群花比。'),
	(5137, 496, '御街行·秋日怀旧', '纷纷坠叶飘香砌。夜寂静，寒声碎。真珠帘卷玉楼空，天淡银河垂地。年年今夜，月华如练，长是人千里。 愁肠已断无由醉，酒未到，先成泪。残灯明灭枕头欹，谙尽孤眠滋味。都来此事，眉间心上，无计相回避。'),
	(5138, 451, '如梦令·春景', '莺嘴啄花红溜，燕尾点波绿皱。 指冷玉笙寒，吹彻小梅春透。 依旧，依旧，人与绿杨俱瘦。'),
	(5139, 494, '浣溪沙·端午', '轻汗微微透碧纨，明朝端午浴芳兰。流香涨腻满晴川。 彩线轻缠红玉臂，小符斜挂绿云鬟。佳人相见一千年。'),
	(5140, 305, '凤凰台上忆吹箫·香冷金猊', '香冷金猊，被翻红浪，起来慵自梳头。任宝奁尘满，日上帘钩。生怕离怀别苦，多少事、欲说还休。新来瘦，非干病酒，不是悲秋。 休休，这回去也，千万遍《阳关》，也则难留。念武陵人远，烟锁秦楼。惟有楼前流水，应念我、终日凝眸。凝眸处，从今又添，一段新愁。(版本一) 香冷金猊，被翻红浪，起来人未梳头。任宝奁闲掩，日上帘钩。生怕闲愁暗恨，多少事、欲说还休。今年瘦，非干病酒，不是悲秋。 明朝，者回去也，千万遍阳关，也即难留。念武陵春晚，云锁重楼。记取楼前绿水，应念我、终日凝眸。凝眸处，从今更数，几段新愁。(版本二)'),
	(5141, 352, '采桑子·荷花开后西湖好', '荷花开后西湖好，载酒来时。不用旌旗，前后红幢绿盖随。 画船撑入花深处，香泛金卮。烟雨微微，一片笙歌醉里归。'),
	(5142, 451, '江城子', '清明天气醉游郎。莺儿狂。燕儿狂。翠盖红缨，道上往来忙。记得相逢垂柳下，雕玉珮，缕金裳。 春光还是旧春光。桃花香。李花香。浅白深红，一一斗新妆。惆怅惜花人不见，歌一阕，泪千行。'),
	(5143, 515, '梅花引·荆溪阻雪', '白鸥问我泊孤舟，是身留，是心留？心若留时，何事锁眉头？风拍小帘灯晕舞，对闲影，冷清清，忆旧游。 旧游旧游今在否？花外楼，柳下舟。梦也梦也，梦不到，寒水空流。漠漠黄云，湿透木棉裘。都道无人愁似我，今夜雪，有梅花，似我愁。'),
	(5144, 305, '念奴娇·春情', '萧条庭院，又斜风细雨，重门须闭。宠柳娇花寒食近，种种恼人天气。险韵诗成，扶头酒醒，别是闲滋味。征鸿过尽，万千心事难寄。 楼上几日春寒，帘垂四面，玉阑干慵倚。被冷香消新梦觉，不许愁人不起。清露晨流，新桐初引，多少游春意。日高烟敛，更看今日晴未。'),
	(5145, 591, '贺新郎·同父见和再用韵答之', '老大那堪说。似而今、元龙臭味，孟公瓜葛。我病君来高歌饮，惊散楼头飞雪。笑富贵千钧如发。硬语盘空谁来听？记当时、只有西窗月。重进酒，换鸣瑟。 事无两样人心别。问渠侬：神州毕竟，几番离合？汗血盐车无人顾，千里空收骏骨。正目断关河路绝。我最怜君中宵舞，道“男儿到死心如铁”。看试手，补天裂。'),
	(5146, 104, '采桑子·恨君不似江楼月', '恨君不似江楼月，南北东西，南北东西，只有相随无别离。 恨君却似江楼月，暂满还亏，暂满还亏，待得团圆是几时？'),
	(5147, 382, '卜算子', '芍药打团红，萱草成窝绿。帘卷疏风燕子归，依旧卢仝屋。 贫放麹生疏，闲到青奴熟。扫地焚香伴老仙，人胜连环玉。'),
	(5148, 188, '西江月·阻风山峰下', '满载一船秋色，平铺十里湖光。波神留我看斜阳，放起鳞鳞细浪。 明日风回更好，今宵露宿何妨？水晶宫里奏霓裳，准拟岳阳楼上。'),
	(5149, 549, '六州歌头·少年侠气', '少年侠气，交结五都雄。肝胆洞。毛发耸。立谈中。死生同。一诺千金重。推翘勇。矜豪纵。轻盖拥。联飞鞚。斗城东。轰饮酒垆，春色浮寒瓮。吸海垂虹。闲呼鹰嗾犬，白羽摘雕弓。狡穴俄空。乐匆匆。(闲呼 一作：间呼) 似黄粱梦。辞丹凤。明月共。漾孤篷。官冗從。怀倥偬。落尘笼。簿书丛。鹖弁如云众。供粗用。忽奇功。笳鼓动。渔阳弄。思悲翁。不请长缨，系取天骄种。剑吼西风。恨登山临水，手寄七弦桐。目送归鸿。'),
	(5150, 188, '水调歌头·金山观月', '江山自雄丽，风露与高寒。寄声月姊，借我玉鉴此中看。幽壑鱼龙悲啸，倒影星辰摇动，海气夜漫漫。涌起白银阙，危驻紫金山。 表独立，飞霞佩，切云冠。漱冰濯雪，眇视万里一毫端。回首三山何处，闻道群仙笑我，要我欲俱还。挥手从此去，翳凤更骖鸾。'),
	(5151, 247, '汉宫春·梅', '潇洒江梅，向竹梢疏处，横两三枝。东君也不爱惜，雪压霜欺。无情燕子，怕春寒、轻失花期。却是有，年年塞雁，归来曾见开时。 清浅小溪如练，问玉堂何似，茅舍疏篱。伤心故人去后，冷落新诗。微云淡月，对江天、分付他谁。空自忆，清香未减，风流不在人知。'),
	(5152, 72, '唐多令·芦叶满汀洲', '安远楼小集，侑觞歌板之姬黄其姓者，乞词于龙洲道人，为赋此《唐多令》。同柳阜之、刘去非、石民瞻、周嘉仲、陈孟参、孟容。时八月五日也。 芦叶满汀洲，寒沙带浅流。二十年重过南楼。柳下系船犹未稳，能几日，又中秋。 黄鹤断矶头，故人今在否？旧江山浑是新愁。欲买桂花同载酒，终不似，少年游。(在否 一作：在不)'),
	(5153, 267, '西江月·世事短如春梦', '世事短如春梦，人情薄似秋云。不须计较苦劳心。万事原来有命。 幸遇三杯酒好，况逢一朵花新。片时欢笑且相亲。明日阴晴未定。'),
	(5154, 591, '贺新郎·别茂嘉十二弟', '别茂嘉十二弟。鹈鴂、杜鹃实两种，见《离骚补注》 绿树听鹈鴂。更那堪、鹧鸪声住，杜鹃声切。啼到春归无寻处，苦恨芳菲都歇。算未抵、人间离别。马上琵琶关塞黑，更长门、翠辇辞金阙。看燕燕，送归妾。 将军百战身名裂。向河梁、回头万里，故人长绝。易水萧萧西风冷，满座衣冠似雪。正壮士、悲歌未彻。啼鸟还知如许恨，料不啼清泪长啼血。谁共我，醉明月。'),
	(5155, 267, '鹧鸪天·西都作', '我是清都山水郎，天教分付与疏狂。曾批给雨支风券，累上留云借月章。 诗万首，酒千觞。几曾着眼看侯王？玉楼金阙慵归去，且插梅花醉洛阳。'),
	(5156, 494, '水龙吟·闾丘大夫孝终公显尝守黄州', '闾丘大夫孝终公显尝守黄州，作栖霞楼，为郡中胜绝。元丰五年，余谪居黄。正月十七日，梦扁舟渡江，中流回望，楼中歌乐杂作。舟中人言：公显方会客也。觉而异之，乃作此词。公显时已致仕在苏州。 小舟横截春江，卧看翠壁红楼起。云间笑语，使君高会，佳人半醉。危柱哀弦，艳歌余响，绕云萦水。念故人老大，风流未减，独回首、烟波里。 推枕惘然不见，但空江、月明千里。五湖闻道，扁舟归去，仍携西子。云梦南州，武昌东岸，昔游应记。料多情梦里，端来见我，也参差是。'),
	(5157, 591, '鹧鸪天·陌上柔桑破嫩芽', '陌上柔桑破嫩芽，东邻蚕种已生些。平冈细草鸣黄犊，斜日寒林点暮鸦。 山远近，路横斜，青旗沽酒有人家。城中桃李愁风雨，春在溪头荠菜花。'),
	(5158, 344, '少年游·长安古道马迟迟', '长安古道马迟迟，高柳乱蝉嘶。夕阳岛外，秋风原上，目断四天垂。(岛 一作：鸟) 归云一去无踪迹，何处是前期？狎兴生疏，酒徒萧索，不似少年时。'),
	(5159, 494, '如梦令', '唐庄宗制，名忆仙姿，嫌其名不雅，故改为如梦令。盖庄宗作此词，卒章云：“如梦如梦，和泪出门相送。”因取以为名云 水垢何曾相受。细看两俱无有。寄语揩背人，尽日劳君挥肘。轻手，轻手，居士本来无垢。'),
	(5160, 251, '采桑子·时光只解催人老', '时光只解催人老，不信多情，长恨离亭，泪滴春衫酒易醒。 梧桐昨夜西风急，淡月胧明，好梦频惊，何处高楼雁一声？'),
	(5161, 271, '蝶恋花·送春', '楼外垂杨千万缕。欲系青春，少住春还去。犹自风前飘柳絮。随春且看归何处。 绿满山川闻杜宇。便做无情，莫也愁人苦。把酒送春春不语。黄昏却下潇潇雨。'),
	(5162, 693, '水调歌头·游览', '瑶草一何碧，春入武陵溪。溪上桃花无数，枝上有黄鹂。我欲穿花寻路，直入白云深处，浩气展虹霓。只恐花深里，红露湿人衣。 坐玉石，倚玉枕，拂金徽。谪仙何处？无人伴我白螺杯。我为灵芝仙草，不为朱唇丹脸，长啸亦何为？醉舞下山去，明月逐人归。'),
	(5163, 251, '浣溪沙·玉碗冰寒滴露华', '玉碗冰寒滴露华，粉融香雪透轻纱。晚来妆面胜荷花。 鬓亸欲迎眉际月，酒红初上脸边霞。一场春梦日西斜。'),
	(5164, 498, '水调歌头·细数十年事', '细数十年事，十处过中秋。今年新梦，忽到黄鹤旧山头。老子个中不浅，此会天教重见，今古一南楼。星汉淡无色，玉镜独空浮。 敛秦烟，收楚雾，熨江流。关河离合，南北依旧照清愁。想见姮娥冷眼，应笑归来霜鬓，空敝黑貂裘。酾酒问蟾兔，肯去伴沧洲？'),
	(5165, 633, '蝶恋花·禹庙兰亭今古路', '禹庙兰亭今古路。一夜清霜，染尽湖边树。鹦鹉杯深君莫诉。他时相遇知何处。 冉冉年华留不住。镜里朱颜，毕竟消磨去。一句丁宁君记取。神仙须是闲人做。'),
	(5166, 250, '清平乐·留人不住', '留人不住，醉解兰舟去。一棹碧涛春水路，过尽晓莺啼处。 渡头杨柳青青，枝枝叶叶离情。此后锦书休寄，画楼云雨无凭。'),
	(5167, 342, '相思令·吴山青', '吴山青，越山青，两岸青山相送迎，争忍有离情？ 君泪盈，妾泪盈，罗带同心结未成，江边潮已平。'),
	(5168, 494, '永遇乐·彭城夜宿燕子楼', '彭城夜宿燕子楼，梦盼盼，因作此词。 明月如霜，好风如水，清景无限。曲港跳鱼，圆荷泻露，寂寞无人见。紞如三鼓，铿然一叶，黯黯梦云惊断。夜茫茫，重寻无处，觉来小园行遍。 天涯倦客，山中归路，望断故园心眼。燕子楼空，佳人何在，空锁楼中燕。古今如梦，何曾梦觉，但有旧欢新怨。异时对，黄楼夜景，为余浩叹。'),
	(5169, 494, '西江月·梅花', '玉骨那愁瘴雾，冰姿自有仙风。海仙时遣探芳丛。倒挂绿毛么凤。(么 同：幺) 素面翻嫌粉涴，洗妆不褪唇红。高情已逐晓云空。不与梨花同梦。'),
	(5170, 409, '浪淘沙令·伊吕两衰翁', '伊吕两衰翁，历遍穷通。一为钓叟一耕佣。若使当时身不遇，老了英雄。 汤武偶相逢，风虎云龙。兴王只在谈笑中。直至如今千载后，谁与争功！'),
	(5171, 456, '忆秦娥', '情脉脉。半黄橙子和香擘。和香擘。分明记得，袖香熏窄。 别来人远关山隔。见梅不忍和花摘。和花摘。有书无雁，寄谁归得。'),
	(5172, 494, '鹧鸪天·林断山明竹隐墙', '林断山明竹隐墙。乱蝉衰草小池塘。翻空白鸟时时见，照水红蕖细细香。 村舍外，古城旁。杖藜徐步转斜阳。殷勤昨夜三更雨，又得浮生一日凉。'),
	(5173, 112, '渡江云三犯·西湖清明', '羞红颦浅恨，晚风未落，片绣点重茵。旧堤分燕尾，桂棹轻鸥，宝勒倚残云。千丝怨碧，渐路入、仙坞迷津。肠漫回，隔花时见，背面楚腰身。 逡巡。题门惆怅，堕履牵萦，数幽期难准。还始觉、留情缘眼，宽带因春。明朝事与孤烟冷，做满湖、风雨愁人。山黛暝，尘波澹绿无痕。'),
	(5174, 352, '浣溪沙·堤上游人逐画船', '堤上游人逐画船，拍堤春水四垂天。绿杨楼外出秋千。 白发戴花君莫笑，六幺催拍盏频传。人生何处似尊前！'),
	(5175, 680, '少年游·草', '春风吹碧，春云映绿，晓梦入芳裀。软衬飞花，远连流水，一望隔香尘。 萋萋多少江南恨，翻忆翠罗裙。冷落闲门，凄迷古道，烟雨正愁人。'),
	(5176, 688, '南乡子·冬夜', '万籁寂无声。衾铁稜稜近五更。香断灯昏吟未稳，凄清。只有霜华伴月明。 应是夜寒凝。恼得梅花睡不成。我念梅花花念我，关情。起看清冰满玉瓶。'),
	(5177, 130, '少年游·并刀如水', '并刀如水，吴盐胜雪，纤手破新橙。锦幄初温，兽烟不断，相对坐调笙。 低声问向谁行宿，城上已三更。马滑霜浓，不如休去，直是少人行。'),
	(5178, 409, '雨霖铃', '孜孜矻矻。向无明里、强作窠窟。浮名浮利何济，堪留恋处，轮回仓猝。幸有明空妙觉，可弹指超出。缘底事、抛了全潮，认一浮沤作瀛渤。本源自性天真佛。只些些、妄想中埋没。贪他眼花阳艳，谁信道、本来无物。一旦茫然，终被阎罗老子相屈。便纵有、千种机筹，怎免伊唐突。'),
	(5179, 97, '西江月·宝髻松松挽就', '宝髻松松挽就，铅华淡淡妆成。青烟翠雾罩轻盈，飞絮游丝无定。 相见争如不见，有情何似无情。笙歌散后酒初醒，深院月斜人静。'),
	(5180, 494, '浣溪沙·缥缈红妆照浅溪', '缥缈红妆照浅溪。薄云疏雨不成泥。送君何处古台西。 废沼夜来秋水满，茂林深处晚莺啼。行人肠断草凄迷。'),
	(5181, 494, '阮郎归·初夏', '绿槐高柳咽新蝉。薰风初入弦。碧纱窗下水沈烟。棋声惊昼眠。(水沈 一作 水沉） 微雨过，小荷翻。榴花开欲然。玉盆纤手弄清泉。琼珠碎却圆。'),
	(5182, 587, '卜算子', '十载仰高明，一见心相许。来日孤舟西水门，风饱征帆腹。 后夜起相思，明月清江曲。若见秋风寒雁来，能寄音书否。'),
	(5183, 305, '南歌子·天上星河转', '天上星河转，人间帘幕垂。凉生枕簟泪痕滋。起解罗衣聊问、夜何其。 翠贴莲蓬小，金销藕叶稀。旧时天气旧时衣。只有情怀不似、旧家时。'),
	(5184, 494, '满庭芳·蜗角虚名', '蜗角虚名，蝇头微利，算来著甚干忙。事皆前定，谁弱又谁强。且趁闲身未老，尽放我、些子疏狂。百年里，浑教是醉，三万六千场。(尽放我 一作：须放我) 思量。能几许，忧愁风雨，一半相妨，又何须，抵死说短论长。幸对清风皓月，苔茵展、云幕高张。江南好，千钟美酒，一曲满庭芳。'),
	(5185, 494, '菩萨蛮·回文夏闺怨', '柳庭风静人眠昼，昼眠人静风庭柳。香汗薄衫凉，凉衫薄汗香。 手红冰碗藕，藕碗冰红手。郎笑藕丝长，长丝藕笑郎。'),
	(5186, 409, '渔家傲·平岸小桥千嶂抱', '平岸小桥千嶂抱。柔蓝一水萦花草。茅屋数间窗窈窕。尘不到。时时自有春风扫。 午枕觉来闻语鸟。欹眠似听朝鸡早。忽忆故人今总老。贪梦好。茫然忘了邯郸道。'),
	(5187, 451, '好事近·梦中作', '春路雨添花，花动一山春色。行到小溪深处，有黄鹂千百。 飞云当面化龙蛇，夭矫转空碧。醉卧古藤阴下，了不知南北。'),
	(5188, 305, '鹧鸪天·寒日萧萧上琐窗', '寒日萧萧上琐窗，梧桐应恨夜来霜。酒阑更喜团茶苦，梦断偏宜瑞脑香。 秋已尽，日犹长，仲宣怀远更凄凉。不如随分尊前醉，莫负东篱菊蕊黄。'),
	(5189, 451, '八六子·倚危亭', '倚危亭。恨如芳草，萋萋刬尽还生。念柳外青骢别后，水边红袂分时，怆然暗惊。 无端天与娉婷。夜月一帘幽梦，春风十里柔情。怎奈向、欢娱渐随流水，素弦声断，翠绡香减，那堪片片飞花弄晚，蒙蒙残雨笼晴。正销凝。黄鹂又啼数声。'),
	(5190, 494, '蝶恋花·昨夜秋风来万里', '昨夜秋风来万里。月上屏帏，冷透人衣袂。有客抱衾愁不寐。那堪玉漏长如岁。 羁舍留连归计未。梦断魂销，一枕相思泪。衣带渐宽无别意。新书报我添憔悴。'),
	(5191, 642, '菩萨蛮·绿芜墙绕青苔院', '绿芜墙绕青苔院，中庭日淡芭蕉卷。蝴蝶上阶飞，烘帘自在垂。 玉钩双语燕，宝甃杨花转。几处簸钱声，绿窗春睡轻。'),
	(5192, 494, '江城子', '前瞻马耳九仙山。碧连天。晚云间。城上高台，真个是超然。莫使匆匆云雨散，今夜里，月婵娟。 小溪鸥鹭静联拳。去翩翩。点轻烟。人事凄凉，回首便他年。莫忘使君歌笑处，垂柳下，矮槐前。'),
	(5193, 272, '水调歌头·隐括杜牧之齐山诗', '江水浸云影，鸿雁欲南飞。携壶结客何处？空翠渺烟霏。尘世难逢一笑，况有紫萸黄菊，堪插满头归。风景今朝是，身世昔人非。 酬佳节，须酩酊，莫相违。人生如寄，何事辛苦怨斜晖。无尽今来古往，多少春花秋月，那更有危机。与问牛山客，何必独沾衣。'),
	(5194, 251, '浣溪沙', '湖上西风急暮蝉。夜来清露湿红莲。少留归骑促歌筵。 为别莫辞金盏酒。入朝须近玉炉烟。不知重会是何年。'),
	(5195, 591, '念奴娇·梅', '疏疏淡淡，问阿谁、堪比天真颜色。笑杀东君虚占断，多少朱朱白白。雪里温柔，水边明秀，不借春工力。骨清香嫩，迥然天与奇绝。 尝记宝篽寒轻，琐窗人睡起，玉纤轻摘。漂泊天涯空瘦损，犹有当年标格。万里风烟，一溪霜月，未怕欺他得。不如归去，阆苑有个人忆。'),
	(5196, 251, '蝶恋花', '梨叶疏红蝉韵歇。银汉风高，玉管声凄切。枕簟乍凉铜漏咽。谁教社燕轻离别。 草际蛩吟珠露结。宿酒醒来，不记归时节。多少衷肠犹未说。朱帘一夜朦胧月。'),
	(5197, 456, '卜算子', '枕簟暑风消，帘幕秋风动。月到夜来愁处明，只照团衾凤。 去意杳无凭，别语愁难送。一纸鱼笺枕底香，且做新来梦。'),
	(5198, 549, '半死桐·重过阊门万事非', '重过阊门万事非。同来何事不同归。梧桐半死清霜后，头白鸳鸯失伴飞。 原上草，露初晞。旧栖新垅两依依。空床卧听南窗雨，谁复挑灯夜补衣。'),
	(5199, 451, '点绛唇·桃源', '醉漾轻舟，信流引到花深处。尘缘相误，无计花间住。 烟水茫茫，千里斜阳暮。山无数，乱红如雨。不记来时路。'),
	(5200, 344, '昼夜乐·洞房记得初相遇', '洞房记得初相遇。便只合、长相聚。何期小会幽欢，变作离情别绪。况值阑珊春色暮，对满目、乱花狂絮。直恐好风光，尽随伊归去。 一场寂寞凭谁诉。算前言、总轻负。早知恁地难拚，悔不当时留住。其奈风流端正外，更别有，系人心处。一日不思量，也攒眉千度。'),
	(5201, 305, '忆秦娥·咏桐', '临高阁，乱山平野烟光薄。烟光薄，栖鸦归后，暮天闻角。 断香残酒情怀恶，西风催衬梧桐落。梧桐落，又还秋色，又还寂寞。'),
	(5202, 352, '诉衷情·眉意', '清晨帘幕卷轻霜。呵手试梅妆。都缘自有离恨，故画作远山长。 思往事，惜流芳。易成伤。拟歌先敛，欲笑还颦，最断人肠。'),
	(5203, 355, '虞美人·玉楼缥缈孤烟际', '玉楼缥缈孤烟际。徒倚愁如醉。雁来人远暗消魂。帘卷一钩新月、怯黄昏。 那人音信全无个。幽恨谁凭破。扑花蝴蝶若知人。为我一场清梦、去相亲。'),
	(5204, 305, '减字木兰花·卖花担上', '卖花担上。买得一枝春欲放。泪染轻匀。犹带彤霞晓露痕。 怕郎猜道。奴面不如花面好。云鬓斜簪。徒要教郎比并看。'),
	(5205, 251, '清平乐·金风细细', '金风细细。叶叶梧桐坠。绿酒初尝人易醉。一枕小窗浓睡。 紫薇朱槿花残。斜阳却照阑干。双燕欲归时节，银屏昨夜微寒。'),
	(5206, 352, '采桑子·何人解赏西湖好', '何人解赏西湖好，佳景无时。飞盖相追。贪向花间醉玉卮。 谁知闲凭阑干处，芳草斜晖。水远烟微。一点沧洲白鹭飞。'),
	(5207, 518, '一剪梅·堆枕乌云堕翠翘', '堆枕乌云堕翠翘。午梦惊回，满眼春娇。嬛嬛一袅楚宫腰。那更春来，玉减香消。 柳下朱门傍小桥。几度红窗，误认鸣镳。断肠风月可怜宵。忍使恹恹，两处无聊。'),
	(5208, 494, '减字木兰花·莺初解语', '莺初解语，最是一年春好处。微雨如酥，草色遥看近却无。 休辞醉倒，花不看开人易老。莫待春回，颠倒红英间绿苔。'),
	(5209, 250, '蝶恋花·梦入江南烟水路', '梦入江南烟水路，行尽江南，不与离人遇。睡里消魂无说处，觉来惆怅消魂误。 欲尽此情书尺素，浮雁沉鱼，终了无凭据。却倚缓弦歌别绪，断肠移破秦筝柱。'),
	(5210, 494, '贺新郎·夏景', '乳燕飞华屋。悄无人、桐阴转午，晚凉新浴。手弄生绡白团扇，扇手一时似玉。渐困倚、孤眠清熟。帘外谁来推绣户，枉教人、梦断瑶台曲。又却是，风敲竹。 石榴半吐红巾蹙。待浮花、浪蕊都尽，伴君幽独。秾艳一枝细看取，芳心千重似束。又恐被、秋风惊绿。若待得君来向此，花前对酒不忍触。共粉泪，两簌簌。'),
	(5211, 494, '江神子·恨别', '天涯流落思无穷。既相逢。却匆匆。摧手佳人，和泪折残红。为问东风余几许，春纵在，与谁同。 隋堤三月水溶溶。背归鸿。去吴中。回首彭城，清泗与淮通。寄我相思千点泪，流不到，楚江东。(版本一) 天涯流落思无穷！既相逢，却匆匆。携手佳人，和泪折残红。为问东风余几许？春纵在，与谁同！ 隋堤三月水溶溶。背归鸿，去吴中。回首彭城，清泗与淮通。欲寄相思千点泪，流不到，楚江东。(版本二)'),
	(5212, 251, '破阵子·燕子欲归时节', '燕子欲归时节，高楼昨夜西风。求得人间成小会，试把金尊傍菊丛。歌长粉面红。 斜日更穿帘幕，微凉渐入梧桐。多少襟情言不尽，写向蛮笺曲调中。此情千万重。'),
	(5213, 305, '蝶恋花·上巳召亲族', '永夜恹恹欢意少。空梦长安，认取长安道。为报今年春色好，花光月影宜相照。 随意杯盘虽草草。酒美梅酸，恰称人怀抱。醉里插花花莫笑，可怜春似人将老。'),
	(5214, 451, '行香子·树绕村庄', '树绕村庄，水满陂塘。倚东风、豪兴徜徉。小园几许，收尽春光。有桃花红，李花白，菜花黄。 远远围墙，隐隐茅堂。飏青旗、流水桥旁。偶然乘兴、步过东冈。正莺儿啼，燕儿舞，蝶儿忙。'),
	(5215, 591, '生查子·重叶梅', '百花头上开，冰雪寒中见。霜月定相知，先识春风面。 主人情意深，不管江妃怨。折我最繁枝，还许冰壶荐。'),
	(5216, 130, '蝶恋花·早行', '月皎惊乌栖不定，更漏将残，辘轳牵金井。唤起两眸清炯炯。泪花落枕红绵冷。 执手霜风吹鬓影。去意徊徨，别语愁难听。楼上阑干横斗柄，露寒人远鸡相应。'),
	(5217, 117, '卜算子', '苕＿水能清，更有人如水。秋水横边簇远山，相对盈盈里。 溪上有鸳鸯，艇子频惊起。何似收归碧玉池。长在阑干底。'),
	(5218, 633, '鹊桥仙·华灯纵博', '华灯纵博，雕鞍驰射，谁记当年豪举。酒徒一半取封侯，独去作、江边渔父。 轻舟八尺，低篷三扇，占断苹洲烟雨。镜湖元自属闲人，又何必、君恩赐与。'),
	(5219, 494, '沁园春·孤馆灯青', '孤馆灯青，野店鸡号，旅枕梦残。渐月华收练，晨霜耿耿；云山摛锦，朝露漙漙。世路无穷，劳生有限，似此区区长鲜欢。微吟罢，凭征鞍无语，往事千端。 当时共客长安，似二陆初来俱少年。有笔头千字，胸中万卷；致君尧舜，此事何难？用舍由时，行藏在我，袖手何妨闲处看。身长健，但优游卒岁，且斗尊前。'),
	(5220, 456, '南歌子', '荷盖倾新绿，榴巾蹙旧红。水亭烟榭晚凉中。又是一钩新月、静方栊。 丝藕清如雪，橱纱薄似空。好维今夜与谁同。唤取玉人来共、一帘风。'),
	(5221, 344, '定风波·自春来', '自春来、惨绿愁红，芳心是事可可。日上花梢，莺穿柳带，犹压香衾卧。暖酥消、腻云亸，终日厌厌倦梳裹。无那。恨薄情一去，音书无个。 早知恁么，悔当初、不把雕鞍锁。向鸡窗，只与蛮笺象管，拘束教吟课。镇相随、莫抛躲，针线闲拈伴伊坐。和我，免使年少，光阴虚过。'),
	(5222, 149, '点绛唇·丁未冬过吴松作', '燕雁无心，太湖西畔随云去。数峰清苦。商略黄昏雨。 第四桥边，拟共天随住。今何许。凭阑怀古。残柳参差舞。'),
	(5223, 250, '虞美人·小梅枝上东君信', '小梅枝上东君信。雪后花期近。南枝开尽北枝开。长被陇头游子、寄春来。 年年衣袖年年泪。总为今朝意。问谁同是忆花人。赚得小鸿眉黛、也低颦。'),
	(5224, 591, '木兰花慢·可怜今夕月', '中秋饮酒将旦，客谓前人诗词有赋待月无送月者，因用《天问》体赋。 可怜今夕月，向何处，去悠悠？是别有人间，那边才见，光影东头？是天外。空汗漫，但长风浩浩送中秋？飞镜无根谁系？姮娥不嫁谁留？ 谓经海底问无由，恍惚使人愁。怕万里长鲸，纵横触破，玉殿琼楼。虾蟆故堪浴水，问云何玉兔解沉浮？若道都齐无恙，云何渐渐如钩？'),
	(5225, 233, '满江红·赤壁怀古', '赤壁矶头，一番过、一番怀古。想当时，周郎年少，气吞区宇。万骑临江貔虎噪，千艘列炬鱼龙怒。卷长波、一鼓困曹瞒，今如许？ 江上渡，江边路。形胜地，兴亡处。览遗踪，胜读史书言语。几度东风吹世换，千年往事随潮去。问道傍、杨柳为谁春，摇金缕。'),
	(5226, 633, '诉衷情·青衫初入九重城', '青衫初入九重城，结友尽豪英。蜡封夜半传檄，驰骑谕幽并。 时易失，志难城，鬓丝生。平章风月，弹压江山，别是功名。'),
	(5227, 360, '玉楼春·己卯岁元日', '一年滴尽莲花漏。碧井酴酥沉冻酒。晓寒料峭尚欺人，春态苗条先到柳。 佳人重劝千长寿。柏叶椒花芬翠袖。醉乡深处少相知，只与东君偏故旧。'),
	(5228, 321, '忆王孙·春词', '萋萋芳草忆王孙。柳外楼高空断魂。杜宇声声不忍闻。欲黄昏。雨打梨花深闭门。'),
	(5229, 249, '摸鱼儿·东皋寓居', '买陂塘、旋栽杨柳，依稀淮岸江浦。东皋嘉雨新痕涨，沙觜鹭来鸥聚。堪爱处最好是、一川夜月光流渚。无人独舞。任翠幄张天，柔茵藉地，酒尽未能去。 青绫被，莫忆金闺故步。儒冠曾把身误。弓刀千骑成何事，荒了邵平瓜圃。君试觑。满青镜、星星鬓影今如许。功名浪语。便似得班超，封侯万里，归计恐迟暮。'),
	(5230, 167, '江南春·波渺渺', '波渺渺，柳依依。孤村芳草远，斜日杏花飞。江南春尽离肠断，苹满汀洲人未归。'),
	(5231, 591, '清平乐·题上卢桥', '清溪奔快。不管青山碍。千里盘盘平世界。更著溪山襟带。 古今陵谷茫茫。市朝往往耕桑。此地居然形胜，似曾小小兴亡。'),
	(5232, 494, '洞仙歌·冰肌玉骨', '仆七岁时，见眉州老尼，姓朱，忘其名，年九十岁。自言尝随其师入蜀主孟昶宫中，一日大热，蜀主与花蕊夫人夜纳凉摩诃池上，作一词，朱具能记之。今四十年，朱已死久矣，人无知此词者，但记其首两句，暇日寻味，岂《洞仙歌》令乎？乃为足之云。 冰肌玉骨，自清凉无汗。水殿风来暗香满。绣帘开，一点明月窥人，人未寝，欹枕钗横鬓乱。 起来携素手，庭户无声，时见疏星渡河汉。试问夜如何？夜已三更，金波淡，玉绳低转。但屈指西风几时来，又不道流年暗中偷换。'),
	(5233, 693, '虞美人·宜州见梅作', '天涯也有江南信。梅破知春近。夜阑风细得香迟。不道晓来开遍、向南枝。 玉台弄粉花应妒。飘到眉心住。平生个里愿杯深。去国十年老尽、少年心。'),
	(5234, 639, '念奴娇·登多景楼', '危楼还望，叹此意、今古几人曾会？鬼设神施，浑认作、天限南疆北界。一水横陈，连岗三面，做出争雄势。六朝何事，只成门户私计？ 因笑王谢诸人，登高怀远，也学英雄涕。凭却长江，管不到，河洛腥膻无际。正好长驱，不须反顾，寻取中流誓。小儿破贼，势成宁问强对！(强对 一作：疆场)'),
	(5235, 494, '行香子·述怀', '清夜无尘。月色如银。酒斟时、须满十分。浮名浮利，虚苦劳神。叹隙中驹，石中火，梦中身。 虽抱文章，开口谁亲。且陶陶、乐尽天真。几时归去，作个闲人。对一张琴，一壶酒，一溪云。'),
	(5236, 494, '鹊桥仙·七夕', '缑山仙子，高清云渺，不学痴牛騃女。凤箫声断月明中，举手谢时人欲去。 客槎曾犯，银河波浪，尚带天风海雨。相逢一醉是前缘，风雨散、飘然何处？'),
	(5237, 112, '唐多令·惜别', '何处合成愁。离人心上秋。纵芭蕉、不雨也飕飕。都道晚凉天气好，有明月、怕登楼。 年事梦中休。花空烟水流。燕辞归、客尚淹留。垂柳不萦裙带住。漫长是、系行舟。'),
	(5238, 305, '孤雁儿·世人作梅诗', '世人作梅词，下笔便俗。予试作一篇，乃知前言不妄耳。 藤床纸帐朝眠起。说不尽、无佳思。沈香断续玉炉寒，伴我情怀如水。笛声三弄，梅心惊破，多少春情意。 小风疏雨萧萧地。又催下、千行泪。吹箫人去玉楼空，肠断与谁同倚。一枝折得，人间天上，没个人堪寄。'),
	(5239, 250, '鹧鸪天·小令尊前见玉箫', '小令尊前见玉箫。银灯一曲太妖娆。歌中醉倒谁能恨，唱罢归来酒未消。 春悄悄，夜迢迢。碧云天共楚宫遥。梦魂惯得无拘检，又踏杨花过谢桥。'),
	(5240, 633, '秋波媚·七月十六日晚登高兴亭望长安南山', '秋到边城角声哀，烽火照高台。悲歌击筑，凭高酹酒，此兴悠哉。 多情谁似南山月，特地暮云开。灞桥烟柳，曲江池馆，应待人来。'),
	(5241, 549, '芳心苦·杨柳回塘', '杨柳回塘，鸳鸯别浦。绿萍涨断莲舟路。断无蜂蝶慕幽香，红衣脱尽芳心苦。 返照迎潮，行云带雨。依依似与骚人语。当年不肯嫁春风，无端却被秋风误。'),
	(5242, 99, '卜算子', '岁见梅追和一首，终恨有儿女子态耳 竹里一枝梅，雨洗娟娟静。疑是佳人日暮来，绰约风前影。 新恨有谁知，往事何堪省。梦绕阳台寂寞回，沾袖余香冷。'),
	(5243, 186, '青门引·春思', '乍暖还轻冷。风雨晚来方定。庭轩寂寞近清明，残花中酒，又是去年病。 楼头画角风吹醒。入夜重门静。那堪更被明月，隔墙送过秋千影。'),
	(5244, 185, '忆秦娥', '桃花萼。雨肥红绽东风恶。东风恶。长亭无寐，短书难托。 征衫辜负深闺约。禁烟时候春罗薄。春罗薄。多应消瘦，可堪梳掠。'),
	(5245, 591, '水龙吟·甲辰岁寿韩南涧尚书', '渡江天马南来，几人真是经纶手。长安父老，新亭风景，可怜依旧。夷甫诸人，神州沉陆，几曾回首。算平戎万里，功名本是，真儒事、君知否。(君知否 一作：公知否) 况有文章山斗。对桐阴、满庭清昼。当年堕地，而今试看，风云奔走。绿野风烟，平泉草木，东山歌酒。待他年，整顿乾坤事了，为先生寿。'),
	(5246, 430, '点绛唇·感兴', '雨恨云愁，江南依旧称佳丽。水村渔市。一缕孤烟细。 天际征鸿，遥认行如缀。平生事。此时凝睇。谁会凭栏意。(栏 通：阑)'),
	(5247, 494, '蝶恋花·雨霰疏疏经泼火', '雨霰疏疏经泼火。巷陌秋千，犹未清明过。杏子梢头香蕾破。淡红褪白胭脂涴。 苦被多情相折挫。病绪厌厌，浑似年时个。绕遍回廊还独坐。月笼云暗重门锁。'),
	(5248, 591, '满江红·中秋寄远', '快上西楼，怕天放、浮云遮月。但唤取、玉纤横管，一声吹裂。谁做冰壶凉世界，最怜玉斧修时节。问嫦娥、孤令有愁无？应华发。 云液满，琼杯滑。长袖起，清歌咽。叹十常八九，欲磨还缺。但愿长圆如此夜，人情未必看承别。把从前、离恨总成欢，归时说。'),
	(5249, 591, '醉花阴·黄花谩说年年好', '黄花谩说年年好。也趁秋光老。绿鬓不惊秋，若斗尊前，人好花堪笑。 蟠桃结子知多少。家住三山岛。何日跨归鸾，沧海飞尘，人世因缘了。'),
	(5250, 422, '声声慢', '高寒户牖，虚白尊罍，千山尽入孤光。玉影如空，天葩暗落清香。平生此兴不浅，记当年、独据胡床。怎知道，是岁华换却，处处堪伤。 已是南楼曲断，纵疏花淡月，也只凄凉。冷雨斜风，何况独掩西窗。天涯故人总老，谩相思、永夜相望。断梦远，趁秋声、一片渡江。'),
	(5251, 185, '贺新郎·送胡邦衡待制赴新州', '梦绕神州路。怅秋风、连营画角，故宫离黍。底事昆仑倾砥柱，九地黄流乱注。聚万落千村狐兔。天意从来高难问，况人情老易悲难诉。更南浦，送君去。 凉生岸柳催残暑。耿斜河，疏星残月，断云微度。万里江山知何处？回首对床夜语。雁不到，书成谁与？目尽青天怀今古，肯儿曹恩怨相尔汝！举大白，听《金缕》。'),
	(5252, 352, '蝶恋花·海燕双来归画栋', '海燕双来归画栋。帘影无风，花影频移动。半醉腾腾春睡重。绿鬟堆枕香云拥。 翠被双盘金缕凤。忆得前春，有个人人共。花里黄莺时一弄。日斜惊起相思梦。'),
	(5253, 156, '河满子·秋怨', '怅望浮生急景，凄凉宝瑟馀音。楚客多情偏怨别，碧山远水登临。目送连天衰草，夜阑几处疏砧。 黄叶无风自落，秋云不雨长阴。天若有情天亦老，摇摇幽恨难禁。惆怅旧欢如梦，觉来无处追寻。'),
	(5254, 591, '水龙吟·过南剑双溪楼', '举头西北浮云，倚天万里须长剑。人言此地，夜深长见，斗牛光焰。我觉山高，潭空水冷，月明星淡。待燃犀下看，凭栏却怕，风雷怒，鱼龙惨。 峡束苍江对起，过危楼，欲飞还敛。元龙老矣！不妨高卧，冰壶凉簟。千古兴亡，百年悲笑，一时登览。问何人又卸，片帆沙岸，系斜阳缆？'),
	(5255, 549, '行路难·缚虎手', '缚虎手，悬河口，车如鸡栖马如狗。白纶巾，扑黄尘，不知我辈可是蓬蒿人？衰兰送客咸阳道，天若有情天亦老。作雷颠，不论钱，谁问旗亭美酒斗十千？ 酌大斗，更为寿，青鬓长青古无有。笑嫣然，舞翩然，当垆秦女十五语如弦。遗音能记秋风曲，事去千年犹恨促。揽流光，系扶桑，争奈愁来一日却为长。'),
	(5256, 28, '水调歌头·平生太湖上', '平生太湖上，短棹几经过。如今重到何事？愁比水云多。拟把匣中长剑，换取扁舟一叶，归去老渔蓑。银艾非吾事，丘壑已蹉跎。脍新鲈，斟美酒，起悲歌。太平生长，岂谓今日识兵戈？欲泻三江雪浪，净洗胡尘千里，不用挽天河！回首望霄汉，双泪堕清波。'),
	(5257, 193, '离亭燕·一带江山如画', '一带江山如画，风物向秋潇洒。水浸碧天何处断？霁色冷光相射。蓼屿荻花洲，掩映竹篱茅舍。 云际客帆高挂，烟外酒旗低亚。多少六朝兴废事，尽入渔樵闲话。怅望倚层楼，寒日无言西下。'),
	(5258, 250, '鹧鸪天·醉拍春衫惜旧香', '醉拍春衫惜旧香。天将离恨恼疏狂。年年陌上生秋草，日日楼中到夕阳。 云渺渺，水茫茫。征人归路许多长。相思本是无凭语，莫向花笺费泪行。'),
	(5259, 269, '渔家傲·小雨纤纤风细细', '小雨纤纤风细细，万家杨柳青烟里。恋树湿花飞不起，愁无比，和春付与东流水。 九十光阴能有几？金龟解尽留无计。寄语东阳沽酒市，拼一醉，而今乐事他年泪。'),
	(5260, 149, '暗香·旧时月色', '辛亥之冬，余载雪诣石湖。止既月，授简索句，且征新声，作此两曲，石湖把玩不已，使二妓肆习之，音节谐婉，乃名之曰《暗香》、《疏影》。 旧时月色，算几番照我，梅边吹笛？唤起玉人，不管清寒与攀摘。何逊而今渐老，都忘却春风词笔。但怪得竹外疏花，香冷入瑶席。 江国，正寂寂，叹寄与路遥，夜雪初积。翠尊易泣，红萼无言耿相忆。长记曾携手处，千树压、西湖寒碧。又片片、吹尽也，几时见得？'),
	(5261, 250, '更漏子·柳丝长', '柳丝长，桃叶小。深院断无人到。红日淡，绿烟晴。流莺三两声。 雪香浓，檀晕少。枕上卧枝花好。春思重，晓妆迟。寻思残梦时。'),
	(5262, 624, '木兰花·城上风光莺语乱', '城上风光莺语乱，城下烟波春拍岸。绿杨芳草几时休，泪眼愁肠先已断。 情怀渐觉成衰晚，鸾镜朱颜惊暗换，昔年多病厌芳尊，今日芳尊惟恐浅。'),
	(5263, 633, '夜游宫·记梦寄师伯浑', '雪晓清笳乱起。梦游处、不知何地。铁骑无声望似水。想关河，雁门西，青海际。 睡觉寒灯里。漏声断、月斜窗纸。自许封侯在万里。有谁知，鬓虽残，心未死。'),
	(5264, 518, '卜算子', '春事付莺花，曾是莺花主。醉拍春衫金缕衣，只向花间住。 密意君听取。莫逐风来去。若是真心待于飞，云里千条路。'),
	(5265, 305, '满庭芳·小阁藏春', '小阁藏春，闲窗锁昼，画堂无限深幽。篆香烧尽，日影下帘钩。手种江梅渐好，又何必、临水登楼。无人到，寂寥浑似，何逊在扬州。 从来，知韵胜，难堪雨藉，不耐风揉。更谁家横笛，吹动浓愁。莫恨香消雪减，须信道、扫迹情留。难言处，良宵淡月，疏影尚风流。'),
	(5266, 641, '长相思', '风萧萧。雨骚骚。风雨萧骚梧叶飘。潇湘江畔楼。 云迢迢。水遥遥。云水迢遥天尽头。相思心上秋。'),
	(5267, 494, '浣溪沙·送梅庭老赴上党学官', '门外东风雪洒裾。山头回首望三吴。不应弹铗为无鱼。 上党从来天下脊，先生元是古之儒。时平不用鲁连书。'),
	(5268, 305, '怨王孙·湖上风来波浩渺', '湖上风来波浩渺。秋已暮、红稀香少。水光山色与人亲，说不尽、无穷好。 莲子已成荷叶老。青露洗、萍花汀草。眠沙鸥鹭不回头，似也恨、人归早。'),
	(5269, 515, '声声慢·秋声', '黄花深巷，红叶低窗，凄凉一片秋声。豆雨声来，中间夹带风声。疏疏二十五点，丽谯门、不锁更声。故人远，问谁摇玉佩，檐底铃声。 彩角声吹月堕，渐连营马动，四起笳声。闪烁邻灯，灯前尚有砧声。知他诉愁到晓，碎哝哝、多少蛩声。诉未了，把一半、分与雁声。'),
	(5270, 305, '临江仙·庭院深深深几许', '欧阳公作《蝶恋花》，有“深深深几许”之句，予酷爱之。用其语作“庭院深深”数阕，其声即旧《临江仙》也。 庭院深深深几许？云窗雾阁常扃。柳梢梅萼渐分明。春归秣陵树，人老建康城。 感月吟风多少事，如今老去无成。谁怜憔悴更凋零。试灯无意思，踏雪没心情。(试灯无意思，踏雪没心情 一作：灯花空结蕊，离别共伤情)'),
	(5271, 202, '清平乐·采芳人杳', '采芳人杳，顿觉游情少。客里看春多草草，总被诗愁分了。 去年燕子天涯，今年燕子谁家？ 三月休听夜雨，如今不是催花。'),
	(5272, 352, '采桑子·清明上巳西湖好', '清明上巳西湖好，满目繁华。争道谁家。绿柳朱轮走钿车。 游人日暮相将去，醒醉喧哗。路转堤斜。直到城头总是花。'),
	(5273, 251, '踏莎行·小径红稀', '小径红稀，芳郊绿遍。高台树色阴阴见。春风不解禁杨花，蒙蒙乱扑行人面。(蒙 通：濛) 翠叶藏莺，朱帘隔燕。炉香静逐游丝转。一场愁梦酒醒时，斜阳却照深深院。'),
	(5274, 591, '生查子·独游雨岩', '溪边照影行，天在清溪底。天上有行云，人在行云里。 高歌谁和余，空谷清音起。非鬼亦非仙，一曲桃花水。'),
	(5275, 559, '燕山亭·北行见杏花', '裁剪冰绡，轻叠数重，淡著胭脂匀注。新样靓妆，艳溢香融，羞杀蕊珠宫女。易得凋零，更多少、无情风雨。愁苦。闲院落凄凉，几番春暮。（闲院 一作：问院) 凭寄离恨重重，者双燕，何曾会人言语。天遥地远，万水千山，知他故宫何处。怎不思量，除梦里、有时曾去。无据，和梦也新来不做。'),
	(5276, 149, '鹧鸪天·元夕有所梦', '肥水东流无尽期。当初不合种相思。梦中未比丹青见，暗里忽惊山鸟啼。 春未绿，鬓先丝。人间别久不成悲。谁教岁岁红莲夜，两处沉吟各自知。(沉 通：沈)'),
	(5277, 494, '满江红', '遇东坡于齐安。怪其丰暇自得。余问之，曰：吾再娶柳氏，三日而去官。吾固不戚戚，而忧柳氏不能忘情于进退也。已而欣然同忧患，如处富贵，吾是以益安焉。命其侍儿歌其所作满江红。嗟叹之不足，乃次其韵 忧喜相寻，风雨过、一江春绿。巫峡梦、至今空有，乱山屏簇。何似伯鸾携德耀，箪瓢未足清欢足。渐粲然、光彩照阶庭，生兰玉。 幽梦里，传心曲。肠断处，凭他续。文君婿知否，笑君卑辱。君不见周南歌汉广，天教夫子休乔木。便相将、左手抱琴旧，云间宿。'),
	(5278, 250, '蝶恋花·醉别西楼醒不记', '醉别西楼醒不记。春梦秋云，聚散真容易。斜月半窗还少睡。画屏闲展吴山翠。 衣上酒痕诗里字。点点行行，总是凄凉意。红烛自怜无好计。夜寒空替人垂泪。'),
	(5279, 176, '长相思·游西湖', '南高峰，北高峰，一片湖光烟霭中。春来愁杀侬。 郎意浓，妾意浓。油壁车轻郎马骢，相逢九里松。'),
	(5280, 408, '清平乐·春晚', '留春不住，费尽莺儿语。满地残红宫锦污，昨夜南园风雨。 小怜初上琵琶，晓来思绕天涯。不肯画堂朱户，春风自在杨花。'),
	(5281, 674, '凤箫吟·锁离愁', '锁离愁，连绵无际，来时陌上初熏。绣帏人念远，暗垂珠泪，泣送征轮。长亭长在眼，更重重、远水孤云。但望极楼高，尽日目断王孙。 消魂。池塘别后，曾行处、绿妒轻裙。恁时携素手，乱花飞絮里，缓步香茵。朱颜空自改，向年年、芳意长新。遍绿野，嬉游醉眠，莫负青春。'),
	(5282, 250, '思远人·红叶黄花秋意晚', '红叶黄花秋意晚，千里念行客。飞云过尽，归鸿无信，何处寄书得。 泪弹不尽临窗滴。就砚旋研墨。渐写到别来，此情深处，红笺为无色。'),
	(5283, 475, '鹧鸪天·别情', '玉惨花愁出凤城，莲花楼下柳青青。尊前一唱阳关曲，别个人人第五程。 寻好梦，梦难成。有谁知我此时情，枕前泪共阶前雨，隔个窗儿滴到明。'),
	(5284, 236, '酹江月·和友驿中言别', '乾坤能大，算蛟龙元不是池中物。风雨牢愁无著处，那更寒蛩四壁。横槊题诗，登楼作赋，万事空中雪。江流如此，方来还有英杰。(寒蛩 一作：寒虫) 堪笑一叶漂零，重来淮水，正凉风新发。镜里朱颜都变尽，只有丹心难灭。去去龙沙，江山回首，一线青如发。故人应念，杜鹃枝上残月。'),
	(5285, 591, '水龙吟·老来曾识渊明', '老来曾识渊明，梦中一见参差是。觉来幽恨，停觞不御，欲歌还止。白发西风，折腰五斗，不应堪此。问北窗高卧，东篱自醉，应别有，归来意。 须信此翁未死，到如今凛然生气。吾侪心事，古今长在，高山流水。富贵他年，直饶未免，也应无味。甚东山何事，当时也道，为苍生起。'),
	(5286, 633, '鹊桥仙·夜闻杜鹃', '茅檐人静，蓬窗灯暗，春晚连江风雨。林莺巢燕总无声，但月夜、常啼杜宇。 催成清泪，惊残孤梦，又拣深枝飞去。故山犹自不堪听，况半世、飘然羁旅！'),
	(5287, 71, '忆秦娥·烧灯节', '中斋上元客散感旧，赋《忆秦娥》见属，一读凄然。随韵寄情，不觉悲甚。 烧灯节，朝京道上风和雪。风和雪，江山如旧，朝京人绝。 百年短短兴亡别，与君犹对当时月。当时月，照人烛泪，照人梅发。'),
	(5288, 498, '鹊桥仙·七夕', '双星良夜，耕慵织懒，应被群仙相妒。娟娟月姊满眉颦，更无奈、风姨吹雨。 相逢草草，争如休见，重搅别离心绪。新欢不抵旧愁多，倒添了、新愁归去。'),
	(5289, 305, '添字丑奴儿·窗前谁种芭蕉树', '窗前谁种芭蕉树，阴满中庭。阴满中庭。叶叶心心，舒卷有馀清。(馀清 一作：情) 伤心枕上三更雨，点滴霖霪。点滴霖霪。愁损北人，不惯起来听。'),
	(5290, 12, '如梦令·道是梨花不是', '道是梨花不是。 道是杏花不是。 白白与红红，别是东风情味。 曾记，曾记，人在武陵微醉。'),
	(5291, 305, '摊破浣溪沙·病起萧萧两鬓华', '病起萧萧两鬓华，卧看残月上窗纱。豆蔻连梢煎熟水，莫分茶。 枕上诗书闲处好，门前风景雨来佳。终日向人多酝藉，木犀花。'),
	(5292, 494, '行香子·过七里濑', '一叶舟轻，双桨鸿惊。水天清、影湛波平。鱼翻藻鉴，鹭点烟汀。过沙溪急，霜溪冷，月溪明。 重重似画，曲曲如屏。算当年、虚老严陵。君臣一梦，今古空名。但远山长，云山乱，晓山青。'),
	(5293, 494, '八声甘州·寄参寥子', '有情风、万里卷潮来，无情送潮归。问钱塘江上，西兴浦口，几度斜晖。不用思量今古，俯仰昔人非。谁似东坡老，白首忘机。 记取西湖西畔，正暮山好处，空翠烟霏。算诗人相得，如我与君稀。约他年、东还海道，愿谢公、雅志莫相违。西州路，不应回首，为我沾衣。(正暮 一作：正春)'),
	(5294, 352, '临江仙·记得金銮同唱第', '记得金銮同唱第，春风上国繁华。如今薄宦老天涯。十年岐路，空负曲江花。 闻说阆山通阆苑，楼高不见君家。孤城寒日等闲斜。离愁难尽，红树远连霞。'),
	(5295, 112, '风入松·听风听雨过清明', '听风听雨过清明。愁草瘗花铭。楼前绿暗分携路，一丝柳、一寸柔情。料峭春寒中酒，交加晓梦啼莺。 西园日日扫林亭。依旧赏新晴。黄蜂频扑秋千索，有当时、纤手香凝。惆怅双鸳不到，幽阶一夜苔生。'),
	(5296, 633, '谢池春·壮岁从戎', '壮岁从戎，曾是气吞残虏。阵云高、狼烽夜举。朱颜青鬓，拥雕戈西戍。笑儒冠、自来多误。 功名梦断，却泛扁舟吴楚。漫悲歌、伤怀吊古。烟波无际，望秦关何处。叹流年、又成虚度。'),
	(5297, 104, '踏莎行·雪似梅花', '雪似梅花，梅花似雪。似和不似都奇绝。恼人风味阿谁知？请君问取南楼月。 记得去年，探梅时节。老来旧事无人说。为谁醉倒为谁醒？到今犹恨轻离别。'),
	(5298, 344, '忆帝京·薄衾小枕凉天气', '薄衾小枕凉天气，乍觉别离滋味。展转数寒更，起了还重睡。毕竟不成眠，一夜长如岁。 也拟待、却回征辔；又争奈、已成行计。万种思量，多方开解，只恁寂寞厌厌地。系我一生心，负你千行泪。'),
	(5299, 639, '贺新郎·寄辛幼安和见怀韵', '老去凭谁说。看几番、神奇臭腐，夏裘冬葛。父老长安今余几，后死无仇可雪。犹未燥、当时生发。二十五弦多少恨，算世间、那有平分月。胡妇弄，汉宫瑟。 树犹如此堪重别。只使君、从来与我，话头多合。行矣置之无足问，谁换妍皮痴骨。但莫使、伯牙弦绝。九转丹砂牢拾取，管精金、只是寻常铁。龙共虎，应声裂。'),
	(5300, 591, '玉楼春·风前欲劝春光住', '风前欲劝春光住。春在城南芳草路。未随流落水边花，且作飘零泥上絮。 镜中已觉星星误。人不负春春自负。梦回人远许多愁，只在梨花风雨处。'),
	(5301, 494, '蝶恋花·京口得乡书', '雨后春容清更丽。只有离人，幽恨终难洗。北固山前三面水。碧琼梳拥青螺髻。 一纸乡书来万里。问我何年，真个成归计。白首送春拚一醉。东风吹破千行泪。'),
	(5302, 352, '望江南·江南柳', '江南柳，叶小未成阴。人为丝轻那忍折，莺嫌枝嫩不胜吟。留著待春深。 十四五，闲抱琵琶寻。阶上簸钱阶下走，恁时相见早留心。何况到如今。(莺嫌 一作：莺怜；留著 一作：留取)'),
	(5303, 437, '眼儿媚·杨柳丝丝弄轻柔', '杨柳丝丝弄轻柔，烟缕织成愁。海棠未雨，梨花先雪，一半春休。 而今往事难重省，归梦绕秦楼。相思只在：丁香枝上，豆蔻梢头。'),
	(5304, 251, '破阵子', '湖上西风斜日，荷花落尽红英。金菊满丛珠颗细，海燕辞巢翅羽轻。年年岁岁情。 美酒一杯新熟，高歌数阕堪听。不向尊前同一醉，可奈光阴似水声。迢迢去未停。'),
	(5305, 494, '西江月·平山堂', '三过平山堂下，半生弹指声中。十年不见老仙翁，壁上龙蛇飞动。 欲吊文章太守，仍歌杨柳春风。休言万事转头空，未转头时皆梦。'),
	(5306, 591, '木兰花慢·滁州送范倅', '老来情味减，对别酒，怯流年。况屈指中秋，十分好月，不照人圆。无情水都不管；共西风、只管送归船。秋晚莼鲈江上，夜深儿女灯前。 征衫，便好去朝天，玉殿正思贤。想夜半承明，留教视草，却遣筹边。长安故人问我，道愁肠殢酒只依然。目断秋霄落雁，醉来时响空弦。'),
	(5307, 305, '好事近·风定落花深', '风定落花深，帘外拥红堆雪。长记海棠开后，正伤春时节。 酒阑歌罢玉尊空，青缸暗明灭。魂梦不堪幽怨，更一声啼鴂。'),
	(5308, 305, '玉楼春·红酥肯放琼苞碎', '红酥肯放琼苞碎。探著南枝开遍未。不知酝藉几多香，但见包藏无限意。 道人憔悴春窗底。闷损阑干愁不倚。要来小酌便来休，未必明朝风不起。'),
	(5309, 255, '卜算子·兰', '松竹翠萝寒，迟日江山暮。幽径无人独自芳，此恨凭谁诉。似共梅花语。尚有寻芳侣。着意闻时不肯香，香在无心处。'),
	(5310, 494, '卜算子·感旧', '蜀客到江南，长忆吴山好。吴蜀风流自古同，归去应须早。 还与去年人，共藉西湖草。莫惜尊前仔细看，应是容颜老。'),
	(5311, 49, '沁园春·梦孚若', '何处相逢，登宝钗楼，访铜雀台。唤厨人斫就，东溟鲸脍，圉人呈罢，西极龙媒。天下英雄，使君与操，余子谁堪共酒杯。车千乘，载燕南赵北，剑客奇才。 饮酣画鼓如雷。谁信被晨鸡轻唤回。叹年光过尽，功名未立，书生老去，机会方来。使李将军，遇高皇帝，万户侯何足道哉。披衣起，但凄凉感旧，慷慨生哀。'),
	(5312, 186, '一丛花令·伤高怀远几时穷', '伤高怀远几时穷？无物似情浓。离愁正引千丝乱，更东陌、飞絮蒙蒙。嘶骑渐遥，征尘不断，何处认郎踪！ 双鸳池沼水溶溶，南北小桡通。梯横画阁黄昏后，又还是、斜月帘栊。沉恨细思，不如桃杏，犹解嫁东风。'),
	(5313, 236, '沁园春·题潮阳张许二公庙', '为子死孝，为臣死忠，死又何妨。 自光岳气分，士无全节；君臣义缺，谁负刚肠。 骂贼睢阳，爱君许远，留取声名万古香。 后来者，无二公之操，百炼之钢。 人生翕歘云亡。 好烈烈轰轰做一场。 使当时卖国，甘心降虏，受人唾骂，安得流芳。 古庙幽沉，仪容俨雅，枯木寒鸦几夕阳。 邮亭下，有奸雄过此，仔细思量。'),
	(5314, 4, '长相思·雨', '一声声。一更更。窗外芭蕉窗里灯。此时无限情。梦难成。恨难平。不道愁人不喜听。空阶滴到明。'),
	(5315, 149, '踏莎行·自沔东来丁未元日至金陵江上感梦而作', '燕燕轻盈，莺莺娇软，分明又向华胥见。夜长争得薄情知？春初早被相思染。 别后书辞，别时针线，离魂暗逐郎行远。淮南皓月冷千山，冥冥归去无人管。'),
	(5316, 633, '鹧鸪天·家住苍烟落照间', '家住苍烟落照间，丝毫尘事不相关。斟残玉瀣行穿竹，卷罢《黄庭》卧看山。 贪啸傲，任衰残，不妨随处一开颜。元知造物心肠别，老却英雄似等闲！'),
	(5317, 549, '浪淘沙', '一叶忽惊秋。分付东流。殷勤为过白苹洲。洲上小楼帘半卷，应认归舟。 回首恋朋游。迹去心留。歌尘萧散梦云收。惟有尊前曾见月，相伴人愁。'),
	(5318, 250, '临江仙·浅浅余寒春半', '浅浅余寒春半，雪消蕙草初长。烟迷柳岸旧池塘。风吹梅蕊闹，雨细杏花香。 月堕枝头欢意，从前虚梦高唐，觉来何处放思量。如今不是梦，真个到伊行。'),
	(5319, 188, '六州歌头·长淮望断', '长淮望断，关塞莽然平。征尘暗，霜风劲，悄边声。黯销凝。追想当年事，殆天数，非人力，洙泗上，弦歌地，亦膻腥。隔水毡乡，落日牛羊下，区脱纵横。看名王宵猎，骑火一川明。笳鼓悲鸣。遣人惊。 念腰间箭，匣中剑，空埃蠹，竟何成。时易失，心徒壮，岁将零。渺神京。干羽方怀远，静烽燧，且休兵。冠盖使，纷驰骛，若为情。闻道中原遗老，常南望、翠葆霓旌。使行人到此，忠愤气填膺。有泪如倾。'),
	(5320, 267, '相见欢·金陵城上西楼', '金陵城上西楼，倚清秋。万里夕阳垂地，大江流。 中原乱，簪缨散，几时收？试倩悲风吹泪，过扬州。'),
	(5321, 451, '望海潮·洛阳怀古', '梅英疏淡，冰澌溶泄，东风暗换年华。金谷俊游，铜驼巷陌，新晴细履平沙。长记误随车。正絮翻蝶舞，芳思交加。柳下桃蹊，乱分春色到人家。 西园夜饮鸣笳。有华灯碍月，飞盖妨花。兰苑未空，行人渐老，重来是事堪嗟。烟暝酒旗斜。但倚楼极目，时见栖鸦。无奈归心，暗随流水到天涯。'),
	(5322, 352, '渔家傲·五月榴花妖艳烘', '五月榴花妖艳烘。绿杨带雨垂垂重。五色新丝缠角粽。金盘送。生绡画扇盘双凤。 正是浴兰时节动。菖蒲酒美清尊共。叶里黄鹂时一弄。犹瞢忪。等闲惊破纱窗梦。'),
	(5323, 352, '渔家傲', '对酒当歌劳客劝。惜花只惜年华晚。寒艳冷香秋不管。情眷眷。凭栏尽日愁无限。 思抱芳期随寒雁。悔无深意传双燕。怅望一枝难寄远。人不见。楼头望断相思眼。'),
	(5324, 409, '南乡子·自古帝王州', '自古帝王州，郁郁葱葱佳气浮。四百年来成一梦，堪愁。晋代衣冠成古丘。 绕水恣行游。上尽层城更上楼。往事悠悠君莫问，回头。槛外长江空自流。'),
	(5325, 352, '朝中措·平山堂', '平山栏槛倚晴空，山色有无中。 手种堂前垂柳，别来几度春风？ 文章太守，挥毫万字，一饮千钟。 行乐直须年少，尊前看取衰翁。(尊前 通：樽)'),
	(5326, 456, '卜算子', '几日赏花天，月淡荼＿小。写尽相思唤不来，又是花飞了。 春在怕愁多，春去怜欢少。一夜安排梦不成，月堕西窗晓。'),
	(5327, 591, '西江月·堂上谋臣尊俎', '堂上谋臣尊俎，边头将士干戈。天时地利与人和，“燕可伐欤？”曰：“可”。 今日楼台鼎鼐，明年带砺山河。大家齐唱《大风歌》，不日四方来贺。(版本一) 堂上谋臣帷幄，边头猛将干戈。天时地利与人和。燕可伐与曰可。 此日楼台鼎鼐，他时剑履山河。都人齐和《大风歌》。管领群臣来贺。(版本二)'),
	(5328, 494, '少年游·润州作', '去年相送，余杭门外，飞雪似杨花。今年春尽，杨花似雪，犹不见还家。 对酒卷帘邀明月，风露透窗纱。恰似姮娥怜双燕，分明照、画梁斜。'),
	(5329, 250, '点绛唇·花信来时', '花信来时，恨无人似花依旧。又成春瘦，折断门前柳。 天与多情，不与长相守。分飞后，泪痕和酒，占了双罗袖。'),
	(5330, 17, '南柯子·十里青山远', '十里青山远，潮平路带沙。数声啼鸟怨年华。又是凄凉时候，在天涯。 白露收残月，清风散晓霞。绿杨堤畔问荷花：记得年时沽酒，那人家？'),
	(5331, 348, '苏幕遮·草', '露堤平，烟墅杳。乱碧萋萋，雨后江天晓。独有庾郎年最少。窣地春袍，嫩色宜相照。 接长亭，迷远道。堪怨王孙，不记归期早。落尽梨花春又了。满地残阳，翠色和烟老。'),
	(5332, 326, '昭君怨·咏荷上雨', '午梦扁舟花底，香满西湖烟水。急雨打篷声，梦初惊。 却是池荷跳雨，散了真珠还聚。聚作水银窝，泻清波。 (泻清波 一作：泛清波)'),
	(5333, 305, '菩萨蛮·风柔日薄春犹早', '风柔日薄春犹早，夹衫乍著心情好。睡起觉微寒，梅花鬓上残。 故乡何处是，忘了除非醉。沉水卧时烧，香消酒未消。'),
	(5334, 494, '浣溪沙·咏橘', '菊暗荷枯一夜霜。新苞绿叶照林光。竹篱茅舍出青黄。 香雾噀人惊半破，清泉流齿怯初尝。吴姬三日手犹香。'),
	(5335, 250, '长相思·长相思', '长相思，长相思。若问相思甚了期，除非相见时。 长相思，长相思。欲把相思说似谁，浅情人不知。'),
	(5336, 30, '临江仙·未遇行藏谁肯信', '未遇行藏谁肯信，如今方表名踪。无端良匠画形容。当风轻借力，一举入高空。 才得吹嘘身渐稳，只疑远赴蟾宫。雨馀时候夕阳红。几人平地上，看我碧霄中。'),
	(5337, 24, '忆秦娥', '伤离别。江南雁断音书绝。音书绝。两行珠泪，寸肠千结。 伤心长记中秋节。今年还似前年月。前年月。那知今夜，月圆人缺。'),
	(5338, 451, '千秋岁·水边沙外', '水边沙外。城郭春寒退。花影乱，莺声碎。飘零疏酒盏，离别宽衣带。人不见，碧云暮合空相对。 忆昔西池会。鹓鹭同飞盖。携手处，今谁在。日边清梦断，镜里朱颜改。春去也，飞红万点愁如海。'),
	(5339, 494, '满江红·怀子由作', '清颍东流，愁目断，孤帆明灭。宦游处，青山白浪，万重千叠。孤负当年林下意，对床夜雨听萧瑟。恨此生，长向别离中，生华发。 一尊酒，黄河侧。无限事，从头说。相看恍如昨，许多年月。衣上旧痕余苦泪，眉间喜气占黄色。便与君，池上觅残春，花如雪。'),
	(5340, 591, '汉宫春·立春日', '春已归来，看美人头上，袅袅春幡。无端风雨，未肯收尽余寒。年时燕子，料今宵梦到西园。浑未辨，黄柑荐酒，更传青韭堆盘？ 却笑东风，从此便薰梅染柳，更没些闲。闲时又来镜里，转变朱颜。清愁不断，问何人会解连环？生怕见花开花落，朝来塞雁先还。'),
	(5341, 591, '念奴娇·书东流村壁', '野棠花落，又匆匆过了，清明时节。刬地东风欺客梦，一枕云屏寒怯。曲岸持觞，垂杨系马，此地曾经别。楼空人去，旧游飞燕能说。 闻道绮陌东头，行人长见，帘底纤纤月。旧恨春江流不断，新恨云山千叠。料得明朝，尊前重见，镜里花难折。也应惊问：近来多少华发？'),
	(5342, 494, '南乡子·重九涵辉楼呈徐君猷', '霜降水痕收。浅碧鳞鳞露远洲。酒力渐消风力软，飕飕。破帽多情却恋头。 佳节若为酬。但把清尊断送秋。万事到头都是梦，休休。明日黄花蝶也愁。'),
	(5343, 250, '临江仙·斗草阶前初见', '斗草阶前初见，穿针楼上曾逢。罗裙香露玉钗风。靓妆眉沁绿，羞脸粉生红。 流水便随春远，行云终与谁同。酒醒长恨锦屏空。相寻梦里路，飞雨落花中。'),
	(5344, 7, '无俗念·灵虚宫梨花词', '春游浩荡，是年年、寒食梨花时节。白锦无纹香烂漫，玉树琼葩堆雪。静夜沉沉，浮光霭霭，冷浸溶溶月。人间天上，烂银霞照通彻。 浑似姑射真人，天姿灵秀，意气舒高洁。万化参差谁信道，不与群芳同列。浩气清英，仙材卓荦，下土难分别。瑶台归去，洞天方看清绝。'),
	(5345, 344, '少年游·参差烟树灞陵桥', '参差烟树灞陵桥，风物尽前朝。衰杨古柳，几经攀折，憔悴楚宫腰。 夕阳闲淡秋光老，离思满蘅皋。一曲阳关，断肠声尽，独自凭兰桡。'),
	(5346, 95, '贺圣朝·留别', '满斟绿醑留君住。莫匆匆归去。三分春色二分愁，更一分风雨。 花开花谢、都来几许。且高歌休诉。不知来岁牡丹时，再相逢何处。'),
	(5347, 188, '西江月', '盖未知其事，重为感叹，赋此 不识平原太守，向来水北山人。世间功业谩亏成。华发萧萧满镜。 幸有田园故里，聊分风月江城。西湖西畔晚波平。袖手时来照影。'),
	(5348, 225, '卜算子', '清池过雨凉，暗有清香度。缥缈娉婷绝代歌，翠袖风中举。 忽敛双眉去。总是关情处。一段江山一片云，又下阳台雨。'),
	(5349, 591, '鹧鸪天·鹅湖归病起作', '枕簟溪堂冷欲秋。断云依水晚来收。红莲相倚浑如醉，白鸟无言定自愁。 书咄咄，且休休。一丘一壑也风流。不知筋力衰多少，但觉新来懒上楼。'),
	(5350, 332, '忆秦娥', '情难足。不堪黄帽催行速。催行速。扁舟一叶，别愁千斛。 津亭送客惊相嘱。举杯欲唱眉先蹙。眉先蹙。背人掩面，不能终曲。'),
	(5351, 494, '江城子·孤山竹阁送述古', '翠蛾羞黛怯人看。掩霜纨，泪偷弹。且尽一尊，收泪唱《阳关》。漫道帝城天样远，天易见，见君难。 画堂新构近孤山。曲栏干，为谁安？飞絮落花，春色属明年。欲棹小舟寻旧事，无处问，水连天。'),
	(5352, 352, '阮郎归·南园春半踏青时', '南园春半踏青时，风和闻马嘶。青梅如豆柳如眉，日长蝴蝶飞。 花露重，草烟低，人家帘幕垂。秋千慵困解罗衣，画堂双燕归。'),
	(5353, 352, '蝶恋花·越女采莲秋水畔', '越女采莲秋水畔。窄袖轻罗，暗露双金钏。照影摘花花似面。芳心只共丝争乱。 鸂鶒滩头风浪晚。雾重烟轻，不见来时伴。隐隐歌声归棹远。离愁引著江南岸。'),
	(5354, 251, '诉衷情·东风杨柳欲青青', '东风杨柳欲青青。烟淡雨初晴。恼他香阁浓睡，撩乱有啼莺。 眉叶细，舞腰轻。宿妆成。一春芳意，三月如风，牵系人情。'),
	(5355, 49, '卜算子·片片蝶衣轻', '片片蝶衣轻，点点猩红小。道是天公不惜花，百种千般巧。 朝见树头繁，暮见枝头少。道是天公果惜花，雨洗风吹了。'),
	(5356, 591, '临江仙·探梅', '老去惜花心已懒，爱梅犹绕江村。一枝先破玉溪春。更无花态度，全有雪精神。 剩向空山餐秀色，为渠著句清新。竹根流水带溪云。醉中浑不记，归路月黄昏。'),
	(5357, 591, '念奴娇·登建康赏心亭呈史致道留守', '我来吊古，上危楼、赢得闲愁千斛。虎踞龙蟠何处是，只有兴亡满目。柳外斜阳，水边归鸟，陇上吹乔木。片帆西去，一声谁喷霜竹。 却忆安石风流，东山岁晚，泪落哀筝曲。儿辈功名都付与，长日惟消棋局。宝镜难寻，碧云将暮，谁劝杯中绿。江头风怒，朝来波浪翻屋。'),
	(5358, 494, '蝶恋花·蝶懒莺慵春过半', '蝶懒莺慵春过半。花落狂风，小院残红满。午醉未醒红日晚，黄昏帘幕无人卷。 云鬓鬅松眉黛浅。总是愁媒，欲诉谁消遣。未信此情难系绊，杨花犹有东风管。'),
	(5359, 494, '虞美人·有美堂赠述古', '湖山信是东南美，一望弥千里。使君能得几回来？便使樽前醉倒更徘徊。 沙河塘里灯初上，水调谁家唱？夜阑风静欲归时，惟有一江明月碧琉璃。'),
	(5360, 188, '忆秦娥', '天一角。南枝向我情如昨。情如昨。水寒烟淡，雾轻云薄。 吹花嚼蕊愁无托。年华冉冉惊离索。惊离索。倩春留住，莫教摇落。'),
	(5361, 451, '虞美人·碧桃天上栽和露', '碧桃天上栽和露。不是凡花数。乱山深处水潆回。可惜一枝如画、为谁开。 轻寒细雨情何限。不道春难管。为君沉醉又何妨。只怕酒醒时候、断人肠。'),
	(5362, 451, '点绛唇·醉漾轻舟', '醉漾轻舟，信流引到花深处。尘缘相误，无计花间住。 烟水茫茫，千里斜阳暮。山无数，乱红如雨。不记来时路。'),
	(5363, 250, '阮郎归·天边金掌露成霜', '天边金掌露成霜。云随雁字长。绿杯红袖趁重阳。人情似故乡。 兰佩紫，菊簪黄。殷勤理旧狂。欲将沉醉换悲凉。清歌莫断肠。'),
	(5364, 344, '雪梅香·景萧索', '景萧索，危楼独立面晴空。动悲秋情绪，当时宋玉应同。渔市孤烟袅寒碧，水村残叶舞愁红。楚天阔，浪浸斜阳，千里溶溶。 临风。想佳丽，别后愁颜，镇敛眉峰。可惜当年，顿乖雨迹云踪。雅态妍姿正欢洽，落花流水忽西东。无憀恨、相思意，尽分付征鸿。'),
	(5365, 271, '眼儿媚·迟迟春日弄轻柔', '迟迟春日弄轻柔，花径暗香流。清明过了，不堪回首，云锁朱楼。 午窗睡起莺声巧，何处唤春愁？绿杨影里，海棠亭畔，红杏梢头。'),
	(5366, 94, '水调歌头·秋色渐将晚', '秋色渐将晚，霜信报黄花。小窗低户深映，微路绕欹斜。为问山翁何事，坐看流年轻度，拚却鬓双华。徙倚望沧海，天净水明霞。 念平昔，空飘荡，遍天涯。归来三径重扫，松竹本吾家。却恨悲风时起，冉冉云间新雁，边马怨胡笳。谁似东山老，谈笑静胡沙。'),
	(5367, 250, '鹧鸪天·十里楼台倚翠微', '十里楼台倚翠微。百花深处杜鹃啼。殷勤自与行人语，不似流莺取次飞。 惊梦觉，弄晴时。声声只道不如归。天涯岂是无归意，争奈归期未可期。'),
	(5368, 112, '卜算子·凉挂晓云轻', '凉挂晓云轻，声度西风小。井上梧桐应未知，一叶云鬟袅。 来雁带书迟，别燕归程早。频探秋香开未开，恰似春来了。'),
	(5369, 513, '水调歌头·江上春山远', '江上春山远，山下暮云长。相留相送，时见双燕语风樯。满目飞花万点，回首故人千里，把酒沃愁肠。回雁峰前路，烟树正苍苍。 漏声残，灯焰短，马蹄香。浮云飞絮，一身将影向潇湘。多少风前月下，迤逦天涯海角，魂梦亦凄凉。又是春将暮，无语对斜阳。'),
	(5370, 591, '满江红·汉水东流', '汉水东流，都洗尽，髭胡膏血。人尽说，君家飞将，旧时英烈。破敌金城雷过耳，谈兵玉帐冰生颊。想王郎，结发赋从戎，传遗业。 腰间剑，聊弹铗。尊中酒，堪为别。况故人新拥，汉坛旌节。马革裹尸当自誓，蛾眉伐性休重说。但从今，记取楚楼风，庾台月。'),
	(5371, 591, '南乡子·好个主人家', '好个主人家。不问因由便去嗏。病得那人妆晃了，巴巴。系上裙儿稳也哪。 别泪没些些。海誓山盟总是赊。今日新欢须记取，孩儿，更过十年也似他。'),
	(5372, 352, '蝶恋花·画阁归来春又晚', '画阁归来春又晚。燕子双飞，柳软桃花浅。细雨满天风满院，愁眉敛尽无人见。 独倚阑干心绪乱。芳草芊绵，尚忆江南岸。风月无情人暗换，旧游如梦空肠断。'),
	(5373, 305, '鹧鸪天·寒日萧萧上琐窗', '寒日萧萧上琐窗，梧桐应恨夜来霜。酒阑更喜团茶苦，梦断偏宜瑞脑香。 秋已尽，日犹长，仲宣怀远更凄凉。不如随分尊前醉，莫负东篱菊蕊黄。'),
	(5374, 202, '清平乐·候蛩凄断', '候蛩凄断，人语西风岸。月落沙平江似练，望尽芦花无雁。 暗教愁损兰成，可怜夜夜关情。只有一枝梧叶，不知多少秋声！'),
	(5375, 366, '忆秦娥', '马萧萧。燕支山中风飘飘。风飘飘。黄昏寒雨，直是无憀。 玉人何处教吹箫。十年不见心如焦。心如焦。彩笺难寄，水远山遥。'),
	(5376, 128, '鹧鸪天·一点残红欲尽时', '一点残红欲尽时。乍凉秋气满屏帏。梧桐叶上三更雨，叶叶声声是别离。 调宝瑟，拨金猊。那时同唱鹧鸪词。如今风雨西楼夜，不听清歌也泪垂。'),
	(5377, 344, '蝶恋花·凤栖梧', '蜀锦地衣丝步障。屈曲回廊，静夜闲寻访。玉砌雕阑新月上。朱扉半掩人相望。 旋暖熏炉温斗帐。玉树琼枝，迤逦相偎傍。酒力渐浓春思荡。鸳鸯绣被翻红浪。'),
	(5378, 446, '如梦令', '照水粉梅开尽。春残峭寒犹甚。秋气著人衣，斗帐玉儿生晕。那更。那更。帘外月斜风横。'),
	(5379, 494, '浣溪沙·软草平莎过雨新', '徐州石潭谢雨，道上作五首。潭在城东二十里，常与泗水增减清浊相应。 软草平莎过雨新，轻沙走马路无尘。何时收拾耦耕身？ 日暖桑麻光似泼，风来蒿艾气如薰。使君元是此中人。'),
	(5380, 250, '蝶恋花·初捻霜纨生怅望', '初捻霜纨生怅望。隔叶莺声，似学秦娥唱。午睡醒来慵一饷。双纹翠簟铺寒浪。 雨罢苹风吹碧涨。脉脉荷花，泪脸红相向。斜贴绿云新月上。弯环正是愁眉样。'),
	(5381, 167, '踏莎行·春暮', '春色将阑，莺声渐老，红英落尽青梅小。画堂人静雨蒙蒙，屏山半掩余香袅。 密约沉沉，离情杳杳，菱花尘满慵将照。倚楼无语欲销魂，长空黯淡连芳草。'),
	(5382, 591, '水调歌头·我饮不须劝', '淳熙丁酉，自江陵移帅隆兴，到官之三月被召，司马监、赵卿、王漕饯别。司马赋《水调歌头》，席间次韵。时王公明枢密薨，坐客终夕为兴门户之叹，故前章及之。 我饮不须劝，正怕酒樽空。别离亦复何恨？此别恨匆匆。头上貂蝉贵客，苑外麒麟高冢，人世竟谁雄？一笑出门去，千里落花风。 孙刘辈，能使我，不为公。余发种种如是，此事付渠侬。但觉平生湖海，除了醉吟风月，此外百无功。毫发皆帝力，更乞鉴湖东。'),
	(5383, 494, '虞美人', '持杯摇劝天边月。愿月圆无缺。持杯复更劝花枝。且愿花枝长在、莫离披。 持杯月下花前醉。休问荣枯事。此欢能有几人知。对酒逢花不饮、待何时。'),
	(5384, 633, '鹊桥仙·一竿风月', '一竿风月，一蓑烟雨，家在钓台西住。卖鱼生怕近城门，况肯到红尘深处？ 潮生理棹，潮平系缆，潮落浩歌归去。时人错把比严光，我自是无名渔父。'),
	(5385, 494, '如梦令·春思', '手种堂前桃李，无限绿阴青子。帘外百舌儿，惊起五更春睡。居士，居士。莫忘小桥流水。'),
	(5386, 591, '沁园春·灵山斋庵赋时筑偃湖未成', '叠嶂西驰，万马回旋，众山欲东。正惊湍直下，跳珠倒溅；小桥横截，缺月初弓。老合投闲，天教多事，检校长身十万松。吾庐小，在龙蛇影外，风雨声中。 争先见面重重，看爽气朝来三数峰。似谢家子弟，衣冠磊落；相如庭户，车骑雍容。我觉其间，雄深雅健，如对文章太史公。新堤路，问偃湖何日，烟水蒙蒙？'),
	(5387, 130, '菩萨蛮·梅雪', '银河宛转三千曲。浴凫飞鹭澄波绿。何处是归舟。夕阳江上楼。 天憎梅浪发。故下封枝雪。深院卷帘看。应怜江上寒。'),
	(5388, 549, '将进酒·城下路', '城下路，凄风露，今人犁田古人墓。岸头沙，带蒹葭，漫漫昔时流水今人家。黄埃赤日长安道，倦客无浆马无草。开函关，掩函关，千古如何不见一人闲？ 六国扰，三秦扫，初谓商山遗四老。驰单车，致缄书，裂荷焚芰接武曳长裾。高流端得酒中趣，深入醉乡安稳处。生忘形，死忘名。谁论二豪初不数刘伶？'),
	(5389, 352, '渔家傲·暖日迟迟花袅袅', '暖日迟迟花袅袅。人将红粉争花好。花不能言惟解笑。金壶倒。花开未老人年少。 车马九门来扰扰。行人莫羡长安道。丹禁漏声衢鼓报。催昏晓。长安城里人先老。'),
	(5390, 305, '转调满庭芳·芳草池塘', '芳草池塘，绿阴庭院，晚晴寒透窗纱。玉钩金锁，管是客来唦。寂寞尊前席上，唯愁海角天涯。能留否？酴釄落尽，犹赖有梨花。 当年曾胜赏，生香熏袖，活火分茶。极目犹龙骄马，流水轻车。不怕风狂雨骤，恰才称，煮酒笺花。如今也，不成怀抱，得似旧时那？'),
	(5391, 267, '西江月·日日深杯酒满', '日日深杯酒满，朝朝小圃花开。自歌自舞自开怀，无拘无束无碍。 青史几番春梦，黄泉多少奇才。不须计较与安排，领取而今现在。（版本一） 日日深杯酒满，朝朝小圃花开。自歌自舞自开怀，且喜无拘无碍。 青史几番春梦，红尘多少奇才。不须计较与安排，领取而今现在。（版本二）'),
	(5392, 250, '阮郎归·旧香残粉似当初', '旧香残粉似当初。人情恨不如。一春犹有数行书。秋来书更疏。 衾凤冷，枕鸳孤。愁肠待酒舒。梦魂纵有也成虚。那堪和梦无。'),
	(5393, 344, '戚氏·晚秋天', '　　晚秋天，一霎微雨洒庭轩。槛菊萧疏，井梧零乱，惹残烟。凄然，望江关，飞云黯淡夕阳间。当时宋玉悲感，向此临水与登山。远道迢递，行人凄楚，倦听陇水潺湲。正蝉吟败叶，蛩响衰草，相应喧喧。 　　孤馆，度日如年。风露渐变，悄悄至更阑。长天净，绛河清浅，皓月婵娟。思绵绵。夜永对景，那堪屈指暗想从前。未名未禄，绮陌红楼，往往经岁迁延。 　　帝里风光好，当年少日，暮宴朝欢。况有狂朋怪侣，遇当歌对酒竞留连。别来迅景如梭，旧游似梦，烟水程何限。念名利，憔悴长萦绊。追往事、空惨愁颜。漏箭移，稍觉轻寒。渐呜咽，画角数声残。对闲窗畔，停灯向晓，抱影无眠。'),
	(5394, 84, '江城子·画楼帘暮卷新晴', '画楼帘幕卷新晴。掩银屏。晓寒轻。坠粉飘香，日日唤愁生。暗数十年湖上路，能几度，著娉婷。 年华空自感飘零。拥春酲。对谁醒。天阔云间，无处觅箫声。载酒买花年少事，浑不似，旧心情。'),
	(5395, 149, '念奴娇·闹红一舸', '余客武陵。湖北宪治在焉：古城野水，乔木参天。余与二三友，日荡舟其间。薄荷花而饮，意象幽闲，不类人境。秋水且涸。荷叶出地寻丈，因列坐其下，上不见日。清风徐来，绿云自动。间于疏处，窥见游人画船，亦一乐也。朅来吴兴。数得相羊荷花中，又夜泛西湖，光景奇绝。故以此句写之。 闹红一舸，记来时，尝与鸳鸯为侣，三十六陂人未到，水佩风裳无数。翠叶吹凉，玉容消酒，更洒菇蒲雨。嫣然摇动，冷香飞上诗句。 日暮，青盖亭亭，情人不见，争忍凌波去？只恐舞衣寒易落，愁人西风南浦。高柳垂阴，老鱼吹浪，留我花间住。田田多少，几回沙际归路。(愁人 一作：愁入)'),
	(5396, 693, '鹧鸪天·座中有眉山隐客史应之和前韵即席答之', '黄菊枝头生晓寒。人生莫放酒杯干。风前横笛斜吹雨，醉里簪花倒著冠。 身健在，且加餐。舞裙歌板尽清欢。黄花白发相牵挽，付与时人冷眼看。'),
	(5397, 112, '瑞鹤仙·秋感', '泪荷抛碎璧。正漏云筛雨，斜捎窗隙。林声怨秋色。对小山不迭，寸眉愁碧。凉欺岸帻。暮砧催、银屏翦尺。最无聊、燕去堂空，旧幕暗尘罗额。 行客。西园有分，断柳凄花，似曾相识。西风破屐。林下路，水边石。念寒蛩残梦，归鸿心事，那听江村夜笛。看雪飞、苹底芦梢，未如鬓白。'),
	(5398, 591, '木兰花慢·中秋饮酒', '可怜今夕月，向何处，去悠悠？是别有人间，那边才见，光影东头？是天外，空汗漫，但长风浩浩送中秋？飞镜无根谁系？姮娥不嫁谁留？ 谓经海底问无由，恍惚使人愁。怕万里长鲸，纵横触破，玉殿琼楼。虾蟆故堪浴水，问云何玉兔解沉浮？若道都齐无恙，云何渐渐如钩？'),
	(5399, 352, '采桑子·画船载酒西湖好', '画船载酒西湖好，急管繁弦，玉盏催传，稳泛平波任醉眠。 行云却在行舟下，空水澄鲜，俯仰留连，疑是湖中别有天。'),
	(5400, 251, '渔家傲·画鼓声中昏又晓', '画鼓声中昏又晓。时光只解催人老。求得浅欢风日好。齐揭调。神仙一曲渔家傲。 绿水悠悠天杳杳。浮生岂得长年少。莫惜醉来开口笑。须信道。人间万事何时了。'),
	(5401, 633, '鹧鸪天·懒向青门学种瓜', '懒向青门学种瓜，只将渔钓送年华。双双新燕飞春岸，片片轻鸥落晚沙。 歌缥缈，舻呕哑，酒如清露鲊如花。逢人问道归何处，笑指船儿此是家。'),
	(5402, 494, '昭君怨·送别', '谁作桓伊三弄。惊破绿窗幽梦。新月与愁烟。满江天。 欲去又还不去。明日落花飞絮。飞絮送行舟。水东流。'),
	(5403, 250, '南乡子·新月又如眉', '新月又如眉。长笛谁教月下吹。楼倚暮云初见雁，南飞。漫道行人雁后归。 意欲梦佳期。梦里关山路不知。却待短书来破恨，应迟。还是凉生玉枕时。'),
	(5404, 344, '玉女摇仙佩·佳人', '飞琼伴侣，偶别珠宫，未返神仙行缀。取次梳妆，寻常言语，有得几多姝丽。拟把名花比。恐旁人笑我，谈何容易。细思算、奇葩艳卉，惟是深红浅白而已。争如这多情，占得人间，千娇百媚。 须信画堂绣阁，皓月清风，忍把光阴轻弃。自古及今，佳人才子，少得当年双关。且恁相偎倚。未消得、怜我多才多艺。愿奶奶、兰心蕙性，枕前言下，表余心意。为盟誓。今生断不孤鸳被。(双关 一作：双美）'),
	(5405, 591, '满江红·点火樱桃', '点火樱桃，照一架、荼蘼如雪。春正好，见龙孙穿破，紫苔苍壁。乳燕引雏飞力弱，流莺唤友娇声怯。问春归、不肯带愁归，肠千结。 层楼望，春山叠；家何在？烟波隔。把古今遗恨，向他谁说？蝴蝶不传千里梦，子规叫断三更月。听声声、枕上劝人归，归难得。'),
	(5406, 451, '减字木兰花·天涯旧恨', '天涯旧恨，独自凄凉人不问。欲见回肠，断尽金炉小篆香。 黛蛾长敛，任是春风吹不展。困倚危楼，过尽飞鸿字字愁。'),
	(5407, 693, '念奴娇·断虹霁雨', '八月十七日，同诸生步自永安城楼，过张宽夫园待月。偶有名酒，因以金荷酌众客。客有孙彦立，善吹笛。援笔作乐府长短句，文不加点。 断虹霁雨，净秋空，山染修眉新绿。桂影扶疏，谁便道，今夕清辉不足？万里青天，姮娥何处，驾此一轮玉。寒光零乱，为谁偏照醽醁？ 年少从我追游，晚凉幽径，绕张园森木。共倒金荷，家万里，难得尊前相属。老子平生，江南江北，最爱临风笛。孙郎微笑，坐来声喷霜竹。'),
	(5408, 483, '虞美人·寄公度', '芙蓉落尽天涵水，日暮沧波起。背飞双燕贴云寒，独向小楼东畔、倚阑看。 浮生只合尊前老，雪满长安道。故人早晚上高台，赠我江南春色、一枝梅。'),
	(5409, 494, '江城子', '墨云拖雨过西楼。水东流。晚烟收。柳外残阳，回照动帘钩。今夜巫山真个好，花未落，酒新篘。 美人微笑转星眸。月花羞。捧金瓯。歌扇萦风，吹散一春愁。试问江南诸伴侣，谁似我，醉扬州。'),
	(5410, 344, '安公子·远岸收残雨', '远岸收残雨。雨残稍觉江天暮。拾翠汀洲人寂静，立双双鸥鹭。望几点、渔灯隐映蒹葭浦。停画桡、两两舟人语。道去程今夜，遥指前村烟树。 游宦成羁旅。短樯吟倚闲凝伫。万水千山迷远近，想乡关何处。自别后、风亭月榭孤欢聚。刚断肠、惹得离情苦。听杜宇声声，劝人不如归去。'),
	(5411, 682, '卷珠帘·记得来时春未暮', '记得来时春未暮，执手攀花，袖染花梢露。暗卜春心共花语，争寻双朵争先去。 多情因甚相辜负，轻拆轻离，欲向谁分诉。泪湿海棠花枝处，东君空把奴分付。'),
	(5412, 515, '女冠子·元夕', '蕙花香也。雪晴池馆如画。春风飞到，宝钗楼上，一片笙箫，琉璃光射。而今灯漫挂。不是暗尘明月，那时元夜。况年来、心懒意怯，羞与蛾儿争耍。 江城人悄初更打。问繁华谁解，再向天公借。剔残红灺。但梦里隐隐，钿车罗帕。吴笺银粉砑。待把旧家风景，写成闲话。笑绿鬟邻女，倚窗犹唱，夕阳西下。'),
	(5413, 188, '西江月·问讯湖边春色', '问讯湖边春色，重来又是三年。东风吹我过湖船，杨柳丝丝拂面。 世路如今已惯，此心到处悠然。寒光亭下水如天，飞起沙鸥一片。'),
	(5414, 591, '阮郎归·耒阳道中为张处父推官赋', '山前灯火欲黄昏，山头来去云。鹧鸪声里数家村，潇湘逢故人。 挥羽扇，整纶巾，少年鞍马尘。如今憔悴赋招魂，儒冠多误身。'),
	(5415, 451, '南乡子·妙手写徽真', '妙手写徽真，水剪双眸点绛唇。疑是昔年窥宋玉，东邻，只露墙头一半身。 往事已酸辛，谁记当年翠黛颦？尽道有些堪恨处，无情，任是无情也动人。'),
	(5416, 186, '醉垂鞭·双蝶绣罗裙', '双蝶绣罗裙。东池宴。初相见。朱粉不深匀。闲花淡淡春。 细看诸处好。人人道。柳腰身。昨日乱山昏。来时衣上云。'),
	(5417, 117, '水调歌头·焦山', '铁瓮古形势，相对立金焦。长江万里东注，晓吹卷惊涛。天际孤云来去，水际孤帆上下，天共水相邀。远岫忽明晦，好景画难描。 混隋陈，分宋魏，战孙曹。回头千载陈迹，痴绝倚亭皋。惟有汀边鸥鹭，不管人间兴废，一抹度青霄。安得身飞去，举手谢尘嚣。'),
	(5418, 113, '水龙吟·寿李长孺', '清江社雨初晴，秋香吹彻高堂晓。天然带得，酒星风骨，诗囊才调。沔水春深，屏山月淡，吟鞭俱到。算一生绕遍，瑶阶玉树，如君样、人间少。 未放鹤归华表。伴仙翁、依然天杪。知他费几，雁边红粒，马边青草。待得清夷，彩衣花绶，哄堂一笑。且和平心事，等闲博个，千秋不老。'),
	(5419, 326, '好事近·七月十三日夜登万花川谷望月作', '月未到诚斋，先到万花川谷。不是诚斋无月，隔一林修竹。 如今才是十三夜，月色已如玉。未是秋光奇绝，看十五十六。'),
	(5420, 210, '卖花声·题岳阳楼', '木叶下君山。空水漫漫。十分斟酒敛芳颜。不是渭城西去客，休唱阳关。 醉袖抚危栏。天淡云闲。何人此路得生还。回首夕阳红尽处，应是长安。'),
	(5421, 515, '虞美人·梳楼', '丝丝杨柳丝丝雨。春在溟濛处。楼儿忒小不藏愁。几度和云飞去、觅归舟。 天怜客子乡关远。借与花消遣。海棠红近绿阑干。才卷朱帘却又、晚风寒。'),
	(5422, 149, '疏影·苔枝缀玉', '辛亥之冬，余载雪诣石湖。止既月，授简索句，且征新声，作此两曲，石湖把玩不已，使二妓肆习之，音节谐婉，乃名之曰《暗香》、《疏影》。 苔枝缀玉，有翠禽小小，枝上同宿。客里相逢，篱角黄昏，无言自倚修竹。昭君不惯胡沙远，但暗忆、江南江北。想佩环、月夜归来，化作此花幽独。 犹记深宫旧事，那人正睡里，飞近蛾绿。莫似春风，不管盈盈，早与安排金屋。还教一片随波去，又却怨、玉龙哀曲。等恁时、重觅幽香，已入小窗横幅。'),
	(5423, 305, '菩萨蛮·归鸿声断残云碧', '归鸿声断残云碧。背窗雪落炉烟直。烛底凤钗明。钗头人胜轻。 角声催晓漏。曙色回牛斗。春意看花难。西风留旧寒。'),
	(5424, 494, '青玉案·送伯固归吴中', '三年枕上吴中路。遣黄耳、随君去。若到松江呼小渡。莫惊鸥鹭，四桥尽是，老子经行处。 辋川图上看春暮。常记高人右丞句。作个归期天已许。春衫犹是，小蛮针线，曾湿西湖雨。'),
	(5425, 494, '江城子·江景', '湖上与张先同赋，时闻弹筝 凤凰山下雨初晴，水风清，晚霞明。一朵芙蕖，开过尚盈盈。何处飞来双白鹭，如有意，慕娉婷。 忽闻江上弄哀筝，苦含情，遣谁听！烟敛云收，依约是湘灵。欲待曲终寻问取，人不见，数峰青。'),
	(5426, 251, '木兰花·燕鸿过后莺归去', '燕鸿过后莺归去。细算浮生千万绪。长于春梦几多时，散似秋云无觅处。 闻琴解佩神仙侣。挽断罗衣留不住。劝君莫作独醒人，烂醉花间应有数。'),
	(5427, 162, '鹧鸪天·画毂雕鞍狭路逢', '画毂雕鞍狭路逢，一声肠断绣帘中。身无彩凤双飞翼，心有灵犀一点通。 金作屋，玉为笼，车如流水马如龙。刘郎已恨蓬山远，更隔蓬山几万重。'),
	(5428, 591, '粉蝶儿·和赵晋臣敷文赋落花', '昨日春如，十三女儿学绣。一枝枝、不教花瘦。甚无情，便下得，雨僝风僽。向园林、铺作地衣红绉。 而今春似，轻薄荡子难久。记前时、送春归后。把春波，都酿作，一江春酎。约清愁、杨柳岸边相候。'),
	(5429, 554, '清平乐·春风依旧', '春风依旧，著意隋堤柳。搓得鹅儿黄欲就，天气清明时候。 去年紫陌青门，今宵雨魄云魂。断送一生憔悴，只消几个黄昏？'),
	(5430, 494, '满江红·寄鄂州朱使君寿昌', '江汉西来，高楼下、蒲萄深碧。犹自带、岷峨云浪，锦江春色。君是南山遗爱守，我为剑外思归客。对此间、风物岂无情，殷勤说。 江表传，君休读。狂处士，真堪惜。空洲对鹦鹉，苇花萧瑟。不独笑书生争底事，曹公黄祖俱飘忽。愿使君、还赋谪仙诗，追黄鹤。'),
	(5431, 352, '采桑子·轻舟短棹西湖好', '轻舟短棹西湖好，绿水逶迤，芳草长堤，隐隐笙歌处处随。 无风水面琉璃滑，不觉船移，微动涟漪，惊起沙禽掠岸飞。'),
	(5432, 559, '燕山亭·北行见杏花', '裁剪冰绡，轻叠数重，淡著胭脂匀注。新样靓妆，艳溢香融，羞杀蕊珠宫女。易得凋零，更多少、无情风雨。愁苦。问院落凄凉，几番春暮。 凭寄离恨重重，者双燕，何曾会人言语。天遥地远，万水千山，知他故宫何处。怎不思量，除梦里、有时曾去。无据，和梦也新来不做。'),
	(5433, 10, '贺新郎', '寄一曲歌，睹物怀人，想见临风激烈也 兰芷湘东国。正愁予、一江红叶，水程孤驿。欲写潇湘无限意，那得如椽彩笔。但满眼、西风萧瑟。我所思兮何处所，在镡津、津上沧湾侧。谁氏子，阆风客。阆风仙客才无敌。赋悲秋、抑扬顿挫，流离沈郁。百赋千诗朝复暮，解道波涛春力。忆共尔、乘槎吹笛。八表神游吾梦见，涉洞庭、青草烟波隔。空怅望、楚天碧。'),
	(5434, 591, '菩萨蛮·金陵赏心亭为叶丞相赋', '青山欲共高人语。联翩万马来无数。烟雨却低回。望来终不来。 人言头上发。总向愁中白。拍手笑沙鸥。一身都是愁。'),
	(5435, 633, '点绛唇·采药归来', '采药归来，独寻茅店沽新酿。暮烟千嶂。处处闻渔唱。 醉弄扁舟，不怕黏天浪。江湖上，遮回疏放。作个闲人样。'),
	(5436, 253, '满江红', '日暖烟轻，竹梢映、花阴凌乱。微风皱、池光青碧，绿杨垂岸。艳杏墙头红粉，幽兰砌下飘香暖。称邀宾、明日去寻芳，频欢宴。 光景速，浑如箭。醉梦里，春强半。且花前莫厌，玉杯频劝。一枕游仙方警悟，浮名自笑犹萦绊。醉挥毫、付与雪儿歌，娇莺啭。古诗：“丽词付与雪儿歌。”'),
	(5437, 494, '西江月·重九', '点点楼头细雨，重重江外平湖。当年戏马会东徐，今日凄凉南浦。 莫恨黄花未吐，且教红粉相扶。酒阑不必看茱萸，俯仰人间今古。'),
	(5438, 352, '临江仙·柳外轻雷池上雨', '柳外轻雷池上雨，雨声滴碎荷声。小楼西角断虹明。阑干倚处，待得月华生。 燕子飞来窥画栋，玉钩垂下帘旌。凉波不动簟纹平。水精双枕，傍有堕钗横。'),
	(5439, 352, '南歌子·凤髻金泥带', '凤髻金泥带，龙纹玉掌梳。走来窗下笑相扶，爱道画眉深浅入时无？ 弄笔偎人久，描花试手初。等闲妨了绣功夫，笑问鸳鸯两字怎生书？'),
	(5440, 352, '蝶恋花·面旋落花风荡漾', '面旋落花风荡漾。柳重烟深，雪絮飞来往。雨后轻寒犹未放。春愁酒病成惆怅。 枕畔屏山围碧浪。翠被华灯，夜夜空相向。寂寞起来褰绣幌。月明正在梨花上。'),
	(5441, 344, '曲玉管·陇首云飞', '陇首云飞，江边日晚，烟波满目凭阑久。一望关河萧索，千里清秋，忍凝眸？杳杳神京，盈盈仙子，别来锦字终难偶。断雁无凭，冉冉飞下汀洲，思悠悠。 暗想当初，有多少、幽欢佳会，岂知聚散难期，翻成雨恨云愁？阻追游。每登山临水，惹起平生心事，一场消黯，永日无言，却下层楼。'),
	(5442, 158, '清平乐·雪', '悠悠飏飏。做尽轻模样。半夜萧萧窗外响。多在梅边竹上。 朱楼向晓帘开。六花片片飞来。无奈熏炉烟雾，腾腾扶上金钗。'),
	(5443, 591, '水调歌头·壬子三山被召陈端仁给事饮饯席上作', '长恨复长恨，裁作短歌行。何人为我楚舞，听我楚狂声？余既滋兰九畹，又树蕙之百亩，秋菊更餐英。门外沧浪水，可以濯吾缨。 一杯酒，问何似，身后名？人间万事，毫发常重泰山轻。悲莫悲生离别，乐莫乐新相识，儿女古今情。富贵非吾事，归与白鸥盟。'),
	(5444, 591, '贺新郎·把酒长亭说', '陈同父自东阳来过余，留十日。与之同游鹅湖，且会朱晦庵于紫溪，不至，飘然东归。既别之明日，余意中殊恋恋，复欲追路。至鹭鸶林，则雪深泥滑，不得前矣。独饮方村，怅然久之，颇恨挽留之正是遂也。夜半投宿吴氏泉湖四望楼，闻邻笛悲甚，为赋《贺新郎》以见意。又五日，同父书来索词，心所同然者如此，可发千里一笑。 把酒长亭说。看渊明、风流酷似，卧龙诸葛。何处飞来林间鹊，蹙踏松梢微雪。要破帽多添华发。剩水残山无态度，被疏梅料理成风月。两三雁，也萧瑟。 佳人重约还轻别。怅清江、天寒不渡，水深冰合。路断车轮生四角，此地行人销骨。问谁使、君来愁绝？铸就而今相思错，料当初、费尽人间铁。长夜笛，莫吹裂。'),
	(5445, 591, '满江红·敲碎离愁', '敲碎离愁，纱窗外、风摇翠竹。人去后、吹箫声断，倚楼人独。满眼不堪三月暮，举头已觉千山绿。但试将一纸寄来书，从头读。 相思字，空盈幅；相思意，何时足？滴罗襟点点，泪珠盈掬。芳草不迷行客路，垂杨只碍离人目。最苦是、立尽月黄昏，阑干曲。'),
	(5446, 329, '如梦令·满院落花春寂', '满院落花春寂。风絮一帘斜日。翠钿晓寒轻，独倚秋千无力。无力。无力。蹙破远山愁碧。'),
	(5447, 248, '雨霖铃·槐阴添绿', '槐阴添绿。雨馀花落，酒病相续。闲寻双杏凝伫，池塘暖、鸳鸯浴。却向窗昼卧，正春睡难足。叹好梦、一一无凭，帐掩金花坐凝目。 当时共赏移红烛。向花间、小饮杯盘促。蔷薇花下曾记，双凤带、索题诗曲。别后厌厌，应是香肌，瘦减罗幅。问燕子、不肯传情，甚入华堂宿。'),
	(5448, 494, '行香子·丹阳寄述古', '携手江村。梅雪飘裙。情何限、处处消魂。故人不见，旧曲重闻。向望湖楼，孤山寺，涌金门。 寻常行处，题诗千首，绣罗衫、与拂红尘。别来相忆，知是何人。有湖中月，江边柳，陇头云。'),
	(5449, 250, '生查子·关山魂梦长', '关山魂梦长，鱼雁音尘少。两鬓可怜青，只为相思老。 归梦碧纱窗，说与人人道。真个别离难，不似相逢好。'),
	(5450, 409, '菩萨蛮·数间茅屋闲临水', '数间茅屋闲临水，窄衫短帽垂杨里。花是去年红，吹开一夜风。 梢梢新月偃，午醉醒来晚。何物最关情，黄鹂三两声。'),
	(5451, 496, '剔银灯·与欧阳公席上分题', '昨夜因看蜀志，笑曹操孙权刘备。用尽机关，徒劳心力，只得三分天地。屈指细寻思，争如共、刘伶一醉？ 人世都无百岁。少痴騃、老成尪悴。只有中间，些子少年，忍把浮名牵系？一品与千金，问白发、如何回避？'),
	(5452, 693, '菩萨蛮·半烟半雨溪桥畔', '半烟半雨溪桥畔，渔翁醉着无人唤。疏懒意何长，春风花草香。 江山如有待，此意陶潜解。问我去何之，君行到自知。'),
	(5453, 434, '蝶恋花·小雨初晴回晚照', '小雨初晴回晚照。金翠楼台，倒影芙蓉沼。杨柳垂垂风袅袅。嫩荷无数青钿小。 似此园林无限好。流落归来，到了心情少。坐到黄昏人悄悄。更应添得朱颜老。'),
	(5454, 130, '夜飞鹊·河桥送人处', '河桥送人处，凉夜何其。斜月远堕余辉。铜盘烛泪已流尽，霏霏凉露沾衣。相将散离会，探风前津鼓，树杪参旗。华骢会意，纵扬鞭、亦自行迟。 迢递路回清野，人语渐无闻，空带愁归。何意重红满地，遗钿不见，斜迳都迷。兔葵燕麦，向残阳、欲与人齐。但徘徊班草，欷歔酹酒，极望天西。(红满地 一作：经前地 斜迳 一作：斜径)'),
	(5455, 130, '玉楼春·桃溪不作从容住', '桃溪不作从容住，秋藕绝来无续处。当时相候赤阑桥，今日独寻黄叶路。 烟中列岫青无数，雁背夕阳红欲暮。人如风后入江云，情似雨馀粘地絮。'),
	(5456, 251, '破阵子', '海上蟠桃易熟，人间好月长圆。惟有擘钗分钿侣，离别常多会面难。此情须问天。 蜡烛到明垂泪，熏炉尽日生烟。一点凄凉愁绝意，谩道秦筝有剩弦。何曾为细传。'),
	(5457, 473, '忆秦娥', '三月时。杨花飞尽无花飞。无花飞。不教春去，争得春归。 高楼望断黄金羁。绿窗眉黛伤新离。伤新离。好将别后，长做归时。'),
	(5458, 12, '如梦令·道是梨花不是', '道是梨花不是。道是杏花不是。白白与红红，别是东风情味。曾记，曾记。人在武陵微醉。'),
	(5459, 342, '点绛唇·金谷年年', '金谷年年，乱生春色谁为主？余花落处，满地和烟雨。 又是离歌，一阕长亭暮。王孙去，萋萋无数，南北东西路。'),
	(5460, 591, '鹧鸪天', '樽俎风流有几人。当年未遇已心亲。金陵种柳欢娱地，庾岭逢梅寂寞滨。 樽似海，笔如神。故人南北一般春。玉人好把新妆样，淡画眉儿浅注唇。'),
	(5461, 250, '御街行·街南绿树春饶絮', '街南绿树春饶絮，雪满游春路。树头花艳杂娇云，树底人家朱户。北楼闲上，疏帘高卷，直见街南树。 阑干倚尽犹慵去，几度黄昏雨。晚春盘马踏青苔，曾傍绿荫深驻。落花犹在，香屏空掩，人面知何处？'),
	(5462, 344, '甘草子·秋暮', '秋暮。乱洒衰荷，颗颗真珠雨。雨过月华生，冷彻鸳鸯浦。 池上凭阑愁无侣。奈此个、单栖情绪。却傍金笼共鹦鹉。念粉郎言语。'),
	(5463, 112, '浣溪沙·门隔花深梦旧游', '门隔花深梦旧游。夕阳无语燕归愁。玉纤香动小帘钩。 落絮无声春堕泪，行云有影月含羞。东风临夜冷于秋。'),
	(5464, 49, '长相思·惜梅', '寒相催。暖相催。催了开时催谢时。丁宁花放迟。 角声吹。笛声吹。吹了南枝吹北枝。明朝成雪飞。'),
	(5465, 149, '琵琶仙·双桨来时', '《吴都赋》云：“户藏烟浦，家具画船。”唯吴兴为然。春游之盛，西湖未能过也。己酉岁，予与萧时父载酒南郭，感遇成歌。 双桨来时，有人似、旧曲桃根桃叶。歌扇轻约飞花，蛾眉正奇绝。春渐远、汀洲自绿，更添了几声啼鴂。十里扬州 ，三生杜牧，前事休说。 又还是、宫烛分烟，奈愁里、匆匆换时节。都把一襟芳思，与空阶榆荚。千万缕、藏鸦细柳，为玉尊、起舞回雪。想见西出阳关，故人初别。'),
	(5466, 456, '卜算子·独自上层楼', '独自上层楼，楼外青山远。望以斜阳欲尽时，不见西飞雁。 独自下层楼，楼下蛩声怨。待到黄昏月上时，依旧柔肠断。'),
	(5467, 305, '长寿乐·南昌生日', '微寒应候。望日边六叶，阶蓂初秀。爱景欲挂扶桑，漏残银箭，杓回摇斗。庆高闳此际，掌上一颗明珠剖。有令容淑质，归逢佳偶。到如今，昼锦满堂贵胄。 荣耀，文步紫禁，一一金章绿绶。更值棠棣连阴，虎符熊轼，夹河分守。况青云咫尺，朝暮重入承明后。看彩衣争献，兰羞玉酎。祝千龄，借指松椿比寿。'),
	(5468, 130, '解语花·上元', '风消绛蜡，露浥红莲，灯市光相射。桂华流瓦。纤云散，耿耿素娥欲下。衣裳淡雅。看楚女纤腰一把。箫鼓喧，人影参差，满路飘香麝。 因念都城放夜。望千门如昼，嬉笑游冶。钿车罗帕。相逢处，自有暗尘随马。年光是也。唯只见、旧情衰谢。清漏移，飞盖归来，从舞休歌罢。'),
	(5469, 91, '绮罗香·咏春雨', '做冷欺花，将烟困柳，千里偷催春暮。尽日冥迷，愁里欲飞还住。惊粉重、蝶宿西园，喜泥润、燕归南浦。最妨它、佳约风流，钿车不到杜陵路。 沉沉江上望极，还被春潮晚急，难寻官渡。隐约遥峰，和泪谢娘眉妩。临断岸、新绿生时，是落红、带愁流处。记当日、门掩梨花，剪灯深夜语。'),
	(5470, 352, '渔家傲·近日门前溪水涨', '近日门前溪水涨。郎船几度偷相访。船小难开红斗帐。无计向。合欢影里空惆怅。 愿妾身为红菡萏。年年生在秋江上。重愿郎为花底浪。无隔障。随风逐雨长来往。'),
	(5471, 352, '望江南·江南蝶', '江南蝶，斜日一双双。身似何郎全傅粉，心如韩寿爱偷香。天赋与轻狂。 微雨后，薄翅腻烟光。才伴游蜂来小院，又随飞絮过东墙。长是为花忙。'),
	(5472, 697, '减字木兰花·竞渡', '红旗高举，飞出深深杨柳渚。鼓击春雷，直破烟波远远回。 欢声震地，惊退万人争战气。金碧楼西，衔得锦标第一归。'),
	(5473, 149, '鹧鸪天·正月十一日观灯', '巷陌风光纵赏时。笼纱未出马先嘶。白头居士无呵殿，只有乘肩小女随。 花满市，月侵衣。少年情事老来悲。沙河塘上春寒浅，看了游人缓缓归。'),
	(5474, 591, '永遇乐·戏赋辛字送茂嘉十二弟赴调', '烈日秋霜，忠肝义胆，千载家谱。得姓何年，细参辛字，一笑君听取。艰辛做就，悲辛滋味，总是辛酸辛苦。更十分，向人辛辣，椒桂捣残堪吐。 世间应有，芳甘浓美，不到吾家门户。比著儿曹，累累却有，金印光垂组。付君此事，从今直上，休忆对床风雨。但赢得，靴纹绉面，记余戏语。'),
	(5475, 591, '八声甘州·故将军饮罢夜归来', '夜读《李广传》，不能寐。因念晁楚老、杨民瞻约同居山间，戏用李广事，赋以寄之。 故将军饮罢夜归来，长亭解雕鞍。恨灞陵醉尉，匆匆未识，桃李无言。射虎山横一骑，裂石响惊弦。落魄封侯事，岁晚田间。 谁向桑麻杜曲，要短衣匹马，移住南山？看风流慷慨，谈笑过残年。汉开边、功名万里，甚当时、健者也曾闲。纱窗外、斜风细雨，一阵轻寒。'),
	(5476, 591, '定风波·暮春漫兴', '少日春怀似酒浓，插花走马醉千钟。老去逢春如病酒，唯有，茶瓯香篆小帘栊。 卷尽残花风未定，休恨，花开元自要春风。试问春归谁得见？飞燕，来时相遇夕阳中。'),
	(5477, 591, '水调歌头·舟次扬州和人韵', '落日塞尘起，胡骑猎清秋。汉家组练十万，列监耸高楼。谁道投鞭飞渡，忆昔鸣髇血污，风雨佛狸愁。季子正年少，匹马黑貂裘。 今老矣，搔白首，过扬州。倦游欲去江上，手种橘千头。二客东南名胜，万卷诗书事业，尝试与君谋。莫射南山虎，直觅富民侯。'),
	(5478, 633, '忆秦娥', '玉花骢。晚街金辔声璁珑。声璁珑。闲敧乌帽，又过城东。 富春巷陌花重重。千金沽酒酬春风。酬春风。笙歌园里，锦绣丛中。'),
	(5479, 271, '卜算子（咏梅）', '竹里一枝斜，映带林逾静。雨后清奇画不成，浅水横疏影。 吹彻小单于，心事思重省。拂拂风前度暗香，月色侵花冷。'),
	(5480, 554, '蝶恋花·欲减罗衣寒未去', '欲减罗衣寒未去，不卷珠帘，人在深深处。红杏枝头花几许？啼痕止恨清明雨。 尽日沉烟香一缕，宿酒醒迟，恼破春情绪。飞燕又将归信误，小屏风上西江路。'),
	(5481, 305, '小重山·春到长门春草青', '春到长门春草青，江梅些子破，未开匀。 碧云笼碾玉成尘，留晓梦，惊破一瓯春。 花影压重门，疏帘铺淡月，好黄昏。 二年三度负东君，归来也，著意过今春。'),
	(5482, 494, '减字木兰花·立春', '春牛春杖，无限春风来海上。便与春工，染得桃红似肉红。 春幡春胜，一阵春风吹酒醒。不似天涯，卷起杨花似雪花。'),
	(5483, 250, '破阵子·柳下笙歌庭院', '柳下笙歌庭院，花间姊妹秋千。记得春楼当日事，写向红窗夜月前。凭谁寄小莲。 绛蜡等闲陪泪，吴蚕到了缠绵。绿鬓能供多少恨，未肯无情比断弦。今年老去年。'),
	(5484, 321, '忆王孙·夏词', '风蒲猎猎小池塘。过雨荷花满院香。沈李浮瓜冰雪凉。竹方床。针线慵拈午梦长。'),
	(5485, 49, '忆秦娥·梅谢了', '梅谢了，塞垣冻解鸿归早。鸿归早，凭伊问讯，大梁遗老。 浙河西面边声悄，淮河北去炊烟少。炊烟少。宣和宫殿，冷烟衰草。'),
	(5486, 591, '祝英台近·晚春', '宝钗分，桃叶渡，烟柳暗南浦。怕上层楼，十日九风雨。断肠片片飞红，都无人管，更谁劝、啼莺声住？ 鬓边觑，试把花卜归期，才簪又重数。罗帐灯昏，哽咽梦中语：是他春带愁来，春归何处？却不解、带将愁去。'),
	(5487, 130, '满庭芳·夏日溧水无想山作', '风老莺雏，雨肥梅子，午阴嘉树清圆。地卑山近，衣润费炉烟。人静乌鸢自乐，小桥外、新绿溅溅。凭阑久，黄芦苦竹，拟泛九江船。 年年。如社燕，飘流瀚海，来寄修椽。且莫思身外，长近尊前。憔悴江南倦客，不堪听、急管繁弦。歌筵畔，先安簟枕，容我醉时眠。'),
	(5488, 693, '瑞鹤仙·环滁皆山也', '环滁皆山也。望蔚然深秀，琅琊山也。山行六七里，有翼然泉上，醉翁亭也。翁之乐也。得之心、寓之酒也。更野芳佳木，风高日出，景无穷也。 游也。山肴野蔌，酒洌泉香，沸筹觥也。太守醉也。喧哗众宾欢也。况宴酣之乐、非丝非竹，太守乐其乐也。问当时、太守为谁，醉翁是也。'),
	(5489, 494, '蝶恋花·暮春别李公择', '簌簌无风花自堕。寂寞园林，柳老樱桃过。落日有情还照坐，山青一点横云破。 路尽河回人转舵。系缆渔村，月暗孤灯火。凭仗飞魂招楚些，我思君处君思我。'),
	(5490, 344, '迷仙引·才过笄年', '才过笄年，初绾云鬟，便学歌舞。席上尊前，王孙随分相许。算等闲、酬一笑，便千金慵觑。常只恐、容易蕣华偷换，光阴虚度。 已受君恩顾，好与花为主。万里丹霄，何妨携手同归去。永弃却、烟花伴侣。免教人见妾，朝云暮雨。'),
	(5491, 352, '青玉案·一年春事都来几', '一年春事都来几，早过了、三之二。绿暗红嫣浑可事。绿杨庭院，暖风帘幕，有个人憔悴。 买花载酒长安市，又争似、家山见桃李。不枉东风吹客泪。相思难表，梦魂无据，惟有归来是。'),
	(5492, 591, '鹧鸪天·博山寺作', '不向长安路上行。却教山寺厌逢迎。味无味处求吾乐，材不材间过此生。 宁作我，岂其卿。人间走遍却归耕。一松一竹真朋友，山鸟山花好弟兄。'),
	(5493, 635, '临江仙·高咏楚词酬午日', '高咏楚词酬午日，天涯节序匆匆。榴花不似舞裙红。无人知此意，歌罢满帘风。 万事一身伤老矣，戎葵凝笑墙东。酒杯深浅去年同。试浇桥下水，今夕到湘中。'),
	(5494, 267, '如梦令', '盏底一盘金凤。满泛酒光浮动。引我上烟霞，智力一时无用。无用。无用。蹋破十洲三洞。'),
	(5495, 451, '江城子·南来飞燕北归鸿', '南来飞燕北归鸿，偶相逢，惨愁容。绿鬓朱颜重见两衰翁。别后悠悠君莫问，无限事，不言中。 小槽春酒滴珠红，莫匆匆，满金钟。饮散落花流水各西东。后会不知何处是，烟浪远，暮云重。'),
	(5496, 494, '蝶恋花（佳人）', '一颗樱桃樊素口。不爱黄金，只爱人长久。学画鸦儿犹未就。眉尖已作伤春皱。 扑蝶西园随伴走。花落花开，渐解相思瘦。破镜重圆人在否。章台折尽青青柳。'),
	(5497, 112, '声声慢·咏桂花', '蓝云笼晓，玉树悬秋，交加金钏霞枝。人起昭阳，禁寒粉粟生肌。浓香最无著处，渐冷香、风露成霏。绣茵展，怕空阶惊坠，化作萤飞。 三十六宫愁重，问谁持金锸，和月都移。掣锁西厢，清尊素手重携。秋来鬓华多少，任乌纱、醉压花低。正摇落，叹淹留、客又未归。'),
	(5498, 49, '贺新郎·九日', '湛湛长空黑。更那堪、斜风细雨，乱愁如织。老眼平生空四海，赖有高楼百尺。看浩荡、千崖秋色。白发书生神州泪，尽凄凉、不向牛山滴。追往事，去无迹。 少年自负凌云笔。到而今、春华落尽，满怀萧瑟。常恨世人新意少，爱说南朝狂客。把破帽、年年拈出。若对黄花孤负酒，怕黄花、也笑人岑寂。鸿北去，日西匿。'),
	(5499, 591, '一剪梅·游蒋山呈叶丞相', '独立苍茫醉不归。日暮天寒，归去来兮。探梅踏雪几何时。今我来思，杨柳依依。 白石冈头曲岸西。一片闲愁，芳草萋萋。多情山鸟不须啼。桃李无言，下自成蹊。'),
	(5500, 591, '念奴娇·赋雨岩', '近来何处有吾愁，何处还知吾乐。一点凄凉千古意，独倚西风寥郭。并竹寻泉，和云种树，唤做真闲客，此心闲处，不应长藉邱壑。 休说往事皆非，而今云是，且把清尊酌。醉里不知谁是我，非月非云非鹤。露冷风高，松梢桂子，醉了还醒却。北窗高卧，莫教啼鸟惊著。'),
	(5501, 255, '如梦令·门外绿阴千顷', '门外绿阴千顷，两两黄鹂相应。睡起不胜情，行到碧梧金井。人静，人静。风动一庭花影。'),
	(5502, 545, '千秋岁·咏夏景', '楝花飘砌。蔌蔌清香细。梅雨过，萍风起。情随湘水远，梦绕吴峰翠。琴书倦，鹧鸪唤起南窗睡。 密意无人寄。幽恨凭谁洗。修竹畔，疏帘里。歌余尘拂扇，舞罢风掀袂。人散后，一钩淡月天如水。'),
	(5503, 451, '满庭芳·碧水惊秋', '碧水惊秋，黄云凝暮，败叶零乱空阶。洞房人静，斜月照徘徊。又是重阳近也，几处处，砧杵声催。西窗下，风摇翠竹，疑是故人来。 伤怀。增怅望，新欢易失，往事难猜。问篱边黄菊，知为谁开。谩道愁须殢酒，酒未醒、愁已先回。凭阑久，金波渐转，白露点苍苔。'),
	(5504, 451, '水龙吟·小楼连苑横空', '小楼连苑横空，下窥绣毂雕鞍骤。朱帘半卷，单衣初试，清明时候。破暖轻风，弄晴微雨，欲无还有。卖花声过尽，斜阳院落；红成阵，飞鸳甃。 玉佩丁东别后。怅佳期、参差难又。名韁利锁，天还知道，和天也瘦。花下重门，柳边深巷，不堪回首。念多情、但有当时皓月，向人依旧。'),
	(5505, 494, '采桑子·润州多景楼与孙巨源相遇', '多情多感仍多病，多景楼中。尊酒相逢。乐事回头一笑空。 停杯且听琵琶语，细捻轻拢。醉脸春融。斜照江天一抹红。'),
	(5506, 250, '鹧鸪天·守得莲开结伴游', '守得莲开结伴游，约开萍叶上兰舟。来时浦口云随棹，采罢江边月满楼。 花不语，水空流，年年拚得为花愁。明朝万一西风动，争向朱颜不耐秋。'),
	(5507, 409, '千秋岁引·秋景', '别馆寒砧，孤城画角，一派秋声入寥廓。东归燕从海上去，南来雁向沙头落。楚台风，庾楼月，宛如昨。 无奈被些名利缚，无奈被他情担阁！可惜风流总闲却！当初漫留华表语，而今误我秦楼约。梦阑时，酒醒后，思量着。'),
	(5508, 352, '踏莎行', '雨霁风光，春分天气。千花百卉争明媚。画梁新燕一双双，玉笼鹦鹉愁孤睡。 薜荔依墙，莓苔满地。青楼几处歌声丽。蓦然旧事上心来，无言敛皱眉山翠。'),
	(5509, 594, '武陵春·人道有情须有梦', '人道有情须有梦，无梦岂无情？夜夜相思直到明，有梦怎生成？ 伊若忽然来梦里，邻笛又还惊。笛里声声不忍听，浑是断肠声。'),
	(5510, 244, '卜算子·赠乐婉杭妓', '相逢情便深，恨不相逢早。识尽千千万万人，终不似、伊家好。 别你登长道。转更添烦恼。楼外朱楼独倚阑，满目围芳草。'),
	(5511, 660, '江城子·中秋早雨晚晴', '中秋佳月最端圆。老痴顽。见多番。杯酒相延，今夕不应慳。残雨如何妨乐事，声淅淅，点斑斑。 天应有意故遮阑。拍人间。等闲看。好处时光，须用著些难。直待黄昏风卷霁，金滟滟，玉团团。'),
	(5512, 591, '踏莎行·庚戌中秋后二夕带湖篆冈小酌', '夜月楼台，秋香院宇。笑吟吟地人来去。是谁秋到便凄凉？当年宋玉悲如许。 随分杯盘，等闲歌舞。问他有甚堪悲处？思量却也有悲时，重阳节近多风雨。'),
	(5513, 591, '瑞鹤仙·赋梅', '雁霜寒透幕。正护月云轻，嫩冰犹薄。溪奁照梳掠。想含香弄粉，艳妆难学。玉肌瘦弱。更重重、龙绡衬著。倚东风，一笑嫣然，转盼万花羞落。 寂寞。家山何在，雪后园林，水边楼阁。瑶池旧约。鳞鸿更仗谁托。粉蝶儿只解，寻桃觅柳，开遍南枝未觉。但伤心，冷落黄昏，数声画角。'),
	(5514, 591, '丑奴儿·烟芜露麦荒池柳', '烟迷露麦荒池柳，洗雨烘晴。洗雨烘晴。一样春风几样青。 提壶脱袴催归去，万恨千情。万恨千情。各自无聊各自鸣。'),
	(5515, 559, '眼儿媚·玉京曾忆昔繁华', '玉京曾忆昔繁华。万里帝王家。琼林玉殿，朝喧弦管，暮列笙琶。 花城人去今萧索，春梦绕胡沙。家山何处，忍听羌笛，吹彻梅花。'),
	(5516, 494, '满庭芳·归去来兮', '元丰七年四月一日，余将去黄移汝，留别雪堂邻里二三君子，会仲览自江东来别，遂书以遗之。 归去来兮，吾归何处？万里家在岷峨。百年强半，来日苦无多。坐见黄州再闰，儿童尽楚语吴歌。山中友，鸡豚社酒，相劝老东坡。 云何，当此去，人生底事，来往如梭。待闲看秋风，洛水清波。好在堂前细柳，应念我，莫剪柔柯。仍传语，江南父老，时与晒渔蓑。'),
	(5517, 251, '山亭柳·赠歌者', '家住西秦。赌博艺随身。花柳上、斗尖新。偶学念奴声调，有时高遏行云。蜀锦缠头无数，不负辛勤。 数年来往咸京道，残杯冷炙谩消魂。衷肠事、托何人。若有知音见采，不辞遍唱阳春。一曲当筵落泪，重掩罗巾。'),
	(5518, 251, '踏莎行·碧海无波', '碧海无波，瑶台有路。思量便合双飞去。当时轻别意中人，山长水远知何处。 绮席凝尘，香闺掩雾。红笺小字凭谁附。高楼目尽欲黄昏，梧桐叶上萧萧雨。'),
	(5519, 392, '卜算子·风雨送人来', '风雨送人来，风雨留人住。草草杯盘话别离，风雨催人去。 泪眼不曾晴，眉黛愁还聚。明日相思莫上楼，楼上多风雨。'),
	(5520, 289, '浣溪沙', '为怯＿云挟暑飞。嫩凉故故著征衣。江风吹雨过楼西。 未必男儿生不遇，时来咳唾是珠玑。功名终岂壮心违。'),
	(5521, 633, '朝中措·梅', '幽姿不入少年场。无语只凄凉。一个飘零身世，十分冷淡心肠。 江头月底，新诗旧梦，孤恨清香。任是春风不管，也曾先识东皇。'),
	(5522, 693, '诉衷情·小桃灼灼柳鬖鬖', '小桃灼灼柳鬖鬖，春色满江南。雨晴风暖烟淡，天气正醺酣。 山泼黛，水挼蓝，翠相搀。歌楼酒旆，故故招人，权典青衫。'),
	(5523, 352, '浪淘沙', '花外倒金翘。饮散无憀。柔桑蔽日柳迷条。此地年时曾一醉，还是春朝。 今日举轻桡。帆影飘飘。长亭回首短亭遥。过尽长亭人更远，特地魂销。'),
	(5524, 161, '满江红·喜遇重阳', '喜遇重阳，更佳酿今朝新熟。 见碧水丹山，黄芦苦竹。 头上尽教添白发，鬓边不可无黄菊。 愿樽前长叙弟兄情，如金玉。 统豺虎，御边幅，号令明，军威肃。 中心愿，平虏保民安国。日月常悬忠烈胆，风尘障却奸邪目。望天王降诏，早招安，心方足。'),
	(5525, 272, '菩萨蛮（回文）', '晚红飞尽春寒浅。浅寒春尽飞红晚。尊酒绿阴繁。繁阴绿酒尊。 老仙诗句好。好句诗仙老。长恨送年芳。芳年送恨长。'),
	(5526, 149, '齐天乐·蟋蟀', '丙辰岁，与张功父会饮张达可之堂。闻屋壁间蟋蟀有声，功父约予同赋，以授歌者。功父先成，辞甚美。予裴回末利花间，仰见秋月，顿起幽思，寻亦得此。蟋蟀，中都呼为促织，善斗。好事者或以三二十万钱致一枚，镂象齿为楼观以贮之。 庾郎先自吟愁赋，凄凄更闻私语。露湿铜铺，苔侵石井，都是曾听伊处。哀音似诉。正思妇无眠，起寻机杼。曲曲屏山，夜凉独自甚情绪？ 西窗又吹暗雨。为谁频断续，相和砧杵？候馆迎秋，离宫吊月，别有伤心无数。豳诗漫与。笑篱落呼灯，世间儿女。写入琴丝，一声声更苦。'),
	(5527, 591, '菩萨蛮·送曹君之庄所', '人间岁月堂堂去，劝君快上青云路。圣处一灯传，工夫萤雪边。 麴生风味恶，辜负西窗约。沙岸片帆开，寄书无雁来。'),
	(5528, 329, '贺新郎', '具能歌张仲宗目尽青天等句，音韵洪畅，听之慨然。戏用仲宗韵呈张君量府判 薄暮垂虹去。正江天、残霞冠日，乱鸿遵渚。万顷云涛风浩荡，笑整羽轮飞渡。问弱水、神仙何处。翳凤骑麟思往事，记朝元、金殿闻钟鼓。环佩响，翠鸾舞。 梦中失却江南路。待西风、长城饮马，朔庭张弩。目尽青天何时到，赢得儿童好语。怅未复、长陵抔土。西子五湖归去后，泛仙舟、尚许寻盟否。风袂逐，片帆举。'),
	(5529, 271, '减字木兰花·春怨', '独行独坐，独唱独酬还独卧。伫立伤神，无奈轻寒著摸人。 此情谁见，泪洗残妆无一半。愁病相仍，剔尽寒灯梦不成。'),
	(5530, 669, '霜天晓角·题采石蛾眉亭', '倚天绝壁，直下江千尺。天际两蛾凝黛，愁与恨，几时极！ 暮潮风正急，酒阑闻塞笛。试问谪仙何处？青山外，远烟碧。'),
	(5531, 185, '卜算子·风露湿行云', '风露湿行云，沙水迷归艇。卧看明河月满空，斗挂苍山顶。 万古只青天，多事悲人境。起舞闻鸡酒未醒，潮落秋江冷。'),
	(5532, 185, '石州慢·寒水依痕', '寒水依痕，春意渐回，沙际烟阔。溪梅晴照生香，冷蕊数枝争发。天涯旧恨，试看几许消魂，长亭门外山重叠。不尽眼中青，是愁来时节。 情切。画楼深闭，想见东风，暗销肌雪。辜负枕前云雨，尊前花月。心期切处，更有多少凄凉，殷勤留与归时说。到得却相逢，恰经年离别。'),
	(5533, 693, '定风波·次高左藏使君韵', '万里黔中一漏天，屋居终日似乘船。及至重阳天也霁，催醉，鬼门关外蜀江前。 莫笑老翁犹气岸，君看，几人黄菊上华颠？戏马台南追两谢，驰射，风流犹拍古人肩。'),
	(5534, 494, '菩萨蛮·回文秋闺怨', '井桐双照新妆冷。冷妆新照双桐井。羞对井花愁。愁花井对羞。 影孤怜夜永。永夜怜孤影。楼上不宜秋。秋宜不上楼。'),
	(5535, 251, '浣溪沙', '绿叶红花媚晓烟。黄蜂金蕊欲披莲。水风深处懒回船。 可惜异香珠箔外，不辞清唱玉尊前。使星归觐九重天。'),
	(5536, 344, '归朝欢·别岸扁舟三两只', '别岸扁舟三两只。葭苇萧萧风淅淅。沙汀宿雁破烟飞，溪桥残月和霜白。渐渐分曙色。路遥山远多行役。往来人，只轮双桨，尽是利名客。 一望乡关烟水隔。转觉归心生羽翼。愁云恨雨两牵萦，新春残腊相催逼。岁华都瞬息。浪萍风梗诚何益。归去来，玉楼深处，有个人相忆。'),
	(5537, 591, '木兰花慢·席上送张仲固帅兴元', '汉中开汉业，问此地、是耶非。想剑指三秦，君王得意，一战东归。追亡事、今不见，但山川满目泪沾衣。落日胡尘未断，西风塞马空肥。 一编书是帝王师。小试去征西。更草草离筵，匆匆去路，愁满旌旗。君思我、回首处，正江涵秋影雁初飞。安得车轮四角，不堪带减腰围。'),
	(5538, 278, '谢池春·残寒销尽', '残寒销尽，疏雨过，清明后。花径敛余红，风沼萦新皱。乳燕穿庭户，飞絮沾襟袖。正佳时，仍晚昼。著人滋味，真个浓如酒。 频移带眼，空只恁、厌厌瘦。不见又思量，见了还依旧。为问频相见，何似长相守？天不老，人未偶。且将此恨，分付庭前柳。'),
	(5539, 494, '临江仙·送王缄', '忘却成都来十载，因君未免思量。凭将清泪洒江阳。故山知好在，孤客自悲凉。 坐上别愁君未见，归来欲断无肠。殷勤且更尽离觞。此身如传舍，何处是吾乡。'),
	(5540, 251, '诉衷情·芙蓉金菊斗馨香', '芙蓉金菊斗馨香。天气欲重阳。远村秋色如画，红树间疏黄。 流水淡，碧天长。路茫茫。凭高目断。鸿雁来时，无限思量。'),
	(5541, 344, '浪淘沙慢·梦觉透窗风一线', '梦觉透窗风一线，寒灯吹息。那堪酒醒，又闻空阶，夜雨频滴。嗟因循、久作天涯客。负佳人、几许盟言，便忍把、从前欢会，陡顿翻成忧戚。 愁极，再三追思，洞房深处，几度饮散歌阑，香暖鸳鸯被。岂暂时疏散，费伊心力。殚云尤雨，有万般千种，相怜相惜。恰到如今，天长漏永，无端自家疏隔。知何时、却拥秦云态？原低帏昵枕，轻轻细说与，江乡夜夜，数寒更思忆。'),
	(5542, 91, '双双燕·咏燕', '过春社了，度帘幕中间，去年尘冷。差池欲住，试入旧巢相并。还相雕梁藻井。又软语、商量不定。飘然快拂花梢，翠尾分开红影。 芳径。芹泥雨润。爱贴地争飞，竞夸轻俊。红楼归晚，看足柳昏花暝。应自栖香正稳。便忘了、天涯芳信。愁损翠黛双蛾，日日画阑独凭。'),
	(5543, 591, '永遇乐', '章因及之 投老空山，万松手种，政尔堪叹。何日成阴，吾年有几，似见儿孙晚。古来池馆，云烟草棘，长使后人凄断。想当年、良辰已恨，夜阑酒空人散。 停云高处，谁知老子，万事不关心眼。梦觉东窗，聊复尔耳，起欲题书简。霎时风怒，倒翻笔砚，天也只教吾懒。又何事，催诗雨急，片云斗暗。'),
	(5544, 188, '浣溪沙·中秋坐上十八客', '同是瀛洲册府仙，今朝聊结社中莲，胡笳按拍酒如川。 唤起封姨清晚暑，更将荔子荐新圆，从今三夜看婵娟。'),
	(5545, 334, '华胥引', '征车将动，愁不成歌，对颦翠叶。静掩兰房，香铺卧鸭烟罢唼。别后羞看霓裳，更把筝休轧。频数更筹，乍寒孤枕偏怯。 尝为霜髭，弄纤纤、向人轻镊。旧词新句，幽窗时时并阅。药饵衣衾，愁顿放、一番行箧。朝晚归家，又烦春笋重叠。'),
	(5546, 591, '丑奴儿·年年索尽梅花笑', '年年索尽梅花笑，疏影黄昏。疏影黄昏。香满东风月一痕。 清诗冷落无人寄，雪艳冰魂。雪艳冰魂。浮玉溪头烟树村。'),
	(5547, 498, '蝶恋花·春涨一篙添水面', '春涨一篙添水面。芳草鹅儿，绿满微风岸。画舫夷犹湾百转。横塘塔近依前远。 江国多寒农事晚。村北村南，谷雨才耕遍。秀麦连冈桑叶贱。看看尝面收新茧。'),
	(5548, 633, '沁园春·孤鹤归飞', '孤鹤归飞，再过辽天，换尽旧人。念累累枯冢，茫茫梦境，王侯蝼蚁，毕竟成尘。载酒园林，寻花巷陌，当日何曾轻负春。流年改，叹围腰带剩，点鬓霜新。 交亲散落如云。又岂料如今馀此身。幸眼明身健，茶甘饭软，非惟我老，更有人贫。躲尽危机，消残壮志，短艇湖中闲采莼。吾何恨，有渔翁共醉，溪友为邻。'),
	(5549, 397, '酒泉子·长忆西山', '长忆西山，灵隐寺前三竺后，冷泉亭上旧曾游，三伏似清秋。 白猿时见攀高树，长啸一声何处去？别来几向画图看，终是欠峰峦！'),
	(5550, 149, '长亭怨慢·渐吹尽', '余颇喜自制曲。初率意为长短句，然后协以律，故前后阕多不同。桓大司马云：“昔年种柳，依依汉南。今看摇落，凄怆江潭：树犹如此，人何以堪？”此语余深爱之。 渐吹尽，枝头香絮，是处人家，绿深门户。远浦萦回，暮帆零乱向何许？阅人多矣，谁得似长亭树？树若有情时，不会得青青如此！ 日暮，望高城不见，只见乱山无数。韦郎去也，怎忘得、玉环分付：第一是早早归来，怕红萼无人为主。算空有并刀，难剪离愁千缕。'),
	(5551, 591, '水调歌头·盟鸥', '带湖吾甚爱，千丈翠奁开。先生杖屦无事，一日走千回。凡我同盟鸥鹭，今日既盟之后，来往莫相猜。白鹤在何处，尝试与偕来。 破青萍，排翠藻，立苍苔。窥鱼笑汝痴计，不解举吾杯。废沼荒丘畴昔。明月清风此夜，人世几欢哀。东岸绿阴少，杨柳更须栽。'),
	(5552, 99, '秦楼月·芳菲歇', '芳菲歇，故园目断伤心切。伤心切，无边烟水，无穷山色。 可堪更近乾龙节，眼中泪尽空啼血。空啼血，子规声外，晓风残月。'),
	(5553, 305, '鹧鸪天·寒日萧萧上锁窗', '寒日萧萧上锁窗，梧桐应恨夜来霜。酒阑更喜团茶苦，梦断偏宜瑞脑香。 秋已尽，日犹长，仲宣怀远更凄凉。不如随分尊前醉，莫负东篱菊蕊黄。'),
	(5554, 494, '南歌子·游赏', '山与歌眉敛，波同醉眼流。游人都上十三楼。不羡竹西歌吹、古扬州。 菰黍连昌歜，琼彝倒玉舟。谁家水调唱歌头。声绕碧山飞去、晚云留。'),
	(5555, 352, '忆秦娥', '十五六，脱罗裳，长恁黛眉蹙。红玉暖，入人怀，春困熟。 展香裀，帐前明画烛。眼波长，斜浸鬓云绿。看不足。苦残宵、更漏促。'),
	(5556, 352, '生查子·含羞整翠鬟', '含羞整翠鬟，得意频相顾。雁柱十三弦，一一春莺语。 娇云容易飞，梦断知何处。深院锁黄昏，阵阵芭蕉雨。'),
	(5557, 352, '采桑子·天容水色西湖好', '天容水色西湖好，云物俱鲜。鸥鹭闲眠，应惯寻常听管弦。 风清月白偏宜夜，一片琼田。谁羡骖鸾，人在舟中便是仙。'),
	(5558, 251, '木兰花·池塘水绿风微暖', '池塘水绿风微暖。记得玉真初见面。重头歌韵响铮琮，入破舞腰红乱旋。 玉钩阑下香阶畔。醉后不知斜日晚。当时共我赏花人，点检如今无一半。'),
	(5559, 186, '诉衷情·花前月下暂相逢', '花前月下暂相逢。苦恨阻从容。何况酒醒梦断，花谢月朦胧。 花不尽，月无穷。两心同。此时愿作，杨柳千丝，绊惹春风。'),
	(5560, 344, '采莲令·月华收', '月华收，云淡霜天曙。西征客、此时情苦。翠娥执手送临歧，轧轧开朱户。千娇面、盈盈伫立，无言有泪，断肠争忍回顾。 一叶兰舟，便恁急桨凌波去。贪行色、岂知离绪，万般方寸，但饮恨，脉脉同谁语。更回首、重城不见，寒江天外，隐隐两三烟树。'),
	(5561, 607, '昭君怨·梅花', '道是花来春未。道是雪来香异。竹外一枝斜。野人家。 冷落竹篱茅舍。富贵玉堂琼榭。两地不同栽。一般开。'),
	(5562, 209, '秋蕊香·帘幕疏疏风透', '帘幕疏疏风透。一线香飘金兽。朱阑倚遍黄昏后。廊上月华如昼。 别离滋味浓于酒。著人瘦。此情不及墙东柳。春色年年如旧。'),
	(5563, 112, '莺啼序·春晚感怀', '　　残寒正欺病酒，掩沉香绣户。燕来晚、飞入西城，似说春事迟暮。画船载、清明过却，晴烟冉冉吴宫树。念羁情、游荡随风，化为轻絮。 　　十载西湖，傍柳系马，趁娇尘软雾。溯红渐招入仙溪，锦儿偷寄幽素，倚银屏、春宽梦窄，断红湿、歌纨金缕。暝堤空，轻把斜阳，总还鸥鹭。 　　幽兰旋老，杜若还生，水乡尚寄旅。别后访、六桥无信，事往花委，瘗玉埋香，几番风雨。长波妒盼，遥山羞黛，渔灯分影春江宿。记当时、短楫桃根渡，青楼仿佛，临分败壁题诗，泪墨惨淡尘土。危亭望极，草色天涯，叹鬓侵半苎。暗点检、离痕欢唾，尚染鲛绡，亸凤迷归，破鸾慵舞。殷勤待写，书中长恨，蓝霞辽海沉过雁。漫相思、弹入哀筝柱。伤心千里江南，怨曲重招，断魂在否？'),
	(5564, 149, '惜红衣·吴兴荷花', '枕簟邀凉，琴书换日，睡余无力。细洒冰泉，并刀破甘碧。墙头唤酒，谁问讯、城南诗客。岑寂，高柳晚蝉，说西风消息。 虹梁水陌，鱼浪吹香，红衣半狼藉。维舟试望，故国渺天北。可惜柳边沙外，不共美人游历。问甚时同赋，三十六陂秋色？'),
	(5565, 591, '沁园春·带湖新居将成', '三径初成，鹤怨猿惊，稼轩未来。甚云山自许，平生意气；衣冠人笑，抵死尘埃。意倦须还，身闲贵早，岂为莼羹鲈脍哉。秋江上，看惊弦雁避，骇浪船回。 东冈更葺茅斋。好都把轩窗临水开。要小舟行钓，先应种柳；疏篱护竹，莫碍观梅。秋菊堪餐，春兰可佩，留待先生手自栽。沉吟久，怕君恩未许，此意徘徊。'),
	(5566, 426, '南柯子·山冥云阴重', '山冥云阴重，天寒雨意浓。数枝幽艳湿啼红。莫为惜花惆怅对东风。 蓑笠朝朝出，沟塍处处通。人间辛苦是三农。要得一犁水足望年丰。'),
	(5567, 271, '谒金门·春半', '春已半。触目此情无限。十二阑干闲倚遍。愁来天不管。 好是风和日暖。输与莺莺燕燕。满院落花帘不卷。断肠芳草远。'),
	(5568, 94, '点绛唇·绍兴乙卯登绝顶小亭', '缥缈危亭，笑谈独在千峰上。与谁同赏。万里横烟浪。 老去情怀，犹作天涯想。空惆怅。少年豪放。莫学衰翁样。'),
	(5569, 549, '子夜歌·三更月', '三更月，中庭恰照梨花雪。梨花雪，不胜凄断，杜鹃啼血。 王孙何许音尘绝，柔桑陌上吞声别。吞声别，陇头流水，替人呜咽。'),
	(5570, 451, '沁园春·宿霭迷空', '宿霭迷空，腻云笼日，昼景渐长。正兰皋泥润，谁家燕喜；蜜脾香少，触处蜂忙。尽日无人帘幕挂，更风递、游丝时过墙。微雨后，有桃愁杏怨，红泪淋浪。 风流存心易感，但依依伫立，回尽柔肠。念小奁瑶鉴，重匀绛蜡；玉龙金斗，时熨沉香。柳下相将游冶处，便回首、青楼成异乡。相忆事，纵蛮笺万叠，难写微茫。'),
	(5571, 409, '伤春怨·雨打江南树', '雨打江南树。一夜花开无数。绿叶渐成阴，下有游人归路。 与君相逢处。不道春将暮。把酒祝东风，且莫恁、匆匆去。'),
	(5572, 251, '踏莎行·祖席离歌', '祖席离歌，长亭别宴。香尘已隔犹回面。居人匹马映林嘶，行人去棹依波转。 画阁魂消，高楼目断。斜阳只送平波远。无穷无尽是离愁，天涯地角寻思遍。'),
	(5573, 251, '喜迁莺·花不尽', '花不尽，柳无穷。应与我情同。觥船一棹百分空。何处不相逢。 朱弦悄。知音少。天若有情应老。劝君看取利名场。今古梦茫茫。'),
	(5574, 226, '满庭芳·汉上繁华', '汉上繁华，江南人物，尚遗宣政风流。绿窗朱户，十里烂银钩。一旦刀兵齐举，旌旗拥、百万貔貅。长驱入，歌楼舞榭，风卷落花愁。 清平三百载，典章文物，扫地俱休。幸此身未北，犹客南州。破鉴徐郎何在？空惆怅、相见无由。从今后，断魂千里，夜夜岳阳楼。'),
	(5575, 404, '点绛唇·素香丁香', '落木萧萧，琉璃叶下琼葩吐。素香柔树。雅称幽人趣。 无意争先，梅蕊休相妒。含春雨。结愁千绪。似忆江南主。'),
	(5576, 335, '水调歌头·登多景楼', '寒眼乱空阔，客意不胜秋。强呼斗酒，发兴特上最高楼。舒卷江山图画，应答龙鱼悲啸，不暇顾诗愁。风露巧欺客，分冷入衣裘。 忽醒然，成感慨，望神州。可怜报国无路，空白一分头。都把平生意气，只做如今憔悴，岁晚若为谋。此意仗江月，分付与沙鸥。'),
	(5577, 267, '卜算子·旅雁向南飞', '旅雁向南飞，风雨群初失。饥渴辛勤两翅垂，独下寒汀立。 鸥鹭苦难亲，矰缴忧相逼。云海茫茫无处归，谁听哀鸣急。'),
	(5578, 130, '西河·大石金陵', '佳丽地。南朝盛事谁记。山围故国绕清江，髻鬓对起。怒涛寂寞打孤城，风樯遥度天际。 断崖树，犹倒倚。莫愁艇子曾系。空余旧迹郁苍苍，雾沈半垒。夜深月过女墙来，伤心东望淮水。 酒旗戏鼓甚处市。想依稀、王谢邻里。燕子不知何世。入寻常、巷陌人家，相对如说兴亡，斜阳里。'),
	(5579, 282, '洞仙歌·雪云散尽', '一年春物，惟梅柳间意味最深。至莺花烂漫时，则春已衰迟，使人无复新意。予作《洞仙歌》，使探春者歌之，无后时之悔。 雪云散尽，放晓晴池院。杨柳于人便青眼。更风流多处，一点梅心，相映远，约略颦轻笑浅。 一年春好处，不在浓芳，小艳疏香最娇软。到清明时候，百紫千红，花正乱，已失春风一半。早占取韶光共追游，但莫管春寒，醉红自暖。'),
	(5580, 352, '采桑子·残霞夕照西湖好', '残霞夕照西湖好，花坞苹汀，十顷波平，野岸无人舟自横。 西南月上浮云散，轩槛凉生。莲芰香清。水面风来酒面醒。'),
	(5581, 451, '鹧鸪天·枝上流莺和泪闻', '枝上流莺和泪闻，新啼痕间旧啼痕。一春鱼鸟无消息，千里关山劳梦魂。 无一语，对芳尊。安排肠断到黄昏。甫能炙得灯儿了，雨打梨花深闭门。'),
	(5582, 236, '满江红·和王夫人满江红韵以庶几後山妾薄命之意', '燕子楼中，又捱过、几番秋色。相思处、青年如梦，乘鸾仙阙。肌玉暗消衣带缓，泪珠斜透花钿侧。最无端、蕉影上窗纱，青灯歇。 曲池合，高台灭。人间事，何堪说。向南阳阡上，满襟有血。世态便如翻覆雨，妾身元是分明月。笑乐昌、一段好风流，菱花缺。'),
	(5583, 119, '浪淘沙·岸柳可藏鸦', '岸柳可藏鸦。路转溪斜。忘机鸥鹭立汀沙。咫尺钟山迷望眼，一半云遮。 临水整乌纱。两鬓苍华。故乡心事在天涯。几日不来春便老，开尽桃花。'),
	(5584, 202, '八声甘州·记玉关踏雪事清游', '辛卯岁，沈尧道同余北归，各处杭、越。逾岁，尧道来问寂寞，语笑数日。又复别去。赋此曲，并寄赵学舟。 记玉关踏雪事清游，寒气脆貂裘。傍枯林古道，长河饮马，此意悠悠。短梦依然江表，老泪洒西州。一字无题处，落叶都愁。 载取白云归去，问谁留楚佩，弄影中洲？折芦花赠远，零落一身秋。向寻常、野桥流水，待招来，不是旧沙鸥。空怀感，有斜阳处，却怕登楼。'),
	(5585, 112, '西江月·赋瑶圃青梅枝上晚花', '枝袅一痕雪在，叶藏几豆春浓。玉奴最晚嫁东风。来结梨花幽梦。 香力添熏罗被，瘦肌犹怯冰绡。绿阴青子老溪桥。羞见东邻娇小。'),
	(5586, 241, '水调歌头·平山堂用东坡韵', '秋雨一何碧，山色倚晴空。江南江北愁思，分付酒螺红。芦叶蓬舟千重，菰菜莼羹一梦，无语寄归鸿。醉眼渺河洛，遗恨夕阳中。 苹洲外，山欲暝，敛眉峰。人间俯仰陈迹，叹息两仙翁。不见当时杨柳，只是从前烟雨，磨灭几英雄。天地一孤啸，匹马又西风。'),
	(5587, 117, '鹊桥仙·扁舟昨泊', '扁舟昨泊，危亭孤啸，目断闲云千里。前山急雨过溪来，尽洗却、人间暑气。 暮鸦木末，落凫天际，都是一团秋意。痴儿騃女贺新凉，也不道、西风又起。'),
	(5588, 591, '蝶恋花·月下醉书雨岩石浪', '九畹芳菲兰佩好。空谷无人，自怨蛾眉巧。宝瑟泠泠千古调。朱丝弦断知音少。 冉冉年华吾自老。水满汀洲，何处寻芳草。唤起湘累歌未了。石龙舞罢松风晓。'),
	(5589, 99, '阮郎归·绍兴乙卯大雪行鄱阳道中', '江南江北雪漫漫，遥知易水寒。同云深处望三关，断肠山又山。 天可老，海能翻，消除此恨难。频闻遣使问平安，几时鸾辂还？'),
	(5590, 494, '醉翁操·琅然', '琅琊幽谷，山水奇丽，泉鸣空涧，若中音会，醉翁喜之，把酒临听，辄欣然忘归。既去十余年，而好奇之士沈遵闻之往游，以琴写其声，曰《醉翁操》，节奏疏宕而音指华畅，知琴者以为绝伦。然有其声而无其辞。翁虽为作歌，而与琴声不合。又依《楚词》作《醉翁引》，好事者亦倚其辞以制曲。虽粗合韵度而琴声为词所绳约，非天成也。后三十余年，翁既捐馆舍，遵亦没久矣。有庐山玉涧道人崔闲，特妙于琴，恨此曲之无词，乃谱其声，而请于东坡居士以补之云。 琅然。清圜。谁弹。响空山。 无言。惟翁醉中知其天。 月明风露娟娟。人未眠。 荷蒉过山前。曰有心也哉此贤。 醉翁啸咏，声和流泉。 醉翁去后，空有朝吟夜怨。 山有时而童颠，水有时而回川。 思翁无岁年，翁今为飞仙。 此意在人间，试听徽外三两弦。'),
	(5591, 494, '浣溪沙·万顷风涛不记苏', '万顷风涛不记苏。雪晴江上麦千车。但令人饱我愁无。 翠袖倚风萦柳絮，绛唇得酒烂樱珠。尊前呵手镊霜须。'),
	(5592, 251, '浣溪沙·小阁重帘有燕过', '小阁重帘有燕过。晚花红片落庭莎。曲阑干影入凉波。 一霎好风生翠幕，几回疏雨滴圆荷。酒醒人散得愁多。'),
	(5593, 525, '鹊桥仙·说盟说誓', '说盟说誓。说情说意。动便春愁满纸。多应念得脱空经，是那个、先生教底。 不茶不饭，不言不语，一味供他憔悴。相思已是不曾闲，又那得、工夫咒你。'),
	(5594, 463, '水龙吟·燕忙莺懒芳残', '燕忙莺懒芳残，正堤上、柳花飘坠。轻飞乱舞，点画青林，全无才思。闲趁游丝，静临深院，日长门闭。傍珠帘散漫，垂垂欲下，依前被、风扶起。 兰帐玉人睡觉，怪春衣、雪沾琼缀。绣床旋满，香毬无数，才圆却碎。时见蜂儿，仰粘轻粉，鱼吞池水。望章台路杳，金鞍游荡，有盈盈泪。(香毬 一作：香球)'),
	(5595, 188, '浣溪沙·霜日明霄水蘸空', '霜日明霄水蘸空。鸣鞘声里绣旗红。淡烟衰草有无中。 万里中原烽火北，一尊浊酒戍楼东。酒阑挥泪向悲风。'),
	(5596, 379, '如梦令', '雨后轻寒天气。玉酒中人小醉。乍报一番秋，晚＿清凉如水。忺睡。忺睡。窗在芭蕉叶底。'),
	(5597, 633, '南乡子·归梦寄吴樯', '归梦寄吴樯，水驿江程去路长。想见芳洲初系缆，斜阳，烟树参差认武昌。 愁鬓点新霜，曾是朝衣染御香。重到故乡交旧少，凄凉，却恐他乡胜故乡。'),
	(5598, 99, '相见欢', '亭亭秋水芙蓉。翠围中。又是一年风露、笑相逢。 天机畔。云锦乱。思无穷。路隔银河犹解、嫁西风。'),
	(5599, 352, '渔家傲·花底忽闻敲两桨', '花底忽闻敲两桨。逡巡女伴来寻访。酒盏旋将荷叶当。莲舟荡。时时盏里生红浪。 花气酒香清厮酿。花腮酒面红相向。醉倚绿阴眠一饷。惊起望。船头阁在沙滩上。'),
	(5600, 218, '六丑·杨花', '似东风老大，那复有、当时风气。有情不收，江山身是寄。浩荡何世。但忆临官道，暂来不住，便出门千里。痴心指望回风坠。扇底相逢，钗头微缀。他家万条千缕，解遮亭障驿，不隔江水。 瓜洲曾舣，等行人岁岁。日下长秋，城乌夜起。帐庐好在春睡。共飞归湖上，草青无地。愔愔雨、春心如腻。欲待化、丰乐楼前，青门都废。何人念、流落无几。点点抟作，雪绵松润，为君裛泪。'),
	(5601, 47, '一剪梅·唱到阳关第四声', '唱到阳关第四声。香带轻分。罗带轻分。杏花时节雨纷纷。山绕孤村。水绕孤村。 更没心情共酒尊。春衫香满，空有啼痕。一般离思两销魂。马上黄昏。楼上黄昏。'),
	(5602, 72, '柳梢青·送卢梅坡', '泛菊杯深，吹梅角远，同在京城。聚散匆匆，云边孤雁，水上浮萍。 教人怎不伤情。觉几度、魂飞梦惊。后夜相思，尘随马去，月逐舟行。'),
	(5603, 498, '浣溪沙·江村道中', '十里西畴熟稻香，槿花篱落竹丝长，垂垂山果挂青黄。 浓雾知秋晨气润，薄云遮日午阴凉，不须飞盖护戎装。'),
	(5604, 105, '如梦令', '珠阁雨帘高卷。望断碧梧墙院。短梦有幽寻，晓枕一峰云乱。谁见。谁见。风散菊英千片。'),
	(5605, 518, '苍梧谣·天', '天。休使圆蟾照客眠。人何在，桂影自婵娟。'),
	(5606, 305, '庆清朝·禁幄低张', '禁幄低张，彤阑巧护，就中独占残春。容华淡伫，绰约俱见天真。待得群花过后，一番风露晓妆新。妖娆艳态，妒风笑月，长殢东君。 东城边，南陌上，正日烘池馆，竟走香轮。绮筵散日，谁人可继芳尘。更好明光宫殿，几枝先近日边匀。金尊倒，拚了尽烛，不管黄昏。'),
	(5607, 315, '望江南·江上雪', '江上雪，独立钓渔翁。箬笠但闻冰散响，蓑衣时振玉花空。图画若为工。 云水暮，归去远烟中。茅舍竹篱依小屿，缩鳊圆鲫入轻笼。欢笑有儿童。'),
	(5608, 255, '声声慢', '重檐飞峻，丽采横空，繁华壮观都城。云母屏开八面，人在青冥。凭阑瑞烟深处，望皇居、遥识蓬瀛。回环阁道，五花相斗，压尽旗亭。 歌酒长春不夜，金翠照罗绮，笑语盈盈。陆海人山辐輳，万国欢声。登临四时总好，况花朝、月白风清。丰年乐，岁熙熙、且醉太平。'),
	(5609, 250, '虞美人·曲阑干外天如水', '曲阑干外天如水。昨夜还曾倚。初将明月比佳期。长向月圆时候、望人归。 罗衣著破前香在。旧意谁教改。一春离恨懒调弦。犹有两行闲泪、宝筝前。'),
	(5610, 126, '点绛唇·访牟存叟南漪钓隐', '午梦初回，卷帘尽放春愁去。 昼长无侣。自对黄鹂语。 絮影苹香，春在无人处。 移舟去。未成新句。一砚梨花雨。'),
	(5611, 202, '渔歌子', '□□□□□求鱼。钓不得鱼还自如。尘事远，世人疏。何须更写绝交书。'),
	(5612, 112, '点绛唇·试灯夜初晴', '卷尽愁云，素娥临夜新梳洗。暗尘不起。酥润凌波地。 辇路重来，仿佛灯前事。情如水。小楼熏被。春梦笙歌里。'),
	(5613, 591, '菩萨蛮', '功名饱听儿童说。看公两眼明如月。万里勒燕然。老人书一编。 玉阶方寸地。好趁风云会。他日赤松游。依然万户侯。'),
	(5614, 633, '木兰花·立春日作', '三年流落巴山道，破尽青衫尘满帽。身如西瀼渡头云，愁抵瞿塘关上草。 春盘春酒年年好，试戴银旛判醉倒。今朝一岁大家添，不是人间偏我老。'),
	(5615, 185, '浣溪沙·山绕平湖波撼城', '山绕平湖波撼城。湖光倒影浸山青。水晶楼下欲三更。 雾柳暗时云度月，露荷翻处水流萤。萧萧散发到天明。'),
	(5616, 305, '浣溪沙·莫许杯深琥珀浓', '莫许杯深琥珀浓，未成沉醉意先融。疏钟已应晚来风。 瑞脑香消魂梦断，辟寒金小髻鬟松。醒时空对烛花红。'),
	(5617, 46, '青玉案', '小山遮断蓝桥路。恨短梦、难飞去。长记修眉萦曲度。约花开槛，映风招袖，总是怜渠处。 追欢我已伤迟暮。犹有多情旧时句。极目高楼千尺许。竹枝三唱，为君凄断，东日西边雨。'),
	(5618, 249, '盐角儿·亳社观梅', '开时似雪。谢时似雪。花中奇绝。香非在蕊，香非在萼，骨中香彻。 占溪风，留溪月。堪羞损、山桃如血。直饶更、疏疏淡淡，终有一般情别。'),
	(5619, 250, '蝶恋花·黄菊开时伤聚散', '黄菊开时伤聚散。曾记花前，共说深深愿。重见金英人未见。相思一夜天涯远。 罗带同心闲结遍。带易成双，人恨成双晚。欲写彩笺书别怨。泪痕早已先书满。'),
	(5620, 549, '眼儿媚·萧萧江上荻花秋', '萧萧江上荻花秋，做弄许多愁。半竿落日，两行新雁，一叶扁舟。 惜分长怕君先去，直待醉时休。今宵眼底，明朝心上，后日眉头。'),
	(5621, 464, '小重山·柳暗花明春事深', '柳暗花明春事深。小阑红芍药，已抽簪。雨馀风软碎鸣禽。迟迟日，犹带一分阴。 往事莫沉吟。身闲时序好，且登临。旧游无处不堪寻。无寻处，惟有少年心。'),
	(5622, 335, '贺新郎', '十日狂风雨。扫园林、红香万点，送春归去。独有荼＿开未到，留得一分春住。早杨柳、趁晴飞絮。可奈暖埃欺昼永，试薄罗衫子轻如雾。惊旧恨，到眉宇。 东风台榭知何处。问燕莺如今，尚有春光几许。可叹一年游赏倦，放得无情露醑。为唤取、扇歌裙舞。乞得风光还两眼，待为君、满把金杯举。扶醉玉，伴挥尘。'),
	(5623, 591, '丑奴儿', '此生自断天休问，独倚危楼。独倚危楼。不信人间别有愁。 君来正是眠时节，君且归休。君且归休。说与西风一任秋。'),
	(5624, 591, '满江红·暮春', '家住江南，又过了、清明寒食。花径里、一番风雨，一番狼籍。红粉暗随流水去，园林渐觉清阴密。算年年、落尽刺桐花，寒无力。 庭院静，空相忆。无说处，闲愁极。怕流莺乳燕，得知消息。尺素始今何处也，彩云依旧无踪迹。谩教人、羞去上层楼，平芜碧。'),
	(5625, 305, '新荷叶·薄露初零', '薄露初零，长宵共、永书分停。绕水楼台，高耸万丈蓬瀛。芝兰为寿，相辉映、簪笏盈庭。花柔玉净，捧觞别有娉婷。 鹤瘦松青，精神与、秋月争明。德行文章，素驰日下声名。东山高蹈，虽卿相、不足为荣。安石须起，要苏天下苍生。'),
	(5626, 305, '浣溪沙·髻子伤春慵更梳', '髻子伤春慵更梳。晚风庭院落梅初。淡云来往月疏疏。 玉鸭熏炉闲瑞脑，朱樱斗帐掩流苏。通犀还解辟寒无。'),
	(5627, 128, '踏莎行·情似游丝', '情似游丝，人如飞絮。泪珠阁定空相觑。一溪烟柳万丝垂，无因系得兰舟住。 雁过斜阳，草迷烟渚。如今已是愁无数。明朝且做莫思量，如何过得今宵去。'),
	(5628, 267, '好事近·摇首出红尘', '摇首出红尘，醒醉更无时节。活计绿蓑青笠，惯披霜冲雪。 晚来风定钓丝闲，上下是新月。千里水天一色，看孤鸿明灭。'),
	(5629, 371, '点绛唇·新月娟娟', '新月娟娟，夜寒江静山衔斗。起来搔首，梅影横窗瘦。 好个霜天，闲却传杯手。君知否？乱鸦啼后，归兴浓于酒。'),
	(5630, 94, '虞美人·雨后同干誉才卿置酒来禽花下作', '落花已作风前舞。又送黄昏雨。晓来庭院半残红。惟有游丝千丈、罥晴空。 殷勤花下同携手。更尽杯中酒。美人不用敛蛾眉。我亦多情无奈、酒阑时。'),
	(5631, 494, '南乡子·送述古', '回首乱山横。不见居人只见城。谁似临平山上塔，亭亭。迎客西来送客行。 归路晚风清。一枕初寒梦不成。今夜残灯斜照处，荧荧。秋雨晴时泪不晴。'),
	(5632, 28, '长相思·去年秋', '去年秋，今年秋，湖上人家乐复忧。西湖依旧流。 吴循州，贾循州，十五年前一转头。人生放下休。'),
	(5633, 423, '满江红·题南京夷山驿', '太液芙蓉，浑不似、旧时颜色。 曾记得、春风雨露，玉楼金阙。名播兰馨妃后里，晕潮莲脸君王侧。忽一声、鼙鼓揭天来，繁华歇。 龙虎散，风云灭。千古恨，凭谁说。对山河百二，泪盈襟血。客馆夜惊尘土梦，宫车晓碾关山月。问嫦娥、于我肯从容，同圆缺？'),
	(5634, 245, '青门饮·寄宠人', '胡马嘶风，汉旗翻雪，彤云又吐，一竿残照。古木连空，乱山无数，行尽暮沙衰草。星斗横幽馆，夜无眠、灯花空老。雾浓香鸭，冰凝泪烛，霜天难晓。 长记小妆才了，一杯未尽，离怀多少。醉里秋波，梦中朝雨，都是醒时烦恼。料有牵情处，忍思量、耳边曾道。甚时跃马归来，认得迎门轻笑。'),
	(5635, 202, '解连环·孤雁', '楚江空晚。怅离群万里，恍然惊散。自顾影、欲下寒塘，正沙净草枯，水平天远。写不成书，只寄得、相思一点。料因循误了，残毡拥雪，故人心眼。 谁怜旅愁荏苒。谩长门夜悄，锦筝弹怨。想伴侣、犹宿芦花，也曾念春前，去程应转。暮雨相呼，怕蓦地、玉关重见。未羞他、双燕归来，画帘半卷。'),
	(5636, 680, '菩萨蛮·何须急管吹云暝', '何须急管吹云暝，高寒滟滟开金饼。今夕不登楼，一年空过秋。 桂花香雾冷，梧叶西风影。客醉倚河桥，清光愁玉箫。'),
	(5637, 639, '醉花阴', '聊以读时耳 峻极云端潇洒寺。赋我登高意。好景属清游，玉友黄花，谩续龙山事。 秋风满座芝兰媚。杯酒随宜醉。行乐任天真，一笑和同，休问无携妓。'),
	(5638, 591, '玉楼春·戏赋云山', '何人半夜推山去？四面浮云猜是汝。常时相对两三峰，走遍溪头无觅处。 西风瞥起云横度，忽见东南天一柱。老僧拍手笑相夸，且喜青山依旧住。'),
	(5639, 94, '八声甘州·寿阳楼八公山作', '故都迷岸草，望长淮、依然绕孤城。想乌衣年少，芝兰秀发，戈戟云横。坐看骄兵南渡，沸浪骇奔鲸。转盼东流水，一顾功成。 千载八公山下，尚断崖草木，遥拥峥嵘。漫云涛吞吐，无处问豪英。信劳生、空成今古，笑我来、何事怆遗情。东山老，可堪岁晚，独听桓筝。'),
	(5640, 549, '小重山·花院深疑无路通', '花院深疑无路通。碧纱窗影下，玉芙蓉。当时偏恨五更钟。分携处，斜月小帘栊。 楚梦冷沈踪。一双金缕枕，半床空。画桥临水凤城东。楼前柳，憔悴几秋风。'),
	(5641, 468, '蝶恋花·海岱楼玩月作', '千古涟漪清绝地。海岱楼高，下瞰秦淮尾。水浸碧天天似水。广寒宫阙人间世。 霭霭春和一海市。鏊戴三山，顷刻随轮至。宝月圆时多异气。夜光一颗千金贵。'),
	(5642, 278, '鹊桥仙', '风清月莹，天然标韵，自是闺房之秀。情多无那不能禁，常是为、而今时候。 绿云低拢，红潮微上，画幕梅寒初透。一般偏更恼人深，时更把、眉儿轻皱。'),
	(5643, 494, '南歌子·寓意', '雨暗初疑夜，风回忽报晴。淡云斜照著山明。细草软沙溪路、马蹄轻。 卯酒醒还困，仙材梦不成。蓝桥何处觅云英。只有多情流水、伴人行。'),
	(5644, 494, '浣溪沙·春情', '道字娇讹苦未成。未应春阁梦多情。朝来何事绿鬟倾。 彩索身轻长趁燕，红窗睡重不闻莺。困人天气近清明。'),
	(5645, 409, '浣溪沙·百亩中庭半是苔', '百亩中庭半是苔。门前白道水萦回。爱闲能有几人来。 小院回廊春寂寂，山桃溪杏两三栽。为谁零落为谁开。'),
	(5646, 352, '定风波', '把酒花前欲问公。对花何事诉金钟。为问去年春甚处。虚度。莺声撩乱一场空。 今岁春来须爱惜。难得。须知花面不长红。待得酒醒君不见。千片。不随流水即随风。'),
	(5647, 186, '木兰花·乙卯吴兴寒食', '龙头舴艋吴儿竞，笋柱秋千游女并。芳洲拾翠暮忘归，秀野踏青来不定。 行云去后遥山暝，已放笙歌池院静。中庭月色正清明，无数杨花过无影。'),
	(5648, 557, '扬州慢·十里春风', '十里春风，二分明月，蕊仙飞下琼楼。看冰花翦翦，拥碎玉成毬。想长日、云阶伫立，太真肌骨，飞燕风流。敛群芳、清丽精神，都付扬州。 雨窗数朵，梦惊回、天际香浮。似阆苑花神，怜人冷落，骑鹤来游。为问竹西风景，长空淡、烟水悠悠。又黄昏，羌管孤城，吹起新愁。'),
	(5649, 114, '长相思令·烟霏霏', '烟霏霏，雪霏霏。雪向梅花枝上堆，春从何处回！ 醉眼开，睡眼开，疏影横斜安在哉？从教塞管催。'),
	(5650, 515, '少年游·枫林红透晚烟青', '枫林红透晚烟青。客思满鸥汀。二十年来，无家种竹，犹借竹为名。 春风未了秋风到，老去万缘轻。只把平生，闲吟闲咏，谱作棹歌声。'),
	(5651, 71, '柳梢青·春感', '铁马蒙毡，银花洒泪，春入愁城。笛里番腔，街头戏鼓，不是歌声。 那堪独坐青灯。想故国、高台月明。辇下风光，山中岁月，海上心情。'),
	(5652, 112, '八声甘州·灵岩陪庾幕诸公游', '渺空烟四远，是何年、青天坠长星？幻苍崖云树，名娃金屋，残霸宫城。箭径酸风射眼，腻水染花腥。时靸双鸳响，廊叶秋声。 宫里吴王沉醉，倩五湖倦客，独钓醒醒。问苍波无语，华发奈山青。水涵空、阑干高处，送乱鸦斜日落渔汀。连呼酒、上琴台去，秋与云平。'),
	(5653, 49, '清平乐·风高浪快', '风高浪快，万里骑蟾背。曾识姮娥真体态。素面元无粉黛。 身游银阙珠宫。俯看积气蒙蒙。醉里偶摇桂树，人间唤作凉风。'),
	(5654, 569, '蝶恋花（用宜笑之语作）', '解语花枝娇朵朵。不为伤春，爱把眉峰锁。宜笑精神偏一个。微涡媚靥樱桃破。 先自腰肢常袅娜。更被新来，酒饮频过火。茶饭不忺犹自可。脸儿瘦得些娘大。'),
	(5655, 446, '卜算子·见也如何暮', '见也如何暮。别也如何遽。别也应难见也难，后会难凭据。 去也如何去。住也如何住。住也应难去也难，此际难分付。'),
	(5656, 326, '武陵春', '春，因呈子西 长铗归乎逾十暑，不著鵕鸃冠。道是今年胜去年。特地减清欢。 旧赐龙团新作崇，频啜得中寒。瘦骨如柴痛又酸。儿信问平安。'),
	(5657, 326, '昭君怨·赋松上鸥', '晚饮诚斋，忽有一鸥来泊松上，已而复去，感而赋之。 偶听松梢扑鹿，知是沙鸥来宿。稚子莫喧哗，恐惊他。 俄倾忽然飞去，飞去不知何处？我已乞归休，报沙鸥。'),
	(5658, 267, '水龙吟·放船千里凌波去', '放船千里凌波去。略为吴山留顾。云屯水府，涛随神女，九江东注。北客翩然，壮心偏感，年华将暮。念伊嵩旧隐，巢由故友，南柯梦、遽如许。 回首妖氛未扫，问人间、英雄何处。奇谋报国，可怜无用，尘昏白羽。铁锁横江，锦帆冲浪，孙郎良苦。但愁敲桂棹，悲吟梁父，泪流如雨。'),
	(5659, 360, '上林春令·十一月三十日见雪', '蝴蝶初翻帘绣。万玉女、齐回舞袖。落花飞絮蒙蒙，长忆著、灞桥别后。 浓香斗帐自永漏。任满地、月深云厚。夜寒不近流苏，只怜他、后庭梅瘦。'),
	(5660, 549, '钓船归·绿净春深好染衣', '绿净春深好染衣。际柴扉。溶溶漾漾白鸥飞。两忘机。 南去北来徒自老，故人稀。夕阳长送钓船归。鳜鱼肥。'),
	(5661, 494, '蝶恋花·密州上元', '灯火钱塘三五夜，明月如霜，照见人如画。帐底吹笙香吐麝，更无一点尘随马。 寂寞山城人老也！击鼓吹箫，却入农桑社。火冷灯稀霜露下，昏昏雪意云垂野。'),
	(5662, 250, '木兰花·秋千院落重帘暮', '秋千院落重帘暮，彩笔闲来题绣户。墙头丹杏雨余花，门外绿杨风后絮。 朝云信断知何处？应作襄王春梦去。紫骝认得旧游踪，嘶过画桥东畔路。'),
	(5663, 201, '满路花·冬', '罗襟湿未干，又是凄凉雪。欲睡难成寐、音书绝。窗前竹叶，凛凛狂风折。寒衣弱不胜，有甚遥肠，望到春来时节。孤灯独照，字字吟成血。仅梅花知苦、香来接。离愁万种，提起心头切。比霜风更烈。瘦似枯枝，待何人与分说。'),
	(5664, 149, '淡黄柳·空城晓角', '客居合肥南城赤阑桥之西，巷陌凄凉，与江左异。唯柳色夹道，依依可怜。因度此阕，以纾客怀。 空城晓角，吹入垂杨陌。马上单衣寒恻恻。看尽鹅黄嫩绿，都是江南旧相识。 正岑寂，明朝又寒食。强携酒、小桥宅。怕梨花落尽成秋色。燕燕飞来，问春何在？唯有池塘自碧。'),
	(5665, 639, '点绛唇·咏梅月', '一夜相思，水边清浅横枝瘦。小窗如昼，情共香俱透。 清入梦魂，千里人长久。君知否？雨孱云愁，格调还依旧。'),
	(5666, 633, '双头莲·呈范至能待制', '华鬓星星，惊壮志成虚，此身如寄。萧条病骥。向暗里、消尽当年豪气。梦断故国山川，隔重重烟水。身万里，旧社凋零，青门俊游谁记？ 尽道锦里繁华，叹官闲昼永，柴荆添睡。清愁自醉。念此际、付与何人心事。纵有楚柁吴樯，知何时东逝？空怅望，鲙美菰香，秋风又起。'),
	(5667, 271, '菩萨蛮·山亭水榭秋方半', '山亭水榭秋方半，凤帏寂寞无人伴。愁闷一番新，双蛾只旧颦。 起来临绣户，时有疏萤度。多谢月相怜，今宵不忍圆。'),
	(5668, 225, '卜算子·天生百种愁', '天生百种愁，挂在斜阳树。绿叶阴阴占得春，草满莺啼处。 不见生尘步。空忆如簧语。柳外重重叠叠山，遮不断、愁来路。'),
	(5669, 352, '鹊踏枝', '一曲尊前开画扇。暂近还遥，不语仍低面。直至情多缘少见。千金不直双回眄。 苦恨行云容易散。过尽佳期，争向年芳晚。百种寻思千万遍。愁肠不似情难断。'),
	(5670, 344, '秋夜月·当初聚散', '当初聚散。便唤作、无由再逢伊面。近日来、不期而会重欢宴。向尊前、闲暇里，敛著眉儿长叹。惹起旧愁无限。 盈盈泪眼。漫向我耳边，作万般幽怨。奈你自家心下，有事难见。待信真个，恁别无萦绊。不免收心，共伊长远。'),
	(5671, 344, '斗百花·满搦宫腰纤细', '满搦宫腰纤细，年纪方当笄岁。刚被风流沾惹，与合垂杨双髻。初学严妆，如描似削身材，怯雨羞云情意。举措多娇媚。 争奈心性，未会先怜佳婿。长是夜深，不肯便入鸳被。与解罗裳，盈盈背立银釭，却道你但先睡。'),
	(5672, 126, '清平乐·图书一室', '图书一室。香暖垂帘密。花满翠壶熏研席。睡觉满窗晴日。 手寒不了残棋。篝香细勘唐碑。无酒无诗情绪，欲梅欲雪天时。'),
	(5673, 112, '齐天乐·与冯深居登禹陵', '三千年事残鸦外，无言倦凭秋树。逝水移川，高陵变谷，那识当时神禹。幽云怪雨。翠蓱湿空梁，夜深飞去。雁起青天，数行书似旧藏处。 寂寥西窗久坐，故人悭会遇，同翦灯语。积藓残碑，零圭断璧，重拂人间尘土。霜红罢舞。漫山色青青，雾朝烟暮。岸锁春船，画旗喧赛鼓。'),
	(5674, 446, '卜算子', '人自蕊宫来，微步香云拥。小试樽前白雪歌，叶叶秋声动。 一翦艳波横，两点愁山重。收拾眉尖眼尾情，作个鸳鸯梦。'),
	(5675, 456, '最高楼·旧时心事', '旧时心事，说著两眉羞。长记得、凭肩游。缃裙罗袜桃花岸，薄衫轻扇杏花楼。几番行，几番醉，几番留。 也谁料、春风吹已断。又谁料、朝云飞亦散。天易老，恨难酬。蜂儿不解知人苦，燕儿不解说人愁。旧情怀，消不尽，几时休。'),
	(5676, 591, '清平乐·检校山园书所见', '连云松竹，万事从今足。拄杖东家分社肉，白酒床头初熟。 西风梨枣山园，儿童偷把长竿。莫遣旁人惊去，老夫静处闲看。'),
	(5677, 587, '临江仙·暮春', '过尽征鸿来尽燕，故园消息茫然。一春憔悴有谁怜。怀家寒食夜，中酒落花天。 见说江头春浪渺，殷勤欲送归船。别来此处最萦牵。短篷南浦雨，疏柳断桥烟。'),
	(5678, 633, '清商怨·葭萌驿作', '江头日暮痛饮。乍雪晴犹凛。山驿凄凉，灯昏人独寝。 鸳机新寄断锦。叹往事、不堪重省。梦破南楼，绿云堆一枕。'),
	(5679, 509, '卜算子·席间再作', '袅袅水芝红，脉脉蒹葭浦。淅淅西风淡淡烟，几点疏疏雨。 草草展杯觞，对此盈盈女。叶叶红衣当酒船，细细流霞举。'),
	(5680, 94, '卜算子·五月八日夜凤凰亭纳凉', '新月挂林梢，暗水鸣枯沼。时见疏星落画檐，几点流萤小。 归意已无多，故作连环绕。欲寄新声问采菱，水阔烟波渺。'),
	(5681, 549, '绿罗裙·东风柳陌长', '东风柳陌长，闭月花房小。应念画眉人，拂镜啼新晓。 伤心南浦波，回首青门道。记得绿罗裙，处处怜芳草。'),
	(5682, 451, '踏莎行', '晓树啼莺，晴洲落雁。酒旗风飐村烟淡。山田过雨正宜耕，畦塍处处春泉漫。 踏翠郊原，寻芳野涧。风流旧事嗟云散。楚山谁遣送愁来，夕阳回首青无限。'),
	(5683, 693, '南歌子·槐绿低窗暗', '槐绿低窗暗，榴红照眼明。玉人邀我少留行。无奈一帆烟雨、画船轻。 柳叶随歌皱，梨花与泪倾。别时不似见时情。今夜月明江上、酒初醒。'),
	(5684, 494, '蝶恋花·记得画屏初会遇', '记得画屏初会遇。好梦惊回，望断高唐路。燕子双飞来又去。纱窗几度春光暮。 那日绣帘相见处。低眼佯行，笑整香云缕。敛尽春山羞不语。人前深意难轻诉。'),
	(5685, 494, '减字木兰花·春月', '二月十五夜，与赵德麟小酌聚星堂 春庭月午，摇荡香醪光欲舞。步转回廊，半落梅花婉娩香。 轻云薄雾，总是少年行乐处。不似秋光，只与离人照断肠。'),
	(5686, 494, '西江月', '别梦已随流水，泪巾犹裛香泉。相如依旧是臞仙。人在瑶台阆苑。 花雾萦风缥缈，歌珠滴水清圆。蛾眉新作十分妍。走马归来便面。'),
	(5687, 344, '夜半乐·冻云黯淡天气', '冻云黯淡天气，扁舟一叶，乘兴离江渚。渡万壑千岩，越溪深处。怒涛渐息，樵风乍起，更闻商旅相呼。片帆高举。泛画鹢、翩翩过南浦。 望中酒旆闪闪，一簇烟村，数行霜树。残日下，渔人鸣榔归去。败荷零落，衰杨掩映，岸边两两三三，浣沙游女。避行客、含羞笑相语。 到此因念，绣阁轻抛，浪萍难驻。叹后约丁宁竟何据。惨离怀，空恨岁晚归期阻。凝泪眼、杳杳神京路。断鸿声远长天暮。'),
	(5688, 72, '沁园春·斗酒彘肩', '寄辛承旨。时承旨招，不赴。 斗酒彘肩，风雨渡江，岂不快哉！被香山居士，约林和靖，与坡仙老，驾勒吾回。坡谓西湖，正如西子，浓抹淡妆临镜台。二公者，皆掉头不顾，只管衔杯。 白云天竺去来，图画里、峥嵘楼观开。爱东西双涧，纵横水绕；两峰南北，高下云堆。逋曰不然，暗香浮动，争似孤山先探梅。须晴去，访稼轩未晚，且此徘徊。'),
	(5689, 118, '人月圆·宴北人张侍御家有感', '南朝千古伤心事，犹唱后庭花。旧时王谢、堂前燕子，飞向谁家。 恍然一梦，仙肌胜雪，宫鬓堆鸦。江州司马，青衫泪湿，同是天涯。'),
	(5690, 451, '画堂春·落红铺径水平池', '落红铺径水平池，弄晴小雨霏霏。杏园憔悴杜鹃啼，无奈春归。 柳外画楼独上，凭栏手捻花枝，放花无语对斜晖，此恨谁知？'),
	(5691, 250, '鹧鸪天·当日佳期鹊误传', '当日佳期鹊误传。至今犹作断肠仙。桥成汉渚星波外，人在鸾歌凤舞前。 欢尽夜，别经年。别多欢少奈何天。情知此会无长计，咫尺凉蟾亦未圆。'),
	(5692, 174, '水调歌头·题剑阁', '万里云间戍，立马剑门关。乱山极目无际，直北是长安。人苦百年涂炭，鬼哭三边锋镝，天道久应远。手写留屯奏，炯炯寸心丹。 对青灯，搔白发，漏声残。老来勋业未就，妨却一身闲。梅岭绿阴青子，蒲涧清泉白石，怪我旧盟寒。烽火平安夜，归梦到家山。'),
	(5693, 446, '眼儿媚·愁云淡淡雨潇潇', '愁云淡淡雨潇潇。暮暮复朝朝。别来应是，眉峰翠减，腕玉香销。 小轩独坐相思处，情绪好无聊。一丛萱草，数竿修竹，数叶芭蕉。'),
	(5694, 591, '水调歌头·和马叔度游月波楼', '客子久不到，好景为君留。西楼着意吟赏，何必问更筹？唤起一天明月，照我满怀冰雪，浩荡百川流。鲸饮未吞海，剑气已横秋。 野光浮，天宇回，物华幽。中州遗恨，不知今夜几人愁？谁念英雄老矣？不道功名蕞尔，决策尚悠悠。此事费分说，来日且扶头！'),
	(5695, 591, '卜算子·千古李将军', '千古李将军，夺得胡儿马。李蔡为人在下中，却是封侯者。 芸草去陈根，笕竹添新瓦。万一朝家举力田，舍我其谁也。'),
	(5696, 633, '破阵子', '仕至千钟良易，年过七十常稀。眼底荣华元是梦，身后声名不自知。营营端为谁。 幸有旗亭沽酒，何妨茧纸题诗。幽谷云萝朝采药，静院轩窗夕对棋。不归真个痴。'),
	(5697, 633, '水调歌头·多景楼', '江左占形胜，最数古徐州。连山如画，佳处缥缈著危楼。鼓角临风悲壮，烽火连空明灭，往事忆孙刘。千里曜戈甲，万灶宿貔貅。 露沾草，风落木，岁方秋。使君宏放，谈笑洗尽古今愁。不见襄阳登览，磨灭游人无数，遗恨黯难收。叔子独千载，名与汉江流。'),
	(5698, 494, '洞仙歌·咏柳', '江南腊尽，早梅花开后，分付新春与垂柳。 细腰肢自有入格风流，仍更是、骨体清英雅秀。 永丰坊那畔，尽日无人，谁见金丝弄晴昼？ 断肠是飞絮时，绿叶成阴，无个事、一成消瘦。 又莫是东风逐君来，便吹散眉间一点春皱。'),
	(5699, 451, '秋日三首', '霜落邗沟积水清，寒星无数傍船明。 菰蒲深处疑无地，忽有人家笑语声。 月团新碾瀹花瓷，饮罢呼儿课楚词。 风定小轩无落叶，青虫相对吐秋丝。 连卷雌霓小西楼，逐雨追晴意未休。 安得万妆相向舞。酒酣聊把作缠头。'),
	(5700, 202, '疏影·咏荷叶', '碧圆自洁。向浅洲远渚，亭亭清绝。犹有遗簪，不展秋心，能卷几多炎热。鸳鸯密语同倾盖，且莫与、浣纱人说。恐怨歌、忽断花风，碎却翠云千叠。 回首当年汉舞，怕飞去、谩皱留仙裙折。恋恋青衫，犹染枯香，还叹鬓丝飘雪。盘心清露如铅水，又一夜、西风吹折。喜静看、匹练秋光，倒泻半湖明月。'),
	(5701, 422, '水龙吟·落叶', '晓霜初著青林，望中故国凄凉早。萧萧渐积，纷纷犹坠，门荒径悄。渭水风生，洞庭波起，几番秋杪。想重涯半没，千峰尽出，山中路、无人到。 前度题红杳杳。溯宫沟、暗流空绕。啼螀未歇，飞鸿欲过，此时怀抱。乱影翻窗，碎声敲砌，愁人多少。望吾庐甚处，只应今夜，满庭谁扫。'),
	(5702, 591, '一剪梅', '记得同烧此夜香。人在回廊。月在回廊。而今独自睚昏黄。行也思量。坐也思量。 锦字都来三两行。千断人肠。万断人肠。雁儿何处是仙乡。来也恓惶。去也恓惶。'),
	(5703, 633, '浪淘沙·丹阳浮玉亭席上作', '绿树暗长亭，几把离尊。阳关常恨不堪闻。何况今朝秋色里，身是行人。 清泪浥罗巾，各自消魂。一江离恨恰平分。安得千寻横铁锁，截断烟津？'),
	(5704, 271, '清平乐·风光紧急', '风光紧急。三月俄三十。拟欲留连计无及。绿野烟愁露泣。 倩谁寄语春宵。城头画鼓轻敲。缱绻临歧嘱付，来年早到梅梢。'),
	(5705, 645, '清平乐·秋光烛地', '秋光烛地。帘幕生秋意。露叶翻风惊鹊坠。暗落青林红子。 微行声断长廊。熏炉衾换生香。灭烛却延明月，揽衣先怯微凉。'),
	(5706, 549, '思越人·紫府东风放夜时', '紫府东风放夜时。步莲秾李伴人归。五更钟动笙歌散，十里月明灯火稀。 香苒苒，梦依依。天涯寒尽减春衣。凤凰城阙知何处，寥落星河一雁飞。'),
	(5707, 494, '蝶恋花', '春事阑珊芳草歇。客里风光，又过清明节。小院黄昏人忆别。落红处处闻啼鴂。 咫尺江山分楚越。目断魂销，应是音尘绝。梦破五更心欲折。角声吹落梅花月。'),
	(5708, 494, '临江仙', '尊酒何人怀李白，草堂遥指江东。珠帘十里卷香风。花开又花谢，离恨几千重。 轻舸渡江连夜到，一时惊笑衰容。语音犹自带吴侬。夜阑对酒处，依旧梦魂中。'),
	(5709, 344, '木兰花慢·拆桐花烂漫', '拆桐花烂漫，乍疏雨、洗清明。正艳杏浇林，缃桃绣野，芳景如屏。倾城。尽寻胜去，骤雕鞍紺幰出郊坰。风暖繁弦脆管，万家竞奏新声。 盈盈。斗草踏青。人艳冶、递逢迎。向路傍往往，遗簪堕珥，珠翠纵横。欢情。对佳丽地，信金罍罄竭玉山倾。拚却明朝永日，画堂一枕春酲。'),
	(5710, 397, '酒泉子·长忆西湖', '长忆西湖。尽日凭阑楼上望：三三两两钓鱼舟，岛屿正清秋。 笛声依约芦花里，白鸟成行忽惊起。别来闲整钓鱼竿，思入水云寒。'),
	(5711, 130, '关河令·秋阴时晴渐向暝', '秋阴时晴渐向暝，变一庭凄冷。伫听寒声，云深无雁影。 更深人去寂静，但照壁孤灯相映。酒已都醒，如何消夜永！'),
	(5712, 394, '大有·九日', '戏马台前，采花篱下，问岁华、还是重九。恰归来、南山翠色依旧。帘栊昨夜听风雨，都不似、登临时候。一片宋玉情怀，十分卫郎清瘦。 红萸佩、空对酒。砧杆动微寒，暗欺罗袖。秋已无多，早是败荷衰柳。强整帽檐欹侧，曾经向、天涯搔首。几回忆，故国莼鲈，霜前雁後。'),
	(5713, 389, '减字木兰花·淮山隐隐', '淮山隐隐。千里云峰千里恨。淮水悠悠。万顷烟波万顷愁。 山长水远。遮住行人东望眼。恨旧愁新。有泪无言对晚春。'),
	(5714, 289, '谒金门·花过雨', '花过雨，又是一番红素。燕子归来愁不语，旧巢无觅处。 谁在玉关劳苦？谁在玉楼歌舞？若使胡尘吹得去，东风侯万户。'),
	(5715, 239, '解语花', '长空淡碧，素魄凝辉，星斗寒相射。凤楼鸳瓦。天风动，冉冉珮环高下。歌清韵雅。对好景、芳樽满把。花雾浓，灯火荧煌，笑语烘兰麝。 千斛明珠照夜。况人如图画，明艳容冶。绣巾香帕。归来路，缓逐杏鞯骄马。笙歌散也。愁万炬、绛莲分谢。更漏残，惊听西楼，吹小梅初罢。'),
	(5716, 91, '齐天乐·中秋宿真定驿', '西风来劝凉云去，天东放开金镜。照野霜凝，入河桂湿，一一冰壶相映。殊方路永。更分破秋光，尽成悲镜。有客踌躇，古庭空自吊孤影。 江南朋旧在许，也能怜天际，诗思谁领？梦断刀头，书开虿尾，别有相思随定。忧心耿耿。对风鹊残枝，露蛩荒井。斟酌姮娥，九秋宫殿冷。'),
	(5717, 91, '东风第一枝·咏春雪', '巧沁兰心，偷黏草甲，东风欲障新暖。谩凝碧瓦难留，信知暮寒轻浅。行天入镜，做弄出、轻松纤软。料故园、不卷重帘，误了乍来双燕。 青未了、柳回白眼。红欲断、杏开素面。旧游忆著山阴，厚盟遂妨上苑。寒炉重暖，便放慢春衫针线。恐凤靴，挑菜归来，万一灞桥相见。'),
	(5718, 214, '菩萨蛮·芭蕉', '风流不把花为主。多情管定烟和雨。潇洒绿衣长。满身无限凉。 文笺舒卷处。似索题诗句。莫凭小阑干。月明生夜寒。'),
	(5719, 591, '虞美人·赋虞美人草', '当年得意如芳草。日日春风好。拔山力尽忽悲歌。饮罢虞兮从此、奈君何。 人间不识精诚苦。贪看青青舞。蓦然敛袂却亭亭。怕是曲中犹带、楚歌声。'),
	(5720, 498, '霜天晓角·梅', '晚晴风歇。一夜春威折。脉脉花疏天淡，云来去、数枝雪。 胜绝。愁亦绝。此情谁共说。惟有两行低雁，知人倚、画楼月。'),
	(5721, 94, '临江仙·与客湖上饮归', '不见跳鱼翻曲港，湖边特地经过。萧萧疏雨乱风荷。微云吹散，凉月堕平波。 白酒一杯还径醉，归来散发婆娑。无人能唱采莲歌。小轩欹枕，檐影挂星河。'),
	(5722, 545, '江神子·杏花村馆酒旗风', '杏花村馆酒旗风。水溶溶。扬残红。野渡舟横，杨柳绿阴浓。望断江南山色远，人不见，草连空。 夕阳楼外晚烟笼。粉香融。淡眉峰。记得年时，相见画屏中。只有关山今夜月，千里外，素光同。'),
	(5723, 130, '烛影摇红·芳脸匀红', '芳脸匀红，黛眉巧画宫妆浅。风流天付与精神，全在娇波眼。早是萦心可惯。向尊前、频频顾眄。几回想见，见了还休，争如不见。 烛影摇红，夜阑饮散春宵短。当时谁会唱阳关，离恨天涯远。争奈云收雨散。凭阑干、东风泪满。海棠开后，燕子来时，黄昏深院。'),
	(5724, 130, '夜游宫·叶下斜阳照水', '叶下斜阳照水。卷轻浪、沈沈千里。桥上酸风射眸子。立多时，看黄昏，灯火市。 古屋寒窗底。听几片、井桐飞坠。不恋单衾再三起。有谁知，为萧娘，书一纸。'),
	(5725, 352, '少年游·栏干十二独凭春', '栏干十二独凭春，晴碧远连云。千里万里，二月三月，行色苦愁人。 谢家池上，江淹浦畔，吟魄与离魂。那堪疏雨滴黄昏。更特地、忆王孙。'),
	(5726, 251, '鹊踏枝', '紫府群仙名籍秘。五色斑龙，暂降人间世。海变桑田都不记，蟠桃一熟三千岁。 露滴彩旌云绕袂。谁信壶中，别有笙歌地。门外落花随水逝。相看莫惜尊前醉。'),
	(5727, 186, '系裙腰·般涉调', '惜霜蟾照夜云天。朦胧影、画勾阑。人情纵似长情月，算一年年。又能得、几番圆。 欲寄西江题叶字，流不到、五亭前。东池始有荷新绿，尚小如钱。问何日藕、几时莲。'),
	(5728, 344, '倾杯·鹜落霜洲', '鹜落霜洲，雁横烟渚，分明画出秋色。暮雨乍歇，小楫夜泊，宿苇村山驿。何人月下临风处，起一声羌笛。离愁万绪，闲岸草、切切蛩吟如织。 为忆芳容别后，水遥山远，何计凭鳞翼。想绣阁深沉，争知憔悴损，天涯行客。楚峡云归，高阳人散，寂寞狂踪迹。望京国。空目断、远峰凝碧。'),
	(5729, 516, '减字木兰花·题雄州驿', '朝云横度。辘辘车声如水去。白草黄沙。月照孤村三两家。 飞鸿过也。万结愁肠无昼夜。渐近燕山。回首乡关归路难。'),
	(5730, 112, '声声慢·友人以梅兰瑞香水仙供客曰四香分韵得风字', '友人以梅、兰、瑞香、水仙供客，曰四香，分韵得风字。 云深山坞，烟冷江皋，人生未易相逢。一笑灯前，钗行两两春容。清芳夜争真态，引生香、撩乱东风。探花手，与安排金屋，懊恼司空。 憔悴欹翘委佩，恨玉奴销瘦，飞趁轻鸿。试问知心，尊前谁最情浓。连呼紫云伴醉，小丁香、才吐微红。还解语，待携归、行雨梦中。'),
	(5731, 112, '霜叶飞·重九', '断烟离绪。关心事，斜阳红隐霜树。半壶秋水荐黄花，香噀西风雨。纵玉勒、轻飞迅羽，凄凉谁吊荒台古？记醉踏南屏， 彩扇咽寒蝉，倦梦不知蛮素。 聊对旧节传杯，尘笺蠹管，断阕经岁慵赋。小蟾斜影转东篱，夜冷残蛩语。早白发、缘愁万缕。惊飙从卷乌纱去。谩细将、茱萸看，但约明年，翠微高处。'),
	(5732, 370, '行香子·腊八日与洪仲简溪行其夜雪作', '野店残冬。绿酒春浓。念如今、此意谁同。溪光不尽，山翠无穷。有几枝梅，几竿竹，几株松。 篮举乘兴，薄暮疏钟。望孤村、斜日匆匆。夜窗雪阵，晓枕云峰。便拥渔蓑，顶渔笠，作渔翁。'),
	(5733, 591, '西江月', '粉面都成醉梦，霜髯能几春秋。来时诵我伴牢愁。一见尊前似旧。 诗在阴何侧畔，字居罗赵前头。锦囊来往几时休。已遣蛾眉等候。'),
	(5734, 305, '摊破浣溪沙·揉破黄金万点轻', '揉破黄金万点轻。剪成碧玉叶层层。风度精神如彦辅，大鲜明。 梅蕊重重何俗甚，丁香千结苦粗生。熏透愁人千里梦，却无情。'),
	(5735, 360, '渔家傲', '鬓底青春留不住。功名薄似风前絮。何似瓮头春没数。都占取。只消一纸长门赋。 寒日半窗桑柘暮。倚阑目送繁云去。却欲载书寻旧路。烟深处。杏花菖叶耕春雨。'),
	(5736, 360, '惜分飞·泪湿阑干花著露', '泪湿阑干花著露。愁到眉峰碧聚。此恨平分取。更无言语。空相觑。 短雨残云无意绪。寂寞朝朝暮暮。今夜山深处。断魂分付。潮回去。'),
	(5737, 494, '浣溪沙', '明日酒醒，雪大作，又作二首 覆块青青麦未苏。江南云叶暗随车。临皋烟景世间无。 雨脚半收檐断线，雪林初下瓦疏珠。归来冰颗乱黏须。'),
	(5738, 250, '玉楼春·东风又作无情计', '东风又作无情计，艳粉娇红吹满地。碧楼帘影不遮愁，还似去年今日意。 谁知错管春残事，到处登临曾费泪。此时金盏直须深，看尽落花能几醉！'),
	(5739, 409, '望江南（四之三）', '归依佛，弹指越三祇。愿我速登无上觉，还如佛坐道场时。能智又能悲。'),
	(5740, 352, '减字木兰花', '伤怀离抱。天若有情天亦老。此意如何。细似轻丝渺似波。 扁舟岸侧。枫叶荻花秋索索。细想前欢。须著人间比梦间。'),
	(5741, 344, '尾犯·夜雨滴空阶', '夜雨滴空阶，孤馆梦回，情绪萧索。一片闲愁，想丹青难貌。秋渐老、蛩声正苦，夜将阑、灯花旋落。最无端处，总把良宵，只恁孤眠却。 佳人应怪我，别后寡信轻诺。记得当初，翦香云为约。甚时向、幽闺深处，按新词、流霞共酌。再同欢笑，肯把金玉珠珍博。'),
	(5742, 344, '黄莺儿·园林晴昼春谁主', '园林晴昼春谁主。暖律潜催，幽谷暄和，黄鹂翩翩，乍迁芳树。观露湿缕金衣，叶映如簧语。晓来枝上绵蛮，似把芳心深意低诉。 无据。乍出暖烟来，又趁游蜂去。恣狂踪迹，两两相呼，终朝雾吟风舞。当上苑柳秾时，别馆花深处。此际海燕偏饶，都把韶光与。'),
	(5743, 305, '丑奴儿·晚来一阵风兼雨', '晚来一阵风兼雨，洗尽炎光。理罢笙簧，却对菱花淡淡妆。 绛绡缕薄冰肌莹，雪腻酥香。笑语檀郎：今夜纱厨枕簟凉。'),
	(5744, 639, '虞美人·东风荡飏轻云缕', '东风荡飏轻云缕，时送潇潇雨。水边台榭燕新归，一点香泥，湿带落花飞。 海棠糁径铺香绣，依旧成春瘦。黄昏庭院柳啼鸦，记得那人，和月折梨花。'),
	(5745, 255, '蓦山溪·梅', '洗妆真态，不作铅花御。竹外一枝斜，想佳人，天寒日暮。黄昏院落，无处著清香，风细细，雪垂垂，何况江头路。 月边疏影，梦到消魂处。梅子欲黄时，又须作，廉纤细雨。孤芳一世，供断有情愁，消瘦损，东阳也，试问花知否？'),
	(5746, 202, '朝中措·清明时节', '清明时节雨声哗。潮拥渡头沙。翻被梨花冷看，人生苦恋天涯。 燕帘莺户，云窗雾阁，酒醒啼鸦。折得一枝杨柳，归来插向谁家。'),
	(5747, 202, '高阳台·西湖春感', '接叶巢莺，平波卷絮，断桥斜日归船。能几番游，看花又是明年。东风且伴蔷薇住，到蔷薇、春已堪怜。更凄然。万绿西泠，一抹荒烟。 当年燕子知何处，但苔深韦曲，草暗斜川。见说新愁，如今也到鸥边。无心再续笙歌梦，掩重门、浅醉闲眠。莫开帘，怕见飞花，怕听啼鹃。'),
	(5748, 15, '破阵子', '柳浪六桥春碧，香尘十里花风。好是烂游浓醉后，画□阑干见小红。红明绿暗中。 旧约涌金门道，纱笼毕竟相逢。只恐入城归路杂，便转头树北云东。侯门深几重。'),
	(5749, 422, '齐天乐·蝉', '一襟余恨宫魂断，年年翠阴庭树。乍咽凉柯，还移暗叶，重把离愁深诉。西窗过雨。怪瑶佩流空，玉筝调柱。镜暗妆残，为谁娇鬓尚如许。 铜仙铅泪似洗，叹携盘去远，难贮零露。病翼惊秋，枯形阅世，消得斜阳几度？馀音更苦。甚独抱清高，顿成凄楚？谩想熏风，柳丝千万缕。'),
	(5750, 71, '忆江南', '花几许，已报八分催。却问主人何处去，且容老子个中来。花外主人回。 年时客，如今安在哉。正喜锦官城烂漫，忽惊花鸟使摧颓。世事只添杯。时有称宣使折花者，盖诈也，托以肆陵慢。'),
	(5751, 591, '破阵子·赠行', '少日春风满眼，而今秋叶辞柯。便好消磨心下事，莫忆寻常醉后歌。可怜白发多。 明日扶头颠倒，倩谁伴舞婆娑。我定思君拼瘦损，君不思兮可奈何。天寒将息呵。'),
	(5752, 591, '浪淘沙·山寺夜半闻钟', '身世酒杯中。万事皆空。古来三五个英雄。雨打风吹何处是，汉殿秦宫。 梦入少年丛。歌舞匆匆。老僧夜半误鸣钟。惊起西窗眠不得，卷地西风。'),
	(5753, 498, '鹧鸪天·休舞银貂小契丹', '休舞银貂小契丹，满堂宾客尽关山。从今嫋嫋盈盈处，谁复端端正正看。 模泪易，写愁难。潇湘江上竹枝斑。碧云日暮无书寄，寥落烟中一雁寒。'),
	(5754, 526, '鹊桥仙', '明眸皓齿，丰肌秀骨，浑是揉花碎玉。十分心事有谁知，暗恼得、愁红怨绿。 残云断雨，不期而会，也要天来大福。若还虚度可怜宵，便做下、来生不足。'),
	(5755, 271, '江城子·赏春', '斜风细雨作春寒，对尊前，忆前欢。曾把梨花，寂寞泪阑干。芳草断烟南浦路，和别泪，看青山。 昨宵结得梦夤缘，水云间，悄无言。争奈醒来，愁恨又依然。展转衾裯空懊恼，天易见，见伊难。'),
	(5756, 305, '怨王孙（春暮）', '帝里春晚。重门深院。草绿阶前，暮天雁断。楼上远信谁传。恨绵绵。 多情自是多沾惹。难拚舍又是寒食也。秋千巷陌，人静皎月初斜。浸梨花。'),
	(5757, 278, '忆秦娥·用太白韵', '清溪咽。霜风洗出山头月。山头月。迎得云归，还送云别。 不知今是何时节。凌歊望断音尘绝。音尘绝。帆来帆去，天际双阙。'),
	(5758, 494, '减字木兰花（琴）', '神闲意定。万籁收声天地静。玉指冰弦。未动宫商意已传。 悲风流水。写出寥寥千古意。归去无眠。一夜余音在耳边。'),
	(5759, 494, '浣溪沙·忆旧', '长记鸣琴子溅堂。朱颜绿发映垂杨。如今秋鬓数茎霜。 聚散交游如梦寐，升沉闲事莫思量。仲卿终不避桐乡。'),
	(5760, 494, '永遇乐·长忆别时', '孙巨源以八月十五日离海州，坐别于景疏楼上。既而与余会于润州，至楚州乃别。余以十一月十五日至海州，与太守会于景疏楼上，作此词以寄巨源。 长忆别时，景疏楼上，明月如水。美酒清歌，留连不住，月随人千里。别来三度，孤光又满，冷落共谁同醉？卷珠帘、凄然顾影，共伊到明无寐。 今朝有客，来从濉上，能道使君深意。凭仗清淮，分明到海，中有相思泪。而今何在？西垣清禁，夜永露华侵被。此时看、回廊晓月，也应暗记。'),
	(5761, 496, '定风波·自前二府镇穰下营百花洲亲制', '罗绮满城春欲暮。百花洲上寻芳去。浦映□花花映浦。无尽处。恍然身入桃源路。 莫怪山翁聊逸豫。功名得丧归时数。莺解新声蝶解舞。天赋与。争教我悲无欢绪。'),
	(5762, 118, '诉衷情·夜寒茅店不成眠', '夜寒茅店不成眠，残月照吟鞭。黄花细雨时候，催上渡头船。 鸥似雪，水如天，忆当年。到家应是，童稚牵衣，笑我华颠。'),
	(5763, 149, '钓雪亭', '阑干风冷雪漫漫，惆怅无人把钓竿。 时有官船桥畔过，白鸥飞去落前滩。'),
	(5764, 678, '阮郎归·西湖春暮', '清明寒食不多时。香红渐渐稀。番腾妆束闹苏堤。留春春怎知。 花褪雨，絮沾泥。凌波寸不移。三三两两叫船儿。人归春也归。'),
	(5765, 515, '贺新郎·梦冷黄金屋', '梦冷黄金屋。叹秦筝、斜鸿阵里，素弦尘扑。化作娇莺飞归去，犹认纱窗旧绿。正过雨、荆桃如菽。此恨难平君知否，似琼台、涌起弹棋局。消瘦影，嫌明烛。 鸳楼碎泻东西玉。问芳悰、何时再展，翠钗难卜。待把宫眉横云样，描上生绡画幅。怕不是、新来妆束。彩扇红牙今都在，恨无人、解听开元曲。空掩袖，倚寒竹。'),
	(5766, 591, '清平乐', '清词索笑。莫厌银杯小。应是天孙新与巧。剪恨裁愁句好。 有人梦断关河。小窗日饮亡何。想见重帘不卷，泪痕滴尽湘娥。'),
	(5767, 305, '瑞鹧鸪·双银杏', '风韵雍容未甚都，尊前甘橘可为奴。谁怜流落江湖上，玉骨冰肌未肯枯。 谁教并蒂连枝摘，醉后明皇倚太真。居士擘开真有意，要吟风味两家新。'),
	(5768, 494, '南乡子·梅花词和杨元素', '寒雀满疏篱，争抱寒柯看玉蕤。忽见客来花下坐，惊飞。蹋散芳英落酒卮。 痛饮又能诗，坐客无毡醉不知。花尽酒阑春到也，离离。一点微酸已著枝。'),
	(5769, 494, '点绛唇·红杏飘香', '红杏飘香，柳含烟翠拖轻缕。水边朱户。尽卷黄昏雨。 烛影摇风，一枕伤春绪。归不去。凤楼何处。芳草迷归路。'),
	(5770, 344, '凤归云·向深秋', '向深秋，雨馀爽气肃西郊。陌上夜阑，襟袖起凉飙。天末残星，流电未灭，闪闪隔林梢。又是晓鸡声断，阳乌光动，渐分山路迢迢。 驱驱行役，苒苒光阴，蝇头利禄，蜗角功名，毕竟成何事，漫相高。抛掷云泉，狎玩尘土，壮节等闲消。幸有五湖烟浪，一船风月，会须归去老渔樵。'),
	(5771, 344, '二郎神·炎光谢', '炎光谢。过暮雨、芳尘轻洒。乍露冷风清庭户，爽天如水，玉钩遥挂。应是星娥嗟久阻，叙旧约、飙轮欲驾。极目处、微云暗度，耿耿银河高泻。 闲雅。须知此景，古今无价。运巧思、穿针楼上女，抬粉面、云鬟相亚。钿合金钗私语处，算谁在、回廊影下。愿天上人间，占得欢娱，年年今夜。'),
	(5772, 493, '水调歌头·沧浪亭', '潇洒太湖岸，淡伫洞庭山。鱼龙隐处，烟雾深锁渺弥间。方念陶朱张翰，忽有扁舟急桨，撇浪载鲈还。落日暴风雨，归路绕汀湾。 丈夫志，当景盛，耻疏闲。壮年何事憔悴，华发改朱颜。拟借寒潭垂钓，又恐鸥鸟相猜，不肯傍青纶。刺棹穿芦荻，无语看波澜。'),
	(5773, 494, '望海楼晚景·横风吹雨入楼斜', '横风吹雨入楼斜，壮观应须好句夸。 雨过潮平江海碧，电光时掣紫金蛇。'),
	(5774, 31, '清平乐', '数声乌鹊。院宇寒萧索。杨柳梢头秋过却。无叶可供风落。 可人犹有芙蕖。向人冷澹妆梳。云外征鸿过尽，夕阳依旧平芜。'),
	(5775, 12, '鹊桥仙·碧梧初出', '碧梧初出，桂花才吐，池上水花微谢。穿针人在合欢楼，正月露、玉盘高泻。 蛛忙鹊懒，耕慵织倦，空做古今佳话。人间刚道隔年期，指天上、方才隔夜。'),
	(5776, 450, '卜算子·春情', '春透水波明，寒峭花枝瘦。极目烟中百尺楼，人在楼中否。 四和袅金凫，双陆思纤手。拟倩东风浣此情，情更浓于酒。'),
	(5777, 202, '声声慢', '秋高，光景奇绝，与姜白石垂虹夜游，同一清致也。 晴光转树，晓气分岚，何人野渡横舟。断柳枯蝉，凉意正满西州。匆匆载花载酒，便无情、也自风流。芳昼短，奈不堪深夜，秉烛来游。 谁识山中朝暮，向白云一笑，今古无愁。散发吟商，此兴万里悠悠。清狂未应似我，倚高寒、隔水呼鸥。须待月，许多清、都付与秋。'),
	(5778, 112, '浪淘沙·九日从吴见山觅酒', '山远翠眉长。高处凄凉。菊花清瘦杜秋娘。净洗绿杯牵露井，聊荐幽香。 乌帽压吴霜。风力偏狂。一年佳节过西厢。秋色雁声愁几许，都在斜阳。'),
	(5779, 49, '沁园春·答九华叶贤良', '一卷阴符，二石硬弓，百斤宝刀。更玉花骢喷，鸣鞭电抹，乌丝阑展，醉墨龙跳。牛角书生，虬髯豪客，谈笑皆堪折简招。依稀记，曾请缨系粤，草檄征辽。 当年目视云霄。谁信道凄凉今折腰。怅燕然未勒，南归草草，长安不见，北望迢迢。老去胸中，有些磊块，歌罢犹须著酒浇。休休也，但帽边鬓改，镜里颜凋。'),
	(5780, 149, '鹧鸪天·己酉之秋苕溪记所见', '京洛风流绝代人。因何风絮落溪津。笼鞋浅出鸦头袜，知是凌波缥缈身。 红乍笑，绿长嚬。与谁同度可怜春。鸳鸯独宿何曾惯，化作西楼一缕云。'),
	(5781, 639, '小重山·碧幕霞绡一缕红', '碧幕霞绡一缕红。槐枝啼宿鸟，冷烟浓。小楼愁倚画阑东。黄昏月，一笛碧云风。 往事已成空。梦魂飞不到，楚王宫。翠绡和泪暗偷封。江南阔，无处觅征鸿。'),
	(5782, 591, '鹧鸪天·送廓之秋试', '白苎新袍入嫩凉。春蚕食叶响回廊，禹门已准桃花浪，月殿先收桂子香。 鹏北海，凤朝阳。又携书剑路茫茫。明年此日青云去，却笑人间举子忙。'),
	(5783, 498, '秦楼月·楼阴缺', '楼阴缺，栏干影卧东厢月。东厢月，一天风露，杏花如雪。 隔烟催漏金虬烟，罗帏黯淡灯花结。灯花结，片时春梦，江南天阔。'),
	(5784, 633, '汉宫春·初自南郑来成都作', '羽箭雕弓，忆呼鹰古垒，截虎平川。吹笳暮归野帐，雪压青毡。淋漓醉墨，看龙蛇飞落蛮笺。人误许、诗情将略，一时才气超然。 何事又作南来，看重阳药市，元夕灯山？花时万人乐处，欹帽垂鞭。闻歌感旧，尚时时流涕尊前。君记取、封侯事在。功名不信由天。'),
	(5785, 185, '石州慢·己酉秋吴兴舟中作', '雨急云飞，惊散暮鸦，微弄凉月。谁家疏柳低迷，几点流萤明灭。夜帆风驶，满湖烟水苍茫，菰蒲零乱秋声咽。梦断酒醒时，倚危樯清绝。 心折。长庚光怒，群盗纵横，逆胡猖獗。欲挽天河，一洗中原膏血。两宫何处，塞垣祗隔长江，唾壶空击悲歌缺。万里想龙沙，泣孤臣吴越。'),
	(5786, 267, '水调歌头·淮阴作', '当年五陵下，结客占春游。红缨翠带，谈笑跋马水西头。落日经过桃叶，不管插花归去，小袖挽人留。换酒春壶碧，脱帽醉青楼。 楚云惊，陇水散，两漂流。如今憔悴，天涯何处可销忧。长揖飞鸿旧月。不知今夕烟水，都照几人愁。有泪看芳草，无路认西州。'),
	(5787, 549, '石州引·薄雨初寒', '薄雨初寒，斜照弄晴，春意空阔。长亭柳色才黄，远客一枝先折。烟横水际，映带几点归鸦，东风销尽龙沙雪。还记出关来，恰而今时节。 将发。画楼芳酒，红泪清歌，顿成轻别。已是经年，杳杳音尘多绝。欲知方寸，共有几许清愁，芭蕉不展丁香结。枉望断天涯，两厌厌风月。'),
	(5788, 549, '杵声齐·砧面莹', '砧面莹，杵声齐。捣就征衣泪墨题。寄到玉关应万里，戍人犹在玉关西。'),
	(5789, 468, '满庭芳·咏茶', '雅燕飞觞，清谈挥麈，使君高会群贤。密云双凤，初破缕金团。窗外炉烟自动，开瓶试、一品香泉。轻涛起，香生玉乳，雪溅紫瓯圆。 娇鬟，宜美盼，双擎翠袖，稳步红莲。座中客翻愁，酒醒歌阑。点上纱笼画烛，花骢弄、月影当轩。频相顾，馀欢未尽，欲去且留连。'),
	(5790, 693, '蓦山溪·赠衡阳妓陈湘', '鸳鸯翡翠，小小思珍偶。眉黛敛秋波，尽湖南、山明水秀。娉娉袅袅，恰近十三余，春未透。花枝瘦。正是愁时候。 寻花载酒。肯落谁人后。只恐远归来，绿成阴、青梅如豆。心期得处，每自不由人，长亭柳。君知否。千里犹回首。'),
	(5791, 250, '生查子·长恨涉江遥', '长恨涉江遥，移近溪头住。闲荡木兰舟，误入双鸳浦。 无端轻薄云，暗作廉纤雨。翠袖不胜寒，欲向荷花语。'),
	(5792, 242, '解语花·云容冱雪', '云容冱雪，暮色添寒，楼台共临眺。翠丛深窅。无人处、数蕊弄春犹小。幽姿谩好。遥相望、含情一笑。花解语，因甚无言，心事应难表。 莫待墙阴暗老。称琴边月夜，笛里霜晓。护香须早。东风度、咫尺画阑琼沼。归来梦绕。歌云坠、依然惊觉。想恁时，小几银屏冷未了。'),
	(5793, 10, '鹧鸪天·惜别', '一曲危弦断客肠。津桥捩柂转牙樯。江心云带蒲帆重，楼上风吹粉泪香。 瑶草碧，柳芽黄。载将离恨过潇湘。请君看取东流水，方识人间别意长。'),
	(5794, 272, '水调歌头', '不见严夫子，寂寞富春山。空馀千太危石，高插暮云端。想象羊裘披了，一笑两忘身世，来把钓鱼竿。不似林间翮，飞倦始知还。 中兴主，功业就，鬓毛斑。驰驱一世豪杰，相与济时艰。独委狂奴心事，不羡痴儿鼎足，放去任疏顽。爽气动心斗，千古照林峦。'),
	(5795, 428, '望江南·江南月', '江南月，清夜满西楼。云落开时冰吐鉴，浪花深处玉沉钩。圆缺几时休。 星汉迥，风露入新秋。丹桂不知摇落恨，素娥应信别离愁。天上共悠悠。'),
	(5796, 393, '临江仙', '湖水连天天连水，秋来分外澄清。君山自是小蓬瀛。气蒸云梦泽，波撼岳阳城。 帝子有灵能鼓瑟，凄然依旧伤情。微闻兰芝动芳馨。曲终人不见，江上数峰青。'),
	(5797, 366, '忆秦娥', '雪霏霏。蓟门冷落人行稀。人行稀。秦娥渐老，着破宫衣。 强将纤指按金徽。未成曲调心先悲。心先悲。更无言语，玉箸双垂。'),
	(5798, 680, '卜算子·泛西湖坐间寅斋同赋', '屈指数春来，弹指惊春去。 檐外蛛丝网落花，也要留春住。 几日喜春晴，几夜愁春雨。 十二雕窗六曲屏，题遍伤心句。'),
	(5799, 589, '鹧鸪天·建康上元作', '客路那知岁序移。忽惊春到小桃枝。天涯海角悲凉地，记得当年全盛时。 花弄影，月流辉。水精宫殿五云飞。分明一觉华胥梦，回首东风泪满衣。'),
	(5800, 315, '六么令·次韵和贺方回金陵怀古鄱阳席上作', '长江千里，烟淡水云阔。歌沉玉树，古寺空有疏钟发。六代兴亡如梦，苒苒惊时月。兵戈凌灭。豪华销尽，几见银蟾自圆缺。 潮落潮生波渺，江树森如发。谁念迁客归来，老大伤名节。纵使岁寒途远，此志应难夺。高楼谁设。倚阑凝望，独立渔翁满江雪。'),
	(5801, 130, '六丑·落花', '正单衣试酒，恨客里、光阴虚掷。愿春暂留，春归如过翼。一去无迹。为问花何在，夜来风雨，葬楚宫倾国。钗钿堕处遗香泽。乱点桃蹊，轻翻柳陌。多情为谁追惜。但蜂媒蝶使，时叩窗隔。 东园岑寂。渐蒙笼暗碧。静绕珍丛底，成叹息。长条故惹行客。似牵衣待话，别情无极。残英小、强簪巾帻。终不似一朵，钗头颤袅，向人欹侧。漂流处、莫趁潮汐。恐断红、尚有相思字，何由见得。'),
	(5802, 549, '南歌子·疏雨池塘见', '疏雨池塘见，微风襟袖知。阴阴夏木啭黄鹂，何处飞来白鹭立移时。 易醉扶头酒，难逢敌手棋。日长偏与睡相宜，睡起芭蕉叶上自题诗。'),
	(5803, 250, '鹧鸪天·手捻香笺忆小莲', '手捻香笺忆小莲。欲将遗恨倩谁传。归来独卧逍遥夜，梦里相逢酩酊天。 花易落，月难圆。只应花月似欢缘。秦筝算有心情在，试写离声入旧弦。'),
	(5804, 250, '临江仙·身外闲愁空满', '身外闲愁空满，眼中欢事常稀。明年应赋送君诗。细从今夜数，相会几多时。 浅酒欲邀谁劝，深情惟有君知。东溪春近好同归。柳垂江上影，梅谢雪中枝。'),
	(5805, 352, '玉楼春·去时梅萼初凝粉', '去时梅萼初凝粉。不觉小桃风力损。梨花最晚又凋零，何事归期无定准。 阑干倚遍重来凭。泪粉偷将红袖印。蜘蛛喜鹊误人多，似此无凭安足信。'),
	(5806, 251, '少年游·重阳过后', '重阳过后，西风渐紧，庭树叶纷纷。朱阑向晓，芙蓉妖艳，特地斗芳新。 霜前月下，斜红淡蕊，明媚欲回春。莫将琼萼等闲分。留赠意中人。'),
	(5807, 28, '鱼游春水·秦楼东风里', '秦楼东风里，燕子还来寻旧垒。馀寒犹峭，红日薄侵罗绮。嫩草方抽玉茵，媚柳轻窣黄金蕊。莺啭上林，鱼游春水。 几曲阑干遍倚，又是一番新桃李。佳人应怪归迟，梅妆泪洗。凤箫声绝沉孤雁，望断清波无双鲤。云山万重，寸心千里。'),
	(5808, 494, '荔枝叹', '十里一置飞尘灰，五里一堠兵火催。 颠坑仆谷相枕藉，知是荔枝龙眼来。 飞车跨山鹘横海，风枝露叶如新采。 宫中美人一破颜，惊尘溅血流千载。 永元荔枝来交州，天宝岁贡取之涪。 至今欲食林甫肉，无人举觞酹伯游。 我愿天公怜赤子，莫生尤物为疮痏。 雨顺风调百谷登，民不饥寒为上瑞。 君不见，武夷溪边粟粒芽，前丁后蔡相宠加。(相宠加 一作：相笼加) 争新买宠各出意，今年斗品充官茶。 吾君所乏岂此物，致养口体何陋耶？ 洛阳相君忠孝家，可怜亦进姚黄花。'),
	(5809, 232, '青玉案·绿槐烟柳长亭路', '绿槐烟柳长亭路，恨匆忙分离去。日永如年愁难度。高城回首，暮云遮尽，目断知何处？ 解鞍旅舍天将暮，暗忆丁宁千万句。一寸柔肠情几许？薄衾孤枕，梦回人静，侵晓潇潇雨。'),
	(5810, 201, '浣溪沙·散步山前春草香', '散步山前春草香。朱阑绿水绕吟廊。花枝惊堕绣衣裳。 或定或摇江上柳，为鸾为凤月中篁。为谁掩抑锁芸窗。'),
	(5811, 515, '行香子（舟宿兰湾）', '红了樱桃。绿了芭蕉。送春归、客尚蓬飘。昨宵谷水，今夜兰皋。奈云溶溶，风淡淡，雨潇潇。 银字笙调。心字香烧。料芳悰、乍整还凋。待将春恨，都付春潮。过窈娘堤，秋娘渡，泰娘桥。'),
	(5812, 591, '好事近·中秋席上和王路钤', '明月到今宵，长是不如人约。想见广寒宫殿，正云梳风掠。 夜深休更唤笙歌，檐头雨声恶。不是小山词就，这一场寥索。'),
	(5813, 591, '行香子', '归去来兮。行乐休迟。命由天、富贵何时。百年光景，七十者稀。奈一番愁，一番病，一番衰。 名利奔驰。宠辱惊疑。旧家时、都有些儿。而今老矣，识破关机。算不如闲，不如醉，不如痴。'),
	(5814, 188, '水调歌头·过岳阳楼作', '湖海倦游客，江汉有归舟。西风千里，送我今夜岳阳楼。日落君山云气，春到沅湘草木，远思渺难收。徙倚栏杆久，缺月挂帘钩。 雄三楚，吞七泽，隘九州。人间好处，何处更似此楼头。欲吊沈累无所，但有渔儿樵子，哀此写离忧。回首叫虞舜，杜若满芳洲。'),
	(5815, 633, '长相思（五之一）', '云千重。水千重。身在千重云水中。月明收钓筒。 头未童。耳未聋。得酒犹能双脸红。一尊谁与同。'),
	(5816, 46, '喜迁莺·晓行', '晓光催角。听宿鸟未惊，邻鸡先觉。迤逦烟村，马嘶人起，残月尚穿林薄。泪痕带霜微凝，酒力冲寒犹弱。叹倦客、悄不禁，重染风尘京洛。 追念，人别后，心事万重，难觅孤鸿托。翠幌娇深，曲屏香暖，争念岁寒飘泊。怨月恨花烦恼，不是不曾经著。这情味，望一成消减，新来还恶。'),
	(5817, 312, '帝台春·芳草碧色', '芳草碧色，萋萋遍南陌。暖絮乱红，也知人、春愁无力。忆得盈盈拾翠侣，共携赏、凤城寒食。到今来，海角逢春，天涯为客。 愁旋释。还似织。泪暗拭。又偷滴。谩伫立、遍倚危阑，尽黄昏，也只是、暮云凝碧。拚则而今已拚了，忘则怎生便忘得。又还问鳞鸿，试重寻消息。'),
	(5818, 409, '菩萨蛮·数间茅屋闲临水', '数间茅屋闲临水，窄衫短帽垂杨里。花是去年红，吹开一夜风。 梢梢新月偃，午醉醒来晚。何物最关情，黄鹂三两声。'),
	(5819, 693, '清平乐', '舞鬟娟好。白发黄花帽。醉任旁观嘲潦倒。扶老偏宜年小。 舞回脸玉胸酥。缠头一斛明珠。日日梁州薄媚，年年金菊茱萸。'),
	(5820, 494, '浣溪沙·风压轻云贴水飞', '风压轻云贴水飞，乍晴池馆燕争泥。沈郎多病不胜衣。 沙上不闻鸿雁信，竹间时听鹧鸪啼。此情惟有落花知！'),
	(5821, 251, '踏莎行', '绿树归莺，雕梁别燕。春光一去如流电。当歌对酒莫沉吟，人生有限情无限。 弱袂萦春，修蛾写怨。秦筝宝柱频移雁。尊中绿醑意中人，花朝月夜长相见。'),
	(5822, 251, '撼庭秋·别来音信千里', '别来音信千里，恨此情难寄。碧纱秋月，梧桐夜雨，几回无寐。 楼高目断，天遥云黯，只堪憔悴。念兰堂红烛，心长焰短，向人垂泪。'),
	(5823, 344, '鹧鸪天·吹破残烟入夜风', '吹破残烟入夜风。一轩明月上帘栊。因惊路远人还远，纵得心同寝未同。 情脉脉，意忡忡。碧云归去认无踪。只应会向前生里，爱把鸳鸯两处笼。'),
	(5824, 675, '青玉案·春寒恻恻春阴薄', '春寒恻恻春阴薄。整半月，春萧索。晴日朝来升屋角。树头幽鸟，对调新语，语罢双飞却。 红入花腮青入萼。尽不爽，花期约。可恨狂风空自恶。晓来一阵，晚来一阵，难道都吹落？'),
	(5825, 596, '唐多令·雨过水明霞', '雨过水明霞。潮回岸带沙。叶声寒、飞透窗纱。堪恨西风吹世换，更吹我、落天涯。 寂寞古豪华。乌衣日又斜。说兴亡、燕入谁家。惟有南来无数雁，和明月、宿芦花。'),
	(5826, 626, '花心动·春词', '仙苑春浓，小桃开，枝枝已堪攀折。乍雨乍晴，轻暖轻寒，渐近赏花时节。柳摇台榭东风软，帘栊静，幽禽调舌。断魂远，闲寻翠径，顿成愁结。 此恨无人共说。还立尽黄昏，寸心空切。强整绣衾，独掩朱扉，枕簟为谁铺设。夜长更漏传声远，纱窗映、银缸明灭。梦回处，梅梢半笼残月。'),
	(5827, 97, '阮郎归·渔舟容易入春山', '渔舟容易入春山，仙家日月闲。绮窗纱幌映朱颜，相逢醉梦间。 松露冷，海霜殷。匆匆整棹还。落花寂寂水潺潺，重寻此路难。'),
	(5828, 112, '思佳客·闰中秋', '丹桂花开第二番。东篱展却宴期宽。人间宝镜离仍合，海上仙槎去复还。 分不尽，半凉天。可怜闲剩此婵娟。素娥未隔三秋梦，赢得今宵又倚阑。'),
	(5829, 49, '清平乐·纤云扫迹', '纤云扫迹，万顷玻璃色。醉跨玉龙游八极，历历天青海碧。 水晶宫殿飘香，群仙方按霓裳。消得几多风露，变教人世清凉。'),
	(5830, 91, '三姝媚·烟光摇缥瓦', '烟光摇缥瓦，望晴檐多风，柳花如洒。锦瑟横床，想泪痕尘影，凤弦常下。倦出犀帷，频梦见、王孙骄马。讳道相思，偷理绡裙，自惊腰衩。 惆怅南楼遥夜，记翠箔张灯，枕肩歌罢。又入铜驼，遍旧家门巷，首询声价。可惜东风，将恨与、闲花俱谢。记取崔徽模样，归来暗写。'),
	(5831, 72, '贺新郎·老去相如倦', '老去相如倦。向文君、说似而今，怎生消遣？衣袂京尘曾染处，空有香红尚软。料彼此、魂消肠断。一枕新凉眠客舍，听梧桐疏雨秋风颤。灯晕冷，记初见。 楼低不放珠帘卷。晚妆残，翠蛾狼藉，泪痕凝脸。人道愁来须殢酒，无奈愁深酒浅。但托意焦琴纨扇。莫鼓琵琶江上曲，怕荻花枫叶俱凄怨。云万叠，寸心远。'),
	(5832, 456, '愁倚阑·春犹浅', '春犹浅，柳初芽，杏初花。杨柳杏花交影处，有人家。 玉窗明暖烘霞。小屏上、水远山斜。昨夜酒多春睡重，莫惊他。'),
	(5833, 591, '念奴娇·西湖和人韵', '晚风吹雨，战新荷、声乱明珠苍璧。谁把香奁收宝镜，云锦红涵湖碧。飞鸟翻空，游鱼吹浪，惯趁笙歌席。坐中豪气，看公一饮千石。 遥想处士风流，鹤随人去，老作飞仙伯。茅舍疏篱今在否，松竹已非畴昔。欲说当年，望湖楼下，水与云宽窄。醉中休问，断肠桃叶消息。'),
	(5834, 633, '采桑子', '三山山下闲居士，巾履萧然。小醉闲眠。风引飞花落钓船。'),
	(5835, 99, '洞仙歌·中秋', '碧天如水，一洗秋容净。何处飞来大明镜。谁道斫却桂，应更光辉，无遗照，泻出山河倒影。 人犹苦余热，肺腑生尘，移我超然到三境。问姮娥、缘底事，乃有盈亏，烦玉斧、运风重整。教夜夜、人世十分圆，待拚却长年，醉了还醒。'),
	(5836, 267, '减字木兰花·刘郎已老', '刘郎已老。不管桃花依旧笑。要听琵琶。重院莺啼觅谢家。 曲终人醉。多似浔阳江上泪。万里东风。国破山河落照红。'),
	(5837, 255, '忆少年·年时酒伴', '年时酒伴，年时去处，年时春色。清明又近也，却天涯为客。 念过眼、光阴难再得。想前欢、尽成陈迹。登临恨无语，把阑干暗拍。'),
	(5838, 451, '风流子·东风吹碧草', '东风吹碧草，年华换、行客老沧洲。见梅吐旧英，柳摇新绿，恼人春色，还上枝头，寸心乱，北随云黯黯，东逐水悠悠。斜日半山，暝烟两岸，数声横笛，一叶扁舟。 青门同携手，前欢记、浑似梦里扬州。谁念断肠南陌，回首西楼。算天长地久，有时有尽，奈何绵绵，此恨难休。拟待倩人说与，生怕人愁。'),
	(5839, 278, '如梦令', '回首芜城旧苑。还是翠深红浅。春意已无多，斜日满帘飞燕。不见。不见。门掩落花庭院。'),
	(5840, 494, '沁园春', '情若连环，恨如流水，甚时是休。也不须惊怪，沈郎易瘦，也不须惊怪，潘鬓先愁。总是难禁，许多魔难，奈好事教人不自由。空追想，念前欢杳杳，后会悠悠。 凝眸。悔上层楼。谩惹起、新愁压旧愁。向彩笺写遍，相思字了，重重封卷，密寄书邮。料到伊行，时时开看，一看一回和泪收。须知道，□这般病染，两处心头。'),
	(5841, 554, '蝶恋花·卷絮风头寒欲尽', '卷絮风头寒欲尽。坠粉飘红，日日香成阵。新酒又添残酒困。今春不减前春恨。 蝶去莺飞无处问。隔水高楼，望断双鱼信。恼乱横波秋一寸。斜阳只与黄昏近。(横波秋 一作：层波横)'),
	(5842, 352, '采桑子·十年前是尊前客', '十年前是尊前客，月白风清，忧患凋零。老去光阴速可惊。 鬓华虽改心无改，试把金觥。旧曲重听。犹似当年醉里声。'),
	(5843, 344, '八六子·如花貌', '如花貌。当来便约，永结同心偕老。为妙年、俊格聪明，凌厉多方怜爱，何期养成心性近，元来都不相表。渐作分飞计料。 稍觉因情难供，恁殛恼。争克罢同欢笑。已是断弦尤续，覆水难收，常向人前诵谈，空遣时传音耗。漫悔懊。此事何时坏了。'),
	(5844, 24, '摸鱼儿·记年时人人何处', '记年时、人人何处，长亭曾共杯酒。酒阑归去行人远，折不尽长亭柳。渐白首。待把酒送君，恰又清明后。青条似旧，问江北江南，离愁如我，还更有人否。 留不住，强把蔬盘瀹韭。行舟又报潮候。风急岸花飞尽也，一曲啼红满袖。春波皱。青草外、人间此恨年年有。留连握手。数人世相逢，百年欢笑，能得几回又。'),
	(5845, 282, '茶瓶儿·去年相逢深院宇', '去年相逢深院宇，海棠下、曾歌《金缕》。歌罢花如雨。 翠罗衫上，点点红无数。 今岁重寻携手处，空物是人非春暮。回首青门路。乱红飞絮，相逐东风去。'),
	(5846, 596, '浪淘沙·疏雨洗天清', '疏雨洗天清。枕簟凉生。井桐一叶做秋声。谁念客身轻似叶，千里飘零？ 梦断古台城。月淡潮平。便须携酒访新亭。不见当时王谢宅，烟草青青。'),
	(5847, 236, '满江红·代王夫人作', '试问琵琶，胡沙外、怎生风色。最苦是、姚黄一朵，移根仙阙。王母欢阑琼宴罢，仙人泪满金盘侧。听行宫、半夜雨淋铃，声声歇。 彩云散，香尘灭。铜驼恨，那堪说。想男儿慷慨，嚼穿龈血。回首昭阳离落日，伤心铜雀迎秋月。算妾身、不愿似天家，金瓯缺。'),
	(5848, 596, '酹江月·驿中言别友人', '水天空阔，恨东风不惜世间英物。蜀鸟吴花残照里，忍见荒城颓壁。铜雀春情，金人秋泪，此恨凭谁雪。堂堂剑气，斗牛空认奇杰。 那信江海余生，南行万里，属扁舟齐发。正为鸥盟留醉眼，细看涛生云灭。睨柱吞嬴，回旗走懿，千古冲冠发。伴人无寐。秦淮应是孤月。'),
	(5849, 233, '木兰花慢·莺啼啼不尽', '莺啼啼不尽，任燕语、语难通。这一点闲愁，十年不断，恼乱春风。重来故人不见，但依然、杨柳小楼东。记得同题粉壁，而今壁破无踪。 兰皋新涨绿溶溶。流恨落花红。念着破春衫，当时送别，灯下裁缝。相思谩然自苦，算云烟、过眼总成空。落日楚天无际，凭栏目送飞鸿。'),
	(5850, 233, '柳梢青·岳阳楼', '袖剑飞吟。洞庭青草，秋水深深。万顷波光，岳阳楼上，一快披襟。 不须携酒登临。问有酒、何人共斟。变尽人间，君山一点，自古如今。'),
	(5851, 293, '菩萨蛮·子规啼破城楼月', '子规啼破城楼月，画船晓载笙歌发。两岸荔枝红，万家烟雨中。 佳人相对泣，泪下罗衣湿。从此信音稀，岭南无雁飞。'),
	(5852, 638, '生查子·药名闺情', '相思意已深，白纸书难足。字字苦参商，故要檀郎读。 分明记得约当归，远至樱桃熟。何事菊花时，犹未回乡曲。'),
	(5853, 422, '声声慢', '迎门高髻，倚扇清吭，娉婷未数西州。浅拂朱铅，春风二月梢头。相逢靓妆俊语，有旧家、京洛风流。断肠句，试重拈彩笔，与赋闲愁。 犹记凌波欲去，问明榼罗袜，却为谁留。枉梦相思，几回南浦行舟。莫辞玉樽起舞，怕重来、燕子空楼。谩惆怅，抱琵琶、闲过此秋。'),
	(5854, 112, '声声慢·寿魏方泉', '莺团橙径，鲈跃莼波，重来两过中秋。酒市渔乡，西风胜似春柔。宿春去年村墅，看黄云、还委西畴。凤池去，信吴人有分，借与迟留。(橙径 一作：怅径) 应是香山续梦，又凝香追咏，重到苏州。青鬓江山，足成千岁风流。围腰御仙花底，衬月中、金粟香浮。夜宴久，揽秋云、平倚画楼。'),
	(5855, 49, '玉楼春·戏林推', '年年跃马长安市。客舍似家家似寄。青钱换酒日无何，红烛呼卢宵不寐。 易挑锦妇机中字。难得玉人心下事。男儿西北有神州，莫滴水西桥畔泪。'),
	(5856, 49, '木兰花慢·丁未中秋', '水亭凝望久，期不至、拟还差。隔翠幌银屏，新眉初画，半面犹遮。须臾淡烟薄霭，被西风扫尽不留些。失了白衣苍狗，夺回雪兔金蟆。 乘云径到玉皇家。人世鼓三挝。试自判此生，更看几度，小住为佳。何须如钩似玦，便相将、只有半菱花。莫遣素娥知道，和他发也苍华。'),
	(5857, 680, '玲珑四犯·水外轻阴', '水外轻阴，做弄得飞云，吹断晴絮。驻马桥西，还系旧时芳树。不见翠陌寻春，每问著、小桃无语。恨燕莺、不识闲情，却隔乱红飞去。 少年曾识春风意，到如今、怨怀难诉。魂惊冉冉江南远，烟草愁如许。此意待写翠笺，奈断肠、都无新句。问甚时、舞凤歌鸾，花里再看仙侣。'),
	(5858, 91, '满江红·中秋夜潮', '万水归阴，故潮信盈虚因月。偏只到、凉秋半破，斗成双绝。有物指磨金镜净，何人拏攫银河决？想子胥今夜见嫦娥，沉冤雪。 光直下，蛟龙穴；声直上，蟾蜍窟。对望中天地，洞然如刷。激气已能驱粉黛，举杯便可吞吴越。待明朝说似与儿曹，心应折！'),
	(5859, 456, '渔家傲·独木小舟烟雨湿', '独木小舟烟雨湿。燕儿乱点春江碧。江上青山随意觅。人寂寂。落花芳草催寒食。 昨夜青楼今日客。吹愁不得东风力。细拾残红书怨泣。流水急。不知那个传消息。'),
	(5860, 591, '水调歌头·落日古城角', '落日古城角，把酒劝君留。长安路远，何事风雪敝貂裘。散尽黄金身世，不管秦楼人怨，归计狎沙鸥。明夜扁舟去，和月载离愁。 功名事，身未老，几时休。诗书万卷，致身须到古伊周。莫学班超投笔，纵得封侯万里，憔悴老边州。何处依刘客，寂寞赋登楼。'),
	(5861, 591, '武陵春·桃李风前多妩媚', '桃李风前多妩媚，杨柳更温柔。唤取笙歌烂熳游。且莫管闲愁。 好趁春晴连夜赏，雨便一春休。草草杯盘不要收。才晓便扶头。'),
	(5862, 591, '定风波·用药名招婺源马荀仲游雨岩马善医', '山路风来草木香。雨余凉意到胡床。泉石膏肓吾已甚。多病。提防风月费遍章。 孤负寻常山简醉。独自。故应知子草玄忙。湖海早知身汗漫。谁伴。只甘松竹共凄凉。'),
	(5863, 591, '鹧鸪天·东阳道中', '扑面征尘去路遥，香篝渐觉水沉销。山无重数周遭碧，花不知名分外娇。 人历历，马萧萧，旌旗又过小红桥。愁边剩有相思句，摇断吟鞭碧玉梢。'),
	(5864, 188, '水调歌头·和庞佑父', '雪洗虏尘静，风约楚云留。何人为写悲壮，吹角古城楼。湖海平生豪气，关塞如今风景，剪烛看吴钩。剩喜燃犀处，骇浪与天浮。 忆当年，周与谢，富春秋，小乔初嫁，香囊未解，勋业故优游。赤壁矶头落照，肥水桥边衰草，渺渺唤人愁。我欲乘风去，击楫誓中流。'),
	(5865, 400, '如梦令·一饷凝情无语', '一饷凝情无语。手捻梅花何处。倚竹不胜愁，暗想江头归路。东去。东去。短艇淡烟疏雨。'),
	(5866, 104, '减字木兰花·去年今夜', '去年今夜，同醉月明花树下。此夜江边，月暗长堤柳暗船。 故人何处？带我离愁江外去。来岁花前，又是今年忆去年。'),
	(5867, 305, '怨王孙（春暮）', '帝里春晚。重门深院。草绿阶前，暮天雁断。楼上远信谁传。恨绵绵。 多情自是多沾惹。难拚舍又是寒食也。秋千巷陌，人静皎月初斜。浸梨花。'),
	(5868, 267, '感皇恩·一个小园儿', '一个小园儿，两三亩地。花竹随宜旋装缀。槿篱茅舍，便有山家风味。等闲池上饮，林间醉。 都为自家，胸中无事。风景争来趁游戏。称心如意，剩活人间几岁。洞天谁道在、尘寰外。'),
	(5869, 564, '望江南', '新梦断，久立暗伤春。柳下月如花下月，今年人忆去年人。往事梦中身。'),
	(5870, 549, '天香·烟络横林', '烟络横林，山沈远照，迤逦黄昏钟鼓。烛映帘栊，蛩催机杼，共苦清秋风露。不眠思妇，齐应和、几声砧杵。惊动天涯倦宦，骎骎岁华行暮。 当年酒狂自负，谓东君、以春相付。流浪征骖北道，客樯南浦，幽恨无人晤语。赖明月、曾知旧游处。好伴云来，还将梦去。'),
	(5871, 693, '南乡子·诸将说封侯', '诸将说封侯，短笛长歌独倚楼。万事尽随风雨去，休休，戏马台南金络头。 催酒莫迟留，酒味今秋似去秋。花向老人头上笑，羞羞，白发簪花不解愁。'),
	(5872, 494, '木兰花令·梧桐叶上三更雨', '梧桐叶上三更雨。惊破梦魂无觅处。夜凉枕簟已知秋，更听寒蛩促机杼。 梦中历历来时路。犹在江亭醉歌舞。尊前必有问君人，为道别来心与绪。'),
	(5873, 251, '清平乐', '秋光向晚，小阁初开宴。林叶殷红犹未遍。雨后青苔满院。 萧娘劝我金卮。殷勤更唱新词。暮去朝来即老，人生不饮何为。'),
	(5874, 340, '括水调歌', '欲状巴陵胜，千古岳之阳。洞庭在目，远衔山色俯长江。浩浩横无涯际，爽气北通巫峡，南望极潇湘。骚人与迁客，览物兴尤长。 锦鳞游，汀兰郁，水鸥翔。波澜万顷，碧色上下一天光。皓月浮金千里，把酒登楼对景，喜极自洋洋。优乐有谁会，宠辱两俱忘。 李白将进酒：君不见黄河之水天上来，奔流到海不复回。又不见高堂明镜悲白发，朝如青丝暮如雪。人生得意须尽欢。莫把金尊空对月。天生我材必有用，千金散尽还复来。烹羔宰牛且为乐，会须一饮三百怀。岑夫子、丹丘生。与君歌一曲，请君为我听。钟鼎玉帛不足贵，但愿长醉不愿醒。古来贤达皆寂寞，惟有饮者留其名。陈王昔时宴平乐，斗酒十千恣欢谑。主人何为言少钱，且须沽酒对君酌。五花马，千金裘。呼儿将出换美酒，与尔同消万古愁。'),
	(5875, 495, '渔家傲·和门人祝寿', '七十馀年真一梦。朝来寿斝儿孙奉。忧患已空无复痛。心不动。此间自有千钧重。 早岁文章供世用。中年禅味疑天纵。石塔成时无一缝。谁与共。人间天上随他送。'),
	(5876, 433, '红芍药', '人生百岁，七十稀少。更除十年孩童小。又十年昏老。都来五十载，一半被、睡魔分了。那二十五载之中，宁无些个烦恼。仔细思量，好追欢及早。遇酒追朋笑傲。任玉山摧倒。沈醉且沈醉，人生似、露垂芳草。幸新来、有酒如渑，结千秋歌笑。'),
	(5877, 139, '喜迁莺·霞散绮', '霞散绮，月沉钩，帘卷未央楼。夜凉河汉截天流，宫阙锁清秋。 瑶阶曙，金盘露，凤髓香和烟雾。三千珠翠拥宸游，水殿按凉州。'),
	(5878, 234, '忆秦娥·与君别', '与君别。相思一夜梅花发。梅花发。凄凉南浦，断桥斜月。 盈盈微步凌波袜。东风笑倚天涯阔。天涯阔。一声羌管，暮云愁绝。'),
	(5879, 422, '眉妩·新月', '渐新痕悬柳，淡彩穿花，依约破初暝。便有团圆意，深深拜，相逢谁在香径。画眉未稳，料素娥、犹带离恨。最堪爱、一曲银钩小，宝帘挂秋冷。 千古盈亏休问。叹慢磨玉斧，难补金镜。太液池犹在，凄凉处、何人重赋清景。故山夜永。试待他、窥户端正。看云外山河，还老尽、桂花影。'),
	(5880, 71, '兰陵王·丙子送春', '送春去。春去人间无路。秋千外，芳草连天，谁遣风沙暗南浦。依依甚意绪。漫忆海门飞絮。乱鸦过，斗转城荒，不见来时试灯处。 春去。最谁苦。但箭雁沈边，梁燕无主。杜鹃声里长门暮。想玉树凋土，泪盘如露。咸阳送客屡回顾。斜日未能度。 春去。尚来否。正江令恨别，庾信愁赋。二人皆北去。苏堤尽日风和雨。叹神游故国，花记前度。人生流落，顾孺子，共夜语。'),
	(5881, 112, '夜游宫·春语莺迷翠柳', '春语莺迷翠柳。烟隔断、晴波远岫。寒压重帘幔拕绣。袖炉香，倩东风，与吹透。 花讯催时候。旧相思、偏供闲昼。春澹情浓半中酒。玉痕销，似梅花，更清瘦。'),
	(5882, 117, '南柯子·池水凝新碧', '池水凝新碧，阑花驻老红。有人独立画桥东。手把一枝杨柳、系春风。 鹊绊游丝坠，蜂拈落蕊空。秋千庭院小帘栊。多少闲情闲绪、雨声中。'),
	(5883, 72, '西江月·堂上谋臣尊俎', '堂上谋臣尊俎，边头将士干戈。天时地利与人和，“燕可伐欤？”曰：“可”。 今日楼台鼎鼐，明年带砺山河。大家齐唱《大风歌》，不日四方来贺。'),
	(5884, 591, '丑奴儿·近来愁似天来大', '近来愁似天来大，谁解相怜。谁解相怜。又把愁来做个天。 都将今古无穷事，放在愁边。放在愁边。却自移家向酒泉。'),
	(5885, 591, '水调歌头·送杨民瞻', '日月如磨蚁，万事且浮休。君看檐外江水，滚滚自东流。风雨瓢泉夜半，花草雪楼春到，老子已菟裘。岁晚问无恙，归计橘千头。 梦连环，歌弹铗，赋登楼。黄鸡白酒，君去村社一番秋。长剑倚天谁问，夷甫诸人堪笑，西北有神州。此事君自了，千古一扁舟。'),
	(5886, 591, '鹊桥仙·己酉山行书所见', '松冈避暑，茅檐避雨，闲去闲来几度？醉扶怪石看飞泉，又却是、前回醒处。 东家娶妇，西家归女，灯火门前笑语。酿成千顷稻花香，夜夜费、一天风露。'),
	(5887, 591, '贺新郎·赋琵琶', '凤尾龙香拨。自开元霓裳曲罢，几番风月？最苦浔阳江头客，画舸亭亭待发。记出塞、黄云堆雪。马上离愁三万里，望昭阳宫殿孤鸿没。弦解语，恨难说。 辽阳驿使音尘绝。琐窗寒、轻拢慢捻，泪珠盈睫。推手含情还却手，一抹《梁州》哀彻。千古事，云飞烟灭。贺老定场无消息，想沉香亭北繁华歇，弹到此，为呜咽。'),
	(5888, 633, '好事近·秋晓上莲峰', '秋晓上莲峰，高蹑倚天青壁。谁与放翁为伴，有天坛轻策。 铿然忽变赤龙飞，雷雨四山黑。谈笑做成丰岁，笑禅龛榔栗。'),
	(5889, 518, '念奴娇', '岁华晼晚，念羁怀多感，佳会难卜。草草杯盘聊话旧，同剪西窗寒烛。翠袖笼香，双蛾敛恨，低按新翻曲。无情风雨，断肠更漏催促。 匆匆归骑难留，鸾屏鸳被，忍良宵孤宿。回首幽欢成梦境，唯觉衣襟芬馥。海约山盟，云情雨意，何日教心足。不如不见，为君一味愁蹙。'),
	(5890, 549, '小重山', '飘径梅英雪未融。芳菲消息到，杳梢红。隔年欢事水西东。凝思久，不语坐书空。 回想夹城中。彩山箫鼓沸，绮罗丛。钿轮珠网玉花骢。香陌上，谁与斗春风。'),
	(5891, 549, '西江月·携手看花深径', '携手看花深径，扶肩待月斜廊。临分少伫已伥伥。此段不堪回想。 欲寄书如天远，难销夜似年长。小窗风雨碎人肠。更在孤舟枕上。'),
	(5892, 494, '浪淘沙', '昨日出东城。试探春情。墙头红杏暗如倾。槛内群芳芽未吐，早已回春。 绮陌敛香尘。雪霁前村。东君用意不辞辛。料想春光先到处，吹绽梅英。'),
	(5893, 494, '西江月·送别', '昨夜扁舟京口，今朝马首长安。旧官何物与新官。只有湖山公案。 此景百年几变，个中下语千难。使君才气卷波澜。与把新诗判断。'),
	(5894, 494, '木兰花令（次马中玉韵）', '知君仙骨无寒暑。千载相逢犹旦暮。故将别语恼佳人，要看梨花枝上雨。 落花已逐回风去。花本无心莺自诉。明朝归路下塘西，不见莺啼花落处。'),
	(5895, 494, '木兰花令·次欧公西湖韵', '霜余已失长淮阔。空听潺潺清颍咽。佳人犹唱醉翁词，四十三年如电抹。 草头秋露流珠滑。三五盈盈还二八。与余同是识翁人，惟有西湖波底月。'),
	(5896, 250, '菩萨蛮·哀筝一弄湘江曲', '哀筝一弄湘江曲，声声写尽湘波绿。纤指十三弦，细将幽恨传。 当筵秋水慢，玉柱斜飞雁。弹到断肠时，春山眉黛低。'),
	(5897, 162, '锦缠道·燕子呢喃', '燕子呢喃，景色乍长春昼。睹园林、万花如绣。海棠经雨胭脂透。柳展宫眉，翠拂行人首。 向郊原踏青，恣歌携手。醉醺醺、尚寻芳酒。问牧童、遥指孤村道：“杏花深处，那里人家有。”'),
	(5898, 613, '扬州慢·琼花', '弄玉轻盈，飞琼淡泞，袜尘步下迷楼。试新妆才了，炷沈水香球。记晓剪、春冰驰送，金瓶露湿，缇骑新流。甚天中月色，被风吹梦南州。 尊前相见，似羞人、踪迹萍浮。问弄雪飘枝，无双亭上，何日重游。我欲缠腰骑鹤，烟霄远、旧事悠悠。但凭阑无语，烟花三月春愁。'),
	(5899, 31, '风入松·一春长费买花钱', '一春长费买花钱，日日醉湖边。玉骢惯识西湖路，骄嘶过、沽酒楼前。红杏香中箫鼓，绿杨影里秋千。 暖风十里丽人天，花压鬓云偏。画船载取春归去，馀情付、湖水湖烟。明日重扶残醉，来寻陌上花钿。'),
	(5900, 408, '减字木兰花·春情', '画桥流水。雨湿落红飞不起。月破黄昏。帘里馀香马上闻。 徘徊不语。今夜梦魂何处去。不似垂杨。犹解飞花入洞房。'),
	(5901, 232, '青玉案·丝槐烟柳长亭路', '丝槐烟柳长亭路，恨取次、分离去。日永如年愁难度。高城回首，暮云遮尽，目断人何处？ 解鞍旅舍天将暮，暗忆叮咛千万句。一寸柔肠情几许？薄衾孤枕，梦回人静，彻晓潇潇雨。'),
	(5902, 515, '燕归梁·凤莲', '我梦唐宫春昼迟。正舞到、曳裾时。翠云队仗绛霞衣。慢腾腾、手双垂。 忽然急鼓催将起，似彩凤、乱惊飞。梦回不见万琼妃。见荷花、被风吹。'),
	(5903, 366, '水龙吟·淮河舟中夜闻宫人琴声', '鼓鞞惊破霓裳，海棠亭北多风雨。歌阑酒罢，玉啼金泣，此行良苦。驼背模糊，马头匼匝，朝朝暮暮。自都门燕别，龙艘锦缆，空载得，春归去。 目断东南半壁，怅长淮已非吾土。受降城下，草如霜白，凄凉酸楚。粉阵红围，夜深人静，谁宾谁主？对渔灯一点，羁愁一搦，谱琴中语。'),
	(5904, 124, '高阳台·送陈君衡被召', '照野旌旗，朝天车马，平沙万里天低。宝带金章，尊前茸帽风欹。秦关汴水经行地，想登临、都付新诗。纵英游，叠鼓清笳，骏马名姬。 酒酣应对燕山雪，正冰河月冻，晓陇云飞。投老残年，江南谁念方回。东风渐绿西湖柳，雁已还、人未南归。最关情，折尽梅花，难寄相思。'),
	(5905, 557, '扬州慢', '花大而瓣厚，其色淡黄，聚八仙花小而瓣薄，其色微青，不同者一也。琼花叶柔而莹泽，聚八仙叶粗而有芒，不同者二也。琼花蕊与花平，不结子而香，聚八仙蕊低于花，结子而不香，不同者三也。友人折赠数枝，云移根自鄱阳之洪氏。赋而感之，其调曰扬州慢。 十里春风，二分明月，蕊仙飞下琼楼。看冰花翦翦，拥碎玉成球。想长日、云阶伫立，太真肌骨，飞燕风流。敛群芳、清丽精神，都付扬州。 雨窗数朵，梦惊回、天际香浮。似阆苑花神，怜人冷落，骑鹤来游。为问竹西风景，长空淡、烟水悠悠。又黄昏羌管，孤城吹起新愁。'),
	(5906, 91, '秋霁·江水苍苍', '江水苍苍，望倦柳愁荷，共感秋色。废阁先凉，古帘空暮，雁程最嫌风力。故园信息，爱渠入眼南山碧。念上国，谁是、脍鲈江汉未归客。 还又岁晚，瘦骨临风，夜闻秋声，吹动岑寂。露蛩悲，青灯冷屋，翻书愁上鬓毛白。年少俊游浑断得，但可怜处，无奈苒苒魂惊，采香南浦，剪梅烟驿。'),
	(5907, 72, '六州歌头·寄稼轩承旨', '寄辛承旨。时承旨招，不赴。 斗酒彘肩，风雨渡江，岂不快哉！被香山居士，约林和靖，与坡仙老，驾勒吾回。坡谓西湖，正如西子，浓抹淡妆临镜台。二公者，皆掉头不顾，只管衔杯。 白云天竺去来，图画里、峥嵘楼观开。爱东西双涧，纵横水绕；两峰南北，高下云堆。逋曰不然，暗香浮动，争似孤山先探梅。须晴去，访稼轩未晚，且此徘徊。'),
	(5908, 591, '汉宫春·会稽秋风亭观雨', '亭上秋风，记去年袅袅，曾到吾庐。山河举目虽异，风景非殊。功成者去，觉团扇、便与人疏。吹不断，斜阳依旧，茫茫禹迹都无。 千古茂陵词在，甚风流章句，解拟相如。只今木落江冷，眇眇愁余。故人书报，莫因循、忘却蓴鲈。谁念我，新凉灯火，一编太史公书。'),
	(5909, 591, '清平乐', '东园向晓。阵阵西风好。唤起仙人金小小。翠羽玲珑装了。 一枝枕畔开时。罗帏翠幕低垂。恁地十分遮护，打窗早有蜂儿。'),
	(5910, 591, '浣溪沙·泉湖道中赴闽宪别诸君', '细听春山杜宇啼。一声声是送行诗。朝来白鸟背人飞。 对郑子真岩石卧，趁陶元亮菊花期。而今堪诵北山移。'),
	(5911, 591, '鹧鸪天', '自古高人最可嗟。只因疏懒取名多。居山一似庚桑楚，种树真成郭橐驼。 云子饭，水晶瓜。林间携客更烹茶。君归休矣吾忙甚，要看蜂儿趁晚衙。'),
	(5912, 591, '最高楼·吾衰矣', '吾拟乞归，犬子以田产未置止我，赋此骂之。 吾衰矣，须富贵何时？富贵是危机。暂忘设醴抽身去，未曾得米弃官归。穆先生，陶县令，是吾师。 待葺个园儿名“佚老”，更作个亭儿名“亦好”，闲饮酒，醉吟诗。千年田换八百主，一人口插几张匙？便休休，更说甚，是和非！'),
	(5913, 591, '满江红·赣州席上呈陈季陵太守', '落日苍茫，风才定、片帆无力。还记得、眉来眼去，水光山色。倦客不知身近远，佳人已卜归消息。便归来、只是赋行云，襄王客。 些个事，如何得。知有恨，休重忆。但楚天特地，暮云凝碧。过眼不如人意事，十常八九今头白。笑江州、司马太多情，青衫湿。'),
	(5914, 188, '卜算子·雪月最相宜', '雪月最相宜，梅雪都清绝。去岁江南见雪时，月底梅花发。 今岁早梅开，依旧年时月。冷艳孤光照眼明，只欠些儿雪。'),
	(5915, 633, '浣溪沙·和无咎韵', '懒向沙头醉玉瓶，唤君同赏小窗明。夕阳吹角最关情。 忙日苦多闲日少，新愁常续旧愁生。客中无伴怕君行。'),
	(5916, 192, '踏莎行·秋入云山', '秋入云山，物情潇洒。百般景物堪图画。丹枫万叶碧云边，黄花千点幽岩下。 已喜佳辰，更怜清夜。一轮明月林梢挂。松醪常与野人期，忘形共说清闲话。'),
	(5917, 185, '贺新郎·寄李伯纪丞相', '曳杖危楼去。斗垂天、沧波万顷，月流烟渚。扫尽浮云风不定，未放扁舟夜渡。宿雁落、寒芦深处。怅望关河空吊影，正人间、鼻息鸣鼍鼓。谁伴我，醉中舞。 十年一梦扬州路。倚高寒、愁生故国，气吞骄虏。要斩楼兰三尺剑，遗恨琵琶旧语。谩暗涩铜华尘土。唤取谪仙平章看，过苕溪、尚许垂纶否。风浩荡，欲飞举。'),
	(5918, 315, '喜迁莺·晋师胜淝上', '长江千里。限南北、雪浪云涛无际。天险难逾，人谋克庄，索虏岂能吞噬。阿坚百万南牧，倏忽长驱吾地。破强敌，在谢公处画，从容颐指。 奇伟。淝水上，八千戈甲，结阵当蛇豕。鞭弭周旋，旌旗麾动，坐却北军风靡。夜闻数声鸣鹤，尽道王师将至。延晋祚，庇烝民，周雅何曾专美。'),
	(5919, 559, '醉落魄·预赏景龙门追悼明节皇后', '无言哽噎。看灯记得年时节。行行指月行行说。愿月常圆，休要暂时缺。 今年华市灯罗列。好灯争奈人心别。人前不敢分明说。不忍抬头，羞见旧时月。'),
	(5920, 267, '念奴娇·插天翠柳', '插天翠柳，被何人，推上一轮明月。照我藤床凉似水，飞入瑶台琼阙。雾冷笙箫，风轻环佩，玉锁无人掣。闲云收尽，海光天影相接。 谁信有药长生，素娥新链就、飞霜凝雪。打碎珊瑚，争似看、仙桂扶疏横绝。洗尽凡心，满身清露，冷浸萧萧发。明朝尘世，记取休向人说。'),
	(5921, 208, '沁园春', '急急修行，细算人生，能有几时。任万般千种风流好，奈一朝身死，不免抛离。蓦地思量，死生事大，使我心如刀剑挥。难留住，那金乌箭疾，玉兔梭飞。 早觉悟、莫教迟。我清净、谁能婚少妻。便假饶月里，姮娥见在，从他越国，有貌西施。此个风流，更无心恋，且放宽怀免是非。蓬莱路，仗三千行满，独跨鸾归。'),
	(5922, 544, '鹊桥仙·月胧星淡', '月胧星淡，南飞乌鹊，暗数秋期天上。锦楼不到野人家，但门外、清流叠嶂。 一杯相属，佳人何在，不见绕梁清唱。人间平地亦崎岖，叹银汉、何曾风浪。'),
	(5923, 360, '临江仙·都城元夕', '闻道长安灯夜好，雕轮宝马如云。蓬莱清浅对觚棱。玉皇开碧落，银界失黄昏。 谁见江南憔悴客，端忧懒步芳尘。小屏风畔冷香凝。酒浓春入梦，窗破月寻人。'),
	(5924, 553, '浣溪沙·水满池塘花满枝', '水满池塘花满枝。乱香深里语黄鹂。东风轻软弄帘帏。 日正长时春梦短，燕交飞处柳烟低。玉窗红子斗棋时。'),
	(5925, 248, '绿头鸭·咏月', '晚云收，淡天一片琉璃。烂银盘、来从海底，皓色千里澄辉。莹无尘、素娥淡伫，静可数、丹桂参差。玉露初零，金风未凛，一年无似此佳时。露坐久，疏萤时度，乌鹊正南飞。瑶台冷，栏干凭暖，欲下迟迟。 念佳人、音尘别后，对此应解相思。最关情、漏声正永，暗断肠、花影偷移。料得来宵，清光未减，阴晴天气又争知。共凝恋、如今别后，还是隔年期。人强健，清尊素影，长愿相随。'),
	(5926, 494, '江城子', '腻红匀脸衬檀唇。晚妆新。暗伤春。手捻花枝，谁会两眉颦。连理带头双□□，留待与、个中人。 淡烟笼月绣帘阴。画堂深。夜沈沈、谁道□□，□系得人心。一自绿窗偷见后，便憔悴、到如今。'),
	(5927, 250, '浣溪沙·闲弄筝弦懒系裙', '闲弄筝弦懒系裙。铅华消尽见天真。眼波低处事还新。 怅恨不逢如意酒。寻思难值有情人。可怜虚度琐窗春。'),
	(5928, 323, '卜算子·樽前一曲歌', '樽前一曲歌，歌里千重意。才欲歌时泪已流，恨应更、多于泪。 试问缘何事？不语如痴醉。我亦情多不忍闻，怕和我、成憔悴。'),
	(5929, 323, '卜算子', '深院花铺地。淡淡阴天气。水榭风亭朱明景，又别是、愁情味。 有情奈无计。漫惹成憔悴。欲把罗巾暗传寄。细认取、斑点泪。'),
	(5930, 186, '浣溪沙·楼倚春江百尺高', '楼倚春江百尺高，烟中还未见归桡，几时期信似江潮？ 花片片飞风弄蝶，柳阴阴下水平桥，日长才过又今宵。'),
	(5931, 186, '画堂春·外湖莲子长参差', '外湖莲子长参差，霁山青处鸥飞。水天溶漾画桡迟，人影鉴中移。 桃叶浅声双唱，杏红深色轻衣。小荷障面避斜晖，分得翠阴归。'),
	(5932, 201, '更漏子·秋', '墨痕香，红蜡泪。点点愁人离思。桐叶落，蓼花残。雁声天外寒。 五云岭，九溪坞。待到秋来更苦。风淅淅，水淙淙。不教蓬径通。'),
	(5933, 340, '括贺新凉', '生长湖山曲。羡吴儿、呼吸湖光，饱餐山渌。世外不须论隐逸，谁似先生冰玉。自骨冷、神清无俗。我不识君曾梦见，炯双瞳、碧色光相烛。遗妙语，看不足。 生平高节难为绩。到如今、凛凛风生，言犹可录。不作相如封禅藁，身后谁荣谁辱。争肯效、白头吟曲。好与水仙为伴侣，傍西湖、湖畔脩脩竹。时一酹，荐秋菊。 范文正岳阳楼记：庆西四年春，滕子京谪守巴陵。越明年，政通人和，百废具兴。乃重修岳阳楼，增其旧制，刻唐贤今人诗赋于其上，属余作文记之。予观夫巴陵状，在洞庭一湖。衔远山，吞长江，浩浩荡荡，横无际涯，朝晖夕阴，气象万千，此则岳阳楼之大观也，前人之述备矣，然则北通巫峡，南极潇湘，迁客骚人，多会于此，览物之情，得无异乎！若夫霪雨霏霏，连日不开，阴风怒号，浊浪排空，日星隐耀，山岳潜形，商旅不行，樯倾楫摧，薄暮冥冥，虎啸猿啼。登斯楼也，则有去国怀乡，忧谗畏讥，满目萧然，感极而悲者矣。至若春和景明，波澜不惊，上下天光，一碧万顷，沙鸥翔集，锦鳞游泳，岸芷汀兰，郁郁青青；而或长烟一空，皓月千里，浮光耀金，静影沉璧，渔歌互答，此乐何极。登斯楼也，则有心旷神怡，宠辱皆忘，把酒临风，其喜洋洋者矣。嗟夫！予尝求古仁人之心，或异二者之为。何哉？不以物喜，不以已悲。居庙堂之高，则忧其民；处江湖之远，则忧其君。是进亦忧，退亦忧。然则何时而乐耶？其必曰：先天下之忧而忧，后天下之乐而乐欤！噫，微斯人，吾谁与归！时六年九月二十五日记。'),
	(5934, 434, '忆故人·烛影摇红', '烛影摇红，向夜阑，乍酒醒、心情懒。尊前谁为唱《阳关》，离恨天涯远。 无奈云沉雨散。凭阑干、东风泪眼。海棠开后，燕子来时，黄昏庭院。'),
	(5935, 4, '忆秦娥', '天如洗。金波冷浸冰壶里。冰壶里。一年得似，此宵能几。 等闲莫把阑干倚。马蹄去便三千里。三千里。几重云岫，几重烟水。'),
	(5936, 4, '诉衷情·送春', '一鞭清晓喜还家。宿醉困流霞。夜来小雨新霁，双燕舞风斜。 山不尽，水无涯。望中赊。送春滋味，念远情怀，分付杨花。'),
	(5937, 58, '踏莎行·闲游', '水际轻烟，沙边微雨。荷花芳草垂杨渡。多情移徙忽成愁，依稀恰是西湖路。 血染红笺，泪题锦句。西湖岂忆相思苦。只应幽梦解重来，梦中不识从何去。'),
	(5938, 112, '思佳客·癸卯除夜', '自唱新词送岁华。鬓丝添得老生涯。十年旧梦无寻处，几度新春不在家。 衣懒换，酒难赊。可怜此夕看梅花。隔年昨夜青灯在，无限妆楼尽醉哗。'),
	(5939, 662, '卜算子', '梅岭数枝春，疏影斜临水。不借芳华只自香，娇面长如洗。 还把最繁枝，过与偏怜底。试把鸾台子细看，何似丹青里。'),
	(5940, 615, '醉落魄·丙寅中秋', '琼楼玉宇。分明不受人间暑。寻常岂是无三五。惟有今宵，皓彩皆同普。 素娥阅尽今和古。何妨小驻听吾语。当年弄影婆娑舞。妙曲虽传，毕竟人何许。'),
	(5941, 149, '翠楼吟·淳熙丙午冬', '淳熙丙午冬，武昌安远楼成，与刘去非诸友落之，度曲见志。予去武昌十年，故人有泊舟鹦鹉洲者，闻小姬歌此词，问之，颇能道其事，还吴为余言之；兴怀昔游，且伤今之离索也。 月冷龙沙，尘清虎落，今年汉酺初赐。新翻胡部曲，听毡幕元戎歌吹。层楼高峙。看槛曲萦红，檐牙飞翠。人姝丽，粉香吹下，夜寒风细。 此地宜有词仙，拥素云黄鹤，与君游戏。玉梯凝望久，叹芳草萋萋千里。天涯情味。仗酒祓清愁，花销英气。西山外，晚来还卷，一帘秋霁。'),
	(5942, 498, '醉落魄·栖乌飞绝', '栖乌飞绝。绛河绿雾星明灭。烧香曳簟眠清樾。花影吹笙，满地淡黄月。 好风碎竹声如雪。昭华三弄临风咽。鬓丝撩乱纶巾折。凉满北窗，休共软红说。'),
	(5943, 509, '卜算子·赏荷以莲叶劝酒作', '明镜盖红蕖，轩户临烟渚。窣窣珠帘淡淡风，香里开尊俎。 莫把碧筒弯，恐带荷心苦。唤我溪边太乙舟，潋滟盛芳醑。'),
	(5944, 264, '浣溪沙', '绮陌寻芳惜少年。长楸走马著金鞭。玉楼春醉杏花前。 憔悴如今谁作伴，别离还近养花天。碧云凝处忆婵娟。'),
	(5945, 267, '鹊桥仙', '姮娥怕闹，银蟾传令，且与遮鸾翳凤。直须人睡俗尘清，放云汉、冰轮徐动。 山翁散发，披衣松下，琴奏瑶池三弄。曲终鹤警露华寒，笑浊世、饶伊做梦。'),
	(5946, 130, '虞美人·廉纤小雨池塘遍', '廉纤小雨池塘遍。细点看萍面。一双燕子守朱门。比似寻常时候、易黄昏。 宜城酒泛浮香絮。细作更阑语。相将羁思乱如云。又是一窗灯影、两愁人。'),
	(5947, 130, '虞美人·疏篱曲径田家小', '疏篱曲径田家小。云树开清晓。天寒山色有无中。野外一声钟起、送孤蓬。 添衣策马寻亭堠。愁抱惟宜酒。菰蒲睡鸭占陂塘。纵被行人惊散、又成双。'),
	(5948, 130, '绮寮怨·上马人扶残醉', '上马人扶残醉，晓风吹未醒。映水曲、翠瓦朱檐，垂杨里、乍见津亭。当时曾题败壁，蛛丝罩、淡墨苔晕青。念去来、岁月如流，徘徊久、叹息愁思盈。 去去倦寻路程。江陵旧事，何曾再问杨琼。旧曲凄清。敛愁黛、与谁听。尊前故人如在，想念我、最关情。何须渭城。歌声未尽处，先泪零。'),
	(5949, 130, '忆旧游·记愁横浅黛', '记愁横浅黛，泪洗红铅，门掩秋宵。坠叶惊离思，听寒螿夜泣，乱雨潇潇。凤钗半脱云鬓，窗影烛光摇。渐暗竹敲凉，疏萤照晚，两地魂消。 迢迢，问音信，道径底花阴，时认鸣镳。也拟临朱户，叹因郎憔悴，羞见郎招。旧巢更有新燕，杨柳拂河桥。但满目京尘，东风竟日吹露桃。'),
	(5950, 451, '南歌子·香墨弯弯画', '香墨弯弯画，燕脂淡淡匀。揉蓝衫子杏黄裙，独倚玉阑无语点檀唇。 人去空流水，花飞半掩门。乱山何处觅行云？又是一钩新月照黄昏。'),
	(5951, 278, '蝶恋花·天淡云闲晴昼永', '天淡云闲晴昼永。庭户深沈，满地梧桐影。骨冷魂清如梦醒。梦回犹是前时景。 取次杯盘催酩酊。醉帽频欹，又被风吹正。踏月归来人已静。恍疑身在蓬莱顶。'),
	(5952, 494, '定风波·红梅', '好睡慵开莫厌迟。自怜冰脸不时宜。偶作小红桃杏色，闲雅，尚馀孤瘦雪霜姿。 休把闲心随物态，何事，酒生微晕沁瑶肌。诗老不知梅格在，吟咏，更看绿叶与青枝。'),
	(5953, 494, '一丛花·初春病起', '今年春浅腊侵年，冰雪破春妍。东风有信无人见，露微意、柳际花边。寒夜纵长，孤衾易暖，钟鼓渐清圆。 朝来初日半衔山，楼阁淡疏烟。游人便作寻芳计，小桃杏、应已争先。衰病少悰，疏慵自放，惟爱日高眠。'),
	(5954, 250, '蝶恋花·笑艳秋莲生绿浦', '笑艳秋莲生绿浦。红脸青腰，旧识凌波女。照影弄妆娇欲语。西风岂是繁花主。 可恨良辰天不与。才过斜阳，又是黄昏雨。朝落暮开空自许。竟无人解知心苦。'),
	(5955, 352, '浣溪沙·湖上朱桥响画轮', '湖上朱桥响画轮，溶溶春水浸春云，碧琉璃滑净无尘。 当路游丝萦醉客，隔花啼鸟唤行人，日斜归去奈何春。'),
	(5956, 352, '朝中措·送刘仲原甫出守维扬', '平山阑槛倚晴空。山色有无中。手种堂前垂柳，别来几度春风。 文章太守，挥毫万字，一饮千钟。行乐直须年少，尊前看取衰翁。'),
	(5957, 344, '木兰花慢·拆桐花烂熳', '拆桐花烂熳，乍疏雨、洗清明。正艳杏烧林，缃桃绣野，芳景如屏。倾城，尽寻胜去，骤雕鞍绀幰出郊坰。风暖繁弦脆管，万家竞奏新声。 盈盈，斗草踏青。人艳冶，递逢迎。向路旁往往，遗簪堕珥，珠翠纵横。欢情，对佳丽地，信金罍罄竭玉山倾。拚却明朝永日，画堂一枕春酲。'),
	(5958, 272, '忆秦娥（雪、梅二阕怀张敬夫）', '云垂幕。阴风惨淡天花落。天花落。千林琼玖，一空莺鹤。征车渺涉穿华薄。路迷迷路增离索。增离索。剡溪山水，碧湘楼阁。'),
	(5959, 162, '浪淘沙近', '少年不管。流光如箭。因循不觉韶光换。至如今，始惜月满、花满、酒满。 扁舟欲解垂杨岸。尚同欢宴。日斜歌阕将分散。倚兰桡，望水远、天远、人远。'),
	(5960, 15, '声声慢', '藏莺院静，浮鸭池荒。绿阴不减红芳。高卧虚堂。南风时送微凉。游鞯践香未遍，怪青春、别我堂堂。闲里好，有故书盈箧，新酒盈缸。 只怕吴霜侵鬓，叹春深铜雀，空老周郎。弱絮沾泥，如今梦冷平康。翻思旧游踪迹，认断云、低度横塘。离恨满，甚月明、偏照小窗。'),
	(5961, 112, '鹧鸪天·化度寺作', '池上红衣伴倚阑，栖鸦常带夕阳还。殷云度雨疏桐落，明月生凉宝扇闲。 乡梦窄，水天宽。小窗愁黛淡秋山。吴鸿好为传归信，杨柳阊门屋数间。'),
	(5962, 112, '澡兰香·淮安重午', '盘丝系腕，巧篆垂簪，玉隐绀纱睡觉。银瓶露井，彩箑云窗，往事少年依约。为当时曾写榴裙，伤心红绡褪萼。黍梦光阴，渐老汀洲烟蒻。 莫唱江南古调，怨抑难招，楚江沉魄。薰风燕乳，暗雨梅黄，午镜澡兰帘幕。念秦楼也拟人归，应剪菖蒲自酌。但怅望、一缕新蟾，随人天角。'),
	(5963, 112, '浣溪沙·波面铜花冷不收', '波面铜花冷不收。玉人垂钓理纤钩。月明池阁夜来秋。 江燕话归成晓别，水花红减似春休。西风梧井叶先愁。'),
	(5964, 695, '忆秦娥·秋萧索', '秋萧索。梧桐落尽西风恶。西风恶。数声新雁，数声残角。 离愁不管人飘泊。年年孤负黄花约。黄花约。几重庭院，几重帘幕。'),
	(5965, 680, '祝英台近·荷花', '拥红妆，翻翠盖，花影暗南浦。波面澄霞，兰艇采香去。有人水溅红裙，相招晚醉，正月上、凉生风露。 两凝伫。别后歌断云间，娇姿黯无语。魂梦西风，端的此心苦。遥想芳脸轻颦，凌波微步，镇输与、沙边鸥鹭。'),
	(5966, 680, '凤栖梧', '云唤阴来鸠唤雨。谢了江梅，可踏江头路。拚却一番花信阻。不成日日春寒去。 见说东风桃叶渡。岸隔青山，依旧修眉妩。归雁不如筝上柱。一行常见相思苦。'),
	(5967, 72, '四字令·情深意真', '情深意真。眉长鬓青。小楼明月调筝。写春风数声。 思君忆君。魂牵梦萦。翠销香暖云屏。更那堪酒醒。'),
	(5968, 591, '西江月·示儿曹以家事付之 / 西江月·万事云烟忽过', '万事云烟忽过，一身蒲柳先衰。而今何事最相宜，宜醉宜游宜睡。 早趁催科了纳，更量出入收支。乃翁依旧管些儿，管竹管山管水。'),
	(5969, 633, '长相思（五之二）', '桥如虹。水如空。一叶飘然烟雨中。天教称放翁。 侧船篷。使江风。蟹舍参差渔市东。到时闻暮钟。'),
	(5970, 359, '画堂春', '华灯收尽雪初残。踏青还尔游盘。落梅强半已飞翻。刬地春寒。 多病故人日远，几时双燕来还。可怜楼上一凭栏。不见长安。'),
	(5971, 400, '浣溪沙', '体粟须烦鼎力苏。流涎正值麹盈车。坐来兽炭拨还无。 一阕可能酬一绝，双银端不换双珠。松毛粉白老翁须。'),
	(5972, 105, '南歌子', '远色连朱阁，寒鸦噪夕阳。小炉温手酌鹅黄。掩乱一枝清影、在寒窗。 念远歌声小，嗔归泪眼长。纤腰今属冶游郎。朝暮楚宫云雨、恨茫茫'),
	(5973, 489, '鹧鸪天·枫落河梁野水秋', '枫落河梁野水秋。淡烟衰草接郊丘。醉眠小坞黄茅店，梦倚高城赤叶楼。 天杳杳，路悠悠。钿筝歌扇等闲休。灞桥杨柳年年恨，鸳浦芙蓉叶叶愁。'),
	(5974, 247, '临江仙·忆昔西池池上饮', '忆昔西池池上饮，年年多少欢娱。别来不寄一行书。寻常相见了，犹道不如初。 安稳锦屏今夜梦，月明好渡江湖。相思休问定何如。情知春去后，管得落花无。'),
	(5975, 130, '解连环·怨怀无托', '怨怀无托。嗟情人断绝，信音辽邈。纵妙手、能解连环，似风散雨收，雾轻云薄。燕子楼空，暗尘锁、一床弦索。想移根换叶。尽是旧时，手种红药。 汀洲渐生杜若。料舟依岸曲，人在天角。谩记得、当日音书，把闲语闲言，待总烧却。水驿春回，望寄我、江南梅萼。拚今生，对花对酒，为伊泪落。'),
	(5976, 209, '风流子·木叶亭皋下', '木叶亭皋下，重阳近，又是捣衣秋。奈愁入庾肠，老侵潘鬓，谩簪黄菊，花也应羞。楚天晚，白苹烟尽处，红蓼水边头。芳草有情，夕阳无语，雁横南浦，人倚西楼。 玉容知安否？香笺共锦字，两处悠悠。空恨碧云离合，青鸟沉浮。向风前懊恼，芳心一点，寸眉两叶，禁甚闲愁？情到不堪言处，分付东流。'),
	(5977, 249, '忆少年·别历下', '无穷官柳，无情画舸，无根行客。南山尚相送，只高城人隔。 罨画园林溪绀碧。算重来、尽成陈迹。刘郎鬓如此，况桃花颜色。'),
	(5978, 549, '菩萨蛮·彩舟载得离愁动', '彩舟载得离愁动，无端更借樵风送。波渺夕阳迟，销魂不自持。 良宵谁与共，赖有窗间梦。可奈梦回时，一番新别离！'),
	(5979, 494, '醉蓬莱·重九上君猷', '余谪居黄，三见重九，每岁与太守徐君猷会于栖霞。今年公将去，乞郡湖南。念此惘然，故作此词。 笑劳生一梦，羁旅三年，又还重九。华发萧萧，对荒园搔首。赖有多情，好饮无事，似古人贤守。岁岁登高，年年落帽，物华依旧。 此会应须烂醉，仍把紫菊茱萸，细看重嗅。摇落霜风，有手栽双柳。来岁今朝，为我西顾，酹羽觞江口。会与州人，饮公遗爱，一江醇酎。'),
	(5980, 210, '卖花声·楼上久踟躇', '楼上久踟躇。地远身孤。拟将憔悴吊三闾。自是长安日下影，流落江湖。 烂醉且消除。不醉何如。又看暝色满平芜。试问寒沙新到雁，应有来书。'),
	(5981, 250, '诉衷情·凭觞静忆去年秋', '凭觞静忆去年秋，桐落故溪头。诗成自写红叶，和恨寄东流。 人脉脉，水悠悠。几多愁。雁书不到，蝶梦无凭，漫倚高楼。'),
	(5982, 250, '浣溪沙·二月春花厌落梅', '二月春花厌落梅。仙源归路碧桃催。渭城丝雨劝离杯。 欢意似云真薄幸，客鞭摇柳正多才，凤楼人待锦书来。'),
	(5983, 250, '清平乐·千花百草', '千花百草。送得春归了。拾蕊人稀红渐少。叶底杏青梅小。 小琼闲抱琵琶。雪香微透轻纱。正好一枝娇艳，当筵独占韶华。'),
	(5984, 408, '清平乐·留春不住', '留春不住，费尽莺儿语。满地残红宫锦污，昨夜南园风雨。 小怜初上琵琶，晓来思绕天涯。不肯画堂朱户，春风自在杨花。'),
	(5985, 352, '武陵春', '宝幄华灯相见夜，妆脸小桃红。斗帐香檀翡翠笼。携手恨匆匆。 金泥双结同心带，留与记情浓。却望行云十二峰。肠断月斜钟。'),
	(5986, 344, '彩云归·蘅皋向晚舣轻航', '蘅皋向晚舣轻航。卸云帆、水驿鱼乡。当暮天、霁色如晴昼，江练静、皎月飞光。那堪听、远村羌管，引离人断肠。此际浪萍风梗，度岁茫茫。 堪伤。朝欢暮散，被多情、赋与凄凉。别来最苦，襟袖依约，尚有余香。算得伊、鸳衾凤枕，夜永争不思量。牵情处，惟有临歧，一句难忘。'),
	(5987, 637, '长相思·花深深', '花深深。柳阴阴。度柳穿花觅信音。君心负妾心。 怨呜琴。恨孤衾。钿誓钗盟何处寻。当初谁料今。'),
	(5988, 10, '玉楼春·春思', '春风只在园西畔。荠菜花繁胡蝶乱。冰池晴绿照还空，香径落红吹已断。 意长翻恨游丝短。尽日相思罗带缓。宝奁明月不欺人，明日归来君试看。'),
	(5989, 272, '南乡子·落日照楼船', '落日照楼船，稳过澄江一片天。珍重使君留客意，依然，风月从今别一川。 离绪悄危弦，永夜清霜透幕毡。明日回头江树远，怀贤，目断晴空雁字连。'),
	(5990, 101, '长相思', '行相思。坐相思。两处相思各自知。相思更为谁。 朝相思。暮相思。一日相思十二时。相思无尽期。'),
	(5991, 158, '滴滴金·梅', '月光飞入林前屋。风策策，度庭竹。夜半江城击柝声，动寒梢栖宿。 等闲老去年华促，只有江梅伴幽独。梦绕夷门旧家山，恨惊回难续。'),
	(5992, 4, '长相思·山驿', '短长亭，古今情。楼外凉蟾一晕生，雨余秋更清。 暮云平，暮山横。几叶秋声和雁声，行人不要听。'),
	(5993, 4, '南歌子', '梅夏暗丝雨，麦秋扇浪风。香芦结黍趁天中。五日凄凉今古、与谁同。下缺。'),
	(5994, 202, '鹧鸪天·楼上谁将玉笛吹', '楼上谁将玉笛吹？山前水阔暝云低。劳劳燕子人千里，落落梨花雨一枝。 修禊近，卖饧时。故乡惟有梦相随。夜来折得江头柳，不是苏堤也皱眉。'),
	(5995, 515, '霜天晓角·人影窗纱', '人影窗纱。是谁来折花。折则从他折去，知折去、向谁家。 檐牙。枝最佳。折时高折些。说与折花人道，须插向、鬓边斜。'),
	(5996, 112, '浣溪沙·题李中斋舟中梅屏', '冰骨清寒瘦一枝。玉人初上木兰时。懒妆斜立澹春姿。 月落溪穷清影在，日长春去画帘垂。五湖水色掩西施。'),
	(5997, 535, '后庭花·一春不识西湖面', '一春不识西湖面。翠羞红倦。雨窗和泪摇湘管。意长笺短。 知心惟有雕梁燕。自来相伴。东风不管琵琶怨。落花吹遍。'),
	(5998, 117, '满江红·送李御带珙', '红玉阶前，问何事、翩然引去。湖海上、一汀鸥鹭，半帆烟雨。报国无门空自怨，济时有策从谁吐。过垂虹亭下系扁舟，鲈堪煮。 拼一醉，留君住。歌一曲，送君路。遍江南江北，欲归何处。世事悠悠浑未了，年光冉冉今如许。试举头、一笑问青天，天无语。'),
	(5999, 49, '贺新郎·送陈真州子华', '北望神州路。试平章、这场公事，怎生分付。记得太行山百万，曾入宗爷驾驭。今把作、握蛇骑虎。君去京东豪杰喜，想投戈、下拜真吾父。谈笑里，定齐鲁。 两河萧瑟惟狐兔。问当年、祖生去后，有人来否。多少新亭挥泪客，谁梦中原块土。算事业、须由人做。应笑书生心胆怯，向车中、闭置如新妇。空目送，塞鸿去。'),
	(6000, 49, '满江红·夜雨凉甚忽动从戎之兴', '金甲雕戈，记当日、辕门初立。磨盾鼻、一挥千纸，龙蛇犹湿。铁马晓嘶营壁冷，楼船夜渡风涛急。有谁怜、猿臂故将军，无功级。 平戎策，从军什。零落尽，慵收拾。把茶经香传，时时温习。生怕客谈榆塞事，且教儿诵花间集。叹臣之壮也不如人，今何及。'),
	(6001, 239, '苏幕遮', '扇留风，冰却暑。夏木阴阴，相对黄鹂语。薄晚轻阴还阁雨。远岸烟深，仿佛菱歌举。 燕归来，花落去。几度逢迎，几度伤羁旅。油壁西陵人识否。好约追凉，小舣兼葭浦。'),
	(6002, 149, '小重山令·赋潭州红梅', '人绕湘皋月坠时。斜横花树小，浸愁漪。一春幽事有谁知？东风冷，香远茜裙归。 鸥去昔游非。遥怜花可可，梦依依。九疑云杳断魂啼。相思血，都沁绿筠枝。'),
	(6003, 72, '江城子', '淡香幽艳露华浓。晚妆慵。略匀红。春困恹恹，□□鬓云松。早是自来莲步小，新样子，为谁弓。画堂西畔曲栏东。醉醒中。苦匆匆。卷上珠帘，依旧半床空。香灺满炉人未寝，花弄月，竹摇风。'),
	(6004, 591, '蝶恋花', '何物能令公怒喜。山要人来，人要山无意。恰似哀筝弦下齿。千情万意无时已。 自要溪堂韩作记。今代机云，好语花难比。老眼狂花空处起。银钩未见心先醉。'),
	(6005, 591, '浪淘沙·赋虞美人草', '不肯过江东。玉帐匆匆。至今草木忆英雄。唱著虞兮当日曲，便舞春风。 儿女此情同。往事朦胧。湘娥竹上泪痕浓。舜盖重瞳堪痛恨，羽又重瞳。'),
	(6006, 591, '鹊桥仙·送粉卿行', '轿儿排了，担儿装了，杜宇一声催起。从今一步一回头，怎睚得、一千余里。 旧时行处，旧时歌处，空有燕泥香坠。莫嫌白发不思量，也须有、思量去里。'),
	(6007, 591, '满江红·送李正之提刑入蜀', '蜀道登天，一杯送、绣衣行客。还自叹、中年多病，不堪离别。东北看惊诸葛表，西南更草相如檄。把功名、收拾付君侯，如椽笔。 儿女泪，君休滴。荆楚路，吾能说。要新诗准备，庐山山色。赤壁矶头千古浪，铜鞮陌上三更月。正梅花、万里雪深时，须相忆。'),
	(6008, 329, '卜算子·秋晚集杜句吊贾傅', '苍生喘未苏，买笔论孤愤，文采风流今尚存，毫发无遗恨。 凄恻近长沙，地僻秋将尽。长使英雄泪满襟，天意高难问。'),
	(6009, 426, '江城子·癸酉春社', '清波渺渺日晖晖，柳依依，草离离。老大逢春，情绪有谁知？帘箔四垂庭院静，人独处，燕双飞。 怯寒未敢试春衣。踏青时，懒追随。野蔌山肴，村酿可从宜。不向花边拚一醉，花不语，笑人痴。'),
	(6010, 633, '蝶恋花·桐叶晨飘蛩夜语', '桐叶晨飘蛩夜语。旅思秋光，黯黯长安路。忽记横戈盘马处。散关清渭应如故。 江海轻舟今已具。一卷兵书，叹息无人付。早信此生终不遇。当年悔草长杨赋。'),
	(6011, 633, '好事近·湓口放船归', '湓口放船归，薄暮散花洲宿。两岸白苹红蓼，映一蓑新绿。 有沽酒处便为家，菱芡四时足。明日又乘风去，任江南江北。'),
	(6012, 633, '南乡子', '早岁入皇州。尊酒相逢尽胜流。三十年来真一梦，堪愁。客路萧萧两鬓秋。 蓬峤偶重游。不待人嘲我自羞。看镜倚楼俱已矣，扁舟。月笛烟蓑万事休。'),
	(6013, 267, '采桑子·彭浪矶', '扁舟去作江南客，旅雁孤云。万里烟尘。回首中原泪满巾。 碧山对晚汀洲冷，枫叶芦根。日落波平。愁损辞乡去国人。'),
	(6014, 255, '醉花阴', '九陌寒轻春尚早。灯火都门道。月下步莲人，薄薄香罗，峭窄春衫小。 梅妆浅淡风蛾袅。随路听嬉笑。无限面皮儿，虽则不同，各是一般好。'),
	(6015, 94, '水调歌头·九月望日与客习射西园余偶病不能射', '霜降碧天静，秋事促西风。寒声隐地，初听中夜入梧桐。起瞰高城回望，寥落关河千里，一醉与君同。叠鼓闹清晓，飞骑引雕弓。 岁将晚，客争笑，问衰翁。平生豪气安在，沈领为谁雄。何似当筵虎士，挥手弦声响处，双雁落遥空。老矣真堪愧，回首望云中。'),
	(6016, 545, '采桑子', '冰霜林里争先发，独压群花。风送清笳。更引轻烟淡淡遮。 抱墙溪水弯环碧，月色清华。疏影横斜。恰似林逋处士家。'),
	(6017, 130, '关河令·秋阴时晴渐向暝', '秋阴时晴渐向暝，变一庭凄冷。伫听寒声，云深无雁影。 更深人去寂静，但照壁孤灯相映。酒已都醒，如何消夜永！'),
	(6018, 451, '望海潮（四之二）', '秦峰苍翠，耶溪潇洒，千岩万壑争流。鸳瓦雉城，谯门画戟，蓬莱燕阁三休。天际识归舟。泛五湖烟月，西子同游。茂草台荒，苎萝村冷起闲愁。 何人览古凝眸。怅朱颜易失，翠被难留。梅市旧书，兰亭古墨，依稀风韵生秋。狂客鉴湖头。有百年台沼，终日夷犹。最好金龟换酒，相与醉沧洲。'),
	(6019, 494, '浣溪沙（荷花）', '四面垂杨十里荷。问云何处最花多。画楼南畔夕阳和。 天气乍凉人寂寞，光阴须得酒消磨。且来花里听笙歌。'),
	(6020, 494, '清平乐·秋词', '清淮浊汴。更在江西岸。红旆到时黄叶乱。霜入梁王故苑。 秋原何处携壶。停骖访古踟蹰。双庙遗风尚在，漆园傲吏应无。'),
	(6021, 250, '玉楼春·雕鞍好为莺花住', '雕鞍好为莺花住。占取东城南陌路。尽教春思乱如云，莫管世情轻似絮。 古来多被虚名误。宁负虚名身莫负。劝君频入醉乡来，此是无愁无恨处。'),
	(6022, 250, '临江仙·淡水三年欢意', '淡水三年欢意，危弦几夜离情。晓霜红叶舞归程。客情今古道，秋梦短长亭。 渌酒尊前清泪，阳关叠里离声。少陵诗思旧才名。云鸿相约处，烟雾九重城。'),
	(6023, 409, '谒金门·春又老', '春又老。南陌酒香梅小。遍地落花浑不扫。梦回情意悄。红笺寄与添烦恼。细写相思多少。醉后几行书字小。泪痕都揾了。'),
	(6024, 251, '采桑子', '春风不负东君信，遍拆群芳。燕子双双。依旧衔泥入杏梁。 须知一盏花前酒。占得韶光。莫话匆忙。梦里浮生足断肠。'),
	(6025, 186, '菩萨蛮·忆郎还上层楼曲', '忆郎还上层楼曲。楼前芳草年年绿。绿似去时袍。回头风袖飘。 郎袍应已旧。颜色非长久。惜恐镜中春。不如花草新。'),
	(6026, 344, '女冠子·淡烟飘薄', '淡烟飘薄。莺花谢、清和院落。树阴翠、密叶成幄。麦秋霁景，夏云忽变奇峰、倚寥廊。波暖银塘，涨新萍绿鱼跃。想端忧多暇，陈王是日，嫩苔生阁。 正铄石天高，流金昼永，楚榭光风转蕙，披襟处、波翻翠幕。以文会友，沈李浮瓜忍轻诺。别馆清闲，避炎蒸、岂须河朔。但尊前随分，雅歌艳舞，尽成欢乐。'),
	(6027, 344, '少年游（十之三·林钟商）', '层波潋滟远山横。一笑一倾城。酒容红嫩，歌喉清丽，百媚坐中生。 墙头马上初相见，不准拟、恁多情。昨夜怀阑，洞房深处，特地快逢迎。'),
	(6028, 158, '南乡子·春闺', '晓日压重檐，斗帐春寒起来忺。天气困人梳洗懒，眉尖，淡画春山不喜添。 闲把绣丝挦，认得金针又倒拈。陌上游人归也未？恹恹，满院杨花不卷帘。'),
	(6029, 414, '谒金门·双喜鹊', '双喜鹊，几报归期浑错。尽做旧愁都忘却，新愁何处着？ 瘦雪一痕墙角，青子已妆残萼。不道枝头无可落，东风犹作恶。'),
	(6030, 176, '菩萨蛮令·金陵怀古', '龙蟠虎踞金陵郡，古来六代豪华盛。缥凤不来游，台空江自流。下临全楚地，包举中原势。可惜草连天，晴郊狐兔眠。'),
	(6031, 259, '点绛唇·一夜东风', '一夜东风，枕边吹散愁多少。数声啼鸟。梦转纱窗晓。 来是春初，去是春将老。长亭道。一般芳草。只有归时好。'),
	(6032, 610, '忆秦娥·花深深', '花深深。一钩罗袜行花阴。行花阴。闲将柳带，细结同心。 日边消息空沈沈。画眉楼上愁登临。愁登临。海棠开后，望到如今。'),
	(6033, 620, '望江南·春睡起', '春睡起，积雪满燕山。万里长城横玉带，六街灯火已阑珊，人立蓟楼间。 空懊恼，独客此时还。辔压马头金错落，鞍笼驼背锦斓班。肠断唱门关。'),
	(6034, 101, '长相思', '桃花堤。柳花堤。芳草桥边花满溪。而今戎马嘶。 千山西。万山西。归雁横云落日低。登楼望欲迷。'),
	(6035, 101, '临江仙', '乱后此身何计是，翠微深处柴扉。即今双鬓已如丝。虚名将底用，真意在鸱夷。治国无谋归去好，衡门犹可楼迟。不妨沉醉典春衣。人生行乐耳，须富贵何时。'),
	(6036, 682, '菩萨蛮·溪山掩映斜阳里', '溪山掩映斜阳里。楼台影动鸳鸯起。隔岸两三家。出墙红杏花。 绿杨堤下路。早晚溪边去。三见柳绵飞。离人犹未归。'),
	(6037, 433, '庆清朝慢·踏青', '调雨为酥，催冰做水，东君分付春还。何人便将轻暖，点破残寒。结伴踏青去好，平头鞋子小双鸾。烟效外，望中秀色，如有无间。 晴则个，阴则个，饾饤得天气，有许多般。须教镂花拨柳，争要先看。不道吴绫绣袜，香泥斜沁几行斑。东风巧，尽收翠绿，吹在眉山。'),
	(6038, 202, '清平乐·平原放马', '辔摇衔铁。蹴踏平原雪。勇趁军声曾汗血。闲过升平时节。 茸茸春草天涯。涓涓野水晴沙。多少骅骝老去，至今犹困盐车。'),
	(6039, 71, '西江月·新秋写兴', '天上低昂仰旧，人间儿女成狂。夜来处处试新妆。却是人间天上。 不觉新凉似水，相思两鬓如霜。梦从海底跨枯桑。阅尽银河风浪。'),
	(6040, 112, '望江南·三月暮', '三月暮，花落更情浓。人去秋千闲挂月，马停杨柳倦嘶风。堤畔画船空。 恹恹醉，长日小帘栊。宿燕夜归银烛外，啼莺声在绿阴中。无处觅残红。'),
	(6041, 112, '夜合花·自鹤江入京泊葑门外有感', '柳暝河桥，莺晴台苑，短策频惹春香。当时夜泊，温柔便入深乡。词韵窄，酒杯长。翦蜡花、壶箭催忙。共追游处，凌波翠陌，连棹横塘。 十年一梦凄凉。似西湖燕去，吴馆巢荒。重来万感，依前唤酒银罂。溪雨急，岸花狂。趁残鸦、飞过苍茫。故人楼上，凭谁指与，芳草斜阳。'),
	(6042, 112, '祝英台近·除夜立春', '翦红情，裁绿意，花信上钗股。残日东风，不放岁华去。有人添烛西窗，不眠侵晓，笑声转、新年莺语。 旧尊俎。玉纤曾擘黄柑，柔香系幽素。归梦湖边，还迷镜中路。可怜千点吴霜，寒销不尽，又相对、落梅如雨。'),
	(6043, 112, '祝英台近·春日客龟溪游废园', '采幽香，巡古苑，竹冷翠微路。斗草溪根，沙印小莲步。自怜两鬓清霜，一年寒食，又身在、云山深处。 昼闲度。因甚天也悭春，轻阴便成雨。绿暗长亭，归梦趁飞絮。有情花影阑干，莺声门径，解留我、霎时凝伫。'),
	(6044, 91, '钗头凤（寒食饮绿亭）', '春愁远。春梦乱。凤钗一股轻尘满。江烟白，江波碧，柳户清明，燕帘寒食。忆忆忆。 莺声晓，箫声短，落花不许春拘管。新相识。休相失，翠陌吹衣，画楼横笛。得得得。'),
	(6045, 149, '八归·湘中送胡德华', '芳莲坠粉，疏桐吹绿，庭院暗雨乍歇。无端抱影销魂处，还见篠墙萤暗，藓阶蛩切。送客重寻西去路，问水面琵琶谁拨？最可惜、一片江山，总付与啼鴂。 长恨相从未款，而今何事，又对西风离别？渚寒烟淡，棹移人远，飘渺行舟如叶。想文君望久，倚竹愁生步罗袜。归来后，翠尊双饮，下了珠帘，玲珑闲看月。'),
	(6046, 72, '沁园春·张路分秋阅', '万马不嘶，一声寒角，令行柳营。见秋原如掌，枪刀突出，星驰铁骑，阵势纵横。人在油幢，戎韬总制，羽扉从容裘带轻。君知否，是山西将种，曾系诗盟。 龙蛇纸上飞腾。看落笔四筵风雨惊。便尘沙出塞，封侯万里，印金如斗，未惬平生。拂拭腰间，吹毛剑在，不斩楼兰心不平。归来晚，听随军鼓吹，已带边声。'),
	(6047, 335, '蝶恋花·别范南伯', '离恨做成春夜雨。添得春江，划地东流去。弱柳系船都不住。为君愁绝听鸣橹。 君到南徐芳草渡。想得寻春，依旧当年路。后夜独怜回首处。乱山遮隔无重数。'),
	(6048, 591, '浣溪沙', '歌串如珠个个匀。被花勾引笑和颦。向来惊动画梁尘。 莫倚笙歌多乐事，相看红紫又抛人。旧巢还有燕泥新。'),
	(6049, 591, '上西平·送杜叔高', '恨如新，新恨了，又重新。看天上、多少浮云。江南好景，落花时节又逢君。夜来风雨，春归似欲留人。 尊如海，人如玉，诗如锦，笔如神。能几字、尽殷勤。江天日暮，何时重与细论文。绿杨阴里，听阳关、门掩黄昏。'),
	(6050, 591, '沁园春（将止酒、戒酒杯使勿近）', '杯汝来前，老子今朝，点检形骸。甚长年抱渴，咽如焦釜，于今喜睡，气似奔雷。汝说刘伶，古今达者，醉后何妨死便埋。浑如此，叹汝於知已，真少恩哉。 更凭歌舞为媒。算合作平居鸩毒猜。况怨无大小，生於所爱，物无美恶，过则为灾。与汝成言，勿留亟退，吾力犹能肆汝杯。杯再拜，道麾之即去，招则须来。'),
	(6051, 591, '兰陵王·赋一丘一壑', '一丘壑。老子风流占却。茅檐上、松月桂云，脉脉石泉逗山脚。寻思前事错。恼杀晨猿夜鹤。终须是、邓禹辈人，锦绣麻霞坐黄阁。 长歌自深酌。看天阔鸢飞，渊静鱼跃。西风黄菊芗喷薄。怅日暮云合，佳人何处，纫兰结佩带杜若。入江海曾约。 遇合。事难托。莫系磬门前，荷蒉人过，仰天大笑冠簪落。待说与穷达，不须疑著。古来贤者，进亦乐，退亦乐。'),
	(6052, 633, '渔家傲·寄仲高', '东望山阴何处是？往来一万三千里。写得家书空满纸。流清泪，书回已是明年事。 寄语红桥桥下水，扁舟何日寻兄弟？行遍天涯真老矣。愁无寐，鬓丝几缕茶烟里。'),
	(6053, 518, '水调歌头·亭皋木叶下', '亭皋木叶下，原隰菊花黄。凭高满眼秋意，时节近重阳。追想彭门往岁，千骑云屯平野，高宴古球场。吊古论兴废，看剑引杯长。 感流年，思往事，重凄凉。当时坐间英俊，强半已凋亡。慨念平生豪放，自笑如今霜鬓，漂泊水云乡。已矣功名志，此意付清觞。'),
	(6054, 642, '菩萨蛮·赤阑桥尽香街直', '赤阑桥尽香街直，笼街细柳娇无力。金碧上青空，花晴帘影红。 黄衫飞白马，日日青楼下。醉眼不逢人，午香吹暗尘。'),
	(6055, 255, '青玉案·碧山锦树明秋霁', '碧山锦树明秋霁。路转陡、疑无地。忽有人家临曲水。竹篱茅舍，酒旗沙岸，一簇成村市。 凄凉只恐乡心起。凤楼远、回头谩凝睇。何处今宵孤馆里。一声征雁，半窗残月，总是离人泪。'),
	(6056, 130, '浣溪沙·雨过残红湿未飞', '雨过残红湿未飞，疏篱一带透斜晖。游蜂酿蜜窃春归。 金屋无人风竹乱，衣篝尽日水沉微。一春须有忆人时。'),
	(6057, 130, '琐窗寒·寒食', '暗柳啼鸦，单衣伫立，小帘朱户。桐花半亩，静锁一庭愁雨。洒空阶、夜阑未休，故人剪烛西窗语。似楚江暝宿，风灯零乱，少年羁旅。 迟暮。嬉游处。正店舍无烟，禁城百五。旗亭唤酒，付与高阳俦侣。想东园、桃李自春，小唇秀靥今在否。到归时、定有残英，待客携尊俎。'),
	(6058, 451, '丑奴儿', '夜来酒醒清无梦，愁倚阑干。露滴轻寒。雨打芙蓉泪不干。 佳人别后音尘悄，瘦尽难拚。明月无端。已过红楼十二间。'),
	(6059, 248, '鹊桥仙', '从来因被，薄情相误，误得人来已怕。那回时、有愿不昏沈，甚近日，依前又也。 你莫撋就，偎随人便，却骑墙两下。自家懑、都望有前程，背地里、莫教人咒骂。'),
	(6060, 693, '望江东·江水西头隔烟树', '江水西头隔烟树。望不见、江东路。思量只有梦来去。更不怕、江阑住。 灯前写了书无数。算没个、人传与。直饶寻得雁分付。又还是、秋将暮。'),
	(6061, 693, '千秋岁·苑边花外', '少游得谪，尝梦中作词云：“醉卧古藤阴下，了不知南北。”竟以元符庚辰死于藤州光华亭上。崇宁甲申，庭坚窜宜州，道过衡阳。览其遗，始追和其《千秋岁》词。 苑边花外，记得同朝退。飞骑轧，鸣珂碎。齐歌云绕扇，赵舞风回带。严鼓断，杯盘狼藉犹相对。 洒泪谁能会？醉卧藤阴盖。人已去，词空在。兔园高宴悄，虎观英游改。重感慨，波涛万顷珠沉海。'),
	(6062, 693, '醉蓬莱', '对朝云叆叇，暮雨霏微，乱峰相倚。巫峡高唐，锁楚宫朱翠。画戟移春，靓妆迎马，向一川都会。万里投荒，一身吊影，成何欢意。 尽道黔南，去天尺五，望极神州，万里烟水。尊酒公堂，有中朝佳士。荔颊红深，麝脐香满，醉舞裀歌袂。杜宇声声，催人到晓，不如归是。'),
	(6063, 278, '南乡子·端午', '小雨湿黄昏。重午佳辰独掩门。巢燕引雏浑去尽，销魂。空向梁间觅宿痕。 客舍宛如村。好事无人载一樽。唯有莺声知此恨，殷勤。恰似当时枕上闻。'),
	(6064, 250, '秋蕊香·池苑清阴欲就', '池苑清阴欲就。还傍送春时候。眼中人去难欢偶。谁共一杯芳酒。 朱阑碧砌皆如旧。记携手。有情不管别离久。情在相逢终有期。'),
	(6065, 549, '薄幸·淡妆多态', '淡妆多态，更的的、频回眄睐。便认得琴心先许，与绾合欢双带。记画堂、风月逢迎、轻颦浅笑娇无奈。向睡鸭炉边，翔鸳屏里，羞把香罗偷解。 自过了、烧灯后，都不见踏青挑菜。几回凭双燕，丁宁深意，往来却恨重帘碍。约何时再，正春浓酒困，人闲昼永无聊赖。厌厌睡起，犹有花梢日在。'),
	(6066, 683, '玉楼春·寂寂画堂梁上燕', '寂寂画堂梁上燕，高卷翠帘横数扇。一庭春色恼人来，满地落花红几片。 愁倚锦屏低雪面，泪滴绣罗金缕线。好天凉月尽伤心，为是玉郎长不见。'),
	(6067, 167, '踏莎行·寒草烟光阔', '寒草烟光阔，渭水波声咽。春潮雨霁轻尘歇。征鞍发。指青青杨柳，又是轻攀折。动黯然，知有后会甚时节？ 更进一杯酒，歌一阕。叹人生，最难欢聚易离别。且莫辞沉醉，听取阳关彻。念故人，千里至此共明月。'),
	(6068, 272, '念奴娇（用传安道和朱希真梅词韵）', '临风一笑，问群芳谁是，真香纯白。独立无朋，算只有、姑射山头仙客。绝艳谁怜，真心自保，邈与尘缘隔。天然殊胜，不关风露冰雪。 应笑俗李粗桃，无言翻引得，狂蜂轻蝶。争似黄昏闲弄影，清浅一溪霜月。画角吹残，瑶台梦断，直下成休歇。绿阴青子，莫教容易披折。'),
	(6069, 101, '青玉案', '别时抆泪花无语。但一味、教郎佳。此日扁舟游远浦。雨晴云树。月斜烟树。目断家何许。红笺不寄相思句。人在潇湘雁回处。屈指归期秋已幕。万千里路。两三头绪。恨不飞将去。'),
	(6070, 17, '诉衷情·宝月山作', '清波门外拥轻衣。杨花相送飞。西湖又还春晚，水树乱莺啼。闲院宇，小帘帏。晚初归。钟声已过，篆香才点，月到门时。'),
	(6071, 4, '木兰花慢·恨莺花渐老', '恨莺花渐老，但芳草、绿汀洲。纵岫壁千寻，榆钱万叠，难买春留。梅花向来始别，又匆匆、结子满枝头。门外垂杨岸侧，画桥谁系兰舟。 悠悠。岁月如流。叹水覆、杳难收。凭画阑，往往抬头举眼，都是春愁。东风晚来更恶，怕飞红、怕絮入书楼。双燕归来问我，怎生不上帘钩。'),
	(6072, 628, '眼儿媚·楼上黄昏杏花寒', '楼上黄昏杏花寒。斜月小栏干。一双燕子，两行征雁，画角声残。 绮窗人在东风里，洒泪对春间。也应似旧，盈盈秋水，淡淡春山。'),
	(6073, 515, '贺新郎·秋晓', '渺渺啼鸦了。亘鱼天，寒生峭屿，五湖秋晓。竹几一灯人做梦，嘶马谁行古道。起搔首、窥星多少。月有微黄篱无影，挂牵牛数朵青花小。秋太淡，添红枣。 愁痕倚赖西风扫。被西风、翻催鬓鬒，与秋俱老。旧院隔霜帘不卷，金粉屏边醉倒。计无此、中年怀抱。万里江南吹箫恨，恨参差白雁横天杪。烟未敛，楚山杳。'),
	(6074, 124, '一萼红·登蓬莱阁有感', '步深幽。正云黄天淡，雪意未全休。鉴曲寒沙，茂林烟草，俯仰千古悠悠。岁华晚、飘零渐远，谁念我、同载五湖舟？磴古松斜，崖阴苔老，一片清愁。 回首天涯归梦，几魂飞西浦，泪洒东州。故国山川，故园心眼，还似王粲登楼。最负他、秦鬟妆镜，好江山、何事此时游！为唤狂吟老监，共赋消忧。'),
	(6075, 112, '醉桃源·元日', '五更枥马静无声。邻鸡犹怕惊。日华平晓弄春明。暮寒愁翳生。 新岁梦，去年情。残宵半酒醒。春风无定落梅轻。断鸿长短亭。'),
	(6076, 112, '玉漏迟·瓜泾度中秋夕赋', '雁边风讯小，飞琼望杳，碧云先晚。露冷阑干，定怯藕丝冰腕。净洗浮空片玉，胜花影、春灯相乱。秦镜满。素娥未肯，分秋一半。 每圆处即良宵，甚此夕偏饶，对歌临怨。万里婵娟，几许雾屏云幔。孤兔凄凉照水，晓风起、银河西转。摩泪眼。瑶台梦回人远。'),
	(6077, 117, '贺新郎', '言诗者，有言词者。须臾，以酒见酌。中有一人举令云：“各和古词一首”。且目余云：“相公和叶石林睡起流莺语”。余素熟此词成诵，遂援笔赓之，掷笔而寤。枕上记忆，不遗一字，亦异矣。以词意详之，余三上丐归之疏，君父其从欲乎。因录呈同官诸丈，恐可为他时一段佳话云 燕子呢喃语。小园林、残红剩紫，已无三数。绿叶青枝成步障，空有蜂旋蝶舞。又宝扇、轻摇初暑。芳沼拳荷舒展尽，便回头、乱拥宫妆女。惊岁月，能多许。 家山占断凫鸥渚。最相宜、岚烟水月，雾云霏雨。三岛十洲虽铁铸，难把归舟系取。且放我、渔樵为与。从此细斟昌＿酒，况神仙洞府无邀阻。何待结，长生缕。'),
	(6078, 49, '贺新郎·端午', '深院榴花吐。画帘开、束衣纨扇，午风清暑。儿女纷纷夸结束，新样钗符艾虎。早已有、游人观渡。老大逢场慵作戏，任陌头、年少争旗鼓。溪雨急，浪花舞。 灵均标致高如许。忆生平、既纫兰佩，更怀椒醑。谁信骚魂千载后，波底垂涎角黍。又说是、蛟馋龙怒。把似而今醒到了，料当年、醉死差无苦。聊一笑，吊千古。'),
	(6079, 49, '沁园春（送孙季蕃吊方漕西归）', '岁暮天寒，一剑飘然，幅巾布裘。尽缘云鸟道，跻攀绝顶，拍天鲸浸，笑傲中流。畴昔奇君，紫髯铁面，生子当如孙仲谋。争知道，向中年犹未，建节封侯。 南来万里何求。因感慨桥公成远游。叹名姬骏马，都成昨梦，只鸡斗酒，谁吊新丘。天地无情，功名有命，千古英雄只么休。平生客，独羊昙一个，洒泪西州。'),
	(6080, 639, '南乡子·风雨满苹洲', '风雨满苹洲。绣阁银屏一夜秋。当日袜尘何处去，溪楼。怎对烟波不泪流。 天际目归舟。浪卷涛翻一叶浮。也似我侬魂不定，悠悠。宋玉方悲庾信愁。'),
	(6081, 591, '汉宫春·会稽蓬莱阁怀古', '秦望山头，看乱云急雨，倒立江湖。不知云者为雨，雨者云乎。长空万里，被西风、变灭须臾。回首听，月明天籁，人间万窍号呼。 谁向若耶溪上，倩美人西去，麋鹿姑苏。至今故国人望，一舸归欤。岁去暮矣，问何不、鼓瑟吹竽。君不见，王亭谢馆，冷烟寒树啼乌。'),
	(6082, 591, '卜算子（饮酒成病）', '一个去学仙，一个去学佛。仙饮千杯醉似泥，皮骨如金石。 不饮便康强，佛寿须千百。八十余年入涅槃，且进杯中物。'),
	(6083, 591, '清平乐·春宵睡重', '春宵睡重，梦里还相送。枕畔起寻双玉凤，半日才知是梦。 一从卖翠人还，又无音信经年。却把泪来作水，流也流到伊边。'),
	(6084, 591, '水龙吟（爱李延年歌、淳于髡语合为词，庶几高唐、神女、洛神赋之意云）', '昔时曾有佳人，翩然绝世而独立。未论一顾倾城，再顾又倾人国。宁不知其，倾城倾国，佳人难得。看行云行雨，朝朝暮暮，阳台下、襄王侧。 堂上更阑烛灭。记主人、留髡送客。合尊促坐，罗襦襟解，微闻芗泽。当此之时，止乎礼义，不淫其色。但□□□□，啜其泣矣，又何嗟及。'),
	(6085, 591, '蝶恋花·戊申元日立春席间作', '谁向椒盘簪彩胜？整整韶华，争上春风鬓。往日不堪重记省，为花长把新春恨。 春未来时先借问。晚恨开迟，早又飘零近。今岁花期消息定，只愁风雨无凭准。'),
	(6086, 633, '一落索', '识破浮生虚妄。从人讥谤。此身恰似弄潮儿，曾过了、千重浪。 且喜归来无恙。一壶春酿。雨蓑烟笠傍渔矶，应不是、封侯相。'),
	(6087, 633, '采桑子·宝钗楼上妆梳晚', '宝钗楼上妆梳晚，懒上秋千。闲拨沈烟。金缕衣宽睡髻偏。 鳞鸿不寄辽东信，又是经年。弹泪花前。愁入春见十四弦。'),
	(6088, 642, '临江仙·四海十年兵不解', '四海十年兵不解，胡尘直到江城。岁华销尽客心惊。疏髯浑似雪，衰涕欲生冰。 送老薤盐何处是，我缘应在吴兴。故人相望若为情。别愁深夜雨，孤影小窗灯。'),
	(6089, 130, '拜星月·高平秋思', '夜色催更，清尘收露，小曲幽坊月暗。竹槛灯窗，识秋娘庭院。笑相遇，似觉琼枝玉树，暖日明霞光烂。水眄兰情，总平生稀见。 画图中、旧识春风面。谁知道、自到瑶台畔。眷恋雨润云温，苦惊风吹散。念荒寒、寄宿无人馆。重门闭、败壁秋虫叹。怎奈向、一缕相思，隔溪山不断。'),
	(6090, 130, '虞美人', '淡云笼月松溪路。长记分携处。梦魂连夜绕松溪。此夜相逢恰似、梦中时。 海山陡觉风光好。莫惜金尊倒。柳花吹雪燕飞忙。生怕扁舟归去、断人肠。'),
	(6091, 549, '减字浣溪沙·楼角初销一缕霞', '楼角初销一缕霞，淡黄杨柳暗栖鸦。玉人和月摘梅花。 笑捻粉香归洞户，更垂帘幕护窗纱。东风寒似夜来些。'),
	(6092, 451, '临江仙·千里潇湘挼蓝浦', '千里潇湘挼蓝浦，兰桡昔日曾经。月高风定露华清。微波澄不动，冷浸一天星。 独倚危樯情悄悄，遥闻妃瑟泠泠。新声含尽古今情。曲终人不见，江上数峰青。'),
	(6093, 451, '阮郎归·湘天风雨破寒初', '湘天风雨破寒初。深沈庭院虚。丽谯吹罢小单于。迢迢清夜徂。 乡梦断，旋魂孤。峥嵘岁又除。衡阳犹有雁传书。郴阳和雁无。'),
	(6094, 693, '浣溪沙·一叶扁舟卷画帘', '一叶扁舟卷画帘。老妻学饮伴清谈。人传诗句满江南。 林下猿垂窥涤砚，岩前鹿卧看收帆。杜鹃声乱水如环。'),
	(6095, 693, '鹧鸪天', '句耳。”因以玄真子遗事足之。宪宗时，画玄真子像，访之江湖，不可得，因令集其歌诗上之。玄真之兄松龄，惧玄真放浪而不返也，和答其渔父云：“乐在风波钓是闲。草堂松桂已胜攀。太湖水，洞庭山。狂风浪起且须还。”此余续成之意也 西塞山边白鹭飞。桃花流水鳜鱼肥。朝廷尚觅玄真子，何处如今更有诗。 青箬笠，绿蓑衣。斜风细雨不须归。人间底是无波处，一日风波十二时。'),
	(6096, 494, '满庭芳·三十三年', '有王长官者，弃官黄州三十三年，黄人谓之王先生。因送陈慥来过余，因为赋此。 三十三年，今谁存者？算只君与长江。凛然苍桧，霜干苦难双。闻道司州古县，云溪上、竹坞松窗。江南岸，不因送子，宁肯过吾邦？ 摐摐，疏雨过，风林舞破，烟盖云幢。愿持此邀君，一饮空缸。居士先生老矣，真梦里、相对残釭。歌声断，行人未起，船鼓已逢逢。'),
	(6097, 250, '虞美人·疏梅月下歌金缕', '疏梅月下歌金缕。忆共文君语。更谁情浅似春风。一夜满枝新绿、替残红。 苹香已有莲开信。两桨佳期近。采莲时节定来无。醉后满身花影、倩人扶。'),
	(6098, 323, '苏幕遮', '尽思量，还叵耐。因甚当初，故故相招买。早是幽欢多障碍。更遣分飞，脉脉如天外。 有心怜，无计奈。两处厌厌，一点虚恩爱。独上高楼临暮霭。凭暖朱阑，这意无人会。'),
	(6099, 344, '红窗迥·小园东', '小园东，花共柳。红紫又一齐开了。引将蜂蝶燕和莺，成阵价、忙忙走。 花心偏向蜂儿有。莺共燕、吃他拖逗。蜂儿却入、花里藏身，胡蝶儿、你且退后。'),
	(6100, 344, '破阵乐·露花倒影', '露花倒影，烟芜蘸碧，灵沼波暖。金柳摇风树树，系彩舫龙舟遥岸。千步虹桥，参差雁齿，直趋水殿。绕金堤、曼衍鱼龙戏，簇娇春罗绮，喧天丝管。霁色荣光，望中似睹，蓬莱清浅。 时见。凤辇宸游，鸾觞禊饮，临翠水、开镐宴。两两轻舠飞画楫，竞夺锦标霞烂。罄欢娱，歌鱼藻，徘徊宛转。别有盈盈游女，各委明珠，争收翠羽，相将归远。渐觉云海沈沈，洞天日晚。'),
	(6101, 344, '卜算子慢·江枫渐老', '江枫渐老，汀蕙半凋，满目败红衰翠。楚客登临，正是暮秋天气。引疏砧、断续残阳里。对晚景、伤怀念远，新愁旧恨相继。 脉脉人千里。念两处风情，万重烟水。雨歇天高，望断翠峰十二。侭无言、谁会凭高意。纵写得、离肠万种，奈归云谁寄。'),
	(6102, 176, '诉衷情令·长安怀古', '阿房废址汉荒丘。狐兔又群游。豪华尽成春梦，留下古今愁。 君莫上，古原头。泪难收。夕阳西下，塞雁南飞，渭水东流。'),
	(6103, 80, '望江南·燕塞雪', '燕塞雪，片片大如拳。蓟上酒楼喧鼓吹，帝城车马走骈阗。羁馆独凄然。 燕塞月，缺了又还圆。万里妾心愁更苦，十春和泪看婵娟。何日是归年。'),
	(6104, 688, '清平乐·宫怨', '珠帘寂寂。愁背银缸泣。记得少年初选入。三十六宫第一。 当年掌上承恩。而今冷落长门。又是羊车过也，月明花落黄昏。'),
	(6105, 48, '洞仙歌·荷花', '晚风收暑，小池塘荷净。独倚胡床酒初醒。起徘徊、时有香气吹来，云藻乱，叶底游鱼动影。 空擎承露盖，不见冰容，惆怅明妆晓鸾镜。后夜月凉时，月淡花低，幽梦觉、欲凭谁省。且应记、临流凭阑干，便遥想，江南红酣千顷。'),
	(6106, 433, '生查子', '关山魂梦长，塞雁音书少。两鬓可怜青，一夜相思老。 归傍碧纱窗，说与人人道。真个别离难，不似相逢好。'),
	(6107, 4, '昭君怨·春到南楼雪尽', '春到南楼雪尽。惊动灯期花信。小雨一番寒。倚阑干。 莫把栏干频倚。一望几重烟水。何处是京华。暮云遮。'),
	(6108, 71, '忆秦娥·中斋上元客散感旧', '中斋上元客散感旧，赋忆秦娥见属一读凄然，随韵寄情不觉悲甚 烧灯节。朝京道上风和雪。风和雪。江山如旧，朝京人绝。 百年短短兴亡别。与君犹对当时月。当时月。照人烛泪，照人梅发。'),
	(6109, 641, '唐多令·秋暮有感', '休去采芙蓉。秋江烟水空。带斜阳、一片征鸿。欲顿闲愁无顿处，都著在两眉峰。 心事寄题红。画桥流水东。断肠人、无奈秋浓。回首层楼归去懒，早新月、挂梧桐。'),
	(6110, 289, '江城子·平沙浅草接天长', '平沙浅草接天长。路茫茫。几兴亡。昨夜波声。洗岸骨如霜。千古英雄成底事，徒感慨，谩悲凉。 少年有意伏中行。馘名王。扫沙场。击楫中流，曾记泪沾裳。欲上治安双阙远，空怅望，过维扬。'),
	(6111, 116, '念奴娇·我来牛渚', '我来牛渚，聊登眺、客里襟怀如豁。谁著危亭当此处，占断古今愁绝。江势鲸奔，山形虎踞，天险非人设。向来舟舰，曾扫百万胡羯。 追念照水然犀，男儿当似此，英碓豪杰。岁月匆匆留不住，鬓已星星堪镊。云暗江天，烟昏淮地，是断魂时节。栏干捶碎，酒狂忠愤俱发。'),
	(6112, 199, '青玉案·被檄出郊题陈氏山居', '西风乱叶溪桥树。秋在黄花羞涩处。满袖尘埃推不去。马蹄浓露，鸡声淡月，寂历荒村路。 身名多被儒冠误。十载重来漫如许。且尽清樽公莫舞。六朝旧事，一江流水，万感天涯暮。'),
	(6113, 49, '卜算子', '风汉闲中做，彼岸神舟渡。万里晴空无片云，月照南溪路。 割断冤情苦，默默明玄趣。一任傍人笑我贫。肚里非常富。'),
	(6114, 695, '浣溪沙', '墨绿衫儿窄窄裁。翠荷斜嚲领云堆。几时踪迹下阳台。 歌罢樱桃和露小，舞余杨柳趁风回。唤人休诉十分杯。'),
	(6115, 84, '倦寻芳·香泥垒燕', '香泥垒燕，密叶巢莺，春晦寒浅。花径风柔，著地舞茵红软。斗草烟欺罗袂薄，秋千影落春游倦。醉归来，记宝帐歌慵，锦屏香暖。 别来怅、光阴容易，还又酴醿，牡丹开遍。妒恨疏狂，那更柳花迎面。鸿羽难凭芳信短，长安犹近归期远。倚危楼，但镇日、绣帘高卷。'),
	(6116, 370, '沁园春·忆黄山', '三十六峰，三十六溪，长锁清秋。对孤峰绝顶，云烟竞秀，悬崖峭壁，瀑布争流。洞里桃花，仙家芝草，雪后春正取次游。亲曾见，是龙潭白昼，海涌潮头。 当年黄帝浮丘。有玉枕玉床还在不。向天都月夜，遥闻凤管，翠微霜晓，仰盼龙楼。砂穴长红，丹炉已冷，安得灵方闻早修。谁知此，问原头白鹿，水畔青牛。'),
	(6117, 591, '玉楼春', '无心云自来还去。元共青山相尔汝。霎时迎雨障崔嵬，雨过却寻归路处。 侵天翠竹何曾度。遥见屹然星砥柱。今朝不管乱云深，来伴仙翁山下住。'),
	(6118, 591, '鹧鸪天', '桃李漫山过眼空。也宜恼损杜陵翁。若将玉骨冰姿比，李蔡为人在下中。 寻驿使，寄芳容。垅头休放马蹄松。吾家篱落黄昏后，剩有西湖处士风。'),
	(6119, 591, '鹧鸪天·戏题村舍', '鸡鸭成群晚不收，桑麻长过屋山头。有何不可吾方羡，要底都无饱便休。 新柳树，旧沙洲，去年溪打那边流。自言此地生儿女，不嫁余家即聘周。(余家 一作：金家)'),
	(6120, 591, '鹧鸪天·送欧阳国瑞入吴中', '莫避春阴上马迟。春来未有不阴时。人情展转闲中看，客路崎岖倦后知。 梅似雪，柳如丝。试听别语慰相思。短篷炊饮鲈鱼熟，除却松江枉费诗。'),
	(6121, 591, '清平乐·博山道中即事', '柳边飞鞚，露湿征衣重。宿鹭窥沙孤影动，应有鱼虾入梦。 一川淡月疏星，浣纱人影娉婷。笑背行人归去，门前稚子啼声。'),
	(6122, 188, '浣溪沙·洞庭', '行尽潇湘到洞庭。楚天阔处数峰青。旗梢不动晚波平。 红蓼一湾纹缬乱，白鱼双尾玉刀明。夜凉船影浸疏星。'),
	(6123, 188, '鹧鸪天·上元启醮', '子夜封章扣紫清。五霞光里佩环声。驿传风火龙鸾舞，步入烟霄孔翠迎。 瑶简重，羽衣轻。金童双引到通明。三湘五管同民乐，万岁千秋与帝龄。'),
	(6124, 498, '眼儿媚·酣酣日脚紫烟浮', '萍乡道中乍晴，卧舆中困甚，小憩柳塘。 酣酣日脚紫烟浮，妍暖破轻裘。困人天色，醉人花气，午梦扶头。 春慵恰似春塘水，一片縠纹愁。溶溶泄泄，东风无力，欲皱还休。'),
	(6125, 633, '破阵子', '看破空花尘世，放轻昨梦浮名。蜡屐登山真率饮，筇杖穿林自在行。身闲心太平。 料峭余寒犹力，廉纤细雨初晴。苔纸闲题溪上句，菱唱遥闻烟外声。与君同醉醒。'),
	(6126, 633, '长相思（五之五）', '悟浮生。厌浮名。回视千钟一发轻。从今心太平。 爱松声。爱泉声。写向孤桐谁解听。空江秋月明。'),
	(6127, 633, '临江仙（离果州作）', '鸠雨催成新绿，燕泥收尽残红。春光还与美人同。论心空眷眷，分袂却匆匆。只道真情易写，那知怨句难工。水流云散各西东。半廊花院月，一帽柳桥风。'),
	(6128, 526, '水调歌头·定王台', '雄跨洞庭野，楚望古湘州。何王台殿，危基百尺自西刘。尚想霓旌千骑，依约入云歌吹，屈指几经秋。叹息繁华地，兴废两悠悠。 登临处，乔木老，大江流。书生报国无地，空白九分头。一夜寒生关塞，万里云埋陵阙，耿耿恨难休。徙倚霜风里，落日伴人愁。'),
	(6129, 332, '望江南', '钟陵好，和气满江城。忆昨旌麾初至止，到今政令只宽平。仍岁兆丰登。 称庆旦，遐迩一般情。共信我公跻寿考，从来阴德被生灵。襦衤夸听欢声。'),
	(6130, 305, '摊破浣溪沙', '揉破黄金万点轻。剪成碧玉叶层层。风度精神如彦辅，大鲜明。 梅蕊重重何俗甚，丁香千结苦粗生。熏透愁人千里梦，却无情。'),
	(6131, 267, '水调歌头', '中秋一轮月，只和旧青冥。都缘人意，须道今夕别般明。是处登临开宴，争看吴歌楚舞，沈醉倒金尊。各自心中事，悲乐几般情。 烛摧花，鹤惊露，忽三更。舞茵未卷，玉绳低转便西倾。认取眼前流景，试看月归何处，因甚有亏盈。我自阖门睡，高枕笑浮生。'),
	(6132, 415, '醉花阴', '为惜梅花笛里吹。 玉妃谪堕烟村远。犹似瑶池见。缺月挂寒梢，时有幽香，飞到朱帘畔。 春风岭上淮南岸。曾为谁魂断。依旧瘦棱棱，天若有情，天也应须管。'),
	(6133, 94, '鹧鸪天', '一曲青山映小池。绿荷阴尽雨离披。何人解识秋堪美，莫为悲秋浪赋诗。 携浊酒，绕东篱。菊残犹有傲霜枝。一年好景君须记，正是橙黄橘绿时。梁范坚常谓欣成惜败者，物之情。秋为万物成功之时，宋玉作悲秋，非是。乃作美秋赋云。'),
	(6134, 130, '庆春宫·云接平冈', '云接平冈，山围寒野，路回渐转孤城。衰柳啼鸦，惊风驱雁，动人一片秋声。倦途休驾，淡烟里、微茫见星。尘埃憔悴，生怕黄昏，离思牵萦。 华堂旧日逢迎。花艳参差，香雾飘零。弦管当头，偏怜娇凤，夜深簧暖笙清。眼波传意，恨密约、匆匆未成。许多烦恼，只为当时，一饷留情。'),
	(6135, 130, '瑞龙吟·大石春景', '章台路。还见褪粉梅梢，试花桃树。愔愔坊陌人家，定巢燕子，归来旧处。 黯凝伫。因念个人痴小，乍窥门户。侵晨浅约宫黄，障风映袖，盈盈笑语。 前度刘郎重到，访邻寻里，同时歌舞。唯有旧家秋娘，声价如故。吟笺赋笔，犹记燕台句。知谁伴、名园露饮，东城闲步。事与孤鸿去。探春尽是，伤离意绪。官柳低金缕。归骑晚、纤纤池塘飞雨。断肠院落，一帘风絮。'),
	(6136, 130, '浪淘沙慢·晓阴重', '晓阴重，霜凋岸草，雾隐城堞。南陌脂车待发，东门帐饮乍阕。正拂面、垂杨堪揽结。掩红泪、玉手亲折。念汉浦、离鸿去何许？经时信音绝。 情切，望中地远天阔。向露冷风清，无人处，耿耿寒漏咽。嗟万事难忘，惟是轻别。翠尊未竭，凭断云、留取西楼残月。 罗带光消纹衾叠，连环解、旧香顿歇。怨歌永、琼壶敲尽缺。恨春去、不与人期，弄夜色、空馀满地梨花雪。'),
	(6137, 130, '西平乐·稚柳苏晴', '元丰初，予以布衣西上，过天长道中。后四十余年，辛丑正月，避贼复游故地。感叹岁月，偶成此词。 稚柳苏晴，故溪歇雨，川迥未觉春赊。驼褐寒侵，正怜初日，轻阴抵死须遮。叹事逐孤鸿尽去，身与塘蒲共晚，争知向此，征途迢递，伫立尘沙。念朱颜翠发，曾到处，故地使人嗟。 道连三楚，天低四野，乔木依前，临路敧斜。重慕想、东陵晦迹，彭泽归来，左右琴书自乐，松菊相依，何况风流鬓未华。多谢故人，亲驰郑驿，时倒融尊，劝此淹留，共过芳时，翻令倦客思家。'),
	(6138, 249, '少年游', '当年携手，是处成双，无人不羡。自间阻、五年也，一梦拥、娇娇粉面。 柳眉轻扫，杏腮微拂，依前双靥。盛睡里、起来寻觅，却眼前不见。'),
	(6139, 549, '木兰花·清琴再鼓求凰弄', '清琴再鼓求凰弄，紫陌屡盘骄马鞚。远山眉样认心期，流水车音牵目送。 归来翠被和衣拥，醉解寒生钟鼓动。此欢只许梦相亲，每向梦中还说梦。'),
	(6140, 250, '满庭芳·南苑吹花', '南苑吹花，西楼题叶，故园欢事重重。凭阑秋思，闲记旧相逢。几处歌云梦雨，可怜便、汉水西东。别来久，浅情未有，锦字系征鸿。 年光还少味，开残槛菊，落尽溪桐。漫留得，尊前淡月西风。此恨谁堪共说，清愁付、绿酒杯中。佳期在，归时待把，香袖看啼红。'),
	(6141, 250, '清平乐·烟轻雨小', '烟轻雨小。紫陌香尘少。谢客池塘生绿草。一夜红梅先老。 旋题罗带新诗。重寻杨柳佳期。强半春寒去后，几番花信来时。'),
	(6142, 250, '南乡子·花落未须悲', '花落未须悲。红蕊明年又满枝。惟有花间人别后，无期。水阔山长雁字迟。 今日最相思。记得攀条话别离。共说春来春去事，多时。一点愁心入翠眉。'),
	(6143, 250, '蝶恋花·喜鹊桥成催凤驾', '喜鹊桥成催凤驾。天为欢迟，乞与初凉夜。乞巧双蛾加意画。玉钩斜傍西南挂。 分钿擘钗凉叶下。香袖凭肩，谁记当时话。路隔银河犹可借。世间离恨何年罢。'),
	(6144, 409, '渔家傲·灯火已收正月半', '灯火已收正月半，山南山北花撩乱。闻说洊亭新水漫，骑款段，穿云入坞寻游伴。 却拂僧床褰素幔，千岩万壑春风暖。一弄松声悲急管，吹梦断，西看窗日犹嫌短。'),
	(6145, 186, '相思令·苹满溪', '苹满溪。柳绕堤。相送行人溪水西。回时陇月低。 烟霏霏。风凄凄。重倚朱门听马嘶。寒鸥相对飞 。'),
	(6146, 344, '临江仙引·画舸', '画舸、荡桨，随浪前，隔岸虹。□荷点断秋容。疑水仙游泳，向别浦相逢。鲛丝雾吐渐收，细腰无力转娇慵。 罗袜凌波成旧恨，有谁更赋惊鸿。想媚魂杳信，算密锁瑶宫。游人漫劳倦□，奈何不逐东风。'),
	(6147, 344, '佳人醉·暮景萧萧雨霁', '暮景萧萧雨霁。云淡天高风细。正月华如水。金波银汉，潋滟无际。冷浸书帷梦断，却披衣重起。临轩砌。 素光遥指。因念翠蛾，杳隔音尘何处，相望同千里。尽凝睇。厌厌无寐。渐晓雕阑独倚。'),
	(6148, 344, '满朝欢·花隔铜壶', '花隔铜壶，露晞金掌，都门十二清晓。帝里风光烂漫，偏爱春杪。烟轻昼永，引莺啭上林，鱼游灵沼。巷陌乍晴，香尘染惹，垂杨芳草。 因念秦楼彩凤，楚观朝云，往昔曾迷歌笑。别来岁久，偶忆欢盟重到。人面桃花，未知何处，但掩朱扉悄悄。尽日伫立无言，赢得凄凉怀抱。'),
	(6149, 344, '西江月（中吕宫）', '凤额绣帘高卷，兽钚朱户频摇。两竿红日上花梢。春睡厌厌难觉。 好梦狂随飞絮，闲愁浓胜香醪。不成雨暮与云朝。又是韶光过了。'),
	(6150, 532, '长亭怨慢·重过中庵故园', '泛孤艇、东皋过遍。尚记当日，绿阴门掩。屐齿莓阶，酒痕罗袖事何限。欲寻前迹，空惆怅、成秋苑。自约赏花人，别后总、风流云散。 水远。怎知流水外，却是乱山尤远。天涯梦短。想忘了，绮疏雕槛。望不尽，冉冉斜阳，抚乔木、年华将晚。但数点红英，犹记西园凄婉。'),
	(6151, 89, '苏幕遮', '柳垂金，梅褪玉。昴宿呈祥，符应生公族。盖世功名夸九牧。黼衮褒扬，庆阀辉南北。 赐宫醪，分笃耨。天与长生，谩把仙椿祝。好继平阳腾茂躅。富贵千秋，饮听瑶池曲。'),
	(6152, 688, '鹊桥仙·春情', '青林雨歇，珠帘风细，人在绿阴庭院。夜来能有几多寒，已瘦了、梨花一半。 宝钗无据，玉琴难托，合造一襟幽怨。云窗雾阁事茫茫，试与问、可杏双燕。'),
	(6153, 155, '南乡子·璧月小红楼', '璧月小红楼。听得吹箫忆旧游。霜冷阑干天似水，扬州。薄幸声名总是愁。 尘暗鹔鹴裘。针线曾劳玉指柔。一梦觉来三十载，休休。空为梅花白了头。'),
	(6154, 673, '高阳台·除夜', '频听银签，重燃绛蜡，年华衮衮惊心。饯旧迎新，能消几刻光阴。老来可惯通宵饮，待不眠、还怕寒侵。掩清尊。多谢梅花，伴我微吟。 邻娃已试春妆了，更蜂腰簇翠，燕股横金。勾引东风，也知芳思难禁。朱颜那有年年好，逞艳游、赢取如今。恣登临。残雪楼台，迟日园林。'),
	(6155, 233, '水调歌头·题李季允侍郎鄂州吞云楼', '轮奂半天上，胜概压南楼。筹边独坐，岂欲登览快双眸。浪说胸吞云梦，直把气吞残虏，西北望神州。百载一机会，人事恨悠悠。 骑黄鹤，赋鹦鹉，谩风流。岳王祠畔，杨柳烟锁古今愁。整顿乾坤手段，指授英雄方略，雅志若为酬。杯酒不在手，双鬓恐惊秋。'),
	(6156, 272, '西江月', '堂下水浮新绿，门前树长交枝。晚凉快写一篇诗。不说人间忧喜。 身老心闲益壮，形臞道胜还肥。软轮加璧未应迟。莫道前非今是。'),
	(6157, 632, '瑞鹤仙·脸霞红印枕', '脸霞红印枕，睡觉来、冠儿还是不整。屏间麝煤冷，但眉峰压翠，泪珠弹粉。堂深昼永，燕交飞、风帘露井。恨无人说与，相思近日，带围宽尽。 重省，残灯朱幌，淡月纱窗，那时风景。阳台路迥，云雨梦，便无准。待归来，先指花梢教看，欲把心期细问。问因循过了青春，怎生意稳？'),
	(6158, 437, '倦寻芳慢·露晞向晚', '露晞向晚，帘幕风轻，小院闲昼。翠迳莺来，惊下乱红铺绣。倚危墙，登高榭，海棠经雨胭脂透。算韶华，又因循过了，清明时候。 倦游燕、风光满目，好景良辰，谁共携手。恨被榆钱，买断两眉长斗。忆高阳，人散后。落花流水仍依旧。这情怀，对东风、尽成消瘦。'),
	(6159, 495, '调啸词（效韦苏州·二之一）', '渔父。渔父。水上微风细雨。青蓑黄箬裳衣。红酒白鱼暮归。暮归、暮归、归暮。长笛一声何处。'),
	(6160, 682, '点绛唇·波上清风', '波上清风，画船明月人归后。渐消残酒，独自凭阑久。 聚散匆匆，此恨年年有。重回首，淡烟疏柳，隐隐芜城漏。'),
	(6161, 309, '贺新郎·春情', '篆缕销金鼎。醉沉沉、庭阴转午，画堂人静。芳草王孙知何处，惟有杨花糁径。渐玉枕、腾腾春醒。帘外残红春已透，镇无聊、殢酒厌厌病。云鬓乱，未忺整。 江南旧事休重省。遍天涯、寻消问息，断鸿难倩。月满西楼凭阑久，依旧归期未定。又只恐、瓶沉金井。嘶骑不来银烛暗，枉教人、立尽梧桐影。谁伴我，对鸾镜。'),
	(6162, 202, '清平乐', '兰曰国香，为哲人出，不以色香自炫，乃得天之清者也。楚子不作，兰今安在？得见所南翁枝上数笔，斯可矣。赋此以纪情事云。 孤花一叶，比似前时别。烟水茫茫无处说，冷却西湖残月。贞芳只合深山，红尘了不相关。留得许多清影，幽香不到人间。'),
	(6163, 202, '渡江云·山阴久客一再逢春回忆西杭渺然愁思', '山空天入海，倚楼望极，风急暮潮初。一帘鸠外雨，几处闲田，隔水动春锄。新烟禁柳，想如今、绿到西湖。犹记得、当年深隐，门掩两三株。 愁余。荒洲古溆，断梗疏萍，更漂流何处。空自觉、围羞带减，影怯灯孤。常疑即见桃花面，甚近来、翻笑无书。书纵远，如何梦也都无。'),
	(6164, 422, '高阳台·和周草窗寄越中诸友韵', '残雪庭阴，轻寒帘影，霏霏玉管春葭。小帖金泥，不知春在谁家。相思一夜窗前梦，奈个人、水隔天遮。但凄然，满树幽香，满地横斜。 江南自是离愁苦，况游骢古道，归雁平沙。怎得银笺，殷勤与说年华。如今处处生芳草，纵凭高、不见天涯。更消他，几度东风，几度飞花。'),
	(6165, 366, '传言玉女·钱塘元夕', '一片风流，今夕与谁同乐。月台花馆，慨尘埃漠漠。豪华荡尽，只有青山如洛。钱塘依旧，潮生潮落。 万点灯光，羞照舞钿歌箔。玉梅消瘦，恨东皇命薄。昭君泪流，手捻琵琶弦索。离愁聊寄，画楼哀角。'),
	(6166, 124, '闻鹊喜·吴山观涛', '天水碧，染就一江秋色。鳌戴雪山龙起蛰，快风吹海立。 数点烟鬟青滴，一杼霞绡红湿，白鸟明边帆影直，隔江闻夜笛。'),
	(6167, 112, '踏莎行·润玉笼绡', '润玉笼绡，檀樱倚扇。绣圈犹带脂香浅。榴心空叠舞裙红，艾枝应压愁鬟乱。 午梦千山，窗阴一箭。香瘢新褪红丝腕。隔江人在雨声中，晚风菰叶生秋怨。'),
	(6168, 112, '莺啼序·荷和赵修全韵', '　　横塘棹穿艳锦，引鸳鸯弄水。断霞晚、笑折花归，绀纱低护灯蕊。润玉瘦，冰轻倦浴，斜拖凤股盘云坠。听银床，声细梧桐，渐搅凉思。 　　窗隙流光，冉冉迅羽，诉空梁燕子。误惊起、风竹敲门，故人还又不至。记琅玕、新诗细掐，早陈迹、香痕纤指。怕因循，罗扇恩疏，又生秋意。 　　西湖旧日，画舸频移，叹几萦梦寐。霞佩冷，叠澜不定，麝霭飞雨，乍湿鲛绡，暗盛红泪。綀单夜共，波心宿处，琼箫吹月霓裳舞，向明朝、未觉花容悴。嫣香易落，回头澹碧销烟，镜空画罗屏里。 　　残蝉度曲，唱彻西园，也感红怨翠。念省惯、吴宫幽憩，暗柳追凉，晓岸参斜，露零沤起。丝萦寸藕，留连欢事，桃笙平展湘浪影，有昭华秾李冰相倚。如今鬓点凄霜，半箧秋词，恨盈蠹纸。'),
	(6169, 112, '无闷·催雪', '霓节飞琼，鸾驾弄玉，杳隔平云弱水。倩皓鹤传书，卫姨呼起。莫待粉河凝晓，趁夜月、瑶笙飞环佩。正蹇驴吟影，茶烟灶冷，酒亭门闭。 歌丽。泛碧蚁。放绣帘半钩，宝台临砌。要须借东君，灞陵春意。晓梦先迷楚蝶，早风戾、重寒侵罗被。还怕掩、深院梨花，又作故人清泪。'),
	(6170, 289, '江城子', '从来难翦是离愁。这些愁。几时休。才趁风樯，千里到扬州。见说苍茫云海外，天杳杳，水愁愁。 男儿三十敝貂裘。强追游。梦魂羞。可解筹边，谈笑觅封侯。休傍塞垣酾酒去，伤望眼，怕层楼。'),
	(6171, 513, '沁园春', '吹面无寒，沾衣不湿，岂不快哉。正杏花雨嫩，红飞香砌，柳枝风软，绿映芳台。燕似谈禅，莺如演史，犹有海棠连夜开。清明也，尚阴晴莫准，蜂蝶休猜。 朝来。应问苍苔。甚几日都成锦绣堆。念四方宾友，不堪渭树，一年春事，已属庭槐。宿酒难醒，多情易老，争奈传杯不放杯。如何好，看秋千戏剧，蹴鞠恢谐。'),
	(6172, 91, '八归·秋江带雨', '秋江带雨，寒沙萦水，人瞰画阁愁独。烟蓑散响惊诗思，还被乱鸥飞去，秀句难续。冷眼尽归图画上，认隔岸、微茫云屋。想半属、渔市樵村，欲暮竞然竹。 须信风流未老，凭持酒、慰此凄凉心目。一鞭南陌，几篙官渡，赖有歌眉舒绿。只匆匆眺远，早觉闲愁挂乔木。应难奈，故人天际，望彻淮山，相思无雁足。'),
	(6173, 91, '夜合花·柳锁莺魂', '柳锁莺魂，花翻蝶梦，自知愁染潘郎。轻衫未揽，犹将泪点偷藏。 忘前事，怯流光，早春窥、酥雨池塘。向消凝里，梅开半面，情满徐妆。 风丝一寸柔肠，曾在歌边惹恨，烛底萦香。芳机瑞锦，如何未织鸳鸯。 人扶醉，月依墙，是当初、谁敢疏狂！把闲言语，花房夜久，各自思量。'),
	(6174, 335, '水调歌头·把酒对斜日', '把酒对斜日，无语问西风。胭脂何事，都做颜色染芙蓉。放眼暮江千顷，中有离愁万斛，无处落征鸿。天在阑干角，人倚醉醒中。 千万里，江南北，浙西东。吾生如寄，尚想三径菊花丛。谁是中州豪杰，借我五湖舟楫，去作钓鱼翁。故国且回首，此意莫匆匆。'),
	(6175, 446, '南歌子', '蚁酒浮明月，鲸波泛落星。春花秋叶几飘零。只有庐山君眼、向人青。 明日非今日，长亭更短亭。不辞一饮尽双瓶。争奈秋风江口、酒初醒。'),
	(6176, 591, '菩萨蛮', '旌旗依旧长亭路。尊前试点莺花数。何处捧心颦。人间别样春。 功名君自许。少日闻鸡舞。诗句到梅花。春风十万家。时籍中有放自便者。'),
	(6177, 591, '生查子', '去年燕子来，帘幕深深处。香径得泥归，都把琴书污。 今年燕子来，谁听呢喃语。不见卷帘人，一阵黄昏雨。'),
	(6178, 329, '小重山', '一笑回眸百媚生。娇羞佯不语，艳波横。缓移莲步绕阶行。凝情久，幽怨吒银筝。 些事那回曾。水晶双枕冷，簟纹平。窥人燕子苦无情。惊梦断，何处觅云行。'),
	(6179, 498, '南柯子·怅望梅花驿', '怅望梅花驿，凝情杜若洲。香云低处有高楼，可惜高楼不近木兰舟。 缄素双鱼远，题红片叶秋。欲凭江水寄离愁，江已东流那肯更西流。'),
	(6180, 148, '点绛唇（兰花）', '潇洒寒林，玉丛遥映松篁底。凤簪斜倚。笑傲东风里。 一种幽芳，自有先春意。香风细。国人争媚。不数桃和李。'),
	(6181, 271, '菩萨蛮·咏梅', '湿云不渡溪桥冷。娥寒初破东风影。溪下水声长。一枝和月香。 人怜花似旧，花不知人瘦。独自倚阑干，夜深花正寒。'),
	(6182, 669, '好事近·汴京赐宴闻教坊乐有感', '凝碧旧池头，一听管弦凄切。多少梨园声在，总不堪华发。 杏花无处避春愁，也傍野烟发。惟有御沟声断，似知人呜咽。'),
	(6183, 128, '苏幕遮', '老相邀，山作伴。千里西来，始识庐山面。爱酒杨雄浑不管。天与邻翁，来慰穷愁眼。 似惊鸿，吹又散。画舸横江，望断江南岸。地角天涯无近远。一阕清歌，且放梨花满。'),
	(6184, 267, '相见欢', '东风吹尽江梅。橘花开。旧日吴王宫殿、长青苔。 今古事。英雄泪。老相催。长恨夕阳西去、晚潮回。'),
	(6185, 130, '一剪梅', '一剪梅花万样娇。斜插梅枝，略点眉梢。轻盈微笑舞低回，何事尊前拍误招。 夜渐寒深酒渐消。袖里时闻玉钏敲。城头谁恁促残更，银漏何如，且慢明朝。'),
	(6186, 130, '浣沙溪·翠葆参差竹径成', '翠葆参差竹径成。新荷跳雨泪珠倾。曲阑斜转小池亭。 风约帘衣归燕急，水摇扇影戏鱼惊。柳梢残日弄微晴。'),
	(6187, 130, '少年游·朝云漠漠散轻丝', '朝云漠漠散轻丝。楼阁淡春姿。柳泣花啼，九街泥重，门外燕飞迟。 而今丽日明金屋，春色在桃枝。不似当时，小桥冲雨，幽恨两人知。'),
	(6188, 549, '梦江南·九曲池头三月三', '九曲池头三月三，柳毵毵。香尘扑马喷金衔，涴春衫。 苦笋鲥鱼乡味美，梦江南。阊门烟水晚风恬，落归帆。'),
	(6189, 451, '桃源忆故人·玉楼深锁薄情种', '玉楼深锁薄情种，清夜悠悠谁共？羞见枕衾鸳凤，闷则和衣拥。 无端画角严城动，惊破一番新梦。窗外月华霜重，听彻《梅花弄》。'),
	(6190, 693, '品令·茶词', '凤舞团团饼。恨分破、教孤令。金渠体净，只轮慢碾，玉尘光莹。汤响松风，早减了、二分酒病。 味浓香永。醉乡路、成佳境。恰如灯下，故人万里，归来对影。口不能言，心下快活自省。'),
	(6191, 693, '满庭芳·茶', '北苑春风，方圭圆璧，万里名动京关。碎身粉骨，功合上凌烟。尊俎风流战胜，降春睡、开拓愁边。纤纤捧，研膏浅乳，金缕鹧鸪斑。 相如，虽病渴，一觞一咏，宾有群贤。为扶起灯前，醉玉颓山。搜搅胸中万卷，还倾动、三峡词源。归来晚，文君未寐，相对小窗前。'),
	(6192, 494, '千秋岁·次韵少游', '岛边天外， 未老身先退。 珠泪溅， 丹衷碎。 声摇苍玉佩、色重黄金带。一万里，斜阳正与长安对。 道远谁云会，罪大天能盖。君命重，臣节在。新恩犹可觊，旧学终能难改。吾已矣，乘桴且恁浮于海。'),
	(6193, 494, '调笑令', '渔父。渔父。江上微风细雨。青蓑黄箬裳衣。红酒白鱼暮归。归暮。长笛一声何处。'),
	(6194, 494, '浣溪沙（渔父）', '西塞山边白鹭飞。散花洲外片帆微。桃花流水鳜鱼肥。 自庇一身青箬笠，相随到处绿蓑衣。斜风细雨不须归。'),
	(6195, 494, '江神子（冬景）', '相逢不觉又初寒。对尊前。惜流年。风紧离亭，冰结泪珠圆。雪意留君君不住，从此去，少清欢。转头山下转头看。路漫漫。玉花翻。银海光宽，何处是超然。知道故人相念否，携翠袖，倚朱阑。'),
	(6196, 494, '水龙吟·赠赵晦之吹笛侍儿', '楚山修竹如云，异材秀出千林表。龙须半翦，凤膺微涨，玉肌匀绕。木落淮南，雨睛云梦，月明风袅。自中郎不见，桓伊去后，知孤负、秋多少。 闻道岭南太守，后堂深、绿珠娇小。绮窗学弄，梁州初遍，霓裳未了。嚼徵含宫，泛商流羽，一声云杪。为使君洗尽，蛮风瘴雨，作霜天晓。'),
	(6197, 250, '采桑子', '年年此夕东城见，欢意匆匆。明日还重。却在楼台缥缈中。 垂螺拂黛清歌女，曾唱相逢。秋月春风。醉枕香衾一岁同。'),
	(6198, 250, '虞美人·湿红笺纸回纹字', '湿红笺纸回纹字。多少柔肠事。去年双燕欲归时。还是碧云千里、锦书迟。 南楼风月长依旧。别恨无端有。倩谁横笛倚危阑。今夜落梅声里、怨关山。'),
	(6199, 250, '生查子·落梅庭榭香', '落梅庭榭香，芳草池塘绿。春恨最关情，日过阑干曲。 几时花里闲，看得花枝足。醉后莫思家，借取师师宿。'),
	(6200, 250, '鹧鸪天·九日悲秋不到心', '九日悲秋不到心。凤城歌管有新音。凤凋碧柳愁眉淡，露染黄花笑靥深。 初见雁，已闻砧。绮罗丛里胜登临。须教月户纤纤玉，细捧霞觞滟滟金。'),
	(6201, 112, '浣溪沙·门隔花深旧梦游', '门隔花深旧梦游，夕阳无语燕归愁。玉纤香动小帘钩。落絮无声春堕泪，行云有影月含羞。东风临夜冷于秋。'),
	(6202, 28, '一剪梅·漠漠春阴酒半酣', '漠漠春阴酒半酣。风透春衫，雨透春衫。人家蚕事欲眠三。桑满筐篮，柘满筐篮。 先自离怀百不堪。樯燕呢喃，梁燕呢喃。篝灯强把锦书看。人在江南，心在江南。'),
	(6203, 149, '鬲溪梅令·丙辰冬自无锡归作此寓意', '好花不与殢香人。浪粼粼。又恐春风归去绿成阴。玉钿何处寻。 木兰双桨梦中云。小横陈。漫向孤山山下觅盈盈。翠禽啼一春。'),
	(6204, 596, '浪淘沙·疏雨洗天清', '疏雨洗天清。枕簟凉生。井桐一叶做秋声。谁念客身轻似叶，千里飘零？ 梦断古台城。月淡潮平。便须携酒访新亭。不见当时王谢宅，烟草青青。'),
	(6205, 458, '沁园春·读史记有感', '试课阳坡，春后添栽，多少杉松。正桃坞昼浓，云溪风软，从容延叩，太史丞公：底事越人，见垣一壁，比过秦关遽失瞳？江神吏，灵能脱罟，不发卫平蒙？ 休言唐举无功，更休笑丘轲自阣穷。算汨罗醒处，元来醉里；真敖假孟，毕竟谁封？太史亡言，床头酿熟，人在晴岚烟霭中。新堤路，喜樛枝鳞角，夭矫苍龙。'),
	(6206, 583, '恋绣衾·柳丝空有千万条', '柳丝空有千万条。系不住、溪桡。想今宵、也对新月，过轻寒、何处小桥。 玉箫榭春多少，溜啼红、脸霞未消。怪别来、胭脂慵傅，被东风、偷在杏梢。'),
	(6207, 631, '江城子', '绿莺庭院燕莺啼。绣帘垂。瑞烟霏。一片笙箫，声过彩云低。疑是蕊宫仙子降，翻玉袖，舞瑶姬。 冰姿玉质自清奇。看孙枝。列班衣。画鼓新歌，喜映两疏眉。袖里蟠桃花露湿，应不惜，醉金卮。'),
	(6208, 384, '菩萨蛮·宿水口', '断虹远饮横江水，万山紫翠斜阳里。 系马短亭西，丹枫明酒旗。 浮生常客路，事逐孤鸿去。 又是月黄昏，寒灯人闭门。'),
	(6209, 172, '祝英台近·北固亭', '澹烟横，层雾敛。胜概分雄占。月下鸣榔，风急怒涛飐。关河无限清愁，不堪临鉴。正霜鬓、秋风尘染。 漫登览。极目万里沙场，事业频看剑。古往今来，南北限天堑。倚楼谁弄新声，重城正掩。历历数、西州更点。'),
	(6210, 28, '祝英台近·惜多才', '惜多才，怜薄命，无计可留汝。揉碎花笺，忍写断肠句。道旁杨柳依依，千丝万缕，抵不住、一分愁绪。 如何诉。便教缘尽今生，此身已轻许。捉月盟言，不是梦中语。后回君若重来，不相忘处，把杯酒、浇奴坟土。'),
	(6211, 272, '水调歌头（次袁仲机韵）', '长记与君别，丹凤九重城。归来故里，愁思怅望渺难平。今夕不知何夕，得共寒潭烟艇，一笑俯空明。有酒径须醉，无事莫关情。 寻梅去，疏竹外，一枝横。与君吟弄风月，端不负平生。何处车尘不到，有个江天如许，争肯换浮名。只恐买山隐，却要练丹成。'),
	(6212, 447, '燕归梁·春愁', '芳草年年惹恨幽。想前事悠悠。伤春伤别几时休。算从古、为风流。 春山总把，深匀翠黛，千叠在眉头。不知供得几多愁。更斜日、凭危楼。'),
	(6213, 301, '人月圆·小桃枝上春风早', '小桃枝上春风早，初试薄罗衣。年年乐事，华灯竞处，人月圆时。 禁街箫鼓，寒轻夜永，纤手重携。更阑人散，千门笑语，声在帘帏。'),
	(6214, 515, '贺新郎·吴江', '浪涌孤亭起。是当年、蓬莱顶上，海风飘坠。帝遣江神长守护，八柱蛟龙缠尾。斗吐出、寒烟寒雨。昨夜鲸翻坤轴动，卷雕翚、掷向虚空里。但留得，绛虹住。 五湖有客扁舟舣。怕群仙、重游到此，翠旌难驻。手拍阑干呼白鹭，为我殷勤寄语。奈鹭也、惊飞沙渚。星月一天云万壑，览茫茫、宇宙知何处。鼓双楫，浩歌去。'),
	(6215, 112, '金缕歌·陪履斋先生沧浪看梅', '乔木生云气。访中兴、英雄陈迹，暗追前事。战舰东风慳借便，梦断神州故里。旋小筑、吴宫闲地。华表月明归夜鹤，叹当时、花竹今如此。枝上露，溅清泪。 遨头小簇行春队。步苍苔、寻幽别坞，问梅开未。重唱梅边新度曲，催发寒梢冻蕊。此心与、东君同意。后不如今今非昔，两无言、相对沧浪水。怀此恨，寄残醉。'),
	(6216, 112, '惜黄花慢·菊', '粉靥金裳。映绣屏认得，旧日萧娘。翠微高处，故人帽底，一年最好，偏是重阳。避春祗怕春不远，望幽径、偷理秋妆。殢醉乡。寸心似翦，飘荡愁觞。 潮腮笑入清霜。斗万花样巧，深染蜂黄。露痕千点，自怜旧色，寒泉半掬，百感幽香。雁声不到东篱畔，满城但、风雨凄凉。最断肠。夜深怨蝶飞狂。'),
	(6217, 213, '月上瓜洲·寓乌夜啼南徐多景楼作', '江头又见新秋。几多愁。塞草连天何处、是神州。 英雄恨，古今泪，水东流。惟有渔竿明月、上瓜洲。'),
	(6218, 91, '满江红·九月二十一日出京怀古', '缓辔西风，叹三宿、迟迟行客。桑梓外，锄耰渐入，柳坊花陌。双阙远腾龙凤影，九门空锁鸳鸾翼。更无人擫笛傍宫墙，苔花碧。 天相汉，民怀国。天厌虏，臣离德。趁建瓴一举，并收鳌极。老子岂无经世术，诗人不预平戎策。办一襟风月看升平，吟春色。'),
	(6219, 91, '贺新郎', '鹊翅西风浅。乍疏云垂幔，近月银钩将卷。天上应闲支机石，前度芳盟谁践。便好织、回文锦献。乞得秾欢今夜里，算盈盈、一水曾何远。宁不会，暗相见。 彩楼吹断闲针线。想幽情嫩约，别有藓庭花院。青鸟沈沈音尘绝，烟锁蓬莱宫殿。渐木杪、参旗西转。不怕天孙成间阻，怕人间、薄幸心肠变。又学得，易分散。'),
	(6220, 91, '寿楼春·寻春服感念', '裁春衫寻芳。记金刀素手，同在晴窗。几度因风残絮，照花斜阳。谁念我，今无裳？自少年、消磨疏狂。但听雨挑灯，攲床病酒，多梦睡时妆。 飞花去，良宵长。有丝阑旧曲，金谱新腔。最恨湘云人散，楚兰魂伤。身是客、愁为乡。算玉箫、犹逢韦郎。近寒食人家，相思未忘苹藻香。'),
	(6221, 149, '忆王孙·番阳彭氏小楼作', '冷红叶叶下塘秋。长与行云共一舟。零落江南不自由。两绸缪。料得吟鸾夜夜愁。'),
	(6222, 456, '酷相思·月挂霜林寒欲坠', '月挂霜林寒欲坠。正门外、催人起。奈离别、如今真个是。欲住也、留无计。欲去也、来无计。 马上离魂衣上泪。各自个、供憔悴。问江路梅花开也未。春到也、须频寄。人到也、须频寄。'),
	(6223, 591, '浣溪沙·偕叔高子似宿山寺戏作', '花向今朝粉面匀。柳因何事翠眉颦。东风吹雨细於尘。 自笑好山如好色，只今怀树更怀人。闲愁闲恨一番新。'),
	(6224, 591, '满江红·山居即事', '几个轻鸥，来点破、一泓澄绿。更何处、一双鸂鶒，故来争浴。细读离骚还痛饮，饱看修竹何妨肉。有飞泉、日日供明珠，三千斛。 春雨满，秧新谷。闲日永，眠黄犊。看云连麦垄，雪堆蚕簇，若要足时今足矣，以为未足何时足。被野老、相扶入东园，枇杷熟。'),
	(6225, 591, '虞美人', '夜深困倚屏风后。试请毛延寿。宝钗小立白翻香。旋唱新词犹误、笑持觞。 四更山月寒侵席。歌舞催时日。问他何处最情浓。却道小梅摇落、不禁风。'),
	(6226, 591, '满江红·和范先之雪', '天上飞琼，毕竟向、人间情薄。还又跨、玉龙归去，万花摇落。云破林梢添远岫，月临屋角分层阁。记少年、骏马走韩卢，掀东郭。 吟冻雁，嘲饥鹊。人已老，欢犹昨。对琼瑶满地，与君酬酢。最爱霏霏迷远近，却收扰扰还寥廓。待羔儿、酒罢又烹茶，扬州鹤。'),
	(6227, 591, '摸鱼儿（两岩有石状怪甚，取离骚九歌名曰山鬼，因赋摸鱼儿，改名山鬼谣）', '问何年，此山来此，西风落日无语。看君似是羲皇上，直作太初名汝。溪上路。算只有、红尘不到今犹古。一杯谁举。笑我醉呼君，崔嵬未起，山鸟覆杯去。 须记取。昨夜龙湫风雨。门前石浪掀舞。四更山鬼吹灯啸，惊倒世间儿女。依约处。还问我、清游杖履公良苦。神交心许。待万里携君，鞭笞鸾凤，诵我远游赋。'),
	(6228, 591, '满江红·江行和杨济翁韵', '过眼溪山，怪都似、旧时相识。还记得、梦中行遍，江南江北。佳处径须携杖去，能消几緉平生屐。笑尘劳、三十九年非、长为客。 吴楚地，东南坼。英雄事，曹刘敌。被西风吹尽，了无尘迹。楼观才成人已去，旌旗未卷头先白。叹人间、哀乐转相寻，今犹昔。'),
	(6229, 105, '小重山·七夕病中', '半夜灯残鼠上檠。上窗风动竹，月微明。梦魂偏记水西亭。琅玕碧，花影弄蜻蜓。 千里暮云平，南楼催上烛，晚来晴。酒阑人散斗西倾。天如水，团扇扑流萤。'),
	(6230, 425, '长相思', '来匆匆。去匆匆。短梦无凭春又空。难随郎马踪。 山重重。水重重。飞絮流云西复东。音书何处通。'),
	(6231, 267, '苏幕遮', '酒壶空，歌扇去。独倚危楼，无限伤心处。芳草连天云薄暮。故国山河，一阵黄梅雨。 有奇才，无用处。壮节飘零，受尽人间苦。欲指虚无问征路。回首风云，未忍辞明主。'),
	(6232, 360, '烛影摇红·松窗午梦初觉', '一亩清阴，半天潇洒松窗午。床头秋色小屏山，碧帐垂烟缕。 枕畔风摇绿户。唤人醒、不教梦去。可怜恰到，瘦石寒泉，冷云幽处。'),
	(6233, 130, '花犯·小石梅花', '粉墙低，梅花照眼，依然旧风味。露痕轻缀。疑净洗铅华，无限佳丽。去年胜赏曾孤倚。冰盘同宴喜。更可惜，雪中高树，香篝熏素被。 今年对花最匆匆，相逢似有恨，依依愁悴。吟望久，青苔上、旋看飞坠。相将见、脆丸荐酒，人正在、空江烟浪里。但梦想、一枝潇洒，黄昏斜照水。'),
	(6234, 549, '凤求凰', '园林幂翠，燕寝凝香。华池缭绕飞廊。坐按吴娃清丽，楚调圆长。歌阑横流美眄，乍疑生、绮席辉光。文园属意，玉觞交劝，宝瑟高张。 南薰难销幽恨，金徽上，殷勤彩凤求凰。便许卷收行雨，不恋高唐。东山胜游在眼，待纫兰、撷菊相将。双栖安隐，五云溪是故乡。'),
	(6235, 451, '画堂春·春情', '东风吹柳日初长，雨余芳草斜阳。杏花零落燕泥香，睡损红妆。 宝篆烟销龙凤，画屏云锁潇湘。夜寒微透薄罗裳，无限思量。'),
	(6236, 451, '满庭芳·晓色云开', '晓色云开，春随人意，骤雨才过还晴。古台芳榭，飞燕蹴红英。舞困榆钱自落，秋千外、绿水桥平。东风里，朱门映柳，低按小秦筝。 多情，行乐处，珠钿翠盖，玉辔红缨。渐酒空金榷，花困蓬瀛。豆蔻梢头旧恨，十年梦、屈指堪惊。凭阑久，疏烟淡日，寂寞下芜城。'),
	(6237, 494, '点绛唇·闲倚胡床', '闲倚胡床，庾公楼外峰千朵。与谁同坐。明月清风我。 别乘一来，有唱应须和。还知么。自从添个。风月平分破。'),
	(6238, 494, '如梦令·有寄', '为向东坡传语。人在玉堂深处。别后有谁来，雪压小桥无路。归去。归去。江上一犁春雨。'),
	(6239, 250, '采桑子·金风玉露初凉夜', '金风玉露初凉夜，秋草窗前。浅醉闲眠。一枕江风梦不圆。 长情短恨难凭寄，枉费红笺。试拂么弦。却恐琴心可暗传。'),
	(6240, 250, '诉衷情', '长因蕙草记罗裙。绿腰沈水熏。阑干曲处人静，曾共倚黄昏。 风有韵，月无痕。暗消魂。拟将幽恨，试写残花，寄与朝云。'),
	(6241, 250, '浣溪沙·二月和风到碧城', '二月和风到碧城。万条千缕绿相迎。舞烟眠雨过清明。 妆镜巧眉偷叶样，歌楼妍曲借枝名。晚秋霜霰莫无情。'),
	(6242, 250, '生查子·坠雨已辞云', '坠雨已辞云，流水难归浦。遗恨几时休，心抵秋莲苦。 忍泪不能歌，试托哀弦语。弦语愿相逢，知有相逢否。'),
	(6243, 250, '生查子·金鞭美少年', '金鞭美少年，去跃青骢马。牵系玉楼人，绣被春寒夜。 消息未归来，寒食梨花谢。无处说相思，背面秋千下。'),
	(6244, 409, '甘露歌', '折得一枝香在手。人间应未有。疑是经春雪未消。今日是何朝。'),
	(6245, 352, '鹧鸪天', '学画宫眉细细长。芙蓉出水斗新妆。只知一笑能倾国，不信相看有断肠。 双黄鹄，两鸳鸯。迢迢云水恨难忘。早知今日长相忆，不及从初莫作双。'),
	(6246, 352, '采桑子·平生为爱西湖好', '平生为爱西湖好，来拥朱轮。富贵浮云，俯仰流年二十春。 归来恰似辽东鹤，城郭人民，触目皆新，谁识当年旧主人？'),
	(6247, 186, '谢池春慢·玉仙观道中逢谢媚卿', '缭墙重院，时闻有、啼莺到。绣被掩余寒，画幕明新晓。朱槛连空阔，飞絮无多少。径莎平，池水渺。日长风静，花影闲相照。 尘香拂马，逢谢女、城南道。秀艳过施粉，多媚生轻笑。斗色鲜衣薄，碾玉双蝉小。欢难偶，春过了。琵琶流怨，都入相思调。'),
	(6248, 175, '浪淘沙·极目楚天空', '极目楚天空，云雨无踪，漫留遗恨锁眉峰。自是荷花开较晚，孤负东风。 客馆叹飘蓬，聚散匆匆，扬鞭那忍骤花骢。望断斜阳人不见，满袖啼红。'),
	(6249, 98, '黄金缕·妾本钱塘江上住', '妾本钱塘江上住。花落花开，不管流年度。燕子衔将春色去，纱窗几阵黄梅雨。 斜插犀梳云半吐，檀板轻敲，唱彻黄金缕。望断行云无觅处，梦回明月生南浦。'),
	(6250, 71, '桂枝香·吹箫人去', '吹箫人去。但桂影徘徊，荒杯承露。东望鞭芙缥缈，寒光如注。去年夜半横江梦，倚危樯，参差曾赋。茫茫角动，回舟尽兴，未惊鸥鹭。 情知道、明年何处。漫待客黄楼，尘波前度。二十四桥，颇有杜书记否。二三字者今如此，看使君、角巾东路。人间俯仰，悲欢何限，团圆如故。'),
	(6251, 130, '浣溪沙·水涨鱼天拍柳桥', '水涨鱼天拍柳桥。云鸠拖雨过江皋。一番春信入东郊。 闲碾凤团消短梦，静看燕子垒新巢。又移日影上花梢。'),
	(6252, 533, '行香子·秋入鸣皋', '秋入鸣皋，爽气飘萧。挂衣冠、初脱尘劳。窗间岩岫，看尽昏朝。夜山低，晴山近，晓山高。 细数闲来，几处村醪。醉模糊、信手挥毫。等闲陶写，问甚风骚。乐因循，能潦倒，也消摇。'),
	(6253, 583, '梦江南', '帘不卷，细雨熟樱桃。数点霁霞山又晚，一痕凉月酒初消。风紧絮花高。闲处少，磨尽少年豪。昨梦醉来骑白鹿，满湖春水段家桥。濯发听吹箫。'),
	(6254, 272, '鹧鸪天', '已分江湖寄此生。长蓑短笠任阴晴。鸣桡细雨沧洲远，系舸斜阳画阁明。 奇绝处，未忘情。几时还得去寻盟。江妃定许捐双佩，渔父何劳笑独醒。'),
	(6255, 272, '浣溪沙（次秀野酴醿韵）', '压架年来雪作堆。珍丛也是近移栽。肯令容易放春回。却恐阴晴无定度，从教红白一时开。多情蜂蝶早飞来。'),
	(6256, 261, '赏南枝', '暮冬天地闭，正柔木冻折，瑞雪飘飞。对景见南山？岭梅露、几点清雅容姿。丹染萼、玉缀枝。又岂是、一阳有私。大抵是、化工独许，使占却先时。 霜威莫苦凌持。此花根性，想群卉争知。贵用在和羹，三春里、不管绿是红非。攀赏处、宜酒卮。醉捻嗅、幽香更奇。倚阑干、仗何人去，嘱羌管休吹。'),
	(6257, 202, '疏影·梅影', '黄昏片月。似碎阴满地，还更清绝。枝北枝南，疑有疑无，几度背灯难折。依稀倩女离魂处，缓步出、前村时节。看夜深、竹外横斜，应妒过云明灭。 窥镜蛾眉淡抹。为容不在貌，独抱孤洁。莫是花光，描取春痕，不怕丽谯吹彻。还惊海上然犀去，照水底、珊瑚如活。做弄得、酒醒天寒，空对一庭香雪。'),
	(6258, 422, '天香·咏龙涎香', '孤峤蟠烟，层涛蜕月，骊宫夜采铅水。讯远槎风，梦深薇露，化作断魂心字。红瓷候火，还乍识、冰环玉指。一缕萦帘翠影，依稀海天云气。 几回殢娇半醉。翦春灯、夜寒花碎。更好故溪飞雪，小窗深闭。荀令如今顿老，总忘却、樽前旧风味。谩余熏，空篝素被。'),
	(6259, 366, '莺啼序·重过金陵', '金陵故都最好，有朱楼迢递。嗟倦客、又此凭高，槛外已少佳致。更落尽梨花，飞尽杨花，春也成憔悴。问青山、三国英雄，六朝奇伟。 麦甸葵丘，荒台败垒。鹿豕衔枯荠。正朝打孤城，寂寞斜阳影里。听楼头、哀笳怨角，未把酒、愁心先醉。渐夜深，月满秦淮，烟笼寒水。 凄凄惨惨，冷冷清清，灯火渡头市。慨商女不知兴废。隔江犹唱庭花，余音亹亹。伤心千古，泪痕如洗。乌衣巷口青芜路，认依稀、王谢旧邻里。临春结绮。可怜红粉成灰，萧索白杨风起。 因思畴昔，铁索千寻，谩沈江底。挥羽扇、障西尘，便好角巾私第。清谈到底成何事。回首新亭，风景今如此。楚囚对泣何时已。叹人间、今古真儿戏。东风岁岁还来，吹入钟山，几重苍翠。'),
	(6260, 112, '醉桃源·芙蓉', '青春花姊不同时。凄凉生较迟。艳妆临水最相宜。风来吹绣漪。 惊旧事，问长眉。月明仙梦回。凭阑人但觉秋肥。花愁人不知。'),
	(6261, 112, '花犯·谢黄复庵除夜寄古梅枝', '翦横枝，清溪分影，翛然镜空晓。小窗春到。怜夜冷孀娥，相伴孤照。古苔泪锁霜千点，苍华人共老。料浅雪、黄昏驿路，飞香遗冻草。 行云梦中认琼娘，冰肌瘦，窈窕风前纤缟。残醉醒，屏山外、翠禽声小。寒泉贮、绀壶渐暖，年事对、青灯惊换了。但恐舞、一帘胡蝶，玉龙吹又杳。'),
	(6262, 112, '荔枝香近·七夕', '睡轻时闻，晚鹊噪庭树。又说今夕天津，西畔重欢遇。蛛丝暗锁红楼，燕子穿帘处。天上、未比人间更情苦。 秋鬓改，妒月姊、长眉妩。过雨西风，数叶井梧愁舞。梦入蓝桥，几点疏星映朱户。泪湿沙边凝伫。'),
	(6263, 117, '贺新郎', '月绽浮云里。未须臾、长风扫荡、碧空如水。谁在冰壶玉界上，眇视征蛮战蚁。便弃掷、尘寰脱＿。＿葛清冷襟袖冷，露华浓、暗袭人肌理。和酷暑，争些气。 谯楼漏转三更二。夜沈沈、经星纬宿，换垣移市。万籁渐生秋意思，时节那堪屈指。奈投老、未酬归计。矫首高天天不应，忽林梢、睡鹊惊飞起。同一梦，我与尔。'),
	(6264, 117, '满江红·豫章滕王阁', '万里西风，吹我上、滕王高阁。正槛外、楚山云涨，楚江涛作。何处征帆木末去，有时野鸟沙边落。近帘钩、暮雨掩空来，今犹昨。 秋渐紧，添离索。天正远，伤飘泊。叹十年心事，休休莫莫。岁月无多人易老，乾坤虽大愁难著。向黄昏、断送客魂消，城头角。'),
	(6265, 49, '一剪梅·袁州解印', '陌上行人怪府公，还是诗穷，还是文穷？下车上马太匆匆，来是春风，去是秋风。 阶衔免得带兵农，嬉到昏钟，睡到斋钟。不消提岳与知宫，唤作山翁，唤作溪翁。'),
	(6266, 49, '生查子·元夕戏陈敬叟', '繁灯夺霁华。戏鼓侵明发。物色旧时同，情味中年别。 浅画镜中眉，深拜楼西月。人散市声收，渐入愁时节。'),
	(6267, 681, '浪淘沙', '鹤外倚楼看。云＿晴天。天高鸡犬碍云间。掉臂双仙留不彻，还任人间。 客珮振珊珊。来贺平安。年年直待卷灯还。似是天公偏著意，占破春闲。'),
	(6268, 670, '鹧鸪天·兰溪舟中', '雨湿西风水面烟。一巾华发上溪船。帆迎山色来还去，橹破滩痕散复圆。 寻浊酒，试吟篇。避人鸥鹭更翩翩。五更犹作钱塘梦，睡觉方知过眼前。'),
	(6269, 149, '江梅引·丙辰之冬予留梁溪将诣淮而不得因梦思以述志', '人间离别易多时。见梅枝。忽相思。几度小窗，幽梦手同携。今夜梦中无觅处，漫徘徊。寒侵被、尚未知。 湿红恨墨浅封题。宝筝空、无雁飞。俊游巷陌，算空有、古木斜晖。旧约扁舟，心事已成非。歌罢淮南春草赋，又萋萋。漂零客、泪满衣。'),
	(6270, 639, '一丛花·溪堂玩月作', '冰轮斜辗镜天长。江练隐寒光。危阑醉倚人如画，隔烟村、何处鸣根。乌鹊倦栖，鱼龙惊起，星斗挂垂杨。 芦花千顶水微茫。秋色满江乡。楼台恍似游仙梦，又疑是、洛浦潇湘。风露浩然，山河影转，今古照凄凉。'),
	(6271, 591, '西江月（春晚）', '胜欲读书已懒，只因多病长闲。听风听雨小窗眠。过了春光太半。 往事如寻去鸟，清愁难解连环。流莺不肯入西园。唤起画梁飞燕。'),
	(6272, 591, '满江红', '涂堂上，三鼓方醒。国华赋词留别，席上和韵。青涂，端仁堂名也 宿酒醒时，算只有、清愁而已。人正在、青涂堂上，月华如洗。纸帐梅花归梦觉，莼羹鲈鲙秋风起。问人生、得意几何时，吾归矣。 君若问，相思事。料长在，歌声里。这情怀只是，中年如此。明月何妨千里隔，顾君与我何如耳。向尊前、重约几时来，江山美。'),
	(6273, 591, '定风波·席上送范廓之游建康', '听我尊前醉后歌。人生亡奈别离何。但使情亲千里近，须信。无情对面是山河。 寄语石头城下水。居士。而今浑不怕风波。借使未如鸥鸟惯。相伴。也应学得老渔蓑。'),
	(6274, 591, '贺新郎·用前韵送杜叔高', '细把君诗说。怅余音、钧天浩荡，洞庭胶葛。千尺阴崖尘不到，惟有层冰积雪。乍一见、寒生毛发。自昔佳人多薄命，对古来、一片伤心月。金屋冷，夜调瑟。 去天尺五君家别。看乘空、鱼龙惨淡，风云开合。起望衣冠神州路，白日销残战骨。叹夷甫、诸人清绝。夜半狂歌悲风起，听铮铮、阵马檐间铁。南共北，正分裂。'),
	(6275, 591, '满江红·建康史帅致道席上赋', '鹏翼垂空，笑人世，苍然无物。又还向、九重深处，玉阶山立。袖里珍奇光五色，他年要补天西北。且归来，谈笑护长江，波澄碧。 佳丽地，文章伯。《金缕》唱，红牙拍。看尊前飞下，日边消息。料想宝香黄阁梦，依然画舫青溪笛。待如今、端的约钟山，长相识。'),
	(6276, 498, '菩萨蛮·湘东驿', '客行忽到湘东驿。明朝真是潇湘客。晴碧万重云。几时逢故人。 江南如塞北。别后书难得。先自雁来稀。那堪春半时。'),
	(6277, 633, '秋波媚', '曾散天花蕊珠宫。一念堕尘中。铅华洗尽，珠玑不御，道骨仙风。 东游我醉骑鲸去，君驾素鸾从。垂虹看月，天台采药，更与谁同。'),
	(6278, 271, '清平乐·夏日游湖', '恼烟撩露。留我须臾住。携手藕花湖上路。一霎黄梅细雨。 娇痴不怕人猜。随群暂遣愁怀。最是分携时候，归来懒傍妆台。'),
	(6279, 388, '渔家傲引·子月水寒风又烈', '子月水寒风又烈。巨鱼漏网成虚设。圉圉从它归丙穴。谋自拙。空归不管旁人说。 昨夜醉眠西浦月。今宵独钓南溪雪。妻子一船衣百结。长欢悦。不知人世多离别。'),
	(6280, 99, '减字木兰花·斜红叠翠', '公下世。此词，公之绝笔也 斜红叠翠。何许花神来献瑞。粲粲裳衣。割得天孙锦一机。 真香妙质。不耐世间风与日。着意遮围。莫放春光造次归。'),
	(6281, 99, '鹧鸪天·紫禁烟花一万重', '有怀京师上元，与韩叔夏司谏、王夏卿侍郎、曹仲谷少卿同赋。 紫禁烟花一万重，鳌山宫阙倚晴空。玉皇端拱彤云上，人物嬉游陆海中。 星转斗，驾回龙。五侯池馆醉春风。而今白发三千丈，愁对寒灯数点红。'),
	(6282, 589, '点绛唇·春愁', '香冷金炉，梦回鸳帐余香嫩。更无人问。一枕江南恨。 消瘦休文，顿觉春衫褪。清明近。杏花吹尽。薄暮东风紧。'),
	(6283, 305, '瑞鹧鸪（双银杏）', '风韵雍容未甚都。尊前甘橘可为奴。谁怜流落江湖上，玉骨冰肌未肯枯。 谁教并蒂连枝摘，醉后明皇倚太真。居士擘开真有意，要吟风味两家新。'),
	(6284, 128, '临江仙·送光州曾使君', '记得武陵相见日，六年往事堪惊。回头双鬓已星星。谁知江上酒，还与故人倾。 铁马红旗寒日暮，使君犹寄边城。只愁飞诏下青冥。不应霜塞晚，横槊看诗成。'),
	(6285, 267, '好事近·春雨细如尘', '春雨细如尘，楼外柳丝黄湿。风约绣帘斜去，透窗纱寒碧。 美人慵翦上元灯，弹泪倚瑶瑟。却上紫姑香火，问辽东消息。'),
	(6286, 564, '浪淘沙', '约素小腰身。不奈伤春。疏梅影下晚妆新。袅袅娉娉何样似，一缕轻云。 歌巧动朱唇。字字娇嗔。桃花深处一通津。怅望瑶台清夜月，还送归轮。'),
	(6287, 247, '感皇恩·寒食不多时', '寒食不多时，牡丹初卖。小院重帘燕飞碍。昨宵风雨，只有一分春在，今朝犹自得，阴晴快。 熟睡起来，宿酲微带。不惜罗襟揾眉黛，日高梳洗，看著花阴移改。笑摘双杏子，连枝戴。'),
	(6288, 130, '三部乐·商调梅雪', '浮玉飞琼，向邃馆静轩，倍增清绝。夜窗垂练，何用交光明月。近闻道、官阁多梅，趁暗香未远，冻蕊初发。倩谁摘取，寄赠情人桃叶。 回文近传锦字，道为君瘦损，是人都说。祅知染红著手，胶梳黏发。转思量、镇长堕睫。都只为、情深意切。欲报消息，无一句、堪愈愁结。'),
	(6289, 549, '晚云高', '秋尽江南叶未凋。晚云高。青山隐隐水迢迢。接亭皋。 二十四桥明月夜，弭兰桡。玉人何处教吹箫。可怜宵。'),
	(6290, 451, '兰陵王', '雨初歇。帘卷一钩淡月。望河汉、几点疏星，冉冉纤云度林樾。此景清更绝。谁念温柔蕴结。孤灯暗，独步华堂，蟋蟀莎阶弄时节。 沈思恨难说。忆花底相逢，亲赠罗缬。春鸿秋雁轻离别。拟寻个锦鳞，寄将尺素，又恐烟波路隔越。歌残唾壶缺。 凄咽。意空切。但醉损琼卮，望断蔗瑶阙。御沟曾解流红叶。待何日重见，霓裳听彻。彩楼天远，夜夜襟袖染啼血。'),
	(6291, 451, '如梦令·池上春归何处', '池上春归何处？满目落花飞絮。孤馆悄无人，梦断月堤归路。无绪，无绪。帘外五更风雨。'),
	(6292, 248, '苏幕遮', '碧桃花，春婉娩。未断尘缘，暂别瑶池宴。谪限迢迢应未满。乘月骖鸾。曾有深深愿。 帝城赊，凤楼远。长寿杯深，此际谁人劝。闷倚屏山凝泪眼。百和烟中，细想千娇面。'),
	(6293, 494, '鹊桥仙·七夕送陈令举', '缑山仙子，高情云渺，不学痴牛騃女。凤箫声断月明中，举手谢、时人欲去。 客槎曾犯，银河微浪，尚带天风海雨。相逢一醉是前缘，风雨散、飘然何处。'),
	(6294, 250, '木兰花', '初心已恨花期晚。别后相思长在眼。兰衾犹有旧时香，每到梦回珠泪满。 多应不信人肠断。几夜夜寒谁共暖。欲将恩爱结来生，只恐来生缘又短。'),
	(6295, 250, '木兰花·风帘向晓寒成阵', '风帘向晓寒成阵。来报东风消息近。试从梅蒂紫边寻，更绕柳枝柔处问。 来迟不是春无信。开晚却疑花有恨。又应添得几分愁，二十五弦弹未尽。'),
	(6296, 250, '鹧鸪天·梅蕊新妆桂叶眉', '梅蕊新妆桂叶眉。小莲风韵出瑶池。云随绿水歌声转，雪绕红绡舞袖垂。 伤别易，恨欢迟。惜无红锦为裁诗。行人莫便消魂去，汉渚星桥尚有期。'),
	(6297, 344, '双声子·晚天萧索', '晚天萧索，断蓬踪迹，乘兴兰棹东游。三吴风景，姑苏台榭，牢落暮霭初收。夫差旧国，香径没、徒有荒丘。繁华处，悄无睹，惟闻麋鹿呦呦。 想当年、空运筹决战，图王取霸无休。江山如画，云涛烟浪，翻输范蠡扁舟。验前经旧史，嗟漫载、当日风流。斜阳暮草茫茫，尽成万古遗愁。'),
	(6298, 400, '如梦令·一晌凝情无语', '一晌凝情无语，手捻梅花何处。 倚竹不胜愁，暗想江头归路。 东去东去，短艇淡烟疏雨。'),
	(6299, 254, '夹竹桃花·咏题', '诗绛彩娇春，苍筠静锁，掩映夭姿凝露。 花腮藏翠，高节穿花遮护。 重重蕊叶相怜，似青帔艳妆神仙侣。 正武陵溪暗，淇园晓色，宜望中烟雨。 暖景、谁见斜枝处。 喜上苑韶华渐布。 又似瑞霞低拥，却恐随风飞去。 要留最妍丽，须且闲凭佳句。 更秀容、分付徐熙，素屏画图取。'),
	(6300, 145, '紫萸香慢·近重阳', '近重阳偏多风雨，绝怜此日暄明。问秋香浓未，待携客、出西城。正自羁怀多感，怕荒台高处，更不胜情。向尊前，又忆漉酒插花人。只座上、已无老兵。 凄清，浅醉还醒，愁不肯，与诗评。记长楸走马，雕弓笮柳，前事休评。紫萸一枝传赐，梦谁到、汉家陵。尽乌纱便随风去，要天知道，华发如此星星，歌罢涕零。'),
	(6301, 422, '法曲献仙音·聚景亭梅次草窗韵', '层绿峨峨，纤琼皎皎，倒压波痕清浅。过眼年华，动人幽意，相逢几番春换。记唤酒寻芳处，盈盈褪妆晚。 已销黯。况凄凉、近来离思，应忘却、明月夜深归辇。荏苒一枝春，恨东风、人似天远。纵有残花，洒征衣、铅泪都满。但殷勤折取，自遣一襟幽怨。'),
	(6302, 235, '贺新郎·西湖', '一勺西湖水。渡江来、百年歌舞，百年酣醉。回首洛阳花世界，烟渺黍离之地。更不复、新亭堕泪。簇乐红妆摇画艇，问中流、击楫谁人是。千古恨，几时洗。(花世界 一作：花石尽） 余生自负澄清志。更有谁、翻溪未遇，传岩未起。国事如今谁倚仗，衣带一江而已。便都道、江神堪恃。借问孤山林处士，但掉头、笑指梅花蕊。天下事，可知矣。'),
	(6303, 78, '西江月·八十一年住世', '八十一年往事，四千里外无家。 如今流落向天涯。 梦到瑶池阙下。 玉殿五回命相，彤庭几度宣麻。 止因贪此恋荣华。 便有如今事也。'),
	(6304, 495, '调啸词（二之二）', '归雁。归雁。饮啄江南南岸。将飞却下盘桓。塞北春来苦寒。苦寒、苦寒、寒苦。藻荇欲生且住。'),
	(6305, 144, '楚天遥过清江引·有意送春归', '有意送春归，无计留春住。毕竟年年用着来，何似休归去。 目断楚天遥，不见春归路。风急桃花也似愁，点点飞红雨。'),
	(6306, 668, '临江山', '冬看山林萧疏净，春来地润花浓。少年衰老与山同。世间争名利，富贵与贫穷。 荣贵非干长生药，清闲是不死门风。劝君识取主人公。单方只一味，尽在不言中。'),
	(6307, 232, '千秋岁·半身屏外', '半身屏外。睡觉唇红退。春思乱，芳心碎。空馀簪髻玉，不见流苏带。试与问，今人秀整谁宜对。湘浦曾同会。手搴轻罗盖。疑是梦，今犹在。十分春易尽，一点情难改。多少事，却随恨远连云海。'),
	(6308, 124, '齐天乐·清溪数点芙蓉雨', '丁卯七月既望，余偕同志放舟邀凉于三汇之交，远修太白采石、坡仙赤壁数百年故事，游兴甚逸。余尝赋诗三百言以纪清适。坐客和篇交属，意殊快也。越明年秋，复寻前盟于白荷凉月间。风露浩然，毛发森爽，遂命苍头奴横小笛于舵尾，作悠扬杳渺之声，使人真有乘杳飞举想也。举白尽醉，继以浩歌。 清溪数点芙蓉雨，苹飙泛凉吟艗。洗玉空明，浮珠沆瀣，人静籁沉波息。仙潢咫尺。想翠宇琼楼，有人相忆。天上人间，未知今夕是何夕。 此生此夜此景，自仙翁去后，清致谁识？散发吟商，簪花弄水，谁伴凉宵横笛？流年暗惜。怕一夕西风，井梧吹碧。底事闲愁，醉歌浮大白。'),
	(6309, 112, '夜游宫·人去西楼雁杳', '人去西楼雁杳。叙别梦、扬州一觉。云澹星疏楚山晓。听啼乌，立河桥，话未了。 雨外蛩声早。细织就、霜丝多少。说与萧娘未知道。向长安，对秋灯，几人老。'),
	(6310, 112, '点绛唇·时霎清明', '时霎清明，载花不过西园路。嫩阴绿树。正是春留处。 燕子重来，往事东流去。征衫贮。旧寒一缕。泪湿风帘絮。'),
	(6311, 117, '如梦令', '闲向园林点检。又见小桃开遍。切莫便飘零，且为春光留恋。留恋。留恋。待我持杯深劝。'),
	(6312, 557, '鹊桥仙·富沙七夕为友人赋', '翠绡心事，红楼欢宴，深夜沈沈无暑。竹边荷外再相逢，又还是、浮云飞去。 锦笺尚湿，珠香未歇，空惹闲愁千缕。寻思不似鹊桥人，犹自得、一年一度。'),
	(6313, 49, '昭君怨·牡丹', '曾看洛阳旧谱。只许姚黄独步。若比广陵花。太亏他。 旧日王侯园圃。今日荆榛狐兔。君莫说中州。怕花愁。'),
	(6314, 370, '水调歌头', '寄语山阿子，何日出幽篁。兰衣蕙带，为我独立万寻冈。头上青天荡荡，足下白云霭，和气自悠扬。一阵东风至，灵雨过南塘。 招山鬼，吊河伯，俟东皇。朱宫紫阙、何事宛在水中央。长望龙辀雷驾，凭仗箫钟交鼓，宾日出扶桑。我乃援北斗，子亦射天狼。'),
	(6315, 569, '行香子', '春日迟迟。春景熙熙。渐郊原、芳草萋萋。夭桃灼灼，杨柳依依。见燕喃喃，蜂簇簇，蝶飞飞。 闲庭寂寂，曲沼漪漪。更秋千、红索垂垂。游人队队，乐意嬉嬉。尽醉醺醺，歌缓缓，语低低。'),
	(6316, 456, '破阵子', '小小红泥院宇，深深翠色屏帏。簇定熏炉酥酒软，门外东风寒不知，恰疑三月时 钗影半11绿子，歌声轻度红儿。醉里不愁更漏断，更要梅花看几枝。起来霜月低'),
	(6317, 591, '临江仙', '诸君亦不果来，岂牡丹留不住为可恨耶。因以来韵，为牡丹下一转语 只恐牡丹留不住，与春约束分明。未开微雨半开晴。要花开定准，又更与花盟。 魏紫朝来将进酒，玉盘盂样先圣。鞓红似向舞腰横。风流人不见，锦绣夜间行。'),
	(6318, 591, '昭君怨', '人面不如花面。花到开时重见。独倚小阑干。许多山。 落叶西风时候。人共青山都瘦。说道梦阳台。几曾来。'),
	(6319, 591, '丑奴儿（博山道中效李易安体）', '千峰云起，骤雨一霎时价。更远树斜阳，风景怎生图画。青旗卖酒，山那畔、别有人间，只消山水光中，无事过这一夏。 午醉醒时，松窗竹户，万千潇洒。野鸟飞来，又是一般闲暇。却怪白鸥，觑着人、欲下未下。旧盟都在，新来莫是，别有说话。'),
	(6320, 326, '忆秦娥（初春）', '新春早。春前十日春归了。春归了。落梅如雪，野桃红小。 老夫不管春催老。只图烂醉花前倒。花前倒。儿扶归去，醒来窗晓。'),
	(6321, 326, '归去来兮引', '侬家贫甚诉长饥。幼稚满庭闱。正坐瓶无储粟，漫求为吏东西。 偶然彭泽近邻圻。公秫滑流匙。葛巾劝我求为酒，黄菊怨、冷落东篱。五斗折腰，谁能许事，归去来兮。 老圃半榛茨。山田欲蒺藜。念心为形役又奚悲。独惆怅前迷。不谏后方追。觉今来是了，觉昨来非。 扁舟轻风破朝霏。风细漫吹衣。试问征夫前路，晨光小，恨熹微。乃瞻衡宇戴奔驰。迎候满荆扉。已荒三径存松菊，喜诸幼、入室相携。有酒盈尊，引触自酌，庭树遣颜怡。 容膝易安栖。南窗寄傲睨。更小园日涉趣尤奇。尽虽设柴门，长是闭斜晖。纵遐观矫首，短策扶持。 浮云出岫岂心思。鸟倦亦归飞。翳翳流光将入，孤松抚处凄其。息交绝友堑山溪。世与我相违。驾言复出何求者，旷千载、今欲从谁。亲戚笑谈，琴书触咏，莫遣俗人郑。告西畴有事要耘耔。容老子舟车，取意任委蛇。历崎岖窈窕，丘壑随宜。1111 欣欣花木向荣滋。泉水始流澌。万物得时如许，此生休笑吾衰。寓形字内几何时。岂问去留为。委心任运无多虑，顾皇皇、将欲何之。大化中间，乘流归尽，喜惧莫随伊。 富贵本危机。云乡不可期。趁良辰、孤往恣游嬉。独临水登山，舒啸更哦诗。除乐天知命，了复奚疑。'),
	(6322, 633, '鹧鸪天', '杖屦寻春苦未迟。洛城樱笋正当时。三千界外归初到，五百年前事总知。 吹玉笛，渡清伊。相逢休问姓名谁。小车处士深衣叟，曾是天津共赋诗。'),
	(6323, 633, '忆王孙', '一春常是雨和风。风雨晴时春已空。谁惜泥沙万点红。恨难穷。恰似衰翁一世中。'),
	(6324, 105, '望海潮', '侧寒斜雨，微灯薄雾，匆匆过了元宵。帘影护风，盆池见日，青青柳叶柔条。碧草皱裙腰。正昼长烟暖，蜂困莺娇。望处凄迷，半篙绿水浸斜桥。 孙郎病酒无聊。记乌丝醉语，碧玉风标。新燕又双，兰心渐吐，嘉期趁取花朝。心事转迢迢。但梦随人远，心与山遥。误了芳音，小窗斜日对芭蕉。'),
	(6325, 589, '蝶恋花·一朵江梅春带雪', '长道县和元彦修梅词。彦修，钱塘人，名时敏。坐张天觉党，自户部员外郎谪监长道之白石镇。 一朵江梅春带雪。玉软云娇，姑射肌肤洁。照影凌波微步怯。暗香浮动黄昏月。 谩道广平心似铁。词赋风流，不尽愁千结。望断江南音信绝。陇头行客空情切。'),
	(6326, 128, '卜算子·席上送王彦猷', '江北上归舟，再见江南岸。江北江南几度秋，梦里朱颜换。 人是岭头云，聚散天谁管。君似孤云何处归，我似离群雁。'),
	(6327, 255, '青玉案', '田园有计归须早。在家纵贫亦好。南来北去何日了。光阴送尽，可怜青鬓，暗逐流年老。 寂寥孤馆残灯照。乡思惊时梦初觉。落月苍苍关河晓。一声鸡唱，马嘶人起，又上长安道。'),
	(6328, 545, '如梦令', '门外落花流水。日暖杜鹃声碎。蕃马小屏风，一枕画堂春睡。如醉，如醉。正是困人天气。'),
	(6329, 130, '诉衷情·出林杏子落金盘', '出林杏子落金盘。齿软怕尝酸。可惜半残青紫，犹有小唇丹。 南陌上，落花闲。雨斑斑。不言不语，一段伤春，都在眉间。'),
	(6330, 130, '一落索·眉共春山争秀', '眉共春山争秀。可怜长皱。莫将清泪湿花枝，恐花也、如人瘦。 清润玉箫闲久。知音稀有，欲知日日倚阑愁，但问取、亭前柳。'),
	(6331, 249, '水龙吟·次歆林圣予惜春', '问春何苦匆匆，带风伴雨如驰骤。幽葩细萼，小园低槛，壅培未就。吹尽繁红，占春长久，不如垂柳。算春长不老，人愁春老，愁只是、人间有。 春恨十常八九，忍轻孤、芳醪经口。那知自是，桃花结子，不因春瘦。世上功名，老来风味，春归时候。纵樽前痛饮，狂歌似旧，情难依旧。'),
	(6332, 451, '钗头凤（别武昌）', '临丹壑。凭高阁。闲吹玉笛招黄鹤。空江暮。重回顾。一洲烟草，满川云树。住住住。 江风作。波涛恶。汀兰寂寞岸花落。长亭路。尘如雾。青山虽好，朱颜难驻。去去去。'),
	(6333, 494, '画堂春（寄子由）', '柳花飞处麦摇波。晚湖净鉴新磨。小舟飞棹去如梭。齐唱采菱歌。 平野水云溶漾，小楼风日晴和。济南何在暮云多。归去奈愁何。'),
	(6334, 250, '武陵春', '烟柳长堤知几曲，一曲一魂消。秋水无情天共遥。愁送木兰桡。 熏香绣被心情懒，期信转迢迢。记得来时倚画桥。红泪满鲛绡。'),
	(6335, 250, '浪淘沙', '小绿间长红。露蕊烟丛。花开花落昔年同。惟恨花前携手处，往事成空。 山远水重重。一笑难逢。已拚长在别离中。霜鬓知他从此去，几度春风。'),
	(6336, 250, '蝶恋花', '碧草池塘春又晚。小叶风娇，尚学娥妆浅。双燕来时还念远。珠帘绣户杨花满。 绿柱频移弦易断。细看秦筝，正似人情短。一曲啼乌心绪乱。红颜暗与流年换。'),
	(6337, 409, '南乡子（二之一）', '嗟见世间人。但有纤毫即是尘。不住旧时无相貌，沈沦。只为从来认识神。 作麽有疏亲。我自降魔转法轮。不是摄心除妄想，求真。幻化空身即法身。'),
	(6338, 323, '鹤冲天·清明天气', '清明天气。永日愁如醉。台榭绿阴浓。薰风细。燕子巢方就，盆池小，新荷蔽。恰是逍遥际。单夹衣裳，半栊软玉肌体。 石榴美艳，一撮红绡比。窗外数修篁，寒相倚。有个关心处，难相见、空凝睇。行坐深闺里。懒更妆梳，自知新来憔悴。'),
	(6339, 352, '浪淘沙·五岭麦秋残', '五岭麦秋残，荔子初丹。绛纱囊里水晶丸。可惜天教生处远，不近长安。 往事忆开元，妃子偏怜。一从魂散马嵬关，只有红尘无驿使，满眼骊山。'),
	(6340, 251, '蝶恋花', '南雁依稀回侧阵。雪霁墙阴，偏觉兰牙嫩。中夜梦余消酒困。炉香卷穗灯生晕。 急景流年都一瞬。往事前欢，未免萦方寸。腊后花期知渐近。寒梅已作东风信。'),
	(6341, 344, '倾杯·金风淡荡', '金风淡荡，渐秋光老、清宵永。小院新晴天气，轻烟乍敛，皓月当轩练净。对千里寒光，念幽期阻、当残景。早是多愁多病。那堪细把，旧约前欢重省。 最苦碧云信断，仙乡路杳，归雁难倩。每高歌、强遣离怀，奈惨咽、翻成心耿耿。漏残露冷。空赢得、悄悄无言，愁绪终难整。又是立尽，梧桐碎影。'),
	(6342, 344, '长寿乐·繁红嫩翠', '繁红嫩翠。艳阳景，妆点神州明媚。是处楼台，朱门院落，弦管新声腾沸。恣游人、无限驰骤，娇马车如水。竞寻芳选胜，归来向晚，起通衢近远，香尘细细。 太平世。少年时，忍把韶光轻弃。况有红妆，楚腰越艳，一笑千金何啻。向尊前、舞袖飘雪，歌响行云止。愿长绳、且把飞乌系。任好从容痛饮，谁能惜醉。'),
	(6343, 344, '临江仙引·渡口', '渡口、向晚，乘瘦马、陟平冈。西郊又送秋光。对暮山横翠，衫残叶飘黄。凭高念远，素景楚天，无处不凄凉。 香闺别来无信息，云愁雨恨难忘。指帝城归路，但烟水茫茫。凝情望断泪眼，尽日独立斜阳。'),
	(6344, 344, '满江红·暮雨初收', '暮雨初收，长川静、征帆夜落。临岛屿、蓼烟疏淡，苇风萧索。几许渔人飞短艇，尽载灯火归村落。遣行客、当此念回程，伤漂泊。 桐江好，烟漠漠。波似染，山如削。绕严陵滩畔，鹭飞鱼跃。游宦区区成底事，平生况有云泉约。归去来、一曲仲宣吟，从军乐。'),
	(6345, 344, '两同心（二之一·大石调）', '嫩脸修蛾，淡匀轻扫。最爱学、宫体梳妆，偏能做、文人谈笑。绮筵前。舞燕歌云，别有轻妙。 饮散玉炉烟袅。洞房悄悄。锦帐里、低语偏浓，银烛下、细看俱好。那人人，昨夜分明，许伊偕老。'),
	(6346, 28, '清平乐令·帘卷曲阑独倚', '帘卷曲阑独倚，江展暮天无际。泪眼不曾晴，家在吴头楚尾。 数点落红乱委，扑鹿沙鸥惊起。诗句欲成时，没入苍烟丛里。'),
	(6347, 163, '蓦山溪·自述', '壶山居士，未老心先懒。爱学道人家，办竹几、蒲团茗碗。青山可买，小结屋三间，开一径，俯清溪，修竹栽教满。 客来便请，随分家常饭。若肯小留连，更薄酒，三杯两盏，吟诗度曲，风月任招呼。身外事，不关心，自有天公管。'),
	(6348, 596, '疏影（笋薄之平江）', '瑶尊蘸翠。短长亭送别，风恋晴袂。腊树迎春，一路清寒，能消几日羁思。霜华不惜阳关柳，悄莫系、行人嘶骑。对梅花、一笑分携，胜约别来相寄。人物仙蓬妙韵，瑞鸾敛迅翼，聊憩香枳。见说使君，好语先传，付与芙蓉清致。客来欲问荆州事，但细语、岳阳楼记。梦故人，剪烛西窗，已隔洞庭烟水。'),
	(6349, 661, '菩萨蛮·举头忽见衡阳雁', '举头忽见衡阳雁。千声万字情何限。叵而薄情夫。一行书也无。 泣归香阁恨。和泪掩红粉。待雁却回时。也无书寄伊。'),
	(6350, 196, '柳梢青·灯花', '白玉枝头，忽看蓓蕾，金粟珠垂。半颗安榴，一枝秾杏，五色蔷薇。何须羯鼓声催。银釭里、春工四时。却笑灯蛾，学他蝴蝶，照影频飞。'),
	(6351, 204, '梅子黄时雨', '去宿江楼，爱留人夜语，频断灯炷。奈倦情如醉，黑甜清午。谩道迎薰何曾是，簟纺成浪衣成雨。茶瓯注。新期竹院，残梦连渚。 应误。重帘凄伫。记井刀剪翠，秋扇留句。信那回轻道，而今归否。十二曲阑随意凭，楚天不放斜阳暮。沈吟处。池草暗喧蛙鼓。'),
	(6352, 688, '酹江月·夜凉', '西风解事，为人间、洗尽三庚烦暑。一枕新凉宜客梦，飞入藕花深处。冰雪襟怀，琉璃世界，夜气清如许。刬然长啸，起来秋满庭户。 应笑楚客才高，兰成愁悴，遗恨传千古。作赋吟诗空自好，不直一杯秋露。淡月阑干，微云河汉，耿耿天催曙。此情谁会，梧桐叶上疏雨。'),
	(6353, 365, '八声甘州·摘青梅荐酒', '摘青梅荐酒，甚残寒，犹怯苎萝衣。正柳腴花瘦，绿云冉冉，红雪霏霏。隔屋秦筝依约，谁品春词?回首繁华梦，流水斜晖。 寄隐孤山山下，但一瓢饮水，深掩苔菲。羡青山有思，白鹤忘机。怅年华，不禁搔首，又在涯，弹泪送春归。销魂远，千山啼祢，十里荼种。'),
	(6354, 465, '浪淘沙·云藏鹅湖山', '台上凭栏干，犹怯春寒。被谁偷了最高山？将谓六丁移取去，不在人间。 却是晓云闲，特地遮拦。与天一样白漫漫。喜得东风收卷尽，依旧追还。'),
	(6355, 340, '括江神子', '狂胡鞍马自为家。遣宫娃。嫁胡沙。万里风烟，行不见京华。马上思归哀怨极，推却手，奏琵琶。 胡儿共听亦咨嗟。貌如花。落天涯。谁按新声，争向汉宫夸。纤手不知离别苦，肠欲断，恨如麻。 李白蜀道难：噫嘘嚱、危乎高哉，蜀道之难难于上青天！蚕丛及鱼凫，开国何茫然。尔来四万八千岁，不与秦塞通人烟。西当太白有鸟道，可以横绝峨眉巅。地崩山摧壮士死，然后天梯石栈相句连。上有横河断海之浮云，下有冲波逆折之回川。黄鹤之飞尚不能过，猿猱欲度愁攀缘。青泥何盘盘，百步九折萦岩峦，扪参历井仰胁息，以手拊膺坐长叹。问君西游何当还，畏涂巉岩不可攀。但见悲鸟号古木，雄飞呼雌上青天，使人听此凋朱颜。连峰去天不盈尺，枯松倒挂倚绝壁。飞湍瀑流争喧豗，砯崖转石万壑雷。其险也若此，嗟尔远道之人，胡为乎来哉！剑阁峥嵘而崔嵬，一夫当关，万夫莫开。所守或匪亲，化为狼与豺。朝避猛虎，夕避长蛇。磨牙吮血，杀人如麻。锦城虽云乐，不如早还家。蜀道之难难于上青天，侧身西望长咨嗟！'),
	(6356, 340, '括一丛花', '知章骑马似乘船。落井眼花圆。汝阳三斗朝天去，左丞相、鲸吸长川。潇酒宗之，皎如玉树，举盏望青天。 长齐苏晋爱逃禅、李白富诗篇。三杯草圣传张旭，更焦遂、五斗惊筵。一笑相逢，衔杯乐圣，同是饮中仙。 王逸少兰亭记：永和九年，岁在癸丑，暮春之初，会于会稽山阴之兰亭，修禊事也。群贤毕至，少长咸集。此地有崇山峻岭，茂林修竹，又有清流激湍，映带左右，引以为流觞曲水，列坐其次。虽无丝竹管弦之盛，一觞一咏，亦足以畅叙幽情。是日也，天朗气清，惠风和畅，仰观宇宙之大，俯察品类之盛。所以游目骋怀，足以极视听之娱，信可乐也。夫人之相与，俯仰一世。或取诸怀抱，晤言一室之内，或因寄所托，放浪形骸之外。虽取舍万殊，静躁不同，当其欣于所遇，暂得于已，快然自足，不知老之将至。及其所之既倦，情随事迁，感慨系之矣。向之所欣，俯仰之间，已为陈迹，犹不能不以之兴怀。况修短随化，终期于尽。古人云：死生亦大矣。岂不痛哉！每览昔人兴感之由，若合一契，未尝不临文嗟悼，不能喻之于怀。固知一死生为虚诞，齐彭觞为妄作。后之视今，亦犹今之视昔，悲夫！故列叙时人，录其所述，虽世殊事异，所以兴怀，其致一也。后之览者，亦将有感于斯文。'),
	(6357, 579, '婆罗门引·暮霞照水', '暮霞照水，水边无数木芙蓉。晓来露湿轻红。十里锦丝步障，日转影重重。向楚天空迥，人立西风。 夕阳道中。叹秋色、与愁浓。寂寞三千粉黛，临鉴妆慵。施朱太赤，空惆怅、教妾若为容。花易老、烟水无穷。'),
	(6358, 272, '鹧鸪天（叔怀尝梦飞仙，为之赋此。归日以呈茂献待郎，当发一笑）', '脱却儒冠着羽衣。青山绿水浩然归。看成鼎内真龙虎，管甚人间闲是非。 生羽翼，上烟霏。回头只见冢累累。未寻跨凤吹箫侣，且伴孤云独鹤飞。'),
	(6359, 272, '好事近', '春色欲来时，先散满天风雪。坐使七闽松竹，变珠幢玉节。中原佳气郁葱葱，河山壮宫阙。丞相功成千载，映黄流清澈。'),
	(6360, 168, '瑞鹧鸪（落梅）', '梁溪西畔小桥东。落叶纷纷水映空。五夜客愁花片里，一年春事角声中。歌残玉树人何在，舞破山香曲未终。却忆孤山醉归路，马蹄香雪衬东风。'),
	(6361, 101, '如梦令·野店几杯空酒', '野店几杯空酒。醉里两眉长皱。已自不成眠，那更酒醒时候。知否。知否。直是为他消瘦。'),
	(6362, 682, '武陵春', '小院无人帘半卷，独自倚阑时。宽尽春来金缕衣。憔悴有谁知。 玉人近日书来少，应是怨来迟。梦里长安早晚归。和泪立斜晖。'),
	(6363, 644, '踏莎行', '二社良辰，千家庭院。翩翩又见新来燕。凤凰巢稳许为邻，潇湘烟暝来何晚。乱入红楼，低飞绿岸。画梁时拂歌尘散。为谁归去为谁来，主人恩重珠帘卷。'),
	(6364, 114, '小重山·春愁', '谢了荼縻春事休。无多花片子，缀枝头。庭槐影碎被风揉。莺虽老，声尚带娇羞。 独自倚妆楼。一川烟草浪，衬云浮。不如归去下帘钩。心儿小，难着许多愁。'),
	(6365, 472, '卜算子', '送我出东门，乍别长安道。两岸垂杨锁暮烟，正是秋光老。 一曲古阳关，莫惜金尊倒。君向潇湘我向秦，鱼雁何时到。'),
	(6366, 687, '望江南', '思晴好，路滑少人行。早信雨能留得住，仅教尽日自舟横。直等到清明。'),
	(6367, 71, '忆秦娥', '梅花节。白头卧起餐毡雪。餐毡雪。上林雁断，上林书绝。 伤心最是河梁别。无人共拜天边月。天边月。一尊对影，一编残发。'),
	(6368, 112, '思佳客·赋半面女髑髅', '钗燕拢云睡起时。隔墙折得杏花枝。青春半面妆如画，细雨三更花又飞。 轻爱别，旧相知。断肠青冢几斜晖。断红一任风吹起，结习空时不点衣。'),
	(6369, 112, '玉楼春·和吴见山韵', '阑干独倚天涯客。心影暗凋风叶寂。千山秋入雨中青，一雁暮随云去急。 霜花强弄春颜色。相吊年光浇大白。海烟沈处倒残霞，一杼鲛绡和泪织。'),
	(6370, 112, '新雁过妆楼·中秋后一夕李方庵月庭延客命小妓过新水令坐间赋词', '阆苑高寒。金枢动、冰宫桂树年年。翦秋一半，难破万户连环。织锦相思楼影下，钿钗暗约小帘间。共无眠。素娥惯得，西坠阑干。 谁知壶中自乐，正醉围夜玉，浅斗婵娟。雁风自劲，云气不上凉天。红牙润沾素手，听一曲清歌双雾鬟。徐郎老，恨断肠声在，离镜孤鸾。'),
	(6371, 112, '秋蕊香·七夕', '懒浴新凉睡早。雪靥酒红微笑。倚楼起把绣针小。月冷波光梦觉。 怕闻井叶西风到。恨多少。粉河不语堕秋晓。云雨人间未了。'),
	(6372, 117, '江城子·示表侄刘国华', '家园十亩屋头边。正春妍，酿花天。杨柳多情，拂拂带轻烟。别馆闲亭随分有，时策杖，小盘旋。 采山钓水美而鲜。饮中仙，醉中禅。闲处光阴，赢得日高眠。一品高官人道好，多少事，碎心田。'),
	(6373, 117, '满江红·金陵乌衣园', '柳带榆钱，又还过、清明寒食。天一笑、满园罗绮，满城箫笛。花树得晴红欲染，远山过雨青如滴。问江南、池馆有谁来，江南客。 乌衣巷，今犹昔。乌衣事，今难觅。但年年燕子，晚烟斜日。抖擞一春尘土债，悲凉万古英雄迹。且芳尊、随分趁芳时，休虚掷。'),
	(6374, 49, '忆秦娥·春酲薄', '春酲薄，梦中毬马豪如昨。豪如昨。月明横笛，晓寒吹角。 古来成败难描摸，而今却悔当时错。当时错，铁衣犹在，不堪重著。'),
	(6375, 49, '朝天子', '宿雨频飘洒。欢喜西畴耕者。终朝连夜。有珠玑鸣瓦。 渐白水、青秧鸥鹭下。老学种花兼学稼。心两挂。这几树、海棠休也。'),
	(6376, 213, '疏帘淡月·寓桂枝香秋思', '梧桐雨细。渐滴作秋声，被风惊碎。润逼衣篝，线袅蕙炉沈水。悠悠岁月天涯醉。一分秋、一分憔悴。紫箫吟断，素笺恨切，夜寒鸿起。 又何苦、凄凉客里。负草堂春绿，竹溪空翠。落叶西风，吹老几番尘世。从前谙尽江湖味。听商歌、归兴千里。露侵宿酒，疏帘淡月，照人无寐。'),
	(6377, 639, '好事近·咏梅', '的皪两三枝，点破暮烟苍碧。好在屋檐斜入，傍玉奴横笛。 月华如水过林塘，花阴弄苔石。欲向梦中飞蝶，恐幽香难觅。'),
	(6378, 639, '贺新郎·怀辛幼安用前韵', '话杀浑闲说。不成教、齐民也解，为伊为葛。樽酒相逢成二老，却忆去年风雪。新著了、几茎华发。百世寻人犹接踵，叹只今两地三人月。写旧恨，向谁瑟。 男儿何用伤离别。况古来、几番际会，风从云合。千里情亲长晤对，妙体本心次骨。卧百尺、高楼斗绝。天下适安耕且老，看买犁卖剑平家铁。壮士泪，肺肝裂。'),
	(6379, 569, '江南好', '天共水，水远与天连。天净水平寒月漾，水光月色两相兼。月映水中天。 人与景，人景古难全。景若佳时心自快，心还乐处景应妍。休与俗人言。'),
	(6380, 456, '水龙吟·夜来风雨匆匆', '夜来风雨匆匆，故园定是花无几。愁多怨极，等闲孤负，一年芳意。柳困桃慵，杏青梅小，对人容易。算好春长在，好花长见，原只是、人憔悴。 回首池南旧事，恨星星、不堪重记。如今但有，看花老眼，伤时清泪。不怕逢花瘦，只愁怕、老来风味。待繁红乱处，留云借月，也须拚醉。'),
	(6381, 591, '武陵春·走去走来三百里', '走去走来三百里，五日以为期。六日归时已是疑。应是望多时。 鞭个马儿归去也，心急马行迟。不免相烦喜鹊儿。先报那人知。'),
	(6382, 591, '兰陵王', '　　己未八月二十日夜，梦有人以石研屏见饷者。其色如玉，光润可爱。中有一牛，磨角作斗状。云：“湘潭里中有张其姓者，多力善斗，号张难敌。一日，与人搏，偶败，忿赴河而死。居三日，其家人来视之，浮水上，则牛耳。自后并水之山往往有此石，或得之，里中辄不利。”梦中异之，为作诗数百言，大抵皆取古之怨愤变化异物等事，觉而忘其言。后三日，赋词以识其异。 　　恨之极。恨极销磨不得。苌弘事，人道後来，其血三年化为碧。郑人缓也泣。吾父攻儒助墨。十年梦，沈痛化余，秋柏之间既为实。 　　相思重相忆。被怨结中肠，潜动精魄。望夫江上岩岩立。嗟一念中变，後期长绝。君看启母愤所激。又俄倾为石。难敌。最多力。甚一忿沈渊，精气为物。依然困斗牛磨角。便影入山骨，至今雕琢。寻思人间，只合化，梦中蝶。'),
	(6383, 591, '生查子·独游西岩', '青山招不来，偃蹇谁怜汝？岁晚太寒生，唤我溪边住。 山头明月来，本在天高处。夜夜入青溪，听读《离骚》去。'),
	(6384, 591, '卜算子（荷花）', '红粉靓梳妆，翠盖低风雨。占断人间六月凉，期月鸳鸯浦。 根底藕丝长，花里莲心苦。只为风流有许愁，更衬佳人步。'),
	(6385, 591, '鹧鸪天·送元济之归豫章', '敧枕婆娑两鬓霜。起听檐溜碎喧江。那边云筋销啼粉，这里车轮转别肠。 诗酒社，水云乡。可堪醉墨几淋浪。画图恰似归家梦，千里河山寸许长。'),
	(6386, 435, '八声甘州·读诸葛武侯传', '过隆中、桑柘倚斜阳，禾黍战悲风。世若无徐庶，更无庞统，沈了英雄。本计东荆西益，观变取奇功。转尽青天粟，无路能通。 他日杂耕渭上，忽一星飞堕，万事成空。使一曹三马，云雨动蛟龙。看璀璨、出师一表，照乾坤、牛斗气常冲。千年后，锦城相吊，遇草堂翁。'),
	(6387, 498, '鹧鸪天·嫩绿重重看得成', '嫩绿重重看得成。曲阑幽槛小红英。酴醿架上蜂儿闹，杨柳行间燕子轻。 春婉娩，客飘零。残花浅酒片时清。一杯且贾明朝事，送了斜阳月又生。'),
	(6388, 498, '满江红', '罨画溪山，行欲遍、风蒲还举。天渐远、水云初静，柁楼人语。月色波光看不定，玉虹横卧金鳞舞。算五湖、今夜只扁舟，追千古。 怀往事，渔樵侣。曾共醉，松江渚。算今年依旧，一杯沧浦。宇宙此身元是客，不须怅望家何许。但中秋、时节好溪山，皆吾土。'),
	(6389, 498, '满江红（冬至）', '寒谷春生，熏叶气、玉筒吹谷。新阳后、便占新岁，吉云清穆。休把心情关药裹，但逢节序添诗轴。笑强颜、风物岂非痴，终非俗。 清昼永，佳眠熟。门外事，何时足。且团栾同社，笑歌相属。著意调停云露酿，从头检举梅花曲。纵不能、将醉作生涯，休拘束。'),
	(6390, 633, '长相思（五之四）', '暮山青。暮霞明。梦笔桥头艇子横。苹风吹酒醒。 看潮生。看潮平。小住西陵莫较程。莼丝初可烹。'),
	(6391, 633, '月上海棠·斜阳废苑朱门闭', '成都城南有蜀王旧苑，尤多梅，皆二百余年古木 斜阳废苑朱门闭。吊兴亡、遣恨泪痕里。淡淡宫梅，也依然、点酥剪水。凝愁处，似忆宣华旧事。 行人别有凄凉意。折幽香、谁与寄千里。伫立江皋，杳难逢、陇头归骑。音尘远，楚天危楼独倚。宣华，故蜀苑名。'),
	(6392, 633, '蝶恋花', '水漾萍根风卷絮。倩笑娇颦，忍记逢迎处。只有梦魂能再遇。堪嗟梦不由人做。 梦若由人何处去。短帽轻衫，夜夜眉州路。不怕银缸深绣户。只愁风断青衣渡。'),
	(6393, 633, '朝中措·代谭德称作', '怕歌愁舞懒逢迎。妆晚托春酲。总是向人深处，当时枉道无情。 关心近日，啼红密诉，剪绿深盟。杏馆花阴恨浅，画堂银烛嫌明。'),
	(6394, 633, '满江红', '危堞朱栏，登览处、一江秋色。人正似、征鸿社燕，几番轻别。缱绻难忘当日语，凄凉又作它乡客。问鬓边、都有几多丝，真堪织。 杨柳院，秋千陌。无限事，成虚掷。如今何处也，梦魂难觅。金鸭微温香缥渺，锦茵初展情萧瑟。料也应、红泪伴秋霖，灯前滴。'),
	(6395, 192, '诉衷情', '闲中一弄七弦琴。此曲少知音。多因淡然无味，不比郑声淫。 松院静，竹林深。夜沈沈。清风拂轸，明月当轩，谁会幽心。'),
	(6396, 271, '生查子', '不载，今收入此 年年玉镜台，梅蕊宫妆困。今岁未还家，怕见江南信。 酒从别后疏，泪向愁中尽。遥想楚云深，人远天涯近。'),
	(6397, 482, '好事近·富贵本无心', '富贵本无心，何事故乡轻别。空惹猿惊鹤怨，误松萝风月。 囊锥刚强出头来，不道甚时节。欲命巾车归去，恐豺狼当辙。'),
	(6398, 185, '青玉案', '月华冷沁花梢露。芳意恋、香肌住。心字龙涎饶济楚。素馨风味，碎琼流品，别有天然处。 围炉屈曲宜深炷。留取春光向朱户。绿绮声中谁暗许。小窗归去，梦回犹记，金鼎分云缕。'),
	(6399, 185, '醉花阴', '翠箔阴阴笼画阁。昨夜东风恶。芳径满香泥，南陌东郊，惆怅妨行乐。 伤春比似年时恶。潘鬓新来薄。何处不禁愁，雨滴花腮，和泪胭脂落。'),
	(6400, 635, '虞美人·扁舟三日秋塘路', '自琐闼以病得请奉祠。卜居青墩。立秋后三日行，舟之前后，如朝霞相映，望之不断也。以长短句记之 扁舟三日秋塘路。平度荷花去。病夫因病得来游。更值满川微雨、洗新秋。 去年长恨拿舟晚。空见残荷满。今年何以报君恩。一路繁花相送、过青墩。'),
	(6401, 402, '水调歌头·呈汉阳使君', '大别我知友，突兀起西州。十年重见，依旧秀色照清眸。常记鲒狂客，邀我登楼雪霁，杖策拥羊裘。山吐月千仞，残夜水明楼。黄粱梦，未觉枕，几经秋。与君邂逅，相逐飞步碧山头。举酒一觞今古，叹息英雄骨冷，清泪不能收。鹦鹉更谁赋，遗恨满芳州。'),
	(6402, 296, '菩萨蛮·江城烽火连三月', '江城烽火连三月。不堪对酒长亭别。休作断肠声。老来无泪倾。 风高帆影疾。目送舟痕碧。锦字几时来。薰风无雁回。'),
	(6403, 518, '一剪梅', '高宴华堂夜向阑。急管飞霜，羯鼓声乾。仙人掌上水晶盘。回按凌波，舞袖弓弯。 曲罢凝娇整翠鬟。玉笋持杯，巧笑嫣然。为君一醉倒金船。只恐醒来，人隔云山。'),
	(6404, 385, '江梅引·忆江梅', '岁在壬戌，甫临长至，张总侍御邀饮。众宾皆退，独留少款。侍婢歌江梅引，有“念此情、家万里”之句，仆曰：此词殆为我作也。又闻本朝使命将至，感慨久之。既归，不寝，追和四章，多用古人诗赋，各有一笑字，聊以自宽。如暗香、疏影、相思等语，虽甚奇，经前人用者众，嫌其一律，故辄略之。卒押吹字，非风即笛，不可易也。此方无梅花，士人罕有知梅事者，故皆注所出（旧注：阙一首。此录示乡人者，北人谓之四笑江梅引） 天涯除馆忆江梅。几枝开。使南来。还带馀杭、春信到燕台。准拟寒英聊慰远，隔山水，应销落，赴诉谁。 空恁遐想笑摘蕊。断回肠，思故里。漫弹绿绮。引三弄、不觉魂飞。更听胡笳、哀怨泪沾衣。乱插繁花须异日，待孤讽，怕东风，一夜吹。'),
	(6405, 104, '浪淘沙', '柳塘新涨。艇子操双桨。闲倚曲楼成怅望。是处春愁一样。 傍人几点飞花。夕阳又送栖鸦。试问画楼西畔，暮云恐近天涯。'),
	(6406, 104, '采桑子', '乱红夭绿风吹尽，小市疏楼。细雨轻鸥。总向离人恨里收。 年年春好年年病，妾自西游。水自东流。不似残花一样愁。'),
	(6407, 94, '念奴娇', '云峰横起，障吴关三面，真成尤物。倒卷回潮目尽处，秋水黏天无壁。绿鬓人归，如今虽在，空有千茎雪。追寻如梦，漫余诗句犹杰。 闻道尊酒登临，孙郎终古恨，长歌时发。万里云屯瓜步晚，落日旌旗明灭。鼓吹风高，画船遥想，一笑吞穷发。当时曾照，更谁重问山月。'),
	(6408, 94, '贺新郎', '睡起流莺语。掩青苔、房栊向晚，乱红无数。吹尽残花无人见，惟有垂杨自舞。渐暖霭、初回轻暑。宝扇重寻明月影，暗尘侵、尚有乘鸾女。惊旧恨，遽如许。 江南梦断横江渚。浪黏天、葡萄涨绿，半空烟雨。无限楼前沧波意，谁采萍花寄取。但怅望、兰舟容与。万里云帆何时到，送孤鸿、目断千山阻。谁为我，唱金缕。'),
	(6409, 360, '踏莎行·元夕', '拨雪寻春，烧灯续昼。暗香院落梅开后。无端夜色欲遮春，天教月上官桥柳。 花市无尘，朱门如绣。娇云瑞雾笼星斗。沈香火冷小妆残，半衾轻梦浓如酒。'),
	(6410, 249, '失调名', '残腊初雪霁。梅白飘香蕊。依前又还是，迎春时候，大家都备。宠马门神，酒酌酴酥，桃符尽书吉利。 五更催驱傩，爆竹起。虚耗都教退。交年换新岁。长保身荣贵。愿与儿孙、尽老今生，神寿遐昌，年年共同守岁。'),
	(6411, 549, '愁风月', '风清月正圆，信是佳时节。不会长年来，处处愁风月。 心将熏麝焦，吟伴寒虫切。欲遽就床眠，解带翻成结。'),
	(6412, 554, '乌夜啼·春思', '楼上萦帘弱絮，墙头碍月低花。年年春事关心事，肠断欲栖鸦。 舞镜鸾衾翠减，啼珠凤蜡红斜。重门不锁相思梦，随意绕天涯。'),
	(6413, 451, '蝶恋花', '晓日窥轩双燕语。似与佳人，共惜春将暮。屈指艳阳都几许。可无时霎闲风雨。 流水落花无问处。只有飞云，冉冉来还去。持酒劝云云且住。凭君碍断春归路。'),
	(6414, 693, '少年心', '对景惹起愁闷。染相思、病成方寸。是阿谁先有意，阿谁薄幸。斗顿恁、少喜多嗔。 合下休传音问。你有我、我无你分。似合欢桃核，真堪人恨。心儿里、有两个人人。'),
	(6415, 693, '南乡子', '黄菊满东篱。与客携壶上翠微。已是有花兼有酒，良期。不用登临恨落晖。 满酌不须辞。莫待无花空折枝。寂寞酒醒人散后，堪悲。节去蜂愁蝶不知。'),
	(6416, 494, '西江月', '曲肱醉卧少休。及觉，已晓。乱山葱茏，流水锵然，疑非尘世也。书此语桥柱上。 照野弥弥浅浪，横空暧暧微霄。障泥未解玉骢骄。我欲醉眠芳草。 可惜一溪明月，莫教踏破琼瑶。解鞍欹枕绿杨桥。杜宇一声春晓。'),
	(6417, 250, '清商怨·庭花香信尚浅', '庭花香信尚浅。最玉楼先暖。梦觉春衾，江南依旧远。 回纹锦字暗剪。谩寄与、也应归晚。要问相思，天涯犹自短。'),
	(6418, 250, '更漏子·出墙花', '出墙花，当路柳。借问芳心谁有。红解笑，绿能颦。千般恼乱春。 北来人，南去客。朝暮等闲攀折。怜晚芳，惜残阳。情知枉断肠。'),
	(6419, 250, '浣溪沙·绿柳藏乌静掩关', '绿柳藏乌静掩关。鸭炉香细琐窗闲。那回分袂月初残。 惜别漫成良夜醉，解愁时有翠笺还。欲寻双叶寄情难。'),
	(6420, 250, '鹧鸪天', '陌上濛濛残絮飞。杜鹃花里杜鹃啼。年年底事不归去，怨月愁烟长为谁。 梅雨细，晓风微。倚楼人听欲沾衣。故园三度群花谢，曼倩天涯犹未归。'),
	(6421, 409, '甘露歌', '天寒日暮山谷里。的砾愁成水。池上渐多枝上稀。唯有故人知。'),
	(6422, 409, '清平乐', '云垂平野。掩映竹篱茅舍。阒寂幽居实潇洒。是处绿娇红冶。 丈夫运用堂堂。且莫五角六张。若有一卮芳酒，逍遥自在无妨。'),
	(6423, 352, '少年游', '绿云双亸插金翘。年纪正妖饶。汉妃束素，小蛮垂柳，都占洛城腰。 锦屏春过衣初减，香雪暖凝消。试问当筵眼波恨，滴滴为谁娇。'),
	(6424, 352, '浣溪沙', '红粉佳人白玉杯。木兰船稳棹歌催。绿荷风里笑声来。 细雨轻烟笼草树。斜桥曲水绕楼台。夕阳高处画屏开。'),
	(6425, 352, '玉楼春', '雪云乍变春云簇。渐觉年华堪送目。北枝梅蕊犯寒开，南浦波纹如酒绿。 芳菲次第还相续。不奈情多无处足。尊前百计得春归，莫为伤春歌黛蹙。'),
	(6426, 251, '踏莎行·细草愁烟', '细草愁烟，幽花怯露。凭阑总是销魂处。日高深院静无人，时时海燕双飞去。 带缓罗衣，香残蕙炷。天长不禁迢迢路。垂杨只解惹春风，何曾系得行人住。'),
	(6427, 186, '行香子（般涉调）', '舞雪歌云。闲淡妆匀。蓝溪水、深染轻裙。酒香醺脸，粉色生春。更巧谈话，美情性，好精神。 江空无畔，凌波何处，月桥边、青柳朱门。断钟残角，又送黄昏。奈心中事，眼中泪，意中人。'),
	(6428, 344, '小镇西（仙吕调）', '意中有个人，芳颜二八。天然俏、自来奸黠。最奇绝。是笑时、媚靥深深，百态千娇，再三偎著，再三香滑。 久离缺。夜来魂梦里，尤花殢雪。分明似旧家时节。正欢悦。被邻鸡唤起，一场寂寥，无眠向晓，空有半窗残月。'),
	(6429, 344, '婆罗门令·昨宵里恁和衣睡', '昨宵里恁和衣睡，今宵里又恁和衣睡。小饮归来，初更过，醺醺醉。中夜后、何事还惊起？霜天冷，风细细，触疏窗、闪闪灯摇曳。 空床展转重追想，云雨梦、任攲枕难继。寸心万绪，咫尺千里。好景良天，彼此，空有相怜意，未有相怜计。'),
	(6430, 296, '十样花·陌上风光浓处', '陌上风光浓处。第一寒梅先吐。待得春来也，香消减，态凝伫。百花休漫妒。'),
	(6431, 172, '满江红·小院深深', '小院深深，悄镇日、阴晴无据。春未足，闺愁难寄，琴心谁与？曲径穿花寻蛱蝶，虚阑傍日教鹦鹉。笑十三杨柳女儿腰，东风舞。 云外月，风前絮。情与恨，长如许。想绮窗今夜，为谁凝伫？洛浦梦回留佩客，秦楼声断吹箫侣。正黄昏时候杏花寒，廉纤雨。'),
	(6432, 3, '华胥引', '论交眉语，惜别心啼，费情不少。蕙渺溱期，苹深汜经轻误了。几度金铸相思，又燕紧鸿杳。谁料如今，被莺闲占春早。 频把愁勾，惜鸦云、娇红犹绕。浑拚如梦，争奈枕酲屏晓。欲寄芙蓉香半握，怕不禁秋恼。重是亲逢，片帆双度天杪。'),
	(6433, 673, '浪淘沙·莫上玉楼看', '莫上玉楼看。花雨斑斑。四垂罗幕护朝寒。燕子不知人去也，飞认阑干。 回首几关山。后会应难。相逢祗有梦魂间，可奈梦随春漏短，不到江南。'),
	(6434, 445, '蝶恋花·两岸月桥花半吐', '两岸月桥花半吐。红透肌香，暗把游人误。尽道武陵溪上路。不知迷入江南去。 先自冰霜真态度。何事枝头，点点胭脂污。莫是东君嫌淡素。问花花又娇无语。'),
	(6435, 160, '武陵春', '七返阳全阴去尽，精髓满三田。修道无为必自然。论甚后和先。忍取五行真水火，须要认根源。自己丹砂着意看。何用外寻丹。'),
	(6436, 12, '鹊桥仙', '碧梧初出，桂花才吐，池上水花微谢。穿针人在合欢楼，正月露、玉盘高泻。 蛛忙鹊懒，耕慵织倦，空做古今佳话。人间刚道隔年期，指天上、方才隔夜。'),
	(6437, 540, '霜天晓角·桂花', '绿云剪叶，低护黄金屑。占断花中声誉，香与韵、两清洁。 胜绝，君听说。是他来处别。试看仙衣犹带，金庭露、玉阶月。'),
	(6438, 17, '醉花阴', '轻红蔓引丝多少。剪青兰叶巧。人向月中归，留下星钿，弹破真珠小。 等闲不管春知道。多著绣帘围绕。只恐被东风，偷得馀香，分付闲花草。'),
	(6439, 690, '青玉案·和贺方回韵送山谷弟贬宜州', '千峰百嶂宜州路。天黯淡、知人去。晓别吾家黄叔度。弟兄华发，远山修水，异日同归处。 樽罍饮散长亭暮。别语缠绵不成句。已断离肠能几许。水村山馆，夜阑无寐，听尽空阶雨。'),
	(6440, 682, '好事近·雨后晓寒轻', '雨后晓寒轻，花外早莺啼歇。 愁听隔溪残漏，正一声凄咽。 不堪西望去程赊，离肠万回结。 不似海棠阴下，按《凉州》时节。'),
	(6441, 342, '霜天晓角', '冰清霜洁。昨夜梅花发。甚处玉龙三弄，声摇动、枝头月。 梦绝。金兽爇。晓寒兰烬灭。要卷珠帘清赏，且莫扫、阶前雪。'),
	(6442, 624, '玉楼春', '锦箨参差朱栏曲。露濯文犀和粉绿。未容浓翠伴桃红，几许纤枝留凤宿。嫩似春荑明似玉。一寸芳心谁管束。劝君速吃莫踌躇，看被南风吹作竹。'),
	(6443, 167, '甘草子', '春早。柳丝无力，低拂青门道。暖日笼啼鸟。初坼桃花小。遥望碧天净如扫。曳一缕、轻烟缥缈。堪惜流年谢芳草。任玉壶倾倒。'),
	(6444, 321, '忆王孙（秋词）', '飕飕风冷荻花秋。明月斜侵独倚楼。十二珠帘不上钩。黯凝眸。一点渔灯古渡头。'),
	(6445, 177, '烛影摇红·题安陆浮云楼', '霭霭春空，画楼森耸凌云渚。紫薇登览最关情，绝妙夸能赋。惆怅相思迟暮。记当日、朱阑共语。塞鸿难问，岸柳何穷，别愁纷絮。 催促年光，旧来流水知何处。断肠何必更残阳，极目伤平楚。晚霁波声带雨。悄无人、舟横野渡。数峰江上，芳草天涯，参差烟树。'),
	(6446, 294, '虞美人·玉阑干外清江浦', '玉阑干外清江浦，渺渺天涯雨。好风如扇雨如帘，时见岸花汀草、涨痕添。(阑：栏) 青林枕上关山路，卧想乘鸾处。碧芜千里思悠悠，惟有霎时凉梦、到南州。'),
	(6447, 202, '水龙吟·白莲', '仙人掌上芙蓉，涓涓犹滴金盘露。轻装照水，纤裳玉立，飘飘似舞。几度销凝，满湖烟月，一汀鸥鹭。记小舟夜悄，波明香远，浑不见、花开处。 应是浣纱人妒。褪红衣、被谁轻误？闲情淡雅，冶姿清润，凭娇待语。隔浦相逢，偶然倾盖，似传心素。怕湘皋佩解，绿云十里，卷西风去。'),
	(6448, 515, '贺新郎（兵后寓吴）', '深阁帘垂绣。记家人、软语灯边，笑涡红透。万叠城头哀怨角，吹落霜花满袖。影厮伴、东奔西走。望断乡关知何处，羡寒鸦、到著黄昏后。一点点，归杨柳。 相看只有山如旧。叹浮云、本是无心，也成苍狗。明日枯荷包冷饭，又过前头小阜。趁未发、且尝村酒。醉探枵囊毛锥在，问邻翁、要写牛经否。翁不应，但摇手。'),
	(6449, 422, '高阳台·残萼梅酸', '残萼梅酸，新沟水绿，初晴节序暄妍。独立雕栏，谁怜枉度华年。朝朝准拟清明近，料燕翎、须寄银笺。又争知、一字相思，不到吟边。 双蛾不拂青鸾冷，任花阴寂寂，掩户闲眠。屡卜佳期，无凭却恨金钱。何人寄与天涯信，趁东风、急整归船。纵飘零，满院杨花，犹是春前。'),
	(6450, 124, '绣鸾凤花犯·赋水仙', '楚江湄，湘娥乍见，无言洒清泪。淡然春意。空独倚东风，芳思谁寄。凌波路冷秋无际。香云随步起。谩记得，汉宫仙掌，亭亭明月底。 冰弦写怨更多情，骚人恨，枉赋芳兰幽芷。春思远，谁叹赏、国香风味。相将共、岁寒伴侣。小窗净、沈烟熏翠袂。幽梦觉，涓涓清露，一枝灯影里。'),
	(6451, 112, '烛影摇红·元夕雨', '碧澹山姿，暮寒愁沁歌眉浅。障泥南陌润轻酥，灯火深深院。入夜笙歌渐暖。彩旗翻、宜男舞遍。恣游不怕，素袜尘生，行裙红溅。 银烛笼纱，翠屏不照残梅怨。洗妆清靥湿春风，宜带啼痕看。楚梦留情未散。素娥愁、天深信远。晓窗移枕，酒困香残，春阴帘卷。'),
	(6452, 112, '永遇乐·乙巳中秋风雨', '风拂尘徽，雨侵凉榻，才动秋思。缓酒销更，移灯傍影，净洗芭蕉耳。铜华沧海，愁霾重嶂，燕北雁南天外。算阴晴，浑似几番，渭城故人离会。 青楼旧日，高歌取醉，唤出玉人梳洗。红叶流光，苹花两鬓，心事成秋水。白凝虚晓，香吹轻烬，倚窗小瓶疏桂。问深宫，姮娥正在，妒云第几。'),
	(6453, 112, '诉衷情·七夕', '西风吹鹤到人间。凉月满缑山。银河万里秋浪，重载客槎还。 河汉女，巧云鬟。夜阑干。钗头新约，针眼娇颦，楼上秋寒。'),
	(6454, 112, '瑞龙吟·德清清明竞渡', '大溪面。遥望绣羽冲烟，锦梭飞练。桃花三十六陂，鲛宫睡起，娇雷乍转。 去如箭。催趁戏旗游鼓，素澜雪溅。东风冷湿蛟腥，澹阴送昼。轻霏弄晚。 洲上青苹生处，斗春不管，怀沙人远。残日半开一川，花影零乱。山屏醉缬，连棹东西岸。阑干倒、千红妆靥，铅香不断。傍暝疏帘卷。翠涟皱净，笙歌未散。簪柳门归懒。犹自有、玉龙黄昏吹怨。重云暗阁，春霖一片。'),
	(6455, 241, '如梦令（春思）', '知是谁家燕子。直恁惺松言语。深入绣帘来，无奈落花飞絮。春去。春去。且道干卿何事。'),
	(6456, 117, '满江红·齐山绣春台', '十二年前，曾上到、绣春台顶。双脚健、不烦筇杖，透岩穿岭。老去渐消狂气习，重来依旧佳风景。想牧之、千载尚神游，空山冷。 山之下，江流永。江之外，淮山暝。望中原何处，虎狼犹梗。句蠡规模非浅近，石苻事业真俄顷。问古今、宇宙竟如何，无人省。'),
	(6457, 49, '满江红·和王实之韵送郑伯昌', '怪雨盲风，留不住江边行色。烦问讯、冥鸿高士，钓鳌词客。千百年传吾辈语，二三子系斯文脉。听王郎一曲玉箫声，凄金石。 晞发处，怡山碧；垂钓处，沧溟白。笑而今拙宦，他年遗直。只愿常留相见面，未宜轻屈平生膝。有狂谈欲吐且休休，惊邻壁。'),
	(6458, 91, '鹧鸪天·搭柳栏干倚伫频', '搭柳栏干倚伫频，杏帘胡蝶绣床春。十年花骨东风泪，几点螺香素壁尘。 箫外月，梦中云，秦楼楚殿可怜身。新愁换尽风流性，偏恨鸳鸯不念人。'),
	(6459, 91, '夜行船·正月十八日闻卖杏花有感', '不剪春衫愁意态。过收灯、有些寒在。小雨空帘，无人深巷，已早杏花先卖。 白发潘郎宽沈带。怕看山、忆他眉黛。草色拖裙，烟光惹鬓，常记故园挑菜。'),
	(6460, 446, '南乡子', '梅雪弄芳馨。饯日迎辰契月蓂。争着琴堂称贺处，娉婷。满捧霞觞璨鲤庭。 鹤等更龟龄。八十仙翁醉复醒。好倩洛阳驰誉手，丹青。乞与人间作寿星。'),
	(6461, 446, '江城子', '青青杨柳水边桥。水迢迢。柳摇摇。缓引离觞，频驻木兰桡。我是行人君是客，俱有恨，总无聊。 冰澌波暖数琼瑶。舞晴飚。拂春潮。一片别魂，销尽遣谁招。不似严阳山上雪，魂易尽，雪难销。'),
	(6462, 446, '浪淘沙·好恨这风儿', '好恨这风儿，催俺分离！船儿吹得去如飞，因甚眉儿吹不展？叵耐风儿！ 不是这船儿，载起相思？船儿若念我孤恓？载取人人篷底睡，感谢风儿！'),
	(6463, 591, '好事近（西湖）', '日日过西湖，冷浸一天寒玉。山色虽言如画，想画时难邈。 前弦后管夹歌钟，才断又重续。相次藕花开也，几兰舟飞逐。'),
	(6464, 591, '酒泉子·无题', '流水无情，潮到空城头尽白。离歌一曲怨残阳，断人肠。 东风官柳舞雕墙。三十六宫花溅泪，春声何处说兴亡，燕双双。'),
	(6465, 591, '江神子·赋梅寄余叔良', '暗香横路雪垂垂。晚风吹。晓风吹。花意争春，先出岁寒枝。毕竟一年春事了，缘太早，却成迟。 未应全是雪霜姿。欲开时。未开时。粉面朱唇，一半点胭脂。醉里谤花花莫恨，浑冷淡，有谁知。'),
	(6466, 591, '水龙吟', '词，为赋水龙吟。芗林侍郎向公告老所居，高宗皇帝御书所赐名也，与盘园相并云 断崖千丈孤松，挂冠更在松高处。平生袖手，故应休矣，功名良苦。笑指儿曹，人间醉梦，莫嗔惊妆。问黄金余几，旁人欲说，田园计、君推去。 叹息芗林旧隐，对先生、竹窗松户。一花一草，一觞一咏，风流杖屦。野马尘埃，扶摇下视，苍然如许。恨当年、九老图中，忘却画、盘园路。'),
	(6467, 591, '满江红（送徐换斡衡仲之官三山，时马叔会侍郎帅闽辛弃疾）', '绝代佳人，曾一笑、倾城倾国。休更叹、旧时清镜，而今华发。明日伏波堂上客，老当益壮翁应说。恨苦遭、邓禹笑人来，长寂寂。 诗酒社，江山笔。松菊径，云烟屐。怕一觞一咏，风流弦绝。我梦横江孤鹤去，觉来却与君相别。记功名、万里要吾身，佳眠食。'),
	(6468, 587, '武陵春', '落了丹枫残了菊，秋色苦无多。谁唤西风泣泪罗，吹恨入星河。 碧枝头金粟闹，曾乖翠云窝。重柔檀英忆两娥。无奈冷香何。'),
	(6469, 587, '潇湘夜雨·灯词', '斜点银釭，高擎莲炬，夜深不耐微风。重重帘幕卷堂中。香渐远、长烟袅穟，光不定、寒影摇红。偏奇处、当庭月暗，吐焰为虹。 红裳呈艳，丽娥一见，无奈狂踪。试烦他纤手，卷上纱笼。开正好、银花照夜，堆不尽、金粟凝空。丁宁语、频将好事，来报主人公。'),
	(6470, 188, '永遇乐', '夕尽伐去 落日熔金万顷，晴岚洗剑双锋。紫枢元是黑头公。佳处因君愈重。 分得湖光一曲，唤回庐岳千峰。清尊今夜偶然同。早晚商岩有梦。'),
	(6471, 188, '水调歌头（泛湘江）', '濯足夜滩急，晞发北风凉。吴山楚泽行遍，只欠到潇湘。买得扁舟归去，此事天公付我，六月下沧浪。蝉蜕尘埃外，蝶梦水云乡。 制荷衣，纫兰佩，把琼芳。湘妃起舞一笑，抚瑟奏清商。唤起九歌忠愤，拂拭三闾文字，还与日争光。莫遣儿辈觉，此乐未渠央。'),
	(6472, 498, '秦楼月·浮云集', '浮云集。轻雷隐隐初惊蛰。初惊蛰。鹁鸠鸣怒，绿杨风急。 玉炉烟重香罗浥。拂墙浓杏燕支湿。燕支湿。花梢缺处，画楼人立。'),
	(6473, 633, '大圣乐', '电转雷惊，自叹浮生，四十二年。试思量往事，虚无似梦，悲欢万状，合散如烟。苦海无边，爱河无底，流浪看成百漏船。何人解，问无常火里，铁打身坚。须臾便是华颠。好收拾形体归自然。又何须着意，求田问舍，生须宦达，死要名传。寿夭穷通，是非荣辱，此事由来都在天。从今去，任东西南北，作个飞仙。'),
	(6474, 633, '朝中措', '镜湖俯仰两青天。万顷玻璃一叶船。拈棹舞，拥蓑眠。不作天仙作水仙。'),
	(6475, 669, '薄幸·送安伯弟', '送君南浦。对烟柳、青青万缕。更满眼、残红吹尽，叶底黄鹂自语。甚动人、多少离情，楼头水阔山无数。记竹里题诗，花边载酒，魂断江干春暮。 都莫问、功名事，白发渐、星星如许。任鸡鸣起舞，乡关何在，凭高目尽孤鸿去。漫留君住。趁酴醿香暖，持杯且醉瑶台露。相思记取，愁绝西窗夜雨。'),
	(6476, 482, '醉落魄·辛未九月望和答庆符', '百年强半，高秋犹在天南畔。 幽怀已被黄花乱。更恨银蟾。 故向愁人满。招呼诗酒颠狂伴。 羽觞到手判无算。浩歌箕踞巾聊岸。 酒欲醒时，兴在卢仝碗。'),
	(6477, 254, '饮马歌·边头春未到', '兀术每遇对阵之际，吹此则鏖战无还期也 边头春未到。雪满交河道。暮沙明残照。塞烽云间小。断鸿悲。陇月低。泪湿征衣悄。岁华老。'),
	(6478, 185, '瑞鹧鸪（彭德器出示胡邦衡新句次韵）', '白衣苍狗变浮云。千古功名一聚尘。好是悲歌将进酒，不妨同赋惜余春。 风光全似中原日，臭味要须我辈人。雨后飞花知底数，醉来赢取自由身。'),
	(6479, 518, '水龙吟（重过旧隐）', '画桥流水桃溪路，别是壶中佳致。南楼夜月，东窗疏雨，金莲共醉。人静回廊，并肩携手，玉芝香里。念紫箫声断，巫阳梦觉，人何在、花空委。 寂寞危栏触倚。望仙乡、水云无际。芸房花院，重来空锁，苍苔满地。物是人非，小池依旧，彩鸳双戏。念当时风月，如今怀抱，有盈襟泪。'),
	(6480, 372, '江城子', '亦次韵和呈，因以自见穷寂之态 华灯高宴水精宫。浪花中。意无穷。十载江湖，重绾汉符铜。应有青藜存往事，人缥缈，佩丁东。 卧听萧寺响疏钟。渡溪风。转空濛。月上孤窗，邻唱有渔翁。追念使君清坐久，歌一发，恨千里。'),
	(6481, 589, '满江红·丁未九月南渡泊舟仪真江口作', '惨结秋阴，西风送、霏霏雨湿。凄望眼、征鸿几字，暮投沙碛。试问乡关何处是，水云浩荡迷南北。但一抹、寒青有无中，遥山色。 天涯路，江上客。肠欲断，头应白。空搔首兴叹，暮年离拆。须信道消忧除是酒，奈酒行有尽情无极。便挽取、长江入尊罍，浇胸臆。'),
	(6482, 315, '喜迁莺·真宗幸澶渊', '边城寒早。恣骄虏，远牧甘泉丰草。 铁马嘶风，毡裘凌雪，坐使一方云扰。 庙堂折冲无策，欲幸坤维江表。 叱群议，赖寇公力挽，亲行天讨。 缥缈，銮辂动，霓旌龙旆，遥指澶渊道。 日照金戈，云随黄伞，径渡大河清晓。 六军万姓呼舞，箭发狄酋难保。 虏情詟，誓书来，从此年年修好。'),
	(6483, 128, '醉落魄·江天云薄', '江天云薄，江头雪似杨花落。寒灯不管人离索。照得人来，真个睡不著。 归期已负梅花约，又还春动空飘泊。晓寒谁看伊梳掠。雪满西楼，人阑干角。'),
	(6484, 267, '长相思', '昨日晴。今日阴。楼下飞花楼上云。阑干双泪痕。 江南人。江北人。一样春风两样情。晚寒潮未平。'),
	(6485, 267, '鹧鸪天', '曾为梅花醉不归。佳人挽袖乞新词。轻红遍写鸳鸯带，浓碧争斟翡翠卮。 人已老，事皆非。花前不饮泪沾衣。如今但欲关门睡，一任梅花作雪飞。'),
	(6486, 545, '菩萨蛮·暄风迟日春光闹', '暄风迟日春光闹。蒲萄水绿摇轻棹。两岸草烟低。青山啼子规。 归来愁未寝。黛浅眉痕沁。花影转廊腰。红添酒面潮。'),
	(6487, 545, '踏莎行·柳絮风轻', '柳絮风轻，梨花雨细。春阴院落帘垂地。碧溪影里小桥横，青帘市上孤烟起。 镜约关情，琴心破睡。轻寒漠漠侵鸳被。酒醒霞散脸边红，梦回山蹙眉间翠。'),
	(6488, 130, '浣溪沙·楼上晴天碧四垂', '楼上晴天碧四垂。楼前芳草接天涯。劝君莫上最高梯。 新笋已成堂下竹，落花都上燕巢泥。忍听林表杜鹃啼。'),
	(6489, 249, '临江仙·信州作', '谪宦江城无屋买，残僧野寺相依。松间药臼竹间衣。水穷行到处，云起坐看时。 一个幽禽缘底事，苦来醉耳边啼？月斜西院愈声悲。青山无限好？犹道不如归。'),
	(6490, 549, '醉中真（减字浣溪沙七首）', '不信芳春厌老人。老人几度送余春。惜春行乐莫辞频。 巧笑艳歌皆我意，恼花颠酒拚君瞋。物情惟有醉中真。'),
	(6491, 248, '水龙吟·咏月', '倦游京洛风尘，夜来病酒无人问。九衢雪小，千门月淡，元宵灯近。香散梅梢，冻消池面，一番春信。记南楼醉里，西城宴阕，都不管、人春困。 屈指流年未几，早人惊、潘郎双鬓。当时体态，如今情绪，多应瘦损。马上墙头，纵教瞥见，也难相认。凭栏干，但有盈盈泪眼，罗襟揾。'),
	(6492, 693, '西江月（用惠洪韵）', '细细风清撼竹，迟迟日暖开花。香帏深卧醉人家。媚语娇声娅姹。 姹娅声娇语媚，家人醉卧深帏。香花开暖日迟迟。竹撼清风细细。'),
	(6493, 278, '渔家傲', '洗尽秋容天似莹。星稀月淡人初静。策杖萦纡寻远径。披昏暝。堤边犊母闲相并。 遥想去舟魂欲凝。一番佳思从谁咏。憔悴归来如独醒。知何境。沈沈但觉烟村迥。'),
	(6494, 494, '鹧鸪天（佳人）', '罗带双垂画不成。殢人娇态最轻盈。酥胸斜抱天边月，玉手轻弹水面冰。 无限事，许多情。四弦丝竹苦丁宁。饶君拨尽相思调，待听梧桐叶落声。'),
	(6495, 494, '临江仙', '昨夜渡江何处宿，望中疑是秦淮。月明谁起笛中哀。多情王谢女，相逐过江来。 云雨未成还又散，思量好事难谐。凭陵急桨两相催。相伊归去后，应似我情怀。'),
	(6496, 494, '虞美人', '冰肌自是生来瘦。那更分飞后。日长帘幕望黄昏。及至黄昏时候、转销魂。 君还知道相思苦。怎忍抛奴去。不辞迢递过关山。只恐别郎容易、见郎难。'),
	(6497, 494, '阳关曲（李公择）', '济南春好雪初晴。才到龙山马足轻。使君莫忘霅溪女，还作阳关肠断声。'),
	(6498, 494, '醉落魄·离京口作', '轻云微月，二更酒醒船初发。孤城回望苍烟合。记得歌时，不记归时节。 巾偏扇坠藤床滑，觉来幽梦无人说。此生飘荡何时歇？家在西南，常作东南别。'),
	(6499, 494, '醉落魄（席上呈元素）', '分携如昨。人生到处萍飘泊。偶然相聚还离索。多病多愁，须信从来错。 尊前一笑休辞却。天涯同是伤沦落。故山犹负平生约。西望峨嵋，长羡归飞鹤。'),
	(6500, 494, '阮郎归（梅词）', '暗香浮动月黄昏。堂前一树春。东风何事入西邻。儿家常闭门。 雪肌冷，玉容真。香腮粉未匀。折花欲寄岭头人。江南日暮云。'),
	(6501, 494, '渔家傲（七夕）', '皎皎牵牛河汉女。盈盈临水无由语。望断碧云空日暮。无寻处。梦回芳草生春浦。 鸟散余花纷似雨，汀洲苹老香风度。明月多情来照户。但揽取。清光长送人归去。'),
	(6502, 250, '愁倚阑令·花阴月', '花阴月，柳梢莺。近清明。长恨去年今夜雨，洒离亭。 枕上怀远诗成。红笺纸、小砑吴绫。寄与征人教念远，莫无情。'),
	(6503, 352, '玉楼春·洛阳正值芳菲节', '洛阳正值芳菲节，秾艳清香相间发。游丝有意苦相萦，垂柳无端争赠别。 杏花红处青山缺，山畔行人山下歇。今宵谁肯远相随，惟有寂寥孤馆月。'),
	(6504, 251, '清平乐', '春花秋草。只是催人老。总把千山眉黛扫。未抵别愁多少。 劝君绿酒金杯。莫嫌丝管声催。兔走乌飞不住，人生几度三台。'),
	(6505, 251, '更漏子', '寒鸿高，仙露满。秋入银河清浅。逢好客，且开眉。盛年能几时。 宝筝调，罗袖软。拍碎画堂檀板。须尽醉，莫推辞。人生多别离。'),
	(6506, 251, '浣溪沙', '淡淡梳妆薄薄衣。天仙模样好容仪。旧欢前事入颦眉。 闲役梦魂孤烛暗，恨无消息画帘垂。且留双泪说相思。'),
	(6507, 344, '凤凰阁', '匆匆相见，懊恼恩情太薄。霎时云雨人抛却。教我行思坐想，肌肤如削。恨只恨、相违旧约。 相思成病，那更潇潇雨落。断肠人在阑干角。山远水远人远，音信难托。这滋味、黄昏又恶。'),
	(6508, 344, '征部乐·雅欢幽会', '雅欢幽会，良辰可惜虚抛掷。每追念、狂踪旧迹。长祗恁、愁闷朝夕。凭谁去、花衢觅。细说此中端的。道向我、转觉厌厌，役梦劳魂苦相忆。 须知最有，风前月下，心事始终难得。但愿我、虫虫心下，把人看待，长似初相识。况渐逢春色。便是有，举场消息。待这回、好好怜伊，更不轻离拆。'),
	(6509, 344, '斗百花·飒飒霜飘鸳瓦', '飒飒霜飘鸳瓦，翠幕轻寒微透，长门深锁悄悄，满庭秋色将晚。眼看菊蕊，重阳泪落如珠，长是淹残粉面。鸾辂音尘远。 无限幽恨，寄情空殢纨扇。应是帝王，当初怪妾辞辇，陡顿今来，宫中第一妖娆，却道昭阳飞燕。'),
	(6510, 344, '早梅芳·海霞红', '海霞红，山烟翠。故都风景繁华地。谯门画戟，下临万井，金碧楼台相倚。芰荷浦溆，杨柳汀洲，映虹桥倒影，兰舟飞棹，游人聚散，一片湖光里。 汉元侯，自从破虏征蛮，峻陟枢庭贵。筹帷厌久，盛年昼锦，归来吾乡我里。铃斋少讼，宴馆多欢，未周星，便恐皇家，图任勋贤，又作登庸计。'),
	(6511, 494, '临皋闲题', '　　临皋亭下八十数步，便是大江，其半是峨眉雪水，吾饮食沐浴皆取焉，何必归乡哉！江山风月，本无常主，闲者便是主人。闻范子丰新第园池，与此孰胜？所不如者，上无两税及助役钱耳。'),
	(6512, 142, '菩萨蛮·秋风扫尽闲花草', '秋风扫尽闲花草，黄花不逐秋光老。试与插钗头，钗头占断秋。 簪花人有意，共祝年年醉。不用泛瑶觞，花先着酒香。'),
	(6513, 471, '扬州慢', '危榭摧红，断砖埋玉，定王台下园林。听樯干燕子，诉别后惊心。尽江上、青峰好在，可怜曾是，野烧痕深。付潇湘渔笛，吹残今古锁沈。 妙奴不见，纵秦郎、谁更知音。正雁妾悲歌，雕奚醉舞，楚户停砧。化碧旧愁何处，魂归些、晚日阴阴。渺云平铁坝，凄凉天也沾襟。'),
	(6514, 527, '长相思·南高峰', '南高峰。北高峰。南北高峰云淡浓。湖山图画中。采芙容。小小红船西复东。相思无路通。'),
	(6515, 508, '霜天晓月（梅）', '千霜万雪。受尽寒磨折。赖是生来瘦硬，浑不怕、角吹彻。 清绝。影也别。知心惟有月。原没春风情性，如何共、海棠说。'),
	(6516, 171, '霓裳中序第一·茉莉咏', '青颦粲素靥。海国仙人偏耐热。餐尽香风露屑。便万里凌空，肯凭莲叶。盈盈步月。悄似怜、轻去瑶阙。人何在，忆渠痴小，点点爱轻撅。 愁绝。旧游轻别。忍重看、锁香金箧。凄凉清夜簟席。杳杳诗魂，真化风蝶。冷香清到骨。梦十里、梅花霁雪。归来也，恹恹心事，自共素娥说。'),
	(6517, 101, '如梦令', '厮守许多时价。谁信一筹一画。相送到矾园，赢得泪珠如泻。挥洒。挥洒。将底江州司马。'),
	(6518, 158, '忆秦娥·季温老友归樵阳人来闲书因以为寄', '秋寂寞。秋风夜雨伤离索。伤离索。老怀无奈，泪珠零落。 故人一去无期约。尺书忽寄西飞鹤。西飞鹤。故人何在，水村山郭。'),
	(6519, 151, '南浦·旅怀', '风悲画角，听单于、三弄落谯门。投宿骎骎征骑，飞雪满孤村。酒市渐闲灯火，正敲窗、乱叶舞纷纷。送数声惊雁，下离烟水，嘹唳度寒云。 好在半胧溪月，到如今、无处不销魂。故国梅花归梦，愁损绿罗裙。为问暗香闲艳，也相思、万点付啼痕。算翠屏应是，两眉馀恨倚黄昏。'),
	(6520, 45, '渔家傲', '一只孤舟巡海岸。盘陀石上垂钩线。钓得锦鳞鲜又健。堪爱羡。龙王见了将珠换。 钓罢归来莲苑看。满堂尽是真罗汉。便爇名香三五片。梵□献。原来佛不夺众生愿。'),
	(6521, 202, '阮郎归·有怀北游', '钿车骄马锦相连。香尘逐管弦。瞥然飞过水秋千。清明寒食天。 花贴贴，柳悬悬。莺房几醉眠。醉中不信有啼鹃。江南二十年。'),
	(6522, 202, '忆旧游·登蓬莱阁', '问蓬莱何处，风月依然，万里江清。休说神仙事，便神仙纵有，即是闲人。笑我几番醒醉，石磴扫松阴。任狂客难招，采芳难赠，且自微吟。 俯仰成陈迹，叹百年谁在，阑槛孤凭。海日生残夜，看卧龙和梦，飞入秋冥?。还听水声东去，山冷不生云。正目极空寒，萧萧汉柏愁茂陵。'),
	(6523, 202, '摸鱼子·高爱山隐居', '爱吾庐、傍湖千顷，苍茫一片清润。晴岚暖翠融融处，花影倒窥天镜。沙浦迥。看野水涵波，隔柳横孤艇。眠鸥未醒。甚占得莼乡，都无人见，斜照起春暝。 还重省。岂料山中秦晋，桃源今度难认。林间即是长生路，一笑原非捷径。深更静。待散发吹箫，跨鹤天风冷。凭高露饮。正碧落尘空，光摇半壁，月在万松顶。'),
	(6524, 422, '扫花游·秋声', '商飙乍发，渐淅淅初闻，萧萧还住。顿惊倦旅。背青灯吊影，起吟愁赋。断续无凭，试立荒庭听取。在何许。但落叶满阶，惟有高树。 迢递归梦阻。正老耳难禁，病怀凄楚。故山院宇。想边鸿孤唳，砌蛩私语。数点相和，更著芭蕉细雨。避无处。这闲愁、夜深尤苦。'),
	(6525, 366, '一剪梅·怀旧', '十年愁眼泪巴巴。今日思家。明日思家。一团燕月照窗纱。楼上胡笳。塞上胡笳。 玉人劝我酌流霞。急捻琵琶。缓捻琵琶。一从别后各天涯。欲寄梅花。莫寄梅花。'),
	(6526, 124, '扫花游·九日怀归', '江蓠怨碧，早过了霜花，锦空洲渚。孤蛩自语。正长安乱叶，万家砧杵。尘染秋衣，谁念西风倦旅。恨无据。怅望极归舟，天际烟树。 心事曾细数。怕水叶沈红，梦云离去。情丝恨缕。倩回纹为织，那时愁句。雁字无多，写得相思几许。暗凝伫。近重阳、满城风雨。'),
	(6527, 71, '宝鼎现·春月', '红妆春骑。踏月影、竿旗穿市。望不尽、楼台歌舞，习习香尘莲步底。箫声断、约彩鸾归去，未怕金吾呵醉。甚辇路、喧阗且止。听得念奴歌起。 父老犹记宣和事。抱铜仙、清泪如水。还转盼、沙河多丽。滉漾明光连邸第。帘影冻、散红光成绮。月浸葡萄十里。看往来、神仙才子，肯把菱花扑碎。 肠断竹马儿童，空见说、三千乐指。等多时春不归来，到春时欲睡。又说向、灯前拥髻。暗滴鲛珠坠。便当日、亲见霓裳，天上人间梦里。'),
	(6528, 112, '满江红·刘朔斋赋菊和韵', '露浥初英，早遗恨、参差九日。还却笑、萸随节过，桂凋无色。杯面寒香蜂共泛，篱根秋讯蛩催织。爱玲珑、筛月水屏风，千枝结。 芳井韵，寒泉咽。霜著处，微红湿。共评花索句，看谁先得。好漉乌巾连夜醉，莫愁金钿无人拾。算遗踪、犹有枕囊留，相思物。'),
	(6529, 112, '东风第一枝·倾国倾城', '倾国倾城，非花非雾，春风十里独步。胜如西子妖绕，更比太真澹泞。铅华不御。漫道有、巫山洛浦。似恁地、标格无双，镇锁画楼深处。 曾被风、容易送去。曾被月、等闲留住。似花翻使花羞，似柳任从柳妒。不教歌舞。恐化作、彩云轻举。信下蔡、阳城俱迷，看取宋玉词赋。'),
	(6530, 112, '高阳台·落梅', '宫粉雕痕，仙云堕影，无人野水荒湾。古石埋香，金沙锁骨连环。南楼不恨吹横笛，恨晓风、千里关山。半飘零，庭上黄昏，月冷阑干。 寿阳空理愁鸾。问谁调玉髓，暗补香瘢。细雨归鸿，孤山无限春寒。离魂难倩招清此，梦缟衣、解佩溪边。最愁人，啼鸟清明，叶底青圆。'),
	(6531, 112, '探芳信·为春瘦', '为春瘦。更瘦如梅花，花应知否。任枕函云坠，离怀半中酒。雨声楼阁春寒里，寂寞收灯后。甚年年、斗草心期，探花时候。 娇懒强拈绣。暗背里相思，闲供晴昼。玉合罗囊，兰膏渍红豆。舞衣叠损金泥凤，妒折阑干柳。几多愁、两点天涯远岫。'),
	(6532, 112, '惜秋华·七夕', '露罥蛛丝，小楼阴堕月，秋惊华鬓。宫漏未央，当时钿钗遗恨。人间梦隔西风，算天上、年华一瞬。相逢，纵相疏、胜却巫阳无准。 何处动凉讯。听露井梧桐，楚骚成韵。彩云断、翠羽散，此情难问。银河万古秋声，但望中、婺星清润。轻俊。度金针、漫牵方寸。'),
	(6533, 112, '高山流水·丁基仲侧室善丝桐赋咏晓达音吕备歌舞之妙', '素弦一一起秋风。写柔情、都在春葱。徽外断肠声，霜宵暗落惊鸿。低颦处、翦绿裁红。仙郎伴、新制还赓旧曲，映月帘栊。似名花并蒂，日日醉春浓。 吴中。空传有西子，应不解、换徵移宫。兰蕙满襟怀，唾碧总喷花茸。后堂深、想费春工。客愁重、时听蕉寒雨碎，泪湿琼钟。恁风流也称，金屋贮娇慵。'),
	(6534, 112, '诉衷情·秋情', '片云载雨过江鸥。水色澹汀洲。小莲玉惨红怨，翠被又经秋。 凉意思，到南楼。小帘钩。半窗灯晕，几叶芭蕉，客梦床头。'),
	(6535, 112, '水龙吟·寿梅津', '杜陵折柳狂吟，砚波尚湿红衣露。仙桃宴早，江梅春近，还催客句。宫漏传鸡，禁门嘶骑，宦情熟处。正黄编夜展，天香字暖，春葱翦、红蜜炬。 宫帽鸾枝醉舞。思飘扬、F156仙风举。星罗万卷，云驱千阵，飞毫海雨。长寿杯深，探春腔稳，江湖同赋。又看看、便系金狨莺晓，傍西湖路。'),
	(6536, 112, '解连环·秋情', '暮檐凉薄。疑清风动竹，故人来邈。渐夜久、闲引流萤，弄微照素怀，暗呈纤白。梦远双成，凤笙杳、玉绳西落。掩綀帷倦入，又惹旧愁，汗香阑角。 银瓶恨沉断索。叹梧桐未秋，露井先觉。抱素影、明月空闲，早尘损丹青，楚山依约。 翠冷红衰，怕惊起、西池鱼跃。记湘娥、绛绡暗解，褪花坠萼。'),
	(6537, 117, '青玉案', '十年三过苏台路。还又是、匆匆去。迅景流光容易度。鹭洲鸥渚，苇汀芦岸，总是消魂处。 苍烟欲合斜阳暮。付与愁人砌愁句。为问新愁愁底许。酒边成醉，醉边成梦，梦断前山雨。'),
	(6538, 695, '霜天晓角·仪真江上夜泊', '寒江夜宿。长啸江之曲。水底鱼龙惊动，风卷地、浪翻屋。 诗情吟未足。酒兴断还续。草草兴亡休问，功名泪、欲盈掬。'),
	(6539, 91, '鹧鸪天·卫县道中有怀其人', '雁足无书古塞幽。一程烟草一程愁。帽檐尘重风吹野，帐角香销月满楼。 情思乱，梦魂浮。缃裙多忆敝貂裘。官河水静阑干暖，徙倚斜阳怨晚秋。'),
	(6540, 91, '临江仙·闺思', '愁与西风应有约，年年同赴清秋。旧游帘幕记扬州。一灯人著梦，双燕月当楼。 罗带鸳鸯尘暗澹，更须整顿风流。天涯万一见温柔。瘦应因此瘦，羞亦为郎羞。'),
	(6541, 149, '永遇乐（次韵辛克清先生）', '我与先生，夙期已久，人间无此。不学杨郎，南山种豆，十一徵微利。云霄直上，诸公衮衮，乃作道边苦李。五千言，老来受用，肯教造物儿戏。 东冈记得，同来胥宇，岁月几何难计。柳老悲桓，松高对阮。未办为邻地。长干白下，青楼朱阁，往往梦中槐蚁。却不如、＿尊放满，老夫未醉。'),
	(6542, 149, '侧犯·咏芍药', '恨春易去。甚春却向扬州住。微雨。正茧栗梢头弄诗句。红桥二十四，总是行云处。无语。渐半脱宫衣笑相顾。 金壶细叶，千朵围歌舞。谁念我、鬓成丝，来此共尊俎。后日西园，绿阴无数。寂寞刘郎，自修花谱。'),
	(6543, 149, '庆宫春·双桨莼波', '　　绍熙辛亥除夕。余别石湖归吴兴，雪后夜过垂虹，尝赋诗云：“笠泽茫茫雁影微。玉峰重叠护云衣；长桥寂寞春寒夜，只有诗人一舸归。”后五年冬，复与俞商卿、张平甫、钴朴翁自封禺同载，诣梁溪。道经吴淞。山寒天迥，云浪四合，中夕相呼步垂虹。星斗下垂。错杂渔火。朔吹凛凛。卮酒不能支。朴翁以衾自缠，犹相与行吟。因赋此阙，盖过旬，涂稿乃定。朴翁咎余无益，然意所耽，不能自已也。平甫、商卿、朴翁皆工于诗。所出奇诡；余亦强追逐之。此行既归。各得五十余解。 双桨莼波，一蓑松雨，暮愁渐满空阔。呼我盟鸥，翩翩欲下，背人还过木末。那回归去，荡云雪、孤舟夜发。伤心重见，依约眉山，黛痕低压。 采香径里春寒，老子婆娑，自歌谁答？垂虹西望，飘然引去，此兴平生难遏。酒醒波远，正凝想、明挡素袜。如今安在？惟有阑干，伴人一霎。(明挡 一作：珰)'),
	(6544, 149, '浣溪沙·丙辰岁不尽五日吴松作', '雁怯重云不肯啼，画船愁过石塘西。打头风浪恶禁持。 春浦渐生迎棹绿，小梅应长亚门枝，一年灯火要人归。'),
	(6545, 83, '减字木兰花·莎衫筠笠', '莎衫筠笠。正是村村农务急。绿水千畦。惭愧秧针出得齐。 风斜雨细。麦欲黄时寒又至。馌妇耕夫。画作今年稔岁图。'),
	(6546, 215, '念奴娇·宜雨亭咏千叶海棠', '绿云影里，把明霞织就，千重文绣。紫腻红娇扶不起，好是未开时候。半怯春寒，半便晴色，养得胭脂透。小亭人静，嫩莺啼破清昼。 犹记携手芳阴，一枝斜戴，娇艳波双秀。小语轻怜花总见，争得似花长久。醉浅休归，夜深同睡，明日还相守。免教春去，断肠空叹诗瘦。'),
	(6547, 335, '念奴娇', '汉天云静，望一星飞过，湘南湘北。当是郴山猿鹤梦，唤起日边消息。羽扇纶巾，浩然乘兴，此意无人识。扁舟千里，但闻清夜横笛。 记得天上人间，去年今日。曾作称觞客。明月风烟依旧似，只觉蓬莱悬隔。更恐明朝，诏黄飞下，趣驾冲霄翼。衮衣剑履，望公长在南极。'),
	(6548, 639, '青玉案', '武陵溪上桃花路。见征骑、匆匆去。嘶入斜阳芳草渡。读书窗下，弹琴石上，留得销魂处。 落花冉冉春将暮。空写池塘梦中句。黄犬书来何日许。辋川轻舸，杜陵尊酒，半夜灯前雨。'),
	(6549, 639, '念奴娇（至金陵）', '江南春色，算来是、多少胜游清赏。妖冶廉纤，只做得，飞鸟向人偎傍。地＿天开，精神朗慧，到底还京样。人家小语，一声声近清唱。 因念旧日山城，个人如画，已作中州想。邓禹笑人无限也，冷落不堪惆怅。秋水双明，高山一弄，著我些悲壮。南徐好住，片帆有分来往。'),
	(6550, 591, '念奴娇', '勋门积庆，为皇家此日，重生贤辅。崧岳储神须信道，非特当年申甫。清白传芳，高明驰誉，材更兼文武。黑头年少，风云还会龙虎。 天为指日垂弧，张旃持节，不遣穹庐去。中使传宣，颁赐锡赉，清晓欢声旁午。两世光华，一门荣耀，盛事夸千古。祝公千岁，庙堂长佐贤主。'),
	(6551, 591, '念奴娇', '勋门积庆，为皇家此日，重生贤辅。崧岳储神须信道，非特当年申甫。清白传芳，高明驰誉，材更兼文武。黑头年少，风云还会龙虎。 天为指日垂弧，张旃持节，不遣穹庐去。中使传宣，颁赐锡赉，清晓欢声旁午。两世光华，一门荣耀，盛事夸千古。祝公千岁，庙堂长佐贤主。'),
	(6552, 591, '生查子·题京口郡治尘表亭', '悠悠万世功，矻矻当年苦。鱼自入深渊，人自居平土。 红日又西沈，白浪长东去。不是望金山，我自思量禹。'),
	(6553, 591, '水调歌头（赋传岩叟悠然阁）', '岁岁有黄菊，千载一东篱。悠然政须两字，长笑退之诗。自古此山元有，何事当时才见，此意有谁知。君起更斟酒，我醉不须辞。 回首处，云正出，鸟倦飞。重来楼上，一句端的与君期。都把轩窗写遍，更使儿童诵得，归去来兮辞。万卷有时用，植杖且耘耔。'),
	(6554, 591, '鹧鸪天', '欲上高楼去避愁。愁还随我上高楼。经行几处江山改，多少亲朋尽白头。 归休去，去归休。不成人总要封侯。浮云出处元无定，得似浮云也自由。'),
	(6555, 591, '满江红·倦客新丰', '倦客新丰，貂裘敝、征尘满目。弹短铗、青蛇三尺，浩歌谁续。不念英雄江左老，用之可以尊中国。叹诗书、万卷致君人，番沈陆。 休感叹，年华促。人易老，叹难足。有玉人怜我，为簪黄菊。且置请缨封万户，竟须卖剑酬黄犊。叹当年、寂寞贾长沙，伤时哭。'),
	(6556, 587, '水调歌头（中秋）', '今夕知何夕，秋色正平分。＿娥此际、底事越样好精神。已是天高气肃，那是清风洒洒，万里没纤云。把酒临风饮，酒面起红鳞。 歌一曲舞一曲，捧金樽。从他妄想，老兔憔悴正纷纷。我为桂花拚醉，明日扶头不起，颠倒白纶巾。天若知人意，夜雨莫倾盆。'),
	(6557, 188, '念奴娇', '海云四敛，太清楼、极目一天秋色。明月飞来云雾尽，城郭山川历历。良夜悠悠，西风袅袅，银汉冰轮侧。云霓三弄，广寒宫殿长笛。 偏照紫府瑶台，香笼玉座，翠霭迷南北。天上人间凝望处，应有乘风归客。露滴金盘，凉生玉宇，满地新霜白。壶中清赏，画檐高挂虚碧。'),
	(6558, 188, '临江仙·试问梅花何处好', '试问梅花何处好，与君藉草携壶。西园清夜片尘无。一天云破碎，两树玉扶疏。 谁擫昭华吹古调，散花便满衣裾。只疑幽梦在清都。星稀河影转，霜重月华孤。'),
	(6559, 188, '水调歌头（隐静山观雨）', '青嶂度云气，幽壑舞回风。山神助我奇观，唤起碧霄龙。电掣金蛇千丈，雷震灵龟万叠，汹汹欲崩空。尽泻银潢水，倾入宝莲宫。 坐中客，凌积翠，看奔洪。人间应失匕筋，此地独从容。洗了从来尘垢，润及无边焦槁，造物不言功。天宇忽开霁，日在五云东。'),
	(6560, 633, '太平时', '竹里房栊一径深。静愔愔。乱红飞尽绿成阴。有鸣禽。 临罢兰亭无一事，自修琴。铜炉袅袅海南沉。洗尘襟。'),
	(6561, 633, '感皇恩（伯礼立春日生日）', '春色到人间，彩＿初戴。正好春盘细生菜。一般日月，只有仙家偏耐。雪霜从点鬓，朱颜在。 温诏鼎来，延英催对。凤阁鸾台看除拜。对衣裁稳，恰称＿纹新带。个时方旋了、功名债。'),
	(6562, 526, '相思引', '晓鉴燕脂拂紫绵。未忄欠梳掠髻云偏。日高人静，沈水袅残烟。 春老菖蒲花未著，路长鱼雁信难传。无端风絮，飞到绣床边。'),
	(6563, 526, '瑞鹤仙·郊原初过雨', '郊原初过雨。见败叶零乱，风定犹舞。斜阳挂深树。映浓愁浅黛，遥山眉妩。来时旧路。尚岩花、娇黄半吐。到而今，唯有溪边流水，见人如故。 无语。邮亭深静，下马还寻，旧曾题处。无聊倦旅。伤离恨，最愁苦。纵收香藏镜，他年重到，人面桃花在否。念沈沈、小阁幽窗，有时梦去。'),
	(6564, 573, '鹊桥仙（送路勉道赴长乐）', '留花翠幕，添香红袖，常恨情长春浅。南风吹酒玉虹翻，便忍听、离弦声断。 乘鸾宝扇，凌波微步，好在清池凉馆。直饶书与荔枝来，问纤手、谁传冰碗。'),
	(6565, 105, '薄幸·青楼春晚', '青楼春晚。昼寂寂、梳匀又懒。乍听得、鸦啼莺弄，惹起新愁无限。记年时、偷掷春心，花间隔雾遥相见。便角枕题诗，宝钗贳酒，共醉青苔深院。 怎忘得、回廊下，携手处、花明月满。如今但暮雨，蜂愁蝶恨，小窗闲对芭蕉展。却谁拘管。尽无言、闲品秦筝，泪满参差雁。腰支渐小，心与杨花共远。'),
	(6566, 185, '鱼游春水·芳洲生苹芷', '芳洲生苹芷，宿雨收晴浮暖翠。烟光如洗，几片花飞点泪。清镜空余白发添，新恨谁传红绫寄。溪涨岸痕，浪吞沙尾。 老去情怀易醉。十二阑干慵遍倚。双凫人惯风流，功名万里。梦想浓妆碧云边，目断孤帆夕阳里。何时送客，更临春水。'),
	(6567, 518, '青玉案', '鸾凰本是和鸣友。奈无计、长相守。云雨匆匆分袂后。彩舟东去，橹声呕轧，目断长堤柳。 涓涓清泪轻绡透。残粉余香尚依旧。独上南楼空回首。夜来明月，怎知今夜，少个人携手。'),
	(6568, 99, '西江月·五柳坊中烟绿', '政和间，余卜筑宛丘，手植众芗，自号芗林居士。建炎初，解六路漕事，中原俶扰，故庐不得返，卜居清江之五柳坊。绍兴癸丑，罢帅南海，即弃官不仕。乙卯起，以九江郡复转漕江东，入为户部侍郎。辞荣避谤，出守姑苏。到郡少日，请又力焉，诏可，且赐舟曰泛宅，送之以归。己未暮春，复还旧隐。时仲舅李公休亦辞舂陵郡守致仕，喜赋是词。 五柳坊中烟绿，百花洲上云红。萧萧白发两衰翁，不与时人同梦。 抛掷麟符虎节，徜徉江月林风。世间万事转头空，个里如如不动。'),
	(6569, 104, '南歌子·驿路侵斜月', '驿路侵斜月，溪桥度晓霜。短篱残菊一枝黄。正是乱山深处、过重阳。 旅枕元无梦，寒更每自长。只言江左好风光。不道中原归思、转凄凉。'),
	(6570, 267, '卜算子', '古涧一枝梅，免被园林锁。路远山深不怕寒，似共春相趓。 幽思有谁知，托契都难可。独自风流独自香，明月来寻我。'),
	(6571, 267, '西江月', '元是西都散汉，江南今日衰翁。从来颠怪更心风。做尽百般无用。 屈指八旬将到，回头万事皆空。云间鸿雁草间虫。共我一般做梦。'),
	(6572, 415, '雨霖铃（雪）', '琼楼玉宇。满人寰似、海边洲渚。蓬莱又还水浅，鲸涛静见，银宫如许。紫极鸣筲声断，望霓舟何处。待夜深、重倚层霄，认得瑶池广寒路。 郢中旧曲谁能度。恨歌声、响入青云去。西湖近时绝唱，总不道、月梅盐絮。暗想当年宾从，毫端有惊人句。谩说枚叟邹生，共作《梁园赋》。'),
	(6573, 360, '菩萨蛮·富阳道中', '春潮曾送离魂去。春山曾见伤离处。老去不堪愁。凭阑看水流。 东风留不住。一夜檐前雨。明日觅春痕。红疏桃杏村。'),
	(6574, 130, '过秦楼·大石', '水浴清蟾，叶喧凉吹，巷陌马声初断。闲依露井，笑扑流萤，惹破画罗轻扇。人静夜久凭阑，愁不归眠，立残更箭。叹年华一瞬，人今千里，梦沉书远。 空见说、鬓怯琼梳，容销金镜，渐懒趁时匀染。梅风地溽，虹雨苔滋，一架舞红都变。谁信无聊为伊，才减江淹，情伤荀倩。但明河影下，还看稀星数点。'),
	(6575, 130, '鹤冲天（溧水长寿乡作）', '梅雨霁，暑风和。高柳乱蝉多。小园台榭远池波。鱼戏动新荷。 薄纱厨，轻羽扇。枕冷簟凉深院。此时情绪此时天。无事小神仙。'),
	(6576, 130, '点绛唇·伤感', '辽鹤归来，故乡多少伤心地。寸书不寄。鱼浪空千里。 凭仗桃根，说与凄凉意。愁无际。旧时衣袂。犹有东门泪。'),
	(6577, 130, '渡江云·晴岚低楚甸', '晴岚低楚甸，暖回雁翼，阵势起平沙。骤惊春在眼，借问何时，委曲到山家。涂香晕色，盛粉饰、争作妍华。千万丝、陌头杨柳，渐渐可藏鸦。 堪嗟。清江东注，画舸西流，指长安日下。愁宴阑、风翻旗尾，潮溅乌纱。今宵正对初弦月，傍水驿、深舣蒹葭。沉恨处，时时自剔灯花。'),
	(6578, 549, '鹧鸪天', '轰醉王孙玳瑁筵。渴虹垂地吸长川。侧商调里清歌送，破尽穷愁直几钱。 孤棹舣，小江边。爱而不见酒中仙。伤心两岸官杨柳，已带斜阳又带蝉。'),
	(6579, 549, '台城游（水调歌头）', '南国本潇洒。六代浸豪奢。台城游冶。襞笺能赋属宫娃。云观登临清夏。璧月留连长夜。吟醉送年华。回首飞鸳瓦。却羡井中蛙。 访乌衣，成白社。不容车。旧时王谢。堂前双燕过谁家。楼外河横斗挂。淮上潮平霜下。樯影落寒沙。商女篷窗罅。犹唱后庭花。'),
	(6580, 549, '生查子·陌上郎', '西津海鹘舟，径度沧江雨。双舻本无情，鸦轧如人语。 挥金陌上郎，化石山头妇。何物系君心，三岁扶床女。'),
	(6581, 248, '浣溪沙·清润风光雨后天', '清润风光雨后天。蔷薇花谢绿窗前。碧琉璃瓦欲生烟。 十里闲情凭蝶梦，一春幽怨付鲲弦。小楼今夜月重圆。'),
	(6582, 278, '临江仙·登凌歊台感怀', '偶向凌歊台上望，春光已过三分。江山重叠倍销魂。风花飞有态，烟絮坠无痕。 已是年来伤感甚，那堪旧恨仍存。清愁满眼共谁论。却应台下草，不解忆王孙。'),
	(6583, 494, '西江月（佳人）', '碧雾轻笼两凤，寒烟淡拂双鸦。为谁流睇不归家。错认门前过马。 有意偷回笑眼，无言强整衣纱。刘郎一见武陵花。从此春心荡也。'),
	(6584, 494, '西江月（咏梅）', '马趁香微路远，沙笼月淡烟斜。渡波清彻映妍华。倒绿枝寒凤挂。 挂凤寒枝绿倒，华妍映彻清波。渡斜烟淡月笼沙。远路微香趁马。'),
	(6585, 494, '满庭芳', '恩放归阳羡，复作一篇 归去来兮，清溪无底，上有千仞嵯峨。画楼东畔，天远夕阳多。老去君恩未报，空回首、弹铗悲歌。船头转，长风万里，归马驻平坡。 无何。何处有，银潢尽处，天女停梭。问何事人间，久戏风波。顾谓同来稚子，应烂汝、腰下长柯。青衫破，群仙笑我，千缕挂烟蓑。'),
	(6586, 494, '浣溪沙', '山色横侵蘸晕霞。湘川风静吐寒花。远林屋散尚啼鸦。 梦到故园多少路，酒醒南望隔天涯。月明千里照平沙。'),
	(6587, 494, '浣溪沙·麻叶层层檾叶光', '麻叶层层檾叶光，谁家煮茧一村香。隔篱娇语络丝娘。 垂白杖藜抬醉眼，捋青捣麨软饥肠。问言豆叶几时黄。'),
	(6588, 494, '浣溪沙·旋抹红妆看使君', '旋抹红妆看使君。三三五五棘篱门。相挨踏破茜罗裙。 老幼扶携收麦社。乌鸢翔舞赛神村。道逢醉叟卧黄昏。'),
	(6589, 494, '哨遍', '其陋。独鄱阳董毅夫过而悦之，有卜邻之意。乃取归去来词，稍加概括，使就声律，以遗毅夫。使家僮歌之，时相从于东坡，释耒而和之，扣牛角而为之节，不亦乐乎 为米折腰，因酒弃家，口体交相累。归去来，谁不遣君归。觉从前皆非今是。露未晞。征夫指予归路，门前笑语喧童稚。嗟旧菊都荒，新松暗老，吾年今已如此。但小窗容膝闭柴扉。策杖看孤云暮鸿飞。云山无心，鸟倦知还，本非有意。 噫。归去来兮。我今忘我兼忘世。亲戚无浪语，琴书中有真味。步翠麓崎岖，泛溪窈窕，涓涓暗谷流春水。观草木欣荣，幽人自感，吾生行且休矣。念寓形宇内复几时。不自觉皇皇欲何之。委吾心、去留谁计。神仙知在何处，富贵非吾志。但知临水登山啸咏，自引壶觞自醉。此生天命更何疑。且乘流、遇坎还止。'),
	(6590, 494, '菩萨蛮（七夕）', '凤回仙驭云开扇。更阑月坠星河转。枕上梦魂惊。晓檐疏雨零。 相逢虽草草。长共天难老。终不羡人间。人间日似年。'),
	(6591, 494, '西江月（茶词）', '龙焙今年绝品，谷帘自古珍泉。雪芽双井散神仙。苗裔来从北苑。 汤发云腴酽白，盏浮花乳轻圆。人间谁敢更争妍。斗取红窗粉面。'),
	(6592, 250, '临江仙·长爱碧阑干影', '长爱碧阑干影，芙蓉秋水开时。脸红凝露学娇啼。霞觞熏冷艳，云髻袅纤枝。 烟雨依前时候，霜丛如旧芳菲。与谁同醉采香归。去年花下客，今似蝶分飞。'),
	(6593, 409, '望江南（四之二）', '归依法，法法不思议。愿我六根常寂静，心如宝月映琉璃。了法更无疑。'),
	(6594, 409, '诉衷情（五之三）', '茫然不肯住林间。有处即追攀。将他死语图度，怎得离真丹。 浆水价，匹如闲。也须还。何如直截，踢倒军持，赢取沩山。'),
	(6595, 352, '诉衷情', '离怀酒病两忡忡。欹枕梦无踪。可怜有人今夜，胆小怯房空。 杨柳绿，杏梢红。负春风。迢迢别恨，脉脉归心，付与征鸿。'),
	(6596, 352, '千秋岁', '画堂人静，翡翠帘前月。鸾帷凤枕虚铺设。风流难管束，一去音书歇。到而今，高梧冷落西风切。 未语先垂泪，滴尽相思血。魂欲断，情难绝。都来些子事，更与何人说。为个甚，心头见底多离别。'),
	(6597, 352, '渔家傲', '粉蕊丹青描不得。金针线线功难敌。谁傍暗香轻采摘。风淅淅。船头触散双鸂鶒。 夜雨染成天水碧。朝阳借出胭脂色。欲落又开人共惜。秋气逼。盘中已见新荷的。'),
	(6598, 251, '蝶恋花·帘幕风轻双语燕', '帘幕风轻双语燕。午醉醒来，柳絮飞撩乱。心事一春犹未见。余花落尽青苔院。 百尺朱楼闲倚遍。薄雨浓云，抵死遮人面。消息未知归早晚。斜阳只送平波远。'),
	(6599, 251, '木兰花', '紫薇朱槿繁开后。枕簟微凉生玉漏。玳筵初启日穿帘，檀板欲开香满袖。 红衫侍女频倾酒。龟鹤仙人来献寿。欢声喜气逐时新，青鬓玉颜长似旧。'),
	(6600, 251, '浣溪沙', '红蓼花香夹岸稠。绿波春水向东流。小船轻舫好追游。 渔父酒醒重拨棹，鸳鸯飞去却回头。一杯销尽两眉愁。'),
	(6601, 251, '破阵子', '忆得去年今日，黄花已满东篱。曾与玉人临小槛，共折香英泛酒卮。长条插鬓垂。 人貌不应迁换，珍丛又睹芳菲。重把一尊寻旧径，所惜光阴去似飞。风飘露冷时。'),
	(6602, 186, '惜琼花·汀苹白', '汀苹白。苕水碧。每逢花驻乐，随处欢席。别时携手看春色。萤火而今，飞破秋夕。 旱河流，如带窄。任身轻似叶，何计归得。断云孤鹜青山极。楼上徘徊，无尽相忆。'),
	(6603, 186, '剪牡丹·舟中闻双琵琶', '野绿连空，天青垂水，素色溶漾都净。柳径无人，堕絮飞无影。汀洲日落人归，修巾薄袂，撷香拾翠相竞。如解凌波，泊烟渚春暝。 彩绦朱索新整。宿绣屏、画船风定。金凤响双槽，弹出今古幽思谁省。玉盘大小乱珠迸。酒上妆面，花艳眉相并。重听。尽汉妃一曲，江空月静。'),
	(6604, 186, '木兰花（和孙公素别安陆·般涉调）', '相离徒有相逢梦。门外马蹄尘已动。怨歌留待醉时听，远目不堪空际送。 今宵风月知谁共。声咽琵琶槽上凤。人生无物比多情，江水不深山不重。'),
	(6605, 344, '引驾行·红尘紫陌', '红尘紫陌，斜阳暮草长安道，是离人。断魂处，迢迢匹马西征。新晴。韶光明媚，轻烟淡薄和气暖，望花村。路隐映，摇鞭时过长亭。愁生。伤凤城仙子，别来千里重行行。又记得、临歧泪眼，湿莲脸盈盈。 消凝。花朝月夕，最苦冷落银屏。想媚容、耿耿无眠，屈指已算回程。相萦。空万般思忆，争如归去睹倾城。向绣帏、深处并枕，说如此牵情。'),
	(6606, 344, '长相思（京妓·林钟商）', '画鼓喧街，兰灯满市，皎月初照严城。清都绛阙夜景，风传银箭，露叆金茎。巷陌纵横。过平康款辔，缓听歌声。凤烛荧荧。那人家、未掩香屏。 向罗绮丛中，认得依稀旧日，雅态轻盈。娇波艳冶，巧笑依然，有意相迎。墙头马上，漫迟留、难写深诚。又岂知、名宦拘检，年来减尽风情。'),
	(6607, 344, '少年游（十之四·林钟商）', '世间尤物意中人。轻细好腰身。香帏睡起，发妆酒酽，红脸杏花春。 娇多爱把齐纨扇，和笑掩朱唇。心性温柔，品流详雅，不称在风尘。'),
	(6608, 344, '送征衣·过韶阳', '过韶阳，璿枢电绕，华渚虹流，运应千载会昌。罄寰宇、荐殊祥。吾皇。诞弥月，瑶图缵庆，玉叶腾芳。并景贶、三灵眷祐，挺英哲、掩前王。遇年年、嘉节清和，颁率土称觞。 无间要荒华夏，尽万里、走梯航。彤庭舜张大乐，禹会群方。鹓行。望上国，山呼鳌抃，遥爇炉香。竟就日、瞻云献寿，指南山、等无疆。愿巍巍、宝历鸿基，齐天地遥长。'),
	(6609, 397, '酒泉子·长忆孤山', '长忆孤山，山在湖心如黛簇。僧房四面向湖开。轻棹去还来。 芰荷香喷连云阁。阁上清声檐下铎。别来尘土污人衣。空役梦魂飞。'),
	(6610, 21, '小重山·绿树莺啼春正浓', '绿树莺啼春正浓，钗头青杏小，绿成丛。玉船风动酒鳞红。歌声咽，相见几时重？ 车马去匆匆，路随芳草远，恨无穷。相思只在梦魂中。今宵月，偏照小楼东。'),
	(6611, 188, '菩萨蛮·堵莲红乱风翻雨', '堵莲红乱风翻雨。雨翻风乱红莲渚。深处宿幽禽。禽幽宿处深。 淡妆秋水鉴。鉴水秋妆淡。明月思人情。情人思月明。'),
	(6612, 693, '送舅氏野夫之宣城二首', '藉甚宣城郡，风流数贡毛。 霜林收鸭脚，春网荐琴高。 共理须良守，今年辍省曹。 平生割鸡手，聊试发硎刀。 试说宣城郡，停杯且细听。 晚楼明宛水，春骑簇昭亭。 稏丰圩户，桁杨卧讼庭。 谢公歌舞处，时对换鹅经。'),
	(6613, 28, '御街行·霜风渐紧寒侵被', '霜风渐紧寒侵被。听孤雁、声嘹唳。一声声送一声悲，云淡碧天如水。披衣告语：雁儿略住，听我些儿事。 塔儿南畔城儿里，第三个、桥儿外，濒河西岸小红楼，门外梧桐雕砌。请教且与，低声飞过，那里有、人人无寐。'),
	(6614, 526, '剑器近·夜来雨', '夜来雨。赖倩得、东风吹住。海棠正妖娆处。且留取。 悄庭户。试细听、莺啼燕语。分明共人愁绪。怕春去。 佳树。翠阴初转午。重帘卷，乍睡起、寂寞看风絮。偷弹清泪寄烟波，见江头故人，为言憔悴如许。彩笺无数。去却寒暄，到了浑无定据。断肠落日千山暮。'),
	(6615, 2, '渔家傲', '十里寒塘初过雨。采莲舟上谁家女。秋水接云迷远树。天光暮。船头忘了回来路。 却系兰舟深处住。歌声惊散鸳鸯侣。波上清风花上雾。无计去。月明肠断双栖去。'),
	(6616, 657, '满江红', '揽辔埋轮，算不负、苍髯如戟。从争看、横秋一鹗，轩然健翼。只乎天行日月，寸怀与物同苏息。到干今、天定瘴云开，伊谁力。云霄路，金门客。念往事，情何极。把行藏细说，应无惭色。红气上横牛斗剑，梅花不软心肠石。愿此行、珍重不赀躯，无瑕璧。'),
	(6617, 336, '如梦今', '凉月薄阴犹殢。红豆一枝秋思。门外几重山，山外行人千里。归未。归未。又是菊花天气。'),
	(6618, 143, '声声慢', '秋声淅沥，楚棹吴鞭，相逢易老颜色。桐竹鸣骚音韵，水云空觅。炎凉自今自古，信浮生、有谁禁得。漫回首，问黄花还念，故人犹客。 莫管红香狼藉。兰蕙冷，偏他露知霜识。木落山空心事，对秋明白。征衣暗尘易染，算江湖、随人宽窄。正无据，看寒蟾、飞上暮碧。'),
	(6619, 473, '醉桃源·柳', '千丝风雨万丝晴。年年长短亭。暗黄看到绿成阴。春由他送迎。 莺思重，燕愁轻。如人离别情。绕湖烟冷罩波明。画船移玉笙。'),
	(6620, 630, '瑞鹤仙·梅', '湿云粘雁影。望征路愁迷，离绪难整。千金买光景。但疏钟催晓，乱鸦啼暝。花悰暗省。许多情、相逢梦境。便行云、都不归来，也合寄将音信。 孤迥。明鸾心在，跨鹤程高，后期无淮。情丝待翦。翻惹得，旧时恨。怕天教何处，参差双燕，还染残朱剩粉。对菱花、与说相思，看谁瘦损。'),
	(6621, 637, '望江南', '芳思远，南苑惜春时。翠柳枝柔金笛怨，碧桃花老玉笙悲。风日正迟迟。'),
	(6622, 424, '念奴娇·避地溢江书于新亭', '凭高远望，见家乡、只在白云深处。镇日思归归未得，孤负殷勤杜宇。故国伤心，新亭泪眼，更洒潇潇雨。长江万里，难将此恨流去。 遥想江口依然，鸟啼花谢，今日谁为主。燕子归来，雕梁何处，底事呢喃语。最苦金沙，十万户尽，作血流漂杵。横空剑气，要当一洗残虏。'),
	(6623, 11, '满江红·送廖叔仁赴阙', '日近觚棱，秋渐满、蓬莱双阙。 正钱塘江上，潮头如雪。 把酒送君天上去，琼玉琚玉佩軝鸿列。 丈夫儿、富贵等浮云，看名节。 天下事，吾能说；今老矣，空凝绝。 对西风慷慨，唾壶歌缺。 不洒世间儿女泪，难堪亲友中年别。 问相思、他日镜中看，萧萧发。'),
	(6624, 272, '水调歌头', '富贵有馀乐，贫贱不堪忧。谁知天路幽险，倚伏互相酬。请看东门黄犬，更听华亭清唳，千古恨难收。何似鸱夷子，散发弄扁舟。 鸱夷子，成霸业，有馀谋。致身千乘卿相，归把钓渔钩。春昼五湖烟浪，秋夜一天云月，此外尽悠悠。永弃人间事，吾道付沧洲。'),
	(6625, 433, '高阳台', '红入桃腮，青回柳眼，韶华已破三分。人不归来，空教草怨王孙。平明几点催花雨，梦半阑、欹枕初闻。问东君，因甚将春，老了闲人。 东郊十里香尘满，旋安排玉勒，整顿雕轮。趁取芳时，共寻岛上红云。朱衣引马黄金带，算到头、总是虚名。莫闲愁，一半悲秋，一半伤春。'),
	(6626, 209, '少年游', '含羞倚醉不成歌。纤手掩香罗。偎花映烛，偷传深意，酒思入横波。 看朱成碧心迷乱，翻脉脉、敛双蛾。相见时稀隔别多。又春尽、奈愁何。'),
	(6627, 697, '喜迁莺（端午泛湖）', '梅霖初歇。乍绛蕊海榴，争开时节。角黍包金，香蒲切玉，是处玳筵罗列。斗巧尽输年少，玉腕彩丝双结。舣彩舫，看龙舟两两，波心齐发。 奇绝。难画处，激起浪花，飞作湖间雪。画鼓喧雷，红旗闪电，夺罢锦标方彻。望中水天日暮，犹见朱帘高揭。归棹晚，载荷花十里，一钩新月。'),
	(6628, 97, '锦堂春', '红日迟迟，虚郎转影，槐阴迤逦西斜。彩笔工夫，难状晚景烟霞。蝶尚不知春去，谩绕幽砌寻花。奈猛风过后，纵有残红，飞向谁家。 始知青鬓无价，叹飘零官路，荏苒年华。今日笙歌业里，特地咨嗟。席上青衫湿透，算感旧、何止琵琶。怎不教人易老，多少离愁，散在天涯。'),
	(6629, 514, '好事近·叶暗乳鸦啼', '叶暗乳鸦啼，风定老红犹落。蝴蝶不随春去，入薰风池阁。 休歌金缕劝金卮，酒病煞如昨。帘卷日长人静，任杨花飘泊。'),
	(6630, 152, '鹧鸪天·却月凌风度雪清', '却月凌风度雪清。何郎高咏照花明。一枝弄碧传幽信，半额涂黄拾晚荣。 春思淡，暗香轻。江南雨冷若为情。犹胜远隔潇湘水，忽到窗前梦不成。'),
	(6631, 515, '一翦梅（宿龙游朱氏楼）', '小巧楼台眼界宽。朝卷帘看。暮卷帘看。故乡一望一心酸。云又迷漫。水又迷漫。 天不教人客梦安。昨夜春寒。今夜春寒。梨花月底两眉攒。敲遍阑干。拍遍阑干。'),
	(6632, 366, '望江南·幽州九日', '官舍悄，坐到月西斜。永夜角声悲自语，客心愁破正思家。南北各天涯。 肠断裂，搔首一长嗟。绮席象床寒玉枕，美人何处醉黄花。和泪捻琵琶。'),
	(6633, 124, '献仙音·吊雪香亭梅', '松雪飘寒，岭云吹冻，红破数椒春浅。衬舞台荒，浣妆池冷，凄凉市朝轻换。叹花与人凋谢，依依岁华晚。共凄黯。 共东风、几番吹梦，应惯识当年，翠屏金辇。一片古今愁，但废绿、平烟空远。无语消魂，对斜阳、衰草泪满。又西泠残笛，低送数声春怨。'),
	(6634, 124, '玉京秋·烟水阔', '长安独客，又见西风，素月丹枫凄然其为秋也，因调夹钟羽一解 烟水阔。高林弄残照，晚蜩凄切。碧碪度韵，银床飘叶。衣湿桐阴露冷，采凉花、时赋秋雪。叹轻别。一襟幽事，砌蛩能说。 客思吟商还怯。怨歌长、琼壶暗缺。翠扇恩疏，红衣香褪，翻成消歇。玉骨西风，恨最恨、闲却新凉时节。楚箫咽。谁倚西楼淡月。'),
	(6635, 71, '摸鱼儿·酒边留同年徐云屋', '怎知他、春归何处？相逢且尽尊酒。少年袅袅天涯恨，长结西湖烟柳。休回首，但细雨断桥，憔悴人归后。东风似旧。问前度桃花，刘郎能记，花复认郎否？ 君且住，草草留君翦韭。前宵更恁时候。深杯欲共歌声滑，翻湿春衫半袖。空眉皱，看白发尊前，已似人人有。临分把手。叹一笑论文，清狂顾曲，此会几时又？'),
	(6636, 71, '水调歌头', '百千孙子子，八十老翁翁。人间天下清福，阅世苦难同。谁叹东门猎倦，谁笑南阳舞罢，万事五更钟。但愿人长久，聊复进杯中。 故侯瓜，丞相柏，大夫松。诸公健者安在，春梦转头空。可笑先生无病，病在枕流漱石，福至自然通。聋者固多笑，一笑更治聋。'),
	(6637, 71, '永遇乐·璧月初晴', '辄不自堪。遂依其声，又托之易安自喻。虽辞情不及，而悲苦过之。 璧月初晴，黛云远淡，春事谁主。禁苑娇寒，湖堤倦暖，前度遽如许。香尘暗陌，华灯明昼，长是懒携手去。谁知道，断烟禁夜，满城似愁风雨。 宣和旧日，临安南渡，芳景犹自如故。缃帙流离，风鬓三五，能赋词最苦。江南无路，鄜州今夜，此苦又谁知否。空相对，残红无寐，满村社鼓。'),
	(6638, 112, '思佳客·迷蝶无踪晓梦沉', '迷蝶无踪晓梦沉。寒香深闭小庭心。欲知湖上春多少，但看楼前柳浅深。 愁自遣，酒孤斟。一帘芳景燕同吟。杏花宜带斜阳看，几阵东风晚又阴。'),
	(6639, 112, '忆旧游·别黄澹翁', '送人犹未苦，苦送春、随人去天涯。片红都飞尽，正阴阴润绿，暗里啼鸦。赋情顿雪双鬓，飞梦逐尘沙。叹病渴凄凉，分香瘦减，两地看花。 西湖断桥路，想系马垂杨。依旧欹斜。葵麦迷烟处，问离巢孤燕，飞过谁家。故人为写深怨，空壁扫秋蛇。但醉上吴台，残阳草色归思赊。'),
	(6640, 112, '极相思·题陈藏一水月梅扇', '玉纤风透秋痕。凉与素怀分。乘鸾归后，生绡净翦，一片冰云。 心事孤山春梦在，到思量、犹断诗魂。水清月冷，香消影瘦，人立黄昏。'),
	(6641, 112, '柳梢青·与龟翁登研意观雪怀癸卯岁腊朝断桥并马之游', '断梦游轮。孤山路杳，越树阴新。流水凝酥，征衫沾泪，都是离痕。 玉屏风冷愁人。醉烂漫、梅花翠云。傍夜船回，惜春门掩，一镜香尘。'),
	(6642, 112, '尾犯·甲辰中秋', '绀海掣微云，金井暮凉，梧韵风急。何处楼高，想清光先得。江汜冷、冰绡乍洗，素娥堪，菱花再拭。影留人去，忍向夜深，帘户照陈迹。 竹房苔径小，对日暮、数尽烟碧。露蓼香泾，记年时相识。二十五、声声秋点，梦不认、屏山路窄。醉魂幽扬，满地桂阴无人惜。'),
	(6643, 112, '花心动·柳', '十里东风，袅垂杨、长似舞时腰瘦。翠馆朱楼，紫陌青门，处处燕莺晴昼。乍看摇曳金丝细，春浅映、鹅黄如酒。嫩阴里，烟滋露染，翠娇红溜。 此际雕鞍去久。空追念邮亭，短枝盈首。海角天涯，寒食清明，泪点絮花沾袖。去年折赠行人远，今年恨、依然纤手。断肠也，羞眉画应未就。'),
	(6644, 112, '丑奴儿慢·麓翁飞翼楼观雪', '东风未起，花上纤尘无影。峭云湿，凝酥深坞，乍洗梅清。钓卷愁丝，冷浮虹气海空明。若耶门闭，扁舟去懒，客思鸥轻。 几度问春，倡红冶翠，空媚阴晴。看真色、千岩一素，天澹无情。醒眼重开，玉钩帘外晓峰青。相扶轻醉，越王台上，更最高层。'),
	(6645, 112, '十二郎·垂虹桥', '素天际水，浪拍碎、冻云不凝。记晓叶题霜，秋灯吟雨，曾系长桥过艇。又是宾鸿重来后，猛赋得、归期才定。嗟绣鸭解言，香鲈堪钓，尚庐人境。幽兴。争如共载，越娥妆镜。念倦客依前，貂裘茸帽，重向淞江照影。酹酒苍茫，倚歌平远，亭上玉虹腰冷。迎醉面，暮雪飞花，几点黛愁山暝。'),
	(6646, 112, '惜黄花慢·送客吴皋', '次吴江，小泊，夜饮僧窗惜别。邦人赵簿携小妓侑尊。连歌数阕，皆清真词。酒尽已四鼓。赋此词饯尹梅津。 送客吴皋，正试霜夜冷，枫落长桥。望天不尽，背城渐杳，离亭黯黯，恨永迢迢。翠香零落红衣老，暮愁锁、残柳眉梢。念瘦腰、沈郎旧日，曾系兰桡。仙人凤咽琼箫，怅断魂送远，《九辩》难招。醉鬟留盼，小窗剪烛，歌云载恨，飞上银霄。素秋不解随船去，败红趁一叶寒涛。梦翠翘，怨鸿料过南谯。'),
	(6647, 112, '西子妆慢·湖上清明薄游', '流水麹尘，艳阳醅酒，画舸游情如雾。笑拈芳草不知名，乍凌波、断桥西堍。垂杨漫舞。总不解、将春系住。燕归来，问彩绳纤手，如今何许。 欢盟误。一箭流光，又趁寒食去。不堪衰鬓著飞花，傍绿阴、冷烟深树。玄都秀句。记前度、刘郎曾赋。最伤心、一片孤山细雨。'),
	(6648, 112, '浣溪沙·桂', '曲角深帘隐洞房。正嫌玉骨易愁黄。好花偏占一秋香。 夜气清时初傍枕，晓光分处未开窗。可怜人似月中孀。'),
	(6649, 112, '荔枝香近·送人游南徐', '锦带吴钩，征思横雁水。夜吟敲落霜红，船傍枫桥系。相思不管年华，唤酒吴娃市。因话、驻马新堤步秋绮。 淮楚尾。暮云送、人千里。细雨南楼，香密锦温曾醉。花谷依然，秀靥偷春小桃李。为语梦窗憔悴。'),
	(6650, 112, '宴清都·秋感', '万里关河眼。愁凝处，渺渺残照红敛。天低远树，潮分断港，路回淮甸。吟鞭又指孤店。对玉露金风送晚。恨自古、才子佳人，此景此情多感。 吴王故苑。别来良朋鸦集，空叹蓬转。挥毫记烛，飞觞赶月，梦销香断。区区去程何限。倩片纸、丁宁过雁。寄相思，寒雨灯窗，芙蓉旧院。'),
	(6651, 565, '清平乐·怀人', '莺歌蝶舞。池馆春多处。满架花云留不住。散作一川香雨。 相思夜夜情＿。青衫泪满啼红。料想故园桃李，也应怨月愁风。'),
	(6652, 303, '满江红（招云岩、朔斋于雷园，二公用前雪韵赋梅）', '万紫千红，都不似、玉奴一白。三数萼、有冰霜操，无脂粉色。长共竹君松友伴，岂容蝶使蜂媒入。似惠和、伊任与夷清，兼三德。 能洁己，能娱客。成子后，调羹役。更岁寒风味，时然后出。春浅吹回羌管寸，夜阑吟费花笺尺。炯使星、两两月黄昏，真诗伯。'),
	(6653, 557, '扬州慢', '为二花著语 梁苑吟新，高阳饮散，玉容寂寞妆楼。故人应念我，折赠水晶球。不须倩、东风说与，吹箫云路，解珮江流。似天涯、邂逅相逢，低问东州。 为花更醉，细＿香、酒面酥浮。记桥月同看，帘风共笑，仙枕曾游。无奈乍晴还雨，江天暮、飞絮悠悠。莫先教偷取，春归满地清愁。'),
	(6654, 49, '一翦梅（余赴广东，实之夜饯于风亭）', '束缊宵行十里强。挑得诗囊。抛了衣囊。天寒路滑马蹄僵。元是王郎。来送刘郎。 酒酣耳热说文章。惊倒邻墙。推倒胡床。旁观拍手笑疏狂。疏又何妨。狂又何妨。'),
	(6655, 382, '眼儿媚·平沙芳草渡头村', '平沙芳草渡头村。绿遍去年痕。游丝下上，流莺来往，无限销魂。 绮窗深静人归晚，金鸭水沈温。海棠影下，子规声里，立尽黄昏。'),
	(6656, 91, '留春令·咏梅花', '故人溪上，挂愁无奈，烟梢月树。一涓春水点黄昏，便没顿、相思处。 曾把芳心深相许。故梦劳诗苦。闻说东风亦多情，被竹外、香留住。'),
	(6657, 149, '玉梅令', '曰范村，梅开雪落，竹院深静，而石湖畏寒不出，故戏及之 疏疏雪片。散入溪南苑。春寒锁、旧家亭馆。有玉梅几树，背立怨东风，高花未吐，暗香已远。 公来领略，梅花能劝。花长好、愿公更健。便揉春为酒，翦雪作新诗，拚一日、绕花千转。'),
	(6658, 149, '杏花天', '小舟挂席，容与波上 绿丝低拂鸳鸯浦。想桃叶、当时唤渡。又将愁眼与春风，待去。倚兰桡、更少驻。 金陵路。莺吟燕舞。算潮水、知人最苦。满汀芳草不成归，日暮。更移舟、向甚处。'),
	(6659, 215, '八声甘州（中秋夜作）', '叹流光迅景，百年间、能醉几中秋。正凄蛩响砌，惊乌翻树，烟淡苹洲。谁唤金轮出海，不带一云浮。才上青林顶，俄转朱楼。 人老欢情已减，料素娥信我，不为闲愁。念几番清梦，常是故乡留。倩风前、数声横管，叫玉鸾、骑向碧空游。谁能顾，黍炊荣利，蚁战仇雠。'),
	(6660, 639, '贺新郎·酬辛幼安再用韵见寄', '离乱从头说。爱吾民、金缯不爱，蔓藤累葛。壮气尽消人脆好，冠盖阴山观雪。亏杀我、一星星发。涕出女吴成倒转，问鲁为齐弱何年月。丘也幸，由之瑟。 斩新换出旗麾别。把当时、一椿大义，拆开收合。据地一呼吾往矣，万里摇肢动骨。这话霸、又成痴绝。天地洪炉谁扇鞲，算於中、安得长坚铁。淝水破，关东裂。'),
	(6661, 456, '摸鱼儿·掩凄凉黄昏庭院', '掩凄凉、黄昏庭院，角声何处呜咽。矮窗曲屋风灯冷，还是苦寒时节。凝伫切。念翠被熏笼，夜夜成虚设。倚阑愁绝。听凤竹声中，犀影帐外，簌簌酿寒轻雪。 伤心处，却忆当年轻别。梅花满院初发。吹香弄蕊无人见，惟有暮云千叠。情未彻。又谁料而今，好梦分胡越。不堪重说。但记得当初，重门锁处，犹有夜深月。'),
	(6662, 591, '水调歌头', '赋水调歌头以遗之。然君才气不减流辈，岂求田问舍而独乐其身耶 文字觑天巧，亭榭定风流。平生丘壑，岁晚也作稻梁谋。五亩园中秀野，一水田将绿绕，罢稏不胜秋。饭饱对花竹，可是便忘忧。 吾老矣，探禹穴，欠东游。君家风月几许，白鸟去悠悠。插架牙签万轴，射虎南山一骑，容我揽须不。更欲劝君酒，百尺卧高楼。'),
	(6663, 591, '卜算子（饮酒不写书）', '一饮动连宵，一醉长三日。废尽寒暄不写书，富贵何由得。 请看冢中人，冢似当时笔。万札千书只恁休，且进杯中物。'),
	(6664, 591, '西江月（三山作）', '贪数明朝重九。不知过了中秋。人生有得许多愁。惟有黄花如旧。 万象亭中＿酒。九江阁上扶头。城鸦唤我醉归休。细雨斜风时候。'),
	(6665, 591, '贺新郎', '梁、曰齐泽、曰严濑，为四图，属予赋词。予谓蜀君平之高，扬子云所谓虽隋和何以加诸者，班孟坚独取子云所称述为王贡诸传序引，不敢以其姓名列诸传，尊之也。故予谓和之当并图君平像，置之四图之间，庶几严氏之高节者备焉。作乳燕飞词使歌之 濮上看垂钓。更风流、羊裘泽畔，精神孤矫。楚汉黄金公卿印，比著渔竿谁小。但过眼、才堪一笑。惠子焉知濠梁乐，望桐江、千丈高台好。烟雨外，几鱼鸟。 古来如许高人少。细平章、两翁似与，巢由同调。已被尧知方洗耳，毕竟尘污人了。要名字、人间如扫。我爱蜀庄沈冥者，解门前、不使徵书到。君为我，画三老。'),
	(6666, 591, '清平乐（谢叔良惠木犀）', '少年痛饮。忆向吴江醒。明月团圆高树影。十里蔷薇水冷。 大都一点宫黄。人间直恁芳芬。怕是九天风露，染教世界都香。'),
	(6667, 591, '浣溪沙（赋清虚）', '疆欲加餐竟未佳。只宜长伴病僧斋。心似风吹香篆过，也无灰。 山上朝来云出岫，随风一去未曾回。次第前村行雨了，合归来。'),
	(6668, 591, '破阵子（硖石道中有怀吴子似县慰）', '宿麦畦中雉＿，柔桑陌上蚕生，骑火须防花月暗，玉唾长携彩笔行。隔墙人笑声。 莫说弓刀事业，依然诗酒功名。千载图中今古事，万石溪头长短亭。小塘风浪平。'),
	(6669, 591, '念奴娇·和赵国兴知录韵', '为沽美酒，过溪来、谁道幽人难致。更觉元龙楼百尺，湖海平生豪气。自叹年来，看花索句，老不如人意。东风归路，一川松竹如醉。 怎得身似庄周，梦中蝴蝶，花底人间世。记取江头三月暮，风雨不为春计。万斛愁来，金貂头上，不抵银瓶贵。无多笑我，此篇聊当宾戏。'),
	(6670, 426, '清平乐', '儿曹耳语。借问何处去。家在翠微深处住。生计一犁春雨。 客中且恁浮游。莫将事挂心头。纵使人生满百，算来更几春秋。'),
	(6671, 106, '瑞鹤仙', '人多於其上藉草酌酒歌乐 残梅飘簌簌。看柳上春归，柔条新绿。娇莺离幽谷。弄弹簧清响，飞迁乔木。年华妞迅速。叹浮生、流晖转烛。自春来、每每遨游，多办九霞＿醁。 溪北。踏青微步，斗草慵眠，锦裀花褥。铅华簇簇。歌声妙、间丝竹。爱一川好处，高山流水，不减城南杜曲。笑平生、卓地无锥，老来富足。'),
	(6672, 188, '卜算子', '万里去担簦，谁识新丰旅。好事些儿说与郎，奴是姮娥侣。 若到广寒宫，但道奴传语。待我仙郎折桂枝，拣个高枝与。'),
	(6673, 498, '满江红·清江风帆甚快作此与客剧饮歌之', '千古东流，声卷地，云涛如屋。横浩渺、樯竿十丈，不胜帆腹。夜雨翻江春浦涨，船头鼓急风初熟。似当年、呼禹乱黄川，飞梭速。 击楫誓、空警俗。休拊髀，都生肉。任炎天冰海，一杯相属。荻笋蒌芽新入馔，弦凤吹能翻曲。笑人间、何处似尊前，添银烛。'),
	(6674, 633, '朝中措', '长安拜免几公卿。渔父横眠醉未醒。烟艇小，钓车腥。遥指梅山一点青。'),
	(6675, 633, '青玉案·与朱景参会北岭', '西风挟雨声翻浪。恰洗尽、黄茅瘴。老惯人间齐得丧。千岩高卧，五湖归棹，替却凌烟像。 故人小驻平戎帐，白羽腰间气何壮。我老渔樵君将相。小槽红酒，晚香丹荔，记取蛮江上。'),
	(6676, 573, '点绛唇·途中逢管倅', '憔悴天涯，故人相遇情如故。别离何遽。忍唱阳关句。 我是行人，更送行人去。愁无据。寒蝉鸣处。回首斜阳暮。'),
	(6677, 271, '鹊桥仙（七夕）', '巧云妆晚，西风罢暑，小雨翻空月坠。牵牛织女几经秋，尚多少、离肠恨泪。 微凉入袂，幽欢生座，天上人间满意。何如暮暮与朝朝，更改却、年年岁岁。'),
	(6678, 254, '武陵春', '春到小园春草绿，烟雨湿云山。池上梅花已半残。无奈晚来寒。 不怕醉多只怕醒，花影上阑干。人在东风缥缈间。谁与伴幽闲。'),
	(6679, 332, '柳梢青·茅舍疏篱', '茅舍疏篱。半飘残雪，斜卧低枝。可更相宜，烟笼修竹，月在寒溪。 宁宁伫立移时。判瘦损、无妨为伊。谁赋才情，画成幽思，写入新诗。'),
	(6680, 296, '醉花阴', '翠箔阴阴笼画阁。昨夜东风恶。香迳漫春泥，南陌东郊，惆怅妨行乐。 伤春比似年时觉。潘鬓新来薄。何处不禁愁，雨滴花腮，和泪胭脂落。'),
	(6681, 518, '虞美人', '飞梁石径关山路。惨淡秋容暮。一行新雁破寒空。肠断碧云千里、水溶溶。 鸾衾欲展谁堪共。帘幕霜华重。鸭炉香尽锦屏中。幽梦今宵何许、与君同。'),
	(6682, 518, '苏武慢', '雁落平沙，烟笼寒水，古垒鸣笳声断。青山隐隐，败叶萧萧，天际暝鸦零乱。楼上黄昏，片帆千里归程，年华将晚。望碧云空暮，佳人何处，梦魂俱远。 忆旧游、邃馆朱扉，小园香径，尚想桃花人面。书盈锦轴，恨满金徽，难写寸心幽怨。两地离愁，一尊芳酒，凄凉危栏倚遍。尽迟留，凭仗西风，吹干泪眼。'),
	(6683, 589, '浪淘沙（九日会饮分得雁字）', '霜露日凄凉。北雁南翔。惊风吹起不成行。吊影苍波何限恨，日暮天长。 为尔惜流光。还是重阳。故人何处舣危樯。寄我相思千点泪，直过潇湘。'),
	(6684, 315, '苏武令·塞上风高', '塞上风高，渔阳秋早。惆怅翠华音杳。驿使空驰，征鸿归尽，不寄双龙消耗。念白衣、金殿除恩，归黄阁、未成图报。 谁信我、致主丹衷，伤时多故，未作救民方召。调鼎为霖，登坛作将，燕然即须平扫。拥精兵十万，横行沙漠，奉迎天表。'),
	(6685, 545, '蝶恋花·豆蔻梢头春色浅', '豆蔻梢头春色浅。新试纱衣，拂袖东风软。红日三竿帘幕卷。画楼影里双飞燕。 拢鬓步摇青玉碾。缺样花枝，叶叶蜂儿颤。独倚阑干凝望远。一川烟草平如剪。'),
	(6686, 130, '氐州第一·波落寒汀', '波落寒汀，村渡向晚，遥看数点帆小。乱叶翻鸦，惊风破雁，天角孤云缥缈。官柳萧疏，甚尚挂、微微残照？景物关情，川途换目，顿来催老。 渐解狂朋欢意少，奈犹被、丝牵情绕。座上琴心，机中锦字，觉最萦怀抱。也知人、悬望久，蔷薇谢，归来一笑。欲梦高唐，未成眠、霜空已晓。'),
	(6687, 130, '浣溪沙·争挽桐花两鬓垂', '争挽桐花两鬓垂。小妆弄影照清池。出帘踏袜趁蜂儿。 跳脱添金双腕重，琵琶拨尽四弦悲。夜寒谁肯剪春衣。'),
	(6688, 549, '江城子', '麝熏微度绣芙容。翠衾重。画堂空。前夜偷期，相见却匆匆。心事两知何处问，依约是，梦中逢。 坐疑行听竹窗风。出帘栊。杳无踪。已过黄昏，才动寺楼钟。暮雨不来春又去，花满地，月朦胧。'),
	(6689, 451, '望海潮（四之一）', '星分牛斗，疆连淮海，扬州万井提封。花发路香，莺啼人起，珠帘十里东风。豪俊气如虹。曳照春金紫，飞盖相从。巷入垂杨，画桥南北翠烟中。 追思故国繁雄。有迷楼挂斗，月观横空。纹锦制帆，明珠溅雨，宁论爵马鱼龙。往事逐孤鸿。但乱云流水，萦带离宫。最好挥毫万字，一饮拚千钟。'),
	(6690, 549, '阳羡歌·山秀芙蓉', '山秀芙蓉，溪明罨画。真游洞穴沧波下。临风慨想斩蛟灵。长桥千载犹横跨。 解佩投簪，求田问舍。黄鸡白酒渔樵社。元龙非复少时豪，耳根洗尽功名话。'),
	(6691, 494, '渔父', '渔父笑，轻鸥举。漠漠一江风雨。江边骑马是官人，借我孤舟南渡。'),
	(6692, 494, '虞美人', '深深庭院清明过。桃李初红破。柳丝搭在玉阑干。帘外潇潇微雨、做轻寒。 晚晴台榭增明媚。已拚花前醉。更阑人静月侵廊。独自行来行去、好思量。'),
	(6693, 494, '浣溪沙·照日深红暖见鱼', '照日深红暖见鱼。连溪绿暗晚藏乌。黄童白叟聚睢盱。 麋鹿逢人虽未惯，猿猱闻喜不须呼。归家说与采桑姑。'),
	(6694, 494, '减字木兰花·送赵令', '春光亭下。流水如今何在也。岁月如梭。白首相看拟奈何。 故人重见。世事年来千万变。官况阑珊。惭愧青松守岁寒。'),
	(6695, 494, '苏幕遮（咏选仙图）', '暑笼晴，风解愠。雨后余清，暗袭衣裾润。一局选仙逃暑困。笑指尊前、谁向青霄近。 整金盆，轮玉笋。凤驾鸾车，谁敢争先进。重五休言升最紧。纵有碧油，到了输堂印。'),
	(6696, 210, '江神子·癸亥陈和叔会于赏心亭', '七朝文物旧江山。水如天。莫凭栏。千古斜阳，无处问长安。更隔秦淮闻旧曲，秋已半，夜将阑。 争教潘鬓不生斑。敛芳颜。抹么弦。须记琵琶，子细说因缘。待得鸾胶肠已断，重别日，是何年。'),
	(6697, 250, '六么令·雪残风信', '雪残风信，悠扬春消息。天涯倚楼新恨，杨柳几丝碧。还是南云雁少，锦字无端的。宝钗瑶席。彩弦声里，拚作尊前未归客。 遥想疏梅此际，月底香英白。别后谁绕前溪，手拣繁枝摘。莫道伤高恨远，付与临风笛。尽堪愁寂。花时往事，更有多情个人忆。'),
	(6698, 250, '六幺令·绿阴春尽', '绿阴春尽，飞絮绕香阁。晚来翠眉宫样，巧把远山学。一寸狂心未说，已向横波觉。画帘遮匝，新翻曲妙，暗许闲人带偷掐。 前度书多隐语，意浅愁难答。昨夜诗有回文，韵险还慵押。都待笙歌散了，记取来时霎。不消红蜡，闲云归后，月在庭花旧栏角。(栏角 一作：阑)'),
	(6699, 250, '玉楼春', '斑骓路与阳台近。前度无题初借问。暖风鞭袖尽闲垂，微月帘栊曾暗认。 梅花未足凭芳信。弦语岂堪传素恨。翠眉饶似远山长，寄与此愁颦不尽。'),
	(6700, 250, '菩萨蛮', '相逢欲话相思苦。浅情肯信相思否。还恐漫相思。浅情人不知。 忆曾携手处。月满窗前路。长到月来时。不眠犹待伊。'),
	(6701, 250, '减字木兰花·留春不住', '留春不住。恰似年光无味处。满眼飞英。弹指东风太浅情。 筝弦未稳。学得新声难破恨。转枕花前。且占香红一夜眠。'),
	(6702, 352, '雨中花', '千古都门行路。能使离歌声苦。送尽行人，花残春晚，又到君东去。 醉藉落花吹暖絮。多少曲堤芳树。且携手留连，良辰美景，留作相思处。'),
	(6703, 251, '渔家傲', '越女采莲江北岸。轻桡短棹随风便。人貌与花相斗艳。流水慢，时时照影看妆面。 莲叶层层张绿伞。莲房个个垂金盏。一把藕丝牵不断。红日晚。回头欲去心撩乱。'),
	(6704, 251, '木兰花·玉楼朱阁横金锁', '玉楼朱阁横金锁，寒食清明春欲破。窗间斜月两眉愁，帘外落花双泪堕。 朝云聚散真无那，百岁相看能几个。别来将为不牵情，万转千回思想过。'),
	(6705, 251, '浣溪沙', '杨柳阴中驻彩旌。芰荷香里劝金觥。小词流入管弦声。 只有醉吟宽别恨，不须朝暮促归程。雨条烟叶系人情。'),
	(6706, 186, '江南柳·隋堤远', '隋堤远，波急路尘轻。今古柳桥多送别，见人分袂亦愁生。何况自关情。 斜照后，新月上西城。城上楼高重倚望，愿身能似月亭亭，千里伴君行。'),
	(6707, 344, '少年游（十之八·林钟商）', '一生赢得是凄凉。追前事、暗心伤。好天良夜，深屏香被。争忍便相忘。 王孙动是经年去，贪迷恋、有何长。万种千般，把伊情分，颠倒尽猜量。'),
	(6708, 344, '诉衷情近·雨晴气爽', '雨睛气爽，伫立江楼望处。澄明远水生光，重叠暮山耸翠。遥认断桥幽径，隐隐渔村，向晚孤烟起。 残阳里。脉脉朱阑静倚。黯然情绪，未饮先如醉。愁无际。暮云过了，秋光老尽，故人千里。竟日空凝睇。'),
	(6709, 344, '醉蓬莱·渐亭皋叶下', '渐亭皋叶下，陇首云飞，素秋新霁。华阙中天，锁葱葱佳气。嫩菊黄深，拒霜红浅，近宝阶香砌。玉宇无尘，金茎有露，碧天如水。 正值升平，万几多暇，夜色澄鲜，漏声迢递。南极星中，有老人呈瑞。此际宸游，凤辇何处，度管弦清脆。太液波翻，披香帘卷，月明风细。'),
	(6710, 28, '采桑子·年年才到花时候', '年年才到花时候，风雨成旬。不肯开晴，误却寻花陌上人。 今朝报道天晴也，花已成尘。寄语花神，何似当初莫做春。'),
	(6711, 675, '蝶恋花·春江暖涨桃花水', '陈浩然招游观音山，宴张氏楼。徐姬楚兰佐酒，以琵琶度曲。郯云台为之心醉。口占戏之。 春江暖涨桃花水。画舫珠帘，载酒东风里。四面青山青似洗，白云不断山中起。 过眼韶华浑有几。玉手佳人，笑把琶琶理。枉杀云台标内史，断肠只合江州死。'),
	(6712, 164, '朝中措·襄阳古道灞陵桥', '襄阳古道灞陵桥，诗兴与秋高。千古风流人物，一时多少雄豪。 霜清玉塞，云飞陇首，风落江皋。梦到凤凰台上，山围故国周遭。'),
	(6713, 357, '渔家傲·诗句一春浑漫与', '诗句一春浑漫与，纷纷红紫但尘土。楼外垂杨千万缕，风落絮，栏干倚遍空无语。 毕竟春归何处所，树头树底无寻处。唯有闲愁将不去，依旧住，伴人直到黄昏雨。'),
	(6714, 28, '鹧鸪天·月满蓬壶灿烂灯', '月满蓬壶灿烂灯，与郎携手至端门。贪看鹤阵笙歌举，不觉鸳鸯失却群。 天渐晓，感皇恩。传宣赐酒饮杯巡。归家恐被翁姑责，窃取金杯作照凭。'),
	(6715, 422, '庆清朝·榴花', '玉局歌残，金陵句绝，年年负却熏风。西邻窈窕，独怜入户飞红。前度绿阴载酒，枝头色比舞裙同。何须拟，蜡珠作蒂，缃彩成丛。 谁在旧家殿阁？自太真仙去，扫地春空。朱幡护取，如今应误花工。颠倒绛英满径，想无车马到山中。西风后，尚余数点，犹胜春浓。'),
	(6716, 520, '喜迁莺·霜天秋晓', '霜天秋晓，正紫塞故垒，黄云衰草。汉马嘶风，边鸿叫月，陇上铁衣寒早。剑歌骑曲悲壮，尽道君恩须报。塞垣乐，尽櫜鞬锦领，山西年少。 谈笑。刁斗静，烽火一把，时送平安耗。圣主忧边，威怀遐远，骄虏尚宽天讨。岁华向晚愁思，谁念玉关人老？太平也，且欢娱，莫惜金樽频倒。'),
	(6717, 504, '虞美人', '朱楼曾记回娇盼。满坐春风转。红潮生面酒微醺。一曲清歌留往、半窗云。 大都咫尺无消息。望断青鸾翼。夜长香短烛花红。多少思量只在、雨声中。'),
	(6718, 422, '醉蓬莱·归故山', '扫西风门径，黄叶凋零，白云萧散。柳换枯阴，赋归来何晚。爽气霏霏，翠蛾眉妩，聊慰登临眼。故国如尘，故人如梦，登高还懒。 数点寒英，为谁零落，楚魄难招，暮寒堪揽。步屟荒篱，谁念幽芳远。一室秋灯，一庭秋雨，更一声秋雁。试引芳樽，不知消得，和多依黯。'),
	(6719, 236, '齐天乐（庆湖北漕知鄂州李楼峰）', '南楼月转银河曙，玉箫又吹梅早。鹦鹉沙晴，葡萄水暖，一缕燕香清袅。瑶池春透。想桃露霏霞，菊波沁晓。袍锦风流，御仙花带瑞虹绕。 玉关人下未老。唤矶头黄鹤，岸巾谈笑。剑拂准清，槊横楚黛，雨洗一川烟草。印黄似斗。看半砚蔷薇，满鞍杨柳。沙路归来，金貂蝉翼小。'),
	(6720, 583, '渔家傲', '深意缠绵歌宛转。横波停眼灯前见。最忆来时门半掩。春不暖。梨花落尽成秋苑。叠鼓收声帆影乱。燕飞又趁东风软。目力漫长心力短。消息断。青山一点和烟远。'),
	(6721, 207, '酹江月·淮城感兴', '举杯呼月，问神京何在，淮山隐隐。抚剑频看勋业事，惟有孤忠挺挺。宫阙腥膻，衣冠沦没，天地凭谁整。一枰棋坏，救时著数宜紧。 虽是幕府文书，玉关烽火，暂送平安信。满地干戈犹未戢，毕竟中原谁定。便欲凌空，飘然直上，拂拭山河影。倚风长啸，夜深霜露凄冷。'),
	(6722, 228, '莺啼序', '荼コ一番过雨，渍残花似雪。向清晓、步入东风，细拾苔砌馀靥。有数片、飞沾翠柳，萦回半著双归蝶。悄无人、共立幽禽，呢呢能说。 因念年华，最苦易失，对春愁暗结。欢自古、曾有佳人，长门深闭修洁。寄么弦、千言万语，闷满眼、欲弹难彻。靠珠珑，风雨微收，落花时节。 春工渐老，绿草连天，别浦共一色。但暮霭、朝烟无际，尽日目极，江北江南，杜鹃叫裂。此时此意，危魂黯黯，渭城客舍青青树，问何人、把酒来看别。思量怎向，迟回独掩青扉，夕阳犹照南陌。 春应记得，旧日疏狂，等受今磨折。便永谢、五湖烟艇，只有吟诗，曲坞煎茶，小窗眠月。春还自省，把融和事，长留芳书人间世，与羁臣、恨妾销离侧。自题蕙叶回春，坐听蓬壶，漏声细咽。'),
	(6723, 187, '水调歌', '石屋势平旷，峭壁几巉岩。妙哉天造地设，谁复谓神剜。畴昔涪翁题品，曾说人寰稀有，岂特冠湘南。趁取脚轻健，相与上高寒。 避秦者，君莫问，意其间。祖龙文密，至今草木尚愁颜。赢得功成丹鼎，久矣乘风而去，跨鹤与参鸾。犹有白云在，锁日绕禅关。'),
	(6724, 127, '木兰花慢·送人之官九华', '霭芳阴未解，乍天气、过元宵。讶客神犹寒，吟窗易晓，春色无柳。梅梢。尚留顾藉，滞东风、未肯雪轻飘。知道诗翁欲去，递香要送兰桡。清标。会上丛霄。千里阻、九华遥。料今朝别后，他时有梦，应梦今朝。河桥。柳愁未醒，赠行人、又恐越魂销。留取归来紧马，翠长千缕柔条。'),
	(6725, 10, '醉桃源·春景', '拍堤春水蘸垂杨。水流花片香。弄花喈柳小鸳鸯。一双随一双。 帘半卷，露新妆。春衫是柳黄。倚阑看处背斜阳。风流暗断肠。'),
	(6726, 257, '西河·和王潜斋韵', '今日事。何人弄得如此。漫漫白骨蔽川原，恨何日已。关河万里寂无烟，月明空照芦苇。 谩哀痛，无及矣。无情莫问江水。西风落日惨新亭，几人坠泪。战和何者是良筹，扶危但看天意。 只今寂寞薮泽里。岂无人、高卧闾里。试问安危谁寄。定相将、有诏催公起。须信前书言犹未。'),
	(6727, 31, '卜算子', '翦烛写香笺，拨火温寒醑。门外东风将我愁，欲作三更雨。 夜夜玉楼心，日日长亭路。豆蔻花开信不来，尘满金钗股。'),
	(6728, 338, '洞仙歌', '飞梁压水，虹影澄清晓。橘里渔村半烟草。今来古往，物是人非，天地里，唯有江山不老。 雨巾风帽。四海谁知我。一剑横空几番过。按玉龙、嘶未断，月冷波寒，归去也、林屋洞天无锁。认云屏烟障是吾庐，任满地苍苔，年年不扫。'),
	(6729, 272, '水调歌头（联句问讯罗汉同张敬夫）', '雪月两相映，水石互悲鸣。不知岩上枯木，今夜若为情。应见尘土胶扰，便道山间空旷，与么了平生。与么平生了，□水不流行。熹起披衣，瞻碧汉，露华清。寥寥千载，此事本分明。若向乾坤识易，便信行藏无间，处处总圆成。记取渊冰语，莫错定盘星。'),
	(6730, 495, '调啸词（效韦苏州·二之一）', '渔父。渔父。水上微风细雨。青蓑黄箬裳衣。红酒白鱼暮归。暮归、暮归、归暮。长笛一声何处。'),
	(6731, 682, '定风波', '不是无心惜落花。落花无意恋春华。昨日盈盈枝上笑。谁道。今朝吹去落谁家。 把酒临风千种恨。难问。梦回云散见无涯。妙舞清歌谁是主。回顾。高城不见夕阳斜。'),
	(6732, 433, '清平乐·黄金殿里', '黄金殿里，烛影双龙戏。劝得官家真个醉，进酒犹呼万岁。 折旋舞彻《伊州》。君恩与整搔头。一夜御前宣住，六宫多少人愁。'),
	(6733, 33, '阮郎归', '钓鱼船上谢三郎。双鬓已苍苍。蓑衣未必清贵，不肯换金章。 汀草畔，浦花旁。静鸣榔。自来好个，渔父家风，一片潇湘。'),
	(6734, 672, '点绛唇·病起恹恹', '病起恹恹、画堂花谢添憔悴。乱红飘砌。滴尽胭脂泪。 惆怅前春，谁向花前醉。愁无际。武陵回睇。人远波空翠。'),
	(6735, 643, '一络索·送蜀守蒋龙图', '蜀江春色浓如雾。拥双旌归去。海棠也似别君难，一点点、啼红雨。此去马蹄何处。沙堤新路。禁林赐宴赏花时，还忆著、西楼否。'),
	(6736, 428, '望江南（六·江景）', '江南雨，风送满长川。碧瓦烟昏沈柳岸，红绡香润入梅天。飘洒正潇然。朝与暮，长在楚峰前。寒夜愁欹金带枕，暮江深闭木兰船。烟浪远相连。'),
	(6737, 162, '浪淘沙近', '少年不管。流光如箭。因循不觉韶光换。至如今，始惜月满、花满、酒满。 扁舟欲解垂杨岸。尚同欢宴。日斜歌阕将分散。倚兰桡，望水远、天远、人远。'),
	(6738, 474, '多丽·李良定公席上赋', '想人生，美景良辰堪惜。问其间、赏心乐事，就中难是并得。况东城、凤台沙苑，泛晴波、浅照金碧。露洗华桐，烟霏丝柳，绿阴摇曳，荡春一色。画堂迥、玉簪琼佩，高会尽词客。清欢久、重然绛蜡，别就瑶席。 有翩若轻鸿体态，暮为行雨标格。逞朱唇、缓歌妖丽，似听流莺乱花隔。慢舞萦回，娇鬟低亸，腰肢纤细困无力。忍分散、彩云归后，何处更寻觅。休辞醉，明月好花，莫谩轻掷。'),
	(6739, 139, '鹧鸪天·镇日无心扫黛眉', '镇日无心扫黛眉。临行愁见理征衣。尊前只恐伤郎意，阁泪汪汪不敢垂。停宝马，捧瑶卮。相斟相劝忍分离。不如饮待奴先醉，图得不知郎去时。'),
	(6740, 167, '阳关引', '塞草烟光阔，渭水波声咽。春朝雨霁轻尘歇。征鞍发。指青青杨柳，又是轻攀折。动黯然，知有后会甚时节。更尽一杯酒，歌一阕。叹人生，最难欢聚易离别。且莫辞沉醉，听取阳关彻。念故人，千里自此共明月。'),
	(6741, 43, '点绛唇', '眉黛低颦，一声春满流苏帐。却从檀响。渐到梅花上。 归卧孤舟，梅影舟前飏。劳心想。岸横千嶂。霜月铺寒浪。'),
	(6742, 321, '忆王孙（冬词）', '彤云风扫雪初晴。天外孤鸿三两声。独拥寒衾不忍听。月笼明。窗外梅花瘦影横。'),
	(6743, 436, '渔家傲', '日月无根天不老。浮生总被消磨了。陌上红尘常扰扰。昏复晓。一场大梦谁先觉。 雒水东流山四绕。路傍几个新华表。见说在时官职好。争信道。冷烟寒雨埋荒草。'),
	(6744, 98, '黄金缕·家在钱塘江上住', '家在钱塘江上住。花落花开，不管年华度。燕子又将春色去。纱窗一阵黄昏雨。（家在 一作：妾本） 斜插犀梳云半吐。檀板清歌，唱彻黄金缕。望断云行无去处。梦回明月生春浦。'),
	(6745, 202, '凤凰台上忆吹箫', '味凄然。以其号孤篷，嘱余赋之。 水国浮家，渔村古隐，浪游惯占花深。犹记得、琵琶半面，曾湿衫青。不道江空岁晚，桃叶渡、还叹飘零。因乘兴，醉梦醒时，却是山阴。 投闲倦呼俦侣，竟棹入芦花，俗客难寻。风渺渺、云拖暮雪，独钓寒清。远溯流光万里，浑错认、叶竹寰瀛。元来是、天上太乙真人。'),
	(6746, 202, '南浦（春水）', '波暖绿粼粼，燕飞来、好是苏堤才晓。鱼没浪痕圆，流红去、翻笑东风难扫。荒桥断浦，柳阴撑出扁舟小。回首池塘青欲遍，绝似梦中芳草。 和云流出空山，甚年年净洗，花香不了。新渌乍生时，孤村路、犹忆那回曾到。余情渺渺。茂林觞咏如今悄。前度刘郎归去后，溪上碧桃多少。'),
	(6747, 515, '少年游', '梨边风紧雪难晴。千点照溪明。吹絮窗低，唾茸窗小，人隔翠阴行。 而今白鸟横飞处，烟树渺乡城。两袖春寒，一襟春恨，斜日淡无情。'),
	(6748, 515, '秋夜雨·秋夜', '黄云水驿秋笳噎。吹人双鬓如雪。愁多无奈处，谩碎把、寒花轻撧。 红云转入香心里，夜渐深、人语初歇。此际愁更别。雁落影、西窗斜月。'),
	(6749, 515, '沁园春（次强云卿韵）', '结算平生，风流债负，请一笔句。盖攻性之兵，花围锦阵，毒身之鸩，笑齿歌喉。岂识吾儒，道中乐地，绝胜珠帘十里楼。迷因底，叹晴干不去，待雨淋头。 休休。著甚来由。硬铁汉从来气食牛。但只有千篇，好诗好曲，都无半点，闲闷闲愁。自古娇波，溺人多矣，试问还能溺我不。高抬眼，看牵丝傀儡，谁弄谁收。'),
	(6750, 422, '花犯·苔梅', '古婵娟，苍鬟素靥，盈盈瞰流水。断魂十里。叹绀缕飘零，难系离思。故山岁晚谁堪寄。琅玕聊自倚。谩记我、绿蓑冲雪，孤舟寒浪里。 三花两蕊破蒙茸，依依似有恨，明珠轻委。云卧稳，蓝衣正、护春憔悴。罗浮梦、半蟾挂晓，么凤冷、山中人乍起。又唤取、玉奴归去，余香空翠被。'),
	(6751, 71, '如梦令', '寂历柳风斜倚。错莫梦云难记。花影为谁重，一握鲛人丝泪。何事。何事。历历脸潮羞起。托腮'),
	(6752, 641, '虞美人', '春衫薄薄寒犹恋。芳草连天远。嫩红和露入桃腮。柳外东边楼阁、燕飞来。 霓裳一曲凭谁按。错□□重看。金虬闲暖麝檀煤。银烛替人垂泪、共心灰。'),
	(6753, 641, '红林擒近·寿词·满路花', '三万六千顷，玉壶天地寒。 庾岭封的皪，淇园折琅玕。 漠漠梨花烂漫，纷纷柳絮飞残。 直疑潢潦惊翻，斜风溯狂澜。 对此频胜赏，一醉饱清欢。 呼?童翦韭，和冰先荐春盘。 怕东风吹散，留尊待月，倚阑莫惜今夜看。'),
	(6754, 641, '定风波', '慵拂妆台懒画眉。此情惟有落花知。流水悠悠春脉脉，闲倚绣屏，犹自立多时。 有约莫教莺解语，多愁却妒燕于飞。一笑蔷薇孤旧约，载酒寻欢，因甚懒支持。'),
	(6755, 641, '清平乐·凤城春浅', '凤城春浅。寒压花梢颤。有约不来梁上燕。十二绣帘空卷。 去年共倚秋千。今年独倚阑干。误了海棠时候，不成直待花残。'),
	(6756, 640, '沁园春（次韵林南金赋愁）', '抚剑悲歌，纵有杜康，可能解忧。为修名不立，此身易老，古心自许，与世多尤。平子诗中，庾生赋里，满目江山无限愁。关情处，是闻鸡半夜，击楫中流。 淡烟衰草连秋。听鸣鴂声声相应酬。叹霸才重耳，泥涂在楚，雄心元德，岁月依刘。梦落莼边，神游菊外，已分他年专一丘。长安道，且身如王粲，时复登楼。'),
	(6757, 640, '沁园春', '石城之胜，班班在目，而平淮如席，亦横陈樽俎间。既而北历淮山，自齐安溯江泛湖，薄游巴陵，又得登岳阳楼，以尽荆州之伟观，孙刘虎视遗迹依然，山川草木，差强人意。洎回京师，日诣丰乐楼以观西湖。因诵友人“东南妩媚，雌了男儿”之句，叹息者久之。酒酣，大书东壁，以写胸中之勃郁。时嘉熙庚子秋季下浣也 记上层楼，与岳阳楼，酾酒赋诗。望长山远水，荆州形胜，夕阳枯木，六代兴衰。扶起仲谋，唤回玄德，笑杀景升豚犬儿。归来也，对西湖叹息，是梦耶非。 诸君傅粉涂脂。问南北战争都不知。恨孤山霜重，梅凋老叶，平堤雨急，柳泣残丝。玉垒腾烟，珠淮飞浪，万里腥风吹鼓鼙。原夫辈，算事今如此，安用毛锥。'),
	(6758, 112, '生查子·秋社', '当楼月半奁，曾买菱花处。愁影背阑干，素发残风露。 神前鸡酒盟，歌断秋香户。泥落画梁空，梦想青春语。'),
	(6759, 112, '点绛唇·越山见梅', '春未来时，酒携不到千岩路。瘦还如许。晚色天寒处。 无限新愁，难对风前语。行人去。暗消春素。横笛空山暮。'),
	(6760, 112, '双双燕·小桃谢后', '小桃谢后，双双燕，飞来几家庭户。轻烟晓暝，湘水暮云遥度，帘外余寒未卷，共斜入、红楼深处。相将占得雕梁，似约韶光留住。 堪举。翩翩翠羽。杨柳岸，泥香半和梅雨。落花风软，戏促乱红飞舞。多少呢喃意绪。尽日向、流莺分诉。还过短墙，谁会万千言语。'),
	(6761, 112, '暗香·送魏句滨宰吴县解组分韵得阖字', '县花谁葺。记满庭燕麦，朱扉斜阖。妙手作新，公馆青红晓云湿。天际疏星趁马，帘昼隙、冰弦三叠。尽换却、吴水吴烟，桃李靓春靥。 风急。送帆叶。正雁水夜清，卧虹平帖。软红路接。涂粉闱深早催入。怀暖天香宴果，花队簇、轻轩银蜡。更问讯、湖上柳，两堤翠匝。'),
	(6762, 112, '夜游宫·竹窗听雨', '竹窗听雨，坐久隐几就睡，既觉，见水仙娟娟于灯影中 窗外捎溪雨响。映窗里、嚼花灯冷。浑似萧湘系孤艇。见幽仙，步凌波，月边影。 香苦欺寒劲。牵梦绕、沧涛千顷。梦觉新愁旧风景。绀云欹，玉搔斜，酒初醒。'),
	(6763, 112, '浣溪沙·琴川慧日寺蜡梅', '蝶粉蜂黄大小乔。中庭寒尽雪微销。一般清瘦各无聊。 窗下和香封远讯，墙头飞玉怨邻箫。夜来风雨洗春娇。'),
	(6764, 112, '蝶恋花·九日和吴见山韵', '明月枝头香满路。几日西风，落尽花如雨。倒照秦眉天镜古。秋明白鹭双飞处。 自摘霜葱宜荐俎。可惜重阳，不把黄花与。帽堕笑凭纤手取。清歌莫送秋声去。'),
	(6765, 112, '法曲献仙音·秋晚红白莲', '风拍波惊，露零秋觉，断绿衰红江上。艳拂潮妆，澹凝冰靥，别翻翠池花浪。过数点斜阳雨，啼绡粉痕冷。 宛相向。指汀洲、素云飞过，清麝洗、玉井晓霞佩响。寸藕折长丝，笑何郎、心似春荡。半掬微凉，听娇蝉、声度菱唱。伴鸳鸯秋梦，酒醒月斜轻帐。'),
	(6766, 536, '喜迁莺·鸠雨细', '鸠雨细，燕风斜。春悄谢娘家。一重帘外即天涯。何必暮云遮。 钏金寒，钗玉冷。薄醉欲成还醒。一春梳洗不簪花。孤负几韶华。'),
	(6767, 241, '西江月', '蔬甲初肥雨润，茶枪小摘春明。野篱是处可诗情。打过下湖船并。 捷报秋来旁午，贤关早晚同寅。绿杨连骑带春城。不问南山远近。'),
	(6768, 303, '水调歌头（长沙中秋约客赏月）', '洞庭千古月，湘水一天秋。凉宵将傍三五，玩事若为酬。人立梧桐影下，身在桂花香里。疑是玉为州。宇宙大圆镜，沆瀣际空浮。 傍谯城，瞻岳麓，有巍楼。不妨举酒，相与一笑作遨头。人已星星华发，月只团团素魄，几对老蟾羞。回首海天阔，心与水东流。'),
	(6769, 117, '长相思', '上帘钩，下帘钩。夜半天街灯火收。有人曾倚楼。 思悠悠，恨悠悠。只有西湖明月秋。知人如许悠。'),
	(6770, 513, '南乡子', '前度几相逢。此日游从乐不同。竹阁荷亭欢聚处，雍容。如在蓬莱第一宫。 夜半月朦胧。秉烛东园风露中。明日匆匆还入浙，忡忡。却把音书寄远鸿。'),
	(6771, 239, '南乡子', '西北有高楼。淡霭残烟渐渐收。几阵凉风生客袖，飕飕。心逐年华衮衮流。 花卉满前头。老懒心情万事休。独倚栏干无一语，回眸。鼓角声中唤起愁。'),
	(6772, 382, '沁园春（寿俞紫薇）', '诗不云乎，蒹葭苍苍，白露为霜。看高山乔木，青云老干，英华滋液，亦敛而藏。匠石操斤游林下，便一举采之充栋梁。须知道，是天将大任，翕处还张。 薇郎玉佩丁当。问何事午桥花竹庄。又星回岁换，腊残春浅，锦熏笼紫，栗玉杯黄。唤起东风，吹醒宿酒，把甲子从头重数将。明朝去，趁传柑宴近，满袖天香。'),
	(6773, 84, '宴清都·初春', '春讯飞琼管。风日薄、度墙啼鸟声乱。江城次第，笙歌翠合，绮罗香暖。溶溶涧渌冰泮。醉梦里、年华暗换。料黛眉重锁隋堤，芳心还动梁苑。 新来雁阔云音，鸾分鉴影，无计重见。啼春细雨，笼愁澹月，恁时庭院。离肠未语先断。算犹有、凭高望眼。更那堪、芳草连天，飞梅弄晚。'),
	(6774, 680, '西江月', '小舫半帘山色，断桥两岸秋阴。芙蓉消息已愁深。红染云机翠锦。 几度烟波共酌，半生风月关心。飞来鸥鹭是知音。一笑歌边醉醒。'),
	(6775, 91, '玉蝴蝶·晚雨未摧宫树', '晚雨未摧宫树，可怜闲叶，犹抱凉蝉。短景归秋，吟思又接愁边。漏初长、梦魂难禁，人渐老、风月俱寒。想幽欢。土花庭甃，虫网阑干。 无端。啼蛄搅夜，恨随团扇，苦近秋莲。一笛当楼，谢娘悬泪立风前。故园晚、强留诗酒，新雁远、不致寒暄。隔苍烟。楚香罗袖，谁伴婵娟。'),
	(6776, 91, '解佩令·人行花坞', '人行花坞。衣沾香雾。有新词、逢春分付。屡欲传情，奈燕子、不曾飞去。倚珠帘、咏郎秀句。 相思一度。秾愁一度。最难忘、遮灯私语。澹月梨花，借梦来、花边廊庑。指春衫、泪曾溅处。'),
	(6777, 670, '浣溪沙（试香罗）', '风软湖光远荡磨。春衫初试薄香罗。踏青无计奈君何。 莫笑老来多岁月，肯教闲去少诗歌。长安陌上有铜驼。'),
	(6778, 670, '柳梢青（玉水明沙）', '玉水明沙。峰回路转，城倚桥斜。老我登临，同谁酩酊，一望还赊。 飞鸿杳霭天涯。但拚取、心情酒家。紫菊枝枝，红茱颗颗，休问年华。'),
	(6779, 370, '水调歌头', '天有月来几时。”太白去“今人不见古时月”，本於《抱朴子》云：今月不及古月之郎。抱朴子所言，非绮语也。深思而得之，诚有此理。嘉定此理。嘉定元年中秋日，因赋水调，其夜无月 听说古时月，皎洁胜今时。今人但见今月，也道似琉璃。君看少年眸子，那比婴儿神彩，投老又堪悲。明月不再盛，玉斧亦何为。 约东坡，招太白，试寻思。凭谁斫却，里面桂影数千枝。忆在无怀天上，仍向有虞宫殿，看月到陈隋。别有一轮月，万古没成亏。'),
	(6780, 149, '湘月', '起幽适。丙午七月既望，声伯约予与赵景鲁、景望、萧和父、裕父、时父、恭父大舟浮湘，放乎中流。山水空寒，烟月交映，凄然其为秋也。坐客皆小冠綀服，或弹琴、或浩歌、或自酌、或援笔搜句。予度此曲，即念奴娇之鬲指声也，于双调中吹之。鬲指亦谓之过腔，见晁无咎集。凡能吹竹者，便能过腔也 五湖旧约，问经年底事，长负清景。暝入西山，渐唤我、一叶夷犹乘兴。倦网都收，归禽时度，月上汀洲冷。中流容与，画桡不点清镜。 谁解唤起湘灵，烟鬟雾鬓，理哀弦鸿阵。玉尘谈玄，叹坐客、多少风流名胜。暗柳萧萧，飞星冉冉，夜久知秋信。鲈鱼应好，旧家乐事谁省。'),
	(6781, 149, '解连环', '玉鞭重倚。却沈吟未上，又索离思。为大乔、能拨春风，小乔妙移筝，雁啼秋水。柳怯云松，更何必、十发梳洗。道郎携羽扇，那日隔帘，半面曾记。 西窗夜凉雨霁。叹幽欢未足，何事轻弃。问后约、空指蔷薇，算如此溪山，甚时重至。水驿灯昏，又见在、曲屏近底。念唯有、夜来皓月，照伊自睡。'),
	(6782, 446, '青玉案·征鸿过尽秋容谢', '征鸿过尽秋容谢。卷离恨、还东下。剪剪霜风落平野。溪山掩映，水烟摇曳，几簇渔樵舍。 芙蓉城里人如画。春伴春游夜转夜。别后知他如何也。心随云乱，眼随天断，泪逐长江泻。'),
	(6783, 591, '恋绣衾（无题）', '长夜偏冷添被儿。枕头儿、移了又移。我自是笑别人底，却元来、当局者迷。 如今只恨因缘浅，也不曾、抵死恨伊。合手下、安排了，那筵席、须有散时。'),
	(6784, 591, '临江仙', '老去浑身无著处，天教只住山林。百年光景百年心。更欢须叹息，无病也呻吟。 试向浮瓜沈李处，清风散发披襟。莫嫌浅后更频斟。要他诗句好，须是酒杯深。'),
	(6785, 591, '鹧鸪天·和昌父', '万事纷纷一笑中。渊明把菊对秋风。细看爽气今犹在，惟有南山一似翁。 情味好，语言工。三贤高会古来同。谁知止酒停云老，独立斜阳数过鸿。'),
	(6786, 591, '水龙吟·载学士院有之', '倚栏看碧成朱，等闲褪了香袍粉。上林高选，匆匆又换，紫云衣润。几许春风，朝薰暮染，为花忙损。笑旧家桃李，东涂西抹，有多少、凄凉恨。 拟倩流莺说与，记荣华、易消难整。人间得意，千红百紫，转头春尽。白发怜君，儒冠曾误，平生官冷。算风流未减，年年醉里，把花枝问。'),
	(6787, 587, '阮郎归（咏春）', '和风暖日小层楼。人闲春事幽。杏花深处一声鸠。花飞水自流。 寻旧梦，续扬州。眉山相对愁。忆曾和泪送行舟。清江古渡头。'),
	(6788, 188, '转调二郎神·闷来弹鹊', '闷来弹鹊，又搅碎、一帘花影。漫试著春衫，还思纤手，熏彻金猊烬冷。动是愁端如何向，但怪得、新来多病。嗟旧日沈腰，如今潘鬓，怎堪临镜？ 重省。别时泪湿，罗衣犹凝。料为我厌厌，日高慵起，长托春酲未醒。雁足不来，马蹄难驻，门掩一庭芳景。空伫立，尽日阑干倚遍，昼长人静。'),
	(6789, 498, '朝中措·长年心事寄林扃', '长年心事寄林扃，尘鬓已星星。芳意不如水远，归心欲与云平。 留连一醉，花残日永，雨后山明。从此量船载酒，莫教闲却春情。'),
	(6790, 498, '南柯子', '槁项诗余瘦，愁肠酒后柔。晚凉团扇欲知秋。卧看明河银影、界天流。 鹤警人初静，虫吟夜更幽。佳辰只合算花筹。除了一天风月、更何求。'),
	(6791, 633, '朝中措', '晴山滴翠水挼蓝。聚散渔舟两复三。横埭北，断桥南。侧起船篷便作帆。'),
	(6792, 633, '长相思（五之三）', '面苍然。鬓皤然。满腹诗书不直钱。官闲常昼眠。 画凌烟。上甘泉。自古功名属少年。知心惟杜鹃。'),
	(6793, 192, '诉衷情', '闲中一篆百花香。袅袅翠□□。低回宛转何似，行路绕羊肠。 深竹户，小山房。雅相当。清心默坐，燕寝无风，永日芬芳。'),
	(6794, 192, '烛影摇红（上元有怀）', '双阙中天，凤楼十二春寒浅。去年元夜奉宸游，曾侍瑶池宴。玉殿珠帘尽卷。拥群仙、蓬壶阆苑。五云深处，万烛光中，揭天丝管。 驰隙流年，恍如一瞬星霜换。今宵谁念泣孤臣，回首长安远。可是尘缘未断。谩惆怅、华胥梦短。满怀幽恨，数点寒灯，几声归雁。'),
	(6795, 388, '满江红', '暮雨萧萧，飞败叶、增添秋色。登高会、痴风吹散，山居嘉客。人世难逢开口笑，老来更觉流年迫。到如今、黄菊满园开，无人摘。 珠履凑，铢衣窄。萦翠袖，催牙拍。索松儿添半，战酣相吓。橘绿橙黄时节好，舞停歌罢门墙隔。酒醒时、枕上一声鸡，东方白。'),
	(6796, 264, '水龙吟·楚天千里无云', '楚天千里无云，露华洗出秋容净。银蟾台榭，玉壶天地，参差桂影。鸳瓦寒生，画檐光射，碧梧金井。听韶华半夜，江梅三弄，风袅袅、良宵永。 携手西园宴罢，下瑶台、醉魂初醒。吹箫仙子，骖鸾归路，一襟清兴。鳷鹊楼高，建章门迥，星河耿耿。看沧江潮上，丹枫叶落，浸关山冷。'),
	(6797, 313, '忆秦娥', '春寂寞。长安古道东风恶。东风恶。胭脂满地，杏花零落。 臂销不奈黄金约。天寒犹怯春衫薄。春衫薄。不禁珠泪，为君弹却。'),
	(6798, 313, '如梦令（忆别）', '忆被金尊劝倒。灯下红香围绕。别后有谁怜，一任春残莺老。烦恼。烦恼。肠断绿杨芳草。'),
	(6799, 90, '剑舞', '声之融曳，思舞态之飘飖。爰有仙童，能开宝匣。佩干将莫邪之利器，擅龙泉秋水之嘉名。鼓三尺之莹莹，云间闪电；横七星之凉凉，掌上生风。宜到芳筵，同翻雅戏。 二舞者自念：伏以五行擢秀，百链呈功。炭炽红炉，光喷星日；硎新雪刃，气贯虹霓。斗牛间紫雾浮游，波涛里苍龙缔合。久因佩服，粗习回翔。兹闻阆苑之群仙，来会瑶池之重客。辄持薄技，上侑清欢。未敢自专，伏候处分。 竹竿子问：既有清歌妙舞，何不献呈。 二舞者答：旧乐何在。 竹竿子再问：一部俨然。 二舞者答：再韵前来。 乐部唱剑器曲破，作舞一段了，二舞者同唱霜天晓角： 荧荧巨阙。左右凝霜雪。且向玉阶掀舞，终当有、用时节。 唱彻。人尽说。宝此制无折。内使奸雄落胆，外须遣、豺狼灭。 乐部唱曲子，作舞剑器曲破一段（舞罢，二人分立两边。别两人汉装者出，对坐，卓上设酒果）。竹竿子念：伏以断蛇大泽，逐鹿中原。佩赤帝之真符，接苍姬之正统。皇威既振，天命有归。势虽盛于重瞳，德难胜于隆准。鸿门设会，亚父输谋。徒矜起舞之雄姿，厥有解纷之壮士。想当时之贾勇，激烈飞扬；宜后世之效颦，回旋宛转。双鸾奏技，四坐腾欢。乐部唱曲子，舞剑器曲破一段。（一人左立者上裀舞，有欲刺右汉装者之势。又一人舞进前翼蔽之。舞罢，两舞者并退，汉装者亦退。复有两人唐装出，对坐。卓上设笔砚纸，舞者一人换妇人装立裀上）竹竿子勾，念：伏以云鬟耸苍璧，雾縠罩香肌。袖翻紫电以连轩，手握青蛇而的皪。花影下、游龙自跃，锦裀上、跄凤来仪。轶态横生，瑰姿谲起。倾此入神之技，诚为骇目之观。巴女心惊，燕姬色沮。岂唯张长史草书大进，抑亦杜工部丽句新成。称妙一时，流芳万古。宜呈雅态，以洽浓欢。 乐部唱曲子，舞剑器曲破一段（作龙蛇蜿蜒曼舞之势。两人唐装者起。二舞者、一男一女对舞，结剑器曲破彻），竹竿子念：项伯有功扶帝业，大娘驰誉满文场。合兹二妙甚奇特，堪使佳宾酹一觞。霍如羿射九日落，矫如群帝骖龙翔。来如雷霆收震怒，罢如江海凝清光。歌舞既终，相将好去。 念了，二舞者出队。'),
	(6800, 332, '望江南', '钟陵好，褚襦衤夸听欢声。薰入管弦增亮响，唤教罗绮亦光荣。引满劝金觥。 谁信是，元自悟长生。铃阁才投公事笔，云章惟读道家经。家世仰仙卿。'),
	(6801, 105, '青玉案', '一尊聊对西风醉。况九日、明朝是。曾与茱萸论子细。江天虚旷，暮林横远，人隔银河水。 碧云渐展天无际。吹不断、黄昏泪。若作欢期须早计。如何得似，鬓边新菊，双结黄金蕊。'),
	(6802, 105, '选冠子·雨湿花房', '雨湿花房，风斜燕子，池阁昼长春晚。檀盘战象，宝局铺棋，筹画未分还懒。谁念少年，齿怯梅酸，病疏霞盏。正青钱遮路，绿丝明水，倦寻歌扇。 空记得、小合题名，红笺青制，灯火夜深裁剪。明眸似水，妙语如弦，不觉晓霜鸡唤。闻道近来，筝谱慵看，金铺长掩。瘦一枝梅影，回首江南路远。'),
	(6803, 185, '长相思令', '花下愁。月下愁。花落月明人在楼。断肠春复秋。 从他休。任他休。如今青鸾不自由。看看天尽头。'),
	(6804, 518, '长相思·村姑儿', '村姑儿。红袖衣。初发黄梅插稻时。双双女伴随。 长歌诗。短歌诗。歌里真情恨别离。休言伊不知。'),
	(6805, 589, '行香子', '草色芊绵。雨点阑斑。糁飞花、还是春残。天涯万里，海上三年。试倚危楼，将远恨，卷帘看。 举头见日，不见长安。谩凝眸、老泪凄然。山禽飞去，榕叶生寒。到黄昏也，独自个，尚凭阑。'),
	(6806, 104, '宣州竹（墨梅）', '小溪蓬底湖风重。吹破凝酥动。一枝斜映庾门深。冷淡无言香泛、月华清。已经轻瘦谁为共。魂绕徐熙□。耻同桃李困春容。肯向毫端开发、雨云中。'),
	(6807, 559, '临江仙（宣和乙巳冬幸亳·州途次）', '过水穿山前去也，吟诗约句千余。淮波寒重雨疏疏。烟笼滩上鹭，人买就船鱼。 古寺幽房权且住，夜深宿在僧居。梦魂惊起转嗟吁。愁牵心上虑，和泪写回书。'),
	(6808, 128, '江城子', '夕阳低尽柳如烟。淡平川。断肠天。今夜十分，霜月更娟娟。怎得人如天上月，虽暂缺，有时圆。 断云飞雨又经年。思凄然。泪涓涓。且做如今，要见也无缘。因甚江头来处雁，飞不到，小楼边。'),
	(6809, 267, '柳枝·江南岸', '江南岸，柳枝；江北岸，柳枝；折送行人无尽时。恨分离，柳枝。 酒一杯，柳枝；泪双垂，柳枝；君到长安百事违。几时归？柳枝。'),
	(6810, 267, '如梦令', '一夜新秋风雨。客恨客愁无数。我是卧云人，悔到红尘深处。难住。难住。拂袖青山归去。'),
	(6811, 267, '采桑子（重阳病起饮酒连夕）', '天高风劲尘寰静，佳节重阳。叶下潇湘。碧海晴空一阵霜。 安排弦管倾芳酝，报答秋光。昼短歌长。红烛黄花夜未央。'),
	(6812, 642, '虞美人', '绿阴满院帘垂地。落絮萦香砌。池光不定药栏低。闲并一双鸂鶒、没人时。 旧欢黯黯成幽梦。帐卷金泥重。日虹斜处暗尘飞。脉脉小窗孤枕、镜花移。'),
	(6813, 642, '谒金门·花满院', '花满院。飞去飞来双燕。红雨入帘寒不卷。晓屏山六扇。 翠袖玉笙凄断。脉脉两蛾愁浅。消息不知郎近远。一春长梦见。'),
	(6814, 109, '水龙吟（秋兴）', '秋生泽国，无边落木，又作萧萧下。澄江过雨，凉飙吹面，黄花初把。苍鬓羁孤，粗营鸡黍，浊醪催贳。对斜斜露脚，寒香正好，幽人去、空惊咤。 头上纶巾醉堕，要欹眠、水云萦舍。牵衣儿女，归来欢笑，仍邀同社。月底蓬门，一株江树，悲虫鸣夜。把茱萸细看，牛山底事，强成沾洒。'),
	(6815, 510, '点绛唇·县斋愁坐作', '秋晚寒斋，藜床香篆横轻雾。闲愁几许。梦逐芭蕉雨。 云外哀鸿，似替幽人语。归不去。乱山无数。斜日荒城鼓。'),
	(6816, 380, '菩萨蛮·落花迤逦层阴少', '落花迤逦层阴少，青梅竞弄枝头小。红色雨和烟，行人江那边。 好花都过了，满地空芳草。落日醉醒问，一春无此寒。'),
	(6817, 360, '生查子·富阳道中', '春晚出山城，落日行江岸。人不共潮来，香亦临风散。 花谢小妆残，莺困清歌断。行雨梦魂消，飞絮心情乱。'),
	(6818, 360, '醉花阴', '金叶犹温香未歇。尘定歌初彻。暖透薄罗衣，一霎清风，人映团团月。 持杯试听留春阕。此个情肠别。分付与莺莺，劝取东君，停待芳菲节。'),
	(6819, 247, '玉蝴蝶', '目断江南千里，灞桥一望，烟水微茫。尽锁重门，人去暗度流光。雨轻轻、梨花院落，风淡淡、杨柳池塘。恨偏长。佩沈湘浦，云散高唐。 清狂。重来一梦，手搓梅子，煮酒初尝。寂寞经春，小桥依旧燕飞忙。玉钩栏、凭多渐暖，金缕枕、别久犹香。最难忘。看花南陌，待月西厢。'),
	(6820, 130, '齐天乐·绿芜凋尽台城路', '绿芜凋尽台城路，殊乡又逢秋晚。暮雨生寒，鸣蛩劝织，深阁时闻裁剪。云窗静掩。叹重拂罗裀，顿疏花簟。尚有綀囊，露萤清夜照书卷。 荆江留滞最久，故人相望处，离思何限。渭水西风，长安乱叶，空忆诗情宛转，凭高眺远。正玉液新篘，蟹螯初荐。醉倒山翁，但愁斜照敛。'),
	(6821, 130, '瑞鹤仙·悄郊原带郭', '悄郊原带郭，行路永，客去车尘漠漠。斜阳映山落，敛馀红、犹恋孤城阑角。凌波步弱，过短亭、何用素约。有流莺劝我，重解绣鞍，缓引春酌。 不记归时早暮，上马谁扶，醒眠朱阁。惊飙动幕，扶残醉，绕红药。叹西园、已是花深无地，东风何事又恶？任流光过却，犹喜洞天自乐。'),
	(6822, 130, '水龙吟·梨花', '素肌应怯余寒，艳阳占立青芜地。樊川照日，灵关遮路，残红敛避。传火楼台，妒花风雨，长门深闭。亚帘栊半湿，一枝在手，偏勾引、黄昏泪。 别有风前月底。布繁英、满园歌吹。朱铅退尽，潘妃却酒，昭君乍起。雪浪翻空，粉裳缟夜，不成春意。恨玉容不见，琼英谩好，与何人比。'),
	(6823, 249, '金凤钩·送春', '春辞我，向何处？怪草草、夜来风雨。一簪华发，少欢饶恨，无计殢春且住。 春回常恨寻无路，试向我、小园徐步。一栏红药，倚风含露。春自未曾归去。'),
	(6824, 555, '菩萨蛮·春风试手先梅蕊', '春风试手先梅蕊，頩姿冷艳明沙水。不受众芳知，端须月与期。 清香闲自远，先向钗头见。雪后燕瑶池，人间第一枝。'),
	(6825, 556, '天仙子', '宿雨洗空台榭莹。下尽珠帘寒未定。花开花落几番晴，春欲竟。愁未醒。池面杏花红透影。 一纸短书言不尽。明月清风还记省。玉楼香断又添香，闲展兴。临好景。心似乱萍何处整。'),
	(6826, 451, '如梦令', '传与东坡尊舅。欲作栏干护佑。心性慢些儿，先著他人机构。虚谬。虚谬。这段姻缘生受。'),
	(6827, 693, '青玉案（至宜州次韵上酬七兄）', '烟中一线来时路。极目送、归鸿去。第四阳关云不度。山胡新啭，子规言语。正在人愁处。 忧能损性休朝暮。忆我当年醉时句。旧诗云：“我自只如常日醉，满川风月替人愁。”渡水穿云心已许。暮年光景，小轩南浦。同卷西山雨。'),
	(6828, 278, '西江月', '念念欲归未得，迢迢此去何求。都缘一点在心头。忘了霜朝雪后。 要见有时有梦，相思无处无愁。小窗若得再绸缪。应记如今时候。'),
	(6829, 278, '蝶恋花', '万事都归一梦了。曾向邯郸，枕上教知道。百岁年光谁得到。其间忧患知多少。 无事且频开口笑。纵酒狂歌，销遣闲烦恼。金谷繁花春正好。玉山一任樽前倒。'),
	(6830, 494, '减字木兰花（花）', '玉房金蕊。宜在玉人纤手里。淡月朦胧。更有微微弄袖风。 温香熟美。醉慢云鬟垂两耳。多谢春工。不是花红是玉红。'),
	(6831, 494, '减字木兰花·送东武令赵昶失官归海州', '贤哉令尹，三仕已之无喜愠。我独何人，犹把虚名玷缙绅。 不如归去，二顷良田无觅处。归去来兮，待有良田是几时。'),
	(6832, 250, '南乡子', '小蕊受春风。日日宫花花树中。恰向柳绵撩乱处，相逢。笑靥旁边心字浓。 归路草茸茸。家在秦楼更近东。醒去醉来无限事，谁同。说著西池满面红。'),
	(6833, 250, '临江仙', '东野亡来无丽句，于君去后少交亲。追思往事好沾巾。白头王建在，犹见咏诗人。 学道深山空自老，留名千载不干身。酒筵歌席莫辞频。争如南陌上，占取一年春。'),
	(6834, 409, '西江月（红梅）', '梅好惟嫌淡伫，天教薄与胭脂。真妃初出华清池。酒入琼姬半醉。 东阁诗情易动，高楼玉管休吹。北人浑作可花疑。惟有青枝不似。'),
	(6835, 409, '甘露歌', '尽日含毫难比兴。都无色可并。万里晴天何处来。真是屑琼瑰。'),
	(6836, 409, '诉衷情（和俞秀老鹤词·五之一）', '常时黄色见眉间。松桂我同攀。每言天上辛苦，不肯饵金丹。 怜水静，爱云闲。便忘还。高歌一曲，岩谷迤逦，宛似商山。'),
	(6837, 352, '定风波', '把酒花前欲问伊。问伊还记那回时。黯淡梨花笼月影。人静。画堂东畔药阑西。 及至如今都不认。难问。有情谁道不相思。何事碧窗春睡觉。偷照。粉痕匀却湿胭脂。'),
	(6838, 352, '踏莎行', '云母屏低，流苏帐小。矮床薄被秋将晓。乍凉天气未寒时，平明窗外闻啼鸟。 困殢榴花，香添蕙草。佳期须及朱颜好。莫言多病为多情，此身甘向情中老。'),
	(6839, 352, '浪淘沙', '万恨苦绵绵。旧约前欢。桃花溪畔柳阴间。几度日高春垂重，绣户深关。 楼外夕阳闲。独自凭阑。一重水隔一重山。水阔山高人不见，有泪无言。'),
	(6840, 251, '清平乐', '春来秋去。往事知何处。燕子归飞兰泣露。光景千留不住。 酒阑人散忡忡。闲阶独倚梧桐。记得去年今日，依前黄叶西风。'),
	(6841, 186, '木兰花（般涉调）', '人意共怜花月满。花好月圆人又散。欢情去逐远云空，往事过如幽梦断。 草树争春红影乱。一唱鸡声千万怨。任教迟日更添长，能得几时抬眼看。'),
	(6842, 344, '洞仙歌', '嘉景，向少年彼此，争不雨沾云惹。奈傅粉英俊，梦兰品雅。金丝帐暖银屏亚。并粲枕、轻偎轻倚，绿娇红姹。算一笑，百琲明珠非价。 闲暇。每只向、洞房深处，痛怜极宠，似觉些子轻孤，早恁背人沾洒。从来娇多猜讶。更对翦香云，须要深心同写。爱揾了双眉，索人重画。忍孤艳冶。断不等闲轻舍。鸳衾下。愿常恁、好天良夜。'),
	(6843, 344, '木兰花令（仙吕调）', '有个人人真攀羡。问著洋洋回却面。你若无意向他人，为甚梦中频相见。 不如闻早还却愿。免使牵人虚魂乱。风流肠肚不坚牢，只恐被伊牵引断。'),
	(6844, 344, '竹马子·登孤垒荒凉', '登孤垒荒凉，危亭旷望，静临烟渚。对雌霓挂雨，雄风拂槛，微收烦暑。渐觉一叶惊秋，残蝉噪晚，素商时序。览景想前欢，指神京，非雾非烟深处。 向此成追感，新愁易积，故人难聚。凭高尽日凝伫。赢得消魂无语。极目霁霭霏微，瞑鸦零乱，萧索江城暮。南楼画角，又送残阳去。'),
	(6845, 344, '锦堂春·坠髻慵梳', '坠髻慵梳，愁蛾懒画，心绪是事阑珊。觉新来憔悴，金缕衣宽。认得这疏狂意下，向人诮譬如闲。把芳容整顿，恁地轻孤，争忍心安。 依前过了旧约，甚当初赚我，偷翦云鬟。几时得归来，香阁深关。待伊要、尤云殢雨，缠绣衾、不与同欢。侭更深、款款问伊，今后敢更无端。'),
	(6846, 344, '尉迟杯（双调）', '宠佳丽。算九衢红粉皆难比。天然嫩脸修蛾，不假施朱描翠。盈盈秋水。恣雅态、欲语先娇媚。每相逢、月夕花朝，自有怜才深意。 绸缪凤枕鸳被。深深处、琼枝玉树相倚。困极欢余，芙蓉帐暖，别是恼人情味。风流事、难逢双美。况已断、香云为盟誓。且相将、共乐平生，未肯轻分连理。'),
	(6847, 344, '迎新春·嶰管变青律', '嶰管变青律，帝里阳和新布。晴景回轻煦。庆嘉节、当三五。列华灯、千门万户。遍九陌罗绮，香风微度。十里然绛树。鳌山耸，喧天萧鼓。 渐天如水，素月当午。香径里、绝缨掷果无数。更阑烛影花阴下，少年人、往往奇遇。太平时、朝野多欢，民康阜、随分良聚。堪对此景，争忍独醒归去。'),
	(6848, 344, '斗百花·煦色韶光明媚', '煦色韶光明媚，轻霭低笼芳树。池塘浅蘸烟芜，帘幕闲垂飞絮。春困厌厌，抛掷斗草工夫，冷落踏青心绪。终日扃朱户。 远恨绵绵，淑景迟迟难度。年少傅粉，依前醉眠何处。深院无人，黄昏乍拆秋千，空锁满庭花雨。'),
	(6849, 249, '临江仙·绿暗汀洲三月暮', '绿暗汀洲三月暮，落花风静帆收。垂杨低映木兰舟。半篙春水滑，一段夕阳愁。 灞水桥东回首处，美人新上帘钩。青鸾无计入红楼。行云归楚峡，飞梦到扬州。'),
	(6850, 268, '一萼红·盆梅', '玉堂深。正重帘护暝，窗色试新晴。苔暖鳞生，泥融脉起，春意初破琼英。夜深后、寒消绛蜡，误碎月、和露落空庭。暖吹调香，冷芳侵梦，一晌消凝。 长恨年华婉晚，被柔情数曲，抵死牵萦。何事东君，解将芳思，巧缀一斛春冰。那得似、空山静夜，傍疏篱、清浅小溪横。莫问调羹心事，且论笛里平生。'),
	(6851, 458, '念奴娇（初见海棠花）', '嫣然一笑，向烛花光下，经年才见。欲语远羞如有恨，方得东君一盼。天意无情，更教微雨，香泪流丹脸。今朝霁色，笙歌初沸庭院。 因是思入东屏，当年手植，遍桃源低岸。失脚东来春七度，辜负芳丛无限。问讯园丁，宁如归去，细与从头看。东风独立，白云遮断双眼。'),
	(6852, 73, '乌夜啼·石榴', '垂杨影里残红。甚匆匆。只有榴花、全不怨东风。暮雨急。晓鸦湿。绿玲珑。比似茜裙初染、一般同。'),
	(6853, 73, '蝶恋花·送春', '人自怜春春未去。萱草石榴，也解留春住。只道送春无送处。山花落得红成路。 高处莺啼低蝶舞。何况日长，燕子能言语。会与光阴相客主。晴云又卷西边雨。'),
	(6854, 356, '昭君怨（卖花人）', '担子挑春虽小。白白红红都好。卖过巷东家。巷西家。 帘外一声声叫。帘里鸦鬟入报。问道买梅花。买桃花。'),
	(6855, 85, '永遇乐', '早叶初莺，晚风孤蝶，幽思何限。檐角萦云，阶痕积雨，一夜苔生遍。玉窗闲掩，瑶琴慵理，寂寞水沈烟断。消无言、春归无觅处，卷帘见双飞燕。 风亭泉石，烟林薇蕨，梦绕旧时曾见。江上闲鸥，心盟犹在，分得眠沙半。引觞浮月，飞谈卷雾，莫管愁深欢浅。起来倚阑干，拾得残红一片。'),
	(6856, 461, '长相思', '吴山秋。越山秋。吴越两山相对愁。长江不尽流。 风飕飕。雨飕飕。万里归人空白头。南冠泣楚囚。'),
	(6857, 243, '桂枝香', '西凤满目。渐院落悄清，愁近银烛。多少虫书堕翠，又随波縠。姮娥半露扶疏影，向虚檐、似知幽独。浦鸿声断，枝乌漏永，芳梦难续。 记旧日、离亭细嘱。早归趁香边，频泛酝酝。谁遣而今，对景黛娥双蹙。玉鞭但共秋光远，漫空怜、如许金粟。露零襟冷，萧萧更兼，数竿修竹。'),
	(6858, 362, '菩萨蛮·商妇怨', '春时江上帘纤雨。张帆打鼓开船去。秋晚恰归来。看看船又开。 嫁郎如未嫁。长是凄凉夜。情少利心多。郎如年少何。'),
	(6859, 541, '沁园春·寒食郓州道中', '十五年来，逢寒食节，皆在天涯。叹雨濡露润，还思宰柏，风柔日媚，羞看飞花。麦饭纸钱，只鸡斗酒，几误林间噪喜鸦。天笑道，此不由乎我，也不由他。 鼎中炼熟丹砂。把紫府清都作一家。想前人鹤驭，常游绛阙，浮生蝉蜕，岂恋黄沙。帝命守坟，王令修墓，男子正当如是邪。又何必，待过家上冢，书锦荣华。'),
	(6860, 507, '菩萨蛮（春雨）', '春愁一段来无影。著人似醉昏难醒。烟雨湿阑干。杏花惊蛰寒。唾壶敲欲破。绝叫凭谁和。今夜欠添衣。那人知不知。'),
	(6861, 479, '虞美人', '枢庭喜庆生辰到。仙伯离蓬岛。鲁台云物正呈祥。线绣工夫从此、日添长。 满斟绿醑深深劝。岁岁长相见。蟠桃结子几番红。笑赏清歌声调、叶黄钟。'),
	(6862, 64, '玉楼春', '柳梢绿小眉如印。乍暖还寒犹未定。惜花长是为花愁，殢酒却嫌添酒病。 蝇头蜗角都休竞。万古豪华同一尽。东君晓夜促归期，三十六番花递信。'),
	(6863, 340, '满江红', '衮衮诸公，嗟独冷、先生宦薄。夸甲第、纷纷梁肉，谩甘寥寞。道出羲皇知有用，才过屈宋人谁若。剩得钱、沽酒两忘形，更酬酢。 清夜永，开春酌。听细雨，檐花落。但高歌不管，饿填沟壑。司马逸才亲涤器。子云识字终投阁。且生前、相遇共相欢，衔杯乐。 杜工部饮中八仙歌：知章骑马似乘船，眼花落井水底眠。汝阳三斗始朝天，道逢麹车口流涎，恨不移封向酒泉。左相日兴费万钱，饮如长鲸吸百川，衔杯乐圣称世贤。宗之潇洒美少年，举觞白眼望青天，皎如玉树临风前。苏晋长齐绣佛前，醉中往往爱光禅。李白一斗诗百篇，长安市上酒家眠。天子呼来不上船，自称臣是酒中仙。张旭三杯草圣传，脱帽露顶王公前，挥毫落纸如云烟。焦遂五斗方卓然，高谈雄辩惊四筵。'),
	(6864, 233, '醉落魄·九日吴胜之运使黄鹤山登高', '龙山行乐。何如今日登黄鹤。风光政要人酬酢。欲赋归来，莫是渊明错。 江山登览长如昨。飞鸿影里秋光薄。此怀祗有黄花览。牢裹乌纱，一任西风作。'),
	(6865, 31, '卜算子', '翦烛写香笺，拨火温寒醑。门外东风将我愁，欲作三更雨。 夜夜玉楼心，日日长亭路。豆蔻花开信不来，尘满金钗股。'),
	(6866, 542, '卜算子（赠妓）', '双桨浪花平，夹岸青山锁。你自归家我自归，说着如何过。 我断不思量，你莫思量我。将你从前与我心，付与他人可。'),
	(6867, 697, '雨霖铃（送客还浙东）', '天南游客。甚而今、却送君南国。薰风万里无限，吟蝉暗续，离情如织。秣马脂车，去即去、多少人惜。为惠爱、烟惨云山，送两城愁作行色。 飞帆过、浙西封城。到秋深、且舣荷花泽。就船买得鲈鳜，新谷破、雪堆香粒。此兴谁同，须记东秦，有客相忆。愿听了、一阕歌声，醉倒拚今日。'),
	(6868, 17, '鹊踏枝', '几日中元初过复。七叶蓂疏，佳气生晴书。称庆源深流福厚。天精储粹干星斗。 喜人高堂罗燕豆。风弄微凉，帘幕披香绣。暂倩灵龟言永寿。蟠桃花送长生酒。'),
	(6869, 433, '雨中花令（夏词）', '百尺清泉声陆续。映潇洒、碧梧翠竹。面千步回廊，重重帘幕，小枕欹寒玉。 试展鲛绡看画轴。见一片、潇湘凝绿。待玉漏穿花，银河垂地，月上栏干曲。'),
	(6870, 327, '少年游', '江南节物，水昏云淡，飞雪满前村。千寻翠岭，一枝芳艳，迢递寄归人。 寿阳妆罢，冰姿玉态，的的写天真。等闲风雨又纷纷。更忍向、笛中闻。'),
	(6871, 74, '柳梢青·七夕', '干鹊收声，湿萤度影，庭院深香。步月移阴，梳云约翠，人在回廊。 醺醺宿酒残妆。待付与、温柔醉乡。却扇藏娇，牵衣索笑，今夜差凉。'),
	(6872, 60, '临江仙·千里长安名利客', '千里长安名利客，轻离轻散寻常。难禁三月好风光。满阶芳草绿，一片杏花香。 记得年时临上马，看人眼泪汪汪。如今不忍更思量。恨无千日酒，空断九回肠。'),
	(6873, 165, '鹊踏枝', '斜日平山寒已薄。雪过松梢，犹有残英落。晚色际天天似幕。一尊先与东风约。 邀得红梅同宴乐。酒面融春，春满纤纤萼。客意为伊浑忘却。归船且傍花阴泊。'),
	(6874, 4, '忆秦娥（别情）', '千里草。萋萋尽处遥山小。遥山小。行人远似，此山多少。 天若有情天亦老。此情说便说不了。说不了。一声唤起，又惊春晓。'),
	(6875, 539, '忆君王·依依宫柳拂宫墙', '依依宫柳拂宫墙，楼殿无人春昼长。燕子归来依旧忙。忆君王，月破黄昏人断肠。'),
	(6876, 232, '西江月', '大厦吞风吐月，小舟坐水眠空。雾窗春晓翠如葱。睡起云涛正涌。 往事回头笑处，此生弹指声中。玉笺佳句敏惊鸿。闻道衡阳价重。'),
	(6877, 202, '月下笛·万里孤云', '孤游万竹山中，闲门落叶，愁思黯然，因动《黍离》之感。时寓甬东积翠山舍。 万里孤云，清游渐远，故人何处？寒窗梦里，犹记经行旧时路。连昌约略无多柳，第一是难听夜雨。漫惊回凄悄，相看烛影，拥衾谁语？ 张绪归何暮？半零落依依，断桥鸥鹭。天涯倦旅，此时心事良苦。只愁重洒西州泪，问杜曲人家在否？恐翠袖正天寒，犹倚梅花那树。'),
	(6878, 515, '喜迁莺（金村阻风）', '风涛如此，被闲鸥诮我，君行良苦。槲叶深湾，芦窠窄港，小憩倦篙慵橹。壮年夜吹笛去，惊得鱼龙嗥舞。怅今老，但篷窗紧掩，荒凉愁愫。 别浦。云断处。低雁一绳，拦断家山路。佩玉无诗，飞霞乏序，满席快飙谁付。醉中几番重九，今度芳尊孤负。便晴否。怕明朝蝶冷，黄花秋圃。'),
	(6879, 515, '如梦令', '夜月溪篁鸾影。晓露岩花鹤顶。半世踏红尘，到底输他村景。村景。村景。樵斧耕蓑渔艇。'),
	(6880, 515, '解佩令（春）', '春晴也好。春阴也好。著些儿、春雨越好。春雨如丝，绣出花枝红袅。怎禁他、孟婆合皂。 梅花风小。杏花风小。海棠风、蓦地寒峭。岁岁春光，被二十四风吹老。楝花风、尔且慢到。'),
	(6881, 515, '沁园春（为老人书南堂壁）', '老子平生，辛勤几年，始有此庐。也学那陶潜，篱栽些菊，依他杜甫，园种些蔬。除了雕梁，肯容紫燕，谁管门前长者车。怪近日，把一庭明月，却借伊渠。 鬓边白发纷如。又何苦招宾约客欤。但夏榻宵眠，面风欹枕，冬檐昼短，背日观书。若有人寻，只教僮道，这屋主人今自居。休羡彼，有摇金宝辔，织翠华裾。'),
	(6882, 15, '燕归来', '三叠曲，四愁诗。心事少人知。西风未老燕迟归。巢冷半干泥。 流红句，回文字。除燕知，谁能记。一声恰到画楼西。云压小鸿低。'),
	(6883, 640, '沁园春·丁酉岁感事', '　　谁使神州，百年陆沉，青毡未还？怅晨星残月，北州豪杰；西风斜日，东帝江山。刘表坐谈，深源轻进，机会失之弹指间。伤心事，是年年冰合，在在风寒。说和说战都难，算未必江沱堪宴安。叹封侯心在，鳣鲸失水；平戎策就，虎豹当关。渠自无谋，事犹可做，更剔残灯抽剑看。麒麟阁，岂中兴人物，不画儒冠？'),
	(6884, 660, '青玉案', '青山流水迢迢去。总是东风往回路。送得春来春又暮。莺如何诉。燕如何语。只有春知处。 时光渐渐春如许。何用怜春怕红雨。到处空飞无实据。花开也好，花飞也好，此意须双悟。'),
	(6885, 112, '凤栖梧·甲辰七夕', '开过南枝花满院。新月西楼，相约同针线。高树数声蝉送晚。归家梦向斜阳断。 夜色银河情一片。轻帐偷欢，银烛罗屏怨。陈迹晓风吹雾散。鹤钩空带蛛丝卷。'),
	(6886, 112, '浣溪沙·一曲鸾箫别彩云', '一曲鸾箫别彩云。燕钗尘涩镜华昏。灞桥舞色褪蓝裙。 湖上醉迷西子梦，江头春断倩离魂。旋缄红泪寄行人。'),
	(6887, 112, '三姝媚·过都城旧居有感', '湖山经醉惯。渍春衫、啼痕酒痕无限。又客长安，叹断襟零袂，涴尘谁浣。紫曲门荒，沿败井、风摇青蔓。对语东邻，犹是曾巢，谢堂双燕。 春梦人间须断。但怪得、当年梦缘能短。绣屋秦筝，傍海棠偏爱，夜深开宴。舞歇歌沈，花未减、红颜先变。伫久河桥欲去，斜阳泪满。'),
	(6888, 112, '高阳台·过种山即越文种墓', '帆落回潮，人归故国，山椒感慨重游。弓折霜寒，机心已堕沙鸥。灯前宝剑清风断，正五湖、雨笠扁舟。最无情，岸上闲花，腥染春愁。 当时白石苍松路，解勒回玉辇，雾掩山羞。木客歌阑，青春一梦荒丘。年年古苑西风到，雁怨啼、绿水F950秋。莫登临，几树残烟，西北高楼。'),
	(6889, 112, '惜秋华·七夕前一日送人归盐官', '数日西风，打秋林枣熟，还催人去。瓜果夜深，斜河拟看星度。匆匆便倒离尊，怅遇合、云销萍聚。留连，有残蝉韵晚，时歌金缕。 绿水暂如许。奈南墙冷落，竹烟槐雨。此去杜曲，已近紫霄尺五。扁舟夜宿吴江，正水佩霓裳无数。眉妩。问别来、解相思否。'),
	(6890, 112, '金盏子·赋秋壑西湖小筑', '卜筑西湖，种翠萝犹傍，软红尘里。来往载清吟，为偏爱吾庐，画船频繁。笑携雨色晴光，入春明朝市。石桥锁，烟霞五百名仙，第一人是。 临酒论深意。流光转、莺花任乱委。冷然九秋肺腑，应多梦、岩扃冷云空翠。漱流枕石幽情，写猗兰绿绮。专城处，他山小队登临，待西风起。'),
	(6891, 112, '天香·蜡梅', '蟫叶黏霜，蝇苞缀冻，生香远带风峭。岭上寒多，溪头月冷，北枝瘦、南枝小。玉奴有姊，先占立、墙阴春早。初试宫黄澹薄，偷分寿阳纤巧。 银烛泪深未晓。酒钟悭、贮愁多少。记得短亭归马，暮衙蜂闹。豆蔻钗梁恨袅。但怅望、天涯岁华老。远信难封，吴云雁杳。'),
	(6892, 112, '风入松·麓翁园堂宴客', '一番疏雨洗芙蓉。玉冷佩丁东。辘轳听带秋声转，早凉生、傍井梧桐。欢宴良宵好月，佳人修竹清风。 临池飞阁乍青红。移酒小垂虹。贞元供奉梨园曲，称十香、深蘸琼钟。醉梦孤云晓色，笙歌一派秋空。'),
	(6893, 112, '沁园春·送翁宾旸游鄂渚', '情如之何，暮涂为客，忍堪送君。便江湖天远，中宵同月，关河秋近。何日清尘。玉尘生风，貂裘明雪，幕府英雄今几人。行须早，料刚肠肯殢，泪眼离颦。 平生秀句清尊。到帐动风开自有神。听夜鸣黄鹤，楼高百尺，朝驰白马，笔扫千军。贾傅才高，岳家军在，好勒燕然石上文。松江上，念故人老矣，甘卧闲云。'),
	(6894, 112, '暗香疏影·夹钟宫赋墨梅', '占春压一。卷峭寒万里，平沙飞雪。数点酥钿，凌晓东风□吹裂。独曳横梢瘦影，入广平、裁冰词笔。记五湖、清夜推篷，临水一痕月。 何逊扬州旧事，五更梦半醒，胡调吹彻。若把南枝，图入凌烟，香满玉楼琼阙。相将初试红盐味，到烟雨、青黄时节。想雁空、北落冬深，澹墨晚天云阔。'),
	(6895, 112, '江南春（中吕商赋张药翁杜衡山庄）', '风响牙签，云寒古砚，芳铭犹在棠笏。秋床听雨，妙谢庭、春草吟笔。城市喧鸣辙。清溪上、小山秀洁。便向此、搜松访石，葺屋营花，红尘远避风月。瞿塘路，随汉节。记羽扇纶巾，气凌诸葛。青天万里，料漫忆、莼丝鲈雪。车马从休歇。荣华事、醉歌耳热。天与此翁，芳芷嘉名，纫兰佩兮琼玦。'),
	(6896, 112, '菩萨蛮·落花夜雨辞寒食', '落花夜雨辞寒食。尘香明日城南陌。玉靥湿斜红。泪痕千万重。 伤春头竟白。来去春如客。人瘦绿阴浓。日长帘影中。'),
	(6897, 112, '醉桃源·赠卢长笛', '沙河塘上旧游嬉。卢郎年少时。一声长笛月中吹。和云和雁飞。 惊物换，叹星移。相看两鬓丝。断肠吴苑草凄凄。倚楼人未归。'),
	(6898, 112, '齐天乐·新烟初试花如梦', '新烟初试花如梦，疑收楚峰残雨。茂苑人归，秦楼燕宿，同惜天涯为旅。游情最苦。早柔绿迷津，乱莎荒圃。数树梨花，晚风吹堕半汀鹭。 流红江上去远，翠尊曾共醉，云外别墅。澹月秋千，幽香巷陌，愁结伤春深处。听歌看舞。驻不得当时，柳蛮樱素。睡起恹恹，洞箫谁院宇。'),
	(6899, 112, '满江红（甲辰岁盘门外寓居过重午）', '结束萧仙，啸梁鬼、依还未灭。荒城外、无聊闲看，野烟一抹。梅子未黄愁夜雨，榴花不见簪秋雪。又重罗、红字写香词，年时节。 帘底事，凭燕说。合欢缕，双条脱。自香消红臂，旧情都别。湘水离魂菰叶怨，扬州无梦铜华阙。倩卧箫、吹裂晚天云，看新月。'),
	(6900, 241, '望江南', '寺中门有扁曰寿丘山，亲意欣然，盖以丘山为岳字云 梅欲老，撑月过南徐。家口纵多难减鹤，路程不远易携书。只是废春锄。 霜满袖，茶灶借僧庐。湖海甚豪今倦矣，丘山虽寿竟如何。一笑荐冰蔬。'),
	(6901, 138, '沁园春', '敢隐默，不敢戏传，始以小词，庸谢雅意 天下江山，无如甘露，多景楼前。有谪仙公子，依山傍水，结茅筑圃，花竹森然。四季风光，一生乐事，真个壶中别有天。亭台巧，一琴一鹤，泥絮心田。 不须块坐参禅。也不要区区学挂冠。但对境无心，山林钟鼎，流行坎止，闹里偷闲。向上玄关，南辰北斗，昼夜璇玑炼火还。分明见，本来面目，不是游魂。'),
	(6902, 49, '长相思', '烟凄凄。草凄凄。野火原头烧断碑。不知名姓谁。 印累累。冢累累。千万人中几个归。荣华朝露晞。'),
	(6903, 513, '念奴娇（咏雪）', '广寒宫里，散天花、点点空中柳絮。是处楼台皆似玉，半夜风声不住。万里盐城，千家珠瓦，无认蓬莱处。但呼童、且去探梅花、攀那树。 垂帘未敢掀开，狮儿初捏就，佳人偷觑。溪畔渔翁蓑又重，几点沙鸥无语。竹折庭前，松僵路畔，满目都如许。问要晴，更待积痕消，须无雨。'),
	(6904, 513, '酹江月', '思量世事，几千般翻覆，是非多少。随分随缘天地里，心与江山不老。道在天先，神游物外，自有长生宝。洞门无锁，悄无一个人到。 一条柱杖横肩，芒鞋紧峭，正风清月好。惊赏百年浑似梦，空被利名萦绕。野鹤纵横，孤云自在，对落花芳草。来朝拂袖，谁来南岳寻我。'),
	(6905, 213, '南歌子·柳户朝云湿', '柳户朝云湿，花窗午篆清。东风未放十分晴。留恋海棠颜色、过清明。 垒润栖新燕，笼深锁旧莺。琵琶可是不堪听。无奈愁人把做、断肠声。'),
	(6906, 695, '清平乐', '西园啼鸟。留得春多少。客里情怀无日好。愁损连天芳草。 博山灰冷香残。微风吹满银笺。卓午花阴不动，一双蝴蝶团＿。'),
	(6907, 382, '卜算子', '簸弄柳梢春，呼吸花心露。倦粉娇黄扇底风，尽向眉心度。 唤醒海棠红，约住樱桃素。上到瑶台最上层，共跨青鸾去。后句梦中得。'),
	(6908, 84, '贺新郎·挽住风前柳', '彭传师于吴江三高堂之前钓雪亭，盖擅渔人之窟宅以供诗境也，赵子野约余赋之。 挽住风前柳，问鸱夷当日扁舟，近曾来否？月落潮生无限事，零落茶烟未久。谩留得莼鲈依旧。可是功名从来误，抚荒祠、谁继风流后？今古恨，一搔首。 江涵雁影梅花瘦，四无尘、雪飞云起，夜窗如昼。万里乾坤清绝处，付与渔翁钓叟。又恰是、题诗时候。猛拍阑干呼鸥鹭，道他年、我亦垂纶手。飞过我，共樽酒。'),
	(6909, 149, '卜算子', '象笔带香题，龙笛吟春咽。杨柳娇痴未觉愁，花管人离别。 路出古昌源，石瘦冰霜洁。折得青＿碧藓花，持向人间说。||'),
	(6910, 149, '一萼红', '橘幽篁，一径深曲。穿径而南，官梅数十株，如椒、如菽，或红破白露，枝影扶疏。著屐苍苔细石间，野兴横生，亟命驾登定王台。乱湘流、入麓山，湘云低昂，湘波容与。兴尽悲来，醉吟成调 古城阴。有官梅几许，红萼未宜簪。池面冰胶，墙腰雪老，云意还又沈沈。翠藤共、闲穿径竹，渐笑语、惊起卧沙禽。野老林泉，故王台榭，呼唤登临。 南去北来何事，荡湘云楚水，目极伤心。朱户黏鸡，金盘簇燕，空叹时序侵寻。记曾共、西楼雅集，想垂杨、还袅万丝金。待得归鞍到时，只怕春深。'),
	(6911, 72, '贺新郎', '弹铗西来路。记匆匆、经行十日，几番风雨。梦里寻秋秋不见，秋在平芜远树。雁信落、家山何处。万里西风吹客鬓，把菱花、自笑人如许。留不住，少年去。 男儿事业无凭据。记当年、悲歌击楫，酒酣箕踞。腰下光茫三尺剑，时解挑灯夜语。谁更识、此时情绪。唤起杜陵风月手，写不东渭北相思句。歌此恨，慰羁旅。'),
	(6912, 215, '柳梢青（舟泊秦淮）', '天远山围，龙蟠淡霭，虎踞斜晖。几度功名，几番成败，浑似鸥飞。 楼台一望凄迷。算到底、空争是非。今夜潮生，明朝风顺，且送船归。'),
	(6913, 335, '玉人歌', '风西起。又老尽篱花，寒轻香细。漫题红叶，句里意谁会。长天不恨江南远，苦恨无书寄。最相思，盘橘千枚，脍鲈十尾。 鸿雁阻归计。算愁满离肠，十分岂止。倦倚阑干，顾影在天际。凌烟图画青山约，总是浮生事。判从今，买取朝醒夕醉。'),
	(6914, 639, '虞美人（春愁）', '东风荡漾轻云缕，时送萧萧雨。水边画榭燕新归，一口香泥湿带、落花飞。 海棠糁径铺香绣，依旧成春瘦。黄昏庭院柳啼鸦，记得那人和月、折梨花。'),
	(6915, 639, '祝英台近（九月一日寿俞德载）', '嫩寒天，金气雨，揽断一秋事。仝样霏微，还作小晴意。世间万宝都成，些儿无欠，只待与、黄花为地。 好招致。对此郁郁葱葱，新＿未成醉。翻手为云，造物等儿戏。也知富贵来时，一班呈露，便做出人中祥瑞。'),
	(6916, 639, '桂枝香（观木樨有感寄吕郎中）', '天高气肃。正月色分明，秋容新沐。桂子初收，三十六宫都足。不辞散落人间去，怕群花、自嫌凡俗。向他秋晚，唤回春意，几曾幽独。 是天上、余香剩馥。怪一树香风，十里相续。坐对花旁，但见色浮金粟。芙蓉只解添秋思，况东篱、凄凉黄菊。入时太浅，背时太远，爱寻高躅。'),
	(6917, 634, '水调歌头', '玉鉴十分满，清露一年秋。漂流踪迹，谁念楚尾与吴头。此夜刮明尘眼。望极好张诗胆。何处有高楼。浩荡银潢冷。缥缈白云浮。 笑劳生，难坎止，亦乘流。阑干拍碎，清夜起舞不胜愁。万里关河依旧。一寸功名乌有。清泪滴衣裘。老去心空在，归梦绕苹洲。'),
	(6918, 591, '醉太平·春晚', '态浓意远。眉颦笑浅。薄罗衣窄絮风软。鬓云欺翠卷。 南园花树春光暖。红香径里榆钱满。欲上秋千又惊懒。且归休怕晚。'),
	(6919, 591, '鹧鸪天（读渊明诗不能去手，戏作小词以送之）', '晚岁躬耕不怨贫，只鸡斗酒聚比邻。都无晋宋之间事，自是羲皇以上人。 千载后，百篇存。更无一字不清真。若教王谢诸郎在，未抵柴桑陌上尘。'),
	(6920, 591, '卜算子（饮酒败德）', '盗跖傥名丘，孔子还名跖。跖圣丘愚直至今，美恶无真实。 简册写虚名，蝼蚁侵枯骨。千古光阴一霎时，且进杯中物。'),
	(6921, 591, '卜算子', '修竹翠罗寒，迟日江山暮。幽迳无人独自芳，此恨知无数。只共梅花语。 懒逐游丝去。著意寻春不肯香，香在无寻处。'),
	(6922, 591, '沁园春·和吴尉子似', '我见君来，顿觉吾庐，溪山美哉。怅平生肝胆，都成楚越，只今胶漆，谁是陈雷。搔首踟蹰，爱而不见，要得诗来渴望梅。还知否，快清风入手，日看千回。 直须抖擞尘埃。人怪我柴门今始开。向松间乍可，从他喝道：庭中且莫，踏破苍苔。岂有文章，谩劳车马，待唤青刍白饭来。君非我，任功名意气，莫恁徘徊。'),
	(6923, 591, '忆王孙（集句）', '登山临水送将归。悲莫悲兮生别离。不用登临怨落晖。昔人非。惟有年年秋雁飞。'),
	(6924, 591, '鹧鸪天·游鹅湖醉书家壁', '春入平原荠菜花，新耕雨后落群鸦。多情白发春无奈，晚日青帘酒易赊。 闲意态，细生涯。牛栏西畔有桑麻。青裙缟袂谁家女，去趁蚕生看外家。'),
	(6925, 591, '清平乐（检校山园书所见）', '断崖修竹。竹里藏冰玉。路绕清溪三百曲。香满黄昏雪屋。 行人系马疏篱。折残犹有高枝。留得东风数点，只缘娇嫩春迟。'),
	(6926, 591, '西江月（渔父词）', '千丈悬崖削翠，一川落日镕金。白鸥来往本无心。选甚风波一任。 别浦鱼肥堪脍，前村酒美重斟。千年往事已沈沈。闲管兴亡则甚。'),
	(6927, 587, '一剪梅（秋雨感悲）', '霁霭迷空晓未收。羁馆残灯，永夜悲秋。梧桐叶上三更雨，别是人间一段愁。 睡又不成梦又休。多愁多病，当甚风流。真情一点苦萦人，才下眉尖，恰上心头。'),
	(6928, 188, '丑奴儿', '绝代佳人淑且真。雪为肌骨月为神。烛前花底不胜春。 倚竹袖长寒卷翠，凌波袜小暗生尘。十分京洛旧家人。'),
	(6929, 435, '水调歌头（饶风岭上见梅）', '花上插苍碧，花下走清湍。浓霜深覆残雪，更有月相参。似我竹溪茅屋，欲晓未明天气，扶杖绕篱看。秦楚五千里，何处是江南。 饶风下，人不断，马相连。颇尝见有此客，相属意忄卷々。欲为横吹出塞，无处可寻羌管，短策叩征鞍。策断征鞍裂，惊堕玉毵毵。'),
	(6930, 498, '霜天晓角', '少年豪纵。袍锦团花凤。曾是京城游子，驰宝马、飞金鞚。 旧游浑似梦。鬓点吴霜重。多少燕情莺意，都泻入、玻璃瓮。'),
	(6931, 498, '菩萨蛮（木芙蓉）', '冰明玉润天然色。凄凉拚作西风客。不肯嫁东风。殷勤霜露中。 绿窗梳洗晚。笑把玻璃盏。斜日上妆台。酒红和困来。'),
	(6932, 498, '如梦令', '两两莺啼何许。寻遍绿阴浓处。天气润罗衣，病起却忄欠微暑。休雨。休雨。明日榴花端午。'),
	(6933, 498, '朝中措', '天容云意写秋光。木叶半青黄。珍重西风祛暑，轻衫早怯新凉。 故人情分，留连病客，孤负清觞。陌上千愁易散，尊前一笑难忘。'),
	(6934, 633, '如梦今（闺思）', '独倚博山峰小。翠雾满身飞绕。只恐学行云，去作阳台春晓。春晓。春晓。满院绿杨芳草。'),
	(6935, 633, '朝中措', '湘湖烟雨长莼丝。菰米新炊滑上匙。云散后，月斜时。潮落舟横醉不知。'),
	(6936, 633, '柳梢青（乙巳二月西兴赠别）', '十载江湖，行歌沽酒，不到京华。底事翩然，长亭烟草，衰鬓风沙。 凭高目断天涯。细雨外、楼台万家。只恐明朝，一时不见，人共梅花。'),
	(6937, 633, '好事近', '混迹寄人间，夜夜画楼银烛。谁见五云丹灶，养黄芽初熟。 春风归从紫皇游，东海宴旸谷。进罢碧桃花赋，赐玉尘千斛。'),
	(6938, 633, '蝶恋花（离小益作）', '陌上箫声寒食近。雨过园林，花气浮芳润。千里斜阳钟欲暝。凭高望断南楼信。 海角天涯行略尽。三十年间，无处无遗恨。天若有情终欲问。忍教霜点相思鬓。'),
	(6939, 633, '鹧鸪天（七之五）', '插脚红尘已是颠。更求平地上青天。新来有个生涯别，买断烟波不用钱。 沽酒市，采菱船。醉听风雨拥蓑眠。三山老子真堪笑，见事迟来四十年。'),
	(6940, 633, '鹧鸪天（葭萌驿作·七之二）', '看尽巴山看蜀山。子规江上过春残。惯眠古驿常安枕，熟听阳关不惨颜。 慵服气，懒烧丹。不妨青鬓戏人间。秘传一字神仙诀，说与君知只是顽。'),
	(6941, 633, '鹧鸪天（送叶梦锡·七之一）', '家住东吴近帝乡。平生豪举少年场。十千沽酒青楼上，百万呼卢锦瑟傍。 身易老，恨难忘。尊前赢得是凄凉。君归为报京华旧，一事无成两鬓霜。'),
	(6942, 466, '醉落魄·正月二十日张园赏海棠作', '春阴漠漠。海棠花底东风恶。人情不似春情薄。守定花枝，不放花零落。 绿尊细细供春酌。酒醒无奈愁如昨。殷勤待与东风约。莫苦吹花，何似吹愁却。'),
	(6943, 526, '东坡引·陇头梅半吐', '陇头梅半吐。江南岁将暮。闲窗尽日将愁度。黄昏愁更苦。 归期望断，双鱼尺素。念嘶骑、今到何处。残灯背壁三更鼓。斜风吹细雨。'),
	(6944, 573, '鹧鸪天（萧莹）', '花动仪容玉润颜。温柔袅娜趁清闲。盈盈醉眼横秋水，淡淡蛾眉抹远山。 膏雨霁，晓风寒。一枝红杏拆朱阑。天台迥失刘郎路，因忆前缘到世间。'),
	(6945, 573, '豆叶黄', '粉墙丹柱柳丝中。帘箔轻明花影重。午醉醒来一面风。绿葱葱。几颗樱桃叶底红。'),
	(6946, 573, '眼儿媚（王漕赴介庵赏梅）', '黄昏小宴史君家。梅粉试春华。暗香素蕊，横枝疏影，月淡风斜。 更饶红烛枝头挂，粉蜡斗香奢。元宵近也，小园先试，火树银花。'),
	(6947, 573, '新荷叶', '欲暑还凉，如春有意重归。春若归来，任他莺老花飞。轻雷淡雨，似晚风、欺得单衣。檐声惊醉，起来新绿成围。 回首分携。光风冉冉菲菲。曾几何时，故山疑梦还非。鸣琴再抚，将清恨、都入金徽。永怀桥下，系船溪柳依依。'),
	(6948, 271, '忆秦娥（正月初六日夜月）', '弯弯曲。新年新月钩寒玉。钩寒玉。凤鞋儿小，翠眉儿蹙。闹蛾雪柳添妆束。烛龙火树争驰逐。争驰逐。元宵三五，不如初六。'),
	(6949, 669, '六州歌头·东风著意', '东风著意，先上小桃枝。红粉腻，娇如醉，倚朱扉。记年时，隐映新妆面，临水岸，春将半，云日暖，斜桥转，夹城西。草软莎平，跋马垂杨渡，玉勒争嘶。认娥眉凝笑，脸薄拂燕脂。绣户曾窥，恨依依。 共携手处，香如雾，红随步，怨春迟。消瘦损，凭谁问？只花知，泪空垂。旧日堂前燕，和烟雨，又双飞。人自老，春长好，梦佳期。前度刘郎，几许风流地，花也应悲。但茫茫暮霭，目断武陵溪，往事难追。'),
	(6950, 388, '生查子', '腊月到盘洲，寒重层冰结。试去探梅花，休把南枝折。 顷刻暗同云，不觉红炉热。隐隐绿蓑翁，独钓寒江雪。'),
	(6951, 686, '菩萨蛮·眉尖早识愁滋味', '眉尖早识愁滋味，娇羞未解论心事。试问忆人不？无言但点头。 嗔人归不早，故把金杯恼。醉看舞时腰，还如旧日娇。'),
	(6952, 332, '生查子·秋来愁更深', '秋来愁更深，黛拂双蛾浅。翠袖怯春寒，修竹萧萧晚。 此意有谁知，恨与孤鸿远。小立背西风，又是重门掩。'),
	(6953, 332, '醉花阴', '淋漓尽日黄梅雨。断送春光暮。目断向高楼，持酒停歌，无计留春住。 扑人飞絮浑无数。总是添愁绪。回首问春风，争得春愁，也解随春去。'),
	(6954, 99, '相见欢', '桃源深闭春风。信难通。流水落花余恨、几时穷。 水无定。花有尽。会相逢。可是人生长在、别离中。'),
	(6955, 99, '浣溪沙', '短句赠行 樽俎风流意气倾。一杯相属忍催行。离歌更作断肠声。 衮衮大江前后浪，娟娟明月短长亭。水程山驿总关情。'),
	(6956, 589, '浪淘沙', '玉宇洗秋晴。凉月亭亭。梦回孤枕琐窗明。何处飞来三弄笛，风露凄清。 曾看玉纤横。苦爱新声。由来百虑为愁生。此夜曲中闻折柳，都是离情。'),
	(6957, 315, '雨霖铃（明皇幸西蜀）', '蛾眉修绿。正君王恩宠，曼舞丝竹。华清赐浴瑶甃，五家会处，花盈山谷。百里遗簪堕珥，尽宝钿珠玉。听突骑、鼙鼓声喧，寂寞霓裳羽衣曲。 金舆远幸匆匆速。奈六军不发人争目。明眸皓齿难恋，肠断处、绣囊犹馥。剑阁峥嵘，何况铃声，带雨相续。谩留与、千古伤神，尽入生绡幅。'),
	(6958, 267, '西江月', '淡淡薰风庭院，青青过雨园林。铜驼陌上旧莺声。今日江边重听。 落帽酒中有趣，题桥琴里无心。香残沈水缕烟轻。花影阑干人静。'),
	(6959, 267, '雨中花（岭南作）', '故国当年得意，射麋上苑，走马长楸。对葱葱佳气，赤县神州。好景何曾虚过，胜友是处相留。向伊川雪夜，洛浦花朝，占断狂游。 胡尘卷地，南走炎荒，曳裾强学应刘。空漫说、螭蟠龙卧，谁取封侯。塞雁年年北去，蛮江日日西流。此生老矣，除非春梦，重到东周。'),
	(6960, 255, '点绛唇·云透斜阳', '云透斜阳，半楼红影明窗户。暮山无数。归雁愁还去。 十里平芜，花远重重树。空凝伫。故人何处。可惜春将暮。'),
	(6961, 94, '水调歌头', '何处难忘酒，朱夏日偏长。湖山地胜潇湘，十里芰荷香。柳外新蝉惊晚，楼上疏帘重翠，簟枕晚生凉。纨扇摇霜月，曲水泛流觞。 流年去，今古梦，几千场。虚名浮利，输却几许好时光。幸有碧云深处，存取朱颜绿鬓，流落又何妨。莫厌人间世，频入醉中乡。'),
	(6962, 94, '菩萨蛮（湖光亭晚集）', '平波不尽蒹葭远。清霜半落沙痕浅。烟树晚微茫。孤鸿下夕阳。 梅花消息近。试向南枝问。记得水边春。江南别后人。'),
	(6963, 489, '临江仙', '猎猎风蒲初暑过，萧然庭户秋清。野航渡口带烟横。晚山千万叠，别鹤两三声。 秋水芙蓉聊荡桨，一樽同破愁城。蓼花滩上白鸥明。暮云连极浦，急雨暗长汀。'),
	(6964, 545, '点绛唇', '金气秋分，风清露冷秋期半。凉蟾光满。桂子飘香远。 素练宽衣，仙仗明飞观。霓裳乱。银桥人散。吹彻昭华管。'),
	(6965, 130, '满庭芳', '白玉楼高，广寒宫阙，暮云如幛褰开。银河一派，流出碧天来。无数星躔玉李，冰轮动、光满楼台。登临处，全胜瀛海，弱水浸蓬莱。 云鬟，香雾湿，月娥韵涯，云冻江梅。况餐花饮露，莫惜裴徊。坐看人间如掌，山河影、倒入琼杯。归来晚，笛声吹彻，九万里尘埃。'),
	(6966, 130, '大酺·越调春雨', '对宿烟收，春禽静，飞雨时鸣高屋。墙头青玉旆，洗铅霜都尽，嫩梢相触。润逼琴丝，寒侵枕障，虫网吹黏帘竹。邮亭无人处，听檐声不断，困眠初熟。奈愁极顿惊，梦轻难记，自怜幽独。 行人归意速。最先念、流潦妨车毂。怎奈向、兰成憔悴，卫玠清羸，等闲时、易伤心目。未怪平阳客，双泪落、笛中哀曲。况萧索、青芜国。红糁铺地，门外荆桃如菽。夜游共谁秉烛。'),
	(6967, 549, '鹤冲天', '冬冬鼓动，花外沈残漏。华月万枝灯，还清昼。广陌衣香度，飞盖影、相先后。个处频回首。锦坊西去，期约武陵溪口。 当时早恨欢难偶。可堪流浪远，分携久。小畹兰英在，轻付与、何人手。不似长亭柳。舞风眠雨，伴我一春销瘦。'),
	(6968, 549, '望书归（同前）', '边堠远，置邮稀。附与征衣衬铁衣。连夜不妨频梦见，过年惟望得书归。'),
	(6969, 468, '西江月（秋兴）', '溪面荷香粲粲，林端远岫青青。楚天秋色太多情。云卷烟收风定。 夜静冰娥欲上，梦回醉眼初醒。玉瓶未耻有新声。一曲请君来听。'),
	(6970, 451, '南柯子', '霭霭迷春态，溶溶媚晓光。不应容易下巫阳。只恐翰林前世、是襄王。暂为清歌驻，还因暮雨忙。瞥然飞去断人肠。空使兰台公子、赋高唐。'),
	(6971, 451, '念奴娇', '千门明月，天如水，正是人间佳节。开尽小梅春气透，花烛家家罗列。来往绮罗，喧阗箫鼓，达旦何曾歇。少年当此，风光真是殊绝。 遥想二十年前，此时此夜，共绾同心结。窗外冰轮依旧在，玉貌已成长别。旧著罗衣，不堪触目，洒泪都成血。细思往事，只添镜里华发。'),
	(6972, 451, '临江仙', '十里红楼依绿水，当年多少风流。高楼重上使人愁。远山将落日，依旧上帘钩。 一曲琵琶思往事，青衫泪满江州。访邻休问杜家秋。寒烟沙外鸟，残雪渡傍舟。'),
	(6973, 451, '虞美人', '陌头柳色春将半。枝上莺声唤。客游晓日绮罗稠。紫陌东风弦管、咽朱楼。 少年抚景渐虚过。终日看花坐。独愁不见玉人留。洞府空教燕子、占风流。'),
	(6974, 451, '如梦令（五之二）', '遥夜沈沈如水。风系驿亭深闭。梦破鼠窥灯，霜送晓寒侵被。无寐。无寐。门外马嘶人起。'),
	(6975, 693, '沁园春', '把我身心，为伊烦恼，算天便知。恨一回相见，百方做计，未能偎倚，早觅东西。镜里拈花，水中捉月，觑著无由得近伊。添憔悴，镇花销翠减，玉瘦香肌。 奴儿。又有行期。你去即无妨我共谁。向眼前常见，心犹未足，怎生禁得，真个分离。地角天涯，我随君去。掘井为盟无改移。君须是，做些儿相度，莫待临时。'),
	(6976, 693, '江城子', '新来曾被眼奚搐。不甘伏。怎拘束。似梦还真，烦乱损心曲。见面暂时还不见，看不足、惜不足。 不成欢笑不成哭。戏人目。远山蹙。有分看伊，无分共伊宿。一贯一文跷十贯，千不足、万不足。'),
	(6977, 278, '菩萨蛮·五云深处蓬山杳', '五云深处蓬山杳。寒轻雾重银蟾小。枕上挹余香。春风归路长。 雁来书不到。人静重门悄。一阵落花风。云山千万重。'),
	(6978, 494, '少年游（重阳）', '与客携壶上翠微。江涵秋影雁初飞。尘世难逢开口笑。年少。菊花须插满头归。酩酊但酬佳节了。云峤。登临不用怨斜晖。古往今来谁不老。多少。牛山何必更沾衣。'),
	(6979, 494, '渔父', '渔父醒，春江午。梦断落花飞絮。酒醒还醉醉还醒，一笑人间今古。'),
	(6980, 494, '行香子（与泗守过南山晚归作）', '北望平川。野水荒湾。共寻春、飞步孱颜。和风弄袖，香雾萦鬟。正酒酣时，人语笑，白云间。 飞鸿落照，相将归去，淡娟娟、玉宇清闲。何人无事，宴坐空山。望长桥上，灯火乱，使君还。'),
	(6981, 494, '天仙子', '走马探花花发末。人与化工俱不易。千回来绕百回看，蜂作婢。莺为使。谷雨清明空屈指。 白发卢郎情未已。一夜翦刀收玉蕊。尊前还对断肠红，人有泪。花无意。明日酒醒应满地。'),
	(6982, 494, '少年游', '为诗敏捷，立成。余往观之。神请余作少年游，乃以此戏之 玉肌铅粉傲秋霜。准拟凤呼凰。伶伦不见，清香未吐，且糠秕吹扬。 到处成双君独只，空无数、烂文章。一点香檀，谁能借箸，无复似张良。'),
	(6983, 494, '蝶恋花（同安生日放鱼，取金光明经救鱼事）', '泛泛东风初破五。江柳微黄，万万千千缕。佳气郁葱来绣户。当年江上生奇女。 一盏寿觞谁与举。三个明珠，膝上王文度，放尽穷鳞看圉圉。天公为下曼陀雨。'),
	(6984, 494, '蝶恋花（送潘大临）', '别酒劝君君一醉。清润潘郎，又是何郎婿。记取钗头新利市。莫将分付东邻子。 回首长安佳丽地。三十年前，我是风流帅。为向青楼寻旧事。花枝缺处余名字。'),
	(6985, 494, '浣溪沙（赠闾丘朝议，时还徐州）', '一别姑苏已四年。秋风南浦送归船。画帘重见水中仙。 霜鬓不须催我老，杏花依旧驻君颜。夜阑相对梦魂间。'),
	(6986, 494, '更漏子·送孙巨源', '水涵空，山照市。西汉二疏乡里。新白发，旧黄金。故人恩义深。 海东头，山尽处。自古空槎来去。槎有信，赴秋期。使君行不归。'),
	(6987, 494, '菩萨蛮（回文冬闺怨）', '雪花飞暖融香颊。颊香隔暖飞花雪。欺雪任单衣。衣单任雪欺。 别时梅子结。结子梅时别。归不恨开迟。迟开恨不归。'),
	(6988, 494, '菩萨蛮（感旧）', '玉笙不受朱唇暖。离声凄咽胸填满。遗恨几千秋。恩留人不留。 他年京国酒。泫泪攀枯柳。莫唱短因缘。长安远似天。'),
	(6989, 494, '南歌子（再用前韵）', '苒苒中秋过，萧萧两鬓华。寓身化世一尘沙。笑看潮来潮去、了生涯。 方士三山路，渔人一叶家。早知身世两聱牙。好伴骑鲸公子、赋雄夸。'),
	(6990, 494, '雨中花', '赏。至九月，忽开千叶一朵。雨中特为置酒，遂作。 今岁花时深院，尽日东风，荡扬茶烟。但有绿苔芳草，柳絮榆钱。闻道城西，长廊古寺，甲第名园。有国艳带酒。天香染袂，为我留连。 清明过了，残红无处，对此泪洒尊前。秋向晚，一枝何事，向我依然。高会聊追短景，清商不暇余妍。不如留取，十分春态，付与明年。'),
	(6991, 250, '武陵春', '九日黄花如有意，依旧满珍丛。谁似龙山秋兴浓。吹帽落西风。 年年岁岁登高节，欢事旋成空。几处佳人此会同。今在泪痕中。'),
	(6992, 250, '望仙楼', '小春花信日边来，未上江楼先坼。今岁东君消息。还自南枝得。 素衣染尽天香，玉酒添成国色。一自故溪疏隔。肠断长相忆。'),
	(6993, 250, '采桑子', '秋来更觉消魂苦，小字还稀。坐想行思。怎得相看似旧时。 南楼把手凭肩处，风月应知。别后除非。梦里时时得见伊。'),
	(6994, 250, '虞美人', '玉箫吹遍烟花路。小谢经年去。更教谁画远山眉。又是陌头风细、恼人时。 时光不解年年好。叶上秋声早。可怜蝴蝶易分飞。只有杏梁双燕、每来归。'),
	(6995, 250, '诉衷情·小梅风韵最妖娆', '小梅风韵最妖娆。开处雪初消。南枝欲附春信，长恨陇人遥。 闲记忆，旧江皋。路迢迢。暗香浮动，疏影横斜，几处溪桥。'),
	(6996, 250, '御街行', '年光正似花梢露。弹指春还暮。翠眉仙子望归来，倚遍玉城珠树。岂知别后，好风良月，往事无寻处。 狂情错向红尘住。忘了瑶台路。碧桃花蕊已应开，欲伴彩云飞去。回思十载，朱颜青鬓，枉被浮名误。'),
	(6997, 250, '玉楼春·琼酥酒面风吹醒', '琼酥酒面风吹醒。一缕斜红临晚镜。小颦微笑尽妖绕，浅注轻匀长淡净。 手挼梅蕊寻香径。正是佳期期未定。春来还为个般愁，瘦损宫腰罗带剩。'),
	(6998, 250, '清平乐', '双纹彩袖。笑捧金船酒。娇妙如花轻似柳。勤客千春长寿。 艳歌更倚疏弦。有情须醉尊前。恰是可怜时候，玉娇今夜初圆。'),
	(6999, 250, '生查子·轻匀两脸花', '轻匀两脸花，淡扫双眉柳。会写锦笺时，学弄朱弦后。 今春玉钏宽，昨夜罗裙皱。无计奈情何，且醉金杯酒。'),
	(7000, 250, '鹧鸪天·一醉醒来春又残', '一醉醒来春又残。野棠梨雨泪阑干。玉笙声里鸾空怨，罗幕香中燕未还。 终易散，且长闲。莫教离恨损朱颜。谁堪共展鸳鸯锦，同过西楼此夜寒。'),
	(7001, 250, '蝶恋花', '金翦刀头芳意动。彩蕊开时，不怕朝寒重。晴雪半消花{髟蒙}{髟恖}。晓妆呵尽香酥冻。 十二楼中双翠凤。缥缈歌声，记得江南弄。醉舞春风谁可共。秦云已有鸳屏梦。'),
	(7002, 250, '临江仙', '旖旎仙花解语，轻盈春柳能眠。玉楼深处绮窗前。梦回芳草夜，歌罢落梅天。 沈水浓熏绣被。流霞浅酌金船。绿娇红小正堪怜。莫如云易散，须似月频圆。'),
	(7003, 409, '望江南（四之四）', '三界里，有取总灾危。普愿从生同我愿，能于空有善思惟。三宝共住持。'),
	(7004, 352, '卜算子', '极得醉中眠，迤逦翻成病。莫是前生负你来，今世里、教孤冷。 言约全无定。是谁先薄幸。不惯孤眠惯成双，奈奴子、心肠硬。'),
	(7005, 352, '醉蓬莱', '见羞容敛翠，嫩脸匀红，素腰袅娜。红药阑边，恼不教伊过。半掩娇羞，语声低颤，问道有人知么。强整罗裙，偷回波眼，佯行佯坐。 更问假如，事还成后，乱了云鬟，被娘猜破。我且归家，你而今休呵。更为娘行，有些针线，诮未曾收啰。却待更阑，庭花影下，重来则个。'),
	(7006, 352, '鹤冲天', '梅谢粉，柳拖金。香满旧园林。养花天气半晴阴。花好却愁深。 花无数。愁无数。花好却愁春去。戴花持酒祝东风。千万莫匆匆。'),
	(7007, 352, '定风波', '把酒花前欲问君。世间何计可留春。纵使青春留得住。虚语。无情花对有情人。 任是好花须落去。自古。红颜能得几时新。暗想浮生何时好。唯有。清歌一曲倒金尊。'),
	(7008, 352, '桃源忆故人', '梅梢弄粉香犹嫩。欲寄江南春信。别后寸肠萦损。说与伊争稳。 小炉独守寒灰烬。忍泪低头画尽。眉上万重新恨。竟日无人问。'),
	(7009, 352, '蝶恋花', '永日环堤乘彩舫。烟草萧疏，恰似晴江上。水浸碧天风皱浪。菱花荇蔓随双桨。 红粉佳人翻丽唱。惊起鸳鸯，两两飞相向。且把金尊倾美酿。休思往事成惆怅。'),
	(7010, 251, '诉衷情·青梅煮酒斗时新', '青梅煮酒斗时新。天气欲残春。东城南陌花下，逢著意中人。 回绣袂，展香茵。叙情亲。此情拚作，千尺游丝，惹住朝云。'),
	(7011, 251, '浣溪沙', '阆苑瑶台风露秋。整鬟凝思捧觥筹。欲归临别强迟留。 月好谩成孤枕梦，酒阑空得两眉愁。此时情绪悔风流。'),
	(7012, 186, '渔家傲·和程公辟赠', '巴子城头青草暮。巴山重叠相逢处。燕子占巢花脱树。杯且举。瞿塘水阔舟难渡。 天外吴门清霅路。君家正在吴门住。赠我柳枝情几许。春满缕。为君将入江南去。'),
	(7013, 186, '蝶恋花·移得绿杨栽后院', '移得绿杨栽后院。学舞宫腰，二月青犹短。不比灞陵多送远。残丝乱絮东西岸。 几叶小眉寒不展。莫唱阳关，真个肠先断。分付与春休细看。条条尽是离人怨。'),
	(7014, 344, '木兰花（柳枝·三之三·林钟商）', '黄金万缕风牵细。寒食初头春有味。殢烟尤雨索春饶，一日三眠夸得意。 章街隋岸欢游地。高拂楼台低映水。楚王空待学风流，饿损宫腰终不似。'),
	(7015, 344, '临江仙引·上国', '上国。去客。停飞盖、促离筵。长安古道绵绵。见岸花啼露，对堤柳愁烟。物情人意，向此触目，无处不凄然。 醉拥征骖犹伫立，盈盈泪眼相看。况绣帏人静，更山馆春寒。今宵怎向漏永，顿成两处孤眠。'),
	(7016, 344, '迷神引·一叶扁舟轻帆卷', '一叶扁舟轻帆卷。暂泊楚江南岸。孤城暮角，引胡笳怨。水茫茫，平沙雁、旋惊散。烟敛寒林簇，画屏展。天际遥山小，黛眉浅。 旧赏轻抛，到此成游宦。觉客程劳，年光晚。异乡风物，忍萧索、当愁眼。帝城赊，秦楼阻，旅魂乱。芳草连空阔，残照满。佳人无消息，断云远。'),
	(7017, 344, '长寿乐（平调）', '尤红殢翠。近日来、陡把狂心牵系。罗绮丛中，笙歌筵上，有个人人可意。解严妆巧笑，取次言谈成娇媚。知几度、密约秦楼尽醉。仍携手，眷恋香衾绣被。 情渐美。算好把、夕雨朝云相继，便是仙禁春深，御炉香袅，临轩亲试。对天颜咫尺，定然魁甲登高第。等恁时、等著回来贺喜。好生地。剩与我儿利市。'),
	(7018, 344, '倾杯（林钟商）', '离宴殷勤，兰舟凝滞，看看送行南浦。情知道世上，难使皓月长圆，彩云镇聚。算人生、悲莫悲于轻别，最苦正欢娱，便分鸳侣。泪流琼脸，梨花一枝春带雨。 惨黛蛾、盈盈无绪。共黯然消魂，重携纤手，话别临行，犹自再三、问道君须去。频耳畔低语。知多少、他日深盟，平生丹素。从今尽把凭鳞羽。'),
	(7019, 238, '忆秦娥·花似雪', '花似雪，东风夜扫苏堤月。苏堤月，香销南国，几回圆缺？ 钱塘江山潮声歇，江边杨柳谁攀折？谁攀折，西陵渡口，古今离别。'),
	(7020, 561, '凤栖梧·霜树重重青嶂小', '霜树重重青嶂小，高栋飞云，正在霜林杪。九日黄花才过了，一尊聊慰秋容老。 翠色有无眉淡扫。身在西山，却爱东山好。流水极天横晚照，酒阑望断西河道。'),
	(7021, 619, '一剪梅', '宰相巍巍坐庙堂。说着经量。便要经量。那个臣僚上一章。头说经量。尾说经量。 轻狂太守在吾邦。闻说经量。星夜经量。山东河北久抛荒。好去经量。胡不经量。'),
	(7022, 218, '如梦令', '今夜故人独宿。小雨梨花当屋。犹有未残枝，轻脆不堪人触。休触。体触。憔悴怕惊郎目。'),
	(7023, 236, '齐天乐（甲戌湘宪种德堂灯屏）', '夜来早得东风信，潇湘一川新绿，柳色含晴，梅心沁暖，春浅千花如束。银蝉乍浴。正沙雁将还，海鳌初矗。云拥旌旗，笑声人在画阑曲。 星虹瑶树缥缈，佩环鸣碧落，瑞笼华屋。露耿铜虬，冰翻铁马，帘幕光摇金粟。迟迟倚竹。更为把瑶尊，满斟醽醁。回首宫莲，夜深归院烛。'),
	(7024, 694, '兰陵王', '絮花弱。吹满斜阳院落。秋千外，无数小舟，绿水溶溶带城郭。流光漫暗觉。辜却。莺呼燕诺。欢游地，都在梦中，双蝶翩翩度帘幕。 凭谁问康乐。又粉过新梢，红褪残萼。阑干休倚东风恶。怜瑟韵空在，鉴容偷改，青青洲渚偏杜若。故交半寥寞。漂泊。镇如昨。念玉指频弹，珠泪还阁。孤灯隐隐巫云薄。奈别遽无语，恨深谁托。明朝何处，夜渐短，听画角。'),
	(7025, 417, '齐天乐·夜来疏雨鸣金井', '夜来疏雨鸣金井，一叶舞空红浅。莲渚收香，兰皋浮爽，凉思顿欺班扇。秋光苒苒。任老却芦花，西风不管。清兴难磨，几回有句到诗卷。 长安故人别後，料征鸿声里，画阑凭偏。横竹吹商，疏砧点月，好梦又随云远。间愁似线。甚击损柔肠，不堪裁翦。听著鸣蛩，一声声是怨。'),
	(7026, 586, '满庭芳·红杏香中', '红杏香中，绿杨影里，画桥春水泠泠。深沉院满，风送卖花声。又是清明近也，粉墙畔，时有迁莺。当此际，人传天上，特降玉麒麟。 风云。今会遇，名邦坐抚，入侍严宸。更儿孙兰玉，都是宁馨。脆管繁弦竞奏，蕙炉袅，沉水烟轻。华筵罢，江城回首，一点寿星明。'),
	(7027, 373, '望海潮', '山连嵩岱，疆分齐鲁，济南自古多奇月。魄堕英昨星，芒剪瑞□，来参汉主龙飞。仙露浥繁枝。振家声赫奕，金印传龟。一种春风，后园桃李自成蹊。 淮壖暂弭旌旗。庆歌浮绿野，人静圜扉。梅破粉痕，柳回星眼，相将紫诏催归。玉勒晓骢嘶。望天墀咫尺，香惹朝衣。最好朱颜绿发，劝宴凤凰池。'),
	(7028, 126, '柳梢青（杨花）', '似雾中花，似风前雪，似雨馀云。本自无情，点萍成缘，却又多情。 西湖南陌东城。甚管定、年年送春。薄幸东风，薄情游子，薄命佳人。'),
	(7029, 64, '水调歌头', '残腊卷愁去，春至莫间愁。荣枯会有成说，无处著机谋。身世石中敲火，富贵草头垂露，何用苦贪求。三尺布衣剑，千载赤松游。 忆亲朋，方丱角，总白头。羊肠世路巇崄，莫莫且休休。选甚范侯高爵，遮莫陶公钜产，争似五湖舟。万事付蜗角，止坎谩乘流。'),
	(7030, 665, '好事近·梅片作团飞', '梅片作团飞，雨外柳丝金湿。客子短篷无据，倚长风挂席。 回头流水小桥东，烟扫画楼出。楼上有人凝伫，似旧家曾识。'),
	(7031, 316, '碧牡丹', '破镜重圆，分钗合钿，重寻绣户珠箔。说与从前，不是我情薄。都缘利役名牵，飘蓬无经，翻成轻负。别后情怀，有万千牢落。 经时最苦分携，都为伊、甘心寂寞。纵满眼、闲花媚柳，终是强欢不乐。待凭鳞羽，说与相思，水远天长又难托。而今幸已再逢，把轻离断却。'),
	(7032, 340, '沁园春·子陵先生', '子陵先生，故人光武，以道相忘。幸炎符在握，六龙在御，臣来亿兆，阳德方刚。自是先生，独全高节，归去江湖乐未央。动星象，被羊裘傲睨，一世轩裳。 高哉不事侯王。爱此地山高水更长。盖先生心地，超乎日月，又谁如光武，器量包荒。立懦帘顽，有功名教，万世清风更激扬。无古今，想云山郁郁，江水泱泱。'),
	(7033, 696, '柳梢青·病酒心情', '病酒心情。唤愁无限，可奈流莺。又是一年，花惊寒食，柳认清明。 天涯翠巘层层。是多少、长亭短亭。倦倚东风，只凭好梦，飞到银屏。'),
	(7034, 47, '念奴娇·送张明之赴京西幕', '艅艎东下，望西江千里，苍茫烟水。试问襄州何处是，雉堞连云天际。叔子残碑，卧龙陈迹，遗恨斜阳里。后来人物，如君瑰伟能几。 其肯为我来耶，河阳下士，正自强人意。勿谓时平无事也，便以言兵为讳。眼底山河，楼头鼓角，都是英雄泪。功名机会，要须问闲先备。'),
	(7035, 252, '满江红', '胶扰劳生，待足后、何时是足。据见定、随家丰俭，便堪龟缩。得意浓时休进步，须知世事多翻覆。漫教人、白了少年头，徒碌碌。谁不爱，黄金屋。谁不羡，千钟禄。奈五行不是，这般题目。枉费心神空计较，儿孙自有儿孙福。也不须、采药访神仙，惟寡欲。'),
	(7036, 272, '忆秦娥', '梅花发。寒梢挂着瑶台月。瑶台月。和羹心事，履霜时节。野桥流水声鸣咽。行人立马空愁绝。空愁绝。为谁凝伫，为谁攀折。'),
	(7037, 272, '满江红（刘知郡生朝）', '秀野诗翁，念故山、十年乖隔。聊命驾、朱门旧隐，绿槐新陌。好雨初晴仍半暖，金釭玉斝开瑶席。更流传、丽藻借江天，留春色。 过里社，将儿侄。谈往事，悲陈迹。喜尊前现在，镜中如昔。两鬓全期烟树绿，方瞳好映寒潭碧。但一年、一度一归来，欢何极。'),
	(7038, 272, '西江月', '睡处林风瑟瑟，觉来山月团团。身心无累久轻安。况有清池凉馆。 句稳翻嫌白俗，情高却笑郊寒。 兰膏元自少陵残。好处金章不换。'),
	(7039, 272, '菩萨蛮（次圭父回文韵）', '暮江寒碧萦长路。路长萦碧寒江暮。花坞夕阳斜。斜阳夕坞花。 客愁无胜集。集胜无愁客。醒似醉多情。情多醉似醒。'),
	(7040, 512, '念奴娇', '年来衰懒，渐无心赏遍，目前佳趣。寂寂墙险春荠老，不到先生鼎俎。诗卷寻医，禅林结局，酒入昏田务。山头云气，为谁来往朝暮。犹有筇杖多情，扪萝踏石，坠半严华雨。更向葭丛摇短艇，惊起飞鸿烟渚。横玉凄清，焦桐古淡，一笑忘千虑。更阑人静，此声今在何处。'),
	(7041, 682, '卷珠帘', '记得来时春未暮。执手攀花，袖染花梢露。暗卜春心共花语。争寻双朵争先去。 多情因甚相辜负。轻拆轻离，欲向谁分诉。泪湿海棠花枝处。东君空把奴分付。'),
	(7042, 682, '减字木兰花', '落花飞絮。杳杳天涯人甚处。欲寄相思。春尽衡阳雁渐稀。 离肠泪眼。肠断泪痕流不断。明月西楼。一曲阑干一倍愁。'),
	(7043, 381, '渔父词', '此事楞严常露布。梅华雪月交光处。一笑寥寥空万古。风瓯语。迥然银汉横天宇。蝶梦南华方栩栩。斑斑谁跨丰干虎。而今忘却来时路。江山暮。天涯目送鸿飞去。'),
	(7044, 17, '柳梢青·吴中', '岸草平沙。吴王故苑，柳袅烟斜。雨后寒轻，风前香软，春在梨花。 行人一棹天涯。酒醒处，残阳乱鸦。门外秋千，墙头红粉，深院谁家？'),
	(7045, 17, '诉衷情·寒食', '涌金门外小瀛洲。寒食更风流。红船满湖歌吹，花外有高楼。晴日暖，淡烟浮。恣嬉游。三千粉黛，十二阑干，一片云头。'),
	(7046, 410, '潇湘忆故人慢', '薰风微动，方樱桃弄色，萱草成窠。翠帏敞轻罗。试冰簟初展，几尺湘波。疏帘广厦，寄潇洒、一枕南柯。引多少、梦中归绪，洞庭雨棹烟蓑。 惊回处，闲昼永，但时时，燕雏莺友相过。正绿影婆娑。况庭有幽花，池有新荷。青梅煮酒，幸随分、赢得高歌。功名事、到头终在，岁华忍负清和。'),
	(7047, 156, '菩萨蛮·楼头尚有三通鼓', '楼头尚有三通鼓，何须抵死催人去！上马苦匆匆，琵琶曲未终。 回头凝望处，那更廉纤雨。漫道玉为堂，玉堂今夜长。'),
	(7048, 162, '蝶恋花（情景）', '绣幕茫茫罗帐卷。春睡腾腾，困人娇波慢。隐隐枕痕留玉脸。腻云斜溜钗头燕。远梦无端欢又散。泪落胭脂，界破蜂黄浅。整了翠鬟匀了面。芳心一寸情何限。'),
	(7049, 74, '玉楼春·东山探梅', '冷冷水向桥东去。漠漠云归溪上住。疏风淡月有来时，流水行云无觅处。 佳人独立相思苦。薄袖欺寒脩竹暮。白头空负雪边春，着意问春春不语。'),
	(7050, 404, '点绛唇（艳香茉莉）', '畏日炎炎，梵香一炷薰亭院。鼻根充满。好利心殊浅。 贝叶书名，名义谁能辨。西风远。胜鬟不见。喜见琼花面。'),
	(7051, 668, '南乡子', '人有几何般。富贵荣华总是闲。自古英雄都如梦，为官。宝玉妻男宿业缠。 年迈已衰残。鬓发苍浪骨髓干。不道山林有好处，贪欢。只恐痴迷误了贤。'),
	(7052, 448, '蝶恋花', '半夜六龙飞海峤。滉漾鳌波，露出珊瑚小。玉粉枝头春意早。东风未绿瀛洲草。 姑射仙人真窈窕。净练明妆，如伴商岩老。梦入水云间缥缈。一楼明月千山晓。'),
	(7053, 476, '酹江月（秋夕兴元使院作，用东坡赤壁韵）', '神州沉陆，问谁是、一范一韩人物。北望长安应不见，抛却关西半壁。塞马晨嘶，胡笳夕引，赢得头如雪。三秦往事，只数汉家三杰。试看百二山河，奈君门万里，六师不发朝议主和。阃外何人回首处，铁骑千群都灭富平之败。拜将台欹，怀贤阁杳，空指冲冠发。阑干拍遍，独对中天明月。'),
	(7054, 4, '武陵春', '燕子飞来花在否，微雨退、掩重门。正满院梨花雪照人。独自个、怯黄昏。 轻风淡月总消魂。罗衣暗惹啼痕。谩觑着、秋千腰褪裙。可セ是、不宜春。'),
	(7055, 129, '菩萨蛮·题梅扇', '梅花韵似才人面。为伊写在春风扇。人面似花妍。花应不解言。 在手微风动。勾引相思梦。莫用插酴醿。酴醿羞见伊。'),
	(7056, 609, '鹧鸪天', '谁折南枝傍小丛。佳人丰色与梅同。有花无叶真潇酒，不问胭脂借淡红。 应未许，嫁春风。天教雪月伴玲珑。池塘疏影伤幽独，何似横斜酒盏中。'),
	(7057, 699, '水调歌头（寄奥屯竹庵察副留金陵约游扬州不果）', '腰缠十万贯，骑鹤上扬州。诗翁那得有此，天地一扁舟。二十四番风信，二十四桥风景，正好及春游。挂席欲东下，烟雨暗层楼。 紫绮冠，绿玉杖，黑貂裘。沧波万里，浩荡踪迹寄浮鸥。想杀南台御史，笑杀南州孺子，何事此淹留。远思渺无极，日夜大江流。'),
	(7058, 687, '莺啼序（吴江长桥）', '银云卷晴缥渺，卧长龙一带。柳丝蘸、几族柔烟，两市帘栋如画。芳草岸、弯环半玉，鳞鳞曲港双流会。看碧天连水，翻成箭样风快。 白露横江，一苇万顷，问灵槎何在。空翠湿衣不胜寒，日华金掌沆瀣。甃花平、绿文衬步，琼田涌出神仙界。黛眉修，依约雾鬟，在秋波外。 阁嘘青蜃，楼啄彩虹，飞盖蹴鳌背。灯火暮，相轮倒影，偷睇别浦，片片归帆，远自天际。舞蛟幽壑，栖鸦古木，有人翦取松江水，忆细鳞巨口鱼堪鲙。波涵笠泽，时见静影浮光，霏阴万貌千态。 蒹葭深处，应有闲鸥，寄语休见怪。倩洗却、香红尘面，买个扁舟，身世飘萍，名利微芥。阑干拍遍，除东曹掾，与天随子是我辈，仅胸中、著得乾坤大。亭前无限惊涛，总把遥吟，月明满载。'),
	(7059, 687, '潇湘神（端午竞渡棹歌）', '望湖天。望湖天。绿杨深处鼓F12AF12A。好是年年三二月，湖边日日看划船。'),
	(7060, 124, '声声慢（送王圣与次韵）', '琼壶歌月，白发簪花，十年一梦扬州。恨入琵琶，小怜重见湾头。尊前漫题金缕，奈芳情、已逐东流。还送远，甚长安乱叶，都是闲愁。 次第重阳近也，看黄花绿酒，也合迟留。脆柳无情，不堪重系行舟。百年正消几别，对西风、休赋登楼。怎去得，怕凄凉时节，团扇悲秋。'),
	(7061, 124, '浪淘沙', '新雨洗晴空。碧浅眉峰。翠楼西畔画桥东。柳线嫩黄才半染，眼眼东风。 绣户掩芙蓉。帐减香筒。远烟轻霭弄春容。雁雁又归莺未到，谁寄愁红。'),
	(7062, 71, '水调歌头', '寂寂复寂寂，此月古时明。银河也变成陆，灰劫断槎横。历落英雄孺子，灭没龙光牛斗，胜败黯然平。玉笛叫空阔，终有故人情。 雁南飞，乌绕树，鹤归城。问君有酒，何不鼓瑟更吹笙。我饮呜呜起舞，我舞僛僛白发，顾影可怜生。旧日中秋客，几处几回晴。'),
	(7063, 71, '莺啼序（感怀）', '匆匆何须惊觉，唤草庐人起。算成败利钝，非臣逆睹，至死后已。又何似、采桑八百，看蚕夜织小窗里。漫二升自苦，教人吊卧龙里。 别有佳人，追桃恨李。拥凝香绣被。争知道、壮士悲歌，萧萧正度寒水。问荆卿、田横古墓，更谁载酒为君酹。过霜桥落月，老人不见遗履。 置之勿道，逝者如斯，甚矣衰久矣。君其为吾归计，为耕计。但问某所泉甘，何乡鱼美。此生不愿多才艺。功名马上兜鍪出，莫书生、误尽了人间事。昔年种柳江潭，攀枝折条，噫嘻树犹如此。 登高一笑，把菊东篱，且复聊尔耳。试回首、龙山路断，走马台荒，渭水秋风，沙河夜市。休休莫莫，毋多酌我，我狂最喜高歌去，但高歌、不是番腔底。此时对影成三，呼娥起舞，为何人喜。'),
	(7064, 71, '青玉案（暮春旅怀）', '无肠可断听花雨。沈沈已是三更许。如此残红那得住。一春情绪。半生羁旅。寂寞空山语。 霖铃不是相思阻。四十平分犹过五。渐远不知何杜宇。不如归去。不如归去。人在江南路。'),
	(7065, 640, '沁园春（问杜鹃）', '为问杜鹃，抵死催归，汝胡不归。似辽东白鹤，尚寻华表，海中玄鸟，犹记乌衣。吴蜀非遥，羽毛自好，合趁东风飞向西。何为者，却身羁荒树，血洒芳枝。 兴亡常事休悲。算人世荣华都几时。看锦江好在，卧龙已矣，玉山无恙，跃马何之。不解自宽，徒然相劝，我辈行藏君岂知。闽山路，待封侯事了，归去非迟。'),
	(7066, 112, '杏花天·鬓棱初翦玉纤弱', '鬓棱初翦玉纤弱。早春入、屏山四角。少年买困成欢谑。人在浓香绣幄。 霜丝换、梅残梦觉。夜寒重、长安紫陌。东风入户先情薄。吹老灯花半萼。'),
	(7067, 112, '虞美人影·咏香橙', '黄包先著风霜劲。独占一年佳景。点点吴盐雪凝。玉脍和齑冷。 洋园谁识黄金径。一棹洞庭秋兴。香荐兰皋汤鼎。残酒西窗醒。'),
	(7068, 112, '采桑子·水亭花上三更月', '水亭花上三更月，扇与人闲。弄影阑干。玉燕重抽拢坠簪。 心期偷卜新莲子，秋入眉山。翠破红残。半簟湘波生晓寒。'),
	(7069, 112, '烛影摇红·越上霖雨应祷', '秋入灯花，夜深檐影琵琶语。越娥青镜洗红埃，山斗秦眉妩。相间金茸翠亩。认城阴、春耕旧处。晚舂相应，新稻炊香，疏烟林莽。 清磬风前，海沈宿袅芙蓉炷。阿香秋梦起娇啼，玉女传幽素。人驾梅槎未渡。试梧桐、聊分宴俎。采菱别调，留取蓬莱，霎时云住。'),
	(7070, 112, '风入松·桂', '兰舟高荡涨波凉。愁被矮桥妨。暮烟疏雨西园路，误秋娘、浅约宫黄。还泊邮亭唤酒，旧曾送客斜阳。 蝉声空曳别枝长。似曲不成商。御罗屏底翻歌扇，忆西湖、临水开窗。和醉重寻幽梦，残衾已断熏香。'),
	(7071, 112, '江神子·送翁五峰自鹤江还都', '西风一叶送行舟。浅迟留。舣汀洲。新浴红衣，绿水带香流。应是离宫城外晚，人伫立，小帘钩。 新归重省别来愁。黛眉头。半痕秋。天上人间，斜月绣针楼。湘浪莫迷花蝶梦，江上约，负轻鸥。'),
	(7072, 112, '探芳新·吴中元日承天寺游人', '九街头，正软尘润酥，雪销残溜。禊赏祇园，花艳云阴笼昼。层梯峭空麝散，拥凌波、萦翠袖。叹年端、连环转，烂漫游人如绣。 肠断回廊伫久。便写意溅波，传愁蹙岫。渐没飘鸿，空惹闲情春瘦。椒杯香乾醉醒，怕西窗、人散后。暮寒深，迟回处、自攀庭柳。'),
	(7073, 112, '霜花腴·重阳前一日泛石湖', '翠微路窄，醉晚风、凭谁为整欹冠。霜饱花腴，烛消人瘦，秋光作也都难。病怀强宽。恨雁声、偏落歌前。记年时、旧宿凄凉，暮烟秋雨野桥寒。 妆靥鬓英争艳，度清商一曲，暗坠金蝉。芳节多阴，兰情稀会，晴晖称拂吟笺。更移画船。引佩环、邀下婵娟。算明朝、未了重阳，紫萸应耐看。'),
	(7074, 112, '虞美人·秋感', '背庭缘恐花羞坠。心事遥山里。小帘愁卷月笼明。一寸秋怀禁得、几蛩声。 井梧不放西风起。供与离人睡。梦和新月未圆时。起看檐蛛结网、又寻思。'),
	(7075, 112, '解蹀躞·醉云又兼醒雨', '醉云又兼醒雨，楚梦时来往。倦蜂刚著梨花、惹游荡。还作一段相思，冷波叶舞愁红，送人双桨。 暗凝想。情共天涯秋黯，朱桥锁深巷。会稀投得轻分、顿惆怅。此去幽曲谁来，可怜残照西风，半妆楼上。'),
	(7076, 112, '六么令·夷则宫七夕', '露蛩初响，机杼还催织。婺星为情慵懒，伫立明河侧。不见津头艇子，望绝南飞翼。云梁千尺。尘缘一点，回首西风又陈迹。 那知天上计拙，乞巧楼南北。瓜果几度凄凉，寂寞罗池客。人事回廊缥缈，谁见金钗擘。今夕何夕。杯残月堕，但耿银河漫天碧。'),
	(7077, 112, '过秦楼·黄钟商芙蓉', '藻国凄迷，麹澜澄映，怨入粉烟蓝雾。香笼麝水，腻涨红波，一镜万妆争妒。湘女归魂，佩环玉冷无声，凝情谁诉。又江空月堕，凌波尘起，彩鸳愁舞。还暗忆、钿合兰桡，丝牵琼腕，见的更怜心苦。玲珑翠屋，轻薄冰绡，稳称锦云留住。生怕哀蝉，暗惊秋被红衰，啼珠零露。能原注去声西风老尽，羞趁东风嫁与。'),
	(7078, 112, '齐天乐·烟波桃叶西陵路', '烟波桃叶西陵路，十年断魂潮尾。古柳重攀，轻鸥聚别，陈迹危亭独倚。凉飕乍起，渺烟碛飞帆，暮山横翠。但有江花，共临秋镜照憔悴。 华堂烛暗送客，眼波回盼处，芳艳流水。素骨凝冰，柔葱蘸雪，犹忆分瓜深意。清尊未洗，梦不湿行云，漫沾残泪。可惜秋宵，乱蛩疏雨里。'),
	(7079, 112, '宴清都·连理海棠', '绣幄鸳鸯柱，红情密、腻云低护秦树。芳根兼倚，花梢钿合，锦屏人妒。东风睡足交枝，正梦枕瑶钗燕股。障滟蜡、满照欢丛，嫠蟾冷落羞度。 人闲万感幽单，华清惯浴，春盎风露。连鬟并暖，同心共结，向承恩处。凭谁为歌长恨？暗殿锁、秋灯夜语。叙旧期、不负春盟，红朝翠暮。'),
	(7080, 112, '庆春宫·秋感', '残叶翻浓，余香栖苦，障风怨动秋声。云影摇寒，波尘锁腻，翠房人去深扃。昼成凄黯，雁飞过、垂杨转青。阑干横暮，酥印痕香，玉腕谁凭。 菱花乍失娉婷。别岸围红，千艳倾城。重洗清杯，同追深夜，豆花寒落愁灯。近欢成梦，断云隔、巫山几层。偷相怜处，熏尽金篝，销瘦云英。'),
	(7081, 112, '一寸金·秋感', '秋压更长，看见姮娥瘦如束。正古花摇落，寒蛩满地，参梅吹老，玉龙横竹。霜被芙蓉宿。红绵透，尚欺暗烛。年年记、一种凄凉。绣幌金圆挂香玉。 顽老情怀，都无欢事，良宵爱幽独。叹画图难仿，橘村砧思，笠蓑有约，莼洲渔屋。心景凭谁语，商弦重、袖寒转轴。疏篱下、试觅重阳，醉擘青露菊。'),
	(7082, 112, '琐窗寒·玉兰', '绀缕堆云，清腮润玉，汜人初见。蛮腥未洗，海客一怀凄惋。渺征槎、去乘阆风，占香上国幽心展。□遗芳掩色，真恣凝澹，返魂骚畹。 一盼。千金换。又笑伴鸱夷，共归吴苑。离烟恨水，梦杳南天秋晚。比来时、瘦肌更销，冷薰沁骨悲乡远。最伤情、送客咸阳，佩结西风怨。'),
	(7083, 303, '水调歌头（丙戌寿蜀阃）', '千一载英杰，百二国山河。提封几半宇宙，万里仗天戈。十乘晋军旗鼓，三岁秦关扃锁，地利属人和。位次功第一，未数＿侯何。 建青油，持柴荷，听黄麻。乾坤整顿都了，玉殿侍羲娥。且醉东湖花柳，却泛西湖舟楫，留不住岷峨。谁为语儒馆，浓墨被诗歌。'),
	(7084, 117, '如梦令', '插遍门前杨柳。又是清明时候。岁月不饶人，鬓影星星知否。知否。知否。且尽一杯春酒。'),
	(7085, 56, '好事近', '秋色到东篱，一种露红先占。应念金英冷淡，摘胭脂浓染。 依稀十月小桃花，霜蕊破霞脸。何事渊明风致，却十分娇艳。'),
	(7086, 557, '念奴娇', '梅花度曲，被多情勾引，枝枝看遍。暗忆孤山今夜月，疏影横斜镜面。萼绿凌风，云英怯冷，未放瑶池宴。人间蜂蝶，也知无计迷恋。 闻道管领多才，清词好句，泥落空梁燕。好唤蕊珠供彩笔，莫待随风面旋。千岁蟠虬，双栖么凤，咫尺蓬莱殿。湖边春暗，料应日日酬劝。'),
	(7087, 49, '卜算子（惜海棠）', '尽是手成持，合得天饶借。风雨于花有底雠，著意相陵藉。 做暖逼教开，做冷催教谢。不负明年花下人，只负栽花者。'),
	(7088, 49, '贺新郎（癸亥九日）', '宿雨轻飘洒。少年时、追欢记节，同人于野。老去登临无脚力，徒倚屋东篱榭。但极目、海山如画。千古惟传吹帽汉，大将军、野马尘埃也。须彩笔，为陶写。 鹤归旧里空悲咤。叹原头、累累高冢，洛英凋谢。留得香山病居士，却入渔翁保社。怅谁伴、先生情话。樽有葡萄簪有菊，西凉州、不似东篱下。休唤醒，利名者。'),
	(7089, 513, '卜算子', '古寺枕空山，楼上昏钟静。饥鼠偷灯尾蘸油，悄悄无人影。 长剑匣中鸣，今古深思省。此夕行藏独倚楼，风雨凄凄冷。'),
	(7090, 513, '行香子（题罗浮）', '满洞苔钱。买断风烟。笑桃花流落晴川。石楼高处，夜夜啼猿。看二更云，三更月，四更天。 细草如毡。独枕空拳。与山麋、野鹿同眠。残霞未散，淡雾沈绵。是晋时人，唐时洞，汉时仙。洞府自唐尧时始开，至东晋葛稚川方来。及伪刘称汉，此时方显，遂兴观。'),
	(7091, 113, '上西平·送陈舍人', '跨征鞍，横战槊，上襄州。便匹马、蹴踏高秋。芙蓉未折，笛声吹起塞云愁。男儿若欲树功名，须向前头。 凤雏寒，龙骨朽，蛟渚暗，鹿门幽。阅人物、渺渺如沤。棋头已动，也须高著局心筹。莫将一片广长舌，博取封侯。'),
	(7092, 57, '糖多令（登多景楼）', '何处浣离忧。消除许大愁。望长江、衮衮东流。一去乡关能几日，才屈指、又中秋。 芦叶满汀洲。沙矶小艇收。醉归来、明月江楼。欲把情怀输写尽，终不似、少年游。'),
	(7093, 84, '清平乐·柳边深院', '柳边深院。燕语明如翦。消息无凭听又懒。隔断画屏双扇。 宝杯金缕红牙。醉魂几度儿家。何处一春游荡，梦中犹恨杨花。'),
	(7094, 91, '西江月', '一片秋香世界，几层凉雨阑干。青天不惜烂银盘，借与先生为劝。 酒唤诗来酒外，人言身在人间，如何得似碧云间。且共嫦娥相伴。'),
	(7095, 91, '海棠春令', '似红如白含芳意。锦宫外、烟轻雨细。燕子不知愁，惊堕黄昏泪。 烛花偏在红帘底。想人怕、春寒正睡。梦著玉环娇，又被东风醉。'),
	(7096, 149, '虞美人', '峰岭高秀过之，观居士题颜，且歌其所虞美人。羲亦作一解 阑干表立苍龙背。三面＿天翠。东游才上小蓬莱。不见此楼烟雨、未应回。 而今指点来时路。却是冥＿处。老仙鹤驭几时归。未必山川城郭、是耶非。'),
	(7097, 149, '凄凉犯', '闻马嘶。出城四顾，则荒烟野草，不胜凄黯，乃著此解。琴有凄凉调，假以为名。凡曲言犯者，谓以宫犯商、商犯宫之类。如道调宫上字住，双调亦上字住。所住字同，故道调曲中犯双调，或于双调曲中犯道调，其他准此。唐人乐书云：犯有正、旁、偏、侧。宫犯宫为正，宫犯商为旁，宫犯角为偏，宫犯羽为侧。此说非也。十二宫所住字各不同，不容相犯，十二宫特可犯商、角、羽耳。予归行都，以此曲示国工田正德，使以哑XXXX栗角吹之，其韵极美，亦曰瑞鹤仙影 绿杨巷陌。秋风起、边城一片离索。马嘶渐远，人归甚处，戍楼吹角。情怀正恶。更衰草寒烟淡薄。似当时、将军部曲，迤逦度沙漠。 追念西湖上，小舫携歌，晚花行乐。旧游在否，想如今、翠凋红落。漫写羊裙，等新雁来时系著。怕匆匆、不肯寄与，误后约。'),
	(7098, 149, '诉衷情（端午宿合路）', '石榴一树浸溪红。零落小桥东。五日凄凉心事，山雨打船篷。 谙世味，楚人弓。莫忡忡。白头行客，不采苹花，孤负薰风。'),
	(7099, 149, '鹧鸪天（丁巳元日）', '柏绿椒红事事新。隔篱灯影贺年人。三茅钟动西窗晓，诗鬓无端又一春。 慵对客，缓开门。梅花闲伴老来身。娇儿学作人间字，郁垒神荼写未真。'),
	(7100, 149, '虞美人（赋牡丹）', '西园曾为梅花醉。叶翦春云细。玉笙凉夜隔帘吹。卧看花梢摇动、一枝枝。 娉娉袅袅教谁惜。空压纱巾侧。沈香亭北又青苔。唯有当时蝴蝶、自飞来。'),
	(7101, 639, '七娘子（三衢道中作）', '风流家世传张绪。似灵和新种垂缕。绮席离词，银台奏赋。当年梦绕蓬山路。 卖花声断蓝桥暮。记吟鞭醉帽曾经处。蜀郡归来，荆州老去。心情零乱随风絮。'),
	(7102, 446, '乌夜啼', '潇湘雨打船篷。别离中。愁见拍天沧水、搅天风。 留不住。终须去。莫匆匆。后夜一尊何处、与谁同。'),
	(7103, 446, '鹧鸪天·一夜冰澌满玉壶', '一夜冰澌满玉壶。五更喜气动洪炉。门前桃李知麟集，庭下芝兰看鲤趋。 泉脉动，草心苏。日长添得绣工夫。试询补衮弥缝手，真个曾添一线无。'),
	(7104, 456, '乌夜啼', '墙外雨肥梅子，阶前水绕荷花。阴阴庭户熏风满，水纹簟、怯菱芽。 春尽难凭燕语，日长惟有蜂衙。沈香火冷珠帘暮，个人在、碧窗纱。'),
	(7105, 456, '长相思', '对重阳。感重阳。身在西风天一方。年年人断肠。 景凄凉。客凄凉。纵有黄花只异乡。晚云连梦长。'),
	(7106, 591, '霜天晓角（赤壁）', '雪堂迁客。不得文章力。赋写曹刘兴废，千古事、泯陈迹。 望中矶岸赤。直下江涛白。半夜一声长啸，悲天地、为予窄。'),
	(7107, 591, '如梦令（赠歌者）', '韵胜仙风缥缈。的皪娇波宜笑。串玉一声歌，占断多情风调。清妙。清妙。留住飞云多少。'),
	(7108, 591, '东坡引', '花梢红未足。条破惊新绿。重帘下遍阑干曲。有人春睡熟。有人春睡熟。 鸣禽破梦，云偏目蹙。起来香腮褪红玉。花时爱与愁相续。罗裙过半福。罗裙过半幅'),
	(7109, 591, '瑞鹧鸪', '期思溪上日千回，樟木桥边酒数杯。人影不随流水去，醉颜重带少年来。 疏蝉响涩林逾静，冷蝶飞轻菊半开。不是长卿终慢世，只缘多病又非才。'),
	(7110, 591, '鹧鸪天·发底青青无限春', '发底青青无限春。落红飞雪谩纷纷。黄花也伴秋光老，何事尊前见在身。 书万卷，笔如神。眼看同辈上青云。个中不许儿童会，只恐功名更逼人。'),
	(7111, 591, '千年调·卮酒向人时', '蔗庵小阁名曰“卮言”，作此词以嘲之。 卮酒向人时，和气先倾倒。最要然然可可，万事称好。滑稽坐上，更对鸱夷笑。寒与热，总随人，甘国老。 少年使酒，出口人嫌拗。此个和合道理，近日方晓。学人言语，未会十会巧。看他们，得人怜，秦吉了。'),
	(7112, 591, '洞仙歌（丁卯八月病中作）', '贤愚相去，算其间能几。差以毫厘缪千里。细思量义利，舜跖之分，孳孳者，等是鸡鸣而起。 味甘终易坏，岁晚还知，君子之交淡如水。一饷聚飞蚊，其响如雷，深自觉、昨非今是。羡安乐窝中泰和汤，更剧饮，无过半醺而已。'),
	(7113, 591, '满江红（中秋）', '美景良辰，算只是、可人风月。况素节扬辉，长是十分清彻。著意登楼瞻玉兔，何人张幕遮银阙。倩飞廉、得得为吹开，凭谁说。 弦与望，从圆缺。今与昨，何区别。羡夜来手把，桂花堪折。安得便登天柱上，从容陪伴酬佳节。更如今，不听尘谈清，愁如发。'),
	(7114, 591, '水调歌头（即席和金华杜仲高韵，并寿诸友，惟酹乃佳耳）', '万事一杯酒，长叹复长歌。杜陵有客，刚赋云外筑婆娑。须信功名儿辈，谁识年来心事，古井不生波。种种看余发，积雪就中多。 二三子，问丹桂，倩素娥。平生萤雪，男儿无奈五车何。看取长安得意，莫恨春风看尽，花柳自蹉跎。今夕且欢笑，明月镜新磨。'),
	(7115, 591, '水调歌头·寿赵漕介庵', '千里渥洼种，名动帝王家。金銮当日奏草，落笔万龙蛇。带得无边春下，等待江山都老，教看鬓方鸦。莫管钱流地，且拟醉黄花。 唤双成，歌弄玉，舞绿华。一觞为饮千岁，江海吸流霞。闻道清都帝所，要挽银河仙浪，西北洗胡沙。回首日边去，云里认飞车。'),
	(7116, 591, '沁园春（答杨世长）', '我醉狂吟，君作新声，倚歌和之。算芬芳定向，梅间得意，轻清多是，雪里寻思。朱雀桥边，何人曾道，野草斜阳春燕飞。都休问，甚元无霁雨，却有晴霓。 诗坛千丈崔嵬。更有笔如山墨作溪。看君才未数，曹刘敌手，风骚合受，屈宋降旗。谁识相如，平生自许，慷慨须乘驷马归。长安路，问垂虹千柱，何处曾题。'),
	(7117, 591, '沁园春', '思之鄙人也。期思属弋阳郡，此地旧属弋阳县。虽古之弋阳、期思，见之图记者不同，然有弋阳则有期思也。桥坏复成，父老请余赋，作沁园春以证之 有美人兮，玉佩琼琚，吾梦见之。问斜阳犹照，渔樵故里，长桥谁记，今古期思。物化苍茫，神游仿佛，春与猿吟秋鹤飞。还惊笑，向晴波忽见，千丈虹霓。 觉来西望崔嵬。更上有青枫下有溪。待空山自荐，寒泉秋菊，中流却送，桂棹兰旗。万事长嗟，百年双鬓，吾非斯人谁与归。凭阑久，正清愁未了，醉墨休题。'),
	(7118, 591, '念奴娇', '洞庭春晚，旧传恐是，人间忧物。收拾瑶池倾国艳，来向朱栏一壁。透户龙香，隔帘莺语，料得肌如雪。月妖真态，是谁教避人杰。 酒罢归对寒窗，相留昨夜，应是梅花发。赋了高唐犹想像，不管孤灯明灭。半面难期，多情易感，愁点星星发。绕梁声在，为伊忘味三月。'),
	(7119, 591, '贺新郎（用前韵再赋）', '肘后俄生柳。叹人生、不如意事，十常八九。右手淋浪才有用，闲却持螯左手。谩赢得、伤今感旧。投阁先生惟寂寞，笑是非、不了身前后。持此语，问乌有。 青山幸自重重秀。问新来、萧萧木落，颇堪秋否。总被西风都瘦损，依旧千岩万岫。把万事、无言搔首。翁比渠侬人谁好，是我常、与我周旋久。宁作我，一杯酒。'),
	(7120, 591, '丑奴儿（和陈簿）', '鹅湖山下长亭路，明月临关。明月临关。几阵西风落叶干。 新词谁解裁冰雪，笔墨生寒。笔墨生寒。曾说离愁千万般。'),
	(7121, 591, '锦帐春·席上和叔高韵', '春色难留，酒杯常浅。把旧恨、新愁相间。五更风，千里梦，看飞红几片。这般庭院。 几许风流，几般娇懒。问相见、何如不见。燕飞忙，莺语乱。恨重帘不卷。翠屏平远。'),
	(7122, 591, '沁园春（城中诸公载酒入山，余不得以止酒为解，遂破戒一醉，再用韵）', '杯汝知乎，酒泉罢侯，鸱夷乞骸。更高阳入谒，都称齑臼，杜康初筮，正得云雷。细数从前，不堪余恨，岁月都将麹蘖埋。君诗好，似提壶却劝，沽酒何哉。 君言病岂无媒。似壁上雕弓蛇暗猜。记醉眠陶令，终全至乐，独醒屈子，未免沈灾。欲听公言，惭非勇者，司马家儿解覆杯。还堪笑，借今宵一醉，为故人来。'),
	(7123, 591, '贺新郎', '觅句如东野。想钱塘、风流处士，水仙祠下。更隐小孤烟浪里，望断彭郎欲嫁。是一色、空濛难画。谁解胸中吞云梦，试呼来、草赋看司马。须更把，上林写。 鸡豚旧日渔樵社。问先生、带湖春涨，几时归也。为爱琉璃三万顷。正卧水亭烟榭。对玉塔、微澜深夜。雁鹜如云休报事，被诗逢敌手皆勍者。春草梦，也宜夏。'),
	(7124, 591, '江神子·送元济之归豫章', '乱云扰扰水潺潺。笑溪山。几时闲。更觉桃源，人去隔仙凡。万壑千岩楼外雪，琼作树，玉为栏。 倦游回首且加餐。短篷寒。画图间。见说娇颦，拥髻待君看。二月东湖湖上路，官柳嫩，野梅残。'),
	(7125, 591, '六州歌头', '西湖万顷，楼观矗千门。春风路，红堆锦，翠连云。俯层轩。风月都无际。荡空蔼，开绝境，云梦泽，饶八九，不须吞。翡翠明珰，争上金堤去，勃窣＿姗。看贤王高会，飞盖入云烟。白鹭振振，鼓咽咽。 记风流远，更休作，嬉游地，等闲看。君不见，韩献子，晋将军，赵孤存。千载传忠献，两定策，纪元勋。孙又子，方谈笑，整乾坤。直使长江如带，依前是、□赵须韩。伴皇家快乐，长在玉津边。只在南园。'),
	(7126, 591, '水调歌头（汤坡见和、用韵为谢）', '白日射金阙，虎豹九关开。见君谏疏频上，高论挽天回。千古忠肝义胆，万里蛮烟瘴雨，往事莫惊猜。政恐不免耳，消息日边来。 笑吾庐，门掩草，径封苔。未应两手无用，要把蟹螯杯。说剑论诗余事，醉舞狂歌欲倒，老子颇堪哀。白发宁有种，一一醒时栽。'),
	(7127, 587, '更漏子·烛消红', '烛消红，窗送白。冷落一衾寒色。鸦唤起，马＿行。月来衣上明。 酒香唇，妆印臂。忆共人人睡魂蝶乱，梦鸾孤。知他睡也无。'),
	(7128, 587, '夏云峰（初秋有作）', '露华清。天气爽、新秋已觉凉生。朱户小窗，坐来低按秦筝。几多妖艳，都总是、白雪余声。那更、玉肌肤韵胜，体段轻盈。 照人双眼偏明，况周郎、自来多病多情。把酒为伊，再三著意须听。销魂无语，一任侧耳与心倾。是我不卿卿，更有谁可卿卿。'),
	(7129, 8, '水调歌头（鄂渚忆浮远）', '彩舰驾飞鹢，帆影漾江乡。肥梅天气，一声横玉换新阳。惊起沙汀鸥鹭，点破暮天寒碧。极目楚天长。一抹残霞外，云断水茫茫。 逆清风，歌白雪，和沧浪。枕流亭馆，昔年行处半荒凉。我欲骖风游戏，收拾烟波佳景，一一付词章。闻说洞天好，何处水中央。'),
	(7130, 287, '水调歌头（咏梅）', '微雨眼明处，春信著南枝。百花头上消息，为我赴襟期。松下凌霜古干，竹外横窗疏影，同是岁寒姿。唤取我曹赏，莫使俗流知。 对风前，看雪后，总相宜。碧天如洗，何许羌笛月边吹。一段出群标格，合得水仙兄事，千古豫章诗。鼎鼐付佳实，终待麦秋时。'),
	(7131, 326, '水调歌头（贺广东漕蔡定夫母生日）', '玉树映阶秀，玉节逐年新。年年九月，好为阿母作生辰。涧底蒲芽九节，海底银涛万顷，酿作一杯春。泛以东篱菊，寿以漆园椿。 对西风，吹鬓雪，炷香云。郎君入奏，又迎珠幰入修门。看即金花紫诰，并举莆常两国，册命太夫人。三点台星上，一点老人星。'),
	(7132, 435, '长相思（渔父）', '山青青。水青青。两岸萧萧芦荻林。水深村又深。 风泠泠。露泠泠。一叶扁舟深处横。垂杨鸥不惊。'),
	(7133, 498, '水调歌头', '万里筹边处，形胜压坤维。恍然旧观重见，鸳瓦拂参旗。夜夜东山衔月，日日西山横雪，白羽弄空晖。人语半霄碧，惊倒路傍儿。分弓了，看剑罢，倚兰时。苍茫平楚无际，千古锁烟霏。野旷岷嶓江动，天阔崤函云拥，太白暝中低。老矣汉都护，却望玉关归。'),
	(7134, 498, '念奴娇', '水乡霜落，望西山一寸，修眉横碧。南浦潮生帆影去，日落天青江白。万里浮云，被风吹散，又被风吹积。尊前歌罢，满空凝淡寒色。 人世会少离多，都来名利，似蝇头蝉翼。赢得长亭车马路，千古＿愁如织。我辈情钟，匆匆相见，一笑真难得。明年谁健，梦魂飘荡南北。'),
	(7135, 498, '西江月', '十月谁云春小，一年两见风娇。云英此夕度蓝桥。人意花枝都好。 百媚朝天淡粉，六铢步月生绡。人间霜叶满庭皋，别有东风不老。'),
	(7136, 498, '浣溪沙', '催下珠帘护绮丛。花枝红里烛枝红。烛光花影夜葱茏。 锦地绣天香雾里，珠星璧月彩云中。人间别有几春风。'),
	(7137, 633, '恋绣衾', '无方能驻脸上红。笑浮生、扰扰梦中。平地是、冲霄路，又何劳、千日用功。 飘然再过莲峰下，乱云深、吹下暮钟。访旧隐、依然在，但鹤巢、时有堕松。'),
	(7138, 633, '好事近（十二之二）', '风露九霄寒，侍宴玉华宫阙。亲向紫皇香案，见金芝千叶。 碧壶仙露酝初成，香味两奇绝。醉后却骑丹凤，看蓬莱春色。'),
	(7139, 573, '瑞鹧鸪·榴花五月眼边明', '榴花五月眼边明。角簟流冰午梦清。江上扁舟停画桨，云间一笑濯尘缨。 主人杯酒留连意，倦客关河去住情。都付驿亭今日水，伴人东去到江城。'),
	(7140, 192, '阮郎归', '寒来暑往几时休。光阴逐水流。浮云身世两悠悠。何劳身外求。 天上月，水边楼。须将一醉酬。陶然无喜亦无忧。人生且自由。'),
	(7141, 271, '西江月（春半）', '办取舞裙歌扇，赏春只怕春寒。卷帘无语对南山。已觉绿肥红浅。 去去惜花心懒，踏青闲步江干。恰如飞鸟倦知还。淡荡梨花深院。'),
	(7142, 271, '点绛唇', '黄鸟嘤嘤，晓来却听丁丁木。芳心已逐。泪眼倾珠斛。 见自无心，更调离情曲。鸳帏独。望休穷目。回首溪山绿。'),
	(7143, 16, '画堂春（即席）', '溪边风物已春分。画堂烟雨黄昏。水沉一缕袅炉薰。尽醉芳尊。 舞袖飘摇回雪，歌喉宛转留云。人间能得几回闻。丞相休嗔。'),
	(7144, 90, '喜迁莺（守岁）', '雪消春浅。听爆竹送穷，椒花待旦，系马合簪，鸣鸦列炬，几处玳筵开宴。介我百千眉寿，齐捧玉壶金盏。最奇绝，是小桃新坼，争妍粉面。 女伴。频告语，守岁通宵，莫放笙歌散。酒晕朝霞，寒欺重翠，却忆凤屏香暖。笑拂满身花影，遥指珠帘深院。待到了，道一声稳睡，明年相见。'),
	(7145, 332, '齐天乐（端午）', '疏疏数点黄梅雨。殊方又逢重五角黍包金，菖蒲泛玉，风物依然荆楚。衫裁艾虎。更钗袅朱符，臂缠红缕。扑粉香绵，唤风绫扇小窗午。 沈湘人去已远，劝君休对酒，感时怀古。慢啭莺喉，轻敲象板，胜读离骚章句。荷香暗度。渐引入陶陶，醉乡深处。卧听江头，画船喧叠鼓。'),
	(7146, 273, '点绛唇·梅', '流水泠泠，断桥横路梅枝亚。雪花飞下。浑似江南画。 白璧青钱，欲买春无价。归来也。西风平野。一点香随马。'),
	(7147, 185, '菩萨蛮·春来春去催人老', '三月晦，送春有集，坐中偶书。 春来春去催人老，老夫争肯输年少。醉后少年狂，白髭殊未妨。 插花还起舞，管领风光处。把酒共留春，莫教花笑人。'),
	(7148, 185, '沁园春', '欹枕深轩，散帙虚堂，畏景屡移。渐披襟临水，支床就月，莲香拂面，竹色侵衣。压玉为醪，折荷当酉戋，卧看银潢星四垂。人归后，任饥蝉自啸，宿鸟相依。 痴儿。莫蹈危机。悟三十九年都尽非。任纡朱拖紫，围金珮玉，青钱流地，白璧如坻。富贵浮云，身名零露，事事无心归便归。秋风动，正吴松月冷，莼长鲈肥。'),
	(7149, 185, '满江红·自豫章阻风吴城山作', '春水迷天，桃花浪、几番风恶。云乍起、远山遮尽，晚风还作。绿卷芳洲生杜若。数帆带雨烟中落。傍向来、沙觜共停桡，伤飘泊。 寒犹在，衾偏薄。肠欲断，愁难著。倚篷窗无寐，引杯孤酌。寒食清明都过却。最怜轻负年时约。想小楼、终日望归舟，人如削。'),
	(7150, 635, '虞美人（亭下桃花盛开，作长短句咏之）', '十年花底承朝露。看到江南树。洛阳城里又东风。未必桃花得似、旧时红。 胭脂睡起春才好。应恨人空老。心情虽在只吟诗。白发刘郎孤负、可怜枝。'),
	(7151, 296, '水龙吟（上巳）', '化工收拾芳菲，晕酥翦彩迎春禊。江山影里，泰阶星聚，重寻古意。曲水流觞，晚林张宴，竹边花外。倩飞英衬地，繁枝障日，游丝驻，羲和旆。 云避清歌自止。放一钩、玉沈寒水。西园飞盖，东山携妓，古今无愧。闻道东君，商量花蕊，作明年计。待公归，独运丹青妙手，忆山阴醉。'),
	(7152, 315, '水调歌头（李太白画象）', '太白乃吾祖，逸气薄青云。开元有道，聊复乘兴一来宾。天子呼来方醉，洒面清泉微醒，余吐拭龙巾。词翰不加点，歌阕满宫春。 笔风雨，心锦绣，极清新。大儿中令，神契兼有坐忘人。不识将军高贵，醉里指污吾足，乃敢尚衣嗔。千载已仙去，图象耸风神。'),
	(7153, 315, '念奴娇·宪宗平淮西', '晚唐姑息，有多少方镇，飞扬跋扈。淮蔡雄藩联四郡，千里公然旅拒。同恶相资，潜伤宰辅，谁敢分明语。媕婀群议，共云旄节应付。 于穆天子英明，不疑不贰处，登庸裴度。往督全师威令使，擒贼功名归诉。半夜衔枚，满城深雪，忽已亡悬瓠。明堂坐治，中兴高映千古。'),
	(7154, 559, '满庭芳', '芳词来上，因俯同其韵以赐。 寰宇清夷，元宵游豫，为开临御端门。暖风摇曳，香气霭轻氛。十万钩陈灿锦，钧台外、罗绮缤纷。欢声里，烛龙衔耀，黼藻太平春。 灵鳌，擎彩岫，冰轮远驾，初上祥云。照万宇嬉游，一视同仁。更起维垣大第，通宵宴、调燮良臣。从兹庆，都愈赓载，千岁乐昌辰。'),
	(7155, 559, '探春令', '帘旌微动，峭寒天气，龙池冰泮。杏花笑吐香犹浅。又还是、春将半。 清歌妙舞从头按。等芳时开宴。记去年、对著东风，曾许不负莺花愿。'),
	(7156, 559, '念奴娇（御制）', '雅怀素态，向闲中、天与风流标格。绿锁窗前湘簟展，终日风清人寂。玉子声干，纹楸色净，星点连还直。跳丸日月，算应局上销得。 全似落浦斜晖，寒鸦游鹭，乱点沙汀碛。妙算神机，须信道，国手都无勍敌。玳席欢余，芸堂香暖，赢取专良夕。桃源归路，烂柯应笑凡客。'),
	(7157, 559, '声声慢（春）', '宫梅粉淡，岸柳金匀，皇州乍庆春回。凤阙端门，棚山彩建蓬莱。沈沈洞天向晚，宝舆还、花满钧台。轻烟里，算谁将金莲，陆地齐开。 触处笙歌鼎沸，香鞯趁，雕轮隐隐轻雷。万家帘幕，千步锦绣相挨。银蟾皓月如昼，共乘欢、争忍归来。疏钟断，听行歌、犹在禁街。'),
	(7158, 128, '一剪梅（送杨师醇赴官）', '无限江山无限愁。两岸斜阳，人上扁舟。阑干吹浪不多时，酒在离尊，情满沧洲。 早是霜华两鬓秋。目送飞鸿，那更难留。问君尺素几时来，莫道长江，不解西流。'),
	(7159, 128, '蓦山溪', '月眉星眼，阆苑真仙侣。娇小正笄年，每当筵、愁歌怕舞。水亭烟树，春去已无踪，桃源路。知何处。往事如风絮。 如今闻道，误剪香云缕，闲系小乌纱，更无心、浅匀深注。三山路杳，终不是人间，知谁与。吹箫女。共驾青鸾去。'),
	(7160, 128, '鹧鸪天', '花褪残红绿满枝。嫩寒犹透薄罗衣。池塘雨细双鸳睡，杨柳风轻小燕飞。 人别后，酒醒时。午窗残梦子规啼。尊前心事人谁问，花底闲愁春又归。'),
	(7161, 267, '减字木兰花', '年衰人老。矍铄支离君莫笑。白日青天。我自心情胜少年。 超凡入妙。游戏神通随意到。酒圣诗仙。舞棹虚空驾铁船。'),
	(7162, 267, '渔家傲', '谁转琵琶弹侧调。征尘万里伤怀抱。客散黄昏庭院悄。灯相照。春寒燕子归来早。 可惜韶光虚过了。多情人已非年少。只恐莺啼春又老。知音少。人间何处寻芳草。'),
	(7163, 267, '风流子', '吴越东风起，江南路，芳草绿争春。倚危楼纵目，绣帘初卷，扇边寒减，竹外花明。看西湖、画船轻泛水，茵幄稳临津。嬉游伴侣，两两携手，醉回别浦，歌遏南云。 有客愁如海，江山异，举目暗觉伤神。空想故园池阁，卷地烟尘。但且恁、痛饮狂歌，欲把恨怀开解，转更销魂。只是皱眉弹指，冷过黄昏。'),
	(7164, 371, '点绛唇·高柳蝉嘶', '高柳蝉嘶，采菱歌断秋风起。晚云如髻。湖上山横翠。 帘卷西楼，过雨凉生袂。天如水。画楼十二。有个人同倚。'),
	(7165, 360, '武陵春', '迎得春来闻好语，贺燕立帘钩。转蕙风光柳弄柔。喜气与春游。 万钱珍鼎期公饭，天自寿留侯。文物升平速置邮。江左属风流。王俭云：江左风流宰相，唯有谢安。'),
	(7166, 489, '诉衷情（渔父家风、醉中赠韦道士）', '杖头挑得布囊行。活计有谁争。不肯侯家五鼎，碧涧一杯羹。 溪上月，岭头云。不劳耕。瓮中春色，枕上华胥，便是长生。'),
	(7167, 489, '浣溪沙·书虞元翁书', '水榭风微玉枕凉。牙床角簟藕花香。野塘烟雨罩鸳鸯。 红蓼渡头青嶂远，绿萍波上白鸥双。淋浪淡墨水云乡。'),
	(7168, 545, '西江月·送朱泮英', '青锦缠条佩剑，紫丝络辔飞骢。入关意气喜生风。年少胸吞云梦。 金阙日高露泣，东华尘软香红。争看荀氏第三龙。春暖桃花浪涌。'),
	(7169, 130, '浪淘沙', '万叶战，秋声露结，雁度砂碛。细草和烟尚绿，遥山向晚更碧。见隐隐、云边新月白。映落照、帘幕千家，听数声何处倚楼笛。装点尽秋色。 脉脉。旅情暗自消释。念珠玉、临水犹悲感，何况天涯客。忆少年歌酒，当时踪迹。岁华易老，衣带宽、懊恼心肠终窄。飞散后、风流人阻，蓝桥约、怅恨路隔。马蹄过、犹嘶旧巷陌。叹往事、一一堪伤，旷望极。凝思又把阑干拍。'),
	(7170, 130, '少年游（黄钟楼月）', '檐牙缥缈小倡楼。凉月挂银钩。聒席笙歌，透帘灯火，风景似扬州。 当时面色欺春雪，曾伴美人游。今日重来，更无人问，独自倚阑愁。'),
	(7171, 645, '木兰花减字（赠晃无咎舞鬟）', '娉婷娜袅。红落东风青子小。妙舞逶迤。拍误周郎却未知。 花前月底。谁唤分司狂御史。欲语还休。唤不回头莫著羞。'),
	(7172, 249, '凤凰台上忆吹箫', '才短官慵，命奇人弃，年年故里来还。记往岁、莲塘送我，远赴荆蛮。莫道风情似旧，青镜里、绿鬓新斑。佳人怪，把盏为我，微敛眉山。 从来嗣宗高韵，独见赏，青云敻绝尘间。谩回首、平生醉语，一梦惊残。莫笑移花种柳，应备办、投老同闲。从枯槁，松桧耐得霜寒。'),
	(7173, 549, '望湘人（春思）', '厌莺声到枕，花气动帘，醉魂愁梦相半。被惜余薰，带惊剩眼。几许伤春晚。泪竹痕鲜，佩兰香老，湘天浓暖。记小江、风月佳时，屡约非烟游伴。 须信鸾弦易断。奈云和再鼓，曲终人远。认罗袜无踪，旧处弄波清浅。青翰棹舣，白苹洲畔。尽目临皋飞观。不解寄、一字相思，幸有归来双燕。'),
	(7174, 549, '望长安', '排办张灯春事早。十二都门。物色宜新晓。金犊车轻玉骢小。拂头杨柳穿驰道。 莼羹鲈鲙非吾好。去国讴吟，半落江南调。满眼青山恨西照。长安不见令人老。'),
	(7175, 556, '蝶恋花', '数四乘间遂道其衷。翌日复至，曰：郎之言，所不敢言，亦不敢泄。然而崔之族姻，君所详也，何不因其媒而求娶焉！张曰：予始自孩提时，性不苟合。昨日一席间，几不自持。数日来，行忘止，食忘饭，恐不能逾旦暮。若因媒氏而娶，纳采问名，则三数月间，索我于枯鱼之肆矣。婢曰：崔之贞顺自保，虽所尊不可以非语犯之。然而善属文，往往沈吟章句，怨慕者久之。君试为谕情诗以乱之。不然，无由得也。张大喜，立缀春词二首以授之。奉劳歌伴，再和前声。商调十二首之三 懊恼娇痴情未惯。不道看看，役得人肠断。万语千言都不管。兰房跬步如天远。 废寝忘餐思想遍。赖有青鸾，不必凭鱼雁。密写香笺论缱绻。春词一纸芳心乱。'),
	(7176, 468, '减字木兰花（涟水登楼寄赵伯山）', '云间皓月。光照银淮来万折。海岱楼中。拂袖雄披楚岸风。 醉余清夜。羽扇纶巾人入画。江远淮长。举首宗英醒更狂。'),
	(7177, 451, '虞美人（三之三）', '行行信马横塘畔。烟水秋平岸。绿荷多少夕阳中。知为阿谁凝恨、背西风。 红妆艇子来何处。荡桨偷相顾。鸳鸯惊起不无愁。柳外一双飞去、却回头。'),
	(7178, 451, '木兰花·秋容老尽芙蓉院', '秋容老尽芙蓉院。草上霜花匀似翦。西楼促坐酒杯深，风压绣帘香不卷。 玉纤慵整银筝雁。红袖时笼金鸭暖。岁华一任委西风，独有春红留醉脸。'),
	(7179, 248, '安公子', '渐渐东风暖。杏梢梅萼红深浅。正好花前携素手，却云飞雨散。是即是、从来好事多磨难。就中我与你才相见。便世间烦恼，受了千千万万。 回首空肠断。甚时与你同欢宴。但得人心长在了，管天须开眼。又只恐、日疏日远衷肠变。便忘了、当本深深愿。待寄封书去，更与丁宁一遍。'),
	(7180, 248, '蓦山溪', '广寒宫殿，千里同云晓。飞雪满空来，翦云英、群仙齐到。乱飘僧舍，密处洒歌楼，闲日少。风光好。且共宾朋笑。 华堂深处，满满觥船掉。梅蕊拆来看，已偷得、春风些小。绮罗香暖，不怕卷珠帘，沈醉了。樽前倒。红袖休来叫。'),
	(7181, 693, '千秋岁', '世间好事。恰恁厮当对。乍夜永，凉天气。雨稀帘外滴，香篆盘中字。长入梦，如今见也分明是。 欢极娇无力，玉软花欹坠。钗罥袖，云堆臂。灯斜明媚眼，汗浃瞢腾醉。奴奴睡，奴奴睡也奴奴睡。'),
	(7182, 693, '水调歌头·落日塞垣路', '落日塞垣路，风劲戛貂裘。翩翩数骑闲猎，深入黑山头。极目平沙千里，惟见雕弓白羽，铁面骏骅骝。隐隐望青冢，特地起闲愁。 汉天子，方鼎盛，四百州。玉颜皓齿，深锁三十六宫秋。堂有经纶贤相，边有纵横谋将，不减翠蛾羞。戎虏和乐也，圣主永无忧。'),
	(7183, 484, '菩萨蛮·画船捶鼓催君去', '画船捶鼓催君去。高楼把酒留君住。去住若为情。西江潮欲平。 江潮容易得。只是人南北。今日此尊空。知君何日同。'),
	(7184, 278, '青玉案（用贺方回韵，有所祷而作）', '小篷又泛曾行路。这身世、如何去。去了还来知几度。多情山色，有情江水，笑我归无处。 夕阳杳杳还催暮。练净空吟谢郎句。试祷波神应见许。帆开风转，事谐心遂，直到明年雨。'),
	(7185, 494, '少年游', '野作六客词，其卒章云：‘见说贤人聚吴分。试问。也应旁有老人星。’凡十五年，再过吴兴，而五人者皆已亡矣。时张仲谋与曹子方、刘景文、苏伯固、张秉道为坐客，仲谋请作后六客词 月满苕溪照夜堂。五星一老斗光芒。十五年间真梦里。何事。长庚对月独凄凉。 绿鬓苍颜同一醉。还是。六人吟笑水云乡。宾主谈锋谁得似。看取。曹刘今对两苏张。'),
	(7186, 494, '浣溪沙（寓意）', '炙手无人傍屋头。萧萧晚雨脱梧楸。谁怜季子敝貂裘。 愿我已无当世望，似君须向古人求。岁寒松柏肯惊秋。'),
	(7187, 494, '浣溪沙（有赠）', '惟见眉间一点黄。诏书催发羽书忙。从教娇泪洗红妆。 上殿云霄生羽翼，论兵齿颊带风霜。归来衫袖有天香。'),
	(7188, 494, '阳关曲（军中）', '受降城下紫髯郎。戏马台南旧战场。恨君不取契丹首，金甲牙旗归故乡。'),
	(7189, 494, '蝶恋花（密州冬夜文安国席上作）', '帘外东风交雨霰。帘里佳人，笑语如莺燕。深惜今年正月暖。灯光酒色摇金盏。 掺鼓渔阳挝未遍。舞褪琼钗，汗湿香罗软。今夜何人吟古怨。清诗未就冰生砚。'),
	(7190, 494, '少年游（端午赠黄守徐君猷）', '银塘朱槛麹尘波。圆绿卷新荷。兰条荐浴，菖花酿酒，天气尚清和。 好将沈醉酬佳节，十分酒、一分歌。狱草烟深，讼庭人悄，无吝宴游过。'),
	(7191, 494, '渔家傲（送张元唐省亲秦州）', '一曲阳关情几许。知君欲向秦川去。白马皂貂留不住。回首处。孤城不见天霖雾。 到日长安花似雨。故关杨柳初飞絮。渐见靴刀迎夹路。谁得似。风流膝上王文度。'),
	(7192, 494, '渔家傲（金陵赏心亭送王胜之龙图。王守金陵，视事一日移南郡）', '千古龙蟠并虎踞。从公一吊兴亡处。渺渺斜风吹细雨。芳草渡。江南父老留公住。 公驾飞车凌彩雾。红鸾骖乘青鸾驭。却讶此洲名白鹭。非吾侣。翩然欲下还飞去。'),
	(7193, 494, '临江仙', '九十日春都过了，贪忙何处追游。三分春色一分愁。雨翻榆荚阵，风转柳花球。 阆苑先生须自责，蟠桃动是千秋。不知人世苦厌求。东皇不拘束，肯为使君留。'),
	(7194, 494, '满江红（东武会流怀亭）', '东武南城，新堤固、涟漪初溢。隐隐遍、长林高阜，卧红堆碧。枝上残花吹尽也，与君更向江头觅。问向前、犹有几多春，三之一。 官里事，何时毕。风雨外，无多日。相将泛曲水，满城争出。君不见兰亭修禊事，当时坐上皆豪逸。到如今、修竹满山阴，空陈迹。'),
	(7195, 494, '水龙吟', '万里，不可到。天台有司马子微，身居赤域，名在绛阙，可往从之。”自然乃还，受道于子微，白日仙去。子微著《坐忘论》七篇，《枢》一篇，年百余。将终，谓弟子曰：“吾居玉霄峰，东望蓬莱，尝有真灵降焉。今为东海青童君所召。”乃蝉脱而去。其后，李太白作《大鹏赋》云：“尝见子微于江陵，谓余有仙风道骨，可与神游八极之表。”元丰七年冬，余过临淮，而湛然先生梁公在焉。童颜清澈，如二三十许人，然人亦有自少见之者。善吹铁笛，嘹然有穿云裂石之声。乃作《水龙吟》一首，记子微、太白之事，倚其声而歌之。 古来云海茫茫，道山绛阙知何处。人间自有，赤城居士，龙蟠凤举。清净无为，坐忘遗照，八篇奇语。向玉霄东望，蓬莱晻霭，有云驾、骖凤驭。 行尽九州四海，笑粉粉、落花飞絮。临江一见，谪仙风采，无言心许。八表神游，浩然相对，酒酣箕踞。待垂天赋就，骑鲸路稳，约相将去。'),
	(7196, 250, '风入松', '柳阴庭院杏梢墙。依旧巫阳。凤箫已远青楼在，水沈谁、复暖前香。临镜舞鸾离照，倚筝飞雁辞行。 坠鞭人意自凄凉。泪眼回肠。断云残雨当年事，到如今、几处难忘。两袖晓风花陌，一帘夜月兰堂。'),
	(7197, 250, '留春令', '画屏天畔，梦回依约，十洲云水。手捻红笺寄人书，写无限伤春事。别浦高楼曾漫倚。对江南千里。楼下分流水声中，有当日凭高泪。'),
	(7198, 250, '采桑子·西楼月下当时见', '西楼月下当时见，泪粉偷匀。歌罢还颦。恨隔炉烟看未真。 别来楼外垂杨缕，几换青春。倦客红尘。长记楼中粉泪人。'),
	(7199, 250, '生查子', '红尘陌上游，碧柳堤边住。才趁彩云来，又逐飞花去。 深深美酒家，曲曲幽香路。风月有情时，总是相思处。'),
	(7200, 409, '望江南（归依三宝赞·四之一）', '归依众，梵行四威仪。愿我遍游诸佛土，十方贤圣不相离。永灭世间痴。'),
	(7201, 323, '两同心·巍巍剑外', '巍巍剑外，寒霜覆林枝。望衰柳、尚色依依。暮天静、雁阵高飞。入碧云际。江山秋色，遣客心悲。 蜀道巇崄行迟。瞻京都迢递。听巴峡、数声猿啼。惟独个、未有归计。谩空怅望，每每无言，独对斜晖。'),
	(7202, 352, '圣无忧', '相别重相遇，恬如一梦须臾。尊前今日欢娱事，放盏旋成虚。 莫惜斗量珠玉，随他雪白髭须。人间长久身难得，斗在不如吾。'),
	(7203, 352, '蝶恋花', '百种相思千种恨。早是伤春，那更春醪困。薄幸辜人终不愤。何时枕畔分明问。 懊恼风流心一寸。强醉偷眠，也即依前闷。此意为君君不信。泪珠滴尽愁难尽。'),
	(7204, 352, '蝶恋花（咏枕儿）', '宝琢珊瑚山样瘦。缓髻轻拢，一朵云生袖。昨夜佳人初命偶。论情旋旋移相就。 几叠鸳衾红浪皱。暗觉金钗，磔磔声相扣。一自楚台人梦后。凄凉暮雨沾裀绣。'),
	(7205, 352, '凉州令（东堂石榴）', '翠树芳条飐。的的裙腰初染。佳人携手弄芳菲，绿阴红影，共展双纹簟。插花照影窥鸾鉴。只恐芳容减。不堪零落春晚，青苔雨后深红点。 一去门闲掩。重来却寻朱槛。离离秋实弄轻霜，娇红脉脉，似见胭脂脸。人非事往眉空敛。谁把佳期赚。芳心只愿长依旧，春风更放明年艳。'),
	(7206, 352, '浣溪沙', '青杏园林煮酒香。佳人初著薄罗裳。柳丝摇曳燕飞忙。 乍雨乍晴花自落，闲愁闲闷昼偏长。为谁消瘦损容光。'),
	(7207, 352, '渔家傲', '叶有清风花有露。叶笼花罩鸳鸯侣。白锦顶丝红锦羽。莲女妒。惊飞不许长相聚。 日脚沈红天色暮。青凉伞上微微雨。早是水寒无宿处。须回步。枉教雨里分飞去。'),
	(7208, 352, '渔家傲（与赵康靖公）', '四纪才名天下重。三朝构厦为梁栋。定册功成身退勇。辞荣宠。归来白首笙歌拥。 顾我薄才无可用。君恩近许归田垅。今日一觞难得共。聊对捧。官奴为我高歌送。'),
	(7209, 352, '减字木兰花', '歌檀敛袂。缭绕雕梁尘暗起。柔润清圆。百琲明珠一线穿。 樱唇玉齿。天上仙音心下事。留往行云。满坐迷魂酒半醺。'),
	(7210, 251, '蝶恋花', '紫菊初生朱槿坠。月好风清，渐有中秋意。更漏乍长天似水。银屏展尽遥山翠。 绣幕卷波香引穗。急管繁弦，共庆人间瑞。满酌玉杯萦舞袂。南春祝寿千千岁。'),
	(7211, 251, '木兰花', '帘旌浪卷金泥凤。宿醉醒来长瞢忪。海棠开后晓寒轻，柳絮飞时春睡重。 美酒一杯谁与共。往事旧欢时节动。不如怜取眼前人，免更劳魂兼役梦。'),
	(7212, 251, '喜迁莺', '曙河低，斜月淡，帘外早凉天。玉楼清唱倚朱弦。余韵入疏烟。 脸霞轻，眉翠重。欲舞钗钿摇动。人人如意祝炉香。为寿百千长。'),
	(7213, 251, '凤衔杯', '青苹昨夜秋风起。无限个、露莲相倚。独凭朱阑、愁望晴天际。空目断、遥山翠。 彩笺长，锦书细。谁信道、两情难寄。可惜良辰好景、欢娱地。只恁空憔悴。'),
	(7214, 251, '浣溪沙', '三月和风满上林。牡丹妖艳直千金。恼人天气又春阴。 为我转回红脸面，向谁分付紫檀心。有情须殢酒杯深。'),
	(7215, 344, '木兰花（海棠·三之二·林钟商）', '东风催露千娇面。欲绽红深开处浅。日高梳洗甚时忄欠，点滴燕脂匀未遍。 霏微雨罢残阳院。洗出都城新锦段。美人纤手摘芳枝，插在钗头和风颤。'),
	(7216, 344, '临江仙（仙吕调）', '梦觉小庭院，冷风淅淅，疏雨潇潇。绮窗外，秋声败叶狂飘。心摇。奈寒漏永，孤帏悄，泪烛空烧。无端处，是绣衾鸳枕，闲过清宵。 萧条。牵情系恨，争向年少偏饶。觉新来、憔悴旧日风标。魂消。念欢娱事，烟波阻、后约方遥。还经岁，问怎生禁得，如许无聊。'),
	(7217, 344, '鹤冲天（大石调）', '闲窗漏永，月冷霜花堕。悄悄下帘幕，残灯火。再三追往事，离魂乱、愁肠锁。无语沈吟坐。好天好景，未省展眉则个。 从前早是多成破。何况经岁月，相抛嚲。假使重相见，还得似、旧时么。悔恨无计那。迢迢良夜。自家只恁摧挫。'),
	(7218, 397, '忆余杭·长忆吴山', '长忆吴山，山上森森吴相庙。庙前江水怒为涛。千古恨犹高。 寒鸦日暮鸣还聚。时有阴云笼殿宇。别来有负谒灵祠。遥奠酒盈卮。'),
	(7219, 28, '玉蝴蝶·为甚夜来添病', '为甚夜来添病，强临宝镜，憔悴娇慵。一任钗横鬓乱，永日熏风。恼脂消榴红径里，羞玉减蝶粉丛中。思悠悠，垂帘独坐，传遍熏笼。 朦胧。玉人不见，罗裁囊寄，锦写笺封。约在春归，夏来依旧各西东。粉墙花影来疑是，罗帐雨梦断成空。最难忘，屏边瞥见，野外相逢。'),
	(7220, 621, '生查子·软金杯', '风流紫府郎，痛饮乌纱岸。柔软九回肠，冷怯玻璃盏。 纤纤白玉葱，分破黄金弹。借得洞庭春，飞上桃花面。'),
	(7221, 370, '谒金门·帘漏滴', '帘漏滴，却是春归消息。带雨牡丹无气力，黄鹂愁雨湿。 争着洛阳春色，忘却连天草碧。南浦绿波双桨急，沙头人伫立。'),
	(7222, 513, '凤栖梧·绿暗红稀春已暮', '绿暗红稀春已暮，燕子衔泥，飞入垂杨处。柳絮欲停风不住，杜鹃声里山无数。 竹杖芒鞋无定据，穿过溪南，独木横桥路。樵子渔师来又去，一川风月谁为主。'),
	(7223, 306, '谒金门·杨花落', '杨花落，燕子横穿朱阁。苦恨春醪如水薄，闭愁无处着。 绿野带红山落角，桃杏参差残萼。历历危樯沙外泊，东风晚来恶。'),
	(7224, 458, '水调歌头（登甘露寺多景楼望淮有感）', '天地本无际，南北竟谁分。楼前多景，中原一恨杳难论。却似长江万里，忽有孤山两点，点破水晶盆。为借鞭霆力，驱去附昆仑。 望淮阴，兵冶处，俨然存。看来天意，止欠士雅与刘琨。三拊当时顽石，唤醒隆中一老，细与酌芳尊。孟夏正须雨，一洗北尘昏。'),
	(7225, 664, '苏幕遮（闺怨）', '与君别，情易许。执手相将，永远成鸳侣。一去音书千万里。望断阳关，泪滴如秋雨。 到如今，成间阻。等候郎来，细把相思诉。看著梅花花不语。花已成梅，结就心中苦。'),
	(7226, 73, '少年游（戏友人与女客对棋）', '石榴花下薄罗衣。睡起却寻棋。未省高低，被伊春笋，拈了白玻璃。 钏脱钗斜浑不省，意重子声迟。对面痴心，只愁必局，肠断欲输时。'),
	(7227, 576, '阮郎归', '舞红一架欲生衣。残英辞旧枝。雨声自唱惜春词。行人应未知。 新火后，薄罗时。君归何太迟。镜中失却少年姿。年随花共飞。'),
	(7228, 236, '酹江月（南康军和苏韵）', '庐山依旧，凄凉处、无限江南风物。空翠睛岚浮汗漫，还障天东半壁。雁过孤峰，猿归危嶂，风急波翻雪。乾坤未老，地灵尚有人杰。 堪嗟漂泊孤舟，河倾斗落，客梦催明发。南浦闲云过草树，回首旌旗明灭。三十年来，十年一过，空有星星发。夜深悉听，胡笳吹彻寒月。'),
	(7229, 110, '点绛唇·送李琴泉', '江上旗亭，送君还是逢君处。酒阑呼渡。云压沙鸥暮。 漠漠萧萧，香冻梨花雨。添愁绪。断肠柔橹。相逐寒潮去。'),
	(7230, 266, '意难忘（元宵雨）', '角动寒谯。看雨中灯市，雪意潇潇。星球明戏马，歌管杂鸣刁。泥没膝，舞停腰。焰蜡任风销。更可怜、红啼桃槛，绿黯杨桥。 当年乐事朝朝。曾锦鞍呼妓，金屋藏娇。围香春斗酒，坐月夜吹箫。今老矣，倦歌谣。嫌杀杜家乔。漫三杯、踞炉觅句，断送春宵。'),
	(7231, 337, '念奴娇（题锺山楼）', '麦埸桑陇，道都是、六代宫城遗迹。梦里江山经几觉，还似堠旁征驿。燕去燕来，花开花谢，那个成端的。人烟牢落，晚风何处羌笛。 堪欢挥泪新亭，算兴亡莫补、万分之一。到我凭阑，休更向酒畔，是今非昔。紧楫誓清，闻鸡起舞，毕竟英雄得。伤心残照，塔尖遥露秋碧。'),
	(7232, 585, '临江仙·西湖春泛', '堤曲朱墙近远，山明碧瓦高低。好风二十四花期。骄总穿柳去，文艗挟春飞。 箫鼓晴雷殷殷，笑歌香雾霏霏，间情不受酒禁持。断肠无立处，斜日欲归时。'),
	(7233, 77, '西江月（寿游侍郎）', '某恭审某官瑞纪门弧，辉增从从。适逢八帙，共庆千秋。挂神虎之冠，未酬雅志；叶非熊之卜，会有好音。某受知最深，赞喜尤剧。康宁富寿，公其五福之具全；倬耆期颐，我则一忱而有祷。谩寄西江月调，以寿似山仙人。偿蒙薰慈，特赐采瞩，某下情宠耀之至。 雅意浯亭宽碧，何心禁路宽华。芝兰玉树侍臣家。一段洛滨图画。 庆事两年亲见，今年福寿堪夸。更从头上人添些。却是八千岁也。'),
	(7234, 127, '玉楼春·华堂帘幕飘香雾', '华堂帘幕飘香雾。一搦楚腰轻束素。翩跹舞态燕还鹭，绰约妆容花尽妒。樽前谩咏高唐赋巫峡云深留不住。重来花畔倚阑干，愁满阑干无倚处。'),
	(7235, 155, '醉思凡', '吹箫跨鸾。香销夜阑。杏花楼上春残。绣罗衾半闲。 衣宽带宽。千山万山。断肠十二阑干。更斜阳暮寒。'),
	(7236, 340, '括水调歌', '庐山几千仞，屹立并长江。杨澜左里，洪涛巨浪日春撞。风止雪消冰净，相与泊舟登岸，攀磴望空谾。岩壑响松桧，巨石激流淙。 事幽寻，遗世俗，绝纷痝。幽花野草香满，时有鹤飞双。羡子买田筑室，欲使浮岚暖翠，坐卧对轩窗。我欲为君说，安得笔如杠。 东坡前赤壁赋：壬戌之秋，七月既望，苏子与客，泛舟游于赤壁之下。清风徐来，水波不兴。举酒属客，诵明月之诗，歌窈窕之章。少焉，月出于东山之上，徘徊于斗牛之间。白露横江，水光接天。纵一苇之所如，凌万顷之茫然。浩浩乎如凭虚御风，而不知其所止；飘飘乎如遗世独立，羽化而登仙。于是饮酒乐甚，扣舷而歌之。歌曰：“桂棹兮兰桨。击空明兮溯流光。渺渺兮余怀，望美人兮天一方。”客有吹洞箫者，倚歌而和之，其声呜呜然，如怨如慕，如泣如诉，馀音袅袅，不绝如缕。舞幽壑之潜蛟，泣孤舟之嫠妇。苏子愀然，正襟危坐，而问客曰：何为其然也？客曰：月朗星稀。乌鹊南飞。此非曹孟德之诗乎！西望夏口，东望武昌，山川相缪，郁乎苍苍，此非孟德之困于周郎者乎！方其破荆州，下江陵，顺流而东也，舳舻千里，旌旗蔽空，酾酒临江，横槊赋诗，固一世之雄也，而今安在哉！况我与子，渔樵于江渚之上，侣鱼暇而友麋鹿。驾一叶之扁舟，举匏樽以相属。寄蜉蝣于天地，渺沧海之一粟，哀我生之须臾，羡长江之无穷。挟飞仙以遨游，抱明月而长终。知不可乎骤得，托遗响于悲风。苏子曰：客亦知夫水与月乎？逝者如斯，而未尝往也；盈虚者如彼，而卒莫消长也。盖将自其变者而观之，则天地曾不能以一瞬；自其不变者而观之，则物与我皆无尽也，而又何羡乎。且夫天地之间，物各有主。苟非吾之所有，虽一毫而莫取。惟江上之清风，与山间之明月，耳得之而为声，目遇之而成色，取之无尽，用之不竭。是造物者之无尽藏也，而吾与子之所共适。客喜而笑，洗盏更酌。肴核既尽，杯盘狼藉。相与枕藉乎舟中，不知东方之既白。'),
	(7237, 233, '贺新郎·寄丰真州', '忆把金罍酒。叹别来、光阴荏苒，江湖宿留。世事不堪频着眼，赢得两眉长皱。但东望、故人翘首。木落山空天远大，送飞鸿、北去伤怀久。天下事，公知否。 钱塘风月西湖柳。渡江来、百年机会，从前未有。唤起东山丘壑梦，莫惜风霜老手。要整顿、封疆如旧。早晚枢庭开幕府，是英雄、尽为公奔走。看金印，大如斗。'),
	(7238, 68, '好事近·花底一声莺', '花底一声莺，花上半钩斜月。月落乌啼何处，点飞英如雪。 东风吹尽去年愁，解放丁香结。惊动小亭红雨，舞双双金蝶。'),
	(7239, 160, '武陵春', '七返还丹人怎晓，晓后有何难。夜静存神向内观。神水满泥丸。 搬运金精无夜昼，呼吸不会闲。功行成时出世寰。名姓列仙班。'),
	(7240, 698, '江神子（晚泊分水）', '秋风袅袅夕阳红。晚烟浓。暮云重。万叠青山，山外叫孤鸿。独上高楼三百尺，凭玉楯，睇层空。人间日月去匆匆。碧梧桐。又西风。北去南来，销尽几英雄。掷下玉尊天外去，多少事，不言中。'),
	(7241, 272, '鹧鸪天（江槛）', '暮雨朝云不自怜。放教春涨绿浮天。只令画阁临无地，宿昔新诗满系船。 青鸟外，白鸥前。几生香火旧因缘。酒阑山月移雕槛，歌罢江风拂玳筵。'),
	(7242, 107, '念奴娇', '相逢恨晚，人谁道、早有轻离轻折。不是无情，都只为、离合因缘难测。秋去云鸿，春深花絮，风雨随南北。絮飞鸿散，问谁解舀得得。君自举远高飞，知他此去、萍梗何时息。雅阖幽窗欢笑处，回首翻成陈迹。小楷缄题，细行针线，一一重收拾。风花雪月，此生长是思忆。'),
	(7243, 387, '踏莎行', '院落深沉，池塘寂静。帘钩卷上梨花影。宝筝拈得雁难寻，篆香消尽山空冷。钗凤斜欹，鬓蝉不整。残红立褪慵看镜。杜鹃啼月一声声，等闲又是三春尽。'),
	(7244, 682, '武陵春', '小院无人帘半卷，独自倚阑时。宽尽春来金缕衣。憔悴有谁知。 玉人近日书来少，应是怨来迟。梦里长安早晚归。和泪立斜晖。'),
	(7245, 433, '临江仙（离怀）', '别岸相逢何草草，扁舟两岸垂杨。绣屏珠箔绮香囊。酒深歌拍缓，愁入翠眉长。 燕子归来人去也，此时无奈昏黄。桃花应是我心肠。不禁微雨，流泪湿红妆。'),
	(7246, 512, '水调歌头（送唯齐之官回舟松江赋）', '年来惯行役，楚尾又吴头。餐霞吸露，何事佳处辄迟留。云听渔舟夜唱，花落牧童横笛，占尽五湖秋。胡床兴不浅，人在庾公楼。绣帘卷，曲阑暝，翠鬟愁。森罗万象，与渠诗里一时收。天设四桥风月，地会三州山水，邀我伴沙鸥。明朝起归梦，一枕过蘋洲。'),
	(7247, 685, '念奴娇·炎精中否', '炎精中否，叹人材委靡，都无英物。胡马长驱三犯阙，谁作长城坚壁。万国奔腾，两宫幽陷，此恨何时雪。草庐三愿，岂无高卧贤杰。天意眷我中兴，吾皇神武，踵曾孙周发。河海封疆俱效顺，狂虏何劳灰灭。翠羽南巡，叩阍无路，徒有冲冠发。孤忠耿耿，剑铓冷浸秋月。'),
	(7248, 439, '卜算子', '欲整别离情，怯对尊中酒。野梵幽幽石上飘，搴落楼头柳。 不系黄金绶。粉黛愁成垢。春风三月有时阑，遮不尽、梨舒亶花丑。'),
	(7249, 403, '丑奴儿', '牡丹不好长春好，有个因依。一两枝儿。但是风光总属伊。当初只为嫦娥种，月正明时。教恁芳菲。伴著团圆十二回。'),
	(7250, 682, '菩萨蛮', '红楼斜倚连溪曲。楼前溪水凝寒玉。荡漾木兰船。船中人少年。 荷花娇欲语。笑入鸳鸯浦。波上暝烟低。菱歌月下归。'),
	(7251, 433, '减字木兰花', '天之美禄。会饮思量平生福。一硕刘伶，五斗将来且解酲。 百年长解。三万六千能几日。劝饮瑶觞。祝寿不如岁月长。'),
	(7252, 433, '木兰花令（柳）', '铜驼陌上新正后。第一风流除是柳。勾牵春事不如梅，断送离人强似酒。东君有意偏撋就。惯得腰肢真个瘦。阿谁道你不思量，因甚眉头长恁皱。'),
	(7253, 433, '天香', '霜瓦鸳鸯，风帘翡翠，今年早是寒少。矮钉明窗，侧开朱户，断莫乱教人到。重阴未解，云共雪、商量不了。青帐垂毡要密，红炉收围宜小。 呵梅弄妆试巧。绣罗衣、瑞云芝草。伴我语时同语，笑时同笑。已被金尊劝倒。又唱个新词故相恼。尽道穷冬，元来恁好。'),
	(7254, 428, '望江南（二）', '江南酒，何处味偏浓。醉卧春风深巷里，晓寻香旆小桥东。竹叶满金钟。檀板醉，人面粉生红。青杏黄梅朱阁上，鲥鱼苦笋玉盘中。酩酊任愁攻。'),
	(7255, 162, '蝶恋花（情景）', '绣幕茫茫罗帐卷。春睡腾腾，困人娇波慢。隐隐枕痕留玉脸。腻云斜溜钗头燕。远梦无端欢又散。泪落胭脂，界破蜂黄浅。整了翠鬟匀了面。芳心一寸情何限。'),
	(7256, 162, '好事近', '睡起玉屏风，吹去乱红犹落。天气骤生轻暖，衬沈香帷箔。珠帘约住海棠风，愁拖两眉角。昨夜一庭明月，冷秋千红索。'),
	(7257, 429, '诉衷情', '烧残绛蜡泪成痕。街鼓报黄昏。碧云又阻来信，廊上月侵门。愁永夜，拂香茵。待谁温。梦兰憔悴，掷果凄凉，两处销魂。'),
	(7258, 193, '满江红', '无利无名，无荣无辱，无烦无恼。夜灯前、独歌独酌，独吟独笑。况值群山初雪满，又兼明月交光好。便假饶百岁拟如何，从他老。知富贵，谁能保。知功业，何时了。算簟瓢金玉，所争多少。一瞬光阴何足道，但思行乐常不早。待春来携酒殢东风，眠芳草。'),
	(7259, 644, '踏莎行·二社良辰', '二社良辰，千秋庭院。翩翩又见新来燕。凤凰巢稳许为邻，潇湘烟暝来何晚。 乱入红楼，低飞绿岸。画梁时拂歌尘散。为谁归去为谁来，主人恩重珠帘卷。'),
	(7260, 158, '如梦令（宫词）', '翠柏红蕉影乱。月上朱栏一半。风自碧空来，吹落歌珠一串。 不见。不见。人被绣帘遮断。'),
	(7261, 32, '百字令', '残蝉断雁，政西风萧索，夕阳流水。落木无边幽眺处，云拥登山屐齿。岁月如驰，古今同梦，惟有悲欢异。绿尊空对，故人相望千里。 追念淮海当年，五云行殿，咫尺天颜喜。清晓胪传仙仗里，衣染玉龙香细。今日天涯，黄花零乱，满眼重阳泪。艰难多病，二陵无奈秋思。'),
	(7262, 114, '惜分飞（送别）', '岸柳依依拖金缕。是我朝来别处。惟有多情絮。故来衣上留人住。两眼啼红空弹与。未见桃花又去。一片征帆举。断肠遥指苕溪路。'),
	(7263, 636, '西江月（七夕）', '我笑牛郎织女，一年一度相逢。欢情尽逐晓云空。愁损舞鸾歌凤。 牛女而今笑我，七年独卧西风。西风还解过江东。为报佳期入梦。'),
	(7264, 177, '好事近·夕景', '落日水熔金，天淡暮烟凝碧。楼上谁家红袖，靠阑干无力。鸳鸯相对浴红衣。短棹弄长笛。惊起一双飞去，听波声拍拍。'),
	(7265, 223, '转调二郎神', '闷来弹雀，又搅破、一帘花影。谩试著春衫，还思纤手，薰彻金炉烬冷。动是愁多如何向，但怪得、新来多病。想旧日沉腰，而今潘鬓，不堪临镜。 重省。别来泪滴，罗衣犹凝。料为我厌厌，日高慵起，长托春酲未醒。雁翼不来，马蹄轻驻，门闭一庭芳景。空伫立，尽日阑干倚遍，昼长人静。'),
	(7266, 449, '小重山·谁向江头遗恨浓', '谁向江头遗恨浓，碧波流不断，楚山重。柳烟和雨隔疏钟。黄昏后，罗幕更朦胧。 桃李小园空，阿谁犹笑语，拾残红？珠帘卷尽夜来风。人不见，春在绿芜中。'),
	(7267, 202, '清平乐（题处梅家藏所南翁画兰）', '黑云飞起。夜月啼湘鬼。魂返灵根无二纸。千古不随流水。 香心淡染清华。似花还似非花。要与闲梅相处，孤山山下人家。'),
	(7268, 202, '玉蝴蝶（赋玉绣球花）', '留得一团和气，此花开尽，春已规圆。虚白窗深，恍讶碧落星悬。扬芳丛、低翻雪羽，凝素艳、争簇冰蝉。向西园。几回错认，明月秋千。 欲觅生香何处，盈盈一水，空对娟娟。待折归来，倩谁偷解玉连环。试结取、鸳鸯锦带，好移傍、鹦鹉珠帘。晚阶前。落梅无数，因甚啼鹃。'),
	(7269, 202, '声声慢（都下与沈尧道同赋别本作北游答曾心传惠诗）', '平沙催晓，野水惊寒，遥岑寸碧烟空。万里冰霜，一夜换却西风。晴梢渐无坠叶，撼秋声、都是梧桐。情正远，奈吟湘赋楚，近日偏慵。 客里依然清事，爱窗深帐暖，戏拣香筒。片霎归程，无奈梦与心同。空教故林怨鹤，掩闲门、明月山中。春又小，甚梅花、犹自未逢。'),
	(7270, 515, '洞仙歌（对雨思友）', '世间何处，最难忘杯酒。惟是停云想亲友。此时无一盏，千种离愁，西风外，长伴枯荷衰柳。 去年深夜语，倾倒书□，窗烛心悬小红豆。记得到门时，雨正萧萧，嗟今雨、此情非旧。待与子、相期采黄花，又未卜重阳，果能晴否。'),
	(7271, 124, '满江红（寄剡中自醉兄）', '秋水涓涓，情渺渺、美人何许。还记得、东堂松桂，对床风雨。流水桃花西塞隐，茂林修竹山阴路。二十年、历历旧经行，空怀古。 评砚品，临书谱。笺画史，修茶具。喜一愚天禀，一闲天赋，百战征求千里马，十年饾饤三都赋。问何如、石鼎约弥明，同联句。'),
	(7272, 124, '长相思', '灯辉辉。月微微。帐暖香深春漏迟。梦回闻子规。 欲成诗。未成诗。生怕春归春又归。花飞花未飞。'),
	(7273, 71, '摸鱼儿', '正何须、阳关肠断，吴姬苦劝人酒。中年怀抱萦萦处，看取伴烟和柳。柳摇首。笑飞到家山，已是酴醿后。留连话旧。同溪上儿童，颇曾见我，有此故人否。 相逢地，还忆今宵三韭，青山只了迎候。东风自送归帆去，吹得乱红沾袖。暮云皱。听杜宇高高，啼向无何有。江花垂手。任春色重来，江花更好，难可少年又。'),
	(7274, 71, '八声甘州（和汪士安海棠下先归，前是观桃水东，至其乡真常观）', '问海棠花下，又何如、玄都观中游。叹佺巢蜀锦，常时不数，前度何稠。谁见宣华故事，歌舞簇遨头。共是西江水，不解西流。 在处繁华如梦，梦占人年少，生死堪羞。任倾城倾国，风雨一春休。醉逢君、何须有约，醉留君、系不住扁舟。空又失，花前一笑，绿尽芳洲。'),
	(7275, 24, '小重山', '吹断笙箫春梦寒。倚楼思往事，泪偷弹。别时容易见时难。相看处，惟有玉连环。 人在万重山。近来应不似，旧时颜。重门深院柳阴间。曾携手，休去倚危阑。'),
	(7276, 640, '沁园春（赠人）', '如此男儿，可是疏狂，才大兴浓。看曹瞒事业，雀台夜月，建封气概，燕子春风。叱咤生雷，肝肠似石，才到尊前都不同。人间世，只婵娟一剑，磨尽英雄。 半生书剑无功。漫赢得闲情如二公。向紫云歌畔，玉舠最满，红桃笑里，金错长空。驰陌三年，牛腰几束，半在兰香巾笥中。君知否，是扬州景物，消得司封。'),
	(7277, 660, '洞仙歌（次韵苏子瞻）', '冰肌玉骨，自清凉无汗，午梦醒来盼娇满。扇轻拈又放，浅炷兰薰，微笑处、吹著烟丝散乱。 凉亭还独步，曾是凭阑，携手心盟指云汉。碧云斜阳外，信有如今，音书杳、寸肠千转。漫伫立、无言对荷花，看转眼秋风，翠移红换。'),
	(7278, 112, '凄凉犯·重台水仙', '空江浪阔。清尘凝、层层刻碎冰叶。水边照影，华裾曳翠，露搔泪湿。湘烟暮合。□尘袜、凌波半涉。怕临风、□欺瘦骨，护冷素衣叠。 樊姊玉奴恨，小钿疏唇，洗妆轻怯。汜人最苦，纷痕深、几重愁靥。花隘香浓，猛熏透、霜绡细摺。倚瑶台，十二金钱晕半掐。'),
	(7279, 112, '探芳信·暖风定', '丙申岁，吴灯市盛常年。余借宅幽坊，一时名胜遇合，置杯酒，接殷勤之欢，甚盛事也。分镜字韵。 暖风定。正卖花吟春，去年曾听。旋自洗幽兰，银瓶钓金井。斗窗香暖悭留客，街鼓还催暝。调雏莺、试遣深杯，唤将愁醒。 灯市又重整。待醉勒游缰，缓穿斜径。暗忆芳盟，绡帕泪犹凝。吴宫十里吹笙路，桃李都羞靓。绣帘人、怕惹飞梅翳镜。'),
	(7280, 112, '醉蓬莱·夷则商七夕和方南山', '望碧天书断，宝枕香留，泪痕盈袖。谁识秋娘，比行云纤瘦。象尺熏炉，翠针金缕，记倚床同绣。月亸琼梳，冰销粉汗，南花熏透。 尽是当时，少年清梦，臂约痕深，帕绡红皱。凭鹊传音，恨语多轻漏。润玉留情，沈郎无奈，向柳阴期候。数曲催阑，双铺深掩，风镮鸣兽。'),
	(7281, 112, '永遇乐·探梅次时斋韵', '阁雪云低，卷沙风急，惊雁失序。户掩寒宵，屏闲冷梦，灯飐唇似语。堪怜窗景，都闲刺绣，但续旧愁一缕。邻歌散，罗襟印粉，袖湿茜桃红露。 西湖旧日，留连清夜，爱酒几将花误。遗袜尘销，题裙墨黯，天远吹笙路。吴台直下，缃梅无限，未放野桥香度。重谋醉，揉香弄影，水清浅处。'),
	(7282, 112, '风入松·为友人放琴客赋', '春风吴柳几番黄。欢事小蛮窗。梅花正结双头梦，被玉龙、吹散幽香。昨夜灯前歌黛，今朝陌上啼妆。 最怜无侣伴雏莺。桃叶已春江。曲屏先暖鸳衾惯，夜寒深、都是思量。莫道蓝桥路远，行云中隔幽坊。'),
	(7283, 112, '江神子·送桂花吴宪时已有检详之命未赴阙', '天街如水翠尘空。建章宫。月明中。人未归来，玉树起秋风。宝粟万钉花露重，催赐带，过垂虹。 夜凉沈水绣帘栊。酒香浓。雾濛濛。钗列吴娃，腰褭带金虫。三十六宫蟾观冷，留不住，佩丁东。'),
	(7284, 112, '花犯·郭希道送水仙索赋', '小娉婷，清铅素靥，蜂黄暗偷晕。翠翘欹鬓。昨夜冷中庭，月下相认。睡浓更苦凄风紧。惊回心未稳。送晓色、一壶葱茜，才知花梦准。 湘娥化作此幽芳，凌波路，古岸云沙遗恨。临砌影，寒香乱、冻梅藏韵。熏炉畔、旋移傍枕，还又见、玉人垂绀鬓。料唤赏、清华池馆，台杯须满引。'),
	(7285, 112, '丁香结·夷则商秋日海棠', '香袅红霏，影高银烛，曾纵夜游浓醉。正锦温琼腻。被燕踏、暖雪惊翻庭砌。马嘶人散后，秋风换、故园梦里。吴霜融晓，陡觉暗动偷春花意。 还似。海雾冷仙山，唤觉环儿半睡。浅薄朱唇，娇羞艳色，自伤时背。帘外寒挂澹月，向日秋千地。怀春情不断，犹带相思旧子。'),
	(7286, 112, '风流子·黄钟商芍药', '金谷已空尘。薰风祝攥舞低鸾翅，绛笼蜜炬，绿映龙盆。窈窕绣窗人睡起，临砌脉无言。慵整堕鬟，怨时迟暮，可怜憔悴，啼雨黄昏。 轻桡移花市，秋娘渡、飞浪溅湿行裙。二十四桥南北，罗荐香分。念碎劈芳心，萦思千缕，赠将幽素，偷翦重云。终待凤池归去，催咏红翻。'),
	(7287, 112, '扫花游·西湖寒食', '冷空淡碧，带翳柳轻云，护花深雾。艳晨易午。正笙箫竞渡，绮罗争路。骤卷风埃，半掩长蛾翠妩。散红缕。渐红湿杏泥，愁燕无语。 乘盖争避处。就解佩旗亭，故人相遇。恨春太妒。溅行裙更惜，凤钩尘污。酹入梅根，万点啼痕暗树。峭寒暮。更萧萧、陇头人去。'),
	(7288, 112, '齐天乐·竹深不放斜阳度', '竹深不放斜阳度，横披澹墨林沼。断莽平烟，残莎剩水，宜得秋深才好。荒亭旋扫。正著酒寒轻，弄花春小。障锦西风，半围歌袖半吟草。 独游清兴易懒，景饶人未胜，乐事长少。柳下交车，尊前岸帻，同抚云根一笑。秋香未老。渐风雨西城，暗欹客帽。背月移舟，乱鸦溪树晓。'),
	(7289, 112, '水龙吟·送万信州', '几番时事重论，座中共惜斜阳下。今朝翦柳，东风送客，功名近也。约住飞花，暂听留燕，更攀情语。问千牙过阙，一封入奏，忠孝事、都应写。 闻道兰台清暇。载鸱夷、烟江一舸。贞元旧曲，如今谁听，惟公和寡。儿骑空迎，舜瞳回盼，玉阶前借。便急回暖律，天边海上，正春寒夜。'),
	(7290, 112, '夜飞鹊·蔡司户席上南花', '金规印遥汉，庭浪无纹。清雪冷沁花薰。天街曾醉美人畔，凉枝移插乌巾。西风骤惊散，念梭悬愁结，蒂翦离痕。中郎旧恨，寄横竹、吹裂哀云。 空剩露华烟彩，人影断幽坊，深闭千门。浑似飞仙入梦，袜罗微步，流水青苹。轻冰润□，怅今朝、不共清尊。怕云槎来晚，流红信杳，萦断秋魂。'),
	(7291, 112, '瑞鹤仙·晴丝牵绪乱', '晴丝牵绪乱。对沧江斜日，花飞人远。垂杨暗吴苑。正旗亭烟冷，河桥风暖。兰情蕙盼。惹相思，春根酒畔。又争知、吟骨萦销，渐把旧衫重剪。 凄断。流红千浪，缺月孤楼，总难留燕。歌尘凝扇。待凭信，拌分钿。试挑灯欲写，还依不妨，笺幅偷和泪卷。寄残云剩雨蓬莱，也应梦见。'),
	(7292, 568, '如梦令', '窗外燕娇莺妒。窗下梦魂无据。梦好却频惊，不到彩云深处。无绪。无绪。红重一帘花雨。'),
	(7293, 303, '沁园春·送李御带珙', '唐人以处士辟幕府如石、温辈甚多。税君巽甫以命士来淮幕三年矣，略不能挽之以寸。巽甫虽安之，如某歉何！临别，赋《沁园春》以饯。 水北洛南，未尝无人，不同者时。赖交情兰臭，绸缪相好；宦情云薄，得失何知？夜观论兵，春原吊古，慷慨事功千载期。萧如也，料行囊如水，只有新诗。归兮，归去来兮，我亦办征帆非晚归。正姑苏台畔，米廉酒好；吴松江上，莼嫩鱼肥。我住孤村，相连一水，载月不妨时过之。长亭路，又何须回首，折柳依依。'),
	(7294, 117, '秋夜雨（客有道秋夜雨古词，因用其韵，而不知角之为阁也。并付一笑）', '云头电掣如金索。须臾天尽帏幕。一凉恩到骨，正骤雨、盆倾檐角。 桃笙今夜难禁也，赖醉乡、情分非薄。清梦何处托。又只是、故园篱落。'),
	(7295, 117, '鹊桥仙·馨香饼饵', '馨香饼饵，新鲜瓜果，乞巧千门万户。到头人事控抟难，与拙底、无多来去。 痴儿妄想，夜看银汉，要待云车飞度。谁知牛女已尊年，又那得、欢娱意绪。'),
	(7296, 117, '如梦令', '江上绿杨芳草，想见故园春好。一树海棠花，昨夜梦魂飞绕。惊晓。惊晓。窗外一声啼鸟。'),
	(7297, 117, '满江红（梅）', '试马东风，且来问、南枝消息。正小墅、几株斜倚，数花轻拆。自有山中幽态度，谁知世上真颜色。叹君家、五岭我双溪，俱成客。 长塞管，孤城笛。天未晓，人犹寂。有几多心事，露清月白。好把寒英都放了，莫教春讯能占得。问竹篱、茅舍景如何，惟渠识。'),
	(7298, 199, '青玉案', '少时贪看琼林绕。任马上、寒威峭。昨暮六花飞逗晓。拥衾慵起，鬓丝笼帽，顿觉年来老。 朱阑翠竹枝枝倒。把玉甃、棱层趁风扫。楼上一樽须放早。同云收尽，红轮初上，对面狼峰好。'),
	(7299, 49, '卜算子', '自入玄门户，寂寂清虚做。静里披搜四假身，勘破尘行路。 悟上还重悟，得得真闲趣。收住身中无价真，岂逐人情去。'),
	(7300, 49, '满江红（题范尉梅谷）', '赤日黄埃，梦不到、清溪翠麓。空健羡、君家别墅，几株幽独。骨冷肌清偏要月，天寒日暮尤宜竹。想主人、杖履绕千回，山南北。 宁委涧，嫌金屋。宁映水，羞银烛。叹出群风韵，背时装束，竞爱东邻姬傅粉，谁怜空谷人如玉。笑林逋、何逊漫为诗，无人读。'),
	(7301, 513, '酹江月（次韵东坡赋别）', '寄言天上，石麒麟、化作人间英物。醉拥诗兵驱笔阵，百万词锋退壁。世事空花，赏心泥絮，一点红炉雪。识时务者，当今惟有俊杰。 我本浩气天成，才逢知己，便又清狂发。富贵于我如浮云，且看云生云灭。羊石论交，鹅湖惜别，别恨多于发。共君千里，登楼何患无月。'),
	(7302, 513, '水龙吟', '层峦叠巘浮空，断崖直下分三井。苍苔路古，鹿鸣芝涧，猿号松岭。露浥凤箫，烟迷枸杞，绿深翠冷。笑携筇一到，登高眺远，是多少、仙家景。 长念青春易老，尚区区、枯蓬断梗。人间天上，喟然俯仰，只身孤影。世事空花，春心泥絮，此回还省。向琼台双阙，结间茅屋，坐千峰顶。'),
	(7303, 239, '华胥引', '长亭无数，羁客将归，故园换叶。乳鸭随波，轻苹满渚时共唼。接眼春色何穷，更橹声伊轧。思忆前欢，未言心已愁怯。 欺鬓吴霜。恨星星、又还盈镊。锦纹鱼素，那堪重翻再阅。粉指香痕依旧，在绣裳鸳箧。多少相思，皱成眉上千叠。'),
	(7304, 84, '谒金门·闲院宇', '闲院宇。独自行来行去。花片无声帘外雨。峭寒生碧树。 做弄清明时序。料理春酲情绪。忆得归时停棹处。画桥看落絮。'),
	(7305, 681, '贺新郎（生日谢寓公载酒）', '只记来时节。又三年、朱炜过了，恰如时霎。独立薰风苍凉外，笑傍环湖花月。多少事、欲拈还辍。扶木之阴三千丈，远茫茫、无计推华发。容易过，三十八。 此身待向清尊说。似江头、泛乎不系，扁舟一叶。将我东西南北去，都任长年旋折。风不定、川云如撇。惟有君恩浑未报，又故山、猿鹤催归切。将进酒，缓歌阕。'),
	(7306, 680, '点绛唇', '天外青鸾，几时常向人间住。断歌零舞。月上桐阴暮。 憔悴潘郎，不解为花主。知何处。梦云愁雨。怕向西楼去。'),
	(7307, 680, '清平乐（秋叶）', '盘枝翦翠。叶叶西风意。吹上玉人云□底。无限新凉气味。 飘萧露卷烟柔。绝怜不逐宫流。寄语多情宋玉，悲秋得似宜秋。'),
	(7308, 680, '菩萨蛮', '玉阑秋色知谁主。隔阑一架葡萄雨。绿藓怕啼＿。可堪宫漏长。 乌丝吟古怨。清泪消尘砚。梦冷不成云。□峰峰外情。'),
	(7309, 680, '菩萨蛮', '春风吹绿湖边草。春光依旧湖边道。玉勒锦障泥。少年游冶时。 烟明花似绣。且醉旗亭酒。斜日照花西。归鸦花外啼。'),
	(7310, 680, '齐天乐·碧云阙处无多雨', '碧云阙处无多雨，愁与去帆俱远。倒苇沙闲，枯兰溆冷，寥落寒江秋晚。楼阴纵览。正魂怯清吟，病多依黯。怕挹西风，袖罗香自去年减。 风流江左久客，旧游得意处，朱帘曾卷。载酒春情，吹箫夜约，犹忆玉娇香软。尘栖故苑，叹璧月空檐，梦云飞观。送绝征鸿，楚峰烟数点。'),
	(7311, 149, '玲珑四犯（此曲双调，世别有大石调一曲越中岁暮闻箫鼓感怀）', '叠鼓夜寒，垂灯春浅，匆匆时事如许。倦游欢意少，俯仰悲今古。江淹又吟恨赋。记当时、送君南浦。万里乾坤，百年身世，唯有此情苦。 扬州柳，垂官路。有轻盈换马，端正窥户。酒醒明月下，梦逐潮声去。文章信美知何用，漫赢得、天涯羁旅。教说与。春来要寻花伴侣。'),
	(7312, 149, '满江红', '心字融入去声，方谐音律。予欲以平韵为之，久不能成。因泛巢湖，闻远岸箫鼓声。问之舟师，云：居人为此湖神姥寿也。予因祝曰：得一席风径至居巢，当以平韵满江红为迎送神曲。言讫，风与笔俱驶，顷刻而成。末句云“闻佩环”，则协律矣。书以绿笺，沈于白浪。辛亥正月晦也。是岁六月，复过祠下，因刻之柱间。有客来自居巢云：士人祠姥，辄能歌此词。按曹操至濡须口，孙权遗操书曰：春水方生，公宜速去。操曰：孙权不欺孤。乃彻军还。濡须口与东关相近，江湖水之所出入。予意春水方生，必有司之者，故归其功于姥云 仙姥来时，正一望、千顷翠澜。旌旗共、乱云俱下，依约前山。命驾群龙金作轭，相从诸娣玉为冠||。向夜深、风定悄无人，闻佩环。 神奇处，君试看。奠淮右，阻江南。遣六丁雷电，别守东关。却笑英雄无好手，一篙春水走曹瞒。又怎知、人在小红楼，帘影间。'),
	(7313, 149, '浣溪沙·著酒行行满袂风', '沙露，衰草入云。丙午之秋，予与安甥或荡舟采菱，或举火罝兔，或观鱼簺下，山行野吟，自适其适，凭虚怅望，因赋是阙 著酒行行满袂风。草枯霜鹘落晴空。销魂都在夕阳中。 恨入四弦人欲老，梦寻千驿意难通。当时何似莫匆匆。'),
	(7314, 149, '醉吟商小品', '梁州、转关绿腰、醉吟商湖渭州、历弦薄媚也。予每念之。辛亥之夏，予谒杨廷秀丈於金陵邸中，遇琵琶工，解作醉吟商湖渭州，因求得品弦法，译成此谱，实双声耳 又正是春归，细柳暗黄千缕。暮鸦啼处。梦逐金鞍去。一点芳心休诉。琵琶解语。'),
	(7315, 149, '点绛唇', '金谷人归，绿杨低扫吹笙道。数声啼鸟。也学相思调。 月落潮生，掇送刘郎老。淮南好。甚时重到。陌上生春草。'),
	(7316, 83, '念奴娇', '碧池如染，把玻璃瓮就，纤埃都绝。西国夫人空里坠，圆盖亭亭排列。莹质无瑕，尘心不染，远社堪重结。当时盛事，虎溪茗碗翻雪。 千载以意谁论，人争买笑，醉眼看罗袜。坐上如今皆我辈，纱蕴从来蠲洁。击节临风，停杯对月。浩气俱英发。两翁仙举，玉堂正在金阙。'),
	(7317, 72, '浣溪沙（赠妓徐楚楚）', '黄鹤楼前识楚卿。彩云重叠拥娉婷。席间谈笑觉风生。 标格胜如张好好，情怀浓似薛琼琼。半帘花月听弹筝。'),
	(7318, 72, '六州歌头（题岳鄂王庙）', '中兴诸将，谁是万人英。身草莽，人虽死，气填膺。尚如生。年少起河朔，弓两石，剑三尺，定襄汉，开虢洛，洗洞庭。北望帝京。狡兔依然在，良犬先烹。过旧时营垒，荆鄂有遗民。忆故将军。泪如倾。 说当年事，知恨苦，不奉诏，伪耶真。臣有罪，陛下圣，可鉴临。一片心。万古分茅土，终不到，旧奸臣。人世夜，白日照，忽开明。衮佩冕圭百拜，九泉下、荣感君恩。看年年三月，满地野花春。卤簿迎神。'),
	(7319, 215, '长相思', '晴时看。雨时看。红绿云中驾彩鸾。阳台梦未阑。 咏伊难。画伊难。服透东皇九转丹。光生玉炼颜。'),
	(7320, 639, '好事近', '横玉叫清宵，帘外月侵残烛。人在画楼高处，倚阑干几曲。 穿云裂石韵悠扬，风细断还续。惊落小梅香粉，点一庭苔绿。'),
	(7321, 569, '采桑子（樱桃花）', '梅花谢后樱花绽，浅浅匀红。试手天工。百卉千葩一信通。 余寒未许开舒妥，怨雨愁风。结子筠笼。万颗匀圆讶许同。'),
	(7322, 569, '生查子', '迟迟春昼长，冉冉东风软。寒食乍晴天，红紫芳菲遍。 前峰积翠横，新涨＿蓝远。向晚淡烟迷，一段屏山展。'),
	(7323, 569, '鹧鸪天（丁已除夕）', '爆竹声中岁又除。顿回和气满寰区。春见解绿江南树，不与人间染白须。 残蜡烛，旧桃符。宁辞末后饮屠苏。归欤幸有园林胜，次第花开可自娱。'),
	(7324, 446, '望海潮', '离情冰泮，归心云扰，黯然凝伫江皋。柳色摇金，梅香弄粉，依稀满眼春娇。常记极游遨。更与持玉斗，因解金貂。郎去瞿塘，妾家巫峡水迢迢。 别来暗减风标。奈碧云暗断，翠被香消。春草生池，芳尘凝榭，凄凉月夕花朝。千里梦魂劳。但乌啼渡口，猿响山椒。拟把无穷幽恨，万叠写霜绡。'),
	(7325, 446, '念奴娇', '闷红颦翠，惜流年、忍对艳阳时节。白玉楼成人去后，两地音尘都绝。鸾鉴分飞，梦云零乱，欢意今衰飒。墨痕红淡，忆曾题遍红叶。 须信后约难凭，臂啮鬟剪，也只成虚说。满眼凄凉无限事，付与丁香愁结。欲语情酸，临岐步懒，怅望兰舟发。出门谁伴，泪昏一片孤月。'),
	(7326, 634, '卜算子', '涧下水声寒，壑底松风静。时有清香度竹来，步月寻疏影。 往事属东风，试问花应省。曾是花前把酒人，别梦溪堂冷。'),
	(7327, 456, '念奴娇', '秋风秋雨，正黄昏、供断一窗愁绝。带减衣宽谁念我，难忍重城离别。转枕褰帷，挑灯整被，总是相思切。知他别后，负人多少风月。 不是怨极愁浓，只愁重见了，相思难说。料得新来魂梦里，不管飞来蝴蝶。排闷人间，寄愁天上，终有归时节。如今无奈，乱云依旧千叠。'),
	(7328, 456, '谒金门', '春夜雨。催润柳塘花坞。小院深深门几许。画帘香一缕。 独立晚庭凝伫。细把花枝闲数。燕子不来天欲暮。说愁无处所。'),
	(7329, 456, '摊破江城子', '娟娟霜月又侵门。对黄昏。怯黄昏。愁把梅花，独自泛清尊。酒又难禁花又恼，漏声远，一更更，总断魂。 断魂。断魂。不堪闻。被半温。香半温。睡也睡也，睡不稳、谁与温存。只有床前、红独伴啼痕。一夜无眠连晓角，人瘦也，比梅花，瘦几分。'),
	(7330, 591, '绿头鸭（七夕）', '叹飘零。离多会少堪惊。又争如、天人有信，不同浮世难凭。占秋初、桂花散采，向夜久、银汉无声。凤驾催云，红帷卷月，泠泠一水会双星。素杼冷，临风休织，深诉隔年诚。飞光浅，青童语款，丹鹊桥平。 看人间、争求新巧，纷纷女伴欢迎。避灯时、彩丝未整，拜月处、蛛网先成。谁念临州，萧条官舍，烛摇秋扇坐中庭。笑此夕、金钗无据，遗恨满蓬瀛。欹高枕，梧桐听雨，如是天明。'),
	(7331, 591, '浣溪沙', '妙手都无斧凿瘢。饱参佳处却成颦。恰如春入浣花村。 笔墨今宵光有艳，管弦从此悄无言。主人席次两眉轩。'),
	(7332, 591, '破阵子（赵晋臣敷文幼女县主觅词）', '菩萨丛中惠眼，《硕人》诗里娥眉。天上人间真福相，画就描成好靥儿。行时娇更迟。 劝酒偏他最劣，笑时犹有些痴。更著十年君看取，两国夫人更是谁。殷勤秋水词。'),
	(7333, 591, '水调歌头（和赵景明知县韵）', '官事未易了，且向酒边来。君如无我，问君怀抱向谁开。但放平生丘壑，莫管旁人嘲骂，深蛰要惊雷。白发还自笑，何地置衰颓。 五车书，千石饮，百遍才。新词未到，琼瑰先梦满吾怀。已过西风重九，且要黄花入手，诗兴未关梅。君要花满县，桃李趁时栽。'),
	(7334, 591, '卜算子', '珠玉作泥沙，山谷量牛马。试上累累丘垅看，谁是强梁者。 水浸浅深帘，山压高低瓦。山水朝来笑问人，翁早去声归来也。'),
	(7335, 591, '西江月（和晋臣登悠然阁）', '一柱中擎远碧，两峰旁倚高寒。横陈削就短长山。莫把一分增减。 我望云烟目断，人言风景天慳。被公诗笔尽追还。更上层楼一览。'),
	(7336, 591, '临江仙·和叶仲洽赋羊桃', '忆醉三山芳树下，几曾风韵忘怀。黄金颜色五花开，味如卢橘熟。贵似荔枝来。 闻道商山余四老，橘中自酿秋醅。试呼名品细推排。重重香腑脏，偏殢圣贤杯。'),
	(7337, 591, '点绛唇', '身后功名，古来不换生前醉。青鞋自喜。不踏长安市。 竹外僧归，路指霜钟寺。孤鸿起。丹青手里。剪破松江水。'),
	(7338, 591, '菩萨蛮（赠周国辅侍人）', '画楼影蘸清溪水。歌声响彻行云里。帘幕燕双双。绿杨低映窗。 曲中特地误。要试周郎顾。醉里客魂消。春风大小乔。'),
	(7339, 591, '西江月（题可卿影像）', '人道偏宜歌舞，天教只入丹青。喧天画鼓要他听。把著花枝不＿。 何处娇魂瘦影，向来软语柔情。有时醉里唤卿卿。却被傍人笑问。'),
	(7340, 591, '菩萨蛮（双韵赋摘阮）', '阮琴斜挂香罗绶。玉纤初试琵琶手。桐叶雨声干。真珠落玉盘。 朱弦调未惯。笑倩春风伴。莫作别离声。且听双凤鸣。'),
	(7341, 591, '沁园春（再到期思卜筑）', '一水西来，千丈晴虹，十里翠屏。喜草堂经岁，重来杜老，斜川好景，不负渊明。老鹤高飞，一枝投宿，长笑蜗牛戴屋行。平章了，待十分佳处，著个茅亭。 青山意气峥嵘。似为我归来妩媚生。解频教花鸟，前歌后舞，更催云水，暮送朝迎。酒圣诗豪，可能无势，我乃而今驾驭卿。清溪上，被山灵却笑，白发归耕。'),
	(7342, 591, '水调歌头（严子文同传安道和盟鸥韵，和以谢之辛弃疾）', '寄我五云字，恰向酒边来。东风过尽归雁，不见客星回。闻道琐窗风月，更著诗翁杖履，合作雪堂猜。岁旱莫留客，霖雨要渠来。 短檠灯，长剑铗，欲生苔。雕弓挂无用，照影落清杯。多病关心药，小摘亲鉏菜甲，老子正须哀。夜雨北窗竹，更倩野人栽。'),
	(7343, 591, '蝶恋花（送祐之弟）', '衰草残阳三万顷。不逢飘零，天外孤鸿影。几许凄凉须痛饮。行人自向江头醒。 会少离多看两鬓。万缕千丝，何况新来病。不是离愁难整顿。被他引惹其他恨。'),
	(7344, 591, '摸鱼儿（观潮上叶丞相）', '望飞来、半空鸥鹭。须臾动地鼙鼓。截江组练驱山去，鏖战未收貔虎。朝又暮。诮惯得、吴儿不怕蛟龙怒。风波平步。看红旆惊飞，跳鱼直上，蹙踏浪花舞。 凭谁问，万里长鲸吞吐。人间儿戏千弩。滔天力倦知何事，白马素车东去。堪恨处。人道是、子胥冤愤终千古。功名自误。谩教得陶朱，五湖西子，一舸弄烟雨。'),
	(7345, 426, '鹧鸪天（梅）', '淡淡疏疏不惹尘。暗香一点静中闻。人间怪有晴时雪，天上偷回腊里春。 疑浅笑，又轻颦。虽然无语意相亲。老来尚可花边饮，惆怅相携失玉人。'),
	(7346, 587, '如梦令', '别恨眉尖无数。后夜王孙何处。歌馆与妆楼，目断行云凝伫。凝伫。忆泪千行红雨。'),
	(7347, 587, '水龙吟', '事为之词 危楼横枕清江上，两岸碧山如画。夕烟幂幂，晚灯点点，楼台新夜。明月当天，白沙流水，冷光连野。浸栏干万顷，琉璃软皱，打渔艇、相高下。 何处一声羌管，是谁家、倚楼人也。多情对景，无言有恨，欲歌还罢。把酒临筵，阿谁知我，此怀难写。忍思量后夜，芳容不似，暗尘随马。'),
	(7348, 587, '阮郎归·客中见梅', '年年为客遍天涯。梦迟归路赊。无端星月浸窗纱。一枝寒影斜。 肠未断，鬓先华。新来瘦转加。角声吹彻小梅花。夜长人忆家。'),
	(7349, 587, '探春令（元夕）', '去年元夜，正钱塘，看天街灯烛。闹蛾儿转处，熙熙语笑，百万红妆女。 今年肯把轻辜负。列荧煌千炬。趁闲身未老，良辰美景，款醉新歌舞。'),
	(7350, 188, '长相思', '小楼重。下帘栊。万点芳心绿间红。秋千图画中。草茸茸。柳松松。细卷玻璃水面风。春寒依旧浓。'),
	(7351, 188, '忆秦娥（元夕）', '元宵节。凤楼相对鳌山结。鳌山结。香尘随步。柳梢微月。 多情又把珠帘揭。游人不放笙歌歇。笙歌歇。晓烟轻散，帝城宫阙。'),
	(7352, 188, '菩萨蛮·庭叶翻翻秋向晚', '庭叶翻翻秋向晚。凉砧敲月催金剪。楼上已清寒。不堪频倚栏。 邻翁开社瓮。唤客情应重。不醉且无归。醉时归路迷。'),
	(7353, 188, '踏莎行', '杨柳东风，海棠春雨。清愁冉冉无来处。曲径惊飞峡蝶丛，回塘冻湿鸳鸯侣。 舞彻霓裳，歌残金缕。蘼芜白芷愁烟渚。不识阳台梦里云，试听华表归来语。'),
	(7354, 378, '画堂春（风中荷花）', '荷花含笑调薰风。两情著意尤浓。水精栏槛四玲珑。照见妆容。 醉里偷开盏面，晓来暗坼香风。不知何事苦匆匆。飘落残红。'),
	(7355, 435, '八声甘州（读周公瑾传）', '事茫茫、赤壁半帆风，四海忽三分。想苍烟金虎，碧云铜爵，恨满乾坤。郁郁秣陵王气，传到第三孙。风虎云龙会，自有其人。 朱颜二十有四，正锦帏秋梦，玉帐春声。望吴江楚汉，明月伴英魂。浥浥小桥红浪湿，抚虚弦、何处得郎闻。雪堂老，千年一瞬，再击空明。'),
	(7356, 435, '相见欢（薄霜）', '霜花零落全稀。不成飞。寒水溶溶漾漾、软琉璃。 红未涌，青已露，白都晞。□□□□沙暖、戏凫鹥。'),
	(7357, 498, '鹧鸪天（雪梅）', '压蕊拈须粉作团。疏香辛苦颤朝寒。须知风月寻常见，不似层层带雪看。 春髻重，晓眉弯。一枝斜并缕金＿。酒红不解东风冻，惊怪钗头玉燕干。'),
	(7358, 498, '梦玉人引', '送行人去，犹追路、再相觅。天末交情，长是合堂同席。从此尊前，便顿然少个，江南＿客。不忍匆匆，少驻船梅驿。 酒斟虽满，尚少如、别泪万千滴。欲语吞声，结心相对呜咽。灯火凄清，笙歌无颜色。从别后，尽相忘，算也难忘今夕。'),
	(7359, 633, '风入松', '十年裘马锦江滨。酒隐红尘。万金选胜莺花海，倚疏狂、驱使青春。吹笛鱼龙尽出，题诗风月俱新。 自怜华发满纱巾。犹是官身。凤楼常记当年语，问浮名、何似身亲。欲寄吴笺说与，这回真个闲人。'),
	(7360, 633, '恋绣衾', '不惜貂裘换钓篷。嗟时人、谁识放翁。归棹借、樵风稳，数声闻、林外暮钟。 幽栖莫笑蜗庐小，有云山、烟水万重。半世向、丹青看，喜如今、身在画中。'),
	(7361, 633, '上西楼（一名相见欢）', '江头绿暗红稀。燕交飞。忽到当年行处、恨依依。 洒清泪。叹人事。与心违。满酌玉壶花露、送春归。'),
	(7362, 633, '菩萨蛮', '小院蚕眠春欲老。新巢燕乳花如扫。幽梦锦城西。海棠如旧时。 当年真草草。一棹还吴草。题罢惜春诗。镜中添鬓丝。'),
	(7363, 633, '南歌子（送周机宜之益昌）', '异县相逢晚，中年作别难。暮秋风雨客衣寒。又向朝天门外、话悲欢。 瘦马行霜栈，轻舟下雪滩。乌奴山下一林丹。为说三年常寄、梦魂间。'),
	(7364, 633, '洞庭春色', '壮岁文章，暮年勋业，自昔误人。算英雄成败，轩裳得失，难如人意，空丧天真。请看邯郸当日梦，待炊罢黄粱徐欠伸。方知道，许多时富贵，何处关身。 人间定无可意，怎换得、玉鲙丝莼。且钓竿渔艇，笔床茶灶，闲听荷雨，一洗衣尘。洛水秦关千古后，尚棘暗铜驼空怆神。何须更，慕封侯定远，图像麒麟。'),
	(7365, 633, '月上海棠', '兰房绣户厌厌病。叹春酲、和闷甚时醒。燕子空归，几曾传、玉关边信。伤心处，独展团窠瑞锦。 熏笼消歇沈烟冷。泪痕深、展转看花影。漫拥余香，怎禁他、峭寒孤枕。西窗晓，几声银瓶玉井。'),
	(7366, 633, '汉宫春（张园赏海棠作，园故蜀燕王宫也）', '浪迹人间。喜闻猿楚峡，学剑秦川。虚舟泛然不系，万里江天。朱颜绿鬓，作红尘、无事神仙。何妨在，莺花海里，行歌闲送流年。 休笑放慵狂眼，看闲坊深院，多少婵娟。燕宫海棠夜宴，花覆金船。如椽画烛，酒阑时、百炬吹烟。凭寄语，京华旧侣，幅巾莫换貂蝉。'),
	(7367, 633, '感皇恩·小阁倚秋空', '小阁倚秋空，下临江渚。漠漠孤云未成雨。数声新雁，回首杜陵何处。壮心空万里，人谁许！ 黄阁紫枢，筑坛开府。莫怕功名欠人做。如今熟计，只有故乡归路。石帆山脚下，菱三亩。'),
	(7368, 457, '韵令（硕人生日）', '是男是女，都有官称。孙儿仕也登。时新衣著，不待经营。寒时火柜，春里花亭。星辰上履，我只唤卿卿。 寿开八秩，两鬓全青。颜红步武轻。定知前面，大有年龄。芝兰玉树，更愿充庭。为询王母，桃颗几时赪。白乐天《开六秩诗》自注：年五十一岁，即曰开第六秩矣。言自五十一，即为六十纪数之始也。'),
	(7369, 526, '鹊桥仙（七夕）', '牛郎织女，因缘不断，结下生生世世。人言恩爱久长难，又不道、如今几岁。 眼穿肠断，一年今夜，且做不期而会。三杯酒罢闭云房，管上得、床儿仝睡。'),
	(7370, 573, '浣沙溪', '水到桐江镜样清。有人还似水清明。尊前无语更盈盈。 翠袖舞衫何日了，白头归去几时成。老来犹有惜花情。'),
	(7371, 573, '看花回', '端有恨，留春无计，花飞何速。槛外青青翠竹。镇高节凌云，清阴常足。春寒风袂，带雨穿窗如利镞。催处处、燕巧莺慵，几声钩辀叫云木。 看波面、垂杨蘸绿。最好是、风梳烟沐。阴重熏帘未卷，正泛乳新芽，香飘清馥。新诗惠我，开卷醒然欣再读。叹词章、过人华丽，掷地胜如金玉。'),
	(7372, 271, '浣溪沙（春夜）', '玉体金钗一样娇。背灯初解绣裙腰。衾寒枕冷夜香消。 深院重关春寂寂，落花和雨夜迢迢。恨情和梦更无聊。'),
	(7373, 271, '鹧鸪天', '独倚阑干昼日长。纷纷蜂蝶斗轻狂。一天飞絮东风恶，满路桃花春水香。 当此际，意偏长。萋萋芳草傍池塘。千钟尚欲偕春醉，幸有荼蘼与海棠。'),
	(7374, 669, '南乡子（龙眼未闻有诗词者，戏为赋之）', '江路木犀天。梨枣吹风树树悬。只道荔枝无驿使，依然。赢得骊珠万颗传。 香露滴芳鲜。并蒂连枝照绮筵。惊走梧桐双睡鹊，腰底黄金作弹圆。'),
	(7375, 388, '满江红', '春色匆匆，三分过、二分光景。吾老矣，坡轮西下，可堪弄影。曲水流觞时节好，茂林修竹池台永。望前村、绿柳荫茅檐，云封岭。 蜂蝶闹，烟花整。百年梦，如俄顷。这回头陈迹，漫劳深省。吹竹弹丝谁不爱，焚琴煮鹤人何肯。尽三觥、歌罢酒来时，风吹醒。'),
	(7376, 332, '双雁儿（除夕）', '穷阴急景暗推迁。减绿鬓，损朱颜。利名牵役几时闲。又还惊，一岁圆。 劝君今夕不须眠。且满满，泛觥船。大家沈醉对芳筵。愿新年，胜旧年。'),
	(7377, 105, '思佳客（竹西从人去数年矣，今得归，偶以此烦全美达之）', '曾醉扬州十里楼。竹西歌吹至今愁。燕衔柳絮春心远，鱼入晴江水自流。 情渺渺，梦悠悠。重寻罗带认银钩。挂帆欲伴渔人去，只恐桃花误客舟。'),
	(7378, 185, '临江仙·送宇文德和被召赴行在所', '露坐榕阴须痛饮，从渠叠鼓频催。暮山新月两徘徊。离愁秋水远，醉眼晓帆开。 泛宅浮家游戏去，流行坎止忘怀。江边鸥鹭莫相猜。上林消息好，鸿雁已归来。'),
	(7379, 635, '虞美人（大光祖席醉中赋长短句）', '张帆欲去仍搔首。更醉君家酒。吟诗日日待春风。及至桃花开后、却匆匆。 歌声频为行人咽。记著尊前雪。明朝酒醒大江流。满载一船离恨、向衡州。'),
	(7380, 635, '菩萨蛮（荷花）', '南轩面对芙蓉浦。宜风宜月还宜雨。红少绿多时。帘前光景奇。 绳床乌木几。尽日繁香里。睡起一篇新。与花为主人。'),
	(7381, 635, '定风波（重阳）', '九日登临有故常。随晴随雨一传觞。多病题诗无好句。孤负。黄花今日十分黄。 记得眉山文翰老。曾道。四时佳节是重阳。江海满前怀古意。谁会。阑干三抚独凄凉。'),
	(7382, 296, '永遇乐（初夏独坐西山钓台新亭）', '曲径通幽，小亭依翠，春事才过。看笋成竿，等花著果，永昼供闲坐。苍苍晚色，临渊小立，引首暮鸥飞堕。悄无人，一溪山影，可惜被渠分破。 百年似梦，一身如寄，南北去留皆可。我自知鱼，翛然濠上，不问鱼非我。隔篱呼取，举杯对影，有唱更凭谁和。知渊明，清流临赋，得似恁么。'),
	(7383, 518, '朝中措', '章台杨柳月依依。飞絮送春归。院宇日长人静，园林绿暗红稀。 庭前花谢了，行云散后，物是人非。唯有一襟清泪，凭阑洒遍残枝。'),
	(7384, 518, '愁倚阑', '天如水，月如钩。正新秋。月影参差人窈窕，小红楼。 如今往事悠悠。楼前水、肠断东流。旧物忍看金约腕，玉搔头。'),
	(7385, 518, '如梦令', '人静重门深亚。朱阁画帘高挂。人与月俱圆，月色波光相射。潇洒。潇洒。人月长长今夜。'),
	(7386, 518, '点绛唇', '人面桃花，去年今日津亭见。瑶琴锦荐。一弄清商怨。 今日重来，不见如如花面。空肠断。乱红千片。流水天涯远。'),
	(7387, 518, '虞美人', '红尘匹马长安道。人与花俱老。缓垂鞭袖过平康。散尽高阳、零落少年场。 朱弦重理相思调。无奈知音少。十年如梦尽堪伤。乐事如今、回首做凄凉。'),
	(7388, 518, '念奴娇', '凌空宝观，乍登临、多少伤离情味。淼淼烟波吴会远，极目江淮无际。槛外长江，楼中红袖，淡荡秋光里。一声横吹，半滩鸥鹭惊起。 因念邃馆香闺，玉肌花貌，有盈盈仙子。弄水题红传密意，宝墨银钩曾寄。泪粉香销，碧云□杳，脉脉人千里。一弯新月，断肠危栏独倚。'),
	(7389, 99, '生查子（与王丰父、郑曼卿兄弟嵩山道中）', '月在两山间，人在空明里。山色碧于天，月色光于水。 心闲物物幽，心动尘尘起。莫向动中来，长愿闲如此。'),
	(7390, 559, '声声慢（梅）', '欺寒冲暖，占早争春，江梅已破南枝。向晚阴凝，偏宜映月临池。天然莹肌秀骨，笑等闲、桃李芳菲。劳梦想，似玉人羞懒，弄粉妆迟。 长记行歌声断，犹堪恨，无情塞管频吹。寄远丁宁，折赠陇首相思。前村夜来雪里，殢东君、须索饶伊。烂漫也，算百花、犹自未知。'),
	(7391, 128, '江城子', '碧梧和露滴清秋。小庭幽。翠烟流。羞带一襟，明月上危楼。苦恨秋江风与月，偏管断，这些愁。 此情空道两绸缪。信悠悠。几时休。到得如今，刬地见无由。拟待不能思想得，无限事，在心头。'),
	(7392, 267, '相见欢', '秋风又到人间。叶珊珊。四望烟波无尽、欠青山。 浮生事。长江水。几时闲。幸是古来如此、且开颜。'),
	(7393, 267, '风蝶令', '试看何时有，元来总是空。丹砂只在酒杯中。看取乃公双颊、照人红。 花外庄周蝶，松间御寇风。古人漫尔说西东。何似自家识取、卖油翁。'),
	(7394, 267, '生查子', '卧病独眠人，无月中秋节。淡照碧纱灯，冷看银屏雪。 风露转萧寒，砧杵添凄切。伏枕漫书空，到晓愁难说。'),
	(7395, 267, '一落索', '惯被好花留住。蝶飞莺语。少年场上醉乡中，容易放、春归去。 今日江南春暮。朱颜何处。莫将愁绪比飞花，花有数、愁无数。'),
	(7396, 267, '浪淘沙（中秋阴雨，同显忠、椿年、谅之坐寺门作）', '圆月又中秋。南海西头。蛮云瘴雨晚难收。北客相逢弹泪坐，合恨分愁。 无酒可销忧。但说皇州。天家宫阙酒家楼。今夜只应清汴水，呜咽东流。'),
	(7397, 267, '朝中措', '先生筇杖是生涯。挑月更担花。把住都无憎爱，放行总是烟霞。 飘然携去，旗亭问酒，萧寺寻茶。恰似黄鹂无定，不知飞到谁家。'),
	(7398, 267, '鹧鸪天', '检尽历头冬又残。爱他风雪忍他寒。拖条竹杖家家酒，上个篮舆处处山。 添老大，转痴顽。谢天教我老来闲。道人还了鸳鸯债，纸帐梅花醉梦间。'),
	(7399, 267, '沁园春', '七十衰翁，告老归来，放怀纵心。念聚星高宴，围红盛集，如何著得，华发陈人。勉意追随，强颜陪奉，费力劳神恐未真。君休怪，近频辞雅会，不是无情。 岩扃。旧菊犹存。更松偃、梅疏新种成。爱静窗明几，焚香宴坐，闲调绿绮，默诵黄庭。莲社轻舆，雪溪小棹，有兴何妨寻弟兄。如今且，趓花迷酒困，心迹双清。'),
	(7400, 415, '醉花阴', '月娥昨夜江头过。把素衫揉破。冷逐晓云归，留与东风，吹作千千朵。 云残香瘦春犹可。玉笛愁无那。倚著画楼人，且莫教他，吹动些儿个。'),
	(7401, 415, '醉花阴', '红尘紫陌春来早。晚市烟光好。灯发万枝莲，华月光中，天净开蓬岛。 老人旧日曾年少。年少还须老。今夕在天涯，烛影星桥，也似长安道。'),
	(7402, 94, '鹧鸪天（十二月二十二日与许干誉赏梅）', '不怕微霜点玉肌。恨无流水照冰姿。与君著意从头看，初见今年第一枝。 人醉后，雪消时。江南春色寄来迟。使君本是花前客，莫怪殷勤为赋诗。'),
	(7403, 94, '虞美人（二日小雨达旦，西园独卧，寒甚不能寐，时窗前梨花将谢）', '数声微雨风惊晓，烛影欹残照。客愁不奈五更寒。明日梨花开尽、有谁看。 追寻犹记清明近。为向花前问。东风正使解欺侬。不道花应有恨、也匆匆。'),
	(7404, 564, '阮郎归·马蹄踏月响空山', '马蹄踏月响空山。梅生烟壑寒。水妃去后泪痕干。天风吹珮兰。 纫香久，怕花残。与君聊据鞍。一枝欲寄北人看。如今行路难。'),
	(7405, 510, '浪淘沙（十月十九夜赏菊）', '我爱菊花枝。浥露偏宜。旋移佳种一年期。照眼黄金三径烂，可但东篱。 秋老摘花吹。敢恨开迟。只愁一夜便香衰。待插满头年大也，且泛芳卮。'),
	(7406, 360, '烛影摇红·送会宗', '老景萧条，送君归去添凄断。赠君明月满前溪，直到西湖畔。 门掩绿苔应遍。为黄花、频开醉眼。橘奴无恙，蝶子相迎，寒窗日短。会宗小斋名梦蝶，前植橘，东偏甚广。'),
	(7407, 247, '如梦令', '墙外辘轳金井。惊梦瞢腾初省。深院闭斜阳，燕入阴阴帘影。人静。人静。花落鸟啼风定。'),
	(7408, 247, '渔家傲', '浦口潮来沙尾涨。危樯半落帆游漾，水调不知何处唱。风淡荡。鳜鱼吹起桃花浪。 雪尽小桥梅总放。层楼一任愁人上。万里长安回首望。山四向。澄江日色如春酿。'),
	(7409, 130, '玉团儿', '妍姿艳态腰如束。笑无限、桃粗杏俗。玉体横陈，云鬟斜坠，春睡还熟。 夕阳斗转阑干曲。乍醉起、余霞衫肉。搦粉搓酥，剪云裁雾，比并不足。'),
	(7410, 130, '丑奴儿', '酒熟微红生眼尾。半额龙香，冉冉飘衣袂。云压宝钗撩不起。黄金心字双垂耳。 愁入眉痕添秀美。无限柔情，分付西流水。忽被惊风吹别泪。只应天也知人意。'),
	(7411, 249, '御街行', '天街月照珠帘粉。亸辔曾相近。繁华乐事老来慵，对酒尚怜佳景。王孙年少，风流应更，无奈春愁闷。 幽期莫误香闺恨。罗带今朝褪。月圆花好一般春，触处总堪乘兴。有人惆怅，何如归好，相见凭君问。'),
	(7412, 249, '鹧鸪天', '绣幕低低拂地垂。春风何事入罗帏。胡麻好种无人种，正是归时君未归。 临晚景，忆当时。愁心一动乱如丝。夕阳芳草本无恨，才子佳人空自悲。'),
	(7413, 249, '浣溪沙', '江上秋高风怒号。江声不断雁嗷嗷。别魂迢递为君销。 一夜不眠孤客耳，耳边愁听雨萧萧。碧纱窗外有芭蕉。'),
	(7414, 249, '迷神引·贬玉溪对江山作', '黯黯青山红日暮，浩浩大江东注。余霞散绮，向烟波路。使人愁，长安远，在何处。几点渔灯小，迷近坞。一片客帆低，傍前浦。 暗想平生，自悔儒冠误。觉阮途穷，归心阻。断魂素月，一千里、伤平楚。怪竹枝歌，声声怨，为谁苦。猿鸟一时啼，惊岛屿。烛暗不成眠，听津鼓。'),
	(7415, 549, '蝶恋花', '小院朱扉开一扇。内样新妆，镜里分明见。眉晕半深唇注浅。朵云冠子偏宜面。 被掩芙蓉熏麝煎。帘影沈沈，只有双飞燕。心事向人犹勔腼。强来窗下寻针线。'),
	(7416, 549, '琴调相思引·送范殿监赴黄岗', '终日怀归翻送客。春风祖席。南城陌。便莫惜。离觞频卷白。动管色。催行色。动管色。催行色。 何处投鞍风雨夕。临水驿。空山驿。临水驿。空山驿。纵明月相思千里隔。梦咫尺。勤书尺。梦咫尺。勤书尺。'),
	(7417, 468, '浪淘沙（祝寿）', '祝寿庆生申。德日维新。期颐眉寿寿长春。五福三灵禄永永，长寿仙人。 遐算等庄椿。□德康宁。年年欢会笑欣欣。岁岁仰依□寿域，彭祖广成。'),
	(7418, 451, '御街行', '银烛生花如红豆。这好事、而今有。夜阑人静曲屏深，惜宝瑟、轻轻招手。一阵白苹风，故灭烛、教相就。花带雨、冰肌香透。恨啼鸟、辘轳声晓。岸柳微风吹残酒。断肠时、至今依旧。镜中消瘦。那人知后，怕你来僝僽。'),
	(7419, 451, '解语花', '窗涵月影，瓦冷霜华，深院重门悄。画楼雪杪。谁家笛、弄彻梅花新调。寒灯凝照。见锦帐、双鸾翔绕。当此时、倚几沈吟，好景都成恼。 曾过云山烟岛。对绣襦甲帐，亲逢一笑。人间年少。多情子、惟恨相逢不早。如今见了。却又惹、许多愁抱。算此情、除是青禽，为我殷勤报。'),
	(7420, 451, '念奴娇', '满天风雪，向行人、做出征途模样。回首家山才咫尺，便有许多离况。少岁交游，当时风景，喜得重相傍。一樽谈旧，骊驹门外休唱。 自笑二十年来，扁舟来往，惭愧湖头浪。献策彤庭身渐老，惟有丹心增壮。玉洞花光，金城柳眼，何用生凄怆。为君起舞，惊看豪气千丈。'),
	(7421, 451, '风入松（西山）', '崇峦雨过碧瑶光。花木递幽香。青冥杳霭无尘到，比龙宫、分外清凉。霁景一楼苍翠。薰风满壑笙簧。 不妨终日此徜徉。宇宙总俳场。石边试剑人何在，但荒烟、蔓草迷茫。好酹杯中芳酒，少留树杪斜阳。'),
	(7422, 451, '一落索', '杨花终日空飞舞，奈久长难驻。海潮虽是暂时来，却有个、堪凭处。 紫府碧云为路。好相将归去。肯如薄幸五更风，不解与、花为主。'),
	(7423, 451, '梦杨州', '晚云收。正柳塘、烟雨初休。燕子未归，恻恻轻寒如秋。小阑外、东风软，透绣帏、花蜜香稠。江南远，人何处，鹧鸪啼破春愁。 长记曾陪燕游。酬妙舞清歌，丽锦缠头。殢酒为花，十载因谁淹留。醉鞭拂面归来晚，望翠楼、帘卷金钩。佳会阻，离情正乱，频梦扬州。'),
	(7424, 451, '望海潮（四之四）', '奴如飞絮，郎如流水，相沾便肯相随。微月户庭，残灯帘幕，匆匆共惜佳期。才话暂分携。早抱人娇咽，双泪红垂。画舸难停，翠帏轻别两依依。 别来怎表相思。有分香帕子，合数松儿。红粉脆痕，青笺嫩约，丁宁莫遣人知。成病也因谁。更自言秋杪，亲去无疑。但恐生时注著，合有分于飞。'),
	(7425, 248, '武陵春', '湖上风光寒食近，准拟醉花枝。不忍东风烂熳时。红泪湿胭脂。 情知今后游从少，鸾镜懒重窥。金凤衔花绣衣。憔悴舞腰肢。'),
	(7426, 248, '踏莎行·萱草栏干', '萱草栏干，榴花庭院。悄无人语重帘卷。屏山掩梦不多时，斜风雨细江南岸。 昼漏初传，林莺百啭。日长暗记残香篆。洞房消息有谁知，几回欲问梁间燕。'),
	(7427, 693, '菩萨蛮', '轻风袅断沈烟炷。霏微尽日寒塘雨。残绣没心情。鸟啼花外声。离愁难自制。年少乖盟誓。寂寞掩朱门。罗衣空泪痕。'),
	(7428, 693, '喝火令', '见晚情如旧，交疏分已深。舞时歌处动人心。烟水数年魂梦，无处可追寻。 昨夜灯前见，重题汉上襟。便愁云雨又难寻。晓也星稀，晓也月西沈。晓也雁行低度，不曾寄芳音。'),
	(7429, 693, '采桑子', '樱桃著子如红豆，不管春归。闻道开时。蜂惹香须蝶惹衣。 楼台灯火明珠翠，酒恋歌迷。醉玉东西。少个人人暖被携。'),
	(7430, 693, '浪淘沙（荔枝）', '忆昔谪巴蛮。荔子亲攀。冰肌照映柘枝冠。日擘轻红三百颗，一味甘寒。 重入鬼门关。也似人间。一双和叶插云鬟。赖得清湘燕玉面，同倚阑干。'),
	(7431, 693, '定风波', '把酒花前欲问溪。问溪何事晚声悲。名利往来人尽老。谁道。溪声今古有休时。 且共玉人斟玉醑。休诉。笙歌一曲黛眉低。情似长溪长不断。君看。水声东去月轮西。'),
	(7432, 693, '西江月（老夫既戒酒不饮，遇宴集，独醒其旁。坐客欲得小词，援笔为赋）', '断送一生惟有，破除万事无过。远山横黛蘸秋波。不饮旁人笑我。 花病等闲瘦弱，春愁没处遮拦。杯行到手莫留残。不道月斜人散。'),
	(7433, 693, '诉衷情', '家风如何？为拟金华道人作此章 一波才动万波随。蓑笠一钩丝。锦鳞正在深处，千尺也须垂。 吞又吐，信还疑。上钩迟。水寒江静，满目青山，载月明归。'),
	(7434, 693, '南歌子', '诗有渊明语，歌无子夜声。论文思见老弥明。坐想罗浮山下、羽衣轻。 何处黔中郡，遥知隔晚晴。雨余风急断虹横。应梦池塘春草、若为情。'),
	(7435, 278, '减字木兰花', '渠不改。古有斯人。千载谁能继后尘。春风入手。乐事自应随处有。与众熙怡。何似幽居独乐时。 触涂是碍。一任浮沈何必改。有个人人。自说居尘不染尘。 谩夸千手。千物执持都是有。气候融怡。还取青天白日时。'),
	(7436, 494, '减字木兰花', '凭谁妙笔。横扫素缣三百尺。天下应无。此是钱塘湖上图。（苏轼） 一般奇绝。云淡天高秋夜月。费尽丹青。只这些儿画不成。（仲殊）'),
	(7437, 494, '瑶池燕', '飞花成阵。春心困。寸寸。别肠多少愁闷。无人问。偷啼自揾。残妆粉。抱瑶琴、寻出新韵。玉纤趁。南风来解幽愠。低云鬟、眉峰敛晕。娇和恨。'),
	(7438, 494, '南乡子（集句）', '寒玉细凝肤（吴融）。清歌一曲倒金壶（郑谷）。冶叶倡条遍相识（李商隐），争如。豆寇花梢二月初（杜牧）。 年少即须臾（白居易）。芳时偷得醉工夫（白居易）。罗帐细垂银烛背（韩偓），欢娱。豁得平生俊气无（杜牧）。'),
	(7439, 494, '渔父（四首）', '渔父饮，谁家去。鱼蟹一时分付。酒无多少醉为期，彼此不论钱数。'),
	(7440, 494, '浣溪沙（端午）', '入袂轻风不破尘。玉簪犀璧醉佳辰。一番红粉为谁新。 团扇只堪题往事，新丝那解系行人。酒阑滋味似残春。'),
	(7441, 494, '减字木兰花（得书）', '晓来风细。不会鹊声来报喜。却羡寒梅。先觉春风一夜来。 香笺一纸。写尽回文机上意。欲卷重开。读遍千回与万回。'),
	(7442, 494, '诉衷情（琵琶女）', '小莲初上琵琶弦。弹破碧云天。分明绣阁幽恨，都向曲中传。 肤莹玉，鬓梳蝉。绮窗前。素娥今夜，故故随人，似斗婵娟。'),
	(7443, 494, '殢人娇（或云赠朝云）', '白发苍颜，正是维摩境界。空方丈、散花何碍。朱唇箸点，更髻鬟生彩。这些个，千生万生只在。 好事心肠，著人情态。闲窗下、敛云凝黛。明朝端午，待学纫兰为佩。寻一首好诗，要书裙带。'),
	(7444, 494, '虞美人', '作，非也。山谷亦云。大观中，于金陵见其亲笔，实《东坡词》也 波声拍枕长淮晓。隙月窥人小。无情汴水自东流。只载一船离恨、向西州。 竹溪花浦曾同醉。酒味多于泪。谁教风鉴在尘埃。酝造一场烦恼、送人来。'),
	(7445, 494, '蝶恋花（述怀）', '云水萦回溪上路。叠叠青山，环绕溪东注。月白沙汀翘宿鹭。更无一点尘来处。 溪叟相看私自语。底事区区，苦要为官去。尊酒不空田百亩。归来分得闲中趣。'),
	(7446, 494, '蝶恋花（过涟水军赠赵晦之）', '自古涟漪佳绝地。绕郭荷花，欲把吴兴比。倦客尘埃何处洗。真君堂下寒泉水。 左海门前酤酒市。夜半潮来，月下孤舟起。倾盖相逢拚一醉。双凫飞去人千里。'),
	(7447, 494, '江神子', '乃作斜川诗，至今使人想见其处。元丰壬戍之春，余躬耕于东坡，筑雪堂居之。南挹四望亭之后丘，西控北山之微泉，慨然而叹，此亦斜川之游也 梦中了了醉中醒。只渊明。是前生。走遍人间，依旧却躬耕。昨夜东坡春雨足，乌鹊喜，报新晴。 雪堂西畔暗泉鸣。北山倾。小溪横。南望亭丘，孤秀耸曾城。都是斜川当日境，吾老矣，寄余龄。'),
	(7448, 494, '临江仙', '冬夜夜寒冰合井，画堂明月侵帏。青缸明灭照悲啼。青缸挑欲尽，粉泪裛还垂。 未尽一尊先掩泪，歌声半带清悲。情声两尽莫相违。欲知肠断处，梁上暗尘飞。'),
	(7449, 494, '满庭芳', '水浅冻，久留郡中。晦日同游南山，话旧感叹，因作满庭芳云 三十三年，飘流江海，万里烟浪云帆。故人惊怪，憔悴老青衫。我自疏狂异趣，君何事、奔走尘凡。流年尽，穷途坐守，船尾冻相衔。 巉巉。淮浦外，层楼翠壁，古寺空岩。步携手林间，笑挽扦扦。莫上孤峰尽处，萦望眼、云海相搀。家何在，因君问我，归步绕松衫。'),
	(7450, 494, '满庭芳', '香叆雕盘，寒生冰箸，画堂别是风光。主人情重，开宴出红妆。腻玉圆搓素颈，藕丝嫩、新织仙裳。双歌罢，虚檐转月，余韵尚悠扬。 人间，何处有，司空见惯，应谓寻常。坐中有狂客，恼乱愁肠。报道金钗坠也，十指露、春笋纤长。亲曾见，全胜宋玉，想像赋高唐。'),
	(7451, 250, '燕归梁', '莲叶雨，蓼花风。秋恨几枝红。远烟收尽水溶溶。飞雁碧云中。 衷肠事。鱼笺字。情绪年年相似。凭高双袖晚寒浓。人在月桥东。'),
	(7452, 250, '醉落魄', '鸾孤月缺。两春惆怅音尘绝。如今若负当时节。信道欢缘，狂向衣襟结。 若问相思何处歇。相逢便是相思彻。尽饶别后留心别。也待相逢。细把相思说。'),
	(7453, 250, '浪淘沙', '高阁对横塘。新燕年光。柳花残梦隔潇湘。绿浦归帆看不见，还是斜阳。 一笑解愁肠，人会娥妆。藕丝衫袖郁金香。曳雪牵云留客醉，且伴春狂。'),
	(7454, 250, '清平乐', '春云绿处。又见归鸿去。侧帽风前花满路。冶叶倡条情绪。 红楼桂酒新开。曾携翠袖同来。醉弄影娥池水，短箫吹落残梅。'),
	(7455, 250, '生查子·狂花顷刻香', '狂花顷刻香，晚蝶缠绵意。天与短因缘，聚散常容易。 传唱入离声，恼乱双蛾翠。游子不堪闻，正是衷肠事。'),
	(7456, 250, '鹧鸪天', '清颍尊前酒满衣。十年风月旧相知。凭谁细话当时事，肠断山长水远诗。 金凤阙，玉龙墀。看君来换锦袍时。姮娥已有殷勤约，留著蟾宫第一枝。'),
	(7457, 409, '诉衷情（五之四）', '营巢燕子逞翱翔。微志在雕梁。碧云举翮千里，其奈有鸾皇。 临济处，德山行。果承当。自时降住，一切天魔，扫地焚香。'),
	(7458, 409, '诉衷情（五之二）', '练巾藜杖白云间。有兴即跻攀。追思往昔如梦，华毂也曾丹。 尘自扰，性长闲。更无还。达如周召，究似丘轲，祗个山山。'),
	(7459, 352, '清商怨', '关河愁思望处满。渐素秋向晚。雁过南云，行人回泪眼。双鸾衾裯悔展。夜又永、枕孤人远。梦未成归，梅花闻塞管。'),
	(7460, 352, '玉楼春（印眉）', '半辐霜绡亲手剪。香染青蛾和泪卷。画时横接媚霞长，印处双沾愁黛浅。 当时付我情何限。欲使妆痕长在眼。一回忆著一拈看，便似花前重见面。'),
	(7461, 352, '夜行船', '满眼东风飞絮。催行色、短亭春暮。落花流水草连云，看看是、断肠南浦。 檀板未终人去去。扁舟在、绿杨深处。手把金尊难为别，更那听、乱莺疏雨。'),
	(7462, 352, '蝶恋花', '帘幕东风寒料峭。雪里香梅，先报春来早。红蜡枝头双燕小。金刀剪彩呈纤巧。 旋暖金炉薰蕙藻。酒入横波，困不禁烦恼。绣被五更春睡好。罗帏不觉纱窗晓。'),
	(7463, 352, '阮郎归', '刘郎何日是来时。无心云胜伊。行云犹解傍山飞。郎行去不归。 强匀画，又芳菲。春深轻薄衣。花无语伴相思。阴阴月上时。'),
	(7464, 352, '长相思', '深花枝。浅花枝。深浅花枝相并时。花枝难似伊。 玉如肌。柳如眉。爱著鹅黄金缕衣。啼妆更为谁。'),
	(7465, 352, '采桑子', '十年一别流光速，白首相逢。莫话衰翁。但斗尊前语笑同。 劝君满酌君须醉，尽日从容。画鹢牵风。即去朝天沃舜聪。'),
	(7466, 251, '连理枝', '玉宇秋风至。帘幕生凉气。朱槿犹开，红莲尚拆，芙蓉含蕊。送旧巢归燕拂高檐，见梧桐叶坠。 嘉宴凌晨启。金鸭飘香细。凤竹鸾丝，清歌妙舞，尽呈游艺。愿百千遐寿此神仙，有年年岁岁。'),
	(7467, 251, '秋蕊香', '梅蕊雪残香瘦。罗幕轻寒微透。多情只似春杨柳。占断可怜时候。 萧娘劝我杯中酒。翻红袖。金乌玉兔长飞走。争得朱颜依旧。'),
	(7468, 251, '谒金门', '秋露坠。滴尽楚兰红泪。往事旧欢何限意。思量如梦寐。 人貌老于前岁。风月宛然无异。座有嘉宾尊有桂。莫辞终夕醉。'),
	(7469, 186, '碧牡丹（晏同叔出姬）', '步帐摇红绮。晓月堕，沈烟砌。缓板香檀，唱彻伊家新制。怨入眉头，敛黛峰横翠。芭蕉寒，雨声碎。 镜华翳。闲照孤鸾戏。思量去时容易。钿盒瑶钗，至今冷落轻弃。望极蓝桥，但暮云千里。几重山，几重水。'),
	(7470, 186, '玉树后庭花（上元·般涉调）', '华灯火树红相斗。往来如昼。桥河水白天青，讶别生星斗。 落梅秾李还依旧。宝钗沽酒。晓蟾残漏心情，恨雕鞍归后。'),
	(7471, 186, '天仙子（别渝州·仙吕调）', '醉笑相逢能几度。为报江头春且住。主人今日是行人，红袖舞。清歌女。凭仗东风教点取。 三月柳枝柔似缕。落絮尽飞还恋树。有情宁不忆西园，莺解语。花无数。应讶使君何处去。'),
	(7472, 344, '归去来（中吕调）', '一夜狂风雨。花英坠、碎红无数。垂杨漫结黄金楼。尽春残、萦不住。 蝶稀蜂散知何处。殢尊酒、转添愁绪。多情不惯相思苦。休惆怅、好归去。'),
	(7473, 344, '西施（三之一·仙吕调）', '苎萝妖艳世难偕。善媚悦君怀。后庭恃宠，尽使绝嫌猜。正恁朝欢暮宴，情未足，早江上兵来。 捧心调态军前死，罗绮旋变尘埃。至今想，怨魂无主尚徘徊。夜夜姑苏城外，当时月，但空照荒台。'),
	(7474, 344, '望汉月（平调）', '明月明月明月。争奈作圆还缺。恰如年少洞房人，暂欢会、依前离别。 小楼凭槛处，正是去年时节。千里清光又依旧，奈夜永、厌厌人绝。'),
	(7475, 344, '击梧桐（中吕调）', '香靥深深，姿姿媚媚，雅格奇容天与。自识伊来，便好看承，会得妖娆心素。临歧再约同欢，定是都把、平生相许。又恐恩情，易破难成，不免千般思虑。 近日书来，寒暄而已，苦没忉忉言语。便认得、听人教当，拟把前言轻负。见说兰台宋玉，多才多艺善词赋。试与问、朝朝暮暮。行云何处去。'),
	(7476, 344, '洞仙歌（中吕调）', '佳景留心惯。况少年彼此，风情非浅。有笙歌巷陌，绮罗庭院。倾城巧笑如花面。恣雅态、明眸回美盼。同心绾。算国艳仙材，翻恨相逢晚。 缱绻。洞房悄悄，绣被重重，夜永欢余，共有海约山盟，记得翠云偷翦。和鸣彩凤于飞燕。间柳径花阴携手遍。情眷恋。向其间、密约轻怜事何限。忍聚散。况已结深深愿。愿人间天上，暮云朝雨长相见。'),
	(7477, 344, '望远行·绣帏睡起', '绣帏睡起。残妆浅，无绪匀红补翠。藻井凝尘，金梯铺藓。寂寞凤楼十二。风絮纷纷，烟芜苒苒，永日画阑，沈吟独倚。望远行，南陌春残悄归骑。 凝睇。消遣离愁无计。但暗掷、金钗买醉。对好景、空饮香醪，争奈转添珠泪。待伊游冶归来，故故解放翠羽，轻裙重系。见纤腰，图信人憔悴。'),
	(7478, 344, '驻马听·凤枕鸾帷', '凤枕鸾帷。二三载，如鱼似水相知。良天好景，深怜多爱，无非尽意依随。奈何伊。恣性灵、忒煞些儿。无事孜煎，万回千度，怎忍分离。 而今渐行渐远，渐觉虽悔难追。漫寄消寄息，终久奚为。也拟重论缱绻，争奈翻覆思维。纵再会，只恐恩情，难似当时。'),
	(7479, 344, '集贤宾·小楼深巷狂游遍', '小楼深巷狂游遍，罗绮成丛。就中堪人属意，最是虫虫。有画难描雅态，无花可比芳容。几回饮散良宵永，鸳衾暖、凤枕香浓。算得人间天上，惟有两心同。 近来云雨忽西东。诮恼损情悰。纵然偷期暗会，长是匆匆。争似和鸣偕老，免教敛翠啼红。眼前时、暂疏欢宴，盟言在、更莫忡忡。待作真个宅院，方信有初终。'),
	(7480, 344, '御街行·前时小饮春庭院', '前时小饮春庭院。悔放笙歌散。归来中夜酒醺醺，惹起旧愁无限。虽看坠楼换马，争奈不是鸳鸯伴。 朦胧暗想如花面。欲梦还惊断。和衣拥被不成眠，一枕万回千转。惟有画梁，新来双燕，彻曙闻长欢。'),
	(7481, 176, '丑奴儿令（促养直赴雪夜溪堂之约）', '冯夷剪碎澄溪练，飞下同云。著地无痕。柳絮梅花处处春。 山阴此夜明如昼，月满前村。莫掩溪门。恐有扁舟乘兴人。'),
	(7482, 477, '采桑子', '与君别后愁无限，永远团圞。间阴多方。水远山遥过断肠。 终朝等候郎音耗，捱过春光。烟水茫茫。梅子青青又待黄。'),
	(7483, 108, '扬州慢（初秋）', '露叶犹青，岩药迟动，幽幽未似秋阴。似梅风带溽，吹度长林。记当日、西廊共月，小屏轻扇，人语凉深。对清觞，醉笑醒颦，何似如今。 临高欲赋，甚年来、渐减狂心。为谁倚多才，难凭易感，早付销沈。解事张郎风致，鲈鱼好、归听吴音。又夜阑闻笛，故人忽到幽襟。'),
	(7484, 532, '踏莎行（题草窗词卷）', '白石飞仙，紫霞凄调。断歌人听知音少。几番幽梦欲回时，旧家池馆生青草。风月交游，山川怀抱。凭谁说与春知道。空留离恨满江南，相思一夜苹花老。'),
	(7485, 86, '暗香', '梢，开花繁艳，兵後流落人间。对花泫然承脸而赋 馆娃艳骨。见数枝雪里，争开时节。底事化工，著意阳和暗偷泄。偏把红膏染质，都点缀、枝头如血。最好是、院落黄昏，压栏照水清绝。 风韵自迥别。谩记省故家，玉手曾折。翠条袅娜，犹学宫妆舞残月。肠断江南倦客，歌未了、琼壶敲缺。更忍见，吹万点、满庭绛雪。'),
	(7486, 368, '水调歌头（冬至）', '候应黄钟动，吹出白葭灰。五云重压头，潜蛰地中雷。莫道希声妙寂，嶰竹雄鸣合凤，九寸律初裁。欲识天心处，请问学颜回。 冷中温，穷时达，信然哉。彩云山外如画，送上笔尖来。一气先通关窍，万物旋生头角，谁合又谁开。官路春光早，箫落数枝梅。'),
	(7487, 265, '摸鱼儿', '对西风、鬓摇烟碧，参差前事流水。紫丝罗带鸳鸯结。的的镜盟钗誓。浑不记、漫手织回文，几度欲心碎。安花著蒂。奈雨覆云翻，情宽分窄，石上玉簪脆。 朱楼外。愁压空云欲坠。月痕犹照无寐。阴晴也只随天意。枉子玉消香碎。君且醉。君不见、长门青草春风泪。一时左计。悔不早荆钗，暮天修竹，头白倚寒翠。'),
	(7488, 200, '酹江月（中秋对月）', '奁霏净洗，唤素娥睡起，平分秋色。雁背风高孀兔冷，露脚侵衣香湿。银浦流云，珠房迎晓，鬓影霜争白。玉尊良夜，与谁同醉瑶席。 忍记倚桂分题，簪花簪酒，处处成陈迹。十二楼空环佩杳，惟有孤云知得。如此江山，依然风月，月底人非昔。知音何许，泪痕空沁愁碧。'),
	(7489, 196, '唐多令', '金勒鞚花骢。故山云雾中。翠苹洲、先有西风。可惜嫩凉时枕簟，都付与、旧山翁。 双翠合眉峰。泪华分脸红。向尊前、何太匆匆。才是别离情便苦，都莫问、淡和浓。'),
	(7490, 611, '诉衷情', '酒旗摇曳柳花天。莺语软於棉。碎绿未盈芳沼，倒影蘸秋千。 奁玉燕，套金蝉。负华年。试问归期，是酴醿后，是牡丹前。'),
	(7491, 212, '解语花', '轻雷殷殷，小枕惊回，帘影摇庭户。嫩凉遥度。江云堕、结作西窗暗雨。闲阶静伫。叹疏袂、愁宽一缕。凭画栏，润叶鸣条，总是安秋处。 因唤扁舟晚渡。渐闻歌招得，采菱俦侣。临平归路。花无数、应认订洲倦旅。飞红怨暮。长趁得、断鸿南浦。闲枕衾，谁更无聊，应最怜纨素。'),
	(7492, 583, '汉宫春·着破荷衣', '着破荷衣，笑西风吹我，又落西湖。湖间旧时饮者，今与谁俱？山山映带，似携来、画卷重舒。三十里，芙蓉步障，依然红翠相扶。 一目清无留处，任屋浮天上，身集空虚。残烧夕阳过雁，点点疏疏。故人老大，好襟怀，消减全无。慢赢得，秋风两耳，冷泉亭下骑驴。'),
	(7493, 583, '摘红英', '东风冽。红梅拆。画帘几片飞来雪。银屏悄。罗裙小。一点相思，满塘春草。 空愁切。何年彻。不归也合分明说。长安道。箫声闹。去时骢马，谁家系了。'),
	(7494, 583, '梅花引', '对花时节不曾忺。见花残。任花残。小约帘栊，一面受春寒。题破玉笺双喜鹊，香烬冷，绕银屏，浑是山。 待眠。未眠。事万千。也问天。也恨天。髻儿半偏。绣裙儿、宽了还宽。自取红毡，重坐暖金船。惟有月知君去处，今夜月，照秦楼，第几间。'),
	(7495, 583, '清平乐', '锦屏香褪。寒隐轻衫嫩。燕子护泥飞不稳。庭掩百花难认。 双双绣带微风。海棠此夜帘栊。愁损一番寒食，小窗淡月残红。'),
	(7496, 143, '长相思慢', '日折霜檐，寒欺雾幕，睛枝浅弄开华。惊回绮梦，独拥绫衾，腮痕微印朝霞。倦起心情，念风移霜换，依旧天涯。雁影水云斜。算相思、一点愁赊。 问橘楚橙吴，旧香犹在，别後襟袖输他。琴心知未许，想钿钗、流落谁家。怕上高楼，归思远、斜阳暮鸦。几多年、江湖浪识，知心只许梅花。'),
	(7497, 242, '兰陵王', '柳花白。飞入青烟巷陌。凭高处，愁锁断桥，十里东风正无力。西湖路咫尺。犹阻仙源信息。伤心事，还似去年，中酒恹恹度寒食。 闲窗掩春寂。但粉指留红，茸唾凝碧。歌尘不散蒙香泽。念鸾孤金镜，雁空瑶瑟。芳时凉夜尽怨忆。梦魂省难觅。鳞鸿，渺踪迹。纵罗帕亲题，锦字谁织。缄情欲寄重城隔。又流水斜照，倦箫残笛。楼台相望，对暮色，恨无极。'),
	(7498, 242, '曲游春（清明湖上）', '画舸西泠路，占柳阴花影，芳意如织。小楫冲波，度麹尘扇底，粉香帘隙。岸转斜阳隔。又过尽、别船箫笛。傍断桥、翠绕红围，相对半篙晴色。 顷刻。千山暮碧。向沽酒楼前，犹击金勒。乘月归来，正梨花夜缟，海棠烟幂。院宇明寒食。醉乍醒、一庭春寂。任满身、露湿东风，欲眠未得。'),
	(7499, 79, '水调歌头', '英荚才开六，宝历已当千。人间收尽繁溽，凉意入虞弦。尽道荐衡交剡，更值生申佳节，喜色动闾阎。终夜望银汉，文宿贯台躔。 定燕秦，封晋魏，信当然。处处红莲开幕，曹掾岂能淹。见说玉堂飞诏，已许金闺通籍，蓬岛伴神仙。来岁寿卮酒，应醉御炉烟。'),
	(7500, 431, '水调歌头', '今夕是何夕，南极见祥光。自天飘下佳气，五色覆黄堂。为借监梅妙手，暂对袴襦欢颂，森戟护凝香。余事剩吟咏，金薤灿琳琅。 眷田园，松迳旧，菊畦荒。欲乘风驭归去，策杖从相羊。物外乾坤自在，壶里无尘日月，千岁傲义皇。天意未应许，军国要平章。'),
	(7501, 677, '满江红', '人品如君，人尽道，士林横绝。那更是、关西流庆，三山英杰。欣遇当年神降日，又逢初度阳生月。把八千余岁祝君龄，为君说。 君自有，封侯骨。君不是，栖鸾客。况如今东阁，正收人物。坦腹素知王逸少，求贤不必商岩说。便明朝、有诏自天来，君王礼。'),
	(7502, 608, '八声甘州', '渐莺声近也，探年芳、河畔扼轻轮。旋东风染绿，绵绵平野，无际烟春。最苦夕阳天外，愁损倚阑人。无奈潇湘杳，留滞王孙。 冷落池塘残梦，是送君归后，南浦消魂。赖东君能客，醉卧展香茵。尽教更、行人远，也相伴、连水复连云。关山道、算无今古，客恨长新。'),
	(7503, 334, '醉桃源', '大都修炼似蒸沙。阴阳失两家。正如飞鳌舞长蛇。宁知饮月华。 回老貌，假群姓。熏蒸成绛霞。但教心地不倾斜。巢中能养鸦。'),
	(7504, 334, '浣溪沙（素馨茉莉）', '南国幽花比并香。直从初夏到秋凉。素馨茉莉古时光。 梅□正寒方著蕊，芙蓉过暑即空塘。个中春色最难量。'),
	(7505, 688, '鹧鸪天', '天气清和仅两旬。一旬前是佛生辰。当年来应徐卿梦，此夕遥瞻寿宿明。 拚一笑，对诸贤。山翁何以祝龟龄。蟠桃瓜枣皆虚诞，愿把阴功福后人。'),
	(7506, 688, '谒金门（初春）', '花事浅。方费化工匀染。墙角红梅开未遍。小桃才数点。 人在暮寒庭院。闲续茶经香传。酒思如冰诗思懒。雨声帘不卷。'),
	(7507, 421, '菩萨蛮（西湖曲）', '横湖十顷琉璃碧。画桥百步通南北。沙暖睡鸳鸯。春风花草香。 间来撑小艇。割破楼台影。四面望青山。浑如蓬莱间。'),
	(7508, 585, '吴山青·金璞明', '金璞明。玉璞明。小小杯柈翠袖擎。满将春色盛。仙珮鸣。玉珮鸣。雪月花中过洞庭。此时人独清。'),
	(7509, 691, '水龙吟', '间情小院沈吟，草深柳密帘空翠。风檐夜响，残灯慵剔，寒轻怯睡。店舍无烟，关山有月，梨花满地。二十年好梦，不曾圆合，而今老、都休矣。 谁共题诗秉烛，两厌厌、天涯别袂。柔肠一寸，七分是恨，三分是泪。芳信不来，玉箫尘染，粉衣香退。待问春，怎把千红换得，一池绿水。'),
	(7510, 277, '祝英台近（寿张路钤·四月初）', '夏初临，春正满，花事在红药。一阵光风，香雾喷珠箔。画堂旧日张家，梦中玉燕，早拂晓、飞来帘幕。 酒深酌。曾记走马长安，功名戏樊郭。螺浦如杯，豪气怎生著。直须用了圯编，封侯万户，却归共、赤松翁约。'),
	(7511, 172, '六州歌头', '海棠开后，红雨洒江濆。春风路，窥杏非，剪葵榛。十三、杨柳女儿腰，东风舞。 云外月，风前絮。情与恨，长如许。想绮窗今夜，为谁凝伫。洛浦梦回留佩客，秦楼声断吹箫侣。正黄昏时候杏花寒，帘纤雨。'),
	(7512, 340, '括临江仙', '须信乾坤如逆旅，都来一梦浮生。夜游秉烛尽欢情。阳春烟景媚，乐事史来并。 座上群公皆俊秀，高谈幽赏俱清。飞觞醉月莫辞频。休论金谷罚，七步看诗成。 李白清平调辞：开元中，禁中初重木芍药，即今牡丹也。得四本：红、紫、浅红通白者，上移植于沉香亭前。花方繁开，上乘照夜车，太真妃以步辇从。诏选梨园弟子，得乐一十六色。李龟年以歌擅一时，手持檀版，将欲歌。上曰：赏名花，对妃子，焉用旧乐：命以金花笺宣赐翰林供奉李白立进清平调辞三章。白承诏旨，宿酲犹未解，援笔赋之： 云想衣裳花想容。春风拂槛露花浓。若非群玉山头见，会向瑶台月下逢。 一枝红艳露凝香。云雨巫山枉断肠。借问汉宫谁得似，可怜飞燕舞红妆。 名花倾国两相欢。长得君王带笑看。解释春风无限恨，沉香亭北倚兰干。 辞进，促龟年歌之。太真妃持颇黎七宝杯，酌西凉州蒲萄酒，笑领歌辞，意甚厚。饮罢，敛绣巾重拜。上自是顾李白尤异于诸学士云。'),
	(7513, 233, '西江月', '宿酒才醒又醉，春霄欲雨还晴。柳边花底听莺声。白发莫教临镜。 过隙光阴易去，浮云富贵难凭。但将一笑对公卿。我是无名百姓。'),
	(7514, 31, '水调歌头', '灵谷有荚气，盘结在巴山。云蒸雨朵欲遣，奇怪出人间。不作丰城宝剑，不作渥洼灵种，不作化龙竿。生此济时杰，理乱总相关。 入为相，出为将，两余闲。忠肝义胆，曾将鲠论破天颜。好把升平勋业，趁取河清桃熟，千岁奉金銮。草却登封检，双鬓未曾斑。'),
	(7515, 411, '满江红', '八十归来，方岩下、几竿修竹。柴门外，沙铺软路，水流清玉。栽接新来桃与李，安排旧日松和菊。过小桥、作个看山楼，千峰绿。 收笔砚，藏棋局。酒莫饮，经须读。但平平放下，顿超凡俗。独睡已无年少梦，闲吟不唱他家曲。算人生、万事苦无多，相将足。'),
	(7516, 679, '眼儿媚', '翠帘低护郁金堂。犹自未堪妆。梨花新月，杏花新雨，怎奈昏黄。 春今不管人相忆，欲去又相将。只销相约，与春同去，须到君行。'),
	(7517, 47, '贺新郎（题吴江）', '重唤松江渡。叹垂虹亭下，销磨几番今古。依旧四桥风景在，为问坡仙甚处。但遗爱、沙边鸥鹭。天水相连苍茫外，更碧云、去尽山无数。潮正落，日远暮。 十年到此长凝伫。恨无人、与共秋风，鲙丝莼缕。小转朱弦弹九奏，拟致湘妃伴侣。俄皓月、飞来烟渚。恍若乘槎河汉上，怕客星、犯斗蛟龙怒。歌欸乃，过江去。'),
	(7518, 119, '酹江月', '玉虹遥挂，望青山隐隐，一眉如抹。忽觉天风吹海立，好似春霆初发。白马凌空，琼鳌驾水，日夜朝天阙。飞龙舞凤，郁葱环拱吴越。 此景天下应无，东南形胜，伟观真奇绝。好是吴儿飞彩帜，蹴起一江秋雪。黄屋天临，水犀云拥，看击中流楫。晚来波静，海门飞上明月。'),
	(7519, 70, '水调歌头（中秋）', '天淡四垂幕，云细不成衣。西风扫尽纤翳，凉我鬓边丝。破匣菱花飞动，跨海清光无际，草露滴明玑。杯到莫停手，何用问来期。 坐虚堂，揩病眼，诉流辉。云山应有幽恨，瑶瑟掩金徽。河汉无声自转，玉兔有情亦老，世事巧相违。一写谪仙怨，双泪满君颐。'),
	(7520, 678, '海棠春（春景）', '柳腰暗怯花风弱。红映秋千院落。归逐燕儿飞，斜撼真珠箔。 满林翠叶胭脂萼。不忍频频觑着。护取一庭春，莫弹花间鹊。'),
	(7521, 308, '鹊桥仙（七夕）', '迢迢郎意，盈盈妾恨，今夕鹊桥欲度。世间儿女一何痴，斗乞巧、纷纷无数。遥知此际，有人孤坐，心切天街云路。好因缘是恶因缘，梦魂远、阳台雨暮。'),
	(7522, 107, '西江月', '山色不随春老，竹枝长向人新。桃蹊李径已成阴。深院莺啼人静。尘世白驹过隙，人情苍狗浮云。不须计较谩劳神。且凭随缘任连。'),
	(7523, 147, '生查子（情景）', '郎如陌上尘，妾似堤边絮。相见两悠扬，踪迹无寻处。 酒面扑春风，泪眼零秋雨。过了别离时，还解相思否。'),
	(7524, 439, '卜算子', '欲整别离情，怯对尊中酒。野梵幽幽石上飘，搴落楼头柳。 不系黄金绶。粉黛愁成垢。春风三月有时阑，遮不尽、梨舒亶花丑。'),
	(7525, 495, '调啸词（二之二）', '归雁。归雁。饮啄江南南岸。将飞却下盘桓。塞北春来苦寒。苦寒、苦寒、寒苦。藻荇欲生且住。'),
	(7526, 433, '减字木兰花', '才鸣□鼓。曲奏仙时如乐府。美似梨园。一派箫韶列玳筵。 使人清耳。满尘宾朋皆欢喜。劝饮金荷。祝寿延长福更多。'),
	(7527, 433, '红芍药', '人生百岁，七十稀少。更除十年孩童小。又十年昏老。都来五十载，一半被、睡魔分了。那二十五载之中，宁无些个烦恼。仔细思量，好追欢及早。遇酒追朋笑傲。任玉山摧倒。沈醉且沈醉，人生似、露垂芳草。幸新来、有酒如渑，结千秋歌笑。'),
	(7528, 433, '江城梅花引', '年年江上见寒梅。暗香来。为谁开。疑是月宫、仙子下瑶台。冷艳一枝春在手，故人远，相思寄与谁。怨极恨极嗅香蕊。念此情，家万里。暮霞散绮。楚天碧、片片轻飞。为我多情，特地点征衣。花易飘零人易老，正心碎，那堪塞管吹。'),
	(7529, 408, '点绛唇', '秋气微凉，梦回明月穿帘幕。井梧萧索。正绕南枝鹊。宝瑟尘生，金雁空零落。情无托。鬓云慵掠。不似君恩薄。'),
	(7530, 690, '青玉案', '行人欲上来时路。破晓雾、轻寒去。隔叶子规声暗度。十分酒满，舞茵歌袖，沾夜无寻处。 故人近送旌旗暮。但听阳关第三句。欲断离肠馀几许。满天星月，看人憔悴，烛泪垂如雨。'),
	(7531, 697, '渔家傲（冬月）', '风入金波凝不住。玉楼间倚谁飞举。霜艳雪光来竞素。分辨处。独垂馀意窥庭户。 强薄罗衣催玉步。美人为我当尊舞。醉到春来能几度。愁今古。月华不去年华去。'),
	(7532, 495, '渔家傲（和门人祝寿）', '七十馀年真一梦。朝来寿斝儿孙奉。忧患已空无复痛。心不动。此间自有千钧重。 早岁文章供世用。中年禅味疑天纵。石塔成时无一缝。谁与共。人间天上随他送。'),
	(7533, 682, '系裙腰', '灯花耿耿漏迟迟。人别后、夜凉时。西风潇洒梦初回。谁念我，就单枕，皱双眉。 锦屏绣幌与秋期。肠欲断、泪偷垂。月明还到小窗西。我恨你，我忆你，你争知。'),
	(7534, 433, '减字木兰花', '多愁早老。着甚由来闲烦恼。休管浮名。安乐身康似宝珍。 酒逢知已，好向尊前朝日醉。满劝瑶觥。祝寿如山岁岁青。'),
	(7535, 433, '高阳台', '红入桃腮，青回柳眼，韶华已破三分。人不归来，空教草怨王孙。平明几点催花雨，梦半阑、欹枕初闻。问东君，因甚将春，老了闲人。 东郊十里香尘满，旋安排玉勒，整顿雕轮。趁取芳时，共寻岛上红云。朱衣引马黄金带，算到头、总是虚名。莫闲愁，一半悲秋，一半伤春。'),
	(7536, 433, '菩萨蛮（归思）', '单于吹落山头月。漫漫江上沙如雪。谁唱缕金衣。水寒船舫稀。 芦花枫叶浦。忆抱琵琶语。身未发长沙。梦魂先到家。'),
	(7537, 375, '念奴娇', '杏花过雨，渐残红零落，胭脂颜色。流水飘香人渐远，难托春心脉脉。恨别王孙，墙阴目断，手把青梅摘。金鞍何处，绿杨依旧南陌。消散云雨须臾，多情因甚，有轻离轻拆。燕语千般，争解说、些子伊家消息。厚约深盟，除非重见，见了方端的。而今无奈，寸肠千恨堆积。'),
	(7538, 428, '望江南（七）', '江南水，江路转平沙。雨霁高烟收素练，风晴细浪吐寒花。迢递送星槎。名利客，飘泊未还家。西塞山前渔唱远，洞庭波上雁行斜。征棹宿天涯。'),
	(7539, 428, '定风波', '把酒花前欲问天。春来秋去苦茫然。风雨满枝花满地。何事。却教纤草占流年。 试把钿筝重促柱。无绪。酒阑清泪滴朱弦。赖有玉人相顾好。轻笑。却疑春色在婵娟。'),
	(7540, 95, '江南好', '丞相有才裨造化，圣皇宽诏养疏顽。赢取十年闲。'),
	(7541, 167, '点绛唇', '水陌轻寒，社公雨足东风慢。定巢新燕。湿雨穿花转。象尺熏炉，拂晓停针线。愁蛾浅。飞红零乱。侧卧珠帘卷。'),
	(7542, 74, '水龙吟（丙戊清明和章质夫韵）', '弄晴台馆收烟候，时有燕泥香坠。宿酲未解，单衣初试，腾腾春思。前度桃花，去年人面，重门深闭。记彩鸾别后，青驄归去，长亭路、芳尘起。 十二屏山遍倚。任苍苔、点红如缀。黄昏人静，暖香吹月，一帘花碎。芳意婆娑，绿阴风雨，画桥烟水。笑多情司马，留春无计，湿青衫泪。'),
	(7543, 322, '玉蝴蝶', '望处水寒云绕，倚栏千里，澹荡晴晖。梦燕门阑，和气喜动帘帏。舞因重、花明彩凤，歌扇小、香暖金猊。漏声迟。洞天秋晓，风露霏霏。 嘉时。谪仙高宴，华分玉蜡，艳列文姬。玉树临风，自然老月中枝。酒肠宽、胸容云梦，词源壮、笔勇助溪。且追随。伫看飞诏，稳步沙堤。'),
	(7544, 502, '木兰花（雨中）', '花时人道多风雨。梅蕊都来无几许。何须飘洒湿芳心，粉面琳琅如泪注。 家童莫扫花阴土。留浥琼林枝上露。若教燕子早衔泥，径里馀香应满户。'),
	(7545, 442, '南柯子（春景）', '梦怕愁时断，春从醉里回。凄凉怀抱向谁开。些子清明时候、被莺催。 柳外都成絮，栏边半是苔。多情帘燕独徘徊。依旧满身花雨、又归来。'),
	(7546, 232, '浣溪沙（妙高墨梅）', '日暮江空船自流。谁家院落近沧洲。一枝闲暇出墙头。 数朵幽香和月暗，十分归意为春留。风撩片片是闲愁。'),
	(7547, 202, '渔歌子', '□□□□白云多。童子贪眠枕绿蓑。莞尔笑，浩然歌。奈此萧萧落叶何。'),
	(7548, 202, '浪淘沙（秋江）', '万里一飞篷。吟老丹枫。潮生潮落海门东。三两点鸥沙外月，闲意谁同。 一色与天通。绝去尘红。渔歌忽断荻花风。烟水自流心不竞，长笛霜空。'),
	(7549, 202, '瑶台聚八仙（杭友寄声，以词答意）', '秋水涓涓。人正远、鱼雁待拂吟笺。也知游意，多在第二桥边。花底鸳鸯深处影，柳阴淡隔里湖船。路绵绵。梦吹旧笛，如此山川。 平生几两谢屐，任放歌自得，直上风烟。峭壁谁家，长啸竟落松前。十年孤剑万里，又何以、畦分抱瓮泉。山中酒，且醉餐石髓，白眼青天。'),
	(7550, 515, '南乡子（黄葵）', '冷淡是秋花。更比秋花冷淡些。到处芙蓉供醉赏，从他。自有幽人处士夸。 寂寞两三葩。昼日无风也带斜。一片西窗残照里，谁家。卷却湘裙薄薄纱。'),
	(7551, 515, '玉楼春（桃花湾马迹）', '秦人占得桃源地。说道花深堪避世。桃花湾内岂无花，吕政马来拦不住。 明朝与子穿花去。去看霜蹄剜石处。茫茫秦事是耶非，万一问花花解语。'),
	(7552, 515, '蝶恋花（风莲）', '我爱荷花花最软。锦拶云挨，朵朵娇如颤。一阵微风来自远。红低欲蘸凉波浅。 莫是羊家张静婉。抱月飘烟，舞得腰肢倦。偷把翠罗香被展。无眠却又频翻转。'),
	(7553, 515, '水龙吟（效稼轩体招落梅之魂）', '醉兮琼瀣浮觞些。招兮遣巫阳些。君毋去此，飓风将起，天微黄些。野马尘埃，污君楚楚，白霓裳些。驾空兮云浪，茫洋东下，流君往、他方些。 月满兮西厢些。叫云兮、笛凄凉些。归来为我，重倚蛟背，寒鳞苍些。俯视春红，浩然一笑，吐山香些。翠禽兮弄晓，招君未至，我心伤些。'),
	(7554, 15, '浣溪沙', '红紫妆林绿满池，游丝飞絮两依依。正当谷雨弄晴时。 射鸭矮阑苍藓滑，画眉小槛晚花迟。一年弹指又春归。'),
	(7555, 422, '声声慢', '啼螀门静，落叶阶深，秋声又入吾庐。一枕新凉，西窗晚雨疏疏。旧香旧色换却，但满川、残柳荒蒲。茂陵远，任岁华苒苒，老尽相如。 昨夜西风初起，想莼边呼棹，橘后思书。短景凄然，残歌空叩铜壶。当时送行共约，雁归时、人赋归欤。雁归也，问人归、如雁也无。'),
	(7556, 422, '水龙吟（牡丹）', '晓寒慵揭珠帘，牡丹院落花开未。玉栏干畔，柳丝一把，和风半倚。国色微酣，天香乍染，扶春不起。自真妃舞罢，谪仙赋后，繁华梦、如流水。 池馆家家芳事。记当时、买栽无地。争如一朵，幽人独对，水边竹际。把酒花前，剩拚醉了，醒来还醉。怕洛中、春色匆匆，又入杜鹃声里。'),
	(7557, 422, '南浦（春水）', '柳下碧粼粼，认曲尘乍生，色嫩如染。清溜满银塘，东风细、参差谷纹初遍。别君南浦，翠眉曾照波痕浅。再来涨绿迷旧处，添却残红几片。 葡萄过雨新痕，正拍拍轻鸥，翩翩小燕。帘影蘸楼阴，芳流去，应有泪珠千点。沧浪一舸，断魂重唱苹花怨。采香幽径鸳鸯睡，谁道湔裙人远。'),
	(7558, 366, '锦瑟清商引', '玉窗夜静月流光。拂鸳弦、先奏清商。天外塞鸿飞呼，群夜渡潇湘。风回处，戛玉铿金，翩翻作新势，声声字字，历历锵锵。忽低颦有恨，此意极凄凉。 炉香帘栊正清洒，转调促柱成行。机籁杂然鸣素手，击碎琳琅。翠云深梦里昭阳。此心长。回顾穷阴绝漠，片影悠扬。那昭君更苦，香泪湿红裳。'),
	(7559, 366, '好事近（浙江楼闻笛）', '独倚浙江楼，满耳怨笳哀笛。犹有梨园声在，念那人天北。 海棠憔悴怯春寒，风雨怎禁得。回首华清池畔，渺露芜烟荻。'),
	(7560, 124, '四字令·拟花间', '眉消睡黄。春凝泪妆。玉屏水暖微香。听蜂儿打窗。 筝尘半妆。绡痕半方。愁心欲诉垂杨。奈飞红正忙。'),
	(7561, 124, '风入松（立春日即席次寄闲韵）', '柳梢烟软已璁珑。娇眼试东风。情丝又逐青丝乱，剩寒轻、犹恋芳栊。笋玉新裁早燕，杏钿时引晴蜂。 当时兰柱系花骢。人在小楼东。莺娇戏索迎春句，爱露笺、新染香红。未信闲情便懒，探花拚醉琼钟。'),
	(7562, 124, '鹧鸪天（清明）', '燕子时时度翠帘。柳寒犹未褪香绵。落花门巷家家雨，新火楼台处处烟。 情默默，恨恹恹。东风吹动画秋千。拆桐开尽莺声老，无奈春何只醉眠。'),
	(7563, 71, '摸鱼儿（甲午送春）', '又非他、今年晴少，海棠也恁空过。清赢欲与花同梦，不似蝶深深卧。春怜我。我又自、怜伊不见侬赓和。已无可奈。但愁满清漳，君归何处，无泪与君堕。 春去也，尚欲留春可可。问公一醉能颇。钟情剩有词千首，待写大招招些。休阿那。阿那看、荒荒得似江南麽。老夫婆娑。问篱下闲花，残红有在，容我更簪朵。'),
	(7564, 71, '水调歌头（中秋口占）', '明月几万里，与子共中秋。古今良夜如此，寂寂几时留。何处胡笳三弄，尚有南楼余兴，风起木飕飕。白石四山立，玉露下平洲。 醉青州，歌赤壁，赋黄楼。人间安得十客，谭笑发中流。看取横江皓彩，犹似沈河白璧，光气彻天浮。举首快哉去，灯火见神州。'),
	(7565, 71, '双调望江南（赋所见）', '长欲语，欲语又蹉跎。已是厌听夷甫颂，不堪重省越人歌。孤负水云多。 羞拂拂，懊恼自摩挲。残烛不教人径去，断云时有泪相和。恨恨欲如何。'),
	(7566, 641, '点绛唇', '莺语愁春，海棠风里胭脂雨。酒杯慵举。闲扑亭前絮。 漠漠斜阳，截断愁来路。凭阑伫。满怀离苦。分付楼南鼓。'),
	(7567, 641, '望江南', '烟漠漠，湖外绿杨堤。满地落花春雨后，一帘飞絮夕阳西。梁燕落香泥。 流水恨，和泪入桃蹊。鹦鹉洲边鹦鹉恨，杜鹃枝上杜鹃啼。归思越凄凄。'),
	(7568, 640, '沁园春（守岁）', '太岁茫茫，犹有归时，我胡不归。为桂枝关约，十年阙下，梅花梦想，半夜天涯。婪尾三杯，胶牙一标，节物依然心事非。长安市，只喧喧箫鼓，催老男儿。 篝灯自理征衣。正历乱愁肠千万丝。想椒盘寂寞，空传旧颂，桃符冷落，谁撰新诗。世事干忙，人生寡遂，何限春风抛路歧。身安处，且开眉一笑，何以家为。'),
	(7569, 660, '金盏子（四时怀古秋词）', '眼底时光，奈老来、如何奈得秋何。黄叶最多情，天分付、凉意一声先做。是处著露莎蛩，也酸吟相和。新雁想，飞到故都徘徊，未忍轻过。 往事是堪唾。红枣信、烽折尽任他。湖山桂香自好。笙歌舫、沈沈醉也谁拖。可怜瘦月凄凉，把兴亡看破。如今□，但留下满城□，西风悲些。'),
	(7570, 166, '念奴矫·送陈正言', '南来数骑，问征尘、正是江头风恶。耿耿孤忠磨不尽，惟有老天知得。短棹浮淮，轻毡渡汉，回首觚棱泣。缄书欲上，惊传天外清跸。 路人指示荒台，昔汉家使者，曾留行迹。我节君袍雪样明，俯仰都无愧色。送子先归，慈颜未老，三径有余乐。逢人问我，为说肝肠如昨。'),
	(7571, 112, '古香慢·赋沧浪看桂', '怨娥坠柳，离佩摇葓，霜讯南圃。漫忆桥扉，倚竹袖寒日暮。还问月中游，梦飞过、金风翠羽。把残云、胜水万顷，暗熏冷麝凄苦。 渐浩渺、凌山高处。秋澹无光，残照谁主。露粟侵肌，夜约羽林轻误。翦碎惜秋心，更肠断、珠尘藓路。怕重阳，又催近、满城细雨。'),
	(7572, 112, '木兰花慢·寿秋壑', '记琼林宴起，软红路、几西风。想汉影千年，荆江万顷，槎信长通。金狨。锦鞯赐马，又霜横、汉节枣仍红。细柳春阴喜色，四郊秋事年丰。 从容。岁晚玉关，长不闭、静边鸿。访武昌旧垒，山川相缪，日费诗筒。兰宫。系书翠羽，带天香、飞下玉芙蓉。明月瑶笙奏彻，倚楼黄鹤声中。'),
	(7573, 112, '八声甘州·和梅津', '记行云梦影，步凌波、仙衣翦芙容。念杯前烛下，十香揾袖，玉暖屏风。分种寒花旧盎，藓土蚀吴蛩。人远云槎渺，烟海沈蓬。 重访樊姬邻里，怕等闲易别，那忍相逢。试潜行幽曲，心荡□匆匆。井梧凋、铜铺低亚，映小眉、瞥见立惊鸿。空惆怅，醉秋香畔，往事朦胧。'),
	(7574, 112, '高阳台·丰乐楼分韵得如字', '修竹凝妆，垂杨驻马，凭阑浅画成图。山色谁题？楼前有雁斜书。东风紧送斜阳下，弄旧寒、晚酒醒馀。自消凝，能几花前，顿老相如。 伤春不在高楼上，在灯前攲枕，雨外熏炉。怕舣游船，临流可奈清臞？飞红若到西湖底，搅翠澜、总是愁鱼。莫重来，吹尽绵，泪满平芜。'),
	(7575, 112, '烛影摇红·赋德清县圃古红梅', '莓锁虹梁，稽山祠下当时见。横斜无分照溪光，珠网空凝遍。姑射青春对面。驾飞虬、罗浮路远。千年春在，新月苔池，黄昏山馆。 花满河阳，为君羞褪晨妆茜。云根直下是银河，客老秋槎变。雨外红铅洗断。又晴霞、惊飞暮管。倚阑祗怕，弄水鳞生，乘东风便。'),
	(7576, 112, '瑞龙吟·送梅津', '黯分袖。肠断去水流萍，住船系柳。吴宫娇月娆花，醉题恨倚，蛮江豆蔻。 吐春绣。笔底丽情多少，眼波眉岫。新园锁却愁阴，露黄漫委，寒香半亩。 还背垂虹秋去，四桥烟雨，一宵歌酒。犹忆翠微携壶，乌帽风骤。西湖到日，重见梅钿皱。谁家听、琵琶未了，朝骢嘶漏。印剖黄金籀。待来共凭，齐云话旧。莫唱朱樱口。生怕遣、楼前行云知后。泪鸿怨角，空教人瘦。'),
	(7577, 112, '垂丝钓近·云麓先生以画舫载洛花宴客', '听风听雨，春残花落门掩。乍倚玉阑，旋剪夭艳。携醉靥。放溯溪游缆。波光撼。映烛花黯澹。 碎霞澄水，吴宫初试菱鉴。旧情顿减。孤负深杯滟。衣露天香染。通夜饮。问漏移几点。'),
	(7578, 112, '宴清都·送马林屋赴南宫分韵得动字', '柳色春阴重。东风力，快将云雁高送。书檠细雨，吟窗乱雪，井寒笔冻。家林秀桔霜老，笑分得、蟾边桂种。应茂苑、斗转苍龙，唯潮献奇吴凤。 玉眉暗隐华年，凌云气压，千载云梦。名笺淡墨，恩袍翠草，紫骝青鞚。飞香杏园新句，眩醉眼、春游乍纵。弄喜音、鹊绕庭花，红帘影动。'),
	(7579, 112, '玉烛新·花穿帘隙透', '花穿帘隙透。向梦里销春，酒中延昼。嫩篁细掐，想思字、堕粉轻黏D139袖。章台别后，展绣络、红蔫香旧。□□□，应数归舟，愁凝画阑眉柳。 移灯夜语西窗，逗晓帐迷香，问何时又。素纨乍试，还忆是、绣懒思酸时候。兰清蕙秀。总未比、蛾眉螓首。谁诉与，惟有金笼，春簧细奏。'),
	(7580, 112, '水龙吟·赋张斗墅家古松五粒', '有人独立空山，翠髯未觉霜颜老。新香秀粒，浓光绿浸，千年春小。布影参旗，障空云盖，沉沉秋晓。驷苍虬万里，笙吹凤女，骖飞乘、天风袅。 般巧。霜斤不到。汉游仙、相从最早。皱鳞细雨，层阴藏月，朱弦古调。问讯东桥，故人南岭，倚天长啸。待凌霄谢了，山深岁晚，素心才表。'),
	(7581, 112, '拜星月慢·林钟羽姜石帚以盆莲数十置中庭宴客其中', '绛雪生凉，碧霞笼夜，小立中庭芜地。昨梦西湖，老扁舟身世。叹游荡，暂赏、吟花酌露尊俎，冷玉红香罍洗。眼眩魂迷，古陶洲十里。 翠参差、澹月平芳砌。砖花滉、小浪鱼鳞起。雾盎浅障青罗，洗湘娥春腻。荡兰烟、麝馥浓侵醉。吹不散、绣屋重门闭。又怕便、绿减西风，泣秋檠烛外。'),
	(7582, 241, '满江红（壬子生日）', '晓傍苍崖，滴寒露、研朱点易。五十四卦为归姝，惟幽人吉。彼美人兮春上下，如吾徒者山南北。辨一生、坚壁卧烟霞，诗无敌。 人间世，胶中漆。功名事，刀头蜜。放乾坤醉眼，看朱成碧。曾共梅花相尔汝，尽教雪后无消息。莫怕寒、容易嫁东风，春狼藉。'),
	(7583, 241, '水调歌头（九日多景楼用吴侍郎韵）', '醉我一壶玉，了此十分秋。江涛还此，当日击楫渡中流。问讯重阳烟雨，俯仰人间今古，此意渺沧洲。天地几今夕，举白与君浮。 旧黄花，新白发，笑重游。满船明月犹在，何日大刀头。谁跨扬州鹤去，已怨故山猿老，借箸欲前筹。莫倚阑干北，天际是神州。'),
	(7584, 303, '八声甘州（癸丑生朝）', '对西风、先自念莼鲈，又还月生西。叹平生霜露，而今都在，两鬓丝丝。当年门垂蓬矢，壮岁竟奚为。磊落中心事，只有天知。 多谢君恩深厚，费丁宁温诏，犹置驱驰。看弓刀何事，终是愧毛锥。愿今年、四郊无警，向酒边、多作数篇诗。山林下，相将见一，舍我其谁。'),
	(7585, 117, '柳梢青（己未元夕）', '好把元宵。良辰美景，暮暮朝朝。万盏华灯，一轮明月，燕管秦箫。 何人帕坠鲛绡。有玉凤、金鸾绣雕。目下欢娱，眼前烦恼，只在今宵。'),
	(7586, 117, '沁园春（丙辰十月十日）', '夜雨三更，有人＿枕，晓檐报晴。算顽云痴雾，不难扫荡，青天白日，元自分明。权植油幢，聊张皂纛，坐听前驺鼓角鸣。君要诧，岂宣申南翰，成旦东征。 鸿冥。哽噎秋声。正万里榆关未罢兵。幸扬州上督，为吾石友，荆州元帅，是我梅兄。约束鲸鲵，奠安＿鼠，更使嵎夷海晏清。连宵看，怕天狼隐耀，太白沈枪。'),
	(7587, 117, '望江南', '家山好，不是撰虚名。世上盛衰常倚伏，天家日月也亏盈。退步是前程。 且恁地，卷索了收绳。六宇五胡生口面，三言两语费颜情。赢得鬓星星。'),
	(7588, 117, '沁园春（多景楼）', '第一江山，无边境界，压四百州。正天低云冻，山寒木落，萧条楚塞，寂寞吴舟。白鸟孤飞，暮鸦群注，烟霭微茫锁戍楼。凭阑久，问匈奴未灭，底事菟裘。 回头，祖敬何刘。曾解把功名谈笑收。算当时多少，英雄气概，到今惟有，废垅荒丘。梦里光阴，眼前风景，一片今愁共古愁。人间事，尽悠悠且且，莫莫休休。'),
	(7589, 116, '沁园春（梅）', '十月江南，一番春信，怕凭玉栏。正地连边塞，角声三弄，人思乡国，愁绪千般。草草村墟，疏疏篱落，犹记花间曾卓庵。茶瓯罢，问几回吟绕，冷淡相看。 堪怜。影落溪南。又月午无人更漏三。虽虚林幽壑，数枝偏瘦，已存鼎鼐，一点微酸。松竹交盟，雪霜心事，断是平生不肯寒。林逋在，倩诗人此去，为语湖山。'),
	(7590, 199, '念奴娇（重午次丁广文韵）', '楚湘旧俗，记包黍沈流，缅怀忠节。谁挽汨罗千丈雪，一洗些魂离别。赢得儿童，红丝缠臂，佳话年年说。龙舟争渡，搴旗捶鼓骄劣。 谁念词客风流，菖蒲桃柳，忆闺门铺设。嚼徵含商陶雅兴，争似年时娱悦。青杏园林，一樽煮酒，当为浇＿切。南薰应解，把君愁袂吹裂。'),
	(7591, 557, '孤鸾（梅）', '江南春早。问江上寒梅，占春多少。自照疏星冷，只许春风到。幽香不知甚处，但迢迢、满汀烟草。回首谁家竹外，有一枝斜好。记当年、曾共花前笑。念玉雪襟期，有谁知道。唤起罗浮梦，正参横月小。凄凉更吹塞管，漫相思、鬓华惊老。待觅西湖半曲，对霜天清晓。'),
	(7592, 49, '卜算子', '四大因缘做，苦海凭船渡。一棹添风到岸头，得上无生路。 人叹风贫苦，我步闲闲趣。脱体全空没一文，胜似石崇富。'),
	(7593, 49, '忆秦娥', '泥滑滑。一声声唤征鞍发。征鞍发。客亭杨柳，不禁攀折。 荀郎衣上香初歇。萧郎心下书难说。书难说。霎时吹散，一生愁绝。'),
	(7594, 49, '清平乐·赠陈参议师文侍儿', '宫腰束素。只怕能轻举。好筑避风台护取。莫遣惊鸿飞去。 一团香玉温柔。笑颦俱有风流。贪与萧郎眉语，不知舞错伊州。'),
	(7595, 49, '贺新郎（张＿·生日）', '辇路东风里。试回头、金闺昨梦，侵寻三纪。岁晚岿然灵光殿，仆与君侯而已。漫过眼、几番桃李。珠履金钗常满座，问谁人、得似张公子。驰骥＿，佩龟紫。 宿云收尽檐声止。玳筵开、高台风月，后堂罗绮。恰近洛人修禊节，莫惜飞觞临水。怕则怕、追锋徵起。此老一生江海客，愿风云、际会从今始。宁郁郁。久居此。'),
	(7596, 49, '长相思', '劝一杯。复一杯。短锸相随死便埋。英雄安在哉。 眉不开。怀不开。幸有江边旧钓台。拂衣归去来。'),
	(7597, 49, '长相思（饯别）', '风萧萧。雨萧萧。相送津亭折柳条。春愁不自聊。 烟迢迢。水迢迢。准拟江边驻画桡。舟人频报潮。'),
	(7598, 49, '沁园春', '后四年发箧见之，有感 帝赐玄圭，臣妾潘衡，奴隶侯璋。因封还除目，见瞋鬼质，窜涂贽卷，取怨奇章。肯比寒儒，自夸秘宝，十袭庭＿寸许香。下岩石，要朝朝磨试，不论闲忙。 何须狗监揄扬。这衡尺曾经圣手量。纵埋之地下，居然光怪，栖之梁上，亦恐偷将。蓬户无人，花村有犬，添几重茅覆野堂。交游少，约文房四友，泛浩摩苍。'),
	(7599, 49, '沁园春（答陈上舍应祥）', '华发萧萧，归碧鸡坊，出金马门。把一枝色笔，掷还郭璞，些儿残锦，回乞天孙。永免朝参，更无宣锁，送老三家水竹村。休休也，任巫阳来下，未易招魂。 茅檐安得庖阍。倩便了沽来酒满樽。叹角巾东路，吾寻初服，上书北阙，子漫危言。漏院霜靴，火城雪辔，得似先生败絮温。安危事，付布衣融泰，鼎足膺蕃。'),
	(7600, 695, '丑奴儿', '绿阴窗几明如拭，粉黛初匀。无限芳心。翻动牙签却＿人。 多娇爱学秋来曲，微颤朱唇。别后销魂。字底依稀记指痕。'),
	(7601, 695, '沁园春（送赵运使之江西）', '有美一人，昔在何居，今方见之。俨琼缨翠弁，气清芬只，珠幢绛节，光陆离兮。吾道非耶，世情复尔，天骥昂藏不受羁。还知否，定曲高寡和，才大难施。行吟湘水之湄。看云卷云舒无定姿。想粲然长笑，物皆有用，时哉易失，我亦奚为。袖手旁观，何如小试，欲脱囊中失利锥。君休叹，正梅花将发，尘满征衣。'),
	(7602, 239, '兰陵王', '晚烟直。池沼波痕皱碧。年芳为、花态柳情。挼粉揉情酿春色。繁华记上国。曾识。倾城幼客，风流是、联句送钩，笺绿绡红递书尺。 行云去无迹。念暖响歌台，香雾瑶席。当时谁信盟言食。知一岁离聚，几多间阻，人生如梦寄堠驿。况分散南北。 悲恻。万愁积。奈鸾凤欢疏，鱼雁音寂。天涯何处相思极。但目断芳草，恨随塞笛。那堪庭院，更听得，夜雨滴。'),
	(7603, 239, '六么令', '照人明艳，肌雪消繁燠。娇云慢垂柔领，绀发浓于沐。微晕红潮一线，拂拂桃＿熟。群芳难逐。天香国艳，试比春兰共秋菊。 当时相见恨晚，彼此萦心目。别后空忆仙姿，路隔吹箫玉。何处栏干十二，缥缈阳台曲。佳期重卜。都将离恨，拚与尊前细留嘱。'),
	(7604, 239, '浣沙溪', '菱藕花开来路香。满船丝竹载西凉。波摇发彩粉生光。 翡翠双飞寻密浦，鸳鸯浓睡倚回塘。闲情须与酒商量。'),
	(7605, 84, '鹊桥仙', '澄江晓碧，君山秋静，人与江山俱秀。最声吹下紫泥封，看宣献、风流依旧。 □袍对引，鱼轩徐驾，小队旌旗陪后。万家指点寿星明，更把菊、登高时候。'),
	(7606, 84, '木兰花慢·别西湖两诗僧', '嫩寒催客棹，载酒去、载诗归。正红叶漫山，清泉漱石，多少心期。三生溪桥话别，怅薜萝、犹惹翠云衣。不似今番醉梦，帝城几度斜晖。 鸿飞。烟水氵弥氵弥。回首处，只君知。念吴江鹭忆，孤山鹤怨，依旧东西。高峰梦醒云起，是瘦吟、窗底忆君时。何日还寻后约，为余先寄梅枝。'),
	(7607, 680, '解连环·柳', '露条烟叶。惹长亭旧恨，几番风月。爱细缕、先窣轻黄，渐拂水藏鸦，翠阴相接。纤软风流，眉黛浅、三眠初歇。奈年华又晚，萦绊游蜂，絮飞晴雪。 依依灞桥怨别。正千丝万绪，难禁愁绝。怅岁久、应长新条，念曾系花骢，屡停兰楫。弄影摇晴，恨闲损、春风时节。隔邮亭，故人望断，舞腰瘦怯。'),
	(7608, 91, '鹧鸪天', '睡袖无端几折香。有人丹脸可占霜。半窗月印梅犹瘦，一律瓶笙夜正长。 情艳艳，酒狂狂。小屏谁与画鸳鸯。解衣恰恨敲金钏，惊起春风傍枕囊。'),
	(7609, 91, '阮郎归（月下感事）', '旧时明月旧时身。旧时梅萼新。旧时月底似梅人。梅春人不春。 香入梦，粉成尘。情多多断魂。芙蓉孔雀夜温温。愁痕即泪痕。'),
	(7610, 670, '虞美人（姑苏画莲）', '西湖十里孤山路。犹记荷花处。翠茎红蕊最关情。不是薰风、吹得晚来晴。 而今老去丹青底。醉腻娇相倚。棹歌声缓采香归。如梦如酲、新月照涟漪。'),
	(7611, 670, '祝英台近（寒食词）', '馆娃宫，采香径，范蠡五湖侧。子夜吴歌，声缓不须拍。崇桃积李花闲，芳洲绿遍，更冉冉、柳丝无力。 试思忆。老去一片身心，孤负好春色。古往今来，时序恼行客。去年今日山中，如何知得。却又在、他乡寒食。'),
	(7612, 615, '卜算子', '春事到清明，过了三之二。秾李夭桃委路尘，太半成泥滓。 只有海棠花，恰似杨妃醉。折向铜壶把烛看，且莫教渠睡。'),
	(7613, 615, '菩萨蛮（县斋木犀今年殊未开，而盆菊特茂盛，以晦日约客。）', '未观严桂先观菊。世间底事真迟速。节物苦相催。重阳便到来。 白衣何处觅。沽酒邀佳客。一笑有余欢。官居终日闲。'),
	(7614, 370, '行香子（雪后闲眺）', '策杖溪边。倚杖峰前。望琼林、玉树森然。谁家残雪，何处孤烟。向一溪桥，一茅店，一渔船。 别般天地，新样山川。唤家僮、访鹤寻猿。山深寺远，云冷钟残。喜竹间灯，梅间屋，石间泉。'),
	(7615, 370, '沁园春', '峰下王道者 家在柳塘，榜挂方壶，图挂黄山。觉仙峰六六，满堂峭峻，仙溪六六，绕屋潺湲。行到水穷，坐看云起，只在吾庐寻丈间。非人世，但鹤飞深谷，猿啸高岩。 如今老疾蹒跚。向画里嬉游卧里看。甚花开花落，悄无人见，山南山北，谁似余闲。住个庵儿，了些活计，月白风清人倚阑。山中友，类先秦气貌，后晋衣冠。'),
	(7616, 149, '卜算子（吏部梅花八咏，次韵。）', '江左咏梅人，梦绕青青路。因向凌风台下看，心事还将与。 忆别庾郎时，又过林逋处。万古西湖寂寞春，惆怅谁能赋。'),
	(7617, 149, '秋宵吟（越调·自制曲）', '古帘空，坠月皎。坐久西窗人悄。蛩吟苦，渐漏水丁丁，箭壶催晓。引凉＿、动翠葆。露脚斜飞云表。因嗟念，似去国情怀，暮帆烟草。 带眼销磨，为近日、愁多顿老。卫娘何在，宋玉归来，两地暗萦绕。摇落江枫早。嫩约无凭，幽梦又杳。但盈盈、泪洒单衣，今夕何恨未了。'),
	(7618, 149, '角招', '吹香薄人。已而商卿归吴兴，予独来，则山横春烟，新柳被水，游人容与飞花中。怅然有怀，作此寄之。商卿善歌声，稍以儒雅缘饰。予每自度曲，吟洞箫，商卿辄歌而和之，极有山林缥缈之思。今予离忧，商卿一行作吏，殆无复此乐矣 为春瘦。何堪更绕西湖，尽是垂柳。自看烟外岫。记得与君，湖上携手。君归未久。早乱落、香红千亩。一叶凌波缥缈，过三十六离宫，遣游人回首。 犹有。画船障袖。青楼倚扇，相映人争秀。翠翘光欲溜。爱著宫黄，而今时候。伤春似旧。荡一点、春心如酒。写入吴丝自奏。问谁识，曲中心、花前友。'),
	(7619, 149, '探春慢', '惟姊弟之爱，沔之父老儿女子，亦莫不予爱也。丙午冬，千岩老人约予过苕霅，岁晚乘涛载雪而下。顾念依依，殆不能去。作此曲别郑次皋、辛克清、姚刚中诸君 衰草愁烟，乱鸦送日，风沙回旋平野。拂雪金鞭，欺寒茸帽，还记章台走马。谁念漂零久，漫赢得、幽怀难写。故人清沔相逢，小窗闲共情话。 长恨离多会少，重访问竹西，珠泪盈把。雁碛波平，渔汀人散，老去不堪游冶。无奈苕溪月，又照我、扁舟东下。甚日归来，梅花零乱春夜。'),
	(7620, 149, '浣溪沙', '翦翦寒花小更垂。阿琼愁里弄妆迟。东风烧烛夜深归。 落蕊半黏钗上燕，露黄斜映鬓边犀。老夫无味已多时。'),
	(7621, 149, '鹧鸪天（十六夜出）', '辇路珠帘两行垂。千枝银烛舞僛々。东风历历红楼下，谁识三生杜牧之。 欢正好，夜何其。明朝春过小桃枝。鼓声渐远游人散，惆怅归来有月知。'),
	(7622, 72, '六州歌头', '镇长淮，一都会，古扬州。升平日，珠帘十里春风、小红楼。谁知艰难去，边尘暗，胡马扰，笙歌散，衣冠渡，使人愁。屈指细思，血战成何事，万户封侯。但琼花无恙，开落几经秋。故垒荒丘。似含羞。 怅望金陵宅，丹阳郡，山不断绸缪。兴亡梦，荣枯泪，水东流。甚时休。野灶炊烟里，依然是，宿貔貅。叹灯火，今萧索，尚淹留。莫上醉翁亭，看＿＿雨、杨柳丝柔。笑书生无用，富贵拙身谋。骑鹤东游。'),
	(7623, 72, '水调歌头', '弓剑出榆塞，铜椠上蓬山。得之浑不费力，失亦匹如闲。未必古人皆是，未必今人俱错。世事沐猴冠。老子不分别，内外与中间。 酒须饮，诗可作，铗休弹。人生行乐，何自催得鬓毛斑。达则牙旗金甲，穷则蹇驴破帽，莫作两般看。世事只如此，自有识鸮鸾。'),
	(7624, 72, '沁园春（御阅还上郭殿帅）', '玉带猩袍，遥望翠华，马去似龙。拥貂蝉争出，千官鳞集，貔貅不断，万骑云从。细柳营开，团花袍窄，人指汾阳郭令公。山西将，算韬钤有种，五世元戎。 旌旗蔽满寒空。鱼阵整、从容虎帐中。想刀明似雪，纵横脱鞘，箭飞如雨，霹雳鸣弓。威撼边城，气吞胡虏，惨淡尘沙吹北风。中兴事，看君王神武，驾驭英雄。'),
	(7625, 215, '宴山亭', '幽梦初回，重阴未开，晓色吹成疏雨。竹槛气寒，莼畹声摇，新绿暗通南浦。未有人行，才半启、回廊朱户。无绪。空望极霓旌，锦书难据。 苔径追忆曾游，念谁伴、秋千彩绳芳柱。犀奁黛卷，凤枕云孤，应也几番凝伫。怎得伊来，花雾绕、小堂深处。留住。直到老、不教归去。'),
	(7626, 215, '满庭芳（促织儿）', '月洗高梧，露漙幽草，宝钗楼外秋深。土花沿翠，萤火坠墙阴。静听寒声断续，微韵转、凄咽悲沈。争求侣，殷勤劝织，促破晓机心。 儿时，曾记得，呼灯灌穴，敛步随音。任满身花影，犹自追寻。携向华堂戏斗，亭台小、笼巧妆金。今休说，从渠床下，凉夜伴孤吟。'),
	(7627, 215, '杨柳枝', '绿蜡芽疏雪一包。绽云梢。清香却暑置堂坳。晚风飘。 冰雹无声栖碧叶，笑仍娇。相随茉莉展轻绡。伴凉宵。'),
	(7628, 639, '采桑子', '桃花已作东风笑，小蕊嫣然。春色暄妍。缓步烟霞到洞天。 一杯满泻蒲桃绿，且共留连。醉倒花前，也占红香影里眠。'),
	(7629, 639, '蝶恋花（甲辰寿元晦）', '手捻黄花还自笑。笑比渊明，莫也归来早。随世功名浑草草。五湖却共繁华老。 冷淡家生冤得道。旖旎妖娆，春梦如今觉。管个岁华须到了。此花之后花应少。'),
	(7630, 569, '诉衷情', '茫茫云海浩无边。天与水相连。舳舻万里来往，有祷必安全。 专掌握，雨旸权。属丰年。琼卮玉醴，飨此精诚，福庆绵绵。'),
	(7631, 671, '太常引', '荒山连水水连天。忆曾上、桂江船。风雨过吴川。又却在、潇湘岸边。 不堪追念，浪萍踪迹，虚度夜如年。风外晓钟传。尚独对、残灯未眠。'),
	(7632, 446, '摊破浣溪沙', '落日秋风岭上村。全稀过雁少行人。正是悲伤愁绝处，更黄昏。 漠漠野烟生碧树、漫漫衰草际黄云。借使昔人行到此，也销魂。'),
	(7633, 446, '长相思', '你又痴。我又迷。到此痴迷两为谁。问天天怎知。 长相思，极相思。愿得因缘未尽时。今生重共伊。'),
	(7634, 446, '卜算子', '折得月中枝，坐惜青春老。及至归来能几时，又踏关山道。 满眼秋光好。相见应须早。若趁重阳不到家，只怕黄花笑。'),
	(7635, 634, '梦玉人引', '倚阑干久，人不见，暮云碧。芳草池塘，春梦暗惊诗客。昨夜溪梅，向空山、雪里轻匀颜色。好赠行人，折枝南枝北。 雨巾风帽，昔追游、谁念旧踪迹。料得疏篱，暗香时度风息。淡月微云，有何人消得。便归去、踏溪桥，慰我经年思忆。'),
	(7636, 634, '秦楼月', '春膏集。新雷忽起龙蛇蛰。龙蛇蛰。柳塘风快，水流声急。 伤心有泪凭谁浥。尊前容易青衫湿。青衫湿。渡头人去，野船鸥立。'),
	(7637, 634, '水调歌头（燕山九日作）', '有客念行役，劲气凛於秋。男儿未老，衔命如虏亦风流。决定平戎方略。恢复旧燕封壤，安用割鸿沟。莫献肃霜马，好衣白狐裘。 我何人，怀壮节，但凝愁。平生未逢知已、哙伍实堪羞。金马文章何在，玉鼎勋庸何有，一笑等云浮。拚断好风月，羯鼓打梁州。'),
	(7638, 456, '乌夜啼', '杨柳拖烟漠漠，梨花浸月溶溶。吹香院落春还尽，憔悴立东风。 只道芳时易见，谁知密约难通。芳园绕遍无人问，独自拾残红。'),
	(7639, 591, '出塞（春寒有感）', '莺未老。花谢东风扫。秋千人倦彩绳闲，又被清明过了。 日长破夜长眠，别听笙箫吹晓。锦笺封与怨春诗，寄与归云缥缈。'),
	(7640, 591, '江城子（戏同官）', '留仙初试砑罗裙。小腰身。可怜人。江国幽香，曾向雪中闻。过尽东园桃与李，还见此，一枝春。 庾郎襟度最清真。挹芳尘。便情亲。南馆花深，清夜驻行云。拚却日高呼不起，灯半灭，酒微醺。'),
	(7641, 591, '点绛唇（留博山寺，闻光风主人微恙而归，时春涨断桥）', '隐隐轻雷，雨声不受春回护。落梅如许。吹尽墙边去。 春水无情，碍断溪南路。凭谁诉。寄声传语。没个人知处。'),
	(7642, 591, '浣溪沙', '父老争言雨水匀。眉头不似去年颦。殷勤谢却甑中尘。 啼鸟有时能劝客，小桃无赖已撩人。梨花也作白头新。'),
	(7643, 591, '丑奴儿', '寻常中酒扶头后，歌舞支持。歌舞支持。谁把新词唤住伊。 临岐也有旁人笑，笑己争知。笑己争知。明月楼空燕子飞。'),
	(7644, 591, '西江月（和赵晋臣敷文赋秋水瀑泉）', '八万四千偈后，更谁妙语披襟。纫兰结佩有同心。唤取诗翁来饮。 镂玉裁冰著句，高山流水知音。胸中不受一尘侵。却怕灵均独醒。'),
	(7645, 591, '蝶恋花', '洗尽机心随法喜。看取尊前，秋思如春意。谁与先生宽发齿。醉时惟有歌而已。 岁月何须溪上记。千古黄花，自有渊明比。高卧石龙呼不起。微风不动天如醉。'),
	(7646, 591, '临江仙', '逗晓莺啼声昵昵，掩关高树冥冥。小渠春浪细无声。并床听夜雨，出藓辘轳青。 碧草旋荒金谷路，乌丝重记兰亭。强扶残醉绕云屏。一枝风露湿，花重入疏棂。'),
	(7647, 591, '卜算子', '欲行且起行，欲坐重来坐。坐坐行行有倦时，更枕闲书卧。 病是近来身，懒是从前我。静扫瓢泉竹树阴，且恁随缘过。'),
	(7648, 591, '永遇乐（赋梅雪）', '怪底寒梅，一枝雪里，直恁愁绝。问讯无言，依稀似妒，天上飞英白。江山一夜，琼瑶万顷，此段如何妒得。细看来，风流添得，自家越样标格。 晓来楼上，对花临镜，学作半妆宫额。著意争妍，那知却有，人妒花颜色。无情休问，许多般事，且自访梅踏雪。待行过溪桥，夜半更邀素月。'),
	(7649, 591, '念奴娇（重九席上）', '龙山何处，记当年高会，重阳佳节。谁与老兵供一笑，落帽参军华发。莫倚忘怀，西风也曾，点检尊前客。凄凉今古，眼中三两飞蝶。 须信采菊东篱，高情千载，只有陶彭泽。爱说琴中如得趣，弦上何劳声切。试把空杯，翁还肯道，何必杯中物。临风一笑，请翁同醉今夕。'),
	(7650, 591, '水调歌头（题永丰杨少游提点一枝堂）', '万事几时足，日月自西东。无穷宇宙，人是一粟太仓中。一葛一裘经岁，一钵一瓶终日，老子旧家风。更著一杯酒，梦觉大槐宫。 记当年，哧腐鼠，叹冥鸿。衣冠神武门外，惊倒几儿童。休说须弥芥子，看取鹍鹏斥鷃，小大若为同。君欲论齐物，须访一枝翁。'),
	(7651, 591, '朝中措', '夜深残月过山房。睡觉北窗凉。起绕中庭独步，一天星斗文章。 朝来客话，山林钟鼎，那处难忘。君向沙头细问，白鸥知我行藏。'),
	(7652, 591, '新荷叶（上巳日，吴子似谓古今无此词，索赋）', '曲水流觞，赏心乐事良辰。兰蕙光风，转头天气还新。明眸皓齿，看江头、有女如云。折花归去，绮罗陌上芳尘。 能几多春。试听啼鸟殷勤。览物兴怀，向来哀乐纷纷。且题醉墨，似兰亭、列序时人。后之览者，又将有感斯文。'),
	(7653, 591, '鹧鸪天（赋牡丹，主人以谤花索赋解嘲）', '翠盖牙签几百株。杨家姊妹夜游初。五花结队香如雾，一朵倾城醉未苏。 闲小立，困相扶。夜来风雨有情无。愁红惨绿今宵看，却似吴宫教阵图。'),
	(7654, 591, '鹧鸪天（寻菊花无有，戏作）', '掩鼻人间臭腐场。古来惟有酒偏香。自从归住云烟畔，直到而今歌舞忙。 呼老伴，共秋光。黄花何事避重阳。要知烂熳开时节，直待西风一夜霜。'),
	(7655, 591, '鹧鸪天（离豫章别司马汉章大监）', '聚散匆匆不偶然。二年遍历楚山川。但将痛饮酬风月，莫放离歌入管弦。 萦绿带，点青钱。东湖春水碧连天。明朝放我东归去，后夜相思月满船。'),
	(7656, 591, '临江仙（簪花屡堕戏作）', '鼓子花开春烂漫，荒园无限思量。今朝拄杖过西乡。急呼桃叶渡，为看牡丹忙。 不管昨宵风雨横，依然红紫成行。白头陪奉少年场。一枝簪不住，推道帽檐长。'),
	(7657, 591, '踏莎行（赋稼轩，集经句）', '进退存亡，行藏用舍。小人请学樊须稼。衡门之下可栖迟，日之夕矣□□下。 去卫灵公，遭桓司马。东西南北之人也。长沮桀溺耦而耕，丘何为是栖栖者。'),
	(7658, 591, '感皇恩（读庄子闻朱晦庵即世）', '案上数编书，非庄即老。曾说忘言始知道。万言千句，自不能忘堪笑。朝来梅雨霁，青青好。 一壑一丘，轻衫短帽。白发多时故人少。子云何在，应有玄经□草。江河流日夜，何时了。'),
	(7659, 591, '贺新郎·三山雨中游西湖', '三山雨中游西湖有怀赵丞相经始 翠浪吞平野。挽天河谁来照影，卧龙山下。烟雨偏宜晴更好，约略西施未嫁。待细把江山图画。千顷光中堆滟滪，似扁舟欲下瞿塘马。中有句，浩难写。 诗人例入西湖社。记风流重来手种，绿阴成也。陌上游人夸故国，十里水晶台榭。更复道横空清夜。粉黛中洲歌妙曲，问当年鱼鸟无存者。堂上燕，又长夏。'),
	(7660, 591, '满庭芳·和章泉赵昌父', '西崦斜阳，东江流水，物华不为人留。铮然一叶，天下已知秋。屈指人间得意，问谁是、骑鹤扬州。君知我，从来雅意，未老已沧州。 无穷身外事，百年能几，一醉都休。恨儿曹抵死，谓我心忧。况有溪山杖屦，阮籍辈、须我来游。还堪笑，机心早觉，海上有惊鸥。'),
	(7661, 591, '西江月（赋丹桂）', '宫粉厌涂娇额，浓妆要压秋花。西真人醉忆仙家。飞佩丹霞羽化。 十里芬芳未足，一亭风露先加。杏腮桃脸费铅华。终惯秋蟾影下。'),
	(7662, 591, '清平乐（寿赵民则提刑，时新除，且素不喜饮）', '诗书万卷。合上明光殿。案上文书看未遍。眉里阴功早见。 十分竹瘦松坚。看君自是长年。若解尊前痛饮，精神便是神仙。'),
	(7663, 591, '蝶恋花（用前韵送人行）', '意态憨生元自好。学画鸦儿，旧日遍他巧。蜂蝶不禁花引调。西园人去春风少。 春已无情秋又老。谁管闲愁，千里青青草。今夜倩簪黄菊了。断肠明月霜天晓。'),
	(7664, 591, '浣溪沙（别成上人并送性禅师）', '梅子熟时到几回。桃花开后不须猜。重来松竹意徘徊。 惯听禽声浑可谱，饱观鱼阵已能排。晚云挟雨唤归来。'),
	(7665, 591, '定风波（施枢密席上赋）', '春到蓬壶特地晴。神仙队里相公行。翠玉相挨呼小字。须记。笑簪花底是飞琼。 总是倾城来一处。谁妒。谁携歌舞到园亭。柳妒腰肢花妒艳。听看。流莺直是妒歌声。'),
	(7666, 591, '鹧鸪天（石门道中）', '山上飞泉万斛珠。悬崖千丈落鼪鼯。已通樵迳行还碍，似有人声听却无。 闲略彴，远浮屠。溪南修竹有茅庐。莫嫌杖屦频来往，此地偏宜著老夫。'),
	(7667, 591, '满江红（再用前韵）', '照影溪梅，怅绝代、幽人独立。更小驻、雍容千骑，羽觞飞急。琴里新声风响佩，笔端醉墨鸦栖壁。是使君、文度旧知名，方相识。 清可漱，泉长滴。高欲卧，云还湿。快晚风吹赠，满怀空碧。宝马嘶归红旆动，团龙试碾铜瓶泣。怕他年、重到路应迷，桃源客。'),
	(7668, 591, '满江红（题冷泉亭）', '直节堂堂，看夹道、冠缨拱立。渐翠谷、群仙东下，佩环声急。闻道天峰飞堕地，傍湖千丈开青壁。是当年、玉斧削方壶，无人识。 山木润。琅玕温。秋露下，琼珠滴。向危亭横跨，玉渊澄碧。醉舞且摇鸾凤影，浩歌莫遣鱼龙泣。恨此中、风月本吾家，今为客。'),
	(7669, 591, '南歌子', '万万千千恨，前前后后山。傍人道我轿儿宽。不道被他遮得、望伊难。 今夜江头树，船儿系那边。知他热后甚时眠。万万不成眠后、有谁扇。'),
	(7670, 591, '临江仙（再用前韵，送祐之弟归浮梁）', '钟鼎山林都是梦，人间宠辱休惊。只消闲处遇平生。酒杯秋吸露，诗句夜裁冰。 记取小窗风雨夜，对床灯火多情。问谁千里伴君行。晚山眉样翠，秋水镜般明。'),
	(7671, 591, '水调歌头（和王正之右司吴江观雪见寄）', '造物故豪纵，千里玉鸾飞。等闲更把，万斛琼粉盖颇黎。好卷垂虹千丈，只放冰壶一色，云海路应迷。老子旧游处，回首梦耶非。 谪仙人，鸥鸟伴，两忘机。掀髯把酒一笑，诗在片帆西。寄语烟波旧侣，闻道莼鲈正美，休制芰荷衣。上界足官府，汗漫与君期。'),
	(7672, 591, '满江红（游南岩和范廓之韵）', '笑拍洪崖，问千丈、翠岩谁削。依旧是、西风白马，北村南郭。似整复斜僧屋乱，欲吞还吐林烟薄。觉人间、万事到秋来，都摇落。 呼斗酒，同君酌。□小隐，寻幽约。且丁宁休负，北山猿鹤。有鹿从渠求鹿梦，非鱼定未知鱼乐。正仰看、飞鸟却应人，回头错。'),
	(7673, 426, '水调歌头（夜泛湘江）', '江月冷如水，江水碧於空。晚来一霎过雨，为我洗秋容。悄悄四山人静，凛凛三更露下，天阔叫孤鸿。唤醒蓬窗梦，身在水晶宫。 揖湘妃，招月娣，御清风。素琴韵远。不觉醉眼杏花红。禹穴骑鲸仙去，东海钓鳌人远，此意与谁同。倚柁一长啸，出壑舞鱼龙。'),
	(7674, 14, '雨中花（重阳）', '玉局祠前，铜壶阁畔，锦城药市争奇。正紫萸缀席，黄菊浮卮。巷陌联镳并辔，楼台吹竹弹丝。登高望远，一年好景，九日佳期。 自怜行客，犹对佳宾，留连岂是贪痴。谁会得、心驰北阙，兴寄东篱。惜别未催鹢首，追欢且醉蛾眉。明年此会，他乡今日，总是相思。'),
	(7675, 14, '定风波（次韵）', '休卧元龙百尺楼。眼高照破古今愁。若不擎天为八柱，且学鸱夷，归泛五湖舟。 万里西南天一角，骑气乘风，也作等闲游。莫道玉关人老矣，壮志凌云，依旧不惊秋。'),
	(7676, 180, '如梦令（咏梅）', '应是南枝向暖。那更青春未晚。竹外见红腮，芳意与香撩乱。肠断。肠断。无奈东风独占。'),
	(7677, 180, '如梦令（记梦）', '雨歇凉生枕罩。不梦大槐宫殿。惟对谪仙人，一笑高情眷眷。离恨。离恨。无奈晓窗杂啭。'),
	(7678, 180, '沁园春（和苏宣教韵）', '直下承当，本来能解，莫遣干休。算如今蹉过，峥嵘岁月，分阴可惜，一日三秋。闹里偷声，日中逃影，用尽机关无少留。争知道，是沤生即水，水外无沤。 世人等是悠悠。谁著个工夫里求。但掩耳窃钟，将泥洗块，觅花空里，舐蜜刀头。何以忙中，尻舆浸假，邀取三彭同载游。真如界，向毗卢顶上，荐取无忧。'),
	(7679, 587, '南乡子', '月转水晶盘。楼上初闻一鼓残。又是去年天气好，栏干。风动梅梢玉斗寒。 无奈壮情阑。对酒如何欲强欢。谁道破愁须仗酒，君看。酒到愁多破亦难。'),
	(7680, 587, '如梦令', '居士年来懒散。凡事只从宽简。身外更无求，只要夏凉冬暖。美满。美满。得过何须积趱。'),
	(7681, 587, '行香子（马上有感）', '骄马花骢。柳陌经从。小春天、十里和风。个人家住，曲巷墙东。好轩窗，好体面，好仪容。 烛灺歌慵。斜月朦胧。夜新寒、斗帐香浓。梦回画角，云雨匆匆。恨相逢，恨分散，恨情钟。'),
	(7682, 587, '天仙子（寓意）', '眼色媚人娇欲度。行尽巫阳云又雨。花时还复见芳姿，情几许。愁何许。莫向耳边传好语。 往事悠悠曾记否。忍听黄鹂啼锦树。啼声惊碎百花心，分付与。谁为主。落蕊飞红知甚处。'),
	(7683, 587, '念奴娇（夜寒有感）', '据炉肃坐，听瓶笙、别有天然宫徵。纸帐屏山浑不俗，写出江南烟水。檠短灯青，灰闲香软，所欠惟梅矣。风飞无定，数声时颤窗纸。 试问夜已何其，呼童起看，月上东墙未。天外忽闻征雁过，还把音书来寄。短简埋烟，轻蓑鸣雨，已办征船计。放教归去，故乡江上鱼美。'),
	(7684, 587, '探春令（早春）', '笙歌间错华筵启。喜新春新岁。菜传纤手青丝细。和气入、东风里。 幡儿胜儿都姑媂。戴得更忔戏。愿新春已后，吉吉利利。百事都如意。'),
	(7685, 106, '满江红', '往事千端，都笑道、衰翁宦拙。今会得、人情物态，尽皆休说。广厦尽堪舒笑傲，层楼又见凌空阔。试闲思、画戟比衡门，谁优劣。 尘里事，无休歇。楼上趣，真奇绝。有一川虚旷，万山环列。识破古今如旦暮，肯将物我刚分别。愿时时、与客坐楼心，谈风月。'),
	(7686, 188, '鹧鸪天（咏桃菊花）', '桃换肌肤菊换妆。只疑春色到重阳。偷将天上千年艳，染却人间九日黄。 新艳冶，旧风光。东篱分付武陵香。尊前醉眼空相顾，错认陶潜是阮郎。'),
	(7687, 188, '西江月', '冉冉寒生碧树，盈盈露湿黄花。故人玉节有光华。高会仍逢戏马。 万事只今如梦，此身到处为家。与君相遇更天涯。拚了茱萸醉把。'),
	(7688, 188, '鹧鸪天（赠饯横州子山）', '舞凤飞龙五百年。尽将锦绣里山川。王家券册诸孙嗣，主第笙歌故国传。 居玉铉，拥金蝉。只今门户庆蝉联。君侯合侍明光殿，且作横槎海上仙。'),
	(7689, 188, '水龙吟', '平生只说浯溪，斜阳唤我归船系。月华未吐，波光不动，新凉如水。长啸一声，山呜谷应，栖禽惊起。问元颜去后，水流花谢，当年事、凭谁记。 须信两翁不死。驾飞车、时游兹地。漫郎宅里，中兴碑下，应留屐齿。酌我清尊，洗公孤愤，来同一醉。待相将把袂，清都归路，骑鹤去、三千岁。'),
	(7690, 379, '洞仙歌', '夜来惊怪，冷逼流苏帐。梦破初闻打窗响。向晓开帘，凌乱千里寒光，清兴发，鹤毛谁同纵赏。 江南春意动，梅竹潜通，醉帽冲风自来往。慨念故人疏，便理扁舟，须信道、吾曹清旷。待石鼎煎茶洗余醺，更依旧归来，浅斟低唱。'),
	(7691, 378, '减字木兰花', '适然萃止。不待灯花先报喜。不速真风。且免毛生不为通。 诗歌棋酒。真正清欢真正寿。事事真何。更得真真真更多。'),
	(7692, 498, '如梦令', '罨画屏中客住。水色山光无数。斜日满江声，何处撑来小渡。休去。休去。惊散一洲鸥鹭。'),
	(7693, 498, '秦楼月', '珠帘狭。卷帘春院花围合。花围合。昼长人静，双双胡蝶。 花前苦＿金蕉叶。瞢腾午睡扶头怯。扶头怯。闲愁无限，远山斜叠。'),
	(7694, 633, '水龙吟（春日游摩诃池）', '摩诃池上追游路，红绿参差春晚。韶光妍媚，海棠如醉，桃花欲暖。挑菜初闲，禁烟将近，一城丝管。看金鞍争道，香车飞盖，争先占、新亭馆。 惆怅年华暗换。点销魂、雨收云散。镜奁掩月，钗梁拆凤，秦筝斜雁。身在天涯，乱山孤垒，危楼飞观。叹春来只有，杨花和恨，向东风满。'),
	(7695, 633, '一落索', '满路游丝飞絮。韶光将暮。此时谁与说新愁，有百啭、流莺语。 俯仰人间今古。神仙何处。花前须判醉扶归，酒不到、刘伶墓。'),
	(7696, 633, '谢池春', '贺监湖边，初系放翁归棹。小园林、时时醉倒。春眠惊起，听啼莺催晓。叹功名、误人堪笑。 朱桥翠径，不许京尘飞到。挂朝衣、东归欠早。连宵风雨，卷残红如扫。恨樽前、送春人老。'),
	(7697, 633, '菩萨蛮', '江天淡碧云如扫。苹花零落莼丝老。细细晚波平。月从波面生。 渔家真个好。悔不归来早。经岁洛阳城。鬓丝添几茎。'),
	(7698, 633, '忆王孙', '春风楼上柳腰肢。初试花前金缕衣。袅袅娉娉不自持。晚妆迟。画得蛾眉胜旧时。'),
	(7699, 633, '沁园春', '一别秦楼，转眼新春，又近放灯。忆盈盈倩笑，纤纤柔握，玉香花语，雪暖酥凝。念远愁肠，伤春病思，自怪平生殊未曾。君知否，渐香消蜀锦，泪渍吴绫。 难求系日长绳。况倦客飘零少旧朋。但江郊雁起，渔村笛怨，寒＿委烬，孤砚生冰。水绕出围，烟昏云惨，纵有高台常怯登。消魂处，是鱼笺不到，兰梦无凭。'),
	(7700, 633, '沁园春（三荣横溪阁小宴）', '粉破梅梢，绿动萱丛，春意已深。渐珠帘低卷，筇枝微步，冰开跃鲤，林暖鸣禽。荔子扶疏，竹枝哀怨，浊酒一尊和泪斟。凭栏久，叹山川冉冉，岁月骎骎。 当时岂料如今。漫一事无成霜鬓侵。看故人强半，沙堤黄合，鱼悬带玉，貂映蝉金。许国虽坚，朝天无路，万里凄凉谁寄音。东风里，有灞桥烟柳，知我归心。'),
	(7701, 633, '水龙吟（荣南作）', '樽前花底寻春处，堪叹心情全减。一身萍寄，酒徒云散，佳人天远。那更今年，瘴烟蛮雨，夜郎江畔。漫倚楼横笛，临窗看镜，时挥涕、惊流转。 花落月明庭院。悄无言、魂消肠断。凭肩携手，当时曾效，画梁栖燕。见说新来，网萦尘暗，舞衫歌扇。料也羞憔悴，慵行芳径，怕啼莺见。'),
	(7702, 633, '鹧鸪天（七之三）', '梳发金盘剩一窝。画眉鸾镜晕双蛾。人间何处无春到，只有伊家独占多。 微步处，奈娇何。春衫初换麹尘罗。东邻斗草归来晚，忘却新传子夜歌。'),
	(7703, 633, '好事近（十二之九）', '小倦带余酲，淡淡数棂斜日。驱退睡魔十万，有双龙苍璧。 少年莫笑老人衰，风味似平昔。扶杖冻云深处，探溪梅消息。'),
	(7704, 633, '满江红（夔州催王伯礼侍御寻梅之集）', '疏蕊幽香，禁不过、晚寒愁绝。那更是、巴东江上，楚山千叠。＿帽闲寻西瀼路，亸鞭笑向南枝说。恐使君、归去上銮坡，孤风月。 清镜里，悲华发。山驿外，溪桥侧。凄然回首处，凤凰城阙。憔悴如今谁领略，飘零已是无颜色。问行厨、何日唤宾僚，犹堪折。'),
	(7705, 633, '浣沙溪（南郑席上）', '浴罢华清第二汤。红棉扑粉玉肌凉。娉婷初试藕丝裳。 凤尺裁成猩血色，螭奁熏透麝脐香。水亭幽处捧霞觞。'),
	(7706, 633, '赤壁词（招韩无咎游金山）', '禁门钟晓，忆君来朝路，初翔鸾鹄。西府中台推独步，行对金莲宫烛。蹙绣华鞯，仙葩宝带，看即飞腾速。人生难料，一尊此地相属。 回首紫陌青门，西湖闲院，锁千梢修竹。素壁栖鸦应好在，残梦不堪重续，岁月惊心，功名看镜，短鬓无多绿。一欢休惜，与君同醉浮玉。'),
	(7707, 148, '水调歌头（七夕）', '三五半圆夜，二七素秋天。银河耿耿，中渡绛节会星軿。宝鹊喜传佳信，丹凤欢迎仙仗，瑞彩映珠躔。此意天长久，不比在人间。 多情客，捧香饵，洁宾筵。殷勤拜舞，乞寿乞富乞团圆。宜与人人愿足，更看家家欢洽，喜气满江山。今夕莫辞醉，后会是来年。'),
	(7708, 253, '好事近（岩桂）', '蟾苑桂飘香，雅称＿娥珍惜。吹下一丛仙种，伴秋光岑寂。 金风玉露嫩凉天，造化有消息。醉赏绿云金粟，媚枝头月色。'),
	(7709, 253, '凤栖梧（兰溪）', '桂棹悠悠分浪稳。烟幂层峦，绿水连天远。赢得锦囊诗句满。兴来豪饮挥金＿。 飞絮撩人花照眼。天阔风微，燕外晴丝卷。翠竹谁家门可款。舣舟闲上斜阳岸。'),
	(7710, 253, '凤栖梧（牡丹）', '魏紫姚黄凝晓露。国艳天然，造物偏钟赋。独占风光三月暮。声名都压花无数。 蜂蝶寻香随杖屦。睍完莺声，似劝游人住。把酒留春春莫去。玉堂元是常春处。'),
	(7711, 526, '归字谣', '归。随分家山有蕨薇。陶元亮，千载是吾师。'),
	(7712, 526, '惜分飞（九日）', '平日悲秋今已老。细看秋光自好。风紧寒生早。漫将短发还吹帽。 寂寞东篱人不到。只有渊明醉倒。一笑留残照。世间万事蝇头小。'),
	(7713, 526, '安公子', '弱柳丝千缕。嫩黄匀遍鸦啼处。寒入罗衣春尚浅，过一番风雨。问燕子来时，绿水桥边路。曾画楼、见个人人否。料静掩云窗，尘满哀弦危柱。 庾信愁如许。为谁都著眉端聚。独立东风弹泪眼，寄烟波东去。念永昼春闲，人倦如何度。闲傍枕、百啭黄鹂语。唤觉来厌厌，残照依然花坞。'),
	(7714, 526, '水调歌头', '鸟影度疏木，天势入平湖。沧波万顷，轻风落日片帆孤。渡口千章云木，苒苒炊烟一缕，人在翠微居。客里更愁绝，回首忆吾庐。 功名事，今老矣，待何如。拂衣归去，谁道张翰为莼鲈。且就竹深荷静，坐看山高月小，剧饮与谁俱。长啸动林木，意气欲凌虚。'),
	(7715, 285, '前调（谢人送牡丹）', '甲帐春风肯见分。夜陪清梦当炉熏。寻香若傍阑干晓，定见堆红越鄂君。 雕玉佩，郁金裙。凭谁书叶寄朝云。兰芽九畹虽清绝，也要芳心伴小醺。'),
	(7716, 573, '忆少年', '逢春如酒，逢花如露，逢人如玉。东风送寒去，蔚温温香＿。 海上三山元似粟。试招来、共藏金屋。与君醉千岁，看人间新绿。'),
	(7717, 271, '念奴娇（二首催雪）', '冬晴无雪。是天心未肯，化工非拙。不放玉花飞堕地，留在广寒宫阙。云欲同时，霰将集处，红日三竿揭。六花翦就，不知何处施设。 应念陇首寒梅，花开无伴，对景真愁绝。待出和羹金鼎手，为把玉盐飘撒。沟壑皆平，乾坤如画，更吐冰轮洁。梁园燕客，夜明不怕灯灭。'),
	(7718, 359, '念奴娇（次韵寄陆务观、韩无咎）', '少年奇志，笑功名画虎，文章刻鹄。永夜漫漫悲昼短，难挽苍龙衔烛。飞藿飘零，浮云迁变，过眼邮传速。昔人真意，眇然千载谁属。 犹喜二子当年，诸公籍甚，赏云和孤竹。翰墨流传知几许，遗响宫商相续。梦里京华，不须惊叹，春草年年绿。赤霄归去，更看奔电喷玉。'),
	(7719, 509, '雨中花（和）', '寄径濉阳，陌上忽看，夭桃秾李争春。又见楚宫，行雨洗芳尘。红艳霞光夕照，素华琼树朝新。为奇姿芳润，拟倩游丝，留住东君。 拾遣杜老，犹爱南塘，寄情萝薜山林。争似此、花如姝丽，獭髓轻匀。不数江陵玉杖，休夸花岛红云。少须澄霁一，一番清影，更待冰轮。'),
	(7720, 264, '青玉案', '乘鸾影里冰轮度。秋空净、南楼暮。袅袅天风吹玉兔。今宵只在，旧时圆处。往事难重数。 天涯几见新霜露。怎得朱颜旧如故。对酒临风慵作赋。蓝桥烟浪，故人千里，梦也无由做。'),
	(7721, 264, '浪淘沙（观潮作）', '一线海门来。雪喷云开。昆山移玉下瑶台。卷地西风吹不断，直到蓬莱。 羯鼓噪春雷。鼍舞蛟回。歌楼鼓吹夕阳催。今古清愁流不尽，都一樽罍。'),
	(7722, 313, '如梦令', '桥上水光浮雪。桥下柳阴遮月。梦里去寻香，露冷五更时节。胡蝶。胡蝶。飞过闲红千叶。'),
	(7723, 482, '醉落魄（和答陈景卫望湖楼见忆）', '千岩竞秀。西湖好是春时候。谁知梅雪飘零久。藏白收香，空袖和羹手。 天涯万里情难逗。眉峰岂为伤春皱。片愁未信花能绣。若说相思，只恐天应瘦。'),
	(7724, 482, '菩萨蛮（辛未七夕戏答张庆符）', '银河牛女年年渡。相逢未款还忧去。珠斗欲阑干。盈盈一水间。 玉人偷拜月。苦恨匆匆别。此意愿天怜。今宵长似年。'),
	(7725, 254, '沁园春（早春）', '春点烟红，露晞新绿，土膏渐香。散懒慵情性，寻幽选静，一筇烟雨，几处松篁。恨我求闲，已成迟暮，石浅泉甘难屡尝。犹堪去，向清风皓月，南涧东冈。 如今雁断三湘。念酒伴、不来梅自芳。幸隐居药馆，孙登啸咏，从容云水，无负年光。且共山间，琴书朋旧，时饮无何游醉乡。归常是，趁前村桑柘，犹挂残阳。'),
	(7726, 332, '人月圆', '月华灯影光相射。还是元宵也。绮罗如画，笙歌递响，无限风雅。 闹蛾斜插，轻衫乍试，闲趁尖耍。百年三万六千夜，愿长如今夜。'),
	(7727, 400, '满庭芳（立春日呈刘春卿）', '天驷呈祥，土牛颁政，欢呼万井春来。雕轮丹毂，杂沓展轻雷。风动珠帘不卷，香散处、半露梅腮。东郊好，波澜浸绿，萌蘖上条枚。 徘徊。应共怪，十年豹隐，一旦鸾台。问康时术业，混俗情怀。过雪湖山清丽，笙歌沸、舞袖萦回。兵厨富，酒肠似海，莫惜醉金杯。'),
	(7728, 105, '好事近·飞雪过江来', '飞雪过江来，船在赤栏桥侧。惹报布帆无恙，著两行亲札。 从今日日在南楼，鬓自此时白。一咏一觞谁共，负平生书册。'),
	(7729, 185, '鹊桥仙', '靓妆艳态，娇波流盼，双靥横涡半笑。尊前烛畔粉生光，更低唱、新翻转调。 花房结子，冰枝清瘦。醉倚香浓寒峭。雏莺新啭上林声，惊梦断、池塘春草。'),
	(7730, 185, '点绛唇（丙寅秋社前一日溪光亭大雨作）', '山暗秋云，暝鸦接翅啼榕树。故人何处。一夜溪亭雨。 梦入新凉，只道消残暑。还知否。燕将雏去。又是流年度。'),
	(7731, 185, '青玉案（筠翁生朝）', '水芝香远摇红影。泛瑞霭、横山顶。缥缈笙歌云不定。玉钩斜挂，素蟾初满，醉惬浮瓜冷。 庭兰戏彩传金鼎。小袖青衫更辉映。谁道筠溪归计近。秋风催去，凤池难老，长把中书印。'),
	(7732, 185, '醉花阴', '紫枢泽笏趋龙尾。平入钧衡位。春殿听宣麻，争喜登庸，何似今番喜。 昆台宜有神仙裔。奕世貂蝉贵。玉砌长兰芽，好拥笙歌，长向花前醉。'),
	(7733, 185, '卜算子', '凉气入熏笼，暗影欹花砌。紫玉谁人三弄寒，细吹断、江梅意。 花底湿春衣，隔坐风轻递。却笑笙箫缑岭人，明月偷垂泪。'),
	(7734, 185, '水调歌头（追和）', '举手钓鳌客，削迹种瓜侯。重来吴会三伏，行见五湖秋。耳畔风波摇荡，身外功名飘忽，何路射旄头。孤负男儿志，怅望故园愁。 梦中原，挥老泪，遍南州。元龙湖海豪气，百尺卧高楼。短发霜黏两鬓，清夜盆倾一雨，喜听瓦鸣沟。犹有壮心在，付与百川流。'),
	(7735, 185, '兰陵王', '卷珠箔。朝雨轻阴乍阁。阑干外，烟柳弄晴，芳草侵阶映红药。东风妒花恶。吹落。梢头嫩萼。屏山掩，沈水倦熏，中酒心情怕杯勺。 寻思旧京洛。正年少疏狂，歌笑迷著。障泥油壁催梳掠。曾驰道同载，上林携手，灯夜初过早共约。又争信漂泊。 寂寞。念行乐。甚粉淡衣襟，音断弦索。琼枝璧月春如昨。怅别后华表，那回双鹤。相思除是，向醉里、暂忘却。'),
	(7736, 635, '南柯子（塔院僧阁）', '矫矫千年鹤，茫茫万里风。阑干三面看秋空。背插浮屠千尺、冷烟中。 林坞村村暗，溪流处处通。此间何似玉霄峰。遥望蓬莱依约、晚云东。'),
	(7737, 635, '清平乐（木犀）', '黄衫相倚。翠葆层层底。八月江南风日美。弄影山腰水尾。 楚人未识孤妍。离骚遗恨千年。无住庵中新事，一枝唤起幽禅。'),
	(7738, 635, '玉楼春（青墩僧舍作）', '山人本合居岩岭。聊问支郎分半境。残年藜杖与纶巾，八尺庭中时弄影。 呼儿汲水添茶鼎。甘胜吴山山下井。一瓯清露一炉云，偏觉平生今日永。'),
	(7739, 635, '渔家傲（福建道中）', '今日山头云欲举。青蛟素凤移时舞。行到石桥闻细雨。听还住。风吹却过溪西去。 我欲寻诗宽久旅。桃花落尽春无所。渺渺篮舆穿翠楚。悠然处。高林忽送黄鹂语。'),
	(7740, 296, '醉花阴（硕人生日）', '帘卷西风轻雨外。揖数峰横翠。楼上地行仙，压玉为醪，旋摘黄金蕊。 一觞一阕千秋岁。不愿封侯贵。长伴紫髯翁，踏月吹箫，笑咏云山里。'),
	(7741, 296, '声声慢（木犀）', '龙涎染就，沈水薰成，分明乱屑琼瑰。一朵才开，人家十里须知。花儿大则不大，有许多、潇洒清奇。较量尽，诮胜如末利，赛过酴醿。 更被秋光断送，微放些月照，著阵风吹。恼杀多情，猛拚沈醉酬伊。朝朝暮暮守定，尽忙时、也不分离。睡梦里，胆瓶儿、枕畔数枝。'),
	(7742, 518, '忆秦娥', '花阴月。兰堂夜宴神仙客。神仙客。江梅标韵，海棠颜色。 良辰佳会诚难得。花前一醉君休惜。君休惜。楚台云雨，今夕何夕。'),
	(7743, 518, '虞美人', '鸾屏绣被香云拥。平帖幽闺梦。觉来重试古龙涎。深炷玉炉、烧气不烧烟。 匆匆人去三更也。月到回廊下。出门无语送郎时。泪共一天风露、湿罗衣。'),
	(7744, 99, '相见欢', '腰肢一缕纤长。是垂杨。泥泥风中衣袖、冷沈香。 花如颊。眉如叶。语如簧。微笑微颦相恼、过回廊。'),
	(7745, 99, '鹊桥仙', '飞云多态，凉颸微度，都到酒边歌处。冰肌玉骨照人寒，更做弄、一帘风雨。 同盘风味，合欢情思，不管星娥猜妒。桃花溪水接银河，与占断、鹊桥归路。'),
	(7746, 99, '减字木兰花', '去年端午。共结彩丝长命缕。今日重阳。同泛黄花九酝觞。 经时离缺。不为莱菔髭似雪。一笑逢迎。休觅空青眼自明。 §江北旧词'),
	(7747, 99, '浣溪沙', '瞳日，争插新桃换旧符。”东坡诗云：“老去怕看新历日，退归拟学旧桃符。”古今绝唱也。吕居仁诗有“画角声中一岁除。平明更饮屠苏酒”之句，政用以为故事耳。芗林退居之士年，戏集两公诗，辄以鄙意足成浣溪沙，因书以遗灵照 爆竹声中一岁除。东风送暖入屠苏。瞳瞳晓色上林庐。 老去怕看新历日，退归拟学旧桃符。青春不染白髭须。'),
	(7748, 99, '浣溪沙', '为姑苏郡守，浩然有归志，因广其声为浣溪沙，示姑苏诸友 乐在烟波钓是闲。草堂松桂已胜攀。梢梢新月几回弯。 一碧太湖三万顷，屹然相对洞庭山。狂风浪起且须还。'),
	(7749, 206, '浣溪沙（四之三）', '象服华年两鬓青。喜逢生日是嘉平。何妨开宴雪初晴。 酒劝十分金凿落，舞催三叠玉娉婷。满堂欢笑祝椿龄。'),
	(7750, 559, '小重山', '罗绮生香娇上春。金莲开陆海，艳都城。宝舆回望翠峰青。东风鼓，吹下半天星。 万井贺升平。行歌花满路，月随人。龙楼一点玉灯明。萧韶远，高宴在蓬瀛。'),
	(7751, 128, '渔父词', '六首（叔共名竦） 好个神仙张志和。平生只是一渔蓑。和月醉，棹船歌。乐在江湖可奈何。'),
	(7752, 128, '踏莎行', '燕子归来，梅花又落。缃桃雨后燕支薄。眼前先自许多愁，斜阳更在春池阁。 梦里新欢，年时旧约。日长院静空帘幕。几回猛待不思量，抬头又是思量著。'),
	(7753, 128, '鹧鸪天', '终日看山不厌山。寻思百计不如闲。何时得到重阳日，醉把茱萸仔细看。 欹醉帽，倚雕阑。偶然携酒却成欢。篱边黄菊关心事，触误愁人到酒边。'),
	(7754, 267, '相见欢', '泷州几番清秋。许多愁。叹我等闲白了、少年头。 人间事。如何是。去来休。自是不归归去、有谁留。'),
	(7755, 267, '浣溪沙', '风落芙蓉画扇闲。凉随春色到人间。乍垂罗幕乍飞鸾。 好把深杯添绿酒，休拈明镜照苍颜。浮生难得是清欢。'),
	(7756, 267, '浣溪沙', '雨湿清明香火残。碧溪桥外燕泥寒。日长独自倚阑干。 脱箨修篁初散绿，褪花新杏未成酸。江南春好与谁看。'),
	(7757, 267, '清平乐', '当初相见。君恨相逢晚。一曲秦筝弹未遍。无奈昭阳人怨。 便教恩浅情疏。隔花空望金舆。春晚不拈红粉，碧窗自录仙书。'),
	(7758, 267, '卜算子', '碧瓦小红楼，芳草江南岸。雨后纱窗几阵寒，零落梨花晚。 看到水如云，送尽鸦成点。南北东西处处愁，独倚阑干遍。'),
	(7759, 267, '卜算子（除夕）', '江上见新年，年夜听春雨。有个人人领略春，粉淡红轻注。 深劝玉东西，低唱黄金缕。捻底梅花总是愁，酒尽人归去。'),
	(7760, 267, '减字木兰花', '无人请我。我自铺氈松下坐。酌酒裁诗。调弄梅花作侍儿。 心欢易醉。明月飞来花下睡。醉舞谁知。花满妙巾月满杯。'),
	(7761, 267, '桃源忆故人', '玉笙吹彻清商后。寂寞弓弯舞袖。巧画远山不就。只为眉长皱。 灵犀望断星难透。立到凄凉时候。今夜月明如昼。人共梅花瘦。'),
	(7762, 267, '醉春风', '夜饮西真洞。群仙惊戏弄。素娥传酒袖凌风，送送送。吸尽金波，醉朝天阙，斗班星拱。 碧简承新宠。紫微恩露重。忽然推枕草堂空。梦梦梦。帐冷衾寒，月斜灯暗，画楼钟动。'),
	(7763, 267, '木兰花慢', '折芙蓉弄水，动玉佩、起秋风。正柳外闲云，溪头淡月，映带疏钟。人间厌谪堕久，恨霓旌未返碧楼空。且与时人度日，自怜怀抱谁同。 当时种玉五云东。露冷夜耕龙。念瑞草成畦，琼蔬未采，尘染衰容。谁知素心未已，望清都绛阙有无中。寂寞归来隐几，梦听帝乐冲融。'),
	(7764, 267, '念奴娇（垂虹亭）', '放船纵棹，趁吴江风露，平分秋色。帆卷垂虹波面冷，初落萧萧枫叶。万顷琉璃，一轮金鉴，与我成三客。碧空寥廓，瑞星银汉争白。 深夜悄悄鱼龙，灵旗收暮霭，天光相接。莹澈乾坤，全放出、叠玉层冰宫阙。洗尽凡心，相忘尘世，梦想都销歇。胸中云海，浩然犹浸明月。'),
	(7765, 415, '念奴娇（上元）', '少年时节，见皇州灯火，衣冠朝市。天汉桥边瞻凤辇，帘幕千家垂地。人似神仙，身游佛国，谪堕红尘里。如今憔悴，渐无往岁欢味。 此夜帝里喧传，太平祥瑞，有街头人醉。更值端门新又起，楼阙千兵严卫。朝野多欢，边庭初静，歌舞方腾沸。良宵好景，异时多少遗事。'),
	(7766, 46, '望海潮', '垂杨深院，啼莺清昼，东风细绕檐牙。野色乱春，娇云捧日，燕翻蝶舞交加。温诏下天涯。早画船飞楫，叠鼓鸣挝。共挽旌幢，缓留丝竹醉韶华。 江都自古豪夸。正迷楼堕絮，月观吹花。玉扆眷深，金瓯望重，何人暗草黄麻。归路接堤沙。宴上林风月，紫阙烟霞。凤检飞来，便将春色到朝家。'),
	(7767, 46, '踏莎行（游凤凰台）', '二水中分，三山半落。风云气象通寥廓。少年怀古有新诗，清愁不是伤春作。 六代豪华，一时燕乐。从教雨打风吹却。与君携酒近阑干，月明满地天无幕。'),
	(7768, 94, '临江仙（诏芳亭赠坐客）', '一醉年年今夜月，酒船聊更同浮。恨无羯鼓打梁州。遗声犹好在，风景一时留。 老去狂歌君勿笑，已拚双鬓成秋。会须击节溯中流。一声云外笛，惊看水明楼。 世传梁州，西凉府初进此曲，会明皇游月宫还，记霓裳之声适相近，因作霓裳羽衣曲，以梁州名之。是夕，约诸君明夜泛舟，故有梁州、中流之句。'),
	(7769, 94, '临江仙', '闻道今年春信早，梅花不怕余寒。凭君先向近南看。香苞开遍未。莫待北枝残。 肠断陇头他日恨，江南几驿征鞍。一杯聊与尽余欢。风情何似我，老去未应阑。'),
	(7770, 225, '踏莎行', '玉露团花，金风破雾。高台与上晴空去。举杯相属看前山，烟中乱叠青无数。 皓齿明眸，肌香体素。恼人正在秋波注。因何欲雨又还晴，歌声遏得行云住。'),
	(7771, 510, '江神子（初至休宁冬夜作）', '昏昏雪意惨云容。猎霜风。岁将穷。流落天涯，憔悴一衰翁。清夜小窗围兽火，倾酒绿，借颜红。 官梅疏艳小壶中，暗香浓。玉玲珑。对景忽惊，身在大江东。上国故人谁念我，晴嶂远，暮云重。'),
	(7772, 360, '调笑', '心醉双翠翘。西厢月冷蒙花雾。落霞零乱墙东树。此夜灵犀已暗通，玉环寄恨人何处。 何处。长安路。不记墙东花拂树。瑶琴理罢霓裳谱。依旧月窗风户。薄情年少如飞絮。梦逐玉环西去。'),
	(7773, 360, '诉衷情（七夕）', '短疏萦绿象床低。玉鸭度香迟。微云淡著河汉，凉过碧梧枝。 秋韵起，月阴移。下帘时。人间天上，一样风光，我与君知。'),
	(7774, 247, '临江仙', '双舸亭亭横晚渚，城中飞观嵯峨。画桥灯火照清波。玉钩平浸水，金锁半沈河。 试问无情堤上柳，也应厌听离歌。人生无奈别离何。夜长嫌梦短，泪少怕愁多。'),
	(7775, 247, '汉宫春', '黯黯离怀，向东门系马，南浦移舟。薰风乱飞燕子，时下轻鸥。无情渭水，问谁教、日日东流。常是送、行人去后，烟波一向离愁。 回首旧游如梦，记踏青殢饮，拾翠狂游。无端彩云易散，覆水难收。风流未老，拚千金、重入扬州。应又是、当年载酒，依前名占青楼。'),
	(7776, 545, '卜算子', '烟雨幂横塘，绀色涵清浅。谁把并州快剪刀，剪取吴江半。 隐几岸乌巾，细葛含风软。不见柴桑避俗翁，心共孤云远。'),
	(7777, 545, '清平乐', '花边柳际，已渐知春意。归信不知何日是。旧恨欲拚无计。 故人零落西东，题诗待倩归鸿。惟有多情芳草，年年处处相逢。'),
	(7778, 655, '临江仙', '闻道洛阳花正好，家家庭户春风。道人饮去百壶空。年年花下醉，看谢几番红。 此别又从何处去，风萍一任西东。语声虽异笑声同。一轮深夜月，何处不相逢。'),
	(7779, 655, '卜算子', '身如一叶舟，万事潮头起。水长船高一任伊，来往洪涛里。 潮落又潮生，今古长如此。后夜开尊独酌时，月满人千里。'),
	(7780, 130, '蝶恋花', '鱼尾霞生明远树。翠壁粘天，玉叶迎风举。一笑相逢蓬海路。人间风月如尘土。剪水双眸云鬓吐。醉倒天瓢笑语生青雾。此会未阑须记取。桃花几度吹红雨。'),
	(7781, 130, '南柯子', '宝合分时果，金盘弄赐冰。晓来阶下按新声。恰有一方明月、可中庭。 露下天如水，风来夜气清。娇羞不肯傍人行。扬下扇儿拍手、引流萤。'),
	(7782, 130, '减字木兰花', '风鬟雾鬓。便觉蓬莱三岛近。水秀山明。缥缈仙姿画不成。 广寒丹桂。岂是夭桃尘俗世。只恐乘风。飞上琼楼玉宇中。'),
	(7783, 130, '丑奴儿', '叶底寻花春欲暮。折遍柔枝，满手真珠露。不见旧人空旧处。对花惹起愁无数。 却倚阑干吹柳絮。粉蝶多情，飞上钗头住。若遣郎身如蝶羽。芳时争肯抛人去。'),
	(7784, 130, '华胥引（黄钟秋思）', '川原澄映，烟月冥蒙，去舟如叶。岸足沙平，蒲根水冷留雁唼。别有孤角吟秋，对晓风鸣轧。红日三竿，醉头扶起还怯。 离思相萦，渐看看、鬓丝堪镊。舞衫歌扇，何人轻怜细阅。点检从前恩爱，但凤笺盈箧。愁剪灯花，夜来和泪双叠。'),
	(7785, 130, '长相思（舟中作）', '好风浮。晚雨收。林叶阴阴映鹢舟。斜阳明倚楼。 黯凝眸。忆旧游。艇子扁舟来莫愁。石城风浪秋。'),
	(7786, 130, '意难忘（中吕美咏·杂赋）', '衣染莺黄。爱停歌驻拍，劝酒持觞。低鬟蝉影动，私语口脂香。檐露滴，竹风凉。拚剧饮淋浪。夜渐深，笼灯就月，子细端相。 知音见说无双。解移宫换羽，未怕周郎。长颦知有恨，贪耍不成妆。些个事，恼人肠。试说与何妨。又恐伊、寻消问息，瘦减容光。'),
	(7787, 130, '玉楼春（仙吕惆怅）', '玉琴虚下伤心泪。只有文君知曲意。帘烘楼迥月宜人，酒暖香融春有味。 萋萋芳草迷千里。惆怅王孙行未已。天涯回首一销魂，二十四桥歌舞地。'),
	(7788, 209, '满庭芳', '裂楮裁筠，虚明潇洒，制成方丈屠苏。草团蒲坐，中置一山炉。拙似春林鸠宿，易于□、秋野享居。谁相对，时烦孟妇，石鼎煮寒蔬。 嗟吁。人生随分足，风云际会，漫付伸舒。且偷取闲时，向此踌躇。谩取黄金建厦，繁华梦、毕竟空虚。争如且、寒村厨火，汤饼一斋盂。'),
	(7789, 645, '清平乐', '为改之曰官样黄，作清平乐词，且令方内知有此名也。 重重叠叠。娜袅裙千襵。时样官黄香百叶。一岁相逢两节||。 曲阑绕遍芳丛。一枝作意妍秾。折得有谁相忆，却须还与秋风。'),
	(7790, 645, '菩萨蛮·七夕', '东飞乌鹊西飞燕。盈盈一水经年见。急雨洗香车。天回河汉斜。 离愁千载上。相远长相望。终不似人间。回头万里山。'),
	(7791, 549, '试周郎（诉衷情）', '乔家深闭郁金堂。朝镜事梅妆。云鬟翠钿浮动，微步拥钗梁。情尚秘，色犹庄。递瞻相。弄丝调管，时误新声，翻试周郎。'),
	(7792, 549, '减字木兰花', '冷香浮动。望处欲生胡蝶梦。晓日曈昽。愁见凝酥暖渐融。 鼓催歌送。芳酒一尊谁与共。寂寞墙东。门掩黄昏满院风。'),
	(7793, 549, '天门谣', '牛诸天门险，限南北七豪雄占。清雾敛，与闲人登览。 待月上潮平波滟滟。塞管轻软新阿滥。风满槛，历历数西州更点。'),
	(7794, 549, '惜奴娇', '玉立佳人，韵不减、吴苏小。赋深情、华年韵妙。叠鼓新歌，最能作、江南调。缥缈。似阳台。娇云弄晓。 有客临风，梦后拟、池塘草。竟装怀、□愁多少。绿绮芳尊，映花月、东山道。正要。个卿卿、嫣然一笑。'),
	(7795, 549, '风流子', '何处最难忘。方豪健，放乐五云乡。彩笔赋诗，禁池芳草，香鞯调马，辇路垂杨。绮筵上，扇偎歌黛浅，汗浥舞罗香。兰烛伴归，绣轮同载，闭花别馆，隔水深坊。 零落少年场。琴心漫流怨，带眼偷长。无奈占床燕月，侵鬓吴霜。念北里音尘，鱼封永断。便桥烟雨，鹤表相望。好在后庭桃李，应记刘郎。'),
	(7796, 549, '独倚楼（更漏子三首）', '上东门，门外柳。赠别每烦纤手。一叶落，几番秋。江南独倚楼。 曲阑干，凝伫久。薄暮更堪搔首。无际恨，见闲愁。侵寻天尽头。'),
	(7797, 549, '惜余春·急雨收春', '急雨收春，斜风约水。浮红涨绿鱼文起。年年游子惜余春，春归不解招游子。 留恨城隅，关情纸尾。阑干长对西曛倚。鸳鸯俱是白头时，江南渭北三千里。'),
	(7798, 556, '思远人', '素玉朝来有好怀。一枝梅粉照人开。晴云欲向怀中起，春色先从脸上来。 深院落，小楼台。玉盘香篆看徘徊。须知月色撩人恨，数夜春寒不下阶。'),
	(7799, 556, '蝶恋花', '五夜”其词曰：“待月西厢下，迎风户半开。拂墙花影动，疑是玉人来。”奉劳歌伴，再和前声。商调十二首之四 庭院黄昏春雨霁。一缕深心，百种成牵系。青翼蓦然来报喜。鱼笺微谕相容意。 待月西厢人不寐。帘影摇光，朱户犹慵闭。花动拂墙红萼坠。分明疑是情人至。'),
	(7800, 556, '蝶恋花（商调十二首之一）', '夫传奇者，唐元微之所述也。以不载于本集而出于小说，或疑其非是。今观其词，自非大手笔孰能与于此。至今士大夫极谈幽玄，访奇述异，无不举此以为美话。至于娼优女子，皆能调说大略。惜乎不被之以音律，故不能播之声乐，形之管弦。好事君子极饮肆欢之际，愿欲一听其说，或举其末而忘其本，或纪其略而不及终其篇，此吾曹之所共恨者也。今于暇日，详观其文，略其烦亵，分之为十章。每章之下，属之以词。或全摭其文，或止取其意。又别为一曲，载之传前，先叙前篇之义。调曰商调，曲名蝶恋花。句句言情，篇篇见意。奉劳歌伴，先定格调，后听芜词。 丽质仙娥生月殿。谪向人间，未免凡情乱。宋玉墙东流美盼。乱花深处曾相见。 密意浓欢方有便。不奈浮名，旋遣轻分散。最恨多才情太浅。等闲不念离人怨。'),
	(7801, 468, '减字木兰花（展书卷）', '平生真赏。纸上龙蛇三五行。富贵功名。老境谁堪宠辱惊。 寸心谁语。只有当年袁与许。归到寥阳。玉简霞衣侍帝旁。'),
	(7802, 451, '青门饮', '风起云间，雁横天末，严城画角，梅花三奏。塞草西风，冻云笼月，窗外晓寒轻透。人去香犹在，孤衾长闲馀绣。恨与宵长，一夜薰炉，添尽香兽。前事空劳回首。虽梦断春归，相思依旧。湘瑟声沈，庾梅信断，谁念画眉人瘦。一句难忘处，怎忍辜、耳边轻咒。任人攀折，可怜又学，章台杨柳。'),
	(7803, 451, '喜迁莺', '花香馥郁。正春色平中，海筹添屋。金马清才，玉麟旧守，帝遣暂临江国。冠盖光生南楚，川岳灵钟西蜀。堪羡是，有汪洋万顷，珠玑千斛。 听祝。愿多寿，多福多男，溥作苍生福。碧柳绯桃，锦袍乌帽，辉映颜朱鬓绿。早见鹤楼风采，归掌鸾坡机轴。百岁里，庆团B622长似，冰轮满足。'),
	(7804, 451, '念奴娇', '夜凉湖上，酌芳尊，对此一轮皓月。岁月匆匆人老大，又近中秋时节。夜气泬，湖光旷邈。风舞萧萧叶。水天一色，坐来肌骨清彻。 自念尘满征衫，无人为浣，洒泪今成血。玉兔银蟾休道远，不识愁人情切。绣帐香销，画屏烛冷，此意凭谁说。天青海碧，枉教望断瑶阙。'),
	(7805, 451, '渔家傲（七夕立秋）', '七夕湖头闲眺望。风烟做出秋模样。不见云屏月帐。天滉漾。龙軿暗渡银河浪。 二十年前今日况。玄蟾乌鹊高楼上。回首西风犹未忘。追得丧。人间万事成惆怅。'),
	(7806, 451, '蝶恋花', '紫燕双飞深院静。簟枕纱厨，睡起娇如病。一线碧烟萦藻井。小鬟茶进龙香饼。 拂拭菱花看宝镜。玉指纤纤，捻唾撩云鬓。闲折海榴过翠径。雪猫戏扑风花影。'),
	(7807, 451, '临江仙', '客路光阴浑草草，等闲过了元宵。村鸡啼月下林梢。鸾声惊宿鸟，霜气入重貂。 漠漠风沙千里暗，举头一望魂消。问君何事不辞劳。平生经世意，只恐负清朝。'),
	(7808, 451, '浣溪沙（五之二）', '香靥凝羞一笑开。柳腰如醉肯相挨。日长春困下楼台。 照水有情聊整鬓，倚阑无绪更兜鞋。眼边牵系懒归来。'),
	(7809, 451, '河传（二之一）', '乱花飞絮。又望空斗合，离人愁苦。那更夜来，一霎薄情风雨。暗掩将、春色去。 篱枯壁尽因谁做。若说相思，佛也眉儿聚。莫怪为伊，底死萦肠惹肚。为没教、人恨处。'),
	(7810, 248, '朝中措', '短亭杨柳接长亭。攀折赠君行。莫怪尊前无语，大都分外多情。 何须苦计，时间利禄，身后功名。且尽十分芳酒，共倾一梦浮生。'),
	(7811, 248, '清平乐', '清樽泛菊。共剪西窗烛。一抹朱弦新按曲。更遣歌喉细逐。 明朝匹马西风。黄云衰草重重。试问剑歌悲壮，何如玉指轻拢。'),
	(7812, 248, '满庭芳', '浅约鸦黄，轻匀螺黛，故教取次梳妆。减轻琶面，新样小鸾凰。每为花娇玉嫩，容对客、斜倚银床。春来病，兰薰半歇，一管舞衣裳。 悲凉。人事改，三春秾艳，一夜繁霜。似人归洛浦，云散高唐。痛念你、平生分际，辜负我、临老风光。罗裙在，凭谁为我，求取返魂香。'),
	(7813, 248, '望海潮', '高阳方面，河间都会，三关地最称雄。粉堞万层，金城百雉，楼横一带长虹。烟素敛晴空。正望迷平野，目断飞鸿。易水风烟，范阳山色有无中。 安边暂倚元戎。看纶巾对酒，羽扇摇风。金勒少年，吴钩壮士，宁论卫霍前功。乃眷在清衷。恐凤池虚久，归去匆匆。幸有佳人锦瑟，玉笋且轻拢。'),
	(7814, 693, '诉衷情', '旋揎玉指著红靴。宛宛斗弯讹。天然自有殊态，供愁黛、不须多。 分远岫，压横波。妙难过。自欹枕处，独倚阑时，不奈颦何。'),
	(7815, 693, '浣溪沙', '飞鹊台前晕翠蛾。千金新买帝青螺。最难如意为情多。 几处泪痕留醉袖，一春愁思近横波。远山低尽不成歌。'),
	(7816, 693, '蝶恋花', '海角芳菲留不住。笔下风生，吹入青云去。仙籍有名天赐与。致君事业安排取。 要识世间平坦路。当使人人，各有安身处。黑发便逢尧舜主。笑人白首耕南亩。'),
	(7817, 693, '逍遥乐', '春意渐归芳草。故国佳人，千里信沈音杳。雨润烟光，晚景澄明，极目危栏斜照。梦当年少。对樽前、上客邹枚，小鬟燕赵。共舞雪歌尘，醉里谈笑。 花色枝枝争好。鬓丝年年渐老。如今遇风景，空瘦损、向谁道。东君幸赐与，天幕翠遮红绕。休休，醉乡岐路，华胥蓬岛。'),
	(7818, 693, '定风波', '上客休辞酒浅深。素儿歌里细听沈。粉面不须歌扇掩，闲静，一声一字总关心。 花外黄鹂能密语。休诉。有花能得几时斟。画作远山临碧水。明媚。梦为胡蝶去登临。'),
	(7819, 693, '两同心', '秋水遥岑。妆淡情深。尽道教、心坚穿石，更说甚、官不容针。霎时间，雨散云归，无处追寻。 小楼朱阁沈沈。一笑千金。你共人、女边著子，争知我、门里桃心。最难忘，小院回廊，月影花阴。'),
	(7820, 278, '早梅芳', '雪初销，斗觉寒将变。已报梅梢暖。日边霜外，迤逦枝条自柔软。嫩苞匀点缀，绿萼轻裁剪。隐深心，未许清香散。 渐融和，开欲遍。密处疑无间。天然标韵，不与群花斗深浅。夕阳波似动，曲水风犹懒。最销魂，弄影无人见。'),
	(7821, 494, '踏莎行', '这个秃奴，修行忒煞。云山顶上空持戒。一从迷恋玉楼人，鹑衣百结浑无奈。 毒手伤人，花容粉碎。空空色色今何在。臂间刺道苦相思，这回还了相思债。'),
	(7822, 494, '踏青游', '攺火初晴，绿遍禁池芳草。斗锦绣、火城驰道。踏青游，拾翠惜，袜罗弓小。莲步袅。腰支佩兰轻妙。行过上林春好。今困天涯，何限旧情相恼。念摇落、玉京寒早。任刘郎、目断蓬山难到。仙梦杳。良宵又过了。楼台万家清晓。'),
	(7823, 494, '菩萨蛮', '湿云不动溪桥冷。嫩寒初透东风影。桥下水声长。一枝和月香。 人怜花似旧。花比人应瘦。莫凭小栏干。夜深花正寒。'),
	(7824, 494, '南乡子（集句）', '何处倚阑干（杜牧）。弦管高楼月正圆（杜牧）。胡蝶梦中家万里（崔涂），依然。老去愁来强自宽（杜甫）。 明镜借红颜（李商隐）。须著人间比梦间（韩愈）。蜡烛半笼金翡翠（李商隐），更阑。绣被焚香独自眠（许浑）。'),
	(7825, 494, '南乡子（自述）', '凉簟碧纱厨。一枕清风昼睡馀。睡听晚衙无一事，徐徐。读尽床头几卷书。 搔首赋归欤。自觉功名懒更疏。若问使君才与术，何如。占得人间一味愚。'),
	(7826, 494, '雨中花慢', '邃院重帘何处，惹得多情，愁对风光。睡起酒阑花谢，蝶乱蜂忙。今夜何人，吹笙北岭，待月西厢。空怅望处，一株红杏，斜倚低墙。 羞颜易变，傍人先觉，到处被著猜防。谁信道，些儿恩爱。无限凄凉。好事若无间阻，幽欢却是寻常。一般滋味，就中香美，除是偷尝。'),
	(7827, 494, '祝英台近', '挂轻帆，飞急桨，还过钓台路。酒病无聊，欹枕听鸣舻。断肠簇簇云山，重重烟树，回首望、孤城何处。 闲离阻。谁念萦损襄王，何曾梦云雨。旧恨前欢，心事两无据。要知欲见无由，痴心犹自，倩人道、一声传语。'),
	(7828, 494, '渔家傲', '临水纵横回晚鞚。归来转觉情怀动。梅笛烟中闻几弄。秋阴重。西山雪淡云凝冻。 美酒一杯谁与共。尊前舞雪狂歌送。腰跨金鱼旌旆拥。将何用。只堪妆点浮生梦。'),
	(7829, 494, '木兰花令', '经旬未识东君信。一夕薰风来解愠。红绡衣薄麦秋寒，绿绮韵低梅雨润。 瓜头绿染山光嫩。弄色金桃新傅粉。日高慵卷水晶帘，犹带春醪红玉困。'),
	(7830, 494, '占春芳', '红杏了，夭桃尽，独自占春芳。不比人间兰麝，自然透骨生香。 对酒莫相忘。似佳人、兼合明光。只忧长笛吹花落，除是宁王。'),
	(7831, 494, '渔家傲（赠曹光州）', '些小白须何用染。几人得见星星点。作郡浮光虽似箭。君莫厌。也应胜我三年贬。 我欲自嗟还不敢。向来三郡宁非忝。婚嫁事稀年冉冉。知有渐。千钧重担从头减。'),
	(7832, 494, '荷华媚（荷花）', '霞苞电荷碧。天然地、别是风流标格。重重青盖下，千娇照水，好红红白白。 每怅望、明月清风夜，甚低迷不语，娇邪无力。终须放、船儿去，清香深处住，看伊颜色。'),
	(7833, 494, '浣溪沙（即事）', '画隼横江喜再游。老鱼跳槛识清讴。流年未肯付东流。 黄菊篱边无怅望，白云乡里有温柔。挽回霜鬓莫教休。'),
	(7834, 494, '浣溪沙', '野饮松下，设松黄汤，作此阕。 罗袜空飞洛浦尘。锦袍不见谪仙人。携壶藉草亦天真。 玉粉轻黄千岁药，雪花浮动万家春。醉归江路野梅新。'),
	(7835, 494, '浣溪沙（菊节）', '缥缈危楼紫翠间。良辰乐事古难全。感时怀旧独凄然。 璧月琼枝空夜夜，菊花人貌自年年。不知来岁与谁看。'),
	(7836, 494, '浣溪沙（前韵）', '料峭东风翠幕惊。云何不饮对公荣。水晶盘莹玉鳞赪。 花影莫孤三夜月，朱颜未称五年兄。翰林子墨主人卿。'),
	(7837, 494, '点绛唇（再和送钱公永）', '莫唱阳关，风流公子方终宴。秦山禹甸。缥缈真奇观。 北望平原，落日山衔半。孤帆远。我歌君乱。一送西飞雁。'),
	(7838, 494, '南歌子（同前）', '琥珀装腰佩，龙香入领巾。只应飞燕是前身。共看剥葱纤手、舞凝神。 柳絮风前转，梅花雪里春。鸳鸯翡翠两争新。但得周郎一顾、胜珠珍。'),
	(7839, 494, '南歌子（感旧）', '寸恨谁云短，绵绵岂易裁。半年眉绿未曾开。明月好风闲处、是人猜。 春雨消残冻，温风到冷灰。尊前一曲为谁哉。留取曲终一拍、待君来。'),
	(7840, 494, '南歌子（暮春）', '紫陌寻春去，红尘拂面来。无人不道看花回。惟见石榴新蕊、一枝开。 冰簟堆云髻，金尊滟玉醅。绿阴青子相催。留取红巾千点、照池台。'),
	(7841, 494, '南歌子（再用前韵）', '带酒冲山雨，和衣睡晚睛。不知钟鼓报天明。梦里栩然蝴蝶、一身轻。 老去才都尽，归来计未成。求田问舍笑豪英。自爱湖边沙路、免泥行。'),
	(7842, 494, '渔家傲（送台守江郎中）', '送客归来灯火尽。西楼淡月凉生晕。明日潮来无定准。潮来稳。舟横渡口重城近。 江水似知孤客恨。南风为解佳人愠。莫学时流轻久困。频寄问。钱塘江上须忠信。'),
	(7843, 494, '西江月（坐客见和复次韵）', '小院朱阑几曲，重城画鼓三通。更看微月转光风。归去香云入梦。 翠袖争浮大白，皂罗半插斜红。灯花零落酒花秾。妙语一时飞动。'),
	(7844, 250, '采桑子', '别来长记西楼事，结遍兰襟。遗恨重寻。弦断相如绿绮琴。 何时一枕逍遥夜，细话初心。若问如今。也似当时著意深。'),
	(7845, 250, '采桑子', '白莲池上当时月，今夜重圆。曲水兰船。忆伴飞琼看月眠。 黄花绿酒分携后，泪湿吟笺。旧事年年。时节南湖又采莲。'),
	(7846, 250, '虞美人', '秋风不似春风好。一夜金英老。更谁来凭曲阑干。惟有雁边斜月、照关山。 双星旧约年年在。笑尽人情改。有期无定是无期。说与小云新恨、也低眉。'),
	(7847, 250, '丑奴儿', '日高庭院杨花转，闲淡春风。莺语惺忪。似笑金屏昨夜空。 娇慵未洗匀妆手，闲印斜红。新恨重重。都与年时旧意同。'),
	(7848, 250, '浣溪沙', '日日双眉斗画长。行云飞絮共轻狂。不将心嫁冶游郎。 溅酒滴残歌扇字，弄花熏得舞衣香。一春弹泪说凄凉。'),
	(7849, 250, '生查子', '远山眉黛长，细柳腰肢袅。妆罢立春风，一笑千金少。 归去凤城时，说与青楼道。遍看颍川花，不似师师好。'),
	(7850, 250, '鹧鸪天', '绿橘梢头几点春。似留香蕊送行人。明朝紫凤朝天路，十二重城五碧云。 歌渐咽，酒初醺。尽将红泪湿湘裙。赣江西畔从今日，明月清风忆使君。'),
	(7851, 250, '蝶恋花', '碾玉钗头双凤小。倒晕工夫，画得宫眉巧。嫩麹罗裙胜碧草。鸳鸯绣字春衫好。 三月露桃芳意早。细看花枝，人面争多少。水调声长歌未了。掌中杯尽东池晓。'),
	(7852, 323, '少年游', '小楼归燕又黄昏。寂寞锁高门。轻风细雨，惜花天气，相次过春分。 画堂无绪，初燃绛蜡，罗帐掩余薰。多情不解怨王孙，任薄幸、一从君。'),
	(7853, 352, '望江南', '江南柳，花柳两个柔。花片落时黏酒盏，柳条低处拂人头。各自是风流。 江南月，如镜复如钩。似镜不侵红粉面，似钩不挂画帘头。长是照离愁。'),
	(7854, 352, '渔家傲', '妾解清歌并巧笑。郎多才俊兼年少。何事抛儿行远道。无音耗。江头又绿王孙草。 昔日采花呈窈窕。玉容长笑花枝老。今日采花添懊恼。伤怀抱。玉容不及花枝好。'),
	(7855, 352, '蝶恋花', '一掬天和金粉腻。莲子心中，自有深深意。意密莲深秋正媚。将花寄恨无人会。 桥上少年桥下水。小棹归时，不语牵红袂。浪浅荷心圆又碎。无端欲伴相思泪。'),
	(7856, 352, '圣无忧', '珠帘卷，暮云愁。垂杨暗锁青楼。烟雨濛濛如画，轻风吹旋收。 香断锦屏新别，人闲玉簟初秋。多少旧欢新恨，书杳杳、梦悠悠。'),
	(7857, 352, '清平乐', '小庭春老。碧砌红萱草。长忆小阑闲共绕。携手绿丛含笑。 别来音信全乖。旧期前事堪猜。门掩日斜人静，落花愁点青苔。'),
	(7858, 352, '浣溪沙', '云曳香绵彩柱高。绛旗风飐出花梢。一梭红带往来抛。 束素美人羞不打，却嫌裙慢褪纤腰。日斜深院影空摇。'),
	(7859, 352, '浪淘沙', '今日北池游。漾漾轻舟。波光潋滟柳条柔。如此春来春又去，白了人头。 好妓好歌喉。不醉难休。劝君满满酌金瓯。纵使花时常病酒，也是风流。'),
	(7860, 352, '渔家傲', '青女霜前催得绽。金钿乱散枝头遍。落帽台高开雅宴。芳尊满。挼花吹在流霞面。 桃李三春虽可羡。莺来蝶去芳心乱。争似仙潭秋水岸。香不断。年年自作茱萸伴。'),
	(7861, 352, '渔家傲（七夕）', '喜鹊填河仙浪浅。云軿早在星桥畔。街鼓黄昏霞尾暗。炎光敛。金钩侧倒天西面。 一别经年今始见。新欢往恨知何限。天上佳期贪眷恋。良宵短。人间不合催银箭。'),
	(7862, 352, '蝶恋花', '梨叶初红蝉韵歇。银汉风高，玉管声凄切。枕簟乍凉铜漏彻。谁教社燕轻离别。 草际虫吟秋露结。宿酒醒来，不记归时节。多少衷肠犹未说。珠帘夜夜朦胧月。'),
	(7863, 352, '蝶恋花', '水浸秋天风皱浪。缥缈仙舟，只似秋天上。和露采莲愁一饷。看花却是啼妆样。 折得莲茎丝未放。莲断丝牵，特地成惆怅。归棹莫随花荡漾。江头有个人想望。'),
	(7864, 352, '蝶恋花', '腊雪初销梅蕊绽。梅雪相和，喜鹊穿花转。睡起夕阳迷醉眼。新愁长向东风乱。 瘦觉玉肌罗带缓。红杏梢头，二月春犹浅。望极不来芳信断。音书纵有争如见。'),
	(7865, 352, '采桑子', '春深雨过西湖好，百卉争妍。蝶乱蜂喧。晴日催花暖欲然。 兰桡画舸悠悠去，疑是神仙。返照波间。水阔风高扬管弦。'),
	(7866, 251, '诉衷情', '喧天丝竹韵融融。歌唱画堂中。玲女世间希有，烛影夜摇红。 一同笑，饮千钟。兴何穷。功成名遂，富足年康，祝寿如松。'),
	(7867, 251, '玉堂春', '后园春早。残雪尚濛烟草。数树寒梅，欲绽香英。小妹无端、折尽钗头朵，满把金尊细细倾。 忆得往年同伴，沈吟无限情。恼乱东风、莫便吹零落，惜取芳菲眼下明。'),
	(7868, 251, '玉堂春', '帝城春暖。御柳暗遮空苑。海燕双双，拂扬帘栊。女伴相携、共绕林间路，折得樱桃插髻红。 昨夜临明微雨，新英遍旧丛。宝马香车、欲傍西池看，触处杨花满袖风。'),
	(7869, 251, '秋蕊香', '向晓雪花呈瑞。飞遍玉城瑶砌。何人剪碎天边桂。散作瑶田琼蕊。 萧娘敛尽双蛾翠。回香袂。今朝有酒今朝醉。遮莫更长无睡。'),
	(7870, 251, '拂霓裳', '庆生辰。庆生辰是百千春。开雅宴，画堂高会有诸亲。钿函封大国，玉色受丝纶，感皇恩。望九重、天上拜尧云。 今朝祝寿，祝寿数，比松椿。斟美酒，至心如对月中人。一声檀板动，一炷蕙香焚。祷仙真。愿年年今日、喜长新。'),
	(7871, 251, '长生乐', '玉露金风月正圆。台榭早凉天。画堂嘉会，组绣列芳筵。洞府星辰龟鹤，来添福寿。欢声喜色，同入金炉浓烟。 清歌妙舞，急管繁弦。榴花满酌觥船。人尽祝、富贵又长年。莫教红日西晚，留著醉神仙。'),
	(7872, 251, '渔家傲', '荷叶初开犹半卷。荷花欲拆犹微绽。此叶此花真可羡。秋水畔。青凉伞映红妆面。 美酒一杯留客宴。拈花摘叶情无限。争奈世人多聚散。频祝愿。如花似叶长相见。'),
	(7873, 251, '渔家傲', '荷叶荷花相间斗。红娇绿嫩新妆就。昨日小池疏雨后。铺锦绣。行人过去频回首。 倚遍朱阑凝望久。鸳鸯浴处波文皱。谁唤谢娘斟美酒。萦舞袖。当筵劝我千长寿。'),
	(7874, 251, '诉衷情', '海棠珠缀一重重。清晓近帘栊。胭脂谁与匀淡，偏向脸边浓。 看叶嫩，惜花红。意无穷。如花似叶，岁岁年年，共占春风。'),
	(7875, 251, '诉衷情', '露莲双脸远山眉。偏与淡妆宜。小庭帘幕春晚，闲共柳丝垂。 人别后，月圆时。信迟迟。心心念念，说尽无凭，只是相思。'),
	(7876, 186, '菩萨蛮（中吕调）', '玉人又是匆匆去。马蹄何处垂杨路。残日倚楼时。断魂郎未知。 阑干移倚遍。薄幸教人怨。明月却多情。随人处处行。'),
	(7877, 186, '生查子（双调）', '当初相见时，彼此心萧洒。近日见人来，却恁相谩諕。 休休休便休，美底教他且。匹似没伊时，更不思量也。'),
	(7878, 186, '归朝欢（双调）', '声转辘轳闻露井。晓引银瓶牵素绠。西圆人语夜来风，丛英飘坠红成径。宝猊烟未冷。莲台香蜡残痕凝。等身金，谁能得意，买此好光景。 粉落轻妆红玉莹。月枕横钗云坠领。有情无物不双栖，文禽只合常交颈。昼长欢岂定。争如翻作春宵永。日曈昽，娇柔懒起，帘押残花影。'),
	(7879, 344, '十二时（秋夜）', '晚晴初，淡烟笼月，风透蟾光如洗。觉翠帐、凉生秋思。渐入微寒天气。败叶敲窗，西风满院，睡不成还起。更漏咽、滴破忧心，万感并生，都在离人愁耳。 天怎知、当时一句，做得十分萦系。夜永有时，分明枕上，觑着孜孜地。烛暗时酒醒，元来又是梦里。〓睡觉来、披衣独坐，万种无憀情意。怎得伊来，重谐云雨，再整馀香被。祝告天发愿，从今永无抛弃。'),
	(7880, 344, '梁州令（中吕宫）', '梦觉纱窗晓。残灯掩然空照。因思人事苦萦牵，离愁别恨，无限何时了。 怜深定是心肠小。往往成烦恼。一生惆怅情多少。月不长圆，春色易为老。'),
	(7881, 344, '瑞鹧鸪（二之一）', '天将奇艳与寒梅。乍惊繁杏腊前开。暗想花神、巧作江南信，鲜染燕脂细翦裁。 寿阳妆罢无端饮，凌晨酒入香腮。恨听烟隖深中，谁恁吹羌管、逐风来。绛雪纷纷落翠苔。'),
	(7882, 344, '塞孤（般涉调）', '一声鸡，又报残更歇。秣马巾车催发。草草主人灯下别。山路险，新霜滑。瑶珂响、起栖乌，金钲冷、敲残月。渐西风系，襟袖凄冽。 遥指白玉京，望断黄金阙。还道何时行彻。算得佳人凝恨切。应念念，归时节。相见了、执柔夷，幽会处、偎香雪。免鸳衾、两恁虚设。'),
	(7883, 344, '瑞鹧鸪（二之二·南吕调）', '吴会风流。人烟好，高下水际山头。瑶台绛阙，依约蓬丘。万井千闾富庶，雄压十三州。触处青蛾画舸，红粉朱楼。 方面委元侯。致讼简时丰，继日欢游。襦温袴暖，已扇民讴。旦暮锋车命驾，重整济川舟。当恁时，沙堤路稳，归去难留。'),
	(7884, 344, '瑞鹧鸪（二之一·南吕调）', '宝髻瑶簪。严妆巧，天然绿媚红深。绮罗丛里，独逞讴吟。一曲阳春定价，何啻值千金。倾听处，王孙帝子，鹤盖成阴。 凝态掩霞襟。动象板声声，怨思难任。嘹亮处，回厌弦管低沈。时恁回眸敛黛，空役五陵心。须信道，缘情寄意。别有知音。'),
	(7885, 344, '红窗听（仙吕调）', '如削肌肤红玉莹。举措有、许多端正。二年三岁同鸳寝。表温柔心性。 别后无非良夜永。如何向、名牵利役，归期未定。算伊心里，却冤成薄幸。'),
	(7886, 344, '六么令（仙吕调）', '淡烟残照，摇曳溪光碧。溪边浅桃深杏，迤逦染春色。昨夜扁舟泊处，枕底当滩碛。波声渔笛。惊回好梦，梦里欲归归不得。 展转翻成无寐，因此伤行役。思念多媚多娇，咫尺千山隔。都为深情密爱，不忍轻离拆。好天良夕。鸳帷寂寞，算得也应暗相忆。'),
	(7887, 344, '如鱼水（二之一·仙吕调）', '轻霭浮空，乱峰倒影，潋滟十里银塘。绕岸垂杨。红楼朱阁相望。芰荷香。双双戏、鸂鶒鸳鸯。乍雨过、兰芷汀洲，望中依约似潇湘。 风淡淡，水茫茫。动一片晴光。画舫相将。盈盈红粉清商。紫薇郎。修禊饮、且乐仙乡。更归去，遍历銮坡凤沼，此景也难忘。'),
	(7888, 344, '木兰花（四之三·林钟商）', '虫娘举措皆温润。每到婆娑偏恃俊。香檀敲缓玉纤迟，画鼓声催莲步紧。 贪为顾盼夸风韵。往往曲终情未尽。坐中年少暗消魂，争问青鸾家远近。'),
	(7889, 344, '少年游（十之十·林钟商）', '佳人巧笑值千金。当日偶情深。几回饮散，灯残香暖，好事尽鸳衾。 如今万水千山阻，魂杳杳、信沈沈。孤棹烟波，小楼风月，两处一般心。'),
	(7890, 344, '少年游（十之七·林钟商）', '帘垂深院冷萧萧。花外漏声遥。青灯未灭，红窗闲卧，魂梦去迢迢。 薄情漫有归消息，鸳鸯被、半香消。试问伊家，阿谁心绪，禁得恁无憀。'),
	(7891, 344, '少年游（十之六·林钟商）', '铃斋无讼宴游频。罗绮簇簪绅。施朱溥粉，丰肌清骨，空态尽天真。 舞裀歌扇花光里，翻回雪，驻行云。绮席阑珊，凤灯明灭，谁是意中人。'),
	(7892, 344, '荔枝香（歇指调）', '甚处寻芳赏翠，归去晚。缓步罗袜生尘，来绕琼筵看。金缕霞衣轻褪，似觉春游倦。遥认，众里盈盈好身段。 拟回首，又伫立、帘帏畔。素脸红眉，时揭盖头微见。笑整金翘，一点芳心在娇眼。王孙空恁肠断。'),
	(7893, 344, '永遇乐（二之一·歇指调）', '薰风解愠，昼景清和，新霁时候。火德流光，萝图荐祉，累庆金枝秀。璿枢绕电，华渚流虹，是日挺生元后。缵唐虞垂拱，千载应期，万灵敷祐。 殊方异域，争贡琛赆，架巘航波奔凑。三殿称觞，九仪就列，韶頀锵金奏。藩侯瞻望彤庭，亲携僚吏，竞歌元首。祝尧龄、北极齐尊，南山共久。'),
	(7894, 344, '倾杯乐（仙吕宫）', '禁漏花深，绣工日永，蕙风布暖。变韶景、都门十二，元宵三五，银蟾光满。连云复道凌飞观。耸皇居丽，嘉气瑞烟葱茜。翠华宵幸，是处层城阆苑。 龙凤烛、交光星汉。对咫尺鳌山开羽扇。会乐府两籍神仙，梨园四部弦管。向晓色、都人未散。盈万井、山呼熬抃。顾岁岁，天仗里、常瞻凤辇。'),
	(7895, 344, '甘草子（二之二）', '秋尽。叶翦红绡，砌菊遗金粉。雁字一行来，还有边庭信。 飘散露华清风紧。动翠幕、晓寒犹嫩。中酒残妆慵整顿。聚两眉离恨。'),
	(7896, 397, '酒泉子（十之七）', '长忆高峰，峰上塔高尘世外。昔年独上最高层。月出见觚棱。 举头咫尺疑天汉。星斗分明在身畔。别来无翼可飞腾。何日得重登。'),
	(7897, 348, '玉楼春', '天然不比花含粉。约月眉黄春色嫩。小桥低映欲迷人，闲倚东风无奈困。烟姿最与章台近。冉冉千丝谁结恨。狂莺来往恋芳阴，不道风流真能尽。'),
	(7898, 477, '采桑子', '与君别后愁无限，永远团圞。间阴多方。水远山遥过断肠。 终朝等候郎音耗，捱过春光。烟水茫茫。梅子青青又待黄。'),
	(7899, 488, '减字木兰花', '别离情绪。万里关山如底数。遣妾伤悲。未心郎家知不知。 自从君去。数尽残冬春又暮。音信全乖。等到花开不见来。'),
	(7900, 377, '水调歌头', '汉事正犹豫，足迹正跫然。严陵老子，当时底事动天顽。曾把丝纶一掷，藐视山河九鼎，高议凛人寒。竹帛非吾事，霄汉任腾骞。 问云台，还得似，钓台巅。几年山下，使人犹识汉衣冠。寄语功名余子，今日成尘何在，百战亦多艰。一笑桐江上，来往钓名船。'),
	(7901, 653, '百字令（寿虞守·正月十二）', '梅峰孕秀，太仙翁、咫尺元宵三夕。盛德温温如玉粹，多少殊勋嘉绩。两拥朱幡，独清节，高谊真难屈。景疏堂下，台星长伴南极。 雅羡椿桂联芳，金章紫绶，拜舞欢声溢。福备宜高仁者寿，喜占人间八十。汉重申公，周登尚父，盛事今犹昔。九重注想，蒲车行聘遗逸。'),
	(7902, 20, '摸鱼儿（送陈太史东归）', '极知君、腰骑鹤，此心与水相似。梧桐叶上黄昏雨，恨杀无情流水。滔滔地。今日大江头，明日人千里。君思往事。两袖淮云，一轮明月，要亦寻常耳。 君去也，我亦扁舟东矣。尊前何惜同醉。五年未老君犹健，五十行当富贵。书频寄。报王粲、何时可决荆州计。俱胝一纸。更细算何时，五星同会，天下太平未。'),
	(7903, 443, '雨中花（贺欧文建楼与桥）', '饤斗云山，挨排烟水，六丁午夜文移。道滁孙子，欲寄游嬉。高趁鹜霞舒啸，低群鸥鹭忘机。牢笼两下，楼乘汗漫，桥枕清漪。 灞陵吟畅，岳阳登览，百色都副襟期。还好是、行天马渡，深月人归。倚柱荷香扑面，凭栏桂影侵衣。索梅无便，春风不碍，容我追随。'),
	(7904, 169, '齐天乐（赠卢天隐）', '江湖千里秋风客，翩然白云黄鹄。石鼎烟霏，篆书红湿，随处菊香泉绿。幅巾野服。尽扫叶开门，抱琴听瀑。何事蓬壶，归来犹待海涛陆。 卢鸿旧时隐处，想斜阳草树，水榭云屋。尘尾玄玄，笔花语语，剪尽雨窗残烛。黄庭误读。且东老留诗，采和歌曲。后日重寻，洞天三十六。'),
	(7905, 55, '凤栖梧（舞酒妓）', '一翦晴波娇欲溜。绿怨红愁，长为春风瘦。舞罢金杯眉黛皱。背人倦倚晴窗绣。 脸晕潮生微生微带酒。催唱新词，不应频摇手。闲把琵琶调未就。羞郎却又垂红袖。'),
	(7906, 55, '蝶恋花', '日暮杨花飞乱雪。宝镜慵拈，强整双鸳结。烧罢夜香愁万叠。穿暗避阶前月。 凤尾罗衾寒尚怯，却悔当时，容易成分别。闷对枕鸾谁共说。柔情一点蔷薇血。'),
	(7907, 480, '万年欢（上元）', '灯月交光，渐轻风布暖，先到南国。罗绮娇容，十里绛纱笼烛。花艳惊郎醉目。有多少、佳人如玉。春衫袂，整整齐齐，内家新样妆束。 欢情未足。更兰谩勾牵旧恨，萦乱心曲。怅望归期，应是紫姑频卜。暗想双眉对蹙。断弦待、鸾胶重续。休迷恋，野草闲花，凤箫人在金谷。'),
	(7908, 135, '桂枝香（天柱山房拟赋蟹）', '松江舍北。正水落晚汀，霜老枯荻。还见青匡似绣，绀螯如戟。西风有恨无肠断，恨东流、几番潮汐。夜灯争聚微光，挂影误投帘隙。 更喜荐、新篘玉液。正半■含黄，一醉秋色。纤手香橙风味，有人相忆。江湖岁晚听飞雪，但沙痕、空记行迹。至今茶鼎，时时犹认，眼波愁碧。'),
	(7909, 103, '水龙吟（浮翠山房拟赋白莲）', '素肌不污天真，晓来玉立瑶池里。亭亭翠盖，盈盈素靥，时妆净洗。太液波翻，霓裳舞罢，断魂流水。甚依然、旧日浓香淡粉，花不似、人憔悴。 欲唤凌波仙子。泛扁舟、浩波千里。只愁回首，冰帘半掩，明珰乱坠。月影凄迷，露花零落，小阑谁倚。共芳盟，犹有双栖雪鹭，夜寒惊起。'),
	(7910, 422, '如梦令', '妾似春蚕抽缕。君似筝弦移柱。无语结同心，满地落花飞絮。 归去。归去。遥指乱云遮处。'),
	(7911, 532, '更漏子', '日衔山，山带雪。笛弄晚风残月。湘梦断，楚魂迷。金河秋雁飞。 别离心，思忆泪。锦带已伤憔悴。蛩韵急，杵声寒。征衣不用宽。'),
	(7912, 532, '三姝媚（古卫舟。人谓此舟曾载钱塘宫人）', '一篷儿别苦。是谁家、花天月地儿女。紫曲藏娇，惯锦窠金翠，玉璈钟吕。绮席传宣，笑声里、龙楼三鼓。歌扇题诗，舞袖笼香，几曾尘土。 因甚留春不住。怎知道人间，匆匆今古。金屋银屏，被西风吹换，蓼汀苹渚。如此江山，应悔却、西湖歌舞。载取断云何处。江南烟雨。'),
	(7913, 532, '桂枝香（题写韵轩）', '紫薇花露，潇洒作凉云，点商勾羽。字字飞仙，下笔一帘风雨。江亭月观今如许。叹飘零、墨香千古。夕阳芳草，落花流水，依然南浦。 甚两两、凌风驾虎。恁天孙标致，月娥眉妩。一笑生春，那学世间儿女。笔床砚滴曾窥处，有西山、青眼如故。素笺寄与，玉箫声彻，凤鸣鸾舞。'),
	(7914, 86, '天香', '迟日侵阶，和风入户，朱弦欲奏还倦。一幅鸾笺，五云飞下，赐予内家琴苑。音随指动，犹仿佛、虞薰再见。妙处谁能解心，和平自无哀怨。 猩罗帕封古洗，有龙涎、渗花千片。骤睹瑶台清品，眼明如电。爇白桐窗竹几，渐缕缕腾腾细成篆。就祝金闺，天长地远。'),
	(7915, 576, '玉烛新', '梅花新霁後。正锦样华堂，一时装就。洞房花烛深深处，慢转铜壶银漏。新妆未了。奈浩荡、春心相候。香篆里、簇簇笙歌。微寒半侵罗袖。 侵晨浅捧兰汤，问堂上萱花，夜来安否。功名漫斗。漫赢得、万里相思清瘦。蓝袍俊秀。便胜却、登科龙首。春画永，帘幕重重，箫声缓奏。'),
	(7916, 576, '阮郎归（梨花）', '冰肌玉骨淡裳衣。素云翠枝。一生不晓摘仙诗。雪香应自知。 微雨後，禁烟时，洗妆君莫迟。东风不解惜妍姿。吹成蝴蝶飞。'),
	(7917, 552, '摸鱼儿（寿贾师宪）', '指庭前、翠云金雨。霏霏香满仙宇。一清透彻浑无底，秋水也无流处。君试数。此样襟怀，顿得乾坤住。闲情半许。听万物氤氲，从来形色，每向静中觑。 琪花落，相接西池寿母。年年弦月时序。荷衣菊佩寻常事，分付两山容与。天证取。此老平生，可向青天语。瑶卮缓举。要见我何心，西湖万顷，来去自鸥鹭。'),
	(7918, 218, '解珮环（寻梅不见）', '江空不渡，恨蘼芜杜若，零落无数。还道荒寒，婉娩流年，望望美人迟暮。风烟雨雪阴睛晚，更何须，春风千树。尽城、落木萧萧，日夜江声流去。 日晏山深闻笛，恐他年流落，与子同赋。事阔心违，交淡媒劳，蔓草沾衣多露。汀洲窈窕余醒寐，遗珮浮沈沣浦。有白鸥淡月，微波寄语，逍遥容与。'),
	(7919, 123, '小重山', '谢了梅花恨不禁。小楼羞独倚，暮云平。夕阳微放柳梢明。东风冷，眉岫翠寒生。 无限远山青。重重遮不断，旧离情。伤春还上去年心，怎禁得，时节又烧灯。'),
	(7920, 676, '忆秦娥', '水云幽。怕黄霜竹生新愁生新愁。生新愁如今何处，倚月明楼。 龙吟杳杳天悠悠。腾蛟起舞鸣空篌。鸣箜篌。听吹短气，江上无秋。'),
	(7921, 470, '长相思', '心悠悠。恨悠悠。谁剪青山两点愁。笙寒燕子楼。 晓星稀。暮云飞。纤就回文不下机。花飞人未归。'),
	(7922, 413, '如梦令', '竹上一楼岚翠。竹下一渠春水。中有隐人居，破屋数间而已。无事。无事。静夜月明千里。'),
	(7923, 505, '菩萨蛮', '断红流水香难觅。行云一去无踪迹。杨柳漫遮阑。闲愁付远山。 玉筝弹未了。倚柱人空老。青子摘来酸。酸心有几般。'),
	(7924, 100, '浪淘沙', '结客去登楼。谁击兰舟。半篙清涨雨初收。把酒留春春不住，柳暗江头。 老去怕闲愁。莫莫休休。晚来风恶下帘钩。试问落花随水去，还解西流。'),
	(7925, 654, '踏莎行', '江阔天低，楼高思迥。春烟蘸淡如秋景。今年芳草去年愁，分明又报明年信。燕子还来，归期未定。可堪醉梦红尘境。世间万事尽消磨，水流不尽青山影。'),
	(7926, 336, '长相思', '溪水清。溪水浑。溪上人家数亩园。垂杨深闭门。 青罗裙。白罗裙。采尽青蘩到白苹。江南三月春。'),
	(7927, 588, '法曲献仙音', '花匣么弦，象奁双陆，旧日留欢情意。梦别银屏，恨裁兰烛，香篝夜闲鸳被。料燕子重来地，桐阴琐窗绮。 倦梳洗。晕芳钿、自羞鸳镜，罗袖冷，烟柳画阑半倚。浅雨压荼コ，指东风、芳事余几。院落黄昏，怕春莺、惊笑憔悴。倩柔红约定，唤取玉箫同醉。'),
	(7928, 111, '大江东去（自寿）', '半生习气，被风霜、销尽头颅如许。七十年来都铸错，回首邯郸何处。杜曲桑麻，柴桑松菊，归计成迟暮。一樽自寿，不妨沈醉狂舞。 休问沧海桑田，看朱颜白发，转次全故。乌兔相催天也老，千古英雄坏土。汾水悲歌，雍江苦调，堕泪真儿女。兴亡一梦，大江依旧东注。'),
	(7929, 279, '桂殿秋', '漓兽石，错虬松。黛岚终日下天风。杖藜携我恣遥望，缥缈霓裳飞碧空。'),
	(7930, 146, '柳梢青（忆西湖）', '长记西湖，水光山色，浓淡相宜。丰乐楼前，涌金门外，买个船儿。而今又是春时。清梦只、孤山赋诗。绿盖芙蓉，青丝杨柳，好在苏堤。'),
	(7931, 146, '贺新郎（送易子炎运干之任）', '冷眼三边处。喜舍人、水滨跃马，上京西路。三国英雄千载矣，开胜依然如故。这勋业、向谁分付。袖里翰林风月手，也何妨、戮力风寒护。谈笑暇，诗吟虏。 岘山几载无人顾。幸如今、翦除荆棘，扫清飙雾。换得东南新局面，政欠十分著数。算人物、须还羊杜。玉帐筹边机会好，把规模、赶出中原去。天下事，书生做。'),
	(7932, 146, '沁园春（送友人补太学）', '一部周官，学间渊源，山齐得来。最雄姿直气，不涂脂粉，仙风道骨，不涴尘埃。万里青云，相期阔步，底事向天门折翼回。君知否，这白衣御史，卿相胚胎。 时人休用惊猜。机会到功名节节催。看蒲质易凋，何如松茂，菊花已老，须是梅开。万事何难，时来得做，且信天工次第排。从今去，愿径游璧水，直上兰台。'),
	(7933, 204, '应天长（花港观鱼）', '岸容浣锦，波影堕红，纤鳞巧避凫唼。禹浪未成头角，吞舟胆犹怯。湖山外，江海匝。怕自有、暗泉流接。楚天远，尺素无期，枉误停楫。 四望涌金楼，带草帟烟，缥缈际城堞。渐见暮榔敲月，轻舫乱如叶。濠梁兴，归未惬。记旧伴、袖携留折。指鱼水，总是心期，体怨三叠。'),
	(7934, 481, '霓裳中序第一', '江郊雨正歇。燕子飞来人忆别。未了残梅怨结。渺野色波光，春与天接。相思辽阔。怕柳风、吹老吟发。关情处，满汀芳草，遮莫是鶗鴂。 时节。一饷愁绝。谩料理、新翻几阕。山尊堪共谁设。梦到断桥，飞絮仍雪。岁华休省阅。早霍地、小园花发。已办著、海棠开后，独立半廊月。'),
	(7935, 537, '清玉案', '虾须帘上银钩小。筵内轻寒绕。红叶征香侵语笑。金钗珠履，凤箫龟鼓，疑是蓬莱岛。 萱堂日日春生貌。嫩绿休然鬓边好。赋得修龄应不少。年年长见，画梁双燕，楼外青袍草。'),
	(7936, 570, '济天乐', '重阳还近秋光好，银屏翠箔凉透。凤髓炉温，鱼轩瑞应，天与冰壶清透。多文在手。更赖玉腰肢，唾花衫袖。旧约梅仙，为来人世作珍偶。 虚堂帘半卷，化工何限意，妆点时昼。裛真香葵倾劝盏，都把芳心为寿。当歌对酒。愿绿发朱颜，镇长依旧。宝箓名高，已书千岁久。'),
	(7937, 570, '念奴娇', '镜天露洗，荡仁风，满县正开桃李。无讼堂深晴画永，帘卷鹅山浓翠。吏散棠阴，鸟啼花用，箫鼓弦歌地。印封碧苏，笑谈无限清致。 佳会正属悬弧，十分蕉叶，共祝千秋岁。道骨仙风元自有，功业人间游戏。名覆金瓯，班联玉笋，行庆风云会。莲舟容与，此时归继仙裔。'),
	(7938, 29, '鹧鸪天（县圃约同官赏海棠）', '万点胭脂落日烘。坐间酒面散微红。谁教艳质撩潘鬓，生怕朝云逐楚风。 寻画烛，照芳容。夜深两行锦灯笼。朱唇翠袖休凝伫，几许春情睡思中。'),
	(7939, 29, '水龙吟（老人寿词）', '夜来霜拂帘旌，淡云丽日开清晓。香貌金暖，冰壶玉嫩，佳辰寒早。橘绿橙黄，袖红裙翠，一堂欢笑。正梅妃月姊，雪肌粉面，争妆点、潇湘好。 莫惜芳尊屡倒。拥群仙、醉游蓬岛。东床俊选，南溟归信，一时俱到。鬓影摇春，命书纡锦，子孙环绕。看他时归去，飞觞石涧，侍甘泉老。'),
	(7940, 486, '念奴娇', '化工着意，向柳稍梅萼，偷回春色。蓂叶双飞政天上，一点文星初谪。发藻儒林，当年荣耀，四海声名白。谪仙飞貌，澹然物外踪迹。 且与笑傲瀛洲，风流词翰，自是西垣客。异日功成辞富贵，却与赤松游剧。一曲清歌，三千珠履，不愧非潘璧。满斟芳醑，仰称遐寿千百。'),
	(7941, 92, '感皇恩', '春水满池塘，春风吹柳。春草茸茸媚晴书。春烟骀荡，春色着人如旧。春光无限好，花时候。 春院宴开，春屏环绣。春酒争持介眉寿。春衫春暖，春回遏云声透。春年常不老，松筠茂。'),
	(7942, 197, '木兰花慢', '歌尘凝燕叠，又软语、在雕梁。记剪烛调弦，翻香校谱，学品伊凉。屏山梦云正暖，放东风、卷雨入巫阳。金冷红条孔雀，翠间彩结鸳鸯。银缸。焰冷小兰房。夜悄怯更长。待采叶题诗，含情赠远，烟水茫茫。春妍尚如旧否，料啼痕、暗里浥红妆。须觅流莺寄语，为谁老却刘郎。'),
	(7943, 237, '虞美人', '歌唇乍启尘飞处。翠叶轻轻举。似通舞态逞妖容。嫩条纤丽玉玲珑。怯秋风。 虞姬珠碎兵戈里。莫认埋魂地。只应遗恨寄芳丛。露和清泪湿轻红。古今同。'),
	(7944, 688, '木兰花慢（怀旧）', '问春春不语，谩新绿、满芳州。记历历前游，看花南陌，命酒西楼。东风翠红围绕，把功名、一笑付糟丘。醉里了忘身世，吟边自负风流。 风流。莫莫复休休。白发渐盈头。恨十载重来，略无欢意，惟有间愁。多情向人似旧，但小桃、婀娜柳纤柔。望断残霞落日，水天拍拍飞鸥。'),
	(7945, 688, '重叠金·壬寅立秋', '西风半夜惊罗扇。蛩声入梦传幽怨。碧藕试初凉。露痕啼粉香。 清冰凝簟竹。不许双鸳宿。又是五更钟。鸦啼金井桐。'),
	(7946, 688, '鹧鸪天（暮春）', '沈水香销梦半醒。斜阳恰照竹间亭，戏临小草书团扇，自拣残花插净瓶。 莺宛转，燕丁宁。晴波不动晚山青。玉人只怨春归去，不道槐云绿满庭。'),
	(7947, 688, '浣沙溪', '钟磬冷冷夜未央。梨花庭院月如霜。步虚声里拜瑶章。 紫极清都云渺渺，红尘浊世事茫茫。未知谁有返魂香。'),
	(7948, 688, '长相思（秋怀）', '天悠悠。水悠悠。月印金枢晓未收。笛声人倚楼。 芦花秋。蓼花秋。催得吴霜点鬓稠。香笺莫寄愁。'),
	(7949, 688, '行香子（梅）', '寒意方浓。暖信才通。是晴阳、暗拆花封。冰霜作骨，玉雪为容。看体清癯，香淡伫，影朦胧。孤城小驿，断角残钟。又无边、散与春风。芳心一点，幽恨千重。任雪霏霏，云漠漠，月溶溶。'),
	(7950, 386, '浪淘沙（别意）', '花雾涨冥冥。欲雨还晴。薄罗衫子正宜春。无奈今宵鸳帐里，身是行人。 别酒不须斟。难洗离情。丝鞘如电紫骝鸣。肠断画桥芳草路，月晓风清。'),
	(7951, 421, '菩萨蛮（西湖曲）', '横湖十顷琉璃碧。画桥百步通南北。沙暖睡鸳鸯。春风花草香。 间来撑小艇。割破楼台影。四面望青山。浑如蓬莱间。'),
	(7952, 618, '望海潮', '云收飞脚，日祛怒暑，新蝉高柳鸣时。兰佩紫囊，蒲抽碧剑，吴丝两腕双垂。闻道五陵儿。蛟龙吼波面，卫碎琉璃。画鼓声中，锦标争处飐红旗。 使君冠盖追。正霞翻酒浪，翠敛歌眉。扇动水，风生玉宇，微凉透入单衣。日暮楚天低。金蛇掣电漾，千顷霜溪。宴罢休燃宝蜡，凭月照人归。'),
	(7953, 396, '洞仙歌', '雕檐绮户，倚晴空如画。曾是吴王旧台榭。自浣纱去后，落日平芜，行云断，几见花开花谢。 凄凉阑干外，一簇江山，多少图王共争霸。莫间愁、金杯潋滟，对酒当歌，欢娱地、梦中兴亡休话。渐倚遍、西风晚潮生，明月里、路鹚背人飞下。'),
	(7954, 396, '乌夜啼', '无端小雪帘纤。入平檐。金鸭旋添龙饼，莫开帘。寻梅约。开还落。可曾欢。合作一年春恨，上眉尖。'),
	(7955, 473, '风流子（闻桂花怀西湖）', '天阔玉屏空。轻阴弄、淡墨画秋容。正凉挂半蟾，酒醒窗下，露催新雁，人在山中。又一片，好秋花占了，香换却西风。箫女夜归，帐栖青凤，镜娥妆冷，钗坠金虫。 西湖花深窈，间庭砌、曾占席地歌钟。载取断云归去，几处房栊。恨小帘灯暗，粟肌消瘦，薰炉烟减，珠袖玲珑。三十六宫清梦，还与谁同。'),
	(7956, 228, '桂枝香', '人间秋至。对暮雨满城，沈思如水。桐叶惊风，似语怨蛩齐起。南楼月冷曾多恨，怕而今、夜深横吹。那堪更听，萧萧槭槭，透窗摇睡。 问楚梦、间云何地。但手约轻绡，省人深意。红树池塘，谁见宿妆凝睇。旧时裘马行歌事，合都归、汀苹烟芷。思王渐老，休为明珰，沈吟洛涘。'),
	(7957, 361, '水调歌头（寿二亲）', '生日重重见，馀闰有新春。为吾母寿，富贵外物总休论。且说家怀旧话，教学也曾菽水，亲意尽欣欣。只此是真乐，乐岂在邦君。吾二老，常说与，要帘勤。庐陵几千万户，休戚属儿身。三瑞堂中绿醑，酿就满城和气，端又属人伦。吾亦老吾老，谁不敬其亲。'),
	(7958, 351, '二郎神', '露床转玉，唤睡醒、绿云梳晓。正倦立银屏，新宽衣带，生怯轻寒料峭。闷绝相思无人问，但怨入、墙阴啼鸟。嗟露屋销春，晴风暄昼，柳轻梅小。人悄。日长谩忆，秋千嬉笑。怅烬冷炉薰，花深莺静，帘箔微红醉袅。带结留诗，粉痕销帕，情远窃香年少。凝恨极，尽日凭高目断，淡烟芳草。'),
	(7959, 459, '满庭芳（答友人）', '五柳先生，宦情无几，赋成归去来兮。吾归何所，任运且随时。曾向高人问道，清妙处、已悟希夷。谁能羡，胸中芥子，容易纳须弥。 竹林，新职事，神交狂客，志慕天随。但能乐天知命，夫复何疑。多谢故人念我，平安报、不必纲维。饮君酒，愿君同寿，此外本无为。'),
	(7960, 10, '菩萨蛮（双浮亭）', '征鸿点破空云碧。丹霞染出新秋色。返照落平洲。半江红锦流。 风清鱼笛晚。寸寸愁肠断寄语笛休横。只消三两声。'),
	(7961, 10, '木兰花慢（社日有怀）', '东风吹雾雨，更吹起、夹衣寒。正莽莽丛林，潭潭伐鼓，郁郁焚兰。阑干曲、多少意，看青烟如篆绕溪湾。桑柘绿阴犹薄，杏桃红雨初翻。 飞花片片走潺湲。问何日西还。叹扰扰人生，纷纷离合，渺渺悲欢。想云軿、何处也，对芳时、应只在人间。惆怅回纹锦字，断肠斜日云山。'),
	(7962, 673, '长相思', '夜萧萧。梦萧萧。又趁杨花到谢桥。凤沉明月箫。 来迢迢。去迢迢。枉把吟笺寄寂寥。飞鸿不受招。'),
	(7963, 673, '浪淘沙（丰乐楼）', '裙色草初青。鸭绿波轻。试花霏雨湿春晴。三十六梯人不到，独唤瑶筝。 艇子忆逢迎。依旧多情。朱门只合锁娉婷。却逐彩鸾归去路，香陌春城。'),
	(7964, 420, '玉楼春（闻笛）', '红楼十二春寒恻。楼角何人吹玉笛。天津桥上旧曾听，三十六宫秋草碧。 昭华人去无消息。江上青山空晚色。一声落尽短亭花，无数行人归未得。'),
	(7965, 125, '浪淘沙', '还了酒家钱。便好安眠。大槐宫里着貂蝉。行到江南知是梦，雪压渔船。 盘礴古梅边。也信前缘。鹅黄雪白又醒然。一事最奇君吸取。明日新年。'),
	(7966, 340, '括意难忘', '蜀道登天。望峨眉横绝，石栈相连。西来当鸟道，逆浪俯回川。狷与鹤，莫攀缘。九折耸岩峦。算咫尺、扪参历井，回首长叹。 西游何日当还。听子规啼月，愁减朱颜。连峰天一握，飞瀑壑争喧。排剑阁，越天关。豺虎乱朝昏。问锦城，虽云乐土，何似家山。 白乐天庐山草堂记：匡庐奇秀，甲天下大山。山北峰日香炉，峰北寺曰遗爱寺。介峰寺间，其境胜绝，又甲庐山。元和十一年秋，太原人白乐天见而爱之，若远行客过故乡，恋恋不能去，因面峰腋寺，作为草堂。明年春，草堂成。三间两柱，二室四牖，广袤丰杀，一称心力。洞北户，来阴风，防徂暑也；敞南甍，纳阳日，虞祁寒也。木D96B而已，不加丹；墙圬而已，不加白。E041阶用石，幂■用纸，竹帘纻帏，率称是焉。堂中设木榻四，素屏二，漆琴一张、儒道佛书各三两卷。乐天既来为主，仰观山、俯听泉，旁睨竹树云石，自辰至酉，应接不暇。俄而物诱气随，外适内和，一宿体宁，再宿心恬，三宿后颓然嗒然，不知其然而然。自问其故。答曰：是居也，前有平地，轮广十丈；中有平台，半乎地；台南有方池，倍乎台。环池多山竹野杉，大仅十人围，高不知几百尺，修柯戛云，低枝拂潭，如幢竖、如盖张、如龙蛇走。松下多灌丛，萝茑叶蔓骈织，承翳日月，光不到地，盛夏风气，如八九月时。下铺白石，为出入道。堂北五步，据层崖积石，嵌空垤垤，杂木异草，盖覆其上，绿阴蒙蒙，朱实离离，不识其名，四时一色。又有飞泉植茗，就以烹DF75，好事者见，可以永日。堂东有瀑布，水悬三尺，泻阶隅、落石渠，昏晓如练色，夜中如环珮、琴、筑声。堂西倚北崖右止，以剖竹架空，引崖上泉，脉分线悬，自檐注砌，累累如贯珠，霏微如雨露，滴沥飘洒，随风远去。其四旁耳目杖履可及者，春有锦绣谷花，夏有石门涧云，秋有虎溪月，冬有炉峰雪，阴晴显晦，昏旦含吐，千变万状，不可殚幻■缕而言，故云甲庐山者。噫！凡人丰一屋，华一箦，而起居其间，尚不免有骄稳之态。今我为是物主，物至致知，各以类至，又安得不外适内和，体宁心恬哉！昔永、远、宗、雷辈十八人，同入此山，老死不返，去我千载，我知其心以是哉！矧余自思，从幼迨老，若白屋、若朱门，凡所止，虽一日二日，聊覆篑土为台，聚拳石为山，环斗水为池，其喜山水病癖如此。一日蹇剥，来佐江郡，郡守以优容抚我，庐山以灵胜待我，是天与我时，地与我所，卒获所好，又何求焉！尚以冗员所羁，馀累未尽，或往或求，未遑宁处。待余异日，弟妹婚嫁毕。司马岁秩满，出处行止，得以自遂，则必左手引妻子，右手抱琴书，终老于斯，以成就我平生之志。清泉白石，实闻此言。时三月二十七日，始居新堂。四月九日，与河南元集虚、范阳张允中、南阳张深之、东西二林长老凑公、朗、满、晦、坚等凡二十有二人，具齐施茶果以乐之，因为草堂记。'),
	(7967, 340, '括水调歌', '听说竹楼好，佳地占黄冈。月波相接，俯临江濑挹山光。急雨檐喧瀑布，密雪瓴敲碎玉，幽阒兴尤长。琴调更虚畅，诗韵转清扬。 公退暇，披鹤氅，戴华阳。手披周易，消磨世虑坐焚香。缥缈烟云竹树，迎送夕阳素月，胜概总难量。欲辨骚人事，沦茗漱清觞。 李白采莲曲：若耶溪旁采莲女，笑隔荷花共人语。日照新妆水底明，风飖香袖空中举。岸上谁家游冶郎，三三五五映垂杨。紫骝嘶入落花去，见此踌躇空断肠。'),
	(7968, 340, '括酹江月', '问陶彭泽，有田园活计，归来何晚。昨梦皆非今觉是，实迷途其未远。松菊犹存，壶觞自酌，寄傲南窗畔。闲云出岫，更看飞鸟投倦。 归去请息交游，驾言焉往，独把琴书玩。孤棹巾车邱壑趣，物与吾生何恨。宇内寓形，帝乡安所，富贵非吾愿。乐夫天命，聊乘化以归尽。 刘伯伦酒德颂：有大人先生，以天地为一朝，万期为须臾。日月为扃牖，八荒为庭衢。行无辙迹，居无室庐。幕天席地，纵意所如。止则操■执觚，动则挈榼提壶。维酒是务，焉知其馀。有贵介公子，搢绅处士。闻吾风声，议其所以。乃奋袂攘衿，怒目切齿。陈说礼法，是非锋起。先生于是方捧罂承槽，衔杯漱醪。奋髯箕踞，枕麹藉糟。无思无虑，其乐陶陶。兀然而醉，恍然而醒。静听不闻雷霆之声，熟视不睹泰山之形。不觉寒暑之切肌，嗜欲之感情。俯观万物，扰扰焉如江汉之浮萍。二豪侍侧焉，如蜾裸之与螟蛉。'),
	(7969, 44, '望江南', '娑婆苦，身世一浮萍。蚊蚋睫中争小利，蜗牛角上窃虚名。一点气难平。 人我盛，日夜长无明。地狱争头成队入，西方无个肯修行。空死复空生。争一作尽。'),
	(7970, 88, '渔家傲', '枝边。 佛赞西方经现在。广长舌相三千界。为要众生生信解。临终迈。不修净业犹何待。 七宝池塘波一派。莲华朵朵车轮大。华内托生真自在。分三辈。阿鞞跋致长无退。'),
	(7971, 280, '风流子', '双燕立虹梁。东风外、烟雨湿流光。望芳草云连，怕经南浦，葡萄波涨，怎博西凉。空记省，浅妆眉晕敛，罥袖唾痕香。春满绮罗，小莺捎蝶，夜留弦索，么凤求凰。 江湖飘零久，频回首、无奈触绪难忘。谁信温柔牢落，翻坠愁乡。仗玉笺铜爵，花间陶写，宝钗金镜，月底平章。十二主家楼苑，应念萧郎。'),
	(7972, 651, '鹊桥仙（赠蛰子沙道昭）', '红莲含蕊，露珠凝碧，飞落华池滴滴。运归金鼎唤丁公，炼得似、一枚朱橘。 三花喷火，五云拥月，上有金胎神室。洞房云雨正春风，十个月、胎仙了毕。'),
	(7973, 233, '望江南', '石屏老，长忆少年游。自谓虎头须食肉，谁知猿臂不封侯。身世一虚舟。 平生事，说着也堪羞。四海九州双脚底，千愁万恨两眉头。白发早归休。'),
	(7974, 233, '望江南', '壶山好，也解忆狂夫。转首便成千里别，轻年不寄一行书。浑似不相疏。 催归曲，一唱一愁予。有剑卖来酤酒吃，无钱归去买山居。安处即吾庐。壶山有催归曲赠仆，甚妙。'),
	(7975, 233, '望江南', '有说未尽处，为续四曲 壶山好，博古又通今。结屋三间藏万卷，挥毫一字直千金。四海有知音。 门外路，咫尺是湖阴。万柳堤边行处乐，百花洲上醉时吟。不负一生心。'),
	(7976, 233, '沁园春', '一曲狂歌，有百馀言，说尽一生。费十年灯火，读书读史，四方奔走，求利求名。蹭蹬归来，闭门独坐，赢得穷吟诗句清。夫诗者，皆吾侬平日，愁叹之声。 空馀豪气峥嵘。安得良田二顷耕。向临邛涤器，可怜司马，成都卖卜，谁识君平。分则宜然，吾何敢怨，蝼蚁逍遥戴粒行。开怀抱，有青梅荐酒，绿树啼莺。'),
	(7977, 120, '雨中花', '眷浓恩重，长离永别，凭谁为返香魂。忆湘裙霞袖，杏脸樱唇。眉扫春山淡淡，眼裁秋水盈盈。便如何忘得，温柔情态，恬静天真。 凭栏念及，夕阳西下，幕烟四起江村。渐入夜、疏星映柳，新月笼云。酝造一生清瘦，能消几个黄昏。断肠时候，帘垂深院，人掩重门。'),
	(7978, 679, '莺啼序', '屈原九歌东皇太一，春之神也。其词凄惋，含意无穷。略采其意，以度春曲。 青旂报春来了，玉鳞鳞风旎。陈瑶席、新奏琳琅，窈窕来荐嘉祉。桂酒洗琼芳，丽景晖晖，日夜催红紫。湛青阳新沐，人声澹荡花里。 光汛崇兰，坼遍桃李，把深心料理。共携手、蘅室兰房，奈何新恨如此。对佳时、芳情脉脉，眉黛蹙、羞搴琼珥。折微馨、聊寄相思，莫愁如水。 青苹再转，淑思菲菲，春又过半矣。细雨湿香尘，未晓又止。莫教一鴂无聊，群芳亹亹。伤情漠漠，泪痕轻洗。曲琼桂帐流苏暖，望美人、又是论千里。佳期杳缈，香风不肯为媒，可堪玩此芳芷。春今渐歇，不忍零花，犹恋馀绮。度美曲、造新声，乐莫乐此新知。思美人兮，有花同倚。年华做了，功成如委。天时相代何日已。怅春功、非与他时比。殷勤举酒酬春，春若能留，□还亦喜。'),
	(7979, 542, '卜算子（赠妓）', '双桨浪花平，夹岸青山锁。你自归家我自归，说着如何过。 我断不思量，你莫思量我。将你从前与我心，付与他人可。'),
	(7980, 68, '清平乐', '鸳鸯翡翠。小小池塘水。落絮游丝花满地。度日阑干独倚。金刀裁就春衫。起来初试轻寒。满把相思清恨，题诗欲寄江南。'),
	(7981, 519, '好事近（送春）', '日日惜春残，春去更无明日。拟把醉同春住，又醒来岑寂。 明年不怕不逢春，娇春怕无力。待向灯前休睡，与留连今夕。'),
	(7982, 160, '浪淘沙', '我有一张琴。随坐随行。无弦胜似有弦声。欲对人前弹一曲，不遇知音。 夜静响琤轰。神鬼俱惊。惊天动地若雷鸣。只候功成归去后，携向蓬瀛。'),
	(7983, 592, '清平乐（渔父）', '阵鸿惊处。一网沉江渚。落叶乱风和细雨。拨棹不如归去。 芦花轻泛微澜。蓬窗独自清闲。一觉游仙好梦，任它竹冷松寒。'),
	(7984, 678, '海棠春（春景）', '柳腰暗怯花风弱。红映秋千院落。归逐燕儿飞，斜撼真珠箔。 满林翠叶胭脂萼。不忍频频觑着。护取一庭春，莫弹花间鹊。'),
	(7985, 354, '满江红（寿吴漕·正月十五）', '恰则元宵，灿万灯、星球如昼。春乍暖、化工未放，十分花柳。和气并随灯夕至，一时钟作人间秀。问烟霄、直上舌含香，文摛绣。 命世杰，调元手。荆楚地，淹留欠。看日边追诏，印垂金斗。翠竹苍松身逾健，蛾儿雪竹论人如旧。愿湘江、卷入玉壶中，为公寿。'),
	(7986, 224, '一剪梅', '道学从来不则声。行也东铭。坐也西铭。爷娘死后更伶仃。也不看经。也不斋僧。 却言渊子太狂生。行也轻轻。坐也轻轻。他年青史总无名。你也能亨。我也能亨。'),
	(7987, 540, '浪淘沙', '黄道雨初干。霁霭空蟠。东风杨柳碧毵毵。燕子不归花有恨，小院春寒。倦客亦何堪。尘满征衫。明朝野水几重山。归梦已随芳草绿，先到江南。'),
	(7988, 168, '瑞鹧鸪（海棠）', '两行芳蕊傍溪阴。一笑嫣然抵万金。火齐照林光灼灼，彤霞射水影沉沉。晓妆无力胭脂重，春醉方酣酒晕深。定自格高难着句，不应工部总无心。'),
	(7989, 441, '霜天晓角', '峨眉仙客。四海文章伯。来向东坡游戏，人间世、著不得。去国谁爱惜。在天何处觅。但见尊前人唱，前赤壁、后赤壁。'),
	(7990, 512, '满庭霜（和前）', '归去来兮，家林不远，梦魂飞绕烟峰。洞房花木，只在小池东。谁道云深无路，小桥外、一径相通。功名小，从教群蚁，鏖战大槐宫。 故人，书夜到，秫田百亩，已兆年丰。把乌程烂醉，不数郫筒。醉后村歌正舞，团圞坐、一笑春风。洪崖伴，定驱鸾鹤，时一访衰翁。'),
	(7991, 101, '如梦令', '谁伴明窗独坐。和我影儿两个。灯烬欲眠时，影也把人抛躲。无那。无那。好个恓惶的我。'),
	(7992, 101, '如梦令（书百方观音寺壁）', '直面雨轻风峭。极目水空烟渺。家在武陵溪，无限壑讥峰诮。归好。归好。睡足一江春晓。'),
	(7993, 381, '渔父词', '此事楞严常露布。梅华雪月交光处。一笑寥寥空万古。风瓯语。迥然银汉横天宇。蝶梦南华方栩栩。斑斑谁跨丰干虎。而今忘却来时路。江山暮。天涯目送鸿飞去。'),
	(7994, 246, '鹧鸪天', '笑擘黄柑酒半醒。玉壶金斗夜生冰。开窗尽见千山雪，雪未消时月正明。兰烬短，麝煤轻。画楼钟鼓已三更。倚栏谁唱清真曲，人与梅花一样清。'),
	(7995, 181, '如梦令（秋怀）', '送过雕梁旧燕。听到妆楼新雁。菊讯一何迟，倒尽清樽谁伴。魂断。魂断。人与暮云俱远。'),
	(7996, 195, '少年游', '含羞倚醉不成歌。纤手掩香罗。偎花映烛，偷传深意，酒思入横波。 看朱成碧心迷乱，翻脉脉、敛双蛾。相见时稀隔别多。又春尽、奈愁何。'),
	(7997, 697, '蝶恋花', '古往今来忙里过。今古清光，静照人行道。难似素娥长见好。见频只是催人老。 欲驻征轮无计那。世上多情，却被无情恼。夜夜鸟飞谁识破。满头空恨霜华早。'),
	(7998, 616, '醉翁操（效东坡）', '予甥法真祥师以子瞻内相所作醉翁操见寄。予以为未工也，倚其声作之，写呈法真，知可意否。谢山醉吟先生书。 冷冷潺潺。寒泉。泻云间。如弹。醉翁洗心逃区环。自期猿鹤俱闲。情未阑。日暮造深原。异芳谁与搴。忘还。泛声同。 琼楼玉阙，归去何年。遗风馀思，犹有猿吟鹤怨。花落溪边。萧然。莺语林中清圆。空山。春又残。客怀文章仙。度曲响涓涓。清商回徵星斗寒。'),
	(7999, 439, '鹊桥仙（吕使君饯会）', '教来歌舞，接成桃李。尽是使君指似。如今装就满城春，忍便拥、双旌归去。 莺心巧啭，花心争吐。无计可留君住。两堤芳草一江云，早晚是、西楼望处。'),
	(8000, 433, '减字木兰花', '华筵布巧。绿绕红□花枝闹。朵朵风流。好向尊前插满头。 此花妖艳，愿得年年长相见。满劝金钟，祝寿如花岁岁红。'),
	(8001, 433, '浪淘沙（杨梅）', '素手不晶盘。垒起仙丸。红绡剪碎却成团。逗得安排金粟遍，何似鸡冠。味胜玉浆寒。只被宜酸。莫将荔子一般看。色淡香消僝僽损，才到长安。'),
	(8002, 137, '渔家傲', '本是潇湘一钓客。自东自西自南北。只把孤舟为屋宅。无宽窄。幕天席地人难测。 顷闻四海停戈革。金门懒去投书册。时向滩头歌月白。真高格。浮名浮利谁拘得。'),
	(8003, 101, '虞美人（临安客店）', '酒阑欹枕新凉夜。断尽人肠也。西风吹起许多愁。不道沉腰潘鬓、不禁秋。如今病也无人管。真个难消遣。东邻一笑直千金。争奈茂陵情分、在文君。'),
	(8004, 61, '如梦令', '休絮。休絮。我自明朝归去。'),
	(8005, 140, '南乡子', '洪迈被拘留。稽首垂哀告彼酋。七日忍饥犹不耐，堪羞。苏武争禁十九秋。厥父既无谋。厥子安能解国忧。万里归来夸舌辨，村牛。好摆头时便摆头。'),
	(8006, 17, '诉衷情（春词）', '长桥春水拍堤沙。疏雨带残霞。几声脆管何处桥下有人家。宫树绿，晚烟斜。噪闲鸦。山光无尽，水风长在，满面杨花。'),
	(8007, 492, '西江月', '韩愈文章盖世，谢安情性风流。良辰美景在西楼。敢劝一卮芳酒。 记得南宫高第，弟兄争占鳌头。金炉玉殿瑞烟浮。高占甲科第九。'),
	(8008, 697, '蝶恋花（劝酒致语）', '适来已陈十二短章，辄歌三五盛景。累累清韵，尚渐梁上之飞尘；抑抑佳宾，须作乡中之醉客。同乐当勤于今夕，相从或系于他年。更赋幽情，再声佳咏。 万籁无声天地静。清抱朱弦，不愧丹霄镜。照到林梢风有信。抬头疑是梅花领。 万感只应闲对景。独倚危栏，扰扰人初定。吟不尽中愁不尽。溪山千古沈沈影。'),
	(8009, 697, '蝶恋花', '杳杳晴虚寒漫漫。放下尘劳，相共游银汉。便入醉乡休浩叹。神仙只在云门馆。饮兴偏宜流水畔。时有红蕖，落在黄金盏。鹭未忘机移别岸。画船更上前汀看。'),
	(8010, 485, '点绛唇', '独自临池，闷来强把阑干凭。旧愁新恨。耗却年时兴。鹭散鱼潜，烟敛风初定。波心静。照人如镜。少个年时影。'),
	(8011, 648, '无愁可解', '天下传咏，以谓几于达者。龙丘子犹笑之。此虽免乎愁，犹有所解也。若夫游于自然而托于不得已，人乐亦乐，人愁亦愁，彼且恶乎解哉。乃反其词，作无愁可解云： 光景百年，看便一世，生来不识愁味。问愁何处来，更开解个甚底。万事从来风过耳。何用不著心里。你唤做、展却眉头，便是达者，也则恐未。此理。本不通言，何曾道、欢游胜如名利。道即浑是错，不道如何即是。这里元无我与你。甚唤做、物情之外。若须待醉了、方开解时，问无酒、怎生醉。'),
	(8012, 434, '花心动（蜡梅）', '春欲来时，看雪里、新梅品流珍绝。气韵楚江，颜色中央，数朵巧镕香蜡。嫩苞珠泪圆金烛，娇腮润、蜂房微缺。画栏悄，佳人道妆，醉吟风月。 淡白轻红谩说。算何事、东君用心偏别。赋与异姿，添与清香，堪向苦寒时节。但教开后金尊满，休惆怅、落时歌阕。断肠也，繁枝为谁赠折。'),
	(8013, 157, '离亭燕', '一带江山如画。景物向秋潇洒。水浸碧天何处断，霁色冷光相射。橘树荻花洲，掩映竹篱茅舍。天际客帆高挂。烟外酒旗低亚。多少六朝兴废事，尽入渔樵闲话。怅望倚层楼，红日无言西下。'),
	(8014, 433, '临江仙（离怀）', '别岸相逢何草草，扁舟两岸垂杨。绣屏珠箔绮香囊。酒深歌拍缓，愁入翠眉长。 燕子归来人去也，此时无奈昏黄。桃花应是我心肠。不禁微雨，流泪湿红妆。'),
	(8015, 433, '江城梅花引', '年年江上见寒梅。暗香来。为谁开。疑是月宫、仙子下瑶台。冷艳一枝春在手，故人远，相思寄与谁。怨极恨极嗅香蕊。念此情，家万里。暮霞散绮。楚天碧、片片轻飞。为我多情，特地点征衣。花易飘零人易老，正心碎，那堪塞管吹。'),
	(8016, 433, '忆黄梅', '枝上叶儿未展。已有坠红千片。春意怎生防，怎不怨。被我安排，矮牙床斗帐，和娇艳。移在花业里面。 请君看。惹清香，偎媚暖。爱香爱暖金杯满。问春怎管。大家拚、便做东风，总吹交零乱。犹肯自、轮我鸳鸯一半。'),
	(8017, 306, '失调名', '杨花落。燕子穿朱阁。苦恨春醪如水薄。闲愁无处著。 去年今日王陵舍，鼓角秋风。千岁辽东。回首人间万事空。'),
	(8018, 491, '踏莎行（寄姊妹）', '孤馆深沈，晓寒天气。解鞍独自阑干倚。暗香浮动月黄昏，落梅风送沾衣袂。待写红笺，凭谁与寄。先教觅取嬉游地。到家正是早春时，小桃花下拚沉醉。'),
	(8019, 522, '好事近', '瑞雪满京都，宫殿尽成银阙。常对素光遥望，是江梅时节。 如今江上见寒梅，幽香自清绝。重看落英残艳，想飘零如雪。'),
	(8020, 672, '安阳好', '安阳好，戟户使君宫。白昼锦衣清宴处，铁楹丹榭画图中。壁记旧三公。 棠讼悄，池馆北园通。夏夜泉声来枕簟，春来花气透帘栊。行乐兴何穷。'),
	(8021, 428, '望江南（十）', '江南雪，轻素剪云端。琼树忽惊春意早，梅花偏觉晓香寒。冷影褫清欢。蟾玉迥，清夜好重看。谢女联诗衾翠幕，子猷乘兴泛平澜。空惜舞英残。'),
	(8022, 428, '望江南（八·江乡）', '江南岸，云树半晴阴。帆去帆来天亦老，潮生潮落日还沉。南北别离心。 兴废事，千古一沾襟。山下孤烟渔市晓，柳边疏雨酒家深。行客莫登临。'),
	(8023, 428, '望江南（五）', '江南草，如种复如描。深映落花莺舌乱，绿迷南浦客魂销。日日斗青袍。风欲转，柔态不胜娇。远翠天涯经夜雨，冷痕沙上带昏潮。谁梦与兰苕。'),
	(8024, 348, '玉楼春', '天然不比花含粉。约月眉黄春色嫩。小桥低映欲迷人，闲倚东风无奈困。 烟姿最与章台近。冉冉千丝谁结恨。狂莺来往恋芳阴，不道风流真能尽。'),
	(8025, 170, '水调歌头（和苏子美）', '万顷太湖上，朝暮浸寒光。吴王去后，台榭千古锁悲凉。谁信蓬山仙子，天与经纶才器，等闲厌名僵。敛翼下霄汉，雅意在沧浪。 晚秋里，烟寂静，雨微凉。危亭好景，佳树修竹绕回塘。不用移舟酌酒，自有青山绿水，掩映似潇湘。莫问平生意，别有好思量。'),
	(8026, 551, '木兰花令', '都城水绿嬉游处。仙棹往来人笑语。红随远浪泛桃花，雪散平堤飞柳絮。东君欲共春归去。一阵狂风和骤雨。碧油红旆锦障泥，斜日画桥芳草路。'),
	(8027, 543, '夜行船·别情', '昨夜佳期初共。鬓云低、翠翘金凤。尊前和笑不成歌，意偷转、眼波微送。 草草不容成楚梦。渐寒深、翠帘霜重。相看送到断肠时，月西斜、画楼钟动。'),
	(8028, 284, '蝶恋花（佳人）', '贴鬓香云双绾绿。柳弱花娇，一点春心足。不肯玉箫闲度曲。恼人特把青蛾蹙。 静夜溪桥霜薄屋。独影行歌，惊起双鸾宿。愁破酒阑闺梦熟。月斜窗外风敲竹。'),
	(8029, 284, '六州歌头', '秦亡草昧，刘项起吞并。鞭寰宇。驱龙虎。扫欃枪。斩长鲸。血染中原战。视馀耳，皆鹰犬。平祸乱。归炎汉。势奔倾。兵散月明。风急旌旗乱，刁斗三更。共虞姬相对，泣听楚歌声。玉帐魂惊。 泪盈盈。念花无主。凝愁苦。挥雪刃，掩泉扃。时不利。骓不逝。闲阴陵。叱追兵。鸣暗摧天地，望归路，忍偷生。功盖世，何处见遗灵。江静水寒烟冷，波纹细、古木凋零。遣行人到此，追念益伤情。胜负难凭。'),
	(8030, 490, '越江吟', '神仙神仙瑶池宴。片片。碧桃零落春风晚。翠云开处，隐隐金舆挽。玉麟背冷清风远。'),
	(8031, 404, '点绛唇（寒香水仙）', '清夜沉沉，携来深院柔枝小。俪兰开巧。雪里乘风袅。 温室寒祛，旖旎仙姿早。看成好。花仙欢笑。不管年华老。'),
	(8032, 404, '点绛唇（细香竹）', '秀色娟娟，最宜雨沐风梳际。径幽香细。草滴青襟袂。 一日才无，便觉生尘态。轩窗外。数竿相对。不减王猷爱。'),
	(8033, 404, '点绛唇（国香兰）', '芳友依依，结根遥向深林外。国香风递。始见殊萧艾。 雅操幽姿，不怕无人采，堪纫佩。灵均千载。九畹遗芳在。'),
	(8034, 404, '点绛唇（咏十八香·异香牡丹）', '庭院深深，异香一片来天上。傲春迟放。百卉皆推让。 忆昔西都，姚魏声名旺堪惆怅。醉翁何往。谁与花标榜。'),
	(8035, 39, '剔银灯', '小院烟凉雨细。正好恹恹春睡。蓦被金枝，连推绣枕，报道皇都书至。良人得意。集英殿、首攀仙桂。斗帐重襟惊起。斜倚屏山偷喜。宝髻慵梳，香笺折破，果见中、高高名第。秦楼十二。知他向、谁家沉醉。'),
	(8036, 580, '渔父词（并序·其一）', '绍兴元年七月十日，余至会稽，因览黄庭坚所书张志和渔父词十五首，戏同其韵，赐辛永宗。 一湖春水夜来生。几叠春山远更横。烟艇小，钓丝轻。赢得闲中万古名。'),
	(8037, 478, '水龙吟', '玉梅街腊传香，瑞蓂秀荚开三四。莲花沉漏，熊罴占应，洛阳名裔。岁比甘罗，便疏同队，累棋观志。向修文寓直，仙楼侍宴，梁王宝、真难俪。 多少襟怀未试，暂超然、壶中游戏。行看献策，归瞻旒藻，常勋游记。歌畔巫云，舞回邹管，金钗扶醉。有阴功不乞，丹砂十纪，寿祺全畀。'),
	(8038, 487, '失调名（答外）', '奴启情人勿见罪。闲将小书作尹字。情人不解其中意。问伊间别几多时。身边少个人儿。'),
	(8039, 487, '伊川令（寄外）', '西风昨夜穿帘幕。闺院添消索。最是梧桐零落。迤逦秋光过却。 人情音信难托。鱼雁成耽阁教奴独自守空房，泪珠与、灯花共落。'),
	(8040, 546, '长相思令', '旧燕初归，梨花满院，迤逦天气融和。新晴巷陌，是处轻车骏马，禊饮笙歌。旧赏人非，对佳时、一向乐少愁多。远意沉沉，幽闺独自颦蛾。 正消黯、无言自感，凭高远意，空寄烟波。从来美事，因甚天教，两处多磨。开怀强笑，向新来、宽却衣罗。似恁他、人怪憔悴，甘心总为伊呵。'),
	(8041, 606, '一剪梅', '汉粉重番内样妆。新染冰肌，浅浅莺黄。广寒宫迥阻归期，襟袖空馀黯淡香。 江路迢迢楚塞长。梦里题诗欲寄将。觉来斜月又沉西，一点檀心，半染微霜。'),
	(8042, 320, '调笑令', '双绿。淡匀拂。两脸春融光透玉。起来却怕东风触。本是一团香玉。飞鸾台上看未足。贮向阿娇金屋。'),
	(8043, 442, '探春', '小雨分山，断云镂日，丹青欢状清晓。柳眼窥晴，梅妆迎暖，林外幽禽啼早。烟径润如酥，正浓淡遥看堤草。望中新景无穷，最是一年春好。 骄马黄金络脑。争探得东君，何处先到。万盏飞觞，千金倚玉，不肯轻辜年少。桃李怯残寒，半吐芳心犹小。谩教蜂蝶多情，未应知道。'),
	(8044, 4, '别瑶姬慢', '可惜香红。又一番骤雨，几阵狂风。霎时留不住，便夜来和月，飞过帘栊。离愁未了，酒病相仍，便堪此恨中。片片随、流水斜阳去，各自西东。 又还是、九十春光，误双飞戏蝶，并采游蜂。人生能几许，细算来何物，得似情浓。沈腰暗减，潘鬓先秋，寸心不易供。望暮云、千里沉沉障翠峰。'),
	(8045, 4, '明月照高楼慢（中秋应制）', '平分素商。四垂翠幕，斜界银潢。颢气通建章。正烟澄练色，露洗水光。明映波融太液，影随帘挂披香。楼观壮丽，附霁云、耀绀碧相望。 宫妆。三千从赭黄。万年世代，一部笙簧。夜宴花漏长。乍莺歌断续，燕舞回翔。玉座频燃绛蜡，素娥重按霓裳。还是共唱御制词，送御觞。'),
	(8046, 129, '蓦山溪（墨梅、荆楚间鸳鸯梅、赋此）', '江南春信，望断人千里。魂梦入花枝，染相思、同心并蒂。鸳鸯名字，赢得一双双，无限意。凝烟水。念远教谁寄。 毫端写兴，莫把丹青拟。墨客要卿卿，想临池、等闲梳洗。香衣黯淡，元不涴缁尘，怜缟袂。东风里。只恐于飞起。'),
	(8047, 66, '菩萨蛮（四首·春）', '湿花春雨如珠泣。泣珠如雨春花湿。花枕并欹斜。斜欹并枕花。 织文回字密。密字回文织。嗟更数年华。华年数更嗟。'),
	(8048, 66, '八宝妆', '门掩黄昏，画堂人寂，暮雨乍收残暑。帘卷疏星门户悄，隐隐严城钟鼓。空街烟暝半开，斜日朦胧，银河澄淡风凄楚。还是凤楼人远，桃源无路。 惆怅夜久星繁，碧空望断，玉箫声在何处。念谁伴、茜裙翠袖，共携手、瑶台归去。对修竹、森森院宇。曲屏香暖凝沈炷。问对酒当歌，情怀记得刘郎否。'),
	(8049, 216, '声声慢', '长天霞散，远浦潮平，危阑注目江皋。长记年年荣遇，同是今朝。金銮两回命相，对清光、频许挥毫。雍容久，正茶杯初赐，香袖时飘。 归去玉堂深夜，泥封罢，金莲一寸才烧。帝语丁宁，曾被华衮亲褒。如今谩劳梦想，叹尘踪、杳隔仙鳌。无聊意，强当歌对酒怎消。'),
	(8050, 558, '感皇恩', '骑马踏红尘，长安重到。人面依前似花好。旧欢才展，又被新愁分了。未成云雨梦，巫山晓。 千里断肠，关山古道。回首高城似天杳。满怀离恨，付与落花啼鸟。故人何处也，青春老。'),
	(8051, 151, '惜馀春慢（情景）', '弄月馀花，团风轻絮，露湿池塘春草。莺莺恋友，燕燕将雏，惆怅睡残清晓。还似初相见时，携手旗亭，酒香梅小。向登临长是，伤春滋味，泪弹多少。 因甚却、轻许风流，终非长久，又说分飞烦恼。罗衣瘦损，绣被香消，那更乱红如扫。门外无穷路岐，天若有情，和天须老。念高唐归梦，凄凉何处，水流云绕。'),
	(8052, 294, '品令', '唱歌须是，玉人檀口，皓齿冰肤。意传心事，语娇声颤，字如贯珠。老翁虽是解歌，无奈雪鬓霜须。大家且道，是伊模样，怎如念奴。'),
	(8053, 652, '沁园春（次白兰谷韵）', '浪迹烟霞，有酒千钟，有书五车。任从来萧散，闲心似水，何堪妩媚，笑面如花。濯发沧浪，放歌江海，肯被红尘半点遮。谁知道，抱无名钜璞，重价难赊。 嘻嗟。大泽龙蛇。且蟠屈、深潜得计些。看淋漓醉墨，神情自足，摩挲雄剑，肝胆无邪。渭水烟蓑，营丘绣衮，出处何尝有异耶。今何在，但素蟾东出，红日西斜。'),
	(8054, 58, '满江红（和李圆峤话别）', '南浦绿波，只断送、行人行色。虽只是、鹏搏九万，天池春碧。鸾侣凤朋争快睹，鸥盟鹭宿空曾识。到玉堂、天上念西江，今非昔。 公去也，宁怀别。人感旧，情空切。但岁寒松柏，相期茂悦。好在莫偿尘土债，风流宁可金门客。俯人间、大暑少清风，多炎热。'),
	(8055, 202, '南乡子', '野色一桥分。活水流云直到门。落叶堆篱从不扫，开尊。醉里教儿诵楚文。 隔断马蹄痕。商鼎熏花独自闻。吟思更添清绝处，黄昏。月白枝寒雪满村。'),
	(8056, 202, '渔歌子', '□□□年酒半酣。知鱼知我静中参。峰六六，径三三。此怀难与俗人谈。'),
	(8057, 202, '渔歌子', '□□□□□孤村。路隔尘寰水到门。斜照散，远云昏。白鹭飞来老树根。'),
	(8058, 202, '渔歌子', '□□□□□浮家。篷底光阴鬓未华。停短棹，舣平沙。流来恐是杏坛花。'),
	(8059, 202, '渔歌子', '□□□□濯尘缨。严濑磻溪有重轻。多少事，古今情。今人当似古人清。'),
	(8060, 202, '渔歌子', '□□□□□溪流。紧系篱边一叶舟。沽酒去，闭门休。从此清闲不属鸥。'),
	(8061, 202, '声声慢（寄叶书隐）', '百花洲畔，十里湖边，沙鸥未许盟寒。旧隐琴书，犹记渭水长安。苍云数千万叠，却依然、一笑人间。似梦里，对清尊白发，秉烛更兰。 渺渺烟波无际，唤扁舟欲去，且与凭兰。此别何如，能消几度阳关。江南又听夜雨，怕梅花、零落孤山。归最好，甚闲人、犹自未闲。'),
	(8062, 202, '壶中天（寿月溪）', '波明昼锦，看芳莲迎晓，风弄晴碧。乔木千年长润屋，清荫图书琴桑攥虾须帘卷，瑶草秋无色。和熏兰麝，彩衣欢拥诗伯。 溪上燕往鸥还，笔床茶灶，筇竹随游屐。闲似神仙闲最好，未必如今闲得。书染芝香，驿传梅信，次第来云北。金尊须满，月光长照歌席。'),
	(8063, 202, '祝英台近（题陆壶天水墨兰石）', '带飘飘，衣楚楚。空谷饮甘露。一转花风，萧艾遽如许。细看息影云根，淡然诗思，曾□被、生香轻误。 此中趣。能消几笔幽奇，羞掩众芳谱。薜老苔荒，山鬼竟无语。梦游忘了江南，故人何处，听一片、潇湘夜雨。'),
	(8064, 202, '浪淘沙', '寒食不多时。燕燕才归。杏花零落水痕肥。浅碧分山初过雨，一霎晴晖。 闲折小桃枝。蝶也相随。晚妆不合整蛾眉。蓦忽思量张敞画，又被愁知。'),
	(8065, 202, '浪淘沙（题许由掷瓢手卷）', '拂袖入山阿。深隐松萝。掬流洗耳厌尘多。石上一般清意味，不羡渔蓑。 日月静中过。俗□消磨。风瓢分付与清波。却笑唐求因底事，无奈诗何。'),
	(8066, 202, '解语花（吴子云家姬，号爱菊，善歌舞，忽有朝云之感，作此以寄。）', '行歌趁月，唤酒延秋，多买莺莺笑。蕊枝娇小。浑无奈、一掬醉乡怀抱。筹花斗草。几曾放、好春闲了。芳意阑，可惜香心，一夜酸风扫。 海上仙山缥缈。问玉环何事，苦无分晓。旧愁空杳。蓝桥路、深掩半庭斜照。余情暗恼。都缘是、那时年少。惊梦回、懒说相思，毕竟如今老。'),
	(8067, 202, '声声慢', '旧游编否 穿花省路，傍竹寻邻，如何故隐都荒。问取堤边，因甚减却垂杨。消磨纵然未尽，满烟波、添了斜阳。空叹息，又翻成无限，杜老凄凉。 一舸清风何处，把秦山晋水，分贮诗囊。发已飘飘，休问岁晚空江。松陵试招旧隐，怕白鸥、犹识清狂。渐溯远，望并州、却是故乡。'),
	(8068, 202, '探春慢（雪霁）', '银浦流云，绿房迎晓，一抹墙腰月淡。暖玉生烟，悬冰解冻，碎滴瑶阶如霰。才放些晴意，早瘦了、梅花一半。也知不做花看，东风何事吹散。 摇落似成秋苑。甚酿得春来，怕教春见。野渡舟回，前村门掩，应是不胜清怨。次第寻芳去，灞桥外、蕙香波暖。犹妒檐声，看灯人在深院。'),
	(8069, 202, '祝英台近（与周草窗话旧）', '水痕深，花信足，寂寞汉南树。转首青阴，芳事顿如许。不知多少消魂，夜来风雨。犹梦到、断红流处。 最无据。长年息影空山，愁入庾郎句。玉老田荒，心事已迟暮。几回听得啼鹃，不如归去。终不似、旧时鹦鹉。'),
	(8070, 202, '忆旧游（大都长春宫，即旧之太极宫也）', '看方壶拥翠，太极垂光，积雪初晴。阊阖开黄道，正绿章封事，飞上层青。古台半压琪树，引袖拂寒星。见玉冷闲坡，金明邃宇，人往深清。 幽寻。自来去，对华表千年，天籁无声。别有长生路，看花开花落，何处无春。露台深锁丹气，隔水唤青禽。尚记得归时，鹤衣散影都是云。'),
	(8071, 515, '秋夜雨', '红麟不暖瓶笙噎。炉灰一片晴雪。醉无香嗅醒，但手把、新橙闲撧。 更深冻损梅花也，听画堂、箫鼓方歇。想是天气别。豫借与、春风三月。'),
	(8072, 515, '白苎', '正春晴，又春冷，云低欲落。琼苞未剖，早是东风作恶。旋安排、一双银蒜镇罗幕。幽壑。水生漪，皱嫩绿、潜鳞初跃。愔愔门巷，桃树红才约略。知甚时，霁华烘破青青萼。 忆昨。□□□□，引蝶花边，近来重见，身学垂杨瘦削。问小翠眉山，为谁攒却。斜阳院宇，任蛛丝罥遍，玉筝弦索。户外惟闻，放翦刀声，深在妆阁。料想裁缝，白苎春衫薄。'),
	(8073, 515, '最高楼（催春）', '新春景，明媚在何时。宜早不宜迟。软尘巷陌青油幰，重帘深院画罗衣。要些儿，晴日照，暖风吹。 一片片、雪儿休要下。一点点、雨儿休要洒。才恁地，越愆期。悠悠下趁梅花到，匆匆枉带柳花飞。倩黄莺，将我语，报春归。'),
	(8074, 515, '绛都春', '春愁怎画。正莺背带雪，酴醿花谢。细雨院深，淡月廊斜重帘挂。归时记约烧灯夜。早拆尽、秋千红架。纵然归近，风光又是，翠阴初夏。 娅姹。嚬青泫白，恨玉佩罢舞，芳尘凝榭。几拟倩人，付与兰香秋罗帕。知他堕策斜拢马。在底处、垂杨楼下。无言暗拥娇鬟，凤钗溜也。'),
	(8075, 515, '昼锦堂（荷花）', '染柳烟消，敲菰雨断，历历犹寄斜阳。掩冉玉妃芳袂，拥出灵场。倩他鸳鸯来寄语，驻君舴艋亦何妨。渔榔静，独奏棹歌，邀妃试酌清觞。 湖上云渐暝，秋浩荡，鲜风支尽蝉粮。赠我非环非佩，万斛生香。半蜗茅屋归吹影，数螺苔石压波光。鸳鸯笑，何似且留双楫，翠隐红藏。'),
	(8076, 699, '一翦梅（菊酒）', '小小黄花尔许愁。楚事悠悠。晋事悠悠。荒芜三径渺中洲。开几番秋。落几番秋。 不是孤芳万古留。餐亦堪羞。采亦堪羞。离骚赋罢酒新篘。醒也风流。醉也风流。'),
	(8077, 699, '八声甘州（金陵怀古）', '恨巨灵、多事凿长江，消沈几英雄。恨乌江亭长，天机轻泄，说与重瞳。更恨南阳耕叟，撺掇紫髯翁。一弹金陵土，战虎争龙。 杯酒凤凰台上，对石城流水，钟阜诸峰。问六朝陵阙，何处是遗踪。后庭花、更无留响，渺春潮、残照笛声中。悲欢梦，芜城杨柳，几度春风。'),
	(8078, 699, '蝶恋花（元旦）', '密炬瑶霞光颤酒。翠柏红椒，细翦青丝韭。且劝金樽千万寿。年时芳梦休回首。 小雨轻寒风满袖，下却帘儿，莫遣梅花瘦。万点鹅黄春色透。玉箫吹上江南柳。'),
	(8079, 574, '鹧鸪天（戏赠黄医）', '湖海相逢尽赏音。囊中粒剂值千金。单传扁鹊卢医术，不用杨高廓玉针。 三斛火，一壶冰。蓝桥捣熟隔云深。无方可疗相思病，有药难医薄幸心。'),
	(8080, 574, '兰陵王（赣上用美成韵）', '画阑直。饾饤千红万碧。无端被，怪雨狂风，僽柳僝花禁春色。寻芳遍楚国。谁识。五陵俊客。流水远，题叶无情，雁足不来杳笺尺。 浮生等萍踪。才卸却归鞍，坐未温席。匆匆还又京华食。叹聚少离多，漂零因甚，江南逢梅望寄驿。美人兮天北。 悲恻。恨成积。怅钗玉尘生，猊金烟寂。绿杨芳草情何极。偏懒拨琵琶，愁听羌笛。梨花院落，黄昏后，珠泪滴。'),
	(8081, 687, '望江南', '思晴好，日影漏些儿。油菜花间蝴蝶舞，刺桐枝上鹁鸠啼。闲坐看春犁。'),
	(8082, 422, '摸鱼儿', '洗芳林、夜来风雨。匆匆还送春去。方才送得春归了，那又送君南浦。君听取。怕此承、春归也过吴中路。君行到处。便快折湖边，千条翠柳，为我系春住。 春还住。休索吟春伴侣。残花今已尘土。姑苏台下烟波远，西子近来何许。能唤否。又恐怕、残春到了无凭据。烦君妙语。更为我将春，连花带柳，写入翠笺句。'),
	(8083, 422, '庆宫春（水仙花）', '明玉擎金，纤罗飘带，为君起舞回雪。柔影参差，幽芳零乱，翠围腰瘦一捻。岁华相误，记前度、湘皋怨别。哀弦重听，都是凄凉，未须弹彻。 国香到此谁怜，烟冷沙昏，顿成愁绝。花恼难禁，酒销欲尽，门外冰澌初结。试招仙魄，怕今夜、瑶簪冻折。携盘独出，空想咸阳，故宫落月。'),
	(8084, 422, '齐天乐（赠秋崖道人西归）', '冷烟残水山阴道，家家拥门黄叶。故里鱼肥，初寒雁落，孤艇将归时节。江南恨节。问还与何人，共歌新阕。换尽秋芳，想渠西子更愁绝。 当时无限旧事，叹繁华似梦，如今休说。短褐临流，幽怀倚石，山色重逢都别。江云冻结。算只有梅花，尚堪攀折。寄取相思，一枝和夜雪。'),
	(8085, 422, '水龙吟（白莲）', '淡妆不扫蛾眉，为谁伫立羞明镜。真妃解语，西施净洗，娉婷顾影。薄露初匀，纤尘不染，移根玉井。想飘然一叶，飕飕短发，中流卧、浮烟艇。 可惜瑶台路迥。抱凄凉、月中难认。相逢还是，冰壶浴罢，牙床酒醒。步袜空留，舞裳微褪，粉残香冷。望海山依约，时时梦想，素波千顷。'),
	(8086, 422, '水龙吟（海棠）', '世间无此娉婷，玉环未破东风睡。将开半敛，似红还白，余花怎比。偏占年华，禁烟才过，夹衣初试。叹黄州一梦，燕宫绝笔，无人解、看花意。 犹记花阴同醉。小阑干、月高人起。千枝媚色，一庭芳景，清寒似水。银烛延娇，绿房留艳，夜深花底。怕明朝、小雨濛濛，便化作燕支泪。'),
	(8087, 366, '忆王孙', '神舟稳驾出沉流。明月辉辉命自周。两个先生暗点头。有来由。万劫轮回向此休。'),
	(8088, 366, '忆王孙', '长安不见使人愁。物换星移几度秋。一自佳人坠玉楼。莫淹留。远别秦城万里游。'),
	(8089, 53, '湘灵瑟（故妓周懿葬桥南）', '酸风泠泠。哀笳吹数声。碎雨冥冥。泣瑶英。花心路，芙蓉城。相思几回魂惊。肠断坟草青。'),
	(8090, 124, '少年游（宫词拟梅溪）', '帘消宝篆卷宫罗。蜂蝶扑飞梭。一样东风，燕梁莺院，那处春多。 晓妆日日随香辇，多在牡丹坡。花深深处，柳阴阴处，一片笙歌。'),
	(8091, 124, '庆宫春（送赵元父过吴）', '重叠云衣，微茫雁影，短篷稳载吴雪。霜叶敲寒，风灯摇晕，棹歌人语呜咽。拥衾呼酒，正百里、冰河乍合。千山换色，一镜舞尘，玉龙吹裂。 夜深醉踏长虹，表里空明，古今清绝。高台在否，登临休赋，忍见旧时明月。翠消香冷，怕空负、年芳轻别。孤山春早，一树梅花，待君同折。'),
	(8092, 124, '夷则商国香慢（赋子固凌波图）', '玉润金明。记曲屏小几，翦叶移根。经年汜人重见，瘦影娉婷。雨带风襟零乱，步云冷、鹅筦吹春。相逢旧京洛，素靥尘缁，仙掌霜凝。 国香流落恨，正冰铺翠薄，谁念遗簪。水天空远，应念矾弟梅兄。渺渺鱼波望极，五十弦、愁满湘云。凄凉耿无语，梦入东风，雪尽江清。'),
	(8093, 124, '四字令（访友不遇）', '残月半篱。残雪半枝。孤吟自款柴扉。听猿啼鸟啼。 人归未归。无诗有诗。水边伫立多时。问梅花便知。'),
	(8094, 124, '夜合花（茉莉）', '月地无尘，珠宫不夜，翠笼谁炼铅霜。南州路杳，仙子误入唐昌。零露滴，湿微妆。逗清芬、蝶梦空忙。梨花云暖，梅花雪冷，应妒秋芳。 虚庭夜气偏凉。曾记幽丛采玉，素手相将。青蕤嫩萼，指痕犹映瑶房。风透幕，月侵床。记梦回、粉艳争香。枕屏金络，钗梁绛楼，都是思量。'),
	(8095, 124, '南楼令（次陈君衡韵）', '开了木芙蓉。一年秋已空。送新愁、千里孤鸿。摇落江蓠多少恨，吟不尽、楚云峰。 往事夕阳红。故人江水东。翠衾寒、几夜霜浓。梦隔屏山飞不去，随夜鹊、绕疏桐。'),
	(8096, 124, '吴山青（赋无心处茅亭）', '山青青。水泠泠。养得风烟数亩成。乾坤一草亭。 云无心。竹无心。我亦无心似竹云。岁寒同此盟。'),
	(8097, 124, '清平乐（横玉亭秋倚）', '诗情画意。只在阑干外。雨露天低生爽气。一片吴山越水。 宫烟醉柳春晴。海风洗月秋明。唤取九霞飞佩，夜凉跨鹤吹笙。'),
	(8098, 124, '清平乐（再次前韵）', '晚莺桥噎。庭户溶溶月。一树湘桃飞茜雪。红豆相思渐结。 看看芳草平沙。游鞯犹未归家。自是萧郎飘荡，错教人恨杨花。'),
	(8099, 124, '鹧鸪天', '相傍清明晴便慳。闭门空自惜花残。海棠半坼难禁雨，燕子初归不耐寒。 金鸭冷，锦鹓闲。银釭空照小屏山。翠罗袖薄东风峭，独倚西楼第几阑。'),
	(8100, 124, '浪淘沙', '柳色淡如秋。蝶懒莺羞。十分春事九分休。开尽楝花寒尚在，怕上帘钩。 京洛少年游。谁念淹留。东风吹雨过西楼。残梦宿酲相合就，一段新愁。'),
	(8101, 124, '曲游春', '后则尽入里湖，抵暮始出，断桥小驻而归，非习于游者不知也。故中山极击节余闲却半湖春色之句，谓能道人之所未云 禁苑东风外，饧暖丝晴絮，春思如织。燕约莺期，恼芳情偏在，翠深红隙。漠漠香尘隔。沸十里、乱弦丛笛。看画船，尽入西泠，闲却半湖春色。 柳陌。新烟凝碧。映帘底宫眉，堤上游勒。轻暝笼寒，怕梨云梦冷，杏香愁幂。歌管酬寒食。奈蝶怨、良宵岑寂。正满湖、碎月摇花，怎生去得。'),
	(8102, 124, '木兰花慢（曲院风荷）', '软尘飞不到，过微雨、锦机张。正荫绿池幽，交枝径窄，临水追凉。宫妆。盖罗障暑，泛青苹、乱舞五云裳。迷眼红绡绛彩翠深偷见鸳鸯。 湖光。两岸潇湘。风荐爽、扇摇香。算恼人偏是，萦丝露藕，连理秋房。涉江。采芳旧恨，怕红衣、夜冷落横塘。折得荷花忘却，棹歌唱入斜阳。');
INSERT INTO `songs` (`id`, `author_id`, `title`, `content`) VALUES
	(8103, 71, '金缕曲', '不相见故耳，为此发歌。 吾鬓如霜蕊。自江南、西风尘起，倒骑秃尾。旧日汾阳中书令，何限门生儿子。到今也、陆沈草昧。醉里不行西州路，但斗间、看望成龙气。聊寂寞，自相慰。 夫君自是人间瑞。叹生儿、当如异日，孙仲谋耳。健笔风云蛟龙起，人物山川形势。犹有封、狼居胥意。伐木嘤嘤出幽谷，问天之将丧斯文未。吾待子，望如岁。'),
	(8104, 71, '沁园春（和刘仲简九日韵）', '九日黄花，渊明之后，谁当汝俦。记龙山昨夜，寒泉九井，帽轻似叶，髯戟如虬。庚扇西风，孔林落照，银海横波十二楼。闲笑道，那华亭上蔡，再见何由。 人生似我何求。算惟有高人高处游。笑如今别驾，前时方外，尘埃半百，岁月如流。如此连墙，今年不见，一首犹胜万户侯。偷闲好，便明朝有约，莫莫休休。'),
	(8105, 71, '莺啼序', '闷如愁红著雨，卷地吹不起。便故人渺渺，相逢前事，欲语还己。凝望久、荒城落日，五湖四海烟浪里。问而今何处，寄声旧时邻里。 闲说那回，海上苏李。雪深夜如被。想携手、汉天不语，叫□不应疑水。待河梁、一尊落月，生非死别君如酹。望故人阁上，依稀长剑方履。 古人已矣，垂名青史，谓当如此矣。又谁料浮沈，自得鱼计。赏心乐事，良辰美景，撞钟舞女，朱门大第。雕鞍骏马番装笠，笑虚名何与身前事。区区相望，饿死西山，悬目东门，人生何乐为此。 古人已矣，天下英雄，使君与操耳。听喔喔、鸡鸣早起，屡舞徘徊，痛饮高楼，狂歌过市。苍苍万古，羲农周孔，文章事业星辰上，至而今、枯见银河底。笑他黄纸除君，红旗报我，为君助喜。'),
	(8106, 71, '虞美人', '天香国色辞脂粉。肯爱红衫嫩。翛然自取玉为衣。似是银河水皱、织成机。 寒欺薄薄春无力。月浸霓裳湿。一窠香雪世间稀。可惜不教留到、布衣时。||'),
	(8107, 71, '踏莎行（樱桃词）', '珠压相于，胭脂同傅。樊家更共谁家语。梢头结取一番愁，玉箫不会双双侣。 风送流莺，前歌后舞。并桃欲吐含来住。双飞燕子自相衔，会教唇舌调鹦鹉。||'),
	(8108, 71, '踏莎行（雨中观海棠）', '命薄佳人，情钟我辈。海棠开后心如碎。斜风细雨不曾晴，倚阑滴尽胭脂泪。 恨不能开，开时又背。春寒只了房栊闭。待他晴后得君来，无言掩帐羞憔悴。'),
	(8109, 71, '最高楼（和咏雪）', '非是雪，只是玉楼成。屑不尽云英。东边老树颓然折，西头稚柳爆然声。试平安，松丈丈，竹兄兄。 有谁向、金船呼小玉、又谁怜、纸帐梦飞琼。怪疏影，坠娉婷。唤起老张寒蔌蔌，好歌白雪与君听但党家，人笑道，太粗生。'),
	(8110, 71, '山花子', '此处情怀欲问天。相期相就复何年。行过章江三十里，泪依然。 早宿半程芳草路，犹寒欲雨暮春天。小小桃花三两处，得人怜。'),
	(8111, 71, '浣溪沙（三月三日）', '高卧何须说打乖。小篱过雨翠长街。缃桃定有踏青鞋。 晴日又思花处所，东风绝似柳情怀。人间安得酒如淮。'),
	(8112, 71, '望江南（晚晴）', '朝朝暮，云雨定何如。花日穿窗梅小小，雪风洒雨柳疏疏。人唱晚晴初。'),
	(8113, 24, '蝶恋花（即景）', '风信花残吹柳絮。柳外池塘，乳燕时飞度。漠漠轻云山约住。半村烟树鸠呼雨。 竹院深深深几许。深处人闲，谁识闲中趣。弹彻瑶琴移玉柱。苍苔满地花阴午。'),
	(8114, 641, '霜叶飞', '碧天如水，新蟾挂、修眉初画云表。半江枫叶自黄昏，深院砧声悄。渐凉蝶、残花梦晓。西风篱落寒E580小。背画阑依依，有数点、流萤乱扑，扇底微照。 凝望渺漠平芜，蒹葭烟远，过雁还带愁到。拚教日日醉斜阳，但素琴横抱。记旧谱、归耕未了。金徽谁度凄凉调。算多少悲秋恨，恨比秋多，比秋犹少。'),
	(8115, 641, '兰陵王', '古堤直。隔水轻阴飏碧。东风路，还是舞烟眠露，年年自春色。红尘遍京国。留滞高阳醉客。斜阳外，千缕翠条，仿佛流莺度金尺。 长亭半陈迹。记曾系征鞍，频护歌席。匆匆江上又寒食。回首处应念，旧曾攀折，依然离恨遍四驿。倦游尚南北。 恻恻。怨怀积。渐楚榭寒收，隋苑春寂。眉颦不尽相思极。想人在何处，倚楼横笛。闲情似絮，更那听，夜雨滴。'),
	(8116, 641, '惜分飞', '钏阁桃腮香玉溜。困倚银床倦绣。双燕归来后。相思叶底寻红豆。 碧唾春衫还在否。重理弓弯舞袖。锦藉芙蓉皱。翠腰羞对垂杨瘦。'),
	(8117, 641, '秋霁（西湖十咏·平湖秋月）', '千顷玻璃，远送目斜阳，渐下林阗。题叶人归，采菱舟散，望中水天一色。碾空桂魄。玉绳低转云无迹。有素鸥，闲伴夜深，呼棹过环碧。 相思万里，顿隔婵媛，几回琼台，同驻鸾翼。对西风、凭谁问取，人间那得有今夕。应笑广寒宫殿窄。露冷烟淡，还看数点残星，两行新雁，倚楼横笛。'),
	(8118, 641, '探春（西湖十咏·苏堤春晓）', '上苑乌啼，中洲鹭起，疏钟才度云窈。篆冷香篝，灯微尘幌，残梦犹吟芳草。搔首卷帘看，认何处、六桥烟柳。翠桡才舣西泠，趁取过湖人少。 掠水风花缭绕。还暗忆年时，旗亭歌酒。隐约春声，钿车宝勒，次第凤城开了。惟有踏青心，纵早起、不嫌寒峭。画阑闲立，东风旧红谁扫。'),
	(8119, 641, '暗香', '霁天秋色。正倚楼待月，谁伴横笛。涨绿浮空，闲数河星手堪摘。弥望澄光练净，分付与、玄晖才笔。烟溆阔，云远波平，归鸟趁风席。 南国。信音寂。帐雁渚渡闲，鹭汀沙积。藓碑露泣。时拊遗踪暗嗟忆。人事空随逝水，今古但、双流一碧。待办取、蓑共笠，小舟泛得。'),
	(8120, 640, '沁园春（咏西湖酒楼）', '南北战争，惟有西湖，长如太平。看高楼倚郭，云边矗栋，小亭连苑，波上飞甍。太守风流，游人欢畅，气象迩来都斩新。秋千外，剩钗骈玉燕，酒列金鲸。 人生。乐事良辰。况莺燕声中长是晴。正风嘶宝马，软红不动，烟分采鹢，澄碧无声。倚柳分题，藉花传令，满眼繁华无限情。谁知道，有种梅处士，贫里看春。'),
	(8121, 640, '沁园春（赠陈用明）', '把酒西湖，花月三年，不见家山。想荆州座上，消磨岁月，唐风集里，收卷波澜。鹤邑朝帆、鲈乡夕棹，来往孤蒲何处间，应思我，似骑驴杜甫，长在长安。 相逢依旧开颜。听玉屑霏霏当暑寒。笑髯生如许，尚夸年少，心忙未了，浪说身闲。尘梦无凭，菟裘堪老，付子声名吾欲还。斜阳外，把平生心事，同倚阑干。'),
	(8122, 660, '浪淘沙', '年事夕阳红。霜满髯丛。摩挲苍藓石婆翁在京口夹冈。能见几人曾百岁，一笑书空。 回首棣华中。消得春浓。平生心事两惺忪。杖屦相从须放密，山月溪风。'),
	(8123, 660, '沁园春（单景山雪中以学佛自夸，因次韵戏抑之）', '潇洒书斋，香清缕直，灯冷晕圆。忽惊窗鸣瓦，霰如筛下，裁冰翦玉，片似花鲜。深怕妨梅，也愁折竹，才作还休亦偶然。更深也，漫题窗记瑞，诗思绵绵。 闻君礼佛日千。浪说道繁华不值钱。想鸳衾底下，都将命乞。蒲龛里畔，未必心安。兜率天宫，清凉境界，总是由心不是缘。雪山上，自有人坐了，不到君边。'),
	(8124, 660, '如梦令（舟泊咸池）', '晚泊江湾平处。楚楚苹花自舞。风翦雨丝轻，江上潮生船去。看取。看取。湿橹不惊鸥鹭。'),
	(8125, 660, '大酺（寿王修斋枢密）', '自有乾坤，扶人极，宗主须还人物。今为何时节，满红尘富贵，絮花飘忽。抵障狂澜，提携正印，一柱天擎突兀。平生分明处，是从容处□，不差毫发。把朝市山林，一般看了，无边风月。 深衣清到骨。紫枢府、谁信曾簪笏。炊脱粟、黄鸡白酒，补菊栽梅，碧溪绕、竹篱茅屋。无限轻描貌。都说道、诏书催发。想回首、招黄鹄。微微自笑，惟有赤松衣钵。相陪对门石佛。'),
	(8126, 298, '浪淘沙', '泼火雨初晴。草色青青。傍檐垂柳卖春饧。画舫载花花解语，绾燕吟鸾。 箫鼓入西泠。一片轻阴。钿车罗盖竞归城。别有水窗人唤酒，弦月初生。'),
	(8127, 112, '西江月', '江上桃花流水，天涯芳草青山。楼台春锁碧云湾。都入行人望眼。 一镜波平鸥去，千林日落鸦还。天风袅袅送轻帆。蓦过星槎银汉。'),
	(8128, 112, '声声慢（宏庵宴席，客有持桐子侑俎者，自云其姬亲剥之）', '寒筲惊坠，香豆初收，银床一夜霜深。乱泻明珠，金盘来荐清斟。绿窗细剥檀皱，料水晶、微损春簪。风韵处，惹手香酥润，樱口脂侵。 重省追凉前事，正风吟莎井，月碎苔阴。颗颗相思，无情漫搅秋心。银台翦花杯散，梦阿娇、金屋沈沈。甚时见，露十香、钗燕坠金。'),
	(8129, 112, '声声慢（饮时贵家，即席三姬求词）', '春星当户，眉月分心，罗屏绣幕围香。歌缓□□，轻尘暗簌文染。秋桐泛商丝雨，恨未回、飘雪垂杨。连宝镜，更一家姊妹，曾入昭阳。 莺燕堂深谁到，为殷勤、须放醉客疏狂。量减离怀，孤负蘸甲清觞。曲中倚娇佯误，算只图、一顾周郎。花镇好，驻年华、长在琐窗。'),
	(8130, 112, '木兰花慢·送翁五峰游江陵', '送秋云万里，算舒卷、总何心。叹路转羊肠，人营燕垒，霜满蓬簪。愁侵。庾尘满袖，便封侯、那羡汉淮阴。一醉莼丝脍玉，忍教菊老松深。 离音。又听西风，金井树、动秋吟。向暮江目断，鸿飞渺渺，天色沈沈。沾襟。四弦夜语，问杨琼、往事到寒砧。争似湖山岁晚，静梅香底同斟。'),
	(8131, 112, '绛都春·余往来清华池馆六年赋咏屡矣感昔伤今益不堪怀乃复作此解', '春来雁渚。弄艳冶、又入垂杨如许。困舞瘦腰，啼湿宫黄池塘雨。碧沿苍藓云根路。尚追想、凌波微步。小楼重上，凭谁为唱，旧时金缕。 凝伫。烟萝翠竹，欠罗袖、为倚天寒日暮。强醉梅边，招得花奴来尊俎。东风须惹春云住。□莫把、飞琼吹去。便教移取熏笼，夜温绣户。'),
	(8132, 112, '绛都春·题蓬莱阁灯屏', '螺屏暖翠。正雾卷暮色，星河浮霁。路幕递香，街马冲尘东风细。梅槎凌海横鳌背。倩稳载、蓬莱云气。宝阶斜转，冰娥素影，夜清如水。 应记。千秋化鹤，旧华表、认得山川犹是。暗解绣囊，争掷金钱游人醉。笙歌晓度晴霞外。又上苑、春生一苇。便教接宴莺花，万红镜里。'),
	(8133, 112, '惜红衣（余从姜石帚游苕霅间三十五年矣，重来伤今感昔，聊以咏怀）', '鹭老秋丝，苹愁暮雪，鬓那不白。倒柳移栽，如今暗溪碧。乌衣细语，伤绊惹、茸红曾约。南陌。前度刘郎，寻流花踪迹。 朱楼水侧。雪面波光，汀莲沁颜色。当时醉近绣箔，夜吟寂。三十六矶重到，清梦冷云南北。买钓舟溪上，庆有烟蓑相识。'),
	(8134, 112, '如梦令', '春在绿窗杨柳。人与流莺俱瘦。眉底暮寒生，帘额时翻波皱。风骤。风骤。花径啼红满袖。'),
	(8135, 112, '绕佛阁（黄钟商与沈野逸东皋天街卢楼追凉小饮）', '夜空似水，横汉静立，银浪声杳。瑶镜奁小。素娥乍起、楼心弄孤照。絮云未巧。梧韵露井，偏借秋早。晴暗多少。怕教彻胆，蟾光见怀抱。 浪迹尚为客，恨满长安千古道。还记暗萤、穿帘街语悄。叹步影归来，人鬓花老。紫箫天渺。又露饮风前，凉堕轻帽。酒杯空、数星横晓。'),
	(8136, 283, '措鱼儿（送王子文知太平州）', '怪朝来、片红初瘦，半分春事风雨。丹山碧水含离恨，有脚阳春难驻。芳草渡。似叫住东君，满树黄鹂语。无端杜宇。报采石矶头，惊涛屋大，寒色要春护。 阳关唱，画鹢徘徊东渚。相逢知又何处。摩挲老剑雄心在，对酒细评今古。君此去。几万里东南，只手擎天柱。长生寿母。更稳步安舆，三槐堂上，好看彩衣舞。'),
	(8137, 536, '三台春曲', '昨夜微风细雨，今朝薄霁轻寒。檐外一声啼鸟，报知花柳平安。'),
	(8138, 536, '相见欢', '绣围春水锦笼山。冶游天。可惜连朝中酒、怯秋千。 妆楼暖。朱帘卷。燕斜穿。冲落两三花片、镜台边。'),
	(8139, 241, '满江红（壬子生日）', '晓傍苍崖，滴寒露、研朱点易。五十四卦为归姝，惟幽人吉。彼美人兮春上下，如吾徒者山南北。辨一生、坚壁卧烟霞，诗无敌。 人间世，胶中漆。功名事，刀头蜜。放乾坤醉眼，看朱成碧。曾共梅花相尔汝，尽教雪后无消息。莫怕寒、容易嫁东风，春狼藉。'),
	(8140, 241, '最高楼（王寅生日）', '溪南北，本自一渔舟。烟雨几盟鸥。白鱼不负鸬鹚杓，青蓑不减肃鸟爽鸟裘。怎无端，贪射策，觅封侯。 既不似、古人能识字。又不似、今人能识事。空老去，自宜休。帝乡五十六朝暮，人间四十四春秋。问何如，茅一把，橘千头。'),
	(8141, 241, '蝶恋花', '山抹修眉横绿净。浦溆生寒，立尽梧桐影。香灺未消帘幕静。醉红如洗风吹醒。 一夜秋声连玉井。梦落孤篷，已尽山阴兴。戍角凄凉清漏永。江南烟雨何堪省。'),
	(8142, 241, '满庭芳（寿通判七月二十二日）', '星昴呈祥，山川钟秀，果然生此真贤。精神莹澈，秋水共长天。况值西风初起，中元过、七日凉先庭院爽，称觞贺客，车马看骈阗。 开筵。称寿处，红袖歌舞，脆管繁弦。愿公与椿松，对阅天年。纵使平分风月，不容暂、吟醉苕川。龙光近，行看凤诏，促入秉钧权。'),
	(8143, 241, '西江月', '蔬甲初肥雨润，茶枪小摘春明。野篱是处可诗情。打过下湖船并。 捷报秋来旁午，贤关早晚同寅。绿杨连骑带春城。不问南山远近。'),
	(8144, 241, '沁园春', '平仄度此曲，俾歌之 岁在永和，癸丑暮春，修禊兰亭。有崇山峻岭，茂林修竹，清流湍激，映带山阴。曲水流觞，群贤毕至，是日风和天气清。亦足以，供一觞一咏，畅叙幽情。 悲夫一世之人。或放浪形骸遇所欣。虽快然自足，终期于尽，老之将至，后视犹今。随事情迁，所之既倦，俯仰之间迹已陈。兴怀也，将后之览者，有感斯文。'),
	(8145, 241, '水调歌头（寿吴尚书）', '明日又重午，搀借玉蒲香。劝君且尽杯酒，听我试平章。时事艰难甚矣，人物眇然如此，骚意满潇湘。醉问屈原子，烟水正微茫。 溯层峦，浮叠嶂，碧云乡。眼中犹有公在，吾意亦差强。胸次甲兵百万，笔底天人三策，堪补舜衣裳。要及黑头耳，霖雨趁梅黄。'),
	(8146, 303, '一剪梅（乙卯中秋）', '人生能有几中秋。人自多愁。月又何愁。老娥今夜为谁羞。云意悠悠。雨意悠悠。 自怜踪迹等萍浮。去岁荆州。今岁渝州。可人谁与共斯楼。归去休休。睡去休休。'),
	(8147, 303, '八声甘州（和刘仓贺蜀捷）', '自六朝、用武诧荆州，襟喉重疆陲。更西风似箭，峡江如线，事势夔夔。须仗中流砥柱，天付治平谁。甚矣吾衰矣，将老东篱。 休说纷纷往梦，任阴平邓艾，骆谷姜维。向棋边聊且，官事了痴儿。雨未阴、毋忘户牖，挂长绳、系不住铜仪。空遐想，桃源春媚，安得追随。'),
	(8148, 303, '声声慢（赋红梅）', '红绡剪就，绛蜡熔成，天然一种仙姿。竹外家风，凄凉俭薄为宜。东君苦怜消瘦，强教伊、傅粉匀脂。较量尽，胜夭桃轻俗，繁杏粗肥。 好是新妆雅态，对疏蟾淡淡，薄雾霏霏。迥出红尘，轻盈玉骨冰肌。犹嫌污人颜色，谁云似、虢国娥眉。香韵别，怕满园、蜂蝶未知。'),
	(8149, 303, '念奴娇（丙午和朱希真老来可喜韵）', '云胡不喜。得抽脚篮中，安身局外。世路风涛都历遍，几度眉攒心碎。八尺滕床，二升粟饭，方寸恢余地。翻云覆雨，从伊造物儿戏。 不见刻木牵丝，鸡皮鹤发，弄罢寂无事。随分风光堪领略，聊放疏狂些子。刘项雌雄，跖颜修短，无彼亦无此。茅檐高卧，不知春到花底。'),
	(8150, 303, '满江红（甲午宜兴赋僧舍墨梅）', '姑射山人，仙去后、唯存标格。犹赖有、墨池老手，草玄能白。留得岁寒风骨在，岂烦造化栽培力。有世间、肉眼莫教看，非渠识。 元不夜，枝何月。元未腊，花何雪。最孤高不受，多情轻折。只有暗香天靳予，黄金作指难为术。更若将、解语付真真，空成色。'),
	(8151, 303, '水调歌头（丁亥重阳登益昌二郎庙楼）', '老子世北客，家本住吴头。登临聊复尔耳，佳节懒为酬。刚被西风断送，又为黄花牵帅，草创作斯游。目力眇无际，更上一层楼。 对长江，流不尽，古今愁。凭栏正拟一笑，襟抱怯于秋。高处令人心悸，放旷舒怀何暇，好趁醒时休。留取江湖量，归去醉中州。'),
	(8152, 117, '南乡子', '野景有谁收，只在苍鸥白鹭洲。风树飘摇云树暗，衣飕。目断青天天际头。 壮志世难酬，丹桂红蕖又晚秋。多少心情多少事，都休。载取江湖一片愁。'),
	(8153, 117, '满江红（上已后日即事）', '寒食清明，叹人在、天涯海角。饶锦绣，十洲装就，只成离索。岁去已空莺燕侣，年来尽负鸥凫约。想南溪、溪水一篙深，孤舟泊。 天向晚，东风恶。春向晚，花容薄。又荼＿架底，绿阴成幄。舴艋也闻钲鼓闹，秋千半当笙歌乐。问山公、倒载接＿无，都休却。'),
	(8154, 117, '满江红（戊午九月七日，碧沚和制几韵）', '岁岁重阳，何曾是、两般时景。人自有、悲欢离合，晦明朝暝。日月湖边来往艇，楼台水底参差影。又何妨、时暂狎沧波，轻鸥并。 闲顿放，朱门静。新结里，朱帘整。尽百年人事，移场换境。欲插黄花身已老，强倾绿醑心先醒。羡游鱼、有钓不能收，钩空饼。'),
	(8155, 117, '水调歌头（夜来月佳甚，呈景回、自昭二兄。戊午八月十八日）', '过了中秋后，今夜月方佳。看来前夜圆满，才自阙些些。扫尽乌云黑雾，放出青霄碧落，恰似我情怀。把酒自斟酌，脱略到形骸。 问渠侬，分玉镜，断金钗。少年心事，不知容易鬓边华。千古天同此月，千古人同此兴，不是旋安排。安得高飞去，长以月为家。'),
	(8156, 117, '暗香（再和）', '雪来比色。对澹然一笑，休喧笙笛。莫怪广平，铁石心肠为伊折。偏是三花两蕊，消万古、才人骚笔。尚记得，醉卧东园，天幕地为席。 回首，往事寂。正雨暗雾昏，万种愁积。锦江路悄，媒聘音沈两空忆。终是茅檐竹户，难指望、凌烟金碧。憔悴了、羌管里，怨谁始得。'),
	(8157, 117, '沁园春（己未翠山劝农）', '二十年前，君王东顾，诏牧此州。念昔时豪杰，犹难辟阖，如今老大，却更迟留。四载相望，三春又半，邂逅劭农得纵游。田畴事，是桑条正长，麦含初抽。 悠悠。身世何求。算七十迎头合罢休。谩绕堤旌纛，牵连＿棹，喧天鼓吹，断送龙舟。翠＿层边，碧云堆处，一担担来天外愁。如何好，且同斟绿醑，自课清讴。'),
	(8158, 117, '水调歌头（题烟雨楼）', '有客抱幽独，高立万人头。东湖千顷烟雨，占断几春秋。自有茂林修竹，不用买花沽酒，此乐若为酬。秋到天空阔，浩气与云浮。 叹吾曹，缘五斗，尚迟留。练江亭下，长忆闲了钓鱼舟。矧更飘摇身世，又更奔腾岁月，辛苦复何求。咫尺桃源隔，他日拟重游。'),
	(8159, 117, '虞美人', '美人一舸横秋水。冉冉烟波里。绿杨也解织离愁。故向东风摇曳、不能休。 是非得失都休计。只有抽身是。橙黄蟹熟正当时。想见双溪风月、待人归。'),
	(8160, 117, '如梦令', '雨过远山如洗。云散落霞如绮。嫩绿与残红，又是一般春意。春意。春意。只怕杜鹃催里。'),
	(8161, 117, '如梦令', '镇日春阴漠漠。新燕乍穿帘幕。睡起不胜情，闲拾瑞香花萼。寂寞。寂寞。没个人人如昨。'),
	(8162, 117, '柳梢青', '断续残虹，翩飞去鸟，别岸孤村。傍水楼台，满城钟鼓，又是黄昏。 悠悠岁月如奔。正目断、边尘塞云。两鬓秋风，百年人事，无限消魂。'),
	(8163, 117, '卜算子', '春事到西湖，处处梅花笑。抖擞长安车马尘，眼底青山好。 身世两悠悠，岁月闲中老。极目烟波万顷愁，此意谁知道。'),
	(8164, 117, '声声慢（和吴梦窗赋梅）', '挨晴拶暖，载酒呼朋，夷犹东圃西园。绿萼枝头，两三初破轻寒。平生自甘寂寞，占冷妆、不为人妍。林逋去，问影疏香暗，谁赋其间。 空想故山奇事，正烟横岭曲，月浸溪湾。杏错桃讹，那时青子都圆。惟饶梦窗知处，对翠禽、依约神仙。休引角，怕征人、泪落塞边。'),
	(8165, 56, '贺新郎（登玉田峰）', '拄杖凌高绝。望千山隐隐，波澜动摇天末。下有白云平远壑，涌起潮头喷雪。浸绝岛、孤峰出没。赤县神州何处是，但风烟、杳杳迷空阔。呼不见，古人物。 碧松枝下青瑶石。举头看、长空湛湛，淡琉璃色。上界星辰多官府，夸父忙鞭日月。任兔走、乌飞超忽。宇宙茫茫如许大，百年间、何用争优劣。身世事，一毛发。'),
	(8166, 289, '清平乐·瓜洲渡口', '瓜州渡口。恰恰城如斗。乱絮飞钱迎马首。也学玉关榆柳。 面前直控金山。极知形胜东南。更愿诸公著意，休教忘了中原。'),
	(8167, 199, '念奴娇', '三闾何在，把离骚细读，几番击节。蓠蕙椒兰纷江渚，较以艾萧终别。清浊同流，醉醒一梦，此恨谁能说。忠魂耿耿，只凭天辨优劣。 须信千古湘流，彩丝缠黍，端为英雄设。堪笑儿童浮昌＿，悲愤翻为嬉悦。三叹灵均，竟罹谗网，我独中情切。薰风窗户，榴花知为谁裂。'),
	(8168, 49, '卜算子（茉莉）', '老圃献花来，异域移根至。相对炎官火伞中，便有清凉意。 淡薄古梳妆，娴雅仙标致。欲起涪翁再品花，压了出＿弟。'),
	(8169, 49, '卜算子（曹守生朝十二月初六日）', '东圳宁馨儿，南国循良守。先把炉熏祝帝尧，次祝君侯寿。 广致米商船，多酿兵厨酒。客有须眉似盖延，许至华堂否。'),
	(8170, 49, '浪淘沙（素馨）', '目力已茫茫。缝菊为囊。论衡何必帐中藏。却爱素馨清鼻观，采伴禅床。 风露送新凉。山麝开房。旋吹银烛闭华堂。无奈纱厨遮不住，一地闻香。'),
	(8171, 49, '风入松（同前）', '归鞍尚欲小徘徊。逆境难排。人言酒是消忧物，奈病余、孤负金罍。萧瑟捣衣时候，凄凉鼓缶情怀。 远林摇落晚风哀。野店犹开。多情惟是灯前影，伴此翁、同去同来。逆旅主人相问，今回老似前回。'),
	(8172, 49, '八声甘州（雁）', '物微生处远，往还来、非但稻粱求。似爱长安日，怕阴山雪，善自为谋。个里幸无鸣镝，随意占沙洲。归兴何妨待，风景和柔。 昔到衡阳回去，今随阳避地，遍海南头。与西川流寓，彼此各淹留。未得云中消息，登望乡台了又登楼。江天阔，几行草字，字字含愁。'),
	(8173, 49, '洞仙歌（和居厚弟韵）', '眇难揽镜，跛尤难穿履。赖有胡公菊潭水。信医言、断了重碧轻红，禁害杀，不遗高吟大醉。 古来稀七十，添许多年，赢得笺天致君事。莫问客去门前，金尽床头，留宝扇、御诗珍秘。畴昔慕、乖崖老尚书，到晚节依稀，有些儿似。'),
	(8174, 49, '贺新郎（甲子端午）', '过眼光阴驶。忆垂髫、留连节物，逢场游戏。初试綀衣弄纨扇，斗采菖蒲涧里。今发白、颜苍如此。艾子萧郎方用事，怪先生、苦死纫兰芷。君不乐，欲何俟。 头标夺得群儿喜。向溪边、旁观助噪，叹吾衰矣。欲建鼓旗无气力，唤起龙泉改委水心评余诗，有建大将旗鼓，非子孰当之语。但酒户、加封而已去秋＿霈，余忝加三百户。晚觉醉乡差快活，那独醒、公子真呆底。聊洗净，笛筝耳。'),
	(8175, 49, '贺新郎（六用韵。叙谪仙为宫教兄寿）', '鹏赋年犹少||。晚飘蓬、夜郎秋浦，渔歌猿啸|<“猿啸风中断，渔歌月下闻”见太白诗>|。骏马名姬俱散去，参透南华微妙。敛万丈、光芒迥照。妃子将军瞋个甚，老先生、拂袖金闺了。供玉齿，粲然笑。 解骖赖有汾阳老。叹今人、布衣交薄，绨袍情少。黄祖斗筲何足算，鹦鹉才高命夭。与贺监、其归同道。脱下锦袍与呆底，谪仙人、白苎乌纱帽||。邀素月，入杯釂。'),
	(8176, 49, '贺新郎（五用韵。读坡公《和陶诗》，其九篇为重九作，乃叙坡事而赋之）', '行乐尤宜少。忆坡公、洞箫听罢，划然长啸。四海共知霜鬓满，莫问近来何妙。也不记、金莲曾照。老没太官糕酒分，把茱萸、便准登高了。齐得丧，等嘻笑。 集无韩子潮州表。数当时、南迁者众，北归人少。赤壁玉堂均一梦，此岂蛮烟能夭。与同叔、俱尝知道。谁问进贤冠底说，画出来、不似眉山帽。秋菊盏，献公＿。'),
	(8177, 49, '贺新郎', '人老难重少。强追陪、李侯痛饮，刘郎清啸与、兄也，琨、弟也。报答秋光无一字。虚说君房语妙。且匣起、青铜休照。赖有多情篱下菊，待西风、不肯先开了。留晚节，发孤笑。 孔璋客绍衡依表。有谁怜、戴花翁病，插萸人少。生不逢场闲则剧，年似龚生犹夭。吃紧处、无人曾道。到得扶他迂叟出，算貂蝉、未抵深衣帽。街酒贱，更沽釂。'),
	(8178, 49, '贺新郎（客赠芍药）', '一梦扬州事。画堂深、金瓶万朵，元戎高会。座上祥云层层起，不减洛中姚魏。叹别后、关山迢递。国色天香何处在，想东风、犹忆狂书记。惊岁月，一弹指。 数枝清晓烦驰骑。向小窗、依稀重见，芜城妖丽。料得花怜侬消瘦，侬亦怜花憔悴。漫怅望、竹西歌吹。老矣应无骑鹤日，但春衫、点点当时泪。那更有，旧情味。'),
	(8179, 49, '贺新郎（戊戌寿张守）', '南国秋容晚。晓寒轻、菊花台榭，拒霜池馆。试向壶山堂上望，万顷黄云＿遍。总吃著、君侯方寸。不要汉廷夸击断，要史家、编入循良传。春脚到，福星见。 家家香火人人愿。要还他、庆元狨座，建炎蝉冕。稳奉安舆迎两国，谁谓山遥水远。福寿比、河沙难算。来岁而今黄花节，早骖鸾、入侍瑶池宴。风浩荡，海清浅。'),
	(8180, 49, '满江红（四首并和实之）', '往日封章，曾耸动、君王颜色。今似得、三闾公子，四明狂客。古不能箝言者口，天方欲寿中朝脉。算人间、岂有病无医，须针石。 年冉冉，袍犹碧。心耿耿，头先白。笑臣舒迂缓，臣山愚直。拂袖归来羞炙手，望尘拜了难伸膝。把富春濑与首阳山，图斋壁。'),
	(8181, 49, '满江红（送王实之）', '天壤王郎，数人物、方今第一。谈笑里、风霆惊座，云烟生笔。落落元龙湖海气，琅琅董相天人策。问如何、十载尚青衫，诸缑客。 易爱底，些官职。难保底，些名节。拟闭门投辖，剧谈三日。畴昔评君天下宝，当为天下苍生惜。向临分、慷慨出商声，摧金石。'),
	(8182, 49, '满江红（送宋惠父入江西幕）', '满腹诗书，余事到、穰苴兵法。新受了、乌公书币，著鞭垂发。黄纸红旗喧道路，黑风青草空巢穴。向幼安、宣子顶头行，方奇特。 溪峒事，听侬说。龚遂外，无长策。便献俘非勇，纳降非怯。帐下健儿休尽锐，草间赤子俱求活。到崆峒、快寄凯歌来，宽离别。'),
	(8183, 49, '沁园春（十和。林卿得女）', '莫信人言，虺不如熊，瓦不如璋。为孟坚补史，班昭才学，中郎传业，蔡琰词章。尽洗铅华，亦无璎珞，犹带＿檀国里香。笑贫女，尚寒机轧轧，催嫁衣忙。 好逑不数潘杨。占梦者曾言大秤量。待银河浪静，金针穿了，蓝桥路近，玉杵携将。倩似凝之，媲如道韫，帘卷燕飞王谢堂。恁时节，看孙皆朱紫，翁未皤苍。'),
	(8184, 49, '沁园春（四和）', '余少之时，赋和仲宣，檄如孔璋。也曾观万舞，铺陈商颂，曾闻九奏，制作尧章。抖擞空囊，存留谏笏，犹带虚皇案畔香。今归矣，省听鸡骑马，趁早朝忙。 榻前密启明扬。宰物者方持玉尺量。元未尝弃汝，自云耄及，无宁寿我，或者天将李泰伯云：天将寿我欤。富有图书，贫无钗泽，不似安昌列后堂。新腔好，任伊川看见，非亵穹苍。'),
	(8185, 49, '沁园春（梦中作梅词）', '天造梅花，有许孤高，有许芬芳。似湘娥凝望，敛君山黛，明妃远嫁，作汉宫妆。冷艳谁知，素标难亵，又似夷齐饿首阳。幽雅意，纵写之缣楮，未得毫芒。 曾经诸老平章。只一个孤山说影香。便诏书存问，漫招处士，节旄落尽，早屈中郎。日暮天寒，山空月堕，茅舍清于白玉堂。宁淡杀，不敢凭羌笛，告诉凄凉。'),
	(8186, 49, '沁园春（送包尉）', '我羡君归，一路秋风，芙蓉木犀。想慈颜望久，灵乌乍噪，新眉画就，郎马频嘶。忙脱征衫，快呼斗酒，细为家人说建溪。争知道，这中年怀抱，最怕分携。 丈夫南北东西。应笑杀离筵粉泪啼。怅佳人来未，碧云冉冉，王孙去后，芳草萋萋。明日相思，山重水复，古道人稀茅店鸡。元龙老，有高楼百尺，谁共登梯。'),
	(8187, 49, '水调歌头', '致。属先受制置使李公之辟，崔公始聘洪公舜俞入幕。后二十五年，奉使岭外，拜公祠象，俯仰今昔，辄和公所作水调歌头以寓悲慨云。 敕使竟空反，公不出梅关。当年玉座记忆，仄席问平安。羽扇尉佗城上，野服仙游阁下，辽鹤几时还。赖有蜀耆旧，健笔与书丹。 青油士，珠履客，各凋残。四方蹙蹙靡骋，独此尚宽闲。丞相祠堂何处，太傅石碑堕泪，木老瀑泉寒。往者不可作，置酒且登山。'),
	(8188, 513, '杨柳枝', '＿碎梅花一断肠。送斜阳。风烟缥渺月微茫。又昏黄。 平野寒芜何处断，接天长。短篱浅水橘青黄。度清香。 要做神仙，炼丹工夫，譬之似闲。但姹女乘龙，金公御虎，玉炉火炽，土釜灰寒。铅里藏银，砂中取汞，神水华池上下间。山田内，有一条径路，直透泥丸。 一声雷震昆山。真橐＿、飞冲夹脊关。见白雪漫天，黄芽满地，龟蛇缭绕，乌兔掀翻。自古乾坤，这些离坎，九转烹煎结大还。灵丹就，未飞升上阙，且在人寰。'),
	(8189, 513, '卜算子（景泰山次韵东坡三首）', '云散雨初晴，蝉噪林逾静。古寺敲钟暮掩门，灯映琉璃影。 浩气镇长存，昨梦还重省。独倚阑干啸一声，毛发萧萧冷。'),
	(8190, 513, '水调歌头', '要做神仙去，工夫譬似闲。一阳初动，玉炉起火炼还丹。捉住天魂地魄，不与龙腾虎跃，满鼎汞花乾。一任河车运，径路入泥丸。 飞金精，采木液，过三关。金木间隔，如何上得玉京山。寻得曹溪路脉，便把华池神水，结就紫金团。免得饥寒了，天上即人间。'),
	(8191, 513, '兰陵王（紫元席上作）', '桃花瘦。寒食清明前后。新燕子，禁得余寒，风雨把人苦＿＿。梅粒今如豆。减却春光多少。空自有，满树山茶，似语如愁卧晴昼。 幽人展襟袖。惜莺花未老，江山如旧。杜鹃声里同携手。叹陌上芳草，堤边垂柳。一春十病九因酒。愁来独搔首。 豆蔻。枝头小。应可惜年华，孤负时候。九十韶光那得久。问芍药觅醉，牡丹索笑。三万六千，能几度，君知否。'),
	(8192, 213, '念奴娇', '嫩凉生晓，怪今朝湖上，秋风无迹。古寺桂香山色外，肠断幽丛金碧。骤雨俄来，苍烟不见，苔径孤吟屐。系船高柳，晚蝉嘶破愁寂。 且约携酒高歌，与鸥相好，分坐渔矶石。算只藕花知我意，犹把红芳留客。楼阁空＿，管弦清润，一水盈盈隔。不如休去，月悬良夜千尺。'),
	(8193, 213, '广寒秋（寓鹊桥仙）', '杯行将半，月来犹未，潇洒水亭无暑。清宵数客一阑秋，对冰雪、荷花似语。 雄边台上，文游台上，咫尺红云容与。天风吹送广寒秋，正画舸、湖光佳处。'),
	(8194, 695, '虞美人', '十年不作湖湘客。亭堠催行色。浅山荒草记当时。筱竹篱边羸马、向人嘶。 书生万字平戎策。苦泪风前滴。莫辞衫袖障征尘。自古英雄之楚、又云之秦。'),
	(8195, 695, '祝英台近', '试单衣，扶短策，沙路净如洗。乍雨还晴，花柳自多丽。争知话别南楼，片帆天际，便孤了、同心连理。 镇萦系。谩有罗带香囊，殷红斗轻翠。一纸浓愁，无处倩双鲤。可堪飞梦悠悠，春风无赖，时吹过、乱莺声里。'),
	(8196, 695, '酹江月', '东篱成趣，有西风解事，催开丛菊。碎摺黄金谁试手，一一清香堪掬。露湿凉轻，霞凝寒重，秀发如新沐。宫妆匀就，岂知红紫粗俗。 因念昔日渊明，微官不受，归伴花幽独。弹压秋光三径里，浊酒床头初熟。饮剧肠宽，醉深吻燥，更把纶巾漉。此翁无恙，唤渠同醉船玉。'),
	(8197, 113, '谒金门（宣城鹿鸣宴）', '将进酒。吹起黄钟清调。手按玉笙寒尚峭。陇梅春已透。 蓝染溪光绿皱。花簇马蹄红斗。尽使宛陵人说道。状元今岁又。'),
	(8198, 113, '沁园春（生日和蓬莱仙降词）', '春事阑斑，桐花烂漫，不堪凤栖。叹交枰世道，容容是福，危航宦海，了了成痴。邵子豪情，乐天狂态，六十六年才觉非。邵尧夫有六十六岁吟，白乐天有六十六岁诗。溪山畔，要看承风月，舍我其谁。 文章高下随时。料织锦应须用锦机。愧老无健笔，高凌月胁，病无佳句，下解人颐。君昔东坡，我今韩愈，仙自云：某即坡仙。还以韩愈相戏。造化一炉如小儿。都休管，看龟翻荷露，燕落芹泥。'),
	(8199, 239, '霜叶飞', '塞云垂地，堤烟重，燕鸿初度江表。露荷风柳向人疏，台榭还清悄。恨脉脉、离情怨晓。相思魂梦银屏小。奈倦客征衣，自遍拂尘埃，玉镜羞照。 无限静陌幽坊，追欢寻赏，未落人后先到。少年心事转头空，况老来怀抱。尽绿叶红英过了。离声慵整当时调。问丽质，从憔悴，消减腰围，似郎多少。'),
	(8200, 382, '蝶恋花', '画斛黄花寒更好。人爱花繁，却被花催老。旧恨新愁谁酝造。带围暗减知多少。 开眼万般浑是恼。只仗微醺，假寐宽怀抱。隔屋愁眉春思早。数声啼破池塘草。'),
	(8201, 382, '沁园春（寿淮东制置）', '饮马咸池，总辔昆仑，横骛九州。庆中兴机会，天生山甫，非常事业，天授留侯。左搏龙蛇，右驯虎兕，万里中原谈笑收。功名早，便貂蝉猎猎，飞出兜牟。 新氓无限欢讴。尽卖剑卖刀归买牛。正麦摇熏吹，黄迷断垄，秧涵朝雨，绿遍平畴。眼底太平，不图再见，罗拜焚香青海头。从今去，愿君王万岁，元帅千秋。'),
	(8202, 84, '水龙吟（赋酴醿）', '荡红流水无声，暮烟细草黏天远。低回倦蝶，往来忙燕，芳期顿懒。绿雾迷墙，翠虬腾架，雪明香暖。笑依依欲挽，春风教住，还疑是，相逢晚。 不似梅妆瘦减。占人间、丰神萧散。攀条弄蕊，天涯犹记，曲阑小院。老去情怀，酒边风味，有时重见。对枕帏空想，东床旧梦，带将离恨。'),
	(8203, 681, '满江红（和虞土胥·惠生日）', '月上南箕，还认得、去年星历。知谁把、一天星象，荡摩朝昔。若使平生浑自弃，如今老大何嗟及。更年来、偏得钝工夫，蹉跎力。 溪瘴碍，蛮烟隔。穹壤断，江山窄。纵燕巾滥宝，楚山囚玉。小小穷通都未问，忍闻同气相煎急。诵虞郎、百字短长诗，忧何极。'),
	(8204, 681, '喜迁莺（即席次韵南叔兄同亲友饯王万里万回宣幕）', '鬓霜盈握。叹刍牧荒墟，稻粱衰索。落日牛羊，晚云鸿雁，傍地飞空无托。牧人困和雨睡，田父醉连云酌。醉梦未醒，虎嗥川谷，麕惊林薄。 离别。谁不恶。心事同时，都不论离合。眼底时几，鼻端人物。谁辨北征东略。最怜世途局趣，只道书生疏阔。无可赠君，松阴庭院，菊华篱落。'),
	(8205, 681, '小重山', '缺然。小词寄五兄代劝 养得儿男百不中。年年随举子，踏春风。寿觞庭院燕泥融。将雏处，长是半西东。 移孝便为忠。儿行虽在远，母心同。若将一念答天公。归来拜，也胜橘双红。'),
	(8206, 680, '留春令', '粉绡轻试，绿裙微褪，吴姬娇小。一点清香著芳魂，便添起、春怀抱。 玉脸窥人舒浅笑。寄此情天渺。酒醒罗浮角声寒，正月挂、南枝晓。'),
	(8207, 680, '兰陵王（春雨）', '洒虚阁。幂幂天垂似幕。春寒峭，吹断万丝，湿影和烟暗帘箔。清愁晚来觉。佳景愔愔过却。芳郊外，莺恨燕愁，不管秋千冷红索。 行云楚台约。念今古凝情，朝暮如昨。啼红湿翠春情薄。谩一犁江上，半篙堤外，勾引轻阴趁暮角。正孤绪寂寞。 斑驳。止还作。听点点檐声，沈沈春酌。只愁入夜东风恶。怕催教花放，趁将花落。冥冥烟草，梦正远，恨怎托。'),
	(8208, 680, '兰陵王（为十年故人作）', '凤箫咽。花底寒轻夜月。兰堂静，香雾翠深，曾与瑶姬恨轻别。罗巾泪暗叠。情入歌声怨切。殷勤意，欲去又留，柳色和愁为重折。 十年迥凄绝。念髻怯瑶簪，衣褪香雪。双鳞不渡烟江阔。自春来人见，水边花外，羞倚东风翠袖怯。正愁恨时节。 南陌。阻金勒。甚望断青禽，难倩红叶。春愁欲解丁香结。整新欢罗带，旧香宫箧。凄凉风景，待见了，尽向说。'),
	(8209, 680, '金人捧露盘', '楚宫闲，金成屋，玉为阑。断云梦、容易惊残。骊歌几叠，至今愁思怯阳关。清音恨阻，抱哀筝、知为谁弹。 年华晚，月华冷，霜华重，鬓华斑。也须念、间损雕鞍。斜缄小字，锦江三十六鳞寒。此情天阔，正梅信、笛里关山。'),
	(8210, 91, '换巢鸾凤（梅意花庵作春情）', '人若梅娇。正愁横断坞，梦绕溪桥。倚风融汉粉，坐月怨秦箫。相思因甚到纤腰。定知我今，无魂可销。佳期晚，谩几度、泪痕相照。 人情。天眇眇。花外语香，时透郎怀抱。暗握荑苗，乍尝樱颗，犹恨侵阶芳草。天念王昌忒多情，换巢鸾凤教偕老。温柔乡，醉芙蓉、一帐春晓。'),
	(8211, 91, '玉簟凉', '秋是愁乡。自锦瑟断弦，有泪如江。平生花里活，奈旧梦难忘。蓝桥云树正绿，料抱月、几夜眠香。河汉阻，但凤音传恨，阑影敲凉。 新妆。莲娇试晓，梅瘦破春，因甚却扇临窗。红巾衔翠翼，早弱水茫茫。柔指各自未翦，问此去、莫负王昌。芳信准，更敢寻、红杏西厢。'),
	(8212, 91, '临江仙·倦客如今老矣', '倦客如今老矣，旧时不奈春何。几曾湖上不经过。看花南陌醉，驻马翠楼歌。 远眼愁随芳草，湘裙忆著春罗。枉教装得旧时多。向来箫鼓地，犹见柳婆娑。'),
	(8213, 91, '菩萨蛮（夜景）', '梨花不碍东城月。月明照见空兰雪。雪底夜香微。褰帘拜月归。 锦衾幽梦短。明日南堂宴。宴罢小楼台。春风来不来。'),
	(8214, 367, '贺新郎（开禧丁卯端午中都借石林韵）', '贴子传新语。问自来、翰林学士，几多人数。或道江心空铸镜，或道艾人如舞。或更道、冰盘消暑。或道芸香能去蠹，有宫中、斗草盈盈女。都不管，道何许。 离骚古意盈洲渚。也莫道、龙舟吊屈，浪花吹雨。只有辟兵符子好，少有词人拈取。谁肯向、贴中道与。绝口用兵两个字，是老臣、忠爱知难阻。写此句，绛纱缕。'),
	(8215, 670, '浣溪沙', '春入疏弦调外声。雪云初霁带湖清。屏温香软绮窗深。 山倚虚窗情淡淡，水流清浅韵泠泠。断魂醒处梦难凭。'),
	(8216, 670, '一翦梅（腊前梅）', '一朵梅花百和香。浅色春风，别样宫妆。西湖衣钵更难忘。雪意江天，浑断人肠。 清夜横斜竹影窗。赢得相思，魂梦悠扬。玉溪山外水云乡。茅舍疏篱，不换金章。'),
	(8217, 670, '菩萨蛮（花间意）', '小园红入春无际。新声休写花间意。一笑唤真真。香腮酒未醒。 惜芳追胜事。畅饮余诗思。无处说衷情。暗尘罗帐生。'),
	(8218, 615, '减字木兰花', '偶然相聚。最是人间堪乐处。散步寻春。来作琴堂不速宾。 缓歌一曲。野鹜纷纷都退缩。不用多杯。准拟花时日日来。'),
	(8219, 615, '卜算子', '客以赏之，仍呼侑尊者六辈，酒半，人簪其一，恰恰无欠余。因赋 谁把洛阳花，翦送河阳县。魏紫姚黄此地无，随分红深浅。 小插向铜瓶，一段真堪羡。十二人簪十二枝，面面交相看。'),
	(8220, 615, '念奴娇（次贾子济韵）', '琼苞玉屑，问天公、底事乱抛轻坠。城郭山川都一样，那得个般清气。谢女才情，如何只道，柳絮因风起。比梅差可，但无绿萼红蕊。 坐上十客雄豪，颓然一老，草具相邀致。驱尽寒威凭酒力，买笑千金须费。谁办佳词，洛阳年少，笔下生新意。待添几盏，共君今夕同醉。'),
	(8221, 615, '鹊桥仙（甲子中秋）', '金飙乍歇，冰轮欲上，万里秋空如扫。一年十二度团圆，甚恰限、今宵最好。 烹麟脍凤，幕天席地，争似杯盘草草。明年应更胜今年，但只恐、朱颜暗老。'),
	(8222, 615, '西江月（次彭天若元夕观灯之韵）', '歌扇潜回暖吹，酒兵顿解寒围。红莲绛蜡两交辉。小醉何妨大醉。 落笔君如王勃。属辞我愧周墀。明年应记盍簪时。耿耿怀人不寐。'),
	(8223, 370, '江神子（再赠）', '鹧鸪声里别江东。绿阴中。夕阳红。一点离愁，相对景重重。目断大罗天上客，朝玉帝，把芙蓉。 紫阳山下偶相逢。醉金钟。跨苍龙。归去故山，犹带白云封。洞口桃花如恨我，飘满地，任春风。'),
	(8224, 370, '浣沙溪', '属余赋词。遂以此意赋之。二月初二夜 白日青天蘸水开。落花江上玉鞭回。东君擎出牡丹来。 独占洛阳春气足，遂中天下作花魁。相知深处举离杯。使君时擢浙东仓使。'),
	(8225, 370, '八声甘州', '惜余春、蛱蝶引春来，杜鹃趣春归。算何如桃李，浑无言说，开落忘机。多谢黄鹂旧友，相逐落花飞。芳草连天远，愁杀斜晖。 谁向西湖南畔，问亭台在否，花木应非。看孤山山下，惟说隐君庐。想钱塘、春游依旧，到梨花、寒食隘舟车。寻常事，不须惆怅，暮雨沾衣。邵康节云：“风雨寻常事，人心胡不安。”'),
	(8226, 370, '水调歌头（客有言持志者，未知其用因赋）', '志可洞金石，气可塞堪舆。问君所志安在，富贵胜人乎。看取首阳二子，叩住孟津匹马，天讨不枝梧。特立浮云外，大块可齐驱。 铁可折，玉可碎，海可枯。不论穷达生死，直节贯殊途。立处孤峰万仞，袖里青蛇三尺，用舍付河图。晞汝阳阿上，濯汝洞庭湖。'),
	(8227, 370, '汉宫春', '春色平分，甚偏他杨柳，分外风流。夭桃自适其适，一笑还休。可怜仙李，对东风、却少温柔。争奈得、海棠妆点，向人浑不知羞。 谁觉韶华如梦，到酴醿开后，莺语供愁。天教姚黄晚出，贵与王侔。花中隐者，有春兰、秋菊俱优。须是到、溪山清冻，江梅香喷枝头。'),
	(8228, 370, '忆秦娥', '村南北。夜来陡觉霜风急。霜风急。征途情绪，塞垣消息。 佳人独倚琵琶泣。一江明月空相忆。空相忆。寒衣未絮，荻花狼藉。《淮南子》曰：“适苗类絮，而不可以为絮。”'),
	(8229, 370, '蓦山溪', '金风玉露，洗出乾坤体。乘兴到前村，见一片、清溪无底。竹篱茅舍，鸡犬两三家，寻渔父，问湘灵，拄杖斜阳里。 青春误我，白发今如此。幸自识方壶，有个人、神通游戏。涧边野鹤，岩上忽孤云，倾浊酒，对黄花，又似东篱子。'),
	(8230, 370, '浣溪沙', '一曲清溪绕舍流。数间茅屋正宜秋，芙蓉灼灼出墙头。 元亮气高还作令，少陵形瘦不封侯。村醪闲饮两三瓯。'),
	(8231, 149, '越女镜心（别席毛莹）', '风竹吹香，水枫鸣绿，睡觉凉生金缕。镜底同心，枕前双玉，相看转伤幽素。傍绮阁、轻阴度。飞来鉴湖雨。 近重午。燎银篝、暗薰溽暑。罗扇小、空写数行怨苦。纤手结芳兰，且休歌、九辩怀楚。故国情多，对溪山、都是离绪。但一川烟苇，恨满西陵归路。'),
	(8232, 149, '水调歌头（富览亭永嘉作）', '日落爱山紫，沙涨省潮回。平生梦犹不到，一叶眇西来。欲讯桑田成海，人世了无知者，鱼鸟两相推。天外玉笙杳，子晋只空台。 倚阑干，二三子，总仙才。尔歌远游章句，云气入吾杯。不问王郎五马，颇忆谢生双屐，处处长青苔。东望赤城近，吾兴亦悠哉。'),
	(8233, 149, '洞仙歌（黄木香赠辛稼轩）', '花中惯识，压架玲珑雪。乍见缃蕤间琅叶。恨春见将了，染额人归，留得个、袅袅垂香带月。 鹅儿真似酒，我爱幽芳，还比酴醿又娇绝。自种古松根，待看黄龙，乱飞上、苍髯五鬣。更老仙、添与笔端春，敢唤起桃花，问谁优劣。'),
	(8234, 149, '鹧鸪天（元夕不出）', '忆昨天街预赏时。柳慳梅小未教知。而今正是欢游夕，却怕春寒自掩扉。 帘寂寂，月低低。旧情惟有绛都词。芙蓉影暗三更后，卧听邻娃笑语归。'),
	(8235, 83, '清平乐', '方池小小。风扌习玻璃皱。数朵荷花开更好。把住薰风一笑。 芳容淡注胭脂。亭亭翠盖相依。只欠一双鸂鶒，便如画底屏帷。'),
	(8236, 83, '水调歌头（上沈倅）', '再拜识英度，喜气觉飞浮。神清骨秀，元是蓬莱谪仙流。盖去冲摩霄汉。刚向平分风月，半剌岭南州。素蕴未施展，阔步尚淹留。 从此去，朝帝阙，侍宸旒。论思献替、要须直与古人侔。好是羽仪朝著，勒就鼎彝勋业，却伴赤松游。曳杖太湖曲，笑傲八千秋。'),
	(8237, 72, '江城子', '海棠风韵玉梅春。小腰身。晓妆新。长是花时，犹系茜罗裙。一撮精神娇欲滴，说不似，画难真。楼前江柳又江云。隔音尘。泪沾巾。一点征帆，烟浪渺无津。万斛相思红豆子，凭寄与个中人。'),
	(8238, 72, '霜天晓角', '宝鉴年来微有晕。懒照容华，人远天涯近。昨夜灯花还失信。无心更唱江城引。 行过短墙回首认。醉撼花梢，红雨飞成阵。拌了为郎憔悴损。庞儿恰似江梅韵。'),
	(8239, 72, '蝶恋花（赠张守宠姬）', '帘幕闻声歌已妙。一曲尊前，真个梅花早。眉黛两山谁为扫。风流京兆江南调。 醉得白须人易老。老去侯鲭，旧也曾年少。后夜短篷霜月晓。梦魂依约云山绕。'),
	(8240, 72, '念奴娇（留别辛稼轩）', '知音者少，算乾坤许大，著身何处。直待功成方肯退，何日可寻归路。多景楼前，垂虹亭下，一枕眠秋雨。虚名相误，十年枉费辛苦。 不是奏赋明光，上书北阙，无惊人之语。我自匆忙天未许，赢得衣裾尘土。白璧追欢，黄金买笑，付与君为主。莼鲈江上，浩然明日归去。'),
	(8241, 72, '沁园春·卢蒲江席上时有新第宗室', '一剑横空，飞过洞庭，又为此来。有汝阳璡者，唱名殿陛，玉川公子，开宴尊罍。四举无成，十年不调，大宋神仙刘秀才。如何好，将百千万事，付两三杯。 未尝戚戚于怀。问自古英雄安在哉。任钱塘江上，潮生潮落，姑苏台畔，花谢花开。盗号书生，强名举子，未老雪从头上催。谁羡汝，拥三千珠履，十二金钗。'),
	(8242, 215, '水龙吟', '余曰：人间虚幻，子能毕辞荣宠，清净寡欲，当享万寿。惊觉，因赋此词，乙丑冬十二月也 这番真个休休，梦中深谢仙翁教。浮生幻境，向来识破，那堪又老。苦我身心，顺他眼耳，思量颠倒。许多时打＿，鲇鱼上竹，被人弄、知多少。 解放微官系缚，似笼槛、猿归林草。云山有约，儿孙无债，为谁烦恼。自古高贤，急流勇退，直须闻早。把忧煎换取，长伸脚睡，大开口笑。'),
	(8243, 215, '昭君怨（园池夜泛）', '月在碧虚中住。人向乱荷中去。花气杂风凉。满船香 云被歌声摇动。酒被诗情掇送。醉里卧花心。拥红衾。'),
	(8244, 335, '相见欢', '江湖万里征鸿。再相逢。多少风烟摸在、笑谈中。 歌裙醉。罗巾泪。别愁浓。瘦减腰围不碍、带金重。'),
	(8245, 335, '浣溪沙', '杨柳笼烟袅嫩黄。桃花蘸水染红香。薄罗衫子日初长。 饮尽东风三百盏，醉来愁断几回肠。教人独自遣风光。'),
	(8246, 335, '满江红（寿稼轩）', '寿酒如渑，拚一醉、劝君休惜。君不记、济河津畔，当年今夕。万丈文章光焰里，一星飞堕从南极。便御风、乘兴入京华，班卿棘。 君不是，长庚白。又不是，严陵客。只应是，明主梦中良弼。好把袖间经济手，如今去补天西北。等瑶池、侍宴夜归时，骑箕翼。'),
	(8247, 639, '暮花天', '天意微慳，春工多裕，长须末后殷勤。骨瘦挽先，肌韵恰好，花头径尺徐陈。红黄粉紫，更牛家、姚魏为真。留几种，蒂殢中州，异时齐顿浑身。 承平当日开多少，笙歌何限，是甚人人。气入江南，心知芍药，彷佛前事犹存。名品应须，认旧家、雨露方新。成一处，蓓蕾根株，剩看诸谱纷纷。'),
	(8248, 639, '诉衷情', '独凭江槛思悠悠。斜日堕林邱。鸳鸯属玉飞处，急桨荡轻舟。 红蓼岸，白苹洲。夜来秋。数声渔父，一曲水仙，歌断还愁。'),
	(8249, 639, '好事近', '篱菊吐寒花，香弄小园秋色。携手画阑西畔，忆去年同摘。 小亭依旧锁西风，往事已无迹。懒向碧云深处，问征鸿消息。'),
	(8250, 639, '南歌子', '池草抽新碧，山桃褪小红。寻春闲过小园东。春在乱花深处、鸟声中。 游镫归敲月，春衫醉舞风。谁家三弄学元戎。吹起闲愁、容易上眉峰。'),
	(8251, 639, '三部乐（七月廿六日寿王道甫）', '入脚西风，渐去去来来，早三之一。春花无数，毕竟何如秋实。不须待、名品如麻，试为君屈指，是谁层出。十朝半月，争看搏空霜鹘。 从来别真共假，任盘根错节，更饶仓卒。还他济时好手，封侯奇骨。没些儿、＿姗勃窣也不是、峥嵘突兀。百二十岁，管做彻，元分人物。'),
	(8252, 639, '水调歌头（和赵用锡）', '事业随人品，今古几麾旌。向来谋国，万事尽出汝书生。安识鲲鹏变化，九万里风在下，如许上南溟。斥鷃旁边笑，河汉一头倾。 叹世间，多少恨，几时平。霸图消歇，大家创见又成惊。邂逅汉家龙种，正尔乌纱白纻，驰鹜觉身轻。樽酒从渠说，双眼为谁明。'),
	(8253, 639, '水调歌头（癸卿九月十五日寿朱元晦）', '人物从来少，篱菊为谁黄。去年今日，倚楼还是听行藏。未觉霜风无赖，好在月华如水，心事楚天长。讲论参洙泗，杯酒到虞唐。 人未醉，歌宛转，兴悠扬。太平胸次，笑他磊磈欲成狂。且向武夷深处，坐对云烟开敛，逸思入微茫。我欲为君寿，何许得新腔。'),
	(8254, 639, '满江红（怀韩子师尚书）', '曾洗乾坤，问何事、雄图顿屈。试著眼、阶除当下，又添英物。北向争衡幽愤在，南来遗恨狂酋失。算凄凉部曲几人存，三之一。 诸老尽，郎君出。恩未报，家何恤。念横飞直上，有时还戢。笑我只知存饱暖，感君元不论阶级。休更上百尺旧家楼，尘侵帙。'),
	(8255, 569, '清平乐（迎春花一名金腰带）', '纤秾娇小。也解争春早。占得中央颜色好。装点枝枝新巧。 东皇初到江城。殷勤先去迎春。乞与黄金腰带，压持红紫纷纷。'),
	(8256, 569, '蝶恋花（道中有簪二色菊花）', '百叠霜罗香蕊细。袅袅垂铃，缀簇黄金碎。独占九秋风露里。芳心不与群英比。 采采东篱今古意。秀色堪餐，更惹兰膏腻。不用南山横紫翠。悠然消得因花醉。'),
	(8257, 569, '酹江月（题赵文炳枕屏）', '枕山平远。记当年小阁，牙床曾展。围幅高深春昼永，寂寂重帘不卷。棹舣西湖，人归南陌，酒晕红生脸。困来无那，玉肌小倚娇软。 堪恨身在天涯，曲屏环枕，此意何由见。想像高唐无梦到，独拥闲衾展转。物是人非，山长水阔，触处思量遍。愁遮不断，夜阑依旧斜掩。'),
	(8258, 671, '卜算子', '杨柳绿成阴，初过寒食节。门掩金铺独自眠，那更逢寒夜。 强起立东风，惨惨梨花谢。何事王孙不早归，寂寞秋千月。'),
	(8259, 671, '水调歌头（上辛幼安生日）', '重午日过六，灵岳再生申。丰神英毅，端是天上谪仙人。夙蕴机权才略，早岁来归明圣，惊耸汉庭臣。言语妙天下，名德冠朝绅。 绣衣节，移方面，政如神。九重隆眷倚注，伟业富经纶。闻道山东出相，行拜紫泥飞诏，归去秉洪钧。寿嘏自天锡，安用拟庄椿。'),
	(8260, 446, '好事近', '微雨洒芳尘，酝造可人春色。闻道梦云楼外，正小桃花发。 殷勤留取最繁枝，樽前待闲折。准拟乱红深处，化一双蝴蝶。'),
	(8261, 446, '惜奴娇', '我已多情，更撞著、多情底你。把一心、十分向你。尽他们，劣心肠、偏有你。共你。风了人、只为个你。 宿世冤家，百忙里、方知你。没前程、阿谁似你。坏却才名，到如今、都因你。是你。我也没、星儿恨你。'),
	(8262, 446, '长相思', '蝶团飞。莺乱啼。陌上花开人未归。碧台歌舞稀。 月入扉。风满帷。坐到黄昏人静时。清愁君不知。'),
	(8263, 446, '渔家傲', '夜半潮声来枕上。击残梦破惊魂荡。见说钱塘雄气象。披衣望。碧波堆里排银浪。 月影徘徊天滉漾。金戈铁马森相向。洗尽尘根磨业障。增豪放。从公笔力诗词壮。'),
	(8264, 446, '眼儿媚', '何须著粉更施朱。元不在妆梳。寻常结束，珊珊环佩，短短裙襦。 花羞柳妒空撩乱，冰雪做肌肤。而今便好，小名弄玉，小字琼奴。'),
	(8265, 634, '秦楼月', '青楼缺。楼心人待黄昏月。黄昏月。入帘无奈，柳绵吹雪。 谁人弄笛声呜咽。伤春未解丁香结。丁香结。鳞鸿何处，路遥江阔。'),
	(8266, 456, '一剪梅（冬至）', '斗转参横一夜霜。玉律声中，又报新阳。起来无绪赋行藏。只喜人间，一线添长。 帘幕垂垂月半廊。节物心情，都付椒觞。年华渐晚鬓毛苍。身外功名，休苦思量。'),
	(8267, 456, '一丛花', '伤春时候一凭阑。何况别离难。东风只解催人去，也不道、莺老花残。青笺未约，红绡忍泪，无计锁征鞍。 宝钗瑶钿一时闲。此恨苦天慳。如今直恁抛人去，也不念、人瘦衣宽。归来忍见，重楼淡月，依旧五更寒。'),
	(8268, 456, '孤雁儿', '在家不觉穷冬好。向客里、方知道。故园梅花正开时，记得清尊频倒。高烧红蜡，暖熏罗幌，一任花枝恼。 如今客里伤怀抱。忍双鬓、随花老。小窗独自对黄昏，只有月华飞到。假饶真个，雁书频寄，何以归来早。'),
	(8269, 456, '蝶恋花', '晴日溪山春可数。水绕池塘，知有人家住。寻日寻花花不语。旧时春恨还如许。 苦恨东风无意绪。只解催花，不解催人去。日晚荒烟迷古戍。断魂正在梅花浦。'),
	(8270, 456, '南歌子', '雨燕翻新幕，风鹃绕旧枝。画堂春尽日迟迟。又是一番平绿、涨西池。 病起尊难尽，腰宽带易垂。不堪村落子规啼。问道行人一去、几时归。'),
	(8271, 456, '清平乐（咏雪）', '疏疏整整。风急花无定。红烛照筵寒欲凝。时见筛帘玉影。 夜深明月笼纱。醉归凉面香斜。犹有惜梅心在，满庭误作吹花。'),
	(8272, 456, '好事近（待月不至）', '天淡一帘秋，明月几时来得。何事桂底香近，把清光邀勒。 人间明晦总由天，何必问通塞。且为人如月好，醉莫分南北。'),
	(8273, 456, '青玉案（用贺方回韵）', '宝林岩畔凌云路。记藉草、寻梅去。咏绿书红知几度。行云归后，碧云遮断，寂寞人何处。 一声长笛江天暮。别后谁吟倚楼句。匀面照溪心已许。欲凭锦字，写人愁去，生怕梨花雨。'),
	(8274, 456, '碧牡丹', '睡起情无著。晓雨尽，春寒弱。酒盏飘零，几日顿疏行乐。试数花枝，问此情何若。为谁开，为谁落。 正愁却。不是花情薄。花元笑人萧索。旧观千红，至今冷梦难讠乇。燕麦春风，更几人惊觉。对花羞，为花恶。'),
	(8275, 563, '水调歌头（奉饯冠之之行）', '佳客志淮海，贱子设樽罍。楚江昨夜清涨，短棹已安排。休问南楼风月，且念阳台云雨，几日却重来。银烛正凝泪，画鼓且休催。 彩云飞，黄鹤举，两徘徊。林泉归去高卧，回首笑尘埃。我唱更凭君和，君起谁同我舞，莫惜玉山颓。他日扬州路，散策愿相陪。'),
	(8276, 563, '水调歌头（渡江）', '山险号北固，景胜冠南州。洪涛江上乱云，山里簇红楼。堪笑萍踪无定，拟泊叶舟何许，无计可依刘。金阙自帷幄，玉垒老貔貅。 问兴亡，成底事，几春秋。六朝人物，五胡妖雾不胜愁。休学楚囚垂泪，须把祖鞭先著，一鼓版图收。惟有金焦石，不逐水漂流。'),
	(8277, 563, '满江红（和坡公韵）', '一雨连春，东湖涨、蒲萄新绿。湖上路，柳浓花艳，绿围红簇。尘世难逢开口笑，人生待足何时足。况南州高士是西邻，人如玉。 行路唱，谁家曲。悉易感，欢难续。问轩裳於我，有何荣辱。众醉岂容君独醒，出林休恨风摧木。叹惊弦、飞鸟尚知还，安巢宿。'),
	(8278, 563, '沁园春（和辛帅）', '虎啸风生，龙跃云飞，时不再来。试凭高望远，长淮清浅，伤今怀古，故国氛埃。壮志求伸，匈奴未灭，早以家为何谓哉。多应是，待著鞭事了，税驾方回。 稼轩聊尔名斋。笑学请樊迟心未开。似南阳高卧，莘郊自乐，磻溪韬略，傅野盐梅。植杖亭前，集山楼下，五桂三槐次第栽。功名遂，向急流勇退，肯恁徘徊。'),
	(8279, 563, '柳梢青（用万元亨送冠之韵）', '愁别欣逢。人间离合，自古难同。写就茶经，注成花谱，何事西东。 一尊良夜匆匆。怎忍见轻帆短篷。汉水无情，楚云有意，目断飞鸿。'),
	(8280, 591, '醉蓬莱', '正梧阴碧转，扇浪凉生，渐回秋意。祥绕朱门，有崧灵钟瑞。笔走龙蛇，句雕风月，好客敦高谊。苏小琵琶，绿珠箫管，日添罗绮。 邃阁清班，贰车清政，奕叶蝉联，世间谁比。行侍甘泉，趁青春荣贵。尽卷虾须，满斟鹦鹉，向画堂沉醉。铜狄摩挲，蓬莱清浅，八千椿岁。'),
	(8281, 591, '渔家傲（湖州幕官作舫室）', '风月小斋模画舫。绿窗朱户江湖样。酒是短桡歌是桨。和情放。醉乡稳到无风浪。 自有拍浮千斛酿。从教日日蒲桃涨。门外独醒人也访。同俯仰。赏心却在鸱夷上。'),
	(8282, 591, '水调歌头（巩采若寿）', '泰岳倚空碧，汶□卷云寒。萃兹山水奇秀，列宿下人寰。八世家传素业，一举手攀丹桂，依约笑谈间。宾幕佐储副，和气满长安。 分虎符，来近甸，自金銮。政平讼简无事，酒社与诗坛。曾看沙堤归去，应使神京再复，款曲问家山。玉佩揖空阔，碧雾翳苍鸾。'),
	(8283, 591, '念奴娇（赠夏成玉）', '妙龄秀发，湛灵台一点，天然奇绝。万壑千岩归健笔，扫尽平山风月。雪里疏梅，霜头寒菊，迥与余花别。识人青眼，慨然怜我疏拙。 遐想后日蛾眉，两山横黛，谈笑风生颊。握手论文情极处，冰玉一时清洁。扫断尘劳，招呼萧散，满酌金蕉叶。醉乡深处，不知天地空阔。'),
	(8284, 591, '满江红', '老子当年，饱经惯、花期酒约。行乐处，轻裘缓带，绣鞍金络。明月楼台箫鼓夜，梨花院落秋千索。共何人、对饮五三钟，颜如玉。 嗟往事，空萧索。怀新恨，又飘泊。但年来何待，许多幽独。海水连天凝望远，山风吹雨征衫薄。向此际、羸马独骎骎，情怀恶。'),
	(8285, 591, '如梦令（赋梁燕）', '燕子几曾归去。只在翠岩深处。重到画梁间，谁与旧巢为主。深许。深许。闻道凤凰来住。'),
	(8286, 591, '丑奴儿（醉中有歌此诗以劝酒者，聊隐括之）', '晚来云淡秋光薄，落日晴天。落日晴天。堂上风斜画烛烟。 从渠去买人间恨，字字都圆。字字都圆。肠断西风十四弦。'),
	(8287, 591, '鹊桥仙（席上和赵晋臣敷文）', '少年风月，少年歌舞，老去方知堪羡。叹折腰、五斗赋归来，问走了、羊肠几遍。 高车驷马，金章紫绶，传语渠侬稳便。问东湖、带得几多春，且看凌云笔健。'),
	(8288, 591, '瑞鹧鸪（乙丑奉祠归舟次余干赋）', '江头日日打头风。憔悴归来邴曼容。郑买正应求死鼠，叶公岂是好真龙。 孰居无事陪犀首，未办求封遇万松。却笑千年曹孟德，梦中相对也龙钟。'),
	(8289, 591, '鹧鸪天（和传先之提举赋雪）', '泉上长吟我独清。喜君来共雪争明。已惊并水鸥无色，更怪行沙蟹有声。 添爽气，动雄情。奇因六出忆陈平。却嫌鸟雀投林去，触破当楼云母屏。'),
	(8290, 591, '鹧鸪天', '困不成眠奈夜何。情知归未转愁多。暗将往事思量遍，谁把多情恼乱他。 些底事，误人哪。不成真个不思家。娇痴却妒香香睡，唤起醒松说梦些。'),
	(8291, 591, '临江仙（戏为期思詹老寿）', '手种门前乌臼树，而今千尺苍苍。田园只是旧耕桑。杯盘风月夜，箫鼓子孙忙。 七十五年无事客，不妨两鬓如霜。绿窗划地调红妆。更从今日醉，三万六千场。'),
	(8292, 591, '临江仙', '春色饶君白发了，不妨倚绿偎红。翠鬟催唤出房栊。垂肩金缕窄，醮甲宝杯浓。 睡起鸳鸯飞燕子，门前沙暖泥融。画楼人把玉西东。舞低花外月，唱澈柳边风。'),
	(8293, 591, '定风波（自和）', '金印累累佩陆离。河梁更赋断肠诗。莫拥旌旗真个去。何处。玉堂元自要论思。 且约风流三学士。同醉。春风看试几枪旗。从此酒酣明月夜。耳热。那边应是说侬时。'),
	(8294, 591, '声声慢（送上饶黄倅职满赴调）', '东南形胜，人物风流，白头见君恨晚。便觉君家叔度，去人未远。长怜士元骥足，道直须、别驾方展。问个里，待怎么销杀，胸中万卷。 况有星辰剑履，是传家合在，玉皇香案。零落新诗，我欠可人消遣。留君再三不住，便直饶、万家泪眼。怎抵得，这眉间、黄色一点。'),
	(8295, 591, '归朝欢（丁卯岁寄题眉山李参政石林）', '见说岷峨千古雪。都作岷峨山上石。君家右史老泉公，千金费尽勤收拾。一堂真石室。空庭更与添突兀。记当时，长编笔砚，日日云烟湿。 野老时逢山鬼泣。谁夜持山去难觅。有人依样入明光，玉阶之下岩岩立。琅玕无数碧。风流不数平原物。欲重吟，青葱玉树，须倩子云笔。'),
	(8296, 591, '念奴娇', '参，未有以品题，乞援香月堂例。欣然许之，且用前遍体制戏赋 是谁调护，岁寒枝、都把苍苔封了。茅舍疏篱江上路，清夜月高山小。摸索应知，曹刘沈谢，何况霜天晓。芬芳一世，料君长被花恼。 惆怅立马行人，一枝最爱，竹外横斜好。我向东邻曾醉里，唤起诗家二老。拄杖而今，婆娑雪里，又识商山皓。请君置酒，看渠与我倾倒。'),
	(8297, 591, '卜算子（用庄语）', '一以我为牛，一以吾为马。人与之名受不辞，善学庄周者。 江海任虚舟，风雨从飘瓦。醉者乘车坠不伤，全得於天也。'),
	(8298, 591, '卜算子（齿落）', '刚者不坚牢，柔者难摧挫。不信张开口了看，舌在牙先堕。 已阙两边厢，又豁中间个。说与儿曹莫笑翁，狗窦从君过。'),
	(8299, 591, '鹊桥仙（赠鹭鸾）', '溪边白鹭。来吾告汝。溪里鱼儿堪数。主人怜汝汝怜鱼，要物我、欣然一处。 白沙远浦。青泥别渚。剩有虾跳鳅舞。任君飞去饱时来，看头上、风吹一缕。'),
	(8300, 591, '鹧鸪天（重九席上作）', '戏马台前秋雁飞。管弦歌舞更旌旗。要知黄菊清高处，不入当年二谢诗。 倾白酒，绕东篱。只於陶令有心期。明朝重九浑潇洒，莫使尊前欠一枝。'),
	(8301, 591, '鹧鸪天（和人韵有所赠）', '趁得东风汗漫游。见他歌后怎生愁。事如芳草春长在，人似浮云影不留。 眉黛敛，眼波流。十年薄＿谩扬州。明朝短棹轻衫梦，只在溪南罨画楼。'),
	(8302, 591, '醉翁操', '意其昌未艾也。今天子即位，覃庆中外，命国朝勋臣子孙之无见仕者官之。先是，朝廷屡语甄录元祐党籍家。合是二者，先之应仕矣。将告诸朝，行有日，请予作歌以赠。属予避谤，持此戒甚力，不得如先之请。又念先之与予游八年，日从事诗酒间，意相得欢甚，於其别也，何独能恝然。顾先之长於楚词，而妙於琴，辄拟醉翁操，为之词以叙别。毕时先之绾组东归，仆当买羊沽酒，先之为鼓一再行，以为山中盛事云 长松。之风。如公。肯余从。山中。人心与吾兮谁同。湛湛千里之江。上有枫。噫，送子东。望君之门兮九重。女无悦已，谁适为容。 不龟手药，或一朝兮取封。昔与游兮皆童。我独穷兮今翁。一鱼兮一龙。劳心兮忡忡。噫，命与时逢。子取之食兮万钟。'),
	(8303, 591, '清平乐（为儿铁柱作）', '灵皇醮罢。福禄都来也。试引鹓雏花树下。断了惊惊怕怕。 从今日日聪明。更宜潭妹嵩兄。看取辛家铁柱，无灾无难公卿。'),
	(8304, 591, '玉蝴蝶（叔高书来戒酒用韵）', '贵贱偶然，浑似随风帘幌，篱落飞花。空使儿曹，马上羞面频遮。向空江、谁捐玉佩，寄离恨、应折疏麻。暮云多。佳人何处，数尽归鸦。 侬家。生涯蜡屐，功名破甑，交友搏沙。往日曾论，渊明似胜卧龙些。记从来、人生行乐，休更问、日饮亡何。快斟呵。裁诗未稳，得酒良佳。'),
	(8305, 591, '新荷叶（再题傅岩叟悠然阁）', '种豆南山，零落一顷为萁。几晚渊明，也吟草盛苗稀。风流划地，向尊前、采菊题诗。悠然忽见，此山正绕东篱。 千载襟期。高情想像当时。小阁横空，朝来翠扑人衣。是中真趣，问骋怀、游目谁知。无心出岫，白云一片孤飞。'),
	(8306, 591, '水调歌头', '秋水之约。八月十四日，余卧病博山寺中，因用韵为谢，兼简子似 我志在寥阔，畴昔梦登天。摩挲素月，人世俯仰已千年。有客骖麟并凤，云遇青山赤壁，相约上高寒。酌酒援北斗，我亦虱其间。 少歌曰，神甚放，形则眠。鸿鹄一再高举，天地睹方圆。欲重歌兮梦觉，推枕惘然独念，人事义亏全。有美人可语，秋水隔娟娟。'),
	(8307, 591, '水调歌头（题子似真山经德堂，堂，陆象山所名也）', '唤起子陆子，经德问何如。万钟於我何有，不负古人书。闻道千章松桂，剩有四时柯叶，霜雪岁寒余。此是真山境，还似象山无。 耕也馁，学也禄，孔之徒。青衫毕竟升斗，此意正关渠。天地清宁高下，日月东西寒暑，何用著工夫。两字君勿惜，借我榜吾庐。'),
	(8308, 591, '贺新郎（韩仲止判院山中见访，席上用前韵）', '听我三章约。有谈功、谈名者舞，谈经深酌。作赋相如亲涤器，识字子云投阁。算枉把、精神费却。此曾不如公荣者，莫呼来、政尔妨人乐。医俗士，苦无药。 当年众鸟看孤鹗。意飘然、横空直把，曹吞刘攫。老我山中谁来伴，须信穷愁有脚。似剪尽、还生僧发。自断此生天休问，倩何人、说与乘轩鹤。吾有志，在沟壑。'),
	(8309, 591, '生查子', '漫天春雪来，才抵梅花半。最爱雪边人，楚些裁成乱。 雪儿偏解歌，只要金杯满。谁道雪天寒，翠袖阑干暖。'),
	(8310, 591, '鹧鸪天', '石壁云积渐高。溪声绕屋几周遭。自从一两花零乱，却爱微风草动摇。 呼玉友，荐溪毛。殷勤野老苦相邀。杖藜忽避行人去，认是翁来却过桥。'),
	(8311, 591, '南歌子（新开池，戏作）', '散发披襟处，浮瓜沈李杯。涓涓流水细侵阶。凿个池儿，唤个月儿来。 画栋频摇动，红葵尽倒开。斗匀红粉照香腮。有个人人，把做镜儿猜。'),
	(8312, 591, '归朝欢', '爱。不数日，风雨摧败殆尽。意有感，因效介庵体为赋，且以菖蒲绿名之。丙辰岁三月三日也 山下千林花太俗。山上一枝看不足。春风正在此花边，菖蒲自蘸清溪绿。与花同草木。问谁风雨瓢零速。莫怨歌，夜深岩下，惊动白云宿。 病怯残年频自卜。老爱遗编难细读。苦无妙手画於菟，人间雕刻真成鹄。梦中人似玉。觉来更忆腰如束。许多愁，问君有酒，何不日丝竹。'),
	(8313, 591, '行香子（山居客至）', '白露园蔬。碧水溪鱼。笑先生、网钓还锄。小窗高卧，风展残书。看北山移，盘谷序，辋川图。 白饭青刍。赤脚长须。客来时、酒尽重沽。听风听雨，吾爱吾庐。笑本无心，刚自瘦，此君疏。'),
	(8314, 591, '行香子（三山作）', '好雨当春。要趁归耕。况而今、已是清明。小窗坐地，侧听檐声。恨夜来风，夜来月，夜来云。 花絮飘零。莺燕丁宁。怕妨侬、湖上闲行。天心肯后，费甚心情。放霎时阴，霎时雨，霎时晴。'),
	(8315, 591, '水调歌头（醉吟）', '四坐且勿语，听我醉中吟。池塘春草未歇，高树变鸣禽。鸿雁初飞江上，蟋蟀还来床下，时序百年心。谁要卿料理，山水有清音。 欢多少，歌长短，酒浅深。而今已不如昔，后定不如今。闲处直须行乐，良夜更教秉烛，高曾惜分阴。白发短如许，黄菊倩谁簪。'),
	(8316, 591, '生查子（独游西岩）', '青山非不佳，未解留侬住。赤脚踏沧浪，为爱清溪故。 朝来山鸟啼，劝上山高处。我意不关渠，自要寻兰去。'),
	(8317, 591, '南歌子', '世事从头减，秋怀彻底清。夜深犹道枕边声。试问清溪底事、不能平。 月到愁边白，鸡先远处鸣。是中无有利和名。因甚山前未晓、有人行。'),
	(8318, 591, '好事近（元夕立春）', '彩胜斗华灯，平地东风吹却。唤取雪中明月，伴使君行乐。 红旗铁马响春冰，老去此情薄。惟有前村梅在，倩一枝随著。'),
	(8319, 591, '渔家傲', '城西，直龟山之北。溪水XXXX山足矣，意伯熙当之耶。伯熙学道有新功，一日语余云：溪上尝得异石，有文隐然，如记姓名，且有“长生”等字。余未之见也。因其生朝，姑摭二事为词以寿之 道德文章传几世。到君合上三台位。自是君家门户事。当此际。龟山正抱西江水。 三万六千排日醉。鬓毛只恁青青地。江里石头争献瑞。分明是。中间有个长生字。'),
	(8320, 591, '永遇乐（送陈仁和自便东归。陈至上饶之一年，得子甚喜）', '紫陌长安，看花年少，无限歌舞。白发怜君，寻芳较晚，卷地惊风雨。问君知否，鸱夷载酒，不似井瓶身误。细思量，悲欢梦里，觉来总无寻处。 芒鞋竹杖，天教还了，千古玉溪佳句。落魄东归，风流赢得，掌上明珠去。起看清镜，南冠好在，拂了旧时尘土。向君道，云霄万里，这回稳步。'),
	(8321, 591, '一枝花（醉中戏作）', '千丈擎天手。万卷悬河口。黄金腰下印，大如斗。更千骑弓刀，挥霍遮前后。百计千方久。似斗草儿童，赢个他家偏有。 算枉了、双眉长恁皱。白发空回首。那时闲说向，山中友。看丘陇牛羊，更辨贤愚否。且自栽花柳。怕有人来，但只道、今朝中酒。'),
	(8322, 591, '定风波（杜鹃花）', '百紫千红过了春。杜鹃声苦不堪闻。却解啼教春小住。风雨。空山招得海棠魂。 一似蜀宫当日女。无数。猩猩血染赭罗巾。毕竟花开谁作主。记取。大都花属惜花人。'),
	(8323, 591, '临江仙（即席和韩南涧韵）', '风雨催春寒食近，平原一片丹青。溪边唤渡柳边行。花飞蝴蝶乱，桑嫩野蚕生。 绿野先生闲袖手，却寻诗酒功名。未知明日定阴晴。今宵成独醉，却笑众人醒。'),
	(8324, 591, '江神子·和陈仁和韵', '宝钗飞凤鬓惊鸾。望重欢，水云宽。肠断新来，翠被粉香残。待得来时春尽也：梅着子，笋成竿。 湘筠帘卷泪痕斑。佩声闲，玉垂环。个里柔温，容我老其间。却笑将军三羽箭，何日去，定天山。'),
	(8325, 591, '沁园春（弄溪赋）', '有酒忘杯，有笔忘诗，弄溪奈何。看纵横斗转，龙蛇起陆，崩腾决去，雪练倾河，袅袅东风，悠悠倒景，摇动云山水又波。还知否，欠菖蒲攒港，绿竹缘坡。 长松谁剪嵯峨。笑野老来耘山上禾。算只因鱼鸟，天然自乐，非关风月，闲处偏多。芳草春深，佳人日暮，濯发沧浪独浩歌。徘徊久，问人间谁似，老子婆娑。'),
	(8326, 591, '最高楼', '归闽中矣 相思苦，君与我同心。鱼没雁沈沈。是梦他松后追轩冕，是化为鹤后去山林。对西风，直怅望，到如今。 待不饮、奈何君有恨。待痛饮、奈何吾有病。君起舞，试重斟。苍梧云外湘妃泪，鼻亭山下鹧鸪吟。早归来，流水外，有知音。'),
	(8327, 591, '念奴娇（赠妓善作墨梅）', '江南尽处，堕玉京仙子，绝尘英秀。彩笔风流，偏解写、姑射冰姿清瘦。笑杀春工，细窥天巧，妙绝应难有。丹青图画，一时都愧凡陋。 还似篱落孤山，嫩寒清晓，只欠香沾袖。淡伫轻盈，谁付与、弄粉调朱纤手。疑是花神，朅来人世，占得佳名久。松篁佳韵，倩君添做三友。'),
	(8328, 591, '念奴娇（用东坡赤壁韵）', '倘来轩冕，问还是、今古人间何物。旧日重城愁万里，风月而今坚壁。药笼功名，酒垆身世，可惜蒙头雪。浩歌一曲，坐中人物之杰。 堪叹黄菊凋零，孤标应也有，梅花争发。醉里重揩西望眼，惟有孤鸿明灭。世事从教，浮云来去，枉了冲冠发。故人何在，长歌应伴残月。'),
	(8329, 591, '水调歌头（和德和上南涧韵）', '上界足官府，公是地行仙。青毡剑履旧物，玉立侍天颜。莫怪新来白发，恐是当年柱下，道德五千言。南涧旧活计，猿鹤且相安。 歌秦缶，宝康瓠，世皆然。不知清庙钟磬，零落有谁编。堪笑行藏用舍，试问山林钟鼎，底事有亏全。再拜荷公赐，双鹤一千年。'),
	(8330, 591, '水调歌头（元日投宿博山寺，见者惊叹其老）', '头白齿牙缺，君勿笑衰翁。无穷天地今古，人在四之中。臭腐神奇俱尽，贵贱贤愚等耳，造物也儿童。老佛更堪笑，谈妙说虚空。 坐堆＿，行答飒，立龙钟。有时三盏两盏，淡酒醉蒙鸿。四十九年前事，一百八盘狭路，拄杖倚墙东。老境何所似，只与少年同。'),
	(8331, 591, '生查子（山行寄杨民瞻）', '昨宵醉里行，山吐三更月。不见可怜人，一夜头如雪。 今宵醉里归，明月关山笛。收拾锦囊诗，要寄扬雄宅。'),
	(8332, 591, '感皇恩·滁州为范倅寿', '春事到清明，十分花柳。唤得笙歌劝君酒。酒如春好，春色年年如旧。青春元不老，君知否。 席上看君，竹清松瘦。待与青春斗长久。三山归路，明日天香襟袖。更持银盏起，为君寿。'),
	(8333, 591, '太常引（寿南涧）', '君王著意履声间。便令押、紫宸班。今代又尊韩。道吏部、文章泰山。 一杯千岁，问公何事，早伴赤松闲。功业后来看。似江左、风流谢安。'),
	(8334, 591, '菩萨蛮', '稼轩日向儿童说。带湖买得新风月。头白早归来。种花花已开。 功名浑是错。更莫思量着。见说小楼东。好山千万重。'),
	(8335, 591, '鹧鸪天（鹅湖寺道中）', '一榻清风殿影凉。涓涓流水响回廊。千章云木钩辀叫，十里溪风罢稏香。 冲急雨，趁斜阳。山园细路转微茫。倦途却被行人笑，只为林泉有底忙。'),
	(8336, 591, '声声慢（赋红木犀·余儿时尝入京师禁中凝碧池，因书当时所见）', '开元盛日，天上栽花，月殿桂影重重。十里芬芳，一枝金粟玲珑。管弦凝碧池上，记当时、风月愁侬。翠华远，但江南草木，烟锁深宫。 只为天姿冷淡，被西风酝酿，彻骨香浓。枉学丹蕉，叶展偷染妖红。道人取次装束，是自家、香底家风。又怕是，为凄凉、长在醉中。'),
	(8337, 591, '八声甘州（寿建康帅胡长文给事。时方阅拆红梅之舞，且有锡带之宠）', '把江山好处付公来，金陵帝王州。想今年燕子，依然认得，王谢风流。只用平时尊俎，弹压万貔貅。依旧钧天梦，玉殿东头。 看取黄金横带，是明年准拟，丞相封侯。有红梅新唱，香阵卷温柔。且华堂、通宵一醉，待从今、更数八千秋。公知否，邦人香火，夜半才收。'),
	(8338, 591, '洞仙歌（访泉於期师，得周氏泉，为赋）', '飞流万壑，共千岩争秀。孤负平生弄泉手。叹轻衫短帽，几许红尘，还自喜，濯发沧浪依旧。 人生行乐耳，身后虚名，何似生前一杯酒。便此地、结吾庐，待学渊明，更手种、门前五柳。且归去、父老约重来，问如此青山，定重来否。'),
	(8339, 591, '念奴娇（赋白牡丹和范廓之韵）', '对花何似，似吴宫初教，翠围红阵。欲笑还愁羞不语，惟有倾城娇韵。翠盖风流，牙签名字，旧赏那堪省。天香染露，晓来衣润谁整。 最爱弄玉团酥，就中一朵，曾入扬州咏。华屋金盘人未醒，燕子飞来春尽。最忆当年，沈香亭北，无限春风恨。醉中休问，夜深花睡香冷。'),
	(8340, 591, '水调歌头（再用韵答李子永）', '君莫赋幽愤，一语试相开。长安车马道上，平地起崔嵬。我愧渊明久矣，独借此翁湔洗，素壁写归来。斜日透虚隙，一线万飞埃。 断吾生，左持蟹，右持杯。买山自种云树，山下劚烟莱。百炼都成绕指，万事直须称好，人世几舆台。刘郎更堪笑，刚赋看花回。'),
	(8341, 591, '满江红（贺王宣子产湖南寇）', '笳鼓归来，举鞭问、何如诸葛。人道是、匆匆五月，渡泸深入。白羽风生貔虎噪，青溪路断猩鼯泣。早红尘、一骑落平冈，捷书急。 三万卷，龙韬客。浑未得，文章力。把诗书马上，笑驱锋镝。金印明年如斗大，貂蝉却自兜鍪出。待刻公、勋业到□云浯溪石。'),
	(8342, 426, '南柯子', '天末家何许，津头客未归。柳梢绿暗早莺啼。蝴蝶不知春去、绕园飞。 选胜多游冶，当垆有丽姝。青翰载酒泛晴晖。不忍十分寥落、负花时。'),
	(8343, 426, '临江仙', '思忆故园花又发，等闲过了流年。休论升擢与平迁。拂衣归去好，无事即神仙。 况是老人头雪白，羞看红粉婵娟。鸾孤凤只且随缘。莫将桃叶曲，留与世人传。'),
	(8344, 426, '念奴娇（海棠时过江潭）', '晓来雨过，正海棠枝上，胭脂如滴。桃杏不堪来此似，信是倾城倾国。藏韵收香，谁能描貌，阁尽诗人笔。从教睡去，为留银烛终夕。 不待过了清明，绿阴结子，无处寻春色。簌簌轻红飞一片，便觉临风＿恻。莫道无情，嫣然一笑，也似曾相识。惜花无主，自怜身是行客。'),
	(8345, 426, '木兰花慢', '缃桃花树下，记罗袜、昔经行。至今日重来，人惟独自，花亦凋零。青鸟杳无信息，遍人间、何处觅云軿。红锦织残旧字，玉箫吹断余声。 销凝。衣故几时更。又谁复卿卿。念镜里琴中，离鸾有恨，别鹄无情。齐眉处同笑语，但有时、梦见似平生。愁对婵娟三五，素光暂缺还盈。'),
	(8346, 426, '点绛唇（崇阳野次）', '雨湿东风，谁家燕子穿庭户。孤村薄暮。花落春归去。 浪走天涯，归思萦心绪。家何处。乱山无数。不记来时路。'),
	(8347, 14, '汉宫春（元宵十四夜作，是日立春）', '暖律初回。又烧灯市井，卖酒楼台。谁将星移万点，月满千街。轻车细马，隘通衢、蹴起香埃。今岁好，土牛作伴，挽留春色同来。 不是天公省事，要一时壮观，特地安排。何妨彩楼鼓吹，绮席尊罍。良宵胜景，语邦人、莫惜徘徊。休笑我，痴顽不去，年年烂醉金钗。'),
	(8348, 14, '水调歌头（中秋）', '明月四时好，何事喜中秋。瑶台宝鉴，宜挂玉宇最高头。放出白毫千丈，散作太虚一色，万象入吾眸。星斗避光彩，风露助清幽。 等闲来，天一角，岁三周。东奔西走，在处依旧若从游。照我尊前只影，催我镜中华发，蟾兔漫悠悠。连璧有佳客，乘兴且登楼。'),
	(8349, 587, '浣溪沙', '闲理丝簧听好音。西楼剪烛夜深深。半嗔半喜此时心。 暖语温存无恙语，韵开香靥笑吟吟。别来烦恼到如今。'),
	(8350, 587, '贺新郎', '负你千行泪。大都来、一寸心儿，万般萦系。似恁愁烦那里洎。故自三年二岁。为你后、甘心憔悴。终待说、山盟海誓。这恩情、到此非容易。拚做个，久长计。 紧要事须评议。怕人人、蓦地知时，怎生处置。毒害心肠袄知是。怕你生烦到底。便莫待、将人轻弃。不是我多疑你。被傍人、赚后失圈＿。经一事，长一智。'),
	(8351, 587, '临江仙（杨柳）', '十里春风杨柳路，年年带雨披云。柔条万缕不胜情。还将无意眼，识遍有心人。 饿损宫腰终不似，效颦总是难成。只愁秋色入高林。残蝉和落叶，此际不堪论。'),
	(8352, 587, '眼儿媚（霜夜对月）', '一钩新月照西楼。清夜思悠悠。那堪更被，征鸿嘹唳，绊惹离愁。 倚栏不语情如醉，都总寄眉头。从前只为，惜他伶俐，举措风流。'),
	(8353, 587, '柳梢青·过何郎石见早梅', '云暗天低。枫林凋翠，寒雁声悲。茅店儿前，竹篱笆后，初见横枝。 盈盈粉面香肌。记月榭、当年见伊。有恨难传，无肠可断，立马多时。'),
	(8354, 587, '浣溪沙（初夏有感）', '薄雾轻阴酿晓寒。起来宿酒尚酡颜。柳莺何事苦关关。 新恨旧愁俱唤起，当年紫袖看弓弯。泪和梅雨两潸潸。'),
	(8355, 587, '卜算子（夏日送吴主簿）', '执手送行人，水满荷花浦。旧恨新愁不忍论，泪压潇潇雨。 行计已匆匆，无计留伊住。一点相思万里心，谁怕关山阻。'),
	(8356, 587, '鹧鸪天（送春）', '只惯娇痴不惯愁。离情浑不挂眉头。可怜恼尽尊前客，却趁东风上小舟。 真个去，不忄欠留。落花流水一春休。自怜不及春江水，随到滕王阁下流。'),
	(8357, 587, '卜算子（春景）', '春水满江南，三月多芳草。幽鸟衔将远恨来，一一都啼了。 不学鸳鸯老。回首临平道。人道长眉似远山，山不似长眉好。'),
	(8358, 587, '桃源忆故人（初春）', '夜来一夜东风暖。春到桃腮柳眼。对景可堪肠断。强把愁眉展。 花期惹起归期念。前事从头忖遍。凝想水遥山远。空结相思怨。'),
	(8359, 106, '如梦令（同官新得故官故姬）', '王谢堂前旧燕。毕意情高意远。只恐宠恩深，后会不教人见。深劝。深劝。不枉追欢一遍。'),
	(8360, 106, '木兰花慢', '朝天门外路，路坦坦、走去声瑶京。悔年少狂图，争名远宦，为米孤征。星星。半凋鬓发，事千端、回首只堪惊。居士新来悟也，渭川小隐初成。 临清。巧创幽亭。真富贵、享安荣。有猿鸟清讴，松篁森卫，桧柏双旌。蛙鸣。自然鼓吹，粲林华、前后锦围屏。须信早朝鸡唱，未如夜枕滩声。'),
	(8361, 106, '临江仙（同王侯二公登裴公亭）', '忽忆裴公台上去，远空秋气棱棱。万山一水秀还明。此时三楚客，何意续骚经。 爱竹子猷参杖履，能诗侯喜同登。赓酬不尽古今情。清风生白尘，侧月照疏星。'),
	(8362, 106, '蝶恋花（一名凤栖梧长沙作）', '天际行云红一缕。无尽青山，江水悠悠去。更上层楼凭远处。凄凉今古悲三楚。 心事多端谁共语。酒醒愁来，望望家何所。薄宦漂零成久旅。天涯却羡鸿遵渚。'),
	(8363, 8, '如梦令（元宵席上口占）', '门外绮罗如绣。堂上华灯如昼。领略一番春，共醉连宵歌酒。今后。今后。如此遨头少有。'),
	(8364, 8, '鹧鸪天（采莲曲）', '两两维舟近柳堤。菱歌迤逦过前溪。曲中自诉衷肠事，岸上行人那得知。 金齿屐，翠云篦。女萝为带蕙为衣。惜花贪折归时晚，急浆相呼入翠微。'),
	(8365, 287, '玉楼春（守岁）', '年光箭脱无留计。才过立春还守岁。要知一岁已寻侬，听打个惊人喷嚏。 椒盘荐寿休辞醉。坐听爆竹浑无寐。明朝末后饮屠苏，白发从渠相点缀。'),
	(8366, 188, '浪淘沙', '琪树间瑶林。春意深深。梅花还被晓寒禁。竹里一枝斜向我，欲诉芳心。楼外卷重阴。玉界沉沉。何人低唱醉泥金。掠水飞来双翠碧，应寄归音。'),
	(8367, 188, '卜算子', '风生杜若洲，日暮垂杨浦。行到田田乱叶边，不见凌波女。 独自倚危栏，欲向荷花语。无奈荷花不应人，背立啼红雨。'),
	(8368, 188, '菩萨蛮（回文）', '落霞残照横西阁。阁西横照残霞落。波浅戏鱼多。多鱼戏浅波。手携行客酒。酒客行携手。肠断九歌长。长歌九断肠。'),
	(8369, 188, '浣溪沙', '湓浦从君已十年。京江仍许借归船。相逢此地有因缘。 十万貔貅环武帐，三千珠翠入歌筵。功成去作地行仙。'),
	(8370, 188, '虞美人', '清宫初入韶华管。宫叶秋声满。满庭芳草月婵娟。想见明朝喜色、动天颜。 持杯满劝龙头客。荣遇时难得。词源三峡泻瞿塘。便是醉中空去、也无妨。'),
	(8371, 188, '水调歌头（送谢倅之临安）', '客里送行客，常苦不胜情。见公秣马东去，底事却欣欣。不为青毡俯拾，自是公家旧物，何必更关心。且喜谢安石，重起为苍生。 圣天子，方侧席，选豪英。日边仍有知已，应剡荐章间。好把文经武略，换取碧幢红旆，谈笑扫胡尘。勋业在此举，莫厌短长亭。'),
	(8372, 188, '苍梧谣', '归。猎猎薰风颭绣旗。拦教住，重举送行杯。'),
	(8373, 188, '点绛唇', '萱草榴花，画堂永书风清暑。麝团菰黍。助泛菖蒲醑。 兵辟神符，命续同心缕。宜欢聚。绮筵歌舞。岁岁酬端午。'),
	(8374, 188, '西江月（代五三弟为老母寿）', '慈母行封大国，老仙早上蓬山。天怜阴德遍人间。赐与还丹七返。 莫问清都紫府，长教绿鬓朱颜。年年今日彩衣斑。兄弟同扶酒盏。'),
	(8375, 188, '丑奴儿', '玉节珠幢出翰林。诗书谋帅眷方深。威声虎啸复龙吟。 我是先生门下士，相逢有酒且教斟。高山流水遇知音。'),
	(8376, 188, '蝶恋花（送姚主管横州）', '君泛仙槎银海去。后日相思，地角天涯路。草草杯盘深夜语。冥冥四月黄梅雨。 莫拾明珠并翠羽。但使邦人，爱我如慈母。待得政成民按堵。朝天衣袂翩翩举。'),
	(8377, 188, '雨中花慢', '一叶凌波，十里驭风，烟鬟雾鬓萧萧。认得兰皋琼佩，水馆冰绡。秋霁明霞乍吐，曙凉宿霭初消。恨微颦不语，少进还收，伫立超遥。 神交冉冉，愁思盈盈，断魂欲遣谁招。犹自待、青鸾传信，乌鹊成桥。怅望胎仙琴叠，忍看翡翠兰苕。梦回人远，红云一片，天际笙箫。'),
	(8378, 188, '木兰花慢', '送归云去雁，淡寒采、满溪楼。正佩解湘腰，钗孤楚鬓，鸾鉴分收。凝情望行处路，但疏烟远树织离忧。只有楼前溪水，伴人清泪长流。 霜华夜永逼衾裯。唤谁护衣篝。念粉馆重来，芳尘未扫，争见嬉游。情知闷来殢酒，奈回肠、不醉只添愁。脉脉无言竟日，断魂双鹜南州。'),
	(8379, 188, '水调歌头（为时传之寿）', '云海漾空阔，风露凛高寒。仙翁鹤驾，羽节缥缈下天端。指点虚无征路，时见双凫飞舞，挥朱隘尘寰。吹笛向何处，海上有三山。 彩衣新，鱼服丽，更朱颜。蟠桃未熟，千岁容与且人间。早晚金泥封诏，归侍玉皇香案，踵武列仙班。玉骨自难老，未用九霞丹。'),
	(8380, 379, '虞美人', '暮云衰草连天远。不记离人怨。可怜无处不关情。梦断孤鸿哀怨、两三声。 恨眉醉眼何时见。夜夜相思遍。梧桐叶落候蛩秋。唯有一江烟雨、替人愁。'),
	(8381, 304, '木兰花慢', '占西风早处。一番雨、一番秋。记故园斜阳。去年今日，落叶林幽，悲歌几回。激烈寄疏狂。酒令与诗筹。遗恨清商，易改多情，紫燕难留。 嗟休。触绪茧丝，抽旧事，续何由奈。予怀渺渺，＿愁郁郁，归梦悠悠。生平不如老杜，便如他漂泊之风流。寄语庭柯径菊，甚时得棹孤舟。'),
	(8382, 326, '念奴娇（上章乞休致，戏作念奴娇以自贺）', '老夫归去，有三径、足可长拖衫袖。一道官衔清彻骨，别有监临主守。主守清风，监临明月，兼管栽花柳。登山临水，作诗三首两首。 休说白日升天，莫夸金印，斗大悬双肘。且说庐传盛事，三个闲人眉寿。拣罢军员，归农押录，致政诚斋叟。只愁醉杀，螺江门外私酒。'),
	(8383, 435, '苏幕遮', '淹留佳客坐、百年粗粝腐儒餐”者也。两辞极道湖山云月之趣因以记相与之意云 水风轻，吹不皱。上下浮光，两镜光相就。云锦摇香吹散酒。细听清谈，玉屑津津嗽。 明月前，斜阳后。竹露秋声，拂拂寒生袖。掇取湖山聊入手。紫阁黄扉，到了终须有。'),
	(8384, 435, '定风波', '白璧黄金爵上卿。紫宸殿下拜丝纶。才出龙门开虎帐，但看，甲光如水夜无尘。 古古今今男子事，摇动，芙蓉旗影入金尊。到得关河公事了，早去，白云堆里养精神。'),
	(8385, 435, '一斛珠（桃园赏雪）', '寒江凝碧。是谁翦作梨花出。花心犹带江痕湿。轻注香腮，却是桃花色。 飞来飞去何曾密。疏疏全似新相识。横吹小弄梅花笛。看你飘零，不似江南客。'),
	(8386, 435, '浣溪沙（和王通一韵简虞祖予）', '何药能医肠九回。榴莲不似蜀当归。却簪征帽解戎衣。 泪下猿声巴峡里，眼荒鸥碛楚江涯。梦魂只傍故人飞。'),
	(8387, 435, '长相思（暮春）', '红疏疏。紫疏疏。可惜飘零著地铺。春残心转孤。 莺相呼。燕相呼。楼下垂杨遮得乌。倚阑人已无。'),
	(8388, 498, '西江月', '樱笋园林绿暗，槐榆院落清和，年年高会引笙歌，戏彩人随燕贺。 一笑难逢身健，十分休惜颜酡。还将瓜枣送金荷，遍照金章满座。'),
	(8389, 498, '卜算子', '云压小桥深，月到重门静。冷蕊疏枝半不禁，更著横窗影。 回首故园春，往事难重省。半夜清香入梦来，从此熏炉冷。'),
	(8390, 498, '卜算子', '凉夜竹堂虚，小睡匆匆醒。银漏无声月上阶，满地阑干影。 何处最知秋，风在梧桐井。不惜骖鸾弄玉箫，露湿衣裳冷。'),
	(8391, 498, '临江仙', '羽扇纶巾风袅袅，东厢月到蔷薇。新声谁唤出罗帏。龙须将笛绕，雁字入筝飞。 陶写中年须个里，留连月扇云衣。周郎去后赏音稀。为君持酒听，那肯带春归。'),
	(8392, 498, '菩萨蛮（元夕立春）', '雪林一夜收寒了。东风恰向灯前到。今夕是何年。新春新月圆。 绮丛香雾隔。犹记疏狂客。留取缕金＿。夜蛾相并看。'),
	(8393, 498, '梦玉人引', '共登临处，飘风袂、倚空碧。雨卷云飞，长有桂娥看客。箫喜生春，遍锦城如画，雪山无色。一梦才成，怳天涯南北。 舞余歌罢，料宣华、回首尽陈迹。万里秦吴，有情应问消息。我欲归耕，如何重来得。故人若望江南，且折梅花花相忆。'),
	(8394, 498, '水调歌头·又燕山九日作', '万里汉家使，双节照清秋。旧京行遍，中夜呼禹济黄流。寥落桑榆西北，无限太行紫翠，相伴过芦沟。岁晚客多病，风露冷貂裘。 对重九，须烂醉，莫牢愁。黄花为我，一笑不管鬓霜羞。袖里天书咫尺，眼底关河百二，歌罢此生浮。惟有平安信，随雁到南州。'),
	(8395, 150, '浪淘沙', '春事有来期。且喜春归。问春何似去年时。报道今年春意好，随分开眉。 往事莫伤悲。光景如飞。十分潘鬓已成丝。幸是风流犹未减，且醉芳菲。'),
	(8396, 633, '夜游宫（宴席）', '宴罢珠帘半卷。画檐外、蜡香人散。翠雾霏霏漏声断。倚香肩，看中庭，花影乱。宛是高唐馆。宝奁炷、麝烟初暖。璧月何妨夜夜满。拥芳柔，恨今年，寒尚浅。'),
	(8397, 633, '恋绣衾', '雨断西山晚照明。悄无人、幽梦自惊。说道去、多时也，到如今、真个是行。远山已是无心画，小楼空、斜掩绣屏。你嚎早、收心呵，趁刘郎、双鬓未星。'),
	(8398, 633, '一丛花', '尊前凝伫漫魂迷。犹恨负幽期。从来不惯伤春泪，为伊后、滴满罗衣。那堪更是，吹箫池馆，青子绿阴时。 回廊帘影昼参差。偏共睡相宜。朝云梦断知何处，倩双燕、说与相思。从今判了，十分憔悴，图要个人知。'),
	(8399, 633, '桃源忆故人（题华山图·五之五）', '中原当日三川震。关辅回头煨烬。泪尽两河征镇。日望中兴运。 秋风霜满青青鬓。老却新丰英俊。云外华山千仞。依旧无人问。'),
	(8400, 633, '绣停针', '叹半纪，跨万里秦吴，顿觉衰谢。回首鹓行，英俊并游，咫尺玉堂金马。气凌嵩华。负壮略、纵横王霸。梦经洛浦梁园，觉来泪流如泻。 山林定去也。却自恐说著，少年时话。静院焚香，闲倚素屏，今古总成虚假。趁时婚嫁。幸自有、湖边茅舍。燕归应笑，客中又还过社。'),
	(8401, 633, '夜游宫（宫词）', '独夜寒侵翠被。奈幽梦、不成还起。欲写新愁泪溅纸。忆承恩，叹余生，今至此。 蔌蔌灯花坠。问此际、报人何事。咫尺长门过万里。恨君心，似危栏，难久倚。'),
	(8402, 633, '乌夜啼（八之八）', '纨扇婵娟素月，纱巾缥缈轻烟。高槐叶长阴初合，清润雨余天。 弄笔斜行小草，钩帘浅醉闲眠。更无一点尘埃到，枕上听新蝉。'),
	(8403, 633, '好事近（十二之十一）', '平旦出秦关，雪色驾车双鹿。借问此行安往，赏清伊修竹。 汉家宫殿劫灰中，春草几回绿。君看变迁如许，况纷纷荣辱。'),
	(8404, 633, '好事近（十二之五）', '华表又千年，谁记驾云孤鹤。回首旧曾游处，但山川城郭。 纷纷车马满人间，尘土污芒＿。且访葛仙丹井，看岩花开落。'),
	(8405, 633, '好事近（次宇文卷臣韵·十二之三）', '客路苦思归，愁似茧丝千绪。梦里镜湖烟雨，看山无重数。 尊前消尽少年狂，慵著送春语。花落燕飞庭户，叹年光如许。'),
	(8406, 633, '好事近（寄张真甫·十二之一）', '＿雁未成归，肠断宝筝零落。那更冻醪无力，似故人情薄。 瘴云蛮雨暗孤城，身在楚山角。烦问剑南消息，怕还成疏索。'),
	(8407, 633, '定风波（进贤道上见梅赠王伯寿）', '＿帽垂鞭送客回。小桥流水一枝梅。衰病逢春都不记。谁谓。幽香却解逐人来。 安得身闲频置酒。携手。与君看到十分开。少壮相从今雪鬓。因甚。流年＿恨两相催。'),
	(8408, 466, '西江月', '夜雨落花满地，晓风飞絮连天。苦无春恨可萦牵。只数年华暗换。 生意惟添白发，化工不染朱颜。老来愈觉欠清闲。梦想故园春晚。'),
	(8409, 466, '虞美人（与客赏海棠，忆去岁临川所赋，怅然有远宦之叹。晚过楚塞作）', '海棠花下春风里。曾拚千场醉。如今老去谩情多。步绕芳丛无力、奈春何。 蜀乡不远长安远。相向空肠断。不如携客过西楼。却是江山如画、可消忧。'),
	(8410, 148, '鹧鸪天（王清叔具草酌赏海棠为作二绝句，清叔击节，概括以鹧鸪天歌之）', '昨夜东风到海涯。繁红簇簇吐胭脂。恍疑仙子朝天罢，醉面匀霞韵更宜。|| 欢未足，困相依。羞将兰麝污天姿。少陵可是风情薄，却为无香不作诗。'),
	(8411, 253, '夏初临（婺州郡圃）', '水榭风台，竹轩梅迳，双溪新创名园。极目遐观，碧岑敛散瑶烟。柳塘风皱清涟。烂红云、花岛争妍。艳妆佳丽，相携笑歌，学弄秋千。 遨头多暇，命友寻芳，赏心行乐，物态熙然。偎香拾翠，雅宜飞盖联翩。满劝金船。任玉山、频醉花前。且留连，赏月画兰，拟斗婵娟。'),
	(8412, 526, '长相思', '荷花香。竹风凉。万动声沈更点长。荧荧月半床。 好思量。恶凄惶。独立西厢花拂墙。如今空断肠。'),
	(8413, 526, '减字木兰花（灯下见梅）', '灯前初见。冰玉玲珑惊眼眩。艳溢香繁。绝胜溪边月下看。 铅华尽洗。只有檀唇红不退。倾坐精神。全似当时一个人。'),
	(8414, 526, '念奴娇（次郢州张推韵）', '满城风雨近重阳，云卷天空垂幕。林表初阳光似洗，屋角呼晴双鹊。香泽方熏，烘帘初下，森森霜华薄。发妆酒暖，＿人须要同酌。 老手为拂春山，休夸京兆扫，宫眉难学。客里清欢随分有，争似还家时乐。料得厌厌，云窗深锁，宽尽黄金约。不堪重省，泪和灯烬偷落。'),
	(8415, 285, '凤栖梧', '一岁光阴寒共暑。一日光阴，只个朝还暮。有物分明能唤寤。晚钟晨角君听取。 扰扰胶胶劳百虑。究竟思量，没个相干处。只有一般携得去。世人唤作闲家具。'),
	(8416, 405, '醉落魄', '惊鸥扑蔌。萧萧卧听鸣幽屋。窗明怪得鸡啼速。墙角烂平声斑，一半露松绿。 歌楼管竹谁翻曲。丹唇冰面喷余馥。遗珠满地无人掬。归著红靴，踏碎一街玉。'),
	(8417, 405, '虞美人', '琵琶弦畔春风面。曾向尊前见。彩云初散燕空楼。萧寺相逢各认、两眉愁。 旧时曲谱曾翻否。好在曹纲手。老来心绪怯么弦。出塞移船莫遣、到愁边。'),
	(8418, 573, '临江仙（赏芙蓉）', '十载长安桃李梦，年来镜净尘空。忽传彩笔小笺红。满怀秋思，倾倒为芙蓉。 莫恨霜浓开较晚，尊前元有春风。酣娇肯为别人容。试携银烛，斜照绿波中。'),
	(8419, 573, '清平乐（席上赠人）', '桃根桃叶。一树芳相接。春到江南三二月。迷损东家蝴蝶。 殷勤踏取青阳。风前花正低昂。与我同心栀子，报君百结丁香。'),
	(8420, 192, '霜天晓角', '晓风摇幕。＿枕闻残角。霜月可窗寒影，金猊冷、翠衾薄。 旧恨无处著，新愁还又作。夜夜单于声里，灯花共、泪珠落。'),
	(8421, 271, '念奴娇', '鹅毛细翦，是琼珠密洒，一时堆积。斜倚东风浑漫漫，顷刻也须盈尺。玉作楼台，铅镕天地，不见遥岑碧。佳人作戏，碎揉些子抛掷。 争奈好景难留，风僝雨僽，打碎光凝色。总有十分轻妙态，谁似旧时怜惜。担阁梁吟，寂寥楚舞，笑捏狮儿只。梅花依旧，岁寒松竹三益。'),
	(8422, 271, '菩萨蛮（木樨）', '也无梅柳新标格。也无桃李妖娆色。一味恼人香。群花争敢当。 情知天上种。飘落深岩洞。不管月宫寒。将枝比并看。'),
	(8423, 271, '菩萨鬘（秋）', '秋声乍起梧桐落。蛩吟唧唧添萧索。＿枕背灯眠。月和残梦圆。 起来钩翠箔。何处寒砧作。独倚小阑干。逼人风露寒。'),
	(8424, 388, '生查子（姚母寿席，以龟游莲叶杯酌酒）', '碧涧有神龟，千岁游莲叶。七十古来稀，寿母杯频接。 绣衣牵彩衣，喜庆相重叠。龟紫看孙曾，鹤发何须镊。'),
	(8425, 359, '江城子（和德初灯夕词次叶石林韵）', '神仙楼观梵王宫。月当中。望难穷。坐听三通，谯鼓报笼铜。还忆当年京辇旧，车马会，五门东。 华堂歌舞间笙钟。夕香濛。度花风。翠袖传杯，争劝紫髯翁。归去不堪春梦断，烟雨晓，乱山重。'),
	(8426, 359, '满庭芳（自宛陵易倅东阳，留别诸同寮世事难穷，人生无定偶然蓬转萍）', '世事难穷，人生无定，偶然蓬转萍浮。为谁教我，从宦到东州。还似翩翩海燕，乘春至、归及凉秋。回头笑，浑家数口，又泛五湖舟。 悠悠。当此去。黄童白叟，莫漫相留。但溪山好处，深负重游。珍重诸公送我，临岐泪、欲语先流。应须记，从今风月，相忆在南楼。'),
	(8427, 359, '水调歌头（次韵陆务观陪太守方务德登多景楼）', '襟带大江左，平望见三州。凿空遗迹，千古奇胜米公楼。太守中朝耆旧，别乘当今豪逸，人物眇应刘。此地一尊酒，歌吹拥貔貅。 楚山晓，淮月夜，海门秋。登临无尽，须信诗眼不供愁。恨我相望千里，空想一时高唱，零落几人收。妙赏频回首，谁复继风流。'),
	(8428, 509, '满庭芳（簪梅）', '弄月黄昏，封霜清晓，数枝影堕溪滨。化工先手，幻出一番新。片片雕酥碾玉，寒苞似、已泄香尘。聊相对，畸人投分，尊酒认荀陈。 吾年，今老矣，佳人薄相，笑插林巾。愧苍颜白发，回授乌云。玉镜台边试看，相宜是、浅笑轻颦。君知否，寿阳额上，不似鬓边春。'),
	(8429, 686, '青玉案', '之。及泉幕任满，始以故事召赴行在，公虽知非当路意，而迫于君命，不敢俟驾，故寓意此词。道过分水岭，复题诗云：“谁知不作多时别。”又题崇安驿诗云：“睡美生习晓色催。”皆此意也。既而罢归，离临安有词云：“湖上送残春，已负别时归约。”则公之去就，盖蚤定矣。 邻鸡不管离怀苦。又还是、催人去。回首高城音信阻。霜桥月馆，水村烟市，总是思君处。 裛残别袖燕支雨。谩留得、愁千缕。欲倩归鸿分付与。鸿飞不住。倚栏无语。独立长天暮。'),
	(8430, 264, '壶中天慢', '素飚漾碧，看天衢稳送、一轮明月。翠水瀛壶人不到，比似世间秋别。玉手瑶笙，一时同色，小按霓裳叠。天津桥上，有人偷记新阕。 当日谁幻银桥，阿瞒儿戏，一笑成痴绝。肯信群仙高宴处，移下水晶宫阙。云海尘清，山河影满，桂冷吹香雪。何劳玉斧，金瓯千古无缺。'),
	(8431, 264, '浣溪沙（樱桃）', '谷雨郊园喜弄晴。满林璀璨缀繁星。筠篮新采绛珠倾。 樊素扇边歌未发，葛洪炉内药初成。金盘乳酪齿流冰。'),
	(8432, 264, '鹧鸪天（了堂净惠师示予寒食感怀二阕，因次其韵）', '每上春泥向晓干。花间幽鸟舞姗姗。年华不管人将老，门外东风依旧寒。 投簪易，息机难。鹿门归路不曾关。羡君早觉无生法，识破南柯一梦间。'),
	(8433, 264, '浣溪沙（郑相席上赠舞者）', '元是昭阳宫里人。惊鸿宛转掌中身。只疑飞过洞庭云。 按彻凉州莲步紧，好花风袅一枝新。画堂香暖不胜春。'),
	(8434, 313, '望江南', '重阳日，四面雨垂垂。戏马台前泥拍肚，龙山路上水平脐。渰浸倒东篱。 茱萸胖，黄菊湿齑齑。落帽孟嘉寻蒻笠，漉巾陶令买蓑衣。都道不如归。'),
	(8435, 313, '雨中花慢（次宇文吏部赠黄如圭韵）', '潋滟云霞，空濛雾雨，长堤柳色如茵。问西湖何似，粉面初匀。尽道软红香土，东华风月俱新。旧游如梦，尘缘未断，几度逢春。 蓬莱阁上，风流二老，相携把酒论文。最好是、四娘桃李，约近东邻。别后使君须鬓，十分白了三分。是人笑道，醉中文字，更要红裙。'),
	(8436, 16, '水调歌头（浮远堂）', '静练平千顷，华栋俯中流。凌晨画戟，来看宿雨断虹收。八九胸中云梦，三千笔端风月，无处快凝眸。笑咏一堂上，挥尘气横秋。 俯危阑，红日下，暮云稠。无穷伟观，只应天意为君谋。容我时醒时醉，独泛微烟微雨，浩荡逐轻鸥。不羡岳阳胜，丹碧耸层楼。'),
	(8437, 90, '念奴娇（亲情拾得一婢，名念奴，雪中来归）', '枝头蓓蕾，褪红绡微露，江南春色。多谢东风吹半朵，来入骚人瑶席。粉脸轻红，芳心羞吐，别有真消息。妆台帘卷，寿阳著意留得。 好是雪满群山，玉纤频捻，泛清波文鹢。深院相逢人尽道，标格都从天锡。梦蝶徒劳，霜禽休妒，争奈伊怜惜。高楼谁倚，寄言休为横笛。'),
	(8438, 90, '瑞鹤仙（劝酒）', '瑞烟笼绣幕。正玳席欢燕，觥筹交错。高情动寥廓。恣清谈雄辩，珠玑频落。锵锵妙乐。且赢取、升平快乐。又何辞、醉玉颓山，是处有人扶著。 追念抟风微利，画饼浮名，久成离索。输忠素约。没材具，漫担阁。怅良辰美景，花前月下，空把欢游蹉却。到如今、对酒当歌，怎休领略。'),
	(8439, 90, '卜算子（端午）', '符箓玉搔头，艾虎青丝鬓。一曲清歌倒酒莲，尚有香蒲晕。 角簟碧纱厨，挥扇消烦闷。唯有先生心地凉，不怕炎曦近。'),
	(8440, 90, '太清舞', '天门阙锁烟萝。琼室瑶台瑞气多。欲识仙凡光景异，欢谣须听太平歌。 花心念：伏以兽炉缥缈喷祥烟，玳席荧煌开邃幄。谛视人间之景物，何殊洞府之风光。恭惟衮绣主人，簪缨贵客。或碧瞳漆发，或绿鬓童颜。雄辩风生，英姿玉立。曾向蕊宫贝阙，为逍遥游：俱膺丹篆玉书，作神仙伴。故今此会，式契前踪。但儿等偶到尘寰，欣逢雅宴；欲陈末艺，上助清欢。未敢自专，伏候处分。竹竿问，念：既有清歌妙舞，何不献呈。 花心答，念：旧乐何在。 竹竿子问，念：一部俨然。 花心答，念：再韵前来。 念了，后行吹太清，众舞讫，众唱： 武陵自古神仙府。有渔人迷路。洞户迸寒泉，泛桃花容与。 寻花迤逦见灵光，舍扁舟、飘然入去。注目渺红霞，有人家无数。 唱了，后行吹太清歌，众舞，舞讫，花心唱： 须臾却有人相顾。把肴浆来聚。礼数既雍容，更衣冠淳古。 渔人方问此何乡，众颦眉、皆能深诉。元是避嬴秦，共携家来住。 唱了，后行吹太清歌，众舞，换坐，当花心一人唱： 当时脱得长城苦。但熙熙朝暮。上帝锡长生，任跳丸乌兔。 种桃千万已成阴，望家乡、杳然何处。从此与凡人，隔云霄烟雨。 唱了，后行吹太清歌，众舞，换坐，当花心一人唱： 渔舟之子来何所。尽相猜相语。夜宿玉堂空，见火轮飞舞。 凡心有虑尚依然，复归指、维舟沙浦。回首已茫茫，叹愚迷不悟。 唱了，后行吹太清歌，众舞，换坐，当花心一人唱： 我今来访烟霞侣。沸华堂箫鼓。疑是奏钧天，宴瑶池金母。 却将桃种散阶除，俾华实、须看三度。方记古人言，信有缘相遇。 唱了，后行吹太清歌，众舞，换坐，当花心一人唱： 云軿羽巾宪仙风举。指丹霄烟雾。行作玉京朝，趁两班鹓鹭。 玲珑环佩拥霓裳，却自有、箫韶随步。含笑嘱芳筵，后会须来赴。 唱了，后行吹太清歌，众舞，舞讫，竹竿子念：欣听嘉音，备详仙迹。固知玉步，欲返云程。宜少驻于香车，伫再闻于雅咏。 念了，花心念：但儿等暂离仙岛，来止洞天。属当嘉节之临，行有清都之觐。芝华羽葆，已杂B241于青冥；玉女仙童，正逢迎于黄道。既承嘉命，聊具新篇。 篇曰：仙家日月如天远，人世光阴若电飞。绝唱已闻惊列坐，他年同步太清归。 念了，众唱破子： 游尘世、到仙乡。喜君王。跻治虞唐。文德格遐荒。四裔尽来王。干戈偃息岁丰穰。三万里农桑。归去告穹苍。锡圣寿无疆。 唱了，后行吹步虚子，四人舞上，劝心酒，花心复劝。劝讫，众舞列作一字行。竹竿子念遣队：仙音缥缈，丽句清新。既归美于皇家，复激昂于坐客。桃源归路，鹤驭迎风。抃手阶前，相将好去。 念了，后行吹步虚子，出场。'),
	(8441, 254, '武陵春', '惨惨江云浑不动，玉雪耿孤芳。萼绿仙人带暗香。风韵冷尤长。 陇信不来寒日晚，疏影照澄江。肯借横斜伴酒觞。应共月商量。'),
	(8442, 254, '二色莲（咏题）', '凤沼湛碧，莲影明洁，清泛波面。素肌鉴玉，烟脸晕红深浅。占得薰风弄色，照醉眼、梅妆相问。堤上柳垂轻帐，飞尘尽教遮断。 重重翠荷净，列向横塘暖。争映芳草岸。画船未桨，清晓最宜遥看。似约鸳鸯并侣，又更与、春锄为伴。频宴赏，香成阵、瑶池任晚。'),
	(8443, 254, '尾犯（中秋）', '秋空过雨静，晚景澄明，天淡如水。渐看蟾彩，东山旋升，金饼上云际。轻烟散尽，莹皓色、消尘翳。倚琼楼，皎若瑶台阆风，翠阑十二。 正好登临无外。满斟与、清光对。虽桂华飘下，玉轮移影，归兴犹未。待继日同宴赏，听秘乐、广寒宫里。惟怕却，明月阴晴未定，且宜欢醉。'),
	(8444, 254, '松梢月', '院静无声。天边正、皓月初上重城。群木摇落，松路径暖风轻。喜揖蟾华当松顶，照榭阁、细影纵横。杖策徐步空明里，但襟袖皆清。 恍若如临异境，漾凤沼岸阔，波净鱼惊。气入层汉，疑有素鹤飞鸣。夜色徘徊迟宫漏，渐坐久、露湿金茎。未忍归去，闻何处、重吹笙。'),
	(8445, 332, '渔家傲（十月二日老妻生辰）', '昨日小春才得信。明宵新月初生晕。又对寿觞斟九酝。香成阵。欢声点破梅梢粉。 琪树长青资玉润。鸳鸯不老眠沙稳。此去期程知远近。君休问。山河有尽情无尽。'),
	(8446, 332, '醉花阴', '楚乡易得天时恶。风雨长如约。不道有幽人，衣带秋深，犹自悬鹑索。 招呼朋侣如花萼。有酒须同酌。世态任凋疏，却爱黄花，不似群花落。'),
	(8447, 332, '鹊桥仙', '云容掩帐，星辉排烛，待得鹊成桥后。匆匆相见夜将阑，更应副、家家乞巧。 经年怨别，霎时欢会，心事如何可了。朝朝暮暮是佳期，乍可在、人间先老。'),
	(8448, 332, '醉蓬莱', '见禾山凝秀，禾水澄清，地灵境胜。天与珍奇，产凌霄峰顶。嫩叶森枪，轻尘飞雪，冠中州双井。绝品家藏，武陵有客，清奇相称。 坐列群贤，手呈三昧，云逐瓯圆，乳随汤迸。珍重殷勤，念文园多病。毛孔生香，舌根回味，助苦吟幽兴。两腋风生，从教飞到，蓬莱仙境。'),
	(8449, 332, '醉蓬莱', '正才过七夕，又近中元，素秋时候。月皎风高，渐凉生襟袖。灏气澄凝，是谁清白，应此□□秀。味洗膏粱，才侔沈谢，三朝勋旧。 好是新来，日临连帅，化格黔黎，政归仁厚。早祷群祠，有雨随车骤。愿与寰区，共资膏泽，岁岁称眉寿。孝感灵泉，涓涓不绝，斟为醇酎。'),
	(8450, 332, '水龙吟（雪）', '小轩潇洒清宵午，风正紧、门深闭。藜床危坐，竹窗频听，春虫扑纸。灯烬垂红，篆烟消碧，衣轻如水。料飞花未止，堆檐已满，时摧折、琅玕尾。 骨冷魂清无寐。这身在、广寒宫里。暗怀千古，浑疑一夜，冰生肠胃。岁事峥嵘，故园睽阻，归期犹未。向寒乡、不念丰年，只忆青天万里。'),
	(8451, 400, '玉楼春（和李宜仲）', '少年心性消磨尽。三斗烂肠浑是闷。看书聊复强寻行，属句不妨闲趁韵。 此生自断天休问。富贵时来还有分。一卮芳酒送清歌，楼下玉人相去近。'),
	(8452, 105, '豆叶黄', '芰荷香外一声蝉。风撼琅玕惊昼眠。刻烛题诗花满笺。小神仙。对倚阑干月正圆。'),
	(8453, 105, '倾杯令', '隔座藏钩，分曹射覆烛艳渐催三鼓。筝按教坊新谱。楼外月生春浦。 徘徊争忍忙归去。怕明朝、无情风雨。珍花美酒团坐，且作尊前笑侣。'),
	(8454, 105, '梦玉人引', '上危梯尽，尽画合迥，昼帘垂。曲水飘香，小园莺唤春归。舞袖弓弯，正满城、烟草凄迷。结伴踏青，趁蝴蝶双飞。 赏心欢计，从别后、无意到西池。自检罗囊，要寻红叶留诗。懒约无凭，莺花都不知。怕人问，强开怀、细酌酴醿。'),
	(8455, 105, '思佳客', '江上何人一笛横。倚楼吹得月华生。寒风堕指倾三弄，小市收灯欲二更。 持蟹股，破霜橙。玉人水调品秦筝。细看桃李春时面，共尽玻璃酒一觥。'),
	(8456, 185, '菩萨蛮', '微云红衬余霞绮。明星碧浸银河水。欹枕画檐风。愁生草际蛩。 雁行离塞晚。不道衡阳远。归恨隔重山。楼高莫凭栏。'),
	(8457, 185, '采桑子（奉和秦楚材史君荔枝词）', '华堂清暑榕阴重，梦里江寒。火齐星繁。兴在冰壶玉井栏。 风枝露叶谁新采，欲饱防慳。遗恨空盘。留取香红满地看。'),
	(8458, 185, '渔家傲（题玄真子图）', '钓笠披云青障绕。橛头细雨春江渺。白鸟飞来风满棹。收纶了。渔童拍手樵青笑。 明月太虚同一照。浮家泛宅忘昏晓。醉眼冷看城市闹。烟波老。谁能惹得闲烦恼。'),
	(8459, 185, '春光好', '疏雨洗，细风吹。淡黄时。不分小亭芳草绿，映檐低。 楼下十二层梯。日长影里莺啼。倚遍阑干看尽柳，忆腰肢。'),
	(8460, 185, '如梦令', '卧看西湖烟渚。绿盖红妆无数。帘卷曲栏风，拂面荷香吹雨。归去。归去。笑损花边鸥鹭。'),
	(8461, 185, '如梦令（七夕）', '雨洗青冥风露。云外双星初度。乞巧夜楼空，月妒回廊私语。凝伫。凝伫。不似去年情绪。'),
	(8462, 635, '法驾导引（三之三）', '帘漠漠，帘漠漠，天淡一帘秋。自洗玉舟斟白醴，月华微映是空舟。歌罢海西流。'),
	(8463, 635, '法驾导引（三之二）', '东风起，东风起，海上百花摇。十八风鬟云半动，飞花和雨著轻绡。归路碧迢迢。'),
	(8464, 635, '清平乐（木犀）', '黄衫相倚。翠葆层层底。八月江南风日美。弄影山腰水尾。 楚人未识孤妍。离骚遗恨千年。无住庵中新事，一枝唤起幽禅。'),
	(8465, 635, '浣溪沙·离杭日梁仲谋惠酒极清而美', '七月十二日晚卧小阁，已而月上，独酌数杯 送了栖鸦复暮钟。栏干生影曲屏东。卧看孤鹤驾天风。 起舞一尊明月下，秋空如水酒如空。谪仙已去与谁同。'),
	(8466, 635, '虞美人（大光祖席醉中赋长短句）', '张帆欲去仍搔首。更醉君家酒。吟诗日日待春风。及至桃花开后、却匆匆。 歌声频为行人咽。记著尊前雪。明朝酒醒大江流。满载一船离恨、向衡州。'),
	(8467, 402, '虞美人（宿龟山夜登秋汉亭）', '归来峰下霜如水。明月三千里。幽人独立瞰长淮。谁棹扁舟一叶、趁潮来。 洞庭湖上银涛观。忆我烟蓑伴。此身天地一浮萍。去国十年华发、欲星星。'),
	(8468, 402, '踏莎行', '我自山中，渔樵冷族。一丘一壑平生熟。朅来江海寄余生，心兵语阱频奔北。 九里灵河，十分光烛。分辉借润须邻曲。柳家兄弟莫瞋人，狂奴小户元低促。'),
	(8469, 296, '十样花', '陌上风光浓处。第一寒梅先吐。待得春来也，香销减，态凝伫。百花休谩妒。 陌上风光浓处。繁杏枝头春聚。艳态最娇娆，堪比并，东邻女。红梅何足数。 陌上风光浓处。日暖山樱红露。结子点朱唇，花谢后，君看取。流莺偏嘱付。 陌上风光浓处。忘却桃源归路。洞口水流迟，香风动，红无数。吹愁何处去。 陌上风光浓处。最是海棠风措。翠袖衬轻红，盈盈泪，怨春去。黄昏微带雨。 陌上风光浓处。自有花王为主。富艳压群芳，蜂蝶戏，燕莺语。东君都付与。 陌上风光浓处。红药一番经雨。把酒绕芳丛，花解语。劝春住。莫教容易去。'),
	(8470, 296, '蝶恋花（拟古）', '百尺游丝当绣户。不系春晖，只系闲愁住。拾翠归来芳草路。避人蝴蝶双飞去。 因脸羞眉无意绪。陌上行人，记得清明否。消息未来池阁暮。濛濛一饷梨花雨。'),
	(8471, 296, '水调歌头（再用前韵）', '不上长安道，霜鬓几惊秋。故人何在，时序欺我去如流。赏对洛滨仙伯，共说芗林佳致，魂梦与追游。更唱中秋句，得月上东楼。 云岩底，秋香下，楚江头。十年笑傲、真是骑鹤上扬州。却忆金门联辔，晓殿催班同到，高拱翠云裘。明月今千里，何计缓离忧。'),
	(8472, 425, '减字木兰花（政和癸丑）', '飞霞半缕。收尽一天风和雨。可惜黄昏。残角疏钟要断魂。 双鱼传信。只道横塘消息近。心事悠悠。同向春风各自愁。'),
	(8473, 425, '点绛唇（赋登楼）', '休惜余春，试来把酒留春住。问春无语。帘卷西山雨。 一掬愁心，强欲登高赋。山无数。烟波无数。不放春归去。'),
	(8474, 518, '长相思', '我心坚。你心坚。各自心坚石也穿。谁言相见难。 小窗前。月婵娟。玉困花柔并枕眠。今宵人月圆。'),
	(8475, 518, '小重山', '淡淡秋容烟水寒。楼高清夜永，倚阑干。玉人不见坐长叹。箫声远，明月满空山。 遐想绿云鬟。青冥风露冷，独乘鸾。别时容易见时难。凭孤枕，聊复梦婵娟。'),
	(8476, 518, '柳梢青', '数声鶗鳺。可怜又是，春归时节。满院东风，海棠铺绣，梨花飘雪。 丁香露泣残枝，算未比、愁肠寸结。自是休文，多情多感，不干风月。'),
	(8477, 518, '忆秦娥（西湖）', '湖光碧。春花秋月无今昔。无今昔。十年往事，尽成陈迹。 玉箫声断云屏隔。山遥水远长相忆。长相忆。一生怀抱，为君牵役。'),
	(8478, 518, '水调歌头', '相逢非草草，分袂太匆匆。征裘泪痕浥遍，眸子怯酸风。天际孤帆难驻，柳外香軿望断，云雨各西东。回首重城远，楼观暮烟中。 黯销魂，思陈事，已成空。东郊胜赏，归路骑马踏残红。月下一樽芳酒，凭阑几曲清歌，别后少人同。为问桃花脸，一笑为谁容。'),
	(8479, 99, '浣溪沙', '姑射肌肤雪一团。掺掺玉手弄冰纨。著人情思几多般。 水上月如天样远，眼前花似镜中看。见时容易近时难。'),
	(8480, 99, '采桑子', '人如濯濯春杨柳，彻骨风流。脱体温柔。牵系多情尽未休。 最怜恰恰新眠起，云雨初收。斜倚琼楼。叶叶眉心一样愁。'),
	(8481, 99, '西江月', '微步凌波尘起，弄妆满镜花开。春心掷处眼频来。秀色著人无耐。 旧事如风无迹，新愁似水难裁。相思日夜梦阳台。减尽沈郎衣带。'),
	(8482, 99, '生查子', '近似月当怀，远似花藏雾。好是月明时，同醉花深处。 看花不自持，对月空相顾。愿学月频圆，莫作花飞去。'),
	(8483, 99, '秦楼月', '虫声切。柔肠欲断伤离别。伤离别。几行清泪，界残红颊。 玉阶白露侵罗袜。下帘却望玲珑月。玲珑月。寒光凌乱，照人愁绝。'),
	(8484, 99, '减字木兰花（政和癸巳）', '几年不见。胡蝶枕中魂梦远。一日相逢。鹦鹉杯深笑靥浓。 欢心未已。流水落花愁又起。离恨如何。细雨斜风晚更多。'),
	(8485, 99, '菩萨蛮', '鸳鸯翡翠同心侣。惊风不得双飞去。春水绿西池。重期相见时。 长怜心共语。梦里池边路。相见不如新。花应解笑人。'),
	(8486, 99, '梅花引（戏代李师明作）', '花如颊。梅如叶。小时笑弄阶前月。最盈盈。最惺惺。闲愁未识、无计定深情。十年空省春风面。花落花开不相见。要相逢。得相逢。须信灵犀，中自有心通。'),
	(8487, 99, '如梦令', '水之法，乃神仙术也。其香着人不灭，名曰芗林秋露。李长吉诗亦云：“山头老桂吹古香。”戏作二阕，以贻好事者二之一 欲问芗林秋露。来自广寒深处。海上说蔷薇，何似桂华风度。高古。高古。不著世间尘污。'),
	(8488, 99, '生查子', '天上得灵根，不是凡花数。清似水沉香，色染蔷薇露。芗林月冷时，玉笋云深处。归梦托秋风，夜夜江头路。'),
	(8489, 99, '鹧鸪天（老妻生日）', '玉篆题名在九天。而今且作地行仙。挂冠神武归休后，同醉芗林是几年。 龟游泳，鹤蹁跹。疏梅修竹两清妍。欲知福寿都多少，阁皂清江可比肩。'),
	(8490, 99, '虞美人', '以送之。时正之被召 淮阳堂上曾相对。笑把姚黄醉。十年离乱有深忧。白发萧萧同见、渚江秋。 履声细听知何处。欲上星辰去。清寒初溢暮云收。更看碧天如水、月如流。'),
	(8491, 589, '醉蓬莱（庆寿）', '破新正春到，五叶尧蓂，弄芳初秀。翦彩然膏，灿华筵如昼。家庆图中，老莱堂上，竞祝翁遐寿。喜气欢容，光生玉斝，香霏金兽。 谁会高情，淡然声利，一笑尘寰，万缘何有。解组归来，访渔樵朋友。华发苍颜，任从老去，但此情依旧。岁岁年年，花前月下，一尊芳酒。'),
	(8492, 589, '人月圆（中秋）', '连环宝瑟深深愿，结尽一生愁。人间天上，佳期胜赏，今夜中秋。 雅歌妍态，嫦娥见了，应羡风流。芳尊美酒，年年岁岁，月满高楼。'),
	(8493, 104, '如梦令（忆旧）', '海雁桥边春苦。几见落花飞絮。重到柳行西，懒问画楼何处。凝伫。凝伫。十顷荷花风雨。'),
	(8494, 104, '蝶恋花（春词）', '巧语娇莺春未暮。杨柳风流，恰过池塘雨。芳草满庭花满树。无情胡蝶飞来去。 睡起小奁香一缕。玉篆回纹，等个人分付。桃叶不言人不语。眉尖一点君知否。'),
	(8495, 314, '西江月', '拾翠亭前水满，郎官湖上春回。仪龙新碾试琼杯。更觉春江有味。 拄杖行穿翠筱，吹花醉绕江梅。故园心事老相催。此意陶潜能会。'),
	(8496, 315, '望江南（过分水岭）', '征骑远，千里别沙阳。泛碧斋傍凝翠阁，栖云寺里印心堂。回首意茫茫。 分水岭，烟雨正凄凉。南望瓯闽连海峤，北归吴越过江乡。极目暮云长。'),
	(8497, 315, '水龙吟（上巳日出郊，呈知宗安抚、张参、观文汪相二首）', '莫春清淑之初，赏心最乐惟修禊。兰亭序饮，泛觞流咏，萧然适意。旧弼宗英，文章贤牧，肯临坰外。向碧山深处，寻花问柳，有佳气、随旌旆。 寥落幽人寓止。借僧园、缭云萦水。栽花种竹，凿池开径，渊明岂愧。美景良辰，玉颜方老，欲留何计。对残红嫩绿，开怀笑语，且须同醉。'),
	(8498, 315, '江城子', '去年九日在衡阳。满林霜。俯潇湘。回雁峰头，依约雁南翔。遥想茱萸方遍插，唯少我，一枝香。 今年佳节幸相将。陟层冈。举华觞。地暖风和，犹未菊开黄。此会明年知健否，判酩酊，醉秋光。'),
	(8499, 315, '水调歌头（和李似之横山对月）', '秋杪暑方退，清若玉壶冰。高楼对月，天上宫殿不曾扃。散下凄然风露，影照江山如昼，浑觉俗缘轻。弋者欲何慕，鸿羽正冥冥。 世间法，唯此事，最堪凭。太虚心量，聊假梨枣制颓龄。但使心安身健，静看草根泉际，吟蚓与飞萤。一坐小千劫，无念契无生。'),
	(8500, 315, '水龙吟（光武战昆阳）', '汉家炎运中微，坐令闰位余分据。南阳自有，真人膺历，龙翔虎步。初起昆城，旋驱乌合，块然当路。想莽军百万，旌旗千里，应道是、探囊取。 豁达刘郎大度。对劲敌、安恬无惧。提兵夹击，声喧天坏，雷风借助。虎豹哀嗥，戈鋋委地，一时休去。早复收旧物，扫清氛祲，作中兴主。'),
	(8501, 559, '金莲绕凤楼', '绛烛朱笼相随映，驰绣毂、尘清香衬。万金光射龙轩莹。绕端门、瑞雷轻振。 元宵为开圣景。严敷坐、观灯锡庆。帝家华英乘春兴。搴珠帘、望尧瞻舜。'),
	(8502, 128, '采桑子（将离武林）', '云踪老去浑无定，飘泊寒空。又被东风。吹过江南第几峰。 长安市上看花眼，不到衰翁。好趁归鸿。家在西岩碧桂丛。'),
	(8503, 128, '感皇恩（竹坡老人步上南冈，得堂基于孤峰绝顶间，喜甚，戏作长短句）', '无事小神仙，世人谁会。著甚来由自萦系。人生须是，做些闲中活计。百年能几许，无多子。 近日谢天，与片闲田地。作个茅堂待打睡。酒儿熟也，赢取山中一醉。人间如意事，只此是。'),
	(8504, 128, '感皇恩', '残月挂征鞍，路长山绕。独拥寒貂犯霜晓。水边林下，孤负此生多少。星星空满鬓，因谁有。 不如办个，短蓑长钓。唤取轻鸥伴人老。思量也胜，看人眉头眼脑。世间浑是梦，何时了。'),
	(8505, 128, '好事近（海棠）', '春似酒杯浓，醉得海棠无力。谁染玉肌丰脸，做燕支颜色。 送春风雨最无情，吹残也堪惜。何似且留花住，唤小鬟催拍。'),
	(8506, 128, '永遇乐（五日）', '槐幄如云，燕泥犹湿，雨余清暑。细草摇风，小荷擎雨，时节还端午。碧罗窗底，依稀记得，闲系翠丝烟缕。到如今、前欢如梦，还对彩绥条无语。 榴花半吐，金刀犹在，往事更堪重数。艾虎钗头，菖蒲酒里，旧约浑无据。轻衫如雾，玉肌似削，人在画楼深处。想灵符、无人共带，翠眉暗聚。'),
	(8507, 128, '宴桃源（与孙祖恭求蘼酴）', '旧日荼酴时候。酒涴粉香襟袖。老去惜春心，试问孙郎知否。花瘦。花瘦。剪取一枝重嗅。'),
	(8508, 267, '如梦令', '一夜蟠桃吹坼。刚道有人偷折。幸自没踪由，无奈蝶蜂胡说。胡说。胡说。方朔不须耳热。'),
	(8509, 267, '相见欢', '深秋庭院初凉。近重阳。篱畔一枝金菊、露微黄。 鲈脍韵。橙薤品。酒新香。我是升平闲客、醉何妨。'),
	(8510, 267, '西湖曲', '今冬寒早风光好。休怪搀先欹絮帽。蟹肥一个可称斤，酒美三杯真合道。 年年闲梦垂垂了。且喜风松吹不倒。平分两月是新春，却共梅花依旧笑。'),
	(8511, 267, '菩萨蛮', '老人谙尽人间苦。近来恰似心头悟。九九是重阳。重阳菊散芳。 出门何处去。对面谁相语。枕臂卧南窗。铜炉柏子香。'),
	(8512, 267, '诉衷情', '月中玉兔日中鸦。随我度年华。不管寒暄风雨，饱饭热煎茶。 居士竹，故侯瓜。老生涯。自然天地，本分云山，到处为家。'),
	(8513, 267, '清平乐（木樨）', '人间花少。菊小芙蓉老。冷淡仙人偏得道。买定西风一笑。 前身原是疏梅。黄姑点碎冰肌。惟有暗香长在，饱参清露霏微。'),
	(8514, 267, '减字木兰花', '虚空无碍。你自痴迷不自在。撒手游行。到处笙歌拥路迎。 天然美满。不用些儿心计算。莫听先生。引入深山百丈坑。'),
	(8515, 267, '好事近', '眼里数闲人，只有钓翁潇洒。已佩水仙宫印，恶风波不怕。 此心那许世人知，名姓是虚假。一棹五湖三岛，任船儿尖要。'),
	(8516, 267, '忆帝京', '元来老子曾垂教。挫锐和光为妙。因甚不听他，强要争工巧。只为忒惺惺，惹尽闲烦恼。 你但莫、多愁早老。你但且、不分不晓。第一随风便倒拖，第二君言亦大好。管取没人嫌，便总道、先生俏。'),
	(8517, 267, '一落索', '一夜雨声连晓。青灯相照。旧时情绪此时心，花不见、人空老。 可惜春光闲了。阴多晴少。江南江北水连云，问何处、寻芳草。'),
	(8518, 267, '苏幕遮', '瘦仙人，穷活计。不养丹砂，不肯参同契。两顿家餐三觉睡。闭著门儿，不管人闲事。 又经年，知几岁。老屋穿空，幸有天遮蔽。不饮香醑常似醉。白鹤飞来，笑我颠颠地。'),
	(8519, 267, '蓦山溪（和人冬至韵）', '西江东去，总是伤时泪。北陆日初长，对芳尊、多悲少喜。美人去后，花落几春风，杯漫洗。人难醉。愁见飞灰细。 梅边雪外。风味犹相似。迤逦暖乾坤，仗君王、雄风英气。吾曹老矣，端是有心人、追剑履。辞黄绮。珍重萧生意。'),
	(8520, 267, '临江仙', '信取虚空无一物，个中著甚商量。风头紧后白云忙。风元无去住，云自没行藏。 莫听古人闲语话，终归失马亡羊。自家肠肚自端详。一齐都打碎，放出大圆光。'),
	(8521, 267, '临江仙', '生长西都逢化日，行歌不记流年。花间相过酒家眠。乘风游二室，弄雪过三川。 莫笑衰容双鬓改，自家风味依然。碧潭明月水中天。谁闲如老子，不肯作神仙。'),
	(8522, 267, '临江仙', '堪笑一场颠倒梦，元来恰似浮云。尘劳何事最相亲。今朝忙到夜，过腊又逢春。 流水滔滔无住处，飞光匆匆西沈。世间谁是百年人。个中须著眼，认取自家身。'),
	(8523, 267, '鹊桥仙', '携琴寄鹤，辞山别水，乘兴随云做客。囊中欲试紫金丹，待点化、鸾红凤碧。 谁知到此，玉梯无路，天上难通消息。不如却趁白云归，免误使、山英扫迹。'),
	(8524, 267, '满庭芳', '花满金盆，香凝碧帐，小楼晓日飞光。有人相伴，开镜点新妆。脸嫩琼肌著粉，眉峰秀、波眼宜长。云鬟就，玉纤溅水，轻笑换明珰。 檀郎。犹恣意，高欹凤枕，慵下银床。问今日何处，斗草寻芳。不管余酲未解，扶头酒、亲捧瑶觞。催人起，雕鞍翠幰，乘露看姚黄。'),
	(8525, 642, '豆叶黄', '秋千人散小庭空。麝冷灯昏愁杀侬。独有闲阶两袖风。月胧胧。一树梨花细雨中。'),
	(8526, 642, '豆叶黄', '树头初日鹁鸠鸣。野店山桥新雨晴。短褐无泥竹杖轻。水泠泠。梅片飞时春草青。'),
	(8527, 642, '谒金门·柳丝碧', '柳丝碧。柳下人家寒食。莺语匆匆花寂寂。玉阶春藓湿。 闲凭薰笼无力。心事有谁知得。檀炷绕窗灯背壁。画檐残雨滴。'),
	(8528, 415, '鹊桥仙', '云静天高，山明水阔。一年待个中秋月。十分魄少一分圆，今宵已觉蟾光别。 且尽金尊，遥瞻玉阙。更深江水沙如雪。来宵风雨不相关，重须买酒追佳节。'),
	(8529, 415, '柳梢青（和张元晖清明）', '兰亭丝竹。高会群贤，其人如玉。曲水流觞，灯前细雨，檐花蔌蔌。 舞雩初试春衣，听咏歌、童子五六。泽畔行吟，沙汀拾翠，满江新绿。'),
	(8530, 255, '渔家傲', '水上落红时片片。江头雪絮飞缭乱。渺渺碧波天漾远。平沙暖。花风一阵萍香满。 晚来醉著无人唤。残阳已在青山半。睡觉只疑花改岸。抬头看。元来弱缆风吹断。'),
	(8531, 94, '南歌子（四月二十六日集客临芳观）', '麦陇深初转，桃溪曲渐成。绿槐重叠午阴清。更有榴花一朵、照人明。 画栋清微暑，疏帘入晚晴。请君坐待縠纹平。看取红幢翠盖、引前旌。'),
	(8532, 94, '南乡子（自后圃晚步湖上）', '小院雨新晴。初听黄鹂第一声。满地绿阴人不到，盈盈。一点孤花尚有情。 却傍水边行。叶底跳鱼浪自惊。日暮小舟何处去，斜横。冲破波痕久未平。'),
	(8533, 94, '水龙吟（三月十日西湖宴客作）', '对花常欲留春，恨春故遣花飞早。晓来雨过，绿阴新处，几番芳草。一片飘时，已知消减，满庭谁扫。料多情也似，愁人易感，先催趁、朱颜老。 犹有清明未过，但狂风、匆匆难保。酒醒梦断，年年此恨，不禁相恼。只恐春应，暗留芳信，与花争好。有姚黄一朵，殷勤付与，送金杯倒。'),
	(8534, 94, '鹧鸪天（续采莲曲）', '晓日初开露未晞。夕烟轻散雨还微。暗摇绿雾游鯈戏，斜映红云属玉飞。 情脉脉，恨依依。沙边空见棹船归。何人解舞新声曲，一试纤腰六尺围。'),
	(8535, 94, '临江仙（乙卯八月九日，南山绝项作台新成，与客赏月作）', '绝顶参差千嶂列，不知空水相浮。下临湖海见三州。落霞横暮景，为客小迟留。 卷尽微云天更阔，此行不负清秋。忽惊河汉近人流。青霄元有路，一笑倚琼楼。'),
	(8536, 94, '定风波（鲁卿见和，复答之）', '斜汉初看素月流。坐惊金饼出云头。华发萧然吹素领。光景。何妨分付属沧洲。 莫待霜花飘烂漫。萍岸。更凭佳句尽拘收。解与破除消万事，犹记。一尊同得二年秋。'),
	(8537, 94, '定风波', '渺渺空波下夕阳。睡痕初破水风凉。过雨归云留不住。何处。远村烟树半微茫。 莫笑经年人老矣。归计。得迟留处也何妨。老子兴来殊不浅。帘卷。更邀明月坐胡床。'),
	(8538, 208, '望江南', '西源好，冬日雪中松。携手石坛承爱景，静观天地入清宫。恰似大茅峰。 襟袂冷，琴里意浓浓。吹月洞箫含碧玉，动人佳趣转黄钟。情绪发于中。'),
	(8539, 225, '念奴娇', '素光练静，照青山隐隐，修眉横绿。鳷鹊楼高天似水，碧瓦寒生银粟。万丈辉光，奔云涌雾，飞过卢鸿屋。更无尘翳，皓然冷浸梧竹。 因念鹤发仙翁，当时曾共赏，紫岩飞瀑。对影三人聊痛饮，一洗闲愁千斛。斗转参移，翻然归去，万里骑黄鹄。一川霜晓，叫云吹断横玉。'),
	(8540, 225, '鹧鸪天', '满眼纷纷恰似花。飘飘泊泊自天涯。雨中添得无穷湿，风里吹成一道斜。 银作屋，玉为车。姮娥青女过人家。应嫌素面微微露，故着轻云薄薄遮。'),
	(8541, 564, '忆王孙', '日长高柳一蝉声。翡翠帘深宝簟清。梦远春云不散情。晓风轻。玉楝花飞宿雨晴。'),
	(8542, 467, '减字木兰花', '柳塘微雨。两两飞鸥来复去。倚遍重阑。人在碧云山外山。 一春离怨。日照绮窗长几线。酒病情魔。两事春来无奈何。'),
	(8543, 467, '念奴娇（裁成渊明归去来辞）', '阑干倚处。戏裁成、彭泽当年奇语。三径荒凉怀旧里，我欲扁舟归去。鸟倦知还，寓形宇内，今已年如许。小窗容膝，要寻情话亲侣。 郭外粗有西畴，故园松菊，日涉方成趣。流水涓涓千涧上，云绕奇峰无数。窈窕经丘，风清月了，时看烟中雨。萧然巾岸，引觞寄傲衡宇。'),
	(8544, 510, '浪淘沙（九月十八日与千里赏菊三首）', '又见菊花新。色浅香匀。老人衰病卧漳滨。虽是无聊仍止酒，幸有嘉宾。 不用怨萧辰。不似芳春。请看金蕊照金尊。今夜花前须醉倒，直到黎明。'),
	(8545, 510, '蝶恋花', '只恐夜深花睡去。火照红妆，满意留宾住。凤烛千枝花四顾。消愁更待寻何处。 汉苑红光非浪语。栖静亭前，都是珊瑚树。便请催尊鸣酹鼓。明朝风恶飘红雨。'),
	(8546, 510, '鹧鸪天', '婆律香浓气味佳。玻璃仙碗进流霞。凝膏清涤高阳醉，灵液甘和正焙芽。 香染指，浪浮花。加笾礼尽客还家。贯珠声断红裳散，踏影人归素月斜。'),
	(8547, 510, '蝶恋花', '共乐堂深帘不卷。恻恻寒轻，二月春犹浅。续寿竞来歌舞院。龙涎香衫鲛绡段。 画栋朝飞双语燕。端似知人，著意窥金盏。柳外花前同祝愿。朱颜长在年龄远。'),
	(8548, 380, '梦玉人引', '旧追游处，思前事、俨如昔。过尽莺花，横雨暴风初息。杏子枝头，又自然、别是般天色。好傍垂杨，系画船桥侧。 小欢幽会，一霎时、光景也堪惜。对酒当歌，故人情分难觅。水远山长，不成空相忆。这归去重来，又却是、几时来得。'),
	(8549, 544, '减字木兰花（和人梅词）', '江边一树。愁绝黄昏谁与度。琪树琼枝。不受雄蜂取次欺。 风前望处。直恐乘风吹得去。能动诗情。故与诗人独目成。'),
	(8550, 544, '偷声木兰花（梅）', '景阳楼上钟声晓。半面啼妆匀未了。斜月纷纷。斜影幽香暗断魂。 玉颜应在昭阳殿。却向前村深夜见。冰雪肌肤。还有斑斑雪点无。'),
	(8551, 360, '调笑', '谓花月无情，长寄绮罗之遗恨。试为调笑，戏追风流。少延重客之余欢，聊发清尊之雅兴。诗词：珠树阴中翡翠儿。莫论生小被鸡欺。鹳鹊楼高荡春思，秋瓶盼碧双琉璃。御酥作肌花作骨。燕钗横玉云堆发。使梁年少断肠人，凌波袜冷重城月。 城月。冷罗袜。郎睡不知鸾帐揭。香凄翠被灯明灭。花困钗横时节。河桥杨柳催行色。愁黛有人描得。'),
	(8552, 360, '少年游（长至日席上作）', '遥山雪气入疏帘。罗幕晓寒添。爱日腾波，朝霞入户，一线过冰檐。 绿尊香嫩蒲萄暖，满酌破冬严。庭下早梅，已含芳意，春近瘦枝南。'),
	(8553, 360, '武陵春', '银浦流云初度月，空碧挂团团。照夜珠胎贝阙寒。光彩满长安。 春风为拂新沙路，珂马款天关。篆印金窠红屈盘。嵬磊押千官。'),
	(8554, 360, '玉楼春（立春日）', '小园半夜东风转。吹皱冰池云母面。晓披阊阖见朝阳，知向碧阶添几线。 小烟弄柳晴先暖。残雪禁梅香尚浅。殷勤洗拂旧东君，多少韶华聊借看。'),
	(8555, 360, '玉楼春（定空寺赏梅）', '蕊珠宫里三千女。滴粉为春尘不住。月华冷处欲迎人，七里香风生满路。 一枝谁寄长安去。想得韶光能几许。醉翁满眼玉玲珑，直到烟空云尽处。'),
	(8556, 360, '水调歌头（元会曲）', '九金增宋重，八玉变秦余。上手诏在廷云：六玺之用，尚循秦旧。千年清浸，洗净河洛出图书。一段升平光景，不但五星循轨，万点共连珠。崇宁、大观之间，太史数奏五星循轨，众星顺乡，靡有错乱垂衣本神圣，补衮妙工夫。 朝元去，锵环佩，冷云衢。芝房雅奏，仪凤矫首听笙竽。天近黄麾仗晓，春早红鸾扇暖，迟日上金铺。万岁南山色，不老对唐虞。'),
	(8557, 489, '菩萨蛮', '年时忆著花前醉。而今花落人憔悴。麦浪卷晴川。杜鹃声可怜。 有书无雁寄。初夏槐风细。家在落霞边。愁逢江月圆。'),
	(8558, 545, '江神子', '一江秋水碧湾湾。绕青山。玉连环。帘幕低垂，人在画图间。闲抱琵琶寻旧曲，弹未了，意阑珊。 飞鸿数点拂云端。倚阑看。楚天寒。拟倩东风，吹梦到长安。恰似梨花春带雨，愁满眼，泪阑干。'),
	(8559, 545, '武陵春（茶）', '画烛笼纱红影乱，门外紫骝嘶。分破云团月影亏。雪浪皱清漪。 捧碗纤纤春笋瘦，乳雾泛冰瓷。两袖清风拂袖飞。归去酒醒时。'),
	(8560, 545, '玉楼春', '弄晴数点梨梢雨。门外画桥寒食路。杜鹃飞破草间烟、蛱蝶惹残花底露。 东君著意怜樊素。一段韶华都付与。妆成不管露桃嗔，舞罢从教风柳妒。'),
	(8561, 545, '如梦令', '花落莺啼春暮。陌上绿杨飞絮。金鸭晚香寒，人在洞房深处。无语。无语，叶上数声疏雨。'),
	(8562, 655, '卜算子', '梦里不知眠，觉后眠何在。试问眠身与梦身，那个能只对。 醉后有人醒，醒了无人醉。要识三千与大千，不在微尘外。'),
	(8563, 130, '鹤冲天（溧水长寿乡作）', '梅雨霁，暑风和。高柳乱蝉多。小园台榭远池波。鱼戏动新荷。 薄纱厨，轻羽扇。枕冷簟凉深院。此时情绪此时天。无事小神仙。'),
	(8564, 130, '鹊桥仙令（歇指）', '浮花浪蕊，人间无数，开遍朱朱白白。瑶池一朵玉芙蓉，秋露洗、丹砂真色。 晚凉拜月，六铢衣动，应被姮娥认得。翩然欲上广寒宫，横玉度、一声天碧。'),
	(8565, 130, '青玉案', '良夜灯光簇如豆。占好事、今宵有。酒罢歌阑人散后。琵琶轻放，语声低颤，灭烛来相就。 玉体偎人情何厚。轻惜轻怜转唧口留。雨散云收眉儿皱。只愁彰露，那人知后。把我来僝僽。'),
	(8566, 130, '南浦（中吕）', '浅带一帆风，向晚来、扁舟稳下南浦。迢递阻潇湘，衡皋迥，斜舣蕙兰汀渚。危樯影里，断云点点遥天暮。菡萏里风，偷送清香，时时微度。 吾家旧有簪缨，甚顿作天涯，经岁羁旅。羌管怎知情，烟波上，黄昏万斛愁绪。无言对月，皓彩千里人何处。恨无凤翼身，只待而今，飞将归去。'),
	(8567, 130, '念奴娇（大石）', '醉魂乍醒，听一声啼鸟，幽斋岑寂。淡日朦胧初破晓，满眼娇晴天色。最惜香梅，凌寒偷绽，漏泄春消息。池塘芳草，又还淑景催逼。 因念旧日芳菲，桃花永巷，恰似初相识。荏苒时光，因惯却、觅雨寻云踪迹。奈有离拆，瑶台月下，回首频思忆。重愁叠恨，万般都在胸臆。'),
	(8568, 130, '南乡子（商调）', '晨色动妆楼。短烛荧荧悄未收。自在开帘风不定，飕飕。池面冰澌趁水流。 早起怯梳头。欲绾云鬟又却休。不会沈吟思底事，凝眸。两点春山满镜愁。'),
	(8569, 130, '夜游宫', '一阵斜风横雨。薄衣润、新添金缕。不谢铅华更清素。倚筠窗，弄么弦，娇欲语。 小阁横香雾。正年少、小娥愁绪。莫是栽花被花妒。甚春来，病恹恹，无会处。'),
	(8570, 130, '南乡子', '秋气绕城闉。暮角寒鸦未掩门。记得佳人冲雨别，吟分。别绪多于雨后云。 小棹碧溪津。恰似江南第一春。应是采莲闲伴侣，相寻。收取莲心与旧人。'),
	(8571, 130, '浪淘沙', '万叶战，秋声露结，雁度砂碛。细草和烟尚绿，遥山向晚更碧。见隐隐、云边新月白。映落照、帘幕千家，听数声何处倚楼笛。装点尽秋色。 脉脉。旅情暗自消释。念珠玉、临水犹悲感，何况天涯客。忆少年歌酒，当时踪迹。岁华易老，衣带宽、懊恼心肠终窄。飞散后、风流人阻，蓝桥约、怅恨路隔。马蹄过、犹嘶旧巷陌。叹往事、一一堪伤，旷望极。凝思又把阑干拍。'),
	(8572, 130, '西河', '长安道，潇洒西风时起。尘埃车马晚游行，霸陵烟水。乱鸦栖鸟夕阳中，参差霜树相倚。 到此际。愁如苇。冷落关河千里。追思唐汉昔繁华，断碑残记。未央宫阙已成灰，终南依旧浓翠。 对此景、无限愁思。绕天涯、秋蟾如水。转使客情如醉。想当时、万古雄名，尽作往来人、凄凉事。'),
	(8573, 130, '鹤冲天', '白角簟，碧纱厨。梅雨乍晴初。谢家池畔正清虚。香散嫩芙蕖。 日流金，风解愠。一弄素琴歌舞。慢摇纨扇诉花笺。吟待晚凉天。'),
	(8574, 130, '燕归梁（高平晓）', '帘底新霜一夜浓。短烛散飞虫。曾经洛浦见惊鸿。关山隔、梦魂通。 明星晃晃，回津路转，榆影步花骢。欲攀云驾倩西风。吹清血、寄玲珑。'),
	(8575, 130, '玉楼春（大石）', '当时携手城东道。月堕檐牙人睡了。酒边难使客愁惊，帐底不教春梦到。 别来人事如秋草。应有吴霜侵翠葆。夕阳深锁绿苔门，一任卢郎愁里老。'),
	(8576, 130, '醉桃源（大石）', '冬衣初染远山青。双丝云雁绫。夜寒袖湿欲成冰。都缘珠泪零。 情黯黯，闷腾腾。身如秋后蝇。若教随马逐郎行。不辞多少程。'),
	(8577, 130, '隔浦莲（大石）', '新篁摇动翠葆。曲径通深窈。夏果收新脆，金丸落、惊飞鸟。浓霭迷岸草。蛙声闹。骤雨鸣池沼。 水亭小。浮萍破处，帘花檐影颠倒。纶巾羽扇，困卧北窗清晓。屏里吴山梦自到。惊觉。依然身在江表。'),
	(8578, 130, '垂丝钓（商调）', '缕金翠羽。妆成才见眉妩。倦倚绣帘，看舞风絮。愁几许。寄凤丝雁柱。 春将暮。向层城苑路。钿车似水，时时花径相遇。旧游伴侣。还到曾来处。门掩风和雨。梁间燕语。问那人在否。'),
	(8579, 130, '南乡子（商调）', '晨色动妆楼。短烛荧荧悄未收。自在开帘风不定，飕飕。池面冰澌趁水流。 早起怯梳头。欲绾云鬟又却休。不会沈吟思底事，凝眸。两点春山满镜愁。'),
	(8580, 130, '渔家傲（般涉）', '灰暖香融销永昼。蒲萄架上春藤秀。曲角栏干群雀斗。清明后。风梳万缕亭前柳。 日照钗梁光欲溜。循阶竹粉沾衣袖。拂拂面红如著酒。沈吟久。昨宵正是来时候。'),
	(8581, 130, '丹凤吟（越调）', '迤逦春光无赖，翠藻翻池，黄蜂游阁。朝来风暴，飞絮乱投帘幕。生憎暮景，倚墙临岸，杏靥夭斜，榆钱轻薄。昼永惟思傍枕，睡起无憀，残照犹在亭角。 况是别离气味，坐来但觉心绪恶。痛引浇愁酒，奈愁浓如酒，无计消铄。那堪昏暝，簌簌半檐花落。弄粉调朱柔素手，问何时重握。此时此意，长怕人道著。'),
	(8582, 645, '菩萨蛮（佳人）', '晓来误入桃源洞。恰见佳人春睡重。玉腕枕香腮。荷花藕上开。 一扇俄惊起。敛黛凝秋水。笑倩整金衣。问郎来几时。'),
	(8583, 249, '少年游', '前时相见，楼头窗畔，尊酒望银蟾。如今间阻，银蟾又满，小阁下珠帘。 愿得吴山山前雨，长恁晚廉纤。不见楼头婵娟月，且寂寞、闭窗眠。'),
	(8584, 249, '感皇恩', '终岁忆春回，西园行尽。欢喜梅梢上春信。去年携手，暗约芳时还近。燕来莺又到，人无准。 凭谁向道，流光一瞬。佳景闲无事衣褪。春归何处，又对飞花难问。旧欢都未遇，成新恨。'),
	(8585, 249, '青玉案', '三年宋玉墙东畔。怪相见、常低面。一曲文君芳心乱。匆匆依旧吹散，月淡梨花馆。 秋娘苦妒浮金盏。漏些子堪猜是娇盼。归去相思肠应断。五更无寐，一怀好事，依旧蓝桥远。'),
	(8586, 249, '鹧鸪天（杜四侍郎郡君十二姑生日）', '吉梦灵蛇朱夏宜。佳辰阿母会瑶池。竹风荷雨来消暑，玉李冰瓜可疗饥。 心悟了，道成时。不劳龙女骋威仪。僧祇世界供游戏，贤懿光阴比寿期。'),
	(8587, 249, '满庭芳', '乡物牵情，家山回首，浩然归兴难收。报恩心事，投老拚悠悠。却笑当年牛下，轻自许、激烈寒讴。成何事，夷犹桂辑，兰芷咏芳洲。 人生，萍梗迹，谁非乐土，何处吾州。算不须，临岐惝恍迟留。要看香炉瀑布，丹枫乱、江色凝秋。真堪与，潇湘暮雨，图上画扁舟。'),
	(8588, 249, '少年游', '如今田野谩抛春。红雨掩衡门。懒读诗书，欠伸扶杖，几案任生尘。 从教便向东山老，谁知是个中人。莫怪年来，倦寻城市，嫌我性情真。'),
	(8589, 249, '洞仙歌（温园赏海棠）', '群芳老尽，海棠花时候。雨过寒轻好清昼。最妖饶一段，全是初开，云鬟小，涂粉施朱未就。 全开还自好，骀荡春余，百样宫罗斗繁绣。纵无语也，心应恨来迟，恰柳絮、将春归后。醉犹倚柔柯，怯黄昏，这一点愁，须共花同瘦。'),
	(8590, 249, '行香子（同前）', '前岁栽桃，今岁成蹊。更黄鹂、久住相知。微行清露，细履斜晖。对林中侣，闲中我，醉中谁。 何妨到老，常闲常醉，任功名、生事俱非。衰颜难强，拙语多迟。但酒同行，月同坐，影同嬉。'),
	(8591, 249, '金凤钩（同前）', '雪消闲步花畔。试屈指、早春将半。樱桃枝上最先到，却恨小梅芳浅。 忽惊拂水双来燕。暗自忆、故人犹远。一分风雨占春愁，一来又对花肠断。'),
	(8592, 249, '梁州令垒韵', '田野闲来惯。睡起初惊晓燕。樵青走挂小帘钩，南园昨夜，细雨红芳遍。平芜一带烟光浅。过尽南归雁。俱远。凭栏送目空肠断。 好景难常占。过眼韶华如箭。莫教鶗鴂送韶华，多情杨柳，为把长条绊。 清樽满酌谁为伴。花下提壶劝。何妨醉卧花底，愁容不上春风面。'),
	(8593, 249, '黄莺儿（同前）', '南园佳致偏宜暑。两两三三修篁，新篁新出初齐，猗猗过檐侵户。听乱飐芰荷风，细洒梧桐雨。午余帘影参差，远林蝉声，幽梦残处。 凝伫。既往尽成空，暂遇何曾住。算人间事、岂是追思，依依梦中情绪。观数点茗浮花，一缕香萦炷。怪来人道陶潜，做得羲皇侣。'),
	(8594, 249, '满庭芳（赴信日舟中别次膺十二钗）', '鸥起苹中，鱼惊荷底，画船天上来时。翠湾红渚，宛似武陵迷。更晚青山更好，孤云带、远雨丝垂。清歌里，金尊未掩，谁使动分携。 竹林、高晋阮，阿咸潇散，犹愧风期。便弃官终隐，钓叟苔矶。纵是冥鸿云外，应念我、垂翼低飞。新词好，他年认取，天际片帆归。'),
	(8595, 549, '小梅花', '思前别。记时节。美人颜色如花发。美人归。天一涯。娟娟姮娥，三五满还亏。翠眉蝉鬓生离诀。遥望青楼心欲绝。梦中寻。卧巫云。觉来珠泪，滴向湘水深。 愁无已。奏绿绮。历历高山与流水。妙通神。绝知音。不知暮雨朝云、何山岑。相思无计堪相比。珠箔雕阑几千里。漏将分。月窗明。一夜梅花忽开、疑是君。'),
	(8596, 549, '蝶恋花（改徐冠卿词）', '几许伤春春复暮。杨柳清阴，偏碍游丝度。天际小山桃叶步。白头花满湔裙处。 竟日微吟长短句。帘影灯昏，心寄胡琴语。数点雨声风约住。朦胧淡月云来去。'),
	(8597, 549, '木兰花', '银簧雁柱香檀拨。镂板三声催细抹。舞腰轻怯绛裙长，羞按筑球花十八。 东城柳岸匆匆发。画舫一篙烟水阔。可怜单枕欲眠时，还见尊前前夜月。'),
	(8598, 549, '惜双双', '皎镜平湖三十里。碧玉山围四际。莲荡香风里。彩鸳鸯觉双飞起。 明月多情随柁尾。偏照空床翠被。回首笙歌地。醉更衣处长相记。'),
	(8599, 549, '天门谣·牛渚天门险', '　　天门谣牛渚天门险，限南北、七雄豪占。清雾敛，与闲人登览。待月上潮平波滟滟，塞管轻吹新《阿滥》。风满槛，历历数，西州更点。'),
	(8600, 549, '减字浣溪沙（十五之七）', '闲把琵琶旧谱寻。四弦声怨却沈吟。燕飞人静画堂深。 欹枕有时成雨梦，隔帘无处说春心。一从灯夜到如今。'),
	(8601, 549, '减字浣溪沙（十五之一）', '秋水斜阳演漾金。远山隐隐隔平林。几家村落几声砧。 记得西楼凝醉眼，昔年风物似如今。只无人与共登临。'),
	(8602, 549, '绿头鸭', '玉人家，画楼珠箔临津。托微风、彩箫流怨，断肠马上曾闻。燕堂开、艳妆丛里，调琴思、认歌颦。麝蜡烟浓，玉莲漏短，更衣不待酒初醺。绣屏掩、枕鸳相就，香气渐暾暾。回廊影，疏钟淡月，几许销魂。 翠钗分、银笺封泪，舞鞋从此生尘。住兰舟、载将离恨，转南浦、背西曛。记取明年，蔷薇谢后，佳期应未误行云。凤城远，楚梅香嫩，先寄一枝春。青门外，只凭芳草，寻记郎君。'),
	(8603, 549, '诉衷情（二之一）', '不堪回首卧云乡。羁宦负清狂。年来镜湖风月，鱼鸟两相忘。 秦塞险，楚山苍。更斜阳，画桥流水，曾见扁舟，几度刘郎。'),
	(8604, 549, '忆仙姿（二之一）', '莲叶初生南浦。两岸绿杨飞絮。向晚鲤鱼风，断送彩帆何处。凝伫。凝伫。楼外一江烟雨。'),
	(8605, 549, '浪淘沙（四之四）', '雨过碧云秋。烟草汀洲。远山相对一眉愁。可惜芳年桥畔柳，不系兰舟。 为问木兰舟。何处淹留。相思今夜忍登楼。楼下谁家歌水调。明月杨州。'),
	(8606, 549, '下水船', '芳草青门路。还拂京尘东去。回想当年离绪。送君南浦。愁几许。尊酒流连薄暮。帘卷津楼风雨。 凭阑语。草草蘅皋赋。分首惊鸿不驻。灯火虹桥，难寻弄波微步。漫凝伫。莫怨无情流水，明月扁舟何处。'),
	(8607, 549, '薄幸', '艳真多态。更的的、频回眄睐。便认得、琴心相许，与写宜男双带。记画堂、斜月朦胧，轻颦微笑娇无奈。便翡翟屏开，芙蓉帐掩，与把香罗偷解。 自过了收灯后，都不见、踏青挑菜。几回凭双燕，丁宁深意，往来翻恨重帘碍。约何时再。正春浓酒暖，人闲昼永无聊赖。厌厌睡起，犹有花梢日在。'),
	(8608, 549, '人南渡（感皇恩）', '兰芷满芳洲，游丝横路。罗袜尘生步。迎顾。整鬟颦黛，脉脉两情难语。细风吹柳絮。人南渡。 回首旧游，山无重数。花底深朱户。何处。半黄梅子，向晚一帘疏雨。断魂分付与。春将去。'),
	(8609, 549, '思牛女', '楼角参横，庭心月午。侵阶夜色凉经雨。轻罗小扇扑流萤，微云度汉思牛女。 拥髻柔情，扶肩暱语。可怜分破□□□。□□□□有佳期，人间底事长如许。'),
	(8610, 556, '浣溪沙', '少日怀山老住山。一官休务得身闲。几年食息白云闲。 似我乐来真是少，见人忙处不相关。养真高静出尘寰。'),
	(8611, 556, '西江月', '人世一场大梦，我生魔了十年。明窗千古探遗编。不救饥寒一点。 更被维摩老子，不教此处容言。炉熏清炷坐安禅。物物头头显现。'),
	(8612, 312, '临江仙', '翠袖卷纱红映肉，无风玉骨生寒。可堪新晓雨初残。颦眉谁恼著，粉泪滴阑干。闻道谪仙歌妙语，新妆再发愁颜。雾帘云幕荐金盘。笔间长借句，直莫放春还。'),
	(8613, 312, '过秦楼', '卖酒炉边，寻芳原上，乱花飞絮悠悠。已蝶稀莺散，便拟把长绳、系日无由。谩道草忘忧。也徒将、酒解闲愁。正江南春尽，行人千里，苹满汀洲。 有翠红径里，盈盈似簇，芳茵禊饮，时笑时讴。当暖风迟景，任相将永日，烂熳狂游。谁信盛狂中，有离情、忽到心头。向尊前拟问，双燕来时，曾过秦楼。'),
	(8614, 468, '诉衷情（思归）', '劳生奔走因粗官。揽镜鬓毛斑。物外平生萧散，微宦兴阑珊。 寄胜处，每凭阑。定忘还。好山如画，水绕云萦。无计成闲。'),
	(8615, 468, '点绛唇（示儿尹仁尹智）', '莘野寥寥，渭滨漠漠情何限。万重堆案。懒更重经眼。 儿辈休惊，头上霜华满。功名晚。水云萧散。漫就驿亭看。'),
	(8616, 451, '醉蓬莱', '见扬州独有，天下无双，号为琼树。占断天风，岁花开两次。九朵一苞，攒成环玉，心似珠玑缀。瓣瓣玲珑，枝枝洁净，世上无花类。冷露朝凝，香风远送，信是琼瑶贵。料得天宫有，此地久难留住。翰苑才人，贵家公子，都要看花去。莫吝金钱，好寻诗伴，日日花前醉。'),
	(8617, 451, '夜游宫', '何事东君又去。空满院、落花飞絮。巧燕呢喃向人语。何曾解、说伊家、些子苦。况是伤心绪。念个人、又成暌阻。一觉相思梦回处。连宵雨、更那堪、闻杜宇。'),
	(8618, 451, '念奴娇（赤壁舟中咏雪）', '中流鼓楫，浪花舞，正见江天飞雪。远水长空连一色，使我吟怀逸发。寒峭千峰，光摇万象，四野人踪灭。孤舟垂钓，渔蓑真个清绝。 遥想溪上风流，悠然乘兴，独棹山阴月。争似楚江帆影净，一曲浩歌空阔。禁体词成，过眉酒热，把唾壶敲缺。冯夷惊道，城翁无比赤壁。'),
	(8619, 451, '满庭芳（赏梅）', '庭院余寒，帘栊清晓，东风初破丹苞。相逢未识，错认是夭桃。休道寒香波较晚，芳丛里、便觉孤高。凭阑久，巡檐索笑，冷蕊向青袍。 扬州，春兴动，主人情重，招集吟豪。信冰姿潇洒，趣在风骚。脉脉此情谁会，和羹事、且付香醪。归来后，湖头月淡，伫立看烟涛。'),
	(8620, 451, '渔家傲', '刚过淮流风景变。飞沙四面连天卷。霜拆冻髭如利剪。情莫遣。素衣一任缁尘染。 回首家山云渐远。离肠暗逐车轮转。古木荒烟鸦点点。人不见。平原落日吟羌管。'),
	(8621, 451, '虞美人（三之一）', '高城望断尘如雾。不见联骖处。夕阳村外小湾头。只有柳花无数。送归舟。 琼枝玉树频相见。只恨离人远。欲将幽事寄青楼。争奈无情江水、不西流。'),
	(8622, 59, '安平乐慢', '细想劳生，等闲聚散，冉冉轻似秋烟。莲心暗苦，月意难圆。神京去路三千。当日风流，有妖饶枕上，软媚尊前。何计访蓬仙。断肠中、一叶晴川。 至而今、追思往事，奈向梦也难到奴边。自恨不如兰灯，通宵尚照伊眠。恰道无缘。被人劝休莫瞒天。多应是、前生负你，今世使我偿填。'),
	(8623, 282, '洞仙歌', '廉纤细雨，殢东风如困。萦断千丝为谁恨。向楚宫一梦，千古悲凉，无处问。愁到而今未尽。 分明都是泪，泣柳沾花，常与骚人伴孤闷。记当年、得意处，酒力方融，怯轻寒、玉炉香润。又岂识、情怀苦难禁，对点滴檐声，夜寒灯晕。'),
	(8624, 604, '调笑转踏', '上助清欢。女伴相将，调笑入队。 春楼有女字罗敷。二十未满十五余。金钚约腕携笼去，攀枝摘叶城南隅。使君春思如飞絮。五马徘徊芳草路。东风吹鬓不可亲，日晚蚕饥欲归去。 归去。携笼女。南阳柔桑三月暮。使君春思如飞絮。五马徘徊频驻。蚕饥日晚空留顾。笑指秦楼归去。 石城女子名莫愁。家住石城西渡头。拾翠每寻芳草路，采莲时过绿苹洲。五陵豪客青楼上。醉倒金壶待清唱。风高江阔白浪飞，争催艇子操双桨。 双桨。小舟荡。唤取莫愁迎叠浪。五陵豪客青楼上。不道风高江广。千金难买倾城样。那听绕梁清唱。 绣户朱帘翠幕张。主人置酒宴华堂。相如年少多才调，消得文君暗断肠。断肠初认琴心挑。么弦暗写相思调。从来万曲不关心，此度伤心何草草。 草草。最年少。绣户银屏人窈窕。瑶琴暗写相思调。一曲关心多少。临邛客舍成都道。苦恨相逢不早。 湲湲流水武陵溪。洞里春长日月迟。红英满地无人扫，此度刘郎去后迷。行行渐入清流浅。香风引到神仙馆。琼浆一饮觉身轻，玉砌云房瑞烟暖。 烟暖。武陵晚。洞里春长花烂熳。红英满地溪流浅。渐听云中鸡犬。刘郎迷路香风远。误到蓬莱仙馆。 少年锦带佩吴钩。铁马追风塞草秋。凭仗匣中三尺剑，扫平骄虏取封侯。红颜少妇桃花脸。笑倚银屏施宝靥。明眸妙齿起相迎，青楼独占阳春艳。 春艳。桃花脸。笑倚银屏施宝靥。良人少有平戎胆。归路光生弓剑。青楼春永香帏掩。独把韶华都占。 翠盖银鞍冯子都。寻芳调笑酒家胡。吴姬十五夭桃色，巧笑春风当酒垆。玉壶丝络临朱户。结就罗裙表情素。红裙不惜裂香罗，区区私爱徒相慕。 相慕。酒家女。巧笑明眸年十五。当垆春永寻芳去。门外落花飞絮。银鞍白马金吾子。多谢结裙情素。 楼上青帘映绿杨。江波千里对微茫。潮平越贾催船发，酒熟吴姬唤客尝。吴姬绰约开金盏。的的娇波流美盼。秋风一曲采菱歌，行云不度人肠断。 肠断。浙江岸。楼上青帘新酒软。吴姬绰约开金盏。的的娇波流盼。采菱歌罢行云散。望断侬家心眼。 花阴转午漏频移。宝鸭飘帘绣幕垂。眉山敛黛云堆髻，醉倚春风不自持。偷眼刘郎年最少。云情雨态知多少。花前月下恼人肠，不独钱塘有苏小。 苏小。最娇妙。几度尊前曾调笑。云情雨态知多少。悔恨相逢不早。刘郎襟韵正年少。风月今宵偏好。 金翘斜亸淡梳妆。绰约天葩自在芳。几番欲奏阳关曲，泪湿春风眼尾长。落花飞絮青门道。浓愁不散连芳草。骖鸾乘鹤上蓬莱，应笑行云空梦悄。 梦悄。翠屏晓。帐里薰炉残蜡照。赏心乐事能多少。忍听阳关声调。明朝门外长安道。怅望王孙芳草。 绰约妍姿号太真。肌肤冰雪怯轻尘。霞衣乍举红摇影，按出霓裳曲最新。舞钗斜亸乌云发。一点春心幽恨切。蓬莱虽说浪风轻，翻恨明皇此时节。 时节。白银阙。洞里春晴百和爇。兰心底事多悲切。消尽一团冰雪。明皇恩爱云山绝。谁道蓬莱安悦。 江上新晴暮霭飞。碧芦红蓼夕阳微。富贵不牵渔父目，尘劳难染钓人衣。白鸟孤飞烟柳杪。采莲越女清歌妙。腕呈金钏掉鸣榔，惊起鸳鸯归调笑。 调笑。楚江渺。粉面修眉花斗好。擎荷折柳争相调。惊起鸳鸯多少。渔歌齐唱催残照。一叶归舟轻小。 千里潮平小渡边。帘歌白纻絮飞天。苏苏不怕梅风软，空遣春心著意怜。燕钗玉股横青发。怨托琵琶恨难说。拟将幽恨诉新愁，新愁未尽弦声切。 声切。恨难说。千里潮平春浪阔。梅风不解相思结。忍送落花飞雪。多才一去芳音绝。更对珠帘新月。'),
	(8625, 248, '清平乐', '娇羞未惯。长是低花面。笑里爱将红袖掩。遮却双双笑靥。 早来帘下逢伊。怪生频整衫儿。元是那回欢会，齿痕犹在凝脂。'),
	(8626, 248, '黄河清', '晴景初升风细细。云收天淡如洗。望外凤凰双阙，葱葱佳气。朝罢香烟满袖，近臣报、天颜有喜。夜来连得封章，奏大河、彻底清泚。 君王寿与天齐，馨香动上穹，频降嘉瑞。大晟奏功，六乐初调清徵。合殿春风乍转，万花覆、千官尽醉。内家传敕，重开宴、未央宫里。'),
	(8627, 248, '南歌子', '月到中秋夜，还胜别夜圆。高河瑟瑟转金盘。三十六宫深处、卷帘看。 香雾云鬟湿，清辉玉臂寒。寻常岂是不婵娟。吟赏莫辞终夕、动经年。'),
	(8628, 248, '行香子', '别恨绵绵。屈指三年。再相逢、情分依然。君初霜鬓，我已华颠。况其间有，多少恨，不堪言。 小庭幽槛，菊蕊阑斑。近清宵、月已婵娟。莫思身外，且斗樽前。愿花长好，人长健，月长圆。'),
	(8629, 248, '丑奴儿', '来朝匹马萧萧去，且醉芳卮。明夜天涯。浅酌低吟欲殢谁。 归来应过重阳也，菊有残枝。纤手重携。未必秋香一夜衰。'),
	(8630, 248, '黄鹂绕碧树', '鸳瓦霜轻，玳帘风细，高门瑞气非烟。积厚源深，有长庚应梦，乔岳生贤。妙龄秀发，庆谢庭、兰玉争妍。名动缙绅，况文章政术，俱是家传。 别有阴功厚德，向东州、治狱平反。玉函高篆，仙风道骨，锡与长年。最好素秋新霁，对画堂、高启宾筵。何妨纵乐笙歌，剩举觥船。'),
	(8631, 248, '安公子', '帝里重阳好。又对短发来吹帽。满目风光还似旧，奈樽前人老。暗忆当年，伴侣同倾倒。夸俊游、争买千金笑。到如今憔悴，恰似华胥一觉。 此恨何时了。旧游屈指愁重到。小曲深坊闲信马，掩朱扉悄悄。怎得个多情，为我传音耗。但向伊、耳边轻轻道。道近来应是，忘了卢郎年少。'),
	(8632, 693, '卜算子', '要见不得见，要近不得近。试问得君多少怜，管不解、多于恨。 禁止不得泪，忍管不得闷。天上人间有底愁，向个里、都谙尽。'),
	(8633, 693, '桃源忆故人', '碧天露洗春容净。淡月晓收残晕。花上密烟飘尽。花底莺声嫩。 云归楚峡厌厌困。两点遥山新恨。和泪暗弹红粉。生怕人来问。'),
	(8634, 693, '留春令', '江南一雁横秋水。叹咫尺、断行千里。回纹机上字纵横，欲寄远。凭谁是。 谢客池塘春都未。微微动、短墙桃李。半阴才暖却清寒，是瘦损、人天气。'),
	(8635, 693, '好事近', '不见片时霎，魂梦镇相随著。因甚近新无据，误窃香深约。 思量模样忔憎儿，恶又怎生恶。终待共伊相见，与佯佯奚落。'),
	(8636, 693, '鹧鸪天（重九日集句）', '寒雁初来秋影寒。霜林风过叶声乾。龙山落帽千年事，我对西风犹整冠。 兰委佩，菊堪餐。人情时事半悲欢。但将酩酊酬佳节，更把茱萸仔细看。'),
	(8637, 693, '归田乐引', '暮雨濛阶砌。漏渐移、转添寂寞，点点心如碎。怨你又恋你。恨你惜你。毕竟教人怎生是。 前欢算未已。奈向如今愁无计。为伊聪俊，销得人憔悴。这里诮睡里。梦里心里。一向无言但垂泪。'),
	(8638, 693, '西江月', '月，小舟坐水眠空。雾窗春色翠如葱。睡起云涛正拥。¤往事回头笑处，此生弹指声中。玉笺佳句敏惊鸿。闻道衡阳价重。”次韵酬之。时余方谪宜阳，而洪归分宁龙安 月侧金盆堕水，雁回醉墨书空。君诗秀色雨园葱。想见衲衣寒拥。 蚁穴梦魂人世，杨花踪迹风中。莫将社燕等秋鸿。处处春山翠重。'),
	(8639, 693, '两同心', '一笑千金。越样情深。曾共结、合欢罗带，终愿效、比翼纹禽。许多时，灵利惺惺，蓦地昏沈。 自从官不容针。直至而今。你共人、女边著子，争知我、门里挑心。记携手，小院回廊，月影花阴。'),
	(8640, 693, '虞美人（至当涂呈郭功甫）', '平王本爱江湖住。鸥鹭无人处。江南江北水云连。莫笑醯鸡歌舞、瓮中天。 当涂舣棹蒹葭外。赖有宾朋在。此身无路入修门。惭愧诗翁清些、与招魂。'),
	(8641, 693, '渔家傲', '百丈峰头开古镜。马驹踏杀重苏醒。接得古灵心眼净。光炯炯。归来藏在袈裟影。 好个佛堂佛不圣。祖师沈醉犹看镜。却与斩新提祖令。方猛省。无声三昧天皇饼。'),
	(8642, 693, '渔家傲', '忆昔药山生一虎。华亭船上寻人渡。散却夹山拈坐具。呈见处。繁驴橛上合头语。 千户垂丝君看取。离钩三寸无生路。蓦口一桡亲子父，犹回顾。瞎驴丧我儿孙去。'),
	(8643, 693, '鹧鸪天', '教服长松汤，遂复为完人 汤泛冰瓷一坐春。长松林下得灵根。吉祥老子亲拈出，个个教成百岁人。 灯焰焰，酒醺醺。壑源曾未醒酲魂。与君更把长生碗，聊为清歌驻白云。'),
	(8644, 693, '鹧鸪天', '紫菊黄花风露寒。平沙戏马雨新干。且看欲尽花经眼，休说弹冠与挂冠。 甘酒病，废朝餐。何人得似醉中欢。十年一觉扬州梦，为报时人洗眼看。'),
	(8645, 693, '木兰花令', '黔中士女游晴昼。花信轻寒罗袖透。争寻穿石道宜男，更买江鱼双贯柳。 竹枝歌好移船就。依倚风光垂翠袖。满倾芦酒指谐围，相守与郎如许寿。'),
	(8646, 693, '阮郎归', '黔中桃李可寻芳。摘茶人自忙。月团犀腌斗圆方。研膏入焙香。 青箬裹，绛纱囊。品高闻外江。酒阑传碗舞红裳。都濡春味长。'),
	(8647, 693, '雨中花（送彭文思使君）', '政乐中和，夷夏宴喜，官梅乍传消息。待作新年欢计，断送春色。桃李成阴，甘棠少讼，又移旌戟。念画楼朱阁，风流高会，顿冷谈席。 西州纵有，舞裙歌板，谁共茗邀棋敌。归来未得，先沾离袖，管弦催滴。乐事赏心易散，良辰美景难得。会须醉倒，玉山扶起，更倾春碧。'),
	(8648, 278, '踏莎行', '一别芳容，五经寒暑。回文欲寄无鳞羽。多情犹自梦中来，向人粉泪流如雨。梦破南窗，愁肠万缕。那听角动城头鼓。人生弹指事成空，断魂惆怅无寻处。'),
	(8649, 278, '南乡子', '睡起绕回塘。不见衔泥燕子忙。前日花梢都绿遍，西墙。犹有轻风递暗香。 步懒恰寻床。卧看游丝到地长。自恨无聊常病酒。凄凉。岂有才情似沈阳。'),
	(8650, 278, '丑奴儿（谢人寄蜡梅）', '春风似有灯前约，先报佳期。点缀相宜。天气犹寒蝶未知。 嫩黄染就蜂须巧，香压团枝。淡注仙衣。方士临门未起时。'),
	(8651, 278, '朝中措', '腊穷天际傍危栏。密雪舞初残。表里江山如画，分明不似人间。 功名何在，文章漫与，空叹流年。独恨归来已晚，半生孤负渔竿。'),
	(8652, 278, '鹧鸪天', '避暑佳人不著妆。水晶冠子薄罗裳。摩绵扑粉飞琼屑，滤蜜调冰结绛霜。 随定我，小兰堂。金盆盛水绕牙床。时时浸手心头熨，受尽无人知处凉。'),
	(8653, 278, '满庭芳（八月十六夜，景修咏东坡旧词，因韵成此）', '一到江南，三逢此夜，举头羞见婵娟。黯然怀抱，特地遣谁宽。分外清光泼眼，迷滉漾、无计拘拦。天如洗，星河尽掩，全胜异时看。 佳人，还忆否，年时此际，相见方难。谩红绫偷寄，孤被添寒。何事佳期再睹，翻怅望、重叠关山。归来呵，休教独自，肠断对团圆。'),
	(8654, 494, '阮郎归', '歌停檀板舞停鸾。高阳饮兴阑。兽烟喷尽玉壶干。香分小凤团。 雪浪浅，露珠圆。捧瓯春笋寒。绛纱笼下跃金鞍。归时人倚栏。'),
	(8655, 494, '南乡子（赠行）', '旌旗满江湖。诏发楼船万舳舻。投笔将军因笑我，迂儒。帕首腰刀是丈夫。 粉泪怨离居。喜子垂窗报捷书。试问伏波三万语，何如。一斛明珠换绿珠。'),
	(8656, 494, '少年游（元丰六年七月六日，王文甫家饮酿白酒，大醉。集古句作墨竹词）', '雨洗娟娟嫩叶光。风吹细细绿筠香。秀色乱侵书帙晚。帘卷。清阴微过酒尊凉。 人画竹身肥拥肿。何用。先生落笔胜萧郎。记得小轩岑寂夜。廊下。月和疏影上东墙。'),
	(8657, 494, '少年游（送元素）', '千古风流阮步兵。平生游宦爱东平。千里远来还不住。归去。空留风韵照人清。 红粉尊前深懊恼。休道。怎生留得许多情。记得明年花絮乱。须看。泛西湖是断肠声。'),
	(8658, 494, '少年游（感旧）', '莫怪鸳鸯绣带长。腰轻不胜舞衣裳。薄幸只贪游冶去。何处。垂杨系马恣轻狂。花谢絮飞春又尽。堪恨。断弦尘管伴啼妆。不信归来但自看。怕见。为郎憔悴却羞郎。'),
	(8659, 494, '渔父', '渔父醉，蓑衣舞。醉里却寻归路。轻舟短棹任斜横，醒后不知何处。'),
	(8660, 494, '水龙吟', '小沟东接长江，柳堤苇岸连云际。烟村潇洒，人闲一哄，渔樵早市。永昼端居，寸阴虚度，了成何事。但丝莼玉藕，珠粳锦鲤，相留恋，又经岁。 因念浮丘旧侣，惯瑶池、羽觞沈醉。青鸾歌舞，铢衣摇曳，壶中天地。飘堕人间，步虚声断，露寒风细。抱素琴，独向银蟾影里，此怀难寄。'),
	(8661, 494, '雨中花慢', '嫩脸羞蛾，因甚化作行云，却返巫阳。但有寒灯孤枕，皓月空床。长记当初，乍谐云雨，便学鸾凤。又岂料、正好三春桃李，一夜风霜。 丹青□画，无言无笑，看了漫结愁肠。襟袖上，犹存残黛，渐减余香。一自醉中忘了，奈何酒后思量。算应负你，枕前珠泪，万点千行。'),
	(8662, 494, '如梦令（题淮山楼）', '城上层楼叠巘。城下清淮古汴。举手揖吴云，人与暮天俱远。魂断。魂断。后夜松江月满。'),
	(8663, 494, '减字木兰花（胜之）', '双鬟绿坠。娇眼横波眉黛翠。妙舞蹁跹。掌上身轻意态妍。 曲穷力困。笑倚人旁香喘喷。老大逢欢。昏眼犹能仔细看。'),
	(8664, 494, '减字木兰花（赠徐君猷三侍人妩卿）', '娇多媚煞。体柳轻盈千万态。殢主尤宾。敛黛含颦喜又瞋。 徐君乐饮。笑谑从伊情意恁。脸嫩敷红。花倚朱阑里住风。'),
	(8665, 494, '浣溪沙（春情）', '桃李溪边驻画轮。鹧鸪声里倒清尊。夕阳虽好近黄昏。 香在衣裳妆在臂，水连芳草月连云。几时归去不销魂。'),
	(8666, 494, '浣溪沙（有感）', '傅粉郎君又粉奴。莫教施粉与施朱。自然冰玉照香酥。 有客能为神女赋，凭君送与雪儿书。梦魂东去觅桑榆。'),
	(8667, 494, '浣溪沙（九月九日二首）', '珠桧丝杉冷欲霜。山城歌舞助凄凉。且餐山色饮湖光。 共挽朱轓留半日，强揉青蕊作重阳。不知明日为谁黄。'),
	(8668, 494, '减字木兰花', '曰：“臣无功受赏。”帝曰：“此事岂容卿有功乎？”同舍每以为笑。余过吴兴，而李公择生子，三日会客，求歌辞，乃作此词戏之，举座皆绝倒。 惟熊佳梦。释氏老君亲抱送。壮气横秋。未满三朝已食牛。 犀钱玉果。利市平分沾四坐。多谢无功。此事如何到得侬。'),
	(8669, 494, '减字木兰花（赠润守许仲涂，且以“郑容落籍、高莹从良”为句首）', '郑庄好客。容我尊前先堕帻。落笔生风。籍籍声名不负公。 高山白早。莹骨冰肤那解老。从此南徐。良夜清风月满湖。'),
	(8670, 494, '点绛唇（己巳重九和苏坚）', '我辈情钟，古来谁似龙山宴。而今楚甸。戏马余飞观。 顾谓佳人，不觉秋强半。筝声远。鬓云吹乱。愁入参差雁。'),
	(8671, 494, '虞美人（述怀）', '归心正似三春草。试著莱衣小。橘怀几日向翁开。怀祖已瞋文度、不归来。 禅心已断人间爱。只有平交在。笑论瓜葛一枰同。看取灵光新赋、有家风。'),
	(8672, 494, '菩萨蛮', '买田阳羡吾将老。从来只为溪山好。来往一虚舟。聊随物外游。 有书仍懒著。水调歌归去。筋力不辞诗。要须风雨时。'),
	(8673, 494, '菩萨蛮（杭妓往苏迓新守）', '玉童西迓浮丘伯。洞天冷落秋萧瑟。不用许飞琼。瑶台空月明。 清香凝夜宴。借与韦郎看。莫便向姑苏。扁舟下五湖。'),
	(8674, 494, '菩萨蛮（西湖）', '秋风湖上萧萧雨。使君欲去还留住。今日漫留君。明朝愁杀人。 佳人千点泪。洒向长河水。不用敛双蛾。路人啼更多。'),
	(8675, 494, '行香子（寓意）', '三入承明。四至九卿。问书生、何辱何荣。金张七叶，纨绮貂缨。无汗马事，不献赋，不明经。 成都卜肆，寂寞君平。郑子真、岩谷躬耕。寒灰炙手，人重人轻。除竺乾学，得无念，得无名。'),
	(8676, 494, '江神子', '曲，余尝作数绝以纪其事矣。 玉人家在凤凰山。水云间。掩门关。门外行人，立马看弓弯。十里春风谁指似，斜日映，绣帘斑。 多情好事与君还。闵新鳏。拭余潸。明月空江，香雾著云鬟。陌上花开春尽也，闻旧曲，破朱颜。'),
	(8677, 494, '三部乐（情景）', '美人如月。乍见掩暮云，更增妍绝。算应无恨，安用阴晴圆缺。娇甚空只成愁，待下床又懒，未语先咽。数日不来，落尽一庭红叶。 今朝置酒强起，问为谁减动，一分香雪。何事散花却病，维摩无疾。却低眉、惨然不答。唱金缕、一声怨切。堪折便折。且惜取、少年花发。'),
	(8678, 494, '十拍子（暮秋）', '白酒新开九酝，黄花已过重阳。身外徜来都似梦，醉里无何即是乡。东坡日月长。 玉粉旋烹茶乳，金薤新捣橙香。强染霜髭扶翠袖。莫道狂夫不解狂。狂夫老更狂。'),
	(8679, 494, '瑞鹧鸪（观潮）', '碧山影里小红旗。侬是江南踏浪儿。拍手欲嘲山简醉，齐声争唱浪婆词。 西兴渡口帆初落、渔浦山头日未欹。侬欲送潮歌底曲，尊前还唱使君诗。'),
	(8680, 494, '望江南（暮春）', '春已老，春服几时成。曲水浪低蕉叶稳，舞雩风软纻罗轻。酣咏乐升平。 微雨过，何处不催耕。百舌无言桃李尽，柘林深处鹁鸪鸣。春色属芜菁。'),
	(8681, 494, '好事近（湖上）', '湖上雨晴时，秋水半篙初没。朱槛俯窥寒鉴，照衰颜华发。 醉中吹坠白纶巾，溪风漾流月。独棹小舟归去，任烟波飘兀。'),
	(8682, 494, '南歌子（湖州作）', '山雨潇潇过，溪桥浏浏清。小园幽榭枕苹汀。门外月华如水、彩舟横。 苕岸霜花尽，江湖雪阵平。两山遥指海门青。回首水云何处、觅孤城。'),
	(8683, 494, '南歌子（晚春）', '日薄花房绽，风和麦浪轻。夜来微雨洗郊坰。正是一年春好、近清明。 已改煎茶火，犹调入粥饧。使君高会有余清。此乐无声无味、最难名。'),
	(8684, 494, '临江仙（辛未离杭至润，别张弼秉道）', '我劝髯张归去好，从来自己忘情。尘心消尽道心平。江南与塞北，何处不堪行。 俎豆庚桑真过矣，凭君说与南荣。愿闻吴越报丰登。君王如有问，结袜赖王生。'),
	(8685, 494, '临江仙', '以为异人。后十年，筑室黄冈之北，号静安居士。作此记之。 细马远驮双侍女，青巾玉带红靴。溪山好处便为家。谁知巴峡路，却见洛城花。 面旋落英飞玉蕊，人间春日初斜。十年不见紫云车。龙丘新洞府，铅鼎养丹砂。'),
	(8686, 494, '归朝欢', '“我梦扁舟浮震泽。雪浪横江千顷白。觉来满眼是庐山，倚天无数开青壁。”盖实梦也。然公诗复云：“扁舟震泽定何时，满眼庐山觉又非。” 我梦扁舟浮震泽。雪浪摇空千顷白。觉来满眼是庐山，倚天无数开青壁。此生长接淅。与君同是江南客。梦中游，觉来清赏，同作飞梭掷。 明日西风还挂席。唱我新词泪沾臆。灵均去后楚山空，沣阳兰芷无颜色。君才如梦得。武陵更在西南极。竹枝词，莫摇新唱，谁谓古今隔。'),
	(8687, 494, '满江红（正月十三日送文安国还朝）', '天岂无情，天也解、多情留客。春向暖、朝来底事，尚飘轻雪。君过春来纡组绶，我应归去耽泉石。恐异时、怀酒忽相思，云山隔。 浮世事，俱难必。人纵健，头应白。何辞更一醉，此欢难觅。欲向佳人诉离恨，泪珠先已凝双睫。但莫遣、新燕却来时，音书绝。'),
	(8688, 250, '梁州令', '莫唱阳关曲。泪湿当年金缕。离歌自古最消魂，闻歌更在魂消处。 南楼杨柳多情绪。不系行人住。人情却似飞絮。悠扬便逐春风去。'),
	(8689, 250, '秋蕊香', '歌彻郎君秋草。别恨远山眉小。无情莫把多情恼。第一归来须早。 红尘自古长安道。故人多。相思不比相逢好。此别朱颜应老。'),
	(8690, 250, '踏莎行', '雪尽寒轻，月斜烟重。清欢犹记前时共。迎风朱户背灯开，拂檐花影侵帘动。 绣枕双鸳，香苞翠凤。从来往事都如梦。伤心最是醉归时，眼前少个人人送。'),
	(8691, 250, '采桑子', '无端恼破桃源梦，明日青楼。玉腻花柔。不学行云易去留。 应嫌衫袖前香冷，重傍金虬。歌扇风流。遮尽归时翠黛愁。'),
	(8692, 250, '采桑子', '高吟烂醉淮西月，诗酒相留。明日归舟。碧藕花中醉过秋。 文姬赠别双团扇，自写银钩。散尽离愁。携得清风出画楼。'),
	(8693, 250, '采桑子', '芦鞭坠遍杨花陌，晚见珍珍。疑是朝云。来作高唐梦里人。 应怜醉落楼中帽，长带歌尘。试拂香茵。留解金鞍睡过春。'),
	(8694, 250, '虞美人', '一弦弹尽仙韶乐。曾破千金学。玉楼银烛夜深深。愁见曲中双泪、落香襟。 从来不奈离声怨。几度朱弦断。未知谁解赏新音。长是好风明月、暗知心。'),
	(8695, 250, '虞美人', '飞花自有牵情处。不向枝边坠。随风飘荡已堪愁。更伴东流流水、过秦楼。 楼中翠黛含春怨。闲倚阑干见。远弹双泪惜香红。暗恨玉颜光景、与花同。'),
	(8696, 250, '两同心', '楚乡春晚，似入仙源。拾翠处、闲随流水，踏青路、暗惹香尘。心心在，柳外青帘，花下朱门。 对景且醉芳尊。莫话消魂。好意思、曾同明月，恶滋味、最是黄昏。相思处，一纸红笺，无限啼痕。'),
	(8697, 250, '泛清波摘遍', '催花雨小，著柳风柔，都似去年时候好。露红烟绿，尽有狂情斗春早。长安道。秋千影里，丝管声中，谁放艳阳轻过了。倦客登临，暗惜光阴恨多少。 楚天渺。归思正如乱云，短梦未成芳草。空把吴霜鬓华，自悲清晓。帝城杳。双凤旧约渐虚，孤鸿后期难到。且趁朝花夜月，翠尊频倒。'),
	(8698, 250, '木兰花', '阿茸十五腰肢好。天与怀春风味早。画眉匀脸不知愁，殢酒熏香偏称小。 东城杨柳西城草。月会花期如意少。思量心事薄轻云，绿镜台前还自笑。'),
	(8699, 250, '清平乐', '心期休问。只有尊前分。勾引行人添别恨。因是语低香近。 劝人满酌金钟。清歌唱彻还重。莫道后期无定，梦魂犹有相逢。'),
	(8700, 250, '清平乐', '沈思暗记。几许无凭事。菊靥开残秋少味。闲却画阑风意。 梦云归处难寻。微凉暗入香襟。犹恨那回庭院，依前月浅灯深。'),
	(8701, 250, '清平乐', '幺弦写意。意密弦声碎。书得凤笺无限事。犹恨春心难寄。 卧听疏雨梧桐。雨余淡月朦胧。一夜梦魂何处，那回杨叶楼中。'),
	(8702, 250, '南乡子·画鸭懒熏香', '画鸭懒熏香。绣茵犹展旧鸳鸯。不似同衾愁易晓，空床。细剔银灯怨漏长。 几夜月波凉。梦魂随月到兰房。残睡觉来人又远，难忘。便是无情也断肠。'),
	(8703, 250, '南乡子·渌水带青潮', '渌水带青潮。水上朱阑小渡桥。桥上女儿双笑靥，妖娆。倚著阑干弄柳条。 月夜落花朝。减字偷声按玉箫。柳外行人回首处，迢迢。若比银河路更遥。'),
	(8704, 250, '蝶恋花', '庭院碧苔红叶遍。金菊开时，已近重阳宴。日日露荷凋绿扇。粉塘烟水澄如练。 试倚凉风醒酒面。雁字来时，恰向层楼见。几点护霜云影转。谁家芦管吹秋怨。'),
	(8705, 409, '诉衷情（又和秀老·五之五）', '莫言普化祗颠狂。真解作津梁。蓦然打个斤斗，直跳过羲皇。 临济处，德山行。果承当。将他建立，认作心诚，也是寻香。'),
	(8706, 183, '朱涣', '白云封断仙岩路，重重洞门深窈。翠竹笼烟，苍崖溅瀑，古木阴森回抱。坛空不老。锁一片莓台、几丛莎草。试把桃源，较量风景是谁好。 乘鸾人去已久，只今惟有，鹤飞猿啸。树拥香幢，泉敲玉佩，疑是群仙重到。尘氛可笑。久志慕丹台，梦思蓬岛，愿把英游，细参梨与枣。'),
	(8707, 183, '西江月（七）', '人我众生寿者，宁分彼此高低。法身通照没吾伊。念念体分同异。 见是何曾是是，闻非未必非非。往来诸用不相知。生死谁能碍你。'),
	(8708, 323, '凤栖梧', '别浦迟留恋清浅。菱蔓荷花，尽日妨钩线。向晚澄江静如练。风送归帆飞似箭。 鸥鹭相将是家眷。坐对云山，一任炎凉变。定是寰区又清宴。不见龙骧波上战。'),
	(8709, 323, '行香子', '黄金叶细，碧玉枝纤。初暖日、当乍晴天。向武昌溪畔，于彭泽门前。陶潜影，张绪态，两相牵。 数株堤面，几树桥边。嫩垂条、絮荡轻绵。系长江舴艋，拂深院秋千。寒食下，半和雨，半和烟。'),
	(8710, 323, '凤栖梧', '整顿云鬟初睡起。庭院无风，尽日帘垂地。画阁巢新燕声喜。杨花狂散无拘系。 近来早是添憔悴。金缕衣宽，赛过宫腰细。苒苒光阴似流水。春残莺老人千里。'),
	(8711, 323, '凤衔杯', '留花不住怨花飞。向南园、情绪依依。可惜倚红斜白、一枝枝。经宿雨、暮江披。 凭朱槛，把金卮。对芳丛、惆怅多时。何况旧欢新恨、阻心期。空满眼、是相思。'),
	(8712, 323, '丑奴儿', '樱桃谢了梨花发，红白相催。燕子归来。几处风帘绣户开。 人生乐事知多少，且酌金杯。管咽声哀。慢引萧娘舞一回。'),
	(8713, 352, '玉楼春', '金雀双鬟年纪小。学画蛾眉红淡扫。尽人言语尽人怜，不解此情惟解笑。 稳着舞衣行动俏。走向绮筵呈曲妙。刘郎大有惜花心，只恨寻花来较早。'),
	(8714, 352, '恨春迟', '欲借江梅荐饮。望陇驿、音息沈沈。住在柳州东，彼此相思，梦回云去难寻。 归燕来时花期浸。淡月坠、将晓还阴。争奈多情易感，风信无凭，如何消遣初心。'),
	(8715, 352, '诉衷情', '歌时眉黛舞时腰。无处不妖饶。初剪菊、欲登高。天气怯鲛绡。 紫丝障，绿杨桥。路迢迢。酒阑歌罢，一度归时，一度魂消。'),
	(8716, 352, '系裙腰', '水轩檐幕透薰风。银塘外、柳烟浓。方床遍展鱼鳞簟，碧纱笼。小墀面、对芙蓉。 玉人共处双鸳枕，和娇困、睡朦胧。起来意懒含羞态，汗香融。素裙腰，映酥胸。'),
	(8717, 352, '渔家傲', '昨日采花花欲尽。隔花闻道潮来近。风猎紫荷声又紧。低难奔。莲茎刺惹香腮损。 一缕艳痕红隐隐。新霞点破秋蟾晕。罗袖挹残心不稳。羞人问。归来剩把胭脂衬。'),
	(8718, 352, '渔家傲', '为爱莲房都一柄。双苞双蕊双红影。雨势断来风色定。秋水静。仙郎彩女临鸾镜。 妾有容华君不省。花无恩爱犹相并。花却有情人薄幸。心耿耿。因花又染相思病。'),
	(8719, 352, '定风波', '把酒花前欲问伊。忍嫌金盏负春时。红艳不能旬日看。宜算。须知开谢只相随。 蝶去蝶来犹解恋。难见。回头还是度年期。莫候饮阑花已尽。方信。无人堪与补残枝。'),
	(8720, 352, '渔家傲', '七月新秋风露早。渚莲尚拆庭梧老。是处瓜华时节好。金尊倒。人间彩缕争祈巧。 万叶敲声凉乍到。百虫啼晚烟如扫。箭漏初长天杳杳。人语悄。那堪夜雨催清晓。'),
	(8721, 352, '玉楼春', '酒美春浓花世界。得意人人千万态。莫教辜负艳阳天，过了堆金何处买。 已去少年无计奈。且愿芳心长恁在。闲愁一点上心来，算得东风吹不解。'),
	(8722, 352, '玉楼春', '残春一夜狂风雨。断送红飞花落树。人心花意待留春，春色无情容易去。 高楼把酒愁独语。借问春归何处所。暮云空阔不知音，惟有绿杨芳草路。'),
	(8723, 352, '渔家傲', '红粉墙头花几树。落花片片和惊絮。墙外有楼花有主。寻花去。隔墙遥见秋千侣。 绿索红旗双彩柱。行人只得偷回顾。肠断楼南金锁户。天欲暮。流莺飞到秋千处。'),
	(8724, 352, '蝶恋花', '尝爱西湖春色早。腊雪方销，已见桃开小。顷刻光阴都过了。如今绿暗红英少。 且趁余花谋一笑。况有笙歌，艳态相萦绕。老去风情应不到。凭君剩把芳尊倒。'),
	(8725, 352, '减字木兰花', '留春不住。燕老莺慵无觅处。说似残春。一老应无却少人。 风和月好。办得黄金须买笑。爱惜芳时。莫待无花空折枝。'),
	(8726, 251, '相思儿令', '昨日探春消息，湖上绿波平。无奈绕堤芳草，还向旧痕生。 有酒且醉瑶觥。更何妨、檀板新声。谁教杨柳千丝，就中牵系人情。'),
	(8727, 251, '菩萨蛮', '高梧叶下秋光晚。珍丛化出黄金盏。还似去年时。傍阑三两枝。 人情须耐久。花面长依旧。莫学蜜蜂儿。等闲悠扬飞。'),
	(8728, 251, '渔家傲', '粉笔丹青描未得。金针彩线功难敌。谁傍暗香轻采摘。风渐渐。船头解散双鸂鶒。 夜雨染成天水碧。朝阳借出胭脂色。欲落又开人共惜。秋气逼。盘中已见新莲菂。'),
	(8729, 251, '木兰花', '红绦约束琼肌稳。拍碎香檀催急衮。垅头呜咽水声繁，叶下间关莺语近。 美人才子传芳信。明月清风伤别恨。未知何处有知音。长为此情言不尽。'),
	(8730, 251, '少年游', '谢家庭槛晓无尘。芳宴祝良辰。风流妙舞，樱桃清唱，依约驻行云。 榴花一盏浓香满，为寿百千春。岁岁年年，共欢同乐，嘉庆与时新。'),
	(8731, 251, '采桑子', '林间摘遍双双叶，寄与相思。朱槿开时。尚有山榴一两枝。 荷花欲绽金莲子，半落红衣。晚雨微微。待得空梁宿燕归。'),
	(8732, 251, '采桑子', '樱桃谢了梨花发，红白相催。燕子归来。几处风帘绣户开。 人生乐事知多少，且酌金杯。管咽弦哀。慢引萧娘舞袖回。'),
	(8733, 251, '更漏子', '菊花残，梨叶堕。可惜良辰虚过。新酒熟，绮筵开。不辞红玉杯。 蜀弦高，羌管脆。慢飐舞娥香袂。君莫笑，醉乡人。熙熙长似春。'),
	(8734, 186, '浪淘沙', '肠断送韶华。为惜杨花。雪球摇曳逐风斜。容易著人容易去，飞过谁家。 聚散苦咨嗟。无计留他。行人洒泪滴流霞。今日画堂歌舞地，明日天涯。'),
	(8735, 186, '满江红（初春）', '飘尽寒梅，笑粉蝶游蜂未觉。渐迤逦、水明山秀，暖生帘幕。过雨小桃红未透，舞烟新柳青犹弱。记画桥深处水边亭，曾偷约。 多少恨，今犹昨。愁和闷，都忘却。拚从前烂醉，被花迷著。晴鸽试铃风力软，雏莺弄舌春寒薄。但只愁、锦绣闹妆时，东风恶。'),
	(8736, 186, '苏幕遮（般涉调）', '柳飞绵，花实少。镂板音清，浅发江南调。斜日两竿留碧□。马足重重，又近青门道。 去尘浓，人散了。回首旗亭，渐渐红裳小。莫讶安仁头白早。天若有情，天也终须老。'),
	(8737, 186, '御街行（般涉调）', '夭非花艳轻非雾。来夜半、天明去。来如春梦不多时，去似朝云何处。远鸡栖燕，落星沈月，紞紞城头鼓。 参差渐辨西池树。珠阁斜开户。绿苔深径少人行，苔上屐痕无数。余香遗粉，剩衾闲枕，天把多情付。'),
	(8738, 186, '天仙子（郑毅夫移青社·中吕调）', '持节来时初有雁。十万人家春已满。龙标名第凤池身，堂阜远。江桥晚。一见湖山看未遍。 障扇欲收歌泪溅。亭下花空罗绮散。樯竿渐向望中疏，旗影转。鼙声断。惆怅不如船尾燕。'),
	(8739, 186, '少年游（井桃·林钟商）', '碎霞浮动晓朦胧。春意与花浓。银瓶素绠，玉泉金甃，真色浸朝红。 花枝人面难常见，青子小丛丛。韶华长在，明年依旧，相与笑春风。'),
	(8740, 186, '南歌子（林钟商）', '蝉抱高高柳，莲开浅浅波。倚风疏叶下庭柯。况是不寒不暖、正清和。 浮世欢会少，劳生怨别多。相逢惜醉颜酡。赖有西园明月、照笙歌。'),
	(8741, 186, '武陵春（双调）', '秋染青溪天外水，风棹采菱还。波上逢郎密意传。语近隔丛莲。 相看忘却归来路，遮日小荷圆。菱蔓虽多不上船。心眼在郎边。'),
	(8742, 186, '宴春台慢（仙吕宫）', '丽日千门，紫烟双阙，琼林又报春回。殿阁风微，当时去燕还来。五侯池馆频开。探芳菲、走马天街。重帘人语，辚辚绣轩，远近轻雷。 雕觞霞滟，翠幕云飞，楚腰舞柳，宫面妆梅。金猊夜暖、罗衣暗袅香煤。洞府人归，放笙歌、灯火下楼台。蓬莱。犹有花上月，清影徘徊。'),
	(8743, 186, '西江月（中吕宫）', '体态看来隐约，梳妆好是家常。檀槽初抱更安详。立向尊前一行。 小打登钩怕重，尽缠绣带由长。娇春莺舌巧如簧。飞在四条弦上。'),
	(8744, 344, '西江月', '师师生得艳冶，香香于我情多。安安那更久比和。四个打成一个。 幸自苍皇未款，新词写处多磨。几回扯了又重按。奸字中心著我。'),
	(8745, 344, '清平乐（越调）', '繁花锦烂。已恨归期晚。翠减红稀莺似懒。特地柔肠欲断。 调不堪尊酒频倾。恼人转转愁生。□□□□□□，多情争似无情。'),
	(8746, 344, '燕归梁（中吕调）', '轻蹑罗鞋掩绛绡。传音耗、苦相招。语声犹颤不成娇。乍得见、两魂消。 匆匆草草难留恋、还归去、又无聊。若谐雨夕与云朝。得似个、有嚣嚣。'),
	(8747, 344, '倾杯乐（散水调）', '楼锁轻烟，水横斜照，遥山半隐愁碧。片帆岸远，行客路杳，簇一天寒色。楚梅映雪数枝艳，报青春消息。年华梦促，音信断、声远飞鸿南北。 算伊别来无绪，翠消红减，双带长抛掷。但泪眼沈迷，看朱成碧。惹闲愁堆积。雨意云情，酒心花态，孤负高阳客。梦难极。和梦也、多时间隔。'),
	(8748, 344, '倾杯（黄钟羽）', '水乡天气，洒蒹葭、露结寒生早。客馆更堪秋杪。空阶下、木叶飘零，飒飒声乾，狂风乱扫。当无绪、人静酒初醒，天外征鸿，知送谁家归信，穿云悲叫。 蛩响幽窗，鼠窥寒砚，一点银釭闲照。梦枕频惊，愁衾半拥，万里归心悄悄。往事追思多少。赢得空使方寸挠。断不成眠，此夜厌厌，就中难晓。'),
	(8749, 344, '甘州令（仙吕调）', '冻云深，淑气浅，寒欺绿野。轻雪伴、早梅飘谢。艳阳天，正明媚，却成潇洒。玉人歌，画楼酒，对此景、骤增高价。 卖花巷陌，放灯台榭。好时节、怎生轻舍。赖和风，荡霁霭，廓清良夜。玉尘铺，桂华满，素光里、更堪游冶。'),
	(8750, 344, '减字木兰花（仙吕调）', '花心柳眼。郎似游丝常惹绊。慵困谁怜。绣线金针不喜穿。 深房密宴。争向好天多聚散。绿锁窗前。几日春愁废管弦。'),
	(8751, 344, '临江仙（仙吕调）', '鸣珂碎撼都门晓，旌幛拥下天人。马摇金辔破香尘。壶浆盈路，欢动一城春。 扬州曾是追游地，酒台花径仍存。凤箫依旧月中闻。荆王魂梦，应认岭头云。'),
	(8752, 344, '剔银灯（仙吕调）', '何事春工用意。绣画出、万红千翠。艳杏夭桃，垂杨芳草，各斗雨膏烟腻。如斯佳致。早晚是、读书天气。 渐渐园林明媚。便好安排欢计。论槛买花，盈车载酒，百琲千金邀妓。何妨沈醉。有人伴、日高春睡。'),
	(8753, 344, '小镇西犯（仙吕调）', '水乡初禁火，青春未老。芳菲满、柳汀烟岛。波际红帏缥缈。尽杯盘小。歌祓禊，声声谐楚调。 路缭绕。野桥新市里，花秾妓好。引游人、竞来喧笑。酩酊谁家年少。信玉山倒。家何处，落日眠芳草。'),
	(8754, 344, '满江红（四之二·仙吕调）', '访雨寻云，无非是、奇容艳色。就中有、天真妖丽，自然标格。恶发姿颜欢喜面，细追想处皆堪惜。自别后、幽怨与闲愁，成堆积。 鳞鸿阻，无信息。梦魂断，难寻觅。尽思量，休又怎生休得。谁恁多情凭向道，纵来相见且相忆。便不成、常遣似如今，轻抛掷。'),
	(8755, 344, '玉蝴蝶（五之四·仙吕调）', '误入平康小巷，画檐深处，珠箔微褰。罗绮丛中，偶认旧识婵娟。翠眉开、娇横远岫，绿鬓亸、浓染春烟。忆情牵。粉墙曾恁，窥宋三年。 迁延。珊瑚筵上，亲持犀管，旋叠香笺。要索新词，殢人含笑立尊前。接新声、珠喉渐稳，想旧意、波脸增妍。苦留连。凤衾鸳枕，忍负良天。'),
	(8756, 344, '菊花新（中吕调）', '欲掩香帏论缱绻。先敛双蛾愁夜短。催促少年郎，先去睡、鸳衾图暖。 须臾放了残针线。脱罗裳、恣情无限。留取帐前灯，时时待、看伊娇面。'),
	(8757, 344, '离别难（中吕调）', '花谢水流倏忽，嗟年少光阴。有天然、蕙质兰心。美韶容、何啻值千金。便因甚、翠弱红衰，缠绵香体，都不胜任。算神仙、五色灵丹无验，中路委瓶簪。 人悄悄，夜沈沈。闭香闺、永弃鸳衾。想娇魂媚魄非远，纵洪都方士也难寻。最苦是、好景良天，尊前歌笑，空想遗音。望断处，杳杳巫峰十二，千古暮云深。'),
	(8758, 344, '引驾行（中吕调）', '虹收残雨。蝉嘶败柳长堤暮。背都门、动消黯，西风片帆轻举。愁睹。泛画鹢翩翩，灵鼍隐隐下前浦。忍回首、佳人渐远，想高城、隔烟树。 几许。秦楼永昼，谢阁连宵奇遇。算赠笑千金，酬歌百琲，尽成轻负。南顾。念吴邦越国，风烟萧索在何处。独自个、千山万水，指天涯去。'),
	(8759, 344, '木兰花（四之二·林钟商）', '佳娘捧板花钿簇。唱出新声群艳伏。金鹅扇掩调累累，文杏梁高尘簌簌。 鸾吟凤啸清相续。管裂弦焦争可逐。何当夜召入连昌，飞上九天歌一曲。'),
	(8760, 344, '木兰花（四之一·林钟商）', '心娘自小能歌舞。举意动容皆济楚。解教天上念奴羞，不怕掌中飞燕妒。 玲珑绣扇花藏语。宛转香茵云衫步。王孙若拟赠千金，只在画楼东畔住。'),
	(8761, 344, '少年游（十之九·林钟商）', '日高花榭懒梳头。无语倚妆楼。修眉敛黛，遥山横翠，相对结春愁。 王孙走马长楸陌，贪迷恋、少年游。似恁疏狂，费人拘管，争似不风流。'),
	(8762, 344, '少年游（十之五·林钟商）', '淡黄衫子郁金裙。长忆个人人。文谈间雅，歌喉清丽，举措好精神。 当初为倚深深宠，无个事、爱娇嗔。想得别来，旧家模样，只是翠蛾颦。'),
	(8763, 344, '宣清（林钟商）', '残月朦胧，小宴阑珊，归来轻寒凛凛。背银釭、孤馆乍眠，拥重衾、醉魄犹噤。永漏频传，前欢已去，离愁一枕。暗寻思、旧追游，神京风物如锦。 念掷果朋侪，绝缨宴会，当时曾痛饮。命舞燕翩翻，歌珠贯串，向玳筵前，尽是神仙流品。至更阑、疏狂转甚。更相将、凤帏鸳寝。玉钗乱横，任散尽高阳，这欢娱、甚时重恁。'),
	(8764, 344, '浪淘沙令（歇指调）', '有个人人。飞燕精神。急锵环佩上华裀。促拍尽随红袖举，风柳腰身。 簌簌轻裙。妙尽尖新。曲终独立敛香尘。应是西施娇困也，眉黛双颦。'),
	(8765, 344, '鹊桥仙（歇指调）', '届征途，携书剑，迢迢匹马东去。惨离怀，嗟少年易分难聚。佳人方恁缱绻，便忍分鸳侣。当媚景，算密意幽欢，尽成轻负。 此际寸肠万绪。惨愁颜、断魂无语。和泪眼、片时几番回顾。伤心脉脉谁诉。但黯然凝伫。暮烟寒雨。望秦楼何处。'),
	(8766, 344, '永遇乐（二之二·歇指调）', '天阁英游，内朝密侍，当世荣遇。汉守分麾，尧庭请瑞，方面凭心膂。风驰千骑，云拥双旌，向晓洞开严署。拥朱轓、喜色欢声，处处竞歌来暮。 吴王旧国，今古江山秀异，人烟繁富。甘雨车行，仁风扇动，雅称安黎庶。棠郊成政，槐府登贤，非久定须归去。且乘闲、孙阁长开，融尊盛举。'),
	(8767, 344, '愁蕊香引（小石调）', '留不得。光阴催促，奈芳兰歇，好花谢，惟顷刻。彩云易散琉璃脆，验前事端的。 风月夜，几处前踪旧迹。忍思忆。这回望断，永作终天隔。向仙岛，归冥路，两无消息。'),
	(8768, 344, '法曲第二（小石调）', '青翼传情，香径偷期，自觉当初草草。未省同衾枕，便轻许相将，平生欢笑。怎生向、人间好事到头少。漫悔懊。 细追思，恨从前容易，致得恩爱成烦恼。心下事千种，尽凭音耗。以此萦牵，等伊来、自家向道。洎相见，喜欢存问，又还忘了。'),
	(8769, 344, '巫山一段云（五之五·双调）', '萧氏贤夫妇，茅家好弟兄。羽轮飙驾赴层城。高会尽仙卿。 一曲云谣为寿。倒尽金壶碧酒。醺酣争撼白榆花。踏碎九光霞。'),
	(8770, 344, '御街行（圣寿·二之一·双调）', '燔柴烟断星河曙。宝辇回天步。端门羽卫簇雕阑，六乐舜韶先举。鹤书飞下，鸡竿高耸，恩霈均寰寓。 赤霜袍烂飘香雾。喜色成春煦。九仪三事仰天颜，八彩旋生眉宇。椿龄无尽，萝图有庆，常作乾坤主。'),
	(8771, 344, '定风波（双调）', '伫立长堤，淡荡晚风起。骤雨歇，极目萧疏，塞柳万株，掩映箭波千里。走舟车向此，人人奔名竞利。念荡子、终日驱驱，争觉乡关转迢递。 何意。绣阁轻抛，锦字难逢，等闲度岁。奈泛泛旅迹，厌厌病绪，迩来谙尽，宦游滋味。此情怀、纵写香笺，凭谁与寄。算孟光、争得知我，继日添憔悴。'),
	(8772, 344, '传华枝（大石调）', '平生身负，风流才调。口儿里、道知张陈赵。唱新词，改难令，总知颠倒。解剧扮，能兵嗽，表里都峭。每遇著、饮席歌筵，人人尽道。可惜许老了。 阎罗大伯曾教来，道人生、但不须烦恼。遇良辰，当美景，追欢买笑。剩活取百十年，只恁厮好。若限满、鬼使来追，待倩个、掩通著到。'),
	(8773, 344, '女冠子（大石调）', '断云残雨。洒微凉、生轩户。动清籁、萧萧庭树。银河浓淡，华星明灭，轻云时度。莎阶寂静无睹。幽蛩切切秋吟苦。疏篁一径，流萤几点，飞来又去。 对月临风，空恁无眠耿耿，暗想旧日牵情处。绮罗丛里，有人人、那回饮散，略曾谐鸳侣。因循忍便瞪阻。想思不得长相聚。好天良夜，无端惹起，千愁万绪。'),
	(8774, 344, '昼夜乐（二之二·中吕宫）', '秀香家住桃花径。算神仙、才堪并。层波细翦明眸，腻玉圆搓素颈。爱把歌喉当筵逞。遏天边，乱云愁凝。言语似娇莺，一声声堪听。 洞房饮散帘帏静。拥香衾、欢心称。金炉麝袅青烟，凤帐烛摇红影。无限狂心乘酒兴。这欢娱、渐入嘉景。犹自怨邻鸡，道秋宵不永。'),
	(8775, 397, '酒泉子（十之一）', '长忆钱塘，不是人寰是天上。万家掩映翠微间。处处水潺潺。 异花四季当窗放。出入分明在屏障。别来隋柳几经秋。何日得重游。'),
	(8776, 412, '夜行船·曲水溅裙三', '曲水溅裙三月二。马如龙、钿车如水。风飏游丝，日烘晴昼，人共海棠俱醉。客里光阴难可意。扫芳尘、旧游谁记。午梦醒来，小窗人静，春在卖花声里。'),
	(8777, 458, '满庭芳（戊戍上元喜霁，访开桃洞）', '去腊飞花，今春未已，迤逦将度元宵。俄然甲子，青帝下新条。净扫一天沈霭，红轮满、大地山河。从今好，便当听取，万国起歌谣。 有人，当此际，＿云深坞，翦月中阿。已占断春风，自种仙桃。更扶疏桂影直，从岩底、上拂云梢。仍为我，长摩苍石，无负此清波。'),
	(8778, 458, '酹江月（丙子自寿）', '平生有意，把六经膏泽，人人沾受。白被子明康节辈，浪说乘除先后。遇合一时，英雄千古，谁是高强手。蹉跎岁晚，临风浩然搔首。 今但入梦青山，云＿深处，烟月生怀袖。宿有十年萧散愿，此段功缘须就。因忆坡公，仇池有约，莫误归时候。今朝对酒，歌此与君为寿。'),
	(8779, 458, '烛影摇红（元宵）', '青旆摇风，朱帘漏月黄昏早。蓬山万叠忽蜚来，上有千灯照。和气祥烟缭绕。映琼楼、五云缥缈。青裙缟袂，乱吹繁弦，九衢欢笑。 元是琴堂，十分管领春光到。手移星宿下人寰，招客来仙岛。信道邦人见少。彷佛似、皇都春好。明年只恐，鳌山扈从，随班清晓。'),
	(8780, 458, '水调歌头（戊戍自寿）', '渤＿东南界，西北倚昆仑。当时推步，但知宇内有乾坤。午夜风轮微转，驾我浮空泛景，一息过天垠。俯视人间世，渺渺聚沤尘。 挽天吴，摩海若，吐还吞。宁用计年，八十阳九又三阴。要自白榆星外，直至黑流沙底，山与泽俱平。不论初末度，一色界如银。'),
	(8781, 176, '应天长（闺思）', '管弦绣陌，灯火画桥，尘香旧时归路。肠断萧娘，旧日风帘映朱户。莺能舞，花解语。念后约、顿成轻负。缓雕辔、独自归来，凭栏情绪。 楚岫在何处。香梦悠悠，花月更谁主。惆怅后期，空有鳞鸿寄纨素。枕前泪，窗外雨。翠幕冷、夜凉虚度。未应信、此度相思，寸肠千缕。'),
	(8782, 176, '风入松（闺思）', '碧苔满地衬残红。绿树阴浓。晓莺啼破眉心事，旧愁新恨重重。翠黛不B62C重扫，佳时每恨难同。 花开花谢任东风。此恨无穷。梦魂拟逐杨花去，殢人休下帘栊。要见只凭清梦，几时真个相逢。'),
	(8783, 176, '风入松（春晚）', '一宵风雨送春归。绿暗红稀。画楼整日无人到，与谁同捻花枝。门外蔷薇开也，枝头梅子酸时。 玉人应是数归期。翠敛愁眉。塞鸿不到双鱼远，叹楼前、流水难西。新恨欲题红叶，东风满院花飞。'),
	(8784, 176, '卖花声·闺思', '愁捻断钗金。远信沈沈。秦筝调怨不成音。郎马不知何处也，楼外春深。 好梦已难寻。夜夜余衾。目穷千里正伤心。记得当初郎去路，绿树阴阴。'),
	(8785, 176, '卖花声（闺思）', '蹙损远山眉。幽怨谁知。罗衾滴尽泪胭脂。夜过春寒愁未起，门外鸦啼。 惆怅阻佳期。人在天涯。东风频动小桃枝。正是销魂时候也，撩乱花飞。'),
	(8786, 176, '汉宫春（慈宁殿元夕被旨作）', '云海沈沈，峭寒收建章，雪残DE32鹊。华灯照夜，万井禁城行乐。春随鬓影，映参差、柳丝梅萼。丹禁杳，鳌峰对耸，三山上通寥廓。 春衫绣罗香薄。步金莲影下，三千绰约。冰轮桂满，皓色冷浸楼阁。霓裳帝乐，奏升平、天风吹落。留凤辇、通宵宴赏，莫放漏声闲却。'),
	(8787, 176, '瑞鹤仙（上元应制）', '瑞烟浮禁苑。正绛阙春回，新正方半。冰轮桂华满。溢花衢歌市，芙蓉开遍。龙楼两观。见银烛、星球有烂。卷珠帘、尽日笙歌，盛集宝钗金钏。 堪羡。绮罗丛里，兰麝香中，正宜游玩。风柔夜暖。花影乱，笑声喧。闹蛾儿满路，成团打块，簇著冠儿斗转。喜皇都、旧日风光，太平再见。'),
	(8788, 176, '洞仙歌令·若耶溪路。', '若耶溪路。别岸花无数。欲敛娇红向人语。与绿荷、相倚愄，回首西风，波淼淼、三十六陂烟雨。新妆明照水，汀渚生香，不嫁东风被谁误。 遣踟蹰、骚客意，千里绵绵，仙浪远、何处凌波微步。想南浦、潮生画桡归，正月晓风清，断肠凝伫。'),
	(8789, 297, '渔歌', '宝觉禅河波浩浩。五湖衲子来求宝。忽竖拳头宜速道。茫然讨。难逃背触君须到。'),
	(8790, 63, '青玉案（子寿父）', '江南十月春风早。见枝上、梅英小。爱日初升清雾晓。绣筵中启，星图高挂，膝下斑衣烧。 今年献寿多欢笑。弄玉新将二雏好。此曲尊前何所祷。十分康乐，十分强健，一树壮棒老。'),
	(8791, 530, '水调歌头（游武夷）', '翠蕤插云表，初意隔仙凡。临风据案一见，邂逅似开颜。几欲拿舟九曲，便拟扪参绝顶，直下俯尘寰。聊此税吾驾，赢得片时闲。 问仙人，缘底事，去不还。长风浩浩，何许清梦杳难攀。只有苍烟古木，好在清湍白石，依旧画图间。回首武夷路，杳霭没云鬟。'),
	(8792, 6, '水调歌头（九日修故事访南山，崖间有前太守所作水调歌头，率尔次韵）', '卷尽风和雨，晴日照清秋。南山高处回首、潇洒一扁州。且向飞霞沦茗，还归云间书院，何幸有从游。随分了公事，同乐与同尤。 少年事，湖海气，百尺楼。萧萧华发、归兴只念故山幽。今日聊修故事，口岁大江东去，应念我穷愁。不但莼鲈口，杜若访芳洲。'),
	(8793, 330, '选冠子', '碧眼连车，黄头间座，望断故人何处。当时胜丽，旧是繁华，都变虏言胡语。万里衔冤，几年埋恨，仔细向谁分诉。对南风凝眸，眺神旌、触目泪流如雨。 今幸会，电扫雷驱，云开雾敛，一旦青天重睹。桃林卧草，华岳嘶风，行迓太平周武。洗尽腥膻巷陌。从此追欢，酒坏频举。任笙箫声里，花朝月夕。醉中歌舞。'),
	(8794, 258, '水调歌头', '有客泛轻舸，迤逦到桐庐。山湾水曲，个中依约是仙区。试唤清江渔父，为问来今往古，兴废事如何。笑指寒烟里，此是子陵居。 汉光武，兴皇运，握乾符。客星侵座，方见不与故人疏。自是先生高尚，无限经纶才略，飘泛寄江湖。凛凛亘千载，风月属樵渔。'),
	(8795, 339, '八声甘州', '客星堂下水，碧浮空、烟树几重重。想故人当日，论情蓬藋，际会云龙。底事泥涂轩冕，不肯作三公。千仞钓江浒，此意谁同。 应笑赤松黄石，效痴儿成事，犹自言功。怎知他箕颍，袖手独舂容。幸风月、有人料理，自家山叟与溪翁。鸣榔晚，一声长啸，相送冥鸿。'),
	(8796, 67, '沁园春（赠较弓会诸友）', '男子才生，桑弧蓬矢，志期古同。况平生慷慨，胸襟磊落，弛张洞晓，经艺该通。笔扫云烟，腹储兵甲，志气天边万丈虹。行藏事，笑不侯李广，射石夸雄。 仰天一问穷通。叹风虎去龙时未逢。羡傅岩版筑，终符求象，渭滨渔钓，果兆非熊。白额未除，长鲸未脍，臂健何嫌二石弓。天山定，任扶桑高挂，凌阁图功。'),
	(8797, 19, '沁园春（赠谈命许丈）', '客有问余，号曰当水，逸民者谁。是胸罗星斗，熟知天命，口分造化，妙泄天机。百十日前，再三地说，端的秋来攀桂枝。那时节，果鳌头高跨，鹗首横飞。 君休说是谈非。是则是干支带得来。也要他有个、读书种子，一丁不识，富贵何为。报道长安，梅边春色，早趁东风掠马蹄。重逢处，办一封好纸，觅状元诗。'),
	(8798, 595, '江城子（中秋忆举场）', '秀才落得甚乾忙。冗中秋，闷重阳。百年三万，消得几科场。吟配十年灯火梦，新米粥，紫苏汤。如今且说世平康。收战场。息战枪。路断邯郸，无复梦黄粱。浪说为农今决矣，新酒熟，菊花香。'),
	(8799, 300, '沁园春（寿韦轩八十一岁·九月初二）', '八九十翁，似婴儿戏，汉司马迁。记渭川垂钓，一年之长，龙山吹帽，七日之前。口角春风，襟期秋月，万事从来只任缘。随渠道，更登天富贵，陆地神仙。 儿孙绿绶青编。看鼓舞云霄高刺天。但阶移花影，闲寻棋局，风斜竹径，缓起茶烟。心迹逾清，精神越健，不用金丹资节宣。洪崖老，笑将眉寿，祝我老人轩。'),
	(8800, 189, '满庭芳（寿字守·四月十四）', '气吐虹霓，笔飞鸾凤，从来锦绣文章。谪仙才调，复见庆流芳。向自题名雁塔，不十载、德播河阳。慰民望，一麾出守，风月任平章。 清和，时欲半，吕仙庭日，正比相当。欣逢初度旦，敢献椒觞。只恐经纶大手，应休期、便趣曹装。愿箕宿，照临南极，拱北远流光。'),
	(8801, 184, '百字谣（寿叶教·三月初九）', '榆烟新起，正清明节过，翠蓂九叶。欣会谪仙初度日，凤穴产真鸑鷟。心肠琅琅，文章锦绣，看镜期勋业。暂居马帐，后知有赖先觉。 可想大器晚成，功名有志，未逊苏秦学。奈不在身先在子，果向秋风抟鹗。诗酒琴棋，风花雪月，养浩全真乐。寿觞五福，太公须遇文猩。'),
	(8802, 452, '沁园春（二月廿七）', '毓德婺虚，联辉魁宿，天启儒英。正仲春蓂阶，三留叶秀，晓来蓬矢，六挂门荣。星昴庭萧，神嵩降甫，怎似今朝记始生。当此际，有满城和气，万井欢声。 龚黄政事勋名。镜里文书史胆惊。况百年经界，于今修复，四贤道学，至此彰明。月封艳阳，日逢初度，紫诏入行催观玉京。黄堂上、寿觞称处，介福邦民。'),
	(8803, 51, '虞美人（寿女人·二月十一）', '搀先四日花朝节。红紫争罗列。传言玉女降生朝。箕宿光联婺宿、灿云霄。 媥衣红袖齐歌舞。称颂椒觞举。君仙列侍宴瑶池。王母麻如同寿、更无期。'),
	(8804, 20, '贺新郎（刘快轩新居）', '上界神仙府。谁移来、登瀛堂畔，五云深处。江水一弓山万朵，竹外梅花千树。看都入、高人庭户。南北两厅相对起，要门前、便是浮岚路。依暖翠，开吟圃。 莺迁喜奉高堂母。向销金帐下，坐看秦歌赵舞。唐宋几年名阀阅，到此步高一步。君听取，邦人庆语。多少朱门随世化，独眼前、突兀新如许。真个是，擎天柱。'),
	(8805, 443, '醉蓬莱', '怪柳吟翻雪，梅笑冲寒，郁匆如彩。还是瑶池，宴神仙俦侣。罗幕轻掀，绣帘低揭，按霓裳宫羽。宝炷熏浓，佩环声颤，凤飞鸾舞。 犹记年时，玉箫吹彻，并驾萧郎，共骖赢女。旋捧麒麟，种旧家前武。政了摘星天上，早约个、嫦娥住。妆点华堂，双扶醉玉，黑颠如许。'),
	(8806, 219, '临江仙（寿六十二）', '佛说波斯王此岁，衰颜羞见河流此首楞严。翁今丹脸发光浮。毗卢金色界，烂熳菊花秋。 七个明朝方九日，年年税在今朝。八千秋老又从头。明朝无尽在，蝴蝶不须愁。'),
	(8807, 219, '贺新郎（童养合卺）', '一点阳春小。傍妆台、梅梢粉嫩，桃花红透。合卺尊前人笑语，银烛两行红补。云正暖、流苏香兽。金屋阿娇元共贮，待玄霜、杵就方成偶。□□□，□□□。 西园扑蝶春风早。看浮花、浪蕊飞尽，娟娟闺秀。柳带草蒲堪绾结，只绾同心未就。算今夜、心都同了。待阙鸳鸯情似海，锦衾温、说到鸡声晓。头白也，镇相守。'),
	(8808, 601, '百字令（韩知事美任）', '三年幕画，是小试相业，桐阴个谱。协赞拥容心似佛，春在螺山螺浦。白玉无瑕，黄扉倚重，一府中流柱。萧然锦满，扁舟明日归去。 此去南北才名，看青云稳驾，玉阶徐步。共说荆州老长史，宰相须还他做。沙路星明，甘棠人远，无计攀辕住。薰香三祝，苍生正望霖雨。'),
	(8809, 259, '水龙吟（春梦）', '日高深院无人，杨花扑帐春云暖。回文未就，停针不语，绣床倚遍。翠被笼香，绿鬟坠腻，伤春成怨。尽云山烟水，柔情一缕，又暗逐、金鞍远。鸾佩相逢甚处，似当年、刘郎仙苑。凭肩后约，画眉新巧，从来未惯。枕落钗声，帘开燕语，风流云散。甚依稀难记，人间天上，有缘重见。'),
	(8810, 222, '忆旧游（雨中海棠）', '玉环扶浅醉，翠袖笼寒，香汗初融。昨夜残妆在，最难胜珠络，都沁铅红。朝云低护深约，蜂蝶不知踪。奈燕子情多，斜飞轻触，泪洒羞容。重逢。记前度，解翦烛调笙，踏月鸣骢。风入人间远，待尘缘洗尽，飞佩凌空。丁宁为我留住，携酒寿东风。便花谱重修，高堂再赋疑梦中。'),
	(8811, 288, '虞美人', '西风海色秋无际。双泪如铅水。白羊成队梦初平。挂杖敲云、云处晓鸿惊。 小琼闲抱银筝笑。问有芳卿否。玉书分付莫开封。明日人间临水、拾流红。'),
	(8812, 288, '永遇乐', '玉砌标鲜，雪园风致，似曾相识。蝉锦霞香，乌丝云湿，吹渴蟾蜍滴。青青白白，关关滑滑，寒损珠衣狂客。尽声声、不如归去，归也怎生归得。 含桃红小，香芹翠软，惆怅宜城山色。百折浮岚，几湾流水，那一些儿直。落花情味，露花魂梦，蒲花消息。抚纤眉，织乌西下，为君凝碧。'),
	(8813, 401, '阮郎归', '风中柳絮水中萍。聚散两无情。斜阳路上短长亭。今朝第几程。 何限事，可怜生。能消几度春。别时言语总伤心。何曾一字真。'),
	(8814, 460, '沁园春（贺新冠）', '髡彼两髦，末几见兮，突而弁兮。记昔年犀玉，奇资秀质，今朝簪佩，丰颊修眉。满面春风，一团和气，发露胸中书与诗。人都羡，是君家驹子，天上麟儿。 画堂人物熙熙。会簪履雍容举庆宜。看筮日礼宾，陈钟列俎，三加致祝，一献成仪。绿鬓貂蝉，朱颜豸角，早有君臣庆会期。荣冠带，看绶悬若若，印佩累累。'),
	(8815, 666, '鹧鸪天（子寿母）', '柳色青青罩翠烟。花光灼灼映临川。欲知窈窕呈祥日，恰近清明淑景天。 浮瑞霭，庆真仙。不须男女祝椿年。异时早约西王母，剩折蟠桃荐寿筵。'),
	(8816, 650, '渡江云（寿妇人集曲名）', '瑞云浓，缥缈弦月当庭，天香满院。玉女传言，把真珠帘卷。拥鹊桥仙，引江城子，拟醉蓬莱宴。吹紫玉箫，唱黄金缕，按拍声声慢。 昼锦堂前，倾杯为寿，祝快活年，应天长远。喜似娘儿，解称人心愿。梦兰蕙芳，种宜男草，丹凤吟非晚。更步蟾宫，宴琼林日，汉宫春暖。'),
	(8817, 455, '沁园春（寿竹林亭长）', '呼伯雅来，满进松精，致寿于公。况富矣锦囊，吟边得句，森然武库，书里称雄。亭长新封，亩宫雅趣，一笑侯王名位穹。闲官守，任平章批抹，明月清风。 年年申庆桑蓬。幸至节今截江网重出一首作‘令’晨恰又逢。想霭霭其祥，瑞云阅兆，绵绵之算，线日增红。一段文章，三千功行，名在长生宝箓中。人间窄，待骖鸾驾鹤，上祝融峰。'),
	(8818, 501, '满江红（寿东人）', '律转黄钟，尧蓂尚、零星一叶。人尽道、当年此日，诞生豪杰。我是君家门下士，三年屡献阳春雪。更此行、骑鹤上扬州，恩稠叠。君有子，文章伯。君有女，夸才色。更风流酝藉，东床佳客。婿祝长生儿祝寿，玉杯举罢金杯接。愿年年、长醉腊前梅，梅梢月。'),
	(8819, 582, '念奴娇（寿萧守）', '萧守六月九日生。其时购得右曼卿书寿字碑，挂於厅壁，乃景佑五年六月九日书，月日恰同，用以为意。 金塘瑞溢，爱琉璃十顷，风漪摇碧。玉女三千擎翠盖，簇拥芙容仙伯。鸾鹤回翔，龙蛇飞动，醉墨挥仙笔。磨崖书寿，分明知是今日。 应为今日生申，银钩照坐，光满图书壁。竹马儿童传好语，小住湖山开国。玉玺成文，金莲赐对，剑履登玄石。丹砂九转，功成依旧头黑。'),
	(8820, 622, '齐天乐（寿谢丞）', '南墙槐竹风摇翠。欢声为谁吹起。水秀龟神，松清鹤健，人在蓬壶深处。金猊喷雾。睹鬓雪青归，脸霞红驻。一点寿星，分明光照梅花树。 三年冰蘖清苦。这长生富贵，天应分付。经阅黄庭，鼎烹丹井，心事澹然如水。东山霖雨。正出洗太虚，未容别墅。紫诏飞来，玉堂锵步武。'),
	(8821, 182, '福建士子', '月上小楼西，鸡唱霜天晓。泪眼相看话别时，把定纤纤手。伊道不忘人，伊却都忘了。我若无情似你时，瞒不得、桥头柳。'),
	(8822, 182, '柳梢青', '挂起招牌。一声喝采，旧店新开。熟事孩儿，家怀老子，毕竟招财。 当初合下安排。又不豪门买呆。自古道、正身替代，见任添差。'),
	(8823, 647, '沁园春', '记得爷爷，说与奴奴，陈郎俊哉。笑世人无眼，老夫得法，官人易聘，国土难媒。印信乘龙，夤绿叶凤，选似扬鞭选得来。果然是，西雍人物，京样官坯。 郑郎上马三杯。莫把离愁恼别怀。那孤灯只砚，郎君珍重，离愁别恨，奴自推排。白发夫妻，青衫事业，两句微吟当折梅。彦章去，早归则个，免待相催。'),
	(8824, 627, '失调名（赠妓）', '东风捻就，腰儿纤细。系的粉裙儿不起。近来只惯掌中看，忍教在、烛花影里。 更阑应是，酒红微褪。暗蹙损、眉儿娇翠。夜深著两小鞋儿，靠那处、屏风立地。'),
	(8825, 347, '千秋岁引（寿女人·八月初二）', '两叶蓂开，千年桃熟，恰近秋期十三日。寿星辉映福星现，寿山高对城山立。蕊宫仙，王母宴，瑶池客。 齐劝芳樽斟玉液。齐唱新词翻玉笛。岁岁今朝陪燕集。荣华富贵长年出，重重锦上花添色。谢庭兰，燕山桂，登科必。'),
	(8826, 406, '忆旧游（前题分得论字）', '正落花时节，憔悴东风，绿满愁痕。悄客梦惊呼伴侣，断鸿有约，回泊归云。江空共道惆怅，夜雨隔篷闻。尽世外纵横，人间恩怨，细酌重论。 叹他乡异县，渺旧雨新知，历落情真。匆匆那忍别，料当君思我，我亦思君。人生自非麋鹿，无计久同群。此去重销魂，黄昏细雨人闭门。'),
	(8827, 356, '华胥引（赋松花）', '碧浮春盖，黄点秋旗，细芳泛月。露委残钗，烟梳高髻曾戏折。几度宿寄山房，□麹尘云屑。香入蜂须，蜜房风味应别。 篘酒浮汤，爱霏霏、粉黄清绝。嫩苞新子，凭谁香歌五粒。只怕东风吹尽，长萧萧黄发。独鹤归来，满庭零乱金雪。'),
	(8828, 356, '卜算子（离念）', '书是玉关来，泪向松江随。梅自飘香柳自青，嘹唳征鸿过。 沙漠暗尘飞，嵩岳愁云锁。淮上千营夜枕戈，此恨凭谁破。'),
	(8829, 62, '甘州', '万一云 爱累累、万颗贯骊珠，特地写幽芳。想黄昏云淡，夜深人静，清影横窗。冷澹一枝两叶，笔下老秋光。参透圆明相，日观开荒。 最是柔髭修梗，映风姿雾质，雅趣悠长。更淋漓草圣，把玩墨犹香。珍重好、卷藏归去，枕屏间、偏称道人床。江南路，后回重见，同话凄凉。'),
	(8830, 471, '虞美人（净慈尼）', '君王曾惜如花面。往事多恩怨。霓裳和泪换袈裟。又送鸾舆北去、听琵琶。 当年未削青螺髻。知是归期未。天花丈室万缘空。结绮临春何处、泪痕中。'),
	(8831, 85, '永遇乐', '早叶初莺，晚风孤蝶，幽思何限。檐角萦云，阶痕积雨，一夜苔生遍。玉窗闲掩，瑶琴慵理，寂寞水沈烟断。消无言、春归无觅处，卷帘见双飞燕。 风亭泉石，烟林薇蕨，梦绕旧时曾见。江上闲鸥，心盟犹在，分得眠沙半。引觞浮月，飞谈卷雾，莫管愁深欢浅。起来倚阑干，拾得残红一片。'),
	(8832, 416, '齐天乐（余闲书院拟赋蝉）', '翠云深锁齐姬恨，纤柯暗翻冰羽。锦瑟重调，绡衣乍著，聊饮人间风露。相逢甚处。记槐影初凉，柳阴新雨。听尽残声，为谁惊起又飞去。 商量秋信最早。晚来吟未彻，却是凄楚。断韵还连，余悲似咽，欲和愁边佳句。幽期谁语。怕寒叶凋零，蜕痕尘土。古木斜晖，向人怀抱苦。'),
	(8833, 416, '水龙吟（浮翠山房拟赋白莲）', '翠裳微护冰肌，夜深暗泣瑶台露。芳容淡泞，风神萧散，凌波晚步。西子残妆，环儿初起，未须匀注。看明珰素袜，相逢憔悴，当应被、西风误。十里云愁雪妒。抱凄凉、盼娇无语。当时姊妹，朱颜褪酒，红衣按舞。别浦重寻，旧盟唯有，一行鸥鹭。伴玉颜月晓，盈盈冷艳，洗人间暑。'),
	(8834, 416, '庆宫春（谢草窗惠词卷）', '庭草春迟，汀苹香老，数声佩悄苍玉。年晚江空，天寒日暮，壮怀聊寄幽独。倦游多感，更西北、高楼送目。佳人不见，慷慨悲歌，夕阳乔木。 紫霞洞窅云深，袅袅余香，凤箫谁续。桃花赋在，竹枝词远，此恨年年相触。翠椾芳字，谩重省、当时顾曲。因君凝伫，依约吴山，半痕蛾绿。'),
	(8835, 529, '百字令', '半堤花雨。对芳辰消遣，无奈情绪。春色尚堪描画在，万紫千红尘土。鹃促归期，莺收佞舌，燕作留人语。绕栏红药，韵华留此孤主。 真个恨杀东风，几番过了，不似今番苦。乐事赏心磨灭尽，忽见飞书传羽。湖水湖烟，峰南峰北，总是堪伤处。新塘杨柳，小腰犹自歌舞。'),
	(8836, 159, '渔父词（和玄真子）', '平湖千顷浪花飞。春后银鱼霜更肥。菱叶饭，芦花衣。酒酣载月忙呼归。'),
	(8837, 418, '锦堂春（寿李仁山）', '浅帻分秋，凉尊试月，西风未雁犹蝉。看芙蓉影里，绿鬓年年。日上云颿压海，尘清玉马行天。更烟楼凤举，风幕麟游，锦后珠前。 绿阴池馆如画，记晴春药径，雨晓芝田。已办十年笑语，小聚云边。舞称香围艳雪，歌迟酒落红船。早群仙醉去，柳掖花扶，似雾非烟。'),
	(8838, 418, '摸鱼儿（寿王尉·癸未冬至后五日）', '问谁歌、暗香疏影，此花堪照人世。起持霜月为花如寿，天亦愿花千岁。谁有意。著如此人间，更著花如此。高寒洒洒。看浩荡刚风，跨虬飞佩，玉影乱如水。 行春处，一笑人间紫翠。纷纷窥此天地。寿如川至。□好是、涧翁兹岁喜。荣沾南儒恩例。捧觞更喜郎君美。任夜来归待。见说生辰，恰逢本命，寿筵且未。听老聃孙子。祝公耆艾，祝公富贵。'),
	(8839, 419, '水龙吟（送人归武夷）', '武夷一片闲云，被风吹落清原顶。溪梅馥馥、飞来相傍，不嫌凄冷。瓦斧争鸣，丝桐一曲，满城倾听。奈阳春未了，骊驹已驾，桐阴底、梦魂醒。 何限吟笺赋笔，甚乡心、分却清兴。鳌峰胜处，遥知风月，属谁管领。邂逅何时，无穷事业，有穷光景。但相期、不负初心，此外分、皆前定。'),
	(8840, 531, '贺新郎（端午）', '梅子黄时雨。对幽窗、依依抱独，几多愁绪。润逼琴丝无雅韵，难续文园旧诣。头白尽、相如谁顾。燕子楼空尘又锁，望天涯、不寄红丝缕。嗟往事，且休语。 伤情当日班衣舞。更宫衣，香罗乍带，九天繁露。一寸草心迎永日，更把葵心自许。怎料有、风推雨如。惹起灵均千古恨，转凄凉、更不成端午。拚小醉，读骚句。'),
	(8841, 145, '如梦令', '昨夜佳人凭酒。隔著罗衾厮守。听彻五更钟，陡觉霜飞寒逗。却又。却又。陪笑倩人温手。'),
	(8842, 145, '齐天乐', '柳花引过横塘路，萦回曲蹊通圃。插槿编篱，挨梅砌石，次第海棠成坞。吟筇独拄。待寻访斜桥，水边窥户。已约青山，云深不碍客来处。 繁华阅人无数。问旧日平原，君还知否。啼鸟窗幽，昼阴春寂，慵因不如飞絮。匆匆燕语。似迎得春来，且留春住。惜取名花，一枝堪寄怀。'),
	(8843, 93, '失调名（赠贾似道）', '君来路。吾归路。来来去去何时住。公田关子竟何如，国事当时谁汝误。 雷州户。厓州户。人生会有相逢处。客中颇恨乏蒸羊，聊赠一篇长短句。'),
	(8844, 9, '百字令（戴平轩新居，子侄新婚）', '排云拓月，向天上移下，神仙花屋。画栋飞檐千万落，黼黼城南乔木。出谷莺迁，趋庭燕尔，袍绾登科绿。嫦娥分付，广寒今夜花烛。 遥想高越于门，烂盈百辆，夹道争车毂。春满剡溪溪上路，一任瑶华飞六。舆奉潘慈，楼高华萼，坐享齐眉福。庭槐列戟，公侯衮衮相属。'),
	(8845, 345, '蝶恋花（已卯菊节得家书，欲归未得）', '去年走马章台路。送酒无人，寂寞黄花雨。又是重阳秋欲暮。西风此恨谁分付。 无限归心归不去。却梦佳人，约我花间住。蓦地觉来无觅处。雁声叫断潇湘浦。'),
	(8846, 345, '唱金缕（咸淳癸酉茶陵灯夕，时文文山为湖南提刑）', '春到云中早。恰梅花、雪後□□，锁窗寒悄。鼓吹喧天灯市闹，在处鳌山蓬岛。正新岁、金鸡唱晓。一点魁星光焰里，这水晶、庭院知多少。鸣凤舞，洞箫袅。 太平官府人嬉笑。道紫微、魁星聚会，参差联照。借地栽花河阳县，桃李芳菲正好。暖沁入、东风池沼。百里楼台天不夜，看祥烟、瑞霭相缭绕。生意满，翠庭草。'),
	(8847, 220, '疏影（庐山瀑布）', '银云缥缈。正石梁倒挂，飞下晴昊。早挽悬河，高泻鲸宫，洪声百步低小。分明仙仗崆峒过，又化作，归帆杳杳。倚参差、翠影红霞，远落明湖残照。曾共呼龙夭矫。几回过月下，先种瑶草。九叠屏风，青鸟冥冥，更约谪仙重到。昨梦骑黄鹄，飞不去、和天也笑。等恁时、秋夜携琴，已落洞天霜晓。'),
	(8848, 220, '兰陵王（渭城朝雨）', '章台路。西出重城几步。秦楼晓、花气未明，一霎空濛洗高树。行人半倚户。飞去黄鹂自语。秋千小，不系柳条，惟有轻阴约飞絮。 钿车暗相遇。早拂拭红巾，初放鹦鹉。闻歌犹是淋铃处。掩面鸣筝，倚垆呼酒，东风重记旧眉妩。报伊共歌舞。西去。屡回顾。渐客舍荒凉，嘶马先驻。玉关万里知何许。但倦拥荒泽，瓜洲难渡。将军垂老，望故国，夜寒苦。'),
	(8849, 532, '西江月（为赵元父赋雪梅图）', '裉粉轻盈琼靥，护香重叠冰绡。数枝谁带玉痕描。夜夜东风不扫。 溪上横斜影淡，梦中落莫魂销。峭寒未肯放春娇。素被独眠清晓。'),
	(8850, 532, '金盏子', '雨叶吟蝉，露草流萤，岁华将晚。对静夜无眠，稀星散、时度绛河清浅。其处画角凄凉，引轻寒催燕。西楼外，斜月未沈，风急雁行吹断。 此际怎消遣。要相见、除非待梦见。盈盈洞房泪眼，看人似、冷落过秋纨扇。痛惜小院桐阴，空啼鸦零乱。厌厌地，终日为伊，香愁粉怨。'),
	(8851, 532, '庆清朝慢', '红雨争妍，芳尘生润，将春都揉成泥。分明蕙风薇露，持搦花枝。款款汗酥薰透，娇羞无奈湿云痴。偏厮称，霓裳霞佩，玉骨冰肌。 梅不似，兰不似，风流处，那更著意闻时。蓦地生绡扇底，嫩凉浮动好风微。醉得浑无气力，海棠一色睡胭脂。闲滋味，殢人花气，韩寿争知。'),
	(8852, 532, '浣溪沙', '淡淡青山两点春。娇羞一点口见樱。一梭儿玉一纣云。 白藕香中见西子，玉梅花下遇昭君。不曾真个也销魂。'),
	(8853, 115, '霜天晓角', '塞门桂月。蔡琰琴心切。弹到笳声悲处，千万恨、不能雪。 愁绝。泪还北。更与胡儿别。一片关山怀抱，如何对、别人说。||'),
	(8854, 331, '望江南', '江北路，一望雪皑皑。万里打围鹰隼急，六军刁斗去还来。归客别金台。 江北酒，一饮动千坏。客有黄金如粪土，薄情不肯赎奴回。挥泪洒黄埃。'),
	(8855, 593, '望江南', '寒料峭，独立望长城。木落萧萧天远大，□声羌管遏云行。归客若为情。樽酒尽，勒马问归程。渐近芦沟桥畔路，墙荒驿夕阳明。长短几邮亭。'),
	(8856, 560, '柳梢青（友人至）', '客里凄凉，桐花满地，杜宇深山。幸自君来，谁教春去，剪剪轻寒。 愁怀无语相看。谩写人、徽弦自弹。小院黄昏，前村风雨，莫倚阑干。'),
	(8857, 576, '石州慢', '京浙尘动态，闽峤风霜，不觉催老。封侯事付儿曹，懒把菱花频照。明光赋笔，那知白首山中，年年管领闲花草。叩角夜漫漫，问何时能晓。 堪笑。空有传世千篇，正似病呻饥啸。欲傲王侯，早被王侯相傲。盖棺事定，即今老子独龙，荣枯得丧浑难料。无酒不须愁，问黄花知道。'),
	(8858, 576, '凤凰台上忆吹箫（转官球）', '白玉磋成，香罗捻就，为谁特地团团。羡司花神女，有此清闲。疑是弓靴蹴鞠，刚一踢、误挂花间。方信道，酴醿失色，玉蕊无颜。 凭阑。几回淡月，怪天上冰轮，移下尘寰。奈堪同玉手，难插云鬟。人道转官球也，春去也，欲转何官。聊寄与、诗人案头，冰雪相看。'),
	(8859, 576, '苏幕遮（春情）', '绿秧平，烟树远，村燕声喧，凫雁归来晚。自倚阑干舒困眼。一架葡萄，青得池塘满。 饮先愁，吟又懒。几许闲情，百计难消遣。客路不如归梦短。何况啼鹃，怎不教肠断。'),
	(8860, 576, '阮郎归', '舞红一架欲生衣。残英辞旧枝。雨声自唱惜春词。行人应未知。 新火后，薄罗时。君归何太迟。镜中失却少年姿。年随花共飞。'),
	(8861, 34, '夜行船', '年少疏狂今已老。筵度散、杂剧打了。生向空来，死从空去，有何喜、有何烦恼。说与无常二鬼道。福亦不作，祸亦不造。地狱阎王，天堂玉帝，看你去、那里押到。'),
	(8862, 689, '卜算子（寿两国夫人胡氏）', '清晓听麻姑，来约西王母。共取蟠桃簇玉盘，来劝摩取酒。 王母问摩耶，比意还知否。只为曾生我佛来，更与千千寿。'),
	(8863, 179, '个侬', '恨个侬无赖，卖娇眼、春心偷掷。苍苔花落，先印下一双春迹。花不知名，乔才闻气，似月下箜篌，蒋山倾国。半解罗襟，蕙薰微度，镇宿粉、栖香双蝶。语态眠情，感多情、轻怜细阅。体问望宋墙高，窥韩路隔。 寻寻觅觅。又暮雨凝碧。花径横烟，红扉映月，尽一刻、千金堪值。卸袜熏笼，藏灯衣桁，任裹臂金斜，搔头玉滑。更恨檀郎，恶怜深惜。尽颤袅、周旋倾侧。软玉香钩，怪无端、凤珠微脱。多少怕晓听钟，琼钗暗擘。'),
	(8864, 218, '隔浦莲近', '夜寒晴早人起。见柳知新翠。撼树试花意。两蜂狂救堕蕊。见著羞懒避。春都在，时节到愁地。 屏间字。香痕半掏，误期一一曾记。朱弦谩锁，不会近番慵脆。强踏秋千似醉里。扶下，眼花踮踮飞坠。'),
	(8865, 218, '瑞鹧鸪', '东洲游伴寄兰苕。人日晴时不用招。微雨来看杨柳色，故人相遇浴龙桥。 愁如春水年年长，老共东风日日消。几欲作笺无可寄，双鱼犹自等归潮。'),
	(8866, 661, '更漏子', '妾倚门，君上马。残月晓星犹挂。眉黛敛，泪珠凝。别离多少情。 翠翘横，云鬓乱。复入绮衾犹暖。人独自，枕成双。争教不断肠。'),
	(8867, 122, '喜迁莺', '微雨後，落花天。娇态病恹恹。怕人猜著是相思，成日不开帘。 宝钗横，蝉鬓乱。院宇待人归尽。缓移莲步玉阑前。纤手招花钿。'),
	(8868, 256, '江城子', '夜香烧了夜寒生。掩银屏。理银筝。一曲春风，都是断肠声。杜宇欲啼杨柳外，愁似海，思如云。 背灯暗卸乳鹅裙。酒初酲。梦初醒。兰炷香篝，谁为暖罗衾。二十四帘人悄悄，花影碎，月痕深。'),
	(8869, 205, '绮罗香（渔浦有感）', '浦月窥檐，松泉漱枕，屏里吴山何处。暗粉疏红，依旧为谁匀注。都负了、燕约莺期，更闲却、柳烟花雨。纵十分、春到邮亭，赋怀应是断肠句。 青青原上荠麦，还被东风无赖，翻成离绪。望极天西，惟有陇云江树。斜照带、一缕新愁，尽分付、暮潮归去。步闲阶、待卜心期，落花空细数。'),
	(8870, 667, '绮寮怨（月下残棋）', '又见花阴如水，两心犹未平。正坐久、主客成三，空无语、影落楸枰。千年人间事业，垂成处、一著容易倾。便解围、小住何妨，机锋在，瞬息天又明。 甚似汉吴对营。纷纷不了，孤光照彻连城。又似残星，向零落，有余情。姮娥笑人迟暮，念才力，底须争。从亏又成。何人正听隔壁声。'),
	(8871, 694, '琐窗寒', '驻马林塘，还寻旧迹，雨收秋晚。残蕉映牖，强把碧心偷展。记相逢、画堂宴开，乱花影入帘初卷。正小池涨绿，丝纶曾试，事随鸿远。 凄断。情何限。料素扇尘深，怨娥碧浅。清宫丽羽，漫有苔笺题满。问低墙、双柳尚存，几时艳烛亲共剪。但凝眸，数点遥峰，春色青如染。'),
	(8872, 694, '忆旧游', '乍梅黄雨过，偏倚层楼，时舞垂杨。暗绿知谁换，似烟浓雾薄，望眼偏妨。昔人画鷁无数，相引入横塘。纵细切香蒲，重开绛蕊，懒向清觞。 凄凉。旧游地，谩赋减兰成，才退周郎。总把芳辰误，念纱窗深静，冰簟流光。凤笺试写新句，青羽碧天长。又隐隐城头，随风断角斜照黄。'),
	(8873, 325, '雨淋铃', '窗影珑璁，画楼平晓，翳柳啼鸦。门巷渐有新烟，东风定、人扫桐花。峭寒斗减，看旅雁、争起蒹葭。溯断云，多少悲鸣，数行又下远行沙。 应是故园桃李谢。送清江、一曲阑干下。染翰为春羁，嗟双鬓、客舍成华。绣鞭绮陌，强携酒、来觅吴娃。听扇底、凄惋新声，醉里翻念家。'),
	(8874, 578, '望江南', '霜月湿，人睡矮篷秋。惊觉夜深儿女梦，渔歌风起白苹洲。别岸又潮头。'),
	(8875, 577, '恋绣衾', '江南烟水几万重。记玉人、花底旧容。待欲寄、飞鸿信，望前山、夕照冷红。 塞笳月下声凄楚，怨百花、春事梦空。倩谁共、东君说，把阳和、分付朔风。'),
	(8876, 399, '踏莎行', '柳外寒轻，水边亭小。昨朝燕子归来了。天涯无数旧愁根，东风种得成芳草。 亭畔秋千，当时欢笑。香肌不满和衣抱。那堪别後更思量，春来瘦得知多少。'),
	(8877, 588, '踏莎行', '照眼菱花，剪情菰叶。梦云吹散无踪迹。听郎言语识郎心，当时一点谁消得。 柳暗花明，萤飞月黑。临窗滴泪研残墨。合欢带上旧题诗，如今化作相思碧。'),
	(8878, 588, '贺新郎', '池馆收新雨。耿幽丛、流光几点，半侵疏户。入夜凉风吹不灭，冷焰微茫暗度。碎影落、仙盘秋露。漏断长门空照泪，袖纱寒、映竹无心顾。孤枕掩，残灯炷。 练囊不照诗人苦。夜沈沈、拍手相亲，騃儿痴女。栏外扑来罗扇小，谁在风廊笑语。竞戏踏、金钗双股。故苑荒凉悲旧赏，怅寒芜、衰草隋宫路。同磷火。偏秋圃。'),
	(8879, 143, '华胥引（中秋紫霞席上）', '澄空无际，一幅轻绡，素秋弄色。翦翦天风，飞飞万里，吹净遥碧。想玉杵芒寒，听佩环无迹。圆缺何心，有心偏向歌席。 多少情怀，甚年年、共怜今夕。蕊宫珠殿，还吟飘香秀笔。隐约霓裳声度，认紫霞楼笛。独鹤归来，更无清梦成觅。'),
	(8880, 143, '宴瑶池（神仙词）', '紫鸾飞舞，又东华宴罢，归步凝碧。缥缈天风吹送睡，冷冷佩声清逸。表童两两，争笑捻、琪花半折。羽衣寒露香披，翠幢珠辂去云疾。 西真还又传帝敕。霞城检校，问学仙消息。玉府高寒，有不老丹容，自然琼液。人间尘梦，应误认、烟痕雾迹。洞云依约开时，丹华飞素白。'),
	(8881, 111, '最高楼（寿南山弟七旬）', '南山老，还记少陵诗。七十古来稀。清池拥出红蕖坠，西风吹上碧梧枝。趁今朝，斟寿酒，记生时。也不羡、鲲鹏飞击水。也不羡、蛟龙行得雨。人世事，总危机。扶床正好看孙戏，舞衫不要笑儿痴。更埙篪，三老子，鬓如丝。'),
	(8882, 111, '玉漏迟（和）', '自怜翠袖，向天寒、独倚孤篁吟啸。半世虚名，孤负白云多少。欲问梅翁旧约，怕误我、沙头鸥鸟。时一笑。行行且止，人间蜀道。休怪岁月无情，叹尘世浮生，闲忙闲老。待趁黑头，万里封侯都了。今古勋名一梦，听未彻、钧天还觉。羌管晓。楼角曙星稀小。'),
	(8883, 111, '玉漏迟（自寿）', '青衫华发，对风霜、倚遍危楼孤啸。恶浪平波，看尽世间多少。忘却金闺故步，都付与、野花啼鸟。只自笑。悠悠心事，无人知道。 扰扰世路红尘，看销尽英雄，青山亦老。宇宙无穷，事业到头谁了。高楼一声书角，把千古、梦中吹觉。天欲晓。起看蕊梅春小。'),
	(8884, 279, '桂殿秋（题洞霄）', '飞翠盖，走篮舆。乱山千叠为先驱。洞天迎目深且窈，满耳天风吹步虚。'),
	(8885, 547, '水调歌头', '富贵在何许，五十已平头。人生得失且笑，体遣两眉愁。管甚轮云世变，管甚风波世态，沙渚且盟鸥。止即尽教止，流即尽教流。 田可秫，山可簌，又何求。问天只觅穷健，游戏八千秋。好对梅花如粉，细剪烛花如豆，不改旧时游。翠袖更能舞，骑鹤上扬州。'),
	(8886, 623, '步蟾宫', '东风又送酴醿信。早吹得、愁成潘鬓。花开犹似十年前，人不似、十年前俊。 水边珠翠香成阵。也消得、燕窥莺认。归来沈醉月朦胧，觉花气、满襟犹润。'),
	(8887, 310, '击梧桐（别西湖社友）', '枫叶浓於染。秋正老、江上征衫寒浅。又是秦鸿过，霁烟外，写出离愁几点。年来岁去，朝生暮落，人似吴潮展转。怕听阳关曲，奈短笛唤起，天涯清远。 双屐行春，扁舟啸晚。忆昔鸥湖莺苑。鹤帐梅花屋，霜月後、记把山扉牢掩。惆怅明朝何处，故人相望，但碧云半敛。定苏堤、重来时候，芳草如翦。'),
	(8888, 524, '浣溪纱', '柳映疏帘花映林。春光一半几销魂。新诗未了枕先温。燕子说将千万恨，海棠开到二三分。小窗银烛又黄昏。'),
	(8889, 524, '眼儿媚（绿笺）', '碧筒新展绿蕉芽。黄露洒榴花。蘸烟染就，和云卷起，秋水人家。 只因一朵芙蓉月，生怕黛帘遮。燕衔不去，雁飞不到，愁满天涯。'),
	(8890, 242, '水龙吟', '翠鳌涌出沧溟，影横栈壁迷烟墅。楼台对起，阑干重凭，山川自古。梁苑平芜，汴堤疏柳，几番晴雨。看天低四远，江空万里，登临处、分吴楚。 两岸花飞絮舞。度春风、满城箫鼓。英雄暗老，昏潮晓汐，归帆过舻。淮水东流，塞云北渡，夕阳西去。正凄凉望极，中原路杳，月来南浦。'),
	(8891, 146, '沁园春（寿贾丞相）', '章武中兴，淮蔡欲平，晋公已生。信天生英杰，正为国计，擎天著柱，要自支撑。万里长江，古称天险，去岁里风涛忽震惊。公谈笑，把云腥霓翳，一日都清。 归来奠枕于京。有辉焕明堂前一星。称衮衣廊庙，枫宸眷宠，彩衣公府，萱砌春荣。著片公心，辨双明眼，长与群贤扶太平。无它愿，植万年宗社，万古功名。'),
	(8892, 146, '沁园春（寿王高安，七月二十八日）', '超逸天才，文如三松，诗如卢溪。自白莲赋就，已高声价，梅花句出，远见襟期。玉尘精神，瑶林风韵，雪里神仙小氅衣。缑山夕，已再经旬浃，戏鹤重归。 花间凫舄轻飞。便一似元规报十奇。最风流膝上，双亲未老，三槐手种，分付佳儿。人想夷吾，帝思王某，钧轴舍人知未迟。长生药，在玉盘麟脯，琼苑蟾芝。'),
	(8893, 146, '沁园春（七月朔寿卢守）', '一叶新凉，又是西风，吹转素商。揽玉壶英气，钟为人物，银河精采，融作文章。司巧天公，搀先七夕，分付天孙云锦裳。骑龙凤，自九霄飞至，万丈光芒。 早年奏赋长杨。饱挹尽瀛洲风露香。间石渠天禄，借将班马，荷山锦水，暂作龚黄。药省春风，薇垣夜月，合佩仙花侍玉皇。明年里，饮寿椒何处，宣劝持觞。'),
	(8894, 146, '沁园春（寿张府判夫人）', '梅笑东风，只两日间，又新岁华。有玉龟阿母，献三蟠实，蕊宫仙子，飞七香车。春满虾帘，雪晴鸳瓦，窗户非烟笼翠纱。萱堂上，看衣翻戏采，觞捧流霞。 君家。元是仙家。几度看菖蒲九节花。来剑池丹井，平分风月，一溪流水，犹泛胡麻。寿庆千秋，荣封两国，绿鬓犹深杨柳鸦。长生药，在蓬莱顶上，不必丹砂。'),
	(8895, 146, '沁园春（寿婺州陈可齐九月九日）', '四海中间，第一清流，惟有可齐。看平生践履，真如冰玉，雄文光焰，不涴尘埃。无佑诸贤，纷纷台省，惟有景仁招不来。狂澜倒，独中流砥柱，屹立崔嵬。 挂冠有请高哉。但清庙正需梁栋材。便撑舟野水，出航巨海，有官鼎鼐，无地楼台。制菊龄高，看萸人健，万顷秋江入寿杯。经纶了，却驭风骑气，阆苑蓬莱。'),
	(8896, 146, '霜天晓角（湖上泛月归）', '秋怀轩豁。痛饮天机发。世界只如掌大，算只有、醉乡阔。烟抹。山态活。雨睛波面滑。艇子慢摇归去，莫搅碎、一湖月。'),
	(8897, 204, '应天长（断桥残雪）', '甃澌冱晓，篙水涨漪，孤山渐卷云簇。又见岸容舒腊，菱花照新沐。横斜树，香未北。倩点缀、数梢疏玉。断肠处，日影轻消，休怨霜竹。 帘上涌金楼，酒滟酥融，金缕试春曲，最好半残鳷鹊，登临快心目。瑶台梦，春未足。更看取、洒窗填屋。灞桥外，柳下吟鞭，归趁游烛。'),
	(8898, 204, '应天长（曲院荷风）', '换桥度舫，添柳护堤，坡仙题欠今续。四面水窗如染，香波酿春麹。田田处，成暗绿。正万羽、背风斜矗。乱鸥去，不信双鸳，午睡犹熟。 还记涌金楼，共抚雕阑，低度浣沙曲。自与故人轻别，荣枯换凉燠。亭亭影，惊艳目。忍到手、又成轻触。悄无语，独捻花须，心事曾卜。'),
	(8899, 507, '浪淘沙（中秋雨）', '愁似晚天云。醉亦无凭。秋光此夕属何人。贫得今年无月看，留滞江城。夜起候檐声。似雨还晴。旧家谁信此时情。惟有桂香时入梦，勾引诗成。'),
	(8900, 658, '沁园春（寿游侍郎）', '某恭审某官受天异禀，间世笃生。光辅三朝，伟甚忠清之节；退发一壑，粹然恬淡之风。帝眷耆英，神绥福祉。某不量寸朽，切庇万间。心之所祈，姑寄沁园春之赋；仁者必寿，愿同庄椿岁之多。俯伏露忱，仰祈电盼。 拂袖归来，懒踏前回，京华欢麈。算宦游虽好，何如生处，急流勇退，赢得闲人。身羡仙翁，萧然野服，笑永东溪溪上亭。头犹黑、甚丰姿鹤瘦，标致梅清。 今年转觉精神。便次第平头八十人。肯又抛渭钓，似周尚父，且来洛社，作宋耆英。报答明恩，一时分付，贤子贤孙事业新。丹心在，尚瓣香岁岁，遥祝尧龄。'),
	(8901, 395, '南乡子', '十日后重阳。甘菊阶前满意黄。生日无钱留贺客，何妨。尚有儿曹理寿觞。 双鬓已沧浪。休问金门与玉堂。二仲相期三径在，徜徉。何用功成似子房。'),
	(8902, 87, '满庭芳', '龙角辉春，蛾春惊晓，梦阑金翠屏开。异芬薰室，风送蕊仙来。玉女擎香沐浴，人间世、洗彻凡埃。梅开后，留花酝染，清味俗难猜。 东君，尤雅爱，传香芳畹，香发庭陔。宁馨满尊前，喜奏瑶台。便好纽为佩玉，瀛洲路、同赏蓬莱。蟠桃宴，从今曼倩，三骋奇材。'),
	(8903, 550, '临江仙', '月寺星轺尘梦断，如今平地仙人。烟霞卷起旧精神。焚香金鸑鷟，书奏玉麒麟。 闻道枫宸求侍从，看庆命重新。且将风月剩酬身。樽中长有酒，花下不辜春。'),
	(8904, 479, '阮郎归', '良辰佳景列华筵。笙歌奏筦弦。位居荣显子孙贤。功名事双全。 庆祝寿，拜尊前。重重福禄坚。愿如彭祖寿齐年。金杯劝寿仙。'),
	(8905, 548, '新荷叶', '莲萼飘香，金风乍扇轻凉。郁郁葱葱，瑞烟萦绕华堂。星辰孕秀，良霄梦、吉协熊祥。紫芝眉宇，莹然如峙圭璋。 忠孝传芳，集庆门、奕世簪裳。他日岩廊，致君直上虞唐。百千椿算，争期并、鹤老龟长。年年今日，会拚一醉觥觞。'),
	(8906, 35, '青玉案', '熙春堂下花无数。红紫映、桃溪路。蝶往蜂来知几许。翠筠亭外，绿阳堤畔，时听娇莺语。 绮筵罗列开樽俎。况总是、神仙侣。竞举笙歌持玉醑。介公眉寿，年年此日，常与花为主。'),
	(8907, 266, '谒金门', '情知是梦无凭了。好梦依然少。单于吹尽五更风。谁见梅花如泪、不言中。 儿童问我今何在。烟雨楼台改。江山画出古今愁。人与落花何处、水空流。'),
	(8908, 79, '水调歌头', '万里楚天阔，一点寿星明。庭语以识歌舞，拍拍□秋声。会过牛郎六夕，圆欠嫦娥一夜，此际是佳辰。□□□□，□□□□赉予说，□神岳，庆生申。□领仓符，□兼漕节握藩旌。但□闽山建水，无地不阳春。今日范滂刘宴，来岁周公伊尹，元化斡洪钧。北阙中书令，南极老人星。'),
	(8909, 79, '霜天晓角', '蒲帆十幅。飞破秋江绿。天际彩虹千丈，阑干外、泻寒玉。 一雨收残溽。云山开画轴。试问故人何处，青楼在画桥北。'),
	(8910, 364, '蝶恋花', '身世谁人知觉梦。阳焰空花，尽被三彭弄。可但运机畦上瓮。由来不了轻根重。 休要寻文披大洞。丹鼎屯蒙，养取元珠动。意马此时何用鞚。长生自与天齐共。'),
	(8911, 538, '点绛唇', '冰玉神人，岁寒时节迎初诞。照溪梅绽。秀岭孤松远。 香雾氤氲，不放重帘卷。歌声缓。酒杯深劝。此会年年见。'),
	(8912, 605, '虞美人', '蓂飞八叶书云后。此日孙枝秀。爱他风味似吾人。都是笔头能篆、又能文。 青青两鬓年方壮。儿女俱成长。要添福寿与荣华。教取一庭兰玉、共成家。'),
	(8913, 605, '菩萨蛮', '小春撋就双桃靥。伴他二六尧蓂荚。红绿若为新。个时生玉人。 玉人持酒笑。笑与仙翁道。从此百年中。莺花几度封。'),
	(8914, 605, '临江仙', '解事西风真可意，秋声不到尧蓂。尚余荚十分清。知君当此际，清梦应长庚。 道骨仙风元不老，天开鹤算龟龄。更看宝带换红鞓。拂衣辞筦库，谈笑取功名。'),
	(8915, 605, '满江红', '老子生朝，萧然坐离骚窟宅。更莫诧、才雄屈宋，诗高刘白。不向凤凰池上住，不逃鹦鹉洲边迹。谩一官、如水过称呼，诸侯客。 平生志，水投石。首已皓，心犹灵。算陆沉雄奋，总非人力。广武成名惟孺子，高阳适意须欢伯。睨醉乡、一笑抚青萍，乾坤窄。'),
	(8916, 440, '蝶恋花', '照水绮霞明木杪。彩翼双栖，未觉桐阴少。正是一年秋光好。世间重赌香山老。 祝寿人如群玉绕。满酌金莲，愿永谐歌笑。仙客莫疑来下早。洞中日月天昏晓。'),
	(8917, 570, '念奴娇（暮春寿太守）', '楚天春晚，尽替了，残红点成层碧。蝴蝶蜂儿娇未足，舞破游丝千尽。一夜风高，满天清露，冷浸琼楼彻。遮鸾绕凤，使君不是凡骨。 游荡偶尔人间，铃斋谈笑，好作龚黄匹。火急功成天上去，却问蓬莱消息。酒泻鹅儿，十分金盏，染就眉间色。满城争看，一封鹗荐飞入。'),
	(8918, 29, '醉落魄（夜静闻琴）', '铜壶漏歇。纱窗倒挂梅梢月。玉人酒晕消香雪。促轸调弦，弹个古离别。 雏莺小凤交飞说。嘈嘈软语丁宁切。相如敧枕推红毯。脉脉无言，还记旧时节。'),
	(8919, 29, '南歌子（为吕圣俞寿）', '菊润初经雨，橙香独占秋。碧琳仙酿试新篘。内集熙熙休试、蚁浮瓯。 家世传黄阁，功名起黑头。双凫傍故人舟。咫尺青云岐路、看英游。'),
	(8920, 29, '踏莎行（壬午元宵戏呈元汝功参议）', '元夕风光，中兴时候。东风著意催梅柳。谁家银字小笙簧，倚阑度曲黄昏后。 拨雪张灯，解衣贳酒。觚棱金碧闻依旧。明年何处看升平，景龙门下灯如昼。'),
	(8921, 29, '朝中措（建康大雪，戏呈母舅晁留守）', '漏云初见六花开。惊巧妒江梅。飘洒元戎小队，玉妆旌旆归来。 恩同化手，春回陇亩，欢到尊＿。记取明朝登览，绿漪惟有秦淮。'),
	(8922, 29, '四犯令', '月破轻云天淡注。夜悄花无语。莫听阳关牵离绪。拚酩酊、花深处。 明日江郊芳草路。春逐行人去。不似酴醿开独步。能著意、留春住。'),
	(8923, 29, '菩萨蛮（照溪）', '江梅占尽江头雪。忍寒玉骨夸清绝。不似杜秋娘。婆娑秋水傍。 波光涵晚日。照影从教密。隐隐认遥黄。隔溪十里香。'),
	(8924, 29, '点绛唇', '约莫香来，倚阑低瞰花如雪。怨深愁绝。瘦似年时节。 岁一相逢，常是匆匆别。歌壶缺。又还吹彻。笛里关山月。'),
	(8925, 29, '昭君怨（亦名宴西园）', '晴日烘香花睡。花艳浮杯人醉。杨柳绿丝风。水溶溶。 留恋芳丛深处。懒上锦鞯归去。待得牡丹开。更同来。'),
	(8926, 29, '菩萨蛮（簪髻）', '交刀剪碎琉璃碧。深黄一穗珑松色。玉蕊纵妖娆。恐无能样娇。 绿窗初睡起。堕马慵梳髻。斜插紫鸾钗。香从鬓底来。'),
	(8927, 29, '蝶恋花（次韵张子原寻梅）', '雪压小桥溪路断。独立无言，雾鬓风鬟乱。拂拭冰霜君试看。一枝堪寄天涯远。 拟向南邻寻酒伴。折得花归，醉著歌声缓。姑射梦回星斗转。依然月下重相见。'),
	(8928, 358, '满庭芳', '井络储精，岷江钟秀，挺生名世真贤。充闾佳气，非雾亦非烟。况值小春时候，蓂背下、八荚争妍。余波在，九华父老，歌颂播喧阗。 三年。书治最，南唐重赋，一旦俱蠲。此恩垂不朽，刻石流传。愿借沧溟为寿，玳筵上、满吸长川。从兹去，鸾坡凤阁，平步稳登仙。'),
	(8929, 197, '风入松', '春寒懒下碧云楼。花事等闲休，红线湿透秋千索，记伴仙、曾倚娇柔。重叠黄金约臂，玲珑翠玉搔头。 薰炉谁熨暖衣篝。消遣酒醒愁。旧巢未著新来燕，任珠帘、不上琼钩。何处东风院宇，数声揭调甘州。'),
	(8930, 198, '浣溪沙', '雨压杨花路半乾。蜂遗花粉在阑干。牡丹开尽正春寒。 懒品么弦金雁并，瘦惊双钏玉鱼宽。新愁不放翠眉间。'),
	(8931, 198, '菩萨蛮', '东风忽骤无人见。玉塘烟浪浮花片。步湿下香阶。苔粘金凤鞋。 翠鬟愁不整。临水间窥影。摘得野蔷薇。游蜂相趁归。'),
	(8932, 54, '氐州第一', '冰缩寒流，川凝冻霭，前回鹭渚冬晚。燕阁红炉，驼峰翠釜，曾忆花柔酒软。云海沧洲，甚又寄、南来客雁。洒雪朱门，回桡剡曲，镜华霜满。 万里银霄凝望眼。恁吟袖、画阑空暖。树带潮墟，笳鸣古戍，簇仲宣幽怨。相愁思、春近也，随宫绣、时宽一线。昨夜扁舟，梦湖山、眉横黛浅。'),
	(8933, 659, '沁园春', '霜剥枯崖，何处邮亭，玉龙夜呼。唤经年幽梦，悠然独觉，参横璇汉，潜彻彻铜壶。漠漠风烟，昏昏水月，醉耸诗肩骑瘦驴。孤吟处，更寻香书影，搔首踟蹰。 古心落落如予。悄独立高寒凌万夫。对荒烟野草，浅浅沙路，班荆三嗅，此意谁知。高卧南阳，归来彭泽，借问风光还似无。难穷处，待凭将妙手，作岁寒图。'),
	(8934, 40, '眼儿媚（春情）', '自频双黛听啼鸦。帘外翠烟斜。社前风雨，已归燕子，未入人家。鞋儿试著无人看，莫是忒宽些。想它楼上，问拈箫管，憔悴莺花。'),
	(8935, 40, '云仙引（桂花·夹锺羽）', '紫凤台高，戏鸾镜里，馡々几度秋馨。黄金重，绿云轻。丹砂鬓边滴粟，翠叶玲珑烟剪成。含笑出帘，月香满袖，天雾萦身。 年时花下逢迎。有游女、翩翩如五云。乱掷芳英，为箸斜朵，事事关心。长向金风，一枝在手，噢药悲歌双黛嚬。远临溪树，对初弦月，露下更深。'),
	(8936, 334, '丑奴儿（梅花）', '冰姿冠绝人间世，傲雪凌霜。蕊点檀黄。更看红唇间素妆。 清芬不是先桃李，桃李无香。迥出林塘。万木丛中独秉阳。'),
	(8937, 334, '解语花', '星桥夜度，火树宵开，灯月光交射。翠檐铜瓦。相辉映、隐隐绛霞飘下。风流艳雅。向柳陌、纤纤共把。筵宴时、频酌香醪，宝鸭喷沈麝。 已是欢娱尽夜。对芳时堪画，条倡叶冶。鸳灯诗帕。嬉游看、到处骤轮驰马。十千换也。惟好事、寸心难谢。听九衢、三市行歌，到晓钟才罢。'),
	(8938, 334, '霜叶飞（咏雪）', '朔风严紧，长空布、同云低黯天表。更堪中夕振寒威，欹枕风声悄。望皓洁、窗纱向晓。珠帘才上银钩小。听美人都惊□，老尽群山，远近相照。 深意劝客金尊，皑皑千里，琼台瑶圃重到。绮罗香暖恣欢娱，暂尔宽怀抱。更几朵、梅花开了。巡檐聊与花相调。算瑞气、丰穰兆。来岁强如，旧年多少。'),
	(8939, 334, '宴清都', '早作听晨鼓。征车动、画桥乘月先度。邻鸡唱晓，人家未起，尚扃柴户。沙边寒雁声遥，料不见、当时伴侣。似怎地、满眼愁悲，秋如宋玉难赋。 休论爱合暌离，微官紧缚，期会良苦。封侯万里，金堆北斗，不如归去。欢娱渐入佳趣，算画在、屏帏邃处。仗小词、说与相思，伊还会否。'),
	(8940, 334, '诉衷情', '眼前时果漫堆盘。莫是又贪酸。因何近来销减，微褪脸霞丹。 还只为，枕衾闲。泪痕斑。我能医疗，一服收功，只霎时间。'),
	(8941, 334, '点绛唇（集句）', '雨歇方塘，清圆一一风荷举。舣舟南浦。忘却来时路。 醉拍春衫，便欲随君去。犹回顾。阿蛮樊素。更有留人处。'),
	(8942, 334, '浣溪沙（兰）', '一径栽培九畹成。丛生幽谷兔欹倾。异芳止合在林亭。 馥郁国香难可拟，纷纭俗眼不须惊。好风披拂雨初晴。'),
	(8943, 334, '选官子', '塞雁呼云，寒蝉噪晚，绕砌夜蛩凄断。迢迢玉宇，耿耿银河，明月又歌团扇。行客暮泊邮亭，孤枕难禁，一窗风箭。念松荒三径，门低五柳，故山犹远。堪欢处。对敌风光，题评景物，恶句斐然挥染。风埃世路，冷暖人情，一瞬几分更变。唯有芳姿为人，歌意尤深，笑容偏倩。把新词拍段，偎人低唱，凤鞋轻点。'),
	(8944, 334, '浣溪沙（檐卜）', '原上芳华已乱飞。林间佛日却晖晖。一花六叶殿春归。 身外色香空荏苒，鼻端消息正霏微。禅林曲几坐忘时。'),
	(8945, 334, '秋蕊香', '向晓银瓶香暖。宿蕊犹残娇面。风尘一缕透窗眼。恨入春山黛浅。短书封了凭金线。紧双燕。良人贪逐利名远。不忆幽花静院。'),
	(8946, 334, '少年游', '三分芳髻拢青丝。花下岁仙姿。殢雨情怀，沾风踪迹，相见恨欢迟。 能言艳色如桃李，曾折最先枝。冶叶丛中，间花堆里，那有者相知。'),
	(8947, 334, '渡江云', '渔乡回落照，晚风势急，鹜鹭集汀沙。解鞍将憩息，细径疏篱，竹隐两三家。山肴野蔌，竞素样、都没浮华。回望时，绕村流水，万点舞寒鸦。休嗟。明年秋暮，一叶扁舟，望平川北下。应免劳、尘巾乌帽，宵炬红纱。青蓑短棹长江碧，弄几曲、羌管吹葭。人借问，鸣粮便入芦花。'),
	(8948, 688, '鹧鸪天（张园作）', '雨过芙蕖叶叶凉。摩挲短发照横塘。一行归鹭拖秋色，几树呜蝉饯夕阳。 花侧畔，柳旁相。微云澹月又昏黄。风流不在谈锋胜，袖手无言味最长。'),
	(8949, 688, '鹧鸪天（张园作）', '雨过芙蕖叶叶凉。摩挲短发照横塘。一行归鹭拖秋色，几树呜蝉饯夕阳。 花侧畔，柳旁相。微云澹月又昏黄。风流不在谈锋胜，袖手无言味最长。'),
	(8950, 688, '西河（已亥秋作）', '天似洗。残秋未有寒意。何人短笛弄西风，数声壮伟。倚栏感慨展双眸，离离烟树如荠。 少年事，成梦里。客愁付与流水。笔床荼具老空山，未妨肆志。世间富贵要时贤，深居宜有馀味。 大江东去日西坠。想悠悠千古兴废。此地阅人多矣。且挥弦寄兴、氛埃之外。目送蜚鸿归天际。'),
	(8951, 688, '水龙吟（赠丁南邻）', '少年有志封侯，弯弓欲挂扶桑外。一朝敛缩，萧然清兴，了无拘碍。袖里阴符，枕中鸿宝，功名蝉蜕。看舌端霹雳，剧谈玄妙，人间世、疑无对。 阆苑醉乡佳处，想当年、绿阴犹在。君仙寄语，不须点勘，鬼神功罪。碧海千寻，赤城万丈，风高浪快。待踞龟食蛤，相期汗漫，与烟霞会。'),
	(8952, 688, '长相思（春晚）', '惜春归。爱春归。脱了罗衣著贮衣。绿阴黄鸟啼。 酒醒时。梦醒时。清簟疏帘一局棋。丁东风马儿。'),
	(8953, 688, '长相思（秋夜）', '砧声齐。杵声齐。金井栏边败叶飞。夜寒乌不栖。 风凄凄。露凄凄。影转梧桐月已西。花冠窗外啼。'),
	(8954, 688, '卖花声（己亥三月一日）', '莺蝶太匆匆。恼杀哀翁。牡丹开尽状元红。俯仰之间增感慨，花事成空。 垂柳绿阴中。粉絮濛濛。多情多病转疏慵。不是东风孤负我，我负东风。'),
	(8955, 688, '南柯子（丁酉清明）', '天上传新火，人间试袷衣。定巢新燕觅香泥。不为绣帘朱户、说相思。侧帽吹飞絮，凭栏送落晖。粉痕销淡锦书稀。怕见山南山北、子规啼。'),
	(8956, 688, '贺新郎（菊）', '莫恨黄花瘦。正千林、风霜摇落，暮秋时候。晚节相看元不恶，采采东篱独秀。试揽结、幽香盈手。几劫修来方得到，与渊明、千载为知旧。同冷淡，比兰友。 柴桑心事君知否。把人间、功名富贵，付之尘垢。不肯折腰营口腹，一笑归欤五柳。怅此意、而今安有。若得风流如此老，也何妨、相对无杯酒。诗自可，了重九。'),
	(8957, 391, '念奴娇', '暑尘收尽，快晚来急雨，一番初过。是处凉飙回爽气，直把残云吹破。星律飞流，银河摇荡，只恐冰轮堕。云梯稳上，琼楼今夜无锁。 便觉浮世卑沈，回翔偃薄，似蚁空旋磨。想得九天高绝处，不比人间更火。独立乾坤，浩歌春雪，可惜无人和。广寒宫里，有谁潇洒如我。'),
	(8958, 572, '沁园春（寿处州吴雨岩）', '持节淅东，六十年前，紫阳老师。看粟移七郡，功深到处，棠阴一道，民尚思之。心印亲传，雨岩来括，寅岁幸无庚子饥。仁同亲，更备存先具，仓积千斯。 青州阴德天知。只此事堪为崧岳祈。与太夫人寿，相看华发，转中书令，长著斑衣。岁岁梅花，樽前索笑，霜月先圆两夜规。称觞了，报春风千里，班观龙墀。'),
	(8959, 75, '风入松', '断芜幽树际烟平。山外又山青。天南海北知何极，年年是、匹马孤征。看尽好花成子，暗惊新笋抽林。 岁华情事苦相寻。弱雪鬓毛侵。十千斗酒悠悠醉，斜河界、白日云心。孤鹤尽边天阔，清猿咽处山深。'),
	(8960, 307, '声声慢', '轻鞯绣谷。柔屐烟堤，六年遗赏新续。小舫重来，惟有寒沙鸥熟。徘徊旧情易冷，但溶溶、翠波如縠。愁望远，甚云销月老，暮山自绿。 嚬笑人生悲乐，且听我尊前，渔歌樵曲。旧阁尘对，长得树阴如屋。凄凉五桥归路，载寒秀、一枝疏玉。翠袖薄，晚无言、寄倚修竹。'),
	(8961, 365, '二郎神', '碧崖倒影，浸一片、寒江如练。正岸岸柳花，村村修竹，唤醒春风笔砚。溯水舟轻轻如叶，只消得、溪风一箭。看水部雄文，太师健笔，月寒波卷。 游倦。片云孤鹤，江湖都遍。慨金屋藏妖，绣屏包祸，欲与三郎痛辨。回首前朝，断魂残照，几度山花崖藓。无限都付窳尊，漠漠水天远。'),
	(8962, 597, '点绛唇', '卷上珠帘，晚来一阵东风恶。客怀萧索。看尽残花落。 自把银瓶，买酒成孤酌。伤漂泊。知音难托。闷倚阑干角。'),
	(8963, 260, '水调歌头（临桂水月洞）', '风月无尽藏，泉石有膏肓。古今桂岭奇胜，骚客费平章。不假鬼谋神运，自是地藏天作，圆魂钟相望。举首吸空翠，赤脚踏沧浪。 惊龙卧，攀栖鹘，翳鸾凰。秋爽一天凉露，桂子更飘香。坐我水精宫阙，呼彼神仙伴侣，大杓挹琼浆。主醉客起舞，今夕是何乡。'),
	(8964, 328, '踏莎行（雪中疏寮借阁帖，更以薇露送之）', '梅观初花，蕙庭残叶。当时惯听山阴雪。东风吹梦到清都，今年雪花此年前别。 重酿宫醪，双钩官帖。伴翁一笑成三绝。夜深何用对青藜，窗前一片蓬莱月。'),
	(8965, 465, '念奴娇', '垂杨得地，在楼台侧畔，无人攀折。不似津亭舟紧处，只伴客愁离别。丝过摇金，带铺新翠，雅称莺调舌。芳筵相映，最宜斜挂残月。 却得连日春寒，未教轻滚，一片庭前雪。应恨张郎今老去，难比风流时节。醉眼浑醒，愁眉都展，舞困腰肢怯。有时微笑，把伊绾个双结。'),
	(8966, 386, '行香子（代赠）', '楚楚精神。杨柳腰身。是风流、天上飞琼。凌波微步，罗袜生尘。有许多娇，许多韵，许多情。 十年心事，两字眉婚。问何时、真个行云。秋衾半冷，窗月窥人。相为人愁，为人瘦，为人颦。'),
	(8967, 386, '永遇乐（送春）', '歌雪徘徊，梦云溶曳，欲劝春住。薄幸杨花，无端杜宇，抵死催教去。参差烟岫，千回百匝，不解禁春归路。病厌厌，那堪更听，小楼一夜风雨。 金钗斗草，玉盘行菜，往事了无凭据。合楼松儿，分香帕子，总是牵情处。小桃朱户，题诗在否，尚忆去年崔护。绿阴中，莺莺燕燕，也应解语。'),
	(8968, 292, '庆清朝（木芙蓉）', '碧玉云深，彤绡云薄，芳丛乱迷秋渚。重城傍水，中有吹箫俦侣。应是琼楼夜冷，月明谁伴乘鸾女。仙游处。翠帟障尘，红绮随步。 别岸玉容伫倚，爱浅抹蜂黄，淡笼纨素。娇羞未语，脉脉悲烟泣露。彩扇何人，妙笔丹青，招得花魂住。歌声暮。梦入锦江，香里归路。'),
	(8969, 571, '霜天晓角（桂）', '姮娥戏剧。手种长生粒。宝幹婆娑千古，飘芳吹、满虚碧。 韵色。檀露滴。人间秋第一。金粟如来境界，谁移在、小亭侧。'),
	(8970, 396, '南乡子（题南剑州妓馆）', '生怕倚阑干。阁下溪声阁外山。惟有旧时山共水，依然。暮雨朝云去不还。 应是蹑飞鸾。月下时时整佩环。月又渐低霜又下，更阑。折得梅花独自看。'),
	(8971, 5, '蝶恋花（春风）', '啼鴂一声云榭晚。好梦惊回，蓬岛疑行遍。无绪东风帘自卷。香苞云压荼コ院。 似有还无烟色展。絮暖鱼肥，时复吹池面。扇影著花蜂蝶见。药栏春静红尘远。'),
	(8972, 473, '朝中措（赋茉利）', '花情偏与夜相投。心事鬓边羞。薰醒半床凉梦，能消几个开头。风轮慢卷，冰壶低架，香雾飕飕。更著月华相恼，木犀淡了中秋。'),
	(8973, 473, '江城子', '一年箫鼓又疏钟。爱东风。恨东风。吹落灯花，移在杏梢红。玉靥翠钿无半点，空湿透，绣罗弓。 燕魂莺梦渐惺松。月帘栊。影迷蒙。催趁年华，都在艳歌中。明日柳边春意思，便不与，夜来同。'),
	(8974, 27, '小桃红', '芳草连天春。斜日明汀渚。懊恨东风，恍如春梦，匆匆又去。早知人、酒病更诗愁，镇轻随飞絮。 宝镜空留恨，筝雁浑无据。门外当时，薄情流水，如今何处。正相思、望断碧山云，又莺啼晚雨。'),
	(8975, 567, '东风第一枝', '妒雪梅苏，迷烟柳醒，游丝轻飏新霁。卷帘看燕初归，步履为花早起。春来犹浅，便做出、十分春意。喜凤钗、才卸珠幡，早换巧梳描翠。 著数点、催花雨腻。更一番、递香风细。小莺欢暖调声，嫩蝶试晴舞翅。清欢易失，怕轻负、年芳流水。好趁间、共整吟鞯，日访桃寻李。'),
	(8976, 350, '霜天晓月', '翦雪裁冰。有人嫌太清。又有人嫌太瘦，都不是、我知音。 谁是我知音。孤山人姓林。一自西湖别后，辜负我、到如今。'),
	(8977, 566, '金明池（素盘）', '桂海云蒸，瘴山雾暖，片雪何曾到地。羡长日、岛仙清暑，自学得、剪冰裁□。把岁寒、五出工夫，别妆点薰风，尽成清致。尽虹雨翻晴，暮霞焦土，一种凄凉如洗。 酝藉丰标浑无比。应似惜、潇湘蕙疏兰弃。纵未入、众芳题品，终自倚、一涯风味。待等间、留取遗芬，伴萝卜芳菲，蔷薇清泚。看佩贯胡绳，心灰宝燎，到了未输兰蕙。'),
	(8978, 612, '一萼红', '忆燕台。正倚帘吹絮，小立望郎来。擫管调丝，涂妆绾髻，密意曾蜂媒。空恁误、湔裙暗约，最无奈、好梦易惊回。想见而今，浅颦双翠，沁破妆梅。 沈带悄然宽尽，恨年时行处，红糁苍苔。前事重寻，幽欢难偶，钿合空委鸾钗。这一点、相思清泪，做心下、烦恼几时灰。数叠蛮笋怨歌，忍对花裁。'),
	(8979, 506, '西江月', '要识真铅汞，都来只一根源。烹前火候妙中玄。不是知音难辨。 采取莫差时日，仍分弦后弦前。玉炉一霎火烧天。无位真人出现。'),
	(8980, 506, '南乡子', '长子到西方。少女归乾变六阳。便好下功修二八，提防。至九方知道自昌。 牛斗共商量。巧夺天工妙莫量。离坎夫妻交媾后，难忘。始觉壶中日月长。'),
	(8981, 506, '南乡子', '两手擘鸿濛。慧剑飞来第一峰。外道修罗惊缩项，神通。造化元来在掌中。 煅炼玉炉红。橐龠吹嘘藉巽风。十月脱胎吞入腹，坤宫。立见三清太上翁。'),
	(8982, 506, '西江月', '金液还丹大道，古人万劫一传。倾心剖腹露诸篇。接引直超道岸。 莫怪天机泄尽，此玄玄外无玄。留传后代与名贤。有目分明觑见。'),
	(8983, 374, '酹江月', '我来访古。把尘襟、都付一声鸣橹。笑把瑶觞波浩荡，却忆长鲸吞吐。坐挹高风，骨清毛冷，不作器尘语。客星何在，谩留遗像江渚。 试问泽畔羊裘，当时何事，知禹弇宫武。金印貂蝉谁不爱，只为汗颜巢许。幸有高台，较他箕颍，未肯轻输与。酒酣长啸，翩然谁共飞举。'),
	(8984, 41, '喜迁莺', '凉生遥渚。正绿芰擎霜，黄花招雨。雁外渔村，蛩边蟹舍，绛叶满秋来路。世事不离双鬓，远梦偏欺孤旅。送望眼，但凭舷微笑，书空无语。 慵觑。清镜里，十载征尘，长把朱颜污。借箸青油，挥毫紫塞，旧事不堪重举。间阔故山猿鹤，冷落同盟鸥鹭。倦游也，便樯云柁月，浩歌归去。'),
	(8985, 231, '百字令', '八旬加二，荷君天垂祐，扶持老拙。目送新来檐外燕，手拣好花轻折。比似去年，十分强健，日看朱诗说。篇三百五，岁前尽有披阅。 天教两子供官，一男留养，左右相娱悦。五见孙枝三拜授，童冠参差袍笏。四侍鳌峰，拏舟在即，次五今圆月。曾孙淳老，想能随叔嬉劣。'),
	(8986, 231, '乳燕飞', '一雨炎□洗。似天知、溪山佳处，玳筵珠履。六十年前今朝庆，门左桑弧蓬矢。也似恁、郁葱佳气。绿鬓童颜春未老，问寿星、模样君真是。新甲子，从头起。 应门有子能承志。总人间、皱眉底事，不关君耳。看不日孙枝毓秀，衮衮教人满意。更又报、门阑多喜。饱受人生真富贵，便蟠桃、三熟堪弹指。知几个，千秋岁。'),
	(8987, 132, '瑞鹤仙（寿萧通判·十月初一日）', '小春天未雪。见两蕊三花，放梅时节。昴宿孕人杰。对梅花雪片，平分风月。冰清玉洁。天赋与、仙风道骨。更等间、来访刘仙，觅取秘传真诀。闻说。辉联台宿，瑞应文昌，世承阀阅。相门事业。有祖父、旧风烈。管泥封飞下，沙堤归来，光复青氈旧物。庆家传，八叶联芳，又添一叶。'),
	(8988, 132, '水调歌头（贺人新娶，集曲名）', '紫陌风光好，绣阁绮罗香。相将人月圆夜，早庆贺新郎。先自少年心意，为惜殢人娇态，久俟愿成双。此夕于飞乐，共学燕归梁。 索酒子，迎仙客，醉红妆。诉衷情处，些儿好语意难忘。但愿千秋岁里，结取万年欢会，恩爱应天长。行喜长春宅，兰玉满庭芳。'),
	(8989, 64, '金菊对芙蓉（沙邑宰绾琴妓，用旧韵戏之）', '浅拂春山，慢横秋水，玉纤间理丝桐。按清冷繁露，淡伫悲风。素弦瑶轸调新韵，颤翠翘、金簇芙蓉。叠蠲重锁，轻挑慢摘，特地情浓。 泛商刻羽无穷。似和鸣鸾凤，律应雌雄。问高山流水，此意谁同。个中只许知音听，有茂陵、车马雍容。画帘人静，琴心三叠，时倒金钟。'),
	(8990, 64, '沁园春（咏刘篁栗碧莲，时内子将诞）', '浅碧芙蓉，素艳亭亭，前身阿娇。记湘滨露冷，酥容倍洁，华清水滑，酒晕全消。瑶剪丰肥，云翻碎萼，白羽鲜明时自摇。风流处，是古香幽韵，时度鲜飙。 琼枝璧月清标。对千朵婵娟倾翠瓢。况水晶台榭，低迷净缘，冰霜词调，隐约轻桡。细认金房，钟奇孕秀，已觉青衿横素腰。西风晚，看花开十丈，玉井非遥。'),
	(8991, 351, '玉漏迟', '絮花寒食路。晴丝罥日，绿阴吹雾。客帽欺风，愁满画船烟浦。丝柱秋千散后，怅尘销、燕帘莺户。从问阻。梦云无准，鬓霜如许。 夜永绣阁藏娇，记掩扇传歌，翦灯留语。月约星期，细把花须频数。弹指一襟幽恨，谩空趁、啼鹃声诉。深院宇。黄昏杏花微雨。'),
	(8992, 50, '水调歌头（同黄主簿登清风峡刘魁读书岩赋水调歌头调）', '解变西昆体，一赋冠群英。清风峡畔，至今堂已读书名。富贵轻于尘土，孝义高于山岳，惜不大其成。陵谷纵迁改，草木亦光荣。 与仇香，穿阮屐，试同登。石龛虽窄，可容一几短檠灯。千仞苍崖如削，四面翠屏不断，云雾镇长生。最爱岩前水，犹作诵弦声。'),
	(8993, 629, '临江仙（涪州北岩玩易有感）', '乐意相关莺对语，春风遍满天涯。生香不断树交花。个中皆实理，何处是浮华。 收敛回来还夜气，一轮明月千家。看梅休用隔窗纱。清光辉皎洁，疏影自横斜。'),
	(8994, 459, '思越人（题挟弹人）', '曾把隋珠抵鹊来。拓弓花下不虚开。醉馀戏把行人弹，堪笑齐王谩筑台。 穿兔手，落雕材。狭斜衢路共徘徊。流星一点高飞处，笑坐金鞍歌落梅。'),
	(8995, 511, '珍珠帘', '阴阳内感相交结。有铅汞、分八卦罗列。金鼎炼黄芽，正一阳时节。子后午前方进火，向玉炉、烹成白雪。通彻。这玄关、深奥难轻泄。 因师指诀幽微，把金丹大药，将来分说。捉住虎龙精，自然日月。造化天机人怎晓，换俗骨、永无魔折。超越。望仙都稽首，朝元金阙。'),
	(8996, 10, '南柯子', '柳陌通云径，琼梳启翠楼。桃花纸薄渍水油。记得年时诗句、为君留。 晓绿千层出，春红一半休。门前溪水泛花流。流到西州犹是、故家愁。'),
	(8997, 10, '诉衷情（章贡别怀）', '一声水调解兰舟。人间无此愁。无情江水东流去，与我泪争流。 人已远，更回头。苦凝眸。断魂何处，梅花岸曲，小小红楼。'),
	(8998, 10, '鹧鸪天（春思）', '病去那知春事深。流莺唤起惜春心。桐舒碧叶慳三寸，柳引金丝可一寻。 怜绣阁，对云岑。苦无多力懒登临。翠罗衫底寒犹在，弱骨难支瘦不禁。'),
	(8999, 10, '水龙吟（题天风海涛呈潘料院）', '飚车飞上蓬莱，不须更跨琴高鲤。砉然长啸，天风澒洞，云涛无际。我欲乘桴，从兹浮海，约任公子。办虹竿千丈，犗钩五十亲点对连鳌饵。 谁榜佳名空翠。紫阳仙、去骑箕尾。银钩铁画，龙怒凤翥，留人间世。更忆东山，哀筝一曲，洒沾襟泪。到而今，幸有高亭遗爱，寓甘棠意。'),
	(9000, 10, '水龙吟（题连州翼然亭呈欧守）', '翼然新榜高亭，翰林铁画燕公手。滁阳盛事，何人重继，湟川太守。太守谓谁，文章的派，醉翁贤胄。对千峰削翠，双溪注玉，端不减、琅琊秀。 坐啸清香画戟，听丁丁、滴花晴漏。棠阴画寂，细赓赓客，竹枝杨柳。只恐明朝，绨封趣观，未容借冠。尽江山识赏，盐梅事业，焕青颤旧。'),
	(9001, 240, '壮椿岁（寿赵丞相）', '恭审某官间期淑气，特立高标。仰维岳之生贤，一朝献颂；赋缁衣而入相，四海同声。欣逢五百年之期，愿上八千岁之祝。可占耆艾，曷尽形容。音寄水龙吟，名为壮椿岁。倘蒙省览，万有荣光。 纶巾少驻家山，北窗睡觉南薰起。黄庭细看，长生秘诀，神仙奇趣。奈此苍生，顾苏炎热，仰为霖雨。趁丹心未老，将整顿乾坤，手为经理。 好是今年庆事。抱奇孙、一门佳气。蓬山振佩，麟符重锡，褒纶新美。玉树参庭，桂枝分种，香浮兰芷。看他年、接武三槐，长是伴、壮椿岁。'),
	(9002, 275, '采桑子', '障泥油壁人归后，满院花阴。楼影沈沈。中有伤春一片心。 间穿绿树寻梅子，斜日笼明。团扇风轻。一径杨花不避人。'),
	(9003, 172, '酹江月', '天然灵种，遍尘寰、不许一枝分植。瀛海沈沈群玉宴，迥出八仙标格。珠幄留云，翠绡笼雪，浅露宫黄额。无双亭下，未容凡卉连璧。 犹是射虎归来，朱兰独倚，曾作东风客。素态自羞时态改，何必铅华倾国。舞影鸾孤，绕心蝶倦，占断春消息。月明十里，坐中还记曾识。'),
	(9004, 76, '水调歌头（三月初三）', '羽觞随曲水，佳气溢双清。真贤瑞世，恰与真圣日同生。出侍红云一朵，出按皇华六辔，特地福吾闽。底是长生箓，八郡咏歌声。 奏天子，倾义廪，济饥民。南州指使，青州公案一般仁。却恐紫泥有诏，社稷重臣事业，非晚观岩宸。来岁这般节，宣劝玉堂人。'),
	(9005, 454, '沁园春（用履齐多景楼韵）', '万里飘萍，送江入海，过古润州。正羁怀无奈，凭高纵览，濛濛烟雨，簇簇渔舟。南北区分，江山形胜，忧愤令人扶上楼。沉凝久，任斜飞雪片，急洒貂裘。 英风追想孙刘。似黑白两奁棋未收。把烟霞饶与，坡仙米老，丹青难觅，摩诘营邱。斗野号风，海门残照，长与人间管领愁。凭谁问，借天河一挽，洗甲兵休。'),
	(9006, 663, '兰陵王', '角声切。何处梅梢弄雪。还乡梦，玉井楼前，千朵芙蕖插空碧。邻翁问消息。为说红尘倦客。应怜笑、弓剑旌旗，底事留人未归得。 淮山旧相识。记急处笙歌，静里锋镝。隋堤杨柳犹春色。嗟十载人事，几番棋局，青油年少已鬓白。漫惆怅京国。 朱墨。困无力。似病鹤樊笼，老骥羁勒。夕阳不系栖林翼。待添竹东圃，种松西陌。功名休问，吾老矣，付俊杰。'),
	(9007, 221, '留春令', '夜来小雨三更作。近水处、小桃开却。玉女向晓掀朱箔。似与花枝有约。 绿池上、柳腰纤弱。燕子过、谁家院落。春衫试着香罗薄。无奈东风太恶。'),
	(9008, 155, '水龙吟（除夕）', '小童教写桃符，道人还了常年例。神前灶下，祓除清净，献花酌水。祷告些儿，也都不是，求名求利。但吟诗写字，分数上面，略精进、尽足矣。 饮量添教不醉。好时节，逢场作戏。驱傩爆竹，软饧酥豆，通宵不睡。四海皆兄弟，阿鹊也、同添一岁。愿家家户户，和和顺顺，乐升平世。'),
	(9009, 155, '昼锦堂', '薄袖禁寒。轻妆媚晚，落梅庭院春妍。映户盈盈，回倩笑整花钿。柳裁云翦腰支小，凤蟠鸦耸髻鬟偏。东风里，香步翠摇，蓝桥那日因缘。 婵娟。留慧盼，浑当了、匆匆密爱深怜。梦过阑干，犹认冷月秋千。杏梢空闹相思眼，燕翎难系断肠笺。银屏下，争信有人，真个病也天天。'),
	(9010, 82, '沁园春（庆友人陈碧山）', '才大文豪，朱有暗里，今须点头。奈兰宫一跌，槐黄时候。银袍逐浪，韦带随流。过尽鹤书，阅周鹗表，必竟都无名字留。空长恨，蕡终不第，齿且先侯。 休愁。有路堪由。最喜徐卿百不忧。正椿松未老，芝兰竞秀，奇毛雏凤，骍角犁牛。汉殿少年，新丰逆旅，岂肯卑微名位休。行将见，长沙召贾，御史除周。'),
	(9011, 82, '眼儿媚（题苏小楼）', '丈夫只手把吴钩。能断万人头。如何铁石，打作心肺，却为花柔。 尝观项籍并刘季，一怒世人愁。只因撞着，虞姬戚氏，豪杰都休。'),
	(9012, 673, '长相思', '郎恩深。妾思深。祗为恩深便有今。回纹辜旧吟。 云沉沉。水沉沉。一点坚如百炼金。郎应知妾心。'),
	(9013, 257, '红窗迥', '春闱期近也，望帝乡迢迢，犹在天际。懊恨这一双脚底。一日厮赶上五六十里。 争气。扶持我去，转得官归，恁时赏你。穿对朝靴，安排你在轿儿里。更选个、宫样鞋，夜间伴你。'),
	(9014, 340, '括江神子', '拾遗流落锦宫城。故人情。眼为青。时向百花，潭水濯冠缨。韦曲杜陵行乐地，尘土暗，叹漂零。 园翁溪友总比邻。酒盈尊。肯相亲。落日蹇驴，扶醉两眉颦。磊落平生忠义胆，诗与酒，醉还醒。 范文正严先生祠堂记：先生，汉光武之故人也，相尚以道。及帝握赤符，乘六龙，得圣人之时，臣妾亿兆，天下孰加焉，惟先生以节高之。既而动星象，归江湖，得圣人之清，泥涂轩冕，天下熟加焉，惟光武以礼下之。在虫之上九，众方有为，而独不事王侯，高尚其志，先生以之。在屯之初九，阳德方亨，而能以贵下贱，大得民也，光武以之。盖先生之心，出乎日月之上；光武之器，包乎天地之外。微先生，不能成光武之大；微光武，岂能遂先生之高哉！而使贪夫廉、儒夫立，是有大功于名教也。某来守都邦，始构堂而尊焉。乃复其为后者四家，以奉祠事。又从而歌曰：“云山苍苍。江水泱泱。先生之风，山高水长。”'),
	(9015, 340, '括摸鱼儿', '泛松江、水遥山碧，清寒微动秋浦。霜云霁色横无际，别鹄惊鸿无数。朝又暮。听牧笛长吹，隐隐渔榔度。骚人才子。既览物兴怀，浮游尘外，啸傲剧清思。 人间世。扰扰荣途要路。瀛洲琼馆安所。轩裳何似渔蓑兴，萧散龙游鹤渚。须归去。办双桨孤帆，云月和烟雨。江湖伴侣。趁社橘初黄，汀葭馀翠，成我莼鲈趣。 山谷煎茶赋：汹汹如乎涧松之发清吹，皓皓乎如春空之行白云。宾主欲眠而同味，水茗相投而不浑。苦口利病，解胶涤昏。未尝一日不放箸而策茗碗之勋者也。余尝为嗣直沦茗，因录其涤烦破睡之功，为之甲乙。建溪如割。双井如呕。日铸如■。其馀苦则辛螫，甘则底滞。呕酸寒胃，令人失睡。亦未足与议。或曰：无甚高论，敢问其次？涪翁曰：味江之罗山，严道之蒙顶。黔阳之都濡、高株，泸川之纳溪、梅岭。夷陵之压砖，邛之火井。不得已而去于三，则六者亦可酌兔褐之瓯，瀹鱼眼之鼎者也。或者又曰：寒中瘠气，莫甚于茶。或济之盐，句贼破家。滑窍走水，又况鸡苏之与胡麻！涪翁于是酌岐雷之醪醴，参伊圣之汤液。C958附子如博投，以熬葛仙之垩。去EB5C而用盐，去橘而用姜。不夺茗味，而佐以草石之良。所以固太仓而坚作强。于是有胡桃、松实、庵摩、鸭脚、勃贺、糜芜、水苏、甘菊。既加臭味，亦厚宾客。前面四后四，各用其一。少则美，多则恶。发挥其精神，又益于咀嚼。盖大匠无可弃之材，太平非一士之略。厥初贪味隽永，速化汤饼。乃至中夜，不眠耿耿。既作温齐，殊可屡歃。如以六经济三尺法。虽有除治，与人安乐。宾至则煎，去则就榻。不游轩后之华胥，则化庄周之蝴蝶。'),
	(9016, 340, '括满江红', '太一真人，莲叶向、中流荡漾。凌万顷、风为帆席，浪为轻桨。乱舞翠绡云雾薄，卧看玉宇琉璃晃。似飘然、万斛举龙骧，随波荡。 太华顶，花十丈。飘一叶，借清赏。倩龙眠老手、为渠摹仿。鳌禁岧峣谁寓直，玉堂学士今刘向。要清宵、特地杖青藜，来相访。 东坡书林和靖诗后：吴侬生长湖山曲，呼吸湖光饮山渌。不论世外隐君子，佣奴贩妇皆冰玉。先生可是绝俗人，神清骨冷无由俗。我不识君曾梦见，瞳子了然光可烛。遗篇妙字处处有，步绕西湖看不足。诗如东野不言寒，书似西台差少肉。平生高节已难继，将死微言犹可录。自言不作封禅书，更有悲吟白头曲。我笑吴人不好事，好作祠堂傍修竹。不然配食水仙王，一盏寒泉荐秋菊。'),
	(9017, 340, '括酹江月', '泛舟赤壁，正风徐波静，举尊属客。渺渺予怀天一望，万顷凭虚独立。桂桨空明，洞箫声彻，怨慕还凄恻。星稀月淡，江山依旧陈迹。 因念酾酒临江，赋诗横槊，好在今安适。谩寄蜉蝣天地尔，瞬目盈虚消息。江上清风，山间明月，与子欢无极。翻然一笑，不知东方既白。 东坡后赤壁赋：是岁十月之望，步自雪堂，将归于临疙。二客从予过黄泥之坂。霜露既降，草木尽脱。人影在地，仰见明月，顾而乐之，行歌相答。已而叹曰：有客无酒，有酒无肴，月白风清，如此良夜何！客曰：今者薄暮，举纲得鱼，巨口细鳞，状似松江之鲈。顾安所得酒乎？归而谋诸妇。妇曰：我有斗酒，藏之久矣，以待子不时之须。于是携酒与鱼，复游于赤壁之下。江流有声，断岸千尺，山高月小，水落石出。曾日月之几何，而江山不可复识矣！予乃摄衣而上，履B357岩，披蒙茸。踞虎豹，登虬龙，攀栖鹘之危巢，俯凭夷之幽宫。盖二客不能从焉。划然长啸，草木震动。山鸣谷应，风起水涌。予亦悄然而悲，肃然而恐，凛乎其不可留也。返而登舟，放乎中流，听其所止而休焉。时夜将半，四顾寂寥。适有孤鹤，横江东来，翅如车轮，玄裳缟衣，戛然长鸣，掠予舟而西也。须臾客去，予亦就睡。梦一道士，羽衣蹁跹，过临皋之下，揖予而言曰：赤壁之游乐乎？问其姓名，BA3D而不答，呜呼噫嘻，我知之矣！畴昔之夜，飞鸣而过我者，非子也耶！道士顾笑，予亦惊寤，开户视之，不见其处。'),
	(9018, 340, '括声声慢', '暮春天气，争看长安，水边多丽人人。意远态浓，肌理骨肉轻匀。绣罗衣裳照映，尽蹙金、孔雀麒麟。夸荣贵，是椒房云幕，恩宠无伦。簇簇紫驼翠釜，间去声水精盘里，绣鲙纷纶。御送珍羞，夹道箫鼓横陈。后来宾从杂B241，认青鸾、飞舞红巾。扶下马，似杨花、翻入锦茵。'),
	(9019, 340, '括摸鱼儿', '醉之乡、其去中国，不知其几千里。其土平旷无涯际。其气和平一揆。无寒暑，无聚落居城，无怒而无喜。昔黄帝氏。仅获造其都，归而遂悟，结绳已非矣。 及尧舜，盖亦至其边鄙。终身太平而治。武王得志于周世。命立酒人之氏。从此后，独阮籍渊明，往往逃而至。何其淳寂。岂古华胥，将游是境，余故为之记。 杜工部丽人行：三月三日天气新，长安水边多丽人。熊浓意远淑且真，肌理细腻骨肉匀。绣罗衣裳照暮春。蹙金孔雀银麒麟。头上何所有？翠微バ叶垂鬓唇，背后何所见？珠压腰衱稳称身。就中云幕椒房亲，赐名大国虢与秦。紫驼之峰出翠釜，水精之盘行秦鳞。犀筋厌饫久未下，銮刀缕切空纷纶。黄门飞鞚不动尘，御厨丝络送八珍。箫鼓哀吟感鬼神，宾从杂遝实要津。后来鞍马何逡巡。当轩下马入锦茵。杨花落雪覆白苹，青鸟飞去衔红巾。炙手可热势绝伦，慎莫近前丞相嗔。'),
	(9020, 340, '括贺新凉', '兰亭当日事。有崇山、茂林修竹，群贤毕至。湍急清流相映带，旁引流觞曲水。但畅叙、幽情而已。一咏一觞真足乐，厌管弦丝竹纷尘耳。春正暮，共修禊。 惠风和畅新天气。骋高怀、仰观宇宙，俯察品类。俯仰之间因所寄，放浪形骸之外。曾不知、老之将至。感慨旧游成陈迹，念人生、行乐都能几。后视今，犹昔尔。 陶渊明归去来：归去来兮，田园将芜胡不归。既自以心为形役，奚惆怅而独悲。悟已往之不谏，知来者之可追。识迷途其未远，觉今是而昨非。舟遥遥以轻飏，风飘飘而吹衣。问征夫以前途，恨晨光之熹微。乃瞻衡宇，载欣载奔。僮仆欢迎，稚子候门。三径就荒，松菊犹存。携幼入室，有酒盈尊。引壶觞以自酌，盼庭柯以怡颜。倚南窗以寄傲，审容膝之易安。园日涉以成趣，门虽设而常关。策扶老以游憩，时矫首而遐观。云无心以出岫，鸟倦飞而知远。景翳翳以将入，抚孤松而盘桓。归去来兮，请息交以绝游。世与我而相遗，复驾言兮焉求。悦亲戚之情话，乐琴书以消忧。农人告予以春及，将有事于西畴。或命巾画车或棹孤舟。既窈窕以寻壑，亦崎岖而经邱。木欣欣以向荣，泉涓涓而始流。喜万物之得时，感吾生之行休。已矣哉！寓形宇内复几时，曷不委心任去留，胡为乎遑遑欲何之！富贵非吾愿，帝乡不可期。怀良辰以孤往，或执杖而耘耔。登东皋以舒啸，临清流而赋诗。聊乘化以归尽，乐夫天命复溪疑！'),
	(9021, 44, '望江南（西方好·六首）', '西方好，随念即超群。一点灵光随落日，万端尘事付浮云。人世自纷纷。 凝望处，决定去栖神。金地经行光里步，玉楼宴坐定中身。方好任天真。'),
	(9022, 88, '渔家傲', '数珠。 净土故乡嗟乍别。天涯流浪经时节。老去染沾眉鬓雪。思归切。闻声愿寄辽天月。 念念时时修净业。临终佛定来迎接。有誓表为诚实说。广长舌。三千遍覆红莲叶。'),
	(9023, 88, '渔家傲', '诵持。 休纵心猿驰意马。牢将系念绳头把。说破十疑因智者。争传写。庐山又结莲华社。 十八大贤居会下。功成五色云西驾。诸上善人都在那。相迎迓。聚头只说无生话。'),
	(9024, 88, '渔家傲', '刹那。 文墨尖新无处用。已将名利浑如梦。一串数珠随手弄。休千种。唯闻念佛心欢勇。 滉漾空中仙乐动。笙箫声细天风送。接引凡夫归圣众。香云捧。男儿此日方崇重。'),
	(9025, 88, '渔家傲', '天涯。 理性本来长自在。灵通昭彻光无碍。因被无明风恼害。真如海。等床吹动波千派。 五蕴山头云叆叇。遮藏心月无光彩。六贼会须知悔改。除贪爱。刹那跳出娑婆界。'),
	(9026, 499, '沁园春（庆杨平）', '襟韵何如，文雅风流，王谢辈人。问传家何物，多书插架，放怀无可，有酒盈樽。一咏一谈，悠然高致，似醉当年曲水春。还知否，壮胸中万卷，笔下千军。 门前我有佳宾。但明月、清风更此君。喜西庐息驾，心间胜日，东皇倚杖，目送行云。闻道君王，玉堂佳处，欲诏长杨奏赋孙。功名看，一枝丹桂，两树灵椿。'),
	(9027, 153, '临江仙', '摘索枝头何处玉，吹来万里春风。须臾陆地遍芙蓉。珠帘和气扑，一笑夺炉红。 文字红裙相间出，主人钟鼎仙翁。清谈隽语与香浓。太平欢意远，人在玉壶中。'),
	(9028, 280, '乌夜啼', '径藓痕沿碧甃，檐花影压红阑。今年春事浑无几，游冶懒情慳。 旧梦莺莺沁水，新愁燕燕长干。重门十二帘休卷，三月尚春寒。'),
	(9029, 280, '一丛花令', '梨花随月过中庭。月色冷如银。金闺平帖阳台路，恨酥雨、不扫行云。妆褪臂闲，髻慵簪卸，盟海浪花沉。 洞箫清吹最关情。腔拍懒温寻。知音一去教谁听，再拈起、指法都生。天阔雁稀，帘空莺悄，相傍又春深。'),
	(9030, 281, '如梦令（石门岩）', '门外数峰围绕。帖石路儿弯小。花老不禁风，委地乱红多少。人悄。人悄。隔叶数声啼鸟。'),
	(9031, 603, '鹧鸪天', '九十吾家两寿星。今夫人赛昔夫人。百年转眼新开帙，十月循环小有春。 生日到，转精神。目光如镜步如云。年年长侍华堂宴，子子孙孙孙又孙。'),
	(9032, 651, '水调歌头（赠九霞子鞠九思）', '夺取天机妙，夜半看辰杓。一些珠露，阿谁运倒稻花头。便向此时采取，宛如碧莲合蕊，滴破玉池秋。万籁风初起，明月一沙鸥。 紫河车，乘赤凤，入琼楼。谓之玉汞，与铅与土正相投。五气三花聚顶，吹着自然真火，炼得似红榴。十月胎仙出，雷电送金虬。'),
	(9033, 651, '望江南', '丹已返，四转运行阴。逢六闭藏阳户气，三关全透合丁壬。龟游任浮沉。 时出入，无碍贯他心。游戏神通常出面，圆光周匝绕千寻。寒暑不相侵。'),
	(9034, 229, '醉太平', '冰肌玉容。情真意浓。小楼几度春风。醉琉璃酒钟。 关山万重。何时又逢。思量雨迹云踪。似襄王梦中。'),
	(9035, 291, '玉楼春', '纱窗春睡朦胧着。相见尚怀相别恶。梦随城上角声残，泪逐楼前花片落。 东风不解吹愁却。明月几番乖后约。当时惟恐不多情，今日情多无处着。'),
	(9036, 233, '沁园春（送姚雪篷之贬所）', '访衡山之顶，雪鸿渺渺，湘江之上，梅竹娟娟。寄语波臣，传言鸥鹭，稳护渠侬书画船。'),
	(9037, 233, '渔父（二）', '渔父醉，钓竿闲。柳下呼儿牢系船。高眠风月天。'),
	(9038, 233, '望江南', '石屏老，悔不住山林。注定一生知有命，老来万事付无心。巧语不如喑。 贫亦乐，莫负好光阴。但愿有头生白发，何忧无地觅黄金。遇酒且须斟。'),
	(9039, 233, '望江南（仆既为宋壶山说其自说未尽处，壶山必有答语，仆自嘲三解）', '石屏老，家住海东云。本是寻常田舍子，如何呼唤作诗人。无益费精神。 千首富，不救一生贫。贾岛形模元自瘦，杜陵言语不妨村。谁解学西昆。'),
	(9040, 233, '祝英台近（别李择之诸丈后途中寄此）', '泛杭川，临尘水。几日共游戏。歌笑开怀，酒醒又还醉。奈何一旦分携，连宵风雨，剪不断、客愁千里。 水云际。遥望一片飞鸿，苦是失群地。满眼春风，管甚闲桃李。此行归老家山，相逢难又，但一味、相思而已。'),
	(9041, 233, '清平乐（嘲人）', '醉狂痴作。误信青楼约。酒醒梅花吹画角。翻得一场寂寞。 相如谩赋凌雪。琴台不遇文君。江上琵琶旧曲，只堪分付商人。'),
	(9042, 233, '浣溪沙', '病起无聊倚绣床。玉容清瘦懒梳妆。水沉烟冷橘花香。 说个话儿方有味，吃些酒子又何妨。一声啼鴂断人肠。'),
	(9043, 233, '鹊桥仙', '新荷池沼，绿槐庭院。檐外雨声初断。喧喧两部乱蛙鸣，怎得似、啼莺睍睆。 风光流转。客游汗漫。莫问鬓丝长短。即时杯酒醉时歌，算省得、闲愁一半。'),
	(9044, 233, '贺新郎（丰真州建江淮伟观楼）', '百尺连云起。试登临、江山人物，一时俱伟。旁挹金陵龙虎势，京岘诸峰对峙。隐隐接、扬州歌吹。雪浪舞从三峡下，乍逢迎、海若谈秋水。形胜地，有如此。 使君一世经纶志。把风斤月斧，来此等闲游戏。见说楼成无多日，大手一何容易。笑天下、纷纷血指。酝酿春风与和气，举长江、变作香醪美。人共乐，醉桃李。'),
	(9045, 411, '沁园春（敬次自真人韵）', '湖海襟期，烟霞气宇，天下星郎。有灵方时后，年年却老，神锋耳底，夜夜腾光。万卷蟠胸，千钟蘸甲，衮衮词源三峡滂。功成处，见须弥日月，河岳星霜。 兴来引笔千行。看举世何人是智囊。任纵横万变，难瞒道眼，优游自乐，不识愁肠。闹市业中，密林静处，鼻观常闻三界香。天书到，听笙箫竞奏，幢盖班行。'),
	(9046, 462, '水调歌头（寿提刑）', '帝念重湖远，使者选清强。关西夫子人杰，揽辔出鸳行。熊楚一天坐镇，虎节三台更历，号令肃秋霜。褰帷间风俗，原隰总生光。 瞻南极，朝北斗，酌霞觞。潢池息浪奏凯，筹饷属萧张。一点眉间多□，一札日边有诏，香案侍东皇。岁岁蓬壶宴，好景对枨黄。'),
	(9047, 295, '生查子', '玉女翠帷熏，香粉开妆面。不是占春迟，羞被群花见。 纤手折柔条，绛雪飞千片。流入紫金卮，未许停歌扇。'),
	(9048, 47, '菩萨蛮', '东风去了秦楼畔。一川烟草无人管。芳树雨初晴。黄鹂三两声。 海棠花已谢。春事无多也。只有牡丹时。知他归不归。'),
	(9049, 68, '桂殿秋（寿于湖先生）', '青帝子，碧莲宫，不驾云车骑白龙。瑶池路远羽衣湿，玉佩冷冷明月中。'),
	(9050, 96, '西江月（和李参政）', '识贯事中枢纽，笔开象外精神。传观弓力异常钧。衣我六铢羞问。 周后数茎命粒，鲁儒一点芳心。啄残楼老付谁论。谩要睡馀支枕。'),
	(9051, 160, '苏幕遮', '气随神，神随气。神气相随，透入泥丸里。长把金关牢锁闭。捉得金晶，暗地添欢喜。 下辛勤，须发志。十二时中，莫把工夫弃。阴尽阳全神出体。功行成时，名列神仙位。'),
	(9052, 318, '六州歌头（吊武穆鄂王忠烈庙）', '高皇神武，善驾驭豪英。攘北狄，驱群盗，命天膺。救苍生。奈梦绕沙漠，隔温清，屈和好，召大将，归兵柄，列枢庭。公指汴京。威已振河洛，不顾身烹。失一时机会，嗟左衽吾民。痛岳家军。孰扶倾。 久沉冤愤，七十载，还复遇，帝王真。表遗烈，锡王号，日照临。激士心。始认安刘计，宁祸己，是忠臣。我乘传，访壁垒，想精明。英气凛然若在，仍题扁、昭揭天恩。笑原头芳草，一死不能春。交怨人神。'),
	(9053, 678, '苏幕遮', '地偏灵，天应瑞。簇簇银花，团绕真珠蕊。金阙玉楼分十二。要伴姮娥，与月循环睡。 月如花，花表岁。人道闰年，添个真奇异。不许扬州夸间气。昨夜春风，吹送柴门里。'),
	(9054, 678, '归朝欢（春游）', '听得提壶沽美酒。人道杏花深处有。杏花狼藉鸟啼风，十分春色今无九。麝煤销永昼。青烟飞上庭前柳。 画堂深，不寒不暖，正是好时候。 团团宝月纤手。暂借歌喉招舞袖。真珠滴破小槽红，香肌缩尽纤罗瘦。投分须白首。黄金散与亲和旧。且衔杯，壮心未落，风月长相守。'),
	(9055, 678, '鱼游春水（怨别）', '池塘生春草。数尽归鸿人未到。天涯目断，青鸟尚赊音耗。晓月频窥白玉堂，暮雨还湿青门道。巢燕引雏，乳莺空老。 庭际香红倦扫。乾鹊休来枝上噪。前回准拟同他，翻成病了。欲题红叶凭谁寄，独抱孤桐无心挑。眉间翠攒，鬓边霜早。'),
	(9056, 678, '天仙子', '白玉为台金作盏。香是江梅名阆苑。年时把酒对君歌，歌不断。杯无算。花月当楼人意满。 翘戴一枝蝉影乱。乐事且随人意换。西楼回首月明中，花已绽。人何远。可惜国香天不管。'),
	(9057, 678, '二郎神', '日高睡起，又恰见、柳梢飞絮。倩说与、年年相挽，却又因他相误。南北东西何时定，看碧沼、青萍无数。念蜀郡风流，金陵年少，那寻张绪。 应许。雪花比并，扑帘堆户。更羽缀游丝，毡铺小径，肠断鹁鸠唤雨。舞态颠狂，腰肢轻怯，散了几回重聚。空暗想，昔日长亭别酒，杜鹃催去。'),
	(9058, 22, '满江红', '其侧有亭，山谷榜曰奇观。 一水飞空，揭起珠帘全幅。不须人卷，不须人轴。一点不容飞燕入，些儿未许游鱼宿。向山头、款步听疏音，清如玉。 三峡水，堪人掬。三汲浪，堪龙浴。更两边潇洒，数竿脩竹。晓倩碧烟为绳束，夜凭新月为钩曲。问当年、题品是何人，黄山谷。'),
	(9059, 48, '长相思（别意）', '玉尊凉。玉人凉。若听离歌须断肠。休教成鬓霜。画桥西，画桥东。有泪分明清涨同。如何留醉翁。'),
	(9060, 398, '鹧鸪天（寿熊尚友）', '清晓南窗笑语喧。今朝生日胜从前。鹤吞沆瀣神逾爽，松饱风霜节更坚。 调气马，殖心田。几曾佞佛与夸仙。戏衫脱了浑无累，快活人间百十年。'),
	(9061, 398, '鹧鸪天（寿熊尚友）', '清晓南窗笑语喧。今朝生日胜从前。鹤吞沆瀣神逾爽，松饱风霜节更坚。 调气马，殖心田。几曾佞佛与夸仙。戏衫脱了浑无累，快活人间百十年。'),
	(9062, 227, '满江红（约斋同席用马庄父韵）', '挥手华堂，重整顿、选花场屋。撩鼻观、飞浮杂沓，异香芬馥。金缕尚馀闲态度，冰姿早作新妆束。恨尊前、缺典费思量，无松竹。蜂蝶恨，何时足。桃李怨，成粗俗。为情深、拚了一生愁独。菊信谩劳频探问，兰心未许相随逐。想从今、无暇劚蔷薇，鉏罂粟。'),
	(9063, 562, '重叠金（春宵）', '一川花月青春夜。玉容依约花阴下。月照曲阑干。红绡挹露寒。 袖香温素手。意铄金卮酒。香远绣帘开。画楼吹落梅。'),
	(9064, 217, '水调歌头（徐高士游洞霄）', '雨后烟景绿，春水涨桃花。系舟溪上，笋舆十里达平沙。路转峰回胜处，无数青荧玉树，缥缈羽人家。楼观倚空碧，水竹湛清华。 纵幽寻，携蜡屐，上苍霞。古仙何在，空馀药灶委岩洼。他日倘然归老，乞取一庵云卧，随分了生涯。底用更辛苦，九转炼黄芽。'),
	(9065, 341, '水调歌头', '螺水亘千古，鳌顶冠三山。年丰帅阃尘静，栏槛纵遐观。四望潮登浦溆，万顷绿浮原野，堤岸溢波澜。畎浍皆沾足，日永桔槔闲。 肆华筵，鱼鸟乐，众宾让。良辰好景，年年莫放此盟寒。且念新湖遗爱，莫作故园遐想，到处是乡关。勋业知非晚，聊把镜频看。'),
	(9066, 341, '浣溪沙（忆西湖）', '却忆西湖烂漫游。水涵山影翠光浮。轻舟短棹不惊鸥。 带露精神花妩媚，依风情态柳温柔。莺歌燕语巧相留。'),
	(9067, 36, '柳梢青（庆叶丞）', '春到江南，今年寒少，早有疏梅。相伴双松，吟哦风月，着意花开。 天香甚处安排。便何似、调羹去来。劲节仙姿，玉堂难老，身在蓬莱。'),
	(9068, 36, '水调歌头', '草草三间屋，爱竹旋添栽。碧纱窗户，眼前都是翠云堆。一月山翁高卧，踏雪水村清冷，木落远山开。唯有平安竹，留得伴寒梅。 唤家童，开门看，有谁来。客来一笑，清话煮茗更传杯。有酒只愁无客，有客又愁无酒，酒熟且俳徊。明日人间事，天自有安排。'),
	(9069, 211, '采桑子', '佳人满劝金蕉叶，夜玉春温。别后黄昏。燕子楼高月一痕。 年年依旧梨花雨，粉泪空存。流水孤村。不着寒鸦也断魂。'),
	(9070, 211, '西江月', '四壁空围恨玉，十香浅捻啼绡。殷云度雨井桐凋。雁雁无书又到。 别后钗分燕股，病馀镜减鸾腰。蛮江豆蔻影连梢。不道参横易晓。'),
	(9071, 692, '望海潮', '梅天雨歇，柳堤风定，江浮画鹢纵横。瀛女弄箫，冯夷伐鼓，云间凤咽鼍鸣。波面走长鲸。卷怒涛来往，搅碎沧溟。两岸游人笑语，罗绮间簪缨。 灵均逝魄无凭。但湘沅一水，到底澄清。菰黍万家，丝桐五彩，年年吊古深情。锦帜片霞明。使操舟妙手，翻动心旌。向晚鱼龙戏罢，千里浪花平。'),
	(9072, 432, '念奴娇（题钓台）', '扁舟夜泛，向子陵台下，偃帆收橹。水阔风摇舟不定，依约月华新吐。细酌清泉，痛浇尘臆，唤起先生语。当年纶钓，为谁高卧烟渚。 还念古往今来，功名可共，能几人光武。一旦星文惊四海，从此故人何许。到底轩裳，不如蓑笠，久矣心相与。天低云淡，浩然吾欲高举。'),
	(9073, 625, '水龙吟', '少年闻说京华，上元景色烘睛昼。朱输画毂，雕鞍玉勒，九衢争骤。春满鳌山，夜沉陆海，一天星斗。正红球过了，鸣鞘声断，回莺驭、钧天奏。 谁料此生亲到，十五年、都城如旧。而今但有，伤心烟雾，萦愁杨柳。宝箓宫前，绛霄楼下，不堪回首。愿皇图早复，端门灯火，照人还又。'),
	(9074, 190, '水调歌头（联句问讯罗汉同朱熹）', '雪月两相映，水石互悲鸣。不知岩上枯木，今夜若为情。应见尘中胶扰，便道山间空旷，与么了平生。与么平生了，□水不流行。熹起披衣，瞻碧汉，露华清。寥寥千载，此事本分明。若向乾坤识易，便信行藏无间，处处总圆成。记取渊冰语，莫错定盘星。'),
	(9075, 107, '浣溪沙（咏梅）', '茅舍疏离出素英。临风照水眩精神。娟娟新月又黄昏。削约寒枝香未透，细看频嗅独消魂。为谁消瘦不禁春。'),
	(9076, 107, '浣溪沙', '风入枯藜衣袂凉。江枫园柳半青黄。洗车飞雨带天香。世事一场真大梦，宦情都薄似秋光。竹洲有酒可徜徉。'),
	(9077, 500, '念奴娇（上太守月词）', '玉楼绛气，卷霞绡云浪，飞空蟾魄。人世江山惊照耀，烟霭鳌峰千尺。陆海蓬壶，银葩星晕，点破琉璃碧。有人吟笑，紫荷香满晴陌。况是东府君侯，西清别骑，樽俎开华席。迤逦飞轮催杖履，入对青藜仙客。襦袴歌谣，升平风露，拼取金莲侧。梅花吹动，满城依旧春色。'),
	(9078, 441, '南乡子（寿木状元）', '十月小阳春。放榜梅花作状元。重庆礼成三日后，生贤。第一龙飞不偶然。劝酒自弹弦。更着班衣寿老仙。见说海坛沙涨也，明年。此夜休嗔我近前。'),
	(9079, 512, '兰陵王（和吴宣卿）', '乱烟簇。帘外青山渐肃。莲房静。荷盖半残，欲放清涟媚溪绿。凭高送远目。飞起沧洲雁鹜。寒窗静，荼コ未深，一枕胡床昼眠足。闲行问松菊。今□雨谁家，空对银烛。箫声忽下瑶台曲。看鹤舞风动，乌啼云起，何须舟内怨女哭。抱琴写幽独。情触。会相续。况节近中元，月浪翻屋。长鲸愁晓寒蟾促。要百柁倾□，万花流玉。山肴倒尽，又空腹，鲙野蔌。'),
	(9080, 512, '鹧鸪天（咏野梅）', '千树家园锁旧津。谁移数点在孤村。海仙探蕊禽留影，楚客穿花蝶舞魂。桥断港，水横门。残霞零落晚烟昏。只因留住三更月，暗里香来别是春。'),
	(9081, 101, '长相思', '行相思。坐相思。两处相思各自知。相思更为谁。 朝相思。暮相思。一日相思十二时。相思无尽期。'),
	(9082, 101, '如梦令', '杨柳千丝万缕。特地织成愁绪。休更唱阳关，便是渭城西路。归去。归去。红杏一腮春雨。'),
	(9083, 101, '如梦令（道人书郡楼）', '旧恨新愁无际。近水远山都是。西北有高楼，正为行藏独倚。留滞。留滞。家在吴头楚尾。'),
	(9084, 101, '点绛唇', '屈指新冬，肃霜天气重阳后。授衣时候。兰菊香盈袖。此日生申，维岳钟神秀。倾名酎。篆添金兽。共祝如椿寿。'),
	(9085, 81, '卜算子（题徐仙亭）', '流水一湾西。晚坐孤亭静。不见高人跨鹤归，风竹摇清影。往古与来今，休用重重省。十里梅花雪正晴，月挂遥山冷。'),
	(9086, 61, '鹊桥仙', '只应将巧畀人间，定却向、人间乞取。'),
	(9087, 614, '瑞鹤仙（赠官妓周韵）', '醉归来，不悟人间天上，云雨难寻旧迹。但馀香、暗着罗衾，怎生忘得。'),
	(9088, 140, '南乡子', '洪迈被拘留。稽首垂哀告彼酋。七日忍饥犹不耐，堪羞。苏武争禁十九秋。厥父既无谋。厥子安能解国忧。万里归来夸舌辨，村牛。好摆头时便摆头。'),
	(9089, 147, '怨王孙（春情）', '毵毵杨柳绿初低。澹澹梨花开未齐。楼上情人听马嘶。忆郎归。细雨春风湿酒旗。'),
	(9090, 17, '步蟾宫', '凤帘舞带花铺绣。水沉暖、祥烟迷书。凤街灯照蕊珠筵，韵双琯、钧声已奏。 绮罗人劝千秋寿。吉祥满、十分芳酒。愿人间、嘉庆一千年，共南极、东华长久。'),
	(9091, 17, '南柯子（六和塔）', '金甃蟠龙尾，莲开舞凤头。凉生宫殿不因秋。门外莫寻尘世，卷地江流。霁色澄千里，潮声带两洲。月华清泛浪花浮。今夜蓬莱归梦，十二琼楼。'),
	(9092, 17, '望江南', '成都好，蚕市趁遨游。夜放笙歌喧紫陌，春邀灯火上红楼。车马溢瀛洲。人散后，茧馆喜绸缪。柳叶已饶烟黛细，桑条何似玉纤柔。立马看风流。'),
	(9093, 17, '夏云峰（伤春）', '天阔云高，溪横水远，晚日寒生轻晕。闲阶静、杨花渐少，朱门掩、莺声犹嫩。悔匆匆、过却清明，旋占得馀芳，已成幽恨。都几日阴沈，连宵慵困。起来韶华都尽。怨入双眉闲斗损。乍品得情怀，看承全近。深深态、无非自许。厌厌意、终羞人问。争知道、梦里蓬莱，待忘了馀香，时传音信。纵留得莺花，东风不住，也则眼前愁闷。'),
	(9094, 17, '西江月', '味过华林芳蒂，色兼阳井沈朱。轻匀绛蜡里团酥。不比人间甘露。神鼎十分火棘，龙盘三寸红珠。清含冰蜜洗云腴。只恐身轻飞去。'),
	(9095, 469, '浣溪沙（野眺）', '日射平溪玉宇中。云横远渚岫重重。野花犹向涧边红。静看沙头鱼入网，闲支藜杖醉吟风。小春天气恼人浓。'),
	(9096, 1, '无闷', '风急还收，云冻又开，海阔无人翦水。算六出工夫，怎教容易。刚被郢歌楚舞，镇独向、尊前夸轻细。想谢庭诗咏，梁园赋赏，未成欢计。 天意。是则是。便下得控持，柳梢梅蕊。又争奈、看看渐回春意。好趁东君未觉，预先把、园林都装缀。看是处、玉树琼枝，胜却万红千翠。'),
	(9097, 102, '调笑令', '华草。华草。秀发乘春更好。深心密竹纷纷。妖韶随处动人。人动。人动。王孙公子情重。'),
	(9098, 444, '惜花容', '少年看花双鬓绿。走马章台管弦逐。而今老更惜花深，终日看花看不足。坐中美女颜如玉。为我一歌金缕曲。归时压得帽檐欹，头上春风红簌簌。'),
	(9099, 194, '虞美人', '春风曾见桃花面。重见胜初见。两枝独占小春开。应怪刘郎迷路、又重来。旁人应笑髯公老。独爱花枝好。世间好景不长圆。莫放笙歌归院、且尊前。'),
	(9100, 697, '瑶池月（烟波行）', '扁舟寓兴，江湖上、无人知道名姓。忘机对景，咫尺群鸥相认。烟雨急、一片篷声碎，醉眼看山还醒。晴云断，狂风信。寒蟾倒，远山影。谁听。横琴数曲，瑶池夜冷。这些子、名利休问。况是物、都归幻境。须臾百年梦，去来无定。向婵娟、留住青春，笑世上、风流多病。蒹葭渚，芙蓉径。放侯印，趁渔艇。争甚。须知九鼎，金砂如圣。'),
	(9101, 697, '洞仙歌', '杳无风色，肠断莲花信。水鉴云垂数峰影。向劳生辛苦，寒暑煎人，争不老，空想秋堂夜静。玉姬挥皓月，时送微凉，莫吝金卮为伊尽。柳下夕阳收，傍水重游，花茵上、雪回襟冷。问避暑天机、自有奇人，但且对湖光，世间谁醒。'),
	(9102, 141, '极相思令', '柳烟霁色方春。花露逼金茎。秋千院落，海棠渐老，才过清明。嫩玉腕托香脂脸，相传粉、更与谁情。秋波绽处，相思泪迸，天阻深诚。'),
	(9103, 438, '虞美人（寄情）', '黄金柳嫩摇丝软。永日堂堂掩。卷帘飞燕未归来。客去醉眠欹枕、殢残杯。眉山浅拂青螺黛。整整垂双带。水沈香熨窄衫轻。莹玉碧溪春溜、眼波横。'),
	(9104, 656, '沁园春', '小雪初晴，画舫明月，强饮未眠。念翠鬟双耸，舞衣半卷，琵琶催拍，促管危弦。密意虽具，欢期难偶，遣我离情愁绪牵。追思处，奈溪桥道窄，无计留连。 天天。莫是前缘。自别后、深诚谁为传。想玉篦偷付，珠囊暗解，两心长在，须合金钿。浅淡精神，温柔情性，记我疏狂应痛怜。空肠断，奈衾寒漏永，终夜如年。'),
	(9105, 648, '无愁可解', '天下传咏，以谓几于达者。龙丘子犹笑之。此虽免乎愁，犹有所解也。若夫游于自然而托于不得已，人乐亦乐，人愁亦愁，彼且恶乎解哉。乃反其词，作无愁可解云： 光景百年，看便一世，生来不识愁味。问愁何处来，更开解个甚底。万事从来风过耳。何用不著心里。你唤做、展却眉头，便是达者，也则恐未。此理。本不通言，何曾道、欢游胜如名利。道即浑是错，不道如何即是。这里元无我与你。甚唤做、物情之外。若须待醉了、方开解时，问无酒、怎生醉。'),
	(9106, 434, '花发沁园春', '帝里春归，早先妆点，皇家池馆园林。雏莺未迁，燕子乍归，时节戏弄晴阴。琼楼珠阁，恰正在、柳曲花心。翠袖艳、衣凭阑干，惯闻弦管新音。此际相携宴赏，纵行乐随处，芳树遥岑。桃腮杏脸，嫩英万叶，千枝绿浅红深。轻风终日，泛暗香、长满衣襟。洞户醉，归访笙歌，晚来云海沉沈沈。'),
	(9107, 434, '玉楼春（海棠）', '锦城春色花无数。排比笙歌留客住。轻寒轻暖夹衣天，乍雨乍晴寒食路。 花虽不语莺能语。莫放韶光容易去。海棠开后月明前，纵有千金无买处。'),
	(9108, 403, '丑奴儿', '牡丹不好长春好，有个因依。一两枝儿。但是风光总属伊。当初只为嫦娥种，月正明时。教恁芳菲。伴著团圆十二回。'),
	(9109, 682, '定风波', '不是无心惜落花。落花无意恋春华。昨日盈盈枝上笑。谁道。今朝吹去落谁家。 把酒临风千种恨。难问。梦回云散见无涯。妙舞清歌谁是主。回顾。高城不见夕阳斜。'),
	(9110, 682, '菩萨蛮', '东风已绿瀛洲草。画楼帘卷清霜晓。清绝比湖梅。花开未满枝。 长天音信断。又见南归雁。何处是离愁。长安明月楼。'),
	(9111, 262, '排遍第七（撷花十八）', '羲城元靖贤相国，喜慕英雄士，赐金缯。闻斯事，频叹赏，封章归印。请赎冯燕罪，日边紫泥封诏，阖境赦深刑。万古三河风义在，青简上、众知名。河东注，任流水滔滔，水涸名难泯。至今乐府歌咏。流入管弦声。'),
	(9112, 262, '江南好', '江南客，家有宁馨儿。三世文章称大手，一门兄弟独良眉。藉甚众多推。 千里足，来自渥洼池。莫倚善题鹦鹉赋，青山须待健时归。不似傲当时。'),
	(9113, 433, '雨中花令（夏词）', '百尺清泉声陆续。映潇洒、碧梧翠竹。面千步回廊，重重帘幕，小枕欹寒玉。 试展鲛绡看画轴。见一片、潇湘凝绿。待玉漏穿花，银河垂地，月上栏干曲。'),
	(9114, 433, '清平乐（应制）', '黄金殿里。烛影双龙戏。劝得官家真个醉。进酒犹呼万岁。折旋舞彻伊州。君恩与整搔头。一夜御前宣住，六宫多少人愁。'),
	(9115, 156, '菩萨蛮', '楼头上有三冬鼓。何须抵死催人去。上马苦匆匆。琵琶曲未终。 回头肠断处。却更廉纤雨。漫道玉为堂。玉堂今夜长。'),
	(9116, 230, '谁学得', '饱则高歌醉即眠。只知头白不知年。江绕屋，水随船。买得风光不著钱。'),
	(9117, 101, '清平乐（次韵王武子寄远）', '离愁万斛。春思难拘束。瘦尽玉肌清彻骨。蹙损两眉秀绿。画屏罗幌输君。文鳞锦翼尤勤。长记酒醒香冷，笑将髻子隈人。'),
	(9118, 101, '如梦令', '谁伴明窗独坐。和我影儿两个。灯烬欲眠时，影也把人抛躲。无那。无那。好个恓惶的我。'),
	(9119, 101, '踏莎行', '万水千山，两头三绪。凭高望断迢迢路。钱塘江上客归迟，落花流水青春暮。步步金莲，朝朝琼树。目前都是伤心处。飞鸿过尽没书来，梦魂依旧阳台雨。'),
	(9120, 101, '小重山', '翡翠林梢青黛山。小楼新罨画，卷珠帘。碧纱窗外水挼蓝。凭栏处，相对玉纤纤。人散酒阑珊。夜长都睡皱，唾花衫。一弯残月下风檐。凌波去，罗袜步蹁跹。'),
	(9121, 101, '青玉案', '多情赋得相思分。便揽断、愁和闷。万种千般说不尽。吃他圈樻，被他拖逗，便佛也、须教恨。传消寄息无凭信。水远山遥怎生奔。梦也而今难得近。伊还知道，为伊成病，便死也、谁能问。'),
	(9122, 61, '鹊桥仙', '只应将巧畀人间，定却向、人间乞取。'),
	(9123, 598, '虞美人（赏梅月夜有怀）', '玉壶满插梅梢瘦。帘幕轻寒透。从今春恨满天涯。月下几枝疏影、透窗纱。锦城咫尺如千里。乍别难成寐。孤眠半晌断人肠。夜静分明全似、那人香。'),
	(9124, 383, '减字木兰花', '梅花似雪。刚被雪来相挫折。雪里梅花。无限精神总属他。梅花无语。只有东君来作主。传语东君。且与梅花作主人。'),
	(9125, 17, '醉蓬莱', '骤西风凄惨，秋昊平分，晚收清书。素月潜生，倚危墙时候。渐照芳樽，前中孤影，喜暂时为友。醉学吴儿，狂歌乱拍，蹁跹双袖。 堪叹从来，误了词赋，进取才能，桂枝难勾。纵得虚名，与平生相负。缰锁尘埃，愿怀圭组，强剑眉低首。平地神仙，清凉世界，君曾知否。'),
	(9126, 17, '诉衷情（建康）', '钟山影里看楼台。江烟晚翠开。六朝旧时明月，清夜满秦淮。寂寞处，两潮回。黯愁怀。汀花雨细，水树风闲，又是秋来。'),
	(9127, 17, '念奴娇', '水枫叶下，乍湖光清浅。凉生商素。西帝宸游罗翠盖，拥出三千宫女。绛彩娇春，铅华掩昼，占断鸳鸯浦。歌声摇曳，浣纱人在何处。别岸孤袅一枝，广寒宫殿，冷落栖愁苦。雪艳冰肌羞淡泊，偷把胭脂匀注。媚脸笼霞，芳心泣露，不肯为云雨。金波影里，为谁长恁凝伫。'),
	(9128, 178, '瑶池宴令', '飞花成阵。春心困。寸寸。别肠多少愁闷。无人问。偷啼自揾。残妆粉。抱瑶琴、寻出新韵。玉纤趁。南风未解幽愠。低云鬓。眉峰敛晕。娇和恨。'),
	(9129, 136, '蜡梅香', '晓日初长，正锦里轻阴，小寒天气。未报春消息，早瘦梅先发，浅苞纤蕊。揾玉匀香，天赋与、风流标致。问陇头人，音容万里。待凭谁寄。一样晓妆新，倚朱楼凝盼，素英如坠。映月临风处，度几声羌管，愁生乡思。电转光阴，须信道、飘零容易。且频欢赏，柔芳正好，满簪同醉。'),
	(9130, 697, '蝶恋花（月词）', '伏以合欢开宴，奉乐国之宾朋；对景摅怀，待良时之风月。此者偶屈三益，幸逢四并。六幕星稀，万棂风细。天发金精之含蓄，地扬银色之光华。远近万情，若知而莫诘；满虚一色，可揽以□将。是故无累而玩之者，喜乐之心生；不足而对之者，悲伤之态作。感群动以无意，涵长空而不流。对坐北堂，方入陆生之牖；共离南馆，便登韩子之台。顾歌三五之清辉，誓倒十千之芳酝。 忽破黄昏还太素。寒浸楼台，缥缈非烟雾。江上分明星汉路。金银闪闪神仙府。 影卧清光随我舞。邂逅三人，只愿长相聚。今月亭亭曾照古。古人问月今何处。'),
	(9131, 697, '宴琼林（上元）', '霜月和银灯，乍送目楼台，星汉高下。爱东风、已暖绮罗香，竞走去来车马。红莲万斛，开尽处、长安一夜。少年郎、两两桃花面，有馀光相借。因甚灵山在此，是何人、能运神化。对景便作神仙会，恐云轩且驾。思曾待、龙楼俯览，笑声远、洞天飞斝。向东来、尤幸时如故，群芳未开谢。'),
	(9132, 697, '水龙吟（方外述怀）', '五城中锁奇书，世间睡里无人唤。家家自有，月中丹桂，朱衣仙子。能驻光阴，解留颜鬓，引君霄汉。便西归、休梦华胥国□，约无限、烟霞伴。谁是采真高士，幻中寻取元非幻。时人不为，玉峰三秀，尘缘难断。莫说英雄，万端愁绪，夕阳孤馆。到流年过尽，韶华去了，起浮生叹。'),
	(9133, 697, '喜朝天（腊中雪后东湖闲宴）', '雪云浓。送愁思，衾寒更怯霜风。惹起离恨，为光阴恼，人意无穷。谁省年华屡换，渐作个、浮生玉髯翁。休易感，新醅泛蚁，且共时同。 相逢。笑语相契，况驾言游处，山里齐宫。寂寞时候，自有皓景，粉泽冬容。先顾丽人期约，痛赏倏、花开洛城红。三十日，回头过尽，喜对春工。'),
	(9134, 697, '永遇乐（冬日席上）', '天接重云，月临残腊，时有幽意。化作瑶池，纷纷戏蝶，一色非人世。无情征雁，乘风南向，怅望有情难寄。暖惊梅，先传芳信，夜来万宝春到。 中齐胜境，东藩和气，自有名园佳丽。一梦休嗟，三千好客，何处寻珠履。小堂人静，尊前清昼，好惜岁华如逝。管弦中，金杯更劝，朱颜皓齿。'),
	(9135, 697, '永遇乐（玩雪）', '朝霭藏晖，客袍惊暖，天巧无竟。杳杳谁知，包含造化，忽作人间瑞。儿童欢笑，忙来花下，便饮九春和气。急拿舟，高人乘兴，江天助我幽思。 缤纷似翦，峥嵘如画，莫道冬容憔悴。恍象含空，尘无一点，疑在天宫里。酒楼酣宴，茶轩清玩，旦待桂花来至。有馀光，明年待看，明红暗翠。'),
	(9136, 697, '渔家傲（斜月）', '已送清歌归去后。东南楼上人声悄。冷落尤临弦上调。欢意少。空将万感收残照。 窗外剑光初出鞘。斜窥梦断人年少。未到盖棺心未了。尘虑扰。双眸竟入扶桑晓。'),
	(9137, 697, '渔家傲（夏月）', '汗漫金华寒委地。火云散尽奇峰势。纨扇团圆休与比。犹可喜。恩情不怕凉飙至。梦冷魂高何处寄。琉璃砌上笼人睡。逃暑广寒宫似水。缘有累。乘风却下人间世。'),
	(9138, 154, '千秋岁', '春风湖外。红杏花初退。孤馆静，愁肠碎。泪馀痕在枕，别久香销带。新睡起。小园戏蝶飞成对。 惆怅人谁会。随处聊倾盖。情暂遣，心何在。锦书消息断，玉漏花阴改。迟日暮，仙山杳杳空云海。'),
	(9139, 290, '满江红', '荆楚风烟，寂寞近、中秋时候。露下冷、兰英将谢，苇花初秀。归燕殷勤辞巷陌，鸣蛩凄楚来窗牖。又谁念、江边有神仙，飘零久。横琴膝，携筇手。旷望眼，闲吟口。任纷纷万事，到头何有。君不见、凌烟冠剑客，何人气貌长依旧。归去来、一曲为君吟，为君寿。'),
	(9140, 434, '行香子', '金井先秋，梧叶飘黄。几回惊觉梦初长。雨微烟淡。疏雨池塘。渐蓼花明，菱花冷，藕花凉。 幽人已惯，枕单衾冷，任商飚、催换年光。问谁相伴，终日清狂。有竹间风，尊中酒，水边床。'),
	(9141, 157, '夜行船', '何处采菱归暮。隔宵烟、菱歌轻举。白苹风起月华寒，影朦胧、半和梅雨。脉脉相逢心似许。扶兰棹、黯然凝伫。遥指前村，隐隐烟树，含情背人归去。'),
	(9142, 682, '江城子（春恨）', '别郎容易见郎难。几何般。懒临鸾。憔悴容仪，陡觉缕衣宽。门外红梅将谢也，谁信道、不曾看。晓妆楼上望长安。怯轻寒。莫凭阑。嫌怕东风，吹恨上眉端。为报归期须及早，休误妾、一春闲。'),
	(9143, 682, '减字木兰花', '落花飞絮。杳杳天涯人甚处。欲寄相思。春尽衡阳雁渐稀。 离肠泪眼。肠断泪痕流不断。明月西楼。一曲阑干一倍愁。'),
	(9144, 682, '减字木兰花', '西楼明月。掩映梨花千树雪。楼上人归。愁听孤城一雁飞。 玉人何处。又见江南春色暮。芳信难寻。去后桃花流水深。'),
	(9145, 262, '江南好', '江南客，家有宁馨儿。三世文章称大手，一门兄弟独良眉。藉甚众多推。 千里足，来自渥洼池。莫倚善题鹦鹉赋，青山须待健时归。不似傲当时。'),
	(9146, 410, '潇湘忆故人慢', '薰风微动，方樱桃弄色，萱草成窠。翠帏敞轻罗。试冰簟初展，几尺湘波。疏帘广厦，寄潇洒、一枕南柯。引多少、梦中归绪，洞庭雨棹烟蓑。 惊回处，闲昼永，但时时，燕雏莺友相过。正绿影婆娑。况庭有幽花，池有新荷。青梅煮酒，幸随分、赢得高歌。功名事、到头终在，岁华忍负清和。'),
	(9147, 433, '减字木兰花', '才鸣□鼓。曲奏仙时如乐府。美似梨园。一派箫韶列玳筵。 使人清耳。满尘宾朋皆欢喜。劝饮金荷。祝寿延长福更多。'),
	(9148, 433, '减字木兰花', '三皇五帝。古代英雄闲争气。勇猛韩彭。十大功劳空有名。 休谈人我。大限催煎如何亸。前酌觥舡。祝寿如同海月圆。'),
	(9149, 433, '减字木兰花', '寿星明久。寿曲高歌沉醉后。寿烛荣煌。手把金炉爇寿香。 满斟寿酒。我意殷勤来祝寿。问寿如何。寿比南山福更多。'),
	(9150, 433, '减字木兰花（寿女婿）', '瑞云仙雾。拂晓重重遮绣户。一炷清香。千尺流霞入寿觞。 家门转好。从此应须长不老。来岁春风。看拜西枢小令公。'),
	(9151, 433, '生查子', '关山魂梦长，塞雁音书少。两鬓可怜青，一夜相思老。 归傍碧纱窗，说与人人道。真个别离难，不似相逢好。'),
	(9152, 433, '浪淘沙（杨梅）', '素手不晶盘。垒起仙丸。红绡剪碎却成团。逗得安排金粟遍，何似鸡冠。味胜玉浆寒。只被宜酸。莫将荔子一般看。色淡香消僝僽损，才到长安。'),
	(9153, 376, '开元乐', '楼上正临宫外，人间不见仙家。寒食轻烟薄雾，满城明月梨花。'),
	(9154, 376, '开元乐', '鹳鹊楼头日暖，蓬莱殿里花香。草绿烟迷步辇，天高日近龙床。'),
	(9155, 376, '开元乐', '殿后春旗簇仗，楼前御队穿花。一片红云闹处，外人遥认官家。'),
	(9156, 427, '平调发引', '上林春晚，曾是奉宸游。水殿戏龙舟。玉箫吹断催仙驭，一去隔千秋。 游人重到曲江头。事往涕难收。空馀御幄传觞处，依旧水东流。'),
	(9157, 491, '踏莎行（寄姊妹）', '孤馆深沈，晓寒天气。解鞍独自阑干倚。暗香浮动月黄昏，落梅风送沾衣袂。待写红笺，凭谁与寄。先教觅取嬉游地。到家正是早春时，小桃花下拚沉醉。'),
	(9158, 491, '临江仙（立春寄季顺妹）', '一夜东风穿绣户，融融暖应佳时。春来何处最先知。平明堤上柳，染遍郁金枝。姊妹嬉游时节近，今朝应怨来迟。凭谁说与到家期。玉钗头上胜，留待远人归。'),
	(9159, 520, '喜迁莺', '霜天清晓。望紫塞古垒，寒云衰草。汗马嘶风，边鸿翻月，垅上铁衣寒早。剑歌骑曲悲壮，尽道君恩难报。塞垣乐，尽双鞬锦带，山西年少。 谈笑。刁斗静。烽火一把，常送平安耗。圣主忧边，威灵遐布，骄虏且宽天讨。岁华向晚愁思，谁念玉关人老。太平也，且欢娱，不惜金尊频倒。'),
	(9160, 649, '望江南', '丹往右，四转运行阴。逢六闭藏阳户气，玉关泉透合丁壬。龟戏任浮沉。 时出入，无碍贯他心。游戏神通常出面，圆光周匝绕千寻。寒暑不相侵。'),
	(9161, 52, '梅花曲', '浅浅池塘。深深庭院，复出短短垣墙。年年为尔，若九真巡会、宝惜流芳。向人自有，绵渺无言，深意深藏。倾国倾城，天教与、抵死芳香。袅须金色，轻危欲压，绰约冠中央。蒂团红蜡，兰肌粉艳巧能妆。婵娟一种风流，如雪如冰衣霓裳。永日依倚，春风笑野棠。'),
	(9162, 672, '望江南', '维扬好，灵宇有琼花。千点真珠擎素蕊，一环明玉破香葩。芳艳信难加。 如雪貌，绰约最堪夸。疑是八仙乘皓月，羽衣摇曳上云车。来会列仙家。'),
	(9163, 672, '安阳好', '安阳好，形势魏西州。曼衍山川环故国，升平歌吹沸高楼。和气镇飞浮。 笼画陌，乔木几春秋。花外轩窗排远岫，竹间门巷带长流。风物更清幽。'),
	(9164, 284, '六州歌头（骊山）', '凄凉绣岭，宫殿倚山阿。明皇帝。曾游地。锁烟萝。郁嵯峨。忆昔真妃子。艳倾国，方姝丽。朝复暮。嫔嫱妒。宠偏颇。三尺玉泉新浴，莲羞吐、红浸秋波。听花奴，敲羯鼓，酣奏鸣龟。体不胜罗。舞婆娑。正霓裳曳。惊烽燧。千万骑。拥雕戈。情宛转。魂空乱。蹙双蛾。奈兵何。痛惜三春暮，委妖丽，马嵬坡。平寇乱。回宸辇。忍重过。香痉紫囊犹有，鸿都客、钿合应讹。使行人到此，千古只伤歌。事往愁多。'),
	(9165, 65, '六州歌头（项羽朝）', '秦亡草昧，刘项起吞并。驱龙虎。鞭寰宇。斩长鲸。扫欃枪。血染彭门战。视馀耳，皆鹰犬。平祸乱。归炎汉。势奔倾。兵散月明。风急旌旗乱，刁斗三更。命虞姬相对，泣听楚歌声。玉帐魂惊。 泪盈盈。恨花无主。凝愁绪。挥雪刃，掩泉局。时不利。骓不逝。困阴陵。叱追兵。暗鸣摧天地，望归路，忍偷生。功盖世。成闲纪。建遗灵。江静水寒烟冷，波纹细、古木凋零。遣行人到此，追念痛伤情。胜负难凭。'),
	(9166, 38, '迷仙引', '春阴霁。岸柳参差，袅袅金丝细。画阁昼眠莺唤起。烟光媚。燕燕双高，引愁人如醉。慵缓步，眉敛金铺倚。嘉景易失，懊恼韶光改。花空委。忍厌厌地。施朱粉，临鸾鉴，腻香销灭摧桃李。 独自个凝睇。暮云暗、遥山翠。天色无情，四远低垂淡如水。离恨托、征鸿寄。旋娇波、暗落相思泪。妆如洗。向高楼、日日春风里。悔凭兰、芳草人千里。'),
	(9167, 319, '望汉月', '黄菊一业临砌。颗颗露珠装缀。独教冷落向秋天，恨东君不曾留意。雕阑新雨霁。绿藓上，乱铺金蕊。此花开后更无花，愿爱惜、莫同桃李。'),
	(9168, 167, '蝶恋花', '四十年来身富贵。游处烟霞，步履如平地。紫府丹台仙籍里，皆知独擅无双美。 将相兼荣谁敢比。彩凤徊翔，重浴荀池水。位极人臣功济世，芬芳天下歌桃李。'),
	(9169, 131, '十二时', '承宝运，驯致隆平。鸿庆被寰瀛。时清俗阜，治定功成。遐迩咏由庚。严郊祀，文物声明。会天正、星拱奏严更。布羽仪簪缨。宸心虔洁，明德播惟馨。动苍冥。神隆享精诚。（和声） 燔柴半，万乘移天仗，肃銮辂旋衡。千官云拥，群后葵倾。玉帛旅明庭。韶沪荐，金奏谐声。集休亨。皇泽浃黎庶，普率洽恩荣。仰钦元后，睿圣贯三灵。万邦宁。景贶福千龄。'),
	(9170, 131, '六州', '严夜警，铜莲漏迟迟。清禁肃，森陛戟，羽卫俨皇闱。角声励，钲鼓攸宜。金管成雅奏，逐吹逶迤。荐苍璧，郊祀神祇。属景运纯禧。京坻丰衍，群材乐育，诸侯述职，盛德服蛮夷。（和声） 殊祥萃，九苞丹凤来仪。膏露降，和气洽，三秀焕灵芝。鸿猷播，史册相辉。张四维。卜世永固丕基。敷玄化，荡荡无为。合尧舜文思。混并寰宇，休牛归马，销金偃革，蹈咏庆昌期。'),
	(9171, 131, '导引（开宝元年南郊鼓吹歌曲三首）', '气和玉烛，睿化著鸿明。缇管一阳生。郊禋盛礼燔柴毕，旋轸凤凰城。森罗仪卫振华缨。载路溢欢声。皇图大业超前古，垂象泰阶平。（和声） 岁时丰衍，九土乐升平。睹环海澄清。道高尧舜垂衣治，日月并文明。嘉禾甘露登歌荐，云物焕祥经。兢兢惕惕持谦德，未许禅云亭。'),
	(9172, 74, '绛都春（清明）', '和风乍扇，又还是去年，清明重到。喜见燕子，巧说千般如人道。墙头陌上青梅小。是处有、闲花芳草。偶然思想，前欢醉赏，牡丹时候。 当此三春媚景，好连宵恣乐，情怀歌酒。纵有珠珍，难买红颜长年少。从他乌兔茫茫走。更莫待、花残莺老。恁时欢笑，休把万金换了。'),
	(9173, 74, '阮郎归', '寒阴漠漠夜来霜。阶庭风叶黄。归鸦数点带斜阳。谁家砧杵忙。 灯弄幌，月侵廊。熏笼添宝香。小屏低枕怯更长。知云入醉乡。'),
	(9174, 74, '浣溪沙（丁亥饯元宵）', '帘幕收灯断续红。歌台人散彩云空。夜寒归路噤鱼龙。 宿醉未消花市月，芳心已逐柳塘风。丁宁莺燕莫匆匆。'),
	(9175, 274, '瑞鹤仙（寿秦伯和侍郎）', '樱桃抄乳酪。正雨厌肥梅，风忺吹箨。咸瞻格天阁。见十眉环侍，争鸣弦索。茶瓯试沦。更良夜、沉沉细酌。问间生、此日为谁，曾向玉皇、案前持橐。 龟鹤。从他祝寿，未比当年，阴功堪托。天应不错。教公议，细评泊。自和戎以来，谋国多少，萧曹卫霍。奈胡儿自若，惟守绍兴旧约。'),
	(9176, 453, '锁窗寒', '雨洗红尘，云迷翠麓，小车难去。凄凉感慨，未有今年春暮。想曲江水边丽人，影沉香歇谁为主。但兔葵燕麦，风前摇荡，径花成土。 空被多情苦。庆会难逢，少年几许。纷纷沸鼎，负了青阳百五。待何时、重享太平，典衣贳洒相尔汝。算兰亭、有此欢娱，又却悲今古。'),
	(9177, 404, '点绛唇（清香莲）', '十里西湖，淡妆浓抹如西子。藕花簪水。清净香无比。 记得曾游，短棹红云里。聊相拟。一盆池水。十里西湖似。'),
	(9178, 404, '点绛唇（嘉香海棠）', '丝蕊垂垂，嫣然一笑新妆就。锦亭前后。燕子来时候。 谁恨无香，试把花枝嗅。风微透。细熏锦袖。不止嘉州有。'),
	(9179, 404, '点绛唇（韵香荼蘼）', '羽盖垂垂，玉英乱簇春光满。韵香清远。暖日烘庭院。 露浥琼枝，脸透何郎晕。凝余恨。古人不见。谁与花公论。'),
	(9180, 404, '点绛唇（天香桂）', '仙友苍苍，西风吹散天香好。暗飘龙脑。金粟枝头小。 谁种丹霄，造化玄功妙。真堪笑。学仙疏谬。有似西河老。'),
	(9181, 534, '临江仙', '不见都门亭畔柳，春来绿尽长条。柳边行色马萧萧。一枝折赠，相见又何朝。 酒尽曲终人去也，风前亦自无聊。祗应于我恨偏饶。东君特地，付与沈郎腰。'),
	(9182, 534, '临江仙', '不见随河堤上柳，绿阴流水依依。龙舟东下疾于飞。千条万叶，浓翠染旌旗。记得当年春去也，锦帆不见西归。故抛轻絮点人衣。如将亡国恨，说与路人知。'),
	(9183, 534, '临江仙（柳）', '不见昭阳宫内柳，黄金齐捻轻柔。东君昨夜到皇州。玉阶金井，无处不风流。 怅望翠华春欲暮，六宫都锁春愁。暖风吹动绣帘钩。飞花委地，时转玉香球。'),
	(9184, 684, '卜算子（夜泛镜湖）', '一叶鉴中来，两岸青山起。送我红蕖万柄香，疑在蓬壶里。 天地莹无尘，巾袂凉如水。白浪无声月自高，不是人间世。'),
	(9185, 580, '渔父词（其五）', '扁舟小缆荻花风。四合青山暮霭中。明细火，倚孤松。但愿尊中酒不空。'),
	(9186, 26, '临江仙', '在世为仙须有分，不须素食持齐。寸丝不着挂形骸。蓑衣为伴侣，箬笠作家怀。 行满三千上界，奉敕宣至金台。传言问汝有何哉。人生长富贵，阴骘种将来。'),
	(9187, 158, '少年游（葛氏侄女子告归，作少年游送之）', '雨晴云敛，烟花澹荡，遥山凝碧。驱车问征路，赏春风南陌。 正雨后、梨花幽艳白。悔匆匆、过了寒食。归家渐春暮，探酴醿消息。'),
	(9188, 158, '菩萨蛮', '栏干六曲天围碧。松风亭下梅初白。腊尽见春回。寒梢花又开。 曲琼闲不卷。沈燎看星转。凝伫小徘徊。云间征雁来。'),
	(9189, 121, '水调歌头（寿徐大参·九月二十六）', '九月二十六，公相纪生辰。橙黄橘绿时候、天气暖于春。奎画有堂辉焕，中着台星一点，长伴寿星明。衮衮有家庆，未羡古徐卿。 谢元枢，营绿野，避洪名。六年裁判止衮切国事、曾费几精神。歇了传岩霖雨，闲了孤舟野渡，旒冕合知心。吾道苟尊尚，元不在蒲轮。'),
	(9190, 602, '满庭芳', '落日旌旗，清霜剑戟，塞角声唤严更。论兵慷慨，齿颊带风生。坐拥貔貅十万，衔枚勇、云槊交横。笑谈顷，匈奴授首，千里静材枪 荆襄，人按堵，提壶劝酒，布谷催耕。芝夫荛子，歌舞威名。好是轻裘缓带，驱营阵、绝漠横行。功谁纪，风神宛转，麟阁画丹青。'),
	(9191, 581, '眼儿媚', '宸传三百旧京华。仁孝自名家。一旦奸邪，倾天拆地，忍听琵琶。 如今在外多萧索，迤逦近胡沙。家邦万里，伶仃父子，向晓霜花。'),
	(9192, 581, '西江月', '历代恢文偃武，四方晏粲无虞。奸臣招致北匈奴。边境年年侵侮。 一旦金汤失守，万邦不救銮舆。我今父子在穹庐。壮士忠臣何处。'),
	(9193, 263, '念奴娇（送淮漕钱处和）', '绣衣直指，问凌风一笑，翩然何许。诏山层霄持汉节，千里秋风淮浦。鉴远江山，竹西歌吹，曾被腥膻污。须君椽笔，为渠一洗尘土。 休厌共倒金荷，翠眉重为唱，渭城朝雨。看即扬鞭归骑稳，还指郁葱深处。宝带兼金，华鞯新绣，直上云霄去。回头莫忘，玉霄今夜风露。'),
	(9194, 646, '浪淘沙（云藏鹅湖山）', '台上凭阑干。犹怯春寒。被谁偷了最高山。将谓六丁移取去，不在人间。 却是晓寒间。特地遮拦。与天一样自漫漫。喜得东风收卷尽，依旧追还。 幽芳独秀在山林。不怕晓寒侵。应笑钱塘苏小，语娇终带吴音。 乘槎归去，云涛万顷，谁是知心。写向生绡屏上，萧然伴我寒衾。'),
	(9195, 270, '蝶恋花（醉宿郑氏阁）', '清晓方塘开一镜。落絮飞花，肯向春风定。点破翠奁人未醒。馀寒犹倚芭蕉劲。 拟托行云医酒病。帘卷闲愁，空占红香径。青鸟呼君君莫听。日边幽梦从来正。'),
	(9196, 43, '渔家傲（冬至）', '云覆衡茅霜雪后。风吹江面青罗皱。镜里功名愁里瘦。闲袖手。去年长至今年又。 梅逼玉肌春欲透。小槽新压冰澌溜。好把升沉分付酒。光阴骤。须臾又绿章台柳。'),
	(9197, 37, '满庭芳', '团菊苞金，丛兰减翠，画成秋暮风烟。使君归去，千里倍潸然。两度朱幡雁水，全胜得、陶侃当年。如何见，一时盛事，都在送行篇。 愁烦。梳洗濑，寻思陪宴，把月湖边。有多少、风流往事萦牵。闻道霓旌羽驾，看看是、玉局神仙。应相许，冲云破雾，一到洞中天。'),
	(9198, 617, '朝中措（改欧阳修词）', '屏山栏槛倚晴空。山色有无中。手种庭前桃李，别来几度春风。文章宰相，挥毫万字，一饮千钟。行乐不须年少，尊前看取仙翁。'),
	(9199, 18, '雨中花慢', '事往人离，还似暮峡归云，陇上流泉。奈向分罗带，已断么弦。长记歌时酒畔，难忘月夕花前。相携手处，琼楼珠户，触目依然。 从来惯共，锦衾屏枕，长效比翼文鸳。谁念我，而今清夜，常是孤眠。入户不如飞絮，傍怀争及炉烟。这回休也，一生心性，为你萦牵。'),
	(9200, 575, '燕归梁', '细柳营中有亚夫。华宴簇名姝。雅歌长许佐投壶。无一日、不欢娱。 汉王拓境思名将，捧飞诏欲登途。从前密约尽成虚。空赢得、泪流珠。'),
	(9201, 343, '最高楼（别妓王幼玉）', '人间最苦，最苦是分离。伊爱我，我怜伊。青草岸头人独立，画船东去橹声迟。楚天低，回望处，两依依。 后会也知俱有愿，未知何日是佳期。心下事、乱如丝。好天良夜还虚过，辜负我、两心知。愿伊家，衷肠在，一双飞。'),
	(9202, 23, '采桑子', '百花丛里花君子，取信东君。取信东君。名策花中第一勋。 结成宝鼎和羹味，多谢东君。多谢东君。香遍还应号令春。'),
	(9203, 324, '西江月', '晓镜初妆玉粉，轻风暗递幽香。间随月影到寒塘。忘却人间天上。 雪意空惊春意，孤芳已断年芳。从教驿使为伊忙。乞个寿阳宫样。'),
	(9204, 600, '鹧鸪天（蜡梅）', '不比江梅粉作华。天香肯作俗香夸。高悬蜡蓓蜂房密，遍挂金钟雁字斜。 侵月影，上窗纱。中央颜色自仙家。玉人插向乌云畔，浑似灵犀正透芽。'),
	(9205, 276, '凤凰台忆吹箫', '水国云乡，冰魂雪魄，朝来新领春还。便未怕、天暄峰蝶，笛转羌蛮。一树垂云似画，香暗暗、白浅红班。东风外，清新雪月，潇洒溪山。 应是飞琼弄玉，天不管、年年谪向人间。占芳事，铅华一洗，红叶俱残。多少烟愁雨恨，空脉脉、意远情闲。无人见，翠袖倚竹天寒。'),
	(9206, 165, '柳梢青', '脉脉春心，情人渐远，难托离愁。雨后寒轻，风前香软，春在梨花。 行人倚棹天涯。酒醒处、残阳乱鸦。门外秋千，墙头红粉，深院谁家。'),
	(9207, 521, '满庭芳（寓向仲德宿云轩几两月归南艳、道中寄）', '手染橙芗，杯摇烛影，夜阑人意醺酣。故人惜别，却酒更清谈。只合相从老矣，知何事、还着征衫。流年晚，长途匹马，奔走亦何堪。 江潭。摇落后，云嘘绝壁，雪舞空岩。念行人归鬓，今已毵毵。往事真成梦里，十年恨、江北江南。休凝伫，霜风卷地，寒日又西衔。'),
	(9208, 175, '浪淘沙（并序）', '幼卿少与表兄同研席，雅有文字之好。未笄，兄欲缔姻。父兄以兄未禄，难其请，遂适武弁。明年，兄登甲科，职教洮房，而良人统兵陕右，相与邂逅于此。兄鞭马略不相顾，岂前憾未平耶。因作浪淘沙以寄情云。 目送楚云空。前事无踪。谩留遗恨锁眉峰。自是荷花开较晚，孤负东风。 客馆叹飘蓬。聚散匆匆。扬鞭那忍骤花骢。望断斜阳人不见，满袖啼红。'),
	(9209, 528, '撒金钱', '频瞻礼。喜升平、又逢元宵佳致。鳌山高耸翠。对端门、珠玑交制。似嫦娥降仙宫，乍临凡世。 恩露匀施，凭御栏、圣颜垂视。撒金钱，乱抛坠。万姓推抢没理会。告官里。这失仪、且与免罪。'),
	(9210, 363, '五福降中天', '喜元宵三五，纵马御柳沟东。斜日映珠帘，瞥见芳容。秋水娇横俊眼，腻雪轻铺素胸。爱把菱花，笑匀粉面露春葱。 徘徊步懒，奈一点、灵犀未通。怅望七香车去，慢辗春风。云情雨态，愿暂入阳台梦中。路隔烟霞，甚时还许到蓬宫。'),
	(9211, 636, '蓦山溪（元夕）', '半生羁旅，岁度经元夜。长是竞虚名，把良宵、等闲叶舍。去年元夜，道得□身间，依旧是，客长安，寂寞孤眠者。 今年元夜。也则非乡社。却有人□约，携手□、灯前月下。那知风雨，此事又参差，成怨恨，独凄惶，清泪潸然洒。'),
	(9212, 636, '西江月', '风动一轩花竹，琅玕青锦薰笼。怜才自是宋墙东。更识琴心挑弄。 暮雨乍收寒浅，朝云又起春浓。冰肌玉骨信俱融。不比巫山何梦。'),
	(9213, 636, '秦刷子', '谁斫碧琅玕，影撼半庭风月。尚有岁寒心在，留得数根华发。 龙孙受戏碧波涛，喜动清风发。到得浪花深处，一瓯香雪。'),
	(9214, 311, '满庭霜', '白玉肌肤，清冰神彩，仙妃何事烟村。自然标韵，羞入百花群。不易盈盈瘦质，犯寒腊、独作春温。溪桥外，斜枝半吐，行客一销魂。 清香无处着，雪中暗认，月下空闻。算谁许幽人，相伴芳尊。莫放高楼弄笛，忍教看、雪落纷纷。堪调鼎，濛濛烟雨，滋养待和羹。'),
	(9215, 503, '江城子（贺人生子）', '长庚入梦夜何其。月波迟。露华滋。珠褓犀帷，生此宁馨见晋王衍传。天上麒麟人不识，森果砢，骏权奇。文章锦绣识新机。国风诗。楚人词。銮殿逢莱，早晚奉论思。百岁归来如魏武，从老大，好威仪。'),
	(9216, 320, '清平乐（闺情）', '露花烟柳。春思浓如酒。几阵狂风新雨后。满地落红铺绣。 风流何处疏狂。厌厌恨结柔肠。又是危阑独倚，一川烟草斜阳。'),
	(9217, 25, '宴清都', '细草沿阶软。迟日薄，蕙风轻蔼微暖。春工靳惜，桃红尚小，柳芽犹短。罗帏绣幕高卷。又早是、歌慵笑懒。凭画楼，那更天远，山远，水远，人远。 堪欢。传粉疏狂，窃香俊雅，无计拘管。青丝绊马，红巾寄泪，甚处迷恋。无言泪珠零乱。翠袖滴、重重渍遍。故要知、别后思量。归时觑见。'),
	(9218, 333, '婆罗门外', '帐云暮卷，漏声不到小帘栊。银汉夜洗晴空。皓月堂轩高挂，秋入广寒宫。正金波不动，桂影玲珑。佳人未逢。怅此夕、与谁同。对酒当歌，追念霜满愁红。南楼何处，愁人在、横笛一声中。凝望眼、立尽西风。'),
	(9219, 599, '念奴娇', '天然潇洒，尽人间、无物堪齐标格。只与姮娥为伴侣，方显一家颜色。好是多情，一年一度，首作东君客。竹篱茅舍，典刑别是清白。 惆怅玉杵无凭，蓝桥人去，空锁神仙宅。今日天涯凭马上，忽见轻盈冰魄。恰似当年，温柔乡里，晓看新妆额。临风三嗅，挽条不忍空摘。'),
	(9220, 502, '木兰花（雨中）', '花时人道多风雨。梅蕊都来无几许。何须飘洒湿芳心，粉面琳琅如泪注。 家童莫扫花阴土。留浥琼林枝上露。若教燕子早衔泥，径里馀香应满户。'),
	(9221, 523, '西江月', '凿破玄元三五，拨开造化圭璋。希夷妙旨在中央。咫尺无名罔象。 片饷工夫便得，教君地久天长。蓬莱仙岛是吾乡。怎不留心信向。'),
	(9222, 584, '踏青游', '竹外溪边，一枝破寒卫腊。莹素肌、玉雕冰刻。赋闲标，足馀韵，岂同常格。最风流，生来处处尽好，别得造化工力。 疏影幽香，意思迥然殊绝。算不枉、诗人分别。冻云深，凉月皎，愈增清冽。大潇洒，尤得静中雅趣，不许莺栖燕歇。'),
	(9223, 4, '快活年近拍', '千秋万岁君，五帝三王世。观风重令节，与民乐盛际。蕊宫长春，洞天不老，花艳蝉辉，十里照春珠翠。 闹罗绮。遥望太极，一族通明里。钧台奏寿，蓬山呈妙戏。六宫人来，五云楼迥，风送歌声，依约睿思圣制。'),
	(9224, 4, '忆少年（陇首山）', '陇云溶泄，陇山峻秀，陇泉呜咽。行人暂驻马，已不胜愁绝。 上陇首、凝眸天四阔。更一声、寒雁凄切。征书待寄远，有知心明月。'),
	(9225, 4, '昭君怨', '一望西山烟雨。目断心飞何处。天外白云城。几多程。 谩记阳关句。衣上粉啼痕污。陇水一分流。此生休。'),
	(9226, 4, '安平乐慢（都门池苑应制）', '瑞日初迟，绪风乍暖，千花百草争香。瑶池路稳，阆苑春深，云树水殿相望。柳曲沙平，看尘随青盖，絮惹红妆。卖酒绿阴傍。无人不醉春光。 有十里笙歌，万家罗绮，身世疑在仙乡。行乐知无禁，五侯半隐少年场。舞妙歌妍，空妒得、莺娇燕忙。念芳菲、都来几日，不堪风雨疏狂。'),
	(9227, 349, '菩萨蛮（春闺）', '碧窗纱透春寒极。极寒春透纱窗碧。谁送缕金衣。衣金缕送谁。 玉肌生嫩粟。粟嫩生肌玉。温处坐香茵。茵香坐处温。'),
	(9228, 191, '殢人娇', '多少胭脂，匀成点就。千枝乱、攒红堆绣。花无长好，更光阴去骤。对景忆良朋，故应招手。 曾记年时，花间把酒。枉淋浪、春衫湿透。文园今病，问远能来否。却道有，荼コ牡丹时候。'),
	(9229, 472, '如梦令', '日暮马嘶人去。船逐清波东注。后夜最高楼，还有思量人否。无绪。无绪。生怕黄昏疏雨。'),
	(9230, 203, '雨中花慢', '事往人离，还似暮峡归云，陇上流泉。奈强分圆镜，枉断哀弦。常记酒阑歌畔，难忘月底花前。旧携手处，层楼朱户，触目依然。 从来惯向，绣帏罗帐，镇效比翼纹鸳。谁念我、而今清夜，常是孤眠。入户不如飞絮，傍怀争及炉烟。这回休也，一生心事，为尔萦牵。'),
	(9231, 299, '早梅芳近', '残腊里，早梅芳。春信报新阳。晓来枝上斗寒光。轻点寿阳妆。 雪难欺，霜莫妒。别是一般风措。望林人意正夭饶。又看长新条。'),
	(9232, 497, '宝鼎现', '夕阳西下，暮霭红隘，香风罗绮。乘丽景、华灯争放，浓焰烧空连锦砌。睹皓月、浸严城如画，花影寒笼绛蕊。渐掩映、芙蓉万顷，迤逦齐开秋水。 太守无限行歌意。拥麾幢、光动珠翠。倾万井、歌台舞榭，瞻望朱轮骈鼓吹。控宝马、耀貔貅千骑。银烛交光数里。似乱簇、寒星万点，拥入蓬壶影里。 宴阁多才，环艳粉、瑶簪珠履。恐看看、丹诏催奉，宸游燕侍。便趁早、占通宵醉。缓引笙歌妓。任画角、吹老寒梅，月落西楼十二。'),
	(9233, 232, '西江月', '十指嫩抽春笋，纤纤玉软红柔。人前欲展强娇羞。微露云衣霓袖。 最好洞天春晚，黄庭卷罢清幽。凡心无计奈闲愁。试捻花枝频嗅。'),
	(9234, 232, '渔父词（丹霞）', '不怕石头行路滑。归来那爱驹儿踏。言下百骸俱拨撒。无剩法。灵然昼夜光通绕。 古寺天寒还恶发。夜将木佛齐烧杀。炙背横眠真快活。憨抹挞。从教院主无须发。'),
	(9235, 134, '诉衷情（旅愁）', '平生不会敛眉头。诸事等闲休。元来却到愁处，须著与他愁。 残照外，大江流。去悠悠。风悲兰杜，烟淡沧浪，何处扁舟。'),
	(9236, 590, '念奴娇', '旧游何处，记金汤形胜，蓬瀛佳丽。渌水芙蓉，元师与宾僚，风流济济。万柳庭边，雅歌堂上，醉倒春风里。十年一梦，觉来烟水千里。 惆怅送子重游，南楼依旧不，朱阑谁倚。要识当时，惟是有明月，曾陪珠履。量减杯中，雪添头上，甚矣吾衰矣。酒徒相问，为言憔悴如此。'),
	(9237, 436, '浣溪沙', '扇影轻摇一线香。斜红匀过晚来妆。娇多无事做凄凉。 借问谁教春易老，几时能勾夜何长。旧欢新恨总思量。'),
	(9238, 302, '摊破浣溪沙', '几度珠帘卷上钩。折花走马向扬州。老去不堪寻往事，上心头。 陶令无聊惟喜醉，茂陵多病不胜愁。脉脉春情长不断，水东流。'),
	(9239, 609, '思越人（集句）', '欲把长绳系日难。纷纷从此见花残。休将世事兼身事，须看人间比梦间。 红烛继，艳歌阑。等闲留客却成欢。劝君更尽一杯酒，赢得浮生半日闲。'),
	(9240, 66, '菩萨蛮（夏）', '润肌饶汗香红沁。沁红香汗饶肌润。低槛小山围。围山小槛低。 枕横钗坠鬓。鬓坠钗横枕。归梦与郎期。期郎与梦归。'),
	(9241, 286, '风流子', '东君虽不语，年华事、今岁恰如期。向寒雨望中，晓霜清处，领些春意，开两三枝。又不是、山桃红锦烂，溪柳绿摇丝。别是一般，孤高风韵，绛裁纤萼，冰剪芳蕤。 清香还有意，轻飘度勾引，几句新诗。须是放怀追赏，莫恁轻离。更嫦娥为爱，寒光满地，故移疏影，来伴南枝。谁道寿阳妆浅，偏入时宜。'),
	(9242, 652, '虞美人（题玉环玩书图）', '玉搔斜压乌云堕。拄颊看书卧。开元天子惜娉婷。一笑嫣然何事、便倾城。 马嵬风雨归时路。艳骨销黄土。多情谁写画图中。江水江花千古、恨无穷。'),
	(9243, 652, '水龙吟（寿白兰谷）', '此翁疑是香山，老来愈觉才情富。天孙借与，金刀玉尺，裁云缝雾。一曲阳春，樽前惟欠，柳蛮缨素。对苍松翠竹，江空岁晚，伴明月、倾芳醑。 深谷修兰楚楚。续离骚、载歌初度。麻姑素约，天寒相访，遗余琼露。拟借青鸾，吹笙碧落，采芝玄圃。奈玉堂催召，文园醉叟，草凌云赋。'),
	(9244, 58, '沁园春', '沁园春二阕，不能如公哨遍之变化，又局于韵字，不能效公用陶诗之精整，姑就本语，捃拾排比，粗以自遣云 壬戌之秋，七夕既望，苏子泛舟。正赤壁风清，举杯属客，东山月上，遗世乘流。桂棹叩舷，洞箫倚和，何事呜呜怨泣幽。悄危坐，抚苍苍东望，渺渺荆州。 客云天地蜉蝣。记千里舳舻旗帜浮。叹孟德周郎，英雄安在，武昌夏口，山水相缪。客亦知夫，盈虚如彼，山月江风有尽不。喜更酌，任东方既白，与子遨游。'),
	(9245, 58, '满江红', '仙人辞汉歌，自谓缚虎手，殊不佳。因改此调，虽不能如贺方回诸作，然稍觉平安。长日无所用心，非欲求加昔人也。 千里酸风，茂陵客、咸阳古道。宫门夜、马嘶无迹，东关云晓。牵上魏车将汉月，忆君清泪知多少。怅土花、三十六宫墙，秋风袅。 浥露兰，啼痕绕，画兰桂，雕香早。便天还知道，和天也老。独出携盘谁送客，刘郎陵上烟迷草。悄渭城、已远月荒凉，波声小。'),
	(9246, 202, '青玉案（闲居）', '万红梅里幽深处。甚杖屦、来何暮。草带湘香穿水树。尘留不住。云留却住。壶内藏今古。 独清懒入终南去。有忙事、修花谱。骑省不须重作赋。园中成趣。琴中得趣。酒醒听风雨。'),
	(9247, 202, '柳梢青（清明夜雪）', '一夜凝寒，忽成琼树，换却繁华。因甚春深，片红不到，绿水人家。 眼惊白昼天涯。空望断、尘香钿车。独立回风，不阑惆怅，莫是梨花。'),
	(9248, 202, '渔歌子', '画溪，作渔歌子十解，述古调也。 □卯湾头屋数间。放船收尽一溪山。聊适兴，且怡颜。问天难买是真闲。'),
	(9249, 202, '思佳客（题周草窗武林旧事）', '梦里瞢腾说梦华。莺莺燕燕已天涯。蕉中覆处应无鹿，汉上从来不见花。 今古事，古今嗟。西湖流水响琵琶。铜驼烟雨栖芳草，休向江南问故家。'),
	(9250, 202, '声声慢（中吴感旧）', '因风整帽，借柳维舟，休登故苑荒台。去岁何年，游处半入苍苔。白鸥旧盟未冷，但寒沙、空与愁堆。谩叹息，问西门洒泪，不忍徘徊。 眼底江山犹在，把冰弦弹断，苦忆颜回。一点归心，分付布袜青鞋。相寻已期到老，那知人、如此情怀。怅望久，海棠开、依旧燕来。'),
	(9251, 202, '好事近（赠笑倩）', '葱茜满身云，酒晕浅融香颊。水调数声娴雅，把芳心偷说。 风吹裙带下阶迟，惊散双蝴蝶。佯捻花枝微笑，溜晴波一瞥。'),
	(9252, 202, '珍珠令', '桃花扇底歌声杳。愁多少。便觉道花阴闲了。因甚不归来，甚归来不早。 满院飞花休要扫。待留与、薄情知道。怕一似飞花，和春都老。'),
	(9253, 202, '西江月（同前）', '落落奇花未吐，离离瑶草偏幽。蓬山元是不知秋。却笑人间春瘦。 潇酒寒犀尘尾，玲珑润玉搔头。半窗晴日水痕收。不怕杜鹃啼后。'),
	(9254, 202, '浪淘沙（题陈汝朝百鹭画卷）', '玉立水云乡。尔我相忘。披离寒羽庇风霜。不趁白鸥游海上，静看鱼忙。 应笑我凄凉。客路何长。犹将孤影侣斜阳。花底鹓行无认处，却对秋塘。'),
	(9255, 202, '风入松（酌惠山水）', '一瓢饮水曲肱眠。此乐不知年。今朝忽上龙峰顶。却元来、有此甘泉。洗却平生尘土，慵游万里山川。 照人如鉴止如渊。古窦暗涓涓。当时桑苎今何在，想松风、吹断茶烟。著我白云堆里，安知不是神仙。'),
	(9256, 202, '渔家傲', '辛苦移家聊处静。扫除花径歌声趁。也学维摩闲示病。迂疏甚。松风两耳和衣枕。 颇倦扶筇寻捷径。东墙蔼蔼红香盛。少待摇人波自定。蓬壶近。且呼白鹤招韩令。'),
	(9257, 202, '壶中天', '绕枝倦鹊，鬓萧萧、肯信如今犹客。风雪荷衣寒叶补，一点灯花悬壁。万里舟车，十年书剑，此意青天识。泛然身世，故家休问清白。 却笑醉倒衰翁，石床飞梦，不入槐安国。只恐溪山游未了，莫叹飘零南北。滚滚江横，呜呜歌罢，渺渺情何极。正无聊赖，天风吹下孤笛。'),
	(9258, 202, '浣溪沙', '半面妆凝镜里春。同心带舞掌中身。因沾弱水褪精神。 冷艳喜寻梅共笑，枯香羞与佩同纫。湘皋犹有未归人。'),
	(9259, 202, '长相思（赠别笑倩）', '去来心。短长亭。只隔中间一片云。不知何处寻。 闷还瞋。恨还颦。同是天涯流落人。此情烟水深。'),
	(9260, 202, '如梦令（渊明行径）', '苔径独行清昼。瑟瑟松风如旧。出岫本无心，迟种门前杨柳。回首。回首。篱下白衣来否。'),
	(9261, 202, '忆王孙（谢安棋墅）', '争棋赌墅意欣然。心似游丝扬碧天。只为当时一著玄。笑苻坚。百万军声屐齿前。'),
	(9262, 202, '风入松（赠蒋道录溪山堂）', '门前山可久长看。留住白云难。溪虚却与云相傍，对白云、何必深山。爽气潜生树石，晴光竟入阑干。 旧家三径竹千竿。苍雪拂衣寒。绿蓑青笠玄真子，钓风波、不是真闲。得似壶中日月，依然只在人间。'),
	(9263, 202, '蝶恋花（山茶）', '花占枝头忄欠日焙。金汞初抽，火鼎铅华退。还似瘢痕涂獭髓。胭脂淡抹微酣醉。 数朵折来春槛外。欲染清香，只许梅相对。不是临风珠蓓蕾。山童隔竹休敲碎。'),
	(9264, 202, '华胥引（钱舜举幅纸画牡丹、梨花。牡丹名洗妆红，为赋一曲，并题二花）', '温泉浴罢，酣酒才B153，洗妆犹湿。落暮云深，瑶台月下逢太白。素衣初染天香，对东风倾国。惆怅东阑，炯然玉树独立。 只恐江空，顿忘却、锦袍清逸。柳迷归院，欲远花妖未得。谁写一枝淡雅，傍沈香亭北。说与莺莺，怕人错认秋色。'),
	(9265, 202, '临江仙（太白挂巾手卷）', '忆得沈香歌断后，深宫客梦迢遥。研池残墨溅花妖。青山人独自，早不侣渔樵。 石壁苍寒巾尚挂，松风顶上飘飘。神仙那肯混尘嚣。诗魂元在此，空向水中招。'),
	(9266, 202, '惜红衣（赠伎双波）', '两剪秋痕，平分水影，炯然冰洁。未识新愁，眉心倩人贴。无端醉里，通一笑、柔花盈睫。痴绝。不解送情，倚银屏斜瞥。 长歌短舞，换羽移宫，飘飘步回雪。扶娇倚扇，欲把艳怀说。□□杜郎重到，只虑空江桃叶。但数峰犹在，如傍那家风月。'),
	(9267, 202, '台城路（迁居）', '桃花零落玄都观，刘郎此情谁语。鬓发萧疏，襟怀淡薄，空赋天涯羁旅。离情万缕。第一是难招，旧鸥今雨。锦瑟年华，梦中犹记艳游处。 依依心事最苦。片帆浑是月，独抱凄楚。屋破容秋，床空对雨，迷却青门瓜圃。初荷未暑。叹极目烟波，又歌南浦。燕忽归来，翠帘深几许。'),
	(9268, 202, '瑶台聚八仙（为焦云隐赋）', '春树江东。吟正远、清气竟入崆峒。问余栖处，只在缥缈山中。此去山中何所有，芰荷制了集芙蓉。且扶筇。倦游万里，独对青松。 行藏也须在我，笑晋人为菊，出岫方浓。淡然无心，古意且许谁同。飞符夜深润物，自呼起苍龙雨太空。舒还卷，看满楼依旧，霁日光风。'),
	(9269, 202, '如梦令（处梅列芍药于几上酌余，不觉醉酒，陶然有感）', '隐隐烟痕轻注。拂拂脂香微度。十二小红楼，人与玉箫何处。归去。归去。醉插一枝风露。'),
	(9270, 202, '满江红', '矣，作平声满江红赠之。 傅粉何郎，比玉树、琼枝谩夸。看生子、东涂西抹，笑语浮华。蝴蝶一生花里活，似花还却似非花。最可人、娇艳正芳年，如破瓜。 离别恨，生叹嗟。欢情事，起喧哗。听歌喉清润，片玉无瑕。洗尽人间笙笛耳，赏音多向五侯家。好思量、都在步莲中，裙翠遮。'),
	(9271, 202, '清平乐（赠处梅）', '暗香千树。结屋中间住。明月一方流水护。梦入梨云深处。 清冰隔断尘埃。无人踏碎苍苔。一似逋仙归后，吟诗不下山来。'),
	(9272, 202, '踏莎行', '柳未三眠，风才一讯。催人步屟吹笙径。可曾中酒似当时，如今却是看花病。 老愿春迟，愁嫌昼静。秋千院落寒犹剩。卷帘休问海棠开，相传燕子归来近。'),
	(9273, 202, '南楼令', '风雨怯殊乡。梧桐又小窗。甚秋声、今夜偏长。忆著旧时歌舞地，谁得似、牧之狂。 茉莉拥钗梁。云窝一枕香。醉瞢腾、多少思量。明月半床人睡觉，听说道、夜深凉。'),
	(9274, 202, '壶中天（养拙园夜饮）', '瘦筇访隐，正繁阴闲锁，一壶幽绿。乔木苍寒图画古，窈窕行人韦曲。鹤响天高，水流花净，笑语通华屋。虚堂松外，夜深凉气吹烛。 乐事杨柳楼心，瑶台月下，有生香堪掬。谁理商声帘外悄，萧瑟悬珰鸣玉。一笑难逢，四愁休赋，任我云边宿。倚兰歌罢，露萤飞上秋竹。'),
	(9275, 202, '国香（赋兰）', '空谷幽人。曳冰簪雾带，古色生春。结根未同萧艾，独抱孤贞。自分生涯淡薄，隐蓬蒿、甘老山林。风烟伴憔悴，冷落吴宫，草暗花深。 霁痕消蕙雪，向崖阴饮露，应是知心。所思何处，愁满楚水湘云。肯信遗芳千古，尚依依、泽畔行吟。香痕已成梦，短操谁弹，月冷瑶琴。'),
	(9276, 202, '四字令', '莺吟翠屏。帘吹絮云。东风也怕花瞋。带飞花赶春。 邻娃笑迎。嬉游趁晴。明朝何处相寻。那人家柳阴。'),
	(9277, 202, '渡江云（次赵元父韵）', '锦香缭绕地，深灯挂壁，帘影浪花斜。酒船归去后，转首河桥，那处认纹纱。重盟镜约，还记得、前度秦嘉。惟只有、叶题堪寄，流不到天涯。 惊嗟。十年心事，几曲阑干，想萧娘声价。闲过了、黄昏时候，疏柳啼鸦。浦潮夜涌平沙白，问断鸿、知落谁家。书又远，空江片月芦花。'),
	(9278, 202, '庆清朝', '竹，散怀吟眺，一任所适。太白去后三百年，无此乐也。 浅草犹霜。融泥未燕，晴梢润叶初干。闲扶短策，邻家小聚清欢。错认篱根是雪，梅花过了一番寒。风还峭，较迟芳信，恰是春残。 此境此时此意，待移琴独去，石冷慵弹。飘飘爽气，飞鸟相与俱还。醉里不知何处，好诗尽在夕阳山。山深杳，更无人到，流水花间。'),
	(9279, 202, '春从天上来（己亥春，复回西湖，饮静传董高士楼，作此解以写我忧。）', '海上回槎。认旧时鸥鹭，犹恋蒹葭。影散香消，水流云在，疏树十里寒沙。难问钱塘苏小，都不见、擘竹分茶。更堪嗟。似荻花江上，谁弄琵琶。 烟霞。自延晚照，尽换了西林，窈窕纹纱。蝴蝶飞来，不知是梦，犹疑春在邻家。一掬幽怀难写，春何处、春已天涯。减繁华。是山中杜宇，不是杨花。'),
	(9280, 202, '烛影摇红', '二三友行歌云舞绣中，亦清时之可乐，以词写之。 舟舣鸥波，访邻寻里愁都散。老来犹似柳风流，先露看花眼。闲把花枝试拣。笑盈盈，和香待翦。也应回首，紫曲门荒，当年游惯。 箫鼓黄昏，动人心处情无限。锦街不甚月明多，早已骄尘满。才过风柔夜暖。渐迤逦、芳程递趱。向西湖去，那里人家，依然莺燕。'),
	(9281, 202, '凄凉犯（过邻家见故园有感）', '西风暗翦荷衣碎，柔丝不解重缉。荒烟断浦，晴晖历乱，半江摇碧。悠悠望极。忍独听、秋声渐急。更怜他、萧条柳发，相与动秋色。 老态今如此，犹自留连，醉筇游屐。不堪瘦影，渺天涯、尽成行客。因甚忘归，谩吹裂、山阳夜笛。梦三十六陂流水去未得。'),
	(9282, 202, '长亭怨', '之北，遂作此曲 记横笛、玉关高处。万里沙寒，雪深无路。破却貂裘，远游归后与谁谱。故人何许。浑忘了、江南旧雨。不拟重逢，应笑我、飘零如羽。 同去。钓珊瑚海树。底事又成行旅。烟篷断浦。更几点、恋人飞絮。如今又、京洛寻春，定应被、薇花留住。且莫把孤愁，说与当时歌舞。'),
	(9283, 202, '玉漏迟（登无尽上人山楼）', '竹多尘自扫。幽通径曲，禅房深窈。空翠吹衣，坐对闲云舒啸。寒木犹悬故叶，又过了、一番残照。经院悄。诗梦正迷，独怜衰草。 幽趣尽属闲僧，浑未识人间，落花啼鸟。呼酒凭高，莫问四愁三笑。可惜秦山晋水，甚却向、此时登眺。清趣少。那更好游人老。'),
	(9284, 202, '法曲献仙音（席上听琵琶有感）', '云隐山晖，树分溪影，未放妆台帘卷。篝密笼香，镜圆窥粉，花深自然寒浅。正人在、银屏底，琵琶半遮面。 语声软。且休弹、玉关愁怨。怕唤起西湖，那时春感。杨柳古湾头，记小怜、隔水曾见。听到无声，谩赢得、情绪难翦。把一襟心事，散入落梅千点。'),
	(9285, 202, '湘月', '王中仙曳舟溪上。天空水寒，古意萧飒。中仙有词雅丽，平野作晋雪图，亦清逸可观。余述此调，盖白石念奴娇鬲指声也。 行行且止。把乾坤收入，篷窗深里。星散白鸥三四点，数笔横塘秋意。岸觜冲波，篱根受叶，野径通村市。疏风迎面，湿衣原是空翠。 堪叹敲雪门荒，争棋墅冷，苦竹鸣山鬼。纵使如今犹有晋，无复清游如此。落日沙黄，远天云淡，弄影芦花外。几时归去，翦取一半烟水。'),
	(9286, 202, '西子妆慢', '寓罗江，与罗景良野游江上。绿阴芳草，景况离离，因填此解。惜旧谱零落，不能倚声而歌也。 白浪摇天，青阴涨地，一片野怀幽意。杨花点点是春心，替风前、万花吹泪。遥岑寸碧。有谁识、朝来清气。自沈吟、甚流光轻掷，繁华如此。 斜阳外。隐约孤村，隔坞闲门闭。渔舟何似莫归来，想桃源、路通人世。危桥静倚。千年事、都消一醉。谩依依，愁落鹃声万里。'),
	(9287, 202, '梅子黄时雨（病后别罗江诸友）', '流水孤村，爱尘事顿消，来访深隐。向醉里谁扶，满身花影。鸥鹭相看如瘦，近来不是伤春病。嗟流景。竹外野桥，犹系烟艇。 谁引。斜川归兴。便啼鹃纵少，无奈时听。待棹击空明，鱼波千顷。弹到琵琶留不住，最愁人是黄昏近。江风紧。一行柳阴吹暝。'),
	(9288, 202, '还京乐（送陈行之归吴）', '醉吟处。多是琴尊，竟日松下语。有笔床茶灶，瘦筇相引，逢花须住。正翠阴迷路。年光荏苒成孤旅。等趁燕樯，休忘了、玄都前度。 渐烟波远，怕五湖凄冷，佳人袖薄，修竹依依日暮。知他甚处重逢，便匆匆、背潮归去。莫因循、误了幽期，应孤旧雨。伫立山风晚，月明摇碎江树。'),
	(9289, 202, '台城路（游北山寺别本作雪窦寺访同野翁日东岩）', '云多不记山深浅，人行半天岩壑。旷野飞声，虚空倒影，松挂危峰疑落。流泉喷薄。自窈窕寻源，引瓢孤酌。倦倚高寒，少年游事老方觉。 幽寻闲院邃阁。树凉僧坐夏，翻笑行乐。近竹惊秋，穿萝误晚，都把尘缘消却。东林似昨。待学取当年，晋人曾约。童子何知，故山空放鹤。'),
	(9290, 202, '疏影（余于辛卯岁北归，与西湖诸友夜酌，因有感于旧游，寄周草窗。）', '柳黄未结。放嫩晴消尽，断桥残雪。隔水人家，浑是花阴，曾醉好春时节。轻车几度新堤晓，想如今、燕莺犹说。纵艳游、得似当年，早是旧情都别。 重到翻疑梦醒，弄泉试照影，惊见华发。却笑归来，石老云荒，身世飘然一叶。闭门约住青山色，自容与、吟窗清绝。怕夜寒、吹到梅花，休卷半帘明月。'),
	(9291, 202, '三姝媚（送舒亦山游越）', '苍潭枯海树。正雪窦高寒，水声东去。古意萧闲，问结庐人远，白云谁侣。贺监犹狂，还散迹、千岩风露。抱瑟空游，都是凄凉，此愁难语。 莫趁江湖鸥鹭。怕太乙炉荒，暗消铅虎。投老心情，未归来何事，共成羁旅。布袜青鞋，休误入、桃源深处。待得重逢却说，巴山夜雨。'),
	(9292, 202, '琐窗寒', '绝响矣。余悼之玉笥山，所谓长歌之哀，过于痛哭。 断碧分山，空帘剩月，故人天外。香留酒殢。蝴蝶一生花里。想如今、醉魂未醒，夜台梦语秋声碎。自中仙去后，词笺赋笔，便无清致。 都是。凄凉意。怅玉笥埋云，锦袍归水。形容憔悴。料应也、孤吟山鬼。那知人、弹折素弦，黄金铸出相思泪。但柳枝、门掩枯阴，候蛩愁暗苇。'),
	(9293, 202, '三姝媚', '堂。见古瓶中数枝，云自海云来，名芙蓉杏。因爱玩不去，岩起索赋此曲。 芙蓉城伴侣。乍卸却单衣，茜罗重护。傍水开时，细看来、浑似阮郎前度。记得小楼，听一夜、江南春雨。梦醒箫声，流水青苹，旧游何许。 谁翦层芳深贮。便洗尽长安，半面尘土。绝似桃根，带笑痕来伴，柳枝娇舞。莫是孤村，试与问、酒家何处。曾醉梢头双果，园林未暑。'),
	(9294, 515, '秋夜雨', '髹车转急风如噎。冰丝松藕新雪。有人凉满袖，怕汗湿、红绡犹撧。 三更梦断敲荷雨，细听来、疏点还歇。茉莉标致别。占断了、纱厨香月。'),
	(9295, 515, '玉漏迟（寿东轩）', '客窗空翠杪。前生饮惯，长生琼醥。回首红尘，换了□花幰草。隔水神仙洞府，但只有、飞霞能到。谁信道。西风送我，还陪清啸。 缥渺。柳侧双楼，正绣幕围春，露深烟悄。鱼尾停时，雪上EF2F云犹少。醉傍芙蓉自语，愿来此、年年簪帽。青屿小。鹤立淡烟秋晓。'),
	(9296, 515, '摸鱼儿（寿东轩）', '亸吟鞭、雁峰高处。曾游长寿仙府。年年长见瑶簪会，霞杪盖芝轻度。开绣户。笑万朵香红，剩染秋光素。清箫丽鼓。任滟玉杯深，鸾酣凤醉，犹未洞天暮。 尘缘误。迷却桃源旧步。飞琼芳梦同赋。朝来闻道仙童宴，翘首翠房玄圃。云又雾。身恍到微茫，认得胎禽舞。遥汀近浦。便一苇渔航，撑烟载雨，归去半寒鹭。'),
	(9297, 515, '翠羽吟', '泛言仙，似乎寡味，越调之曲与梅花宜，罗浮梅花，真仙事也。演而成章，名翠羽吟。 绀露浓。映素空。楼观峭玲珑。粉冻霁英，冷光摇荡古青松。半规黄昏淡月，梅气山影溟濛。有丽人、步依修竹，萧然态若游龙。 绡袂微皱水溶溶。仙茎清瀣，净洗斜红。劝我浮香桂酒，环佩暗解，声飞芳霭中。弄春弱柳垂丝，慢按翠舞娇童。醉不知何处，惊翦翦、凄紧霜风。梦醒寻痕访踪。但留残星挂穹。梅花未老，翠羽双吟，一片晓峰。'),
	(9298, 515, '纷蝶儿（残春）', '啼鴂声中，春光化成春梦。问东君、仗谁诗送。燕怜晴，莺爱暖，一窗芳哄。奈匆匆、催他柳绵狂纵。 轻罗扇小，桐花又飞么凤。记寒吟、沁梅霜冻。古今□，人易老，莫闲双鞚。尚堪游、荼コ粉云香洞。'),
	(9299, 515, '浪淘沙（重九）', '明露浴疏桐。秋满帘栊。掩琴无语意忡忡。掐破东窗窥皓月，早上芙蓉。 前事渺茫中。烟水孤鸿。一尊重九又成空。不解吹愁吹帽落，恨杀西风。'),
	(9300, 515, '浪淘沙', '人爱晓妆鲜。我爱妆残。翠钗扶住欲欹鬟。印了夜香无事也，月上凉天。 新谱学筝难。愁涌蛾弯。一床衾浪未红翻。听得人催佯不睬，去洗珠钿。'),
	(9301, 515, '南乡子', '泊雁小汀洲。冷淡湔裙水漫秋。裙上唾花无觅处，重游。隔柳惟存月半钩。 准拟架层楼。望得伊家见始休。还怕粉云天末起，悠悠。化作相思一片愁。'),
	(9302, 515, '祝英台（次韵）', '柳边楼，花下馆。低卷绣帘半。帘外天丝，扰扰似情乱。知他蛾绿纤眉，鹅黄小袖，在何处、闲游闲玩。 最堪叹。筝面一寸尘深，玉柱网斜雁。谱字红蔫，翦烛记同看。几回传语东风，将愁吹去，怎奈向、东风不管。'),
	(9303, 515, '满江红', '一掬乡心，付杳杳、露莎烟苇。来相伴、凄然客影，谢他穷鬼。新绿旧红春又老，少玄老白人生几。况无情、世故荡摩中，凋英伟。 词场笔，行群蚁。战场胄，藏群虮。问何如清昼，倚藤凭棐。流水青山屋上下，束书壶酒船头尾。任垂涎、斗大印黄金，狂周顗。'),
	(9304, 515, '春夏两相期（寿谢令人）', '听深深、谢家庭馆。东风对语双燕。似说朝来，天上婺星光现。金裁花诰紫泥香，绣里藤舆红茵软。散蜡宫辉，行鳞厨品，至今人羡。 西湖万柳如线。料月仙当此，小停飙辇。付与长年，教见海心波浅。萦云玉佩五侯门，洗雪华桐三春苑。慢拍调莺，急鼓催鸾，翠阴生院。'),
	(9305, 515, '高阳台（闰元宵）', '桥尾星沈，街心尘敛，天公还把春饶。桂月黄昏，金丝柳换星摇。相逢小曲方嫌冷，便暖薰、珠络香飘。却怜他、隔岁芳期，枉费囊绡。 人情终似娥儿舞，到嚬翻宿粉，怎比初描。认得游踪，花骢不住嘶骄。梅梢一寸残红炬，喜尚堪、移照樱桃。醉醺醺，不记元宵，只道花朝。'),
	(9306, 515, '高阳台（送翠英）', '燕卷晴丝，蜂黏落絮，天教绾住闲愁。闲里清明，匆匆粉涩红羞。灯摇缥晕茸窗冷，语未阑、娥影分收。好伤情，春也难留，人也难留。 芳尘满目悠悠。问萦云佩响，还绕谁楼。别酒才斟，从前心事都休。飞莺纵有风吹转，奈旧家、苑已成秋。莫思量，杨柳湾西，且棹吟舟。'),
	(9307, 515, '瑞鹤仙（寿东轩立冬前一日）', '玉霜生穗也。渺洲云翠痕，雁绳低也。层帘四垂也。锦堂寒早近，开炉时也。香风递也。是东篱、花深处也。料此花、伴我仙翁，未肯放秋归也。 嬉也。缯波稳舫，镜月危楼，釂琼酡也。笼莺睡也。红妆旋、舞衣也。待纱灯客散，纱窗日上，便是严凝序也。换青毡、小帐围春，又还醉也。'),
	(9308, 515, '贺新郎（约友三月旦饮）', '雁屿晴岚薄。倚层屏、千树高低，粉纤红弱。云隘东风藏不尽，吹艳生香万壑。又散入、汀蘅洲药。扰扰匆匆尘土面，看歌莺、舞燕逢春乐。人共物，知谁错。 宝钗楼上围帘幕。小婵娟、双调弹筝，半霄鸾鹤。我辈中人无此分，琴思诗情当却。也胜似、愁横眉角。芳景三分才过二，便绿阴、门巷杨花落。沽斗酒，且同酌。'),
	(9309, 15, '早梅芳近', '碧溪湾，疏竹外，正小春天气。绿珠羞涩，半吐椒红可人意。月香传瘦影，露脸凝清泪。笑倡条冶叶，怕冷尚贪睡。 马行迟，雪未霁。还忆前村里。青禽啁哳，疑是当时梦初起。旧愁归塞管，远恨潇湘水。望江南，故人家万里。'),
	(9310, 15, '菩萨蛮', '瑶琴欲把相思谱。殷勤难写相思语。人在碧苕滨。相思烟水深。 鳞波流碎月。荏苒年芳歇。何处寄相思。白苹秋一枝。'),
	(9311, 15, '西江月', '漠漠河桥柳外，愔愔门巷镫初。笙歌饮散醉相扶。明月伴人归去。 娃馆深藏云木，女墙斜掠烟芜。水天空阔见西湖。鹤立夜寒多处。'),
	(9312, 15, '两同心', '踏青归后，小步西园。翠袖薄、新篁难倚，绿窗润、弱絮轻粘。春风急，暮雨凄然。早听啼鹃。 忆昔几度湖边。款曲花前。约俊客、同倾凿落，看游女、同上秋千。春无主，落日低烟。芳草年年。'),
	(9313, 15, '越山青', '四月时。五月时。柳絮无风不肯飞。卷帘看燕归。 雨凄凄。草凄凄。及早关门睡起迟。省人多少诗。'),
	(9314, 15, '八犯玉交枝（招宝山观月上）', '沧岛云连，绿瀛秋入，暮景欲沈洲屿。无浪无风天地白，听得潮生人语。擎空孤柱。翠倚高阁凭虚，中流苍碧迷烟雾。惟见广寒门外，青无重数。 遥想贝阙珠宫，琼林玉树。不知还是何处。倩谁问、凌波轻步。谩凝睇、乘鸾秦女。想庭曲、霓裳正舞。莫须长笛吹愁去。怕唤起鱼龙，三更喷作前山雨。'),
	(9315, 15, '桃园忆故人', '苎萝山下花藏路。只许流莺来去。吹落梨花无数。香雪迷官渡。 浣纱溪浅人何许。空对碧云凝暮。归去春愁如雾。奈五更风雨。'),
	(9316, 15, '生查子', '飞尘入建章，乐事难重见。白发故宫娃，闲说沈香宴。 霓裳秋梦寒，□及黏冰片。萤火起庭莎，犹忆轻罗扇。'),
	(9317, 15, '蝶恋花', '深院萧萧梧叶雨。知道秋来，不见秋来处。云压小桥人不渡。黄芦苦竹愁如雾。 四壁秋声谁更赋。人只留春，不解留秋住。秋又欲归天又暮。斜阳红影随鸦去。'),
	(9318, 699, '祝英台近（闺怨）', '彩云空，香雨霁。一梦千年事。碧幌如烟，却扇试新睡。恁时杨柳阑干，芙蓿池馆，还只似、如今天气。 远山翠。空相思，淡扫修眉，盈盈照秋水。落日西风，借问雁来未。只愁雁到来时，又无消息，只落得、一番憔悴。'),
	(9319, 699, '水龙吟·金陵雪后西望', '不知玄武湖中，一瓢春水何人借。裁冰翦雨，等闲占断，桃花春社。古阜花城，玉龙盐虎，夕阳图画。是东风吹就，明朝吹散，又还是、东风也。 回首当时光景，渺秦淮、绿波东下。滔滔江水，依依山色，悠悠物化。璧月琼花，世间消得，几多朝夜。笑乌衣、不管春寒，只管说、兴亡话。'),
	(9320, 574, '朝中措（饯梅分韵得疏字）', '冰肌玉骨为谁癯。只为故人疏。憔悴粉销香减，风流不似当初。 聚能几日，匆匆又散，骑鹤西湖。整整一年相别，到家传语林逋。'),
	(9321, 574, '水调歌头（寿梁多竹八十）', '百岁人能几，七十世间稀。何况先生八十，蔗境美如饴。好与七松处士，更与梅花君子，永结岁寒知。菊节先五日，满酌紫霞卮。 美成词，山谷字，老坡诗。三迳田园如昨，久矣赋归辞。不是商山四皓，便是香山九老，红颊白须眉。九十尚入相，绿竹颂猗猗。'),
	(9322, 574, '烛影摇红（县厅壁灯）', '月浸芙蕖，冰壶天地波凝碧。太平歌舞醉东风，花市人如织。桃李一城春色。玉梅娇、闹蛾无力。粉围红阵，灯火楼台，绮罗巷陌。 乐事还同，遨头引领神仙客。高烧银烛照红妆，香雾穿瑶席。款款檀牙细拍。醉金尊、东方未白。传柑相遗，探茧争先，明年今夕。'),
	(9323, 574, '齐天乐（簿厅壁灯）', '红纷绿闹东风透，暖得枳花香也。雪柳捻金，玉梅铺粉，妆点春光无价。鳌蓬如画。簇万顷芙蕖，桂华相射。艳冶逢迎，香尘满路飘兰麝。 人生行乐聊尔，况良辰美景，好天晴夜。茧帖争先，芋郎卜巧，细说成都旧话。传觞立马。看翠阵珠围，歌朋舞社。酒尽更阑，月在蒲萄架。时簿厅新作蒲萄架。'),
	(9324, 574, '华胥引（舟中万安用美成韵）', '沧浪矶外，小舣兰舟，旋沽竹叶。雨过溪肥，波心荡漾鸥地唼。烟晚欸乃渔歌，和栌声咿轧。要泛五湖，只恐西施羞怯。 年少飘零，鬓未霜、底须轻镊。江南归雁，寄来鸳笺细阅。盟言誓语，满鲛绡罗箧。撩弄相思，琴心寸寸三叠。'),
	(9325, 687, '望江南', '思晴好，松路翠光寒。夜夜竹窠常梦到，天天后土几时干。极目雾漫漫。'),
	(9326, 687, '望江南', '思晴好，我欲问花神。刚道社公瞋旧水，一回旧也一回新。不是两般春。'),
	(9327, 687, '潇湘神', '看龙舟。看龙舟。两堤未斗水悠悠。一片笙歌催闹晚，忽然鼓棹起中流。'),
	(9328, 687, '潇湘神', '斗轻桡。斗轻桡。雪中花卷棹声摇。天与玻璃三万顷，尽教看得几吴舠。'),
	(9329, 422, '摸鱼儿（莼）', '玉帘寒、翠痕微断，浮空清影零碎。碧芽也抱春洲怨，双卷小缄芳字。还又似。系罗带相思，几点青钿缀。吴中旧事。怅酪乳争奇，鲈鱼谩好，谁与共秋醉。 江湖兴，昨夜西风又起。年年轻误归计。如今不怕归无准，却怕故人千里。何况是。正落日垂虹，怎赋登临意。沧浪梦里。纵一舸重游，孤怀暗老，余恨渺烟水。'),
	(9330, 422, '八六子·扫芳林', '扫芳林。几番风雨，匆匆老尽春禽。渐薄润侵衣不断，嫩凉随扇初生。晚窗自吟。 沈沈。幽径芳寻。晻霭苔香帘净，萧疏竹影庭深。谩淡却蛾眉，晨妆慵扫，宝钗虫散，绣屏鸾破，当时暗水和云泛酒，空山留月听琴。料如今。门前数重翠阴。'),
	(9331, 422, '高阳台（陈君衡游未还，周公谨有怀人之赋，倚歌和之）', '驼褐轻装，狨鞯小队，冰河夜渡流澌。朔雪平沙，飞花乱拂蛾眉。琵琶已是凄凉调，更赋情、不比当时。想如今，人在龙庭，初劝金卮。 一枝芳信应难寄，向山边水际，独抱相思。江雁孤回，天涯人自归迟。归来依旧秦淮碧，问此愁、还有谁知。对东风，空似垂杨，零乱千丝。'),
	(9332, 422, '三姝媚（樱桃）', '红缨悬翠葆。渐金铃枝深，瑶阶花少。万颗燕支，赠旧情、争奈武珠人老。扇底清歌，还记得、樊姬娇小。几度相思，红豆都销，碧丝空袅。 芳意荼コ开早。正夜色瑛盘，素蟾低照。荐笋同时，叹故园春事，已无多了。赠满筠笼，偏暗触、天涯怀抱。谩想青衣初见，花阴梦好。'),
	(9333, 422, '三姝媚（次周公谨故京送别韵）', '兰缸花半绽。正西窗凄凄，断萤新雁。别久逢稀，谩相看华发，共成销黯。总是飘零，更休赋、梨花秋苑。何况如今，离思难禁，俊才都减。 今夜山高江浅。又月落帆空，酒醒人远。彩袖乌纱，解愁人、惟有断歌幽婉。一信东风，再约看、红腮青眼。只恐扁舟西去，苹花弄晚。'),
	(9334, 422, '绮罗香（红叶）', '玉杵余丹，金刀剩彩，重染吴江孤树。几点朱铅，几度怨啼秋暮。惊旧梦、绿鬓轻凋，诉新恨、绛唇微注。最堪怜，同拂新霜，绣蓉一镜晚妆妒。 千林摇落渐少，何事西风老色，争妍如许。二月残花，空误小车山路。重认取，流水荒沟，怕犹有、寄情芳语。但凄凉、秋苑斜阳，冷枝留醉舞。'),
	(9335, 422, '绮罗香（秋思）', '屋角疏星，庭阴暗水，犹记藏鸦新树。试折梨花，行入小阑深处。听粉片、簌簌飘阶，有人在、夜窗无语。料如今，门掩孤灯，画屏尘满断肠句。 佳期浑似流水，还见梧桐几叶，轻敲朱户。一片秋声，应做两边愁绪。江路远、归雁无凭，写绣笺、倩谁将去。谩无聊，犹掩芳樽，醉听深夜雨。'),
	(9336, 422, '疏影（咏梅影）', '琼妃卧月。任素裳瘦损，罗带重结。石径春寒，碧藓参差，相思曾步芳屧。离魂分破东风恨，又梦入、水孤云阔。算如今，也厌娉婷，带了一痕残雪。 犹记冰奁半掩，冷枝画未就，归棹轻折。几度黄昏，忽到窗前，重想故人初别。苍虬欲卷涟漪去，慢蜕却、连环香骨。早又是，翠荫蒙茸，不似一枝清绝。'),
	(9337, 422, '露华（碧桃）', '绀葩乍坼。笑烂漫娇红，不是春色。换了素妆，重把青螺轻拂。旧歌共渡烟江，却占玉奴标格。风霜峭、瑶台种时，付与仙骨。 闲门昼掩凄恻。似淡月梨花，重化清魄。尚带唾痕香凝，怎忍攀摘。嫩绿渐满溪阴，蔌蔌粉云飞出。芳艳冷、刘郎未应认得。'),
	(9338, 366, '玉楼春（赋双头牡丹）', '帝乡春色浓於雾。谁遣双环堆绣户。金张公子总封侯，姚魏弟兄皆列土。 碧纱窗下修花谱。交颈鸳鸯娇欲语。绛绡新结转官球，桃李仆奴何足数。'),
	(9339, 366, '玉楼春（度宗愍忌长春宫齐醮）', '咸淳十载聪明帝。不见宋家陵寝废。暂离绛阙九重天，飞过黄河千丈水。 长春宫殿仙璈沸。嘉会今辰为愍忌。小儒百拜酹霞觞，寡妇孤儿流血泪。'),
	(9340, 366, '忆秦娥', '如何说。人生自古多离别。多离别。年年辜负，海棠时节。 娇娇独坐成愁绝。胡笳吹落关山月。关山月。春来秋去，几番圆缺。'),
	(9341, 366, '忆秦娥', '水悠悠。长江望断无归舟。无归舟。欲携斗酒，怕上高楼。 当年出塞拥貂裘。更听马上弹箜篌。弹箜篌。万般哀怨，一种离愁。'),
	(9342, 366, '忆秦娥', '笑盈盈。晓妆扫出长眉青。长眉青。双开雉扇，六曲鸳屏。 少年心在尚多情。酒边银甲弹长筝。弹长筝。碧桃花下，醉到三更。'),
	(9343, 366, '失调名（宫人鼓瑟奏霓裳曲）', '绿荷初展。海榴花半吐，绣帘高卷。整顿朱弦，奏霓裳初遍，音清意远。恍然在广寒宫殿，窈窕柔情，绸缪细意，闲愁难剪。 曲中似哀似怨。似梧桐叶落，秋雨声颤。岂待闻铃，自泪珠如霰。春纤罢按，早心已笑慵歌懒。脉脉凭栏，槐阴转午，轻摇歌扇。'),
	(9344, 366, '莺啼序（宫中新进黄莺）', '檀栾宫墙数仞，敞朱帘绣户。正春暖、飘拂和风，衮入红尘香雾。见丝柳青青，袅娜如学宫腰舞。有黄莺、恰恰飞来，一梭金羽。 小巧身儿，锦心绣口，圆滑遽如计。避人，渐飞入琼林藏身，桃杏深处。对银屏、珠圆翠陈，隔叶底、恣歌《金缕》。忽群妃，拍手惊飞，奋然高举。 晓来雨湿，花娜柳垂，误投罗网去。缓缓访、六宫寻问，玉纤争握。放入金笼，眼娇眉妩。身如旅琐，无心求友。烟窗分影光阴里，听蛮声，似怨还如诉。 青山隔断，红妆满眼，谁怜一掬。幽恨难吐，沉香拂拂。亭北阑干，已得君王顾。但暗忆、西湖美景，雨色晴光，入翠穿红，巧转娇语。莺莺休怨天家，已赠金衣公子。生前号这恩荣，物类将何补。娇黄白奏词臣，为尔翻成，太平乐府。'),
	(9345, 366, '瑶花', '天中树木，高耸玲珑，向濯缨亭曲。繁枝缀玉。开朵朵九出。飞琼环簇。唐昌曾见，有玉女、来送春目，更月夜、八仙相聚。素质粲然幽独。 江淮倦客再游，访后土琼英，树已倾覆，攀条掐干，细嗅来、尚有微微清馥。却疑天上列燕赏，催汝归速。恐后时、重谪人间，剩把铅华妆束。'),
	(9346, 366, '汉宫春（春苑赏牡丹）', '玉砌雕栏。见吴宫西子，一笑嫣然。舞困人间半亸，艳粉争妍。珠帘尽卷，看人间、金屋神仙。歌队里，霞裾袅娜，百般娇态堪怜。 别有一枝仙种，更同山并蒂，来奉君筵。猩蜃若教解语，曲谱应传。柘黄独步，昼笼晴，锦幄张天。试剪插，金瓶千朵，醉时细看婵娟。'),
	(9347, 366, '忆王孙', '离宫别苑草萋萋。对此如何不泪垂。满槛山川漾落晖。昔人非。惟有年年秋雁飞。'),
	(9348, 366, '满江红（吴山）', '一霎浮云，都掩尽、日无光色。遥望处、浮图对峙，梵王新阙。燕子自飞关北外，杨花闲度楼西侧。慨金鞍、玉勒早朝人，经年歇。 昭君去，空愁绝。文姬去，难言说。想琵琶哀怨，泪流成血。蝴蝶梦中千种恨，杜鹃声里三更月。最无情、鸿雁自南飞，音书缺。'),
	(9349, 366, '长相思（越上寄雪江）', '吴山深。越山深。空谷佳人金玉音。有谁知此心。 夜沈沈。漏沈沈。闲却梅花一曲琴。月高松竹林。'),
	(9350, 366, '满江红（吴江秋夜）', '一个兰舟，双桂桨、顺流东去。但满目、银光万顷，凄其风露。渔火已归鸿雁汊，棹歌更在鸳鸯浦。渐夜深、芦叶冷飕飕，临平路。 吹铁笛，鸣金鼓。丝玉脍，倾香醑。且浩歌痛饮，藕花深处。秋水长天迷远望，晓风残月空凝伫。问人间、今夕是何年，清如许。'),
	(9351, 53, '贺新郎（催花呈赵云舍）', '办著春游费。奈狂风吹寒，禁定满城花事。天暝云深时度雨。院落秦筝未试。倩谁趱、杏娇桃媚。韶色三停今过一，只淡黄杨柳装愁思。芳径滑，绣窗闭。 玉炉闷炷香温被。忆去年、匆匆胜赏，梦沈烟水。遥望秋千新彩索，难把旧痕重系。待暖入、香红十里。别拥双鸾迎素月，教明年、不恨今焦卒。莺共燕，汝知未。'),
	(9352, 53, '意难忘（咸淳癸酉用清真韵）', '汀柳初黄。送流车出陌，别酒浮觞。乱山迷去路，空阁带余香。人渐远，意凄凉。更暮雨淋浪。悔不办，窄衫细马，两两交相。 春梁语燕犹双。叹晓窗新月，独照刘郎。寄笺频误约，临镜想慵妆。知几梦，恼愁肠。任更驻何妨。但只怜，绿阴匝匝，过了韶光。'),
	(9353, 369, '人月圆', '寻常一样窗前月，人只看中秋。年年今夜，争寻诗酒，共上高楼。 一奁明镜，能圆几度，白了人头。良辰美景，赏心乐事，输少年游。'),
	(9354, 369, '南乡子（初入都门漫赋）', '西北有神州。曾倚斜阳江上楼。目断淮南山一抹，何由。载泪东风洒汴流。 何事却狂游。直驾驴东渡白沟。自古幽燕为绝塞，休愁。未是穷荒天尽头。'),
	(9355, 517, '渔父词', '琉璃为地水精天。一叶渔舟浪满颠。风肃肃，露娟娟。家在芦花何处边。'),
	(9356, 517, '渔父词', '飘忽狂风一霎间。长鱼吹浪势如山。牢系缆，蓼花湾。白鸥沙上伴人闲。'),
	(9357, 517, '渔父词', '葭荻横披众木东。浪花如雪晚来风。云母幌，水晶宫。莲花一叶白头翁。'),
	(9358, 517, '渔父词', '江渚春风淡荡时。斜阳芳草鹧鸪飞。莼菜滑，白鱼肥。浮家泛宅不曾归。'),
	(9359, 517, '渔父词（十三首）', '万里长江一钓丝。萧萧蓬鬓任风吹。微雨过，片帆欹。青山浓淡更多奇。'),
	(9360, 407, '八声甘州（题维扬摘星楼）', '问苍天、苍天阒无言，浩歌摘星楼。这茫茫禹踪，南来第一，是古扬州。当日双龙未渡，风月一家秋。中分胡越后，横断江流。 □百年间春梦，笑槐柯蚁穴。多少王侯。谩平山堂里，棋局几边筹。是谁教；海干仙去，天地付浮沤。书生老，对琼花一笑，白发苍洲。'),
	(9361, 124, '踏莎行（与莫两山谭邗城旧事）', '远草情钟，孤花韵胜。一楼耸翠生秋暝。十年二十四桥春，转头明月箫声冷。 赋药才高，题琼语俊。蒸香压酒芙蓉顶。景留人去怕思量，桂窗风露秋眠醒。'),
	(9362, 124, '甘州（灯夕书寄二隐）', '渐萋萋、芳草绿江南，轻晖弄春容。记少年游处，箫声巷陌，灯影帘栊。月暖烘炉戏鼓，十里步香红。欹枕听新雨，往事朦胧。 还是江春梦晓，怕等闲愁见，雁影西东。喜故人好在，水驿寄诗筒。数芳程、渐催花信，送归帆、知第几番风。空吟想，梅花千树，人在其中。'),
	(9363, 124, '声声慢（柳花咏）', '燕泥沾粉，鱼浪吹香，芳堤十里晴。静惹游丝，花边袅袅扶春。多情最怜飘泊，记章台、曾绾青青。堪爱处，是扑帘娇软，随马轻盈。 长是河桥三月，做一番晴雪，恼乱诗魂。带雨沾衣，罗襟点点离痕。休缀潘郎鬓影，怕绿窗、年少人惊。卷春去，翦东风、千缕碎云。'),
	(9364, 124, '柳梢青', '万雪千霜。禁持不过，玉雪生光。水部情多，杜郎老去，空恼愁肠。 天寒野屿空廓。静倚竹、无人自香。一笑相逢，江南江北，竹屋山窗。'),
	(9365, 124, '柳梢青', '藏双清图，奇悟入神，绝去笔墨畦径。卷尾补之自书柳梢青四词，辞语清丽，翰札遒劲，欣然有契于心。余因戏云：不知点胸老、放鹤翁同生一时，其清风雅韵，优劣当何如哉。翁噱曰：我知画而已，安与许事，君其问诸水滨。因次韵载名于后，庶异时开卷索笑，不为生客云。 约略春痕。吹香新句，照影清尊。洗尽时妆，效颦西子，不负东昏。 金沙旧事休论。尽消得、东风返魂。一段真清，风前孤驿，雪后前村。'),
	(9366, 124, '清平乐（杜陵春游图）', '锦城春晓。苑陌芳菲早。可是杜陵人未老。日日酒迷花恼。 归鞯困倚芳酲。醒来还有新吟。人与杏花俱醉，春风一路闻莺。'),
	(9367, 124, '浣溪沙（题紫清道院）', '竹色苔香小院深。蒲团茶鼎掩山扃。松风吹净世间尘。 静养金芽文武火，时调玉轸短长清。石床闲卧看秋云。长清、短清，皆琴曲名。'),
	(9368, 124, '凤栖梧（赋生香亭）', '竹窈花深连别墅。曲曲回廓，小小闲庭宇。忽地香来无觅处。杖藜闲趁游蜂去。 老桂悬秋森玉树。涧底孤芳，苒苒吹诗句。一B744幽情知几许。钩帘半亩藤花雨。'),
	(9369, 124, '浪淘沙', '芳草碧茸茸。染恨无穷。一春心事雨声中。窄索宫罗寒尚峭，闲倚熏笼。 犹记粉阑东。同醉香丛。金鞍何处骤骅骢。袅袅绿窗残梦断，红杏东风。'),
	(9370, 124, '长亭怨慢', '斋、西安令翁浩堂、郡博士洪恕斋，一时名流星聚，见为奇事。倅居据龟阜，下瞰万室，外环四山，先子作堂曰啸咏。撮登览要，蜿蜒入后圃。梅清竹癯，亏蔽风月，后俯官河，相望一水，则小蓬莱在焉。老柳高荷，吹凉竟日。诸公载酒论文，清弹豪吹，笔研琴尊之乐，盖无虚日也。余时甚少，执杖屦，供洒扫，诸老绪论殷殷，金石声犹在耳。后十年过之，则径草池萍，怃然葵麦之感，一时交从，水逝云飞，无人识令威矣。徘徊水竹间，怅然久之，因谱白石自制调，以寄前度刘郎之怀云 记千竹、万荷深处。绿净池台，翠凉亭宇。醉墨题香，闲箫横玉尽吟趣。胜流星聚。知几诵、燕台句。零落碧云空，叹转眼、岁华如许。 凝伫。望涓涓一水，梦到隔花窗户。十年旧事，尽消得、庾郎愁赋。燕楼鹤表半飘零，算惟有、盟鸥堪语。谩倚遍河桥，一片凉云吹雨。'),
	(9371, 124, '一枝春', '事以寄意，此少游“小楼连苑”之词也。余遂戏用张氏故实次韵代答，亦东坡锦里先生之诗乎 帘影移阴，杏香寒、乍湿西园丝雨。芳期暗数。又是去年心绪。金花谩翦，倩谁画、旧时眉妩。空自想、杨柳风流，泪滴软绡红聚。 罗窗那回歌处。叹庭花倦舞，香消衣缕。楼空燕冷，碎锦懒寻尘谱。么弦谩赋，记曾是、倚娇成妒。深院悄，闲掩梨花，倩莺寄语。'),
	(9372, 124, '解语花', '思撩人，醉捻花枝，倚声成句 晴丝罥蝶，暖蜜酣蜂，重檐卷春寂寂。雨萼烟梢，压阑干、花雨染衣红湿。金鞍误约，空极目、天涯草色。阆苑玉箫人去后，惟有莺知得。 余寒犹掩翠户，梁燕乍归，芳信未端的。浅薄东风，莫因循、轻把杏钿狼藉。尘侵锦瑟。残日绿窗春梦窄。睡起折花无意绪，斜倚秋千立。'),
	(9373, 124, '夜行船', '蛩老无声深夜静。新霜粲、一帘灯影。妒梦鸿高，烘愁月浅，萦乱恨丝难整。 笙字娇娥谁为靓。香襟冷、怕看妆印。绣阁藏春，海棠偷暖，还似去年风景。'),
	(9374, 124, '瑶花慢', '上，间亦分致贵邸。余客辇下，有以一枝已下共缺十八行 朱钿宝玦。天上飞琼，比人间春别。江南江北，曾未见，谩拟梨云梅雪。淮山春晚，问谁识、芳心高洁。消几番、花落花开，老了玉关豪杰。 金壶翦送琼枝，看一骑红尘，香度瑶阙。韶华正好，应自喜、初识长安蜂蝶。杜郎老矣，想旧事、花须能说。记少年，一梦扬州，二十四桥明月。'),
	(9375, 124, '木兰花慢（南屏晚钟）', '疏钟敲暝色，正远树、绿愔愔，看渡水僧归，投林鸟聚，烟冷秋屏。孤云。渐沈雁影，尚残箫、倦鼓别游人。宫柳栖鸦未稳，露梢已挂疏星。 重城。禁鼓催更。罗袖怯、暮寒轻。想绮疏空掩，鸾绡翳锦，鱼钥收银。兰灯。伴人夜语，怕香消、漏永著温存。犹忆回廓待月，画兰倚遍桐阴。'),
	(9376, 71, '金缕曲（壬午五日）', '叶叶跳珠雨。里湖通、十里红香，画桡齐举。昨梦天风高黄鹄，下俯人间何许。但动地、潮声如鼓。竹阁楼台青青草。问木棉、羁客魂归否西湖里湖荷花最盛，贾似道建第葛岭，与竹阁为邻，里湖由是禁不往来。似道贬死漳州木棉庵。盘泣露，寺钟语。 梦回酷似灵均苦。叹神游、前度都非，明朝重五。满眼离骚无人赋，忘却君愁吊古。任醉里、乌乌缕缕。渺渺茂陵安期叟，共鄗池、夜别还于楚。采润绿，久延伫。广州蒲涧寺，安期生家也。始皇尝至其家，共语三日夜。今其地犹产菖蒲十二节，生服菖蒲得仙者。'),
	(9377, 71, '金缕曲（和同姓草叔曲本胡端逸见寿韵并谢）', '忘却来时路。恨苍苍、寒冰弃我，江南闲处。世事早知今如此，何不老农老圃。更种个、梅花深住。冻雨前朝浯溪石，对苍苔、堕泪怜臣甫。山似我，两眉聚。 岁云暮矣如何度。但多情、寂寥相念，二三君子。越石暮年扶风赋，犹解闻鸡起舞。恨不减、二三十岁。一曲相思碧云合，醉凭君、为我歌如缕。君念我，似同祖。'),
	(9378, 71, '水调歌头（陈平章即席赋）', '夜看二星度，高会列群英。苍颜白发乌帽，风人古槐清。客有羽衣来者，仍是寻常百姓，坐觉孟公惊。且勿多酌我，未厌我狂醒。 金瓯启，银信喜，衮衣新。向来淮浙草木，隐隐有余声。闻说并阑沙语，感念石壕村事，倾耳发惊霆。举酒奉公寿，天意厚苍生。'),
	(9379, 71, '水调歌头（自龙眠李氏夜过臞山康氏，走笔和其家灯障水调，迫暮始归）', '不成三五夜，不放霎时晴。长街灯火三两，到此眼方明。把似每时庭，传说个般障子，无路与君行。推手复却手，都付断肠声。|| 漏通晓，灯收市，人下棚。中山铁马何似，遗恨杳难平。一落掺挝声愤，再见大晟舞罢，乐事总伤情。便有尘随马，也任雨霖铃。'),
	(9380, 71, '水调歌头（谢和溪园来寿）', '夫子惠收我，谓我古心徒。闲居有客无酒，有酒又无鱼。报道犀兵远坠，问讯陈人何似，陈似隔年萸。天壤亦大矣，知有孔融乎。 白雪歌，丹元赞，赫蹄书。洪崖何自过我，便作授经图。教我天根骑月，规我扶摇去意，餐我白芝符。从此须溪里，更著赤松湖。'),
	(9381, 71, '水调歌头（寿晏云心）', '五五复五五，二八且重重。后天先甲如此，满月喜相逢。一日须倾三百，月小又输一日，不满九千钟。但愿客常满，莫问海尊空。 赋长篇，赓短韵，剩谈丛。乱来风日自美，一橘两衰翁。几见东陵瓜好，又看西邻葵烂，半醉半醒中。偶得洞宾象，混混起相从。'),
	(9382, 71, '法驾导引（寿刘倅仲简）', '五月五，五月五，细雨绿菖蒲。早是高花开九节，花堪结子节堪扶。持此揆□初。 长命缕，长命缕，儿女漫区区。何似屏星南极里，清如寒露在冰壶。一府号仙儒。'),
	(9383, 71, '法驾导引（寿城山，用寿胡潭东韵）', '臣尚少，臣尚少，少似此翁些。点半点斑今似雪，飞来飞去自如花。醉眼看红霞。 人间事，人间事，倒杖拄颐斜。冷冷清清冰下水，吞吞忍忍饭中砂。选到老人家。'),
	(9384, 71, '沁园春（闻歌）', '十八年间，黄公垆下，崔九堂前。叹人生何似，飘花陌上，妾身难托，卖镜桥边。隔幔云深，绕梁声彻，不负杨枝旧日传。主人好，但留F63C一石，空恼彭宣。 不因浩叹明年。也不为青衫怆四筵。念故人何在，旧游如梦，清风明月，野草荒田。俯仰无情，高歌有恨，四壁萧条久绝弦。秋江晚，但一声河满，我自潸然。'),
	(9385, 71, '沁园春（和槐城见寿）', '成佛生天，自是两途，任祖生先。看二三大老，依稀吾榜，几多新进，少小齐年。紫陌相逢，青山独往，倚杖鹤鸣听布泉。百年里，但儿时难得，老后依然。 吾牛已不耕田。更雨滑泥深自在鞭。叹十年波浪，悠悠何补，三生石上，种种无缘。白发来呵，朱颜去也，一曲狂歌落酒边。谁似我，似官奴出籍，散圣安禅。'),
	(9386, 71, '六州歌头', '后半月闻报，赋此。 向来人道，真个胜周公。燕然眇。浯溪小。万世功。再建隆。十五年宇宙，宫中赝。堂中伴。翻虎鼠，搏F37D雀，覆蛇龙。鹤发庞眉，憔悴空山久，来上东封。便一朝符瑞，四十万人同。说甚东风。怕西风都人窃议者称西头。 甚边尘起，渔阳惨。霓裳断。广寒宫。青楼杳都城籍妓皆隶歌舞，无敢犯。朱门悄。镜湖空。里湖通葛岭瞰里湖，无敢过。大纛高牙去，人不见，港重重。斜阳外，芳草碧，落花红。抛尽黄金无计，方知道、前此和戎。但千年传说，夜半一声铜。何面江东。'),
	(9387, 71, '永遇乐（余方痛海上元夕之习，邓中甫适和易安词至，遂以其事吊之。）', '灯舫华星，崖山矴口，官军围处。璧月辉圆，银花焰短，春事遽如许。麟洲清浅，鳌山流播，愁似汨罗夜雨。还知道，良辰美景，当时邺下仙侣。 而今无奈，元正元夕，把似月朝十五。小庙看灯，团街转鼓，总似添恻楚。传柑袖冷，吹藜漏尽，又见岁来去岁去。空犹记，弓弯一句，似虞兮语。'),
	(9388, 71, '虞美人（大红桃花）', '鞓红干色无光霁。须是鲜鲜翠。翛然一点系裙腰。不著人间金屋、恐难销。 英英肯似焉支贵。漫脱红霞帔。落时且勿涴尘泥。留向天台洞口、泛吾诗。'),
	(9389, 71, '虞美人', '花心定有何人捻。晕晕如娇靥。一痕明月老春宵。正似酥胸潮脸、不曾销。 当年掌上开元宝。半是杨妃爪。若教此掏到痴人。任是高墙无路、蝶翻身。||'),
	(9390, 71, '糖多令', '风露小瀛洲。斜河倒海流。人间尘、不到琼楼。错向五陵陵上望，几回月、几回秋。 落日太湖头。垂虹今是不。醉尊前、往往成愁。便有扁舟西子在，无汗漫、与君游。'),
	(9391, 71, '汉宫春（岁尽得巽吾寄溪南梅相忆韵）', '疏影横斜，似故人安道，只在前溪。年年望雪待月，漫倚吟矶。千红万紫，到春来、也是寻思。君不见，永阳江上，残梅冷两丝丝。 有几情人似我，漫骑牛卧笛，乱插繁枝。市门索笑憔悴，便作新知。城楼画角，又无花、只落空悲。但传说，寿阳一片，何曾迎面看飞。'),
	(9392, 71, '声声慢（九日泛湖游寿乐园赏菊，时海棠花开，即席命赋）', '西风坠绿。唤起春娇，嫣然困倚修竹。落帽人来，花艳乍惊郎目。相思尚带旧子，甚凄凉、未忄欠妆束。吟鬓底，伴寒香一朵，并簪黄菊。 却待金盘华屋。园林静、多情怎禁幽独。蛱蝶应愁，明日落红难触。那堪雁霜渐重，怕黄昏、欲睡未足。翠袖冷，且莫辞、花下秉烛。'),
	(9393, 71, '念奴娇（酬王城山）', '两丸日月，细看来、也是樊笼中物。点点山河经过了，拔帜几番残壁。白是沙堤，苍然吴楚，一片成毡雪。此时把酒，旧词还是坡杰。 歌罢公瑾当年，天长地久，柳与梅都发。几许闲愁斜照里，掌上沤生沤灭。沧海桑枯，东陵瓜远，总不关渠发。簪花起舞，可怜今夕无月。'),
	(9394, 71, '念奴娇', '吾年如此，更梦里、犹作狼居胥意。千首新诗千斛酒，管甚侯何齿。员峤波翻，瀛洲尘败，吾屐能销几。经丘寻壑，是他早计迟计。 犹记辰已嗟嗟，故人贺我，且勉呼君起。五十不来来过二，方悟人言都戏。以我情怀，借公篇韵，恨不天为纸。余生一笑，不须邴曼容例。'),
	(9395, 71, '青玉案（用辛稼轩元夕韵）', '雪销未尽残梅树。又风送、黄昏雨。长记小红楼畔路。杵歌串串，鼓声叠叠，预赏元宵舞。 天涯客鬓愁成缕。海上传柑梦中去。今夜上元何处度。乱山茅屋，寒炉败壁，渔火青荧处。'),
	(9396, 71, '青玉案', '之苦，自伐去也。归涂悄然念之，作此以示同行。 稠塘旧是花千树。曾泛入、溪深误。前度刘郎重唤渡。漫山寂寂，年时花下，往往无寻处。 一年一度相思苦。恨不抛人过江去。及至来时春未暮。兔葵燕麦，冷风斜雨，长恨稠塘路。'),
	(9397, 71, '临江仙（辛巳端午和陈简斋韵）', '旧日采莲羞半面，至今回首匆匆。梦穿斜日水云红。痴心犹独自，等待郑公风。 海上颓云潮不返，侧身空堕辽东。人间天上几时同。宫衣元不遇，无语醉醒中。'),
	(9398, 71, '临江仙（坐悟）', '我去就他甚易，他来认我良难。悟时到处是壶天。古诗寻一句，危坐看香烟。 金玉满堂不守，菁华岁月空迁。从今饱饭更安眠。丹经都不看，闲坐一千年。'),
	(9399, 71, '临江仙（端午）', '幸自不须端帖子，闲中一句如无。爱他午日午时书。惟应三五字，便是辟兵符。 久雨石鲸未没，小风纨扇相疏。邀朋一笑共菖蒲。去年初禁酒，今日漫提壶。||'),
	(9400, 71, '临江仙（有感）', '过雁天边信息，两宫池上心情。沈思海角愤难平。山风欺客梦，耿耿到天明。 幸自不争名利，闲愁夜夜如惊。明朝鬓白两三茎。世人都不念，似汝复何成。'),
	(9401, 71, '一翦梅（和人催雪）', '万事如花不可期。花不堪持。酒不堪持。江天雪意使人迷。翦一枝枝。歌一枝枝。 歌者不来今几时。姜影无词。张影无词。不歌不醉不成诗。歌也迟迟。雪也迟迟。'),
	(9402, 71, '乌夜啼（初夏）', '犹疑薰透帘栊。是东风。不分榴花更胜、一春红。 新雨过，绿连空。蝶飞慵。闲过绿阴深院、小花浓。'),
	(9403, 71, '山花子（春暮）', '东风解手即天涯。曲曲青山不可遮。如此苍茫君莫怪，是归家。 阊阖相迎悲最苦，英雄知道鬓先华。更欲徘徊春尚肯，已无花。'),
	(9404, 71, '减字木兰花（有感）', '东风似客。醉里落花南又北。客似东风。携手斜阳一笑中。 佳人怨我。不寄江南春一朵。我怨佳人。憔悴江南不似春。'),
	(9405, 71, '浣溪沙（春日即事）', '远远游蜂不记家。数行新柳自啼鸦。寻思旧事即天涯。 睡起有恨和画卷，燕归无语傍人斜。晚风吹落小瓶花。'),
	(9406, 71, '好事近（中齐惠念，赐词俾寿，不胜岁寒兄弟之意）', '□后百年闲，元度自伤来暮。打破虚空无碍，共乘龙飞去。 更参末后句如何，此事未能付。前遇小桥风雪，是君诗成处。'),
	(9407, 71, '菩萨蛮（秋兴）', '芭蕉叶上三更雨。人生只合随他去。便不到天涯。天涯也是家。 屏山三五叠。处处飞胡蝶。正是菊堪看。东篱独自寒。'),
	(9408, 71, '霜天晓角（寿萧静安，时归永新）', '归来把菊。春瓮今朝熟。苦苦留君不得，携孺子、到汾曲。 庐山真面目。冰清还映玉。长笑欧公老懒，君且住、饮螺绿。其子昏燕氏。'),
	(9409, 71, '霜天晓角（寿贾教）', '良宵七七。又近中元日。桥上老人有约，后五日、重来觅。 婵娟银海出。木犀新雨湿。惟有延平剑气，箕斗外、广寒逼。'),
	(9410, 71, '江城子（海棠花下烧烛词）', '红欹醉袖殢阑干。夜将阑。去难拚。烧蜜调蜂，重照锦团栾。春到洞房深处暖，方知道，月宫寒。 枝枝红泪不曾干。背人弹。语羞檀。欲睡心情，一似梦惊残。正自朦胧花下好，银烛里，几人看。'),
	(9411, 71, '如梦令', '落叶西风满地。独宿琼楼丹桂。孤影抱蟾寒，寄与月明千里。休寄。休寄。粟粟蕊珠心碎。折桂'),
	(9412, 71, '浪淘沙（有感）', '无谓两眉攒。风雨春寒。池塘小小水漫漫。只为柳花无一点，忘了临安。 何许牡丹残。客倚屏看。小楼面面是春山。日暮不知春去路，一带阑干。'),
	(9413, 71, '浪淘沙（秋夜感怀）', '无叶著秋声。凉鬓堪惊。满城明月半窗横。惟有老人心似醉，未晓偏醒。 起舞故无成。此恨难平。正襟危坐二三更。除却故人曹孟德，更与谁争。'),
	(9414, 71, '双调望江南（寿张粹翁）', '七日后，重会是星前。二月之间浑似此，余年何止万三千。日拟醉华筵。 歌白雪，除是雪儿传。看取长生瓢屡倒，眼前橘粟术何玄。自唱鹊桥仙。'),
	(9415, 71, '双调望江南（寿王秋水）', '齐眉举，彩侍紫霞卮。天上九朝凫冉冉，尊前一笑玉差差。人唱自家词。 篱下菊，醉把一枝枝。花水乞君三十斛，秋风记我一联诗。留看晚香时。'),
	(9416, 24, '喜迁莺', '留春不住。又早是清明，杨花飞絮。杜宇声声，黄昏庭院，那更半帘风雨。劝春且休归去。芳草天涯无路。悄无语。倚阑干立尽，落红无数。 谁诉。长门事，记得当年，曾趁梨园舞。霓羽香消，梁州声歇，昨梦转头今古。金屋玉楼何在，尚有花钿尘土。君不顾。怕伤心，休上危楼高处。'),
	(9417, 641, '虞美人', '夕阳楼上都凭遍。柳下风吹面。强搴罗袖倚重门。懒傍玉台鸾镜、暗尘昏。 离情脉脉如飞絮。此恨凭谁语。一天明月一并云。云外月明应照、凤楼人。'),
	(9418, 641, '迎春乐', '江湖十载疏狂迹。红尘里、倦游客。驻雕鞍、问柳东风陌。花底帽、低欹侧。 斗酒百篇呼太白。傲人世、醉中一息。何日归赋来，水之南、云之北。'),
	(9419, 641, '浣溪沙', '十二珠帘绣带垂。柳烟迷暗楚江涯。自携玉笛凭丹梯。 写恨鸾笺凝粉泪，踏青鸳袜溅金泥。落红深处乱莺啼。'),
	(9420, 641, '丁香结', '尘拥妆台，翠闲歌扇，金井碧梧风陨。听豆虫声小，伴寂寞、冷逼莓墙苍润。料凄凉宋玉，悲秋恨、此际怎忍。莲塘风露，渐入粉艳，红衣落尽。 勾引。记舞歇弓弯，几度柳围花阵。酒薄愁浓，霞腮泪渍，月眉香晕。空对秦镜尚缺，暗结回肠寸。念纤腰柔弱，都为相如瘦损。'),
	(9421, 641, '过秦楼', '倦听蛩砧，初抛纨扇，隔浦乱钟催晚。湘蒲簟冷，楚竹帘稀，窗下乍闻裁翦。倦柳梳烟，枯莲蘸水，芙蓉翠深红浅。对半床灯，虚堂凄寂，近书思遍。 夜漏永、玉宇尘收，银河光灿。梦断楚天空远。婆娑月树，缥缈仙香，身在广寒宫殿。无奈离愁乱织，藉酒销磨，倩花排遣。渐江空霜晓，黄芦漠漠，一声来雁。'),
	(9422, 641, '玲珑四犯', '金屋春深，似灼灼娉婷，真真娇艳。洗净铅华，依旧曲眉丰脸。犹记舞歇凉州，渐缥缈、碧云缭乱。自玉环、宝镜偷换。别后甚时重见。 鸾帏凤席鸳鸯荐。但空余、蕙芳兰蒨。天涯柳色青青恨，不入东风眼。惆账二十四桥，任落絮、飞花乱点。奈翠屏、一枕云雨梦，谁惊散。'),
	(9423, 641, '少年游', '翠罗裙解缕金丝。罗扇掩芳姿。柳色凝寒，花情DA3A雨，生怕踏青迟。 碧纱窗外莺声嫩，春在海棠枝。别后相思，许多憔悴，惟有落红知。'),
	(9424, 641, '蝶恋花', '落尽樱桃春去后。舞絮飞绵，扑簌穿帘牖。惜别情怀愁对酒。翠条折赠劳亲手。 绣幕深沈寒尚透。雨雨晴晴，妆点西湖秀。怅望章台愁转首。画阑十二东风旧。'),
	(9425, 641, '蝶恋花', '寂寞长亭人别后。一把垂丝，乱拂闲轩牖。三月春光浓似酒。传杯莫放纤纤手。 金缕依依红日透。舞彻东风，不减蛮腰秀。扑鬓杨花如白首。少年张绪心如旧。'),
	(9426, 641, '蝶恋花', '谢了梨花寒食后。翦翦轻寒，晓色侵书牖。寂寞幽齐惟酌酒。柔条恨结东风手。 浅黛娇黄春色透。薄雾轻烟，远映苏堤秀。目断章台愁举首。故人应似青青旧。'),
	(9427, 641, '渔家傲', '日转花梢春已昼。双蛾曲理遥山秀。百草偏输羞不斗。随人后。无情自折金丝柳。 秋水盈盈娇欲溜。六么倦舞弓弯袖。偷摘青梅推病酒。徘徊久。一双燕子归时候。'),
	(9428, 641, '月中行', '鬓云斜插映山红。春重淡香融。自携纨扇出帘栊。意欲扑飞虫。 蔷薇架下偏宜酒，纤纤手、自引金钟。倦歌佯醉倚东风。愁在落花中。'),
	(9429, 641, '玉楼春', '东风跃马长安道。一树樱桃花谢了。别来千里梦频归，醉里五更愁不到。 江南两岸无名草。雨遍莓苔生嫩葆。玉楼人远绿腰闲，帘幕深沈双燕老。'),
	(9430, 641, '满庭芳', '槐影连阴，竹光搏露，小荷新绿浮圆。簟纹如浪，绡帐碧笼烟。回合溪桥一曲，初雨过、流水溅溅。阑干外，沙鸥野鸟，飞过钓鱼船。 浮生，同幻境，眼空四海，迹寄三椽。但随天、休问我后谁前。要识渊明琴趣，真真意、都在无弦。薰风里，纶巾羽扇，一枕北窗眠。'),
	(9431, 641, '夜飞鹊', '秋江际天阔，风雨凄其。云阴未放晴晖。归鸦乱叶更萧索，砧声几处寒衣。沙头酒初熟，尽篱边朱槿，竹外青旗。潮期尚晚，怕轻离、故故迟迟。 何似醉中先别，容易为分襟，独抱琴归。回首征帆缥缈，津亭寂寞，衰草烟迷。虹收霁色，渐落霞孤鹜飞齐。更何时，重与论文渭北，翦烛窗西。'),
	(9432, 641, '侧犯', '晚凉倦浴，素妆薄试铅华靓。凝定。似一朵芙蓉泛清镜。轻纨笑自捻，扑蝶鸳鸯径。娇懒。金凤享单、记欹翠蝉影。 冰肌玉骨，衫体红绡莹。还暗省。记青青、双鬓旧潘令。梦想鸾筝，后堂深静。何日西风，碧梧金井。'),
	(9433, 641, '华胥引', '涵空斜照，掠水轻岚，满天红叶。雁泊平芜，凫依乱荻声唼唼。寂寞金井梧桐，渐辘轳伊轧。明月纱窗，夜寒孤枕应怯。 吟老西风，笑衰髯、顿疏如镊。锦笺勤重，频剔兰灯自阅。多谢征衫初寄，尚宝香熏箧。愁忆家山，梦魂飞度千垒。'),
	(9434, 641, '西湖明月引（寿云谷谢右司）', '朝回花底晓星明。瑞烟凝。暖风轻。修禊湔裙，时节又闻莺。绰约岸桃堤柳近，波万顷，碧琉璃，镜样平。 仙翁佩襟秋水清。渺莲舟，浮翠瀛。御楼香近，东风里、吹下青冥。鲛缬围红，春在牡丹屏。日正迟迟人正酒，画帘外，一声声，卖放生。'),
	(9435, 641, '柳梢青', '沁月凝霜。精神好处，曾悟花光。带雪煎茶，和冰酿酒，聊润枯肠。 看花小立疏廓。道是雪、如何恁香。几度巡檐，一枝清瘦，疑在蓬窗。'),
	(9436, 641, '霜天晓角', '玄霜绛雪。散作秋林缬。昨夜西风吹过，最好是、睡时节。 香绝。高处折。中秋还有月。此际人间天上，是两个、广寒阙。'),
	(9437, 641, '浣溪沙', '杨柳烟深五凤楼。绣帘风飏玉梭球。夜寒谁伴锦香篝。 残月有情圆晓梦，落花无语诉春愁。宝笙偷按小梁州。'),
	(9438, 641, '南歌子（茉莉）', '素质盈盈瘦，娇姿淡淡妆。曲勾阑畔倚秋娘。一撮风流都在、晚西凉。 彩线串层玉，金篝络细香。半钩新月浸牙床。犹记东华年少、那门相。'),
	(9439, 641, '思佳客', '锦幄沈沈宝篆残。惜春舞语凭阑干。庭前芳草空惆怅，帘外飞花自往还。 金屋静，玉箫闲。一尊芳酒驻红颜。东风落尽荼コ雪，满院清香夜不寒。'),
	(9440, 641, '江城子', '东风吹恨上眉弯。燕初还。杏花残。帘里春深，帘外雨声寒。拾翠芳期孤负却，空脉脉，倚阑干。 流苏香重玉连环。绕屏山。宝筝闲。泪薄鲛绡，零露湿红兰。瘦却舞腰浑可事，银蹀躞，半阑珊。'),
	(9441, 641, '八声甘州（西湖十咏·曲院风荷）', '放船杨柳下，听鸣蝉、薰风小新堤。正烟F950露蓼，飞尘酿玉，第五桥西。遥认青罗盖底，宫女夜游池。谁在鸳鸯浦，独棹玻璃。 一片天机云锦，见凌波碧翠，照日胭脂。是西湖西子，晴抹雨妆时。便相将无情秋思，向菰蒲深处红衣。醺醺里，半篙香梦，月转星移。'),
	(9442, 641, '扫花游（西湖十咏·雷峰落照）', '数峰蘸碧，记载酒甘园，柳塘花坞。最堪避暑。爱莲香送晚，翠娇红妩。欸乃菱歌乍起，兰桡竞举。日斜处。望孤鹜断霞，初下芳杜。 遥想山寺古。看倒影金轮，逆光朱户。暝烟带树。有投林鹭宿，凭楼僧语。可惜流年，付与朝钟暮鼓。漫凝伫。步长桥、月明归去。'),
	(9443, 641, '宝鼎见（云岩师书灯夕命赋）', '六鳌初驾，缥缈蓬阆，移来洲岛。还又是、梅飘冰泮，一夜青阳回海表。渐媚景、傍元宵时候，花底余寒料峭。更喜报、三边晏静，人乐清平宇宙。 画鼓簇队行春早。拥烟花、粉黛缭绕。开洞府、桃源路窈。戟外东风吹岸柳。正翠霭、映星桥月榭，十里红莲绽了。庆万家、珠帘半卷，绰约歌裙舞袖。 重锦绣幄围香，阖凤管鸾丝环奏。望非烟非雾，春在壶天易晓。早隐隐、半空星斗。看取收灯后。趁凤书、吹入黄扉，立马金门玉漏。'),
	(9444, 641, '酹江月', '霁空虹雨，傍啼螀莎草，宿鹭汀洲。隔岸人家砧杵急，微寒先到帘钩。步幄尘高，征衫酒润，谁暖玉香篝。风灯微暗，夜长频换更筹。 应是雁柱调筝，鸳梭织锦，付与两眉愁。不似尊前今夜月，几度同上南楼。红叶无情，黄花有恨，孤负十分秋。归心如醉，梦魂飞趁东流。'),
	(9445, 641, '疏影', '先生遗墨有感，借韵以赋 千峰翠玉。送孤云伴我，罗窗清宿。拂晓凭虚，春碧生寒，衣单瘦倚筇竹。东风不解吹愁醒，但芳草、溪城南北。认雾鬟，遥锁修颦，眉妩为谁愁独。 江上轻鸥似识，背昭亭两两，飞破晴渌。一片苍烟，隔断家山，梦绕石窗萝屋。相看不厌朝还暮，算几度、赤阑干曲。待倩诗、收拾归来，写作卧游屏幅。'),
	(9446, 641, '永遇乐（旧上声韵，今移入平声）', '玉腕笼寒，翠阑凭晓，莺调新簧。暗水穿苔，游丝度柳，人静芳昼长。云南归雁，楼西飞燕，去来惯认炎凉。王孙远，青青草色，几回望断柔肠。 蔷薇旧约，尊前一笑，等闲孤负年光。斗草庭空，抛梭架冷，帘外风絮香。伤春情绪，惜花时候，日斜尚未成妆。闻嬉笑，谁家女伴，又还采桑。'),
	(9447, 641, '倦寻芳', '杏檐转午。清漏沈沈，春梦无据。凤锦龟纱，空闭酒尘香雾。流水行云天四远，玉箫声断人何处。倦寻芳，镇情尖翠压，强拈飞絮。 记旧约、荼コ开后，屈指心期，数了还数。误我凭阑，几度片帆南浦。燕懒莺慵春去也，落花犹是东风主。正销凝，被愁鹃、又啼烟树。'),
	(9448, 641, '摸鱼儿（西湖送春）', '倚东风、画阑十二，芳阴帘幕低护。玉屏翠冷梨花瘦，寂寞小楼烟雨。肠断处。怅折柳柔情，旧别长亭路。年华似羽。任锦瑟声寒，琼箫梦远，羞对彩鸾舞。 文园赋。重忆河桥眉妩。啼痕犹溅纨素。丁香共结相思恨，空托绣罗金缕。春已暮。纵燕约莺盟，无计留春住。伤春倦旅。趁暗绿稀红，扁舟短棹，载酒送春去。'),
	(9449, 640, '沁园春', '取唐人“白衣闲亦贵，何必谒天阶”之句。友人池袭父邀予同赋，因作长短句遗之 禁鼓蓬蓬，忙杀公侯，穴城影中。正花前豪士，宿酲未解，松间逋客，清梦才浓。龙尾危机，犀围长物，何必飞书交子公。人间世，只闲之一字，受用无穷。 主人窗户玲珑。悟富贵荣华回首空。向竹梧侧畔，聚先秦录，兰荪里许，吟晚唐风。琴外鸿归，棋边鹭静，天把一丘荣此翁。孙刘辈，枉百年争战，一昧雌雄。'),
	(9450, 640, '沁园春', '何如尔。杜子美平生困踬不偶，而叹老羞卑之言少，爱君忧国之意多，可谓知所愁矣。若于著衣吃饭，一一未能忘情，此为不知命者。故用韵以反骚 我自无忧，何用攒眉，今忧古忧。叹风寒楚蜀，百年受病，江分南北，千载归尤。洛下铜驼，昭陵石马，物不自愁人替愁。兴亡事，向西风把剑，清泪双流。 边头。依旧防秋。问诸将君恩酬未酬。怅书生浪说，皇王帝霸，功名已属，韩岳张刘。不许请缨，犹堪草檄，谁肯种瓜归故邱。江中蜃，识平生许事，吐气成楼。'),
	(9451, 640, '沁园春', '诗不穷人，人道得诗，胜如得官。有山川草木，纵横纸上，虫鱼鸟兽，飞动毫端。水到渠成，风来帆速，廿四中书考不难。惟诗也，是乾坤清气，造物须慳。 金张许史浑闲。未必有功名久后看。算南朝将相，到今几姓，西湖名胜，只说孤山。象笏堆床，蝉冠满座，无此新诗传世间。杜陵老，向年时也自，井冻衣寒。'),
	(9452, 640, '沁园春（天问）', '我梦登天，尽把不平，问之化工。似桂花开日，秋高露冷，梅花开日，岁老霜浓。如此清标，依然香性，长在凄凉索寞中。何为者，只纷纷桃李，占断春风。 一时列鼎分封。岂猿臂将军无寸功。想世间成败，不关工拙，男儿济否，只系遭逢。天曰果然，事皆偶尔，凿井得铜奴得翁。君归去，但力行好事，休问穷通。'),
	(9453, 660, '念奴娇（端午酒边）', '雨帘高卷，见榴花、应怪风流人老。是则年年佳节在，无奈闲心悄悄。巧扇风轻，香罗雪湿，梦里曾看了。如今溪上，欢盟分付年少。 遍是眉好相宜，呼儿扶著，把菖蒲迎笑。说道浮生饶百岁，能有时光多少。幸自清贫，何妨乐趣，谱入瑶琴调。杯杯酒满，这般滋味谁晓。'),
	(9454, 660, '如梦令（舟泊咸池）', '晚泊江湾平处。楚楚苹花自舞。风翦雨丝轻，江上潮生船去。看取。看取。湿橹不惊鸥鹭。'),
	(9455, 660, '柳梢青（寿吴竹溪内）', '缥缈瑶城。客情春小，本分寒轻。霞佩云裾，步联西母，笑倚飞琼。 那堪四美都并。喜气与、祥云共生。自在风流，融融箫玉，楚楚兰馨。'),
	(9456, 660, '念奴娇（夏夜流萤照窗）', '暑天向晚，最相宜、一簇凉生新竹。潇洒轩窗还此景，此景真非凡俗。猿鹤相随，烟霞自在，与我交情熟。人生如梦，个中堪把心卜。 休叹乌兔如飞，功名富贵，有分终须足。不管他非非是是，不管他荣和辱。净几明窗，残编断简，且恁闲劳碌。流萤过去，文章如在吾目。'),
	(9457, 660, '瑞鹤仙（寿赵德修检讨必普）', '云无心出岫。游戏间、声名掀揭宇宙。红尘事看透。任高官惟有，鹤随诗瘦。溪山如绣。小轩亭、篘新话旧。想时时，梦到家林，但未有归时候。 知否。分明世界，多少经纶，莫轻回首。平生抱负。金銮殿，有新奏。便相扶君相，从头做去，他又谁能出手。看明年、此日传宣，赐酴醿酒。'),
	(9458, 660, '沁园春（寿陈菊坡枢密卓）', '吾菊坡兄，细观花□，元来一般。是鄮山佳气，胚月军长茂，鄞江润脉，滋养清妍。移傍云霄，浓沾雨露，曾亚百花头上班。谁知道，待芬香透了，收敛东边。 笑他红紫纷然。算眼底何曾长久看。这秋芳自韵，不争春艳，霜根难老，偏耐风寒。占得清名，尊为寿客，晚节谁能如此全。千千岁，把黄金正色，照映人间。'),
	(9459, 660, '浪淘沙（立春日卖春困）', '窗影弄晴红。欢笑成丛。一声春困到衰翁。回首太平儿戏事，雨过云空。人世暗尘中。如梦方浓。也须留取自惺忪。试问若教都困了，谁管春风。'),
	(9460, 660, '浪淘沙（与前人）', '有约泛溪篷。游画图中。沙鸥引入翠重重。认取抱琴人住处，水浅山浓。一笑两衰翁。莫惜从容。瓮醅灰芋雪泥菘。直到梅花飞过也，桃李春风。'),
	(9461, 660, '蝶恋花（次韵黄子羽重午）', '世变无情风挟雨。长夜漫漫，何日开晴午。白发萧疏惊岁序。儿嬉漫说重重午。 粒啄偷生如抟黍。过计何须，负郭多南亩。曾著宫衣沾雨露。如今掩袂悲湘浦。'),
	(9462, 660, '卖花声（立春酒边）', '残梦腾腾。好鸟一声呼醒。小窗明、萧萧鬓影。当年头上，惯曾簪幡胜。到如今、有谁怀省。 东风著面，却自依然相认。哄痴儿、堪声弄景。盘蔬杯酒，强教人欢领。也微酣、带些春兴。'),
	(9463, 660, '江城子（重午书怀）', '年年端午又今朝。鬓萧萧。思摇摇。应是南风，湘浦正波涛。千古独醒魂在否，无处问，有谁招。 何人帘幕倚兰皋。看飞桡。夺高标。饶把笙歌，供笑醉陶陶。孤坐小窗香一篆，弦绿绮，鼓离骚。'),
	(9464, 660, '烛影摇红（寿声仲）', '双杏堂深，山明水秀潆洄著。稳铺心事做平生，不买颦眉错。是则苍髯白发。笑微微、朱颜自渥。一团春意，半隐风流，他谁能学。 六十年华，又从今起新花甲。葵榴初艳芰荷香，争赴开筵约。家庆真堪恣乐。碧瑶杯、须拚满酌。瑟琴声里，弟劝兄酬，儿歌孙拍。'),
	(9465, 660, '金盏子（四时怀古秋词）', '眼底时光，奈老来、如何奈得秋何。黄叶最多情，天分付、凉意一声先做。是处著露莎蛩，也酸吟相和。新雁想，飞到故都徘徊，未忍轻过。 往事是堪唾。红枣信、烽折尽任他。湖山桂香自好。笙歌舫、沈沈醉也谁拖。可怜瘦月凄凉，把兴亡看破。如今□，但留下满城□，西风悲些。'),
	(9466, 660, '宝鼎现（四时怀古春词）', '问今何日，旧也曾尾，东风鹓鹭。回首念，家山桃李，归去来兮闻早赋。梦境里、尽何妨疏散，时趁莺晴信步。是则是、清闲自好，一点心犹怀古。 记得平世痴儿女。自灯宵、游了三五。还次第、湖边去也，寒食清明炊未住。是处处、是丹青图画，随意狂歌醉舞。奈蓦被、烟花浪手，一掷残阳孤注。 须信乐极悲来，谁道是、曾歌琼树。夕阳亭遗涴，翻得江涛似许。忍望著、□天津路。最是鹃啼苦。算世事、消把春看，还有落花飞絮。'),
	(9467, 660, '庆春泽', '春困时光，风流昨梦，逢花便自醒醒。回首宣和，宫莺掖燕相迎。归来只恋春山好，到上林、枉是亲曾。又谁知，自有蟠松，相与论盟。 阑干可是妨飞去，怕惊尘涴却，翠羽红翎。舞态亭亭，浑疑暗折韶声。堪人眼处还看破，道凤来、难与真争。醉扶归，但见啼鹃，怨夕阳亭。'),
	(9468, 660, '洞仙歌（次韵花蕊夫人）', '冰肌玉骨，自清凉无汗。云影蓬F229翠山远。颤金莲缓步，手托珠帘，风微透，扇底荷花香满。 归檐双飞燕，流盼消凝，微带羞红上娇面。又还黄昏近，一片闲情，天涯去、云也遮阑不断。回首处、沈思梦时曾，对十二屏山，怕拈秦阮。'),
	(9469, 660, '沁园春（寿吴竹溪）', '潇洒纶巾，风流野服，红尘外身。向南窗听雨，澜翻墨客，北亭恋月，笔走诗神。倚竹听琴，逢花倒榼，更得放晴游冠春。清闲好，算东洲人物，难得如君。 华堂瑞气如云。西风到帘帷才一分。喜星桥鹊语，佳传依旧，缑山鹤舞，仙样翻新。箫玉香中，烛花影里，听取捧觞低祝人。千千岁，看功名事业，都在儿孙。'),
	(9470, 346, '齐天乐', '凄凄杨柳潇潇雨，悄窗怎禁滴沥。思里传螀，愁边落雁，多少东吴山色。知他恨极。料为我窗前，强鸣刀尺。竟日西风，那堪无寐更邻笛。 黄花开遍未也，花开应笑我，年少难觅。灞上长安，河边渭水，都把韶华暗掷。何人碎璧。尚衰草连天，暮烟凝碧。怕说相思，撼枫喧夜寂。'),
	(9471, 317, '杏花天', '年时中酒风流病。正雨暗、コ芜深径。人家寒食烟初禁。狼藉梨花雪影。 西湖梦、红沈翠冷。记舞板、歌裙厮趁。斜阳苦与黄昏近。生怕书船归尽。'),
	(9472, 317, '浪淘沙', '宝押绣帘斜。莺燕谁家。银筝初试合琵琶。柳色春罗裁袖小，双戴桃花。芳草满天涯。流水韶华。晚风杨柳绿交加。闲倚阑干无藉在，数尽归鸦。'),
	(9473, 317, '扬州慢（琼花次韵）', '玉倚风轻，粉凝冰薄，土花祠冷无人。听吹箫月底，传暮草金城。笑红紫、纷纷成雨，溯空如蝶，恐堕珠尘。叹而今、杜郎还见，应赋悲春。 佩环何许，纵无情、莺燕犹惊。怅朱槛香消，绿屏梦渺，肠断瑶琼。九曲迷楼依旧，沈沈夜、想觅行云。但荒烟幽翠，东风吹作秋声。'),
	(9474, 317, '惜红衣（寄弁阳翁）', '笛送西泠，帆过杜曲。昼阴芳绿。门巷清风，还寻故人屋。苍华发冷，笑瘦影、相看如竹。幽谷。烟树晚莺，诉经年愁独。 残阳古木。书画归船，匆匆又南北。苹洲鸥鹭素熟。旧盟续。甚日浩歌招隐，听雨弁阳同宿。料重来时候，香荡几湾红玉。'),
	(9475, 298, '浣溪沙（题《草窗词》）', '玉雪庭心夜色空。移花小槛斗春红。轻衫短帽醉歌重。 彩扇旧题烟雨外，玉箫新谱燕莺中。阑干到处是春风。'),
	(9476, 298, '四字令', '兰汤晚凉。鸾钗半妆。红巾腻雪初香。擘莲房赌双。 罗纨素榼。冰壶露床。月移花影西厢。数流萤过墙。'),
	(9477, 112, '青玉案', '新腔一唱双金斗。正霜落、分甘手。已是红窗人倦绣。春词裁烛，夜香温被，怕减银壶漏。 吴天雁晓云飞后。百感情怀顿疏酒。彩扇何时翻翠袖。歌边拌取，醉魂和梦，化作梅边瘦。'),
	(9478, 112, '西江月（丙午冬至）', '添线绣床人倦，翻香罗幕烟斜。五更箫鼓贵人家。门外晓寒嘶马。 帽压半檐朝雪，镜开千靥春霞。小帘沽酒看梅花。梦到林逋山下。'),
	(9479, 112, '夜行船', '逗晓阑干沾露水。归期杳、画檐鹊喜。粉汗余香，伤秋中酒，月落桂花影里。 屏曲巫山和梦倚。行云重、梦飞不起。红叶中庭，缘尘斜□，应是宝筝慵理。'),
	(9480, 112, '夜行船（寓化度寺）', '鸦带斜阳归远树。无人听、数声钟暮。日与愁长，心灰香断，月冷竹房扃户。 画扇青山吴苑路。傍怀袖、梦飞不去。忆别西池，红绡盛泪，肠断粉莲啼路。'),
	(9481, 112, '清平乐（书栀子扇）', '柔柯翦翠。胡蝶双飞起。谁堕玉钿花径里。香带薰风临水。 露红滴□秋枝。金泥不染禅衣。结得同心成了，任教春去多时。'),
	(9482, 112, '瑶华（分韵得作字，戏虞宜兴）', '秋风采石，羽扇挥兵，认紫骝飞跃。江蓠塞草，应笑春、空锁凌烟高阁。胡歌秦陇，问铙鼓、新词谁作。有秀荪、来染吴香＿。瘦马青刍南陌。 冰澌细响长桥，荡波底蛟腥，不涴霜锷。乌丝醉墨，红袖暖、十里湖山行乐。老仙何处，算洞府、光阴如昨。想地宽、多种桃花，艳锦东风成幄。'),
	(9483, 112, '杏花天·咏汤', '蛮姜豆蔻相思味。算却在、春风舌底。江清爱与消残醉。悴憔文园病起。 停嘶骑、歌眉送意。记晓色、东城梦里。紫檀晕浅香波细。肠断垂杨小市。'),
	(9484, 112, '桃源忆故人·越山青断西陵浦', '越山青断西陵浦。一片密阴疏雨。潮带旧愁生暮。曾折垂杨处。 桃根桃叶当时渡。鸣咽风前柔橹。燕子不留春住。空寄离樯语。'),
	(9485, 112, '倦寻芳（饯周纠定夫）', '暮帆挂雨，冰岸飞梅，春思零乱。送客将归，偏是故宫离苑。醉酒曾同凉月舞，寻芳还隔红尘面。去难留，怅芙蓉路窄，绿杨天远。 便系马、莺边清晓，烟草晴花，沙润香软。烂锦年华，谁念故人游倦。寒食相思堤上路，行云应在孤山畔。寄新吟，莫空回、五湖春雁。'),
	(9486, 112, '喜迁莺（甲辰冬至寓越，儿辈尚留瓜泾萧寺）', '冬分人别。渡倦客晚潮，伤头俱雪。雁影秋空，蝶情春荡，几处路穷车绝。把酒共温寒夜，倚绣添慵时节。又底事，对愁云江国，离心还折。 吴越。重会面，点检旧吟，同看灯花结。儿女相思，年华轻送，邻户断箫声噎。待移杖藜雪后，犹怯蓬莱寒阔。最起晚，任鸦林催晓，梅窗沈月。'),
	(9487, 112, '惜秋华·木芙蓉', '路远仙城，自王郎去后，芳卿憔悴。锦段镜空，重铺步障新绮。凡花瘦不禁秋，幻腻玉、腴红鲜丽。相携。试新妆乍毕，交扶轻醉。 长记断桥外。骤玉骢过处，千娇凝睇。昨梦顿醒，依约旧时眉翠。愁边暮合碧云，倩唱入、六幺声里。风起。舞斜阳、阑干十二。'),
	(9488, 112, '秋思（夹钟商荷塘为括苍名姝求赋其听雨小阁）', '堆枕香鬟侧。骤夜声、偏称画屏秋色。风碎串珠，润侵歌板，愁压眉窄。动罗D64E清商，寸心低诉叙怨抑。映梦窗，零乱碧。待涨绿春深，落花香泛，料有断红流处，暗题相忆。 欢酌。檐花细滴。送故人、粉黛重饰。漏侵琼瑟。丁东敲断，弄晴月白。怕一曲、霓裳未终，催去骖凤翼。叹谢客、犹未识。漫瘦却东阳，灯前无梦到得。路隔重云雁北。'),
	(9489, 112, '水调歌头（赋魏方泉望湖楼）', '屋下半流水，屋上几青山。当心千顷明镜，入座玉光寒。云起南峰未雨，云敛北峰初霁，健笔写青天。俯瞰古城堞，不碍小阑干。 绣鞍马，软红路，乍回班。层梯影转亭午，信手展缃编。残照游船收尽，新月画帘才卷，人在翠壶间。天际笛声起，尘世夜漫漫。'),
	(9490, 112, '江南好', '问奇字，时斋示江南好词，纪前夕之事，辄次韵 行锦归来，画眉添妩，暗尘重拂雕栊。稳瓶泉暖，花隘斗春容。围密笼香C95C霭，烦纤手、亲点团龙。温柔处，垂杨ED51髻，□暗豆花红。 行藏，多是客，莺边话别，橘下相逢。算江湖幽梦，频绕残钟。好结梅兄B7AF弟，莫轻侣、西燕南鸿。偏宜醉，寒欺酒力，帘外冻云重。'),
	(9491, 112, '如梦令', '秋千争闹粉墙。闲看燕紫莺黄。啼到绿阴处，唤回浪子闲忙。春光。春光。正是拾翠寻芳。'),
	(9492, 112, '诉衷情·柳腰空舞翠裙烟', '柳腰空舞翠裙烟。尽日不成眠。花尘浪卷清昼，渐变晚阴天。 吴社水，系游船。又轻年。东风不管，燕子初来，一夜春寒。'),
	(9493, 112, '蝶恋花（题华山道女扇）', '北斗秋横云髻影。莺羽衣轻，腰减青丝剩。一曲游仙闻玉声。月华深院人初定。 十二阑干和笑凭。风露生寒，人在莲花顶。睡重不知残酒醒。红帘几度啼鸦暝。'),
	(9494, 112, '大酺（无射商荷塘小隐）', '峭石帆收，归期差，林沼年销红碧。渔蓑樵笠畔，买佳邻翻盖，浣花新宅。地凿桃阴，天澄藻镜，聊与渔郎分席。沧波耕不碎，似蓝田初种，翠烟生璧。料情属新莲，梦惊春草，断桥相识。 平生江海客，秀怀抱、云锦当秋织。任岁晚、陶篱菊暗，逋冢梅荒，总输玉井尝甘液。忍弃红香叶。集楚裳、西风催著。正明月、秋无极。归隐何处，门外垂杨天窄。放船五湖夜色。'),
	(9495, 112, '西河（中吕商陪鹤林登袁园）', '春乍霁。清涟画舫融泄。螺云万叠暗凝愁，黛蛾照水。漫将西子比西湖，溪边人更多丽。 步危径、攀艳蕊。掬霞到手红碎。青蛇细折小回廊，去天半咫。画阑日暮起东风，棋声吹下人世。海棠藉雨半绣地。正残寒、初御罗绮。除酒销春何计。向沙头更续，残阳一醉。双玉杯和流花洗。'),
	(9496, 112, '塞翁吟·饯梅津除郎赴阙', '有约西湖去，移棹晓折芙蓉。算才是，称心红。染不尽薰风。千桃过眼春如梦，还认锦叠云重。弄晚色，旧香中。旋撑入深丛。 从容。情犹赋、冰车健笔，人未老、南屏翠峰。转河影、浮槎信早，素妃叫、海月归来，太液池东。红衣卸了，结子成莲，天劲秋浓。'),
	(9497, 112, '法曲献仙音（放琴客，和宏庵韵）', '落叶霞翻，败窗风咽，暮色凄凉深院。瘦不关秋，泪缘轻别，情消鬓霜千点。怅翠冷搔头燕，那有语恩怨。 紫箫远。记桃根、向随春渡，愁未洗、铅水又将恨染。粉缟涩离箱，忍重拈、灯夜裁翦。望极蓝桥，彩云飞、罗扇歌断。料莺笼玉锁，梦里隔花时见。'),
	(9498, 112, '齐天乐·齐云楼', '凌朝一片阳台影，飞来太空不去。栋与参横，帘钩斗曲，西北城高几许。天声似语。便阊阖轻排，虹河平溯。问几阴晴，霸吴平地漫今古。 西山横黛瞰碧，眼明应不到，烟际沉鹭。卧笛长吟，层霾乍裂，寒月溟濛千里。凭虚醉舞。梦凝白阑干，化为飞雾。净洗青红，骤飞沧海雨。'),
	(9499, 112, '解语花·梅花', '门横皱碧，路入苍烟，春近江南岸。暮寒如剪。临溪影、一一半斜清浅。飞霙弄晚。荡千里、暗香平远。端正看、琼树三枝，总似兰昌见。 酥莹云容夜暖。伴兰翘清瘦，箫凤柔婉。冷云荒翠，幽栖久、无语暗申春怨。东风半面。料准拟、何郎词卷、欢未阑、烟雨青黄，宜昼阴庭馆。'),
	(9500, 283, '西江月', '小鹢载池心月，长虹夸水中天。主人情重客留连。便欲乘风寒殿。 霜竹且传秋信，镜蕖不作春妍。夜凉正好倒金船。朔饮而今再见。'),
	(9501, 283, '渔家傲', '重著夹罗犹怯冷。隔帘拜祝团圆镜。取片龙涎安古鼎。香阖静。横窗写出梅花影。 寒鹊颤枝飞不定。回纹刺就更筹永。小玉欣眠呼不醒。霜气紧。丽谯吹动梅花引。'),
	(9502, 283, '念奴娇（宝祐丁已闰四月，偕十友避暑白云寺）', '麦秋时候，薄阴罩炎日，山行乘兴。筇屐追随多胜侣，青佩黄冠方领。坐石谈玄，听泉濯暑，直上千山顶。倚风长啸，籁鸣林谷相应。 忽涌云气漫空，海吹急雨，觉冰D139微冷。洗尽人间名利障，便是蓬莱仙境。半日偷闲，一生清福，岂在荣钟鼎。青灯深夜，陶然独妙清圣。'),
	(9503, 283, '水调歌头（题斗南楼和刘朔斋韵）', '万顷黄湾口，千仞白云头。一亭收拾，便觉炎海豁清秋。潮候朝昏来去，山色雨晴浓淡，天末送双眸。绝域远烟外，高浪舞连艘。 风景别，胜滕阁，压黄楼。胡床老子，醉挥珠玉落南州。稳驾大鹏八极，叱起仙羊五石，飞佩过丹丘。一笑人间世，机动早惊鸥。'),
	(9504, 536, '山花子', '挼柳揉花旋染衣。丝丝红翠扑春辉。罗绮丛中无此艳，小西施。 腰细最便围舞帕，袖寒时复罩香B431。误点一痕残泪粉，怕人知。'),
	(9505, 536, '应天长', '溅紫飘红风又雨。一刻韶芳留不住。燕吞声，莺谇语。待得晴来人已去。 怯新歌，怜旧舞。冷落艳腔芳谱。要识此时情绪。豆梅酸更苦。'),
	(9506, 536, '夜行船', '一辔东风留不住。离歌断、日斜春暮。多事啼莺，妒情飞燕，一路送人归去。 文君自被琴心误。却惆怅、落花飞絮。锦字机寒，玉炉烟冷，门外乱山无数。'),
	(9507, 241, '江城子', '几年诗骨雪槎牙。痼烟霞。老生涯。五十八翁，堪喜亦堪嗟。忽忆香山居士语，还失笑，较争些。 荒寒梅坞月横斜。短篱遮。野人家。枝北枝南，须有两三花。紧闭竹门传语客，那得暇，尽由他。'),
	(9508, 241, '如梦令（海棠）', '雨洗海棠如雪。又是清明时节。燕子几时来，只了为花愁绝。愁绝。愁绝。枉与春风分说。'),
	(9509, 241, '满江红（和程学谕）', '苍石横筇，松风外、自调龟息。浑不记、东皋秋事，西湖春色。底处未嫌吾辈在，此心说与何人得。向海棠、烂醉过清明，酬佳节。 君莫道，江鲈忆。吾自爱，山泉激。尽月明夜半，杜鹃声急。人事略如春梦过，年光不啻惊弦发。怕醒来、失口问诸公，今何日。'),
	(9510, 241, '满江红（九日冶城楼）', '且问黄花，陶令后、几番重九。应解笑、秋崖人老，不堪诗酒。宇宙一舟吾倦矣，山河两戒天知否。倚西风、无奈剑花寒，虬龙吼。 江欲＿，谈天口。秋何负，持螯手。尽石麟芜没，断烟衰柳。故国山围青玉案，何人印佩黄金斗。倘只消、江左管夷吾，终须有。'),
	(9511, 241, '江城子', '几年诗骨雪槎牙。痼烟霞。老生涯。五十八翁，堪喜亦堪嗟。忽忆香山居士语，还失笑，较争些。 荒寒梅坞月横斜。短篱遮。野人家。枝北枝南，须有两三花。紧闭竹门传语客，那得暇，尽由他。'),
	(9512, 241, '最高楼（王寅生日）', '溪南北，本自一渔舟。烟雨几盟鸥。白鱼不负鸬鹚杓，青蓑不减肃鸟爽鸟裘。怎无端，贪射策，觅封侯。 既不似、古人能识字。又不似、今人能识事。空老去，自宜休。帝乡五十六朝暮，人间四十四春秋。问何如，茅一把，橘千头。'),
	(9513, 241, '花心动（和楚客忆梅）', '雪带边寒，渺愁予、雪中谁抱奇节。逊在扬州，逋老孤山，芳信顿成消歇。江南茅屋今安在，疏影瘦、祗堪叹息。归来未，沙头立尽，暮天云碧。 自笑梁园赋客。倚旧日鞍鞯，春风巾帻。问讯横枝，暖热新花，无处访寻诗阁。几年不见冰霜面，知谁共、批风支月。归来也，鸥盟不妨再结。'),
	(9514, 241, '酹江月（八月十四，小集郑子重帅参先月楼。是夕无月和朱希真插天翠柳词韵）', '绿尊翠勺，约秋风、一醉小楼先月。谁取宝奁奔帝所，深琐玉华宫阙。老桂香寒，疏桐云重，生怕金蛇掣。那知天柱，一峰别与天接。 我欲飞佩重游，置之衣袖，照我襟怀雪。玉斧难藏修月手，待做明宵清绝。天地无尘，山河有影，了不遗毫发。举杯相属，唤谁笺与天说。'),
	(9515, 241, '满江红（和程学谕）', '苍石横筇，松风外、自调龟息。浑不记、东皋秋事，西湖春色。底处未嫌吾辈在，此心说与何人得。向海棠、烂醉过清明，酬佳节。 君莫道，江鲈忆。吾自爱，山泉激。尽月明夜半，杜鹃声急。人事略如春梦过，年光不啻惊弦发。怕醒来、失口问诸公，今何日。'),
	(9516, 568, '望海潮（泛舟）', '轻云过雨，炎晖初减，楼台片片余霞。曲径通幽，小阑斜护，水天薄暮人家。暝色趣归鸦，竹风交立玉，清透窗纱。断岸涟漪，乱萍芳苇绕烟沙。 依稀画艇莲娃。掩鲛绡微沁，急桨咿哑。香雾霏微，冷光摇曳，娅红深映低花。天际玉钩斜。矶边菱唱答，惊断鸣蛙。满棹白苹归去，幽兴绕天涯。'),
	(9517, 303, '贺新郎（自和前韵）', '问讯南州守。怅吾生、今非昔比，后犹今否。涉尽风涛凭个甚，一瓣心香在袖。人竞说、顽哉此叟。识破荣途皆幻境，只形骸、已累它何有。姑勉尔，应之手。 休烦下太勤占候。怕漂零、江湖易老，光阴难又。兔魄初生人初度，期共婵娟长久。赖此月、于人犹厚。燕颔封侯非我事，早携书、归卧吾庐旧。渝此约，有如酒。'),
	(9518, 303, '沁园春（送乔宾王）', '二十年前，黄州竹楼，共酬好春。记淮＿江表，群贤毕集，清明上巳，二美相并。一枕黄粱，满头白发，屈指旧游能几人。堪嗟处，怅光阴易老，犹困西尘。 今朝又值良辰。空想像、长安天气新。问兰亭癸丑，雪堂壬戌，倏成畴昔，将似来今。觞泛流泉，茗烹新火，领略韶华聊啸吟。鹦洲去，有故人相问，为语归音。'),
	(9519, 303, '沁园春（乙卯初度和程都大韵）', '雪山有缘，白首重来，信不偶然。怅怆凄未洗，平戎何策，英灵不绝，赖蜀多贤。耆旧二三，甲兵百万，力障狂澜回巨川。秋声静，共巍楼把酒，自足筹边。 何人为我笺天。焉用此客星留井躔。正柴桑栗里，稻肥蟹健，松江笠泽，莼美鲈鲜。百计求闲，一归未得，便得归闲能几年。持公赋，待后堂新唱，夸语彭宣。'),
	(9520, 303, '浪淘沙（舟泊李家步）', '斜日挂汀洲。帆影悠悠。碧云合处是吴头。几片寒芦三两雁，人立清秋。 柳外莫停舟。休问闲愁。人生江海一萍浮。世路相期如此水，万里安流。'),
	(9521, 303, '兰陵王（甲寅初度和次贾韵）', '问梁益。天设金城铁壁。西风外，依约雁来，还报关山旧秋色。三秦听汉檄。远恨绵绵脉脉。频年事，虚掷桑阴，袆允诸人竟何策。 彤弓误殊锡。怅活国难医，救世须佛。平生本藉毛锥力。对弧矢初度，满头白发，何堪兵卫叠画戟。咄青史陈迹。 酒石。羡王绩。任击缶呼天，此乐何极。奚须太息惊前席。望天阍休待，梦如陶翼。柳边春后，放定远，出西域。'),
	(9522, 303, '醉蓬莱（癸丑寿吕马帅）', '问金城方略，数十年来，谁堪称许。万福威名，草木识淮浦。西顾天长，中流地重，著此巨鳌柱。见说棋边，风声鹤唳，胆落胡虏。 老子家声，六韬亲授，渭水归来，非熊非虎。江汉滔滔，建大将旗鼓。孤矢开祥，节旄迎渥，勋业纪盟府。好对芳天，莺花未老，金樽频举。'),
	(9523, 303, '八声甘州（用前韵答和史制参）', '续仲宣、一赋小呼鹰，声名满荆州。向宾筵游戏，毫端月露，皮里阳秋。遍历文书刁斗，何患不封侯。横槊风烟表，独占诗筹。 见说眉攒心事，在岷峨乡国，落日西头。怅英豪遗恨，都付大江流。伟平生、经纶雅志，把重弓、聊为汉关留。中秋近，何如载酒，一笑登楼。'),
	(9524, 303, '西江月（宜兴山间即事）', '不暖不寒天气，无思无虑山人。竹窗时听野禽鸣。更有松风成韵。 竟日蒲团打坐，有时藜仗闲行。呼童开酒荐杯羹。欲睡携书就枕。'),
	(9525, 303, '青玉案（癸未道间）', '栖鸦啼破烟林暝。把旅梦、俄惊醒。猛拍征鞍登小岭。峰回路转，月明人静，幻出清凉境。 马蹄踏碎琼瑶影。任露压巾纱未＿整。贪看前山云隐隐。翠微深处，有人家否，试击柴扃问。'),
	(9526, 303, '念奴娇（见郑文昌于上柏）', '平生宦海，是几番风雨，几番霜雪。绿野来归身强健，镜里微添华发。剑束床头，书寻架上，富贵轻于叶。南坡石竹，年来尤更清绝。 好是梅坞松关，对湘溪一曲，翠屏千叠。柱杖蓝舆诗卷里，尚小东山勋业。只恐鸥盟，难忘鹤怨，未是闲时节。片云收却，照人依旧明月。'),
	(9527, 303, '沁园春（中秋约僚佐观击圆，登怀远，用前韵）', '唤麹生来，与常娥约，从太守游。把玉箫声寄，萧关短笛，霓裳曲换，清塞重裘。桂影飘摇，桐阴立尽，多少征人霜满头。油幢暇，不掀髯一笑，辜负中秋。 斗杓矗处中州。还有解闻鸡起舞不。看鸣弦中鹄，穿杨电激，飞球戏马，策＿星流。绣帽归军，玳簪环客，薄晚同登庾亮楼。浮生事，是几番玩月，何苦多愁。'),
	(9528, 303, '沁园春（丙午登多景楼和吴履斋韵）', '天下奇观，江浮两山，地雄一州。对晴烟抹翠，怒涛翻雪，离离塞草，拍拍风舟。春去春来，潮生潮落，几度斜阳人倚楼。堪怜处，怅英雄白发，空敝貂裘。 淮头。虏尚虔刘。谁为把中原一战收。问只今人物，岂无安石，且容老子，还访浮丘。鸥鹭眠沙，渔樵唱晚，不管人间半点愁。危栏外，渺沧波无极，去去归休。'),
	(9529, 303, '沁园春（庚子登凤凰台，和壁间韵）', '漫浪江头，三听秋砧，一登故台。望烟芜莽苍，令人目断，风樯掀舞，何日眉开。把酒新亭，围棋别墅，老气当时何壮哉。江东事，百年无恙，全是时才。 纷然竞付轻埃。还水绕赏心东向淮。叹阿奴侪辈，因人碌碌，乃翁材略，余地恢恢。凤阙天高，鹭洲潮落，约取白鸥归去来。阑干外，英雄陈迹，一＿琼杯。'),
	(9530, 303, '水龙吟（己亥寿史督相）', '明堂一柱擎天，眼看黄＿空诸老。平生方寸，班班四字，诚心公道。玉帐云旗，金城露布，尚勤征讨。向淮头蜀口，一时做就，安石传、孔明表。 谈笑妖氛如扫。看整齐、乾坤都了。衮衣赤舄，归来廊庙，雍容师保。三相一门，双亲千岁，人间蓬岛。举黄封，细唱调羹，官梅上、正春早。'),
	(9531, 117, '水调歌头', '老子百般足，无事可闲忧。几年思返林壑，今日愿方酬。潦倒戏衫舞袖，郎讲门槌拍板，端的这回收。日月两浮毂，身世一虚舟。 想鹪鹩，与鸿鹄，不相谋。惊鳞万里深逝，谁肯更吞钩。醉则北窗高卧，醒则南园行乐，莫莫更悠悠。云在山中谷，月在水中洲。'),
	(9532, 117, '虞美人（和刘制几舟中送临簿韵）', '东风催客呼前渡，宿鸟投林暮。欲归人送得归人。万叠青山罗列、是愁城。 谁家台榭当年筑，芳草垂杨绿。云深雾暗不须悲。只缘盈虚消息、少人知。'),
	(9533, 117, '朝中措（三用韵）', '杨花撩乱与云干，春事可悲酸。况是雨荒院落，江南但有春寒。 莺残燕懒，蜂慵蝶褪，谩等闲看。不是无情描貌，奚奴且放安单。'),
	(9534, 117, '海棠春（三用韵）', '苍龙夭矫停今雨。正不待、云吞雾吐。绝笑大夫松，今古闲言语。 清光冷艳侵人处。漏月影、婆娑自舞。拟作岁寒人，此愿天应许。'),
	(9535, 117, '念奴娇（三和）', '白苹影里，向何人可话，平生心素。月魄冰魂凝结就，犹薄湘妃洛女。吴沼芙蓉，陈陂菡萏，散入玄珠浦。采花蜂蝶，雾深都忘归去。 堪笑并蒂霞冠，双头酡脸，只为多情苦。空遣隔江游冶子，撩乱心飞目注。同出泥涂，独标玉质，不是曼陀雨。风清露冷，有人长自迟伫。'),
	(9536, 117, '满江红（己未赓李制参直翁俾寿之词）', '午枕神游，晓鸡唱、城关偷度。俄顷里、笋舆伊轧，征夫前路。路入江南天地阔，黄云翠浪千千亩。有皤翁、三五喜相迎，邻田父。 旋策杖，寻幽圃。旋挈＿，陈高俎。疑此身归去，朱陵丹府。布谷数声惊梦断，纱窗小阵梅黄雨。把人间、万事一般看，投芳醑。'),
	(9537, 117, '永遇乐（再和）', '天上人间，这般光景，管无风雨。绣户珠帘，锦坊花巷，戏队将嫫母。月扇团圆，星球灿烂，路遍市三街五。升平事，牙旗铁马，且还旧家藩府。 三陲见说，凯歌频奏，渐次不烦鼙鼓。双凤云间，六鳌尘外，想见都人欢舞。火城春近，金莲地币，消夜果边曾语。如今但，梅花纸帐，睡魔欠补。元宵宰执赐消夜果。'),
	(9538, 117, '暗香', '及歌酒其下，今荒矣。园乃欧公记、君谟书，古今称二绝。犹忆其词云：高甍巨桷，水光日影，动摇而下上，其宽间深靓，可以答远响而生清风，此前日之颓垣断堑而荒墟也。嘉时令节，州人士女，啸歌而管弦，此前日之晦冥风雨、＿鼯鸟兽之嗥音也。令人慨然。 澹然绝色。记故园月下，吹残龙笛。怅望楚云，日日归心大刀折。犹怕冰条冷蕊，轻点污、丹青凡笔。可怪底，屈子离骚，兰蕙独前席。 院宇，深更寂。正目断古邗，暮霭凝积。何郎旧梦，四十余年尚能忆。须索梅兄一笑，但矫首、层霄空碧。春在手、人在远，倩谁寄得。'),
	(9539, 117, '疏影', '佳人步玉。待月来弄影，天挂参宿。冷透屏帏，清入肌肤，风敲又听檐竹。前村不管深雪闭，犹自绕、枝南枝北。算平生、此段幽奇，占压百花曾独。 闲想罗浮旧恨，有人正醉里，姝翠蛾绿。梦断魂惊，几许凄凉，却是千林梅屋。鸡声野渡溪桥滑，又角引、戍楼悲曲。怎得知、清足亭边，自在杖藜巾幅。梅圣俞诗云：“十分清意足。”余别墅有梅亭，扁日清足。'),
	(9540, 117, '暗香', '闻尧章死西湖，尝助诸丈为殡之，今又不知几年矣。自昭忽录示尧章暗香、疏影二词，因信手酬酢，并赓潘德久之诗云。 晓霜一色。正恁时陇上，征人横笛。驿使不来，借问孤芳为谁折。休说和羹未晚，都付与、逋仙吟笔。算只是，野店疏篱，樵子共争席。 寒圃，众籁寂。想暗里度香，万斛堆积。恼他鼻观，巡索还无最堪忆。萼绿堂前一笑，封老干、苔青莓碧。春漏也，应念我、要归未得。'),
	(9541, 117, '浪淘沙', '长记去年时。雪满征衣。佳人携手画楼西。今日关山千里外，此恨谁知。 想见绿窗低。依旧空闺。惜春还是惜花飞。纵有游蜂偷得去，争似帘帷。'),
	(9542, 117, '望江南', '家山好，好处是三冬。梨栗甘鲜输地客，鲂鳊肥美献溪翁。醉滴小槽红。 识破了，不用计穷通。下泽车安如驷马，市门卒稳似王公。一笑等鸡虫。'),
	(9543, 117, '望江南', '家山好，好处是秋来。绿橘黄橙随市有，岩花篱菊逐时开。管领付尊罍。 新筑就，别馆共闲台。摇手出离名利窟，掉头摆脱簿书堆。只在念头灰。'),
	(9544, 117, '如梦令', '庭院深深春寂。还是他乡寒食。闲利与闲名，谩把光阴虚掷。虚掷。虚掷。知道几时归得。'),
	(9545, 117, '如梦令', '枝上蝶纷蜂闹。几树杏花残了。幽鸟亦多情，片片衔归芳草。休扫。休扫。管甚落英还好。'),
	(9546, 117, '如梦令', '昨日春衫初试。今日春寒犹＿。待得晚风收，独上危楼闲倚，闲倚。闲倚。目断半空烟水。'),
	(9547, 117, '蝶恋花（吴中赵园）', '野树梅花香似扑。小径穿幽，乐意天然足。回首人间名利局。大都一觉黄粱熟。 别墅谁家屏簇簇。绮户疏窗，尚有藏春屋。镜断钗分何处续。伤心芳草庭前绿。'),
	(9548, 117, '长相思', '燕高飞，燕低飞。正是黄梅青杏时，榴花开数枝。 梦归期。数归期。相见画楼天四垂。有人攒黛眉。'),
	(9549, 117, '长相思', '要相逢，恰相逢。画舫朱帘脉脉中。霎时烟霭重。 怨东风，笑东风。落花飞絮两无踪。分付与眉峰。'),
	(9550, 117, '青玉案（和刘长翁右司韵）', '人生南北如歧路。惆怅方回断肠句。四野碧云秋日暮。苇汀芦岸，落霞残照，时有鸥来去。 一杯渺渺怀今古。万事悠悠付寒暑。青箬绿蓑便野处。有山堪采，有溪堪钓，归计聊如许。'),
	(9551, 117, '喜迁莺', '良辰佳节。问底事，十番九番为客。景物春妍，莺花日闹，自是情怀今别。只有思归魂梦，却怕杜鹃啼歇。消凝处，正丝扬冉冉，寸肠千折。 谩说。临曲水，修竹茂林，人境成双绝。俯仰俱陈，彭殇等幻，何计世殊时隔。倚楼碧云日暮，漠漠远山千叠。沈醉好，又城头画角，一声声咽。'),
	(9552, 117, '祝英台近（和辛稼轩“宝钗分”韵）', '雾霏霏，云漠漠，新绿涨幽浦。梦里家山，春透一犁雨。伤心塞雁回来，问人归未，怎知道、蜗名留住。 镜中觑。近年短发难簪，丝丝不禁数。蕙帐尘侵，凄切共谁语。被他轻暖轻寒，将人憔悴，正闷里、梅花残去。'),
	(9553, 117, '汉宫春（吴中齐云楼）', '楼观齐云，正霜明天净，一雁高飞。江南倦客徙倚，目断双溪。凭阑自语，算从来、总是儿痴。青镜里，数丝点鬓，问渠何事忘归。 幸有三椽茅屋，更小园随分，秋实春菲。几多清风皎月，美景良时。陶贤乐圣，尽由他、歧路危机。须信道，功名富贵，大都磨蚁醯鸡。'),
	(9554, 117, '二郎神', '小楼向晚，正柳锁、一城烟雨。记十里吴山，绣帘朱户，曾学宫词内舞。浪逐东风无人管，但脉脉、岁移年度。嗟往事未尘，新愁还织，怎堪重诉。 凝伫。问春何事，飞红飘絮。纵杜曲秦川，旧家都在，谁寄音书说与。野草凄迷，暮云深黯，浑自替人无绪。珠泪滴，应把寸肠万结，夜帷深处。'),
	(9555, 117, '满庭芳（春感）', '漠漠春阴，疏疏春雨，鹁鸠唤起春眠。小园人静，独自倚秋千。又见飘红堕雪，芳径里、都是花钿。年年事，闲愁闲闷，挂在绿杨边。 寻思，都遍了，功名竹帛，富贵貂蝉。但身为利锁，心被名牵。争似依山傍水，数椽外、二顷良田。无萦绊，炊粳酿秫，长是好花天。'),
	(9556, 117, '贺新郎（寄赵南仲端明）', '烟树瓜洲岸。望旌旗、猎猎摇空，故人天远。不似沙鸥飞得渡，直到雕鞍侧畔。但徙倚、危阑目断。自古钟情须我辈，况人间、万事思量遍。涛似雪，风如箭。 扬州十里朱帘卷。想桃根桃叶，依稀旧家庭院。谁把青红吹到眼，知有醉翁局段。便回首、舟移帆转。渺渺江波愁未了，正淮山、日幕云撩乱。阁酒盏，倚歌扇。'),
	(9557, 117, '满江红（京口凤凰池和芦川“春水连天”韵。池，苏魏公旧游也。）', '借问如何，春能好、客怀偏恶。消遣底、闲言闲语，近都慵作。岁月从今休点检，江湖自古多流落。倚危亭、目断野云边，孤舟泊。 人事改，人情薄。退后步，争先著。且开尊洗盏，为君斟酌。拂拭凤凰池上景，凄凉猿鹤山中约。更东阳、憔悴到腰围，浑如削。'),
	(9558, 117, '满江红（姑苏灵岩寺涵空阁）', '客子愁来，闲信马、到涵空阁。谁为我、敛云收雾，青天为幕。八万顷湖如镜静，波神护断东南角。望孤帆、杳杳度微茫，山邀却。 三塞外，纷狐貉。三径里，悲猿鹤。笑鸱夷老子，占他头著。正使百年能几许，看来万事难描摸。问吴王、池馆复何如，霜枫落。'),
	(9559, 117, '满江红（九日效行）', '岁岁登高，算难得、今年美景。尽敛却、雨霾风障，雾沈云暝。远岫四呈青欲滴，长空一抹明于镜。更天教、老子放眉头，边烽静。 数本菊，香能劲。数朵桂，香尤胜。向尊前一笑，几多清兴。安得便如彭泽去，不妨且作山翁酩。尽古今、成败共兴亡，都休省。'),
	(9560, 138, '西江月（十之八）', '达磨西来说道，十年面壁安丹。争知水火不交煎。因果谩成罗汉。 仰箭射空力尽，依然坠地何言。虚空拶破强参禅。肯把金丹烧炼。'),
	(9561, 138, '西江月（十之三）', '太一画前是道，全凭龙虎成丹。九还七返保长年。好个逍遥闲汉。 日诣金门玉殿，青衣引赞无言。回风混合万神安。功向虚无中炼。'),
	(9562, 138, '西江月', '道，金鱼要换金丹，龟龄鹤算不知年，子其勉之，当遇赤城人矣。后于祝融峰遇圣师，指迷金丹大道，果应存无守有，顷刻而成之妙。乃知十余年间钻冰取火，盲修瞎炼，今一得永得，实在目前。因足前梦，为《西江月》调，以纪其实，并简同行。林质父。质父见和，意谓有道无丹，当求画前大易，遂与酬唱十首于后。 四十修真学道，金鱼要换金丹。龟龄鹤算不知年。行满身冲霄汉。 此事希夷玄奥，功参造化难言。眼前有药耀山川。好把元阳修炼。'),
	(9563, 138, '沁园春（和吕洞宾）', '大道无名，金丹有验，工夫片时。似婴儿娇俊，不离门户，盈盈姹女，缓步深帏。二八当年，黄婆匹配，隔碍潜通势似危。须臾见，见灵明宝藏，一点星飞。 其时。似执躬圭。深保护阴阳造化儿。转南辰北斗，回风混合，雷轰雨骤，只许天知。梦幻浮生，天长地久，云路著鞭休要迟。金不坏，合朋合德，三教同归。'),
	(9564, 56, '解语花（雪）', '龙沙殿腊，兔苑留寒，花照冰壶夜。乱山平野。装珠树满眼，买春无价。墙头苑下。浑不见、桃夭杏冶。凝趁风、庚岭寒梅，触处都飘谢。 吹面峭寒未怕。览瑶池万里，飞观高榭。霓旌鹤驾。歌黄竹、胜跃踏青骄马。峰峦似画。但点缀、片时相借。惊望中、玉宇琼楼，残溜空鸳瓦。'),
	(9565, 56, '满江红（风泉峡观泉）', '云壑飞泉，蒲根下、悬流陆续。堪爱处、石池湛湛，一方寒玉。暑际直当磐石坐，渴来自引悬瓢掬。听冷冷、清响泻琮琤，胜丝竹。 寒照胆，消炎燠。清彻骨，无尘俗。笑幽人忻玩，滞留空谷。静坐时看松鼠饮，醉眠不碍山禽浴。唤仙人、伴我酌琼瑶，餐秋菊。'),
	(9566, 56, '沁园春（西岩三涧）', '云壑泉泓，小者如杯，大者如罂。更石筵平莹，宽容数客，淙流回激，环绕飞觥。三涧交流，两崖悬瀑，捣雪飞霜落翠屏。经行处，有丹荑碧草，古木苍藤。 徘徊却倚山楹。笑山水娱人若有情。见傍回侧转，峰峦叠叠，欲穷还有，岩谷层层。仰视云间，茅茨鸡犬。疑是仙家来避秦。青林表，望烟霞缥缈，隐隐鸾笙。'),
	(9567, 289, '菩萨蛮', '东园映叶梅如豆。西园扑地花铺绣。春水晓来深。日华娇漾金。 带烟穿径竹。步入飞虹曲。何处早莺啼。曲桥西复西。'),
	(9568, 289, '酹江月', '西风横荡，渐霜余黄落，空山乔木。照水依然冰雪在，耿耿梅花幽独。抖擞征尘，扶携短策，步绕沧浪曲。怅然心事，浮生翻覆陵谷。 试向商乐亭前，冷风台上，把酒招黄鹄。四十男儿当富贵，谁念漂零南北。百亩春耕，三间云卧，此计何时卜。功名休问，卖书归买黄犊。'),
	(9569, 199, '虞美人（借韵）', '龙香浅渍罗屏褶。睡思低眉月。闲愁闲闷不关心。心似窗前梅影、一般清。 绣帏交掩流苏动。一觉华胥梦。枕山轻戛宝钗声。粉裉香腮零乱、鬓鸦青。'),
	(9570, 199, '水龙吟', '昼长帘幕低垂，时时风度杨花过。梁间燕子，芹随香觜，频沾泥污。苦被流莺，蹴翻花影，一阑红露。看残梅飞尽，枝头微认，青青子、些儿大。 谁道洞门无锁。翠苔藓、何曾踏破。好天良夜，清风明月，正须著我。闲展蛮笺，寄情词调，唱成谁和。问晓山亭下，山茶经雨，早来开么。'),
	(9571, 557, '虞美人（红木樨次谢主簿）', '素娥冷淡愁无奈。小作施朱态。霓裾霞珮下瑶台。一朵绿云围绕、送春来。 玄霜捣尽丹砂就。拼醉长生酒。羡君幽壑狎流年。把住西风长对、广寒仙。'),
	(9572, 557, '虞美人', '天凉来傍荷花饮。携手看云锦。城头玉漏已三更。耳畔微闻新雁、几声声。 兰膏影里春山秀。久立还成皱。酒阑天外月华流。我醉欲眠卿且、去来休。'),
	(9573, 557, '二郎神（次陈唯道）', '野塘暗碧，渐点点、翠钿明镜。想昼永珠帘，人闲金屋，时倚妆台照影。睡起阑干凝思处，漫数尽、旭鸦栖暝。知月下莺黄，云边蛾绿，为谁低整。 曾倩。雁传鹊报，心期千定。奈柳絮浮云，桃花流水，长是参差不并。莫怨春归，莫愁柘老，蚕已三眠将醒。肠断句，枉费丹青，漠漠水遥烟迥。'),
	(9574, 557, '燕春台（送徐意一）', '绣地残英，画空飞絮，东风又送春归。雨足郊＿，相将翠密成帷。燕莺犹恋芳菲。向枝头、叶底依依。留春不住，绿波渺渺，碧草萋萋。 锦帆开晓，彩仗迎熏，峰回路转，月淡天低。红云影里，群仙报道班齐。九奏箫韶，算人间、无此埙篪。步新堤。金鼎调羹也，梅子黄时。'),
	(9575, 557, '贺新郎（夜来月色可人，兰香满室，再用前调）', '碾破长空绿。看银蟾、一轮似水，照人清独。缥缈风摇环珮碎，疑是英茎妙曲。忽散作、天花芬馥。帝子双双来洞户，炯肌肤、冰雪颜如玉。愁易老，意难足。 楚江万顷疏汤沐。想佳人、依然携手，碧云修竹。葱茜玲珑方寸许，清过千重夏木。速就我、同倾湘＿。追忆兰亭当日事，尽凄凉、也胜卢仝屋。应不到，羡金谷。'),
	(9576, 557, '贺新郎（次刘后村）', '葵扇秋来贱。阿谁知、初回轻暑，又教题遍。不是琵琶知音少，无限如簧巧啭。倩说似、长门休怨。莫把蛾眉与人妒，但疏梅、淡月深深院。临宝鉴，欲妆懒。 少时声价倾梁苑。到中年、也曾落魄，雾收云卷。待入汉庭金马去，洒笔长江衮衮。好留取、才名久远。过眼荣华俱尘土，听关睢、盈耳离骚婉。歌不足，为嗟叹。'),
	(9577, 557, '烛影摇红', '乍冷还暄，小春时候今朝转。三分历日二分休，镜里清霜满。云幕低垂不展。矮窗明、红麟初暖。老来活计，浊酒三杯，黄庭一卷。 万里关河，朔风吹到边声远。倚楼脉脉数归鸿，谁会愁深浅。最苦山寒日短。但梅花、相看岁晚。何人金屋，巧啭歌莺，慢调筝雁。'),
	(9578, 557, '忆旧游慢（荷花，泛东湖用方时父韵）', '爱东湖六月，十里香风，翡翠铺平。误入红云里，似当年太乙，约我寻盟。叶舟荡漾寒碧，分得一襟冰。渐际晚轻阴，修蒲舞绿，倦柳梳青。 聘婷。黯无语。想怨女三千，长日宫庭。六六阑干曲，有玉儿才貌，谁与看承。柔情一点无奈，频付酒杯行。到夜静人归，凉蟾自照鸥鹭汀。'),
	(9579, 42, '沁园春（二月二日寿玉林）', '禀气之中，具圣之和，生逢令辰。逄三春仲月，方才破二，百年大齐，恰则平分。立玉林深，散花庵小，中有＿然自在身。诗何似，似苏州闲远，庾府清新。 青鞋布袜乌巾。试勇往蓉溪一问津。有心香一瓣，心声一阕，更携阿艾，同寿灵椿。劫劫长存，生生不息，宁极深根秋又春。聊添我，作风流二老，岁岁寻盟。'),
	(9580, 42, '贺新郎（次玉林感时韵）', '知彼须知此。问筹边、攻守规模，云何则是。景色＿＿犹日暮，壮士无由吐气。又安得、将如廉李。燕坐江沱甘自蹙，笑腐儒、枉楦朝家紫。用与舍，徒为耳。 黄芦白苇迷千里。叹长淮、篱落空疏，仅余残垒。读父兵书宁足恃，击楫谁盟江水。有识者、知其庸矣。多少英雄沈草野，岂堂堂、吾国无君子。起诸葛，总戎事。'),
	(9581, 42, '贺新郎（次韵江定轩咏菊）', '句里思黄九。笑王郎、不奈寒芳，腰围如柳。得似江郎饶雅趣，时揽黄花诳口。吐妙语、与之争秀。闲绕珍丛吟不尽，尽风前、露下栾栾瘦。香自足，岂劳嗅。 一尊问我能同否。叹双溪、冷落篱边，傲霜犹有。浩唱云笺金缕调，兴发小槽珠酒。待唤醒、早春梅友。独恨爱花人易老，漫一年、好景还依旧。东望处，立良久。'),
	(9582, 42, '沁园春（二月二日寿玉林）', '禀气之中，具圣之和，生逢令辰。逄三春仲月，方才破二，百年大齐，恰则平分。立玉林深，散花庵小，中有＿然自在身。诗何似，似苏州闲远，庾府清新。 青鞋布袜乌巾。试勇往蓉溪一问津。有心香一瓣，心声一阕，更携阿艾，同寿灵椿。劫劫长存，生生不息，宁极深根秋又春。聊添我，作风流二老，岁岁寻盟。'),
	(9583, 42, '贺新郎（次韵江定轩咏菊）', '句里思黄九。笑王郎、不奈寒芳，腰围如柳。得似江郎饶雅趣，时揽黄花诳口。吐妙语、与之争秀。闲绕珍丛吟不尽，尽风前、露下栾栾瘦。香自足，岂劳嗅。 一尊问我能同否。叹双溪、冷落篱边，傲霜犹有。浩唱云笺金缕调，兴发小槽珠酒。待唤醒、早春梅友。独恨爱花人易老，漫一年、好景还依旧。东望处，立良久。'),
	(9584, 49, '朝中措（陈左藏生日）', '海天万顷碧玻璃。风露洗炎曦。鹦鹉绿毛导从，蟾□雪色追随。 分明来处，补陀大士，先后同时。觅取善财童子，膝边要个孙儿。'),
	(9585, 49, '朝中措（艮翁生日）', '仙风道骨北山翁。万卷著胸中。涣若宦情冰释，作□醉面桃红。 千林冻槁，一枝雪艳，消息先通。颜色□青精饭，姓名在碧纱笼。'),
	(9586, 49, '朝中措（艮翁生日）', '受持鼻祖五千言。留得谷神存。伴我赋诗茅屋，饶渠待诏金门。 此翁岁晚，有书充栋，有酒盈樽。君看多花早落，孰如仙李蟠根。'),
	(9587, 49, '朝中措（元质侍郎生日）', '恰为仙佛做生辰。公又绂麒麟。黑白几枰屡变，丹青百奏如新。 都门饯底，洛中画底，莫是前身。虽老不扶灵寿，有时更上蒲轮。'),
	(9588, 49, '西江月（腰痛，旧传陈复斋名方，岁久失之）', '思邈方书去失，休文老病来攻。新年筋力太龙钟。腰似铁猫儿重。 雅拜怎生搢笏，徐行也要扶筇。田翁邀饮不能从。难伴诸公上雍。'),
	(9589, 49, '清平乐（居厚弟生日）', '人间喘汗。无计翻银汉。有个至人来震旦。宴坐补陀岩畔。 吾闻福寿难量。待看海底生桑。乞取净瓶一滴，普教大地清凉。'),
	(9590, 49, '卜算子（燕）', '已怪社愆期，尚喜巢如故。过了清明未肯来，莫被春寒误。 常傍画檐飞，忽委空梁去。忘却王家与谢家，别有衔泥处。'),
	(9591, 49, '卜算子（良翁礼部生日）', '开＿广延贤，负＿勤求旧。应念南宫老舍人，闲袖丝纶手。 两制必当仁，五福无过寿。且喜新年不要□，天要开元祐。'),
	(9592, 49, '卜算子', '乱似盎中丝，密似风中絮。行遍茫茫禹迹来，底是无愁处。 好客挽难留，俗事推难去。惟有翻身人醉乡，愁欲来无路。'),
	(9593, 49, '鹊桥仙（庚申生日）', '香芸辟蠹，青藜烛阁，天上宝书万轴。前回读得未精详，更罚走、一遭重读。 松风如故，丹炉如故，坐阅人间陵谷。回头调戏窃桃儿，且宁耐、等他桃熟。'),
	(9594, 49, '鹊桥仙（乡守赵丞相生日）', '去年无麦，今年多稼，尽是君侯心地。向来寺寺总拘桩，今有不拘桩底寺。 省仓展日，米场镌价。 万落千村蒙惠。更将补纳放宽些，便是个、西京循吏。'),
	(9595, 49, '鹊桥仙（居厚弟生日）', '俱登瀛馆，俱还洛社，各自健如黄犊。不消外监与留台，也不要、嵩山崇福。 我如原父，君如贡父，且把汉书重读。韩公当局等闲过，又看到、温公当局。'),
	(9596, 49, '鹊桥仙（林侍郎生日）', '出通明殿，入耆英社，谁似侍郎洪福。掌中元自有三珠，更检校、诸孙夜读。 管他莱相，管他鹤相，留我本来面目。希夷一枕未曾醒，笑人世、几回翻局。'),
	(9597, 49, '鹊桥仙（答桃巷弟和篇）', '阁中芸冷，观中桃谢，谁问贞元朝士。吾宗一句好书绅，但记取、毋污青史知几告张说语。 不交平勃，不游田窦，也不朋他牛李。平章此去似何人，似洛社、戴花舞底。'),
	(9598, 49, '鹊桥仙（挑巷弟生日）', '御屏录了，冰衔换了，酷似香山居士。草堂丹灶莫留他，且领取、忠州刺史。 移来芳树，摘来珍果，压尽来禽青李。三千年一荐金盘，又不是、玄都栽底。'),
	(9599, 49, '鹊桥仙（戊戌生朝）', '金风淅淅，银河淡淡，长少群贤毕会。平生心事麹生知，怪此夕、惺惺相对。 玄花生眼，新霜点鬓，不肯遮藏老态。人间何处有仙方，擘划得、二三百岁。'),
	(9600, 49, '踏莎行（巧夕）', '驱鹊营桥，呼蟾出海，朝朝暮暮遥相望。谁知风雨此时来，银河便有些波浪。 玉兔迷离，金鸡嘲口析，二星无语空惆怅。元来上界也多魔，天孙长怨牵牛旷。'),
	(9601, 49, '踏莎行（甲午重九牛山作）', '日月跳丸，光阴脱兔。登临不用深怀古。向来吹帽插花人，尽随残照西风去。 老矣征衫，飘然客路。炊烟三两人家住。欲携斗酒答秋光，山深无觅黄花处。'),
	(9602, 49, '法驾导引', '樵柯烂，丹灶熟，一跳出红尘。斗紫一双龙奋蛰，帝青九万里为程。赤脚踏层云。 鞭鸾上，骑麟下，仿佛睹昆仑。洒马鬃泉苏赤地，翻蟾滴水涨沧溟。笑杀懵仙人。'),
	(9603, 49, '浪淘沙', '叠嶂碧周遮。游子思家。掩藏白发赖乌纱。落日倚楼千万恨，社鼓城笳。 老去淡生涯。虚掷年华。腊茶盂子太清些。待得痴儿公事毕，谢了梅花。'),
	(9604, 49, '浪淘沙', '纸帐素屏遮。全似僧家。无端霜月闯窗纱。唤起玉关征戍梦，几叠寒笳。 岁晚客天涯。短发苍华。今年衰似去年些。诗酒新来俱倚阁，孤负梅花。'),
	(9605, 49, '浪淘沙', '早岁类寒蛩。晚节遭逢。曾开黄卷侍重瞳。归去青藜光照牖，阶药翻红。 出昼颇匆匆。主眷犹浓。除官全似紫阳翁宝文、漳州。换个新衔头面改，又似包公辞郡得小龙。'),
	(9606, 49, '浪淘沙（丁未生日）', '去岁诣公车。天语勤渠。绛纱玉斧照寒儒。恰似昔人曾梦到，帝所清都。 骨相太清＿。谪堕须臾。今年黄敕换称呼。只为此翁霜鬓秃，老不中书。'),
	(9607, 49, '临江仙（潮惠道中）', '不见仙湖能几日，尘沙变尽形容。夜来月冷露华浓。都忘茅屋下，但记画船中。 两岸绿阴犹未合，更须补竹添松。最怜几树木芙蓉。手栽才数尺，别后为谁红。'),
	(9608, 49, '临江仙', '年是日，寓海丰县驿作。 去岁越王台上饮，席间二客如龙。凭高吊古壮怀同。马嘶千嶂暮，乐奏半天中。 今岁三家村市里，故人各自西东。菊花时节酒樽空。可怜双雪鬓，禁得几秋风。'),
	(9609, 49, '临江仙（县圃种花）', '落魄长官江海客，少豪万里寻春。而今憔悴向溪滨。断无觞咏兴，惟有簿书尘。 手插海棠三百本，等闲妆点芳辰。他年绛雪映红云。丁宁风与月，记取种花人。'),
	(9610, 49, '临江仙（己酉和实之灯夕）', '玉笛钿车当日事，东涂西抹都曾。等闲曲子压和凝曲子相公。纵游非草草，已醉强惺惺。 今向三家村送老，身如罢讲吴僧。高楼百尺不须登。半炉烧叶火，一盏勘书灯。'),
	(9611, 49, '风入松', '攀翻宰树暂徘徊。草草安排。昔人徒步陈鸡絮，愧公家、仆马觥罍。华表旧愁满目，黄粱残梦伤怀。 欲将庄列等欢哀。对卷慵开。凭高指点虚无路，问何年、辽鹤归来。宿酒得风渐解，小舆待月同回。'),
	(9612, 49, '风入松（癸卯至石塘追和十五年前韵）', '残更难睚抵年长。晓月凄凉。芙蓉院落深深闭，叹芳卿、今在今亡。绝笔无求凰曲，痴心有返魂香。 起来休镊鬓边霜。半被堆床。定归兜率蓬莱去，奈人间、无路茫茫。缘断漫三弹指，忧来欲九回肠。'),
	(9613, 49, '最高楼（再题周登乐府）', '周郎后，直数到清真。君莫是前身。八音相应谐韶乐，一声未了落梁尘。笑而今，轻郢客，重巴人。 只少个、绿珠横玉笛。更少个、雪儿弹锦瑟。欺贺晏，压黄秦。可怜樵唱并菱曲，不篷御手与龙巾。且醉眠，逢底月，瓮间春。'),
	(9614, 49, '最高楼（戊戌自寿）', '南狱后，累任作祠官。试说与君看。仙都玉局才交卸，新衔又管华州山。怪先生，吟胆壮，饮肠宽。 去岁拥、旌旗称太守。今岁带、＿＿称漫叟。慵入闹，惯投闲。有时拂袖寻种放，有时携枕就陈抟。任旁人，嘲潦倒，笑痴顽。'),
	(9615, 49, '祝英台近', '雨凄迷，风料峭。情绪被花恼。白白红红，满地无人扫。可堪解佩盟寒，坠楼命薄，更杜宇、枝头闲火刍。 绿阴绕。青帝结束匆匆，转眼朱明了。怕与春辞，茗芋玉山倒。后期觉做明年，春年年好，却不道、明年人老。'),
	(9616, 49, '烛影摇红（用林卿韵）', '拙者平生，不曾乞得天孙巧。那回忝卮蜀车来，岂是齐卿小。此膝不曾屈了。更休文、腰难运掉。前贤样子，表圣宜休，申公告老。 凉簟安眠，绝胜＿直铃声搅。集中大半是诗词，幸没潮州表。月夕花朝咏啸。叹人间、愁多乐少。蓬莱有路，办个船儿，逆风也到。'),
	(9617, 49, '洞仙歌（癸亥生朝和居厚弟韵，题谪仙像）', '上林全树，曾借君栖宿。朝过瑶台暮群玉。忽翩然、脱下宫锦袍来，□□□，却向齐州受箓。 等闲挥醉笔，＿唾千篇，长与诗家窃膏馥。身是酒星文星，刚被诗人，□唤做、禁中颇牧。便散发、骑鲸去何妨，从我者谁欤，安期徐福。'),
	(9618, 49, '贺新郎（二鹤）', '家有仙禽二。早追随、先生杖屦，互乡童子。旦旦池边三薰沐，夜夜山中警睡。且伴我、人间游戏。此老生平哀大陆，到未梢、始忆华亭唳。评往事，败佳思。 古云鹤算谁能纪。叹归来、山川如故，人民非是。但愿主君高飞去，莫爱乘轩禄位。更赛过、令威千岁。假使焦山真羽化，待华阳贞逸铭方瘗。我拍手，渠展翅。'),
	(9619, 49, '贺新郎', '拂袖归来也。懒追陪、竹林嵇阮，兰亭王谢。谁与此翁相暖热，赖有平生伯雅。且放意、高吟闲话。山鸟山花皆上客，又何须、胜似公荣者。胸磊块，总浇下。 盘龙痴绝求鹅炙。这先生、黄齑瓮熟，味珍无价。酒颂一篇差要妙，庄列诸书土苴。任礼法、中人嘲骂。君特未知其趣耳，若还知、火急来投社。共秉烛，惜今夜。'),
	(9620, 49, '贺新郎（傅相生日壬戌）', '低局从头错。解危机、除非唤取，国棋来著。不信胡儿能胆大，南岸安他阵脚。谈笑里、乌巢空幕。西起岷峨东海岱，有捷旗、露布无宵柝。莘渭后，到秋壑。 淮田犁遍吴田获。问台家、山河宇宙，是谁擎托。自觉怀中残锦尽，采色彰施技薄。视柳雅、韩碑瞠若。稽首鲁公黄发颂，世何人、堪继奚斯作。楚狂语，莫删却。'),
	(9621, 49, '贺新郎（四用韵）', '犹记臣之少。兴狂时、过陈遵饮，对孙登啸。岁晚登临多感慨，但觉齐山诗妙。任蓉月、柳风吹照。金印不来丹飞去。拟神仙、富贵都差了。空铸错，与人笑。 九年前拜悬车表。试回看、柴桑菊老，玄都花少。周也曾言殇子寿。佛以白头为夭□□□：未得平生心，白头亦为夭。未后句、岩头曾道。头似秃＿巾裹懒，最不宜蝉冕宜僧帽。杯中物，直须釂。'),
	(9622, 49, '贺新郎（居厚、艮翁皆和，余亦继作。）', '何必游嵩少。屋边山、松风浩荡，虎龙吟啸。旧效楚人悲秋作，晚爱陶诗高妙。发如此、临流羞照。屈指向来夸毗子，被西风、一笔都勾了。曾不满，达人笑。 当年玉振于江表。怅而今、老身空在，欢娱全少。假使真如彭祖寿，蒙叟犹嗤渠夭。偶落笔、不经人道。岁晚连床谈至晓，胜冈头、出没看乌帽。君举白，我频釂。'),
	(9623, 49, '贺新郎（已未九日同季弟子侄饮仓部弟免庵，艮翁、宫教来会。）', '忆昔俱年少。向斯晨、登高怀古，赋诗舒啸。追数樽前插花客，人物并皆佳妙。禁几度、西风残照。元子寄奴曾富贵，到而今、一一消磨了。君不乐，后人笑。 山南山北添华表。叹归来、谢池草合，黄台瓜少。老去爱持齐物论，谁管彭殇寿夭。待细说、教天知道。不羡两苏并二宋，愿弟兄、岁岁同吹帽。杯到手，莫辞釂。'),
	(9624, 49, '贺新郎（实之三和有忧边之语，走笔答之）', '国脉微如缕。问长缨、何时入手，缚将戎主。未必人间无好汉，谁与宽些尺度。试看取、当年韩五。岂有谷城公付授，也不干、曾遇骊山母。谈笑起，两河路。 少时棋柝曾联句。叹而今、登楼揽镜，事机频误。闻说北风吹面急，边上冲梯屡舞。君莫道、投鞭虚语。自古一贤能制难，有金汤、便可无张许。快投笔，莫题柱。'),
	(9625, 49, '贺新郎（王实之喜余出岭，命爱姬歌新词以相劳，辄次其韵。）', '此腹元空洞。少年时、诸公过矣，上天吹送。老大被他禁害杀，身与浮名孰重。这鼓笛、休休拈弄。彩笔掷还残锦去用江淹鲍昭事，愿今生、来世无妖梦。且饭犊，莫吞凤。 新来喑哑如翁仲。羡王郎、骖鸾缥缈，玉箫吹动。应笑夔州村里女，灸面生愁进奉。要绝代、倾城安用。今古何人知此理。有吾家、酒德先生颂。三万卷，漫充栋。'),
	(9626, 49, '贺新郎（再用约字）', '浅把宫黄约。细端相、普陀烟里，金身珠络。萼绿华轻罗袜小，飞下祥云仙鹤。朵朵赛、蜂腰纤弱。已被色香僚病思，尽鹅儿、酒美无多酌。看不足，怕残却。 人间难得伤春药。更枝头、流莺呼起，少年狂作。留取姚家花相伴，羞与万红同落。未肯让、蜡梅先著。乐府今无黄绢手，问斯人、清唱何人酢。休草草，认题错。'),
	(9627, 49, '贺新郎（用前韵赋黄荼＿）', '想赴瑶池约。向东风、名姬骏马，翠鞯金络。太液池边鹄群下，又似南楼呼鹤。画不就、秾纤娇弱。罗帕封香来天上，泻铜盘、沆瀣供清酌。春去也，被留却。 芳魂再返应无药。似诗咏、绿衣黄里，感伤而作。爱惜尚嫌蜂采去，何况流莺蹴落。且放下、珠帘遮著。除却江南黄九外，有何人、敢与花酬酢。君认取，莫教错。'),
	(9628, 49, '贺新郎（送黄成父还朝）', '飞诏从天下。道中朝、名流欲尽，君王思贾。时事只今堪痛哭，未可徐徐俟驾。好著手、扶将宗社。多少法筵龙象众，听灵山、属付些儿话。千百世，要传写。 子方行矣乘骢马。又送他、江南太史，去游毡厦。老我伴身惟有影，倚遍风轩月榭。怅玉手、何时重把。君向柳边花底问，看贞元、朝士谁存者。桃满观，几开谢。'),
	(9629, 49, '贺新郎（送唐伯玉还朝）', '驿骑联翩至。道台家、筹边方急，酒行姑止。作么携将琴鹤去，不管州人堕泪。富与贵、平生无味。可但红尘难著脚，便山林、未有安身地。搔白发，兀相对。 前身小范疑公是。忆当年、天章阁上，建明尤伟。庆历诸贤方得路，便不容他老子。须著放、延州城里。一句殷勤牢记取，在朝廷、最好图西事。何必向，玉关外。'),
	(9630, 49, '贺新郎（跋唐伯玉奏稿）', '宣引东华去。似当年、文皇亲擢，马周徒步。殿上风霜生白简，下殿扁舟已具。怎不与、官家留住。古有一言腰相印，谁教他、满箧婴鳞疏。还笏退，不回顾。 新来边报犹飞羽。问诸公、可无长策，少宽明主。攀槛朱云头雪白，流落如今底处。但一片、丹心如故。赖有越台堪眺望，那中原、莫已平安否。风色恶，海天暮。'),
	(9631, 49, '贺新郎（杜子昕凯歌）', '尽说番和汉。这琵琶、依稀似曲，蓦然弦断。作么一年来一度，欺得南人技短。叹几处、城危如卵。元凯后身居玉帐，报胡儿、休作寻常看。布严令，运奇算。 开门决斗雌雄判。笑中宵、奚车毡屋，兽惊禽散。个个巍冠横尘柄，谁了君王此段。也莫靠、长江能限。不论周郎并幼度，便仲尼、复起嗟微管。驰露布，筑京观。'),
	(9632, 49, '水龙吟（辛亥安晚生朝）', '祁公一度貂蝉，先生三度貂蝉了。燔柴升辂，银蟾烛夜，金乌腾晓。喜动龙颜，瑞班虹玉，归功元老。纵擎天力倦，明农心切，先还取、中书考。 末著留侯难办，算除非、烦他商皓。紫芝产遍，赤松待久，何时高蹈。人世无过，鱼羹饭美，布衾铭好。待角巾东路，蹇驴北阜，伴公游钓。'),
	(9633, 49, '水龙吟（己亥自寿二首）', '年年岁岁今朝，左弧悬罢浑无事。吾衰久矣，我辰安在，老之将至。懒写京书，怕看除目，败人佳思。把东篱掩定，北窗开了，悠然酌、颓然睡。 客有过门投贽。道先生访华胥氏。谁能辛苦，陪他绮语，记他奇字。屈指先贤，戴花老监，岂其苗裔。待异时约取，宽夫彦国，入耆英会。'),
	(9634, 49, '满江红', '嫌杀双轮，驾行客、之燕适粤。也不喜、船儿无赖，载他江浙。荡子不归鸳被冷，昭君远嫁毡车发。叹子规、闲管昔人愁，啼成血。 渭城柳，争攀折。关山月，空圆缺。有琵琶改语，锦书难说。若要人生长美满，除非世上无离别。算古今、此恨似连环，何时绝。'),
	(9635, 49, '满江红（傅相生日甲子）', '见宰官身，出只手、擎他宇宙。筹边外、招徕名胜，登崇勋旧。不下莱公扶景德，又如涑水开元祐。尽从渠、干贽及吾门，归斯受。 上林苑，多花柳。祁连塞，稀刁斗。更红旗破贼，黄云栖亩。阿母瑶池枝上实，仙人太华峰头藕。泻铜盘、沆瀣入金卮，为公寿。'),
	(9636, 49, '满江红（和叔永吴尚书，时吴丧少子）', '著破青鞋，浑不忆、踏他龙尾。更冷笑、痴人擘划，二三百岁。殇子彭篯谁寿夭，灵均渔父争醒醉。向江天、极目羡禽鱼，悠然矣。 杯中物，姑停止。床头易，聊抛废。慨事常八九，不如人意。白雪调高尤协律，落霞语好终伤绮。待烦公、老手一摩挲，文公记。'),
	(9637, 49, '满江红', '三黜归来，饭疏食、浑无愠色。中年后、家如旅舍，身如行客。轩冕岂非疣赘具，烟霞已是膏盲脉。有些儿、隙地更疏泉，堆卷石。 邻媪饷，新＿碧。溪友卖，鲜鳞白。向陈编冷笑，孔明元直。俗事不教污两耳，宴居聊可盘双膝。取当年、行脚一枝筇，悬高壁。'),
	(9638, 49, '解连环', '悬弧之旦。忆争骑竹马，各怀金弹。恨岁月、去我堂堂，向酒畔愁生，镜中颜换。灶坏丹飞，慢追悔、邺侯婚宦。已发心忏悔，免去猴冠，卸下麟楦。 依稀仆家铁汉。虽末梢老寿，初节魔难。幸闻早、省了柳枝，更送了朝云，尘念俱断。丈室萧然，独病与、乐天相伴。但归依西方，拈起向来一瓣。'),
	(9639, 49, '念奴娇（三和）', '戏衫抛了，下棚去、谁笑郭郎长袖。小小草庵无宝贝，何必神呵鬼守。黄奶篝灯，青奴拂榻，莫要他桃柳退之二妾。客来问字，此翁高卧摇首。 仿佛曾子当年，商歌满屋，衣不完衿肘曾子捉衿而肘见。混沌若教休凿窍，巧历安知其寿。文叔故人，仲华几个，输与羊裘叟。浮生如寄，切身之物惟酒。'),
	(9640, 49, '汉宫春（再和前韵）', '多谢句芒，露十分春信，一种仙姿。主人领客卜夜，也唤分司。天葩国艳，几曾烦、薄粉浓脂。微似有，酒潮玉颊，更无粟起香肌。 犹记老婆年少，爱斜簪宝髻，浅印红眉。回头笑他桃杏，太赤些儿。而今零落，更禁当、多少风吹。君看取，梢头点滴，绝胜树下纷披。'),
	(9641, 49, '汉宫春（秘书弟家赏红梅）', '青女初晴，向丑梢枯干，幻出妍姿。休烦苑吏翦彩，别有神司。东皇太乙，敕瑶姬、淡傅胭脂。还似得、华清汤暖，薄绡半卸冰肌。 应笑楚宫痴绝，略施朱则个，便妒蛾眉。唐人更无籍在，浪比红儿。祥云难聚，且丁宁、铁笛轻吹。拼醉倒，花间一霎，莫教绛雪离披。'),
	(9642, 49, '水调歌头（癸卯中秋作）', '老年有奇事，天放两中秋。使君飞榭千尺，缥渺见麟洲。景物东徐城上，岁月北征诗里，圆缺几时休。俯仰慨今昔，惟酒可浇愁。 风露高，河汉澹，素光流。贾胡野老相庆，四海十分收。竞看姮娥金镜，争信仙人玉斧，费了一番修。衰晚笔无力，谁伴赋黄楼。'),
	(9643, 49, '水调歌头（十三夜，同官载酒相别，不见月作）', '怪事广寒殿，此夕不开关。林间乌鹊贺，暂得一枝安。只在浮云深处，谁驾长风挟取，明镜忽飞还。玉兔呼不应，难觅臼中丹。 酒行深，歌听彻，笛吹残。嫦娥老去孤另，离别匹如闲。待得银盘擎出，只怕玉峰醉倒，衰病不禁寒。卿去我欲睡，孤负此湖山。'),
	(9644, 49, '水调歌头（客散循堤步月而作）', '落日几呼渡，佳夕每留关。有时来照清浅，鬓雪似潘安。一曲亲蒙君赐，两岸更无人迹，惟见鹭飞还。隙地欠栽接，蕉荔杂黄丹。 柳全疏，松尚幼，怕摧残。旁人笑我痴计，管钥费防闲。翁意在乎林壑，客亦知夫水月，满腹贮清寒。赋咏差有愧，赤壁与滁山。'),
	(9645, 49, '水调歌头（喜归）', '遣作岭头使，似戍玉门关。来时送者，举酒珍重祝身安。街畔小儿拍笑，马上是翁矍铄，头与璧俱还。何处得仙诀，发白颊犹丹。 屋茅破，篱菊瘦，架签残。老夫自计甚审，忙定不如闲。客难扬雄拓落，友笑王良来往，面汗背芒寒。再拜谢不敏，早晚乞还山。'),
	(9646, 513, '蝶恋花', '绿暗红稀春已幕。燕子衔泥，飞入谁家去。柳絮欲停风不住。杜鹃声里山无数。 白马青衫无定据。好底林泉，信脚随缘寓。拼却此生心已许。一川风月聊为主。'),
	(9647, 513, '汉宫春（次韵李汉老咏梅）', '潇洒江梅，似玉妆珠缀，密蕊疏枝。霜风应是，不许蝶近蜂欺。嫣然自笑，与山矾、共水仙期。还亦有，青松翠竹，同今凛冽年时。 何事向人如恨，带苍苔，半倚临水荒篱。孤山嫩寒放晓，尚忆前诗。黄昏顾影，说横斜、清浅今谁。他自是，移春手段，微云淡月应知。'),
	(9648, 513, '促拍满路花（和纯阳韵）', '多才夸李白，美貌说潘安。一朝成万古，又徒闲。如何猛省，心地种仙蟠。堪叹人间事，泡沫风灯，阿谁肯做飞仙。 莫思量、骏马与高轩。快乐任天然。最坚似松柏、更凋残。有何凭据，谁易复谁难。长啸青云外，自嗟自笑，了无恨海愁山。'),
	(9649, 513, '酹江月（武昌怀古）', '汉江北泻，下长淮、洗尽胸中今古。楼橹横波征雁远，谁见鱼龙夜舞。鹦鹉洲云，凤凰池月，付与沙头鹭。功名何处，年年惟见春絮。 非不豪似周瑜，壮如黄祖，亦随秋风度。野草闲花无限数，渺在西山南浦。黄鹤楼人，赤乌年事，江汉亭前路。浮萍无据，水天几度朝暮。'),
	(9650, 513, '酹江月（咏梅）', '孤村篱落，玉亭亭、为问何其清瘦。欲语还愁谁索笑，临水嫣然自照。甘受凄凉，不求识觉，风致何高妙。松挨竹拶，更堪霜雪＿＿。 争奈终是冰肌，也过了几个，晴昏雨晓。冷艳寒香空自惜，后夜山高月小。满地苍苔，一声哀角，疏影归幽渺。世无和靖，三花两蕊不少。'),
	(9651, 513, '一翦梅（赠紫云友）', '剑倚青天笛倚楼。云影悠悠。鹤影悠悠。好同携手上瀛洲。身在阎浮。业在阎浮。 一段红云绿树愁。今也休休。古也休休。夕阳西去水东流。富又何求。贵又何求。'),
	(9652, 513, '柳梢青（送温守王侍郎帅三山）', '五马风流，销金帐暖，药玉船宽。放下荷囊，携来铜虎，又举熊幡。 棠阴已接三山。此列郡、彼食大藩。柳雪萦旗，东风拦马，父老争看。'),
	(9653, 513, '贺新郎（怀仙楼）', '极目飙尘表。醉酣时、楼中起舞，楼前舒啸。坐见四山烟雾散，是处落花啼鸟。忽惊下、九天星斗。双鹤飞来风露爽，一声声、清唳苍松杪。奈对景，不酾酒。 旧家三点蓬莱小。有琼台双阙，长是香花缭绕。铁笛夜吹金剑吼，恨此瀛洲路杳。知几度、琪林春老。闲倚朱阑思昨梦，对江山、感慨无人晓。但千里，月华皎。'),
	(9654, 513, '贺新郎', '遥想阳明洞。夜深时、猿啼鹤唳，露寒烟重。家在神霄归未得，十二玉楼无梦。梦里听、瑶琴三弄。醉卧长安人不识，晚秋天、此意西风共。黄金印，吾何用。 云衢高策青鸾革空。把天书玉篆，留与世人崇奉。垂手入廛长是醉，醉则从教懵懂。那些子、凝然不动。一剑行空神鬼惧，金粟儿、日向丹田种。把得稳，任放纵。'),
	(9655, 513, '贺新郎（再送前人）', '风雨今如此。问行人、如何有得，许多儿泪。为探木犀开也未，只有芙蓉而已。九十日、秋光能几。千里送人须一别，却思量、我了思量你。去则是，住则是。 归归我亦行行矣。便行行、不须回首，也休萦系。一似天边双鸣雁，一个飞从东际。那一个、又飞西际。毕竟人生都是梦，再相逢、除是青霄里。却共饮，却共醉。'),
	(9656, 513, '贺新郎', '且尽杯中酒。问平生、湖海心期，更如君否。渭树江云多少恨，离合古今非偶。更风雨、十常八九。长铗歌弹明月堕，对萧萧、客鬓闲携手。还怕折，渡头柳。 小楼夜久微凉透。倚危阑、一池倒影，半空星斗。此会明年知何处，苹末秋风未久。漫输与、鹭朋鸥友。已办扁舟松江去，与鲈鱼、莼菜论交旧。因念此，重回首。'),
	(9657, 513, '永遇乐', '懒散家风，清虚活计，与君说破。淡酒三杯，浓茶一碗，静处乾坤大。倚藤临水，步＿登山，白日只随缘过。自归来，曲肱隐几，但只恁和衣卧。 柴扉草户，包巾纸袄，未必有人似我。我醉还歌，我歌且舞，一恁憨痴好。绿水青山，清风明月，自有人间仙岛。且偎随、补破遮寒，烧榾柮火。'),
	(9658, 513, '满江红', '钧天高处，元自有、琼楼玉阙。又那更、九霞隐映，五云斗绝。八面玲珑光不夜，四围晃耀寒如月。有广寒、宫殿隐姮娥，冰壶洁。 飘飘去，天风冽。星河外，花飞雪。见三千神女，尊前一阕。来到人间浑似梦，未能归去空悲咽。问仙都、此去几由旬，归心热。'),
	(9659, 513, '水调歌头（石知院生辰）', '两鬓青丝发，双眼黑方瞳。人皆道是，昭庆一个老仙翁。暂别蓬莱弱水，自把星冠月帔，玉佩舞薰风。醉入桃源路，归去不知踪。 举云璈，鸣铁笛，抚丝桐。满前剑弁森列，稽首捧金钟。挺挺松形鹤貌，任待桑田变海，宝鼎粒丹红。玉帝下明诏，独骑上瑶空。'),
	(9660, 513, '菊花新', '十二楼台，但前回旧迹。想琪花似雪，忘了还思。朝暮痴痴地。只有老天知。却自省，玉阶金砌。错抛离。 梧桐声颤，窗外草蛩吟细。醉魂觉，又听秋鸿悲呖。极目寒空，叹未有紫云梯。绛阙消息子。也无一二、枉垂涕。'),
	(9661, 513, '菊花新', '渺渺烟霄风露冷，夜未艾、凉蟾似水。海山外、五云散彩，三峰凝翠。一鹤横空何缥渺，见殿阁、笙歌拥罗绮。笑劳生，空如尺晏鸟，恋槿花篱。 于中青鸾唱美。丹鹤舞奇。有粉娥琼女，齐捧芳卮。天真皇人陈玳席，宴太姥、思之暗生悲。念如今，红尘满面，温洒晚风泪。'),
	(9662, 513, '水调歌头（咏茶）', '二月一番雨，昨夜一声雷。枪旗争展，建溪春色占先魁。采取枝头雀舌，带露和烟捣碎，炼作紫金堆。碾破香无限，飞起绿尘埃。 汲新泉，烹活火，试将来。放下兔毫瓯子，滋味舌头回。唤醒青州从事，战退睡魔百万，梦不到阳台。两腋清风起，我欲上蓬莱。'),
	(9663, 513, '沁园春（题桃源万寿宫）', '黄鹤楼前，吹笛之时，先生朗吟。想剑光飞过，朝游南岳，墨篮放下，夜醉东邻。铛煮山川，粟藏世界，有明月清风知此音。呵呵笑，笑酿成白酒，散尽黄金。 知音。自有相寻。休踏破葫芦折断琴。唱白苹红蓼，庐山日暮，西风黄叶，渭水秋深。三入岳阳，再游湓浦，自一去优游直至今。桃源路，尽不妨来往，时共登临。'),
	(9664, 513, '沁园春', '岁去年来，思量人生，空自沈埋。既这回冬至，一阳来复，便须修炼，更莫疑猜。好个鼎炉，见成铅汞，片晌工夫结圣胎。人身里，三千世界，十二楼台。 周年造化安排。只在这些些真妙哉。要先擒日月，后攒星斗，黄庭中畔，化作琼瑰。谁会天机，分明说破，恰似江头雪里梅。丹成后，做些功行，归去蓬莱。'),
	(9665, 513, '沁园春（赞吕公）', '渭水秋深，溢江春老，洞庭一湖。问城南古树，如今在否，洛中狂客，还更来无。独上君山，渺观岩石，八百里鲸波泛巨区。何曾错，有茶中上灶，酒里仙姑。 终须。度了肩吾。稽首终南钟大夫。日太平寺里，题诗去后，东林沈宅，大醉归欤。天上筵多，人间到少，更不向庐山索鳜鱼。如何好，好借君黄鹤，上我清都。'),
	(9666, 213, '琐窗寒（怀旧寄林七膳部）', '露漏沈沈，洞房灯悄，鹊翻庭树。夜凉如水，人倚玉箫何处。澹纵横、疏星断河，点衣黄叶飞四五。向此时感旧，非关宋玉，悲秋情绪。 追念章台路。共缓辔芳尘，妒花惹絮。旧游梦寐，总付相思新句。想风流还在匆匆，暗惊鬓底霜几缕。凭危栏、立尽归鸿，脆角凝清曙。'),
	(9667, 213, '前调（寿湛庐先生）', '鹊喜花间晓。惜凝香、代将帘卷，海棠开早。前度登楼清啸月，吹入春风不老。后五日、花朝方到。趁舞罗衣花讯暖，捻吟髭、玉勒迎东笑。留肯往，春邮小。 庐峰青里壶天好。一千春、棋声昼永，剑光云表。绛雪骊珠看丹转，金鼎龙盘虎绕。且未可、飞仙蓬岛。河洛烟芜眠狐兔，握风雪、办此升平了。却共我，拾瑶草。'),
	(9668, 213, '阑干万里心（寓忆王孙）', '小楼柳色未春深。湘月牵情入苦吟。翠袖风前冷不禁。怕登临。几曲阑干万里心。'),
	(9669, 213, '花自落（寓谒金门）', '春寂寞。帘底蕙炉烟薄。听尽归鸿书怎托。相思天一角。 象笔鸾笺闲却。秀句与谁商略。睡起愁怀何处著。无风花自落。'),
	(9670, 213, '山渐青（寓长相思）', '山无情。水无情。杨柳飞花春雨晴。征衫长短亭。 拟行行。重行行。吟到江南第几程。江南山渐青。'),
	(9671, 213, '钓船笛（寓好事近）', '载酒岳阳楼，秋入洞庭深碧。极目水天无际，正白苹风急。 月明不见宿鸥惊，醉把玉阑拍。谁解百年心事，恰钓船横笛。'),
	(9672, 695, '醉蓬莱（寿史帅）', '政槐云浓翠，榴火殷红，暑风凉细。紫府神仙，向人间游戏。瑞节珠幢，琼缨宝佩，炯冰壶标致。经济规模，登庸衣钵，家传如此。 礼乐醇儒，诗书元帅，尽洗凡踪，平吞余子。敬简堂深，且从容一醉。庆祉绵绵，功名衮衮，比衡山湘水。更把阳和，从头付与，满门桃李。'),
	(9673, 695, '菩萨蛮', '池落开遍莲房老。秋声已入梧桐表。葵扇与桃笙。尚宜相带行。 危亭三百尺。爽气真堪挹。沦茗且盘旋。翩翩吾欲仙。'),
	(9674, 695, '谒金门', '风雨后。枝上绿肥红瘦。乐事参差团不就。一春如病酒。 楼外暖烟杨柳。忆得年时携手。燕子双双来未久。颇知人意否。'),
	(9675, 695, '木兰花慢（寿）', '政胡尘满野，问谁与、作坚城。有老子行年，平头六十，无限声名。向来试陈大略，便群儿、啁哳耳边鸣。争识规模先定，破羌终属营平。 吾心惟有忠诚。羞媚妩，做逢迎。谓干戈锋镝，动关民命，此不宜轻。听渠自分勇怯，奈何他、天理若持衡。只把从前不杀，也应换得长生。'),
	(9676, 695, '水龙吟', '晴江衮衮东流，为谁流得新愁去。新愁都在，长亭望际，扁舟行处。歌罢翻香，梦回呵酒，别来无据。恨荼＿吹尽，樱桃过了，便只恁、成孤负。 须信情钟易感，数良辰、佳期应误。才高自叹，彩云空咏，凌波谩赋。团扇尘生，吟笺泪渍，一觞慵举。但丁宁双燕，明年还解，寄平安否。'),
	(9677, 695, '乳燕飞（次徐斯远韵寄稼轩）', '兴泼元同宇。唤君来、浮君大白，为君起舞。满袖斑斑功名泪，百岁风吹急雨。愁与恨、凭谁分付。醉里狂歌空漫触，且休歌、只倩琵琶诉。人不语，弦自语。 诗成更将君自赋。渺楼头、烟迷碧草，云连芳树。草树那能知人意，怅望关河梦阻。有心事、笺天天许。绣帽轻裘真男子，政何须、纸上分今古。未办得，赋归去。'),
	(9678, 695, '沁园春（寿）', '六月云初，人争议公，公无阻伤。记传飞急羽，舟还海道，弥漫白水，路入沙场。万姓三军，倚公为命，法有逗遛公自当。君还信，似崔嵬砥柱，屹立瞿塘。 此行阴德难量。到论定才知滋味长。看鱼肥蟹健，妻孥共乐，酒火余稻熟，翁媪相将。何以报公，祝公千岁，多少人家烧夜香。凌烟上，更声名凛凛，冠剑堂堂。'),
	(9679, 113, '摸鱼儿（郫县宴同官）', '倚南墙、几回凝伫。绿筠冉冉如故。帝城景色缘何事，一半花枝风雨。收听取。这气象精神，则要人来做。当留客处。且遇酒高歌，逢场戏剧，莫作皱眉事。 那个是，紫佩飞霞仙侣。骎骎云步如许。清闲笑我如鸥鹭。不肯对松觅句。萍散聚。又明月、还寻锦里烟霞路。浮名自误。待好好归来，携筒载酒，同访子云去。'),
	(9680, 113, '清平乐（寿吴毅夫）', '梅霖未歇。直透菖华节。荔子才丹栀子白。抬贴诞弥嘉月。 峨冠蝉尾＿＿。整衣鹤骨＿＿。闻道彩云深处，新添弄玉吹箫。'),
	(9681, 113, '满江红', '嘉定甲申之秋，七月良夜，梦归家山，过鹤林之下。见老鹤翩跹，从西南来，方瞳而朱顶，玉立而长身，其色内白，其气孔神，殆类有道者、既寤，作而曰：“岂洁庵老仙诞日之祥耶？”遂书此梦，演成满江红一阕，为斯文寿。 梦绕家山，曾访问、鹤林遗迹。见老鹤、翩跹飞下，方瞳如漆。蕙帐香消形色静，玉笙吹彻丰神逸。梦醒来、忽记鹤归时，翁生日。 南陌杖，东山屐。红楼酒，青霄笛。料中梁何似，涪江今夕。君水见洛阳耆英会，花前雅放时闲适。独北都、留守未归来，七十一。'),
	(9682, 239, '菩萨蛮', '黄鸡晓唱玲珑曲。人生两鬓无重绿。官柳系行舟。相思独倚楼。 来时花未发。去后纷如雪。春色不堪看。萧萧风雨寒。'),
	(9683, 239, '蝶恋花', '翠浪蓝光新雨后。整整斜斜，高下笼窗牖。万斛深倾重碧酒。量愁知落何人手。 栊雾梳烟晴色透。照影回风，一段嫣然秀。白下门东空引首。藏鸦枝叶长怀旧。'),
	(9684, 239, '丁香结', '烟湿高花，雨藏低叶，为谁翠消红陨。叹水流波迅。抚艳景、尚有轻阴余润。乳莺啼处路，思归意、泪眼暗忍。青青榆荚满地，纵买闲愁难尽。 勾引。正记著年时，乍怯春寒阵阵。小阁幽窗，残妆剩粉，黛眉曾晕。迢递魂梦万里，恨断柔肠寸。知何时重见，空为相思瘦损。'),
	(9685, 239, '浣沙溪', '刻样衣裳巧刻缯。彩枝环绕万年藤。生香吹透＿蚕冰。 嫩水带山娇不断，湿云堆岭腻无声。香肩婀娜许谁凭。'),
	(9686, 239, '浣沙溪', '面面虚堂水照空。天然一朵玉芙蓉。千娇百媚语惺忪。 未散娇云轻＿鬓，欲融轻雪乍凝胸。石榴裙衩为谁红。'),
	(9687, 239, '浣沙溪', '无数流莺远近飞。垂杨袅袅弄晴晖。断肠声里送春归。 鬓影空思香雾湿，袜尘还想步波微。去年花下酒阑时。'),
	(9688, 239, '少年游', '东风无力扬轻丝。芳草雨余姿。浅绿还池，轻黄归柳，老去愿春迟。 栏干凭暖慵回首，闲把小花枝。怯酒情怀，恼人天气，消瘦有谁知。'),
	(9689, 382, '南乡子（德清舟中和老人韵）', '霜月冷婷婷。夹岸芦花雪点成。短艇水晶宫里系，闲情。谁道芙蓉更有城。 阿鹊数归程。人倚低窗小画屏。莫恨年华飞上鬓，堪凭。一度春风一度莺。'),
	(9690, 382, '念奴娇（敬借老人灯韵为寿）', '光风霁月，信行窝到处，人间天上。一笑唤回新造化，满眼翠舒红放。脑后功名，脚跟富贵，梦断春旗仗。辐巾萧散，任他虮虱龙象。 正是杨柳初眠，海棠半睡，锦绣天开障。鹤骨松筋年望八，得醉不妨澜浪。节过烧灯，时催修禊，迎面韶华荡。宗文扶著，问翁马首何向。'),
	(9691, 382, '贺新郎（寿成都孙宰）', '露洗秋光透。指岷峨、无边峭碧，与君为寿。万里同随琴鹤到，只愿人情长久。尽头白、眼青如旧。从臾功名三尺剑，倚函关、风雨蛟龙吼。谈笑取，印如斗。 从今尽展眉峰皱。看诸郎、翩翩黄甲，班班蓝绶。一族孙枝扶膝下，翠竹碧梧争秀。便嘉庆、图中都有。花影婆娑清昼永，护新凉、更著丝簧手。欢未尽，剩添酒。'),
	(9692, 382, '沁园春（用周潜夫韵）', '秋气悲哉，薄寒中人，皇皇何之。更黄花吹雨，苍苔滑屐，栏空斗鸭，床老支龟。静里跫音，明边眉睫，蹴踏星河天脱革几。清谈久，顿两忘妍丑，嫫姆西施。 濂溪家住江湄。爱出水芙蓉清绝姿。好光风霁月，一团和气，尸居龙见，神动天随。著察工夫，诚存体段，个里语言文字非。君家事，莫空将太极。散打图碑。'),
	(9693, 57, '鹧鸪天（发舟安康，朋游见留，往复三用韵）', '芳草萋萋入眼浓。一年花事又匆匆。吐舒桃脸今朝雨，零落梅妆昨夜风。 云接野，水连空。画栏十二倚谁同。两眉新恨无分付，独立苍苔数落红。'),
	(9694, 57, '长相思（西湖夜醉）', '湖山横。湖水平。买个湖船一叶轻。傍湖随柳行。 秋风清。秋月明。谁捣秋砧烟外声。悲秋无尽情。'),
	(9695, 57, '念奴娇（次人韵）', '断虹开霁，净秋容、点点初收微雨。夕下生阴山影澹，缥渺烟云吞吐。乌帽风偏，青鞋沙软，误入桃溪坞。多情鸥鹭，偶来忽又飞去。 日暮修竹佳人，雾绡琼佩，绰约疑仙侣。愧我禅心春尽絮，不逐东风飞舞。红叶题诗，紫云传恨，密意渠能诉。此情疏隔，不关楼外烟树。'),
	(9696, 57, '满江红（双头莲）', '一柄双花，低翠盖、呈祥现美。人正在、薰风亭上，满襟如水。二陆比方夸俊少，两乔相并修容止。雨初晴、午永斗红酣，真奇耳。 双白鹭，双赪鲤。飞与泳，俱来此。绾双鬟天上，侍香童子。双剑丰城双孕秀，双凫叶县双趋起。谩空谈、国士本无双，今双矣。'),
	(9697, 57, '松江哨遍', '归帆征棹，相望于黄芦烟草之际。去来乎桥之左右者，若非人世，极画工之巧所莫能形容。每来维舟，未尝即去，徜徉延伫，意尽然后行。至欲作数语以状风景胜概，辞不意逮，笔随句阁，良可慨叹。已未冬，自云阳归闽。腊月望后一日，漏下二鼓，舣舟桥西，披衣登垂虹。时夜将半，雪月交辉。水天一色，顾影长啸，不知身之寄于旅。返而登舟，谓偕行者周生曰：佳哉斯景也，讵可无乐乎？于是相与破霜蟹，斫细鳞，持两螯，举大白，歌赤壁之赋。酒酣乐甚。周生请曰：今日之事，安可无一言以识之？余曰：然。遂＿括坡仙之语，为哨遍一阕，词成而歌之。生笑曰：以公之才，岂不能自寓意数语，而乃缀缉古人之词章，得不为名文疵乎？余曰：不然。昔坡仙盖尝以靖节之词寄声乎此曲矣，人莫有非之者。余虽不敏，不敢自亚于昔人。然捧心效颦，不自知丑，盖有之矣。而寓于言之所乐，则虽贤不肖抑何异哉。今取其言之足以寄吾意者，而为之歌，知所以自乐耳，子何哂焉 木叶尽凋，湖色接天，雪月明江水。凌万顷、一苇纵所之。若凭虚驭风仙子。听洞箫、绵延不绝如缕，余音袅袅游丝曳。乃举酒赋诗，玉鳞霜蟹，是中风味偏美。任满头堆絮雪花飞。更月澹篷窗冻云垂。山郁苍苍，桥卧沈沈，夜鹊惊起。 噫。倚兰桨兮。我今恍惚遗身世。渔樵甘放浪，蜉蝣然、寄天地。叹富贵何时。功名浪语，人生寓乐虽情尔。知逝者如斯。盈虚如彼，则知变者如是。且物生宇宙各有司。非已有纤毫莫得之。委吾心、耳目所寄。用之而不竭，取则不吾禁，自色自声，本非有意。望东来孤鹤缟其衣。快乘之、从此仙矣。'),
	(9698, 84, '沁园春（戊辰岁寿攻愧舅）', '台色齐辉，一点长庚，夜来更明。渐日添宫线，功催补衮，春回梅萼，香趁调羹。鹤禁班高，槐庭恩重，八帙骎骎人共荣。谁知道，纵身居公辅，心似书生。 东楼见说初成。有帘卷江山万里横。想高情长羡，碧云出处，清时未计，绿野经营。东阁郎君，南宫进士，管领孙枝扶寿觥。齐眉醉，笑尊前乐事，真个全并。'),
	(9699, 84, '鹊桥仙（寿谢法□）', '槐阴闷暑，荷风清梦，满院双成俦侣。阶庭一笑玉兰新，把酒更、重逢初度。 丹书漫启，青云垂上，莫忘八篇奇语。功成休驾玉霄云，且长占、赤城佳处。'),
	(9700, 84, '鹊桥仙（菊）', '寒丛弄日，宝钿承露，篱落亭亭相倚。当年彭泽未归来，料独抱、幽香一世。 疏风冷雨，澹烟残照，日日重阳天气。帽檐已是半欹斜，问瓮里、新篘熟未。'),
	(9701, 84, '菩萨蛮', '烛房花幌参差见。疏帘镇日萦愁眼。巫峡小山屏。梦云犹未成。 带霜边雁落。双字宫罗薄。二十四阑干。夜来相对寒。'),
	(9702, 84, '木兰花慢', '汀莲凋晚艳，又苹末、起秋风。漫搔首徐吟，微云河汉，疏雨梧桐。飘零倦寻酒笺，记那回、歌管小楼中。玉果蛛丝暗卜，钿钗蝉鬓轻笼。 吴云别后重重。凉宴几时同。纵人间信有，犀灵鹊喜，密意难通。双星分携最苦，念经年、犹有一相逢。寂寞桥边旧月，可堪频照西东。'),
	(9703, 84, '水龙吟（淮西重午）', '会昌湖上扁舟，几年不醉西山路。流光又是，宫衣初试，安榴半吐。千里江山，满川烟草，薰风淮楚。念离骚恨远，独醒人去，阑干外，谁怀古。 亦有鱼龙戏舞。艳晴川、绮罗歌鼓。乡情节意，尊前同是，天涯羁旅。涨渌池塘，翠阴庭院，归期无据。问明年此夜，一眉新月，照人何处。'),
	(9704, 84, '琴调相思引', '陆续鸣鸠呼晓晴。霏微残雾湿春城。未成梅雨，先做麦寒轻。 长日愔愔花又落，短屏曲曲酒初醒。小舟无绪，闲带牡丹行。'),
	(9705, 84, '乌夜啼', '段段寒沙浅水，萧萧暮雨孤篷。香罗不共征衫远，砧杵客愁中。 别恨慵看杨柳，归期暗数芙蓉。碧梧声到纱窗晓，昨夜几秋风。'),
	(9706, 84, '清平乐（申中吴对雪）', '朔风凝沍。不放云来去。稚柳回春能几许。一夜满城飞絮。 羊羔酒面频倾。护寒香缓娇屏。唤取雪儿对舞，看他若个轻盈。'),
	(9707, 84, '画堂春', '柳塘风紧絮交飞。漾花一水平池。暖香飘径日迟迟。何处酴醿。 胡蝶梦中寒浅，杜鹃声里春归。镜容不似旧家时。羞对清溪。'),
	(9708, 84, '西江月', '燕掠晴丝袅袅，鱼吹水叶粼粼。禁街微雨洒香尘。寒食清明相近。 漫著宫罗试暖，闲呼社酒酬春。晚风帘幕悄无人。二十四番花讯。'),
	(9709, 681, '满江红（和虞土胥·惠生日）', '月上南箕，还认得、去年星历。知谁把、一天星象，荡摩朝昔。若使平生浑自弃，如今老大何嗟及。更年来、偏得钝工夫，蹉跎力。 溪瘴碍，蛮烟隔。穹壤断，江山窄。纵燕巾滥宝，楚山囚玉。小小穷通都未问，忍闻同气相煎急。诵虞郎、百字短长诗，忧何极。'),
	(9710, 681, '喜迁莺（即席次韵南叔兄同亲友饯王万里万回宣幕）', '鬓霜盈握。叹刍牧荒墟，稻粱衰索。落日牛羊，晚云鸿雁，傍地飞空无托。牧人困和雨睡，田父醉连云酌。醉梦未醒，虎嗥川谷，麕惊林薄。 离别。谁不恶。心事同时，都不论离合。眼底时几，鼻端人物。谁辨北征东略。最怜世途局趣，只道书生疏阔。无可赠君，松阴庭院，菊华篱落。'),
	(9711, 681, '贺新郎（次韵费五十九丈□□题秋山阁有感时事）', '霞下天垂宇，倚阑干、月华都在，大明生处。扶木元高三千丈，不分闲云无数。谩转却、人间朝暮。万古兴亡心一寸，只涓涓、日夜随流注。奈与世，不同趣。 齐封冀甸今何许。百年间、欲招不住，欲推不去。闸断河流障海水，未放游鱼甫甫。叹多少、英雄尘土。挟客凭高西风外，问举头、还见南山否。花烂熳，草蕃蔗。'),
	(9712, 681, '南柯子（即席次韵张太傅方为叔母生日赋）', '暮雨收尘马，薰风起箨龙。夜凉人锁武成宫。却忆亲旁、寿饼荐油葱。 谁锡诗人类，应晞颖谷封。儿行虽远母心通。触拨今宵、梦逐彩云东。'),
	(9713, 681, '鹊桥仙（七夕之明日载酒李彭州＿家即席赋）', '银潢濯月，金茎团露，一日清于一日。昨宵云雨暗河桥，似划地、不如今夕。 乘查信断，＿机人去，误了桥边消息。天孙问我巧何如，正为怕、不曾陈乞。'),
	(9714, 681, '浣溪沙', '试问伊谁若是班。二乔铜雀锁孱颜。千年痕露尚余潸。 羞向眼前供妩媚，独于静处惬幽娴。人情多少逐河间。'),
	(9715, 681, '卜算子（李季允曾为白芙蕖赋卜算子至是久旱得雨借前韵有赋）', '风雨满空霏，总得江山妙。洗出湖光镜似明，不受纤尘＿。 心事竟堪凭，天意真难料。呼吸丰年顷刻间，也合轩渠笑。'),
	(9716, 681, '临江仙（再和四年前遂宁所赋韵）', '一点阳和浑在里，时来尔许芳妍。春风吹上醉痕边。隽欢欺浅酌，清晤失佳眠。 聊把繁华开笑口，须臾雨送风般。因花识得自家天。炯然长不夜，活处欲生烟。'),
	(9717, 681, '浪淘沙（刘左史光祖之生正月十日李夫人之生以十九日赋两词寄之）', '老眼静中看。知我其天。纷纷得失了无关。花柳乾坤春世界，著我中间。 世念久阑珊。随寓随安。人情犹望衮衣还。我愿时清无一事，尽使公闲。'),
	(9718, 681, '贺新郎（九日席上呈诸友）', '旧日重阳日。叹满城、阑风去雨，寂寥萧瑟。造物翻腾新机杼，不踏诗人陈迹。都扫荡、一天云物。挟客凭高西风外，暮鸢飞、不尽秋空碧。真意思，浩无极。 餻诗酒帽茱萸席。算今朝、无谁不饮，有谁真得。子美不生渊明老，千载寥寥佳客。无限事、欲忘还忆。金气高明弓力劲，正不堪、回首南山北。谁弋雁，问消息。'),
	(9719, 681, '朝中措（次韵同官约瞻叔兄□□及杨仲博约赏郡圃牡丹并遣酒代劝）', '玳筵绮席绣芙蓉。客意乐融融。吟罢风头摆翠，醉余日脚沈红。 简书绊我，赏心无托，笑口难逢。梦草闲眠暮雨，落花独倚春风。'),
	(9720, 681, '海棠春令（同官约瞻叔兄□□饮于郡圃海棠花下，遣酒代劝）', '东君惯得花无赖。看不尽、冶容娇态。拟傍小车来，又被轻阴绐。 阴晴长是随人改。且特地、留花相待。荣悴故寻常，生意长如海。'),
	(9721, 681, '木兰花慢（许侍郎奕生日十月二十四日）', '记北人骑屋，看龙首、许仲元。自拥节来归，持荷直上，谁与争先。好官到头做彻，些儿欠缺便徒然。我爱庆元龙首，当春不逐时妍。 人生天地两仪间。且住百来年。数初度庚寅，未来甲子，尽自宽闲。太平竟须公等，终不成、造物谩生贤。拓取面前路径，著身常要平宽。'),
	(9722, 681, '鹧鸪天（别许侍郎奕即席赋）', '公在春官我已归。公来东蜀我居西。及公自遂移潼日，正我由潼使遂时。 如有碍，巧相违。人生禁得几分飞。只祈彼此身长健，同处何曾有别离。'),
	(9723, 681, '朝中措（和刘左史光祖人日游南山追和去春词韵）', '天公只解作丰年。不相冶游天。小队春旗不动，行庖晚突无烟。 吟须捻断，寒炉拨尽，雁自天边。唤起主人失笑，寒灰依旧重然。||'),
	(9724, 681, '摸鱼儿（饯黄侍郎畴若劝酒）', '向江头、几回凝望，垂杨那畔舟才舣。江神似识东归意，故放一篙春水。却总被。三百里人家、祖帐连天起。且行且止。便为汝迟留，三朝两日，如此只如此。 还须看，世上忧端如猬。一枰白黑棋子。肥边瘦腹都闲事。毕竟到头何似。当此际。要默识沈思，一著惺惺地。目前谁是。料当局诸公，＿容缩手，日夜待公至。'),
	(9725, 681, '柳梢青（郡圃新开云月湖约客试小舫）', '撺掇花枝，趱那天气，一半春休。未分真休，平湖新涨，稚绿初抽。 等闲作个扁舟。便都把、湖光卷收。世事元来，都缘本有，不在他求。'),
	(9726, 681, '水调歌头（安大使丙生日）', '人物正寥阔，有美万夫望。七年填拊方面，帷幄自金汤。千尺玉龙衔诏，六尺宝＿照路，载绩满＿常。富贵姑勿道，难得此芬芳。 尝试看，今古梦，几千场。人情但较目睫，谁解识方将。霜落南山秋实，风卷北邻夜燎，世事正匆忙。海内知公者，只顾寿而臧。'),
	(9727, 681, '水调歌头（赵运判师＿生日四月十一日）', '有匪碧岩使，满腹鉴湖秋。不居上界官府，来作散仙游。长珮高冠人伟，组练锦袍官贵，清献旧风流。杓柄长多少，洗尽蜀民愁。 ＿义冠，貂尾案，鹭鸶＿。时来正恐不免，留滞剑南州。帘卷西州风雨，庭伫百城歌鼓，桃李翠云绸。谁谓蜀山远，只在殿山头。'),
	(9728, 681, '水调歌头（妇生朝李＿·□同其女载酒为寿用韵谢之）', '曾向君王说，臣愿守嘉州。风流别乘初届，元在越王楼。湖上龟鱼何事，桥上雁犀谁使，争挽海山舟。便遣旧姻娅，解后作斯游。 晚风清，初暑涨，暮云收。公堂高会，恍疑仙女下罗浮。好是中郎有女，况是史君有妇，同对藕花洲。拟把鹤山月，换却鉴湖秋。'),
	(9729, 680, '八归（重阳前二日怀梅溪）', '楚峰翠冷，吴波烟远，吹袂万里西风。关河迥隔新愁外，遥怜倦客音尘，未见征鸿。雨帽风巾归梦杳，想吟思、吹入飞蓬。料恨满、幽苑离宫。正愁黯文通。 秋浓。新霜初试，重阳催近，醉红偷染江枫。瘦筇相伴，旧游回首，吹帽知与谁同。想萸囊酒盏，暂时冷落菊花丛。两凝＿，壮怀立尽，微云斜照中。'),
	(9730, 680, '雨中花', '旆拂西风，客应星汉，行参玉节征鞍。缓带轻裘，争看盛世衣冠。吟倦西湖风月，去看北塞关山。过离宫禾黍，故叠烟尘，有泪应弹。 文章俊伟，颖露囊锥，名动万里呼韩。知素有、平戎手段，小试何难。情寄吴梅香冷，梦随陇雁霜寒。立勋未晚，归来依旧，酒社诗坛。'),
	(9731, 680, '意难忘（代赠）', '仙子奇容。是名花第一，美占春风。烟香笼浅翠，露靓＿红。怜舞燕，惜惊鸿。相独步吴宫。料认得、娇云媚雨，来自巫峰。 风流正与欢浓。羡高楼并倚，曲影阑东。烛摇留醉枕，尘坠恋歌钟。三弄笛，五花骢。莫行乐匆匆。但看取、天长地久，笑语相逢。'),
	(9732, 680, '太常引', '玉肌轻衬碧霞衣。似争驾、翠鸾飞。羞问武陵溪，笑女伴、东风醉时。 不飘红雨，不贪青子，冷澹却相宜。春晚涌金池，问一片、将愁寄谁。'),
	(9733, 680, '留春令（淮南道中）', '断霞低映，小桥流水，一川平远。柳影人家起炊烟，仿佛似、江南岸。 马上东风吹醉面。问此情谁管。花里清歌酒边情，问何日、重相见。'),
	(9734, 680, '风入松（闻邻女吹笛）', '粉娇曾隔翠帘看。横玉声寒。夜深不管柔荑冷，樱朱度、香喷云鬟。霜月摇摇吹落，梅花簌簌惊残。 萧郎且放凤箫闲。何处骖鸾。静听三弄霓裳罢，魂飞断、愁里关山。三十六宫天近，念奴却在人间。'),
	(9735, 680, '霜天晓角（九日苏堤）', '霜清水碧。冷浸红云湿。休说季伦锦帐，山南岸、更花密。 露滴。空翠幂。两峰开霁色。不为秾妆一醉，西风帽、为谁侧。'),
	(9736, 680, '霜天晓角', '春云粉色。春水和云湿。试问西湖杨柳，东风外、几丝碧。 望极。连翠陌。兰桡双桨急。欲访莫愁何处，旗亭在、画桥侧。'),
	(9737, 680, '思佳客', '翦翠衫儿稳四停。最怜一曲凤箫吟。同心罗帕轻藏素，合字香囊半影金。 春思悄，昼窗深。谁能拘束少年心。莺来惊碎风流胆，踏动樱桃叶底铃。'),
	(9738, 680, '水龙吟（为梦庵寿）', '夜来曾跨青虬，海风袅袅吹襟袖。蓬莱误入，群仙争问，刘郎安否。玉尘冰壶，日庭星角，孕成奇秀。看丹分宝鼎，箓传秘箧，闻重寄、长生酒。 归梦惊回晓漏。正长庚、辉躔南斗。祥开华旦，菊香秋杪，枨黄霜后。笔扫龙蛇，句裁螭锦，俊才谁右。看功勋绣衮，家声再振，数千龄寿。'),
	(9739, 680, '忆秦娥', '栖乌惊。隔窗月色寒于冰。寒于冰。澹移梅影，冷印疏棂。 幽香未觉魂先清。无端勾起相思情。相思情。恼人无睡，直到天明。'),
	(9740, 680, '生查子（木香）', '春笼云润香，露湿青蛟瘦。偷学汉宫妆，舞彻霓裳后。 酥胸紫领巾，冰翦柔荑手。有意入罗囊，不肯成春酒。'),
	(9741, 680, '江城子（代作）', '绿丛篱菊点娇黄。过重阳。转愁伤。风急天高，归雁不成行。此去郎边知近远，秋水阔，碧天长。 郎心如妾妾如郎。两离肠。一思量。春到春愁，秋色亦凄凉。近得新词知怨妾，无处诉，泣兰房。'),
	(9742, 680, '杏花天', '远山学得修眉翠。看眉展、春愁无际。雨痕半湿东风外。不管梨花有泪。 西园路、青鞋暗记。怕行入、秋千径里。一春多少相思意。说与新来燕子。'),
	(9743, 680, '杏花天', '霁烟消处寒犹嫩。乍门巷、愔愔昼永。池塘芳草魂初醒。秀句吟春未稳。 仙源阻、春风瘦损。又燕子、来无芳信。小桃也自知人恨。满面羞红难问。'),
	(9744, 680, '青玉案', '平生似欠西湖债。每拼了、金貂解。妩媚烟云多变态。雕鞍来处，画船归去，花柳春风隘。 玉京相接蓬壶界。入画遥山翠分黛。苏小不来时节改。一堤风月，六桥烟水，鹭约鸥盟在。'),
	(9745, 680, '凤栖梧（湖头即席，长翁同赋）', '西子湖边眉翠妩。魂冷孤山，谁是风烟主。相唤吟诗天欲雨。嫩凉不隔鸥飞处。 移下天孙云锦渚。翠盖牵风，绰约凌波女。清约已成君记取。月明夜半鱼龙舞。'),
	(9746, 680, '金人捧露盘（水仙花）', '梦湘云，吟湘月，吊湘灵。有谁见、罗袜尘生。凌波步弱，背人羞整六铢轻。娉娉袅袅，晕娇黄、玉色轻明。 香心静，波心冷，琴心怨，客心惊。怕佩解、却返瑶京。杯擎清露，醉春兰友与梅兄。苍烟万顷，断肠是、雪冷江清。'),
	(9747, 680, '玉蝴蝶', '唤起一襟凉思，未成晚雨，先做秋阴。楚客悲残，谁解此意登临。古台荒、断霞斜照，新梦黯、微月疏砧。总难禁。尽将幽恨，分付孤斟。 从今。倦看青镜，既迟勋业，可负烟林。断梗无凭，岁华摇落又惊心。想莼汀、水云愁凝，闲蕙帐、猿鹤悲吟。信沈沈。故园归计，休更侵寻。'),
	(9748, 91, '惜黄花（九月七日定兴道中）', '涵秋寒渚，染霜丹树。尚依稀，是来时、梦中行路。时节正思家，远道仍怀古。更对著、满城风雨。 黄花无数。碧云欲暮。美人兮，美人兮、未知何处。独自卷帘栊，谁为开尊俎。恨不得、御风归去。'),
	(9749, 91, '恋绣衾', '吴梅初试涧谷春。夜幽幽、江雁叫云。人正在、孤窗底，被秾愁、醺破醉魂。 雨窗只剩残灯影，伴罗衣、无限泪痕。瘦骨怕、红绵冷，说午时、斗帐夜分。'),
	(9750, 91, '满江红（书怀）', '好领青衫，全不向、诗书中得。还也费、区区造物，许多心力。未暇买田清颍尾，尚须索米长安陌。有当时、黄卷满前头，多渐德。 思往事，嗟儿剧。怜牛后，怀鸡肋。奈棱棱虎豹，九重九隔。三径就荒秋自好，一钱不直贫相逼。对黄花、常待不吟诗，诗成癖。'),
	(9751, 91, '燕归梁', '楚梦吹成树外云。乍雁影斜分。黄花心事一帘尘。但频忆、小腰身。 今宵素壁冰弦冷，怕弹断、沈郎魂。秋衣因甚满愁痕。是干预、几黄昏。'),
	(9752, 91, '过龙门（春愁）', '醉月小红楼。锦瑟箜篌。夜来风雨晓来收。几点落花饶柳絮，同为春愁。 寄信问晴鸥。谁在芳洲。绿波宁处有兰舟。独对旧时携手地，情思悠悠。'),
	(9753, 91, '过龙门', '一带古苔墙。多听寒＿。箧中针线早销香。燕尾宝刀窗下梦，谁翦秋裳。 宫漏莫添长。空费思量。鸳鸯难得再成双。昨夜楚山花簟里，波影先凉。'),
	(9754, 91, '玲珑四犯（京口寄所思）', '阔甚吴天，顿放得、江南离绪多少。一雨为秋，凉气小窗先到。轻梦听彻风蒲，又散入、楚空清晓。问世间、愁在何处，不离澹烟衰草。 簟纹独浸芙蓉影，想凄凄、欠郎偎抱。即今卧得云衣冷，山月仍相照。方悔翠袖，易分难聚，有玉香花笑。待雁来、先寄新词归去，且教知道。'),
	(9755, 91, '湘江静', '暮草堆青云浸浦。记匆匆、倦篙曾驻。渔榔四起，沙鸥未落，怕愁沾诗句。碧袖一声歌，石城怨、西风随去。沧波荡晚，菰蒲弄秋，还重到、断魂处。 酒易醒，思正苦。想空山、桂香悬树。三年梦冷，孤吟意短，屡烟钟津鼓。屐齿厌登临，移橙后、几番凉雨。潘郎渐老，风流顿减，闲居未赋。'),
	(9756, 91, '兰陵王（南湖同碧莲见寄，走笔次韵）', '汉江侧。月弄仙人佩色。含情久，摇曳楚衣，天水空＿染娇碧。文漪簟影织。凉骨时将粉饰。谁曾见，罗袜去时，点点波间冷云积。 相思旧飞＿。谩想像风裳，追恨瑶席。涉江几度和愁摘。记雪映双腕，刺萦丝缕，分开绿盖素袂湿。放新句吹入。 寂寂。意犹昔。念净社因缘，天许相觅。飘萧羽扇摇团白。屡侧卧寻梦，倚阑无力。风标公子，欲下处、似认得。'),
	(9757, 91, '临江仙', '草脚青回细腻，柳梢绿转条苗。旧游重到合魂销。棹横春水渡，人凭赤兰桥。 归梦有时曾见，新愁未肯相饶。酒香红被夜迢迢。莫交无用月，来照可怜宵。'),
	(9758, 91, '蝶恋花', '二月东风吹客袂。苏小门前，杨柳如腰细。胡蝶识人游冶地。旧曾来处花开未。 几夜湖山生梦寐。评泊寻芳，只怕春寒里。今岁清明逢上巳。相思先到溅裙水。'),
	(9759, 91, '青玉案', '蕙花老尽离骚句。绿染遍、江头树。日午酒消听骤雨。青榆钱小，碧苔钱古。难买东君住。 官河不碍遗鞭路。被芳草、将愁去。多定红楼帘影暮。兰灯初上，夜香初炷。犹自听鹦鹉。'),
	(9760, 91, '瑞鹤仙', '杏烟娇湿鬓。过杜若汀洲，楚衣香润。回头翠楼近。指鸳鸯沙上，暗藏春恨。归鞭隐隐。便不念、芳盟未稳。自箫声、吹落云东，再数故园花信。 谁问。听歌窗罅，倚月钩阑，旧家轻俊。芳心一寸。相思后，总灰尽。奈春风多事，吹花摇柳，也把幽情唤醒。对南溪、桃萼翻红，又成瘦损。'),
	(9761, 91, '桃源忆故人（赋桃花）', '明霞烘透春机杼。春在明霞多处。我是有诗渔父。一梦秦天古。 柳枝巷陌深朱户。墙外风流一树。十五年来凝伫。弹尽胭脂雨。'),
	(9762, 91, '庆清朝', '坠絮孳萍，狂鞭孕竹，偷移红紫池亭。余花未落，似供残蝶经营。赋得送春诗了，夏帷撺断绿阴成。桑麻外，乳鸠稚燕，别样芳情。 荀令旧香易冷，叹俊游疏懒，枉自销凝。尘侵谢屐，幽径斑驳苔生。便觉寸心尚老，故人前度谩丁宁。空相误，祓兰曲水，挑菜东城。'),
	(9763, 91, '祝英台近（蔷薇）', '绾流苏，垂锦绶。烟外红尘逗。莫倚莓墙，花气酽如酒。便愁醺醉青虬，蜿蜿无力，戏穿碎、一屏新绣。 谩怀旧。如今姚魏俱无，风标较消瘦。露点摇香，前度翦花手。见郎和笑拖裙，匆匆欲去，蓦忽地、肯留芳袖。'),
	(9764, 91, '于飞乐（鸳鸯怨曲）', '绮翼兼羽兼羽，问谁常借春陂。生愁近渚风微。紫山深，金殿暖，日暮同归。白头相守，情虽定、事却难期。 带恨飞来，烟埋秦草，年年枉梦红衣。旧沙间，香颈冷，合是单栖。将终怨魂，何年化、连理芳枝。'),
	(9765, 91, '杏花天（清明）', '软波拖碧蒲芽短。画桥外、花晴柳暖。今年自是清明晚。便觉芳情较懒。 春衫瘦、东风翦翦。过花卩乌、香吹醉面。归来立马斜阳岸。隔岸歌声一片。'),
	(9766, 91, '风流子', '飞琼神仙客，因游戏、误落古桃源。藉吟笺赋笔，试融春恨，舞裙歌扇，聊应尘缘。遣人怨，乱云天一角，弱水路三千。还因秀句，意流江外，便随轻梦，身堕愁边。 风流休相误，寻芳纵来晚，尚有它年。只为赋情不浅，弹泪风前。想雾帐吹香，独怜奇俊，露杯分酒，谁伴婵娟。好在夜轩凉月，空自团圆。月轩，其号也。'),
	(9767, 91, '南歌子', '采绿随双桨，看山藉一筇。关南桃树几番红。昨夜诗情频在、雨声中。 花径无云隔，苔垣只梦通。旧欢一饷可过从。试觅鸳鸯新杏、简春风。'),
	(9768, 91, '喜迁莺', '月波疑滴。望玉壶天近，了无尘隔。翠眼圈花，冰丝织练，黄道宝光相直。自怜诗酒瘦，难应接、许多春色。最无赖，是随香趁烛，曾伴狂客。 踪迹。谩记忆。老了杜郎，忍听东风笛。柳院灯疏，梅厅雪在，谁与细倾春碧。旧情拘未定，犹自学、当年游历。怕万一，误玉人、夜寒帘隙。'),
	(9769, 91, '玉楼春（赋梨花）', '玉容寂寞谁为主。寒食心情愁几许。前身清澹似梅妆，遥夜依微留月佳。 香迷胡蝶飞时路。雪在秋千来往处。黄昏著了素衣裳，深闭重门听夜雨。'),
	(9770, 91, '东风第一枝（灯夕清坐）', '酒馆歌云，灯街舞绣，笑声喧似箫鼓。太平京国多欢，大＿绮罗几处。东风不动，照花影、一天春聚。耀翠光、金缕相交，苒苒细吹香雾。 羞醉玉、少年丰度。怀艳雪、旧家伴侣。闭门明月关心，倚窗小梅索句。吟情欲断，念娇俊、知人无据。想袖寒、珠络藏香，夜久带愁归去。'),
	(9771, 367, '鹧鸪词（春愁）', '伤时怀抱不胜愁。野水粼粼绿遍洲。满地落花春病酒，一帘明月夜登楼。 明眸皓齿人难得，寒食清明事又休。只是鹧鸪三两曲，等闲白了几人头。'),
	(9772, 367, '念奴娇（清明）', '谁家野菜饭炊香，正是江南寒食。试问春光今几许，犹有三分之一。枝上花稀，柳间莺老，是处春狼藉。新来燕子，尚传晋苑消息。 应记往日西湖，万家罗绮，见满城争出。急管繁弦嘈杂处，宝马香车如织。猛省狂游，恍如昨梦，何日重寻觅。杜鹃声里，桂轮挂上空碧。'),
	(9773, 670, '摊破浣溪沙（杨梅）', '生与真妃姓氏同。家随西子苎萝东。谁道玉肌寒起粟，酒能红。 火齐烧空来上苑，冰浆凝露在西宫。不似荔枝生处远，恨薰风。'),
	(9774, 670, '浣溪沙（戏成寄李叔谦）', '彩笔新题字字香。雁来时候燕空梁。芙蓉无处着秋光。 人远山长言外意，曲传书恨醉时妆。倩谁闲寄水云乡。'),
	(9775, 670, '水调歌头（次韵＿·车寿守）', '一曲笙歌外，四座笑谈清。使君秋霁领客，别乘更宗英。想像亲闱称寿，写就通家情分，相与庆恩荣。欢动桂花发，香雾扑帘旌。 功名事，台阁路，好同登。只今报政归诏，舆论正蜚声。以我文章学术，与国和平安靖，冠剑入明庭。应顾棠阴下，野老鬓星星。'),
	(9776, 670, '生查子（晴色入青山）', '晴色入青山，更见飞花晚。不是不登临，自是心情懒。 试襞小红笺，与写天涯怨。杜宇一声春，楼下沧波远。'),
	(9777, 670, '浣溪沙（涧上昌甫有词）', '闲里相看两鬓秋。酒能沾醉雨能幽。吾庐何幸得翁留。 世路尽教终易与，山林佳话恐难酬。人来人去亦知不。'),
	(9778, 670, '临江仙（周国正生朝）', '寒食清明春事好，公家霁月光风。年时犹记雨声中。州侯陈乐舞，法从酒杯同。 坐上王杨虽自散，酴醿依旧香浓。待将诗易寿无穷。鸢飞鱼跃矣，风虎更云龙。'),
	(9779, 670, '浣溪沙（至日带湖）', '爱日回春一线长。氛氲谁忆御炉香。孤城岁晚卧沧江。 花底千官迎淑气，湖阴十里写晴光。剩拼华发醉为乡。'),
	(9780, 670, '水调歌头（坐间有伤仲至，且怀昌甫，因呈张宰）', '新月已如许，我问带湖梅。人间题句，赢得浮潋酒盈杯。落拓豪英满坐，烂漫风骚连纸，天外凤凰来。只怕轻孤负，莫待巧安排。 空翠滴，寒爽矣，晚佳哉。为君绝倒，折尽千树玉蓓堆。渺渺章泉好在，寂寂卢泉仙去，今古付尊罍。拍手见花木，放眼记莓苔。'),
	(9781, 670, '谒金门（次韵郑婺源）', '行又住。水远山遥村路。把酒问春春几许。老年花似雾。 坐上风流张绪。留我我还难去。却忆章台飞柳絮。只愁萦暮雨。'),
	(9782, 670, '朝中措（梅月圆）', '为兹春酒寿时翁。我辈一尊同。香动梅梢圆月，年年先得东风。 冰溪清浅流环玉，莲幕漫从容。但愿人生长久，挥弦目送飞鸿。'),
	(9783, 670, '江城子', '雪消霜入小溪舟。试浮游。上山头。薄薄寒烟，依旧未全收。问道梅花开也未，吟不尽，一春愁。 襟怀如此老还休。懒凝眸。转深幽。诗罢一眉，新月又如钩。腊后春前村意远，回棹稳，水西流。'),
	(9784, 670, '浣溪沙（次韵昌甫）', '莫问星星鬓染霜。一杯同看月昏黄。放歌渔父濯沧浪。 却忆手栽双柳句，真成云汉抉天章。苏仙何在立苍茫。'),
	(9785, 670, '浣溪沙（醉木犀）', '一曲西风醉木犀。天香吹梦入瑶池。钗横犹记未开枝。 花重嫩舒红笑脸，叶稀轻拂翠颦眉。酒醒残月雁声迟。'),
	(9786, 670, '满庭芳（王寺簿生朝）', '点点淮山，迢迢江水，分明别是风光。地灵人杰，星斗烂文章。初度充闾佳气，当年瑞、应弄珪璋。薰弦奏，凉宵宝月，玉井藕花香。 清真，如逸少，兰亭修竹，曲水流觞。想醉乡日永，地久天长。小驻屏星怀玉，飞凫舄、元在鹓行。功名事，云龙风虎，行矣佩金章。'),
	(9787, 670, '冉冉云（弄花雨）', '倚遍阑干弄花雨。卷珠帘、草迷芳树。山崦里、几许云烟来去。画不就、人家院宇。 社寒梁燕呢喃舞。小桃红、海棠初吐。谁信道、午醉醒时情绪。闲整春衫自语。'),
	(9788, 670, '一丛花（次韵斯远）', '翻空雪浪送飞花。春晓媚霜华。风回点点迷人处，峭寒轻、诗思殊佳。双燕未来，断鸿何在，微雨又天涯。 绮窗明暗是谁家。雕槛馥兰芽。画檐帘幕黄昏后，试倾杯、笑语喧哗。聚散人生，吾侪老矣，醉墨任横斜。'),
	(9789, 670, '西江月（晚春时候）', '闻道晚春时候，暖风是处花飘。游人争渡水南桥。多少池塘春草。 跃马谁联玉勒，钓鱼应泛兰桡。韶光何限不逍遥。输与溪鸥野鸟。'),
	(9790, 670, '踏莎行（七夕词）', '雨意生凉，云容催暮。画楼人倚阑干处。柳边新月已微明，银潢隐隐疏星渡。 今古佳期，漫传牛女。一杯试与寻新句。幽怀冷眼是青山，旧欢往恨浑无据。'),
	(9791, 670, '贺新郎（坐上有举昔人《贺新郎》一词，极壮，酒半用其韵）', '万事佯休去。漫栖迟、灵山起雾，玉溪流渚。击楫凄凉千古意，怅怏衣冠南渡。泪暗洒、神州沉处。多少胸中经济略，气□□、郁郁愁金鼓。空自笑，听鸡舞。 天关九虎寻无路。叹都把、生民膏血，尚交胡虏。吴蜀江山元自好，形势何能尽语。但目尽、东南风土。赤壁楼船应似旧，问子瑜、公瑾今安否。割舍了，对君举。'),
	(9792, 670, '满江红（和赵公明）', '五老峰前，九江上、曾生仙客。相邂逅、贵池亭下，定交金石。我辈风流宜啸咏，官曹尘冗从煎逼。且簿书、丛里举清觞，偷闲日。 同一笑，当今夕。梅正好，香浮白。便相忘尔汝，醉巾沾湿。已向琼楼夸意态，似闻金殿传消息。想君今、归去际风云，应相忆。'),
	(9793, 615, '鹊桥仙（周监旬会上作）', '六人欢笑，六姬讴唱，六博时分胜负。六家盘馔斗芳鲜，恰两月、六番相聚。 特排整整，华筵楚楚，终是不如草具。赏心乐事四时同，又管甚、落花飞絮。'),
	(9794, 615, '渔家傲（用履齐韵赠邵惜惜）', '自古馀杭多俊俏。风流不独夸苏小。又见尊前人窈窕。花枝袅。贪看忘却朱颜老。 曲巷横街深更杳。追欢买笑须年少。悔不从前相识早。心灰了。逢场落得掀髯笑。'),
	(9795, 615, '菩萨蛮（六月十三日，同官携具，以予被荐）', '转头又是清秋近。晚风淅淅凉犹嫩。多谢客携觞。空惭画饼章。 中亭明月可。未要亲灯火。十阅望舒圆。归期在眼前。'),
	(9796, 370, '玉楼春（赠别孟仓使）', '一片江南春色晚。牡丹花谢莺声懒。问君离恨几多长，芳草连天犹觉短。 昨夜溪头新溜满。樽前自起喷龙管。明朝飞棹下钱塘，心共白苹香不断。'),
	(9797, 370, '满庭芳（雨中再赋牡丹）', '云绕花屏，天横练带，画堂三月初三。斜风细雨，罗幕护轻寒。无数天香国色，枝枝带、洛浦嵩山。烧红烛，吞星□日，光射九霞冠。 仙宫，深几许，黄莺问道，紫燕窥帘。似太真姊妹，半醒微酣。须信生来富贵，何曾在、草舍茅庵。皇州近，扁舟载去，春色冠东南。||'),
	(9798, 370, '乳燕飞', '有石楠红叶，飘下樽俎间，小饮而归，久立碧桃花下，即事赋之 策杖南山去。到南溪、谁家宅院，欺人先渡。羽扇徐麾僮仆退，翠柳白沙西路。帝赐我、阆风玄圃。一片飞来红叶阔，细看来、上有双鸾句。应念我，尘中住。 眼前儿女闲相语。怪人间、禁烟时节，安排樽俎。为道从来寒食好，且莫思量今古。共绿水、春风鸥鹭。望我壶天天未晚，记碧桃、花发闲庭户。归到也，对花舞。'),
	(9799, 370, '杏花天（有感）', '美人家在江南住。每惆恨、江南日暮。白苹洲畔花无数。还忆潇湘风度。 幸自是、断肠无处。怎强作、莺声燕语。东风占断＿筝柱。也逐落花归去。'),
	(9800, 370, '水调歌头（客有言存心者，未得其序，因赋）', '欲觅存心法，当自尽心求。此心尽处，豁地知性与天侔。行尽武陵溪路，忽见桃源洞口，渔子舍渔舟。输与逃秦侣，绝境几春秋。 举全体，既尽得，要敛收。勿忘勿助之际，玄牝一丝头。君看天高地下，中有鸢飞鱼跃，妙用正周流。可与知者道，莫语俗人休。'),
	(9801, 370, '念奴娇（寄孟使君）', '龙山高会，忆当时宾主，风流云散。今日紫阳峰顶上，人望旌旗天半。万叠红蕖，千层黄菊，照耀成飞观。西风浩荡，碧天斜去双雁。 遥想健笔淋漓，龙蛇落纸，妙句高张翰。笼日轻霞横数抹，绿水金波相间。目寄宣平，神追师道，归楫摇星汉。城中万户，烛光香雾交贯。'),
	(9802, 370, '沁园春（自题方壶）', '春至伤春，秋至悲秋，谁在华胥。叹谪仙才气，飞扬跋扈，渊明何事，慷慨欷歔。自我少年，如今晚境，行半人间真有余。都休问，且一觞一咏，吾爱吾庐。 南皋境界何如。舍明月清风谁与居。望蓬山路杳，万株翠桧，方壶门掩，四面红蕖。中有佳人，绰如姑射，一炷清香满太虚。尘寰外，被鸣鸾报客，飞鹤传书。'),
	(9803, 370, '乳燕飞（汪子感秋，采楚词，赋此）', '去郢频回首。正横江、荪桡容与，兰旌悠久。怅望龙门都不见，似把长楸孤负。念往日、佳人为偶。独向芳洲相思处，采苹花、杜若空盈手。乘赤豹，谁来后。 云中眼界穷高厚。览山川、冀州还在，陶唐何有。木叶纷纷秋风晚，缥缈潇湘左右。见帝子、冰魂厮守。应记薰弦相对日，酹一杯、太乙东皇酒。问此意，君知否。'),
	(9804, 149, '小重山令（赵郎中谒告迎侍太夫人，将来都下。予喜，为作此曲）', '寒食飞红满帝城。慈乌相对立，柳青青。玉阶端笏细陈情。天恩许，春尽可还京。 鹊报倚门人。安舆扶上了，更亲擎。看花携乐缓行程。争迎处，堂下拜公卿。'),
	(9805, 149, '水龙吟（黄庆长夜泛鉴湖，有怀归之曲，课予和之）', '夜深客子移舟处，两两沙禽惊起。红衣入桨，青灯摇浪，微凉意思。把酒临风，不思归去，有如此水。况茂林游倦，长干望久，芳心事、箫声里。 屈指归期尚未。鹊南飞、有人应喜。画阑桂子，留香小待，提携影底。我已情多，十年幽梦，略曾如此。甚谢郎、也恨飘零，解道月明千里。'),
	(9806, 149, '法曲献仙音', '斋下瞰湖山，光景奇绝。予数过之，为赋此 虚阁笼寒，小帘通月，暮色偏怜高处。树隔离宫，水平驰道，湖山尽入尊俎。奈楚客淹留久，砧声带愁去。 屡回顾。过秋风、未成归计。谁念我、重见冷枫红舞。唤起淡妆人，问逋仙、今在何许。象笔鸾笺，甚而今、不道秀句。握平生幽恨，化作沙边烟雨。'),
	(9807, 149, '月下笛', '与客携壶，梅花过了，夜来风雨。幽禽自语。啄香心、度墙去。春衣都是柔荑翦，尚沾惹、残茸半缕。怅玉钿似扫，朱门深闭，再见无路。 凝伫。曾游处。但系马垂杨，认郎鹦鹉。扬州梦觉，彩云飞过何许。多情须倩梁间燕，问吟袖、弓腰在否。怎知道，误了人，年少自恁虚度。'),
	(9808, 149, '念奴娇（谢人惠竹榻）', '楚山修竹，自娟娟、不受人间袢暑。我醉欲眠伊伴我，一枕凉生如许。象齿为材，花藤作面，终是无真趣。梅风吹溽，此君直恁清苦。 须信下榻殷勤，＿然成梦，梦与秋相遇。翠袖佳人来共看，漠漠风烟千亩。蕉叶窗纱，荷花池馆，别有留人处。此时归去，为君听尽秋雨。'),
	(9809, 149, '霓裳中序第一', '香。又于乐工故书中得商调霓裳曲十八阕，皆虚谱无辞。按沈氏乐律，霓裳道调，此乃商调。乐天诗云：“散序六阕”，此特两阕，未知孰是。然音书闲雅，不类今曲。予不暇尽作，作中序一阕传于世。予方羁游，感此古音，不自知其辞之怨抑也 亭皋正望极。乱落江莲归未得。多病却无气力。况纨扇渐疏，罗衣初索。流光过隙。叹杏梁、双燕如客。人何在，一帘淡月，仿佛照颜色。 幽寂。乱蛩吟壁。动庾信、清愁似织。沉思年少浪迹。笛里关山，柳下坊陌。坠红无信息。漫暗水、涓涓溜碧。漂零久，而今何意，醉卧酒炉侧。'),
	(9810, 149, '浣溪沙（丙辰腊，与俞商卿、銛朴翁同寓新安溪庄舍，得腊花韵甚，赋二首）', '花里春风未觉时。美人呵蕊缀横枝。隔帘飞过蜜蜂儿。 书寄岭头封不到，影浮杯面误人吹。寂寥惟有夜寒知。'),
	(9811, 149, '浣溪沙（辛亥正月二十四日发合肥）', '钗燕笼云晚不忺，拟将裙带系郎船，别离滋味又今年。 杨柳夜寒犹自舞，鸳鸯风急不成眠，些儿闲事莫萦牵。'),
	(9812, 149, '浣溪沙（巳酉岁客吴兴，收灯夜阖户无聊，俞商卿呼之共出，因记所见）', '春点疏梅雨后枝，翦灯心事峭寒时。市桥携手步迟迟。 蜜炬来时人更好，玉笙吹彻夜何其，东风落靥不成归。'),
	(9813, 149, '少年游（戏平甫）', '双螺未合，双蛾先敛，家在碧云西。别母情怀，随郎滋味，桃叶渡江时。 扁舟载了，匆匆归去，今夜泊前溪。杨柳津头，梨花墙外，心事两人知。'),
	(9814, 149, '鬲溪梅令（仙吕调丙辰冬自无锡归，作此寓意）', '好花不与＿香人。浪粼粼。又恐春风归去绿成阴。玉钿何处寻。 木兰双桨梦中云。小横陈。漫向孤山山下觅盈盈。翠禽啼一春。'),
	(9815, 149, '莺声绕红楼', '工吹笛，妓皆以柳黄为衣 十亩梅花作雪飞。冷香下、携手多时。两年不到断桥西。长笛为予吹。 人妒垂杨绿，春风为、染作仙衣。垂杨却又妒腰肢。近平声前舞丝丝。'),
	(9816, 83, '蝶恋花（和人探梅）', '罗幕护寒遮晓雾。爱日烘晴，又是年华暮。潇洒江梅争欲吐。暗香漏泄春来处。 何日寻芳溪畔路。挈＿携筇，写景论心素。千里相逢真曾遇。羡君解道江南句。'),
	(9817, 83, '朝中措', '晓来天气十分凉。时候近重阳。村落人家潇洒。篱菊有芬芳。 年来渐觉，诗肠愈窄，酒量偏狂。好景不须放过，何妨一醉千觞。'),
	(9818, 83, '武陵春（赓何显夫小舟有景）', '红荻黄芦秋已老，妆点楚江头。更有吴姬拨小桡。来往自妖娆。 款款舣舟临别岸，短缆系花梢。料得前身是莫愁。依旧有风流。'),
	(9819, 83, '念奴娇（上巳太守待同官曲水园，因成）', '快风收雨，正江城初霁，物华如许。丽日融和春思好，是处莺啼燕语。嫩绿成阴，落红堆绣，只恐春将暮。园林清昼，看看又见飞絮。 太守无限风流，铃斋多暇，载酒郊原路。几队旌旗光闪烁，鼓吹更翻新谱。曲水流觞，兰亭修禊，俯仰成今古。为君一醉，归时楼上初鼓。'),
	(9820, 83, '诉衷情', '无端风雨送清秋。天气冷飕飕。行人先自离索，直是不禁愁。 思往事，忆前游。泪难收。重阳近也，黄花依旧，谁伴清瓯。'),
	(9821, 83, '踏莎行', '雅淡容仪，温柔情性。偏伊赋得多风韵。明眸剪水玉为肌，凤鞋弓小金莲衬。 相见虽频，欢娱无定。蛮笺写了凭谁问。坚心好事有成时，须教人道都相称。'),
	(9822, 83, '鹧鸪天', '初过清明春昼长。紫红香雾蔼华堂。朱颜阿母逢生旦，彩戏儿孙捧寿觞。 齐祝颂，喜平康。天教两鬓正苍苍。壶中日月应长久，笑看蟠桃几度芳。'),
	(9823, 83, '水调歌头（题蒲圻景星亭上慕容宰）', '华亭新伟观，胜地得高雄。凭栏徒倚要眇，万里景无穷。好是江流萦绕，那更云天舒阔，叠嶂倚晴空。眼界无尽藏，怀抱有清风。 主人贤，开绮席，泛金钟。放怀一笑，许我满酌醉颜红。只恐玺书即下，促起飞凫东去，行作黑头公。还记今朝客，曾待一杯同。'),
	(9824, 83, '踏莎行', '秋色人家，夕阳洲渚。西风催过黄华渡。江烟引素忽飞来，水禽破暝双双去。 奔走红尘，栖迟羁旅。断肠犹忆江南句。白云低处雁回峰，明朝便踏潇湘路。'),
	(9825, 83, '柳梢青', '兰蕙心情，海棠韵度，杨柳腰肢。步稳金莲。手纤春笋，肤似凝脂。 歌声舞态都宜。拚著个、坚心共伊。无奈相思，带围宽尽，说与教知。'),
	(9826, 72, '天仙子', '彩笔恹恹慵赋咏。斗草闲来寻小迳。西园春事只供愁，当好景。成孤另。春又那知人欲病。 洗尽残妆临晚镜。淡玉一团浆水莹。强持檀板近芳樽，云遏定。君须听。低唱月来花弄影。'),
	(9827, 72, '西江月', '素面偏宜酒晕，晓妆净洗啼痕。只疑身是玉梅魂。长为春风瘦损。 冉冉烟生兰渚，娟娟月挂愁村。落花飞絮耿黄昏。又是一番新恨。'),
	(9828, 72, '满庭芳', '浅约鸦黄，轻匀螺黛，故教取次梳妆。减轻琶面，新样小鸾凰。每为花妖玉软，慵封客、斜倚银床。春来病，兰薰半歇，满笐舞衣裳。 悲凉。人事改，三春秾艳，一夜繁霜。似人归洛浦，云散高唐。痛念平生情分，孤负我、临老风光。罗裙在，凭谁留意，去觅反魂香。'),
	(9829, 72, '浣溪沙（留别）', '着意寻芳已自迟。可堪容易送春归。酒阑无奈思依依。 杨柳小桥人远别，梨花深巷月斜辉，此情惟我与君知。'),
	(9830, 72, '临江仙', '长短驿亭南北路，蒙茸醉拥驼裘。雪天行计欠人留。严风催酒醒，微雨替梅愁。 自作小词呵冻写，冷金淡衬银钩。此恨知得几时休。寒云迷洛浦，残梦绕秦楼。'),
	(9831, 72, '霜天晓角', '霜天晓角。梦回滋味恶。酒醒不禁寒力，纱窗外、月华薄。 拥衾思旧约。无情风透幕。惟有梅花相伴，不成是、也吹落。'),
	(9832, 72, '贺新郎（春思）', '院宇重重掩。醉沈沈、亭阴转午，绣帘高卷。金鸭香浓喷宝篆，惊起雕梁语燕。正一架、酴醿开遍。嫩萼梢头舒素脸。似月娥、初试宫妆浅。风力嫩，异香软。 佳人无意拈针线。绕朱阑、六曲徘徊，为他留恋。试把花心轻轻数，暗卜归期近远，奈数了、依然重怨。把酒问春不管。枉教人、只恁空肠断。肠断处，怎消遣。'),
	(9833, 72, '谒金门（次京口赋）', '归不去。船泊早春梅渚。试听玉人歌白＿。行云无觅处。 翦烛写诗无语。漠漠寒生窗户。明日短篷眠夜雨。宝钗留半股。'),
	(9834, 72, '六州歌头（送王玉良）', '万里湖南，江山历历，皆吾旧游。看飞凫仙子，张帆直上，周郎赤壁，鹦鹉汀洲。吸尽西江，醉中横笛，人在岳阳楼上头。波涛静，泛洞庭青草，重整兰舟。 长沙会府风流。有万户娉婷帘玉钩。恨楚城春晚，岸花樯燕，还将客送，不与人留。且唤阳城，更招元结，摩抚之余歌咏休。心期处，算世间真有，骑鹤扬州。'),
	(9835, 72, '六州歌头（赠王禹锡）', '自注铜瓶，作梅花供，尊前数枝。说边头旧话，人生消得，几番行役，问我何之。小队红旗，黄金大印，直待封侯知几时。杯行处，且淋漓一醉，明日东西。 如椽健笔鸾飞。还为写春风陌上词。便平生豪气，销靡酒里，依然此乐，儿辈争知。霜重貂裘，夜寒如水，饮到月斜犹未归。仙山路，有笙簧度曲，声到琴丝。'),
	(9836, 72, '六州歌头（寄辛稼轩）', '古岂无人，可以似吾，稼轩者谁。拥七州都督，虽然陶侃，机明神鉴，未必能诗。常衮何如，羊公聊尔，千骑东方侯会稽。中原事，纵匈奴未灭，毕竟男儿。 平生出处天知。算整顿干坤终有时。问湖南宾客，侵寻老矣，江西户口，流落何之。尽日楼台，四边屏幛，目断江山魂欲飞。长安道，奈世无刘表，王粲畴依。'),
	(9837, 72, '六州歌头（淮西帅李＿和仍为书庙额）', '高皇神武，善驾驭豪英。攘北狄，驱群盗，命天膺，救苍生。奈梦绕沙漠，隔温清，屈和好，召大将，归兵柄，列枢庭。公指汴亲。威已振河洛，不顾身烹。失一时几会，嗟左衽吾民，痛岳家军，孰扶倾。 久沈怨愤，七十载，还复遇，帝王真。表遗烈，锡王号，日照临，激士心。始识安刘，计宁祸，已是忠臣。我乘传，访壁垒，想精明。英气凛然若在，仍题扁，昭揭天恩。笑原头荒草，一死不能春，交怨人神。'),
	(9838, 215, '蓦山溪', '抚莲吟就，檐葡还曾赋。相伴更无花，倦炉熏、日长难度。柔桑叶里，玉碾小芙蕖，生竺国，长闽山，移向玉城住。 池亭竹院，宴坐冰围处。绿绕百千丛，夜将阑、争开迎露。煞曾评论，妖媚胜江梅，香称月，韵宜风，消尽人间暑。'),
	(9839, 215, '如梦令', '野菊亭亭争秀。闲伴露荷风柳。浅碧小开花，谁摘谁看谁嗅。知否。知否。不入东篱杯酒。'),
	(9840, 215, '渔家傲（渔翁）', '拂拂春风生草际。新晴万景供游戏。鸥鹭飞来斜照里。金和翠。分明画出真山水。 遮个渔翁无愠喜。乾坤都在孤篷底。一曲高歌千古意。闲来睡。从教月到花汀外。'),
	(9841, 215, '鹊桥仙（立秋后一夕）', '暑云犹在，澄空欲变，入夜徘徊庭际。新秋知是昨宵来，爱残月、纤纤西坠。 芭蕉老大，流萤衰倦，静里细观天意。轻风未有半分凉，奈人道、今宵好睡。'),
	(9842, 215, '醉高楼（初月）', '浮云散，天似碧琉璃。月正是、上弦时。＿娥蟾兔俱何在，广寒宫殿不应亏。这神功，千万世，有谁知。 甚只解、催人须鬓老。更不算、将人情绪恼。撺掇酒，撼摇诗。山头望伴疏星落，庭前看照好花移。夜无眠，无笑我，怎如痴。'),
	(9843, 215, '江城子（凯旋）', '春风旗鼓石头城。急麾兵。斩长鲸。缓带轻裘，乘胜诗蛮荆。蚁聚蜂屯三十万，军面缚，赴行营。 舳舻千里大江横。凯歌声。犬羊惊。尊俎风流，谈笑酒徐倾。北望旄头今已灭，河汉淡，两台星。'),
	(9844, 215, '鹧鸪天（咏二色葡萄）', '阴阴一架绀云凉。袅袅千丝翠蔓长。紫玉乳圆秋结穗，水晶珠莹露凝浆。 相并熟，试新尝。累累轻翦粉痕香。小槽压就西凉酒，风月无边是醉乡。'),
	(9845, 215, '柳梢青（秋日感兴）', '烟淡波平。蓬松岸蓼，红浅红深。满院西风，连宵良月，几处清砧。 区区宦海浮沈。幸隐去、将酬素心。两鬓吴霜，一屏秦梦，谁是知音。'),
	(9846, 215, '梦游仙', '归兴动，骑鹤下青冥。几点山河浮色界，一簪风露拂寒星。银汉悄无声。 鸾啸舞，仙乐送霓旌。摘得琪花飞散了，却将何物赠仙卿。衣上彩云轻。'),
	(9847, 335, '贺新郎（寄辛潭州）', '梦里骖鸾驭。望蓬莱不远，翩然被风吹去。吹到楚楼烟月上，不记人间何处。但疑是、蓬壶别所。缥缈霓裳天女队，奉一仙、满把流霞举。如唤我，醉中舞 醉醒梦觉知何许。问潇湘今日，谁与主盟樽俎。无限青春难老意，拟倩管弦寄与。待新筑沙堤隐步。万里云霄都历遍，却依前、流水桃源路。留此笔，为君赋。'),
	(9848, 335, '满江红', '典尽春衣，也应是、京华倦客。都不记、麹尘香雾，西湖南陌。儿女别时和泪拜，牵衣曾问归时节。到归来、稚子已成阴，空头白。 功名事，云霄隔。英雄伴，东南拆。对鸡豚社酒，依然乡国。三径不成陶令隐，一区未有扬雄宅。问渔樵、学作老生涯，从今日。'),
	(9849, 335, '水调歌头', '踏碎九街月，乘醉出京华。半生湖海，谁念今日老还家。独把瓦盆盛酒，自与渔樵分席，说伊政声佳。竹马望尘去，倦客亦随车。 听熏风，清晓角，韵梅花。人家十万，说尽炎热与咨嗟。只恐棠阴未满，已有枫宸趣召，归路不容遮。回首江边柳，空著旧栖鸦。'),
	(9850, 639, '贺新郎（又有实告以九月二十七日者，因和叶少蕴缕字韵并寄）', '昵昵骈头语。笑黄花、重阳去也，不成分数。倾国容华随时换，依旧清歌妙舞。苦未冷、都无星暑。恰好良辰花共酒，斗尊前、见在阳台女。朝共暮，定何许。 蓼红徙倚明汀渚。正萧萧、迎风夹岸，淡烟微雨。笃耨龙涎烧未也，好向儿家祝取。是有分、分工须与。以色事人能几好，愿衾稠、无缝休离阻。心一片，丝千缕。'),
	(9851, 639, '临江仙', '五百年间非一日，可堪只到今年。云龙欲花艳阳天。从来耆旧传，不博地行仙。 昨夜风声何处度，典型犹在南山。自怜不结傍时缘。着鞭非我事，避路只渠贤。'),
	(9852, 639, '水龙吟', '钱王霸图成时，多应是百年遗树。羞将高古，为渠遮映，鱼盐调度。且向空山，趁时多事，四垂盘踞。算兴衰坐阅，权奇磊块，世间□、□斤斧。 又见当天明圣，便弹丸、也难分土。一番整顿，旧家草木，新来雨露。铁石心肠，虬龙根干，亭亭天柱。纵茯苓下结，茑罗高际，怎堪攀附。'),
	(9853, 639, '桂枝香', '仙风透骨。向夏叶丛中，春花重出。骏发天香，不是世间尤物。占些空阔闲田地，共霜轮、伴他秋实。浅非冷蕊，深非幽艳，中无倚握。 任点取、龙涎笃耨。儿女子看承，万屈千屈。做数珠见，刻画毋盐唐突。不知几树栾团着，但口吻、非鸣云室。是耶非也，书生见识，圣贤心术。'),
	(9854, 639, '秋兰香', '未老金茎，些子正气，东篱淡伫齐芳。分头添样白，同局几般黄。向闲处、须一一排行。浅深馓间新妆。那陶令，漉他谁酒，趁醒消详。 况是此花开后，便蝶乱无花，管甚蜂忙。你从今、采却蜜成房。秋英诚商量。多少为谁，甜得清凉。待说破，长生真诀，要饱风霜。'),
	(9855, 639, '丑奴儿（咏梅）', '黄昏山驿消魂处，枝亚疏篱。枝亚疏篱。酝藉香风蜜打围。 隔篱鸡犬谁家舍，门掩斜晖。门掩斜晖。花落花开总不知。'),
	(9856, 639, '清平乐', '恼之 银屏绣阁。不道鲛绡薄。嘶骑匆匆尘漠漠。还过夕阳村落。 乱山千叠无情。今宵遮断愁人。两处香消梦觉，一般晓月秋声。'),
	(9857, 639, '最高楼（咏梅）', '春乍透，香早暗偷传。深院落，斗清妍。紫檀枝似流苏带，黄金须胜辟寒钿。更朝朝，琼树好，笑当年。 花不向沈香亭上看。树不著唐昌宫里玩。衣带水，隔风烟。铜华不御凌波处，蛾眉淡扫至尊前。管如今，浑似了，更堪怜。'),
	(9858, 639, '浪淘沙（梅）', '院落晓风酸。春入西园。芳英吹破玉阑干。墙外红尘飞不到，彻骨清寒。 清浅小堤湾。瘦竹团栾。水光疏影有无间。仿佛浣沙溪上见，波面云鬟。'),
	(9859, 639, '踏莎行（怀叶八十推官）', '书册如仇，旧游浑讳。有怀不断人应异。千山上去梦魂轻，片帆似下蛮溪水。 已共酒杯，长坚海誓。见君忽忘花前醉。从来解事苦无多，不知解到毫芒未。'),
	(9860, 639, '垂丝钓（九月七日自寿）', '菊花细雨。萧萧红蓼汀渚。景物渐幽，风致如许。秋未暮。又值吾初度。 看天宇。正澄清欲往，登高未也，红尘当面飞舞。几人吊古。乌帽牢收取。短发还羞觑。遐寿身、近五云深处。'),
	(9861, 639, '念奴娇（送戴少望参选）', '西风带暑，又还是、长途利牵名役。我已无心，君因甚，更把青衫为客。邂逅卑飞，几时高举不露真消息。大家行处，到头须管行得。 何处寻取狂徒，可能著意，更问渠侬骨。天上人间，最好是、闹里一般岑寂。瀛海无波，玉堂有路，稳著青霄翼。归来何事，眼光依旧生碧。'),
	(9862, 639, '水调歌头（和吴允成游灵洞韵）', '人爱新来景，龙认旧时湫。不论三伏，小住便觉凛生秋。我自醉眠其上，任是水流其下，湍激若为收。世事如斯去，不去为谁留。 本无心，随所寓，触虚舟。东山始末，且向灵洞与沈浮。料得神仙窟穴，争似提封万里，大小几琉球。但有君才具，何用问时流。'),
	(9863, 639, '瑞云浓慢（六月十一日寿罗春伯）', '蔗浆酪粉，玉壶冰醑，朝罢更闻宣赐。去天咫尺，下拜再三，幸今有母可遗。年年此日，共道月入怀中最贵。向暑天，正风云会遇，有恁嘉瑞。 鹤冲霄，鱼得水。一超便、直入神仙地。植根江表，开拓两河，做得黑头公未。骑鲸赤手，问如何、长鞭尺＿。向来王谢风流，只今管是。'),
	(9864, 639, '三部乐（七月送丘宗卿使虏）', '小屈穹庐，但二满三平，共劳均佚。人中龙虎，本为明时而出。只合是、端坐王朝，看指挥整办，扫荡飘忽。也持汉节，聊过旧家宫室。 西风又还带暑，把征衫著上，有时披拂。休将看花泪眼，闻弦□骨。对遗民、有如皎日。行万里、依然故物。入奏几策，天下里、终定于一。'),
	(9865, 639, '贺新郎（同刘元实唐兴正陪叶丞相饮）', '修竹更深处。映帘栊、清阴障日，坐来无暑。水激泠泠如何许。跳碎危栏玉树。都不系、人间朝暮。东阁少年今老矣，况樽中有酒嫌推去。犹著我，名流语。 大家绿野陪容与。算等闲、过了薰风，又还商素。手弄柔条人健否。犹忆当时雅趣。恩未报、恐成辜负。举目江河休感涕，念有君如此何愁虏。歌未罢，谁来舞。'),
	(9866, 569, '卜算子（立石道中）', '晴日敛春泥，陌上东风软。料峭寒禁花柳闲，枉恨春工浅。 绿涨一江深，黛泼千山远。目断平芜无际愁，数尽征鸿点。'),
	(9867, 569, '南乡子（尹先之索净圆子词）', '元夜景忧殊。万斛金莲照九衢。鎚拍豉汤都卖得，争如。甘露杯中万颗珠。 应是著工夫。脑麝浓薰费小厨。不比七夕黄蜡做，知无。要底圆儿糖上浮。'),
	(9868, 569, '西江月（同蔡受之、赵中甫巡城，饮于南楚楼）', '淼淼澄清波面，依依紫翠山光。危栏徙倚对斜阳。山影波流荡漾。 世事一番醒醉，人生几度炎凉。高情收拾付觥觞。何止羲皇人上。'),
	(9869, 569, '双头莲令（信丰双莲）', '太平和气兆嘉祥。草木总成双。红苞翠盖出横塘。两两斗芬芳。 干摇碧玉并青房。仙髻拥新妆。连枝不解引鸾皇。留取映鸳鸯。'),
	(9870, 569, '武陵春（信丰揖翠阁）', '乍雨笼晴云不定，芳草绿纤柔。燕语莺啼小院幽。春色二分休。 试凭危栏凝远目，山与水光浮。滚滚闲愁逐水流。流不尽、许多愁。'),
	(9871, 569, '醉桃源', '杜鹃花发映山红。韶光觉正浓。水流红紫各西东。绿肥春已空。 闲戏蝶，懒游蜂。破除花影重。问春何事不从容。忧愁风雨中。'),
	(9872, 569, '扑蝴蝶', '清和时候，薰风来小院。琅玕脱箨，方塘荷翠毡。柳丝轻度流莺，画栋低飞乳燕。园林绿阴初遍。 景何限。轻纱细葛，纶巾和羽扇。披襟散发，心清尘不染。一杯洗涤无余，万事消磨去远。浮名薄利休羡。'),
	(9873, 569, '朝中措（月季）', '开随律琯度芳辰。鲜艳见天真。不比浮花浪蕊，天教月月常新。 蔷薇颜色，玫瑰态度，宝相精神。休数岁时月季，仙家栏槛长春。'),
	(9874, 569, '永遇乐（为卢显文家金林檎赋）', '日丽风暄，暗催春去，春尚留恋。香褪花梢，苔侵柳径，密幄清阴展。海棠零乱，梨花淡伫，初听闹空莺燕。有轻盈、妍姿靓态，缓步阆风仙苑。 绿丛红萼，芳鲜柔媚，约略试妆深浅。细叶来禽，长梢戏蝶，簇簇枝头见。酡颜真发，春愁无力，困倚画屏娇软。只应怕、风欺雨横，落红万点。'),
	(9875, 569, '酹江月（乙未中元自柳州过白莲）', '晓风清暑，映湖光如练，山光如染。十里荷花香满路，飞盖斜＿妆面。一叶扁舟，数声柔橹，陡觉红尘远。六桥三塔，恍然图画中见。 因念当日三贤，两山佳处，应也经行遍。琢月吟风无限句，景物随人俱显。贺监风流，玄真清致，我亦情非浅。渔蓑投老，利名何用深羡。'),
	(9876, 569, '沁园春', '羊角飘尘，金乌烁石，雨凉念秋。有虚堂临水，披襟散发，纱帱雾卷，湘簟波浮。远列云峰，近参荷气，卧看文书琴枕头。蝉声寂，向庄周梦里，栩栩无谋。 茶瓯。醒困堪求。粗饱饭安居可以休。算悠闲静胜，吾能自乐，荣华纷扰，人谩多愁。习懒非痴，觉迷是病，一力那能胜九牛。俱休问，且追寻觞咏，知友从游。'),
	(9877, 569, '水调歌头（和石林韵）', '世态万纷变，人事一何忙。胸中素韬奇蕴，匣剑岂能藏。不向燕然纪绩，便与渔樵争席，摆脱是非乡。要地时难得，闲处日偏长。 志横秋，谋夺众，谩轩昂。蝇头蜗角微利，争较一毫芒。幸有乔林修竹，随分粗衣粝食，何必计冠裳。我已乐萧散，谁与共平章。'),
	(9878, 569, '水调歌头', '心景两无著，情物岂能移。超然远览失笑，名利苦纷驰。一品官资荣显，百万金珠豪富，宽自喜家肥，会得个中理，川泳与云飞。 静中乐，闲中趣，自舒迟。心如止水，无风无自更生漪。已是都忘人我，一任吾身醒醉，有酒引连卮。万法无差别，融解即同归。'),
	(9879, 390, '赤枣子', '香露湿，草晶荧。起看大地粲瑶琼。下界千门人寂寂，空山夜静海波声。'),
	(9880, 671, '霜天晓月', '竹篱茅屋。一树扶疏玉。客里十分清绝，有人在、江南北。 伫目。诗思促。翠袖倚修竹。不是月媒风聘，谁人与、伴幽独。'),
	(9881, 671, '贺新郎（咏水仙）', '绰约人如玉。试新妆、娇黄半绿，汉宫匀注。倚傍小阑闲伫立。翠带风前似舞。记洛浦、当年俦侣。罗袜尘生香冉冉，料征鸿、微步凌波女。惊梦断，楚江曲。 春工若见应为主。忍教都、闲亭邃馆，冷风凄雨。待把此花都折取。和泪连香寄与。须信道、离情如许。烟水茫茫斜照里，是骚人、《九辨》招魂处。千古恨，与谁语。'),
	(9882, 671, '太常引', '东城归路水云间。几曾放、梦魂闲。何日整归鞍。又人对、西风凭栏。 温柔情性，系怀伤感，欲诉诉应难。愁聚两眉端。又叠起、千山万山。'),
	(9883, 671, '临江仙', '月是银＿溪是镜，云霓与作衣裳。夜寒独立竹篱傍。妆成那待粉，笑罢自生香。 自古佳人多薄命，枉教傲雪凌霜。从来林下异闺房。何须三弄笛，方断九回肠。'),
	(9884, 671, '水调歌头（自广中出，过庐陵，赠歌姬段云卿）', '有美如花客，容饬尚中州。玉京杳渺际，与别几经秋。家在金河堤畔，身寄白苹洲末，南北两悠悠。休苦话萍梗，清泪已难收。 玉壶酒，倾潋滟，听君讴。伫雪却月，新弄一曲洗人忧。同是天涯沦落，何必平生相识，相见且迟留。明日征帆发，风月为君愁。'),
	(9885, 446, '蝶恋花', '别后相思无限忆。欲说相思，要见终无计。拟写相思持送似。如何尽得相思意。 眼底相思心里事。纵把相思，写尽凭谁寄。多少相思都做泪。一齐泪损相思字。'),
	(9886, 446, '满庭芳（寄别）', '修竹＿蓝，梅山耸翠，小小佳处西安。从来闻说，今日远来看。便好求田问舍，耳溪涧、目饱林峦。争知道，尘缘未了，无计与盘桓。 小蛮。应念处，弦孤么凤，镜掩孤鸾。愁再见多情，素日闲闲。早晚扁舟两桨，惊翠枕、云巘风湍。从前去，殷勤细数，细数万重滩。'),
	(9887, 446, '水调歌头', '美人在何许，相望正悠悠。云我雾窗，遥想宛在海中洲。空对残云冷雨，何限重山叠水，一梦到无由。遗怨写红叶，薄幸记青楼。 金乌掷，玉蟾缺，物华休。凤梧眢井，一夜风露各惊秋。唯有远山无赖，淡扫一眉绿，特地向人愁。敛袂且归去，回首谩迟留。'),
	(9888, 446, '浣溪沙', '迎客西来送客行。堆堆历历短长亭。＿人残酒不能醒。 烟染暮山浮紫翠，霜凋秋叶复丹青。凭谁图写入银屏。'),
	(9889, 446, '一剪梅（送晁驹父）', '萍水相逢无定居。同在他乡，又问征途。离歌声里客心孤。花尽园林，水满江湖。 烟树微茫带岸蒲。何处长沙，何处洪都。要知安稳到家无。千里征鸿，一纸来书。'),
	(9890, 446, '清平乐', '山明水嫩。潇洒桐庐郡。极目风烟无限景。说也如何得尽。 自怜俗状尘容。几年断梗飘蓬。借使严陵知道，只应笑问东风。'),
	(9891, 446, '清平乐（送同舍周智隆）', '恼花风雨。断送春将暮。底死留春春不住。那更送春归去。 今朝且赋归与。明年春满皇都。共泛桃花锦浪，与君同醉西湖。'),
	(9892, 446, '忆秦娥', '秦楼月。秦娥本是秦宫客。秦宫客梦云风韵，借仙标格。 相从无计不如休，如今去也空相忆。空相忆。尊前欢笑，梦中寻觅。'),
	(9893, 446, '胜胜慢', '花前月下，好景良辰，厮守日许多时。正美之间，何事便有轻离。无端珠泪暗簌，染征衫、点点红滋。最苦是、□殷勤密约，做造相思。 咿哑橹声离岸，魂断处，高城隐隐天涯。万水千山，一去定失花期。东君斗来无赖，散春红、点破梅枝。病成也，到而今、著个甚医。'),
	(9894, 446, '玉楼春', '风光淡沲云容粹。点染园林添况味。花间照夜簇红纱，柳外踏青摇彩旆。 芳时不分空憔悴。抖擞愁怀赊乐事。罗衫一任涴尘泥，拚了通宵排日醉。'),
	(9895, 446, '醉落魄', '空庭草积。吹花风去春无迹。锁鸾深处应相忆。红染罗巾，颦损眉山碧。 曲屏尘暗双鸂鶒。醉衾不暖炉烟湿。一帘暝色人孤寂。梦里灯残，心上雨声滴。'),
	(9896, 446, '鹧鸪天', '一别音尘两杳然。不堪虚度菊花天。惊秋远雁横斜字，噪晚哀蝉断续弦。 好将息，恶姻缘。凉宵如水复如年。梦魂不怕风波险，飞过江西阿那边。'),
	(9897, 446, '鹧鸪天', '家在东湖湖上头。别来风月为谁留。落霞孤鹜齐飞处，南浦西山相对愁。 真了了，好休休。莫教辜负菊花秋。浮云富贵何须羡，画饼声名肯浪求。'),
	(9898, 634, '如梦令', '红紫不将春住。风定更飘无数。溪涨绿含风，短艇晚横沙渡。归去。归去。肯念西池鸳鹭。'),
	(9899, 634, '朝中措', '求田何处是生涯。双鬓已先华。随分夏凉冬暖，赏心秋月春花。 吾年如此，愁来问酒，困后呼茶。结社竹林诗老，卜邻江上渔家。'),
	(9900, 634, '浣溪沙（新安驿席上留别）', '不怕春寒更出游。兰桡飞动却惊鸥。烟光佳处辄迟留。 屏曲未曾歌醉梦，眉尖空只锁闲愁。从教丝柳绊行舟。'),
	(9901, 634, '满江红', '天岂无情，天若道、有情亦老。功名事、问天因甚，蒙人不了。好伴云烟耕谷口，休将翰墨传江表。算鬓边、能得几春风，惊秋早。 陶令尹，张京兆。怀舒啸，贪荣耀。尽南柯一梦，漏残钟晓。滕阁暮霞孤鹜举，庾楼明月鸟飞绕。念老来、於此兴无穷，知音少。'),
	(9902, 456, '木兰花', '疏枝半作窥窗老。又是一年春意早。风低小院得香迟，月傍女墙和影好。 去年苦被离情恼。今日逢花休草草。后时花紫尽从他，且趁先春拚醉倒。'),
	(9903, 456, '减字木兰花', '双双相并。一点红边偏照映。玉翦云裁。不比浮花共蒂开。 几回心曲。选胜摘来情自足。插向云鬟。要与仙郎比并看。'),
	(9904, 456, '乌夜啼', '绿外深深柳巷，红间曲曲花楼，一春想见贪游台，不道有人愁。 三月东风易老，几宵明月难留。酴醿白尽窗前也，还肯醉来否。'),
	(9905, 456, '小桃红', '不恨残花妥。不恨残春破。只恨流光，一年一度，又催新火。纵青天白日系长绳，也留春得么。 花院重教锁。春事从教过。烧＿园林，尝梅台榭，有何不可。已安排、珍簟小胡床，待日长闲坐。'),
	(9906, 456, '意难忘', '花拥鸳房。记驼肩髻小，约鬓眉长。轻身翻燕舞，低语转莺簧。相见处，便难忘。肯亲度瑶觞。向夜阑，歌翻郢曲，带换韩香。 别来音信难将。似云收楚峡，雨散巫阳。相逢情有在，不语意难量。些个事，断人肠。怎禁得恓惶。待与伊、移根换叶，试又何妨。'),
	(9907, 456, '木兰花慢', '倩娇莺姹燕，说不尽、此时情。正小院春阑，芳园昼锁，人去花零。凭高试回望眼，奈遥山远水隔重云。谁遣风狂雨横，便教无计留春。 谁知雁杳与鸿冥。自难寄丁宁。纵柳院颦深，桃门笑在，知属何人。衣篝几回忘了，奈残香、犹有旧时熏。空使风头卷絮，为他飘荡花城。'),
	(9908, 456, '菩萨蛮', '晓烟笼日浮山翠。春风著水回川媚。远近碧重重。人家山色中。 野花香自度。似识幽人处。安得著三间。与山终日闲。'),
	(9909, 456, '菩萨蛮（回文）', '暑庭消尽风鸣树。树鸣风尽消庭暑。横枕一声莺。莺声一枕横。 扇纨低粉面。面粉低纨扇。凉月淡侵床。床侵淡月凉。'),
	(9910, 456, '菩萨蛮（正月三日西山即事）', '山头翠树调莺舌。山腰野菜飞黄蝶。来为等闲休。去成多少愁。 小庭花木改。犹有啼痕在。别后不曾看。怕花和泪残。'),
	(9911, 456, '蝶恋花', '日下船篷人未起。一个燕儿，说尽伤春意。江上残花能有几。风催雨促成容易。 湖海客心千万里。著力东风，推得人行未。相次桃花三月水。菱歌谁伴西湖醉。'),
	(9912, 456, '鹊桥仙（秋日寄怀）', '角声吹月，风声落枕，梦与柔肠俱断。谁教当日太情浓，扬不下、新愁一段。 黄花开了，梅花开未，曾约那时相见。莫教容易负幽期，怕真个、孤他泪眼。'),
	(9913, 456, '长相思', '风敲窗。雨敲窗。窗外芭蕉云作幢。声声愁对床。 剔银缸。点银缸。梦采芙蓉隔一江。几时蝴蝶双。'),
	(9914, 456, '南乡子', '几日诉离尊。歌尽阳关不忍分。此度天涯真个去，销魂。相送黄花落叶村。 斜日又黄昏。萧寺无人半掩门。今夜粉香明月泪，休论。只要罗巾记旧痕。'),
	(9915, 456, '望江南（夜泊龙桥滩前遇雨作）', '篷上雨，篷底有人愁。身在汉江东畔去，不知家在锦江头。烟水两悠悠。 吾老矣，心事几时休。沈水熨香年似日，薄云垂帐夏如秋。安得小书舟家有拟舫名书舟。'),
	(9916, 456, '如梦令', '风入藕花翻动。夜气与香俱纵。月又带风来，凉意一襟谁共。情重。情重。可惜短宵无梦。'),
	(9917, 456, '眼儿媚（陆校：按此调亦系朝中措，作眼儿媚误）', '一枝烟雨瘦东墙。真个断人肠。不为天寒日暮，谁怜水远山长。 相思月底，相思竹外，犹自禁当。只恐玉楼贪梦，输他一夜清香。'),
	(9918, 456, '一翦梅', '小会幽欢整及时。花也相宜。人也相宜。宝香未断烛光低。莫厌杯迟。莫恨欢迟。 夜渐深深漏渐稀。风已侵衣。露已沾衣。一杯重劝莫相违。何似休归。何自同归。'),
	(9919, 456, '南浦', '金鸭懒熏香，向晚来春酲，一枕无绪。浓绿涨瑶窗，东风外、吹尽乱红飞絮。无言伫立，断肠惟有流莺语。碧云欲暮，空惆怅，韶华一时虚度。 追思旧日心情，记题叶西楼，吹花南浦。老去觉欢疏，伤春恨，都付断云残雨。黄昏院落，问谁犹在凭阑处。可堪杜宇，空只解声声，催他春去。'),
	(9920, 563, '清平乐（和石次仲）', '断云漏雨。依约西山暮。风定樯高须小住。不忍带将春去。 此行抑有求欤。青衣拟问平都。万里一钩新月，相忘常在江湖。'),
	(9921, 563, '鹊桥仙', '东风唤我，西园闲坐，大醉高歌竟日。行藏独倚画栏干，便忘了、征衫泪湿。 亭高烟远，天低云近，相对逃名隐客。掀髯无语看青山，断不信、尘埃到得。'),
	(9922, 563, '念奴娇', '江南到处，被波光云影，留人行色。昔我来时春正好，舞絮□飞南陌。今日登临，读书斋上，重作凭栏客。清希呦福估次⒄切卤獭＊廌桨墩核嫈ㄒ酰籧虾绾釐障，一带连环璧。林外青山千万叠，雨歇半空犹湿。已倩双鳞，更须灵鹊，先报归消息。归来征袖，尽携千里风月。'),
	(9923, 563, '虞美人（无题）', '长空一夜霜风吼。寒色消残酒。问伊今夜在谁行。遗恨落花流水、误刘郎。 忧云＿雨多情话。分付阿谁也。侬家有分受凄惶。只怕娇痴不睡、也思量。'),
	(9924, 563, '鹧鸪天', '雨沐芙蓉秋意清。可人风月满江城。怜风爱月方留恋，对月临风又送行。 人渐远，酒须倾。只凭一醉遣多情。重来休厌刘郎老，明月清风有素盟。'),
	(9925, 591, '品令', '迢迢征路。又小舸、金陵去。西风黄叶，淡烟衰草，平沙将暮。回首高城，一步远如一步。 江边朱户。忍追忆、分携处。今宵山馆，怎生禁得，许多愁绪。辛苦罗巾，揾取几行泪雨。'),
	(9926, 591, '苏武慢（雪）', '帐暖金丝，杯干云液，战退夜□＿飘。障泥系马，扫路迎宾，先借落花春色。歌竹传觞，探梅得句，人在玉楼琼室。唤吴姬学舞，风流轻转，弄娇无力。 尘世换、老尽青山，铺成明月，瑞物已深三尺。丰登意绪，婉娩光阴，都作暮寒堆积。回首驱羊旧节，入蔡奇兵，等闲陈迹。总无如现在，尊前一笑，坐中赢得。'),
	(9927, 591, '好事近', '花月赏心天，抬举多情诗客。取次锦袍须贳，爱春醅浮雪。 黄鹂何处故飞来，点破野云白。一点暗红犹在，正不禁风色。'),
	(9928, 591, '眼儿媚（妓）', '烟花丛里不宜他。绝似好人家。淡妆娇面，轻注朱唇，一朵梅花。 相逢比著年时节，顾意又争些来朝去也，莫因别个，忘了人咱。'),
	(9929, 591, '念奴娇（谢王广文双姬词）', '西真姊妹，料凡心忽起，共辞瑶阙。燕燕莺莺相并比，的当两团儿雪。合韵歌喉，同茵舞袖，举措□□别。江梅影里，迥然双蕊奇绝。 还听别院笙歌，仓皇走报，笑语浑重叠。拾翠洲边携手处，疑是桃根桃叶。并蒂芳莲，双头红药，不意俱攀折。今宵鸳帐，有同对影明月。'),
	(9930, 591, '一翦梅', '歌罢尊空月坠西。百花门外，烟翠霏微。绛纱笼烛照于飞。归去来兮。归去来兮。 酒入香腮分外宜。行行问道，还肯相随。娇羞无力应人迟。何幸如之。何幸如之。'),
	(9931, 591, '一翦梅', '尘洒衣裾客路长。霜林已晚，秋蕊犹香。别离触处是悲凉。梦里青楼，不忍思量。 天宇沈沈落日黄。云遮望眼，山割愁肠。满怀珠玉泪浪浪。欲倩西风，吹到兰房。'),
	(9932, 591, '霜天晓角', '暮山层碧。掠岸西风急。一叶软红深处，应不是、利名客。 玉人还伫立。绿窗生怨泣。万里衡阳归恨，先倩雁、寄消息。'),
	(9933, 591, '谒金门', '归去未。风雨送春行李。一枕离愁头澈尾。如何消遣是。 遥想归舟天际。绿鬓珑璁慵理。好梦未成莺唤起。粉香犹有＿。'),
	(9934, 591, '减字木兰花（宿僧房有作）', '僧窗夜雨。茶鼎熏炉宜小住。却恨春风。勾引诗来恼杀翁。 狂歌未可。且把一尊料理我。我到亡何。却听侬家陌上歌。'),
	(9935, 591, '浣溪沙（寿内子）', '寿酒同斟喜有余。朱颜却对白髭须。两人百岁恰乘除。 婚嫁剩添儿女拜，平安频拆外家书。年年堂上寿星图。'),
	(9936, 591, '清平乐（书王德由主簿扇）', '溪回沙浅。红杏都开遍。鸂鶒不知春水暖。犹傍垂杨春岸。 片帆千里轻船。行人想见＿眠。谁似先生高举，一行白鹭青天。'),
	(9937, 591, '西江月（木犀）', '金粟如来出世，蕊宫仙子乘风。清香一袖意无穷。洗尽尘缘千种。 长为西风作主，更居明月光中。十分秋意与玲珑。拚却今宵无梦。'),
	(9938, 591, '鹧鸪天（三山道中）', '抛却山中诗酒窠。却来官府听笙歌。闲愁做弄天来大，白发栽埋日许多。 新剑戟，旧风波。天生予懒奈予何。此身已觉浑无事，却教儿童莫恁么。'),
	(9939, 591, '鹧鸪天（郑守厚卿席上谢余伯山，用其韵）', '梦断京华故倦游。只今芳草替人愁。阳关莫作三叠唱，越女应须为我留。 看逸韵，自名流。青衫司马且江州。君家兄弟真堪笑，个个能修五凤楼。'),
	(9940, 591, '蝶恋花（客有燕语莺啼人乍远之句，用为首句）', '燕语莺啼人乍远。却恨西园，依旧莺和燕。笑语十分愁一半。翠园特地春光暖。 只道书来无过雁。不道柔肠，近日无肠断。柄玉莫摇湘泪点。怕君唤作秋风扇。'),
	(9941, 591, '临江仙（再用图字韵）', '窄样金杯教换了，房栊试听珊珊。莫教秋扇雪团团。古今悲笑事，长付后人看。 记取桔槔春雨后，短畦菊艾相连。拙於人处巧於天。君看流水地，难得正方圆。'),
	(9942, 591, '临江仙', '手捻黄花无意绪，等闲行尽回廊。卷帘芳桂散余香。枯荷难睡鸭，疏雨暗池塘。 忆得旧时携手处，如今水远山长。罗巾浥泪别残妆。旧欢新梦里，闲处却思量。'),
	(9943, 591, '踏莎行（赋木犀）', '弄影阑干，吹香岩谷。枝枝点点黄金粟。未堪收拾付薰炉，窗前且把《离骚》读。 奴仆葵花，儿曹金菊。一秋风露清凉足。傍边只欠个姮娥，分明身在蟾宫宿。'),
	(9944, 591, '满江红', '紫陌飞尘，望十里、雕鞍绣＿。春未老、已惊台榭，瘦红肥绿。睡雨海棠犹倚醉，舞风杨柳难成曲。问流莺、能说故园无，曾相熟。 岩泉上，飞凫浴。巢林下，栖禽宿。恨荼＿开晚，谩翻船玉。莲社岂堪谈昨梦，兰亭何处寻遗墨。但羁怀、空自倚秋千，无心蹴。'),
	(9945, 591, '满江红', '风卷庭梧，黄叶坠、新凉如洗。一笑折、秋英同尝，弄香挼蕊。天远难穷休久望，楼高欲下还重倚。拚一襟、寂寞泪弹秋，无人会。 今古恨、沈荒垒。悲欢事，随流水。想登楼青鬓，未堪憔悴。极目烟横山数点，孤舟月淡人千里。对婵娟、从此话离愁，金尊里。'),
	(9946, 591, '水调歌头（赋松菊堂）', '渊明最爱菊，三径也栽松。何人收拾，千载风味此山中。手把《离骚》读遍，自扫落英餐罢，杖屦晓霜浓。皎皎太独立，更插万芙蓉。 水潺＿，云澒洞，石＿＿。素琴浊酒唤客，端有古人风。却怪青山能巧，政尔横看成岭，转面已成峰。诗句得活法，日月有新工。'),
	(9947, 591, '沁园春（戊申岁，奏邸忽腾报，谓余以病挂冠，因赋此）', '老子平生，笑尽人间，儿女怨恩。况白头能几，定应独往，青云得意，见说长存。抖擞衣冠，怜渠无恙，合挂当年神武门。都如梦，算能争几许，鸡晓钟昏。 此心无有新冤。况抱瓮年来自灌园。但凄凉顾影，频悲往事，殷勤对佛，欲问前因。却怕青山，也妨贤路，休斗尊前见在身。山中友，试高吟楚些，重与招魂。'),
	(9948, 591, '贺新郎（再用前韵）', '鸟倦飞还矣。笑渊明、瓶中储粟，有无能几。莲社高人留翁语，我醉宁论许事。试沽酒、重斟翁喜。一见萧然音韵古，想东篱、醉卧参差是。千载下，竟谁似。 元龙百尺高楼里。把新诗、殷勤问我，停云情味。北夏门高从拉攞，何事须人料理。翁曾道、繁华朝起。尘土人言宁可用，顾青山、与我何如耳。歌且和，楚狂子。'),
	(9949, 591, '西江月（正月四日和建宁陈安行舍人，时被召）', '风月亭危致爽，管弦声脆休催。主人只是旧时怀。锦瑟旁边须醉。 玉殿何须侬去，沙堤只要公来。看看红药又翻阶。趁取西湖春会。'),
	(9950, 591, '西江月（寿钱塘弟正月十六日，时新居成）', '画栋新垂帘幕，华灯未放笙歌。一杯潋滟泛金波。先向太夫人贺。 富贵吾应自有，功名不用渠多。只将绿鬓抵羲娥。金印须教斗大。'),
	(9951, 591, '鹧鸪天（吴子似过秋水）', '秋水长廊水石间。有谁来共听潺＿。羡君人物东西晋，分我诗名大小山。 穷自乐，懒方闲。人间路窄酒杯宽。看君不了痴儿事，又似风流靖长官。'),
	(9952, 591, '玉楼春', '故也。余谓世无是事而有是理，乐所谓无，犹之有也。戏作数语以明之 有无一理谁差别。乐令区区浑未达。事言无处未尝无，试把所无凭理说。 伯夷饥采西山蕨。何毕捣齑餐杵铁。仲尼去卫又之陈，此是北车入鼠穴。'),
	(9953, 591, '临江仙（诸葛元亮席上见和，再用韵）', '夜语南堂新瓦响，三更急雨珊珊。交情莫作细沙团。死生贫富际，试向此中看。 记取他年耆旧传，与君名字牵连。清风一枕晚凉天。觉来还自笑，此梦倩谁圆。'),
	(9954, 591, '千年调（开山径得石壁，因名曰苍壁，出望外，意天之所赐邪，喜而赋之）', '左手把青霓，右手挟明月。吾使丰隆前导，叫开阊阖。周游上下，径入寥天一。览县平声圃，万斛泉，千丈石。 钧天广乐，燕我瑶之席。帝饮予觞甚乐，赐汝苍璧。嶙峋突兀，正在一丘壑。余马怀仆夫悲，下恍惚。'),
	(9955, 591, '西江月（为范南伯寿）', '秀骨青松不老，新词玉佩相磨。灵槎准拟泛银河。剩摘天星几个。 奠枕楼东风月，驻春亭上笙歌。留君一醉意如何。金印明年斗大。'),
	(9956, 591, '谒金门', '山吐月。画烛从教风灭。一曲瑶琴才听彻。金蕉三两叶。骤雨身凉还热。 似欠舞琼歌雪。近日醉乡音问绝。有时清泪咽。'),
	(9957, 591, '谒金门（和廊之五月雪楼小集韵）', '遮素月，云外金蛇明灭。翻树啼鸦声未彻。雨声惊落叶。 宝蜡成行嫌热。玉腕藕花谁雪。流水高山弦断绝。怒蛙声自咽。'),
	(9958, 591, '鹧鸪天', '一片归心拟乱云。春来谙尽恶黄昏。不堪向晚檐前雨，又待今宵滴梦魂。 炉烬冷，鼎香氛。酒寒谁遣为重温。何人柳外横双笛，客耳那堪不忍闻。'),
	(9959, 591, '洞仙歌·旧交贫贱', '旧交贫贱，太半成新贵。冠盖门前几行李。看匆匆西笑，争出山来，凭谁问，小草何如远志。 悠悠今古事。得丧乘除，暮四朝三又何异。任掀天勋业，冠古文章，有几个、笙歌晚岁。况满屋貂蝉未为荣，记裂土分茅，是公家世。'),
	(9960, 591, '归朝欢', '书不借出 万里康成西走蜀。药市船归书满屋。有时光彩射星躔，何人汗简雠天禄。好之宁有足。请看良买藏金玉。记斯文，千年未丧，四壁闻丝竹。 试问辛勤携一束。何似牙签三万轴。古为不作借人痴，有朋只就云窗读。忆君清梦熟。觉来笑我便便腹。倚危楼，人间何处，扫地八风曲。'),
	(9961, 591, '喜迁莺（谢赵晋臣敷文赋芙蓉词见寿，用韵为谢）', '暑风凉月。爱亭亭无数，绿衣持节。掩冉如羞，参差似妒，拥出芙渠花发。步衬潘娘堪恨，貌比六郎谁洁。添白鹭，晚晴时，公子佳人并列。 休说。搴木末。当日灵均，恨与君王别。心阻媒劳，交疏怨极，恩不甚兮轻绝。千古离骚文字，芳至今犹未歇。都休问，但千杯快饮，露荷翻叶。'),
	(9962, 591, '行香子（博山戏简昌父、仲止）', '少日尝闻。富不如贫。贵不如、贱者长存。由来至乐，总属闲人。且饮瓢泉，弄秋水，看停云。 岁晚情亲。老语弥真，记前时、劝我殷勤。都休＿酒，也莫论文。把相牛经，种鱼法，教儿孙。'),
	(9963, 591, '水龙吟（用瓢泉韵戏陈仁和兼简诸葛元亮，且督和词）', '被公惊倒瓢泉，倒流三峡词源泻。长安纸贵，流传一字，千金争舍。割内怀归，先生自笑，又何廉也渠坐事失官。但衔杯莫问，人间岂有，如孺子、长贫者。 谁识稼轩心事，似风乎、舞＿之下。回头落日，苍茫万里，尘埃野马。更想隆中，卧龙千尺，高吟才罢。倩何人与问，雷鸣瓦釜，甚黄钟哑。'),
	(9964, 591, '贺新郎（用韵题赵晋臣敷文积翠岩、余欲令筑陂於其前）', '拄杖重来约。对东风、洞庭张乐，满空箫勺。巨海拨犀头角出，来向此山高阁。尚两两、三三前却。老我伤怀登临际。问何方、可以平哀乐。唯酒是，万金药。 劝君且作横空鹗。便休论、人间腥腐，纷纷乌攫。九万里风斯在下，翻覆云头雨脚。更直上、昆仑濯发。好卧长虹陂十里，是谁言、听取双黄鹤。推翠影，浸云壑。'),
	(9965, 591, '贺新郎（和徐斯远下第谢诸公载酒相访韵）', '逸气轩眉宇。似王良、轻车熟路，骅骝欲舞。我觉君非池中物，咫尺蛟龙云雨。时与命、犹须天付。兰佩芳菲无人问，叹灵均、欲向重华诉。空壹郁，共谁语。 儿曹不料扬雄赋。怪当年、甘泉误说，青葱玉树。风引船回沧溟阔，目断三山伊阻。但笑指、吾庐何许。门外苍官千百辈，尽堂堂、八尺须髯古。谁载酒，带湖去。'),
	(9966, 591, '柳梢青', '八难之辞 莫炼丹难。黄河可塞，金可成难。休辟谷难。吸风饮露，长忍饥难。 劝君莫远游难。何处有、西王母难。休采药难。人沈下土，我上天难。'),
	(9967, 591, '菩萨蛮（题云岩）', '游人占却岩中屋。白云只向檐头宿。谁解探玲珑。青山十里空。 松篁通一径。噤嘇山花冷。今古几千年。西乡小有天。'),
	(9968, 591, '朝中措（九日小集，世长将赴省）', '年年团扇怨秋风。愁绝宝杯空。山下卧龙丰度、台前戏马英雄。 而今休矣，花残人似，人老花同。莫怪东篱韵减，只今丹桂香浓。'),
	(9969, 591, '乌夜啼', '晚花露叶风条。燕飞高。行过长廊西畔、小红桥。 歌再起，人再舞，酒才消。更把一杯重劝、摘樱桃。'),
	(9970, 591, '浣溪沙（偶作）', '艳杏夭桃两行排。莫携歌舞去相催。次第未堪供醉眼，去年栽。 春意才从梅里过，人情都向柳边来。咫尺东家还又有，海棠开。'),
	(9971, 591, '浣溪沙（种松竹未成）', '草木於人也作疏。秋来咫尺共荣枯。空山晚翠孰华余。 孤竹君穷犹抱节，赤松子嫩已生须。主人相爱肯留无。'),
	(9972, 591, '浣溪沙（黄沙岭）', '寸步人间百尺楼。孤城春水一沙鸥。天风吹树几时休。 突兀趁人山石狠，朦胧避路野花羞。人家平水庙东头。'),
	(9973, 591, '鹧鸪天（祝良显家牡丹一本百朵）', '占断雕栏只一株。春风费尽几工夫。天香夜染衣犹湿，国色朝酣酒未苏。 娇欲语，巧相扶。不妨老斡自扶疏。恰如翠幕高堂上，来看红衫百子图。'),
	(9974, 591, '鹧鸪天（和吴子似山行韵）', '谁共春光管日华。朱朱纷纷野蒿花。闲愁投老无多子，酒病而今较减些。 山远近，路横斜。正无聊处管弦哗。去年醉处犹能记，细数溪边第几家。'),
	(9975, 591, '鹧鸪天（黄沙道中）', '句里春风正剪裁。溪山一片画图开。轻鸥自趁虚船去，荒犬还迎野妇回。 松菊竹，翠成堆。要擎残雪斗疏梅。乱鸦毕竟无才思，时把琼瑶蹴下来。'),
	(9976, 591, '鹧鸪天（用韵赋梅。三山梅开时，犹有青叶盛，予时病齿）', '病绕梅花酒不空。齿牙牢在莫欺翁。恨无飞雪青松畔，却放疏花翠叶中。 冰作骨，玉为容。当年宫额鬓云松。直须烂醉烧银烛，横笛难堪一再风。'),
	(9977, 591, '玉楼春', '三三两两谁家女。听取鸣禽枝上语。提壶沽酒已多时，婆饼焦时须早去。 醉中忘却来时路。借问行人家住处。只寻古庙那边行，更过溪南乌桕树。'),
	(9978, 591, '定风波（三山送卢国华，约上元重来）', '少日犹堪话别离。老来怕作送行诗。极目南云无过雁。君看。梅花也解寄相思。 无限江山行未了。父老。不须和泪看旌旗。后会丁宁何日□须记。春风十日放灯时。'),
	(9979, 591, '行香子（云岩道中）', '云岫如簪。野涨挼蓝。向春阑、绿醒红酣。青裙缟袂，两两三三。把麹生禅，玉版句，一时参。 拄杖弯环。过眼嵌岩。岸轻乌、白发＿＿。他年来种，万桂千杉。听小绵蛮，新格磔，旧呢喃。'),
	(9980, 591, '哨遍（秋水观）', '蜗角斗争，左触右蛮，一战连千里。君试思、方寸此心微。总虚空、并包无际。喻此理。何言泰山毫末，从来天地一稊米。嗟大少相形，鸠鹏自乐，之二虫又何知。记跖行仁义孔丘非。更殇乐长年老彭悲。火鼠论寒，冰蚕语热，定谁同异。 噫。贵贱随时。连城才换一羊皮。谁与齐万物，庄周吾梦见之。正商略遗遍，翩然顾笑，空堂梦觉题秋水。有客问洪河，百川灌雨，湿流不辨涯涘。於是焉河伯欣然喜。以天下之美尽在己。渺沧溟望洋东视。逡巡向若惊叹，谓我非逢子。大方达观之家，未免长见，犹然笑耳。北堂之水几何其。但清溪一曲而已。'),
	(9981, 591, '水调歌头（将迁新居不成有感戏作。时以病止酒，且遗去歌者。末□及之）', '我亦卜居者，岁晚望三闾。昂昂千里，泛泛不作水中凫。好在书携一束，莫问家徒四壁，往日置锥无。借车载家具，家具少於车。 舞乌有，歌亡是，饮子虚。二三子者爱我，此外故人疏。幽事欲论谁共，白鹤飞来似可，忽去复何如。众鸟欣有托，吾亦爱吾庐。'),
	(9982, 591, '满庭芳（和洪丞相景伯韵）', '倾国无媒，入宫见妒，古来颦损蛾眉。看公如月，光彩众星稀。袖手高山流水，听群蛙、鼓吹荒池。文章手，直须补衮，藻火粲宗彝。 痴儿。公事了，吴蚕缠绕，自吐余丝。幸一枝粗稳，三径新治。且约湖边风月，功名事、欲使谁知。都休问，英雄千古，荒草没残碑。'),
	(9983, 591, '感皇恩（寿范倅）', '七十古来稀，人人都道。不是阴功怎生到。松姿虽瘦，偏耐云寒霜晓看君双鬓底，青青好。 楼雪初晴，庭闱嬉笑。一醉何妨玉壶倒。从今康健，不用灵丹仙草。更看一百岁，人难老。'),
	(9984, 591, '虞美人（赋荼＿）', '群花泣尽朝来露。争奈春归去。不知庭下有荼＿。偷得十分春色、怕春知。 淡中有味清中贵。飞絮残英避。露华微渗玉肌香。恰似杨妃初试、出兰汤。'),
	(9985, 591, '鹊桥仙（赠人）', '风流标格，惺松言语，真个十分奇绝。三分兰菊十分梅，斗合就、一枝风月。 笙簧未语，星河易转，凉夜厌厌留客。只愁酒尽各西东，更把酒、推辞一霎。'),
	(9986, 591, '浣溪沙（漫兴作）', '未到山前骑马回。风吹雨打已无梅。共谁消遣两三杯。 一似旧时春意思，百无是处老形骸。也曾头上带花来。'),
	(9987, 591, '浣溪沙（种梅菊）', '百世孤芳肯自媒。直须诗句与推排。不然唤近酒边来。 自有渊明方有菊，若无和靖即无梅。只今何处向人开。'),
	(9988, 591, '定风波（再和前韵药名）', '仄月高寒水石乡。倚空青碧对禅床。白发自怜心似铁。风月。使君子细与平章。 已判生涯筇竹杖。来往。却惭沙鸟笑人忙。便好剩留黄绢句。谁赋。银钩小草晚天凉。'),
	(9989, 591, '菩萨蛮（乙巳冬前间举似前作，因和之）', '锦书谁寄相思语。天边数遍飞鸿数。一夜梦千回。梅花入梦来。 涨痕粉树发。霜冷沙洲日。心事莫惊鸥。人间千万愁。'),
	(9990, 591, '鹧鸪天（鹅湖归病起作）', '著意寻春懒便回。何如信步两三杯。山才好处行还倦，诗未成时雨早催。 携竹杖，更芒鞋。朱朱粉粉野蒿开。谁家寒食归宁女，笑语柔桑陌上来。'),
	(9991, 591, '归朝欢（题晋臣敷文积翠岩）', '我笑共工缘底怒。触断峨峨天一柱。补天又笑女娲忙，却将此石投闲处。野烟荒草路。先生柱杖来看汝。倚苍苔，摩挲试问，千古几风雨。 长被儿童敲火苦。时有牛羊磨角去。霍然千丈翠岩屏，锵然一滴甘泉乳。结亭三四五。曾相暖热携歌舞。细思量，古来寒士，不遇有时遇。'),
	(9992, 591, '水龙吟（用些语再题瓢泉，歌以饮客，声韵甚谐，客为之酹）', '听兮清佩琼瑶些。明兮镜秋毫些。君无去此，流昏涨腻，生蓬蒿些。虎豹甘人，渴而饮汝，宁猿猱些。大而流江海，覆舟如芥，君无助、狂涛些。 路险兮、山高些。愧余独处无聊些。冬槽春盎，归来为我，制松醪些。其外芳芬，团龙片凤，煮云膏些。古人兮既往，嗟余之乐，乐箪瓢些。'),
	(9993, 591, '水龙吟（题瓢泉）', '稼轩何必长贫，放泉檐外琼珠泻。乐天知命，古来谁会，行藏用舍。人不堪忧，一瓢自乐，贤哉回也。料当年曾问，饭蔬饮水，何为是、栖栖者。 且对浮云山上，莫匆匆、去流山下。苍颜照影，故应流落，轻裘肥马。绕齿冰霜，满怀芳乳，先生饮罢。笑挂瓢风树，一鸣渠碎，问何如哑。'),
	(9994, 591, '念奴娇（双陆和坐客韵）', '少年握槊，气凭陵、酒圣诗豪余事。缩手旁观初未识，两两三三而已。变化须臾，鸥飞石镜，鹊抵星桥□捣残秋练，玉砧犹想纤指。 堪笑千古争心，等闲一胜，拚了光阴费。老子忘机浑谩与，鸿鹄飞来天际。武媚宫中，韦娘局上，休把兴亡记。布衣百万，看君一笑沈醉。'),
	(9995, 591, '满江红（稼轩居士花下与郑使群恂别醉赋，侍者飞卿奉命书）', '折尽荼蘼，尚留得、一分春色。还记取、青梅如弹，共伊同摘。少日对花昏醉梦，而今醒眼看风月。恨牡丹、笑我倚东风，形如雪。 人渐远，君休说。榆荚阵，菖蒲叶。算不因风雨，只因鶗鴂XX。老冉冉兮花共柳，是栖栖者蜂和蝶。也不因、春去有闲愁，因离别。'),
	(9996, 591, '南乡子（舟中记梦）', '敧枕橹声边。贪听咿哑聒醉眠。变作笙歌花底去，依然。翠袖盈盈在眼前。 别后两眉尖。欲说还休梦已阑。只记埋冤前夜月，相看。不管人愁独自圆。'),
	(9997, 591, '霜天晓角（旅兴）', '吴头楚尾。一棹人千里。休说旧愁新恨，长亭树、今如此。 宦游吾倦矣。玉人留我醉。明日万花寒食，得且住、为佳耳。'),
	(9998, 591, '踏歌', '攧厥。看精神、压一庞儿劣。更言语、一似春莺滑。一团儿、美满香和雪。 去也。把春衫、换却同心结。向人道、不怕轻离别。问昨宵、因甚歌声咽。 秋被梦，春闺月。旧家事、却对何人说。告弟弟莫趁蜂和蝶。有春归花落时节。'),
	(9999, 591, '杏花天（无题）', '病来自是於春懒。但别院、笙歌一片。蛛丝网遍玻璃盏。更问舞裙歌扇。 有多少、莺愁蝶怨。甚梦里、春归不管。杨花也笑人情浅。故故沾衣扑面。'),
	(10000, 591, '乌夜啼（廓之见和，复用前韵）', '人言我不如公，酒频中。更把平生湖海、问儿童。千尺蔓。云叶乱。系长松。却笑一身缠绕、似衰翁。');
